INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,1,'بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ','Dengan menyebut nama Allah Yang Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,2,'اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ','Segala puji bagi Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,3,'الرَّحْمٰنِ الرَّحِيْمِۙ','Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,4,'مٰلِكِ يَوْمِ الدِّيْنِۗ','Yang menguasai di Hari Pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,5,'اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَعِيْنُۗ','Hanya Engkaulah yang kami sembah, dan hanya kepada Engkaulah kami meminta pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,6,'اِهْدِنَا الصِّرَاطَ الْمُسْتَقِيْمَ ۙ','Tunjukilah kami jalan yang lurus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,1,7,'صِرَاطَ الَّذِيْنَ اَنْعَمْتَ عَلَيْهِمْ ەۙ غَيْرِ الْمَغْضُوْبِ عَلَيْهِمْ وَلَا الضَّاۤلِّيْنَ ࣖ','(yaitu) Jalan orang-orang yang telah Engkau beri nikmat kepada mereka; bukan (jalan) mereka yang dimurkai dan bukan (pula jalan) mereka yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,1,'الۤمّۤ ۚ','Alif laam miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,2,'ذٰلِكَ الْكِتٰبُ لَا رَيْبَ ۛ فِيْهِ ۛ هُدًى لِّلْمُتَّقِيْنَۙ','Kitab (Al Quran) ini tidak ada keraguan padanya; petunjuk bagi mereka yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,3,'الَّذِيْنَ يُؤْمِنُوْنَ بِالْغَيْبِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۙ','(yaitu) mereka yang beriman kepada yang ghaib, yang mendirikan shalat, dan menafkahkan sebahagian rezeki yang Kami anugerahkan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,4,'وَالَّذِيْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ ۚ وَبِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ','dan mereka yang beriman kepada Kitab (Al Quran) yang telah diturunkan kepadamu dan Kitab-kitab yang telah diturunkan sebelummu, serta mereka yakin akan adanya (kehidupan) akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,5,'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ ۙ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ','Mereka itulah yang tetap mendapat petunjuk dari Tuhan mereka, dan merekalah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,6,'اِنَّ الَّذِيْنَ كَفَرُوْا سَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ','Sesungguhnya orang-orang kafir, sama saja bagi mereka, kamu beri peringatan atau tidak kamu beri peringatan, mereka tidak juga akan beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,7,'خَتَمَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَعَلٰى سَمْعِهِمْ ۗ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌ وَّلَهُمْ عَذَابٌ عَظِيْمٌ ࣖ ','Allah telah mengunci-mati hati dan pendengaran mereka, dan penglihatan mereka ditutup. Dan bagi mereka siksa yang amat berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,8,'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِنِيْنَۘ','Di antara manusia ada yang mengatakan: "Kami beriman kepada Allah dan Hari kemudian," pada hal mereka itu sesungguhnya bukan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,9,'يُخٰدِعُوْنَ اللّٰهَ وَالَّذِيْنَ اٰمَنُوْا ۚ وَمَا يَخْدَعُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَۗ','Mereka hendak menipu Allah dan orang-orang yang beriman, padahal mereka hanya menipu dirinya sendiri sedang mereka tidak sadar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,10,'فِيْ قُلُوْبِهِمْ مَّرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضًاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ۢ ەۙ بِمَا كَانُوْا يَكْذِبُوْنَ','Dalam hati mereka ada penyakit, lalu ditambah Allah penyakitnya; dan bagi mereka siksa yang pedih, disebabkan mereka berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,11,'وَاِذَا قِيْلَ لَهُمْ لَا تُفْسِدُوْا فِى الْاَرْضِۙ قَالُوْٓا اِنَّمَا نَحْنُ مُصْلِحُوْنَ','Dan bila dikatakan kepada mereka: "Janganlah kamu membuat kerusakan di muka bumi". Mereka menjawab: "Sesungguhnya kami orang-orang yang mengadakan perbaikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,12,'اَلَآ اِنَّهُمْ هُمُ الْمُفْسِدُوْنَ وَلٰكِنْ لَّا يَشْعُرُوْنَ','Ingatlah, sesungguhnya mereka itulah orang-orang yang membuat kerusakan, tetapi mereka tidak sadar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,13,'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا كَمَآ اٰمَنَ النَّاسُ قَالُوْٓا اَنُؤْمِنُ كَمَآ اٰمَنَ السُّفَهَاۤءُ ۗ اَلَآ اِنَّهُمْ هُمُ السُّفَهَاۤءُ وَلٰكِنْ لَّا يَعْلَمُوْنَ ','Apabila dikatakan kepada mereka: "Berimanlah kamu sebagaimana orang-orang lain telah beriman". Mereka menjawab: "Akan berimankah kami sebagaimana orang-orang yang bodoh itu telah beriman?" Ingatlah, sesungguhnya merekalah orang-orang yang bodoh; tetapi mereka tidak tahu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,14,'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّا ۚ وَاِذَا خَلَوْا اِلٰى شَيٰطِيْنِهِمْ ۙ قَالُوْٓا اِنَّا مَعَكُمْ  ۙاِنَّمَا نَحْنُ مُسْتَهْزِءُوْنَ','Dan bila mereka berjumpa dengan orang-orang yang beriman, mereka mengatakan: "Kami telah beriman". Dan bila mereka kembali kepada syaitan-syaitan mereka, mereka mengatakan: "Sesungguhnya kami sependirian dengan kamu, kami hanyalah berolok-olok".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,15,' اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ','Allah akan (membalas) olok-olokan mereka dan membiarkan mereka terombang-ambing dalam kesesatan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,16,'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِّجَارَتُهُمْ وَمَا كَانُوْا مُهْتَدِيْنَ','Mereka itulah orang yang membeli kesesatan dengan petunjuk, maka tidaklah beruntung perniagaan mereka dan tidaklah mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,17,'مَثَلُهُمْ كَمَثَلِ الَّذِى اسْتَوْقَدَ نَارًا ۚ فَلَمَّآ اَضَاۤءَتْ مَا حَوْلَهٗ ذَهَبَ اللّٰهُ بِنُوْرِهِمْ وَتَرَكَهُمْ فِيْ ظُلُمٰتٍ لَّا يُبْصِرُوْنَ','Perumpamaan mereka adalah seperti orang yang menyalakan api, maka setelah api itu menerangi sekelilingnya Allah hilangkan cahaya (yang menyinari) mereka, dan membiarkan mereka dalam kegelapan, tidak dapat melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,18,'صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُوْنَۙ','Mereka tuli, bisu dan buta, maka tidaklah mereka akan kembali (ke jalan yang benar),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,19,'اَوْ كَصَيِّبٍ مِّنَ السَّمَاۤءِ فِيْهِ ظُلُمٰتٌ وَّرَعْدٌ وَّبَرْقٌۚ يَجْعَلُوْنَ اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۗ وَاللّٰهُ مُحِيْطٌۢ بِالْكٰفِرِيْنَ','atau seperti (orang-orang yang ditimpa) hujan lebat dari langit disertai gelap gulita, guruh dan kilat; mereka menyumbat telinganya dengan anak jarinya, karena (mendengar suara) petir, sebab takut akan mati. Dan Allah meliputi orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,20,'يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْ ۗ كُلَّمَآ اَضَاۤءَ لَهُمْ مَّشَوْا فِيْهِ ۙ وَاِذَآ اَظْلَمَ عَلَيْهِمْ قَامُوْا ۗوَلَوْ شَاۤءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ','Hampir-hampir kilat itu menyambar penglihatan mereka. Setiap kali kilat itu menyinari mereka, mereka berjalan di bawah sinar itu, dan bila gelap menimpa mereka, mereka berhenti. Jikalau Allah menghendaki, niscaya Dia melenyapkan pendengaran dan penglihatan mereka. Sesungguhnya Allah berkuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,21,' يٰٓاَيُّهَا النَّاسُ اعْبُدُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ وَالَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ','Hai manusia, sembahlah Tuhanmu yang telah menciptakanmu dan orang-orang yang sebelummu, agar kamu bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,22,' الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ فِرَاشًا وَّالسَّمَاۤءَ بِنَاۤءً  ۖوَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚ فَلَا تَجْعَلُوْا لِلّٰهِ اَنْدَادًا وَّاَنْتُمْ تَعْلَمُوْنَ ','Dialah yang menjadikan bumi sebagai hamparan bagimu dan langit sebagai atap, dan Dia menurunkan air (hujan) dari langit, lalu Dia menghasilkan dengan hujan itu segala buah-buahan sebagai rezeki untukmu; karena itu janganlah kamu mengadakan sekutu-sekutu bagi Allah, padahal kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,23,'وَاِنْ كُنْتُمْ فِيْ رَيْبٍ مِّمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوْا بِسُوْرَةٍ مِّنْ مِّثْلِهٖ ۖ وَادْعُوْا شُهَدَاۤءَكُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Dan jika kamu (tetap) dalam keraguan tentang Al Quran yang Kami wahyukan kepada hamba Kami (Muhammad), buatlah satu surat (saja) yang semisal Al Quran itu dan ajaklah penolong-penolongmu selain Allah, jika kamu orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,24,'فَاِنْ لَّمْ تَفْعَلُوْا وَلَنْ تَفْعَلُوْا فَاتَّقُوا النَّارَ الَّتِيْ وَقُوْدُهَا النَّاسُ وَالْحِجَارَةُ ۖ اُعِدَّتْ لِلْكٰفِرِيْنَ','Maka jika kamu tidak dapat membuat(nya) -- dan pasti kamu tidak akan dapat membuat(nya), peliharalah dirimu dari neraka yang bahan bakarnya manusia dan batu, yang disediakan bagi orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,25,'وَبَشِّرِ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اَنَّ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗ كُلَّمَا رُزِقُوْا مِنْهَا مِنْ ثَمَرَةٍ رِّزْقًا ۙ قَالُوْا هٰذَا الَّذِيْ رُزِقْنَا مِنْ قَبْلُ  وَاُتُوْا بِهٖ مُتَشَابِهًا ۗوَلَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ  وَّهُمْ فِيْهَا خٰلِدُوْنَ','Dan sampaikanlah berita gembira kepada mereka yang beriman dan berbuat baik, bahwa bagi mereka disediakan surga-surga yang mengalir sungai-sungai di dalamnya. Setiap mereka diberi rezeki buah-buahan dalam surga-surga itu, mereka mengatakan: "Inilah yang pernah diberikan kepada kami dahulu". Mereka diberi buah-buahan yang serupa dan untuk mereka di dalamnya ada isteri-isteri yang suci dan mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,26,'۞ اِنَّ اللّٰهَ لَا يَسْتَحْيٖٓ اَنْ يَّضْرِبَ مَثَلًا مَّا بَعُوْضَةً فَمَا فَوْقَهَا ۗ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۚ وَاَمَّا الَّذِيْنَ كَفَرُوْا فَيَقُوْلُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًا ۘ يُضِلُّ بِهٖ كَثِيْرًا وَّيَهْدِيْ بِهٖ كَثِيْرًا ۗ وَمَا يُضِلُّ بِهٖٓ اِلَّا الْفٰسِقِيْنَۙ','Sesungguhnya Allah tiada segan membuat perumpamaan berupa nyamuk atau yang lebih rendah dari itu. Adapun orang-orang yang beriman, maka mereka yakin bahwa perumpamaan itu benar dari Tuhan mereka, tetapi mereka yang kafir mengatakan: "Apakah maksud Allah menjadikan ini untuk perumpamaan?". Dengan perumpamaan itu banyak orang yang disesatkan Allah, dan dengan perumpamaan itu (pula) banyak orang yang diberi-Nya petunjuk. Dan tidak ada yang disesatkan Allah kecuali orang-orang yang fasik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,27,'الَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْۢ بَعْدِ مِيْثَاقِهٖۖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۗ  اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ','(yaitu) orang-orang yang melanggar perjanjian Allah sesudah perjanjian itu teguh, dan memutuskan apa yang diperintahkan Allah (kepada mereka) untuk menghubungkannya dan membuat kerusakan di muka bumi. Mereka itulah orang-orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,28,'كَيْفَ تَكْفُرُوْنَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتًا فَاَحْيَاكُمْۚ  ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ ','Mengapa kamu kafir kepada Allah, padahal kamu tadinya mati, lalu Allah menghidupkan kamu, kemudian kamu dimatikan dan dihidupkan-Nya kembali, kemudian kepada-Nya-lah kamu dikembalikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,29,'هُوَ الَّذِيْ خَلَقَ لَكُمْ مَّا فِى الْاَرْضِ جَمِيْعًا ثُمَّ اسْتَوٰٓى اِلَى السَّمَاۤءِ فَسَوّٰىهُنَّ سَبْعَ سَمٰوٰتٍ ۗ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ','Dialah Allah, yang menjadikan segala yang ada di bumi untuk kamu dan Dia berkehendak (menciptakan) langit, lalu dijadikan-Nya tujuh langit. Dan Dia Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,30,'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ ِانِّيْ جَاعِلٌ فِى الْاَرْضِ خَلِيْفَةً ۗ قَالُوْٓا اَتَجْعَلُ فِيْهَا مَنْ يُّفْسِدُ فِيْهَا وَيَسْفِكُ الدِّمَاۤءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۗ قَالَ اِنِّيْٓ اَعْلَمُ مَا لَا تَعْلَمُوْنَ','Ingatlah ketika Tuhanmu berfirman kepada para Malaikat: "Sesungguhnya Aku hendak menjadikan seorang khalifah di muka bumi". Mereka berkata: "Mengapa Engkau hendak menjadikan (khalifah) di bumi itu orang yang akan membuat kerusakan padanya dan menumpahkan darah, padahal kami senantiasa bertasbih dengan memuji Engkau dan mensucikan Engkau?" Tuhan berfirman: "Sesungguhnya Aku mengetahui apa yang tidak kamu ketahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,31,'وَعَلَّمَ اٰدَمَ الْاَسْمَاۤءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰۤىِٕكَةِ فَقَالَ اَنْۢبِـُٔوْنِيْ بِاَسْمَاۤءِ هٰٓؤُلَاۤءِ اِنْ كُنْتُمْ صٰدِقِيْنَ','Dan Dia mengajarkan kepada Adam nama-nama (benda-benda) seluruhnya, kemudian mengemukakannya kepada para Malaikat lalu berfirman: "Sebutkanlah kepada-Ku nama benda-benda itu jika kamu mamang benar orang-orang yang benar!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,32,'قَالُوْا سُبْحٰنَكَ لَا عِلْمَ لَنَآ اِلَّا مَا عَلَّمْتَنَا ۗاِنَّكَ اَنْتَ الْعَلِيْمُ الْحَكِيْمُ ','Mereka menjawab: "Maha Suci Engkau, tidak ada yang kami ketahui selain dari apa yang telah Engkau ajarkan kepada kami; sesungguhnya Engkaulah Yang Maha Mengetahui lagi Maha Bijaksana".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,33,'قَالَ يٰٓاٰدَمُ اَنْۢبِئْهُمْ بِاَسْمَاۤىِٕهِمْ ۚ فَلَمَّآ اَنْۢبَاَهُمْ بِاَسْمَاۤىِٕهِمْۙ قَالَ اَلَمْ اَقُلْ لَّكُمْ اِنِّيْٓ اَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۙ وَاَعْلَمُ مَا تُبْدُوْنَ وَمَا كُنْتُمْ تَكْتُمُوْنَ ','Allah berfirman: "Hai Adam, beritahukanlah kepada mereka nama-nama benda ini". Maka setelah diberitahukannya kepada mereka nama-nama benda itu, Allah berfirman: "Bukankah sudah Ku-katakan kepadamu, bahwa sesungguhnya Aku mengetahui rahasia langit dan bumi dan mengetahui apa yang kamu lahirkan dan apa yang kamu sembunyikan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,34,'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ  اَبٰى وَاسْتَكْبَرَۖ وَكَانَ مِنَ الْكٰفِرِيْنَ','Dan (ingatlah) ketika Kami berfirman kepada para malaikat: "Sujudlah kamu kepada Adam," maka sujudlah mereka kecuali Iblis; ia enggan dan takabur dan adalah ia termasuk golongan orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,35,'وَقُلْنَا يٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ','Dan Kami berfirman: "Hai Adam, diamilah oleh kamu dan isterimu surga ini, dan makanlah makanan-makanannya yang banyak lagi baik dimana saja yang kamu sukai, dan janganlah kamu dekati pohon ini, yang menyebabkan kamu termasuk orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,36,'فَاَزَلَّهُمَا الشَّيْطٰنُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا فِيْهِ ۖ وَقُلْنَا اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚ وَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ','Lalu keduanya digelincirkan oleh syaitan dari surga itu dan dikeluarkan dari keadaan semula dan Kami berfirman: "Turunlah kamu! sebagian kamu menjadi musuh bagi yang lain, dan bagi kamu ada tempat kediaman di bumi, dan kesenangan hidup sampai waktu yang ditentukan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,37,'فَتَلَقّٰٓى اٰدَمُ مِنْ رَّبِّهٖ كَلِمٰتٍ فَتَابَ عَلَيْهِ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ ','Kemudian Adam menerima beberapa kalimat dari Tuhannya, maka Allah menerima taubatnya. Sesungguhnya Allah Maha Penerima taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,38,'قُلْنَا اهْبِطُوْا مِنْهَا جَمِيْعًا ۚ فَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Kami berfirman: "Turunlah kamu semuanya dari surga itu! Kemudian jika datang petunjuk-Ku kepadamu, maka barang siapa yang mengikuti petunjuk-Ku, niscaya tidak ada kekhawatiran atas mereka, dan tidak (pula) mereka bersedih hati".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,39,'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ','Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,40,'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوْا بِعَهْدِيْٓ اُوْفِ بِعَهْدِكُمْۚ وَاِيَّايَ فَارْهَبُوْنِ','Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Aku anugerahkan kepadamu, dan penuhilah janjimu kepada-Ku, niscaya Aku penuhi janji-Ku kepadamu; dan hanya kepada-Ku-lah kamu harus takut (tunduk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,41,'وَاٰمِنُوْا بِمَآ اَنْزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُوْنُوْٓا اَوَّلَ كَافِرٍۢ بِهٖ ۖ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا  ۖوَّاِيَّايَ فَاتَّقُوْنِ ','Dan berimanlah kamu kepada apa yang telah Aku turunkan (Al Quran) yang membenarkan apa yang ada padamu (Taurat), dan janganlah kamu menjadi orang yang pertama kafir kepadanya, dan janganlah kamu menukarkan ayat-ayat-Ku dengan harga yang rendah, dan hanya kepada Akulah kamu harus bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,42,'وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ ','Dan janganlah kamu campur adukkan yang hak dengan yang bathil dan janganlah kamu sembunyikan yang hak itu, sedang kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,43,'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوْا مَعَ الرَّاكِعِيْنَ','Dan dirikanlah shalat, tunaikanlah zakat dan ruku´lah beserta orang-orang yang ruku´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,44,'۞ اَتَأْمُرُوْنَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُوْنَ الْكِتٰبَ ۗ اَفَلَا تَعْقِلُوْنَ ','Mengapa kamu suruh orang lain (mengerjakan) kebaktian, sedang kamu melupakan diri (kewajiban)mu sendiri, padahal kamu membaca Al Kitab (Taurat)? Maka tidaklah kamu berpikir?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,45,'وَاسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ وَاِنَّهَا لَكَبِيْرَةٌ اِلَّا عَلَى الْخٰشِعِيْنَۙ','Jadikanlah sabar dan shalat sebagai penolongmu. Dan sesungguhnya yang demikian itu sungguh berat, kecuali bagi orang-orang yang khusyu´,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,46,'الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رٰجِعُوْنَ ࣖ','(yaitu) orang-orang yang meyakini, bahwa mereka akan menemui Tuhannya, dan bahwa mereka akan kembali kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,47,'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ','Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Aku anugerahkan kepadamu dan (ingatlah pula) bahwasanya Aku telah melebihkan kamu atas segala umat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,48,'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَّلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَّلَا هُمْ يُنْصَرُوْنَ ','Dan jagalah dirimu dari (azab) hari (kiamat, yang pada hari itu) seseorang tidak dapat membela orang lain, walau sedikitpun; dan (begitu pula) tidak diterima syafa´at dan tebusan dari padanya, dan tidaklah mereka akan ditolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,49,'وَاِذْ نَجَّيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ يُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ','Dan (ingatlah) ketika Kami selamatkan kamu dari (Fir´aun) dan pengikut-pengikutnya; mereka menimpakan kepadamu siksaan yang seberat-beratnya, mereka menyembelih anak-anakmu yang laki-laki dan membiarkan hidup anak-anakmu yang perempuan. Dan pada yang demikian itu terdapat cobaan-cobaan yang besar dari Tuhanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,50,'وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنٰكُمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُوْنَ ','Dan (ingatlah), ketika Kami belah laut untukmu, lalu Kami selamatkan kamu dan Kami tenggelamkan (Fir´aun) dan pengikut-pengikutnya sedang kamu sendiri menyaksikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,51,'وَاِذْ وٰعَدْنَا مُوْسٰىٓ اَرْبَعِيْنَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ','Dan (ingatlah), ketika Kami berjanji kepada Musa (memberikan Taurat, sesudah) empat puluh malam, lalu kamu menjadikan anak lembu (sembahan) sepeninggalnya dan kamu adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,52,'ثُمَّ عَفَوْنَا عَنْكُمْ مِّنْۢ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُوْنَ','Kemudian sesudah itu Kami maafkan kesalahanmu, agar kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,53,'وَاِذْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُوْنَ','Dan (ingatlah), ketika Kami berikan kepada Musa Al Kitab (Taurat) dan keterangan yang membedakan antara yang benar dan yang salah, agar kamu mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,54,'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوْبُوْٓا اِلٰى بَارِىِٕكُمْ فَاقْتُلُوْٓا اَنْفُسَكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ عِنْدَ بَارِىِٕكُمْۗ فَتَابَ عَلَيْكُمْ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ ','Dan (ingatlah), ketika Musa berkata kepada kaumnya: "Hai kaumku, sesungguhnya kamu telah menganiaya dirimu sendiri karena kamu telah menjadikan anak lembu (sembahanmu), maka bertaubatlah kepada Tuhan yang menjadikan kamu dan bunuhlah dirimu. Hal itu adalah lebih baik bagimu pada sisi Tuhan yang menjadikan kamu; maka Allah akan menerima taubatmu. Sesungguhnya Dialah Yang Maha Penerima taubat lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,55,'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نُّؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصّٰعِقَةُ وَاَنْتُمْ تَنْظُرُوْنَ','Dan (ingatlah), ketika kamu berkata: "Hai Musa, kami tidak akan beriman kepadamu sebelum kami melihat Allah dengan terang, karena itu kamu disambar halilintar, sedang kamu menyaksikannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,56,'ثُمَّ بَعَثْنٰكُمْ مِّنْۢ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ','Setelah itu Kami bangkitkan kamu sesudah kamu mati, supaya kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,57,'وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ ۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ','Dan Kami naungi kamu dengan awan, dan Kami turunkan kepadamu "manna" dan "salwa". Makanlah dari makanan yang baik-baik yang telah Kami berikan kepadamu; dan tidaklah mereka menganiaya Kami; akan tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,58,'وَاِذْ قُلْنَا ادْخُلُوْا هٰذِهِ الْقَرْيَةَ فَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَّادْخُلُوا الْبَابَ سُجَّدًا وَّقُوْلُوْا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطٰيٰكُمْ ۗ وَسَنَزِيْدُ الْمُحْسِنِيْنَ ','Dan (ingatlah), ketika Kami berfirman: "Masuklah kamu ke negeri ini (Baitul Maqdis), dan makanlah dari hasil buminya, yang banyak lagi enak dimana yang kamu sukai, dan masukilah pintu gerbangnya sambil bersujud, dan katakanlah: "Bebaskanlah kami dari dosa", niscaya Kami ampuni kesalahan-kesalahanmu, dan kelak Kami akan menambah (pemberian Kami) kepada orang-orang yang berbuat baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,59,'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذِيْنَ ظَلَمُوْا رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ ࣖ','Lalu orang-orang yang zalim mengganti perintah dengan (mengerjakan) yang tidak diperintahkan kepada mereka. Sebab itu Kami timpakan atas orang-orang yang zalim itu dari langit, karena mereka berbuat fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,60,'۞ وَاِذِ اسْتَسْقٰى مُوْسٰى لِقَوْمِهٖ فَقُلْنَا اضْرِبْ بِّعَصَاكَ الْحَجَرَۗ  فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْ ۗ كُلُوْا وَاشْرَبُوْا مِنْ رِّزْقِ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ','Dan (ingatlah) ketika Musa memohon air untuk kaumnya, lalu Kami berfirman: "Pukullah batu itu dengan tongkatmu". Lalu memancarlah daripadanya dua belas mata air. Sungguh tiap-tiap suku telah mengetahui tempat minumnya (masing-masing). Makan dan minumlah rezeki (yang diberikan) Allah, dan janganlah kamu berkeliaran di muka bumi dengan berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,61,'وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نَّصْبِرَ عَلٰى طَعَامٍ وَّاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْۢبِتُ الْاَرْضُ مِنْۢ بَقْلِهَا وَقِثَّاۤىِٕهَا وَفُوْمِهَا وَعَدَسِهَا وَبَصَلِهَا ۗ قَالَ اَتَسْتَبْدِلُوْنَ الَّذِيْ هُوَ اَدْنٰى بِالَّذِيْ هُوَ خَيْرٌ ۗ اِهْبِطُوْا مِصْرًا فَاِنَّ لَكُمْ مَّا سَاَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِ الْحَقِّ ۗ ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ ࣖ','Dan (ingatlah), ketika kamu berkata: "Hai Musa, kami tidak bisa sabar (tahan) dengan satu macam makanan saja. Sebab itu mohonkanlah untuk kami kepada Tuhanmu, agar Dia mengeluarkan bagi kami dari apa yang ditumbuhkan bumi, yaitu sayur-mayurnya, ketimunnya, bawang putihnya, kacang adasnya, dan bawang merahnya". Musa berkata: "Maukah kamu mengambil yang rendah sebagai pengganti yang lebih baik? Pergilah kamu ke suatu kota, pasti kamu memperoleh apa yang kamu minta". Lalu ditimpahkanlah kepada mereka nista dan kehinaan, serta mereka mendapat kemurkaan dari Allah. Hal itu (terjadi) karena mereka selalu mengingkari ayat-ayat Allah dan membunuh para Nabi yang memang tidak dibenarkan. Demikian itu (terjadi) karena mereka selalu berbuat durhaka dan melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,62,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالنَّصٰرٰى وَالصَّابِــِٕيْنَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Sesungguhnya orang-orang mukmin, orang-orang Yahudi, orang-orang Nasrani dan orang-orang Shabiin, siapa saja diantara mereka yang benar-benar beriman kepada Allah, hari kemudian dan beramal saleh, mereka akan menerima pahala dari Tuhan mereka, tidak ada kekhawatiran kepada mereka, dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,63,'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ','Dan (ingatlah), ketika Kami mengambil janji dari kamu dan Kami angkatkan gunung (Thursina) di atasmu (seraya Kami berfirman): "Peganglah teguh-teguh apa yang Kami berikan kepadamu dan ingatlah selalu apa yang ada didalamnya, agar kamu bertakwa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,64,'ثُمَّ تَوَلَّيْتُمْ مِّنْۢ بَعْدِ ذٰلِكَ  فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَكُنْتُمْ مِّنَ الْخٰسِرِيْنَ','Kemudian kamu berpaling setelah (adanya perjanjian) itu, maka kalau tidak ada karunia Allah dan rahmat-Nya atasmu, niscaya kamu tergolong orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,65,'وَلَقَدْ عَلِمْتُمُ الَّذِيْنَ اعْتَدَوْا مِنْكُمْ فِى السَّبْتِ فَقُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ','Dan sesungguhnya telah kamu ketahui orang-orang yang melanggar diantaramu pada hari Sabtu, lalu Kami berfirman kepada mereka: "Jadilah kamu kera yang hina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,66,'  فَجَعَلْنٰهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِّلْمُتَّقِيْنَ','Maka Kami jadikan yang demikian itu peringatan bagi orang-orang dimasa itu, dan bagi mereka yang datang kemudian, serta menjadi pelajaran bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,67,'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوْا بَقَرَةً ۗ قَالُوْٓا اَتَتَّخِذُنَا هُزُوًا ۗ قَالَ اَعُوْذُ بِاللّٰهِ اَنْ اَكُوْنَ مِنَ الْجٰهِلِيْنَ ','Dan (ingatlah), ketika Musa berkata kepada kaumnya: "Sesungguhnya Allah menyuruh kamu menyembelih seekor sapi betina". Mereka berkata: "Apakah kamu hendak menjadikan kami buah ejekan?" Musa menjawab: "Aku berlindung kepada Allah agar tidak menjadi salah seorang dari orang-orang yang jahil".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,68,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَ ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا فَارِضٌ وَّلَا بِكْرٌۗ عَوَانٌۢ بَيْنَ ذٰلِكَ ۗ فَافْعَلُوْا مَا تُؤْمَرُوْنَ ','Mereka menjawab: "Mohonkanlah kepada Tuhanmu untuk kami, agar Dia menerangkan kepada kami; sapi betina apakah itu". Musa menjawab: "Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang tidak tua dan tidak muda; pertengahan antara itu; maka kerjakanlah apa yang diperintahkan kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,69,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا لَوْنُهَا ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ صَفْرَاۤءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النّٰظِرِيْنَ','Mereka berkata: "Mohonkanlah kepada Tuhanmu untuk kami agar Dia menerangkan kepada kami apa warnanya". Musa menjawab: "Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang kuning, yang kuning tua warnanya, lagi menyenangkan orang-orang yang memandangnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,70,'قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشٰبَهَ عَلَيْنَاۗ وَاِنَّآ اِنْ شَاۤءَ اللّٰهُ لَمُهْتَدُوْنَ','Mereka berkata: "Mohonkanlah kepada Tuhanmu untuk kami agar Dia menerangkan kepada kami bagaimana hakikat sapi betina itu, karena sesungguhnya sapi itu (masih) samar bagi kami dan sesungguhnya kami insya Allah akan mendapat petunjuk (untuk memperoleh sapi itu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,71,'قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا ذَلُوْلٌ تُثِيْرُ الْاَرْضَ وَلَا تَسْقِى الْحَرْثَۚ مُسَلَّمَةٌ لَّاشِيَةَ فِيْهَا ۗ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّ  فَذَبَحُوْهَا وَمَا كَادُوْا يَفْعَلُوْنَ ࣖ','Musa berkata: "Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang belum pernah dipakai untuk membajak tanah dan tidak pula untuk mengairi tanaman, tidak bercacat, tidak ada belangnya". Mereka berkata: "Sekarang barulah kamu menerangkan hakikat sapi betina yang sebenarnya". Kemudian mereka menyembelihnya dan hampir saja mereka tidak melaksanakan perintah itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,72,'وَاِذْ قَتَلْتُمْ نَفْسًا فَادّٰرَءْتُمْ فِيْهَا ۗ وَاللّٰهُ مُخْرِجٌ مَّا كُنْتُمْ تَكْتُمُوْنَ ۚ','Dan (ingatlah), ketika kamu membunuh seorang manusia lalu kamu saling tuduh menuduh tentang itu. Dan Allah hendak menyingkapkan apa yang selama ini kamu sembunyikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,73,'فَقُلْنَا اضْرِبُوْهُ بِبَعْضِهَاۗ  كَذٰلِكَ يُحْيِ اللّٰهُ الْمَوْتٰى وَيُرِيْكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ','Lalu Kami berfirman: "Pukullah mayat itu dengan sebahagian anggota sapi betina itu !" Demikianlah Allah menghidupkan kembali orang-orang yang telah mati, dam memperlihatkan padamu tanda-tanda kekuasaan-Nya agar kamu mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,74,'ثُمَّ قَسَتْ قُلُوْبُكُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةً ۗ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهٰرُ ۗ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاۤءُ  ۗوَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِ ۗوَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ','Kemudian setelah itu hatimu menjadi keras seperti batu, bahkan lebih keras lagi. Padahal diantara batu-batu itu sungguh ada yang mengalir sungai-sungai dari padanya dan diantaranya sungguh ada yang terbelah lalu keluarlah mata air dari padanya dan diantaranya sungguh ada yang meluncur jatuh, karena takut kepada Allah. Dan Allah sekali-sekali tidak lengah dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,75,'۞ اَفَتَطْمَعُوْنَ اَنْ يُّؤْمِنُوْا لَكُمْ وَقَدْ كَانَ فَرِيْقٌ مِّنْهُمْ يَسْمَعُوْنَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُوْنَهٗ مِنْۢ بَعْدِ مَا عَقَلُوْهُ وَهُمْ يَعْلَمُوْنَ ','Apakah kamu masih mengharapkan mereka akan percaya kepadamu, padahal segolongan dari mereka mendengar firman Allah, lalu mereka mengubahnya setelah mereka memahaminya, sedang mereka mengetahui?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,76,'وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُوْٓا اَتُحَدِّثُوْنَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَاۤجُّوْكُمْ بِهٖ عِنْدَ رَبِّكُمْ ۗ اَفَلَا تَعْقِلُوْنَ','Dan apabila mereka berjumpa dengan orang-orang yang beriman, mereka berkata: "Kamipun telah beriman," tetapi apabila mereka berada sesama mereka saja, lalu mereka berkata: "Apakah kamu menceritakan kepada mereka (orang-orang mukmin) apa yang telah diterangkan Allah kepadamu, supaya dengan demikian mereka dapat mengalahkan hujjahmu di hadapan Tuhanmu; tidakkah kamu mengerti?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,77,'اَوَلَا يَعْلَمُوْنَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ','Tidakkah mereka mengetahui bahwa Allah mengetahui segala yang mereka sembunyikan dan segala yang mereka nyatakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,78,' وَمِنْهُمْ اُمِّيُّوْنَ لَا يَعْلَمُوْنَ الْكِتٰبَ اِلَّآ اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّوْنَ','Dan diantara mereka ada yang buta huruf, tidak mengetahui Al Kitab (Taurat), kecuali dongengan bohong belaka dan mereka hanya menduga-duga.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,79,'فَوَيْلٌ لِّلَّذِيْنَ يَكْتُبُوْنَ الْكِتٰبَ بِاَيْدِيْهِمْ ثُمَّ يَقُوْلُوْنَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوْا بِهٖ ثَمَنًا قَلِيْلًا ۗفَوَيْلٌ لَّهُمْ مِّمَّا كَتَبَتْ اَيْدِيْهِمْ وَوَيْلٌ لَّهُمْ مِّمَّا يَكْسِبُوْنَ ','Maka kecelakaan yAng besarlah bagi orang-orang yang menulis Al Kitab dengan tangan mereka sendiri, lalu dikatakannya; "Ini dari Allah", (dengan maksud) untuk memperoleh keuntungan yang sedikit dengan perbuatan itu. Maka kecelakaan yang besarlah bagi mereka, akibat apa yang ditulis oleh tangan mereka sendiri, dan kecelakaan yang besarlah bagi mereka, akibat apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,80,'وَقَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدَةً ۗ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْدًا فَلَنْ يُّخْلِفَ اللّٰهُ عَهْدَهٗٓ اَمْ تَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ','Dan mereka berkata: "Kami sekali-kali tidak akan disentuh oleh api neraka, kecuali selama beberapa hari saja". Katakanlah: "Sudahkah kamu menerima janji dari Allah sehingga Allah tidak akan memungkiri janji-Nya, ataukah kamu hanya mengatakan terhadap Allah apa yang tidak kamu ketahui?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,81,'بَلٰى مَنْ كَسَبَ سَيِّئَةً وَّاَحَاطَتْ بِهٖ خَطِيْۤـَٔتُهٗ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ','(Bukan demikian), yang benar: barangsiapa berbuat dosa dan ia telah diliputi oleh dosanya, mereka itulah penghuni neraka, mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,82,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ','Dan orang-orang yang beriman serta beramal saleh, mereka itu penghuni surga; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,83,'وَاِذْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ لَا تَعْبُدُوْنَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَانًا وَّذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَقُوْلُوْا لِلنَّاسِ حُسْنًا وَّاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۗ  ثُمَّ تَوَلَّيْتُمْ اِلَّا قَلِيْلًا مِّنْكُمْ وَاَنْتُمْ مُّعْرِضُوْنَ ','Dan (ingatlah), ketika Kami mengambil janji dari Bani Israil (yaitu): Janganlah kamu menyembah selain Allah, dan berbuat kebaikanlah kepada ibu bapa, kaum kerabat, anak-anak yatim, dan orang-orang miskin, serta ucapkanlah kata-kata yang baik kepada manusia, dirikanlah shalat dan tunaikanlah zakat. Kemudian kamu tidak memenuhi janji itu, kecuali sebahagian kecil daripada kamu, dan kamu selalu berpaling.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,84,'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ لَا تَسْفِكُوْنَ دِمَاۤءَكُمْ وَلَا تُخْرِجُوْنَ اَنْفُسَكُمْ مِّنْ دِيَارِكُمْ ۖ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُوْنَ ','Dan (ingatlah), ketika Kami mengambil janji dari kamu (yaitu): kamu tidak akan menumpahkan darahmu (membunuh orang), dan kamu tidak akan mengusir dirimu (saudaramu sebangsa) dari kampung halamanmu, kemudian kamu berikrar (akan memenuhinya) sedang kamu mempersaksikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,85,'ثُمَّ اَنْتُمْ هٰٓؤُلَاۤءِ تَقْتُلُوْنَ اَنْفُسَكُمْ وَتُخْرِجُوْنَ فَرِيْقًا مِّنْكُمْ مِّنْ دِيَارِهِمْۖ تَظٰهَرُوْنَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۗ وَاِنْ يَّأْتُوْكُمْ اُسٰرٰى تُفٰدُوْهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْ ۗ اَفَتُؤْمِنُوْنَ بِبَعْضِ الْكِتٰبِ وَتَكْفُرُوْنَ بِبَعْضٍۚ  فَمَا جَزَاۤءُ مَنْ يَّفْعَلُ ذٰلِكَ مِنْكُمْ اِلَّا خِزْيٌ فِى الْحَيٰوةِ الدُّنْيَا ۚوَيَوْمَ الْقِيٰمَةِ يُرَدُّوْنَ اِلٰٓى  اَشَدِّ الْعَذَابِۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ','Kemudian kamu (Bani Israil) membunuh dirimu (saudaramu sebangsa) dan mengusir segolongan daripada kamu dari kampung halamannya, kamu bantu membantu terhadap mereka dengan membuat dosa dan permusuhan; tetapi jika mereka datang kepadamu sebagai tawanan, kamu tebus mereka, padahal mengusir mereka itu (juga) terlarang bagimu. Apakah kamu beriman kepada sebahagian Al Kitab (Taurat) dan ingkar terhadap sebahagian yang lain? Tiadalah balasan bagi orang yang berbuat demikian daripadamu, melainkan kenistaan dalam kehidupan dunia, dan pada hari kiamat mereka dikembalikan kepada siksa yang sangat berat. Allah tidak lengah dari apa yang kamu perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,86,' اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُوْنَ ࣖ','Itulah orang-orang yang membeli kehidupan dunia dengan (kehidupan) akhirat, maka tidak akan diringankan siksa mereka dan mereka tidak akan ditolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,87,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَقَفَّيْنَا مِنْۢ بَعْدِهٖ بِالرُّسُلِ ۖ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ  اَفَكُلَّمَا جَاۤءَكُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰىٓ اَنْفُسُكُمُ اسْتَكْبَرْتُمْ ۚ فَفَرِيْقًا كَذَّبْتُمْ وَفَرِيْقًا تَقْتُلُوْنَ ','Dan sesungguhnya Kami telah mendatangkan Al Kitab (Taurat) kepada Musa, dan Kami telah menyusulinya (berturut-turut) sesudah itu dengan rasul-rasul, dan telah Kami berikan bukti-bukti kebenaran (mukjizat) kepada Isa putera Maryam dan Kami memperkuatnya dengan Ruhul Qudus. Apakah setiap datang kepadamu seorang rasul membawa sesuatu (pelajaran) yang tidak sesuai dengan keinginanmu lalu kamu menyombong; maka beberapa orang (diantara mereka) kamu dustakan dan beberapa orang (yang lain) kamu bunuh?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,88,'وَقَالُوْا قُلُوْبُنَا غُلْفٌ ۗ بَلْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَلِيْلًا مَّا يُؤْمِنُوْنَ ','Dan mereka berkata: "Hati kami tertutup". Tetapi sebenarnya Allah telah mengutuk mereka karena keingkaran mereka; maka sedikit sekali mereka yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,89,'وَلَمَّا جَاۤءَهُمْ كِتٰبٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْۙ وَكَانُوْا مِنْ قَبْلُ يَسْتَفْتِحُوْنَ عَلَى الَّذِيْنَ كَفَرُوْاۚ فَلَمَّا جَاۤءَهُمْ مَّا عَرَفُوْا كَفَرُوْا بِهٖ ۖ فَلَعْنَةُ اللّٰهِ عَلَى الْكٰفِرِيْنَ ','Dan setelah datang kepada mereka Al Quran dari Allah yang membenarkan apa yang ada pada mereka, padahal sebelumnya mereka biasa memohon (kedatangan Nabi) untuk mendapat kemenangan atas orang-orang kafir, maka setelah datang kepada mereka apa yang telah mereka ketahui, mereka lalu ingkar kepadanya. Maka laknat Allah-lah atas orang-orang yang ingkar itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,90,'  بِئْسَمَا اشْتَرَوْا بِهٖٓ اَنْفُسَهُمْ اَنْ يَّكْفُرُوْا بِمَآ اَنْزَلَ اللّٰهُ بَغْيًا اَنْ يُّنَزِّلَ اللّٰهُ مِنْ فَضْلِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۚ فَبَاۤءُوْ بِغَضَبٍ عَلٰى غَضَبٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌ','Alangkah buruknya (hasil perbuatan) mereka yang menjual dirinya sendiri dengan kekafiran kepada apa yang telah diturunkan Allah, karena dengki bahwa Allah menurunkan karunia-Nya kepada siapa yang dikehendaki-Nya diantara hamba-hamba-Nya. Karena itu mereka mendapat murka sesudah (mendapat) kemurkaan. Dan untuk orang-orang kafir siksaan yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,91,'وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا بِمَآ اَنْزَلَ اللّٰهُ قَالُوْا نُؤْمِنُ بِمَآ اُنْزِلَ عَلَيْنَا وَيَكْفُرُوْنَ بِمَا وَرَاۤءَهٗ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُوْنَ اَنْۢبِيَاۤءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ','Dan apabila dikatakan kepada mereka: "Berimanlah kepada Al Quran yang diturunkan Allah," mereka berkata: "Kami hanya beriman kepada apa yang diturunkan kepada kami". Dan mereka kafir kepada Al Quran yang diturunkan sesudahnya, sedang Al Quran itu adalah (Kitab) yang hak; yang membenarkan apa yang ada pada mereka. Katakanlah: "Mengapa kamu dahulu membunuh nabi-nabi Allah jika benar kamu orang-orang yang beriman?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,92,'۞ وَلَقَدْ جَاۤءَكُمْ مُّوْسٰى بِالْبَيِّنٰتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ','Sesungguhnya Musa telah datang kepadamu membawa bukti-bukti kebenaran (mukjizat), kemudian kamu jadikan anak sapi (sebagai sembahan) sesudah (kepergian)nya, dan sebenarnya kamu adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,93,'وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاسْمَعُوْا ۗ قَالُوْا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوْا فِيْ قُلُوْبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۗ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهٖٓ اِيْمَانُكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','Dan (ingatlah), ketika Kami mengambil janji dari kamu dan Kami angkat bukit (Thursina) di atasmu (seraya Kami berfirman): "Peganglah teguh-teguh apa yang Kami berikan kepadamu dan dengarkanlah!" Mereka menjawab: "Kami mendengar tetapi tidak mentaati". Dan telah diresapkan ke dalam hati mereka itu (kecintaan menyembah) anak sapi karena kekafirannya. Katakanlah: "Amat jahat perbuatan yang telah diperintahkan imanmu kepadamu jika betul kamu beriman (kepada Taurat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,94,' قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِّنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Katakanlah: "Jika kamu (menganggap bahwa) kampung akhirat (surga) itu khusus untukmu di sisi Allah, bukan untuk orang lain, maka inginilah kematian(mu), jika kamu memang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,95,'وَلَنْ يَّتَمَنَّوْهُ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢ بِالظّٰلِمِيْنَ ','Dan sekali-kali mereka tidak akan mengingini kematian itu selama-lamanya, karena kesalahan-kesalahan yang telah diperbuat oleh tangan mereka (sendiri), dan Allah Maha Mengetahui siapa orang-orang yang aniaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,96,'وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍ ۛوَمِنَ الَّذِيْنَ اَشْرَكُوْا ۛيَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِهٖ مِنَ الْعَذَابِ اَنْ يُّعَمَّرَۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ ࣖ','Dan sungguh kamu akan mendapati mereka, manusia yang paling loba kepada kehidupan (di dunia), bahkan (lebih loba lagi) dari orang-orang musyrik. Masing-masing mereka ingin agar diberi umur seribu tahun, padahal umur panjang itu sekali-kali tidak akan menjauhkannya daripada siksa. Allah Maha Mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,97,' قُلْ مَنْ كَانَ عَدُوًّا لِّجِبْرِيْلَ فَاِنَّهٗ نَزَّلَهٗ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ','Katakanlah: "Barang siapa yang menjadi musuh Jibril, maka Jibril itu telah menurunkannya (Al Quran) ke dalam hatimu dengan seizin Allah; membenarkan apa (kitab-kitab) yang sebelumnya dan menjadi petunjuk serta berita gembira bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,98,'مَنْ كَانَ عَدُوًّا لِّلّٰهِ وَمَلٰۤىِٕكَتِهٖ وَرُسُلِهٖ وَجِبْرِيْلَ وَمِيْكٰىلَ فَاِنَّ اللّٰهَ عَدُوٌّ لِّلْكٰفِرِيْنَ','Barang siapa yang menjadi musuh Allah, malaikat-malaikat-Nya, rasul-rasul-Nya, Jibril dan Mikail, maka sesungguhnya Allah adalah musuh orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,99,'وَلَقَدْ اَنْزَلْنَآ اِلَيْكَ اٰيٰتٍۢ بَيِّنٰتٍۚ وَمَا يَكْفُرُ بِهَآ اِلَّا الْفٰسِقُوْنَ','Dan sesungguhnya Kami telah menurunkan kepadamu ayat-ayat yang jelas; dan tak ada yang ingkar kepadanya, melainkan orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,100,'اَوَكُلَّمَا عٰهَدُوْا عَهْدًا نَّبَذَهٗ فَرِيْقٌ مِّنْهُمْ ۗ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُوْنَ','Patutkah (mereka ingkar kepada ayat-ayat Allah), dan setiap kali mereka mengikat janji, segolongan mereka melemparkannya? Bahkan sebagian besar dari mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,101,' وَلَمَّا جَاۤءَهُمْ رَسُوْلٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيْقٌ مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَۙ  كِتٰبَ اللّٰهِ وَرَاۤءَ ظُهُوْرِهِمْ كَاَنَّهُمْ لَا يَعْلَمُوْنَۖ','Dan setelah datang kepada mereka seorang Rasul dari sisi Allah yang membenarkan apa (kitab) yang ada pada mereka, sebahagian dari orang-orang yang diberi kitab (Taurat) melemparkan kitab Allah ke belakang (punggung)nya, seolah-olah mereka tidak mengetahui (bahwa itu adalah kitab Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,102,'وَاتَّبَعُوْا مَا تَتْلُوا الشَّيٰطِيْنُ عَلٰى مُلْكِ سُلَيْمٰنَ ۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيٰطِيْنَ كَفَرُوْا يُعَلِّمُوْنَ النَّاسَ السِّحْرَ وَمَآ اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوْتَ وَمَارُوْتَ ۗ وَمَا يُعَلِّمٰنِ مِنْ اَحَدٍ حَتّٰى يَقُوْلَآ اِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْ ۗ فَيَتَعَلَّمُوْنَ مِنْهُمَا مَا يُفَرِّقُوْنَ بِهٖ بَيْنَ الْمَرْءِ وَزَوْجِهٖ ۗ وَمَا هُمْ بِضَاۤرِّيْنَ بِهٖ مِنْ اَحَدٍ اِلَّا بِاِذْنِ اللّٰهِ ۗ وَيَتَعَلَّمُوْنَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ ۗ وَلَقَدْ عَلِمُوْا لَمَنِ اشْتَرٰىهُ مَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ۗ وَلَبِئْسَ مَاشَرَوْا بِهٖٓ اَنْفُسَهُمْ ۗ  لَوْ كَانُوْا يَعْلَمُوْنَ ','Dan mereka mengikuti apa yang dibaca oleh syaitan-syaitan pada masa kerajaan Sulaiman (dan mereka mengatakan bahwa Sulaiman itu mengerjakan sihir), padahal Sulaiman tidak kafir (tidak mengerjakan sihir), hanya syaitan-syaitan lah yang kafir (mengerjakan sihir). Mereka mengajarkan sihir kepada manusia dan apa yang diturunkan kepada dua orang malaikat di negeri Babil yaitu Harut dan Marut, sedang keduanya tidak mengajarkan (sesuatu) kepada seorangpun sebelum mengatakan: "Sesungguhnya kami hanya cobaan (bagimu), sebab itu janganlah kamu kafir". Maka mereka mempelajari dari kedua malaikat itu apa yang dengan sihir itu, mereka dapat menceraikan antara seorang (suami) dengan isterinya. Dan mereka itu (ahli sihir) tidak memberi mudharat dengan sihirnya kepada seorangpun, kecuali dengan izin Allah. Dan mereka mempelajari sesuatu yang tidak memberi mudharat kepadanya dan tidak memberi manfaat. Demi, sesungguhnya mereka telah meyakini bahwa barangsiapa yang menukarnya (kitab Allah) dengan sihir itu, tiadalah baginya keuntungan di akhirat, dan amat jahatlah perbuatan mereka menjual dirinya dengan sihir, kalau mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,103,'وَلَوْ اَنَّهُمْ اٰمَنُوْا وَاتَّقَوْا لَمَثُوْبَةٌ مِّنْ عِنْدِ اللّٰهِ خَيْرٌ ۗ  لَوْ كَانُوْا يَعْلَمُوْنَ ࣖ','Sesungguhnya kalau mereka beriman dan bertakwa, (niscaya mereka akan mendapat pahala), dan sesungguhnya pahala dari sisi Allah adalah lebih baik, kalau mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,104,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقُوْلُوْا رَاعِنَا وَقُوْلُوا انْظُرْنَا وَاسْمَعُوْا  وَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ','Hai orang-orang yang beriman, janganlah kamu katakan (kepada Muhammad): "Raa´ina", tetapi katakanlah: "Unzhurna", dan "dengarlah". Dan bagi orang-orang yang kafir siksaan yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,105,'مَا يَوَدُّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَلَا الْمُشْرِكِيْنَ اَنْ يُّنَزَّلَ عَلَيْكُمْ مِّنْ خَيْرٍ مِّنْ رَّبِّكُمْ ۗ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ','Orang-orang kafir dari Ahli Kitab dan orang-orang musyrik tiada menginginkan diturunkannya sesuatu kebaikan kepadamu dari Tuhanmu. Dan Allah menentukan siapa yang dikehendaki-Nya (untuk diberi) rahmat-Nya (kenabian); dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,106,'۞ مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِّنْهَآ اَوْ مِثْلِهَا ۗ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Ayat mana saja yang Kami nasakhkan, atau Kami jadikan (manusia) lupa kepadanya, Kami datangkan yang lebih baik daripadanya atau yang sebanding dengannya. Tidakkah kamu mengetahui bahwa sesungguhnya Allah Maha Kuasa atas segala sesuatu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,107,'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ','Tiadakah kamu mengetahui bahwa kerajaan langit dan bumi adalah kepunyaan Allah? Dan tiada bagimu selain Allah seorang pelindung maupun seorang penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,108,'اَمْ تُرِيْدُوْنَ اَنْ تَسْـَٔلُوْا رَسُوْلَكُمْ كَمَا سُىِٕلَ مُوْسٰى مِنْ قَبْلُ ۗوَمَنْ يَّتَبَدَّلِ الْكُفْرَ بِالْاِيْمَانِ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ','Apakah kamu menghendaki untuk meminta kepada Rasul kamu seperti Bani Israil meminta kepada Musa pada jaman dahulu? Dan barangsiapa yang menukar iman dengan kekafiran, maka sungguh orang itu telah sesat dari jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,109,'وَدَّ كَثِيْرٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يَرُدُّوْنَكُمْ مِّنْۢ بَعْدِ اِيْمَانِكُمْ كُفَّارًاۚ حَسَدًا مِّنْ عِنْدِ اَنْفُسِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۚ فَاعْفُوْا وَاصْفَحُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Sebahagian besar Ahli Kitab menginginkan agar mereka dapat mengembalikan kamu kepada kekafiran setelah kamu beriman, karena dengki yang (timbul) dari diri mereka sendiri, setelah nyata bagi mereka kebenaran. Maka maafkanlah dan biarkanlah mereka, sampai Allah mendatangkan perintah-Nya. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,110,' وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ ۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ','Dan dirikanlah shalat dan tunaikanlah zakat. Dan kebaikan apa saja yang kamu usahakan bagi dirimu, tentu kamu akan mendapat pahala nya pada sisi Allah. Sesungguhnya Alah Maha Melihat apa-apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,111,'وَقَالُوْا لَنْ يَّدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوْدًا اَوْ نَصٰرٰى ۗ تِلْكَ اَمَانِيُّهُمْ ۗ قُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ','Dan mereka (Yahudi dan Nasrani) berkata: "Sekali-kali tidak akan masuk surga kecuali orang-orang (yang beragama) Yahudi atau Nasrani". Demikian itu (hanya) angan-angan mereka yang kosong belaka. Katakanlah: "Tunjukkanlah bukti kebenaranmu jika kamu adalah orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,112,'بَلٰى مَنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهٗٓ اَجْرُهٗ عِنْدَ رَبِّهٖۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ࣖ','(Tidak demikian) bahkan barangsiapa yang menyerahkan diri kepada Allah, sedang ia berbuat kebajikan, maka baginya pahala pada sisi Tuhannya dan tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,113,'وَقَالَتِ الْيَهُوْدُ لَيْسَتِ النَّصٰرٰى عَلٰى شَيْءٍۖ وَّقَالَتِ النَّصٰرٰى لَيْسَتِ الْيَهُوْدُ عَلٰى شَيْءٍۙ وَّهُمْ يَتْلُوْنَ الْكِتٰبَۗ  كَذٰلِكَ قَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ مِثْلَ قَوْلِهِمْ ۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ','Dan orang-orang Yahudi berkata: "Orang-orang Nasrani itu tidak mempunyai suatu pegangan", dan orang-orang Nasrani berkata: "Orang-orang Yahudi tidak mempunyai sesuatu pegangan," padahal mereka (sama-sama) membaca Al Kitab. Demikian pula orang-orang yang tidak mengetahui, mengatakan seperti ucapan mereka itu. Maka Allah akan mengadili diantara mereka pada hari Kiamat, tentang apa-apa yang mereka berselisih padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,114,'وَمَنْ اَظْلَمُ مِمَّنْ مَّنَعَ مَسٰجِدَ اللّٰهِ اَنْ يُّذْكَرَ فِيْهَا اسْمُهٗ وَسَعٰى فِيْ خَرَابِهَاۗ اُولٰۤىِٕكَ مَا كَانَ لَهُمْ اَنْ يَّدْخُلُوْهَآ اِلَّا خَاۤىِٕفِيْنَ ەۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ وَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ','Dan siapakah yang lebih aniaya daripada orang yang menghalanghalangi menyebut nama Allah dalam mesjid-mesjid-Nya, dan berusaha untuk merobohkannya? Mereka itu tidak sepatutnya masuk ke dalamnya (mesjid Allah), kecuali dengan rasa takut (kepada Allah). Mereka di dunia mendapat kehinaan dan di akhirat mendapat siksa yang berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,115,'وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ  فَاَيْنَمَا تُوَلُّوْا فَثَمَّ وَجْهُ اللّٰهِ ۗ اِنَّ اللّٰهَ وَاسِعٌ عَلِيْمٌ ','Dan kepunyaan Allah-lah timur dan barat, maka kemanapun kamu menghadap di situlah wajah Allah. Sesungguhnya Allah Maha Luas (rahmat-Nya) lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,116,'وَقَالُوا اتَّخَذَ اللّٰهُ وَلَدًا ۙسُبْحٰنَهٗ ۗ بَلْ لَّهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  كُلٌّ لَّهٗ قَانِتُوْنَ ','Mereka (orang-orang kafir) berkata: "Allah mempunyai anak". Maha Suci Allah, bahkan apa yang ada di langit dan di bumi adalah kepunyaan Allah; semua tunduk kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,117,'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ','Allah Pencipta langit dan bumi, dan bila Dia berkehendak (untuk menciptakan) sesuatu, maka (cukuplah) Dia hanya mengatakan kepadanya: "Jadilah!" Lalu jadilah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,118,'وَقَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْتِيْنَآ اٰيَةٌ  ۗ كَذٰلِكَ قَالَ الَّذِيْنَ مِنْ قَبْلِهِمْ مِّثْلَ قَوْلِهِمْ ۗ تَشَابَهَتْ قُلُوْبُهُمْ ۗ قَدْ بَيَّنَّا الْاٰيٰتِ لِقَوْمٍ يُّوْقِنُوْنَ','Dan orang-orang yang tidak mengetahui berkata: "Mengapa Allah tidak (langsung) berbicara dengan kami atau datang tanda-tanda kekuasaan-Nya kepada kami?" Demikian pula orang-orang yang sebelum mereka telah mengatakan seperti ucapan mereka itu; hati mereka serupa. Sesungguhnya Kami telah menjelaskan tanda-tanda kekuasaan Kami kepada kaum yang yakin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,119,'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًاۙ وَّلَا تُسْـَٔلُ عَنْ اَصْحٰبِ الْجَحِيْمِ','Sesungguhnya Kami telah mengutusmu (Muhammad) dengan kebenaran; sebagai pembawa berita gembira dan pemberi peringatan, dan kamu tidak akan diminta (pertanggungan jawab) tentang penghuni-penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,120,'وَلَنْ تَرْضٰى عَنْكَ الْيَهُوْدُ وَلَا النَّصٰرٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰى ۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَ الَّذِيْ جَاۤءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ','Orang-orang Yahudi dan Nasrani tidak akan senang kepada kamu hingga kamu mengikuti agama mereka. Katakanlah: "Sesungguhnya petunjuk Allah itulah petunjuk (yang benar)". Dan sesungguhnya jika kamu mengikuti kemauan mereka setelah pengetahuan datang kepadamu, maka Allah tidak lagi menjadi pelindung dan penolong bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,121,'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَتْلُوْنَهٗ حَقَّ تِلَاوَتِهٖۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗ وَمَنْ يَّكْفُرْ بِهٖ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ࣖ','Orang-orang yang telah Kami berikan Al Kitab kepadanya, mereka membacanya dengan bacaan yang sebenarnya, mereka itu beriman kepadanya. Dan barangsiapa yang ingkar kepadanya, maka mereka itulah orang-orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,122,'يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ ','Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Ku-anugerahkan kepadamu dan Aku telah melabihkan kamu atas segala umat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,123,'وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا عَدْلٌ وَّلَا تَنْفَعُهَا شَفَاعَةٌ وَّلَا هُمْ يُنْصَرُوْنَ ','Dan takutlah kamu kepada suatu hari di waktu seseorang tidak dapat menggantikan seseorang lain sedikitpun dan tidak akan diterima suatu tebusan daripadanya dan tidak akan memberi manfaat sesuatu syafa´at kepadanya dan tidak (pula) mereka akan ditolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,124,'۞ وَاِذِ ابْتَلٰٓى اِبْرٰهٖمَ رَبُّهٗ بِكَلِمٰتٍ فَاَتَمَّهُنَّ ۗ قَالَ اِنِّيْ جَاعِلُكَ لِلنَّاسِ اِمَامًا ۗ قَالَ وَمِنْ ذُرِّيَّتِيْ ۗ قَالَ لَا يَنَالُ عَهْدِى الظّٰلِمِيْنَ','Dan (ingatlah), ketika Ibrahim diuji Tuhannya dengan beberapa kalimat (perintah dan larangan), lalu Ibrahim menunaikannya. Allah berfirman: "Sesungguhnya Aku akan menjadikanmu imam bagi seluruh manusia". Ibrahim berkata: "(Dan saya mohon juga) dari keturunanku". Allah berfirman: "Janji-Ku (ini) tidak mengenai orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,125,'وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِّلنَّاسِ وَاَمْنًاۗ وَاتَّخِذُوْا مِنْ مَّقَامِ اِبْرٰهٖمَ مُصَلًّىۗ  وَعَهِدْنَآ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْعٰكِفِيْنَ وَالرُّكَّعِ السُّجُوْدِ ','Dan (ingatlah), ketika Kami menjadikan rumah itu (Baitullah) tempat berkumpul bagi manusia dan tempat yang aman. Dan jadikanlah sebahagian maqam Ibrahim tempat shalat. Dan telah Kami perintahkan kepada Ibrahim dan Ismail: "Bersihkanlah rumah-Ku untuk orang-orang yang thawaf, yang i´tikaf, yang ruku´ dan yang sujud".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,126,'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اجْعَلْ هٰذَا بَلَدًا اٰمِنًا وَّارْزُقْ اَهْلَهٗ مِنَ الثَّمَرٰتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهٗ قَلِيْلًا ثُمَّ اَضْطَرُّهٗٓ اِلٰى عَذَابِ النَّارِ ۗ وَبِئْسَ الْمَصِيْرُ ','Dan (ingatlah), ketika Ibrahim berdoa: "Ya Tuhanku, jadikanlah negeri ini, negeri yang aman sentosa, dan berikanlah rezeki dari buah-buahan kepada penduduknya yang beriman diantara mereka kepada Allah dan hari kemudian. Allah berfirman: "Dan kepada orang yang kafirpun Aku beri kesenangan sementara, kemudian Aku paksa ia menjalani siksa neraka dan itulah seburuk-buruk tempat kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,127,'وَاِذْ يَرْفَعُ اِبْرٰهٖمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰعِيْلُۗ رَبَّنَا تَقَبَّلْ مِنَّا ۗ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ ','Dan (ingatlah), ketika Ibrahim meninggikan (membina) dasar-dasar Baitullah bersama Ismail (seraya berdoa): "Ya Tuhan kami terimalah daripada kami (amalan kami), sesungguhnya Engkaulah Yang Maha Mendengar lagi Maha Mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,128,'رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَآ اُمَّةً مُّسْلِمَةً لَّكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّحِيْمُ ','Ya Tuhan kami, jadikanlah kami berdua orang yang tunduk patuh kepada Engkau dan (jadikanlah) diantara anak cucu kami umat yang tunduk patuh kepada Engkau dan tunjukkanlah kepada kami cara-cara dan tempat-tempat ibadat haji kami, dan terimalah taubat kami. Sesungguhnya Engkaulah Yang Maha Penerima taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,129,'رَبَّنَا وَابْعَثْ فِيْهِمْ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِكَ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُزَكِّيْهِمْ ۗ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ࣖ','Ya Tuhan kami, utuslah untuk mereka sesorang Rasul dari kalangan mereka, yang akan membacakan kepada mereka ayat-ayat Engkau, dan mengajarkan kepada mereka Al Kitab (Al Quran) dan Al-Hikmah (As-Sunnah) serta mensucikan mereka. Sesungguhnya Engkaulah yang Maha Kuasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,130,'وَمَنْ يَّرْغَبُ عَنْ مِّلَّةِ اِبْرٰهٖمَ اِلَّا مَنْ سَفِهَ نَفْسَهٗ ۗوَلَقَدِ اصْطَفَيْنٰهُ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ','Dan tidak ada yang benci kepada agama Ibrahim, melainkan orang yang memperbodoh dirinya sendiri, dan sungguh Kami telah memilihnya di dunia dan sesungguhnya dia di akhirat benar-benar termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,131,'اِذْ قَالَ لَهٗ رَبُّهٗٓ اَسْلِمْۙ  قَالَ اَسْلَمْتُ لِرَبِّ الْعٰلَمِيْنَ','Ketika Tuhannya berfirman kepadanya: "Tunduk patuhlah!" Ibrahim menjawab: "Aku tunduk patuh kepada Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,132,'وَوَصّٰى بِهَآ اِبْرٰهٖمُ بَنِيْهِ وَيَعْقُوْبُۗ يٰبَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدِّيْنَ فَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ۗ','Dan Ibrahim telah mewasiatkan ucapan itu kepada anak-anaknya, demikian pula Ya´qub. (Ibrahim berkata): "Hai anak-anakku! Sesungguhnya Allah telah memilih agama ini bagimu, maka janganlah kamu mati kecuali dalam memeluk agama Islam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,133,'اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ حَضَرَ يَعْقُوْبَ الْمَوْتُۙ اِذْ قَالَ لِبَنِيْهِ مَا تَعْبُدُوْنَ مِنْۢ بَعْدِيْۗ قَالُوْا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَاۤىِٕكَ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ اِلٰهًا وَّاحِدًاۚ وَنَحْنُ لَهٗ مُسْلِمُوْنَ ','Adakah kamu hadir ketika Ya´qub kedatangan (tanda-tanda) maut, ketika ia berkata kepada anak-anaknya: "Apa yang kamu sembah sepeninggalku?" Mereka menjawab: "Kami akan menyembah Tuhanmu dan Tuhan nenek moyangmu, Ibrahim, Ismail dan Ishaq, (yaitu) Tuhan Yang Maha Esa dan kami hanya tunduk patuh kepada-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,134,'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ','Itu adalah umat yang lalu; baginya apa yang telah diusahakannya dan bagimu apa yang sudah kamu usahakan, dan kamu tidak akan diminta pertanggungan jawab tentang apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,135,'وَقَالُوْا كُوْنُوْا هُوْدًا اَوْ نَصٰرٰى تَهْتَدُوْا ۗ قُلْ بَلْ مِلَّةَ اِبْرٰهٖمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ','Dan mereka berkata: "Hendaklah kamu menjadi penganut agama Yahudi atau Nasrani, niscaya kamu mendapat petunjuk". Katakanlah: "Tidak, melainkan (kami mengikuti) agama Ibrahim yang lurus. Dan bukanlah dia (Ibrahim) dari golongan orang musyrik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,136,'قُوْلُوْٓا اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَمَآ اُوْتِيَ النَّبِيُّوْنَ مِنْ رَّبِّهِمْۚ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ ','Katakanlah (hai orang-orang mukmin): "Kami beriman kepada Allah dan apa yang diturunkan kepada kami, dan apa yang diturunkan kepada Ibrahim, Isma´il, Ishaq, Ya´qub dan anak cucunya, dan apa yang diberikan kepada Musa dan Isa serta apa yang diberikan kepada nabi-nabi dari Tuhannya. Kami tidak membeda-bedakan seorangpun diantara mereka dan kami hanya tunduk patuh kepada-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,137,'فَاِنْ اٰمَنُوْا بِمِثْلِ مَآ اٰمَنْتُمْ بِهٖ فَقَدِ اهْتَدَوْا ۚوَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ فِيْ شِقَاقٍۚ فَسَيَكْفِيْكَهُمُ اللّٰهُ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ ۗ ','Maka jika mereka beriman kepada apa yang kamu telah beriman kepadanya, sungguh mereka telah mendapat petunjuk; dan jika mereka berpaling, sesungguhnya mereka berada dalam permusuhan (dengan kamu). Maka Allah akan memelihara kamu dari mereka. Dan Dialah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,138,'صِبْغَةَ اللّٰهِ ۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةً ۖ وَّنَحْنُ لَهٗ عٰبِدُوْنَ ','Shibghah Allah. Dan siapakah yang lebih baik shibghahnya dari pada Allah? Dan hanya kepada-Nya-lah kami menyembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,139,'قُلْ اَتُحَاۤجُّوْنَنَا فِى اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهٗ مُخْلِصُوْنَ ۙ','Katakanlah: "Apakah kamu memperdebatkan dengan kami tentang Allah, padahal Dia adalah Tuhan kami dan Tuhan kamu; bagi kami amalan kami, dan bagi kamu amalan kamu dan hanya kepada-Nya kami mengikhlaskan hati,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,140,'اَمْ تَقُوْلُوْنَ اِنَّ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطَ كَانُوْا هُوْدًا اَوْ نَصٰرٰى ۗ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُ ۗ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهٗ مِنَ اللّٰهِ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ','ataukah kamu (hai orang-orang Yahudi dan Nasrani) mengatakan bahwa Ibrahim, Isma´il, Ishaq, Ya´qub dan anak cucunya, adalah penganut agama Yahudi atau Nasrani?" Katakanlah: "Apakah kamu lebih mengetahui ataukah Allah, dan siapakah yang lebih zalim dari pada orang yang menyembunyikan syahadah dari Allah yang ada padanya?" Dan Allah sekali-kali tiada lengah dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,1,2,141,'تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ ࣖ   ۔','Itu adalah umat yang telah lalu; baginya apa yang diusahakannya dan bagimu apa yang kamu usahakan; dan kamu tidak akan diminta pertanggungan jawab tentang apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,142,'۞ سَيَقُوْلُ السُّفَهَاۤءُ مِنَ النَّاسِ مَا وَلّٰىهُمْ عَنْ قِبْلَتِهِمُ الَّتِيْ كَانُوْا عَلَيْهَا ۗ قُلْ لِّلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۗ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Orang-orang yang kurang akalnya diantara manusia akan berkata: "Apakah yang memalingkan mereka (umat Islam) dari kiblatnya (Baitul Maqdis) yang dahulu mereka telah berkiblat kepadanya?" Katakanlah: "Kepunyaan Allah-lah timur dan barat; Dia memberi petunjuk kepada siapa yang dikehendaki-Nya ke jalan yang lurus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,143,'وَكَذٰلِكَ جَعَلْنٰكُمْ اُمَّةً وَّسَطًا لِّتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِ وَيَكُوْنَ الرَّسُوْلُ عَلَيْكُمْ شَهِيْدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِيْ كُنْتَ عَلَيْهَآ اِلَّا لِنَعْلَمَ مَنْ يَّتَّبِعُ الرَّسُوْلَ مِمَّنْ يَّنْقَلِبُ عَلٰى عَقِبَيْهِۗ  وَاِنْ كَانَتْ لَكَبِيْرَةً اِلَّا عَلَى الَّذِيْنَ هَدَى اللّٰهُ  ۗوَمَا كَانَ اللّٰهُ لِيُضِيْعَ اِيْمَانَكُمْ ۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ  ','Dan demikian (pula) Kami telah menjadikan kamu (umat Islam), umat yang adil dan pilihan agar kamu menjadi saksi atas (perbuatan) manusia dan agar Rasul (Muhammad) menjadi saksi atas (perbuatan) kamu. Dan Kami tidak menetapkan kiblat yang menjadi kiblatmu (sekarang) melainkan agar Kami mengetahui (supaya nyata) siapa yang mengikuti Rasul dan siapa yang membelot. Dan sungguh (pemindahan kiblat) itu terasa amat berat, kecuali bagi orang-orang yang telah diberi petunjuk oleh Allah; dan Allah tidak akan menyia-nyiakan imanmu. Sesungguhnya Allah Maha Pengasih lagi Maha Penyayang kepada manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,144,'قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِى السَّمَاۤءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰىهَا ۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۗ وَاِنَّ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ','Sungguh Kami (sering) melihat mukamu menengadah ke langit, maka sungguh Kami akan memalingkan kamu ke kiblat yang kamu sukai. Palingkanlah mukamu ke arah Masjidil Haram. Dan dimana saja kamu berada, palingkanlah mukamu ke arahnya. Dan sesungguhnya orang-orang (Yahudi dan Nasrani) yang diberi Al Kitab (Taurat dan Injil) memang mengetahui, bahwa berpaling ke Masjidil Haram itu adalah benar dari Tuhannya; dan Allah sekali-kali tidak lengah dari apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,145,'وَلَىِٕنْ اَتَيْتَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ بِكُلِّ اٰيَةٍ مَّا تَبِعُوْا قِبْلَتَكَ ۚ وَمَآ اَنْتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ مِّنْۢ بَعْدِ مَاجَاۤءَكَ مِنَ الْعِلْمِ ۙ اِنَّكَ اِذًا لَّمِنَ الظّٰلِمِيْنَ ۘ ','Dan sesungguhnya jika kamu mendatangkan kepada orang-orang (Yahudi dan Nasrani) yang diberi Al Kitab (Taurat dan Injil), semua ayat (keterangan), mereka tidak akan mengikuti kiblatmu, dan kamupun tidak akan mengikuti kiblat mereka, dan sebahagian merekapun tidak akan mengikuti kiblat sebahagian yang lain. Dan sesungguhnya jika kamu mengikuti keinginan mereka setelah datang ilmu kepadamu, sesungguhnya kamu -- kalau begitu -- termasuk golongan orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,146,'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْ ۗ وَاِنَّ فَرِيْقًا مِّنْهُمْ لَيَكْتُمُوْنَ الْحَقَّ وَهُمْ يَعْلَمُوْنَ','Orang-orang (Yahudi dan Nasrani) yang telah Kami beri Al Kitab (Taurat dan Injil) mengenal Muhammad seperti mereka mengenal anak-anaknya sendiri. Dan sesungguhnya sebahagian diantara mereka menyembunyikan kebenaran, padahal mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,147,'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ ࣖ','Kebenaran itu adalah dari Tuhanmu, sebab itu jangan sekali-kali kamu termasuk orang-orang yang ragu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,148,'وَلِكُلٍّ وِّجْهَةٌ هُوَ مُوَلِّيْهَا فَاسْتَبِقُوا الْخَيْرٰتِۗ اَيْنَ مَا تَكُوْنُوْا يَأْتِ بِكُمُ اللّٰهُ جَمِيْعًا ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan bagi tiap-tiap umat ada kiblatnya (sendiri) yang ia menghadap kepadanya. Maka berlomba-lombalah (dalam membuat) kebaikan. Di mana saja kamu berada pasti Allah akan mengumpulkan kamu sekalian (pada hari kiamat). Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,149,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَاِنَّهٗ لَلْحَقُّ مِنْ رَّبِّكَ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ','Dan dari mana saja kamu keluar (datang), maka palingkanlah wajahmu ke arah Masjidil Haram, sesungguhnya ketentuan itu benar-benar sesuatu yang hak dari Tuhanmu. Dan Allah sekali-kali tidak lengah dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,150,'وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۙ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِيْ وَلِاُتِمَّ نِعْمَتِيْ عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُوْنَۙ','Dan dari mana saja kamu (keluar), maka palingkanlah wajahmu ke arah Masjidil Haram. Dan dimana saja kamu (sekalian) berada, maka palingkanlah wajahmu ke arahnya, agar tidak ada hujjah bagi manusia atas kamu, kecuali orang-orang yang zalim diantara mereka. Maka janganlah kamu takut kepada mereka dan takutlah kepada-Ku (saja). Dan agar Ku-sempurnakan nikmat-Ku atasmu, dan supaya kamu mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,151,'كَمَآ اَرْسَلْنَا فِيْكُمْ رَسُوْلًا مِّنْكُمْ يَتْلُوْا عَلَيْكُمْ اٰيٰتِنَا وَيُزَكِّيْكُمْ وَيُعَلِّمُكُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَۗ','Sebagaimana (Kami telah menyempurnakan nikmat Kami kepadamu) Kami telah mengutus kepadamu Rasul diantara kamu yang membacakan ayat-ayat Kami kepada kamu dan mensucikan kamu dan mengajarkan kepadamu Al Kitab dan Al-Hikmah, serta mengajarkan kepada kamu apa yang belum kamu ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,152,'فَاذْكُرُوْنِيْٓ اَذْكُرْكُمْ وَاشْكُرُوْا لِيْ وَلَا تَكْفُرُوْنِ ࣖ ','Karena itu, ingatlah kamu kepada-Ku niscaya Aku ingat (pula) kepadamu, dan bersyukurlah kepada-Ku, dan janganlah kamu mengingkari (nikmat)-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,153,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَ','Hai orang-orang yang beriman, jadikanlah sabar dan shalat sebagai penolongmu, sesungguhnya Allah beserta orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,154,'وَلَا تَقُوْلُوْا لِمَنْ يُّقْتَلُ فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتٌ ۗ بَلْ اَحْيَاۤءٌ وَّلٰكِنْ لَّا تَشْعُرُوْنَ','Dan janganlah kamu mengatakan terhadap orang-orang yang gugur di jalan Allah, (bahwa mereka itu) mati; bahkan (sebenarnya) mereka itu hidup, tetapi kamu tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,155,'وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِّنَ الْخَوْفِ وَالْجُوْعِ وَنَقْصٍ مِّنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرٰتِۗ وَبَشِّرِ الصّٰبِرِيْنَ','Dan sungguh akan Kami berikan cobaan kepadamu, dengan sedikit ketakutan, kelaparan, kekurangan harta, jiwa dan buah-buahan. Dan berikanlah berita gembira kepada orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,156,'اَلَّذِيْنَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۗ قَالُوْٓا اِنَّا لِلّٰهِ وَاِنَّآ اِلَيْهِ رٰجِعُوْنَۗ','(yaitu) orang-orang yang apabila ditimpa musibah, mereka mengucapkan: "Inna lillaahi wa innaa ilaihi raaji´uun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,157,'اُولٰۤىِٕكَ عَلَيْهِمْ صَلَوٰتٌ مِّنْ رَّبِّهِمْ وَرَحْمَةٌ  ۗوَاُولٰۤىِٕكَ هُمُ الْمُهْتَدُوْنَ','Mereka itulah yang mendapat keberkatan yang sempurna dan rahmat dari Tuhan mereka dan mereka itulah orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,158,'۞ اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَاۤىِٕرِ اللّٰهِ ۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَّطَّوَّفَ بِهِمَا ۗ وَمَنْ تَطَوَّعَ خَيْرًاۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَلِيْمٌ ','Sesungguhnya Shafaa dan Marwa adalah sebahagian dari syi´ar Allah. Maka barangsiapa yang beribadah haji ke Baitullah atau ber´umrah, maka tidak ada dosa baginya mengerjakan sa´i antara keduanya. Dan barangsiapa yang mengerjakan suatu kebajikan dengan kerelaan hati, maka sesungguhnya Allah Maha Mensyukuri kebaikan lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,159,'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلْنَا مِنَ الْبَيِّنٰتِ وَالْهُدٰى مِنْۢ بَعْدِ مَا بَيَّنّٰهُ لِلنَّاسِ فِى الْكِتٰبِۙ اُولٰۤىِٕكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللّٰعِنُوْنَۙ','Sesungguhnya orang-orang yang menyembunyikan apa yang telah Kami turunkan berupa keterangan-keterangan (yang jelas) dan petunjuk, setelah Kami menerangkannya kepada manusia dalam Al Kitab, mereka itu dilaknati Allah dan dilaknati (pula) oleh semua (mahluk) yang dapat melaknati,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,160,'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَبَيَّنُوْا فَاُولٰۤىِٕكَ اَتُوْبُ عَلَيْهِمْ ۚ وَاَنَا التَّوَّابُ الرَّحِيْمُ ','kecuali mereka yang telah taubat dan mengadakan perbaikan dan menerangkan (kebenaran), maka terhadap mereka itulah Aku menerima taubatnya dan Akulah Yang Maha Menerima taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,161,'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ اُولٰۤىِٕكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ','Sesungguhnya orang-orang kafir dan mereka mati dalam keadaan kafir, mereka itu mendapat laknat Allah, para Malaikat dan manusia seluruhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,162,'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَ','Mereka kekal di dalam laknat itu; tidak akan diringankan siksa dari mereka dan tidak (pula) mereka diberi tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,163,'وَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ  لَآاِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّحِيْمُ ࣖ','Dan Tuhanmu adalah Tuhan Yang Maha Esa; tidak ada Tuhan melainkan Dia Yang Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,164,'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِيْ تَجْرِيْ فِى الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ مَّاۤءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍ ۖ وَّتَصْرِيْفِ الرِّيٰحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاۤءِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ','Sesungguhnya dalam penciptaan langit dan bumi, silih bergantinya malam dan siang, bahtera yang berlayar di laut membawa apa yang berguna bagi manusia, dan apa yang Allah turunkan dari langit berupa air, lalu dengan air itu Dia hidupkan bumi sesudah mati (kering)-nya dan Dia sebarkan di bumi itu segala jenis hewan, dan pengisaran angin dan awan yang dikendalikan antara langit dan bumi; sungguh (terdapat) tanda-tanda (keesaan dan kebesaran Allah) bagi kaum yang memikirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,165,'وَمِنَ النَّاسِ مَنْ يَّتَّخِذُ مِنْ دُوْنِ اللّٰهِ اَنْدَادًا يُّحِبُّوْنَهُمْ كَحُبِّ اللّٰهِ ۗ وَالَّذِيْنَ اٰمَنُوْٓا اَشَدُّ حُبًّا لِّلّٰهِ ۙوَلَوْ يَرَى الَّذِيْنَ ظَلَمُوْٓا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَمِيْعًا ۙوَّاَنَّ اللّٰهَ شَدِيْدُ الْعَذَابِ ','Dan diantara manusia ada orang-orang yang menyembah tandingan-tandingan selain Allah; mereka mencintainya sebagaimana mereka mencintai Allah. Adapun orang-orang yang beriman amat sangat cintanya kepada Allah. Dan jika seandainya orang-orang yang berbuat zalim itu mengetahui ketika mereka melihat siksa (pada hari kiamat), bahwa kekuatan itu kepunyaan Allah semuanya, dan bahwa Allah amat berat siksaan-Nya (niscaya mereka menyesal).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,166,'اِذْ تَبَرَّاَ الَّذِيْنَ اتُّبِعُوْا مِنَ الَّذِيْنَ اتَّبَعُوْا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ','(Yaitu) ketika orang-orang yang diikuti itu berlepas diri dari orang-orang yang mengikutinya, dan mereka melihat siksa; dan (ketika) segala hubungan antara mereka terputus sama sekali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,167,'وَقَالَ الَّذِيْنَ اتَّبَعُوْا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ ۗ كَمَا تَبَرَّءُوْا مِنَّا ۗ كَذٰلِكَ يُرِيْهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرٰتٍ عَلَيْهِمْ ۗ وَمَا هُمْ بِخَارِجِيْنَ مِنَ النَّارِ ࣖ','Dan berkatalah orang-orang yang mengikuti: "Seandainya kami dapat kembali (ke dunia), pasti kami akan berlepas diri dari mereka, sebagaimana mereka berlepas diri dari kami". Demikianlah Allah memperlihatkan kepada mereka amal perbuatannya menjadi sesalan bagi mereka; dan sekali-kali mereka tidak akan keluar dari api neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,168,'يٰٓاَيُّهَا النَّاسُ كُلُوْا مِمَّا فِى الْاَرْضِ حَلٰلًا طَيِّبًا ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ','Hai sekalian manusia, makanlah yang halal lagi baik dari apa yang terdapat di bumi, dan janganlah kamu mengikuti langkah-langkah syaitan; karena sesungguhnya syaitan itu adalah musuh yang nyata bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,169,'اِنَّمَا يَأْمُرُكُمْ بِالسُّوْۤءِ وَالْفَحْشَاۤءِ وَاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ','Sesungguhnya syaitan itu hanya menyuruh kamu berbuat jahat dan keji, dan mengatakan terhadap Allah apa yang tidak kamu ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,170,'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَآ اَلْفَيْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْقِلُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ','Dan apabila dikatakan kepada mereka: "Ikutilah apa yang telah diturunkan Allah," mereka menjawab: "(Tidak), tetapi kami hanya mengikuti apa yang telah kami dapati dari (perbuatan) nenek moyang kami". "(Apakah mereka akan mengikuti juga), walaupun nenek moyang mereka itu tidak mengetahui suatu apapun, dan tidak mendapat petunjuk?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,171,'وَمَثَلُ الَّذِيْنَ كَفَرُوْا كَمَثَلِ الَّذِيْ يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَاۤءً وَّنِدَاۤءً ۗ صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُوْنَ ','Dan perumpamaan (orang-orang yang menyeru) orang-orang kafir adalah seperti penggembala yang memanggil binatang yang tidak mendengar selain panggilan dan seruan saja. Mereka tuli, bisu dan buta, maka (oleh sebab itu) mereka tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,172,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ وَاشْكُرُوْا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ','Hai orang-orang yang beriman, makanlah di antara rezeki yang baik-baik yang Kami berikan kepadamu dan bersyukurlah kepada Allah, jika benar-benar kepada-Nya kamu menyembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,173,'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ بِهٖ لِغَيْرِ اللّٰهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','Sesungguhnya Allah hanya mengharamkan bagimu bangkai, darah, daging babi, dan binatang yang (ketika disembelih) disebut (nama) selain Allah. Tetapi barangsiapa dalam keadaan terpaksa (memakannya) sedang dia tidak menginginkannya dan tidak (pula) melampaui batas, maka tidak ada dosa baginya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,174,'اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلَ اللّٰهُ مِنَ الْكِتٰبِ وَيَشْتَرُوْنَ بِهٖ ثَمَنًا قَلِيْلًاۙ اُولٰۤىِٕكَ مَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۚوَلَهُمْ عَذَابٌ اَلِيْمٌ ','Sesungguhnya orang-orang yang menyembunyikan apa yang telah diturunkan Allah, yaitu Al Kitab dan menjualnya dengan harga yang sedikit (murah), mereka itu sebenarnya tidak memakan (tidak menelan) ke dalam perutnya melainkan api, dan Allah tidak akan berbicara kepada mereka pada hari kiamat dan tidak mensucikan mereka dan bagi mereka siksa yang amat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,175,'اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَآ اَصْبَرَهُمْ عَلَى النَّارِ ','Mereka itulah orang-orang yang membeli kesesatan dengan petunjuk dan siksa dengan ampunan. Maka alangkah beraninya mereka menentang api neraka!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,176,'ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتٰبَ بِالْحَقِّ ۗ وَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِى الْكِتٰبِ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ࣖ','Yang demikian itu adalah karena Allah telah menurunkan Al Kitab dengan membawa kebenaran; dan sesungguhnya orang-orang yang berselisih tentang (kebenaran) Al Kitab itu, benar-benar dalam penyimpangan yang jauh (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,177,'۞ لَيْسَ الْبِرَّاَنْ تُوَلُّوْا وُجُوْهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰۤىِٕكَةِ وَالْكِتٰبِ وَالنَّبِيّٖنَ ۚ وَاٰتَى الْمَالَ عَلٰى حُبِّهٖ ذَوِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنَ وَابْنَ السَّبِيْلِۙ وَالسَّاۤىِٕلِيْنَ وَفىِ الرِّقَابِۚ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ ۚ وَالْمُوْفُوْنَ بِعَهْدِهِمْ اِذَا عَاهَدُوْا ۚ وَالصّٰبِرِيْنَ فِى الْبَأْسَاۤءِ وَالضَّرَّاۤءِ وَحِيْنَ الْبَأْسِۗ اُولٰۤىِٕكَ الَّذِيْنَ صَدَقُوْا ۗوَاُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ ','Bukanlah menghadapkan wajahmu ke arah timur dan barat itu suatu kebajikan, akan tetapi sesungguhnya kebajikan itu ialah beriman kepada Allah, hari kemudian, malaikat-malaikat, kitab-kitab, nabi-nabi dan memberikan harta yang dicintainya kepada kerabatnya, anak-anak yatim, orang-orang miskin, musafir (yang memerlukan pertolongan) dan orang-orang yang meminta-minta; dan (memerdekakan) hamba sahaya, mendirikan shalat, dan menunaikan zakat; dan orang-orang yang menepati janjinya apabila ia berjanji, dan orang-orang yang sabar dalam kesempitan, penderitaan dan dalam peperangan. Mereka itulah orang-orang yang benar (imannya); dan mereka itulah orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,178,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِى الْقَتْلٰىۗ  اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۗ  فَمَنْ عُفِيَ لَهٗ مِنْ اَخِيْهِ شَيْءٌ فَاتِّبَاعٌ ۢبِالْمَعْرُوْفِ وَاَدَاۤءٌ اِلَيْهِ بِاِحْسَانٍ ۗ ذٰلِكَ تَخْفِيْفٌ مِّنْ رَّبِّكُمْ وَرَحْمَةٌ  ۗفَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ ','Hai orang-orang yang beriman, diwajibkan atas kamu qishaash berkenaan dengan orang-orang yang dibunuh; orang merdeka dengan orang merdeka, hamba dengan hamba, dan wanita dengan wanita. Maka barangsiapa yang mendapat suatu pemaafan dari saudaranya, hendaklah (yang memaafkan) mengikuti dengan cara yang baik, dan hendaklah (yang diberi maaf) membayar (diat) kepada yang memberi maaf dengan cara yang baik (pula). Yang demikian itu adalah suatu keringanan dari Tuhan kamu dan suatu rahmat. Barangsiapa yang melampaui batas sesudah itu, maka baginya siksa yang sangat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,179,'وَلَكُمْ فِى الْقِصَاصِ حَيٰوةٌ يّٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُوْنَ ','Dan dalam qishaash itu ada (jaminan kelangsungan) hidup bagimu, hai orang-orang yang berakal, supaya kamu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,180,'كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْرًا ۖ ۨالْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ بِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُتَّقِيْنَ ۗ  ','Diwajibkan atas kamu, apabila seorang di antara kamu kedatangan (tanda-tanda) maut, jika ia meninggalkan harta yang banyak, berwasiat untuk ibu-bapak dan karib kerabatnya secara ma´ruf, (ini adalah) kewajiban atas orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,181,'فَمَنْۢ بَدَّلَهٗ بَعْدَمَا سَمِعَهٗ فَاِنَّمَآ اِثْمُهٗ عَلَى الَّذِيْنَ يُبَدِّلُوْنَهٗ ۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ۗ  ','Maka barangsiapa yang mengubah wasiat itu, setelah ia mendengarnya, maka sesungguhnya dosanya adalah bagi orang-orang yang mengubahnya. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,182,'فَمَنْ خَافَ مِنْ مُّوْصٍ جَنَفًا اَوْ اِثْمًا فَاَصْلَحَ بَيْنَهُمْ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ ','(Akan tetapi) barangsiapa khawatir terhadap orang yang berwasiat itu, berlaku berat sebelah atau berbuat dosa, lalu ia mendamaikan antara mereka, maka tidaklah ada dosa baginya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,183,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ','Hai orang-orang yang beriman, diwajibkan atas kamu berpuasa sebagaimana diwajibkan atas orang-orang sebelum kamu agar kamu bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,184,'اَيَّامًا مَّعْدُوْدٰتٍۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ وَعَلَى الَّذِيْنَ يُطِيْقُوْنَهٗ فِدْيَةٌ طَعَامُ مِسْكِيْنٍۗ  فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهٗ ۗ وَاَنْ تَصُوْمُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','(yaitu) dalam beberapa hari yang tertentu. Maka barangsiapa diantara kamu ada yang sakit atau dalam perjalanan (lalu ia berbuka), maka (wajiblah baginya berpuasa) sebanyak hari yang ditinggalkan itu pada hari-hari yang lain. Dan wajib bagi orang-orang yang berat menjalankannya (jika mereka tidak berpuasa) membayar fidyah, (yaitu): memberi makan seorang miskin. Barangsiapa yang dengan kerelaan hati mengerjakan kebajikan, maka itulah yang lebih baik baginya. Dan berpuasa lebih baik bagimu jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,185,'شَهْرُ رَمَضَانَ الَّذِيْٓ اُنْزِلَ فِيْهِ الْقُرْاٰنُ هُدًى لِّلنَّاسِ وَبَيِّنٰتٍ مِّنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ ۗ وَمَنْ كَانَ مَرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ يُرِيْدُ اللّٰهُ بِكُمُ الْيُسْرَ وَلَا يُرِيْدُ بِكُمُ الْعُسْرَ ۖ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ وَلَعَلَّكُمْ تَشْكُرُوْنَ ','(Beberapa hari yang ditentukan itu ialah) bulan Ramadhan, bulan yang di dalamnya diturunkan (permulaan) Al Quran sebagai petunjuk bagi manusia dan penjelasan-penjelasan mengenai petunjuk itu dan pembeda (antara yang hak dan yang bathil). Karena itu, barangsiapa di antara kamu hadir (di negeri tempat tinggalnya) di bulan itu, maka hendaklah ia berpuasa pada bulan itu, dan barangsiapa sakit atau dalam perjalanan (lalu ia berbuka), maka (wajiblah baginya berpuasa), sebanyak hari yang ditinggalkannya itu, pada hari-hari yang lain. Allah menghendaki kemudahan bagimu, dan tidak menghendaki kesukaran bagimu. Dan hendaklah kamu mencukupkan bilangannya dan hendaklah kamu mengagungkan Allah atas petunjuk-Nya yang diberikan kepadamu, supaya kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,186,'وَاِذَا سَاَلَكَ عِبَادِيْ عَنِّيْ فَاِنِّيْ قَرِيْبٌ ۗ اُجِيْبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَجِيْبُوْا لِيْ وَلْيُؤْمِنُوْا بِيْ لَعَلَّهُمْ يَرْشُدُوْنَ ','Dan apabila hamba-hamba-Ku bertanya kepadamu tentang Aku, maka (jawablah), bahwasanya Aku adalah dekat. Aku mengabulkan permohonan orang yang berdoa apabila ia memohon kepada-Ku, maka hendaklah mereka itu memenuhi (segala perintah-Ku) dan hendaklah mereka beriman kepada-Ku, agar mereka selalu berada dalam kebenaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,187,'اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَاۤىِٕكُمْ ۗ هُنَّ لِبَاسٌ لَّكُمْ وَاَنْتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُوْنَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ ۚ فَالْـٰٔنَ بَاشِرُوْهُنَّ وَابْتَغُوْا مَا كَتَبَ اللّٰهُ لَكُمْ ۗ وَكُلُوْا وَاشْرَبُوْا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْاَبْيَضُ مِنَ الْخَيْطِ الْاَسْوَدِ مِنَ الْفَجْرِۖ  ثُمَّ اَتِمُّوا الصِّيَامَ اِلَى الَّيْلِۚ وَلَا تُبَاشِرُوْهُنَّ وَاَنْتُمْ عَاكِفُوْنَۙ فِى الْمَسٰجِدِ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَقْرَبُوْهَاۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُوْنَ','Dihalalkan bagi kamu pada malam hari bulan puasa bercampur dengan isteri-isteri kamu; mereka adalah pakaian bagimu, dan kamupun adalah pakaian bagi mereka. Allah mengetahui bahwasanya kamu tidak dapat menahan nafsumu, karena itu Allah mengampuni kamu dan memberi maaf kepadamu. Maka sekarang campurilah mereka dan ikutilah apa yang telah ditetapkan Allah untukmu, dan makan minumlah hingga terang bagimu benang putih dari benang hitam, yaitu fajar. Kemudian sempurnakanlah puasa itu sampai (datang) malam, (tetapi) janganlah kamu campuri mereka itu, sedang kamu beri´tikaf dalam mesjid. Itulah larangan Allah, maka janganlah kamu mendekatinya. Demikianlah Allah menerangkan ayat-ayat-Nya kepada manusia, supaya mereka bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,188,'وَلَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوْا بِهَآ اِلَى الْحُكَّامِ لِتَأْكُلُوْا فَرِيْقًا مِّنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُوْنَ ࣖ','Dan janganlah sebahagian kamu memakan harta sebahagian yang lain di antara kamu dengan jalan yang bathil dan (janganlah) kamu membawa (urusan) harta itu kepada hakim, supaya kamu dapat memakan sebahagian daripada harta benda orang lain itu dengan (jalan berbuat) dosa, padahal kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,189,'۞ يَسـَٔلُوْنَكَ عَنِ الْاَهِلَّةِ ۗ قُلْ هِيَ مَوَاقِيْتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوْتَ مِنْ ظُهُوْرِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوْتَ مِنْ اَبْوَابِهَا ۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ ','Mereka bertanya kepadamu tentang bulan sabit. Katakanlah: "Bulan sabit itu adalah tanda-tanda waktu bagi manusia dan (bagi ibadat) haji; Dan bukanlah kebajikan memasuki rumah-rumah dari belakangnya, akan tetapi kebajikan itu ialah kebajikan orang yang bertakwa. Dan masuklah ke rumah-rumah itu dari pintu-pintunya; dan bertakwalah kepada Allah agar kamu beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,190,'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يُقَاتِلُوْنَكُمْ وَلَا تَعْتَدُوْا ۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ','Dan perangilah di jalan Allah orang-orang yang memerangi kamu, (tetapi) janganlah kamu melampaui batas, karena sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,191,'وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ وَاَخْرِجُوْهُمْ مِّنْ حَيْثُ اَخْرَجُوْكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوْهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقٰتِلُوْكُمْ فِيْهِۚ فَاِنْ قٰتَلُوْكُمْ فَاقْتُلُوْهُمْۗ  كَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ ','Dan bunuhlah mereka di mana saja kamu jumpai mereka, dan usirlah mereka dari tempat mereka telah mengusir kamu (Mekah); dan fitnah itu lebih besar bahayanya dari pembunuhan, dan janganlah kamu memerangi mereka di Masjidil Haram, kecuali jika mereka memerangi kamu di tempat itu. Jika mereka memerangi kamu (di tempat itu), maka bunuhlah mereka. Demikanlah balasan bagi orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,192,'فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Kemudian jika mereka berhenti (dari memusuhi kamu), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,193,'وَقٰتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ لِلّٰهِ ۗ فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظّٰلِمِيْنَ','Dan perangilah mereka itu, sehingga tidak ada fitnah lagi dan (sehingga) ketaatan itu hanya semata-mata untuk Allah. Jika mereka berhenti (dari memusuhi kamu), maka tidak ada permusuhan (lagi), kecuali terhadap orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,194,'اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمٰتُ قِصَاصٌۗ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوْا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْ ۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ','Bulan haram dengan bulan haram, dan pada sesuatu yang patut dihormati, berlaku hukum qishaash. Oleh sebab itu barangsiapa yang menyerang kamu, maka seranglah ia, seimbang dengan serangannya terhadapmu. Bertakwalah kepada Allah dan ketahuilah, bahwa Allah beserta orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,195,'وَاَنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلَا تُلْقُوْا بِاَيْدِيْكُمْ اِلَى التَّهْلُكَةِ ۛ وَاَحْسِنُوْا ۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ ','Dan belanjakanlah (harta bendamu) di jalan Allah, dan janganlah kamu menjatuhkan dirimu sendiri ke dalam kebinasaan, dan berbuat baiklah, karena sesungguhnya Allah menyukai orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,196,'وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِ ۗ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوْا رُءُوْسَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهٗ ۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ بِهٖٓ اَذًى مِّنْ رَّأْسِهٖ فَفِدْيَةٌ مِّنْ صِيَامٍ اَوْ صَدَقَةٍ اَوْ نُسُكٍ ۚ فَاِذَآ اَمِنْتُمْ ۗ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ اِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ فِى الْحَجِّ وَسَبْعَةٍ اِذَا رَجَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ  ۗذٰلِكَ لِمَنْ لَّمْ يَكُنْ اَهْلُهٗ حَاضِرِى الْمَسْجِدِ الْحَرَامِ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ ࣖ','Dan sempurnakanlah ibadah haji dan ´umrah karena Allah. Jika kamu terkepung (terhalang oleh musuh atau karena sakit), maka (sembelihlah) korban yang mudah didapat, dan jangan kamu mencukur kepalamu, sebelum korban sampai di tempat penyembelihannya. Jika ada di antaramu yang sakit atau ada gangguan di kepalanya (lalu ia bercukur), maka wajiblah atasnya berfid-yah, yaitu: berpuasa atau bersedekah atau berkorban. Apabila kamu telah (merasa) aman, maka bagi siapa yang ingin mengerjakan ´umrah sebelum haji (di dalam bulan haji), (wajiblah ia menyembelih) korban yang mudah didapat. Tetapi jika ia tidak menemukan (binatang korban atau tidak mampu), maka wajib berpuasa tiga hari dalam masa haji dan tujuh hari (lagi) apabila kamu telah pulang kembali. Itulah sepuluh (hari) yang sempurna. Demikian itu (kewajiban membayar fidyah) bagi orang-orang yang keluarganya tidak berada (di sekitar) Masjidil Haram (orang-orang yang bukan penduduk kota Mekah). Dan bertakwalah kepada Allah dan ketahuilah bahwa Allah sangat keras siksaan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,197,'اَلْحَجُّ اَشْهُرٌ مَّعْلُوْمٰتٌ ۚ فَمَنْ فَرَضَ فِيْهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوْقَ وَلَا جِدَالَ فِى الْحَجِّ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ يَّعْلَمْهُ اللّٰهُ ۗ وَتَزَوَّدُوْا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۖ وَاتَّقُوْنِ يٰٓاُولِى الْاَلْبَابِ','(Musim) haji adalah beberapa bulan yang dimaklumi, barangsiapa yang menetapkan niatnya dalam bulan itu akan mengerjakan haji, maka tidak boleh rafats, berbuat fasik dan berbantah-bantahan di dalam masa mengerjakan haji. Dan apa yang kamu kerjakan berupa kebaikan, niscaya Allah mengetahuinya. Berbekallah, dan sesungguhnya sebaik-baik bekal adalah takwa dan bertakwalah kepada-Ku hai orang-orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,198,'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ ۗ فَاِذَآ اَفَضْتُمْ مِّنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوْهُ كَمَا هَدٰىكُمْ ۚ وَاِنْ كُنْتُمْ مِّنْ قَبْلِهٖ لَمِنَ الضَّاۤلِّيْنَ ','Tidak ada dosa bagimu untuk mencari karunia (rezeki hasil perniagaan) dari Tuhanmu. Maka apabila kamu telah bertolak dari ´Arafat, berdzikirlah kepada Allah di Masy´arilharam. Dan berdzikirlah (dengan menyebut) Allah sebagaimana yang ditunjukkan-Nya kepadamu; dan sesungguhnya kamu sebelum itu benar-benar termasuk orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,199,'ثُمَّ اَفِيْضُوْا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','Kemudian bertolaklah kamu dari tempat bertolaknya orang-orang banyak (´Arafah) dan mohonlah ampun kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,200,'فَاِذَا قَضَيْتُمْ مَّنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَاۤءَكُمْ اَوْ اَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ','Apabila kamu telah menyelesaikan ibadah hajimu, maka berdzikirlah dengan menyebut Allah, sebagaimana kamu menyebut-nyebut (membangga-banggakan) nenek moyangmu, atau (bahkan) berdzikirlah lebih banyak dari itu. Maka di antara manusia ada orang yang bendoa: "Ya Tuhan kami, berilah kami (kebaikan) di dunia", dan tiadalah baginya bahagian (yang menyenangkan) di akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,201,'وَمِنْهُمْ مَّنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ حَسَنَةً وَّقِنَا عَذَابَ النَّارِ ','Dan di antara mereka ada orang yang berdoa: "Ya Tuhan kami, berilah kami kebaikan di dunia dan kebaikan di akhirat dan peliharalah kami dari siksa neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,202,'اُولٰۤىِٕكَ لَهُمْ نَصِيْبٌ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ','Mereka itulah orang-orang yang mendapat bahagian daripada yang mereka usahakan; dan Allah sangat cepat perhitungan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,203,'۞ وَاذْكُرُوا اللّٰهَ فِيْٓ اَيَّامٍ مَّعْدُوْدٰتٍ ۗ فَمَنْ تَعَجَّلَ فِيْ يَوْمَيْنِ فَلَآ اِثْمَ عَلَيْهِ ۚوَمَنْ تَاَخَّرَ فَلَآ اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ اِلَيْهِ تُحْشَرُوْنَ ','Dan berdzikirlah (dengan menyebut) Allah dalam beberapa hari yang berbilang. Barangsiapa yang ingin cepat berangkat (dari Mina) sesudah dua hari, maka tiada dosa baginya. Dan barangsiapa yang ingin menangguhkan (keberangkatannya dari dua hari itu), maka tidak ada dosa pula baginya, bagi orang yang bertakwa. Dan bertakwalah kepada Allah, dan ketahuilah, bahwa kamu akan dikumpulkan kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,204,'وَمِنَ النَّاسِ مَنْ يُّعْجِبُكَ قَوْلُهٗ فِى الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا فِيْ قَلْبِهٖ ۙ وَهُوَ اَلَدُّ الْخِصَامِ ','Dan di antara manusia ada orang yang ucapannya tentang kehidupan dunia menarik hatimu, dan dipersaksikannya kepada Allah (atas kebenaran) isi hatinya, padahal ia adalah penantang yang paling keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,205,'وَاِذَا تَوَلّٰى سَعٰى فِى الْاَرْضِ لِيُفْسِدَ فِيْهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَ اللّٰهُ لَا يُحِبُّ الْفَسَادَ ','Dan apabila ia berpaling (dari kamu), ia berjalan di bumi untuk mengadakan kerusakan padanya, dan merusak tanam-tanaman dan binatang ternak, dan Allah tidak menyukai kebinasaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,206,'وَاِذَا قِيْلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهٗ جَهَنَّمُ ۗ وَلَبِئْسَ الْمِهَادُ ','Dan apabila dikatakan kepadanya: "Bertakwalah kepada Allah", bangkitlah kesombongannya yang menyebabkannya berbuat dosa. Maka cukuplah (balasannya) neraka Jahannam. Dan sungguh neraka Jahannam itu tempat tinggal yang seburuk-buruknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,207,'وَمِنَ النَّاسِ مَنْ يَّشْرِيْ نَفْسَهُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ ','Dan di antara manusia ada orang yang mengorbankan dirinya karena mencari keridhaan Allah; dan Allah Maha Penyantun kepada hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,208,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ادْخُلُوْا فِى السِّلْمِ كَاۤفَّةً  ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ','Hai orang-orang yang beriman, masuklah kamu ke dalam Islam keseluruhan, dan janganlah kamu turut langkah-langkah syaitan. Sesungguhnya syaitan itu musuh yang nyata bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,209,'فَاِنْ زَلَلْتُمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْكُمُ الْبَيِّنٰتُ فَاعْلَمُوْٓا اَنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ','Tetapi jika kamu menyimpang (dari jalan Allah) sesudah datang kepadamu bukti-bukti kebenaran, maka ketahuilah, bahwasanya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,210,'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ يَّأْتِيَهُمُ اللّٰهُ فِيْ ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلٰۤىِٕكَةُ وَقُضِيَ الْاَمْرُ ۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ','Tiada yang mereka nanti-nantikan melainkan datangnya Allah dan malaikat (pada hari kiamat) dalam naungan awan, dan diputuskanlah perkaranya. Dan hanya kepada Allah dikembalikan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,211,'سَلْ بَنِيْٓ اِسْرَاۤءِيْلَ كَمْ اٰتَيْنٰهُمْ مِّنْ اٰيَةٍ ۢ بَيِّنَةٍ ۗ وَمَنْ يُّبَدِّلْ نِعْمَةَ اللّٰهِ مِنْۢ بَعْدِ مَا جَاۤءَتْهُ فَاِنَّ اللّٰهَ  شَدِيْدُ الْعِقَابِ ','Tanyakanlah kepada Bani Israil: "Berapa banyaknya tanda-tanda (kebenaran) yang nyata, yang telah Kami berikan kepada mereka". Dan barangsiapa yang menukar nikmat Allah setelah datang nikmat itu kepadanya, maka sesungguhnya Allah sangat keras siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,212,'زُيِّنَ لِلَّذِيْنَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُوْنَ مِنَ الَّذِيْنَ اٰمَنُوْا ۘ وَالَّذِيْنَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِ ۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ','Kehidupan dunia dijadikan indah dalam pandangan orang-orang kafir, dan mereka memandang hina orang-orang yang beriman. Padahal orang-orang yang bertakwa itu lebih mulia daripada mereka di hari kiamat. Dan Allah memberi rezeki kepada orang-orang yang dikehendaki-Nya tanpa batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,213,'كَانَ النَّاسُ اُمَّةً وَّاحِدَةً ۗ فَبَعَثَ اللّٰهُ النَّبِيّٖنَ مُبَشِّرِيْنَ وَمُنْذِرِيْنَ ۖ وَاَنْزَلَ مَعَهُمُ الْكِتٰبَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيْمَا اخْتَلَفُوْا فِيْهِ ۗ وَمَا اخْتَلَفَ فِيْهِ اِلَّا الَّذِيْنَ اُوْتُوْهُ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ بَغْيًا ۢ بَيْنَهُمْ ۚ فَهَدَى اللّٰهُ الَّذِيْنَ اٰمَنُوْا لِمَا اخْتَلَفُوْا فِيْهِ مِنَ الْحَقِّ بِاِذْنِهٖ ۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Manusia itu adalah umat yang satu. (setelah timbul perselisihan), maka Allah mengutus para nabi, sebagai pemberi peringatan, dan Allah menurunkan bersama mereka Kitab yang benar, untuk memberi keputusan di antara manusia tentang perkara yang mereka perselisihkan. Tidaklah berselisih tentang Kitab itu melainkan orang yang telah didatangkan kepada mereka Kitab, yaitu setelah datang kepada mereka keterangan-keterangan yang nyata, karena dengki antara mereka sendiri. Maka Allah memberi petunjuk orang-orang yang beriman kepada kebenaran tentang hal yang mereka perselisihkann itu dengan kehendak-Nya. Dan Allah selalu memberi petunjuk orang yang dikehendaki-Nya kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,214,'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَّثَلُ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ ۗ مَسَّتْهُمُ الْبَأْسَاۤءُ وَالضَّرَّاۤءُ وَزُلْزِلُوْا حَتّٰى يَقُوْلَ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ مَتٰى نَصْرُ اللّٰهِ ۗ اَلَآ اِنَّ نَصْرَ اللّٰهِ قَرِيْبٌ ','Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum datang kepadamu (cobaan) sebagaimana halnya orang-orang terdahulu sebelum kamu? Mereka ditimpa oleh malapetaka dan kesengsaraan, serta digoncangkan (dengan bermacam-macam cobaan) sehingga berkatalah Rasul dan orang-orang yang beriman bersamanya: "Bilakah datangnya pertolongan Allah?" Ingatlah, sesungguhnya pertolongan Allah itu amat dekat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,215,'يَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ۗ قُلْ مَآ اَنْفَقْتُمْ مِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ','Mereka bertanya tentang apa yang mereka nafkahkan. Jawablah: "Apa saja harta yang kamu nafkahkan hendaklah diberikan kepada ibu-bapak, kaum kerabat, anak-anak yatim, orang-orang miskin dan orang-orang yang sedang dalam perjalanan". Dan apa saja kebaikan yang kamu buat, maka sesungguhnya Allah Maha Mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,216,'كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّهُوَ خَيْرٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تُحِبُّوْا شَيْـًٔا وَّهُوَ شَرٌّ لَّكُمْ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ࣖ','Diwajibkan atas kamu berperang, padahal berperang itu adalah sesuatu yang kamu benci. Boleh jadi kamu membenci sesuatu, padahal ia amat baik bagimu, dan boleh jadi (pula) kamu menyukai sesuatu, padahal ia amat buruk bagimu; Allah mengetahui, sedang kamu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,217,'يَسْـَٔلُوْنَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيْهِۗ  قُلْ قِتَالٌ فِيْهِ كَبِيْرٌ ۗ وَصَدٌّ عَنْ سَبِيْلِ اللّٰهِ وَكُفْرٌۢ بِهٖ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِهٖ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِ ۚ وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُوْنَ يُقَاتِلُوْنَكُمْ حَتّٰى يَرُدُّوْكُمْ عَنْ دِيْنِكُمْ اِنِ اسْتَطَاعُوْا ۗ وَمَنْ يَّرْتَدِدْ مِنْكُمْ عَنْ دِيْنِهٖ فَيَمُتْ وَهُوَ كَافِرٌ فَاُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ','Mereka bertanya kepadamu tentang berperang pada bulan Haram. Katakanlah: "Berperang dalam bulan itu adalah dosa besar; tetapi menghalangi (manusia) dari jalan Allah, kafir kepada Allah, (menghalangi masuk) Masjidilharam dan mengusir penduduknya dari sekitarnya, lebih besar (dosanya) di sisi Allah. Dan berbuat fitnah lebih besar (dosanya) daripada membunuh. Mereka tidak henti-hentinya memerangi kamu sampai mereka (dapat) mengembalikan kamu dari agamamu (kepada kekafiran), seandainya mereka sanggup. Barangsiapa yang murtad di antara kamu dari agamanya, lalu dia mati dalam kekafiran, maka mereka itulah yang sia-sia amalannya di dunia dan di akhirat, dan mereka itulah penghuni neraka, mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,218,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ ۙ اُولٰۤىِٕكَ يَرْجُوْنَ رَحْمَتَ اللّٰهِ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ','Sesungguhnya orang-orang yang beriman, orang-orang yang berhijrah dan berjihad di jalan Allah, mereka itu mengharapkan rahmat Allah, dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,219,'۞ يَسْـَٔلُوْنَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۗ قُلْ فِيْهِمَآ اِثْمٌ كَبِيْرٌ وَّمَنَافِعُ لِلنَّاسِۖ وَاِثْمُهُمَآ اَكْبَرُ مِنْ نَّفْعِهِمَاۗ وَيَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ەۗ قُلِ الْعَفْوَۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَۙ','Mereka bertanya kepadamu tentang khamar dan judi. Katakanlah: "Pada keduanya terdapat dosa yang besar dan beberapa manfaat bagi manusia, tetapi dosa keduanya lebih besar dari manfaatnya". Dan mereka bertanya kepadamu apa yang mereka nafkahkan. Katakanlah: "Yang lebih dari keperluan". Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu supaya kamu berfikir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,220,'فِى الدُّنْيَا وَالْاٰخِرَةِ ۗ وَيَسْـَٔلُوْنَكَ عَنِ الْيَتٰمٰىۗ قُلْ اِصْلَاحٌ لَّهُمْ خَيْرٌ ۗ وَاِنْ تُخَالِطُوْهُمْ فَاِخْوَانُكُمْ ۗ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَعْنَتَكُمْ اِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ','tentang dunia dan akhirat. Dan mereka bertanya kepadamu tentang anak yatim, katakalah: "Mengurus urusan mereka secara patut adalah baik, dan jika kamu bergaul dengan mereka, maka mereka adalah saudaramu; dan Allah mengetahui siapa yang membuat kerusakan dari yang mengadakan perbaikan. Dan jikalau Allah menghendaki, niscaya Dia dapat mendatangkan kesulitan kepadamu. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,221,'وَلَا تَنْكِحُوا الْمُشْرِكٰتِ حَتّٰى يُؤْمِنَّ ۗ وَلَاَمَةٌ مُّؤْمِنَةٌ خَيْرٌ مِّنْ مُّشْرِكَةٍ وَّلَوْ اَعْجَبَتْكُمْ ۚ وَلَا تُنْكِحُوا الْمُشْرِكِيْنَ حَتّٰى يُؤْمِنُوْا ۗ وَلَعَبْدٌ مُّؤْمِنٌ خَيْرٌ مِّنْ مُّشْرِكٍ وَّلَوْ اَعْجَبَكُمْ ۗ اُولٰۤىِٕكَ يَدْعُوْنَ اِلَى النَّارِ ۖ وَاللّٰهُ يَدْعُوْٓا اِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِاِذْنِهٖۚ وَيُبَيِّنُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ࣖ','Dan janganlah kamu menikahi wanita-wanita musyrik, sebelum mereka beriman. Sesungguhnya wanita budak yang mukmin lebih baik dari wanita musyrik, walaupun dia menarik hatimu. Dan janganlah kamu menikahkan orang-orang musyrik (dengan wanita-wanita mukmin) sebelum mereka beriman. Sesungguhnya budak yang mukmin lebih baik dari orang musyrik, walaupun dia menarik hatimu. Mereka mengajak ke neraka, sedang Allah mengajak ke surga dan ampunan dengan izin-Nya. Dan Allah menerangkan ayat-ayat-Nya (perintah-perintah-Nya) kepada manusia supaya mereka mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,222,'وَيَسْـَٔلُوْنَكَ عَنِ الْمَحِيْضِ ۗ  قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَاۤءَ فِى الْمَحِيْضِۙ وَلَا تَقْرَبُوْهُنَّ حَتّٰى يَطْهُرْنَ ۚ فَاِذَا تَطَهَّرْنَ فَأْتُوْهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُ ۗ اِنَّ اللّٰهَ يُحِبُّ التَّوَّابِيْنَ وَيُحِبُّ الْمُتَطَهِّرِيْنَ ','Mereka bertanya kepadamu tentang haidh. Katakanlah: "Haidh itu adalah suatu kotoran". Oleh sebab itu hendaklah kamu menjauhkan diri dari wanita di waktu haidh; dan janganlah kamu mendekati mereka, sebelum mereka suci. Apabila mereka telah suci, maka campurilah mereka itu di tempat yang diperintahkan Allah kepadamu. Sesungguhnya Allah menyukai orang-orang yang bertaubat dan menyukai orang-orang yang mensucikan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,223,'نِسَاۤؤُكُمْ حَرْثٌ لَّكُمْ ۖ فَأْتُوْا حَرْثَكُمْ اَنّٰى شِئْتُمْ ۖ وَقَدِّمُوْا لِاَنْفُسِكُمْ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ مُّلٰقُوْهُ ۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ','Isteri-isterimu adalah (seperti) tanah tempat kamu bercocok tanam, maka datangilah tanah tempat bercocok-tanammu itu bagaimana saja kamu kehendaki. Dan kerjakanlah (amal yang baik) untuk dirimu, dan bertakwalah kepada Allah dan ketahuilah bahwa kamu kelak akan menemui-Nya. Dan berilah kabar gembira orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,224,'وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِّاَيْمَانِكُمْ اَنْ تَبَرُّوْا وَتَتَّقُوْا وَتُصْلِحُوْا بَيْنَ النَّاسِۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ','Jangahlah kamu jadikan (nama) Allah dalam sumpahmu sebagai penghalang untuk berbuat kebajikan, bertakwa dan mengadakan ishlah di antara manusia. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,225,'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوْبُكُمْ ۗ وَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ','Allah tidak menghukum kamu disebabkan sumpahmu yang tidak dimaksud (untuk bersumpah), tetapi Allah menghukum kamu disebabkan (sumpahmu) yang disengaja (untuk bersumpah) oleh hatimu. Dan Allah Maha Pengampun lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,226,'لِلَّذِيْنَ يُؤْلُوْنَ مِنْ نِّسَاۤىِٕهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَاۤءُوْ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Kepada orang-orang yang meng-ilaa´ isterinya diberi tangguh empat bulan (lamanya). Kemudian jika mereka kembali (kepada isterinya), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,227,'وَاِنْ عَزَمُوا الطَّلَاقَ فَاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ','Dan jika mereka berazam (bertetap hati untuk) talak, maka sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,228,'وَالْمُطَلَّقٰتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُوْۤءٍۗ وَلَا يَحِلُّ لَهُنَّ اَنْ يَّكْتُمْنَ مَا خَلَقَ اللّٰهُ فِيْٓ اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ وَبُعُوْلَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ فِيْ ذٰلِكَ اِنْ اَرَادُوْٓا اِصْلَاحًا ۗوَلَهُنَّ مِثْلُ الَّذِيْ عَلَيْهِنَّ بِالْمَعْرُوْفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ  ۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ࣖ','Wanita-wanita yang ditalak handaklah menahan diri (menunggu) tiga kali quru´. Tidak boleh mereka menyembunyikan apa yang diciptakan Allah dalam rahimnya, jika mereka beriman kepada Allah dan hari akhirat. Dan suami-suaminya berhak merujukinya dalam masa menanti itu, jika mereka (para suami) menghendaki ishlah. Dan para wanita mempunyai hak yang seimbang dengan kewajibannya menurut cara yang ma´ruf. Akan tetapi para suami, mempunyai satu tingkatan kelebihan daripada isterinya. Dan Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,229,'اَلطَّلَاقُ مَرَّتٰنِ ۖ فَاِمْسَاكٌۢ بِمَعْرُوْفٍ اَوْ تَسْرِيْحٌۢ بِاِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوْا مِمَّآ اٰتَيْتُمُوْهُنَّ شَيْـًٔا اِلَّآ اَنْ يَّخَافَآ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۗ فَاِنْ خِفْتُمْ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۙ فَلَا جُنَاحَ عَلَيْهِمَا فِيْمَا افْتَدَتْ بِهٖ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَعْتَدُوْهَا ۚوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ','Talak (yang dapat dirujuki) dua kali. Setelah itu boleh rujuk lagi dengan cara yang ma´ruf atau menceraikan dengan cara yang baik. Tidak halal bagi kamu mengambil kembali sesuatu dari yang telah kamu berikan kepada mereka, kecuali kalau keduanya khawatir tidak akan dapat menjalankan hukum-hukum Allah. Jika kamu khawatir bahwa keduanya (suami isteri) tidak dapat menjalankan hukum-hukum Allah, maka tidak ada dosa atas keduanya tentang bayaran yang diberikan oleh isteri untuk menebus dirinya. Itulah hukum-hukum Allah, maka janganlah kamu melanggarnya. Barangsiapa yang melanggar hukum-hukum Allah mereka itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,230,'فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهٗ مِنْۢ بَعْدُ حَتّٰى تَنْكِحَ زَوْجًا غَيْرَهٗ ۗ فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يَّتَرَاجَعَآ اِنْ ظَنَّآ اَنْ يُّقِيْمَا حُدُوْدَ اللّٰهِ ۗ وَتِلْكَ حُدُوْدُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَّعْلَمُوْنَ ','Kemudian jika si suami mentalaknya (sesudah talak yang kedua), maka perempuan itu tidak lagi halal baginya hingga dia kawin dengan suami yang lain. Kemudian jika suami yang lain itu menceraikannya, maka tidak ada dosa bagi keduanya (bekas suami pertama dan isteri) untuk kawin kembali jika keduanya berpendapat akan dapat menjalankan hukum-hukum Allah. Itulah hukum-hukum Allah, diterangkan-Nya kepada kaum yang (mau) mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,231,'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ سَرِّحُوْهُنَّ بِمَعْرُوْفٍۗ وَلَا تُمْسِكُوْهُنَّ ضِرَارًا لِّتَعْتَدُوْا ۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ وَلَا تَتَّخِذُوْٓا اٰيٰتِ اللّٰهِ هُزُوًا وَّاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَمَآ اَنْزَلَ عَلَيْكُمْ مِّنَ الْكِتٰبِ وَالْحِكْمَةِ يَعِظُكُمْ بِهٖ ۗوَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ','Apabila kamu mentalak isteri-isterimu, lalu mereka mendekati akhir iddahnya, maka rujukilah mereka dengan cara yang ma´ruf, atau ceraikanlah mereka dengan cara yang ma´ruf (pula). Janganlah kamu rujuki mereka untuk memberi kemudharatan, karena dengan demikian kamu menganiaya mereka. Barangsiapa berbuat demikian, maka sungguh ia telah berbuat zalim terhadap dirinya sendiri. Janganlah kamu jadikan hukum-hukum Allah permainan, dan ingatlah nikmat Allah padamu, dan apa yang telah diturunkan Allah kepadamu yaitu Al Kitab dan Al Hikmah (As Sunnah). Allah memberi pengajaran kepadamu dengan apa yang diturunkan-Nya itu. Dan bertakwalah kepada Allah serta ketahuilah bahwasanya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,232,'وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوْهُنَّ اَنْ يَّنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوْفِ ۗ ذٰلِكَ يُوْعَظُ بِهٖ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ۗ ذٰلِكُمْ اَزْكٰى لَكُمْ وَاَطْهَرُ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ','Apabila kamu mentalak isteri-isterimu, lalu habis masa iddahnya, maka janganlah kamu (para wali) menghalangi mereka kawin lagi dengan bakal suaminya, apabila telah terdapat kerelaan di antara mereka dengan cara yang ma´ruf. Itulah yang dinasehatkan kepada orang-orang yang beriman di antara kamu kepada Allah dan hari kemudian. Itu lebih baik bagimu dan lebih suci. Allah mengetahui, sedang kamu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,233,'۞ وَالْوَالِدٰتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُّتِمَّ الرَّضَاعَةَ ۗ وَعَلَى الْمَوْلُوْدِ لَهٗ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوْفِۗ  لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَا ۚ لَا تُضَاۤرَّ وَالِدَةٌ ۢبِوَلَدِهَا وَلَا مَوْلُوْدٌ لَّهٗ بِوَلَدِهٖ وَعَلَى الْوَارِثِ مِثْلُ ذٰلِكَ ۚ فَاِنْ اَرَادَا فِصَالًا عَنْ تَرَاضٍ مِّنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَا ۗوَاِنْ اَرَدْتُّمْ اَنْ تَسْتَرْضِعُوْٓا اَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ اِذَا سَلَّمْتُمْ مَّآ اٰتَيْتُمْ بِالْمَعْرُوْفِۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Para ibu hendaklah menyusukan anak-anaknya selama dua tahun penuh, yaitu bagi yang ingin menyempurnakan penyusuan. Dan kewajiban ayah memberi makan dan pakaian kepada para ibu dengan cara ma´ruf. Seseorang tidak dibebani melainkan menurut kadar kesanggupannya. Janganlah seorang ibu menderita kesengsaraan karena anaknya dan seorang ayah karena anaknya, dan warispun berkewajiban demikian. Apabila keduanya ingin menyapih (sebelum dua tahun) dengan kerelaan keduanya dan permusyawaratan, maka tidak ada dosa atas keduanya. Dan jika kamu ingin anakmu disusukan oleh orang lain, maka tidak ada dosa bagimu apabila kamu memberikan pembayaran menurut yang patut. Bertakwalah kamu kepada Allah dan ketahuilah bahwa Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,234,'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًا يَّتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَّعَشْرًا ۚ فاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ بِالْمَعْرُوْفِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ','Orang-orang yang meninggal dunia di antaramu dengan meninggalkan isteri-isteri (hendaklah para isteri itu) menangguhkan dirinya (ber´iddah) empat bulan sepuluh hari. Kemudian apabila telah habis ´iddahnya, maka tiada dosa bagimu (para wali) membiarkan mereka berbuat terhadap diri mereka menurut yang patut. Allah mengetahui apa yang kamu perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,235,'وَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا عَرَّضْتُمْ بِهٖ مِنْ خِطْبَةِ النِّسَاۤءِ اَوْ اَكْنَنْتُمْ فِيْٓ اَنْفُسِكُمْ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُوْنَهُنَّ وَلٰكِنْ لَّا تُوَاعِدُوْهُنَّ سِرًّا اِلَّآ اَنْ تَقُوْلُوْا قَوْلًا مَّعْرُوْفًا ەۗ وَلَا تَعْزِمُوْا عُقْدَةَ النِّكَاحِ حَتّٰى يَبْلُغَ الْكِتٰبُ اَجَلَهٗ ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِيْٓ اَنْفُسِكُمْ فَاحْذَرُوْهُ ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ ࣖ','Dan tidak ada dosa bagi kamu meminang wanita-wanita itu dengan sindiran atau kamu menyembunyikan (keinginan mengawini mereka) dalam hatimu. Allah mengetahui bahwa kamu akan menyebut-nyebut mereka, dalam pada itu janganlah kamu mengadakan janji kawin dengan mereka secara rahasia, kecuali sekedar mengucapkan (kepada mereka) perkataan yang ma´ruf. Dan janganlah kamu berazam (bertetap hati) untuk beraqad nikah, sebelum habis ´iddahnya. Dan ketahuilah bahwasanya Allah mengetahui apa yang ada dalam hatimu; maka takutlah kepada-Nya, dan ketahuilah bahwa Allah Maha Pengampun lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,236,'لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَاۤءَ مَا لَمْ تَمَسُّوْهُنَّ اَوْ تَفْرِضُوْا لَهُنَّ فَرِيْضَةً ۖ وَّمَتِّعُوْهُنَّ عَلَى الْمُوْسِعِ قَدَرُهٗ وَعَلَى الْمُقْتِرِ قَدَرُهٗ ۚ مَتَاعًا ۢبِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُحْسِنِيْنَ ','Tidak ada kewajiban membayar (mahar) atas kamu, jika kamu menceraikan isteri-isteri kamu sebelum kamu bercampur dengan mereka dan sebelum kamu menentukan maharnya. Dan hendaklah kamu berikan suatu mut´ah (pemberian) kepada mereka. Orang yang mampu menurut kemampuannya dan orang yang miskin menurut kemampuannya (pula), yaitu pemberian menurut yang patut. Yang demikian itu merupakan ketentuan bagi orang-orang yang berbuat kebajikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,237,'وَاِنْ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيْضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّآ اَنْ يَّعْفُوْنَ اَوْ يَعْفُوَا الَّذِيْ بِيَدِهٖ عُقْدَةُ النِّكَاحِ ۗ وَاَنْ تَعْفُوْٓا اَقْرَبُ لِلتَّقْوٰىۗ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Jika kamu menceraikan isteri-isterimu sebelum kamu bercampur dengan mereka, padahal sesungguhnya kamu sudah menentukan maharnya, maka bayarlah seperdua dari mahar yang telah kamu tentukan itu, kecuali jika isteri-isterimu itu memaafkan atau dimaafkan oleh orang yang memegang ikatan nikah, dan pemaafan kamu itu lebih dekat kepada takwa. Dan janganlah kamu melupakan keutamaan di antara kamu. Sesungguhnya Allah Maha Melihat segala apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,238,'حَافِظُوْا عَلَى الصَّلَوٰتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُوْمُوْا لِلّٰهِ قٰنِتِيْنَ ','Peliharalah semua shalat(mu), dan (peliharalah) shalat wusthaa. Berdirilah untuk Allah (dalam shalatmu) dengan khusyu´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,239,'فَاِنْ خِفْتُمْ فَرِجَالًا اَوْ رُكْبَانًا ۚ فَاِذَآ اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَ ','Jika kamu dalam keadaan takut (bahaya), maka shalatlah sambil berjalan atau berkendaraan. Kemudian apabila kamu telah aman, maka sebutlah Allah (shalatlah), sebagaimana Allah telah mengajarkan kepada kamu apa yang belum kamu ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,240,'وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًاۖ وَّصِيَّةً لِّاَزْوَاجِهِمْ مَّتَاعًا اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍ ۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِيْ مَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ مِنْ مَّعْرُوْفٍۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ','Dan orang-orang yang akan meninggal dunia di antara kamu dan meninggalkan isteri, hendaklah berwasiat untuk isteri-isterinya, (yaitu) diberi nafkah hingga setahun lamanya dan tidak disuruh pindah (dari rumahnya). Akan tetapi jika mereka pindah (sendiri), maka tidak ada dosa bagimu (wali atau waris dari yang meninggal) membiarkan mereka berbuat yang ma´ruf terhadap diri mereka. Dan Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,241,'وَلِلْمُطَلَّقٰتِ مَتَاعٌ ۢبِالْمَعْرُوْفِۗ حَقًّا عَلَى الْمُتَّقِيْنَ ','Kepada wanita-wanita yang diceraikan (hendaklah diberikan oleh suaminya) mut´ah menurut yang ma´ruf, sebagai suatu kewajiban bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,242,'كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ ࣖ','Demikianlah Allah menerangkan kepadamu ayat-ayat-Nya (hukum-hukum-Nya) supaya kamu memahaminya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,243,'۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ وَهُمْ اُلُوْفٌ حَذَرَ الْمَوْتِۖ  فَقَالَ لَهُمُ اللّٰهُ مُوْتُوْا ۗ ثُمَّ اَحْيَاهُمْ ۗ اِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ ','Apakah kamu tidak memperhatikan orang-orang yang ke luar dari kampung halaman mereka, sedang mereka beribu-ribu (jumlahnya) karena takut mati; maka Allah berfirman kepada mereka: "Matilah kamu", kemudian Allah menghidupkan mereka. Sesungguhnya Allah mempunyai karunia terhadap manusia tetapi kebanyakan manusia tidak bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,244,'وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ وَاعْلَمُوْٓا اَنَّ اللّٰهَ  سَمِيْعٌ عَلِيْمٌ ','Dan berperanglah kamu sekalian di jalan Allah, dan ketahuilah sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,245,'مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗٓ اَضْعَافًا كَثِيْرَةً ۗوَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُوْنَ','Siapakah yang mau memberi pinjaman kepada Allah, pinjaman yang baik (menafkahkan hartanya di jalan Allah), maka Allah akan meperlipat gandakan pembayaran kepadanya dengan lipat ganda yang banyak. Dan Allah menyempitkan dan melapangkan (rezeki) dan kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,246,'اَلَمْ تَرَ اِلَى الْمَلَاِ مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ مِنْۢ بَعْدِ مُوْسٰىۘ اِذْ قَالُوْا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۗ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُوْا ۗ قَالُوْا وَمَا لَنَآ اَلَّا نُقَاتِلَ فِيْ سَبِيْلِ اللّٰهِ وَقَدْاُخْرِجْنَا مِنْ دِيَارِنَا وَاَبْنَاۤىِٕنَا ۗ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا اِلَّا قَلِيْلًا مِّنْهُمْ ۗوَاللّٰهُ عَلِيْمٌ ۢبِالظّٰلِمِيْنَ ','Apakah kamu tidak memperhatikan pemuka-pemuka Bani Israil sesudah Nabi Musa, yaitu ketika mereka berkata kepada seorang Nabi mereka: "Angkatlah untuk kami seorang raja supaya kami berperang (di bawah pimpinannya) di jalan Allah". Nabi mereka menjawab: "Mungkin sekali jika kamu nanti diwajibkan berperang, kamu tidak akan berperang". Mereka menjawab: "Mengapa kami tidak mau berperang di jalan Allah, padahal sesungguhnya kami telah diusir dari anak-anak kami?". Maka tatkala perang itu diwajibkan atas mereka, merekapun berpaling, kecuali beberapa saja di antara mereka. Dan Allah Maha Mengetahui siapa orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,247,'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوْتَ مَلِكًا ۗ قَالُوْٓا اَنّٰى يَكُوْنُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِّنَ الْمَالِۗ قَالَ اِنَّ اللّٰهَ اصْطَفٰىهُ عَلَيْكُمْ وَزَادَهٗ بَسْطَةً فِى الْعِلْمِ وَالْجِسْمِ ۗ وَاللّٰهُ يُؤْتِيْ مُلْكَهٗ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ','Nabi mereka mengatakan kepada mereka: "Sesungguhnya Allah telah mengangkat Thalut menjadi rajamu". Mereka menjawab: "Bagaimana Thalut memerintah kami, padahal kami lebih berhak mengendalikan pemerintahan daripadanya, sedang diapun tidak diberi kekayaan yang cukup banyak?" Nabi (mereka) berkata: "Sesungguhnya Allah telah memilih rajamu dan menganugerahinya ilmu yang luas dan tubuh yang perkasa". Allah memberikan pemerintahan kepada siapa yang dikehendaki-Nya. Dan Allah Maha Luas pemberian-Nya lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,248,'وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِهٖٓ اَنْ يَّأْتِيَكُمُ التَّابُوْتُ فِيْهِ سَكِيْنَةٌ مِّنْ رَّبِّكُمْ وَبَقِيَّةٌ مِّمَّا تَرَكَ اٰلُ مُوْسٰى وَاٰلُ هٰرُوْنَ تَحْمِلُهُ الْمَلٰۤىِٕكَةُ ۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ࣖ','Dan Nabi mereka mengatakan kepada mereka: "Sesungguhnya tanda ia akan menjadi raja, ialah kembalinya tabut kepadamu, di dalamnya terdapat ketenangan dari Tuhanmu dan sisa dari peninggalan keluarga Musa dan keluarga Harun; tabut itu dibawa malaikat. Sesungguhnya pada yang demikian itu terdapat tanda bagimu, jika kamu orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,249,'فَلَمَّا فَصَلَ طَالُوْتُ بِالْجُنُوْدِ قَالَ اِنَّ اللّٰهَ مُبْتَلِيْكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّيْۚ وَمَنْ لَّمْ يَطْعَمْهُ فَاِنَّهٗ مِنِّيْٓ اِلَّا مَنِ اغْتَرَفَ غُرْفَةً ۢبِيَدِهٖ ۚ فَشَرِبُوْا مِنْهُ اِلَّا قَلِيْلًا مِّنْهُمْ ۗ فَلَمَّا جَاوَزَهٗ هُوَ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۙ قَالُوْا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوْتَ وَجُنُوْدِهٖ ۗ قَالَ الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوا اللّٰهِ ۙ كَمْ مِّنْ فِئَةٍ قَلِيْلَةٍ غَلَبَتْ فِئَةً كَثِيْرَةً ۢبِاِذْنِ اللّٰهِ ۗ وَاللّٰهُ مَعَ الصّٰبِرِيْنَ ','Maka tatkala Thalut keluar membawa tentaranya, ia berkata: "Sesungguhnya Allah akan menguji kamu dengan suatu sungai. Maka siapa di antara kamu meminum airnya; bukanlah ia pengikutku. Dan barangsiapa tiada meminumnya, kecuali menceduk seceduk tangan, maka dia adalah pengikutku". Kemudian mereka meminumnya kecuali beberapa orang di antara mereka. Maka tatkala Thalut dan orang-orang yang beriman bersama dia telah menyeberangi sungai itu, orang-orang yang telah minum berkata: "Tak ada kesanggupan kami pada hari ini untuk melawan Jalut dan tentaranya". Orang-orang yang meyakini bahwa mereka akan menemui Allah, berkata: "Berapa banyak terjadi golongan yang sedikit dapat mengalahkan golongan yang banyak dengan izin Allah. Dan Allah beserta orang-orang yang sabar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,250,'وَلَمَّا بَرَزُوْا لِجَالُوْتَ وَجُنُوْدِهٖ قَالُوْا رَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ۗ ','Tatkala Jalut dan tentaranya telah nampak oleh mereka, merekapun (Thalut dan tentaranya) berdoa: "Ya Tuhan kami, tuangkanlah kesabaran atas diri kami, dan kokohkanlah pendirian kami dan tolonglah kami terhadap orang-orang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,251,'فَهَزَمُوْهُمْ بِاِذْنِ اللّٰهِ  ۗوَقَتَلَ دَاوٗدُ جَالُوْتَ وَاٰتٰىهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهٗ مِمَّا يَشَاۤءُ ۗ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوْ فَضْلٍ عَلَى الْعٰلَمِيْنَ ','Mereka (tentara Thalut) mengalahkan tentara Jalut dengan izin Allah dan (dalam peperangan itu) Daud membunuh Jalut, kemudian Allah memberikan kepadanya (Daud) pemerintahan dan hikmah (sesudah meninggalnya Thalut) dan mengajarkan kepadanya apa yang dikehendaki-Nya. Seandainya Allah tidak menolak (keganasan) sebahagian umat manusia dengan sebagian yang lain, pasti rusaklah bumi ini. Tetapi Allah mempunyai karunia (yang dicurahkan) atas semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,2,2,252,'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَاِنَّكَ لَمِنَ الْمُرْسَلِيْنَ  ۔','Itu adalah ayat-ayat dari Allah, Kami bacakan kepadamu dengan hak (benar) dan sesungguhnya kamu benar-benar salah seorang di antara nabi-nabi yang diutus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,253,'تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۘ مِنْهُمْ مَّنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجٰتٍۗ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ وَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلَ الَّذِيْنَ مِنْۢ بَعْدِهِمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ وَلٰكِنِ اخْتَلَفُوْا فَمِنْهُمْ مَّنْ اٰمَنَ وَمِنْهُمْ مَّنْ كَفَرَ ۗوَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلُوْاۗ وَلٰكِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ ࣖ','Rasul-rasul itu Kami lebihkan sebagian (dari) mereka atas sebagian yang lain. Di antara mereka ada yang Allah berkata-kata (langsung dengan dia) dan sebagiannya Allah meninggikannya beberapa derajat. Dan Kami berikan kepada Isa putera Maryam beberapa mukjizat serta Kami perkuat dia dengan Ruhul Qudus. Dan kalau Allah menghendaki, niscaya tidaklah berbunuh-bunuhan orang-orang (yang datang) sesudah rasul-rasul itu, sesudah datang kepada mereka beberapa macam keterangan, akan tetapi mereka berselisih, maka ada di antara mereka yang beriman dan ada (pula) di antara mereka yang kafir. Seandainya Allah menghendaki, tidaklah mereka berbunuh-bunuhan. Akan tetapi Allah berbuat apa yang dikehendaki-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,254,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِمَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خُلَّةٌ وَّلَا شَفَاعَةٌ  ۗوَالْكٰفِرُوْنَ هُمُ الظّٰلِمُوْنَ','Hai orang-orang yang beriman, belanjakanlah (di jalan Allah) sebagian dari rezeki yang telah Kami berikan kepadamu sebelum datang hari yang pada hari itu tidak ada lagi jual beli dan tidak ada lagi syafa´at. Dan orang-orang kafir itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,255,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّوْمُ ەۚ لَا تَأْخُذُهٗ سِنَةٌ وَّلَا نَوْمٌۗ  لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَنْ ذَا الَّذِيْ يَشْفَعُ عِنْدَهٗٓ اِلَّا بِاِذْنِهٖۗ يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۚ وَلَا يُحِيْطُوْنَ بِشَيْءٍ مِّنْ عِلْمِهٖٓ اِلَّا بِمَا شَاۤءَۚ وَسِعَ كُرْسِيُّهُ السَّمٰوٰتِ وَالْاَرْضَۚ وَلَا يَـُٔوْدُهٗ حِفْظُهُمَاۚ وَهُوَ الْعَلِيُّ الْعَظِيْمُ','Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia Yang Hidup kekal lagi terus menerus mengurus (makhluk-Nya); tidak mengantuk dan tidak tidur. Kepunyaan-Nya apa yang di langit dan di bumi. Tiada yang dapat memberi syafa´at di sisi Allah tanpa izin-Nya? Allah mengetahui apa-apa yang di hadapan mereka dan di belakang mereka, dan mereka tidak mengetahui apa-apa dari ilmu Allah melainkan apa yang dikehendaki-Nya. Kursi Allah meliputi langit dan bumi. Dan Allah tidak merasa berat memelihara keduanya, dan Allah Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,256,'لَآ اِكْرَاهَ فِى الدِّيْنِۗ  قَدْ تَّبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَنْ يَّكْفُرْ بِالطَّاغُوْتِ وَيُؤْمِنْۢ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰى لَا انْفِصَامَ لَهَا ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ','Tidak ada paksaan untuk (memasuki) agama (Islam); sesungguhnya telah jelas jalan yang benar daripada jalan yang sesat. Karena itu barangsiapa yang ingkar kepada Thaghut dan beriman kepada Allah, maka sesungguhnya ia telah berpegang kepada buhul tali yang amat kuat yang tidak akan putus. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,257,'اَللّٰهُ وَلِيُّ الَّذِيْنَ اٰمَنُوْا يُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَالَّذِيْنَ كَفَرُوْٓا اَوْلِيَاۤؤُهُمُ الطَّاغُوْتُ يُخْرِجُوْنَهُمْ مِّنَ النُّوْرِ اِلَى الظُّلُمٰتِۗ  اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ࣖ','Allah Pelindung orang-orang yang beriman; Dia mengeluarkan mereka dari kegelapan (kekafiran) kepada cahaya (iman). Dan orang-orang yang kafir, pelindung-pelindungnya ialah syaitan, yang mengeluarkan mereka daripada cahaya kepada kegelapan (kekafiran). Mereka itu adalah penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,258,'اَلَمْ تَرَ اِلَى الَّذِيْ حَاۤجَّ اِبْرٰهٖمَ فِيْ رَبِّهٖٓ اَنْ اٰتٰىهُ اللّٰهُ الْمُلْكَ ۘ اِذْ قَالَ اِبْرٰهٖمُ رَبِّيَ الَّذِيْ يُحْيٖ وَيُمِيْتُۙ قَالَ اَنَا۠ اُحْيٖ وَاُمِيْتُ ۗ قَالَ اِبْرٰهٖمُ فَاِنَّ اللّٰهَ يَأْتِيْ بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِيْ كَفَرَ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۚ','Apakah kamu tidak memperhatikan orang yang mendebat Ibrahim tentang Tuhannya (Allah) karena Allah telah memberikan kepada orang itu pemerintahan (kekuasaan). Ketika Ibrahim mengatakan: "Tuhanku ialah Yang menghidupkan dan mematikan," orang itu berkata: "Saya dapat menghidupkan dan mematikan".Ibrahim berkata: "Sesungguhnya Allah menerbitkan matahari dari timur, maka terbitkanlah dia dari barat," lalu terdiamlah orang kafir itu; dan Allah tidak memberi petunjuk kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,259,'اَوْ كَالَّذِيْ مَرَّ عَلٰى قَرْيَةٍ وَّهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۚ قَالَ اَنّٰى يُحْيٖ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَا ۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهٗ ۗ قَالَ كَمْ لَبِثْتَ ۗ قَالَ لَبِثْتُ يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالَ بَلْ لَّبِثْتَ مِائَةَ عَامٍ فَانْظُرْ اِلٰى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۚ وَانْظُرْ اِلٰى حِمَارِكَۗ وَلِنَجْعَلَكَ اٰيَةً لِّلنَّاسِ وَانْظُرْ اِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوْهَا لَحْمًا ۗ فَلَمَّا تَبَيَّنَ لَهٗ ۙ قَالَ اَعْلَمُ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Atau apakah (kamu tidak memperhatikan) orang yang melalui suatu negeri yang (temboknya) telah roboh menutupi atapnya. Dia berkata: "Bagaimana Allah menghidupkan kembali negeri ini setelah hancur?" Maka Allah mematikan orang itu seratus tahun, kemudian menghidupkannya kembali. Allah bertanya: "Berapakah lamanya kamu tinggal di sini?" Ia menjawab: "Saya tinggal di sini sehari atau setengah hari". Allah berfirman: "Sebenarnya kamu telah tinggal di sini seratus tahun lamanya; lihatlah kepada makanan dan minumanmu yang belum lagi beubah; dan lihatlah kepada keledai kamu (yang telah menjadi tulang belulang); Kami akan menjadikan kamu tanda kekuasaan Kami bagi manusia; dan lihatlah kepada tulang belulang keledai itu, kemudian Kami menyusunnya kembali, kemudian Kami membalutnya dengan daging". Maka tatkala telah nyata kepadanya (bagaimana Allah menghidupkan yang telah mati) diapun berkata: "Saya yakin bahwa Allah Maha Kuasa atas segala sesuatu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,260,'وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اَرِنِيْ كَيْفَ تُحْيِ الْمَوْتٰىۗ قَالَ اَوَلَمْ تُؤْمِنْ ۗقَالَ بَلٰى وَلٰكِنْ لِّيَطْمَىِٕنَّ قَلْبِيْ ۗقَالَ فَخُذْ اَرْبَعَةً مِّنَ الطَّيْرِفَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِّنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِيْنَكَ سَعْيًا ۗوَاعْلَمْ اَنَّ اللّٰهَ عَزِيْزٌحَكِيْمٌ ࣖ','Dan (ingatlah) ketika Ibrahim berkata: "Ya Tuhanku, perlihatkanlah kepadaku bagaimana Engkau menghidupkan orang-orang mati". Allah berfirman: "Belum yakinkah kamu?" Ibrahim menjawab: "Aku telah meyakinkannya, akan tetapi agar hatiku tetap mantap (dengan imanku) Allah berfirman: "(Kalau demikian) ambillah empat ekor burung, lalu cincanglah semuanya olehmu. (Allah berfirman): "Lalu letakkan diatas tiap-tiap satu bukit satu bagian dari bagian-bagian itu, kemudian panggillah mereka, niscaya mereka datang kepadamu dengan segera". Dan ketahuilah bahwa Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,261,'مَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْۢبَتَتْ سَبْعَ سَنَابِلَ فِيْ كُلِّ سُنْۢبُلَةٍ مِّائَةُ حَبَّةٍ ۗ وَاللّٰهُ يُضٰعِفُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ','Perumpamaan (nafkah yang dikeluarkan oleh) orang-orang yang menafkahkan hartanya di jalan Allah adalah serupa dengan sebutir benih yang menumbuhkan tujuh bulir, pada tiap-tiap bulir seratus biji. Allah melipat gandakan (ganjaran) bagi siapa yang Dia kehendaki. Dan Allah Maha Luas (karunia-Nya) lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,262,'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ ثُمَّ لَا يُتْبِعُوْنَ مَآ اَنْفَقُوْا مَنًّا وَّلَآ اَذًىۙ  لَّهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Orang-orang yang menafkahkan hartanya di jalan Allah, kemudian mereka tidak mengiringi apa yang dinafkahkannya itu dengan menyebut-nyebut pemberiannya dan dengan tidak menyakiti (perasaan si penerima), mereka memperoleh pahala di sisi Tuhan mereka. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,263,'۞ قَوْلٌ مَّعْرُوْفٌ وَّمَغْفِرَةٌ خَيْرٌ مِّنْ صَدَقَةٍ يَّتْبَعُهَآ اَذًى ۗ وَاللّٰهُ غَنِيٌّ حَلِيْمٌ','Perkataan yang baik dan pemberian maaf lebih baik dari sedekah yang diiringi dengan sesuatu yang menyakitkan (perasaan si penerima). Allah Maha Kaya lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,264,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُبْطِلُوْا صَدَقٰتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ  كَالَّذِيْ يُنْفِقُ مَالَهٗ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ  فَمَثَلُهٗ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهٗ وَابِلٌ فَتَرَكَهٗ صَلْدًا ۗ  لَا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ','Hai orang-orang yang beriman, janganlah kamu menghilangkan (pahala) sedekahmu dengan menyebut-nyebutnya dan menyakiti (perasaan si penerima), seperti orang yang menafkahkan hartanya karena riya kepada manusia dan dia tidak beriman kepada Allah dan hari kemudian. Maka perumpamaan orang itu seperti batu licin yang di atasnya ada tanah, kemudian batu itu ditimpa hujan lebat, lalu menjadilah dia bersih (tidak bertanah). Mereka tidak menguasai sesuatupun dari apa yang mereka usahakan; dan Allah tidak memberi petunjuk kepada orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,265,'وَمَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ وَتَثْبِيْتًا مِّنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍۢ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ  ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ','Dan perumpamaan orang-orang yang membelanjakan hartanya karena mencari keridhaan Allah dan untuk keteguhan jiwa mereka, seperti sebuah kebun yang terletak di dataran tinggi yang disiram oleh hujan lebat, maka kebun itu menghasilkan buahnya dua kali lipat. Jika hujan lebat tidak menyiraminya, maka hujan gerimis (pun memadai). Dan Allah Maha Melihat apa yang kamu perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,266,'اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُوْنَ لَهٗ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ  لَهٗ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهٗ ذُرِّيَّةٌ ضُعَفَاۤءُۚ فَاَصَابَهَآ اِعْصَارٌ فِيْهِ نَارٌ فَاحْتَرَقَتْ ۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَ ࣖ','Apakah ada salah seorang di antaramu yang ingin mempunyai kebun kurma dan anggur yang mengalir di bawahnya sungai-sungai; dia mempunyai dalam kebun itu segala macam buah-buahan, kemudian datanglah masa tua pada orang itu sedang dia mempunyai keturunan yang masih kecil-kecil. Maka kebun itu ditiup angin keras yang mengandung api, lalu terbakarlah. Demikianlah Allah menerangkan ayat-ayat-Nya kepada kamu supaya kamu memikirkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,267,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِنْ طَيِّبٰتِ مَا كَسَبْتُمْ وَمِمَّآ اَخْرَجْنَا لَكُمْ مِّنَ الْاَرْضِ ۗ وَلَا تَيَمَّمُوا الْخَبِيْثَ مِنْهُ تُنْفِقُوْنَ وَلَسْتُمْ بِاٰخِذِيْهِ اِلَّآ اَنْ تُغْمِضُوْا فِيْهِ ۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ','Hai orang-orang yang beriman, nafkahkanlah (di jalan allah) sebagian dari hasil usahamu yang baik-baik dan sebagian dari apa yang Kami keluarkan dari bumi untuk kamu. Dan janganlah kamu memilih yang buruk-buruk lalu kamu menafkahkan daripadanya, padahal kamu sendiri tidak mau mengambilnya melainkan dengan memincingkan mata terhadapnya. Dan ketahuilah, bahwa Allah Maha Kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,268,'اَلشَّيْطٰنُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاۤءِ ۚ وَاللّٰهُ يَعِدُكُمْ مَّغْفِرَةً مِّنْهُ وَفَضْلًا ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۖ ','Syaitan menjanjikan (menakut-nakuti) kamu dengan kemiskinan dan menyuruh kamu berbuat kejahatan (kikir); sedang Allah menjadikan untukmu ampunan daripada-Nya dan karunia. Dan Allah Maha Luas (karunia-Nya) lagi Maha Mengatahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,269,'يُّؤْتِى الْحِكْمَةَ مَنْ يَّشَاۤءُ ۚ وَمَنْ يُّؤْتَ الْحِكْمَةَ فَقَدْ اُوْتِيَ خَيْرًا كَثِيْرًا ۗ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ','Allah menganugerahkan al hikmah (kefahaman yang dalam tentang Al Quran dan As Sunnah) kepada siapa yang dikehendaki-Nya. Dan barangsiapa yang dianugerahi hikmah, ia benar-benar telah dianugerahi karunia yang banyak. Dan hanya orang-orang yang berakallah yang dapat mengambil pelajaran (dari firman Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,270,'وَمَآ اَنْفَقْتُمْ مِّنْ نَّفَقَةٍ اَوْ نَذَرْتُمْ مِّنْ نَّذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ','Apa saja yang kamu nafkahkan atau apa saja yang kamu nazarkan, maka sesungguhnya Allah mengetahuinya. Orang-orang yang berbuat zalim tidak ada seorang penolongpun baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,271,'اِنْ تُبْدُوا الصَّدَقٰتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوْهَا وَتُؤْتُوْهَا الْفُقَرَاۤءَ فَهُوَ خَيْرٌ لَّكُمْ ۗ وَيُكَفِّرُ عَنْكُمْ مِّنْ سَيِّاٰتِكُمْ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ','Jika kamu menampakkan sedekah(mu), maka itu adalah baik sekali. Dan jika kamu menyembunyikannya dan kamu berikan kepada orang-orang fakir, maka menyembunyikan itu lebih baik bagimu. Dan Allah akan menghapuskan dari kamu sebagian kesalahan-kesalahanmu; dan Allah mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,272,'۞ لَيْسَ عَلَيْكَ هُدٰىهُمْ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْ ۗوَمَا تُنْفِقُوْنَ اِلَّا ابْتِغَاۤءَ وَجْهِ اللّٰهِ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ يُّوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ','Bukanlah kewajibanmu menjadikan mereka mendapat petunjuk, akan tetapi Allah-lah yang memberi petunjuk (memberi taufiq) siapa yang dikehendaki-Nya. Dan apa saja harta yang baik yang kamu nafkahkan (di jalan allah), maka pahalanya itu untuk kamu sendiri. Dan janganlah kamu membelanjakan sesuatu melainkan karena mencari keridhaan Allah. Dan apa saja harta yang baik yang kamu nafkahkan, niscaya kamu akan diberi pahalanya dengan cukup sedang kamu sedikitpun tidak akan dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,273,'لِلْفُقَرَاۤءِ الَّذِيْنَ اُحْصِرُوْا فِيْ سَبِيْلِ اللّٰهِ لَا يَسْتَطِيْعُوْنَ ضَرْبًا فِى الْاَرْضِۖ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَاۤءَ مِنَ التَّعَفُّفِۚ  تَعْرِفُهُمْ بِسِيْمٰهُمْۚ  لَا يَسْـَٔلُوْنَ النَّاسَ اِلْحَافًا ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ࣖ','(Berinfaqlah) kepada orang-orang fakir yang terikat (oleh jihad) di jalan Allah; mereka tidak dapat (berusaha) di bumi; orang yang tidak tahu menyangka mereka orang kaya karena memelihara diri dari minta-minta. Kamu kenal mereka dengan melihat sifat-sifatnya, mereka tidak meminta kepada orang secara mendesak. Dan apa saja harta yang baik yang kamu nafkahkan (di jalan Allah), maka sesungguhnya Allah Maha Mengatahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,274,'اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِرًّا وَّعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ  وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Orang-orang yang menafkahkan hartanya di malam dan di siang hari secara tersembunyi dan terang-terangan, maka mereka mendapat pahala di sisi Tuhannya. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,275,'اَلَّذِيْنَ يَأْكُلُوْنَ الرِّبٰوا لَا يَقُوْمُوْنَ اِلَّا كَمَا يَقُوْمُ الَّذِيْ يَتَخَبَّطُهُ الشَّيْطٰنُ مِنَ الْمَسِّۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْٓا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۘ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۗ  فَمَنْ جَاۤءَهٗ مَوْعِظَةٌ مِّنْ رَّبِّهٖ فَانْتَهٰى فَلَهٗ مَا سَلَفَۗ وَاَمْرُهٗٓ اِلَى اللّٰهِ ۗ وَمَنْ عَادَ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ','Orang-orang yang makan (mengambil) riba tidak dapat berdiri melainkan seperti berdirinya orang yang kemasukan syaitan lantaran (tekanan) penyakit gila. Keadaan mereka yang demikian itu, adalah disebabkan mereka berkata (berpendapat), sesungguhnya jual beli itu sama dengan riba, padahal Allah telah menghalalkan jual beli dan mengharamkan riba. Orang-orang yang telah sampai kepadanya larangan dari Tuhannya, lalu terus berhenti (dari mengambil riba), maka baginya apa yang telah diambilnya dahulu (sebelum datang larangan); dan urusannya (terserah) kepada Allah. Orang yang kembali (mengambil riba), maka orang itu adalah penghuni-penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,276,'يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِى الصَّدَقٰتِ ۗ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَثِيْمٍ','Allah memusnahkan riba dan menyuburkan sedekah. Dan Allah tidak menyukai setiap orang yang tetap dalam kekafiran, dan selalu berbuat dosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,277,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Sesungguhnya orang-orang yang beriman, mengerjakan amal saleh, mendirikan shalat dan menunaikan zakat, mereka mendapat pahala di sisi Tuhannya. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,278,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوْا مَا بَقِيَ مِنَ الرِّبٰوٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ','Hai orang-orang yang beriman, bertakwalah kepada Allah dan tinggalkan sisa riba (yang belum dipungut) jika kamu orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,279,'فَاِنْ لَّمْ تَفْعَلُوْا فَأْذَنُوْا بِحَرْبٍ مِّنَ اللّٰهِ وَرَسُوْلِهٖۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُءُوْسُ اَمْوَالِكُمْۚ  لَا تَظْلِمُوْنَ وَلَا تُظْلَمُوْنَ','Maka jika kamu tidak mengerjakan (meninggalkan sisa riba), maka ketahuilah, bahwa Allah dan Rasul-Nya akan memerangimu. Dan jika kamu bertaubat (dari pengambilan riba), maka bagimu pokok hartamu; kamu tidak menganiaya dan tidak (pula) dianiaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,280,'وَاِنْ كَانَ ذُوْ عُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍ ۗ وَاَنْ تَصَدَّقُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ','Dan jika (orang yang berhutang itu) dalam kesukaran, maka berilah tangguh sampai dia berkelapangan. Dan menyedekahkan (sebagian atau semua utang) itu, lebih baik bagimu, jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,281,'وَاتَّقُوْا يَوْمًا تُرْجَعُوْنَ فِيْهِ اِلَى اللّٰهِ ۗثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ ࣖ','Dan peliharalah dirimu dari (azab yang terjadi pada) hari yang pada waktu itu kamu semua dikembalikan kepada Allah. Kemudian masing-masing diri diberi balasan yang sempurna terhadap apa yang telah dikerjakannya, sedang mereka sedikitpun tidak dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,282,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُّسَمًّى فَاكْتُبُوْهُۗ وَلْيَكْتُبْ بَّيْنَكُمْ كَاتِبٌۢ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَّكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذِيْ عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ وَلَا يَبْخَسْ مِنْهُ شَيْـًٔاۗ  فَاِنْ كَانَ الَّذِيْ عَلَيْهِ الْحَقُّ سَفِيْهًا اَوْ ضَعِيْفًا اَوْ لَا يَسْتَطِيْعُ اَنْ يُّمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهٗ بِالْعَدْلِۗ وَاسْتَشْهِدُوْا شَهِيْدَيْنِ مِنْ رِّجَالِكُمْۚ فَاِنْ لَّمْ يَكُوْنَا رَجُلَيْنِ فَرَجُلٌ وَّامْرَاَتٰنِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَۤاءِ اَنْ تَضِلَّ اِحْدٰىهُمَا فَتُذَكِّرَ اِحْدٰىهُمَا الْاُخْرٰىۗ وَلَا يَأْبَ الشُّهَدَۤاءُ اِذَا مَا دُعُوْا ۗ وَلَا تَسْـَٔمُوْٓا اَنْ تَكْتُبُوْهُ صَغِيْرًا اَوْ كَبِيْرًا اِلٰٓى اَجَلِهٖۗ ذٰلِكُمْ اَقْسَطُ عِنْدَ اللّٰهِ وَاَقْوَمُ لِلشَّهَادَةِ وَاَدْنٰىٓ اَلَّا تَرْتَابُوْٓا اِلَّآ اَنْ تَكُوْنَ تِجَارَةً حَاضِرَةً تُدِيْرُوْنَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَلَّا تَكْتُبُوْهَاۗ وَاَشْهِدُوْٓا اِذَا تَبَايَعْتُمْ ۖ وَلَا يُضَاۤرَّ كَاتِبٌ وَّلَا شَهِيْدٌ ەۗ وَاِنْ تَفْعَلُوْا فَاِنَّهٗ فُسُوْقٌۢ بِكُمْ ۗ وَاتَّقُوا اللّٰهَ ۗ وَيُعَلِّمُكُمُ اللّٰهُ ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ','Hai orang-orang yang beriman, apabila kamu bermu´amalah tidak secara tunai untuk waktu yang ditentukan, hendaklah kamu menuliskannya. Dan hendaklah seorang penulis di antara kamu menuliskannya dengan benar. Dan janganlah penulis enggan menuliskannya sebagaimana Allah mengajarkannya, meka hendaklah ia menulis, dan hendaklah orang yang berhutang itu mengimlakkan (apa yang akan ditulis itu), dan hendaklah ia bertakwa kepada Allah Tuhannya, dan janganlah ia mengurangi sedikitpun daripada hutangnya. Jika yang berhutang itu orang yang lemah akalnya atau lemah (keadaannya) atau dia sendiri tidak mampu mengimlakkan, maka hendaklah walinya mengimlakkan dengan jujur. Dan persaksikanlah dengan dua orang saksi dari orang-orang lelaki (di antaramu). Jika tak ada dua oang lelaki, maka (boleh) seorang lelaki dan dua orang perempuan dari saksi-saksi yang kamu ridhai, supaya jika seorang lupa maka yang seorang mengingatkannya. Janganlah saksi-saksi itu enggan (memberi keterangan) apabila mereka dipanggil; dan janganlah kamu jemu menulis hutang itu, baik kecil maupun besar sampai batas waktu membayarnya. Yang demikian itu, lebih adil di sisi Allah dan lebih menguatkan persaksian dan lebih dekat kepada tidak (menimbulkan) keraguanmu. (Tulislah mu´amalahmu itu), kecuali jika mu´amalah itu perdagangan tunai yang kamu jalankan di antara kamu, maka tidak ada dosa bagi kamu, (jika) kamu tidak menulisnya. Dan persaksikanlah apabila kamu berjual beli; dan janganlah penulis dan saksi saling sulit menyulitkan. Jika kamu lakukan (yang demikian), maka sesungguhnya hal itu adalah suatu kefasikan pada dirimu. Dan bertakwalah kepada Allah; Allah mengajarmu; dan Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,283,'۞ وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَّلَمْ تَجِدُوْا كَاتِبًا فَرِهٰنٌ مَّقْبُوْضَةٌ  ۗفَاِنْ اَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِى اؤْتُمِنَ اَمَانَتَهٗ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَۗ وَمَنْ يَّكْتُمْهَا فَاِنَّهٗٓ اٰثِمٌ قَلْبُهٗ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ࣖ','Jika kamu dalam perjalanan (dan bermu´amalah tidak secara tunai) sedang kamu tidak memperoleh seorang penulis, maka hendaklah ada barang tanggungan yang dipegang (oleh yang berpiutang). Akan tetapi jika sebagian kamu mempercayai sebagian yang lain, maka hendaklah yang dipercayai itu menunaikan amanatnya (hutangnya) dan hendaklah ia bertakwa kepada Allah Tuhannya; dan janganlah kamu (para saksi) menyembunyikan persaksian. Dan barangsiapa yang menyembunyikannya, maka sesungguhnya ia adalah orang yang berdosa hatinya; dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,284,' لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗ وَاِنْ تُبْدُوْا مَا فِيْٓ اَنْفُسِكُمْ اَوْ تُخْفُوْهُ يُحَاسِبْكُمْ بِهِ اللّٰهُ ۗ فَيَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Kepunyaan Allah-lah segala apa yang ada di langit dan apa yang ada di bumi. Dan jika kamu melahirkan apa yang ada di dalam hatimu atau kamu menyembunyikan, niscaya Allah akan membuat perhitungan dengan kamu tentang perbuatanmu itu. Maka Allah mengampuni siapa yang dikehendaki-Nya dan menyiksa siapa yang dikehendaki-Nya; dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,285,'اٰمَنَ الرَّسُوْلُ بِمَآ اُنْزِلَ اِلَيْهِ مِنْ رَّبِّهٖ وَالْمُؤْمِنُوْنَۗ  كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖۗ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْ رُّسُلِهٖ ۗ وَقَالُوْا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَصِيْرُ','Rasul telah beriman kepada Al Quran yang diturunkan kepadanya dari Tuhannya, demikian pula orang-orang yang beriman. Semuanya beriman kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya dan rasul-rasul-Nya. (Mereka mengatakan): "Kami tidak membeda-bedakan antara seseorangpun (dengan yang lain) dari rasul-rasul-Nya", dan mereka mengatakan: "Kami dengar dan kami taat". (Mereka berdoa): "Ampunilah kami ya Tuhan kami dan kepada Engkaulah tempat kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,2,286,'لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا وُسْعَهَا ۗ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَآ اِنْ نَّسِيْنَآ اَوْ اَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَآ اِصْرًا كَمَا حَمَلْتَهٗ عَلَى الَّذِيْنَ مِنْ قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهٖۚ وَاعْفُ عَنَّاۗ وَاغْفِرْ لَنَاۗ وَارْحَمْنَا ۗ اَنْتَ مَوْلٰىنَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ࣖ','Allah tidak membebani seseorang melainkan sesuai dengan kesanggupannya. Ia mendapat pahala (dari kebajikan) yang diusahakannya dan ia mendapat siksa (dari kejahatan) yang dikerjakannya. (Mereka berdoa): "Ya Tuhan kami, janganlah Engkau hukum kami jika kami lupa atau kami tersalah. Ya Tuhan kami, janganlah Engkau bebankan kepada kami beban yang berat sebagaimana Engkau bebankan kepada orang-orang sebelum kami. Ya Tuhan kami, janganlah Engkau pikulkan kepada kami apa yang tak sanggup kami memikulnya. Beri maaflah kami; ampunilah kami; dan rahmatilah kami. Engkaulah Penolong kami, maka tolonglah kami terhadap kaum yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,1,'الۤمّۤ ','Alif laam miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,2,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ الْحَيُّ الْقَيُّوْمُۗ','Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia. Yang hidup kekal lagi terus menerus mengurus makhluk-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,3,'نَزَّلَ عَلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰىةَ وَالْاِنْجِيْلَۙ','Dia menurunkan Al Kitab (Al Quran) kepadamu dengan sebenarnya; membenarkan kitab yang telah diturunkan sebelumnya dan menurunkan Taurat dan Injil,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,4,'مِنْ قَبْلُ هُدًى لِّلنَّاسِ وَاَنْزَلَ الْفُرْقَانَ ەۗ اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍۗ','sebelum (Al Quran), menjadi petunjuk bagi manusia, dan Dia menurunkan Al Furqaan. Sesungguhnya orang-orang yang kafir terhadap ayat-ayat Allah akan memperoleh siksa yang berat; dan Allah Maha Perkasa lagi mempunyai balasan (siksa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,5,'اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ','Sesungguhnya bagi Allah tidak ada satupun yang tersembunyi di bumi dan tidak (pula) di langit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,6,'هُوَ الَّذِيْ يُصَوِّرُكُمْ فِى الْاَرْحَامِ كَيْفَ يَشَاۤءُ ۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ','Dialah yang membentuk kamu dalam rahim sebagaimana dikehendaki-Nya. Tak ada Tuhan (yang berhak disembah) melainkan Dia, Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,7,'هُوَ الَّذِيْٓ اَنْزَلَ عَلَيْكَ الْكِتٰبَ مِنْهُ اٰيٰتٌ مُّحْكَمٰتٌ هُنَّ اُمُّ الْكِتٰبِ وَاُخَرُ مُتَشٰبِهٰتٌ ۗ فَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ زَيْغٌ فَيَتَّبِعُوْنَ مَا تَشَابَهَ مِنْهُ ابْتِغَاۤءَ الْفِتْنَةِ وَابْتِغَاۤءَ تَأْوِيْلِهٖۚ وَمَا يَعْلَمُ تَأْوِيْلَهٗٓ  اِلَّا اللّٰهُ  ۘوَالرَّاسِخُوْنَ فِى الْعِلْمِ يَقُوْلُوْنَ اٰمَنَّا بِهٖۙ  كُلٌّ مِّنْ عِنْدِ رَبِّنَا ۚ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ','Dialah yang menurunkan Al Kitab (Al Quran) kepada kamu. Di antara (isi)nya ada ayat-ayat yang muhkamaat, itulah pokok-pokok isi Al qur´an dan yang lain (ayat-ayat) mutasyaabihaat. Adapun orang-orang yang dalam hatinya condong kepada kesesatan, maka mereka mengikuti sebahagian ayat-ayat yang mutasyaabihaat daripadanya untuk menimbulkan fitnah untuk mencari-cari ta´wilnya, padahal tidak ada yang mengetahui ta´wilnya melainkan Allah. Dan orang-orang yang mendalam ilmunya berkata: "Kami beriman kepada ayat-ayat yang mutasyaabihaat, semuanya itu dari sisi Tuhan kami". Dan tidak dapat mengambil pelajaran (daripadanya) melainkan orang-orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,8,'رَبَّنَا لَا تُزِغْ قُلُوْبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَّدُنْكَ رَحْمَةً ۚاِنَّكَ اَنْتَ الْوَهَّابُ ','(Mereka berdoa): "Ya Tuhan kami, janganlah Engkau jadikan hati kami condong kepada kesesatan sesudah Engkau beri petunjuk kepada kami, dan karuniakanlah kepada kami rahmat dari sisi Engkau; karena sesungguhnya Engkau-lah Maha Pemberi (karunia)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,9,'رَبَّنَآ اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَّا رَيْبَ فِيْهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ ࣖ','"Ya Tuhan kami, sesungguhnya Engkau mengumpulkan manusia untuk (menerima pembalasan pada) hari yang tak ada keraguan padanya". Sesungguhnya Allah tidak menyalahi janji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,10,'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗوَاُولٰۤىِٕكَ هُمْ وَقُوْدُ النَّارِۗ','Sesungguhnya orang-orang yang kafir, harta benda dan anak-anak mereka, sedikitpun tidak dapat menolak (siksa) Allah dari mereka. Dan mereka itu adalah bahan bakar api neraka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,11,'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَذَّبُوْا بِاٰيٰتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗ وَاللّٰهُ شَدِيْدُ الْعِقَابِ','(keadaan mereka) adalah sebagai keadaan kaum Fir´aun dan orang-orang yang sebelumnya; mereka mendustakan ayat-ayat Kami; karena itu Allah menyiksa mereka disebabkan dosa-dosa mereka. Dan Allah sangat keras siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,12,'قُلْ لِّلَّذِيْنَ كَفَرُوْا سَتُغْلَبُوْنَ وَتُحْشَرُوْنَ اِلٰى جَهَنَّمَ ۗ وَبِئْسَ الْمِهَادُ','Katakanlah kepada orang-orang yang kafir: "Kamu pasti akan dikalahkan (di dunia ini) dan akan digiring ke dalam neraka Jahannam. Dan itulah tempat yang seburuk-buruknya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,13,'قَدْ كَانَ لَكُمْ اٰيَةٌ فِيْ فِئَتَيْنِ الْتَقَتَا ۗفِئَةٌ تُقَاتِلُ فِيْ سَبِيْلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَّرَوْنَهُمْ مِّثْلَيْهِمْ رَأْيَ الْعَيْنِ ۗوَاللّٰهُ يُؤَيِّدُ بِنَصْرِهٖ مَنْ يَّشَاۤءُ ۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ','Sesungguhnya telah ada tanda bagi kamu pada dua golongan yang telah bertemu (bertempur). Segolongan berperang di jalan Allah dan (segolongan) yang lain kafir yang dengan mata kepala melihat (seakan-akan) orang-orang muslimin dua kali jumlah mereka. Allah menguatkan dengan bantuan-Nya siapa yang dikehendaki-Nya. Sesungguhnya pada yang demikian itu terdapat pelajaran bagi orang-orang yang mempunyai mata hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,14,'زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوٰتِ مِنَ النِّسَاۤءِ وَالْبَنِيْنَ وَالْقَنَاطِيْرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِ ۗ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَاللّٰهُ عِنْدَهٗ حُسْنُ الْمَاٰبِ','Dijadikan indah pada (pandangan) manusia kecintaan kepada apa-apa yang diingini, yaitu: wanita-wanita, anak-anak, harta yang banyak dari jenis emas, perak, kuda pilihan, binatang-binatang ternak dan sawah ladang. Itulah kesenangan hidup di dunia, dan di sisi Allah-lah tempat kembali yang baik (surga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,15,'۞ قُلْ اَؤُنَبِّئُكُمْ بِخَيْرٍ مِّنْ ذٰلِكُمْ ۗ لِلَّذِيْنَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَاَزْوَاجٌ مُّطَهَّرَةٌ وَّرِضْوَانٌ مِّنَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِۚ','Katakanlah: "Inginkah aku kabarkan kepadamu apa yang lebih baik dari yang demikian itu?". Untuk orang-orang yang bertakwa (kepada Allah), pada sisi Tuhan mereka ada surga yang mengalir dibawahnya sungai-sungai; mereka kekal didalamnya. Dan (mereka dikaruniai) isteri-isteri yang disucikan serta keridhaan Allah. Dan Allah Maha Melihat akan hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,16,'اَلَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اِنَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَقِنَا عَذَابَ النَّارِۚ','(Yaitu) orang-orang yang berdoa: Ya Tuhan kami, sesungguhnya kami telah beriman, maka ampunilah segala dosa kami dan peliharalah kami dari siksa neraka,"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,17,'اَلصّٰبِرِيْنَ وَالصّٰدِقِيْنَ وَالْقٰنِتِيْنَ وَالْمُنْفِقِيْنَ وَالْمُسْتَغْفِرِيْنَ بِالْاَسْحَارِ ','(yaitu) orang-orang yang sabar, yang benar, yang tetap taat, yang menafkahkan hartanya (di jalan Allah), dan yang memohon ampun di waktu sahur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,18,'شَهِدَ اللّٰهُ اَنَّهٗ لَآ اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰۤىِٕكَةُ وَاُولُوا الْعِلْمِ قَاۤىِٕمًاۢ بِالْقِسْطِۗ  لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ','Allah menyatakan bahwasanya tidak ada Tuhan melainkan Dia (yang berhak disembah), Yang menegakkan keadilan. Para Malaikat dan orang-orang yang berilmu (juga menyatakan yang demikian itu). Tak ada Tuhan melainkan Dia (yang berhak disembah), Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,19,'اِنَّ الدِّيْنَ عِنْدَ اللّٰهِ الْاِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗوَمَنْ يَّكْفُرْ بِاٰيٰتِ اللّٰهِ فَاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ','Sesungguhnya agama (yang diridhai) disisi Allah hanyalah Islam. Tiada berselisih orang-orang yang telah diberi Al Kitab kecuali sesudah datang pengetahuan kepada mereka, karena kedengkian (yang ada) di antara mereka. Barangsiapa yang kafir terhadap ayat-ayat Allah maka sesungguhnya Allah sangat cepat hisab-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,20,'فَاِنْ حَاۤجُّوْكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّبَعَنِ ۗوَقُلْ لِّلَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْاُمِّيّٖنَ ءَاَسْلَمْتُمْ ۗ فَاِنْ اَسْلَمُوْا فَقَدِ اهْتَدَوْا ۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِ ࣖ','Kemudian jika mereka mendebat kamu (tentang kebenaran Islam), maka katakanlah: "Aku menyerahkan diriku kepada Allah dan (demikian pula) orang-orang yang mengikutiku". Dan katakanlah kepada orang-orang yang telah diberi Al Kitab dan kepada orang-orang yang ummi: "Apakah kamu (mau) masuk Islam". Jika mereka masuk Islam, sesungguhnya mereka telah mendapat petunjuk, dan jika mereka berpaling, maka kewajiban kamu hanyalah menyampaikan (ayat-ayat Allah). Dan Allah Maha Melihat akan hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,21,'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِحَقٍّۖ وَّيَقْتُلُوْنَ الَّذِيْنَ يَأْمُرُوْنَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍ','Sesungguhnya orang-orang yang kafir kepada ayat-ayat Allah dan membunuh para nabi yang memamg tak dibenarkan dan membunuh orang-orang yang menyuruh manusia berbuat adil, maka gembirakanlah mereka bahwa mereka akan menerima siksa yg pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,22,'اُولٰۤىِٕكَ الَّذِيْنَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ','Mereka itu adalah orang-orang yang lenyap (pahala) amal-amalnya di dunia dan akhirat, dan mereka sekali-kali tidak memperoleh penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,23,'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُدْعَوْنَ اِلٰى كِتٰبِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ وَهُمْ مُّعْرِضُوْنَ','Tidakkah kamu memperhatikan orang-orang yang telah diberi bahagian yaitu Al Kitab (Taurat), mereka diseru kepada kitab Allah supaya kitab itu menetapkan hukum diantara mereka; kemudian sebahagian dari mereka berpaling, dan mereka selalu membelakangi (kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,24,'ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدٰتٍ ۖ وَّغَرَّهُمْ فِيْ دِيْنِهِمْ مَّا كَانُوْا يَفْتَرُوْنَ','Hal itu adalah karena mereka mengaku: "Kami tidak akan disentuh oleh api neraka kecuali beberapa hari yang dapat dihitung". Mereka diperdayakan dalam agama mereka oleh apa yang selalu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,25,'فَكَيْفَ اِذَا جَمَعْنٰهُمْ لِيَوْمٍ لَّا رَيْبَ فِيْهِۗ وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ','Bagaimanakah nanti apabila mereka Kami kumpulkan di hari (kiamat) yang tidak ada keraguan tentang adanya. Dan disempurnakan kepada tiap-tiap diri balasan apa yang diusahakannya sedang mereka tidak dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,26,'قُلِ اللهم مٰلِكَ الْمُلْكِ تُؤْتِى الْمُلْكَ مَنْ تَشَاۤءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاۤءُۖ وَتُعِزُّ مَنْ تَشَاۤءُ وَتُذِلُّ مَنْ تَشَاۤءُ ۗ بِيَدِكَ الْخَيْرُ ۗ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Katakanlah: "Wahai Tuhan Yang mempunyai kerajaan, Engkau berikan kerajaan kepada orang yang Engkau kehendaki dan Engkau cabut kerajaan dari orang yang Engkau kehendaki. Engkau muliakan orang yang Engkau kehendaki dan Engkau hinakan orang yang Engkau kehendaki. Di tangan Engkaulah segala kebajikan. Sesungguhnya Engkau Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,27,'تُوْلِجُ الَّيْلَ فِى النَّهَارِ وَتُوْلِجُ النَّهَارَ فِى الَّيْلِ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَتَرْزُقُ مَنْ تَشَاۤءُ بِغَيْرِ حِسَابٍ','Engkau masukkan malam ke dalam siang dan Engkau masukkan siang ke dalam malam. Engkau keluarkan yang hidup dari yang mati, dan Engkau keluarkan yang mati dari yang hidup. Dan Engkau beri rezeki siapa yang Engkau kehendaki tanpa hisab (batas)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,28,'لَا يَتَّخِذِ الْمُؤْمِنُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ فِيْ شَيْءٍ اِلَّآ اَنْ تَتَّقُوْا مِنْهُمْ تُقٰىةً ۗ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗ وَاِلَى اللّٰهِ الْمَصِيْرُ','Janganlah orang-orang mukmin mengambil orang-orang kafir menjadi wali dengan meninggalkan orang-orang mukmin. Barang siapa berbuat demikian, niscaya lepaslah ia dari pertolongan Allah, kecuali karena (siasat) memelihara diri dari sesuatu yang ditakuti dari mereka. Dan Allah memperingatkan kamu terhadap diri (siksa)-Nya. Dan hanya kepada Allah kembali(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,29,'قُلْ اِنْ تُخْفُوْا مَا فِيْ صُدُوْرِكُمْ اَوْ تُبْدُوْهُ يَعْلَمْهُ اللّٰهُ ۗوَيَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ  عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Katakanlah: "Jika kamu menyembunyikan apa yang ada dalam hatimu atau kamu melahirkannya, pasti Allah mengetahui". Allah mengetahui apa-apa yang ada di langit dan apa-apa yang ada di bumi. Dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,30,'يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَّا عَمِلَتْ مِنْ خَيْرٍ مُّحْضَرًا  ۛوَمَا عَمِلَتْ مِنْ سُوْۤءٍ ۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهٗٓ اَمَدًاۢ بَعِيْدًا ۗوَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ ࣖ','Pada hari ketika tiap-tiap diri mendapati segala kebajikan dihadapkan (dimukanya), begitu (juga) kejahatan yang telah dikerjakannya; ia ingin kalau kiranya antara ia dengan hari itu ada masa yang jauh; dan Allah memperingatkan kamu terhadap siksa-Nya. Dan Allah sangat Penyayang kepada hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,31,'قُلْ اِنْ كُنْتُمْ تُحِبُّوْنَ اللّٰهَ فَاتَّبِعُوْنِيْ يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ','Katakanlah: "Jika kamu (benar-benar) mencintai Allah, ikutilah aku, niscaya Allah mengasihi dan mengampuni dosa-dosamu". Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,32,'قُلْ اَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ ۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكٰفِرِيْنَ','Katakanlah: "Taatilah Allah dan Rasul-Nya; jika kamu berpaling, maka sesungguhnya Allah tidak menyukai orang-orang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,33,'۞ اِنَّ اللّٰهَ اصْطَفٰىٓ اٰدَمَ وَنُوْحًا وَّاٰلَ اِبْرٰهِيْمَ وَاٰلَ عِمْرَانَ عَلَى الْعٰلَمِيْنَۙ','Sesungguhnya Allah telah memilih Adam, Nuh, keluarga Ibrahim dan keluarga ´Imran melebihi segala umat (di masa mereka masing-masing),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,34,'ذُرِّيَّةً ۢ بَعْضُهَا مِنْۢ بَعْضٍۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۚ','(sebagai) satu keturunan yang sebagiannya (turunan) dari yang lain. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,35,'اِذْ قَالَتِ امْرَاَتُ عِمْرَانَ رَبِّ اِنِّيْ نَذَرْتُ لَكَ مَا فِيْ بَطْنِيْ مُحَرَّرًا فَتَقَبَّلْ مِنِّيْ ۚ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ','(Ingatlah), ketika isteri ´Imran berkata: "Ya Tuhanku, sesungguhnya aku menazarkan kepada Engkau anak yang dalam kandunganku menjadi hamba yang saleh dan berkhidmat (di Baitul Maqdis). Karena itu terimalah (nazar) itu dari padaku. Sesungguhnya Engkaulah Yang Maha Mendengar lagi Maha Mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,36,'فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنِّيْ وَضَعْتُهَآ اُنْثٰىۗ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۗ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰى ۚ وَاِنِّيْ سَمَّيْتُهَا مَرْيَمَ وَاِنِّيْٓ اُعِيْذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطٰنِ الرَّجِيْمِ','Maka tatkala isteri ´Imran melahirkan anaknya, diapun berkata: "Ya Tuhanku, sesunguhnya aku melahirkannya seorang anak perempuan; dan Allah lebih mengetahui apa yang dilahirkannya itu; dan anak laki-laki tidaklah seperti anak perempuan. Sesungguhnya aku telah menamai dia Maryam dan aku mohon perlindungan untuknya serta anak-anak keturunannya kepada (pemeliharaan) Engkau daripada syaitan yang terkutuk".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,37,'فَتَقَبَّلَهَا رَبُّهَا بِقَبُوْلٍ حَسَنٍ وَّاَنْۢبَتَهَا نَبَاتًا حَسَنًاۖ وَّكَفَّلَهَا زَكَرِيَّا ۗ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقًا ۚ قَالَ يٰمَرْيَمُ اَنّٰى لَكِ هٰذَا ۗ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِ ۗ اِنَّ اللّٰهَ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ','Maka Tuhannya menerimanya (sebagai nazar) dengan penerimaan yang baik, dan mendidiknya dengan pendidikan yang baik dan Allah menjadikan Zakariya pemeliharanya. Setiap Zakariya masuk untuk menemui Maryam di mihrab, ia dapati makanan di sisinya. Zakariya berkata: "Hai Maryam dari mana kamu memperoleh (makanan) ini?" Maryam menjawab: "Makanan itu dari sisi Allah". Sesungguhnya Allah memberi rezeki kepada siapa yang dikehendaki-Nya tanpa hisab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,38,'هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهٗ ۚ قَالَ رَبِّ هَبْ لِيْ مِنْ لَّدُنْكَ ذُرِّيَّةً طَيِّبَةً ۚ اِنَّكَ سَمِيْعُ الدُّعَاۤءِ','Di sanalah Zakariya mendoa kepada Tuhannya seraya berkata: "Ya Tuhanku, berilah aku dari sisi Engkau seorang anak yang baik. Sesungguhnya Engkau Maha Pendengar doa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,39,'فَنَادَتْهُ الْمَلٰۤىِٕكَةُ وَهُوَ قَاۤىِٕمٌ يُّصَلِّيْ فِى الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقًاۢ بِكَلِمَةٍ مِّنَ اللّٰهِ وَسَيِّدًا وَّحَصُوْرًا وَّنَبِيًّا مِّنَ الصّٰلِحِيْنَ','Kemudian Malaikat (Jibril) memanggil Zakariya, sedang ia tengah berdiri melakukan shalat di mihrab (katanya): "Sesungguhnya Allah menggembirakan kamu dengan kelahiran (seorang puteramu) Yahya, yang membenarkan kalimat (yang datang) dari Allah, menjadi ikutan, menahan diri (dari hawa nafsu) dan seorang Nabi termasuk keturunan orang-orang saleh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,40,'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَتِيْ عَاقِرٌ ۗ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَاۤءُ','Zakariya berkata: "Ya Tuhanku, bagaimana aku bisa mendapat anak sedang aku telah sangat tua dan isteriku pun seorang yang mandul?". Berfirman Allah: "Demikianlah, Allah berbuat apa yang dikehendaki-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,41,'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزًا ۗ وَاذْكُرْ رَّبَّكَ كَثِيْرًا وَّسَبِّحْ بِالْعَشِيِّ وَالْاِبْكَارِ ࣖ','Berkata Zakariya: "Berilah aku suatu tanda (bahwa isteriku telah mengandung)". Allah berfirman: "Tandanya bagimu, kamu tidak dapat berkata-kata dengan manusia selama tiga hari, kecuali dengan isyarat. Dan sebutlah (nama) Tuhanmu sebanyak-banyaknya serta bertasbihlah di waktu petang dan pagi hari".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,42,'وَاِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰىكِ وَطَهَّرَكِ وَاصْطَفٰىكِ عَلٰى نِسَاۤءِ الْعٰلَمِيْنَ','Dan (ingatlah) ketika Malaikat (Jibril) berkata: "Hai Maryam, sesungguhnya Allah telah memilih kamu, mensucikan kamu dan melebihkan kamu atas segala wanita di dunia (yang semasa dengan kamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,43,'يٰمَرْيَمُ اقْنُتِيْ لِرَبِّكِ وَاسْجُدِيْ وَارْكَعِيْ مَعَ الرَّاكِعِيْنَ','Hai Maryam, taatlah kepada Tuhanmu, sujud dan ruku´lah bersama orang-orang yang ruku´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,44,'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَ ۗوَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُوْنَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُوْنَ','Yang demikian itu adalah sebagian dari berita-berita ghaib yang Kami wahyukan kepada kamu (ya Muhammad); padahal kamu tidak hadir beserta mereka, ketika mereka melemparkan anak-anak panah mereka (untuk mengundi) siapa di antara mereka yang akan memelihara Maryam. Dan kamu tidak hadir di sisi mereka ketika mereka bersengketa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,45,'اِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِّنْهُۖ اسْمُهُ الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ وَجِيْهًا فِى الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّبِيْنَۙ','(Ingatlah), ketika Malaikat berkata: "Hai Maryam, seungguhnya Allah menggembirakan kamu (dengan kelahiran seorang putera yang diciptakan) dengan kalimat (yang datang) daripada-Nya, namanya Al Masih Isa putera Maryam, seorang terkemuka di dunia dan di akhirat dan termasuk orang-orang yang didekatkan (kepada Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,46,'وَيُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا وَّمِنَ الصّٰلِحِيْنَ','dan dia berbicara dengan manusia dalam buaian dan ketika sudah dewasa dan dia adalah termasuk orang-orang yang saleh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,47,'قَالَتْ رَبِّ اَنّٰى يَكُوْنُ لِيْ وَلَدٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ ۗ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَاۤءُ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ','Maryam berkata: "Ya Tuhanku, betapa mungkin aku mempunyai anak, padahal aku belum pernah disentuh oleh seorang laki-lakipun". Allah berfirman (dengan perantaraan Jibril): "Demikianlah Allah menciptakan apa yang dikehendaki-Nya. Apabila Allah berkehendak menetapkan sesuatu, maka Allah hanya cukup berkata kepadanya: "Jadilah", lalu jadilah dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,48,'وَيُعَلِّمُهُ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَۚ','Dan Allah akan mengajarkan kepadanya Al Kitab, Hikmah, Taurat dan Injil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,49,'وَرَسُوْلًا اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ اَنِّيْ قَدْ جِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْ ۙاَنِّيْٓ اَخْلُقُ لَكُمْ مِّنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ فِيْهِ فَيَكُوْنُ طَيْرًاۢ بِاِذْنِ اللّٰهِ ۚوَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُحْيِ الْمَوْتٰى بِاِذْنِ اللّٰهِ ۚوَاُنَبِّئُكُمْ بِمَا تَأْكُلُوْنَ وَمَا تَدَّخِرُوْنَ ۙفِيْ بُيُوْتِكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ','Dan (sebagai) Rasul kepada Bani Israil (yang berkata kepada mereka): "Sesungguhnya aku telah datang kepadamu dengan membawa sesuatu tanda (mukjizat) dari Tuhanmu, yaitu aku membuat untuk kamu dari tanah berbentuk burung; kemudian aku meniupnya, maka ia menjadi seekor burung dengan seizin Allah; dan aku menyembuhkan orang yang buta sejak dari lahirnya dan orang yang berpenyakit sopak; dan aku menghidupkan orang mati dengan seizin Allah; dan aku kabarkan kepadamu apa yang kamu makan dan apa yang kamu simpan di rumahmu. Sesungguhnya pada yang demikian itu adalah suatu tanda (kebenaran kerasulanku) bagimu, jika kamu sungguh-sungguh beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,50,'وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذِيْ حُرِّمَ عَلَيْكُمْ  وَجِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْۗ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ','Dan (aku datang kepadamu) membenarkan Taurat yang datang sebelumku, dan untuk menghalalkan bagimu sebagian yang telah diharamkan untukmu, dan aku datang kepadamu dengan membawa suatu tanda (mukjizat) daripada Tuhanmu. Karena itu bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,51,'اِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ','Sesungguhnya Allah, Tuhanku dan Tuhanmu, karena itu sembahlah Dia. Inilah jalan yang lurus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,52,'۞ فَلَمَّآ اَحَسَّ عِيْسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗ قَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ ۚ اٰمَنَّا بِاللّٰهِ ۚ وَاشْهَدْ بِاَنَّا مُسْلِمُوْنَ','Maka tatkala Isa mengetahui keingkaran mereka (Bani lsrail) berkatalah dia: "Siapakah yang akan menjadi penolong-penolongku untuk (menegakkan agama) Allah?" Para hawariyyin (sahabat-sahabat setia) menjawab: "Kamilah penolong-penolong (agama) Allah, kami beriman kepada Allah; dan saksikanlah bahwa sesungguhnya kami adalah orang-orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,53,'رَبَّنَآ اٰمَنَّا بِمَآ اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُوْلَ فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ','Ya Tuhan kami, kami telah beriman kepada apa yang telah Engkau turunkan dan telah kami ikuti rasul, karena itu masukanlah kami ke dalam golongan orang-orang yang menjadi saksi (tentang keesaan Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,54,'وَمَكَرُوْا وَمَكَرَ اللّٰهُ  ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ ࣖ','Orang-orang kafir itu membuat tipu daya, dan Allah membalas tipu daya mereka itu. Dan Allah sebaik-baik pembalas tipu daya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,55,'اِذْ قَالَ اللّٰهُ يٰعِيْسٰٓى اِنِّيْ مُتَوَفِّيْكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِيْنَ كَفَرُوْا وَجَاعِلُ الَّذِيْنَ اتَّبَعُوْكَ فَوْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى يَوْمِ الْقِيٰمَةِ ۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ','(Ingatlah), ketika Allah berfirman: "Hai Isa, sesungguhnya Aku akan menyampaikan kamu kepada akhir ajalmu dan mengangkat kamu kepada-Ku serta membersihkan kamu dari orang-orang yang kafir, dan menjadikan orang-orang yang mengikuti kamu di atas orang-orang yang kafir hingga hari kiamat. Kemudian hanya kepada Akulah kembalimu, lalu Aku memutuskan diantaramu tentang hal-hal yang selalu kamu berselisih padanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,56,'فَاَمَّا الَّذِيْنَ كَفَرُوْا فَاُعَذِّبُهُمْ عَذَابًا شَدِيْدًا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ','Adapun orang-orang yang kafir, maka akan Ku-siksa mereka dengan siksa yang sangat keras di dunia dan di akhirat, dan mereka tidak memperoleh penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,57,'وَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ ۗ وَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَ','Adapun orang-orang yang beriman dan mengerjakan amalan-amalan yang saleh, maka Allah akan memberikan kepada mereka dengan sempurna pahala amalan-amalan mereka; dan Allah tidak menyukai orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,58,'ذٰلِكَ نَتْلُوْهُ عَلَيْكَ مِنَ الْاٰيٰتِ وَالذِّكْرِ الْحَكِيْمِ ','Demikianlah (kisah Isa), Kami membacakannya kepada kamu sebagian dari bukti-bukti (kerasulannya) dan (membacakan) Al Quran yang penuh hikmah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,59,'اِنَّ مَثَلَ عِيْسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَ ۗ خَلَقَهٗ مِنْ تُرَابٍ ثُمَّ قَالَ لَهٗ كُنْ فَيَكُوْنُ','Sesungguhnya misal (penciptaan) Isa di sisi AllAh, adalah seperti (penciptaan) Adam. Allah menciptakan Adam dari tanah, kemudian Allah berfirman kepadanya: "Jadilah" (seorang manusia), maka jadilah dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,60,'اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُنْ مِّنَ الْمُمْتَرِيْنَ','(Apa yang telah Kami ceritakan itu), itulah yang benar, yang datang dari Tuhanmu, karena itu janganlah kamu termasuk orang-orang yang ragu-ragu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,61,'فَمَنْ حَاۤجَّكَ فِيْهِ مِنْۢ بَعْدِ مَا جَاۤءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَاۤءَنَا وَاَبْنَاۤءَكُمْ وَنِسَاۤءَنَا وَنِسَاۤءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْۗ  ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَّعْنَتَ اللّٰهِ عَلَى الْكٰذِبِيْنَ','Siapa yang membantahmu tentang kisah Isa sesudah datang ilmu (yang meyakinkan kamu), maka katakanlah (kepadanya): "Marilah kita memanggil anak-anak kami dan anak-anak kamu, isteri-isteri kami dan isteri-isteri kamu, diri kami dan diri kamu; kemudian marilah kita bermubahalah kepada Allah dan kita minta supaya laknat Allah ditimpakan kepada orang-orang yang dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,62,'اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ ۗوَاِنَّ اللّٰهَ لَهُوَ الْعَزِيْزُ الْحَكِيْمُ','Sesungguhnya ini adalah kisah yang benar, dan tak ada Tuhan (yang berhak disembah) selain Allah; dan sesungguhnya Allah, Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,63,'فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَلِيْمٌ ۢبِالْمُفْسِدِيْنَ ࣖ ','Kemudian jika mereka berpaling (dari kebenaran), maka sesunguhnya Allah Maha Mengetahui orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,64,'قُلْ يٰٓاَهْلَ الْكِتٰبِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَاۤءٍۢ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِهٖ شَيْـًٔا وَّلَا يَتَّخِذَ بَعْضُنَا بَعْضًا اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَقُوْلُوا اشْهَدُوْا بِاَنَّا مُسْلِمُوْنَ','Katakanlah: "Hai Ahli Kitab, marilah (berpegang) kepada suatu kalimat (ketetapan) yang tidak ada perselisihan antara kami dan kamu, bahwa tidak kita sembah kecuali Allah dan tidak kita persekutukan Dia dengan sesuatupun dan tidak (pula) sebagian kita menjadikan sebagian yang lain sebagai tuhan selain Allah". Jika mereka berpaling maka katakanlah kepada mereka: "Saksikanlah, bahwa kami adalah orang-orang yang berserah diri (kepada Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,65,'يٰٓاَهْلَ الْكِتٰبِ لِمَ تُحَاۤجُّوْنَ فِيْٓ اِبْرٰهِيْمَ وَمَآ اُنْزِلَتِ التَّوْرٰىةُ وَالْاِنْجِيْلُ اِلَّا مِنْۢ بَعْدِهٖۗ  اَفَلَا تَعْقِلُوْنَ','Hai Ahli Kitab, mengapa kamu bantah membantah tentang hal Ibrahim, padahal Taurat dan Injil tidak diturunkan melainkan sesudah Ibrahim. Apakah kamu tidak berpikir?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,66,'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ حَاجَجْتُمْ فِيْمَا لَكُمْ بِهٖ عِلْمٌ فَلِمَ تُحَاۤجُّوْنَ فِيْمَا لَيْسَ لَكُمْ بِهٖ عِلْمٌ ۗ وَاللّٰهُ يَعْلَمُ واَنْتُمْ لَا تَعْلَمُوْنَ','Beginilah kamu, kamu ini (sewajarnya) bantah membantah tentang hal yang kamu ketahui, maka kenapa kamu bantah membantah tentang hal yang tidak kamu ketahui? Allah mengetahui sedang kamu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,67,'مَاكَانَ اِبْرٰهِيْمُ يَهُوْدِيًّا وَّلَا نَصْرَانِيًّا وَّلٰكِنْ كَانَ حَنِيْفًا مُّسْلِمًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ','Ibrahim bukan seorang Yahudi dan bukan (pula) seorang Nasrani, akan tetapi dia adalah seorang yang lurus lagi berserah diri (kepada Allah) dan sekali-kali bukanlah dia termasuk golongan orang-orang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,68,'اِنَّ اَوْلَى النَّاسِ بِاِبْرٰهِيْمَ لَلَّذِيْنَ اتَّبَعُوْهُ وَهٰذَا النَّبِيُّ وَالَّذِيْنَ اٰمَنُوْا ۗ وَاللّٰهُ وَلِيُّ الْمُؤْمِنِيْنَ','Sesungguhnya orang yang paling dekat kepada Ibrahim ialah orang-orang yang mengikutinya dan Nabi ini (Muhammad), beserta orang-orang yang beriman (kepada Muhammad), dan Allah adalah Pelindung semua orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,69,'وَدَّتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يُضِلُّوْنَكُمْۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ','Segolongan dari Ahli Kitab ingin menyesatkan kamu, padahal mereka (sebenarnya) tidak menyesatkan melainkan dirinya sendiri, dan mereka tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,70,'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُوْنَ','Hai Ahli Kitab, mengapa kamu mengingkari ayat-ayat Allah, padahal kamu mengetahui (kebenarannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,71,'يٰٓاَهْلَ الْكِتٰبِ لِمَ تَلْبِسُوْنَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوْنَ الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ ࣖ','Hai Ahli Kitab, mengapa kamu mencampur adukkan yang haq dengan yang bathil, dan menyembunyikan kebenaran, padahal kamu mengetahuinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,72,'وَقَالَتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ اٰمِنُوْا بِالَّذِيْٓ اُنْزِلَ عَلَى الَّذِيْنَ اٰمَنُوْا وَجْهَ النَّهَارِ وَاكْفُرُوْٓا اٰخِرَهٗ لَعَلَّهُمْ يَرْجِعُوْنَۚ','Segolongan (lain) dari Ahli Kitab berkata (kepada sesamanya): "Perlihatkanlah (seolah-olah) kamu beriman kepada apa yang diturunkan kepada orang-orang beriman (sahabat-sahabat Rasul) pada permulaan siang dan ingkarilah ia pada akhirnya, supaya mereka (orang-orang mukmin) kembali (kepada kekafiran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,73,'وَلَا تُؤْمِنُوْٓا اِلَّا لِمَنْ تَبِعَ دِيْنَكُمْ ۗ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِ ۙ اَنْ يُّؤْتٰىٓ اَحَدٌ مِّثْلَ مَآ اُوْتِيْتُمْ اَوْ يُحَاۤجُّوْكُمْ عِنْدَ رَبِّكُمْ ۗ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِ ۚ يُؤْتِيْهِ مَنْ يَّشَاۤءُ  ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۚ','Dan janganlah kamu percaya melainkan kepada orang yang mengikuti agamamu. Katakanlah: "Sesungguhnya petunjuk (yang harus diikuti) ialah petunjuk Allah, dan (janganlah kamu percaya) bahwa akan diberikan kepada seseorang seperti apa yang diberikan kepadamu, dan (jangan pula kamu percaya) bahwa mereka akan mengalahkan hujjahmu di sisi Tuhanmu". Katakanlah: "Sesungguhnya karunia itu di tangan Allah, Allah memberikan karunia-Nya kepada siapa yang dikehendaki-Nya; dan Allah Maha Luas karunia-Nya) lagi Maha Mengetahui";')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,74,'يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ  ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ','Allah menentukan rahmat-Nya (kenabian) kepada siapa yang dikehendaki-Nya dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,75,'۞ وَمِنْ اَهْلِ الْكِتٰبِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُّؤَدِّهٖٓ اِلَيْكَۚ وَمِنْهُمْ مَّنْ اِنْ تَأْمَنْهُ بِدِيْنَارٍ لَّا يُؤَدِّهٖٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَاۤىِٕمًا ۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَيْسَ عَلَيْنَا فِى الْاُمِّيّٖنَ سَبِيْلٌۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ ','Di antara Ahli kitab ada orang yang jika kamu mempercayakan kepadanya harta yang banyak, dikembalikannya kepadamu; dan di antara mereka ada orang yang jika kamu mempercayakan kepadanya satu dinar, tidak dikembalikannya kepadamu kecuali jika kamu selalu menagihnya. Yang demikian itu lantaran mereka mengatakan: "tidak ada dosa bagi kami terhadap orang-orang ummi. Mereka berkata dusta terhadap Allah, padahal mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,76,'بَلٰى مَنْ اَوْفٰى بِعَهْدِهٖ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ','(Bukan demikian), sebenarnya siapa yang menepati janji (yang dibuat)nya dan bertakwa, maka sesungguhnya Allah menyukai orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,77,'اِنَّ الَّذِيْنَ يَشْتَرُوْنَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَنًا قَلِيْلًا اُولٰۤىِٕكَ لَا خَلَاقَ لَهُمْ فِى الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','Sesungguhnya orang-orang yang menukar janji (nya dengan) Allah dan sumpah-sumpah mereka dengan harga yang sedikit, mereka itu tidak mendapat bahagian (pahala) di akhirat, dan Allah tidak akan berkata-kata dengan mereka dan tidak akan melihat kepada mereka pada hari kiamat dan tidak (pula) akan mensucikan mereka. Bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,78,'وَاِنَّ مِنْهُمْ لَفَرِيْقًا يَّلْوٗنَ اَلْسِنَتَهُمْ بِالْكِتٰبِ لِتَحْسَبُوْهُ مِنَ الْكِتٰبِ وَمَا هُوَ مِنَ الْكِتٰبِۚ وَيَقُوْلُوْنَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِ ۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ','Sesungguhnya diantara mereka ada segolongan yang memutar-mutar lidahnya membaca Al Kitab, supaya kamu menyangka yang dibacanya itu sebagian dari Al Kitab, padahal ia bukan dari Al Kitab dan mereka mengatakan: "Ia (yang dibaca itu datang) dari sisi Allah", padahal ia bukan dari sisi Allah. Mereka berkata dusta terhadap Allah sedang mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,79,'مَا كَانَ لِبَشَرٍ اَنْ يُّؤْتِيَهُ اللّٰهُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُوْلَ لِلنَّاسِ كُوْنُوْا عِبَادًا لِّيْ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ كُوْنُوْا رَبَّانِيّٖنَ بِمَا كُنْتُمْ تُعَلِّمُوْنَ الْكِتٰبَ وَبِمَا كُنْتُمْ تَدْرُسُوْنَ ۙ','Tidak wajar bagi seseorang manusia yang Allah berikan kepadanya Al Kitab, hikmah dan kenabian, lalu dia berkata kepada manusia: "Hendaklah kamu menjadi penyembah-penyembahku bukan penyembah Allah". Akan tetapi (dia berkata): "Hendaklah kamu menjadi orang-orang rabbani, karena kamu selalu mengajarkan Al Kitab dan disebabkan kamu tetap mempelajarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,80,'وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰۤىِٕكَةَ وَالنَّبِيّٖنَ اَرْبَابًا ۗ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُّسْلِمُوْنَ ࣖ','dan (tidak wajar pula baginya) menyuruhmu menjadikan malaikat dan para nabi sebagai tuhan. Apakah (patut) dia menyuruhmu berbuat kekafiran di waktu kamu sudah (menganut agama) Islam?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,81,'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ النَّبِيّٖنَ لَمَآ اٰتَيْتُكُمْ مِّنْ كِتٰبٍ وَّحِكْمَةٍ ثُمَّ جَاۤءَكُمْ رَسُوْلٌ مُّصَدِّقٌ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهٖ وَلَتَنْصُرُنَّهٗ ۗ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْرِيْ ۗ قَالُوْٓا اَقْرَرْنَا ۗ قَالَ فَاشْهَدُوْا وَاَنَا۠ مَعَكُمْ مِّنَ الشّٰهِدِيْنَ','Dan (ingatlah), ketika Allah mengambil perjanjian dari para nabi: "Sungguh, apa saja yang Aku berikan kepadamu berupa kitab dan hikmah kemudian datang kepadamu seorang rasul yang membenarkan apa yang ada padamu, niscaya kamu akan sungguh-sungguh beriman kepadanya dan menolongnya". Allah berfirman: "Apakah kamu mengakui dan menerima perjanjian-Ku terhadap yang demikian itu?" Mereka menjawab: "Kami mengakui". Allah berfirman: "Kalau begitu saksikanlah (hai para nabi) dan Aku menjadi saksi (pula) bersama kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,82,'فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ','Barang siapa yang berpaling sesudah itu, maka mereka itulah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,83,'اَفَغَيْرَ دِيْنِ اللّٰهِ يَبْغُوْنَ وَلَهٗ ٓ اَسْلَمَ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّاِلَيْهِ يُرْجَعُوْنَ','Maka apakah mereka mencari agama yang lain dari agama Allah, padahal kepada-Nya-lah menyerahkan diri segala apa yang di langit dan di bumi, baik dengan suka maupun terpaksa dan hanya kepada Allahlah mereka dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,84,'قُلْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ عَلَيْنَا وَمَآ اُنْزِلَ عَلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَالنَّبِيُّوْنَ مِنْ رَّبِّهِمْۖ  لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ','Katakanlah: "Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami dan yang diturunkan kepada Ibrahim, Ismail, Ishaq, Ya´qub, dan anak-anaknya, dan apa yang diberikan kepada Musa, Isa dan para nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorangpun di antara mereka dan hanya kepada-Nya-lah kami menyerahkan diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,85,'وَمَنْ يَّبْتَغِ غَيْرَ الْاِسْلَامِ دِيْنًا فَلَنْ يُّقْبَلَ مِنْهُۚ وَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ','Barangsiapa mencari agama selain agama Islam, maka sekali-kali tidaklah akan diterima (agama itu)daripadanya, dan dia di akhirat termasuk orang-orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,86,'كَيْفَ يَهْدِى اللّٰهُ قَوْمًا كَفَرُوْا بَعْدَ اِيْمَانِهِمْ وَشَهِدُوْٓا اَنَّ الرَّسُوْلَ حَقٌّ وَّجَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ','Bagaimana Allah akan menunjuki suatu kaum yang kafir sesudah mereka beriman, serta mereka telah mengakui bahwa Rasul itu (Muhammad) benar-benar rasul, dan keterangan-keteranganpun telah datang kepada mereka? Allah tidak menunjuki orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,87,'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ','Mereka itu, balasannya ialah: bahwasanya laknat Allah ditimpakan kepada mereka, (demikian pula) laknat para malaikat dan manusia seluruhnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,88,'خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَۙ','mereka kekal di dalamnya, tidak diringankan siksa dari mereka, dan tidak (pula) mereka diberi tangguh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,89,'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۗ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','kecuali orang-orang yang taubat, sesudah (kafir) itu dan mengadakan perbaikan. Karena sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,90,'اِنَّ الَّذِيْنَ كَفَرُوْا بَعْدَ اِيْمَانِهِمْ ثُمَّ ازْدَادُوْا كُفْرًا لَّنْ تُقْبَلَ تَوْبَتُهُمْ ۚ وَاُولٰۤىِٕكَ هُمُ الضَّاۤلُّوْنَ','Sesungguhnya orang-orang kafir sesudah beriman, kemudian bertambah kekafirannya, sekali-kali tidak akan diterima taubatnya; dan mereka itulah orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,3,3,91,'اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يُّقْبَلَ مِنْ اَحَدِهِمْ مِّلْءُ الْاَرْضِ ذَهَبًا وَّلَوِ افْتَدٰى بِهٖۗ  اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ وَّمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ࣖ   ۔','Sesungguhnya orang-orang yang kafir dan mati sedang mereka tetap dalam kekafirannya, maka tidaklah akan diterima dari seseorang diantara mereka emas sepenuh bumi, walaupun dia menebus diri dengan emas (yang sebanyak) itu. Bagi mereka itulah siksa yang pedih dan sekali-kali mereka tidak memperoleh penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,92,'لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوْا مِمَّا تُحِبُّوْنَ ۗوَمَا تُنْفِقُوْا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ','Kamu sekali-kali tidak sampai kepada kebajikan (yang sempurna), sebelum kamu menafkahkan sehahagian harta yang kamu cintai. Dan apa saja yang kamu nafkahkan maka sesungguhnya Allah mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,93,'۞ كُلُّ الطَّعَامِ كَانَ حِلًّا لِّبَنِيْٓ اِسْرَاۤءِيْلَ اِلَّا مَا حَرَّمَ اِسْرَاۤءِيْلُ عَلٰى نَفْسِهٖ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰىةُ ۗ قُلْ فَأْتُوْا بِالتَّوْرٰىةِ فَاتْلُوْهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Semua makanan adalah halal bagi Bani Israil melainkan makanan yang diharamkan oleh Israil (Ya´qub) untuk dirinya sendiri sebelum Taurat diturunkan. Katakanlah: "(Jika kamu mengatakan ada makanan yang diharamkan sebelum turun Taurat), maka bawalah Taurat itu, lalu bacalah dia jika kamu orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,94,'فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْۢ بَعْدِ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ','Maka barangsiapa mengada-adakan dusta terhadap Allah sesudah itu, maka merekalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,95,'قُلْ صَدَقَ اللّٰهُ ۗ فَاتَّبِعُوْا مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ','Katakanlah: "Benarlah (apa yang difirmankan) Allah". Maka ikutilah agama Ibrahim yang lurus, dan bukanlah dia termasuk orang-orang yang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,96,'اِنَّ اَوَّلَ بَيْتٍ وُّضِعَ لِلنَّاسِ لَلَّذِيْ بِبَكَّةَ مُبٰرَكًا وَّهُدًى لِّلْعٰلَمِيْنَۚ','Sesungguhnya rumah yang mula-mula dibangun untuk (tempat beribadat) manusia, ialah Baitullah yang di Bakkah (Mekah) yang diberkahi dan menjadi petunjuk bagi semua manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,97,'فِيْهِ اٰيٰتٌۢ بَيِّنٰتٌ مَّقَامُ اِبْرٰهِيْمَ ەۚ وَمَنْ دَخَلَهٗ كَانَ اٰمِنًا ۗ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَبِيْلًا ۗ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعٰلَمِيْنَ','Padanya terdapat tanda-tanda yang nyata, (di antaranya) maqam Ibrahim; barangsiapa memasukinya (Baitullah itu) menjadi amanlah dia; mengerjakan haji adalah kewajiban manusia terhadap Allah, yaitu (bagi) orang yang sanggup mengadakan perjalanan ke Baitullah. Barangsiapa mengingkari (kewajiban haji), maka sesungguhnya Allah Maha Kaya (tidak memerlukan sesuatu) dari semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,98,'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاللّٰهُ شَهِيْدٌ عَلٰى مَا تَعْمَلُوْنَ','Katakanlah: "Hai Ahli Kitab, mengapa kamu ingkari ayat-ayat Allah, padahal Allah Maha menyaksikan apa yang kamu kerjakan?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,99,'قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُوْنَهَا عِوَجًا وَّاَنْتُمْ شُهَدَاۤءُ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ','Katakanlah: "Hai Ahli Kitab, mengapa kamu menghalang-halangi dari jalan Allah orang-orang yang telah beriman, kamu menghendakinya menjadi bengkok, padahal kamu menyaksikan?". Allah sekali-kali tidak lalai dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,100,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوْا فَرِيْقًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ يَرُدُّوْكُمْ بَعْدَ اِيْمَانِكُمْ كٰفِرِيْنَ ','Hai orang-orang yang beriman, jika kamu mengikuti sebahagian dari orang-orang yang diberi Al Kitab, niscaya mereka akan mengembalikan kamu menjadi orang kafir sesudah kamu beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,101,'وَكَيْفَ تَكْفُرُوْنَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيٰتُ اللّٰهِ وَفِيْكُمْ رَسُوْلُهٗ ۗ وَمَنْ يَّعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ࣖ','Bagaimanakah kamu (sampai) menjadi kafir, padahal ayat-ayat Allah dibacakan kepada kamu, dan Rasul-Nya pun berada di tengah-tengah kamu? Barangsiapa yang berpegang teguh kepada (agama) Allah, maka sesungguhnya ia telah diberi petunjuk kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,102,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقٰىتِهٖ وَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ','Hai orang-orang yang beriman, bertakwalah kepada Allah sebenar-benar takwa kepada-Nya; dan janganlah sekali-kali kamu mati melainkan dalam keadaan beragama Islam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,103,'وَاعْتَصِمُوْا بِحَبْلِ اللّٰهِ جَمِيْعًا وَّلَا تَفَرَّقُوْا ۖوَاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ كُنْتُمْ اَعْدَاۤءً فَاَلَّفَ بَيْنَ قُلُوْبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِهٖٓ اِخْوَانًاۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِّنَ النَّارِ فَاَنْقَذَكُمْ مِّنْهَا ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَهْتَدُوْنَ ','Dan berpeganglah kamu semuanya kepada tali (agama) Allah, dan janganlah kamu bercerai berai, dan ingatlah akan nikmat Allah kepadamu ketika kamu dahulu (masa Jahiliyah) bermusuh-musuhan, maka Allah mempersatukan hatimu, lalu menjadilah kamu karena nikmat Allah, orang-orang yang bersaudara; dan kamu telah berada di tepi jurang neraka, lalu Allah menyelamatkan kamu dari padanya. Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu, agar kamu mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,104,'وَلْتَكُنْ مِّنْكُمْ اُمَّةٌ يَّدْعُوْنَ اِلَى الْخَيْرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ ۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Dan hendaklah ada di antara kamu segolongan umat yang menyeru kepada kebajikan, menyuruh kepada yang ma´ruf dan mencegah dari yang munkar; merekalah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,105,'وَلَا تَكُوْنُوْا كَالَّذِيْنَ تَفَرَّقُوْا وَاخْتَلَفُوْا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ عَظِيْمٌ ۙ','Dan janganlah kamu menyerupai orang-orang yang bercerai-berai dan berselisih sesudah datang keterangan yang jelas kepada mereka. Mereka itulah orang-orang yang mendapat siksa yang berat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,106,'يَّوْمَ تَبْيَضُّ وُجُوْهٌ وَّتَسْوَدُّ وُجُوْهٌ ۚ فَاَمَّا الَّذِيْنَ اسْوَدَّتْ وُجُوْهُهُمْۗ  اَ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ','pada hari yang di waktu itu ada muka yang putih berseri, dan ada pula muka yang hitam muram. Adapun orang-orang yang hitam muram mukanya (kepada mereka dikatakan): "Kenapa kamu kafir sesudah kamu beriman? Karena itu rasakanlah azab disebabkan kekafiranmu itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,107,'وَاَمَّا الَّذِيْنَ ابْيَضَّتْ وُجُوْهُهُمْ فَفِيْ رَحْمَةِ اللّٰهِ ۗ هُمْ فِيْهَا خٰلِدُوْنَ','Adapun orang-orang yang putih berseri mukanya, maka mereka berada dalam rahmat Allah (surga); mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,108,'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعٰلَمِيْنَ','Itulah ayat-ayat Allah. Kami bacakan ayat-ayat itu kepadamu dengan benar; dan tiadalah Allah berkehendak untuk menganiaya hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,109,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ','Kepunyaan Allah-lah segala yang ada di langit dan di bumi; dan kepada Allahlah dikembalikan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,110,'كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُوْنَ بِالْمَعْرُوْفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُوْنَ بِاللّٰهِ ۗ وَلَوْ اٰمَنَ اَهْلُ الْكِتٰبِ لَكَانَ خَيْرًا لَّهُمْ ۗ مِنْهُمُ الْمُؤْمِنُوْنَ وَاَكْثَرُهُمُ الْفٰسِقُوْنَ ','Kamu adalah umat yang terbaik yang dilahirkan untuk manusia, menyuruh kepada yang ma´ruf, dan mencegah dari yang munkar, dan beriman kepada Allah. Sekiranya Ahli Kitab beriman, tentulah itu lebih baik bagi mereka, di antara mereka ada yang beriman, dan kebanyakan mereka adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,111,'لَنْ يَّضُرُّوْكُمْ اِلَّآ اَذًىۗ  وَاِنْ يُّقَاتِلُوْكُمْ يُوَلُّوْكُمُ الْاَدْبَارَۗ  ثُمَّ لَا يُنْصَرُوْنَ','Mereka sekali-kali tidak akan dapat membuat mudharat kepada kamu, selain dari gangguan-gangguan celaan saja, dan jika mereka berperang dengan kamu, pastilah mereka berbalik melarikan diri ke belakang (kalah). Kemudian mereka tidak mendapat pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,112,'ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُوْٓا اِلَّا بِحَبْلٍ مِّنَ اللّٰهِ وَحَبْلٍ مِّنَ النَّاسِ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ الْاَنْبِۢيَاۤءَ بِغَيْرِ حَقٍّۗ  ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ ','Mereka diliputi kehinaan di mana saja mereka berada, kecuali jika mereka berpegang kepada tali (agama) Allah dan tali (perjanjian) dengan manusia, dan mereka kembali mendapat kemurkaan dari Allah dan mereka diliputi kerendahan. Yang demikian itu karena mereka kafir kepada ayat-ayat Allah dan membunuh para nabi tanpa alasan yang benar. Yang demikian itu disebabkan mereka durhaka dan melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,113,'۞ لَيْسُوْا سَوَاۤءً ۗ مِنْ اَهْلِ الْكِتٰبِ اُمَّةٌ قَاۤىِٕمَةٌ يَّتْلُوْنَ اٰيٰتِ اللّٰهِ اٰنَاۤءَ الَّيْلِ وَهُمْ يَسْجُدُوْنَ ','Mereka itu tidak sama; di antara Ahli Kitab itu ada golongan yang berlaku lurus, mereka membaca ayat-ayat Allah pada beberapa waktu di malam hari, sedang mereka juga bersujud (sembahyang).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,114,'يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُوْنَ فِى الْخَيْرٰتِۗ وَاُولٰۤىِٕكَ مِنَ الصّٰلِحِيْنَ ','Mereka beriman kepada Allah dan hari penghabisan, mereka menyuruh kepada yang ma´ruf, dan mencegah dari yang munkar dan bersegera kepada (mengerjakan) pelbagai kebajikan; mereka itu termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,115,'وَمَا يَفْعَلُوْا مِنْ خَيْرٍ فَلَنْ يُّكْفَرُوْهُ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِالْمُتَّقِيْنَ','Dan apa saja kebajikan yang mereka kerjakan, maka sekali-kali mereka tidak dihalangi (menenerima pahala)nya; dan Allah Maha Mengetahui orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,116,'اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ','Sesungguhnya orang-orang yang kafir baik harta mereka maupun anak-anak mereka, sekali-kali tidak dapat menolak azab Allah dari mereka sedikitpun. Dan mereka adalah penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,117,'مَثَلُ مَا يُنْفِقُوْنَ فِيْ هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ رِيْحٍ فِيْهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوْٓا اَنْفُسَهُمْ فَاَهْلَكَتْهُ ۗ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُوْنَ ','Perumpamaan harta yang mereka nafkahkan di dalam kehidupan dunia ini, adalah seperti perumpamaan angin yang mengandung hawa yang sangat dingin, yang menimpa tanaman kaum yang menganiaya diri sendiri, lalu angin itu merusaknya. Allah tidak menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,118,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا بِطَانَةً مِّنْ دُوْنِكُمْ لَا يَأْلُوْنَكُمْ خَبَالًاۗ وَدُّوْا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَاۤءُ مِنْ اَفْوَاهِهِمْۖ وَمَا تُخْفِيْ صُدُوْرُهُمْ اَكْبَرُ ۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ  اِنْ كُنْتُمْ تَعْقِلُوْنَ','Hai orang-orang yang beriman, janganlah kamu ambil menjadi teman kepercayaanmu orang-orang yang, di luar kalanganmu (karena) mereka tidak henti-hentinya (menimbulkan) kemudharatan bagimu. Mereka menyukai apa yang menyusahkan kamu. Telah nyata kebencian dari mulut mereka, dan apa yang disembunyikan oleh hati mereka adalah lebih besar lagi. Sungguh telah Kami terangkan kepadamu ayat-ayat (Kami), jika kamu memahaminya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,119,'هٰٓاَنْتُمْ اُولَاۤءِ تُحِبُّوْنَهُمْ وَلَا يُحِبُّوْنَكُمْ وَتُؤْمِنُوْنَ بِالْكِتٰبِ كُلِّهٖۚ وَاِذَا لَقُوْكُمْ قَالُوْٓا اٰمَنَّاۖ وَاِذَا خَلَوْا عَضُّوْا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِ ۗ قُلْ مُوْتُوْا بِغَيْظِكُمْ ۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ','Beginilah kamu, kamu menyukai mereka, padahal mereka tidak menyukai kamu, dan kamu beriman kepada kitab-kitab semuanya. Apabila mereka menjumpai kamu, mereka berkata "Kami beriman", dan apabila mereka menyendiri, mereka menggigit ujung jari antaran marah bercampur benci terhadap kamu. Katakanlah (kepada mereka): "Matilah kamu karena kemarahanmu itu". Sesungguhnya Allah mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,120,'اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۖ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَّفْرَحُوْا بِهَا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـًٔا ۗ اِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ مُحِيْطٌ ࣖ','Jika kamu memperoleh kebaikan, niscaya mereka bersedih hati, tetapi Jika kamu mendapat bencana, mereka bergembira karenanya. Jika kamu bersabar dan bertakwa, niscaya tipu daya mereka sedikitpun tidak mendatangkan kemudharatan kepadamu. Sesungguhnya Allah mengetahui segala apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,121,'وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِنِيْنَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۙ','Dan (ingatlah), ketika kamu berangkat pada pagi hari dari (rumah) keluargamu akan menempatkan para mukmin pada beberapa tempat untuk berperang. Dan Allah Maha Mendengar lagi Maha Mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,122,'اِذْ هَمَّتْ طَّۤاىِٕفَتٰنِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَا ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ','ketika dua golongan dari padamu ingin (mundur) karena takut, padahal Allah adalah penolong bagi kedua golongan itu. Karena itu hendaklah kepada Allah saja orang-orang mukmin bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,123,'وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَّاَنْتُمْ اَذِلَّةٌ  ۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُوْنَ ','Sungguh Allah telah menolong kamu dalam peperangan Badar, padahal kamu adalah (ketika itu) orang-orang yang lemah. Karena itu bertakwalah kepada Allah, supaya kamu mensyukuri-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,124,'اِذْ تَقُوْلُ لِلْمُؤْمِنِيْنَ اَلَنْ يَّكْفِيَكُمْ اَنْ يُّمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُنْزَلِيْنَۗ','(Ingatlah), ketika kamu mengatakan kepada orang mukmin: "Apakah tidak cukup bagi kamu Allah membantu kamu dengan tiga ribu malaikat yang diturunkan (dari langit)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,125,'بَلٰٓى ۙاِنْ تَصْبِرُوْا وَتَتَّقُوْا وَيَأْتُوْكُمْ مِّنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُسَوِّمِيْنَ ','Ya (cukup), jika kamu bersabar dan bersiap-siaga, dan mereka datang menyerang kamu dengan seketika itu juga, niscaya Allah menolong kamu dengan lima ribu Malaikat yang memakai tanda.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,126,'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَىِٕنَّ قُلُوْبُكُمْ بِهٖ ۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِۙ','Dan Allah tidak menjadikan pemberian bala bantuan itu melainkan sebagai khabar gembira bagi (kemenangan)mu, dan agar tenteram hatimu karenanya. Dan kemenanganmu itu hanyalah dari Allah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,127,'لِيَقْطَعَ طَرَفًا مِّنَ الَّذِيْنَ كَفَرُوْٓا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوْا خَاۤىِٕبِيْنَ','(Allah menolong kamu dalam perang Badar dan memberi bala bantuan itu) untuk membinasakan segolongan orang-orang yang kafir, atau untuk menjadikan mereka hina, lalu mereka kembali dengan tiada memperoleh apa-apa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,128,'لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوْبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظٰلِمُوْنَ ','Tak ada sedikitpun campur tanganmu dalam urusan mereka itu atau Allah menerima taubat mereka, atau mengazab mereka karena sesungguhnya mereka itu orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,129,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ࣖ','Kepunyaan Allah apa yang ada di langit dan yang ada di bumi. Dia memberi ampun kepada siapa yang Dia kehendaki; Dia menyiksa siapa yang Dia kehendaki, dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,130,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوا الرِّبٰوٓا اَضْعَافًا مُّضٰعَفَةً ۖوَّاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَۚ ','Hai orang-orang yang beriman, janganlah kamu memakan riba dengan berlipat ganda] dan bertakwalah kamu kepada Allah supaya kamu mendapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,131,'وَاتَّقُوا النَّارَ الَّتِيْٓ اُعِدَّتْ لِلْكٰفِرِيْنَ ۚ ','Dan peliharalah dirimu dari api neraka, yang disediakan untuk orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,132,'وَاَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَۚ ','Dan taatilah Allah dan Rasul, supaya kamu diberi rahmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,133,'۞ وَسَارِعُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوٰتُ وَالْاَرْضُۙ  اُعِدَّتْ لِلْمُتَّقِيْنَۙ','Dan bersegeralah kamu kepada ampunan dari Tuhanmu dan kepada surga yang luasnya seluas langit dan bumi yang disediakan untuk orang-orang yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,134,'الَّذِيْنَ يُنْفِقُوْنَ فِى السَّرَّۤاءِ وَالضَّرَّۤاءِ وَالْكَاظِمِيْنَ الْغَيْظَ وَالْعَافِيْنَ عَنِ النَّاسِۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَۚ ','(yaitu) orang-orang yang menafkahkan (hartanya), baik di waktu lapang maupun sempit, dan orang-orang yang menahan amarahnya dan memaafkan (kesalahan) orang. Allah menyukai orang-orang yang berbuat kebajikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,135,'وَالَّذِيْنَ اِذَا فَعَلُوْا فَاحِشَةً اَوْ ظَلَمُوْٓا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوْا لِذُنُوْبِهِمْۗ وَمَنْ يَّغْفِرُ الذُّنُوْبَ اِلَّا اللّٰهُ ۗ وَلَمْ يُصِرُّوْا عَلٰى مَا فَعَلُوْا وَهُمْ يَعْلَمُوْنَ ','Dan (juga) orang-orang yang apabila mengerjakan perbuatan keji atau menganiaya diri sendiri, mereka ingat akan Allah, lalu memohon ampun terhadap dosa-dosa mereka dan siapa lagi yang dapat mengampuni dosa selain dari pada Allah? Dan mereka tidak meneruskan perbuatan kejinya itu, sedang mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,136,'اُولٰۤىِٕكَ جَزَاۤؤُهُمْ مَّغْفِرَةٌ مِّنْ رَّبِّهِمْ وَجَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَنِعْمَ اَجْرُ الْعٰمِلِيْنَۗ','Mereka itu balasannya ialah ampunan dari Tuhan mereka dan surga yang di dalamnya mengalir sungai-sungai, sedang mereka kekal di dalamnya; dan itulah sebaik-baik pahala orang-orang yang beramal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,137,'قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ','Sesungguhnya telah berlalu sebelum kamu sunnah-sunnah Allah; Karena itu berjalanlah kamu di muka bumi dan perhatikanlah bagaimana akibat orang-orang yang mendustakan (rasul-rasul).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,138,'هٰذَا بَيَانٌ لِّلنَّاسِ وَهُدًى وَّمَوْعِظَةٌ لِّلْمُتَّقِيْنَ ','(Al Quran) ini adalah penerangan bagi seluruh manusia, dan petunjuk serta pelajaran bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,139,'وَلَا تَهِنُوْا وَلَا تَحْزَنُوْا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','Janganlah kamu bersikap lemah, dan janganlah (pula) kamu bersedih hati, padahal kamulah orang-orang yang paling tinggi (derajatnya), jika kamu orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,140,'اِنْ يَّمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِّثْلُهٗ ۗوَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَتَّخِذَ مِنْكُمْ شُهَدَاۤءَ ۗوَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَۙ','Jika kamu (pada perang Uhud) mendapat luka, maka sesungguhnya kaum (kafir) itupun (pada perang Badar) mendapat luka yang serupa. Dan masa (kejayaan dan kehancuran) itu Kami pergilirkan diantara manusia (agar mereka mendapat pelajaran); dan supaya Allah membedakan orang-orang yang beriman (dengan orang-orang kafir) supaya sebagian kamu dijadikan-Nya (gugur sebagai) syuhada´. Dan Allah tidak menyukai orang-orang yang zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,141,'وَلِيُمَحِّصَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَمْحَقَ الْكٰفِرِيْنَ','Dan agar Allah membersihkan orang-orang yang beriman (dari dosa mereka) dan membinasakan orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,142,'اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَيَعْلَمَ الصّٰبِرِيْنَ ','Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum nyata bagi Allah orang-orang yang berjihad diantaramu dan belum nyata orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,143,'وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوْهُ وَاَنْتُمْ تَنْظُرُوْنَ ࣖ','Sesungguhnya kamu mengharapkan mati (syahid) sebelum kamu menghadapinya; (sekarang) sungguh kamu telah melihatnya dan kamu menyaksikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,144,'وَمَا مُحَمَّدٌ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ ۗ اَفَا۟ىِٕنْ مَّاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْ ۗ وَمَنْ يَّنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَّضُرَّ اللّٰهَ شَيْـًٔا  ۗوَسَيَجْزِى اللّٰهُ الشّٰكِرِيْنَ ','Muhammad itu tidak lain hanyalah seorang rasul, sungguh telah berlalu sebelumnya beberapa orang rasul. Apakah Jika dia wafat atau dibunuh kamu berbalik ke belakang (murtad)? Barangsiapa yang berbalik ke belakang, maka ia tidak dapat mendatangkan mudharat kepada Allah sedikitpun, dan Allah akan memberi balasan kepada orang-orang yang bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,145,'وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوْتَ اِلَّا بِاِذْنِ اللّٰهِ كِتٰبًا مُّؤَجَّلًا ۗ وَمَنْ يُّرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۚ وَمَنْ يُّرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِهٖ مِنْهَا ۗ وَسَنَجْزِى الشّٰكِرِيْنَ ','Sesuatu yang bernyawa tidak akan mati melainkan dengan izin Allah, sebagai ketetapan yang telah ditentukan waktunya. Barang siapa menghendaki pahala dunia, niscaya Kami berikan kepadanya pahala dunia itu, dan barang siapa menghendaki pahala akhirat, Kami berikan (pula) kepadanya pahala akhirat itu. Dan kami akan memberi balasan kepada orang-orang yang bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,146,'وَكَاَيِّنْ مِّنْ نَّبِيٍّ قَاتَلَۙ مَعَهٗ رِبِّيُّوْنَ كَثِيْرٌۚ فَمَا وَهَنُوْا لِمَآ اَصَابَهُمْ فِيْ سَبِيْلِ اللّٰهِ وَمَا ضَعُفُوْا وَمَا اسْتَكَانُوْا ۗ وَاللّٰهُ يُحِبُّ الصّٰبِرِيْنَ ','Dan berapa banyaknya nabi yang berperang bersama-sama mereka sejumlah besar dari pengikut(nya) yang bertakwa. Mereka tidak menjadi lemah karena bencana yang menimpa mereka di jalan Allah, dan tidak lesu dan tidak (pula) menyerah (kepada musuh). Allah menyukai orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,147,'وَمَا كَانَ قَوْلَهُمْ اِلَّآ اَنْ قَالُوْا رَبَّنَا اغْفِرْ لَنَا ذُنُوْبَنَا وَاِسْرَافَنَا فِيْٓ اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ','Tidak ada doa mereka selain ucapan: "Ya Tuhan kami, ampunilah dosa-dosa kami dan tindakan-tindakan kami yang berlebih-lebihan dalam urusan kami dan tetapkanlah pendirian kami, dan tolonglah kami terhadap kaum yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,148,'فَاٰتٰىهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِ ۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ࣖ ','Karena itu Allah memberikan kepada mereka pahala di dunia dan pahala yang baik di akhirat. Dan Allah menyukai orang-orang yang berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,149,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوا الَّذِيْنَ كَفَرُوْا يَرُدُّوْكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ ','Hai orang-orang yang beriman, jika kamu mentaati orang-orang yang kafir itu, niscaya mereka mengembalikan kamu ke belakang (kepada kekafiran), lalu jadilah kamu orang-orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,150,'بَلِ اللّٰهُ مَوْلٰىكُمْ ۚ وَهُوَ خَيْرُ النّٰصِرِيْنَ ','Tetapi (ikutilah Allah), Allahlah Pelindungmu, dan Dialah sebaik-baik Penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,151,'سَنُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا ۚ وَمَأْوٰىهُمُ النَّارُ ۗ وَبِئْسَ مَثْوَى الظّٰلِمِيْنَ ','Akan Kami masukkan ke dalam hati orang-orang kafir rasa takut, disebabkan mereka mempersekutukan Allah dengan sesuatu yang Allah sendiri tidak menurunkan keterangan tentang itu. Tempat kembali mereka ialah neraka; dan itulah seburuk-buruk tempat tinggal orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,152,'وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهٗٓ اِذْ تَحُسُّوْنَهُمْ بِاِذْنِهٖ ۚ حَتّٰىٓ اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِى الْاَمْرِ وَعَصَيْتُمْ مِّنْۢ بَعْدِ مَآ اَرٰىكُمْ مَّا تُحِبُّوْنَ ۗ مِنْكُمْ مَّنْ يُّرِيْدُ الدُّنْيَا وَمِنْكُمْ مَّنْ يُّرِيْدُ الْاٰخِرَةَ ۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ ۚ وَلَقَدْ عَفَا عَنْكُمْ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَلَى الْمُؤْمِنِيْنَ ','Dan sesungguhnya Allah telah memenuhi janji-Nya kepada kamu, ketika kamu membunuh mereka dengan izin-Nya sampai pada saat kamu lemah dan berselisih dalam urusan itu dan mendurhakai perintah (Rasul) sesudah Allah memperlihatkan kepadamu apa yang kamu sukai. Di antaramu ada orang yang menghendaki dunia dan diantara kamu ada orang yang menghendaki akhirat. Kemudian Allah memalingkan kamu dari mereka untuk menguji kamu, dan sesunguhnya Allah telah memaafkan kamu. Dan Allah mempunyai karunia (yang dilimpahkan) atas orang orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,153,'۞ اِذْ تُصْعِدُوْنَ وَلَا تَلْوٗنَ عَلٰٓى اَحَدٍ وَّالرَّسُوْلُ يَدْعُوْكُمْ فِيْٓ اُخْرٰىكُمْ فَاَثَابَكُمْ غَمًّا ۢبِغَمٍّ لِّكَيْلَا تَحْزَنُوْا عَلٰى مَا فَاتَكُمْ وَلَا مَآ اَصَابَكُمْ ۗ وَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ','(Ingatlah) ketika kamu lari dan tidak menoleh kepada seseorangpun, sedang Rasul yang berada di antara kawan-kawanmu yang lain memanggil kamu, karena itu Allah menimpakan atas kamu kesedihan atas kesedihan, supaya kamu jangan bersedih hati terhadap apa yang luput dari pada kamu dan terhadap apa yang menimpa kamu. Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,154,'ثُمَّ اَنْزَلَ عَلَيْكُمْ مِّنْۢ بَعْدِ الْغَمِّ اَمَنَةً نُّعَاسًا يَّغْشٰى طَۤاىِٕفَةً مِّنْكُمْ ۙ وَطَۤاىِٕفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّوْنَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۗ يَقُوْلُوْنَ هَلْ لَّنَا مِنَ الْاَمْرِ مِنْ شَيْءٍ ۗ قُلْ اِنَّ الْاَمْرَ كُلَّهٗ لِلّٰهِ ۗ  يُخْفُوْنَ فِيْٓ اَنْفُسِهِمْ مَّا لَا يُبْدُوْنَ لَكَ ۗ يَقُوْلُوْنَ لَوْ كَانَ لَنَا مِنَ الْاَمْرِ شَيْءٌ مَّا قُتِلْنَا هٰهُنَا ۗ قُلْ لَّوْ كُنْتُمْ فِيْ بُيُوْتِكُمْ لَبَرَزَ الَّذِيْنَ كُتِبَ عَلَيْهِمُ الْقَتْلُ اِلٰى مَضَاجِعِهِمْ ۚ وَلِيَبْتَلِيَ اللّٰهُ مَا فِيْ صُدُوْرِكُمْ وَلِيُمَحِّصَ مَا فِيْ قُلُوْبِكُمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ','Kemudian setelah kamu berdukacita, Allah menurunkan kepada kamu keamanan (berupa) kantuk yang meliputi segolongan dari pada kamu, sedang segolongan lagi telah dicemaskan oleh diri mereka sendiri, mereka menyangka yang tidak benar terhadap Allah seperti sangkaan jahiliyah. Mereka berkata: "Apakah ada bagi kita barang sesuatu (hak campur tangan) dalam urusan ini?". Katakanlah: "Sesungguhnya urusan itu seluruhnya di tangan Allah". Mereka menyembunyikan dalam hati mereka apa yang tidak mereka terangkan kepadamu; mereka berkata: "Sekiranya ada bagi kita barang sesuatu (hak campur tangan) dalam urusan ini, niscaya kita tidak akan dibunuh (dikalahkan) di sini". Katakanlah: "Sekiranya kamu berada di rumahmu, niscaya orang-orang yang telah ditakdirkan akan mati terbunuh itu keluar (juga) ke tempat mereka terbunuh". Dan Allah (berbuat demikian) untuk menguji apa yang ada dalam dadamu dan untuk membersihkan apa yang ada dalam hatimu. Allah Maha Mengetahui isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,155,'اِنَّ الَّذِيْنَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطٰنُ بِبَعْضِ مَا كَسَبُوْا ۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْ ۗ اِنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ ࣖ','Sesungguhnya orang-orang yang berpaling di antaramu pada hari bertemu dua pasukan itu, hanya saja mereka digelincirkan oleh syaitan, disebabkan sebagian kesalahan yang telah mereka perbuat (di masa lampau) dan sesungguhnya Allah telah memberi maaf kepada mereka. Sesungguhnya Allah Maha Pengampun lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,156,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ كَفَرُوْا وَقَالُوْا لِاِخْوَانِهِمْ اِذَا ضَرَبُوْا فِى الْاَرْضِ اَوْ كَانُوْا غُزًّى لَّوْ كَانُوْا عِنْدَنَا مَا مَاتُوْا وَمَا قُتِلُوْاۚ  لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ يُحْيٖ وَيُمِيْتُ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ','Hai orang-orang yang beriman, janganlah kamu seperti orang-orang kafir (orang-orang munafik) itu, yang mengatakan kepada saudara-saudara mereka apabila mereka mengadakan perjalanan di muka bumi atau mereka berperang: "Kalau mereka tetap bersama-sama kita tentulah mereka tidak mati dan tidak dibunuh". Akibat (dari perkataan dan keyakinan mereka) yang demikian itu, Allah menimbulkan rasa penyesalan yang sangat di dalam hati mereka. Allah menghidupkan dan mematikan. Dan Allah melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,157,'وَلَىِٕنْ قُتِلْتُمْ فِيْ سَبِيْلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِّنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِّمَّا يَجْمَعُوْنَ ','Dan sungguh kalau kamu gugur di jalan Allah atau meninggal, tentulah ampunan Allah dan rahmat-Nya lebih baik (bagimu) dari harta rampasan yang mereka kumpulkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,158,'وَلَىِٕنْ مُّتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُوْنَ','Dan sungguh jika kamu meninggal atau gugur, tentulah kepada Allah saja kamu dikumpulkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,159,'فَبِمَا رَحْمَةٍ مِّنَ اللّٰهِ لِنْتَ لَهُمْ ۚ وَلَوْ كُنْتَ فَظًّا غَلِيْظَ الْقَلْبِ لَانْفَضُّوْا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِى الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِ ۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّلِيْنَ','Maka disebabkan rahmat dari Allah-lah kamu berlaku lemah lembut terhadap mereka. Sekiranya kamu bersikap keras lagi berhati kasar, tentulah mereka menjauhkan diri dari sekelilingmu. Karena itu maafkanlah mereka, mohonkanlah ampun bagi mereka, dan bermusyawaratlah dengan mereka dalam urusan itu. Kemudian apabila kamu telah membulatkan tekad, maka bertawakkallah kepada Allah. Sesungguhnya Allah menyukai orang-orang yang bertawakkal kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,160,'اِنْ يَّنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْ ۚ وَاِنْ يَّخْذُلْكُمْ فَمَنْ ذَا الَّذِيْ يَنْصُرُكُمْ مِّنْۢ بَعْدِهٖ ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ','Jika Allah menolong kamu, maka tak adalah orang yang dapat mengalahkan kamu; jika Allah membiarkan kamu (tidak memberi pertolongan), maka siapakah gerangan yang dapat menolong kamu (selain) dari Allah sesudah itu? Karena itu hendaklah kepada Allah saja orang-orang mukmin bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,161,'وَمَا كَانَ لِنَبِيٍّ اَنْ يَّغُلَّ  ۗوَمَنْ يَّغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِ ۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ','Tidak mungkin seorang nabi berkhianat dalam urusan harta rampasan perang. Barangsiapa yang berkhianat dalam urusan rampasan perang itu, maka pada hari kiamat ia akan datang membawa apa yang dikhianatkannya itu, kemudian tiap-tiap diri akan diberi pembalasan tentang apa yang ia kerjakan dengan (pembalasan) setimpal, sedang mereka tidak dianiaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,162,'اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْۢ بَاۤءَ بِسَخَطٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ ','Apakah orang yang mengikuti keridhaan Allah sama dengan orang yang kembali membawa kemurkaan (yang besar) dari Allah dan tempatnya adalah Jahannam? Dan itulah seburuk-buruk tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,163,'هُمْ دَرَجٰتٌ عِنْدَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌ ۢبِمَا يَعْمَلُوْنَ ','(Kedudukan) mereka itu bertingkat-tingkat di sisi Allah, dan Allah Maha Melihat apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,164,'لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ اِذْ بَعَثَ فِيْهِمْ رَسُوْلًا مِّنْ اَنْفُسِهِمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَۚ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍ ','Sungguh Allah telah memberi karunia kepada orang-orang yang beriman ketika Allah mengutus diantara mereka seorang rasul dari golongan mereka sendiri, yang membacakan kepada mereka ayat-ayat Allah, membersihkan (jiwa) mereka, dan mengajarkan kepada mereka Al Kitab dan Al Hikmah. Dan sesungguhnya sebelum (kedatangan Nabi) itu, mereka adalah benar-benar dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,165,'اَوَلَمَّآ اَصَابَتْكُمْ مُّصِيْبَةٌ قَدْ اَصَبْتُمْ مِّثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَا ۗ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan mengapa ketika kamu ditimpa musibah (pada peperangan Uhud), padahal kamu telah menimpakan kekalahan dua kali lipat kepada musuh-musuhmu (pada peperangan Badar), kamu berkata: "Darimana datangnya (kekalahan) ini?" Katakanlah: "Itu dari (kesalahan) dirimu sendiri". Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,166,'وَمَآ اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِنِيْنَۙ','Dan apa yang menimpa kamu pada hari bertemunya dua pasukan, maka (kekalahan) itu adalah dengan izin (takdir) Allah, dan agar Allah mengetahui siapa orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,167,'وَلِيَعْلَمَ الَّذِيْنَ نَافَقُوْا ۖوَقِيْلَ لَهُمْ تَعَالَوْا قَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَوِ ادْفَعُوْا ۗ قَالُوْا لَوْ نَعْلَمُ قِتَالًا لَّاتَّبَعْنٰكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَىِٕذٍ اَقْرَبُ مِنْهُمْ لِلْاِيْمَانِ ۚ يَقُوْلُوْنَ بِاَفْوَاهِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ اَعْلَمُ بِمَا يَكْتُمُوْنَۚ','Dan supaya Allah mengetahui siapa orang-orang yang munafik. Kepada mereka dikatakan: "Marilah berperang di jalan Allah atau pertahankanlah (dirimu)". Mereka berkata: "Sekiranya kami mengetahui akan terjadi peperangan, tentulah kami mengikuti kamu". Mereka pada hari itu lebih dekat kepada kekafiran dari pada keimanan. Mereka mengatakan dengan mulutnya apa yang tidak terkandung dalam hatinya. Dan Allah lebih mengetahui dalam hatinya. Dan Allah lebih mengetahui apa yang mereka sembunyikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,168,'اَلَّذِيْنَ قَالُوْا لِاِخْوَانِهِمْ وَقَعَدُوْا لَوْ اَطَاعُوْنَا مَا قُتِلُوْا ۗ قُلْ فَادْرَءُوْا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Orang-orang yang mengatakan kepada saudara-saudaranya dan mereka tidak turut pergi berperang: "Sekiranya mereka mengikuti kita, tentulah mereka tidak terbunuh". Katakanlah: "Tolaklah kematian itu dari dirimu, jika kamu orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,169,'وَلَا تَحْسَبَنَّ الَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتًا ۗ بَلْ اَحْيَاۤءٌ عِنْدَ رَبِّهِمْ يُرْزَقُوْنَۙ','Janganlah kamu mengira bahwa orang-orang yang gugur di jalan Allah itu mati; bahkan mereka itu hidup disisi Tuhannya dengan mendapat rezeki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,170,'فَرِحِيْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۙ وَيَسْتَبْشِرُوْنَ بِالَّذِيْنَ لَمْ يَلْحَقُوْا بِهِمْ مِّنْ خَلْفِهِمْ ۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۘ','Mereka dalam keadaan gembira disebabkan karunia Allah yang diberikan-Nya kepada mereka, dan mereka bergirang hati terhadap orang-orang yang masih tinggal di belakang yang belum menyusul mereka, bahwa tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,171,'۞ يَسْتَبْشِرُوْنَ بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍۗ وَاَنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُؤْمِنِيْنَ ࣖ ','Mereka bergirang hati dengan nikmat dan karunia yang yang besar dari Allah, dan bahwa Allah tidak menyia-nyiakan pahala orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,172,'اَلَّذِيْنَ اسْتَجَابُوْا لِلّٰهِ وَالرَّسُوْلِ مِنْۢ بَعْدِ مَآ اَصَابَهُمُ الْقَرْحُ ۖ لِلَّذِيْنَ اَحْسَنُوْا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظِيْمٌۚ','(Yaitu) orang-orang yang mentaati perintah Allah dan Rasul-Nya sesudah mereka mendapat luka (dalam peperangan Uhud). Bagi orang-orang yang berbuat kebaikan diantara mereka dan yang bertakwa ada pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,173,'اَلَّذِيْنَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوْا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ اِيْمَانًاۖ وَّقَالُوْا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَكِيْلُ ','(Yaitu) orang-orang (yang mentaati Allah dan Rasul) yang kepada mereka ada orang-orang yang mengatakan: "Sesungguhnya manusia telah mengumpulkan pasukan untuk menyerang kamu, karena itu takutlah kepada mereka", maka perkataan itu menambah keimanan mereka dan mereka menjawab: "Cukuplah Allah menjadi Penolong kami dan Allah adalah sebaik-baik Pelindung".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,174,'فَانْقَلَبُوْا بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍ لَّمْ يَمْسَسْهُمْ سُوْۤءٌۙ وَّاتَّبَعُوْا رِضْوَانَ اللّٰهِ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَظِيْمٍ ','Maka mereka kembali dengan nikmat dan karunia (yang besar) dari Allah, mereka tidak mendapat bencana apa-apa, mereka mengikuti keridhaan Allah. Dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,175,'اِنَّمَا ذٰلِكُمُ الشَّيْطٰنُ يُخَوِّفُ اَوْلِيَاۤءَهٗۖ فَلَا تَخَافُوْهُمْ وَخَافُوْنِ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','Sesungguhnya mereka itu tidak lain hanyalah syaitan yang menakut-nakuti (kamu) dengan kawan-kawannya (orang-orang musyrik Quraisy), karena itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, jika kamu benar-benar orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,176,'وَلَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِۚ اِنَّهُمْ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔا ۗ يُرِيْدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِى الْاٰخِرَةِ  وَلَهُمْ عَذَابٌ عَظِيْمٌۚ ','Janganlah kamu disedihkan oleh orang-orang yang segera menjadi kafir; sesungguhnya mereka tidak sekali-kali dapat memberi mudharat kepada Allah sedikitpun. Allah berkehendak tidak akan memberi sesuatu bahagian (dari pahala) kepada mereka di hari akhirat, dan bagi mereka azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,177,'اِنَّ الَّذِيْنَ اشْتَرَوُا الْكُفْرَ بِالْاِيْمَانِ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','Sesungguhnya orang-orang yang menukar iman dengan kekafiran, sekali-kali mereka tidak dapat memberi mudharat kepada Allah sedikitpun; dan bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,178,'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْٓا اَنَّمَا نُمْلِيْ لَهُمْ خَيْرٌ لِّاَنْفُسِهِمْ ۗ اِنَّمَا نُمْلِيْ لَهُمْ لِيَزْدَادُوْٓا اِثْمًا ۚ وَلَهُمْ عَذَابٌ مُّهِيْنٌ ','Dan janganlah sekali-kali orang-orang kafir menyangka, bahwa pemberian tangguh Kami kepada mereka adalah lebih baik bagi mereka. Sesungguhnya Kami memberi tangguh kepada mereka hanyalah supaya bertambah-tambah dosa mereka; dan bagi mereka azab yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,179,'مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِنِيْنَ عَلٰى مَآ اَنْتُمْ عَلَيْهِ حَتّٰى يَمِيْزَ الْخَبِيْثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَبِيْ مِنْ رُّسُلِهٖ مَنْ يَّشَاۤءُ ۖ فَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖ ۚ وَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا فَلَكُمْ اَجْرٌ عَظِيْمٌ ','Allah sekali-kali tidak akan membiarkan orang-orang yang beriman dalam keadaan kamu sekarang ini, sehingga Dia menyisihkan yang buruk (munafik) dari yang baik (mukmin). Dan Allah sekali-kali tidak akan memperlihatkan kepada kamu hal-hal yang ghaib, akan tetapi Allah memilih siapa yang dikehendaki-Nya di antara rasul-rasul-Nya. Karena itu berimanlah kepada Allah dan rasul-rasul-Nya; dan jika kamu beriman dan bertakwa, maka bagimu pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,180,'وَلَا يَحْسَبَنَّ الَّذِيْنَ يَبْخَلُوْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖ هُوَ خَيْرًا لَّهُمْ ۗ بَلْ هُوَ شَرٌّ لَّهُمْ ۗ سَيُطَوَّقُوْنَ مَا بَخِلُوْا بِهٖ يَوْمَ الْقِيٰمَةِ ۗ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ࣖ ','Sekali-kali janganlah orang-orang yang bakhil dengan harta yang Allah berikan kepada mereka dari karunia-Nya menyangka, bahwa kebakhilan itu baik bagi mereka. Sebenarnya kebakhilan itu adalah buruk bagi mereka. Harta yang mereka bakhilkan itu akan dikalungkan kelak di lehernya di hari kiamat. Dan kepunyaan Allah-lah segala warisan (yang ada) di langit dan di bumi. Dan Allah mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,181,'لَقَدْ سَمِعَ اللّٰهُ قَوْلَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ فَقِيْرٌ وَّنَحْنُ اَغْنِيَاۤءُ ۘ سَنَكْتُبُ مَا قَالُوْا وَقَتْلَهُمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّۙ وَّنَقُوْلُ ذُوْقُوْا عَذَابَ الْحَرِيْقِ ','Sesungguhnya Allah telah mendengar perkatan orang-orang yang mengatakan: "Sesunguhnya Allah miskin dan kami kaya". Kami akan mencatat perkataan mereka itu dan perbuatan mereka membunuh nabi-nabi tanpa alasan yang benar, dan Kami akan mengatakan (kepada mereka): "Rasakanlah olehmu azab yang mem bakar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,182,'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۚ ','(Azab) yang demikian itu adalah disebabkan perbuatan tanganmu sendiri, dan bahwasanya Allah sekali-kali tidak menganiaya hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,183,'اَلَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَآ اَلَّا نُؤْمِنَ لِرَسُوْلٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاۤءَكُمْ رُسُلٌ مِّنْ قَبْلِيْ بِالْبَيِّنٰتِ وَبِالَّذِيْ قُلْتُمْ فَلِمَ قَتَلْتُمُوْهُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ ','(Yaitu) orang-orang (Yahudi) yang mengatakan: "Sesungguhnya Allah telah memerintahkan kepada kami, supaya kami jangan beriman kepada seseorang rasul, sebelum dia mendatangkan kepada kami korban yang dimakan api". Katakanlah: "Sesungguhnya telah datang kepada kamu beberapa orang rasul sebelumku membawa keterangan-keterangan yang nyata dan membawa apa yang kamu sebutkan, maka mengapa kamu membunuh mereka jika kamu adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,184,'فَاِنْ كَذَّبُوْكَ فَقَدْ كُذِّبَ رُسُلٌ مِّنْ قَبْلِكَ جَاۤءُوْ بِالْبَيِّنٰتِ وَالزُّبُرِ وَالْكِتٰبِ الْمُنِيْرِ','Jika mereka mendustakan kamu, maka sesungguhnya rasul-rasul sebelum kamupun telah didustakan (pula), mereka membawa mukjizat-mukjizat yang nyata, Zabur dan kitab yang memberi penjelasan yang sempurna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,185,'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَاِنَّمَا تُوَفَّوْنَ اُجُوْرَكُمْ يَوْمَ الْقِيٰمَةِ ۗ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ ','Tiap-tiap yang berjiwa akan merasakan mati. Dan sesungguhnya pada hari kiamat sajalah disempurnakan pahalamu. Barangsiapa dijauhkan dari neraka dan dimasukkan ke dalam surga, maka sungguh ia telah beruntung. Kehidupan dunia itu tidak lain hanyalah kesenangan yang memperdayakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,186,'۞ لَتُبْلَوُنَّ فِيْٓ اَمْوَالِكُمْ وَاَنْفُسِكُمْۗ وَلَتَسْمَعُنَّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَمِنَ الَّذِيْنَ اَشْرَكُوْٓا اَذًى كَثِيْرًا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ ','Kamu sungguh-sungguh akan diuji terhadap hartamu dan dirimu. Dan (juga) kamu sungguh-sungguh akan mendengar dari orang-orang yang diberi kitab sebelum kamu dan dari orang-orang yang mempersekutukan Allah, gangguan yang banyak yang menyakitkan hati. Jika kamu bersabar dan bertakwa, maka sesungguhnya yang demikian itu termasuk urusan yang patut diutamakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,187,'وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَتُبَيِّنُنَّهٗ لِلنَّاسِ وَلَا تَكْتُمُوْنَهٗۖ فَنَبَذُوْهُ وَرَاۤءَ ظُهُوْرِهِمْ وَاشْتَرَوْا بِهٖ ثَمَنًا قَلِيْلًا ۗ فَبِئْسَ مَا يَشْتَرُوْنَ ','Dan (ingatlah), ketika Allah mengambil janji dari orang-orang yang telah diberi kitab (yaitu): "Hendaklah kamu menerangkan isi kitab itu kepada manusia, dan jangan kamu menyembunyikannya," lalu mereka melemparkan janji itu ke belakang punggung mereka dan mereka menukarnya dengan harga yang sedikit. Amatlah buruknya tukaran yang mereka terima.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,188,'لَا تَحْسَبَنَّ الَّذِيْنَ يَفْرَحُوْنَ بِمَآ اَتَوْا وَّيُحِبُّوْنَ اَنْ يُّحْمَدُوْا بِمَا لَمْ يَفْعَلُوْا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِّنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ','Janganlah sekali-kali kamu menyangka, hahwa orang-orang yang gembira dengan apa yang telah mereka kerjakan dan mereka suka supaya dipuji terhadap perbuatan yang belum mereka kerjakan janganlah kamu menyangka bahwa mereka terlepas dari siksa, dan bagi mereka siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,189,'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ ','kepunyaan Allah-lah kerajaan langit dan bumi, dan Allah Maha Perkasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,190,'اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيٰتٍ لِّاُولِى الْاَلْبَابِۙ','Sesungguhnya dalam penciptaan langit dan bumi, dan silih bergantinya malam dan siang terdapat tanda-tanda bagi orang-orang yang berakal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,191,'الَّذِيْنَ يَذْكُرُوْنَ اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِهِمْ وَيَتَفَكَّرُوْنَ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلًاۚ سُبْحٰنَكَ فَقِنَا عَذَابَ النَّارِ ','(yaitu) orang-orang yang mengingat Allah sambil berdiri atau duduk atau dalam keadan berbaring dan mereka memikirkan tentang penciptaan langit dan bumi (seraya berkata): "Ya Tuhan kami, tiadalah Engkau menciptakan ini dengan sia-sia, Maha Suci Engkau, maka peliharalah kami dari siksa neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,192,'رَبَّنَآ اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ','Ya Tuhan kami, sesungguhnya barangsiapa yang Engkau masukkan ke dalam neraka, maka sungguh telah Engkau hinakan ia, dan tidak ada bagi orang-orang yang zalim seorang penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,193,'رَبَّنَآ اِنَّنَا سَمِعْنَا مُنَادِيًا يُّنَادِيْ لِلْاِيْمَانِ اَنْ اٰمِنُوْا بِرَبِّكُمْ فَاٰمَنَّا ۖرَبَّنَا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَكَفِّرْ عَنَّا سَيِّاٰتِنَا وَتَوَفَّنَا مَعَ الْاَبْرَارِۚ ','Ya Tuhan kami, sesungguhnya kami mendengar (seruan) yang menyeru kepada iman, (yaitu): "Berimanlah kamu kepada Tuhanmu", maka kamipun beriman. Ya Tuhan kami, ampunilah bagi kami dosa-dosa kami dan hapuskanlah dari kami kesalahan-kesalahan kami, dan wafatkanlah kami beserta orang-orang yang banyak berbakti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,194,'رَبَّنَا وَاٰتِنَا مَا وَعَدْتَّنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِ ۗ اِنَّكَ لَا تُخْلِفُ الْمِيْعَادَ ','Ya Tuhan kami, berilah kami apa yang telah Engkau janjikan kepada kami dengan perantaraan rasul-rasul Engkau. Dan janganlah Engkau hinakan kami di hari kiamat. Sesungguhnya Engkau tidak menyalahi janji".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,195,'فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنِّيْ لَآ اُضِيْعُ عَمَلَ عَامِلٍ مِّنْكُمْ مِّنْ ذَكَرٍ اَوْ اُنْثٰى ۚ بَعْضُكُمْ مِّنْۢ بَعْضٍ ۚ فَالَّذِيْنَ هَاجَرُوْا وَاُخْرِجُوْا مِنْ دِيَارِهِمْ وَاُوْذُوْا فِيْ سَبِيْلِيْ وَقٰتَلُوْا وَقُتِلُوْا لَاُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَاُدْخِلَنَّهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ ثَوَابًا مِّنْ عِنْدِ اللّٰهِ ۗ وَاللّٰهُ عِنْدَهٗ حُسْنُ الثَّوَابِ ','Maka Tuhan mereka memperkenankan permohonannya (dengan berfirman): "Sesungguhnya Aku tidak menyia-nyiakan amal orang-orang yang beramal di antara kamu, baik laki-laki atau perempuan, (karena) sebagian kamu adalah turunan dari sebagian yang lain. Maka orang-orang yang berhijrah, yang diusir dari kampung halamannya, yang disakiti pada jalan-Ku, yang berperang dan yang dibunuh, pastilah akan Ku-hapuskan kesalahan-kesalahan mereka dan pastilah Aku masukkan mereka ke dalam surga yang mengalir sungai-sungai di bawahnya, sebagai pahala di sisi Allah. Dan Allah pada sisi-Nya pahala yang baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,196,'لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِيْنَ كَفَرُوْا فِى الْبِلَادِۗ','Janganlah sekali-kali kamu terperdaya oleh kebebasan orang-orang kafir bergerak di dalam negeri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,197,'مَتَاعٌ قَلِيْلٌ  ۗ ثُمَّ مَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ ','Itu hanyalah kesenangan sementara, kemudian tempat tinggal mereka ialah Jahannam; dan Jahannam itu adalah tempat yang seburuk-buruknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,198,'لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا نُزُلًا مِّنْ عِنْدِ اللّٰهِ ۗ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِّلْاَبْرَارِ','Akan tetapi orang-orang yang bertakwa kepada Tuhannya, bagi mereka surga yang mengalir sungai-sungai di dalamnya, sedang mereka kekal di dalamnya sebagai tempat tinggal (anugerah) dari sisi Allah. Dan apa yang di sisi Allah adalah lebih baik bagi orang-orang yang berbakti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,199,'وَاِنَّ مِنْ اَهْلِ الْكِتٰبِ لَمَنْ يُّؤْمِنُ بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْكُمْ وَمَآ اُنْزِلَ اِلَيْهِمْ خٰشِعِيْنَ لِلّٰهِ ۙ لَا يَشْتَرُوْنَ بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا ۗ اُولٰۤىِٕكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْ ۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ ','Dan sesungguhnya diantara ahli kitab ada orang yang beriman kepada Allah dan kepada apa yang diturunkan kepada kamu dan yang diturunkan kepada mereka sedang mereka berendah hati kepada Allah dan mereka tidak menukarkan ayat-ayat Allah dengan harga yang sedikit. Mereka memperoleh pahala di sisi Tuhannya. Sesungguhnya Allah amat cepat perhitungan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,3,200,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اصْبِرُوْا وَصَابِرُوْا وَرَابِطُوْاۗ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ ࣖ','Hai orang-orang yang beriman, bersabarlah kamu dan kuatkanlah kesabaranmu dan tetaplah bersiap siaga (di perbatasan negerimu) dan bertakwalah kepada Allah, supaya kamu beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,1,'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيْرًا وَّنِسَاۤءً ۚ وَاتَّقُوا اللّٰهَ الَّذِيْ تَسَاۤءَلُوْنَ بِهٖ وَالْاَرْحَامَ ۗ اِنَّ اللّٰهَ كَانَ عَلَيْكُمْ رَقِيْبًا','Hai sekalian manusia, bertakwalah kepada Tuhan-mu yang telah menciptakan kamu dari seorang diri, dan dari padanya Allah menciptakan isterinya; dan dari pada keduanya Allah memperkembang biakkan laki-laki dan perempuan yang banyak. Dan bertakwalah kepada Allah yang dengan (mempergunakan) nama-Nya kamu saling meminta satu sama lain, dan (peliharalah) hubungan silaturrahim. Sesungguhnya Allah selalu menjaga dan mengawasi kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,2,'وَاٰتُوا الْيَتٰمٰىٓ اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَبِيْثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوْٓا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْ ۗ اِنَّهٗ كَانَ حُوْبًا كَبِيْرًا','Dan berikanlah kepada anak-anak yatim (yang sudah balig) harta mereka, jangan kamu menukar yang baik dengan yang buruk dan jangan kamu makan harta mereka bersama hartamu. Sesungguhnya tindakan-tindakan (menukar dan memakan) itu, adalah dosa yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,3,'وَاِنْ خِفْتُمْ اَلَّا تُقْسِطُوْا فِى الْيَتٰمٰى فَانْكِحُوْا مَا طَابَ لَكُمْ مِّنَ النِّسَاۤءِ مَثْنٰى وَثُلٰثَ وَرُبٰعَ ۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوْا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْ ۗ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُوْلُوْاۗ','Dan jika kamu takut tidak akan dapat berlaku adil terhadap (hak-hak) perempuan yang yatim (bilamana kamu mengawininya), maka kawinilah wanita-wanita (lain) yang kamu senangi: dua, tiga atau empat. Kemudian jika kamu takut tidak akan dapat berlaku adil, maka (kawinilah) seorang saja, atau budak-budak yang kamu miliki. Yang demikian itu adalah lebih dekat kepada tidak berbuat aniaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,4,'وَاٰتُوا النِّسَاۤءَ صَدُقٰتِهِنَّ نِحْلَةً ۗ فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِّنْهُ نَفْسًا فَكُلُوْهُ هَنِيْۤـًٔا مَّرِيْۤـًٔا ','Berikanlah maskawin (mahar) kepada wanita (yang kamu nikahi) sebagai pemberian dengan penuh kerelaan. Kemudian jika mereka menyerahkan kepada kamu sebagian dari maskawin itu dengan senang hati, maka makanlah (ambillah) pemberian itu (sebagai makanan) yang sedap lagi baik akibatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,5,'وَلَا تُؤْتُوا السُّفَهَاۤءَ اَمْوَالَكُمُ الَّتِيْ جَعَلَ اللّٰهُ لَكُمْ قِيٰمًا وَّارْزُقُوْهُمْ فِيْهَا وَاكْسُوْهُمْ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا','Dan janganlah kamu serahkan kepada orang-orang yang belum sempurna akalnya, harta (mereka yang ada dalam kekuasaanmu) yang dijadikan Allah sebagai pokok kehidupan. Berilah mereka belanja dan pakaian (dari hasil harta itu) dan ucapkanlah kepada mereka kata-kata yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,6,'وَابْتَلُوا الْيَتٰمٰى حَتّٰىٓ اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِّنْهُمْ رُشْدًا فَادْفَعُوْٓا اِلَيْهِمْ اَمْوَالَهُمْ ۚ وَلَا تَأْكُلُوْهَآ اِسْرَافًا وَّبِدَارًا اَنْ يَّكْبَرُوْا ۗ وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۚ وَمَنْ كَانَ فَقِيْرًا فَلْيَأْكُلْ بِالْمَعْرُوْفِ ۗ فَاِذَا دَفَعْتُمْ اِلَيْهِمْ اَمْوَالَهُمْ فَاَشْهِدُوْا عَلَيْهِمْ ۗ وَكَفٰى بِاللّٰهِ حَسِيْبًا ','Dan ujilah anak yatim itu sampai mereka cukup umur untuk kawin. Kemudian jika menurut pendapatmu mereka telah cerdas (pandai memelihara harta), maka serahkanlah kepada mereka harta-hartanya. Dan janganlah kamu makan harta anak yatim lebih dari batas kepatutan dan (janganlah kamu) tergesa-gesa (membelanjakannya) sebelum mereka dewasa. Barang siapa (di antara pemelihara itu) mampu, maka hendaklah ia menahan diri (dari memakan harta anak yatim itu) dan barangsiapa yang miskin, maka bolehlah ia makan harta itu menurut yang patut. Kemudian apabila kamu menyerahkan harta kepada mereka, maka hendaklah kamu adakan saksi-saksi (tentang penyerahan itu) bagi mereka. Dan cukuplah Allah sebagai Pengawas (atas persaksian itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,7,'لِلرِّجَالِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَۖ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَ ۗ نَصِيْبًا مَّفْرُوْضًا','Bagi orang laki-laki ada hak bagian dari harta peninggalan ibu-bapa dan kerabatnya, dan bagi orang wanita ada hak bagian (pula) dari harta peninggalan ibu-bapa dan kerabatnya, baik sedikit atau banyak menurut bahagian yang telah ditetapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,8,'وَاِذَا حَضَرَ الْقِسْمَةَ اُولُوا الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنُ فَارْزُقُوْهُمْ مِّنْهُ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا','Dan apabila sewaktu pembagian itu hadir kerabat, anak yatim dan orang miskin, maka berilah mereka dari harta itu (sekedarnya) dan ucapkanlah kepada mereka perkataan yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,9,'وَلْيَخْشَ الَّذِيْنَ لَوْ تَرَكُوْا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعٰفًا خَافُوْا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُوْلُوْا قَوْلًا سَدِيْدًا','Dan hendaklah takut kepada Allah orang-orang yang seandainya meninggalkan dibelakang mereka anak-anak yang lemah, yang mereka khawatir terhadap (kesejahteraan) mereka. Oleh sebab itu hendaklah mereka bertakwa kepada Allah dan hendaklah mereka mengucapkan perkataan yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,10,'اِنَّ الَّذِيْنَ يَأْكُلُوْنَ اَمْوَالَ الْيَتٰمٰى ظُلْمًا اِنَّمَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ نَارًا ۗ وَسَيَصْلَوْنَ سَعِيْرًا ࣖ ','Sesungguhnya orang-orang yang memakan harta anak yatim secara zalim, sebenarnya mereka itu menelan api sepenuh perutnya dan mereka akan masuk ke dalam api yang menyala-nyala (neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,11,'يُوْصِيْكُمُ اللّٰهُ فِيْٓ اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِ ۚ فَاِنْ كُنَّ نِسَاۤءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۗ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ مِمَّا تَرَكَ اِنْ كَانَ لَهٗ وَلَدٌ ۚ فَاِنْ لَّمْ يَكُنْ لَّهٗ وَلَدٌ وَّوَرِثَهٗٓ اَبَوٰهُ فَلِاُمِّهِ الثُّلُثُ ۚ فَاِنْ كَانَ لَهٗٓ اِخْوَةٌ فَلِاُمِّهِ السُّدُسُ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْ بِهَآ اَوْ دَيْنٍ ۗ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْۚ  لَا تَدْرُوْنَ اَيُّهُمْ اَقْرَبُ لَكُمْ نَفْعًا ۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا','Allah mensyari´atkan bagimu tentang (pembagian pusaka untuk) anak-anakmu. Yaitu: bahagian seorang anak lelaki sama dengan bagahian dua orang anak perempuan; dan jika anak itu semuanya perempuan lebih dari dua, maka bagi mereka dua pertiga dari harta yang ditinggalkan; jika anak perempuan itu seorang saja, maka ia memperoleh separo harta. Dan untuk dua orang ibu-bapa, bagi masing-masingnya seperenam dari harta yang ditinggalkan, jika yang meninggal itu mempunyai anak; jika orang yang meninggal tidak mempunyai anak dan ia diwarisi oleh ibu-bapanya (saja), maka ibunya mendapat sepertiga; jika yang meninggal itu mempunyai beberapa saudara, maka ibunya mendapat seperenam. (Pembagian-pembagian tersebut di atas) sesudah dipenuhi wasiat yang ia buat atau (dan) sesudah dibayar hutangnya. (Tentang) orang tuamu dan anak-anakmu, kamu tidak mengetahui siapa di antara mereka yang lebih dekat (banyak) manfaatnya bagimu. Ini adalah ketetapan dari Allah. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,12,'۞ وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَّمْ يَكُنْ لَّهُنَّ وَلَدٌ ۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْنَ بِهَآ اَوْ دَيْنٍ ۗ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَّمْ يَكُنْ لَّكُمْ وَلَدٌ ۚ فَاِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِّنْۢ بَعْدِ وَصِيَّةٍ تُوْصُوْنَ بِهَآ اَوْ دَيْنٍ ۗ وَاِنْ كَانَ رَجُلٌ يُّوْرَثُ كَلٰلَةً اَوِ امْرَاَةٌ وَّلَهٗٓ اَخٌ اَوْ اُخْتٌ فَلِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُۚ  فَاِنْ كَانُوْٓا اَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكَاۤءُ فِى الثُّلُثِ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصٰى بِهَآ اَوْ دَيْنٍۙ غَيْرَ مُضَاۤرٍّ ۚ وَصِيَّةً مِّنَ اللّٰهِ ۗ وَاللّٰهُ عَلِيْمٌ حَلِيْمٌۗ  ','Dan bagimu (suami-suami) seperdua dari harta yang ditinggalkan oleh isteri-isterimu, jika mereka tidak mempunyai anak. Jika isteri-isterimu itu mempunyai anak, maka kamu mendapat seperempat dari harta yang ditinggalkannya sesudah dipenuhi wasiat yang mereka buat atau (dan) seduah dibayar hutangnya. Para isteri memperoleh seperempat harta yang kamu tinggalkan jika kamu tidak mempunyai anak. Jika kamu mempunyai anak, maka para isteri memperoleh seperdelapan dari harta yang kamu tinggalkan sesudah dipenuhi wasiat yang kamu buat atau (dan) sesudah dibayar hutang-hutangmu. Jika seseorang mati, baik laki-laki maupun perempuan yang tidak meninggalkan ayah dan tidak meninggalkan anak, tetapi mempunyai seorang saudara laki-laki (seibu saja) atau seorang saudara perempuan (seibu saja), maka bagi masing-masing dari kedua jenis saudara itu seperenam harta. Tetapi jika saudara-saudara seibu itu lebih dari seorang, maka mereka bersekutu dalam yang sepertiga itu, sesudah dipenuhi wasiat yang dibuat olehnya atau sesudah dibayar hutangnya dengan tidak memberi mudharat (kepada ahli waris). (Allah menetapkan yang demikian itu sebagai) syari´at yang benar-benar dari Allah, dan Allah Maha Mengetahui lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,13,'تِلْكَ حُدُوْدُ اللّٰهِ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَذٰلِكَ الْفَوْزُ الْعَظِيْمُ ','(Hukum-hukum tersebut) itu adalah ketentuan-ketentuan dari Allah. Barangsiapa taat kepada Allah dan Rasul-Nya, niscaya Allah memasukkannya kedalam surga yang mengalir didalamnya sungai-sungai, sedang mereka kekal di dalamnya; dan itulah kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,14,'وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ وَيَتَعَدَّ حُدُوْدَهٗ يُدْخِلْهُ نَارًا خَالِدًا فِيْهَاۖ وَلَهٗ عَذَابٌ مُّهِيْنٌ ࣖ','Dan barangsiapa yang mendurhakai Allah dan Rasul-Nya dan melanggar ketentuan-ketentuan-Nya, niscaya Allah memasukkannya ke dalam api neraka sedang ia kekal di dalamnya; dan baginya siksa yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,15,'وَالّٰتِيْ يَأْتِيْنَ الْفَاحِشَةَ مِنْ نِّسَاۤىِٕكُمْ فَاسْتَشْهِدُوْا عَلَيْهِنَّ اَرْبَعَةً مِّنْكُمْ ۚ فَاِنْ شَهِدُوْا فَاَمْسِكُوْهُنَّ فِى الْبُيُوْتِ حَتّٰى يَتَوَفّٰىهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَبِيْلًا ','Dan (terhadap) para wanita yang mengerjakan perbuatan keji , hendaklah ada empat orang saksi diantara kamu (yang menyaksikannya). Kemudian apabila mereka telah memberi persaksian, maka kurunglah mereka (wanita-wanita itu) dalam rumah sampai mereka menemui ajalnya, atau sampai Allah memberi jalan lain kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,16,'وَالَّذٰنِ يَأْتِيٰنِهَا مِنْكُمْ فَاٰذُوْهُمَا ۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوْا عَنْهُمَا ۗ اِنَّ اللّٰهَ كَانَ تَوَّابًا رَّحِيْمًا ','Dan terhadap dua orang yang melakukan perbuatan keji di antara kamu, maka berilah hukuman kepada keduanya, kemudian jika keduanya bertaubat dan memperbaiki diri, maka biarkanlah mereka. Sesungguhnya Allah Maha Penerima taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,17,'اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذِيْنَ يَعْمَلُوْنَ السُّوْۤءَ بِجَهَالَةٍ ثُمَّ يَتُوْبُوْنَ مِنْ قَرِيْبٍ فَاُولٰۤىِٕكَ يَتُوْبُ اللّٰهُ عَلَيْهِمْ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ','Sesungguhnya taubat di sisi Allah hanyalah taubat bagi orang-orang yang mengerjakan kejahatan lantaran kejahilan, yang kemudian mereka bertaubat dengan segera, maka mereka itulah yang diterima Allah taubatnya; dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,18,'وَلَيْسَتِ التَّوْبَةُ لِلَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِۚ حَتّٰىٓ اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنِّيْ تُبْتُ الْـٰٔنَ وَلَا الَّذِيْنَ يَمُوْتُوْنَ وَهُمْ كُفَّارٌ ۗ اُولٰۤىِٕكَ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا ','Dan tidaklah taubat itu diterima Allah dari orang-orang yang mengerjakan kejahatan (yang) hingga apabila datang ajal kepada seseorang di antara mereka, (barulah) ia mengatakan: "Sesungguhnya saya bertaubat sekarang". Dan tidak (pula diterima taubat) orang-orang yang mati sedang mereka di dalam kekafiran. Bagi orang-orang itu telah Kami sediakan siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,19,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَاۤءَ كَرْهًا ۗ وَلَا تَعْضُلُوْهُنَّ لِتَذْهَبُوْا بِبَعْضِ مَآ اٰتَيْتُمُوْهُنَّ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوْهُنَّ بِالْمَعْرُوْفِ ۚ فَاِنْ كَرِهْتُمُوْهُنَّ فَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّيَجْعَلَ اللّٰهُ فِيْهِ خَيْرًا كَثِيْرًا ','Hai orang-orang yang beriman, tidak halal bagi kamu mempusakai wanita dengan jalan paksa dan janganlah kamu menyusahkan mereka karena hendak mengambil kembali sebagian dari apa yang telah kamu berikan kepadanya, terkecuali bila mereka melakukan pekerjaan keji yang nyata. Dan bergaullah dengan mereka secara patut. Kemudian bila kamu tidak menyukai mereka, (maka bersabarlah) karena mungkin kamu tidak menyukai sesuatu, padahal Allah menjadikan padanya kebaikan yang banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,20,'وَاِنْ اَرَدْتُّمُ اسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍۙ وَّاٰتَيْتُمْ اِحْدٰىهُنَّ قِنْطَارًا فَلَا تَأْخُذُوْا مِنْهُ شَيْـًٔا ۗ اَتَأْخُذُوْنَهٗ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ','Dan jika kamu ingin mengganti isterimu dengan isteri yang lain , sedang kamu telah memberikan kepada seseorang di antara mereka harta yang banyak, maka janganlah kamu mengambil kembali dari padanya barang sedikitpun. Apakah kamu akan mengambilnya kembali dengan jalan tuduhan yang dusta dan dengan (menanggung) dosa yang nyata?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,21,'وَكَيْفَ تَأْخُذُوْنَهٗ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَّاَخَذْنَ مِنْكُمْ مِّيْثَاقًا غَلِيْظًا ','Bagaimana kamu akan mengambilnya kembali, padahal sebagian kamu telah bergaul (bercampur) dengan yang lain sebagai suami-isteri. Dan mereka (isteri-isterimu) telah mengambil dari kamu perjanjian yang kuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,22,'وَلَا تَنْكِحُوْا مَا نَكَحَ اٰبَاۤؤُكُمْ مِّنَ النِّسَاۤءِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّهٗ كَانَ فَاحِشَةً وَّمَقْتًاۗ وَسَاۤءَ سَبِيْلًا ࣖ ','Dan janganlah kamu kawini wanita-wanita yang telah dikawini oleh ayahmu, terkecuali pada masa yang telah lampau. Sesungguhnya perbuatan itu amat keji dan dibenci Allah dan seburuk-buruk jalan (yang ditempuh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,4,4,23,'حُرِّمَتْ عَلَيْكُمْ اُمَّهٰتُكُمْ وَبَنٰتُكُمْ وَاَخَوٰتُكُمْ وَعَمّٰتُكُمْ وَخٰلٰتُكُمْ وَبَنٰتُ الْاَخِ وَبَنٰتُ الْاُخْتِ وَاُمَّهٰتُكُمُ الّٰتِيْٓ اَرْضَعْنَكُمْ وَاَخَوٰتُكُمْ مِّنَ الرَّضَاعَةِ وَاُمَّهٰتُ نِسَاۤىِٕكُمْ وَرَبَاۤىِٕبُكُمُ الّٰتِيْ فِيْ حُجُوْرِكُمْ مِّنْ نِّسَاۤىِٕكُمُ الّٰتِيْ دَخَلْتُمْ بِهِنَّۖ فَاِنْ لَّمْ تَكُوْنُوْا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ ۖ وَحَلَاۤىِٕلُ اَبْنَاۤىِٕكُمُ الَّذِيْنَ مِنْ اَصْلَابِكُمْۙ وَاَنْ تَجْمَعُوْا بَيْنَ الْاُخْتَيْنِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا   ۔','Diharamkan atas kamu (mengawini) ibu-ibumu; anak-anakmu yang perempuan; saudara-saudaramu yang perempuan, saudara-saudara bapakmu yang perempuan; saudara-saudara ibumu yang perempuan; anak-anak perempuan dari saudara-saudaramu yang laki-laki; anak-anak perempuan dari saudara-saudaramu yang perempuan; ibu-ibumu yang menyusui kamu; saudara perempuan sepersusuan; ibu-ibu isterimu (mertua); anak-anak isterimu yang dalam pemeliharaanmu dari isteri yang telah kamu campuri, tetapi jika kamu belum campur dengan isterimu itu (dan sudah kamu ceraikan), maka tidak berdosa kamu mengawininya; (dan diharamkan bagimu) isteri-isteri anak kandungmu (menantu); dan menghimpunkan (dalam perkawinan) dua perempuan yang bersaudara, kecuali yang telah terjadi pada masa lampau; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,24,'۞ وَالْمُحْصَنٰتُ مِنَ النِّسَاۤءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْ ۚ  كِتٰبَ اللّٰهِ عَلَيْكُمْ ۚ وَاُحِلَّ لَكُمْ مَّا وَرَاۤءَ ذٰلِكُمْ اَنْ تَبْتَغُوْا بِاَمْوَالِكُمْ مُّحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ ۗ فَمَا اسْتَمْتَعْتُمْ بِهٖ مِنْهُنَّ فَاٰتُوْهُنَّ اُجُوْرَهُنَّ فَرِيْضَةً  ۗوَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا تَرَاضَيْتُمْ بِهٖ  مِنْۢ بَعْدِ الْفَرِيْضَةِۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا','dan (diharamkan juga kamu mengawini) wanita yang bersuami, kecuali budak-budak yang kamu miliki (Allah telah menetapkan hukum itu) sebagai ketetapan-Nya atas kamu. Dan dihalalkan bagi kamu selain yang demikian (yaitu) mencari isteri-isteri dengan hartamu untuk dikawini bukan untuk berzina. Maka isteri-isteri yang telah kamu nikmati (campuri) di antara mereka, berikanlah kepada mereka maharnya (dengan sempurna), sebagai suatu kewajiban; dan tiadalah mengapa bagi kamu terhadap sesuatu yang kamu telah saling merelakannya, sesudah menentukan mahar itu. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,25,'وَمَنْ لَّمْ يَسْتَطِعْ مِنْكُمْ طَوْلًا اَنْ يَّنْكِحَ الْمُحْصَنٰتِ الْمُؤْمِنٰتِ فَمِنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ فَتَيٰتِكُمُ الْمُؤْمِنٰتِۗ وَاللّٰهُ اَعْلَمُ بِاِيْمَانِكُمْ ۗ بَعْضُكُمْ مِّنْۢ بَعْضٍۚ فَانْكِحُوْهُنَّ بِاِذْنِ اَهْلِهِنَّ وَاٰتُوْهُنَّ اُجُوْرَهُنَّ بِالْمَعْرُوْفِ مُحْصَنٰتٍ غَيْرَ مُسٰفِحٰتٍ وَّلَا مُتَّخِذٰتِ اَخْدَانٍ ۚ فَاِذَآ اُحْصِنَّ فَاِنْ اَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنٰتِ مِنَ الْعَذَابِۗ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ  ۗ  وَاَنْ تَصْبِرُوْا خَيْرٌ لَّكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ࣖ ','Dan barangsiapa diantara kamu (orang merdeka) yang tidak cukup perbelanjaannya untuk mengawini wanita merdeka lagi beriman, ia boleh mengawini wanita yang beriman, dari budak-budak yang kamu miliki. Allah mengetahui keimananmu; sebahagian kamu adalah dari sebahagian yang lain, karena itu kawinilah mereka dengan seizin tuan mereka, dan berilah maskawin mereka menurut yang patut, sedang merekapun wanita-wanita yang memelihara diri, bukan pezina dan bukan (pula) wanita yang mengambil laki-laki lain sebagai piaraannya; dan apabila mereka telah menjaga diri dengan kawin, kemudian mereka melakukan perbuatan yang keji (zina), maka atas mereka separo hukuman dari hukuman wanita-wanita merdeka yang bersuami. (Kebolehan mengawini budak) itu, adalah bagi orang-orang yang takut kepada kemasyakatan menjaga diri (dari perbuatan zina) di antara kamu, dan kesabaran itu lebih baik bagimu. Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,26,'يُرِيْدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِيْنَ مِنْ قَبْلِكُمْ وَيَتُوْبَ عَلَيْكُمْ ۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','Allah hendak menerangkan (hukum syari´at-Nya) kepadamu, dan menunjukimu kepada jalan-jalan orang yang sebelum kamu (para nabi dan shalihin) dan (hendak) menerima taubatmu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,27,'وَاللّٰهُ يُرِيْدُ اَنْ يَّتُوْبَ عَلَيْكُمْ ۗ وَيُرِيْدُ الَّذِيْنَ يَتَّبِعُوْنَ الشَّهَوٰتِ اَنْ تَمِيْلُوْا مَيْلًا عَظِيْمًا','Dan Allah hendak menerima taubatmu, sedang orang-orang yang mengikuti hawa nafsunya bermaksud supaya kamu berpaling sejauh-jauhnya (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,28,'يُرِيْدُ اللّٰهُ اَنْ يُّخَفِّفَ عَنْكُمْ ۚ وَخُلِقَ الْاِنْسَانُ ضَعِيْفًا ','Allah hendak memberikan keringanan kepadamu, dan manusia dijadikan bersifat lemah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,29,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّآ اَنْ تَكُوْنَ تِجَارَةً عَنْ تَرَاضٍ مِّنْكُمْ ۗ وَلَا تَقْتُلُوْٓا اَنْفُسَكُمْ ۗ اِنَّ اللّٰهَ كَانَ بِكُمْ رَحِيْمًا ','Hai orang-orang yang beriman, janganlah kamu saling memakan harta sesamamu dengan jalan yang batil, kecuali dengan jalan perniagaan yang berlaku dengan suka sama-suka di antara kamu. Dan janganlah kamu membunuh dirimu; sesungguhnya Allah adalah Maha Penyayang kepadamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,30,'وَمَنْ يَّفْعَلْ ذٰلِكَ عُدْوَانًا وَّظُلْمًا فَسَوْفَ نُصْلِيْهِ نَارًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا ','Dan barangsiapa berbuat demikian dengan melanggar hak dan aniaya, maka Kami kelak akan memasukkannya ke dalam neraka. Yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,31,'اِنْ تَجْتَنِبُوْا كَبَاۤىِٕرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَنُدْخِلْكُمْ مُّدْخَلًا كَرِيْمًا ','Jika kamu menjauhi dosa-dosa besar di antara dosa-dosa yang dilarang kamu mengerjakannya, niscaya Kami hapus kesalahan-kesalahanmu (dosa-dosamu yang kecil) dan Kami masukkan kamu ke tempat yang mulia (surga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,32,'وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِهٖ بَعْضَكُمْ عَلٰى بَعْضٍ ۗ لِلرِّجَالِ نَصِيْبٌ مِّمَّا اكْتَسَبُوْا ۗ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا اكْتَسَبْنَ ۗوَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِهٖ ۗ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ','Dan janganlah kamu iri hati terhadap apa yang dikaruniakan Allah kepada sebahagian kamu lebih banyak dari sebahagian yang lain. (Karena) bagi orang laki-laki ada bahagian dari pada apa yang mereka usahakan, dan bagi para wanita (pun) ada bahagian dari apa yang mereka usahakan, dan mohonlah kepada Allah sebagian dari karunia-Nya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,33,'وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ ۗ وَالَّذِيْنَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوْهُمْ نَصِيْبَهُمْ ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا ࣖ ','Bagi tiap-tiap harta peninggalan dari harta yang ditinggalkan ibu bapak dan karib kerabat, Kami jadikan pewaris-pewarisnya. Dan (jika ada) orang-orang yang kamu telah bersumpah setia dengan mereka, maka berilah kepada mereka bahagiannya. Sesungguhnya Allah menyaksikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,34,'اَلرِّجَالُ قَوَّامُوْنَ عَلَى النِّسَاۤءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَّبِمَآ اَنْفَقُوْا مِنْ اَمْوَالِهِمْ ۗ فَالصّٰلِحٰتُ قٰنِتٰتٌ حٰفِظٰتٌ لِّلْغَيْبِ بِمَا حَفِظَ اللّٰهُ ۗوَالّٰتِيْ تَخَافُوْنَ نُشُوْزَهُنَّ فَعِظُوْهُنَّ وَاهْجُرُوْهُنَّ فِى الْمَضَاجِعِ وَاضْرِبُوْهُنَّ ۚ فَاِنْ اَطَعْنَكُمْ فَلَا تَبْغُوْا عَلَيْهِنَّ سَبِيْلًا ۗاِنَّ اللّٰهَ كَانَ عَلِيًّا كَبِيْرًا ','Kaum laki-laki itu adalah pemimpin bagi kaum wanita, oleh karena Allah telah melebihkan sebahagian mereka (laki-laki) atas sebahagian yang lain (wanita), dan karena mereka (laki-laki) telah menafkahkan sebagian dari harta mereka. Sebab itu maka wanita yang saleh, ialah yang taat kepada Allah lagi memelihara diri ketika suaminya tidak ada, oleh karena Allah telah memelihara (mereka). Wanita-wanita yang kamu khawatirkan nusyuznya, maka nasehatilah mereka dan pisahkanlah mereka di tempat tidur mereka, dan pukullah mereka. Kemudian jika mereka mentaatimu, maka janganlah kamu mencari-cari jalan untuk menyusahkannya. Sesungguhnya Allah Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,35,'وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوْا حَكَمًا مِّنْ اَهْلِهٖ وَحَكَمًا مِّنْ اَهْلِهَا ۚ اِنْ يُّرِيْدَآ اِصْلَاحًا يُّوَفِّقِ اللّٰهُ بَيْنَهُمَا ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا خَبِيْرًا ','Dan jika kamu khawatirkan ada persengketaan antara keduanya, maka kirimlah seorang hakam dari keluarga laki-laki dan seorang hakam dari keluarga perempuan. Jika kedua orang hakam itu bermaksud mengadakan perbaikan, niscaya Allah memberi taufik kepada suami-isteri itu. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,36,'۞ وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًا وَّبِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَالْجَارِ ذِى الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْۢبِ وَابْنِ السَّبِيْلِۙ وَمَا مَلَكَتْ اَيْمَانُكُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالًا فَخُوْرًاۙ','Sembahlah Allah dan janganlah kamu mempersekutukan-Nya dengan sesuatupun. Dan berbuat baiklah kepada dua orang ibu-bapa, karib-kerabat, anak-anak yatim, orang-orang miskin, tetangga yang dekat dan tetangga yang jauh, dan teman sejawat, ibnu sabil dan hamba sahayamu. Sesungguhnya Allah tidak menyukai orang-orang yang sombong dan membangga-banggakan diri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,37,' ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُوْنَ مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًاۚ','(yaitu) orang-orang yang kikir, dan menyuruh orang lain berbuat kikir, dan menyembunyikan karunia Allah yang telah diberikan-Nya kepada mereka. Dan Kami telah menyediakan untuk orang-orang kafir siksa yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,38,'وَالَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ ۗ وَمَنْ يَّكُنِ الشَّيْطٰنُ لَهٗ قَرِيْنًا فَسَاۤءَ قَرِيْنًا ','Dan (juga) orang-orang yang menafkahkan harta-harta mereka karena riya kepada manusia, dan orang-orang yang tidak beriman kepada Allah dan kepada hari kemudian. Barangsiapa yang mengambil syaitan itu menjadi temannya, maka syaitan itu adalah teman yang seburuk-buruknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,39,'وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوْا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوْا مِمَّا رَزَقَهُمُ اللّٰهُ ۗوَكَانَ اللّٰهُ بِهِمْ عَلِيْمًا ','Apakah kemudharatannya bagi mereka, kalau mereka beriman kepada Allah dan hari kemudian dan menafkahkan sebahagian rezeki yang telah diberikan Allah kepada mereka? Dan adalah Allah Maha Mengetahui keadaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,40,'اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۚوَاِنْ تَكُ حَسَنَةً يُّضٰعِفْهَا وَيُؤْتِ مِنْ لَّدُنْهُ اَجْرًا عَظِيْمًا','Sesungguhnya Allah tidak menganiaya seseorang walaupun sebesar zarrah, dan jika ada kebajikan sebesar zarrah, niscaya Allah akan melipat gandakannya dan memberikan dari sisi-Nya pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,41,'فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍۢ بِشَهِيْدٍ وَّجِئْنَا بِكَ عَلٰى هٰٓؤُلَاۤءِ شَهِيْدًاۗ','Maka bagaimanakah (halnya orang kafir nanti), apabila Kami mendatangkan seseorang saksi (rasul) dari tiap-tiap umat dan Kami mendatangkan kamu (Muhammad) sebagai saksi atas mereka itu (sebagai umatmu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,42,'يَوْمَىِٕذٍ يَّوَدُّ الَّذِيْنَ كَفَرُوْا وَعَصَوُا الرَّسُوْلَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۗ وَلَا يَكْتُمُوْنَ اللّٰهَ حَدِيْثًا ࣖ','Di hari itu orang-orang kafir dan orang-orang yang mendurhakai rasul, ingin supaya mereka disamaratakan dengan tanah, dan mereka tidak dapat menyembunyikan (dari Allah) sesuatu kejadianpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,43,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوْا مَا تَقُوْلُوْنَ وَلَا جُنُبًا اِلَّا عَابِرِيْ سَبِيْلٍ حَتّٰى تَغْتَسِلُوْا ۗوَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ ۗ اِنَّ اللّٰهَ كَانَ عَفُوًّا غَفُوْرًا','Hai orang-orang yang beriman, janganlah kamu shalat, sedang kamu dalam keadaan mabuk, sehingga kamu mengerti apa yang kamu ucapkan, (jangan pula hampiri mesjid) sedang kamu dalam keadaan junub, terkecuali sekedar berlalu saja, hingga kamu mandi. Dan jika kamu sakit atau sedang dalam musafir atau datang dari tempat buang air atau kamu telah menyentuh perempuan, kemudian kamu tidak mendapat air, maka bertayamumlah kamu dengan tanah yang baik (suci); sapulah mukamu dan tanganmu. Sesungguhnya Allah Maha Pemaaf lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,44,'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يَشْتَرُوْنَ الضَّلٰلَةَ وَيُرِيْدُوْنَ اَنْ تَضِلُّوا السَّبِيْلَۗ','Apakah kamu tidak melihat orang-orang yang telah diberi bahagian dari Al Kitab (Taurat)? Mereka membeli (memilih) kesesatan (dengan petunjuk) dan mereka bermaksud supaya kamu tersesat (menyimpang) dari jalan (yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,45,'وَاللّٰهُ اَعْلَمُ بِاَعْدَاۤىِٕكُمْ ۗوَكَفٰى بِاللّٰهِ وَلِيًّا ۙوَّكَفٰى بِاللّٰهِ نَصِيْرًا ','Dan Allah lebih mengetahui (dari pada kamu) tentang musuh-musuhmu. Dan cukuplah Allah menjadi Pelindung (bagimu). Dan cukuplah Allah menjadi Penolong (bagimu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,46,'مِنَ الَّذِيْنَ هَادُوْا يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖ وَيَقُوْلُوْنَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَّرَاعِنَا لَيًّاۢ بِاَلْسِنَتِهِمْ وَطَعْنًا فِى الدِّيْنِۗ وَلَوْ اَنَّهُمْ قَالُوْا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْرًا لَّهُمْ وَاَقْوَمَۙ وَلٰكِنْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًا ','Yaitu orang-orang Yahudi, mereka mengubah perkataan dari tempat-tempatnya. Mereka berkata: "Kami mendengar", tetapi kami tidak mau menurutinya. Dan (mereka mengatakan pula): "Dengarlah" sedang kamu sebenarnya tidak mendengar apa-apa. Dan (mereka mengatakan): "Raa´ina", dengan memutar-mutar lidahnya dan mencela agama. Sekiranya mereka mengatakan: "Kami mendengar dan menurut, dan dengarlah, dan perhatikanlah kami", tentulah itu lebih baik bagi mereka dan lebih tepat, akan tetapi Allah mengutuk mereka, karena kekafiran mereka. Mereka tidak beriman kecuali iman yang sangat tipis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,47,'يٰٓاَيُّهَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ اٰمِنُوْا بِمَا نَزَّلْنَا مُصَدِّقًا لِّمَا مَعَكُمْ مِّنْ قَبْلِ اَنْ نَّطْمِسَ وُجُوْهًا فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَآ اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّآ اَصْحٰبَ السَّبْتِ ۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا','Hai orang-orang yang telah diberi Al Kitab, berimanlah kamu kepada apa yang telah Kami turunkan (Al Quran) yang membenarkan Kitab yang ada pada kamu sebelum Kami mengubah muka(mu), lalu Kami putarkan ke belakang atau Kami kutuki mereka sebagaimana Kami telah mengutuki orang-orang (yang berbuat maksiat) pada hari Sabtu. Dan ketetapan Allah pasti berlaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,48,'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۚ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْمًا عَظِيْمًا ','Sesungguhnya Allah tidak akan mengampuni dosa syirik, dan Dia mengampuni segala dosa yang selain dari (syirik) itu, bagi siapa yang dikehendaki-Nya. Barangsiapa yang mempersekutukan Allah, maka sungguh ia telah berbuat dosa yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,49,'اَلَمْ تَرَ اِلَى الَّذِيْنَ يُزَكُّوْنَ اَنْفُسَهُمْ ۗ بَلِ اللّٰهُ يُزَكِّيْ مَنْ يَّشَاۤءُ وَلَا يُظْلَمُوْنَ فَتِيْلًا ','Apakah kamu tidak memperhatikan orang yang menganggap dirinya bersih?. Sebenarnya Allah membersihkan siapa yang dikehendaki-Nya dan mereka tidak aniaya sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,50,'اُنْظُرْ كَيْفَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ  وَكَفٰى بِهٖٓ اِثْمًا مُّبِيْنًا ࣖ ','Perhatikanlah, betapakah mereka mengada-adakan dusta terhadap Allah? Dan cukuplah perbuatan itu menjadi dosa yang nyata (bagi mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,51,'اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُؤْمِنُوْنَ بِالْجِبْتِ وَالطَّاغُوْتِ وَيَقُوْلُوْنَ لِلَّذِيْنَ كَفَرُوْا هٰٓؤُلَاۤءِ اَهْدٰى مِنَ الَّذِيْنَ اٰمَنُوْا سَبِيْلًا ','Apakah kamu tidak memperhatikan orang-orang yang diberi bahagian dari Al kitab? Mereka percaya kepada jibt dan thaghut, dan mengatakan kepada orang-orang Kafir (musyrik Mekah), bahwa mereka itu lebih benar jalannya dari orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,52,'اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ ۗوَمَنْ يَّلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ نَصِيْرًا ','Mereka itulah orang yang dikutuki Allah. Barangsiapa yang dikutuki Allah, niscaya kamu sekali-kali tidak akan memperoleh penolong baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,53,'اَمْ لَهُمْ نَصِيْبٌ مِّنَ الْمُلْكِ فَاِذًا لَّا يُؤْتُوْنَ النَّاسَ نَقِيْرًاۙ','Ataukah ada bagi mereka bahagian dari kerajaan (kekuasaan)? Kendatipun ada, mereka tidak akan memberikan sedikitpun (kebajikan) kepada manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,54,'اَمْ يَحْسُدُوْنَ النَّاسَ عَلٰى مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۚ فَقَدْ اٰتَيْنَآ اٰلَ اِبْرٰهِيْمَ الْكِتٰبَ وَالْحِكْمَةَ وَاٰتَيْنٰهُمْ مُّلْكًا عَظِيْمًا ','ataukah mereka dengki kepada manusia (Muhammad) lantaran karunia yang Allah telah berikan kepadanya? Sesungguhnya Kami telah memberikan Kitab dan Hikmah kepada keluarga Ibrahim, dan Kami telah memberikan kepadanya kerajaan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,55,'فَمِنْهُمْ مَّنْ اٰمَنَ بِهٖ وَمِنْهُمْ مَّنْ صَدَّ عَنْهُ ۗ وَكَفٰى بِجَهَنَّمَ سَعِيْرًا','Maka di antara mereka (orang-orang yang dengki itu), ada orang-orang yang beriman kepadanya, dan di antara mereka ada orang-orang yang menghalangi (manusia) dari beriman kepadanya. Dan cukuplah (bagi mereka) Jahannam yang menyala-nyala apinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,56,'اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا سَوْفَ نُصْلِيْهِمْ نَارًاۗ كُلَّمَا نَضِجَتْ جُلُوْدُهُمْ بَدَّلْنٰهُمْ جُلُوْدًا غَيْرَهَا لِيَذُوْقُوا الْعَذَابَۗ اِنَّ اللّٰهَ كَانَ عَزِيْزًا حَكِيْمًا ','Sesungguhnya orang-orang yang kafir kepada ayat-ayat Kami, kelak akan Kami masukkan mereka ke dalam neraka. Setiap kali kulit mereka hangus, Kami ganti kulit mereka dengan kulit yang lain, supaya mereka merasakan azab. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,57,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ  لَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ ۙ وَّنُدْخِلُهُمْ ظِلًّا ظَلِيْلًا ','Dan orang-orang yang beriman dan mengerjakan amalan-amalan yang shaleh, kelak akan Kami masukkan mereka ke dalam surga yang di dalamnya mengalir sungai-sungai; kekal mereka di dalamnya; mereka di dalamnya mempunyai isteri-isteri yang suci, dan Kami masukkan mereka ke tempat yang teduh lagi nyaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,58,'۞ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمٰنٰتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوْا بِالْعَدْلِ ۗ اِنَّ اللّٰهَ نِعِمَّا يَعِظُكُمْ بِهٖ ۗ اِنَّ اللّٰهَ كَانَ سَمِيْعًاۢ بَصِيْرًا','Sesungguhnya Allah menyuruh kamu menyampaikan amanat kepada yang berhak menerimanya, dan (menyuruh kamu) apabila menetapkan hukum di antara manusia supaya kamu menetapkan dengan adil. Sesungguhnya Allah memberi pengajaran yang sebaik-baiknya kepadamu. Sesungguhnya Allah adalah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,59,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاُولِى الْاَمْرِ مِنْكُمْۚ  فَاِنْ تَنَازَعْتُمْ فِيْ شَيْءٍ فَرُدُّوْهُ اِلَى اللّٰهِ وَالرَّسُوْلِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا ࣖ ','Hai orang-orang yang beriman, taatilah Allah dan taatilah Rasul (Nya), dan ulil amri di antara kamu. Kemudian jika kamu berlainan pendapat tentang sesuatu, maka kembalikanlah ia kepada Allah (Al Quran) dan Rasul (sunnahnya), jika kamu benar-benar beriman kepada Allah dan hari kemudian. Yang demikian itu lebih utama (bagimu) dan lebih baik akibatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,60,'اَلَمْ تَرَ اِلَى الَّذِيْنَ يَزْعُمُوْنَ اَنَّهُمْ اٰمَنُوْا بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ يُرِيْدُوْنَ اَنْ يَّتَحَاكَمُوْٓا اِلَى الطَّاغُوْتِ وَقَدْ اُمِرُوْٓا اَنْ يَّكْفُرُوْا بِهٖ ۗوَيُرِيْدُ الشَّيْطٰنُ اَنْ يُّضِلَّهُمْ ضَلٰلًا ۢ بَعِيْدًا','Apakah kamu tidak memperhatikan orang-orang yang mengaku dirinya telah beriman kepada apa yang diturunkan kepadamu dan kepada apa yang diturunkan sebelum kamu? Mereka hendak berhakim kepada thaghut, padahal mereka telah diperintah mengingkari thaghut itu. Dan syaitan bermaksud menyesatkan mereka (dengan) penyesatan yang sejauh-jauhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,61,'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ رَاَيْتَ الْمُنٰفِقِيْنَ يَصُدُّوْنَ عَنْكَ صُدُوْدًاۚ','Apabila dikatakan kepada mereka: "Marilah kamu (tunduk) kepada hukum yang Allah telah turunkan dan kepada hukum Rasul", niscaya kamu lihat orang-orang munafik menghalangi (manusia) dengan sekuat-kuatnya dari (mendekati) kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,62,'فَكَيْفَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ ثُمَّ جَاۤءُوْكَ يَحْلِفُوْنَ بِاللّٰهِ ۖاِنْ اَرَدْنَآ اِلَّآ اِحْسَانًا وَّتَوْفِيْقًا ','Maka bagaimanakah halnya apabila mereka (orang-orang munafik) ditimpa sesuatu musibah disebabkan perbuatan tangan mereka sendiri, kemudian mereka datang kepadamu sambil bersumpah: "Demi Allah, kami sekali-kali tidak menghendaki selain penyelesaian yang baik dan perdamaian yang sempurna".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,63,'اُولٰۤىِٕكَ الَّذِيْنَ يَعْلَمُ اللّٰهُ مَا فِيْ قُلُوْبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَّهُمْ فِيْٓ  اَنْفُسِهِمْ قَوْلًا ۢ بَلِيْغًا ','Mereka itu adalah orang-orang yang Allah mengetahui apa yang di dalam hati mereka. Karena itu berpalinglah kamu dari mereka, dan berilah mereka pelajaran, dan katakanlah kepada mereka perkataan yang berbekas pada jiwa mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,64,'وَمَآ  اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِ ۗوَلَوْ اَنَّهُمْ اِذْ ظَّلَمُوْٓا اَنْفُسَهُمْ جَاۤءُوْكَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُوْلُ لَوَجَدُوا اللّٰهَ تَوَّابًا رَّحِيْمًا ','Dan Kami tidak mengutus seseorang rasul melainkan untuk ditaati dengan seizin Allah. Sesungguhnya jikalau mereka ketika menganiaya dirinya datang kepadamu, lalu memohon ampun kepada Allah, dan Rasulpun memohonkan ampun untuk mereka, tentulah mereka mendapati Allah Maha Penerima Taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,65,'فَلَا وَرَبِّكَ لَا يُؤْمِنُوْنَ حَتّٰى يُحَكِّمُوْكَ فِيْمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوْا فِيْٓ اَنْفُسِهِمْ حَرَجًا مِّمَّا قَضَيْتَ وَيُسَلِّمُوْا تَسْلِيْمًا ','Maka demi Tuhanmu, mereka (pada hakekatnya) tidak beriman hingga mereka menjadikan kamu hakim terhadap perkara yang mereka perselisihkan, kemudian mereka tidak merasa dalam hati mereka sesuatu keberatan terhadap putusan yang kamu berikan, dan mereka menerima dengan sepenuhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,66,'وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُوْٓا اَنْفُسَكُمْ اَوِ اخْرُجُوْا مِنْ دِيَارِكُمْ مَّا فَعَلُوْهُ اِلَّا قَلِيْلٌ مِّنْهُمْ ۗوَلَوْ اَنَّهُمْ فَعَلُوْا مَا يُوْعَظُوْنَ بِهٖ لَكَانَ خَيْرًا لَّهُمْ وَاَشَدَّ تَثْبِيْتًاۙ','Dan sesungguhnya kalau Kami perintahkan kepada mereka: "Bunuhlah dirimu atau keluarlah kamu dari kampungmu", niscaya mereka tidak akan melakukannya kecuali sebagian kecil dari mereka. Dan sesungguhnya kalau mereka melaksanakan pelajaran yang diberikan kepada mereka, tentulah hal yang demikian itu lebih baik bagi mereka dan lebih menguatkan (iman mereka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,67,'وَّاِذًا لَّاٰ تَيْنٰهُمْ مِّنْ لَّدُنَّآ اَجْرًا عَظِيْمًاۙ','dan kalau demikian, pasti Kami berikan kepada mereka pahala yang besar dari sisi Kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,68,'وَّلَهَدَيْنٰهُمْ صِرَاطًا مُّسْتَقِيْمًا ','dan pasti Kami tunjuki mereka kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,69,'وَمَنْ يُّطِعِ اللّٰهَ وَالرَّسُوْلَ فَاُولٰۤىِٕكَ مَعَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ وَالصِّدِّيْقِيْنَ وَالشُّهَدَاۤءِ وَالصّٰلِحِيْنَ ۚ وَحَسُنَ اُولٰۤىِٕكَ رَفِيْقًا ','Dan barangsiapa yang mentaati Allah dan Rasul(Nya), mereka itu akan bersama-sama dengan orang-orang yang dianugerahi nikmat oleh Allah, yaitu: Nabi-nabi, para shiddiiqiin, orang-orang yang mati syahid, dan orang-orang saleh. Dan mereka itulah teman yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,70,'ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِ ۗوَكَفٰى بِاللّٰهِ عَلِيْمًا ࣖ','Yang demikian itu adalah karunia dari Allah, dan Allah cukup mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,71,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا خُذُوْا حِذْرَكُمْ فَانْفِرُوْا ثُبَاتٍ اَوِ انْفِرُوْا جَمِيْعًا ','Hai orang-orang yang beriman, bersiap siagalah kamu, dan majulah (ke medan pertempuran) berkelompok-kelompok, atau majulah bersama-sama!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,72,'وَاِنَّ مِنْكُمْ لَمَنْ لَّيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُّصِيْبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَّعَهُمْ شَهِيْدًا ','Dan sesungguhnya di antara kamu ada orang yang sangat berlambat-lambat (ke medan pertempuran). Maka jika kamu ditimpa musibah ia berkata: "Sesungguhnya Tuhan telah menganugerahkan nikmat kepada saya karena saya tidak ikut berperang bersama mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,73,'وَلَىِٕنْ اَصَابَكُمْ فَضْلٌ مِّنَ اللّٰهِ لَيَقُوْلَنَّ كَاَنْ لَّمْ تَكُنْۢ بَيْنَكُمْ وَبَيْنَهٗ مَوَدَّةٌ يّٰلَيْتَنِيْ كُنْتُ مَعَهُمْ فَاَفُوْزَ فَوْزًا عَظِيْمًا ','Dan sungguh jika kamu beroleh karunia (kemenangan) dari Allah, tentulah dia mengatakan seolah-oleh belum pernah ada hubungan kasih sayang antara kamu dengan dia: "Wahai kiranya saya ada bersama-sama mereka, tentu saya mendapat kemenangan yang besar (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,74,'۞ فَلْيُقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يَشْرُوْنَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۗ وَمَنْ يُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ','Karena itu hendaklah orang-orang yang menukar kehidupan dunia dengan kehidupan akhirat berperang di jalan Allah. Barangsiapa yang berperang di jalan Allah, lalu gugur atau memperoleh kemenangan maka kelak akan Kami berikan kepadanya pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,75,'وَمَا لَكُمْ لَا تُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَالْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ الَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ وَلِيًّاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ نَصِيْرًا ','Mengapa kamu tidak mau berperang di jalan Allah dan (membela) orang-orang yang lemah baik laki-laki, wanita-wanita maupun anak-anak yang semuanya berdoa: "Ya Tuhan kami, keluarkanlah kami dari negeri ini (Mekah) yang zalim penduduknya dan berilah kami pelindung dari sisi Engkau, dan berilah kami penolong dari sisi Engkau!".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,76,'اَلَّذِيْنَ اٰمَنُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۚ وَالَّذِيْنَ كَفَرُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ الطَّاغُوْتِ فَقَاتِلُوْٓا  اَوْلِيَاۤءَ الشَّيْطٰنِ ۚ اِنَّ كَيْدَ الشَّيْطٰنِ كَانَ ضَعِيْفًا ۚ ࣖ ','Orang-orang yang beriman berperang di jalan Allah, dan orang-orang yang kafir berperang di jalan thaghut, sebab itu perangilah kawan-kawan syaitan itu, karena sesungguhnya tipu daya syaitan itu adalah lemah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,77,'اَلَمْ تَرَ اِلَى الَّذِيْنَ قِيْلَ لَهُمْ كُفُّوْٓا اَيْدِيَكُمْ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَرِيْقٌ مِّنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةً ۚ وَقَالُوْا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَۚ  لَوْلَآ اَخَّرْتَنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۗ قُلْ مَتَاعُ الدُّنْيَا قَلِيْلٌۚ وَالْاٰخِرَةُ خَيْرٌ لِّمَنِ اتَّقٰىۗ وَلَا تُظْلَمُوْنَ فَتِيْلًا ','Tidakkah kamu perhatikan orang-orang yang dikatakan kepada mereka: "Tahanlah tanganmu (dari berperang), dirikanlah sembahyang dan tunaikanlah zakat!" Setelah diwajibkan kepada mereka berperang, tiba-tiba sebahagian dari mereka (golongan munafik) takut kepada manusia (musuh), seperti takutnya kepada Allah, bahkan lebih sangat dari itu takutnya. Mereka berkata: "Ya Tuhan kami, mengapa Engkau wajibkan berperang kepada kami? Mengapa tidak Engkau tangguhkan (kewajiban berperang) kepada kami sampai kepada beberapa waktu lagi?" Katakanlah: "Kesenangan di dunia ini hanya sebentar dan akhirat itu lebih baik untuk orang-orang yang bertakwa, dan kamu tidak akan dianiaya sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,78,'اَيْنَمَا تَكُوْنُوْا يُدْرِكْكُّمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِيْ بُرُوْجٍ مُّشَيَّدَةٍ ۗ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِ اللّٰهِ ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِكَ ۗ قُلْ كُلٌّ مِّنْ عِنْدِ اللّٰهِ ۗ فَمَالِ هٰٓؤُلَاۤءِ الْقَوْمِ لَا يَكَادُوْنَ يَفْقَهُوْنَ حَدِيْثًا ','Di mana saja kamu berada, kematian akan mendapatkan kamu, kendatipun kamu di dalam benteng yang tinggi lagi kokoh, dan jika mereka memperoleh kebaikan, mereka mengatakan: "Ini adalah dari sisi Allah", dan kalau mereka ditimpa sesuatu bencana mereka mengatakan: "Ini (datangnya) dari sisi kamu (Muhammad)". Katakanlah: "Semuanya (datang) dari sisi Allah". Maka mengapa orang-orang itu (orang munafik) hampir-hampir tidak memahami pembicaraan sedikitpun?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,79,'مَآ اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِ ۖ وَمَآ اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَّفْسِكَ ۗ وَاَرْسَلْنٰكَ لِلنَّاسِ رَسُوْلًا  ۗ وَكَفٰى بِاللّٰهِ شَهِيْدًا ','Apa saja nikmat yang kamu peroleh adalah dari Allah, dan apa saja bencana yang menimpamu, maka dari (kesalahan) dirimu sendiri. Kami mengutusmu menjadi Rasul kepada segenap manusia. Dan cukuplah Allah menjadi saksi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,80,'مَنْ يُّطِعِ الرَّسُوْلَ فَقَدْ اَطَاعَ اللّٰهَ ۚ وَمَنْ تَوَلّٰى فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗ ','Barangsiapa yang mentaati Rasul itu, sesungguhnya ia telah mentaati Allah. Dan barangsiapa yang berpaling (dari ketaatan itu), maka Kami tidak mengutusmu untuk menjadi pemelihara bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,81,'وَيَقُوْلُوْنَ طَاعَةٌ  ۖ فَاِذَا بَرَزُوْا مِنْ عِنْدِكَ بَيَّتَ طَاۤىِٕفَةٌ مِّنْهُمْ غَيْرَ الَّذِيْ تَقُوْلُ ۗ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُوْنَ ۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا ','Dan mereka (orang-orang munafik) mengatakan: "(Kewajiban kami hanyalah) taat". Tetapi apabila mereka telah pergi dari sisimu, sebahagian dari mereka mengatur siasat di malam hari (mengambil keputusan) lain dari yang telah mereka katakan tadi. Allah menulis siasat yang mereka atur di malam hari itu, maka berpalinglah kamu dari mereka dan tawakallah kepada Allah. Cukuplah Allah menjadi Pelindung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,82,'اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ ۗ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوْا فِيْهِ اخْتِلَافًا كَثِيْرًا ','Maka apakah mereka tidak memperhatikan Al Quran? Kalau kiranya Al Quran itu bukan dari sisi Allah, tentulah mereka mendapat pertentangan yang banyak di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,83,'وَاِذَا جَاۤءَهُمْ اَمْرٌ مِّنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوْا بِهٖ ۗ وَلَوْ رَدُّوْهُ اِلَى الرَّسُوْلِ وَاِلٰٓى اُولِى الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِيْنَ يَسْتَنْۢبِطُوْنَهٗ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَاتَّبَعْتُمُ الشَّيْطٰنَ اِلَّا قَلِيْلًا ','Dan apabila datang kepada mereka suatu berita tentang keamanan ataupun ketakutan, mereka lalu menyiarkannya. Dan kalau mereka menyerahkannya kepada Rasul dan Ulil Amri di antara mereka, tentulah orang-orang yang ingin mengetahui kebenarannya (akan dapat) mengetahuinya dari mereka (Rasul dan Ulil Amri). Kalau tidaklah karena karunia dan rahmat Allah kepada kamu, tentulah kamu mengikut syaitan, kecuali sebahagian kecil saja (di antaramu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,84,'فَقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِنِيْنَ ۚ عَسَى اللّٰهُ اَنْ يَّكُفَّ بَأْسَ الَّذِيْنَ كَفَرُوْا ۗوَاللّٰهُ اَشَدُّ بَأْسًا وَّاَشَدُّ تَنْكِيْلًا ','Maka berperanglah kamu pada jalan Allah, tidaklah kamu dibebani melainkan dengan kewajiban kamu sendiri. Kobarkanlah semangat para mukmin (untuk berperang). Mudah-mudahan Allah menolak serangan orang-orang yang kafir itu. Allah amat besar kekuatan dan amat keras siksaan(Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,85,'مَنْ يَّشْفَعْ شَفَاعَةً حَسَنَةً يَّكُنْ لَّهٗ نَصِيْبٌ مِّنْهَا ۚ وَمَنْ يَّشْفَعْ شَفَاعَةً سَيِّئَةً يَّكُنْ لَّهٗ كِفْلٌ مِّنْهَا ۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقِيْتًا ','Barangsiapa yang memberikan syafa´at yang baik, niscaya ia akan memperoleh bahagian (pahala) dari padanya. Dan barangsiapa memberi syafa´at yang buruk, niscaya ia akan memikul bahagian (dosa) dari padanya. Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,86,'وَاِذَا حُيِّيْتُمْ بِتَحِيَّةٍ فَحَيُّوْا بِاَحْسَنَ مِنْهَآ اَوْ رُدُّوْهَا ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَسِيْبًا','Apabila kamu diberi penghormatan dengan sesuatu penghormatan, maka balaslah penghormatan itu dengan yang lebih baik dari padanya, atau balaslah penghormatan itu (dengan yang serupa). Sesungguhnya Allah memperhitungankan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,87,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ  لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِ ۗ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَدِيْثًا ࣖ','Allah, tidak ada Tuhan (yang berhak disembah) selain Dia. Sesungguhnya Dia akan mengumpulkan kamu di hari kiamat, yang tidak ada keraguan terjadinya. Dan siapakah orang yang lebih benar perkataan(nya) dari pada Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,88,'۞ فَمَا لَكُمْ فِى الْمُنٰفِقِيْنَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُوْا ۗ اَتُرِيْدُوْنَ اَنْ تَهْدُوْا مَنْ اَضَلَّ اللّٰهُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ','Maka mengapa kamu (terpecah) menjadi dua golongan dalam (menghadapi) orang-orang munafik, padahal Allah telah membalikkan mereka kepada kekafiran, disebabkan usaha mereka sendiri? Apakah kamu bermaksud memberi petunjuk kepada orang-orang yang telah disesatkan Allah? Barangsiapa yang disesatkan Allah, sekali-kali kamu tidak mendapatkan jalan (untuk memberi petunjuk) kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,89,'وَدُّوْا لَوْ تَكْفُرُوْنَ كَمَا كَفَرُوْا فَتَكُوْنُوْنَ سَوَاۤءً فَلَا تَتَّخِذُوْا مِنْهُمْ اَوْلِيَاۤءَ حَتّٰى يُهَاجِرُوْا فِيْ سَبِيْلِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ وَجَدْتُّمُوْهُمْ ۖ وَلَا تَتَّخِذُوْا مِنْهُمْ وَلِيًّا وَّلَا نَصِيْرًاۙ  ','Mereka ingin supaya kamu menjadi kafir sebagaimana mereka telah menjadi kafir, lalu kamu menjadi sama (dengan mereka). Maka janganlah kamu jadikan di antara mereka penolong-penolong(mu), hingga mereka berhijrah pada jalan Allah. Maka jika mereka berpaling, tawan dan bunuhlah mereka di mana saja kamu menemuinya, dan janganlah kamu ambil seorangpun di antara mereka menjadi pelindung, dan jangan (pula) menjadi penolong,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,90,'اِلَّا الَّذِيْنَ يَصِلُوْنَ اِلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ اَوْ جَاۤءُوْكُمْ حَصِرَتْ صُدُوْرُهُمْ اَنْ يُّقَاتِلُوْكُمْ اَوْ يُقَاتِلُوْا قَوْمَهُمْ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوْكُمْ ۚ فَاِنِ اعْتَزَلُوْكُمْ فَلَمْ يُقَاتِلُوْكُمْ وَاَلْقَوْا اِلَيْكُمُ السَّلَمَ ۙ فَمَا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَبِيْلًا ','kecuali orang-orang yang meminta perlindungan kepada sesuatu kaum, yang antara kamu dan kaum itu telah ada perjanjian (damai) atau orang-orang yang datang kepada kamu sedang hati mereka merasa keberatan untuk memerangi kamu dan memerangi kaumnya. Kalau Allah menghendaki, tentu Dia memberi kekuasaan kepada mereka terhadap kamu, lalu pastilah mereka memerangimu. tetapi jika mereka membiarkan kamu, dan tidak memerangi kamu serta mengemukakan perdamaian kepadamu maka Allah tidak memberi jalan bagimu (untuk menawan dan membunuh) mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,91,'سَتَجِدُوْنَ اٰخَرِيْنَ يُرِيْدُوْنَ اَنْ يَّأْمَنُوْكُمْ وَيَأْمَنُوْا قَوْمَهُمْ ۗ كُلَّ مَا رُدُّوْٓا اِلَى الْفِتْنَةِ اُرْكِسُوْا فِيْهَا ۚ فَاِنْ لَّمْ يَعْتَزِلُوْكُمْ وَيُلْقُوْٓا اِلَيْكُمُ السَّلَمَ وَيَكُفُّوْٓا اَيْدِيَهُمْ فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ ۗ وَاُولٰۤىِٕكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطٰنًا مُّبِيْنًا ࣖ ','Kelak kamu akan dapati (golongan-golongan) yang lain, yang bermaksud supaya mereka aman dari pada kamu dan aman (pula) dari kaumnya. Setiap mereka diajak kembali kepada fitnah (syirik), merekapun terjun kedalamnya. Karena itu jika mereka tidak membiarkan kamu dan (tidak) mau mengemukakan perdamaian kepadamu, serta (tidak) menahan tangan mereka (dari memerangimu), maka tawanlah mereka dan bunuhlah mereka dan merekalah orang-orang yang Kami berikan kepadamu alasan yang nyata (untuk menawan dan membunuh) mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,92,'وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَّقْتُلَ مُؤْمِنًا اِلَّا خَطَـًٔا ۚ وَمَنْ قَتَلَ مُؤْمِنًا خَطَـًٔا فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ وَّدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى  اَهْلِهٖٓ  اِلَّآ  اَنْ يَّصَّدَّقُوْا ۗ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَّكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۗوَاِنْ كَانَ مِنْ قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ فَدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى اَهْلِهٖ وَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِۖ تَوْبَةً مِّنَ اللّٰهِ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ','Dan tidak layak bagi seorang mukmin membunuh seorang mukmin (yang lain), kecuali karena tersalah (tidak sengaja), dan barangsiapa membunuh seorang mukmin karena tersalah (hendaklah) ia memerdekakan seorang hamba sahaya yang beriman serta membayar diat yang diserahkan kepada keluarganya (si terbunuh itu), kecuali jika mereka (keluarga terbunuh) bersedekah. Jika ia (si terbunuh) dari kaum (kafir) yang ada perjanjian (damai) antara mereka dengan kamu, maka (hendaklah si pembunuh) membayar diat yang diserahkan kepada keluarganya (si terbunuh) serta memerdekakan hamba sahaya yang beriman. Barangsiapa yang tidak memperolehnya, maka hendaklah ia (si pembunuh) berpuasa dua bulan berturut-turut untuk penerimaan taubat dari pada Allah. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,93,'وَمَنْ يَّقْتُلْ مُؤْمِنًا مُّتَعَمِّدًا فَجَزَاۤؤُهٗ جَهَنَّمُ خَالِدًا فِيْهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهٗ وَاَعَدَّ لَهٗ عَذَابًا عَظِيْمًا ','Dan barangsiapa yang membunuh seorang mukmin dengan sengaja maka balasannya ialah Jahannam, kekal ia di dalamnya dan Allah murka kepadanya, dan mengutukinya serta menyediakan azab yang besar baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,94,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا ضَرَبْتُمْ فِيْ سَبِيْلِ اللّٰهِ فَتَبَيَّنُوْا وَلَا تَقُوْلُوْا لِمَنْ اَلْقٰىٓ اِلَيْكُمُ السَّلٰمَ لَسْتَ مُؤْمِنًاۚ تَبْتَغُوْنَ عَرَضَ الْحَيٰوةِ الدُّنْيَا ۖفَعِنْدَ اللّٰهِ مَغَانِمُ كَثِيْرَةٌ ۗ كَذٰلِكَ كُنْتُمْ مِّنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُوْاۗ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا','Hai orang-orang yang beriman, apabila kamu pergi (berperang) di jalan Allah, maka telitilah dan janganlah kamu mengatakan kepada orang yang mengucapkan "salam" kepadamu: "Kamu bukan seorang mukmin" (lalu kamu membunuhnya), dengan maksud mencari harta benda kehidupan di dunia, karena di sisi Allah ada harta yang banyak. Begitu jugalah keadaan kamu dahulu, lalu Allah menganugerahkan nikmat-Nya atas kamu, maka telitilah. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,95,'لَا يَسْتَوِى الْقَاعِدُوْنَ مِنَ الْمُؤْمِنِيْنَ غَيْرُ اُولِى الضَّرَرِ وَالْمُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ  فَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقٰعِدِيْنَ دَرَجَةً ۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَفَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ عَلَى الْقٰعِدِيْنَ اَجْرًا عَظِيْمًاۙ  ','Tidaklah sama antara mukmin yang duduk (yang tidak ikut berperang) yang tidak mempunyai ´uzur dengan orang-orang yang berjihad di jalan Allah dengan harta mereka dan jiwanya. Allah melebihkan orang-orang yang berjihad dengan harta dan jiwanya atas orang-orang yang duduk satu derajat. Kepada masing-masing mereka Allah menjanjikan pahala yang baik (surga) dan Allah melebihkan orang-orang yang berjihad atas orang yang duduk dengan pahala yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,96,'دَرَجٰتٍ مِّنْهُ وَمَغْفِرَةً وَّرَحْمَةً ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ','(yaitu) beberapa derajat dari pada-Nya, ampunan serta rahmat. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,97,'اِنَّ الَّذِيْنَ تَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ قَالُوْا فِيْمَ كُنْتُمْ ۗ قَالُوْا كُنَّا مُسْتَضْعَفِيْنَ فِى الْاَرْضِۗ  قَالُوْٓا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوْا فِيْهَا ۗ فَاُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُ ۗ وَسَاۤءَتْ مَصِيْرًاۙ  ','Sesungguhnya orang-orang yang diwafatkan malaikat dalam keadaan menganiaya diri sendiri, (kepada mereka) malaikat bertanya: "Dalam keadaan bagaimana kamu ini?". Mereka menjawab: "Adalah kami orang-orang yang tertindas di negeri (Mekah)". Para malaikat berkata: "Bukankah bumi Allah itu luas, sehingga kamu dapat berhijrah di bumi itu?". Orang-orang itu tempatnya neraka Jahannam, dan Jahannam itu seburuk-buruk tempat kembali,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,98,'اِلَّا الْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ لَا يَسْتَطِيْعُوْنَ حِيْلَةً وَّلَا يَهْتَدُوْنَ سَبِيْلًاۙ  ','kecuali mereka yang tertindas baik laki-laki atau wanita ataupun anak-anak yang tidak mampu berdaya upaya dan tidak mengetahui jalan (untuk hijrah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,99,'فَاُولٰۤىِٕكَ عَسَى اللّٰهُ اَنْ يَّعْفُوَ عَنْهُمْ ۗ وَكَانَ اللّٰهُ عَفُوًّا غَفُوْرًا ','mereka itu, mudah-mudahan Allah memaafkannya. Dan adalah Allah Maha Pemaaf lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,100,'۞ وَمَنْ يُّهَاجِرْ فِيْ سَبِيْلِ اللّٰهِ يَجِدْ فِى الْاَرْضِ مُرَاغَمًا كَثِيْرًا وَّسَعَةً ۗوَمَنْ يَّخْرُجْ مِنْۢ بَيْتِهٖ مُهَاجِرًا اِلَى اللّٰهِ وَرَسُوْلِهٖ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهٗ عَلَى اللّٰهِ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ','Barangsiapa berhijrah di jalan Allah, niscaya mereka mendapati di muka bumi ini tempat hijrah yang luas dan rezeki yang banyak. Barangsiapa keluar dari rumahnya dengan maksud berhijrah kepada Allah dan Rasul-Nya, kemudian kematian menimpanya (sebelum sampai ke tempat yang dituju), maka sungguh telah tetap pahalanya di sisi Allah. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,101,'وَاِذَا ضَرَبْتُمْ فِى الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوْا مِنَ الصَّلٰوةِ ۖ اِنْ خِفْتُمْ اَنْ يَّفْتِنَكُمُ الَّذِيْنَ كَفَرُوْاۗ اِنَّ الْكٰفِرِيْنَ كَانُوْا لَكُمْ عَدُوًّا مُّبِيْنًا ','Dan apabila kamu bepergian di muka bumi, maka tidaklah mengapa kamu men-qashar sembahyang(mu), jika kamu takut diserang orang-orang kafir. Sesungguhnya orang-orang kafir itu adalah musuh yang nyata bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,102,'وَاِذَا كُنْتَ فِيْهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَاۤىِٕفَةٌ مِّنْهُمْ مَّعَكَ وَلْيَأْخُذُوْٓا اَسْلِحَتَهُمْ ۗ فَاِذَا سَجَدُوْا فَلْيَكُوْنُوْا مِنْ وَّرَاۤىِٕكُمْۖ  وَلْتَأْتِ طَاۤىِٕفَةٌ اُخْرٰى لَمْ يُصَلُّوْا فَلْيُصَلُّوْا مَعَكَ وَلْيَأْخُذُوْا حِذْرَهُمْ وَاَسْلِحَتَهُمْ ۗ وَدَّ الَّذِيْنَ كَفَرُوْا لَوْ تَغْفُلُوْنَ عَنْ اَسْلِحَتِكُمْ وَاَمْتِعَتِكُمْ فَيَمِيْلُوْنَ عَلَيْكُمْ مَّيْلَةً وَّاحِدَةً  ۗوَلَا جُنَاحَ عَلَيْكُمْ اِنْ كَانَ بِكُمْ اَذًى مِّنْ مَّطَرٍ اَوْ كُنْتُمْ مَّرْضٰٓى اَنْ تَضَعُوْٓا اَسْلِحَتَكُمْ وَخُذُوْا حِذْرَكُمْ ۗ اِنَّ اللّٰهَ اَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا ','Dan apabila kamu berada di tengah-tengah mereka (sahabatmu) lalu kamu hendak mendirikan shalat bersama-sama mereka, maka hendaklah segolongan dari mereka berdiri (shalat) besertamu dan menyandang senjata, kemudian apabila mereka (yang shalat besertamu) sujud (telah menyempurnakan serakaat), maka hendaklah mereka pindah dari belakangmu (untuk menghadapi musuh) dan hendaklah datang golongan yang kedua yang belum bersembahyang, lalu bersembahyanglah mereka denganmu], dan hendaklah mereka bersiap siaga dan menyandang senjata. Orang-orang kafir ingin supaya kamu lengah terhadap senjatamu dan harta bendamu, lalu mereka menyerbu kamu dengan sekaligus. Dan tidak ada dosa atasmu meletakkan senjata-senjatamu, jika kamu mendapat sesuatu kesusahan karena hujan atau karena kamu memang sakit; dan siap siagalah kamu. Sesungguhnya Allah telah menyediakan azab yang menghinakan bagi orang-orang kafir itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,103,'فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِكُمْ ۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَقِيْمُوا الصَّلٰوةَ ۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِنِيْنَ كِتٰبًا مَّوْقُوْتًا ','Maka apabila kamu telah menyelesaikan shalat(mu), ingatlah Allah di waktu berdiri, di waktu duduk dan di waktu berbaring. Kemudian apabila kamu telah merasa aman, maka dirikanlah shalat itu (sebagaimana biasa). Sesungguhnya shalat itu adalah fardhu yang ditentukan waktunya atas orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,104,'وَلَا تَهِنُوْا فِى ابْتِغَاۤءِ الْقَوْمِ ۗ اِنْ تَكُوْنُوْا تَأْلَمُوْنَ فَاِنَّهُمْ يَأْلَمُوْنَ كَمَا تَأْلَمُوْنَ ۚوَتَرْجُوْنَ مِنَ اللّٰهِ مَا لَا يَرْجُوْنَ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ࣖ ','Janganlah kamu berhati lemah dalam mengejar mereka (musuhmu). Jika kamu menderita kesakitan, maka sesungguhnya merekapun menderita kesakitan (pula), sebagaimana kamu menderitanya, sedang kamu mengharap dari pada Allah apa yang tidak mereka harapkan. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,105,'اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَآ اَرٰىكَ اللّٰهُ ۗوَلَا تَكُنْ لِّلْخَاۤىِٕنِيْنَ خَصِيْمًا ۙ','Sesungguhnya Kami telah menurunkan kitab kepadamu dengan membawa kebenaran, supaya kamu mengadili antara manusia dengan apa yang telah Allah wahyukan kepadamu, dan janganlah kamu menjadi penantang (orang yang tidak bersalah), karena (membela) orang-orang yang khianat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,106,'وَّاسْتَغْفِرِ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ','dan mohonlah ampun kepada Allah. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,107,'وَلَا تُجَادِلْ عَنِ الَّذِيْنَ يَخْتَانُوْنَ اَنْفُسَهُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّانًا اَثِيْمًاۙ','Dan janganlah kamu berdebat (untuk membela) orang-orang yang mengkhianati dirinya. Sesungguhnya Allah tidak menyukai orang-orang yang selalu berkhianat lagi bergelimang dosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,108,'يَّسْتَخْفُوْنَ مِنَ النَّاسِ وَلَا يَسْتَخْفُوْنَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُوْنَ مَا لَا يَرْضٰى مِنَ الْقَوْلِ ۗ وَكَانَ اللّٰهُ بِمَا يَعْمَلُوْنَ مُحِيْطًا ','mereka bersembunyi dari manusia, tetapi mereka tidak bersembunyi dari Allah, padahal Allah beserta mereka, ketika pada suatu malam mereka menetapkan keputusan rahasia yang Allah tidak redlai. Dan adalah Allah Maha Meliputi (ilmu-Nya) terhadap apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,109,'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ جَادَلْتُمْ عَنْهُمْ فِى الْحَيٰوةِ الدُّنْيَاۗ فَمَنْ يُّجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَّنْ يَّكُوْنُ عَلَيْهِمْ وَكِيْلًا ','Beginilah kamu, kamu sekalian adalah orang-orang yang berdebat untuk (membela) mereka dalam kehidupan dunia ini. Maka siapakah yang akan mendebat Allah untuk (membela) mereka pada hari kiamat? Atau siapakah yang menjadi pelindung mereka (terhadap siksa Allah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,110,'وَمَنْ يَّعْمَلْ سُوْۤءًا اَوْ يَظْلِمْ نَفْسَهٗ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوْرًا رَّحِيْمًا ','Dan barangsiapa yang mengerjakan kejahatan dan menganiaya dirinya, kemudian ia mohon ampun kepada Allah, niscaya ia mendapati Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,111,'وَمَنْ يَّكْسِبْ اِثْمًا فَاِنَّمَا يَكْسِبُهٗ عَلٰى نَفْسِهٖ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ','Barangsiapa yang mengerjakan dosa, maka sesungguhnya ia mengerjakannya untuk (kemudharatan) dirinya sendiri. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,112,'وَمَنْ يَّكْسِبْ خَطِيْۤـَٔةً اَوْ اِثْمًا ثُمَّ يَرْمِ بِهٖ بَرِيْۤـًٔا فَقَدِ احْتَمَلَ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ࣖ ','Dan barangsiapa yang mengerjakan kesalahan atau dosa, kemudian dituduhkannya kepada orang yang tidak bersalah, maka sesungguhnya ia telah berbuat suatu kebohongan dan dosa yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,113,'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهٗ لَهَمَّتْ طَّاۤىِٕفَةٌ مِّنْهُمْ اَنْ يُّضِلُّوْكَۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَضُرُّوْنَكَ مِنْ شَيْءٍ ۗ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتٰبَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُۗ وَكَانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظِيْمًا ','Sekiranya bukan karena karunia Allah dan rahmat-Nya kepadamu, tentulah segolongan dari mereka berkeinginan keras untuk menyesatkanmu. Tetapi mereka tidak menyesatkan melainkan dirinya sendiri, dan mereka tidak dapat membahayakanmu sedikitpun kepadamu. Dan (juga karena) Allah telah menurunkan Kitab dan hikmah kepadamu, dan telah mengajarkan kepadamu apa yang belum kamu ketahui. Dan adalah karunia Allah sangat besar atasmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,114,'۞ لَا خَيْرَ فِيْ كَثِيْرٍ مِّنْ نَّجْوٰىهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوْفٍ اَوْ اِصْلَاحٍۢ بَيْنَ النَّاسِۗ وَمَنْ يَّفْعَلْ ذٰلِكَ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ','Tidak ada kebaikan pada kebanyakan bisikan-bisikan mereka, kecuali bisikan-bisikan dari orang yang menyuruh (manusia) memberi sedekah, atau berbuat ma´ruf, atau mengadakan perdamaian di antara manusia. Dan barangsiapa yang berbuat demikian karena mencari keridhaan Allah, maka kelak Kami memberi kepadanya pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,115,'وَمَنْ يُّشَاقِقِ الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِعْ غَيْرَ سَبِيْلِ الْمُؤْمِنِيْنَ نُوَلِّهٖ مَا تَوَلّٰى وَنُصْلِهٖ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا ࣖ ','Dan barangsiapa yang menentang Rasul sesudah jelas kebenaran baginya, dan mengikuti jalan yang bukan jalan orang-orang mukmin, Kami biarkan ia leluasa terhadap kesesatan yang telah dikuasainya itu dan Kami masukkan ia ke dalam Jahannam, dan Jahannam itu seburuk-buruk tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,116,'اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا ','Sesungguhnya Allah tidak mengampuni dosa mempersekutukan (sesuatu) dengan Dia, dan dia mengampuni dosa yang selain syirik bagi siapa yang dikehendaki-Nya. Barangsiapa yang mempersekutukan (sesuatu) dengan Allah, maka sesungguhnya ia telah tersesat sejauh-jauhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,117,'اِنْ يَّدْعُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اِنَاثًاۚ وَاِنْ يَّدْعُوْنَ اِلَّا شَيْطٰنًا مَّرِيْدًاۙ','Yang mereka sembah selain Allah itu, tidak lain hanyalah berhala, dan (dengan menyembah berhala itu) mereka tidak lain hanyalah menyembah syaitan yang durhaka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,118,'لَّعَنَهُ اللّٰهُ ۘ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيْبًا مَّفْرُوْضًاۙ','yang dilaknati Allah dan syaitan itu mengatakan: "Saya benar-benar akan mengambil dari hamba-hamba Engkau bahagian yang sudah ditentukan (untuk saya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,119,'وَّلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِ ۚ وَمَنْ يَّتَّخِذِ الشَّيْطٰنَ وَلِيًّا مِّنْ دُوْنِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَانًا مُّبِيْنًا ','dan aku benar-benar akan menyesatkan mereka, dan akan membangkitkan angan-angan kosong pada mereka dan menyuruh mereka (memotong telinga-telinga binatang ternak), lalu mereka benar-benar memotongnya, dan akan aku suruh mereka (mengubah ciptaan Allah), lalu benar-benar mereka meubahnya". Barangsiapa yang menjadikan syaitan menjadi pelindung selain Allah, maka sesungguhnya ia menderita kerugian yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,120,'يَعِدُهُمْ وَيُمَنِّيْهِمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا ','Syaitan itu memberikan janji-janji kepada mereka dan membangkitkan angan-angan kosong pada mereka, padahal syaitan itu tidak menjanjikan kepada mereka selain dari tipuan belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,121,'اُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُۖ وَلَا يَجِدُوْنَ عَنْهَا مَحِيْصًا ','Mereka itu tempatnya Jahannam dan mereka tidak memperoleh tempat lari dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,122,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ وَعْدَ اللّٰهِ حَقًّا ۗوَمَنْ اَصْدَقُ مِنَ اللّٰهِ قِيْلًا ','Orang-orang yang beriman dan mengerjakan amalan saleh, kelak akan Kami masukkan ke dalam surga yang mengalir sungai-sungai di dalamnya, mereka kekal di dalamnya selama-lamanya. Allah telah membuat suatu janji yang benar. Dan siapakah yang lebih benar perkataannya dari pada Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,123,'لَيْسَ بِاَمَانِيِّكُمْ وَلَآ اَمَانِيِّ اَهْلِ الْكِتٰبِ ۗ مَنْ يَّعْمَلْ سُوْۤءًا يُّجْزَ بِهٖۙ وَلَا يَجِدْ لَهٗ مِنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا ','(Pahala dari Allah) itu bukanlah menurut angan-anganmu yang kosong dan tidak (pula) menurut angan-angan Ahli Kitab. Barangsiapa yang mengerjakan kejahatan, niscaya akan diberi pembalasan dengan kejahatan itu dan ia tidak mendapat pelindung dan tidak (pula) penolong baginya selain dari Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,124,'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ نَقِيْرًا ','Barangsiapa yang mengerjakan amal-amal saleh, baik laki-laki maupun wanita sedang ia orang yang beriman, maka mereka itu masuk ke dalam surga dan mereka tidak dianiaya walau sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,125,'وَمَنْ اَحْسَنُ دِيْنًا مِّمَّنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ وَّاتَّبَعَ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَاتَّخَذَ اللّٰهُ اِبْرٰهِيْمَ خَلِيْلًا ','Dan siapakah yang lebih baik agamanya dari pada orang yang ikhlas menyerahkan dirinya kepada Allah, sedang diapun mengerjakan kebaikan, dan ia mengikuti agama Ibrahim yang lurus? Dan Allah mengambil Ibrahim menjadi kesayangan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,126,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُّحِيْطًا ࣖ ','Kepunyaan Allah-lah apa yang di langit dan apa yang di bumi, dan adalah (pengetahuan) Allah Maha Meliputi segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,127,'وَيَسْتَفْتُوْنَكَ فِى النِّسَاۤءِۗ  قُلِ اللّٰهُ يُفْتِيْكُمْ فِيْهِنَّ ۙوَمَا يُتْلٰى عَلَيْكُمْ فِى الْكِتٰبِ فِيْ يَتٰمَى النِّسَاۤءِ الّٰتِيْ لَا تُؤْتُوْنَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُوْنَ اَنْ تَنْكِحُوْهُنَّ وَالْمُسْتَضْعَفِيْنَ مِنَ الْوِلْدَانِۙ وَاَنْ تَقُوْمُوْا لِلْيَتٰمٰى بِالْقِسْطِ ۗوَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ كَانَ بِهٖ عَلِيْمًا ','Dan mereka minta fatwa kepadamu tentang para wanita. Katakanlah: "Allah memberi fatwa kepadamu tentang mereka, dan apa yang dibacakan kepadamu dalam Al Quran (juga memfatwakan) tentang para wanita yatim yang kamu tidak memberikan kepada mereka apa yang ditetapkan untuk mereka, sedang kamu ingin mengawini mereka dan tentang anak-anak yang masih dipandang lemah. Dan (Allah menyuruh kamu) supaya kamu mengurus anak-anak yatim secara adil. Dan kebajikan apa saja yang kamu kerjakan, maka sesungguhnya Allah adalah Maha Mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,128,'وَاِنِ امْرَاَةٌ خَافَتْ مِنْۢ بَعْلِهَا نُشُوْزًا اَوْ اِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يُّصْلِحَا بَيْنَهُمَا صُلْحًا ۗوَالصُّلْحُ خَيْرٌ ۗوَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۗ وَاِنْ تُحْسِنُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا ','Dan jika seorang wanita khawatir akan nusyuz atau sikap tidak acuh dari suaminya, maka tidak mengapa bagi keduanya mengadakan perdamaian yang sebenar-benarnya, dan perdamaian itu lebih baik (bagi mereka) walaupun manusia itu menurut tabiatnya kikir. Dan jika kamu bergaul dengan isterimu secara baik dan memelihara dirimu (dari nusyuz dan sikap tak acuh), maka sesungguhnya Allah adalah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,129,'وَلَنْ تَسْتَطِيْعُوْٓا اَنْ تَعْدِلُوْا بَيْنَ النِّسَاۤءِ وَلَوْ حَرَصْتُمْ فَلَا تَمِيْلُوْا كُلَّ الْمَيْلِ فَتَذَرُوْهَا كَالْمُعَلَّقَةِ ۗوَاِنْ تُصْلِحُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا','Dan kamu sekali-kali tidak akan dapat berlaku adil di antara isteri-isteri(mu), walaupun kamu sangat ingin berbuat demikian, karena itu janganlah kamu terlalu cenderung (kepada yang kamu cintai), sehingga kamu biarkan yang lain terkatung-katung. Dan jika kamu mengadakan perbaikan dan memelihara diri (dari kecurangan), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,130,'وَاِنْ يَّتَفَرَّقَا يُغْنِ اللّٰهُ كُلًّا مِّنْ سَعَتِهٖۗ وَكَانَ اللّٰهُ وَاسِعًا حَكِيْمًا ','Jika keduanya bercerai, maka Allah akan memberi kecukupan kepada masing-masingnya dari limpahan karunia-Nya. Dan adalah Allah Maha Luas (karunia-Nya) lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,131,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَلَقَدْ وَصَّيْنَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ غَنِيًّا حَمِيْدًا ','Dan kepunyaan Allah-lah apa yang di langit dan yang di bumi, dan sungguh Kami telah memerintahkan kepada orang-orang yang diberi kitab sebelum kamu dan (juga) kepada kamu; bertakwalah kepada Allah. Tetapi jika kamu kafir maka (ketahuilah), sesungguhnya apa yang di langit dan apa yang di bumi hanyalah kepunyaan Allah dan Allah Maha Kaya dan Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,132,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ','Dan kepunyaan Allah-lah apa yang di langit dan apa yang di bumi. Cukuplah Allah sebagai Pemelihara.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,133,'اِنْ يَّشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَرِيْنَۗ  وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَدِيْرًا ','Jika Allah menghendaki, niscaya Dia musnahkan kamu wahai manusia, dan Dia datangkan umat yang lain (sebagai penggantimu). Dan adalah Allah Maha Kuasa berbuat demikian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,134,'مَنْ كَانَ يُرِيْدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِ ۗوَكَانَ اللّٰهُ سَمِيْعًاۢ بَصِيْرًا ࣖ ','Barangsiapa yang menghendaki pahala di dunia saja (maka ia merugi), karena di sisi Allah ada pahala dunia dan akhirat. Dan Allah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,135,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ بِالْقِسْطِ شُهَدَاۤءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَبِيْنَ ۚ اِنْ يَّكُنْ غَنِيًّا اَوْ فَقِيْرًا فَاللّٰهُ اَوْلٰى بِهِمَاۗ فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْدِلُوْا ۚ وَاِنْ تَلْوٗٓا اَوْ تُعْرِضُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا ','Wahai orang-orang yang beriman, jadilah kamu orang yang benar-benar penegak keadilan, menjadi saksi karena Allah biarpun terhadap dirimu sendiri atau ibu bapa dan kaum kerabatmu. Jika ia kaya ataupun miskin, maka Allah lebih tahu kemaslahatannya. Maka janganlah kamu mengikuti hawa nafsu karena ingin menyimpang dari kebenaran. Dan jika kamu memutar balikkan (kata-kata) atau enggan menjadi saksi, maka sesungguhnya Allah adalah Maha Mengetahui segala apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,136,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْ نَزَّلَ عَلٰى رَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْٓ اَنْزَلَ مِنْ قَبْلُ ۗوَمَنْ يَّكْفُرْ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا ','Wahai orang-orang yang beriman, tetaplah beriman kepada Allah dan Rasul-Nya dan kepada kitab yang Allah turunkan kepada Rasul-Nya serta kitab yang Allah turunkan sebelumnya. Barangsiapa yang kafir kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya, rasul-rasul-Nya, dan hari kemudian, maka sesungguhnya orang itu telah sesat sejauh-jauhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,137,'اِنَّ الَّذِيْنَ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ ازْدَادُوْا كُفْرًا لَّمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيْلًاۗ  ','Sesungguhnya orang-orang yang beriman kemudian kafir, kemudian beriman (pula), kamudian kafir lagi, kemudian bertambah kekafirannya, maka sekali-kali Allah tidak akan memberi ampunan kepada mereka, dan tidak (pula) menunjuki mereka kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,138,'بَشِّرِ الْمُنٰفِقِيْنَ بِاَنَّ لَهُمْ عَذَابًا اَلِيْمًاۙ','Kabarkanlah kepada orang-orang munafik bahwa mereka akan mendapat siksaan yang pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,139,' ۨالَّذِيْنَ يَتَّخِذُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۗ اَيَبْتَغُوْنَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ','(yaitu) orang-orang yang mengambil orang-orang kafir menjadi teman-teman penolong dengan meninggalkan orang-orang mukmin. Apakah mereka mencari kekuatan di sisi orang kafir itu? Maka sesungguhnya semua kekuatan kepunyaan Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,140,'وَقَدْ نَزَّلَ عَلَيْكُمْ فِى الْكِتٰبِ اَنْ اِذَا سَمِعْتُمْ اٰيٰتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوْا مَعَهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖٓ  ۖ اِنَّكُمْ اِذًا مِّثْلُهُمْ ۗ اِنَّ اللّٰهَ جَامِعُ الْمُنٰفِقِيْنَ وَالْكٰفِرِيْنَ فِيْ جَهَنَّمَ جَمِيْعًاۙ   ','Dan sungguh Allah telah menurunkan kekuatan kepada kamu di dalam Al Quran bahwa apabila kamu mendengar ayat-ayat Allah diingkari dan diperolok-olokkan (oleh orang-orang kafir), maka janganlah kamu duduk beserta mereka, sehingga mereka memasuki pembicaraan yang lain. Karena sesungguhnya (kalau kamu berbuat demikian), tentulah kamu serupa dengan mereka. Sesungguhnya Allah akan mengumpulkan semua orang-orang munafik dan orang-orang kafir di dalam Jahannam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,141,' ۨالَّذِيْنَ يَتَرَبَّصُوْنَ بِكُمْۗ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِّنَ اللّٰهِ قَالُوْٓا اَلَمْ نَكُنْ مَّعَكُمْ ۖ وَاِنْ كَانَ لِلْكٰفِرِيْنَ نَصِيْبٌ قَالُوْٓا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِّنَ الْمُؤْمِنِيْنَ ۗ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ ۗ وَلَنْ يَّجْعَلَ اللّٰهُ لِلْكٰفِرِيْنَ عَلَى الْمُؤْمِنِيْنَ سَبِيْلًا ࣖ','(yaitu) orang-orang yang menunggu-nunggu (peristiwa) yang akan terjadi pada dirimu (hai orang-orang mukmin). Maka jika terjadi bagimu kemenangan dari Allah mereka berkata: "Bukankah kami (turut berperang) beserta kamu?" Dan jika orang-orang kafir mendapat keberuntungan (kemenangan) mereka berkata: "Bukankah kami turut memenangkanmu, dan membela kamu dari orang-orang mukmin?" Maka Allah akan memberi keputusan di antara kamu di hari kiamat dan Allah sekali-kali tidak akan memberi jalan kepada orang-orang kafir untuk memusnahkan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,142,'اِنَّ الْمُنٰفِقِيْنَ يُخٰدِعُوْنَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُوْٓا اِلَى الصَّلٰوةِ قَامُوْا كُسَالٰىۙ يُرَاۤءُوْنَ النَّاسَ وَلَا يَذْكُرُوْنَ اللّٰهَ اِلَّا قَلِيْلًاۖ  ','Sesungguhnya orang-orang munafik itu menipu Allah, dan Allah akan membalas tipuan mereka. Dan apabila mereka berdiri untuk shalat mereka berdiri dengan malas. Mereka bermaksud riya (dengan shalat) di hadapan manusia. Dan tidaklah mereka menyebut Allah kecuali sedikit sekali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,143,'مُّذَبْذَبِيْنَ بَيْنَ ذٰلِكَۖ  لَآ اِلٰى هٰٓؤُلَاۤءِ وَلَآ اِلٰى هٰٓؤُلَاۤءِ ۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ','Mereka dalam keadaan ragu-ragu antara yang demikian (iman atau kafir): tidak masuk kepada golongan ini (orang-orang beriman) dan tidak (pula) kepada golongan itu (orang-orang kafir), maka kamu sekali-kali tidak akan mendapat jalan (untuk memberi petunjuk) baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,144,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۚ اَتُرِيْدُوْنَ اَنْ تَجْعَلُوْا لِلّٰهِ عَلَيْكُمْ سُلْطٰنًا مُّبِيْنًا ','Hai orang-orang yang beriman, janganlah kamu mengambil orang-orang kafir menjadi wali dengan meninggalkan orang-orang mukmin. Inginkah kamu mengadakan alasan yang nyata bagi Allah (untuk menyiksamu)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,145,'اِنَّ الْمُنٰفِقِيْنَ فِى الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَصِيْرًاۙ','Sesungguhnya orang-orang munafik itu (ditempatkan) pada tingkatan yang paling bawah dari neraka. Dan kamu sekali-kali tidak akan mendapat seorang penolongpun bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,146,'اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَاعْتَصَمُوْا بِاللّٰهِ وَاَخْلَصُوْا دِيْنَهُمْ لِلّٰهِ فَاُولٰۤىِٕكَ مَعَ الْمُؤْمِنِيْنَۗ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِنِيْنَ اَجْرًا عَظِيْمًا ','Kecuali orang-orang yang taubat dan mengadakan perbaikan dan berpegang teguh pada (agama) Allah dan tulus ikhlas (mengerjakan) agama mereka karena Allah. Maka mereka itu adalah bersama-sama orang yang beriman dan kelak Allah akan memberikan kepada orang-orang yang beriman pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,5,4,147,'مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۗ وَكَانَ اللّٰهُ شَاكِرًا عَلِيْمًا   ۔','Mengapa Allah akan menyiksamu, jika kamu bersyukur dan beriman? Dan Allah adalah Maha Mensyukuri lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,148,'۞ لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّوْۤءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَ ۗ وَكَانَ اللّٰهُ سَمِيْعًا عَلِيْمًا ','Allah tidak menyukai ucapan buruk, (yang diucapkan) dengan terus terang kecuali oleh orang yang dianiaya. Allah adalah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,149,'اِنْ تُبْدُوْا خَيْرًا اَوْ تُخْفُوْهُ اَوْ تَعْفُوْا عَنْ سُوْۤءٍ فَاِنَّ اللّٰهَ كَانَ عَفُوًّا قَدِيْرًا','Jika kamu melahirkan sesuatu kebaikan atau menyembunyikan atau memaafkan sesuatu kesalahan (orang lain), maka sesungguhnya Allah Maha Pemaaf lagi Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,150,'اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاللّٰهِ وَرُسُلِهٖ وَيُرِيْدُوْنَ اَنْ يُّفَرِّقُوْا بَيْنَ اللّٰهِ وَرُسُلِهٖ وَيَقُوْلُوْنَ نُؤْمِنُ بِبَعْضٍ وَّنَكْفُرُ بِبَعْضٍۙ وَّيُرِيْدُوْنَ اَنْ يَّتَّخِذُوْا بَيْنَ ذٰلِكَ سَبِيْلًاۙ ','Sesungguhnya orang-orang yang kafir kepada Allah dan rasul-rasul-Nya, dan bermaksud memperbedakan antara (keimanan kepada) Allah dan rasul-rasul-Nya, dengan mengatakan: "Kami beriman kepada yang sebahagian dan kami kafir terhadap sebahagian (yang lain)", serta bermaksud (dengan perkataan itu) mengambil jalan (tengah) di antara yang demikian (iman atau kafir),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,151,'اُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ حَقًّا ۚوَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا','merekalah orang-orang yang kafir sebenar-benarnya. Kami telah menyediakan untuk orang-orang yang kafir itu siksaan yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,152,'وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖ وَلَمْ يُفَرِّقُوْا بَيْنَ اَحَدٍ مِّنْهُمْ اُولٰۤىِٕكَ سَوْفَ يُؤْتِيْهِمْ اُجُوْرَهُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ','Orang-orang yang beriman kepada Allah dan para rasul-Nya dan tidak membeda-bedakan seorangpun di antara mereka, kelak Allah akan memberikan kepada mereka pahalanya. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,153,'يَسْـَٔلُكَ اَهْلُ الْكِتٰبِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتٰبًا مِّنَ السَّمَاۤءِ فَقَدْ سَاَلُوْا مُوْسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُوْٓا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ  ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ فَعَفَوْنَا عَنْ ذٰلِكَ ۚ وَاٰتَيْنَا مُوْسٰى سُلْطٰنًا مُّبِيْنًا','Ahli Kitab meminta kepadamu agar kamu menurunkan kepada mereka sebuah Kitab dari langit. Maka sesungguhnya mereka telah meminta kepada Musa yang lebih besar dari itu. Mereka berkata: "Perlihatkanlah Allah kepada kami dengan nyata". Maka mereka disambar petir karena kezalimannya, dan mereka menyembah anak sapi, sesudah datang kepada mereka bukti-bukti yang nyata, lalu Kami maafkan (mereka) dari yang demikian. Dan telah Kami berikan kepada Musa keterangan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,154,'وَرَفَعْنَا فَوْقَهُمُ الطُّوْرَ بِمِيْثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَّقُلْنَا لَهُمْ لَا تَعْدُوْا فِى السَّبْتِ وَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًا','Dan telah Kami angkat ke atas (kepala) mereka bukit Thursina untuk (menerima) perjanjian (yang telah Kami ambil dari) mereka. Dan kami perintahkan kepada mereka: "Masuklah pintu gerbang itu sambil bersujud", dan Kami perintahkan (pula) kepada mereka: "Janganlah kamu melanggar peraturan mengenai hari Sabtu", dan Kami telah mengambil dari mereka perjanjian yang kokoh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,155,'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ وَكُفْرِهِمْ بِاٰيٰتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّ وَّقَوْلِهِمْ قُلُوْبُنَا غُلْفٌ ۗ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًاۖ ','Maka (Kami lakukan terhadap mereka beberapa tindakan), disebabkan mereka melanggar perjanjian itu, dan karena kekafiran mereka terhadap keterangan-keterangan Allah dan mereka membunuh nabi-nabi tanpa (alasan) yang benar dan mengatakan: "Hati kami tertutup". Bahkan, sebenarnya Allah telah mengunci mati hati mereka karena kekafirannya, karena itu mereka tidak beriman kecuali sebahagian kecil dari mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,156,'وَّبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَانًا عَظِيْمًاۙ ','Dan karena kekafiran mereka (terhadap Isa) dan tuduhan mereka terhadap Maryam dengan kedustaan besar (zina),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,157,'وَّقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَسِيْحَ عِيْسَى ابْنَ مَرْيَمَ رَسُوْلَ اللّٰهِۚ وَمَا قَتَلُوْهُ وَمَا صَلَبُوْهُ وَلٰكِنْ شُبِّهَ لَهُمْ ۗوَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِيْهِ لَفِيْ شَكٍّ مِّنْهُ ۗمَا لَهُمْ بِهٖ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّنِّ وَمَا قَتَلُوْهُ يَقِيْنًاۢ ۙ ','dan karena ucapan mereka: "Sesungguhnya kami telah membunuh Al Masih, Isa putra Maryam, Rasul Allah", padahal mereka tidak membunuhnya dan tidak (pula) menyalibnya, tetapi (yang mereka bunuh ialah) orang yang diserupakan dengan Isa bagi mereka. Sesungguhnya orang-orang yang berselisih paham tentang (pembunuhan) Isa, benar-benar dalam keragu-raguan tentang yang dibunuh itu. Mereka tidak mempunyai keyakinan tentang siapa yang dibunuh itu, kecuali mengikuti persangkaan belaka, mereka tidak (pula) yakin bahwa yang mereka bunuh itu adalah Isa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,158,'بَلْ رَّفَعَهُ اللّٰهُ اِلَيْهِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا','Tetapi (yang sebenarnya), Allah telah mengangkat Isa kepada-Nya. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,159,'وَاِنْ مِّنْ اَهْلِ الْكِتٰبِ اِلَّا لَيُؤْمِنَنَّ بِهٖ قَبْلَ مَوْتِهٖ ۚوَيَوْمَ الْقِيٰمَةِ يَكُوْنُ عَلَيْهِمْ شَهِيْدًاۚ ','Tidak ada seorangpun dari Ahli Kitab, kecuali akan beriman kepadanya (Isa) sebelum kematiannya. Dan di hari kiamat nanti Isa itu akan menjadi saksi terhadap mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,160,'فَبِظُلْمٍ مِّنَ الَّذِيْنَ هَادُوْا حَرَّمْنَا عَلَيْهِمْ طَيِّبٰتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيْلِ اللّٰهِ كَثِيْرًاۙ ','Maka disebabkan kezaliman orang-orang Yahudi, kami haramkan atas (memakan makanan) yang baik-baik (yang dahulunya) dihalalkan bagi mereka, dan karena mereka banyak menghalangi (manusia) dari jalan Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,161,'وَّاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوْا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِ ۗوَاَعْتَدْنَا لِلْكٰفِرِيْنَ مِنْهُمْ عَذَابًا اَلِيْمًا','dan disebabkan mereka memakan riba, padahal sesungguhnya mereka telah dilarang daripadanya, dan karena mereka memakan harta benda orang dengan jalan yang batil. Kami telah menyediakan untuk orang-orang yang kafir di antara mereka itu siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,162,'لٰكِنِ الرَّاسِخُوْنَ فِى الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُوْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيْمِيْنَ الصَّلٰوةَ وَالْمُؤْتُوْنَ الزَّكٰوةَ وَالْمُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ اُولٰۤىِٕكَ سَنُؤْتِيْهِمْ اَجْرًا عَظِيْمًا ࣖ','Tetapi orang-orang yang mendalam ilmunya di antara mereka dan orang-orang mukmin, mereka beriman kepada apa yang telah diturunkan kepadamu (Al Quran), dan apa yang telah diturunkan sebelummu dan orang-orang yang mendirikan shalat, menunaikan zakat, dan yang beriman kepada Allah dan hari kemudian. Orang-orang itulah yang akan Kami berikan kepada mereka pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,163,'۞ اِنَّآ اَوْحَيْنَآ اِلَيْكَ كَمَآ اَوْحَيْنَآ اِلٰى نُوْحٍ وَّالنَّبِيّٖنَ مِنْۢ بَعْدِهٖۚ وَاَوْحَيْنَآ اِلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَعِيْسٰى وَاَيُّوْبَ وَيُوْنُسَ وَهٰرُوْنَ وَسُلَيْمٰنَ ۚوَاٰتَيْنَا دَاوٗدَ زَبُوْرًاۚ ','Sesungguhnya Kami telah memberikan wahyu kepadamu sebagaimana Kami telah memberikan wahyu kepada Nuh dan nabi-nabi yang kemudiannya, dan Kami telah memberikan wahyu (pula) kepada Ibrahim, Isma´il, Ishak, Ya´qub dan anak cucunya, Isa, Ayyub, Yunus, Harun dan Sulaiman. Dan Kami berikan Zabur kepada Daud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,164,'وَرُسُلًا قَدْ قَصَصْنٰهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلًا لَّمْ نَقْصُصْهُمْ عَلَيْكَ ۗوَكَلَّمَ اللّٰهُ مُوْسٰى تَكْلِيْمًاۚ ','Dan (Kami telah mengutus) rasul-rasul yang sungguh telah Kami kisahkan tentang mereka kepadamu dahulu, dan rasul-rasul yang tidak Kami kisahkan tentang mereka kepadamu. Dan Allah telah berbicara kepada Musa dengan langsung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,165,'رُسُلًا مُّبَشِّرِيْنَ وَمُنْذِرِيْنَ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ ۢ بَعْدَ الرُّسُلِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا','(Mereka Kami utus) selaku rasul-rasul pembawa berita gembira dan pemberi peringatan agar supaya tidak ada alasan bagi manusia membantah Allah sesudah diutusnya rasul-rasul itu. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,166,'لٰكِنِ اللّٰهُ يَشْهَدُ بِمَآ اَنْزَلَ اِلَيْكَ اَنْزَلَهٗ بِعِلْمِهٖ ۚوَالْمَلٰۤىِٕكَةُ يَشْهَدُوْنَ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًاۗ ','(Mereka tidak mau mengakui yang diturunkan kepadamu itu), tetapi Allah mengakui Al Quran yang diturunkan-Nya kepadamu. Allah menurunkannya dengan ilmu-Nya; dan malaikat-malaikat pun menjadi saksi (pula). Cukuplah Allah yang mengakuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,167,'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ قَدْ ضَلُّوْا ضَلٰلًا ۢ بَعِيْدًا','Sesungguhnya orang-orang yang kafir dan menghalang-halangi (manusia) dari jalan Allah, benar-benar telah sesat sejauh-jauhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,168,'اِنَّ الَّذِيْنَ كَفَرُوْا وَظَلَمُوْا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيْقًاۙ ','Sesungguhnya orang-orang yang kafir dan melakukan kezaliman, Allah sekali-kali tidak akan mengampuni (dosa) mereka dan tidak (pula) akan menunjukkan jalan kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,169,'اِلَّا طَرِيْقَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا','kecuali jalan ke neraka Jahannam; mereka kekal di dalamnya selama-lamanya. Dan yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,170,'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الرَّسُوْلُ بِالْحَقِّ مِنْ رَّبِّكُمْ فَاٰمِنُوْا خَيْرًا لَّكُمْ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا','Wahai manusia, sesungguhnya telah datang Rasul (Muhammad) itu kepadamu dengan (membawa) kebenaran dari Tuhanmu, maka berimanlah kamu, itulah yang lebih baik bagimu. Dan jika kamu kafir, (maka kekafiran itu tidak merugikan Allah sedikitpun) karena sesungguhnya apa yang di langit dan di bumi itu adalah kepunyaan Allah. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,171,'يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ وَلَا تَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّۗ اِنَّمَا الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ رَسُوْلُ اللّٰهِ وَكَلِمَتُهٗ ۚ اَلْقٰهَآ اِلٰى مَرْيَمَ وَرُوْحٌ مِّنْهُ ۖفَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖۗ وَلَا تَقُوْلُوْا ثَلٰثَةٌ  ۗاِنْتَهُوْا خَيْرًا لَّكُمْ ۗ اِنَّمَا اللّٰهُ اِلٰهٌ وَّاحِدٌ ۗ سُبْحٰنَهٗٓ اَنْ يَّكُوْنَ لَهٗ وَلَدٌ ۘ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا ࣖ ','Wahai Ahli Kitab, janganlah kamu melampaui batas dalam agamamu, dan janganlah kamu mengatakan terhadap Allah kecuali yang benar. Sesungguhnya Al Masih, Isa putera Maryam itu, adalah utusan Allah dan (yang diciptakan dengan) kalimat-Nya yang disampaikan-Nya kepada Maryam, dan (dengan tiupan) roh dari-Nya. Maka berimanlah kamu kepada Allah dan rasul-rasul-Nya dan janganlah kamu mengatakan: "(Tuhan itu) tiga", berhentilah (dari ucapan itu). (Itu) lebih baik bagimu. Sesungguhnya Allah Tuhan Yang Maha Esa, Maha Suci Allah dari mempunyai anak, segala yang di langit dan di bumi adalah kepunyaan-Nya. Cukuplah Allah menjadi Pemelihara.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,172,'لَنْ يَّسْتَنْكِفَ الْمَسِيْحُ اَنْ يَّكُوْنَ عَبْدًا لِّلّٰهِ وَلَا الْمَلٰۤىِٕكَةُ الْمُقَرَّبُوْنَۗ وَمَنْ يَّسْتَنْكِفْ عَنْ عِبَادَتِهٖ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَمِيْعًا','Al Masih sekali-kali tidak enggan menjadi hamba bagi Allah, dan tidak (pula enggan) malaikat-malaikat yang terdekat (kepada Allah). Barangsiapa yang enggan dari menyembah-Nya, dan menyombongkan diri, nanti Allah akan mengumpulkan mereka semua kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,173,'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖۚ وَاَمَّا الَّذِيْنَ اسْتَنْكَفُوْا وَاسْتَكْبَرُوْا فَيُعَذِّبُهُمْ عَذَابًا اَلِيْمًاۙ وَّلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا','Adapun orang-orang yang beriman dan berbuat amal saleh, maka Allah akan menyempurnakan pahala mereka dan menambah untuk mereka sebagian dari karunia-Nya. Adapun orang-orang yang enggan dan menyombongkan diri, maka Allah akan menyiksa mereka dengan siksaan yang pedih, dan mereka tidak akan memperoleh bagi diri mereka, pelindung dan penolong selain dari pada Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,174,'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمْ بُرْهَانٌ مِّنْ رَّبِّكُمْ وَاَنْزَلْنَآ اِلَيْكُمْ نُوْرًا مُّبِيْنًا','Hai manusia, sesungguhnya telah datang kepadamu bukti kebenaran dari Tuhanmu. (Muhammad dengan mukjizatnya) dan telah Kami turunkan kepadamu cahaya yang terang benderang (Al Quran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,175,'فَاَمَّا الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَاعْتَصَمُوْا بِهٖ فَسَيُدْخِلُهُمْ فِيْ رَحْمَةٍ مِّنْهُ وَفَضْلٍۙ وَّيَهْدِيْهِمْ اِلَيْهِ صِرَاطًا مُّسْتَقِيْمًاۗ ','Adapun orang-orang yang beriman kepada Allah dan berpegang teguh kepada (agama)-Nya niscaya Allah akan memasukkan mereka ke dalam rahmat yang besar dari-Nya (surga) dan limpahan karunia-Nya. Dan menunjuki mereka kepada jalan yang lurus (untuk sampai) kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,4,176,'يَسْتَفْتُوْنَكَۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِى الْكَلٰلَةِ ۗاِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهٗ وَلَدٌ وَّلَهٗٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ اِنْ لَّمْ يَكُنْ لَّهَا وَلَدٌ ۚ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثٰنِ مِمَّا تَرَكَ ۗوَاِنْ كَانُوْٓا اِخْوَةً رِّجَالًا وَّنِسَاۤءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۗ يُبَيِّنُ اللّٰهُ لَكُمْ اَنْ تَضِلُّوْا ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ ','Mereka meminta fatwa kepadamu (tentang kalalah). Katakanlah: "Allah memberi fatwa kepadamu tentang kalalah (yaitu): jika seorang meninggal dunia, dan ia tidak mempunyai anak dan mempunyai saudara perempuan, maka bagi saudaranya yang perempuan itu seperdua dari harta yang ditinggalkannya, dan saudaranya yang laki-laki mempusakai (seluruh harta saudara perempuan), jika ia tidak mempunyai anak; tetapi jika saudara perempuan itu dua orang, maka bagi keduanya dua pertiga dari harta yang ditinggalkan oleh yang meninggal. Dan jika mereka (ahli waris itu terdiri dari) saudara-saudara laki dan perempuan, maka bahagian seorang saudara laki-laki sebanyak bahagian dua orang saudara perempuan. Allah menerangkan (hukum ini) kepadamu, supaya kamu tidak sesat. Dan Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,1,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَوْفُوْا بِالْعُقُوْدِۗ  اُحِلَّتْ لَكُمْ بَهِيْمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّى الصَّيْدِ وَاَنْتُمْ حُرُمٌۗ  اِنَّ اللّٰهَ يَحْكُمُ مَا يُرِيْدُ','Hai orang-orang yang beriman, penuhilah aqad-aqad itu. Dihalalkan bagimu binatang ternak, kecuali yang akan dibacakan kepadamu. (Yang demikian itu) dengan tidak menghalalkan berburu ketika kamu sedang mengerjakan haji. Sesungguhnya Allah menetapkan hukum-hukum menurut yang dikehendaki-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,2,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحِلُّوْا شَعَاۤىِٕرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَاۤىِٕدَ وَلَآ اٰۤمِّيْنَ الْبَيْتَ الْحَرَامَ يَبْتَغُوْنَ فَضْلًا مِّنْ رَّبِّهِمْ وَرِضْوَانًا ۗوَاِذَا حَلَلْتُمْ فَاصْطَادُوْا ۗوَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ اَنْ صَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ اَنْ تَعْتَدُوْۘا وَتَعَاوَنُوْا عَلَى الْبِرِّ وَالتَّقْوٰىۖ وَلَا تَعَاوَنُوْا عَلَى الْاِثْمِ وَالْعُدْوَانِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ','Hai orang-orang yang beriman, janganlah kamu melanggar syi´ar-syi´ar Allah, dan jangan melanggar kehormatan bulan-bulan haram, jangan (mengganggu) binatang-binatang had-ya, dan binatang-binatang qalaa-id, dan jangan (pula) mengganggu orang-orang yang mengunjungi Baitullah sedang mereka mencari kurnia dan keridhaan dari Tuhannya dan apabila kamu telah menyelesaikan ibadah haji, maka bolehlah berburu. Dan janganlah sekali-kali kebencian(mu) kepada sesuatu kaum karena mereka menghalang-halangi kamu dari Masjidilharam, mendorongmu berbuat aniaya (kepada mereka). Dan tolong-menolonglah kamu dalam (mengerjakan) kebajikan dan takwa, dan jangan tolong-menolong dalam berbuat dosa dan pelanggaran. Dan bertakwalah kamu kepada Allah, sesungguhnya Allah amat berat siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,3,'حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖ وَالْمُنْخَنِقَةُ وَالْمَوْقُوْذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيْحَةُ وَمَآ اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْۗ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَنْ تَسْتَقْسِمُوْا بِالْاَزْلَامِۗ  ذٰلِكُمْ فِسْقٌۗ  اَلْيَوْمَ يَىِٕسَ الَّذِيْنَ كَفَرُوْا مِنْ دِيْنِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۗ  اَلْيَوْمَ اَكْمَلْتُ لَكُمْ دِيْنَكُمْ وَاَتْمَمْتُ عَلَيْكُمْ نِعْمَتِيْ وَرَضِيْتُ لَكُمُ الْاِسْلَامَ دِيْنًاۗ  فَمَنِ اضْطُرَّ فِيْ مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِّاِثْمٍۙ  فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','Diharamkan bagimu (memakan) bangkai, darah, daging babi, (daging hewan) yang disembelih atas nama selain Allah, yang tercekik, yang terpukul, yang jatuh, yang ditanduk, dan diterkam binatang buas, kecuali yang sempat kamu menyembelihnya, dan (diharamkan bagimu) yang disembelih untuk berhala. Dan (diharamkan juga) mengundi nasib dengan anak panah, (mengundi nasib dengan anak panah itu) adalah kefasikan. Pada hari ini orang-orang kafir telah putus asa untuk (mengalahkan) agamamu, sebab itu janganlah kamu takut kepada mereka dan takutlah kepada-Ku. Pada hari ini telah Kusempurnakan untuk kamu agamamu, dan telah Ku-cukupkan kepadamu nikmat-Ku, dan telah Ku-ridhai Islam itu jadi agama bagimu. Maka barang siapa terpaksa karena kelaparan tanpa sengaja berbuat dosa, sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,4,'يَسْـَٔلُوْنَكَ مَاذَآ اُحِلَّ لَهُمْۗ  قُلْ اُحِلَّ لَكُمُ الطَّيِّبٰتُۙ وَمَا عَلَّمْتُمْ مِّنَ الْجَوَارِحِ مُكَلِّبِيْنَ تُعَلِّمُوْنَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُ  فَكُلُوْا مِمَّآ اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ','Mereka menanyakan kepadamu: "Apakah yang dihalalkan bagi mereka?". Katakanlah: "Dihalalkan bagimu yang baik-baik dan (buruan yang ditangkap) oleh binatang buas yang telah kamu ajar dengan melatih nya untuk berburu; kamu mengajarnya menurut apa yang telah diajarkan Allah kepadamu. Maka makanlah dari apa yang ditangkapnya untukmu, dan sebutlah nama Allah atas binatang buas itu (waktu melepaskannya). Dan bertakwalah kepada Allah, sesungguhnya Allah amat cepat hisab-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,5,'اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبٰتُۗ وَطَعَامُ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حِلٌّ لَّكُمْ ۖوَطَعَامُكُمْ حِلٌّ لَّهُمْ ۖوَالْمُحْصَنٰتُ مِنَ الْمُؤْمِنٰتِ وَالْمُحْصَنٰتُ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّ مُحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ وَلَا مُتَّخِذِيْٓ اَخْدَانٍۗ وَمَنْ يَّكْفُرْ بِالْاِيْمَانِ فَقَدْ حَبِطَ عَمَلُهٗ ۖوَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ ࣖ ','Pada hari ini dihalalkan bagimu yang baik-baik. Makanan (sembelihan) orang-orang yang diberi Al Kitab itu halal bagimu, dan makanan kamu halal (pula) bagi mereka. (Dan dihalalkan mangawini) wanita yang menjaga kehormatan diantara wanita-wanita yang beriman dan wanita-wanita yang menjaga kehormatan di antara orang-orang yang diberi Al Kitab sebelum kamu, bila kamu telah membayar mas kawin mereka dengan maksud menikahinya, tidak dengan maksud berzina dan tidak (pula) menjadikannya gundik-gundik. Barangsiapa yang kafir sesudah beriman (tidak menerima hukum-hukum Islam) maka hapuslah amalannya dan ia di hari kiamat termasuk orang-orang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,6,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوْا وُجُوْهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوْا بِرُءُوْسِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۗ وَاِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوْاۗ وَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ مِّنْهُ ۗمَا يُرِيْدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِّنْ حَرَجٍ وَّلٰكِنْ يُّرِيْدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ','Hai orang-orang yang beriman, apabila kamu hendak mengerjakan shalat, maka basuhlah mukamu dan tanganmu sampai dengan siku, dan sapulah kepalamu dan (basuh) kakimu sampai dengan kedua mata kaki, dan jika kamu junub maka mandilah, dan jika kamu sakit atau dalam perjalanan atau kembali dari tempat buang air (kakus) atau menyentuh perempuan, lalu kamu tidak memperoleh air, maka bertayammumlah dengan tanah yang baik (bersih); sapulah mukamu dan tanganmu dengan tanah itu. Allah tidak hendak menyulitkan kamu, tetapi Dia hendak membersihkan kamu dan menyempurnakan nikmat-Nya bagimu, supaya kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,7,'وَاذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَمِيْثَاقَهُ الَّذِيْ وَاثَقَكُمْ بِهٖٓ ۙاِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَا ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ','Dan ingatlah karunia Allah kepadamu dan perjanjian-Nya yang telah diikat-Nya dengan kamu, ketika kamu mengatakan: "Kami dengar dan kami taati". Dan bertakwalah kepada Allah, sesungguhnya Allah Mengetahui isi hati(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,8,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ لِلّٰهِ شُهَدَاۤءَ بِالْقِسْطِۖ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُوْا ۗاِعْدِلُوْاۗ هُوَ اَقْرَبُ لِلتَّقْوٰىۖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ','Hai orang-orang yang beriman hendaklah kamu jadi orang-orang yang selalu menegakkan (kebenaran) karena Allah, menjadi saksi dengan adil. Dan janganlah sekali-kali kebencianmu terhadap sesuatu kaum, mendorong kamu untuk berlaku tidak adil. Berlaku adillah, karena adil itu lebih dekat kepada takwa. Dan bertakwalah kepada Allah, sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,9,'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۙ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ','Allah telah menjanjikan kepada orang-orang yang beriman dan yang beramal saleh, (bahwa) untuk mereka ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,10,'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ','Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu adalah penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,11,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَّبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ࣖ ','Hai orang-orang yang beriman, ingatlah kamu akan nikmat Allah (yang diberikan-Nya) kepadamu, di waktu suatu kaum bermaksud hendak menggerakkan tangannya kepadamu (untuk berbuat jahat), maka Allah menahan tangan mereka dari kamu. Dan bertakwalah kepada Allah, dan hanya kepada Allah sajalah orang-orang mukmin itu harus bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,12,'۞ وَلَقَدْ اَخَذَ اللّٰهُ مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيْبًاۗ وَقَالَ اللّٰهُ اِنِّيْ مَعَكُمْ ۗ لَىِٕنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُلِيْ وَعَزَّرْتُمُوْهُمْ وَاَقْرَضْتُمُ اللّٰهَ قَرْضًا حَسَنًا لَّاُكَفِّرَنَّ عَنْكُمْ سَيِّاٰتِكُمْ وَلَاُدْخِلَنَّكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ ','Dan sesungguhnya Allah telah mengambil perjanjian (dari) Bani Israil dan telah Kami angkat diantara mereka 12 orang pemimpin dan Allah berfirman: "Sesungguhnya Aku beserta kamu, sesungguhnya jika kamu mendirikan shalat dan menunaikan zakat serta beriman kepada rasul-rasul-Ku dan kamu bantu mereka dan kamu pinjamkan kepada Allah pinjaman yang baik sesungguhnya Aku akan menutupi dosa-dosamu. Dan sesungguhnya kamu akan Kumasukkan ke dalam surga yang mengalir air didalamnya sungai-sungai. Maka barangsiapa yang kafir di antaramu sesudah itu, sesungguhnya ia telah tersesat dari jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,13,'فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ لَعَنّٰهُمْ وَجَعَلْنَا قُلُوْبَهُمْ قٰسِيَةً ۚ يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖۙ وَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَاۤىِٕنَةٍ مِّنْهُمْ اِلَّا قَلِيْلًا مِّنْهُمْ ۖ فَاعْفُ عَنْهُمْ وَاصْفَحْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ','(Tetapi) karena mereka melanggar janjinya, Kami kutuki mereka, dan Kami jadikan hati mereka keras membatu. Mereka suka merubah perkataan (Allah) dari tempat-tempatnya, dan mereka (sengaja) melupakan sebagian dari apa yang mereka telah diperingatkan dengannya, dan kamu (Muhammad) senantiasa akan melihat kekhianatan dari mereka kecuali sedikit diantara mereka (yang tidak berkhianat), maka maafkanlah mereka dan biarkan mereka, sesungguhnya Allah menyukai orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,14,'وَمِنَ الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰٓى اَخَذْنَا مِيْثَاقَهُمْ فَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِ ۗ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوْا يَصْنَعُوْنَ','Dan diantara orang-orang yang mengatakan: "Sesungguhnya kami ini orang-orang Nasrani", ada yang telah kami ambil perjanjian mereka, tetapi mereka (sengaja) melupakan sebagian dari apa yang mereka telah diberi peringatan dengannya; maka Kami timbulkan di antara mereka permusuhan dan kebencian sampai hari kiamat. Dan kelak Allah akan memberitakan kepada mereka apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,15,'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ كَثِيْرًا مِّمَّا كُنْتُمْ تُخْفُوْنَ مِنَ الْكِتٰبِ وَيَعْفُوْا عَنْ كَثِيْرٍەۗ قَدْ جَاۤءَكُمْ مِّنَ اللّٰهِ نُوْرٌ وَّكِتٰبٌ مُّبِيْنٌۙ','Hai Ahli Kitab, sesungguhnya telah datang kepadamu Rasul Kami, menjelaskan kepadamu banyak dari isi Al Kitab yang kamu sembunyi kan, dan banyak (pula yang) dibiarkannya. Sesungguhnya telah datang kepadamu cahaya dari Allah, dan Kitab yang menerangkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,16,'يَّهْدِيْ بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهٗ سُبُلَ السَّلٰمِ وَيُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِ بِاِذْنِهٖ وَيَهْدِيْهِمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ','Dengan kitab itulah Allah menunjuki orang-orang yang mengikuti keridhaan-Nya ke jalan keselamatan, dan (dengan kitab itu pula) Allah mengeluarkan orang-orang itu dari gelap gulita kepada cahaya yang terang benderang dengan seizin-Nya, dan menunjuki mereka ke jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,17,'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَۗ  قُلْ فَمَنْ يَّمْلِكُ مِنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ اَنْ يُّهْلِكَ الْمَسِيْحَ ابْنَ مَرْيَمَ وَاُمَّهٗ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۗوَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗ يَخْلُقُ مَا يَشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Sesungguhnya telah kafirlah orang-orang yang berkata: "Sesungguhnya Allah itu ialah Al Masih putera Maryam". Katakanlah: "Maka siapakah (gerangan) yang dapat menghalang-halangi kehendak Allah, jika Dia hendak membinasakan Al Masih putera Maryam itu beserta ibunya dan seluruh orang-orang yang berada di bumi kesemuanya?". Kepunyaan Allahlah kerajaan langit dan bumi dan apa yang ada diantara keduanya; Dia menciptakan apa yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,18,'وَقَالَتِ الْيَهُوْدُ وَالنَّصٰرٰى نَحْنُ اَبْنٰۤؤُ اللّٰهِ وَاَحِبَّاۤؤُهٗ ۗ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوْبِكُمْ ۗ بَلْ اَنْتُمْ بَشَرٌ مِّمَّنْ خَلَقَۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُۗ وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۖوَاِلَيْهِ الْمَصِيْرُ','Orang-orang Yahudi dan Nasrani mengatakan: "Kami ini adalah anak-anak Allah dan kekasih-kekasih-Nya". Katakanlah: "Maka mengapa Allah menyiksa kamu karena dosa-dosamu?" (Kamu bukanlah anak-anak Allah dan kekasih-kekasih-Nya), tetapi kamu adalah manusia(biasa) diantara orang-orang yang diciptakan-Nya. Dia mengampuni bagi siapa yang dikehendaki-Nya dan menyiksa siapa yang dikehendaki-Nya. Dan Kepunyaan Allah-lah kerajaan antara keduanya. Dan kepada Allah-lah kembali (segala sesuatu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,19,'يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِّنَ الرُّسُلِ اَنْ تَقُوْلُوْا مَا جَاۤءَنَا مِنْۢ بَشِيْرٍ وَّلَا نَذِيْرٍۗ فَقَدْ جَاۤءَكُمْ بَشِيْرٌ وَّنَذِيْرٌ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ','Hai Ahli Kitab, sesungguhnya telah datang kepada kamu Rasul Kami, menjelaskan (syari´at Kami) kepadamu ketika terputus (pengiriman) rasul-rasul agar kamu tidak mengatakan: "Tidak ada datang kepada kami baik seorang pembawa berita gembira maupun seorang pemberi peringatan". Sesungguhnya telah datang kepadamu pembawa berita gembira dan pemberi peringatan. Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,20,'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ فِيْكُمْ اَنْۢبِيَاۤءَ وَجَعَلَكُمْ مُّلُوْكًاۙ وَّاٰتٰىكُمْ مَّا لَمْ يُؤْتِ اَحَدًا مِّنَ الْعٰلَمِيْنَ','Dan (ingatlah) ketika Musa berkata kepada kaumnya: "Hai kaumku, ingatlah nikmat Allah atasmu ketika Dia mengangkat nabi nabi diantaramu, dan dijadikan-Nya kamu orang-orang merdeka, dan diberikan-Nya kepadamu apa yang belum pernah diberikan-Nya kepada seorangpun diantara umat-umat yang lain".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,21,'يٰقَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّتِيْ كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوْا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ','Hai kaumku, masuklah ke tanah suci (Palestina) yang telah ditentukan Allah bagimu, dan janganlah kamu lari kebelakang (karena takut kepada musuh), maka kamu menjadi orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,22,'قَالُوْا يٰمُوْسٰٓى اِنَّ فِيْهَا قَوْمًا جَبَّارِيْنَۖ وَاِنَّا لَنْ نَّدْخُلَهَا حَتّٰى يَخْرُجُوْا مِنْهَاۚ فَاِنْ يَّخْرُجُوْا مِنْهَا فَاِنَّا دٰخِلُوْنَ','Mereka berkata: "Hai Musa, sesungguhnya dalam negeri itu ada orang-orang yang gagah perkasa, sesungguhnya kami sekali-kali tidak akan memasukinya sebelum mereka ke luar daripadanya. Jika mereka ke luar daripadanya, pasti kami akan memasukinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,23,'قَالَ رَجُلَانِ مِنَ الَّذِيْنَ يَخَافُوْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوْا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوْهُ فَاِنَّكُمْ غٰلِبُوْنَ ەۙ وَعَلَى اللّٰهِ فَتَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ','Berkatalah dua orang diantara orang-orang yang takut (kepada Allah) yang Allah telah memberi nikmat atas keduanya: "Serbulah mereka dengan melalui pintu gerbang (kota) itu, maka bila kamu memasukinya niscaya kamu akan menang. Dan hanya kepada Allah hendaknya kamu bertawakkal, jika kamu benar-benar orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,24,'قَالُوْا يٰمُوْسٰٓى اِنَّا لَنْ نَّدْخُلَهَآ اَبَدًا مَّا دَامُوْا فِيْهَا ۖفَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَآ اِنَّا هٰهُنَا قٰعِدُوْنَ','Mereka berkata: "Hai Musa, kami sekali sekali tidak akan memasukinya selama-lamanya, selagi mereka ada didalamnya, karena itu pergilah kamu bersama Tuhanmu, dan berperanglah kamu berdua, sesungguhnya kami hanya duduk menanti disini saja".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,25,'قَالَ رَبِّ اِنِّيْ لَآ اَمْلِكُ اِلَّا نَفْسِيْ وَاَخِيْ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفٰسِقِيْنَ','Berkata Musa: "Ya Tuhanku, aku tidak menguasai kecuali diriku sendiri dan saudaraku. Sebab itu pisahkanlah antara kami dengan orang-orang yang fasik itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,26,'قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَعِيْنَ سَنَةً  ۚيَتِيْهُوْنَ فِى الْاَرْضِۗ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفٰسِقِيْنَ ࣖ','Allah berfirman: "(Jika demikian), maka sesungguhnya negeri itu diharamkan atas mereka selama empat puluh tahun, (selama itu) mereka akan berputar-putar kebingungan di bumi (padang Tiih) itu. Maka janganlah kamu bersedih hati (memikirkan nasib) orang-orang yang fasik itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,27,'۞ وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۘ اِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۗ قَالَ لَاَقْتُلَنَّكَ ۗ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّقِيْنَ','Ceritakanlah kepada mereka kisah kedua putera Adam (Habil dan Qabil) menurut yang sebenarnya, ketika keduanya mempersembahkan korban, maka diterima dari salah seorang dari mereka berdua (Habil) dan tidak diterima dari yang lain (Qabil). Ia berkata (Qabil): "Aku pasti membunuhmu!". Berkata Habil: "Sesungguhnya Allah hanya menerima (korban) dari orang-orang yang bertakwa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,28,'لَىِٕنْۢ بَسَطْتَّ اِلَيَّ يَدَكَ لِتَقْتُلَنِيْ مَآ اَنَا۠ بِبَاسِطٍ يَّدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ','"Sungguh kalau kamu menggerakkan tanganmu kepadaku untuk membunuhku, aku sekali-kali tidak akan menggerakkan tanganku kepadamu untuk membunuhmu. Sesungguhnya aku takut kepada Allah, Tuhan seru sekalian alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,29,'اِنِّيْٓ اُرِيْدُ اَنْ تَبُوْۤاَ بِاِثْمِيْ وَاِثْمِكَ فَتَكُوْنَ مِنْ اَصْحٰبِ النَّارِۚ وَذٰلِكَ جَزَاۤؤُا الظّٰلِمِيْنَۚ ','"Sesungguhnya aku ingin agar kamu kembali dengan (membawa) dosa (membunuh)ku dan dosamu sendiri, maka kamu akan menjadi penghuni neraka, dan yang demikian itulah pembalasan bagi orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,30,'فَطَوَّعَتْ لَهٗ نَفْسُهٗ قَتْلَ اَخِيْهِ فَقَتَلَهٗ فَاَصْبَحَ مِنَ الْخٰسِرِيْنَ ','Maka hawa nafsu Qabil menjadikannya menganggap mudah membunuh saudaranya, sebab itu dibunuhnyalah, maka jadilah ia seorang diantara orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,31,'فَبَعَثَ اللّٰهُ غُرَابًا يَّبْحَثُ فِى الْاَرْضِ لِيُرِيَهٗ كَيْفَ يُوَارِيْ سَوْءَةَ اَخِيْهِ ۗ قَالَ يٰوَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُوْنَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْءَةَ اَخِيْۚ  فَاَصْبَحَ مِنَ النّٰدِمِيْنَ ۛ','Kemudian Allah menyuruh seekor burung gagak menggali-gali di bumi untuk memperlihatkan kepadanya (Qabil) bagaimana seharusnya menguburkan mayat saudaranya. Berkata Qabil: "Aduhai celaka aku, mengapa aku tidak mampu berbuat seperti burung gagak ini, lalu aku dapat menguburkan mayat saudaraku ini?" Karena itu jadilah dia seorang diantara orang-orang yang menyesal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,32,'مِنْ اَجْلِ ذٰلِكَ ۛ كَتَبْنَا عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَنَّهٗ مَنْ قَتَلَ نَفْسًاۢ بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِى الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَمِيْعًاۗ وَمَنْ اَحْيَاهَا فَكَاَنَّمَآ اَحْيَا النَّاسَ جَمِيْعًا ۗوَلَقَدْ جَاۤءَتْهُمْ رُسُلُنَا بِالْبَيِّنٰتِ ثُمَّ اِنَّ كَثِيْرًا مِّنْهُمْ بَعْدَ ذٰلِكَ فِى الْاَرْضِ لَمُسْرِفُوْنَ','Oleh karena itu Kami tetapkan (suatu hukum) bagi Bani Israil, bahwa: barangsiapa yang membunuh seorang manusia, bukan karena orang itu (membunuh) orang lain, atau bukan karena membuat kerusakan dimuka bumi, maka seakan-akan dia telah membunuh manusia seluruhnya. Dan barangsiapa yang memelihara kehidupan seorang manusia, maka seolah-olah dia telah memelihara kehidupan manusia semuanya. Dan sesungguhnya telah datang kepada mereka rasul-rasul Kami dengan (membawa) keterangan-keterangan yang jelas, kemudian banyak diantara mereka sesudah itu sungguh-sungguh melampaui batas dalam berbuat kerusakan dimuka bumi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,33,'اِنَّمَا جَزٰۤؤُا الَّذِيْنَ يُحَارِبُوْنَ اللّٰهَ وَرَسُوْلَهٗ وَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًا اَنْ يُّقَتَّلُوْٓا اَوْ يُصَلَّبُوْٓا اَوْ تُقَطَّعَ اَيْدِيْهِمْ وَاَرْجُلُهُمْ مِّنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۗ ذٰلِكَ لَهُمْ خِزْيٌ فِى الدُّنْيَا وَلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ','Sesungguhnya pembalasan terhadap orang-orang yang memerangi Allah dan Rasul-Nya dan membuat kerusakan di muka bumi, hanyalah mereka dibunuh atau disalib, atau dipotong tangan dan kaki mereka dengan bertimbal balik, atau dibuang dari negeri (tempat kediamannya). Yang demikian itu (sebagai) suatu penghinaan untuk mereka didunia, dan di akhirat mereka beroleh siksaan yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,34,'اِلَّا الَّذِيْنَ تَابُوْا مِنْ قَبْلِ اَنْ تَقْدِرُوْا عَلَيْهِمْۚ فَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ ','kecuali orang-orang yang taubat (di antara mereka) sebelum kamu dapat menguasai (menangkap) mereka; maka ketahuilah bahwasanya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,35,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُوْٓا اِلَيْهِ الْوَسِيْلَةَ وَجَاهِدُوْا فِيْ سَبِيْلِهٖ لَعَلَّكُمْ تُفْلِحُوْنَ','Hai orang-orang yang beriman, bertakwalah kepada Allah dan carilah jalan yang mendekatkan diri kepada-Nya, dan berjihadlah pada jalan-Nya, supaya kamu mendapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,36,'اِنَّ الَّذِيْنَ كَفَرُوْا لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لِيَفْتَدُوْا بِهٖ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْ ۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ','Sesungguhnya orang-orang yang kafir sekiranya mereka mempunyai apa yang dibumi ini seluruhnya dan mempunyai yang sebanyak itu (pula) untuk menebusi diri mereka dengan itu dari azab hari kiamat, niscaya (tebusan itu) tidak akan diterima dari mereka, dan mereka beroleh azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,37,'يُرِيْدُوْنَ اَنْ يَّخْرُجُوْا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِيْنَ مِنْهَا ۖوَلَهُمْ عَذَابٌ مُّقِيمٌ','Mereka ingin keluar dari neraka, padahal mereka sekali-kali tidak dapat keluar daripadanya, dan mereka beroleh azab yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,38,'وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوْٓا اَيْدِيَهُمَا جَزَاۤءًۢ بِمَا كَسَبَا نَكَالًا مِّنَ اللّٰهِ ۗوَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ','Laki-laki yang mencuri dan perempuan yang mencuri, potonglah tangan keduanya (sebagai) pembalasan bagi apa yang mereka kerjakan dan sebagai siksaan dari Allah. Dan Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,39,'فَمَنْ تَابَ مِنْۢ بَعْدِ ظُلْمِهٖ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوْبُ عَلَيْهِ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Maka barangsiapa bertaubat (di antara pencuri-pencuri itu) sesudah melakukan kejahatan itu dan memperbaiki diri, maka sesungguhnya Allah menerima taubatnya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,40,'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَغْفِرُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Tidakkah kamu tahu, sesungguhnya Allah-lah yang mempunyai kerajaan langit dan bumi, disiksa-Nya siapa yang dikehendaki-Nya dan diampuni-Nya bagi siapa yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,41,'۞ يٰٓاَيُّهَا الرَّسُوْلُ لَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِ مِنَ الَّذِيْنَ قَالُوْٓا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوْبُهُمْ ۛ وَمِنَ الَّذِيْنَ هَادُوْا ۛ سَمّٰعُوْنَ لِلْكَذِبِ سَمّٰعُوْنَ لِقَوْمٍ اٰخَرِيْنَۙ لَمْ يَأْتُوْكَ ۗ يُحَرِّفُوْنَ الْكَلِمَ مِنْۢ بَعْدِ مَوَاضِعِهٖۚ يَقُوْلُوْنَ اِنْ اُوْتِيْتُمْ هٰذَا فَخُذُوْهُ وَاِنْ لَّمْ تُؤْتَوْهُ فَاحْذَرُوْا ۗوَمَنْ يُّرِدِ اللّٰهُ فِتْنَتَهٗ فَلَنْ تَمْلِكَ لَهٗ مِنَ اللّٰهِ شَيْـًٔا ۗ اُولٰۤىِٕكَ الَّذِيْنَ لَمْ يُرِدِ اللّٰهُ اَنْ يُّطَهِّرَ قُلُوْبَهُمْ ۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ ۖوَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ ','Hari Rasul, janganlah hendaknya kamu disedihkan oleh orang-orang yang bersegera (memperlihatkan) kekafirannya, yaitu diantara orang-orang yang mengatakan dengan mulut mereka: "Kami telah beriman", padahal hati mereka belum beriman; dan (juga) di antara orang-orang Yahudi. (Orang-orang Yahudi itu) amat suka mendengar (berita-berita) bohong dan amat suka mendengar perkataan-perkataan orang lain yang belum pernah datang kepadamu; mereka merubah perkataan-perkataan (Taurat) dari tempat-tempatnya. Mereka mengatakan: "Jika diberikan ini (yang sudah di rubah-rubah oleh mereka) kepada kamu, maka terimalah, dan jika kamu diberi yang bukan ini maka hati-hatilah". Barangsiapa yang Allah menghendaki kesesatannya, maka sekali-kali kamu tidak akan mampu menolak sesuatupun (yang datang) daripada Allah. Mereka itu adalah orang-orang yang Allah tidak hendak mensucikan hati mereka. Mereka beroleh kehinaan di dunia dan di akhirat mereka beroleh siksaan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,42,'سَمّٰعُوْنَ لِلْكَذِبِ اَكّٰلُوْنَ لِلسُّحْتِۗ فَاِنْ جَاۤءُوْكَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْ ۚوَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَّضُرُّوْكَ شَيْـًٔا ۗ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ','Mereka itu adalah orang-orang yang suka mendengar berita bohong, banyak memakan yang haram. Jika mereka (orang Yahudi) datang kepadamu (untuk meminta putusan), maka putuskanlah (perkara itu) diantara mereka, atau berpalinglah dari mereka; jika kamu berpaling dari mereka maka mereka tidak akan memberi mudharat kepadamu sedikitpun. Dan jika kamu memutuskan perkara mereka, maka putuskanlah (perkara itu) diantara mereka dengan adil, sesungguhnya Allah menyukai orang-orang yang adil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,43,'وَكَيْفَ يُحَكِّمُوْنَكَ وَعِنْدَهُمُ التَّوْرٰىةُ فِيْهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْۢ بَعْدِ ذٰلِكَ ۗوَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ ࣖ ','Dan bagaimanakah mereka mengangkatmu menjadi hakim mereka, padahal mereka mempunyai Taurat yang didalamnya (ada) hukum Allah, kemudian mereka berpaling sesudah itu (dari putusanmu)? Dan mereka sungguh-sungguh bukan orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,44,'اِنَّآ اَنْزَلْنَا التَّوْرٰىةَ فِيْهَا هُدًى وَّنُوْرٌۚ يَحْكُمُ بِهَا النَّبِيُّوْنَ الَّذِيْنَ اَسْلَمُوْا لِلَّذِيْنَ هَادُوْا وَالرَّبَّانِيُّوْنَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوْا مِنْ كِتٰبِ اللّٰهِ وَكَانُوْا عَلَيْهِ شُهَدَاۤءَۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ','Sesungguhnya Kami telah menurunkan Kitab Taurat di dalamnya (ada) petunjuk dan cahaya (yang menerangi), yang dengan Kitab itu diputuskan perkara orang-orang Yahudi oleh nabi-nabi yang menyerah diri kepada Allah, oleh orang-orang alim mereka dan pendeta-pendeta mereka, disebabkan mereka diperintahkan memelihara kitab-kitab Allah dan mereka menjadi saksi terhadapnya. Karena itu janganlah kamu takut kepada manusia, (tetapi) takutlah kepada-Ku. Dan janganlah kamu menukar ayat-ayat-Ku dengan harga yang sedikit. Barangsiapa yang tidak memutuskan menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,45,'وَكَتَبْنَا عَلَيْهِمْ فِيْهَآ اَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوْحَ قِصَاصٌۗ فَمَنْ تَصَدَّقَ بِهٖ فَهُوَ كَفَّارَةٌ لَّهٗ  ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ','Dan Kami telah tetapkan terhadap mereka di dalamnya (At Taurat) bahwasanya jiwa (dibalas) dengan jiwa, mata dengan mata, hidung dengan hidung, telinga dengan telinga, gigi dengan gigi, dan luka luka (pun) ada qishaashnya. Barangsiapa yang melepaskan (hak qishaash)nya, maka melepaskan hak itu (menjadi) penebus dosa baginya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,46,'وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِعِيْسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ ۖواٰتَيْنٰهُ الْاِنْجِيْلَ فِيْهِ هُدًى وَّنُوْرٌۙ وَّمُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ وَهُدًى وَّمَوْعِظَةً لِّلْمُتَّقِيْنَۗ ','Dan Kami iringkan jejak mereka (nabi nabi Bani Israil) dengan Isa putera Maryam, membenarkan Kitab yang sebelumnya, yaitu: Taurat. Dan Kami telah memberikan kepadanya Kitab Injil sedang didalamnya (ada) petunjuk dan dan cahaya (yang menerangi), dan membenarkan kitab yang sebelumnya, yaitu Kitab Taurat. Dan menjadi petunjuk serta pengajaran untuk orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,47,'وَلْيَحْكُمْ اَهْلُ الْاِنْجِيْلِ بِمَآ اَنْزَلَ اللّٰهُ فِيْهِۗ وَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ','Dan hendaklah orang-orang pengikut Injil, memutuskan perkara menurut apa yang diturunkan Allah didalamnya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,48,'وَاَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ الْكِتٰبِ وَمُهَيْمِنًا عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ عَمَّا جَاۤءَكَ مِنَ الْحَقِّۗ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَّمِنْهَاجًا ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْ فَاسْتَبِقُوا الْخَيْرٰتِۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَۙ','Dan Kami telah turunkan kepadamu Al Quran dengan membawa kebenaran, membenarkan apa yang sebelumnya, yaitu kitab-kitab (yang diturunkan sebelumnya) dan batu ujian terhadap kitab-kitab yang lain itu; maka putuskanlah perkara mereka menurut apa yang Allah turunkan dan janganlah kamu mengikuti hawa nafsu mereka dengan meninggalkan kebenaran yang telah datang kepadamu. Untuk tiap-tiap umat diantara kamu, Kami berikan aturan dan jalan yang terang. Sekiranya Allah menghendaki, niscaya kamu dijadikan-Nya satu umat (saja), tetapi Allah hendak menguji kamu terhadap pemberian-Nya kepadamu, maka berlomba-lombalah berbuat kebajikan. Hanya kepada Allah-lah kembali kamu semuanya, lalu diberitahukan-Nya kepadamu apa yang telah kamu perselisihkan itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,49,'وَاَنِ احْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ وَاحْذَرْهُمْ اَنْ يَّفْتِنُوْكَ عَنْۢ بَعْضِ مَآ اَنْزَلَ اللّٰهُ اِلَيْكَۗ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّصِيْبَهُمْ بِبَعْضِ ذُنُوْبِهِمْ ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ لَفٰسِقُوْنَ','dan hendaklah kamu memutuskan perkara di antara mereka menurut apa yang diturunkan Allah, dan janganlah kamu mengikuti hawa nafsu mereka. Dan berhati-hatilah kamu terhadap mereka, supaya mereka tidak memalingkan kamu dari sebahagian apa yang telah diturunkan Allah kepadamu. Jika mereka berpaling (dari hukum yang telah diturunkan Allah), maka ketahuilah bahwa sesungguhnya Allah menghendaki akan menimpakan mushibah kepada mereka disebabkan sebahagian dosa-dosa mereka. Dan sesungguhnya kebanyakan manusia adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,50,'اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُوْنَۗ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْمًا لِّقَوْمٍ يُّوْقِنُوْنَ ࣖ ','Apakah hukum Jahiliyah yang mereka kehendaki, dan (hukum) siapakah yang lebih baik daripada (hukum) Allah bagi orang-orang yang yakin?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,51,'۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْيَهُوْدَ وَالنَّصٰرٰٓى اَوْلِيَاۤءَ ۘ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاِنَّهٗ مِنْهُمْ ۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ','Hai orang-orang yang beriman, janganlah kamu mengambil orang-orang Yahudi dan Nasrani menjadi pemimpin-pemimpin(mu); sebahagian mereka adalah pemimpin bagi sebahagian yang lain. Barangsiapa diantara kamu mengambil mereka menjadi pemimpin, maka sesungguhnya orang itu termasuk golongan mereka. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,52,'فَتَرَى الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يُّسَارِعُوْنَ فِيْهِمْ يَقُوْلُوْنَ نَخْشٰٓى اَنْ تُصِيْبَنَا دَاۤىِٕرَةٌ  ۗفَعَسَى اللّٰهُ اَنْ يَّأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِّنْ عِنْدِهٖ فَيُصْبِحُوْا عَلٰى مَآ اَسَرُّوْا فِيْٓ اَنْفُسِهِمْ نٰدِمِيْنَۗ  ','Maka kamu akan melihat orang-orang yang ada penyakit dalam hatinya (orang-orang munafik) bersegera mendekati mereka (Yahudi dan Nasrani), seraya berkata: "Kami takut akan mendapat bencana". Mudah-mudahan Allah akan mendatangkan kemenangan (kepada Rasul-Nya), atau sesuatu keputusan dari sisi-Nya. Maka karena itu, mereka menjadi menyesal terhadap apa yang mereka rahasiakan dalam diri mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,53,'وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْٓا اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۗ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوْا خٰسِرِيْنَ','Dan orang-orang yang beriman akan mengatakan: "Inikah orang-orang yang bersumpah sungguh-sungguh dengan nama Allah, bahwasanya mereka benar-benar beserta kamu?" Rusak binasalah segala amal mereka, lalu mereka menjadi orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,54,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَنْ يَّرْتَدَّ مِنْكُمْ عَنْ دِيْنِهٖ فَسَوْفَ يَأْتِى اللّٰهُ بِقَوْمٍ يُّحِبُّهُمْ وَيُحِبُّوْنَهٗٓ  ۙاَذِلَّةٍ عَلَى الْمُؤْمِنِيْنَ اَعِزَّةٍ عَلَى الْكٰفِرِيْنَۖ  يُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَخَافُوْنَ لَوْمَةَ لَاۤىِٕمٍ ۗذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ','Hai orang-orang yang beriman, barangsiapa di antara kamu yang murtad dari agamanya, maka kelak Allah akan mendatangkan suatu kaum yang Allah mencintai mereka dan merekapun mencintai-Nya, yang bersikap lemah lembut terhadap orang yang mukmin, yang bersikap keras terhadap orang-orang kafir, yang berjihad dijalan Allah, dan yang tidak takut kepada celaan orang yang suka mencela. Itulah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya, dan Allah Maha Luas (pemberian-Nya), lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,55,'اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُوْلُهٗ وَالَّذِيْنَ اٰمَنُوا الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ رَاكِعُوْنَ','Sesungguhnya penolong kamu hanyalah Allah, Rasul-Nya, dan orang-orang yang beriman, yang mendirikan shalat dan menunaikan zakat, seraya mereka tunduk (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,56,'وَمَنْ يَّتَوَلَّ اللّٰهَ وَرَسُوْلَهٗ وَالَّذِيْنَ اٰمَنُوْا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغٰلِبُوْنَ ࣖ ','Dan barangsiapa mengambil Allah, Rasul-Nya dan orang-orang yang beriman menjadi penolongnya, maka sesungguhnya pengikut (agama) Allah itulah yang pasti menang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,57,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الَّذِيْنَ اتَّخَذُوْا دِيْنَكُمْ هُزُوًا وَّلَعِبًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَاۤءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ','Hai orang-orang yang beriman, janganlah kamu mengambil jadi pemimpinmu, orang-orang yang membuat agamamu jadi buah ejekan dan permainan, (yaitu) di antara orang-orang yang telah diberi kitab sebelummu, dan orang-orang yang kafir (orang-orang musyrik). Dan bertakwalah kepada Allah jika kamu betul-betul orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,58,'وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوْهَا هُزُوًا وَّلَعِبًا ۗذٰلِكَ بِاَ نَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَ','Dan apabila kamu menyeru (mereka) untuk (mengerjakan) sembahyang, mereka menjadikannya buah ejekan dan permainan. Yang demikian itu adalah karena mereka benar-benar kaum yang tidak mau mempergunakan akal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,59,'قُلْ يٰٓاَهْلَ الْكِتٰبِ هَلْ تَنْقِمُوْنَ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فٰسِقُوْنَ','Katakanlah: "Hai Ahli Kitab, apakah kamu memandang kami salah, hanya lantaran kami beriman kepada Allah, kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan sebelumnya, sedang kebanyakan di antara kamu benar-benar orang-orang yang fasik?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,60,'قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكَ مَثُوْبَةً عِنْدَ اللّٰهِ ۗمَنْ لَّعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيْرَ وَعَبَدَ الطَّاغُوْتَۗ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ عَنْ سَوَاۤءِ السَّبِيْلِ','Katakanlah: "Apakah akan aku beritakan kepadamu tentang orang-orang yang lebih buruk pembalasannya dari (orang-orang fasik) itu disisi Allah, yaitu orang-orang yang dikutuki dan dimurkai Allah, di antara mereka (ada) yang dijadikan kera dan babi dan (orang yang) menyembah thaghut?". Mereka itu lebih buruk tempatnya dan lebih tersesat dari jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,61,'وَاِذَا جَاۤءُوْكُمْ قَالُوْٓا اٰمَنَّا وَقَدْ دَّخَلُوْا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوْا بِهٖ ۗوَاللّٰهُ اَعْلَمُ بِمَا كَانُوْا يَكْتُمُوْنَ','Dan apabila orang-orang (Yahudi atau munafik) datang kepadamu, mereka mengatakan: "Kami telah beriman", padahal mereka datang kepadamu dengan kekafirannya dan mereka pergi (daripada kamu) dengan kekafirannya (pula); dan Allah lebih mengetahui apa yang mereka sembunyikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,62,'وَتَرٰى كَثِيْرًا مِّنْهُمْ يُسَارِعُوْنَ فِى الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۗ  لَبِئْسَ مَا كَانُوْا يَعْمَلُوْنَ','Dan kamu akan melihat kebanyakan dari mereka (orang-orang Yahudi) bersegera membuat dosa, permusuhan dan memakan yang haram. Sesungguhnya amat buruk apa yang mereka telah kerjakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,63,'لَوْلَا يَنْهٰىهُمُ الرَّبَّانِيُّوْنَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۗ  لَبِئْسَ مَا كَانُوْا يَصْنَعُوْنَ','Mengapa orang-orang alim mereka, pendeta-pendeta mereka tidak melarang mereka mengucapkan perkataan bohong dan memakan yang haram? Sesungguhnya amat buruk apa yang telah mereka kerjakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,64,'وَقَالَتِ الْيَهُوْدُ يَدُ اللّٰهِ مَغْلُوْلَةٌ  ۗغُلَّتْ اَيْدِيْهِمْ وَلُعِنُوْا بِمَا قَالُوْا ۘ بَلْ يَدٰهُ مَبْسُوْطَتٰنِۙ يُنْفِقُ كَيْفَ يَشَاۤءُۗ وَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۗ وَاَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِۗ  كُلَّمَآ اَوْقَدُوْا نَارًا لِّلْحَرْبِ اَطْفَاَهَا اللّٰهُ ۙوَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًاۗ وَاللّٰهُ لَا يُحِبُّ الْمُفْسِدِيْنَ','Orang-orang Yahudi berkata: "Tangan Allah terbelenggu", sebenarnya tangan merekalah yang dibelenggu dan merekalah yang dilaknat disebabkan apa yang telah mereka katakan itu. (Tidak demikian), tetapi kedua-dua tangan Allah terbuka; Dia menafkahkan sebagaimana Dia kehendaki. Dan Al Quran yang diturunkan kepadamu dari Tuhanmu sungguh-sungguh akan menambah kedurhakaan dan kekafiran bagi kebanyakan di antara mereka. Dan Kami telah timbulkan permusuhan dan kebencian di antara mereka sampai hari kiamat. Setiap mereka menyalakan api peperangan Allah memadamkannya dan mereka berbuat kerusakan dimuka bumi dan Allah tidak menyukai orang-orang yang membuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,65,'وَلَوْ اَنَّ اَهْلَ الْكِتٰبِ اٰمَنُوْا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّاٰتِهِمْ وَلَاَدْخَلْنٰهُمْ جَنّٰتِ النَّعِيْمِ','Dan sekiranya Ahli Kitab beriman dan bertakwa, tentulah Kami tutup (hapus) kesalahan-kesalahan mereka dan tentulah Kami masukkan mereka kedalam surga-surga yang penuh kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,66,'وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْهِمْ مِّنْ رَّبِّهِمْ لَاَكَلُوْا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۗ مِنْهُمْ اُمَّةٌ مُّقْتَصِدَةٌ  ۗ وَكَثِيْرٌ مِّنْهُمْ سَاۤءَ مَا يَعْمَلُوْنَ ࣖ ','Dan sekiranya mereka sungguh-sungguh menjalankan (hukum) Taurat dan Injil dan (Al Quran) yang diturunkan kepada mereka dari Tuhannya, niscaya mereka akan mendapat makanan dari atas dan dari bawah kaki mereka. Diantara mereka ada golongan yang pertengahan. Dan alangkah buruknya apa yang dikerjakan oleh kebanyakan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,67,'۞ يٰٓاَيُّهَا الرَّسُوْلُ بَلِّغْ مَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ ۗوَاِنْ لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسٰلَتَهٗ ۗوَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ','Hai Rasul, sampaikanlah apa yang diturunkan kepadamu dari Tuhanmu. Dan jika tidak kamu kerjakan (apa yang diperintahkan itu, berarti) kamu tidak menyampaikan amanat-Nya. Allah memelihara kamu dari (gangguan) manusia. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,68,'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُقِيْمُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ ۗوَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكٰفِرِيْنَ','Katakanlah: "Hai Ahli Kitab, kamu tidak dipandang beragama sedikitpun hingga kamu menegakkan ajaran-ajaran Taurat, Injil, dan Al Quran yang diturunkan kepadamu dari Tuhanmu". Sesungguhnya apa yang diturunkan kepadamu (Muhammad) dari Tuhanmu akan menambah kedurhakaan dan kekafiran kepada kebanyakan dari mereka; maka janganlah kamu bersedih hati terhadap orang-orang yang kafir itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,69,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـُٔوْنَ وَالنَّصٰرٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ','Sesungguhnya orang-orang mukmin, orang-orang Yahudi, Shabiin dan orang-orang Nasrani, siapa saja (diantara mereka) yang benar-benar saleh, maka tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,70,'لَقَدْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ وَاَرْسَلْنَآ اِلَيْهِمْ رُسُلًا ۗ كُلَّمَا جَاۤءَهُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَرِيْقًا كَذَّبُوْا وَفَرِيْقًا يَّقْتُلُوْنَ','Sesungguhnya Kami telah mengambil perjanjian dari Bani Israil, dan telah Kami utus kepada mereka rasul-rasul. Tetapi setiap datang seorang rasul kepada mereka dengan membawa apa yang yang tidak diingini oleh hawa nafsu mereka, (maka) sebagian dari rasul-rasul itu mereka dustakan dan sebagian yang lain mereka bunuh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,71,'وَحَسِبُوْٓا اَلَّا تَكُوْنَ فِتْنَةٌ فَعَمُوْا وَصَمُّوْا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوْا وَصَمُّوْا كَثِيْرٌ مِّنْهُمْۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ','Dan mereka mengira bahwa tidak akan terjadi suatu bencanapun (terhadap mereka dengan membunuh nabi-nabi itu), maka (karena itu) mereka menjadi buta dan pekak, kemudian Allah menerima taubat mereka, kemudian kebanyakan dari mereka buta dan tuli (lagi). Dan Allah Maha Melihat apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,72,'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَ ۗوَقَالَ الْمَسِيْحُ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۗاِنَّهٗ مَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰىهُ النَّارُ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ ','Sesungguhnya telah kafirlah orang-orang yang berkata: "Sesungguhnya Allah ialah Al Masih putera Maryam", padahal Al Masih (sendiri) berkata: "Hai Bani Israil, sembahlah Allah Tuhanku dan Tuhanmu". Sesungguhnya orang yang mempersekutukan (sesuatu dengan) Allah, maka pasti Allah mengharamkan kepadanya surga, dan tempatnya ialah neraka, tidaklah ada bagi orang-orang zalim itu seorang penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,73,'لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍ ۘ وَمَا مِنْ اِلٰهٍ اِلَّآ اِلٰهٌ وَّاحِدٌ  ۗوَاِنْ لَّمْ يَنْتَهُوْا عَمَّا يَقُوْلُوْنَ لَيَمَسَّنَّ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ','Sesungguhnya kafirlah orang0orang yang mengatakan: "Bahwasanya Allah salah seorang dari yang tiga", padahal sekali-kali tidak ada Tuhan selain dari Tuhan Yang Esa. Jika mereka tidak berhenti dari apa yang mereka katakan itu, pasti orang-orang yang kafir diantara mereka akan ditimpa siksaan yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,74,'اَفَلَا يَتُوْبُوْنَ اِلَى اللّٰهِ وَيَسْتَغْفِرُوْنَهٗۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ','Maka mengapa mereka tidak bertaubat kepada Allah dan memohon ampun kepada-Nya?. Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,75,'مَا الْمَسِيْحُ ابْنُ مَرْيَمَ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۗ وَاُمُّهٗ صِدِّيْقَةٌ  ۗ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيٰتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُوْنَ','Al Masih putera Maryam itu hanyalah seorang Rasul yang sesungguhnya telah berlalu sebelumnya beberapa rasul, dan ibunya seorang yang sangat benar, kedua-duanya biasa memakan makanan. Perhatikan bagaimana Kami menjelaskan kepada mereka (ahli kitab) tanda-tanda kekuasaan (Kami), kemudian perhatikanlah bagaimana mereka berpaling (dari memperhatikan ayat-ayat Kami itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,76,'قُلْ اَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَّلَا نَفْعًا ۗوَاللّٰهُ هُوَ السَّمِيْعُ الْعَلِيْمُ','Katakanlah: "Mengapa kamu menyembah selain daripada Allah, sesuatu yang tidak dapat memberi mudharat kepadamu dan tidak (pula) memberi manfaat?" Dan Allah-lah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,77,'قُلْ يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوْٓا اَهْوَاۤءَ قَوْمٍ قَدْ ضَلُّوْا مِنْ قَبْلُ وَاَضَلُّوْا كَثِيْرًا وَّضَلُّوْا عَنْ سَوَاۤءِ السَّبِيْلِ ࣖ ','Katakanlah: "Hai Ahli Kitab, janganlah kamu berlebih-lebihan (melampaui batas) dengan cara tidak benar dalam agamamu. Dan janganlah kamu mengikuti hawa nafsu orang-orang yang telah sesat dahulunya (sebelum kedatangan Muhammad) dan mereka telah menyesatkan kebanyakan (manusia), dan mereka tersesat dari jalan yang lurus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,78,'لُعِنَ الَّذِيْنَ كَفَرُوْا مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى لِسَانِ دَاوٗدَ وَعِيْسَى ابْنِ مَرْيَمَ ۗذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ','Telah dilaknati orang-orang kafir dari Bani Israil dengan lisan Daud dan Isa putera Maryam. Yang demikian itu, disebabkan mereka durhaka dan selalu melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,79,'كَانُوْا لَا يَتَنَاهَوْنَ عَنْ مُّنْكَرٍ فَعَلُوْهُۗ  لَبِئْسَ مَا كَانُوْا يَفْعَلُوْنَ','Mereka satu sama lain selalu tidak melarang tindakan munkar yang mereka perbuat. Sesungguhnya amat buruklah apa yang selalu mereka perbuat itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,80,'تَرٰى كَثِيْرًا مِّنْهُمْ يَتَوَلَّوْنَ الَّذِيْنَ كَفَرُوْا ۗ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِى الْعَذَابِ هُمْ خٰلِدُوْنَ','Kamu melihat kebanyakan dari mereka tolong-menolong dengan orang-orang yang kafir (musyrik). Sesungguhnya amat buruklah apa yang mereka sediakan untuk diri mereka, yaitu kemurkaan Allah kepada mereka; dan mereka akan kekal dalam siksaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,81,'وَلَوْ كَانُوْا يُؤْمِنُوْنَ بِاللّٰهِ وَالنَّبِيِّ وَمَآ اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوْهُمْ اَوْلِيَاۤءَ وَلٰكِنَّ كَثِيْرًا مِّنْهُمْ فٰسِقُوْنَ','Sekiranya mereka beriman kepada Allah, kepada Nabi (Musa) dan kepada apa yang diturunkan kepadanya (Nabi), niscaya mereka tidak akan mengambil orang-orang musyrikin itu menjadi penolong-penolong, tapi kebanyakan dari mereka adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,6,5,82,'۞ لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِّلَّذِيْنَ اٰمَنُوا الْيَهُوْدَ وَالَّذِيْنَ اَشْرَكُوْاۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَّوَدَّةً لِّلَّذِيْنَ اٰمَنُوا الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰىۗ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسِّيْسِيْنَ وَرُهْبَانًا وَّاَنَّهُمْ لَا يَسْتَكْبِرُوْنَ  ۔','Sesungguhnya kamu dapati orang-orang yang paling keras permusuhannya terhadap orang-orang yang beriman ialah orang-orang Yahudi dan orang-orang musyrik. Dan sesungguhnya kamu dapati yang paling dekat persahabatannya dengan orang-orang yang beriman ialah orang-orang yang berkata: "Sesungguhnya kami ini orang Nasrani". Yang demikian itu disebabkan karena di antara mereka itu (orang-orang Nasrani) terdapat pendeta-pendeta dan rahib-rahib, (juga) karena sesungguhnya mereka tidak menymbongkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,83,'وَاِذَا سَمِعُوْا مَآ اُنْزِلَ اِلَى الرَّسُوْلِ تَرٰٓى اَعْيُنَهُمْ تَفِيْضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوْا مِنَ الْحَقِّۚ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ ','Dan apabila mereka mendengarkan apa yang diturunkan kepada Rasul (Muhammad), kamu lihat mata mereka mencucurkan air mata disebabkan kebenaran (Al Quran) yang telah mereka ketahui (dari kitab-kitab mereka sendiri); seraya berkata: "Ya Tuhan kami, kami telah beriman, maka catatlah kami bersama orang-orang yang menjadi saksi (atas kebenaran Al Quran dan kenabian Muhammad s.a.w.).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,84,'وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَاۤءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُّدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصّٰلِحِيْنَ','Mengapa kami tidak akan beriman kepada Allah dan kepada kebenaran yang datang kepada kami, padahal kami sangat ingin agar Tuhan kami memasukkan kami ke dalam golongan orang-orang yang saleh?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,85,'فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوْا جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ وَذٰلِكَ جَزَاۤءُ الْمُحْسِنِيْنَ ','Maka Allah memberi mereka pahala terhadap perkataan yang mereka ucapkan, (yaitu) surga yang mengalir sungai-sungai di dalamnya, sedang mereka kekal di dalamnya. Dan itulah balasan (bagi) orang-orang yang berbuat kebaikan (yang ikhlas keimanannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,86,'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ࣖ','Dan orang-orang kafir serta mendustakan ayat-ayat Kami, mereka itulah penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,87,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحَرِّمُوْا طَيِّبٰتِ مَآ اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُوْا ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ','Hai orang-orang yang beriman, janganlah kamu haramkan apa-apa yang baik yang telah Allah halalkan bagi kamu, dan janganlah kamu melampaui batas. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,88,'وَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًا ۖوَّاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ ','Dan makanlah makanan yang halal lagi baik dari apa yang Allah telah rezekikan kepadamu, dan bertakwalah kepada Allah yang kamu beriman kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,89,'لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ  فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا عَقَّدْتُّمُ الْاَيْمَانَۚ فَكَفَّارَتُهٗٓ اِطْعَامُ عَشَرَةِ مَسٰكِيْنَ مِنْ اَوْسَطِ مَا تُطْعِمُوْنَ اَهْلِيْكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْرِيْرُ رَقَبَةٍ ۗفَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ ۗذٰلِكَ كَفَّارَةُ اَيْمَانِكُمْ اِذَا حَلَفْتُمْ ۗوَاحْفَظُوْٓا اَيْمَانَكُمْ ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَشْكُرُوْنَ ','Allah tidak menghukum kamu disebabkan sumpah-sumpahmu yang tidak dimaksud (untuk bersumpah), tetapi Dia menghukum kamu disebabkan sumpah-sumpah yang kamu sengaja, maka kaffarat (melanggar) sumpah itu, ialah memberi makan sepuluh orang miskin, yaitu dari makanan yang biasa kamu berikan kepada keluargamu, atau memberi pakaian kepada mereka atau memerdekakan seorang budak. Barang siapa tidak sanggup melakukan yang demikian, maka kaffaratnya puasa selama tiga hari. Yang demikian itu adalah kaffarat sumpah-sumpahmu bila kamu bersumpah (dan kamu langgar). Dan jagalah sumpahmu. Demikianlah Allah menerangkan kepadamu hukum-hukum-Nya agar kamu bersyukur (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,90,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِّنْ عَمَلِ الشَّيْطٰنِ فَاجْتَنِبُوْهُ لَعَلَّكُمْ تُفْلِحُوْنَ ','Hai orang-orang yang beriman, sesungguhnya (meminum) khamar, berjudi, (berkorban untuk) berhala, mengundi nasib dengan panah, adalah termasuk perbuatan syaitan. Maka jauhilah perbuatan-perbuatan itu agar kamu mendapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,91,'اِنَّمَا يُرِيْدُ الشَّيْطٰنُ اَنْ يُّوْقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ فِى الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِ فَهَلْ اَنْتُمْ مُّنْتَهُوْنَ ','Sesungguhnya syaitan itu bermaksud hendak menimbulkan permusuhan dan kebencian di antara kamu lantaran (meminum) khamar dan berjudi itu, dan menghalangi kamu dari mengingat Allah dan sembahyang; maka berhentilah kamu (dari mengerjakan pekerjaan itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,92,'وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاحْذَرُوْا ۚفَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ ','Dan taatlah kamu kepada Allah dan taatlah kamu kepada Rasul-(Nya) dan berhati-hatilah. Jika kamu berpaling, maka ketahuilah bahwa sesungguhnya kewajiban Rasul Kami, hanyalah menyampaikan (amanat Allah) dengan terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,93,'لَيْسَ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جُنَاحٌ فِيْمَا طَعِمُوْٓا اِذَا مَا اتَّقَوْا وَّاٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ ثُمَّ اتَّقَوْا وَّاٰمَنُوْا ثُمَّ اتَّقَوْا وَّاَحْسَنُوْا ۗوَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ࣖ ','Tidak ada dosa bagi orang-orang yang beriman dan mengerjakan amalan yang saleh karena memakan makanan yang telah mereka makan dahulu, apabila mereka bertakwa serta beriman, dan mengerjakan amalan-amalan yang saleh, kemudian mereka tetap bertakwa dan beriman, kemudian mereka (tetap juga) bertakwa dan berbuat kebajikan. Dan Allah menyukai orang-orang yang berbuat kebajikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,94,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِّنَ الصَّيْدِ تَنَالُهٗٓ اَيْدِيْكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَّخَافُهٗ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ ','Hai orang-orang yang beriman, sesungguhnya Allah akan menguji kamu dengan sesuatu dari binatang buruan yang mudah didapat oleh tangan dan tombakmu supaya Allah mengetahui orang yang takut kepada-Nya, biarpun ia tidak dapat melihat-Nya. Barang siapa yang melanggar batas sesudah itu, maka baginya azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,95,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌ ۗوَمَنْ قَتَلَهٗ مِنْكُمْ مُّتَعَمِّدًا فَجَزَۤاءٌ مِّثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهٖ ذَوَا عَدْلٍ مِّنْكُمْ هَدْيًاۢ بٰلِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسٰكِيْنَ اَوْ عَدْلُ ذٰلِكَ صِيَامًا لِّيَذُوْقَ وَبَالَ اَمْرِهٖ ۗعَفَا اللّٰهُ عَمَّا سَلَفَ ۗوَمَنْ عَادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍ ','Hai orang-orang yang beriman, janganlah kamu membunuh binatang buruan, ketika kamu sedang ihram. Barangsiapa di antara kamu membunuhnya dengan sengaja, maka dendanya ialah mengganti dengan binatang ternak seimbang dengan buruan yang dibunuhnya, menurut putusan dua orang yang adil di antara kamu sebagai had-yad yang dibawa sampai ke Ka´bah atau (dendanya) membayar kaffarat dengan memberi makan orang-orang miskin atau berpuasa seimbang dengan makanan yang dikeluarkan itu, supaya dia merasakan akibat buruk dari perbuatannya. Allah telah memaafkan apa yang telah lalu. Dan barangsiapa yang kembali mengerjakannya, niscaya Allah akan menyiksanya. Allah Maha Kuasa lagi mempunyai (kekuasaan untuk) menyiksa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,96,'اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهٗ مَتَاعًا لَّكُمْ وَلِلسَّيَّارَةِ ۚوَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗوَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ','Dihalalkan bagimu binatang buruan laut dan makanan (yang berasal) dari laut sebagai makanan yang lezat bagimu, dan bagi orang-orang yang dalam perjalanan; dan diharamkan atasmu (menangkap) binatang buruan darat, selama kamu dalam ihram. Dan bertakwalah kepada Allah Yang kepada-Nya-lah kamu akan dikumpulkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,97,'۞ جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيٰمًا لِّلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَاۤىِٕدَ ۗذٰلِكَ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۙ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Allah telah menjadikan Ka´bah, rumah suci itu sebagai pusat (peribadatan dan urusan dunia) bagi manusia, dan (demikian pula) bulan Haram, had-ya, qalaid. (Allah menjadikan yang) demikian itu agar kamu tahu, bahwa sesungguhnya Allah mengetahui apa yang ada di langit dan apa yang ada di bumi dan bahwa sesungguhnya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,98,'اِعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۙ وَاَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌۗ ','Ketahuilah, bahwa sesungguhnya Allah amat berat siksa-Nya dan bahwa sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,99,'مَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ ۗوَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ ','Kewajiban Rasul tidak lain hanyalah menyampaikan, dan Allah mengetahui apa yang kamu lahirkan dan apa yang kamu sembunyikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,100,'قُلْ لَّا يَسْتَوِى الْخَبِيْثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَثْرَةُ الْخَبِيْثِۚ فَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُوْنَ ࣖ','Katakanlah: "Tidak sama yang buruk dengan yang baik, meskipun banyaknya yang buruk itu menarik hatimu, maka bertakwalah kepada Allah hai orang-orang berakal, agar kamu mendapat keberuntungan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,101,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَسْـَٔلُوْا عَنْ اَشْيَاۤءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ ۚوَاِنْ تَسْـَٔلُوْا عَنْهَا حِيْنَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْ ۗعَفَا اللّٰهُ عَنْهَا ۗوَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ','Hai orang-orang yang beriman, janganlah kamu menanyakan (kepada Nabimu) hal-hal yang jika diterangkan kepadamu akan menyusahkan kamu dan jika kamu menanyakan di waktu Al Quran itu diturunkan, niscaya akan diterangkan kepadamu, Allah memaafkan (kamu) tentang hal-hal itu. Allah Maha Pengampun lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,102,'قَدْ سَاَلَهَا قَوْمٌ مِّنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوْا بِهَا كٰفِرِيْنَ ','Sesungguhnya telah ada segolongsn manusia sebelum kamu menanyakan hal-hal yang serupa itu (kepada Nabi mereka), kemudian mereka tidak percaya kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,103,'مَا جَعَلَ اللّٰهُ مِنْۢ بَحِيْرَةٍ وَّلَا سَاۤىِٕبَةٍ وَّلَا وَصِيْلَةٍ وَّلَا حَامٍ ۙوَّلٰكِنَّ الَّذِيْنَ كَفَرُوْا يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَاَكْثَرُهُمْ لَا يَعْقِلُوْنَ ','Allah sekali-kali tidak pernah mensyari´atkan adanya bahiirah, saaibah, washiilah dan haam. Akan tetapi orang-orang kafir membuat-buat kedustaan terhadap Allah, dan kebanyakan mereka tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,104,'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ قَالُوْا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْلَمُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ ','Apabila dikatakan kepada mereka: "Marilah mengikuti apa yang diturunkan Allah dan mengikuti Rasul". Mereka menjawab: "Cukuplah untuk kami apa yang kami dapati bapak-bapak kami mengerjakannya". Dan apakah mereka itu akan mengikuti nenek moyang mereka walaupun nenek moyang mereka itu tidak mengetahui apa-apa dan tidak (pula) mendapat petunjuk?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,105,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا عَلَيْكُمْ اَنْفُسَكُمْ ۚ لَا يَضُرُّكُمْ مَّنْ ضَلَّ اِذَا اهْتَدَيْتُمْ ۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ','Hai orang-orang yang beriman, jagalah dirimu; tiadalah orang yang sesat itu akan memberi mudharat kepadamu apabila kamu telah mendapat petunjuk. Hanya kepada Allah kamu kembali semuanya, maka Dia akan menerangkan kepadamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,106,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ حِيْنَ الْوَصِيَّةِ اثْنٰنِ ذَوَا عَدْلٍ مِّنْكُمْ اَوْ اٰخَرٰنِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِى الْاَرْضِ فَاَصَابَتْكُمْ مُّصِيْبَةُ الْمَوْتِۗ تَحْبِسُوْنَهُمَا مِنْۢ بَعْدِ الصَّلٰوةِ فَيُقْسِمٰنِ بِاللّٰهِ اِنِ ارْتَبْتُمْ لَا نَشْتَرِيْ بِهٖ ثَمَنًا وَّلَوْ كَانَ ذَا قُرْبٰىۙ وَلَا نَكْتُمُ شَهَادَةَ اللّٰهِ اِنَّآ اِذًا لَّمِنَ الْاٰثِمِيْنَ ','Hai orang-orang yang beriman, apabila salah seorang kamu menghadapi kematian, sedang dia akan berwasiat, maka hendaklah (wasiat itu) disaksikan oleh dua orang yang adil di antara kamu, atau dua orang yang berlainan agama dengan kamu, jika kamu dalam perjalanan dimuka bumi lalu kamu ditimpa bahaya kematian. Kamu tahan kedua saksi itu sesudah sembahyang (untuk bersumpah), lalu mereka keduanya bersumpah dengan nama Allah, jika kamu ragu-ragu: "(Demi Allah) kami tidak akan membeli dengan sumpah ini harga yang sedikit (untuk kepentingan seseorang), walaupun dia karib kerabat, dan tidak (pula) kami menyembunyikan persaksian Allah; sesungguhnya kami kalau demikian tentulah termasuk orang-orang yang berdosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,107,'فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّآ اِثْمًا فَاٰخَرٰنِ يَقُوْمٰنِ مَقَامَهُمَا مِنَ الَّذِيْنَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيٰنِ فَيُقْسِمٰنِ بِاللّٰهِ لَشَهَادَتُنَآ اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَآ ۖاِنَّآ اِذًا لَّمِنَ الظّٰلِمِيْنَ ','Jika diketahui bahwa kedua (saksi itu) membuat dosa, maka dua orang yang lain di antara ahli waris yang berhak yang lebih dekat kepada orang yang meninggal (memajukan tuntutan) untuk menggantikannya, lalu keduanya bersumpah dengan nama Allah: "Sesungguhnya persaksian kami labih layak diterima daripada persaksian kedua saksi itu, dan kami tidak melanggar batas, sesungguhnya kami kalau demikian tentulah termasuk orang yang menganiaya diri sendiri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,108,'ذٰلِكَ اَدْنٰٓى اَنْ يَّأْتُوْا بِالشَّهَادَةِ عَلٰى وَجْهِهَآ اَوْ يَخَافُوْٓا اَنْ تُرَدَّ اَيْمَانٌۢ بَعْدَ اَيْمَانِهِمْۗ وَاتَّقُوا اللّٰهَ وَاسْمَعُوْا ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ࣖ','Itu lebih dekat untuk (menjadikan para saksi) mengemukakan persaksiannya menurut apa yang sebenarnya, dan (lebih dekat untuk menjadikan mereka) merasa takut akan dikembalikan sumpahnya (kepada ahli waris) sesudah mereka bersumpah. Dan bertakwalah kepada Allah dan dengarkanlah (perintah-Nya). Allah tidak memberi petunjuk kepada orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,109,'۞ يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُوْلُ مَاذَٓا اُجِبْتُمْ ۗ قَالُوْا لَا عِلْمَ لَنَا ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ','(Ingatlah), hari di waktu Allah mengumpulkan para rasul lalu Allah bertanya (kepada mereka): "Apa jawaban kaummu terhadap (seruan)mu?". Para rasul menjawab: "Tidak ada pengetahuan kami (tentang itu); sesungguhnya Engkau-lah yang mengetahui perkara yang ghaib".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,110,'اِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِيْ عَلَيْكَ وَعَلٰى وَالِدَتِكَ ۘاِذْ اَيَّدْتُّكَ بِرُوْحِ الْقُدُسِۗ  تُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا ۚوَاِذْ عَلَّمْتُكَ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَ ۚوَاِذْ تَخْلُقُ مِنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ بِاِذْنِيْ فَتَنْفُخُ فِيْهَا فَتَكُوْنُ طَيْرًاۢ بِاِذْنِيْ وَتُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ بِاِذْنِيْ ۚوَاِذْ تُخْرِجُ الْمَوْتٰى بِاِذْنِيْ ۚوَاِذْ كَفَفْتُ بَنِيْٓ اِسْرَاۤءِيْلَ عَنْكَ اِذْ جِئْتَهُمْ بِالْبَيِّنٰتِ فَقَالَ الَّذِيْنَ كَفَرُوْا مِنْهُمْ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ','(Ingatlah), ketika Allah mengatakan: "Hai Isa putra Maryam, ingatlah nikmat-Ku kepadamu dan kepada ibumu di waktu Aku menguatkan kamu dengan ruhul qudus. Kamu dapat berbicara dengan manusia di waktu masih dalam buaian dan sesudah dewasa; dan (ingatlah) di waktu Aku mengajar kamu menulis, hikmah, Taurat dan Injil, dan (ingatlah pula) diwaktu kamu membentuk dari tanah (suatu bentuk) yang berupa burung dengan ijin-Ku, kemudian kamu meniup kepadanya, lalu bentuk itu menjadi burung (yang sebenarnya) dengan seizin-Ku. Dan (ingatlah) di waktu kamu menyembuhkan orang yang buta sejak dalam kandungan ibu dan orang yang berpenyakit sopak dengan seizin-Ku, dan (ingatlah) di waktu kamu mengeluarkan orang mati dari kubur (menjadi hidup) dengan seizin-Ku, dan (ingatlah) di waktu Aku menghalangi Bani Israil (dari keinginan mereka membunuh kamu) di kala kamu mengemukakan kepada mereka keterangan-keterangan yang nyata, lalu orang-orang kafir diantara mereka berkata: "Ini tidak lain melainkan sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,111,'وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّٖنَ اَنْ اٰمِنُوْا بِيْ وَبِرَسُوْلِيْ ۚ قَالُوْٓا اٰمَنَّا وَاشْهَدْ بِاَنَّنَا مُسْلِمُوْنَ ','Dan (ingatlah), ketika Aku ilhamkan kepada pengikut Isa yang setia: "Berimanlah kamu kepada-Ku dan kepada rasul-Ku". Mereka menjawab: Kami telah beriman dan saksikanlah (wahai rasul) bahwa sesungguhnya kami adalah orang-orang yang patuh (kepada seruanmu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,112,'اِذْ قَالَ الْحَوَارِيُّوْنَ يٰعِيْسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيْعُ رَبُّكَ اَنْ يُّنَزِّلَ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ ۗقَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','(Ingatlah), ketika pengikut-pengikut Isa berkata: "Hai Isa putera Maryam, sanggupkah Tuhanmu menurunkan hidangan dari langit kepada kami?". Isa menjawab: "Bertakwalah kepada Allah jika kamu betul-betul orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,113,'قَالُوْا نُرِيْدُ اَنْ نَّأْكُلَ مِنْهَا وَتَطْمَىِٕنَّ قُلُوْبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُوْنَ عَلَيْهَا مِنَ الشّٰهِدِيْنَ ','Mereka berkata: "Kami ingin memakan hidangan itu dan supaya tenteram hati kami dan supaya kami yakin bahwa kamu telah berkata benar kepada kami, dan kami menjadi orang-orang yang menyaksikan hidangan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,114,'قَالَ عِيْسَى ابْنُ مَرْيَمَ اللهم رَبَّنَآ اَنْزِلْ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ تَكُوْنُ لَنَا عِيْدًا لِّاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِّنْكَ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرّٰزِقِيْنَ','Isa putera Maryam berdoa: "Ya Tuhan kami turunkanlah kiranya kepada kami suatu hidangan dari langit (yang hari turunnya) akan menjadi hari raya bagi kami yaitu orang-orang yang bersama kami dan yang datang sesudah kami, dan menjadi tanda bagi kekuasaan Engkau; beri rzekilah kami, dan Engkaulah pemberi rezeki Yang Paling Utama".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,115,'قَالَ اللّٰهُ اِنِّيْ مُنَزِّلُهَا عَلَيْكُمْ ۚ فَمَنْ يَّكْفُرْ بَعْدُ مِنْكُمْ فَاِنِّيْٓ اُعَذِّبُهٗ عَذَابًا لَّآ اُعَذِّبُهٗٓ اَحَدًا مِّنَ الْعٰلَمِيْنَ ࣖ ','Allah berfirman: "Sesungguhnya Aku akan menurunkan hidangan itu kepadamu, barangsiapa yang kafir di antaramu sesudah (turun hidangan itu), maka sesungguhnya Aku akan menyiksanya dengan siksaan yang tidak pernah Aku timpakan kepada seorangpun di antara umat manusia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,116,'وَاِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُوْنِيْ وَاُمِّيَ اِلٰهَيْنِ مِنْ دُوْنِ اللّٰهِ ۗقَالَ سُبْحٰنَكَ مَا يَكُوْنُ لِيْٓ اَنْ اَقُوْلَ مَا لَيْسَ لِيْ بِحَقٍّ ۗاِنْ كُنْتُ قُلْتُهٗ فَقَدْ عَلِمْتَهٗ ۗتَعْلَمُ مَا فِيْ نَفْسِيْ وَلَآ اَعْلَمُ مَا فِيْ نَفْسِكَ ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ','Dan (ingatlah) ketika Allah berfirman: "Hai Isa putera Maryam, adakah kamu mengatakan kepada manusia: "Jadikanlah aku dan ibuku dua orang tuhan selain Allah?". Isa menjawab: "Maha Suci Engkau, tidaklah patut bagiku mengatakan apa yang bukan hakku (mengatakannya). Jika aku pernah mengatakan maka tentulah Engkau mengetahui apa yang ada pada diriku dan aku tidak mengetahui apa yang ada pada diri Engkau. Sesungguhnya Engkau Maha Mengetahui perkara yang ghaib-ghaib".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,117,'مَا قُلْتُ لَهُمْ اِلَّا مَآ اَمَرْتَنِيْ بِهٖٓ اَنِ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۚوَكُنْتُ عَلَيْهِمْ شَهِيْدًا مَّا دُمْتُ فِيْهِمْ ۚ فَلَمَّا تَوَفَّيْتَنِيْ كُنْتَ اَنْتَ الرَّقِيْبَ عَلَيْهِمْ ۗوَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ','Aku tidak pernah mengatakan kepada mereka kecuali apa yang Engkau perintahkan kepadaku (mengatakan)nya yaitu: "Sembahlah Allah, Tuhanku dan Tuhanmu", dan adalah aku menjadi saksi terhadap mereka, selama aku berada di antara mereka. Maka setelah Engkau wafatkan aku, Engkau-lah yang mengawasi mereka. Dan Engkau adalah Maha Menyaksikan atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,118,'اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَ ۚوَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ','Jika Engkau menyiksa mereka, maka sesungguhnya mereka adalah hamba-hamba Engkau, dan jika Engkau mengampuni mereka, maka sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,119,'قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصّٰدِقِيْنَ صِدْقُهُمْ ۗ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ ','Allah berfirman: "Ini adalah suatu hari yang bermanfaat bagi orang-orang yang benar kebenaran mereka. Bagi mereka surga yang dibawahnya mengalir sungai-sungai; mereka kekal di dalamnya selama-lamanya; Allah ridha terhadap-Nya. Itulah keberuntungan yang paling besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,5,120,'لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا فِيْهِنَّ ۗوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ ','Kepunyaan Allah-lah kerajaan langit dan bumi dan apa yang ada di dalamnya; dan Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,1,'اَلْحَمْدُ لِلّٰهِ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمٰتِ وَالنُّوْرَ ەۗ ثُمَّ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ يَعْدِلُوْنَ','Segala puji bagi Allah Yang telah menciptakan langit dan bumi dan mengadakan gelap dan terang, namun orang-orang yang kafir mempersekutukan (sesuatu) dengan Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,2,'هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ طِيْنٍ ثُمَّ قَضٰٓى اَجَلًا ۗوَاَجَلٌ مُّسَمًّى عِنْدَهٗ ثُمَّ اَنْتُمْ تَمْتَرُوْنَ ','Dialah Yang menciptakan kamu dari tanah, sesudah itu ditentukannya ajal (kematianmu), dan ada lagi suatu ajal yang ada pada sisi-Nya (yang Dia sendirilah mengetahuinya), kemudian kamu masih ragu-ragu (tentang berbangkit itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,3,'وَهُوَ اللّٰهُ فِى السَّمٰوٰتِ وَفِى الْاَرْضِۗ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُوْنَ ','Dan Dialah Allah (yang disembah), baik di langit maupun di bumi; Dia mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan dan mengetahui (pula) apa yang kamu usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,4,'وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ ','Dan tidak ada suatu ayatpun dari ayat-ayat Tuhan sampai kepada mereka, melainkan mereka selalu berpaling dari padanya (mendustakannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,5,'فَقَدْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْۗ فَسَوْفَ يَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ','Sesungguhnya mereka telah mendustakan yang haq (Al-Quran) tatkala sampai kepada mereka, maka kelak akan sampai kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,6,'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ مَّكَّنّٰهُمْ فِى الْاَرْضِ مَا لَمْ نُمَكِّنْ لَّكُمْ وَاَرْسَلْنَا السَّمَاۤءَ عَلَيْهِمْ مِّدْرَارًا ۖوَّجَعَلْنَا الْاَنْهٰرَ تَجْرِيْ مِنْ تَحْتِهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ','Apakah mereka tidak memperhatikan berapa banyak generasi yang telah Kami binasakan sebelum mereka, padahal (generasi itu) telah Kami teguhkan kedudukan mereka di muka bumi, yaitu keteguhan yang belum pernah Kami berikan kepadamu, dan Kami curahkan hujan yang lebat atas mereka dan Kami jadikan sungai-sungai mengalir di bawah mereka, kemudian Kami binasakan mereka karena dosa mereka sendiri, dan Kami ciptakan sesudah mereka generasi yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,7,'وَلَوْ نَزَّلْنَا عَلَيْكَ كِتٰبًا فِيْ قِرْطَاسٍ فَلَمَسُوْهُ بِاَيْدِيْهِمْ لَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ','Dan kalau Kami turunkan kepadamu tulisan di atas kertas, lalu mereka dapat menyentuhnya dengan tangan mereka sendiri, tentulah orang-orang kafir itu berkata: "Ini tidak lain hanyalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,8,'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ مَلَكٌ ۗوَلَوْ اَنْزَلْنَا مَلَكًا لَّقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُوْنَ ','Dan mereka berkata: "Mengapa tidak diturunkan kepadanya (Muhammad) malaikat?" dan kalau Kami turunkan (kepadanya) malaikat, tentulah selesai urusan itu, kemudian mereka tidak diberi tangguh (sedikitpun).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,9,'وَلَوْ جَعَلْنٰهُ مَلَكًا لَّجَعَلْنٰهُ رَجُلًا وَّلَلَبَسْنَا عَلَيْهِمْ مَّا يَلْبِسُوْنَ ','Dan kalau Kami jadikan rasul itu malaikat, tentulah Kami jadikan dia seorang laki-laki dan (kalau Kami jadikan ia seorang laki-laki), tentulah Kami meragu-ragukan atas mereka apa yang mereka ragu-ragukan atas diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,10,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ','Dan sungguh telah diperolok-olokkan beberapa rasul sebelum kamu, maka turunlah kepada orang-orang yang mencemoohkan di antara mereka balasan (azab) olok-olokan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,11,'قُلْ سِيْرُوْا فِى الْاَرْضِ ثُمَّ انْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ','Katakanlah: "Berjalanlah di muka bumi, kemudian perhatikanlah bagaimana kesudahan orang-orang yang mendustakan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,12,'قُلْ لِّمَنْ مَّا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  قُلْ لِّلّٰهِ ۗ كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَ ۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِۗ  اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ ','Katakanlah: "Kepunyaan siapakah apa yang ada di langit dan di bumi". Katakanlah: "Kepunyaan Allah". Dia telah menetapkan atas Diri-Nya kasih sayang. Dia sungguh akan menghimpun kamu pada hari kiamat yang tidak ada keraguan padanya. Orang-orang yang meragukan dirinya mereka itu tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,13,'۞ وَلَهٗ مَا سَكَنَ فِى الَّيْلِ وَالنَّهَارِ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ','Dan kepunyaan Allah-lah segala yang ada pada malam dan siang. Dan Dialah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,14,'قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِيًّا فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَكُوْنَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ','Katakanlah: "Apakah akan aku jadikan pelindung selain dari Allah yang menjadikan langit dan bumi, padahal Dia memberi makan dan tidak memberi makan?" Katakanlah: "Sesungguhnya aku diperintah supaya aku menjadi orang yang pertama kali menyerah diri (kepada Allah), dan jangan sekali-kali kamu masuk golongan orang musyrik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,15,'قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ ','Katakanlah: "Sesungguhnya aku takut akan azab hari yang besar (hari kiamat), jika aku mendurhakai Tuhanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,16,'مَنْ يُّصْرَفْ عَنْهُ يَوْمَىِٕذٍ فَقَدْ رَحِمَهٗ ۗوَذٰلِكَ الْفَوْزُ الْمُبِيْنُ','Barang siapa yang dijauhkan azab dari padanya pada hari itu, maka sungguh Allah telah memberikan rahmat kepadanya. Dan itulah keberuntungan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,17,'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗٓ اِلَّا هُوَ ۗوَاِنْ يَّمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Dan jika Allah menimpakan sesuatu kemudharatan kepadamu, maka tidak ada yang menghilangkannya melainkan Dia sendiri. Dan jika Dia mendatangkan kebaikan kepadamu, maka Dia Maha Kuasa atas tiap-tiap sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,18,'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ','Dan Dialah yang berkuasa atas sekalian hamba-hamba-Nya. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,19,'قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةً ۗ قُلِ اللّٰهُ ۗشَهِيْدٌۢ بَيْنِيْ وَبَيْنَكُمْ ۗوَاُوْحِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِهٖ وَمَنْۢ بَلَغَ ۗ اَىِٕنَّكُمْ لَتَشْهَدُوْنَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۗ  قُلْ لَّآ اَشْهَدُ ۚ قُلْ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّاِنَّنِيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ','Katakanlah: "Siapakah yang lebih kuat persaksiannya?" Katakanlah: "Allah". Dia menjadi saksi antara aku dan kamu. Dan Al Quran ini diwahyukan kepadaku supaya dengan dia aku memberi peringatan kepadamu dan kepada orang-orang yang sampai Al-Quran (kepadanya). Apakah sesungguhnya kamu mengakui bahwa ada tuhan-tuhan lain di samping Allah?" Katakanlah: "Aku tidak mengakui". Katakanlah: "Sesungguhnya Dia adalah Tuhan Yang Maha Esa dan sesungguhnya aku berlepas diri dari apa yang kamu persekutukan (dengan Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,20,'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْۘ  اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ ࣖ','Orang-orang yang telah Kami berikan kitab kepadanya, mereka mengenalnya (Muhammad) seperti mereka mengenal anak-anaknya sendiri. Orang-orang yang merugikan dirinya, mereka itu tidak beriman (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,21,'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ','Dan siapakah yang lebih aniaya daripada orang yang membuat-buat suatu kedustaan terhadap Allah, atau mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang aniaya itu tidak mendapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,22,'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْٓا اَيْنَ شُرَكَاۤؤُكُمُ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ','Dan (ingatlah), hari yang di waktu itu Kami menghimpun mereka semuanya kemudian Kami berkata kepada orang-orang musyrik: "Di manakah sembahan-sembahan kamu yang dulu kamu katakan (sekutu-sekutu) Kami?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,23,'ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّآ اَنْ قَالُوْا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِكِيْنَ ','Kemudian tiadalah fitnah mereka, kecuali mengatakan: "Demi Allah, Tuhan kami, tiadalah kami mempersekutukan Allah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,24,'اُنْظُرْ كَيْفَ كَذَبُوْا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ','Lihatlah bagaimana mereka telah berdusta kepada diri mereka sendiri dan hilanglah daripada mereka sembahan-sembahan yang dahulu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,25,'وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَ ۚوَجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًا ۗوَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَا ۗحَتّٰٓى اِذَا جَاۤءُوْكَ يُجَادِلُوْنَكَ يَقُوْلُ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ','Dan di antara mereka ada orang yang mendengarkani (bacaan)mu, padahal Kami telah meletakkan tutupan di atas hati mereka (sehingga mereka tidak) memahaminya dan (Kami letakkan) sumbatan di telinganya. Dan jikapun mereka melihat segala tanda (kebenaran), mereka tetap tidak mau beriman kepadanya. Sehingga apabila mereka datang kepadamu untuk membantahmu, orang-orang kafir itu berkata: "Al-Quran ini tidak lain hanyalah dongengan orang-orang dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,26,'وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُ ۚوَاِنْ يُّهْلِكُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ ','Dan mereka melarang (orang lain) mendengarkan Al-Quran dan mereka sendiri menjauhkan diri daripadanya, dan mereka hanyalah membinasakan diri mereka sendiri, sedang mereka tidak menyadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,27,'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلَى النَّارِ فَقَالُوْا يٰلَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيٰتِ رَبِّنَا وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ','Dan jika kamu (Muhammad) melihat ketika mereka dihadapkan ke neraka, lalu mereka berkata: "Kiranya kami dikembalikan (ke dunia) dan tidak mendustakan ayat-ayat Tuhan kami, serta menjadi orang-orang yang beriman", (tentulah kamu melihat suatu peristiwa yang mengharukan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,28,'بَلْ بَدَا لَهُمْ مَّا كَانُوْا يُخْفُوْنَ مِنْ قَبْلُ ۗوَلَوْ رُدُّوْا لَعَادُوْا لِمَا نُهُوْا عَنْهُ وَاِنَّهُمْ لَكٰذِبُوْنَ ','Tetapi (sebenarnya) telah nyata bagi mereka kejahatan yang mereka dahulu selalu menyembunyikannya. Sekiranya mereka dikembalikan ke dunia, tentulah mereka kembali kepada apa yang mereka telah dilarang mengerjakannya. Dan sesungguhnya mereka itu adalah pendusta belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,29,'وَقَالُوْٓا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ','Dan tentu mereka akan mengatakan (pula): "Hidup hanyalah kehidupan kita di dunia ini saja, dan kita sekali-sekali tidak akan dibangkitkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,30,'وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلٰى رَبِّهِمْ ۗ قَالَ اَلَيْسَ هٰذَا بِالْحَقِّ ۗقَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ࣖ','Dan seandainya kamu melihat ketika mereka dihadapkan kepada Tuhannya (tentulah kamu melihat peristiwa yang mengharukan). Berfirman Allah: "Bukankah (kebangkitan ini benar?" Mereka menjawab: "Sungguh benar, demi Tuhan kami". Berfirman Allah: "Karena itu rasakanlah azab ini, disebabkan kamu mengingkari(nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,31,'قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ ۗحَتّٰٓى اِذَا جَاۤءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوْا يٰحَسْرَتَنَا عَلٰى مَا فَرَّطْنَا فِيْهَاۙ وَهُمْ يَحْمِلُوْنَ اَوْزَارَهُمْ عَلٰى ظُهُوْرِهِمْۗ  اَلَا سَاۤءَ مَا يَزِرُوْنَ ','Sungguh telah rugilah orang-orang yang mendustakan pertemuan mereka dengan Tuhan; sehingga apabila kiamat datang kepada mereka dengan tiba-tiba, mereka berkata: "Alangkah besarnya penyesalan kami, terhadap kelalaian kami tentang kiamat itu!", sambil mereka memikul dosa-dosa di atas punggungnya. Ingatlah, amat buruklah apa yang mereka pikul itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,32,'وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا لَعِبٌ وَّلَهْوٌ ۗوَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ ','Dan tiadalah kehidupan dunia ini, selain dari main-main dan senda gurau belaka. Dan sungguh kampung akhirat itu lebih baik bagi orang-orang yang bertakwa. Maka tidakkah kamu memahaminya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,33,'قَدْ نَعْلَمُ اِنَّهٗ لَيَحْزُنُكَ الَّذِيْ يَقُوْلُوْنَ فَاِنَّهُمْ لَا يُكَذِّبُوْنَكَ وَلٰكِنَّ الظّٰلِمِيْنَ بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ ','Sesungguhnya Kami mengetahui bahwasanya apa yang mereka katakan itu menyedihkan hatimu, (janganlah kamu bersedih hati), karena mereka sebenarnya bukan mendustakan kamu, akan tetapi orang-orang yang zalim itu mengingkari ayat-ayat Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,34,'وَلَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَ فَصَبَرُوْا عَلٰى مَا كُذِّبُوْا وَاُوْذُوْا حَتّٰٓى اَتٰىهُمْ نَصْرُنَا ۚوَلَا مُبَدِّلَ لِكَلِمٰتِ اللّٰهِ ۚوَلَقَدْ جَاۤءَكَ مِنْ نَّبَإِ۟ى الْمُرْسَلِيْنَ ','Dan sesungguhnya telah didustakan (pula) rasul-rasul sebelum kamu, akan tetapi mereka sabar terhadap pendustaan dan penganiayaan (yang dilakukan) terhadap mereka, sampai datang pertolongan Allah kepada mereka. Tak ada seorangpun yang dapat merubah kalimat-kalimat (janji-janji) Allah. Dan sesungguhnya telah datang kepadamu sebahagian dari berita rasul-rasul itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,35,'وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقًا فِى الْاَرْضِ اَوْ سُلَّمًا فِى السَّمَاۤءِ فَتَأْتِيَهُمْ بِاٰيَةٍ ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُوْنَنَّ مِنَ الْجٰهِلِيْنَ ','Dan jika perpalingan mereka (darimu) terasa amat berat bagimu, maka jika kamu dapat membuat lobang di bumi atau tangga ke langit lalu kamu dapat mendatangkan mukjizat kepada mereka (maka buatlah). Kalau Allah menghendaki, tentu saja Allah menjadikan mereka semua dalam petunjuk sebab itu janganlah sekali-kali kamu termasuk orang-orang yang jahil')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,36,'۞ اِنَّمَا يَسْتَجِيْبُ الَّذِيْنَ يَسْمَعُوْنَ ۗوَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُوْنَ ','Hanya mereka yang mendengar sajalah yang mematuhi (seruan Allah), dan orang-orang yang mati (hatinya), akan dibangkitkan oleh Allah, kemudian kepada-Nya-lah mereka dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,37,'وَقَالُوْا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُّنَزِّلَ اٰيَةً وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Dan mereka (orang-orang musyrik Mekah) berkata: "Mengapa tidak diturunkan kepadanya (Muhammad) suatu mukjizat dari Tuhannya?" Katakanlah: "Sesungguhnya Allah kuasa menurunkan suatu mukjizat, tetapi kebanyakan mereka tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,38,'وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ وَلَا طٰۤىِٕرٍ يَّطِيْرُ بِجَنَاحَيْهِ اِلَّآ اُمَمٌ اَمْثَالُكُمْ ۗمَا فَرَّطْنَا فِى الْكِتٰبِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُوْنَ ','Dan tiadalah binatang-binatang yang ada di bumi dan burung-burung yang terbang dengan kedua sayapnya, melainkan umat (juga) seperti kamu. Tiadalah Kami alpakan sesuatupun dalam Al-Kitab, kemudian kepada Tuhanlah mereka dihimpunkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,39,'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا صُمٌّ وَّبُكْمٌ فِى الظُّلُمٰتِۗ مَنْ يَّشَاِ اللّٰهُ يُضْلِلْهُ وَمَنْ يَّشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Dan orang-orang yang mendustakan ayat-ayat Kami adalah pekak, bisu dan berada dalam gelap gulita. Barangsiapa yang dikehendaki Allah (kesesatannya), niscaya disesatkan-Nya. Dan barangsiapa yang dikehendaki Allah (untuk diberi-Nya petunjuk), niscaya Dia menjadikan-Nya berada di atas jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,40,'قُلْ اَرَءَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُوْنَۚ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Katakanlah: "Terangkanlah kepadaku jika datang siksaan Allah kepadamu, atau datang kepadamu hari kiamat, apakah kamu menyeru (tuhan) selain Allah; jika kamu orang-orang yang benar!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,41,'بَلْ اِيَّاهُ تَدْعُوْنَ فَيَكْشِفُ مَا تَدْعُوْنَ اِلَيْهِ اِنْ شَاۤءَ وَتَنْسَوْنَ مَا تُشْرِكُوْنَ ࣖ','(Tidak), tetapi hanya Dialah yang kamu seru, maka Dia menghilangkan bahaya yang karenanya kamu berdoa kepada-Nya, jika Dia menghendaki, dan kamu tinggalkan sembahan-sembahan yang kamu sekutukan (dengan Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,42,'وَلَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَاَخَذْنٰهُمْ بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَتَضَرَّعُوْنَ ','Dan sesungguhnya Kami telah mengutus (rasul-rasul) kepada umat-umat yang sebelum kamu, kemudian Kami siksa mereka dengan (menimpakan) kesengsaraan dan kemelaratan, supaya mereka memohon (kepada Allah) dengan tunduk merendahkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,43,'فَلَوْلَآ اِذْ جَاۤءَهُمْ بَأْسُنَا تَضَرَّعُوْا وَلٰكِنْ قَسَتْ قُلُوْبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ مَا كَانُوْا يَعْمَلُوْنَ ','Maka mengapa mereka tidak memohon (kepada Allah) dengan tunduk merendahkan diri ketika datang siksaan Kami kepada mereka, bahkan hati mereka telah menjadi keras, dan syaitanpun menampakkan kepada mereka kebagusan apa yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,44,'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۗ حَتّٰٓى اِذَا فَرِحُوْا بِمَآ اُوْتُوْٓا اَخَذْنٰهُمْ بَغْتَةً فَاِذَا هُمْ مُّبْلِسُوْنَ','Maka tatkala mereka melupakan peringatan yang telah diberikan kepada mereka, Kamipun membukakan semua pintu-pintu kesenangan untuk mereka; sehingga apabila mereka bergembira dengan apa yang telah diberikan kepada mereka, Kami siksa mereka dengan sekonyong-konyong, maka ketika itu mereka terdiam berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,45,'فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِيْنَ ظَلَمُوْاۗ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ','Maka orang-orang yang zalim itu dimusnahkan sampai ke akar-akarnya. Segala puji bagi Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,46,'قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوْبِكُمْ مَّنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِهٖۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ ثُمَّ هُمْ يَصْدِفُوْنَ ','Katakanlah: "Terangkanlah kepadaku jika Allah mencabut pendengaran dan penglihatan serta menutup hatimu, siapakah tuhan selain Allah yang kuasa mengembalikannya kepadamu?" Perhatikanlah bagaimana Kami berkali-kali memperlihatkan tanda-tanda kebesaran (Kami), kemudian mereka tetap berpaling (juga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,47,'قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظّٰلِمُوْنَ ','Katakanlah: "Terangkanlah kepadaku jika datang siksaan Allah kepadamu dengan sekonyong-konyong, atau terang-terangan, maka adakah yang dibinasakan (Allah) selain dari orang yang zalim?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,48,'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ','Dan tidaklah Kami mengutus para rasul itu melainkan untuk memberikan kabar gembira dan memberi peringatan. Barangsiapa yang beriman dan mengadakan perbaikan, maka tak ada kekhawatiran terhadap mereka dan tidak pula mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,49,'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوْا يَفْسُقُوْنَ ','Dan orang-orang yang mendustakan ayat-ayat Kami, mereka akan ditimpa siksa disebabkan mereka selalu berbuat fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,50,'قُلْ لَّآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ لَكُمْ اِنِّيْ مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّۗ  قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُۗ اَفَلَا تَتَفَكَّرُوْنَ ࣖ','Katakanlah: Aku tidak mengatakan kepadamu, bahwa perbendaharaan Allah ada padaku, dan tidak (pula) aku mengetahui yang ghaib dan tidak (pula) aku mengatakan kepadamu bahwa aku seorang malaikat. Aku tidak mengikuti kecuali apa yang diwahyukan kepadaku. Katakanlah: "Apakah sama orang yang buta dengan yang melihat?" Maka apakah kamu tidak memikirkan(nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,51,'وَاَنْذِرْ بِهِ الَّذِيْنَ يَخَافُوْنَ اَنْ يُّحْشَرُوْٓا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِّنْ دُوْنِهٖ وَلِيٌّ وَّلَا شَفِيْعٌ لَّعَلَّهُمْ يَتَّقُوْنَ','Dan berilah peringatan dengan apa yang diwahyukan itu kepada orang-orang yang takut akan dihimpunkan kepada Tuhannya (pada hari kiamat), sedang bagi mereka tidak ada seorang pelindung dan pemberi syafa´atpun selain daripada Allah, agar mereka bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,52,'وَلَا تَطْرُدِ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ ۗمَا عَلَيْكَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِّنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُوْنَ مِنَ الظّٰلِمِيْنَ ','Dan janganlah kamu mengusir orang-orang yang menyeru Tuhannya di pagi dan petang hari, sedang mereka menghendaki keridhaan-Nya. Kamu tidak memikul tanggung jawab sedikitpun terhadap perbuatan mereka dan merekapun tidak memikul tanggung jawab sedikitpun terhadap perbuatanmu, yang menyebabkan kamu (berhak) mengusir mereka, (sehingga kamu termasuk orang-orang yang zalim).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,53,'وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِّيَقُوْلُوْٓا اَهٰٓؤُلَاۤءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِّنْۢ بَيْنِنَاۗ  اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشّٰكِرِيْنَ ','Dan demikianlah telah Kami uji sebahagian mereka (orang-orang kaya) dengan sebahagian mereka (orang-orang miskin), supaya (orang-orang yang kaya itu) berkata: "Orang-orang semacam inikah di antara kita yang diberi anugerah Allah kepada mereka?" (Allah berfirman): "Tidakkah Allah lebih mengetahui tentang orang-orang yang bersyukur (kepada-Nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,54,'وَاِذَا جَاۤءَكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاٰيٰتِنَا فَقُلْ سَلٰمٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهٗ مَنْ عَمِلَ مِنْكُمْ سُوْۤءًاۢ بِجَهَالَةٍ ثُمَّ تَابَ مِنْۢ بَعْدِهٖ وَاَصْلَحَ فَاَنَّهٗ غَفُوْرٌ رَّحِيْمٌ  ','Apabila orang-orang yang beriman kepada ayat-ayat Kami itu datang kepadamu, maka katakanlah: "Salaamun alaikum. Tuhanmu telah menetapkan atas diri-Nya kasih sayang, (yaitu) bahwasanya barang siapa yang berbuat kejahatan di antara kamu lantaran kejahilan, kemudian ia bertaubat setelah mengerjakannya dan mengadakan perbaikan, maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,55,'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلِتَسْتَبِيْنَ سَبِيْلُ الْمُجْرِمِيْنَ ࣖ','Dan demikianlah Kami terangkan ayat-ayat Al-Quran (supaya jelas jalan orang-orang yang saleh, dan supaya jelas (pula) jalan orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,56,'قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗ قُلْ لَّآ اَتَّبِعُ اَهْوَاۤءَكُمْۙ قَدْ ضَلَلْتُ اِذًا وَّمَآ اَنَا۠ مِنَ الْمُهْتَدِيْنَ ','Katakanlah: "Sesungguhnya aku dilarang menyembah tuhan-tuhan yang kamu sembah selain Allah". Katakanlah: "Aku tidak akan mengikuti hawa nafsumu, sungguh tersesatlah aku jika berbuat demikian dan tidaklah (pula) aku termasuk orang-orang yang mendapat petunjuk".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,57,'قُلْ اِنِّيْ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَكَذَّبْتُمْ بِهٖۗ مَا عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗيَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِلِيْنَ','Katakanlah: "Sesungguhnya aku berada di atas hujjah yang nyata (Al Quran) dari Tuhanku, sedang kamu mendustakannya. Tidak ada padaku apa (azab) yang kamu minta supaya disegerakan kedatangannya. Menetapkan hukum itu hanyalah hak Allah. Dia menerangkan yang sebenarnya dan Dia Pemberi keputusan yang paling baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,58,'قُلْ لَّوْ اَنَّ عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖ لَقُضِيَ الْاَمْرُ بَيْنِيْ وَبَيْنَكُمْ ۗوَاللّٰهُ اَعْلَمُ بِالظّٰلِمِيْنَ ','Katakanlah: "Kalau sekiranya ada padaku apa (azab) yang kamu minta supaya disegerakan, tentu telah diselesaikan Allah urusan yang ada antara aku dan kamu. Dan Allah lebih mengetahui tentang orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,59,'۞ وَعِنْدَهٗ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَآ اِلَّا هُوَۗ وَيَعْلَمُ مَا فِى الْبَرِّ وَالْبَحْرِۗ وَمَا تَسْقُطُ مِنْ وَّرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِيْ ظُلُمٰتِ الْاَرْضِ وَلَا رَطْبٍ وَّلَا يَابِسٍ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ','Dan pada sisi Allah-lah kunci-kunci semua yang ghaib; tidak ada yang mengetahuinya kecuali Dia sendiri, dan Dia mengetahui apa yang di daratan dan di lautan, dan tiada sehelai daun pun yang gugur melainkan Dia mengetahuinya (pula), dan tidak jatuh sebutir biji-pun dalam kegelapan bumi, dan tidak sesuatu yang basah atau yang kering, melainkan tertulis dalam kitab yang nyata (Lauh Mahfudz)"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,60,'وَهُوَ الَّذِيْ يَتَوَفّٰىكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيْهِ لِيُقْضٰٓى اَجَلٌ مُّسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ࣖ','Dan Dialah yang menidurkan kamu di malam hari dan Dia mengetahui apa yang kamu kerjakan di siang hari, kemudian Dia membangunkan kamu pada siang hari untuk disempurnakan umur(mu) yang telah ditentukan, kemudian kepada Allah-lah kamu kembali, lalu Dia memberitahukan kepadamu apa yang dahulu kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,61,'وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً ۗحَتّٰٓى اِذَا جَاۤءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُوْنَ ','Dan Dialah yang mempunyai kekuasaan tertinggi di atas semua hamba-Nya, dan diutus-Nya kepadamu malaikat-malaikat penjaga, sehingga apabila datang kematian kepada salah seorang di antara kamu, ia diwafatkan oleh malaikat-malaikat Kami, dan malaikat-malaikat Kami itu tidak melalaikan kewajibannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,62,'ثُمَّ رُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّۗ  اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِبِيْنَ ','Kemudian mereka (hamba Allah) dikembalikan kepada Allah, Penguasa mereka yang sebenarnya. Ketahuilah bahwa segala hukum (pada hari itu) kepunyaan-Nya. Dan Dialah Pembuat Perhitungan yang paling cepat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,63,'قُلْ مَنْ يُّنَجِّيْكُمْ مِّنْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ تَدْعُوْنَهٗ تَضَرُّعًا وَّخُفْيَةً ۚ لَىِٕنْ اَنْجٰىنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ ','Katakanlah: "Siapakah yang dapat menyelamatkan kamu dari bencana di darat dan di laut, yang kamu berdoa kepada-Nya dengan rendah diri dengan suara yang lembut (dengan mengatakan: "Sesungguhnya jika Dia menyelamatkan kami dari (bencana) ini, tentulah kami menjadi orang-orang yang bersyukur"".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,64,'قُلِ اللّٰهُ يُنَجِّيْكُمْ مِّنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُوْنَ ','Katakanlah: "Allah menyelamatkan kamu dari bencana itu dan dari segala macam kesusahan, kemudian kamu kembali mempersekutukan-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,65,'قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَّبْعَثَ عَلَيْكُمْ عَذَابًا مِّنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعًا وَّيُذِيْقَ بَعْضَكُمْ بَأْسَ بَعْضٍۗ  اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ لَعَلَّهُمْ يَفْقَهُوْنَ ','Katakanlah: "Dialah yang berkuasa untuk mengirimkan azab kepadamu, dari atas kamu atau dari bawah kakimu atau Dia mencampurkan kamu dalam golongan-golongan (yang saling bertentangan) dan merasakan kepada sebahagian kamu keganasan sebahagian yang lain. Perhatikanlah, betapa Kami mendatangkan tanda-tanda kebesaran Kami silih berganti agar mereka memahami(nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,66,'وَكَذَّبَ بِهٖ قَوْمُكَ وَهُوَ الْحَقُّۗ قُلْ لَّسْتُ عَلَيْكُمْ بِوَكِيْلٍ ۗ','Dan kaummu mendustakannya (azab) padahal azab itu benar adanya. Katakanlah: "Aku ini bukanlah orang yang diserahi mengurus urusanmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,67,'لِكُلِّ نَبَاٍ مُّسْتَقَرٌّ وَّسَوْفَ تَعْلَمُوْنَ ','Untuk setiap berita (yang dibawa oleh rasul-rasul) ada (waktu) terjadinya dan kelak kamu akan mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,68,'وَاِذَا رَاَيْتَ الَّذِيْنَ يَخُوْضُوْنَ فِيْٓ اٰيٰتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖۗ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطٰنُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظّٰلِمِيْنَ ','Dan apabila kamu melihat orang-orang memperolok-olokkan ayat-ayat Kami, maka tinggalkanlah mereka sehingga mereka membicarakan pembicaraan yang lain. Dan jika syaitan menjadikan kamu lupa (akan larangan ini), maka janganlah kamu duduk bersama orang-orang yang zalim itu sesudah teringat (akan larangan itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,69,'وَمَا عَلَى الَّذِيْنَ يَتَّقُوْنَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُوْنَ','Dan tidak ada pertanggungjawaban sedikitpun atas orang-orang yang bertakwa terhadap dosa mereka; akan tetapi (kewajiban mereka ialah) mengingatkan agar mereka bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,70,'وَذَرِ الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَعِبًا وَّلَهْوًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِهٖٓ اَنْ تُبْسَلَ نَفْسٌۢ بِمَا كَسَبَتْۖ  لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ وَلِيٌّ وَّلَا شَفِيْعٌ ۚوَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَّا يُؤْخَذْ مِنْهَاۗ اُولٰۤىِٕكَ الَّذِيْنَ اُبْسِلُوْا بِمَا كَسَبُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ ࣖ','Dan tinggalkan lah orang-orang yang menjadikan agama mereka sebagai main-main dan senda gurau, dan mereka telah ditipu oleh kehidupan dunia. Peringatkanlah (mereka) dengan Al-Quran itu agar masing-masing diri tidak dijerumuskan ke dalam neraka, karena perbuatannya sendiri. Tidak akan ada baginya pelindung dan tidak pula pemberi syafa´at selain daripada Allah. Dan jika ia menebus dengan segala macam tebusanpun, niscaya tidak akan diterima itu daripadanya. Mereka itulah orang-orang yang dijerumuskan ke dalam neraka. Bagi mereka (disediakan) minuman dari air yang sedang mendidih dan azab yang pedih disebabkan kekafiran mereka dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,71,'قُلْ اَنَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰىنَا اللّٰهُ كَالَّذِى اسْتَهْوَتْهُ الشَّيٰطِيْنُ فِى الْاَرْضِ حَيْرَانَ لَهٗٓ اَصْحٰبٌ يَّدْعُوْنَهٗٓ اِلَى الْهُدَى ائْتِنَا ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۗ وَاُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعٰلَمِيْنَۙ ','Katakanlah: "Apakah kita akan menyeru selain daripada Allah, sesuatu yang tidak dapat mendatangkan kemanfaatan kepada kita dan tidak (pula) mendatangkan kemudharatan kepada kita dan (apakah) kita akan kembali ke belakang, sesudah Allah memberi petunjuk kepada kita, seperti orang yang telah disesatkan oleh syaitan di pesawangan yang menakutkan; dalam keadaan bingung, dia mempunyai kawan-kawan yang memanggilnya kepada jalan yang lurus (dengan mengatakan): "Marilah ikuti kami". Katakanlah: "Sesungguhnya petunjuk Allah itulah (yang sebenarnya) petunjuk; dan kita disuruh agar menyerahkan diri kepada Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,72,'وَاَنْ اَقِيْمُوا الصَّلٰوةَ وَاتَّقُوْهُۗ وَهُوَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ','dan agar mendirikan sembahyang serta bertakwa kepada-Nya". Dan Dialah Tuhan yang kepada-Nya-lah kamu akan dihimpunkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,73,'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ وَيَوْمَ يَقُوْلُ كُنْ فَيَكُوْنُۚ قَوْلُهُ الْحَقُّۗ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِى الصُّوْرِۗ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ','Dan Dialah yang menciptakan langit dan bumi dengan benar. Dan benarlah perkataan-Nya di waktu Dia mengatakan: "Jadilah, lalu terjadilah", dan di tangan-Nya-lah segala kekuasaan di waktu sangkakala ditiup. Dia mengetahui yang ghaib dan yang nampak. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,74,'۞ وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ اٰزَرَ اَتَتَّخِذُ اَصْنَامًا اٰلِهَةً ۚاِنِّيْٓ اَرٰىكَ وَقَوْمَكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Dan (ingatlah) di waktu Ibrahim berkata kepada bapaknya, Aazar, "Pantaskah kamu menjadikan berhala-berhala sebagai tuhan-tuhan? Sesungguhnya aku melihat kamu dan kaummu dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,75,'وَكَذٰلِكَ نُرِيْٓ اِبْرٰهِيْمَ مَلَكُوْتَ السَّمٰوٰتِ وَالْاَرْضِ وَلِيَكُوْنَ مِنَ الْمُوْقِنِيْنَ ','Dan demikianlah Kami perlihatkan kepada Ibrahim tanda-tanda keagungan (Kami yang terdapat) di langit dan bumi dan (Kami memperlihatkannya) agar dia termasuk orang yang yakin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,76,'فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَبًا ۗقَالَ هٰذَا رَبِّيْۚ فَلَمَّآ اَفَلَ قَالَ لَآ اُحِبُّ الْاٰفِلِيْنَ','Ketika malam telah gelap, dia melihat sebuah bintang (lalu) dia berkata: "Inilah Tuhanku", tetapi tatkala bintang itu tenggelam dia berkata: "Saya tidak suka kepada yang tenggelam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,77,'فَلَمَّا رَاَ الْقَمَرَ بَازِغًا قَالَ هٰذَا رَبِّيْ ۚفَلَمَّآ اَفَلَ قَالَ لَىِٕنْ لَّمْ يَهْدِنِيْ رَبِّيْ لَاَكُوْنَنَّ مِنَ الْقَوْمِ الضَّاۤلِّيْنَ ','Kemudian tatkala dia melihat bulan terbit dia berkata: "Inilah Tuhanku". Tetapi setelah bulan itu terbenam, dia berkata: "Sesungguhnya jika Tuhanku tidak memberi petunjuk kepadaku, pastilah aku termasuk orang yang sesat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,78,'فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبِّيْ هٰذَآ اَكْبَرُۚ فَلَمَّآ اَفَلَتْ قَالَ يٰقَوْمِ اِنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ ','Kemudian tatkala ia melihat matahari terbit, dia berkata: "Inilah Tuhanku, ini yang lebih besar". Maka tatkala matahari itu terbenam, dia berkata: "Hai kaumku, sesungguhnya aku berlepas diri dari apa yang kamu persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,79,'اِنِّيْ وَجَّهْتُ وَجْهِيَ لِلَّذِيْ فَطَرَ السَّمٰوٰتِ وَالْاَرْضَ حَنِيْفًا وَّمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَۚ ','Sesungguhnya aku menghadapkan diriku kepada Rabb yang menciptakan langit dan bumi, dengan cenderung kepada agama yang benar, dan aku bukanlah termasuk orang-orang yang mempersekutukan Tuhan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,80,'وَحَاۤجَّهٗ قَوْمُهٗ ۗقَالَ اَتُحَاۤجُّوْۤنِّيْ فِى اللّٰهِ وَقَدْ هَدٰىنِۗ وَلَآ اَخَافُ مَا تُشْرِكُوْنَ بِهٖٓ اِلَّآ اَنْ يَّشَاۤءَ رَبِّيْ شَيْـًٔا ۗوَسِعَ رَبِّيْ كُلَّ شَيْءٍ عِلْمًا ۗ اَفَلَا تَتَذَكَّرُوْنَ ','Dan dia dibantah oleh kaumnya. Dia berkata: "Apakah kamu hendak membantah tentang Allah, padahal sesungguhnya Allah telah memberi petunjuk kepadaku". Dan aku tidak takut kepada (malapetaka dari) sembahan-sembahan yang kamu persekutukan dengan Allah, kecuali di kala Tuhanku menghendaki sesuatu (dari malapetaka) itu. Pengetahuan Tuhanku meliputi segala sesuatu. Maka apakah kamu tidak dapat mengambil pelajaran (daripadanya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,81,'وَكَيْفَ اَخَافُ مَآ اَشْرَكْتُمْ وَلَا تَخَافُوْنَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ عَلَيْكُمْ سُلْطٰنًا ۗفَاَيُّ الْفَرِيْقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُوْنَۘ ','Bagaimana aku takut kepada sembahan-sembahan yang kamu persekutukan (dengan Allah), padahal kamu tidak mempersekutukan Allah dengan sembahan-sembahan yang Allah sendiri tidak menurunkan hujjah kepadamu untuk mempersekutukan-Nya. Maka manakah di antara dua golongan itu yang lebih berhak memperoleh keamanan (dari malapetaka), jika kamu mengetahui?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,82,'اَلَّذِيْنَ اٰمَنُوْا وَلَمْ يَلْبِسُوْٓا اِيْمَانَهُمْ بِظُلْمٍ اُولٰۤىِٕكَ لَهُمُ الْاَمْنُ وَهُمْ مُّهْتَدُوْنَ ࣖ','Orang-orang yang beriman dan tidak mencampuradukkan iman mereka dengan kezaliman (syirik), mereka itulah yang mendapat keamanan dan mereka itu adalah orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,83,'وَتِلْكَ حُجَّتُنَآ اٰتَيْنٰهَآ اِبْرٰهِيْمَ عَلٰى قَوْمِهٖۗ نَرْفَعُ دَرَجٰتٍ مَّنْ نَّشَاۤءُۗ اِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ ','Dan itulah hujjah Kami yang Kami berikan kepada Ibrahim untuk menghadapi kaumnya. Kami tinggikan siapa yang Kami kehendaki beberapa derajat. Sesungguhnya Tuhanmu Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,84,'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ  كُلًّا هَدَيْنَا وَنُوْحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهٖ دَاوٗدَ وَسُلَيْمٰنَ وَاَيُّوْبَ وَيُوْسُفَ وَمُوْسٰى وَهٰرُوْنَ ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَۙ ','Dan Kami telah menganugerahkan Ishak dan Ya´qub kepadanya. Kepada keduanya masing-masing telah Kami beri petunjuk; dan kepada Nuh sebelum itu (juga) telah Kami beri petunjuk, dan kepada sebahagian dari keturunannya (Nuh) yaitu Daud, Sulaiman, Ayyub, Yusuf, Musa dan Harun. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,85,'وَزَكَرِيَّا وَيَحْيٰى وَعِيْسٰى وَاِلْيَاسَۗ  كُلٌّ مِّنَ الصّٰلِحِيْنَۙ ','dan Zakaria, Yahya, Isa dan Ilyas. Semuanya termasuk orang-orang yang shaleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,86,'وَاِسْمٰعِيْلَ وَالْيَسَعَ وَيُوْنُسَ وَلُوْطًاۗ وَكُلًّا فَضَّلْنَا عَلَى الْعٰلَمِيْنَۙ ','dan Ismail, Alyasa´, Yunus dan Luth. Masing-masing Kami lebihkan derajatnya di atas umat (di masanya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,87,'وَمِنْ اٰبَاۤىِٕهِمْ وَذُرِّيّٰتِهِمْ وَاِخْوَانِهِمْ ۚوَاجْتَبَيْنٰهُمْ وَهَدَيْنٰهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Dan Kami lebihkan (pula) derajat sebahagian dari bapak-bapak mereka, keturunan dan saudara-saudara mereka. Dan Kami telah memilih mereka (untuk menjadi nabi-nabi dan rasul-rasul) dan Kami menunjuki mereka ke jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,88,'ذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَلَوْ اَشْرَكُوْا لَحَبِطَ عَنْهُمْ مَّا كَانُوْا يَعْمَلُوْنَ ','Itulah petunjuk Allah, yang dengannya Dia memberi petunjuk kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya. Seandainya mereka mempersekutukan Allah, niscaya lenyaplah dari mereka amalan yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,89,'اُولٰۤىِٕكَ الَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚفَاِنْ يَّكْفُرْ بِهَا هٰٓؤُلَاۤءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَّيْسُوْا بِهَا بِكٰفِرِيْنَ ','Mereka itulah orang-orang yang telah Kami berikan kitab, hikmat dan kenabian Jika orang-orang (Quraisy) itu mengingkarinya, maka sesungguhnya Kami akan menyerahkannya kepada kaum yang sekali-kali tidak akan mengingkarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,90,'اُولٰۤىِٕكَ الَّذِيْنَ هَدَى اللّٰهُ فَبِهُدٰىهُمُ اقْتَدِهْۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًاۗ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعٰلَمِيْنَ ࣖ ','Mereka itulah orang-orang yang telah diberi petunjuk oleh Allah, maka ikutilah petunjuk mereka. Katakanlah: "Aku tidak meminta upah kepadamu dalam menyampaikan (Al-Quran)". Al-Quran itu tidak lain hanyalah peringatan untuk seluruh ummat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,91,'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖٓ اِذْ قَالُوْا مَآ اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِّنْ شَيْءٍۗ  قُلْ مَنْ اَنْزَلَ الْكِتٰبَ الَّذِيْ جَاۤءَ بِهٖ مُوْسٰى نُوْرًا وَّهُدًى لِّلنَّاسِ تَجْعَلُوْنَهٗ قَرَاطِيْسَ تُبْدُوْنَهَا وَتُخْفُوْنَ كَثِيْرًاۚ وَعُلِّمْتُمْ مَّا لَمْ تَعْلَمُوْٓا اَنْتُمْ وَلَآ اٰبَاۤؤُكُمْ ۗقُلِ اللّٰهُ ۙثُمَّ ذَرْهُمْ فِيْ خَوْضِهِمْ يَلْعَبُوْنَ ','Dan mereka tidak menghormati Allah dengan penghormatan yang semestinya, di kala mereka berkata: "Allah tidak menurunkan sesuatupun kepada manusia". Katakanlah: "Siapakah yang menurunkan kitab (Taurat) yang dibawa oleh Musa sebagai cahaya dan petunjuk bagi manusia, kamu jadikan kitab itu lembaran-lembaran kertas yang bercerai-berai, kamu perlihatkan (sebahagiannya) dan kamu sembunyikan sebahagian besarnya, padahal telah diajarkan kepadamu apa yang kamu dan bapak-bapak kamu tidak mengetahui(nya)?" Katakanlah: "Allah-lah (yang menurunkannya)", kemudian (sesudah kamu menyampaikan Al Quran kepada mereka), biarkanlah mereka bermain-main dalam kesesatannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,92,'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ مُّصَدِّقُ الَّذِيْ بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۗ وَالَّذِيْنَ يُؤْمِنُوْنَ بِالْاٰخِرَةِ يُؤْمِنُوْنَ بِهٖ وَهُمْ عَلٰى صَلَاتِهِمْ يُحٰفِظُوْنَ ','Dan ini (Al Quran) adalah kitab yang telah Kami turunkan yang diberkahi; membenarkan kitab-kitab yang (diturunkan) sebelumnya dan agar kamu memberi peringatan kepada (penduduk) Ummul Qura (Mekah) dan orang-orang yang di luar lingkungannya. Orang-orang yang beriman kepada adanya kehidupan akhirat tentu beriman kepadanya (Al Quran) dan mereka selalu memelihara sembahyangnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,93,'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ قَالَ اُوْحِيَ اِلَيَّ وَلَمْ يُوْحَ اِلَيْهِ شَيْءٌ وَّمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَآ اَنْزَلَ اللّٰهُ ۗوَلَوْ تَرٰٓى اِذِ الظّٰلِمُوْنَ فِيْ غَمَرٰتِ الْمَوْتِ وَالْمَلٰۤىِٕكَةُ بَاسِطُوْٓا اَيْدِيْهِمْۚ اَخْرِجُوْٓا اَنْفُسَكُمْۗ  اَلْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَقُوْلُوْنَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ اٰيٰتِهٖ تَسْتَكْبِرُوْنَ','Dan siapakah yang lebih zalim daripada orang yang membuat kedustaan terhadap Allah atau yang berkata: "Telah diwahyukan kepada saya", padahal tidak ada diwahyukan sesuatupun kepadanya, dan orang yang berkata: "Saya akan menurunkan seperti apa yang diturunkan Allah". Alangkah dahsyatnya sekiranya kamu melihat di waktu orang-orang yang zalim berada dalam tekanan sakratul maut, sedang para malaikat memukul dengan tangannya, (sambil berkata): "Keluarkanlah nyawamu" Di hari ini kamu dibalas dengan siksa yang sangat menghinakan, karena kamu selalu mengatakan terhadap Allah (perkataan) yang tidak benar dan (karena) kamu selalu menyombongkan diri terhadap ayat-ayat-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,94,'وَلَقَدْ جِئْتُمُوْنَا فُرَادٰى كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍ وَّتَرَكْتُمْ مَّا خَوَّلْنٰكُمْ وَرَاۤءَ ظُهُوْرِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَاۤءَكُمُ الَّذِيْنَ زَعَمْتُمْ اَنَّهُمْ فِيْكُمْ شُرَكٰۤؤُا ۗ لَقَدْ تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَّا كُنْتُمْ تَزْعُمُوْنَ ࣖ','Dan sesungguhnya kamu datang kepada Kami sendiri-sendiri sebagaimana kamu Kami ciptakan pada mulanya, dan kamu tinggalkan di belakangmu (di dunia) apa yang telah Kami karuniakan kepadamu; dan Kami tiada melihat besertamu pemberi syafa´at yang kamu anggap bahwa mereka itu sekutu-sekutu Tuhan di antara kamu. Sungguh telah terputuslah (pertalian) antara kamu dan telah lenyap daripada kamu apa yang dahulu kamu anggap (sebagai sekutu Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,95,'۞ اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۗ  يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۗذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُوْنَ ','Sesungguhnya Allah menumbuhkan butir tumbuh-tumbuhan dan biji buah-buahan. Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup. (Yang memiliki sifat-sifat) demikian ialah Allah, maka mengapa kamu masih berpaling?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,96,'فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَنًا وَّالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ ','Dia menyingsingkan pagi dan menjadikan malam untuk beristirahat, dan (menjadikan) matahari dan bulan untuk perhitungan. Itulah ketentuan Allah Yang Maha Perkasa lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,97,'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ النُّجُوْمَ لِتَهْتَدُوْا بِهَا فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ','Dan Dialah yang menjadikan bintang-bintang bagimu, agar kamu menjadikannya petunjuk dalam kegelapan di darat dan di laut. Sesungguhnya Kami telah menjelaskan tanda-tanda kebesaran (Kami) kepada orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,98,'وَهُوَ الَّذِيْٓ اَنْشَاَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ فَمُسْتَقَرٌّ وَّمُسْتَوْدَعٌ  ۗقَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّفْقَهُوْنَ ','Dan Dialah yang menciptakan kamu dari seorang diri, maka (bagimu) ada tempat tetap dan tempat simpanan. Sesungguhnya telah Kami jelaskan tanda-tanda kebesaran Kami kepada orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,99,'وَهُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِرًا نُّخْرِجُ مِنْهُ حَبًّا مُّتَرَاكِبًاۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَّجَنّٰتٍ مِّنْ اَعْنَابٍ وَّالزَّيْتُوْنَ وَالرُّمَّانَ مُشْتَبِهًا وَّغَيْرَ مُتَشَابِهٍۗ اُنْظُرُوْٓا اِلٰى ثَمَرِهٖٓ اِذَٓا اَثْمَرَ وَيَنْعِهٖ ۗاِنَّ فِيْ ذٰلِكُمْ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ','Dan Dialah yang menurunkan air hujan dari langit, lalu Kami tumbuhkan dengan air itu segala macam tumbuh-tumbuhan maka Kami keluarkan dari tumbuh-tumbuhan itu tanaman yang menghijau. Kami keluarkan dari tanaman yang menghijau itu butir yang banyak; dan dari mayang korma mengurai tangkai-tangkai yang menjulai, dan kebun-kebun anggur, dan (Kami keluarkan pula) zaitun dan delima yang serupa dan yang tidak serupa. Perhatikanlah buahnya di waktu pohonnya berbuah dan (perhatikan pulalah) kematangannya. Sesungguhnya pada yang demikian itu ada tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,100,'وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوْا لَهٗ بَنِيْنَ وَبَنٰتٍۢ بِغَيْرِ عِلْمٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَصِفُوْنَ ࣖ ','Dan mereka (orang-orang musyrik) menjadikan jin itu sekutu bagi Allah, padahal Allah-lah yang menciptakan jin-jin itu, dan mereka membohong (dengan mengatakan): "Bahwasanya Allah mempunyai anak laki-laki dan perempuan", tanpa (berdasar) ilmu pengetahuan. Maha Suci Allah dan Maha Tinggi dari sifat-sifat yang mereka berikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,101,'بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ اَنّٰى يَكُوْنُ لَهٗ وَلَدٌ وَّلَمْ تَكُنْ لَّهٗ صَاحِبَةٌ  ۗوَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ  ','Dia Pencipta langit dan bumi. Bagaimana Dia mempunyai anak padahal Dia tidak mempunyai isteri. Dia menciptakan segala sesuatu; dan Dia mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,102,'ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ  لَآ اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوْهُ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ  ','(Yang memiliki sifat-sifat yang) demikian itu ialah Allah Tuhan kamu; tidak ada Tuhan selain Dia; Pencipta segala sesuatu, maka sembahlah Dia; dan Dia adalah Pemelihara segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,103,'لَا تُدْرِكُهُ الْاَبْصَارُ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ  ','Dia tidak dapat dicapai oleh penglihatan mata, sedang Dia dapat melihat segala yang kelihatan; dan Dialah Yang Maha Halus lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,104,'قَدْ جَاۤءَكُمْ بَصَاۤىِٕرُ مِنْ رَّبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِهٖۚ وَمَنْ عَمِيَ فَعَلَيْهَاۗ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ  ','Sesungguhnya telah datang dari Tuhanmu bukti-bukti yang terang; maka barangsiapa melihat (kebenaran itu), maka (manfaatnya) bagi dirinya sendiri; dan barangsiapa buta (tidak melihat kebenaran itu), maka kemudharatannya kembali kepadanya. Dan aku (Muhammad) sekali-kali bukanlah pemelihara(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,105,'وَكَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ وَلِيَقُوْلُوْا دَرَسْتَ وَلِنُبَيِّنَهٗ لِقَوْمٍ يَّعْلَمُوْنَ  ','Demikianlah Kami mengulang-ulangi ayat-ayat Kami supaya (orang-orang yang beriman mendapat petunjuk) dan supaya orang-orang musyrik mengatakan: "Kamu telah mempelajari ayat-ayat itu (dari Ahli Kitab)", dan supaya Kami menjelaskan Al Quran itu kepada orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,106,'اِتَّبِعْ مَآ اُوْحِيَ اِلَيْكَ مِنْ رَّبِّكَۚ  لَآ اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ  ','Ikutilah apa yang telah diwahyukan kepadamu dari Tuhanmu; tidak ada Tuhan selain Dia; dan berpalinglah dari orang-orang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,107,'وَلَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكُوْاۗ وَمَا جَعَلْنٰكَ عَلَيْهِمْ حَفِيْظًاۚ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ','Dan kalau Allah menghendaki, niscaya mereka tidak memperkutukan(Nya). Dan Kami tidak menjadikan kamu pemelihara bagi mereka; dan kamu sekali-kali bukanlah pemelihara bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,108,'وَلَا تَسُبُّوا الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْوًاۢ بِغَيْرِ عِلْمٍۗ  كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْۖ ثُمَّ اِلٰى رَبِّهِمْ مَّرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ','Dan janganlah kamu memaki sembahan-sembahan yang mereka sembah selain Allah, karena mereka nanti akan memaki Allah dengan melampaui batas tanpa pengetahuan. Demikianlah Kami jadikan setiap umat menganggap baik pekerjaan mereka. Kemudian kepada Tuhan merekalah kembali mereka, lalu Dia memberitakan kepada mereka apa yang dahulu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,109,'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَتْهُمْ اٰيَةٌ لَّيُؤْمِنُنَّ بِهَاۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْ اَنَّهَآ اِذَا جَاۤءَتْ لَا يُؤْمِنُوْنَ ','Mereka bersumpah dengan nama Allah dengan segala kesungguhan, bahwa sungguh jika datang kepada mereka sesuatu mu jizat, pastilah mereka beriman kepada-Nya. Katakanlah: "Sesungguhnya mukjizat-mukjizat itu hanya berada di sisi Allah". Dan apakah yang memberitahukan kepadamu bahwa apabila mukjizat datang mereka tidak akan beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,7,6,110,'وَنُقَلِّبُ اَفْـِٕدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوْا بِهٖٓ اَوَّلَ مَرَّةٍ وَّنَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ࣖ   ۔ ','Dan (begitu pula) Kami memalingkan hati dan penglihatan mereka seperti mereka belum pernah beriman kepadanya (Al Quran) pada permulaannya, dan Kami biarkan mereka bergelimang dalam kesesatannya yang sangat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,111,'۞ وَلَوْ اَنَّنَا نَزَّلْنَآ اِلَيْهِمُ الْمَلٰۤىِٕكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَّا كَانُوْا لِيُؤْمِنُوْٓا اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُوْنَ ','Kalau sekiranya Kami turunkan malaikat kepada mereka, dan orang-orang yang telah mati berbicara dengan mereka dan Kami kumpulkan (pula) segala sesuatu ke hadapan mereka, niscaya mereka tidak (juga) akan beriman, kecuali jika Allah menghendaki, tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,112,'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيٰطِيْنَ الْاِنْسِ وَالْجِنِّ يُوْحِيْ بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوْرًا ۗوَلَوْ شَاۤءَ رَبُّكَ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ  ','Dan demikianlah Kami jadikan bagi tiap-tiap nabi itu musuh, yaitu syaitan-syaitan (dari jenis) manusia dan (dan jenis) jin, sebahagian mereka membisikkan kepada sebahagian yang lain perkataan-perkataan yang indah-indah untuk menipu (manusia). Jikalau Tuhanmu menghendaki, niscaya mereka tidak mengerjakannya, maka tinggalkanlah mereka dan apa yang mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,113,'وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٕدَةُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوْا مَا هُمْ مُّقْتَرِفُوْنَ ','Dan (juga) agar hati kecil orang-orang yang tidak beriman kepada kehidupan akhirat cenderung kepada bisikan itu, mereka merasa senang kepadanya dan supaya mereka mengerjakan apa yang mereka (syaitan) kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,114,'اَفَغَيْرَ اللّٰهِ اَبْتَغِيْ حَكَمًا وَّهُوَ الَّذِيْٓ اَنْزَلَ اِلَيْكُمُ الْكِتٰبَ مُفَصَّلًا ۗوَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْلَمُوْنَ اَنَّهٗ مُنَزَّلٌ مِّنْ رَّبِّكَ بِالْحَقِّ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ ','Maka patutkah aku mencari hakim selain daripada Allah, padahal Dialah yang telah menurunkan kitab (Al Quran) kepadamu dengan terperinci? Orang-orang yang telah Kami datangkan kitab kepada mereka, mereka mengetahui bahwa Al Quran itu diturunkan dari Tuhanmu dengan sebenarnya. Maka janganlah kamu sekali-kali termasuk orang yang ragu-ragu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,115,'وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَّعَدْلًاۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ','Telah sempurnalah kalimat Tuhanmu (Al-Quran) sebagai kalimat yang benar dan adil. Tidak ada yang dapat merubah rubah kalimat-kalimat-Nya dan Dialah yang Maha Mendenyar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,116,'وَاِنْ تُطِعْ اَكْثَرَ مَنْ فِى الْاَرْضِ يُضِلُّوْكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ','Dan jika kamu menuruti kebanyakan orang-orang yang di muka bumi ini, niscaya mereka akan menyesatkanmu dari jalan Allah. Mereka tidak lain hanyalah mengikuti persangkaan belaka, dan mereka tidak lain hanyalah berdusta (terhadap Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,117,'اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَّضِلُّ عَنْ سَبِيْلِهٖۚ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ','Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui tentang orang yang tersesat dari jalan-Nya dan Dia lebih mengetahui tentang orang orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,118,'فَكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيٰتِهٖ مُؤْمِنِيْنَ ','Maka makanlah binatang-binatang (yang halal) yang disebut nama Allah ketika menyembelihnya, jika kamu beriman kepada ayat-ayat-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,119,'وَمَا لَكُمْ اَلَّا تَأْكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَّا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِ ۗوَاِنَّ كَثِيرًا لَّيُضِلُّوْنَ بِاَهْوَاۤىِٕهِمْ بِغَيْرِ عِلْمٍ ۗاِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْتَدِيْنَ ','Mengapa kamu tidak mau memakan (binatang-binatang yang halal) yang disebut nama Allah ketika menyembelihnya, padahal sesungguhnya Allah telah menjelaskan kepada kamu apa yang diharamkan-Nya atasmu, kecuali apa yang terpaksa kamu memakannya. Dan sesungguhnya kebanyakan (dari manusia) benar benar hendak menyesatkan (orang lain) dengan hawa nafsu mereka tanpa pengetahuan. Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,120,'وَذَرُوْا ظَاهِرَ الْاِثْمِ وَبَاطِنَهٗ ۗاِنَّ الَّذِيْنَ يَكْسِبُوْنَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوْا يَقْتَرِفُوْنَ ','Dan tinggalkanlah dosa yang nampak dan yang tersembunyi. Sesungguhnya orang yang mengerjakan dosa, kelak akan diberi pembalasan (pada hari kiamat), disebabkan apa yang mereka telah kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,121,'وَلَا تَأْكُلُوْا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهٗ لَفِسْقٌۗ وَاِنَّ الشَّيٰطِيْنَ لَيُوْحُوْنَ اِلٰٓى اَوْلِيَاۤىِٕهِمْ  لِيُجَادِلُوْكُمْ ۚوَاِنْ اَطَعْتُمُوْهُمْ اِنَّكُمْ لَمُشْرِكُوْنَ ࣖ ','Dan janganlah kamu memakan binatang-binatang yang tidak disebut nama Allah ketika menyembelihnya. Sesungguhnya perbuatan yang semacam itu adalah suatu kefasikan. Sesungguhnya syaitan itu membisikkan kepada kawan-kawannya agar mereka membantah kamu; dan jika kamu menuruti mereka, sesungguhnya kamu tentulah menjadi orang-orang yang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,122,'اَوَمَنْ كَانَ مَيْتًا فَاَحْيَيْنٰهُ وَجَعَلْنَا لَهٗ نُوْرًا يَّمْشِيْ بِهٖ فِى النَّاسِ كَمَنْ مَّثَلُهٗ فِى الظُّلُمٰتِ لَيْسَ بِخَارِجٍ مِّنْهَاۗ  كَذٰلِكَ زُيِّنَ لِلْكٰفِرِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ','Dan apakah orang yang sudah mati kemudian dia Kami hidupkan dan Kami berikan kepadanya cahaya yang terang, yang dengan cahaya itu dia dapat berjalan di tengah-tengah masyarakat manusia, serupa dengan orang yang keadaannya berada dalam gelap gulita yang sekali-kali tidak dapat keluar dari padanya? Demikianlah Kami jadikan orang yang kafir itu memandang baik apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,123,'وَكَذٰلِكَ جَعَلْنَا فِيْ كُلِّ قَرْيَةٍ اَكٰبِرَ مُجْرِمِيْهَا لِيَمْكُرُوْا فِيْهَاۗ وَمَا يَمْكُرُوْنَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُوْنَ ','Dan demikianlah Kami adakan pada tiap-tiap negeri penjahat-penjahat yang terbesar agar mereka melakukan tipu daya dalam negeri itu. Dan mereka tidak memperdayakan melainkan dirinya sendiri, sedang mereka tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,124,'وَاِذَا جَاۤءَتْهُمْ اٰيَةٌ قَالُوْا لَنْ نُّؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَآ اُوْتِيَ رُسُلُ اللّٰهِ ۘ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسٰلَتَهٗۗ سَيُصِيْبُ الَّذِيْنَ اَجْرَمُوْا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَدِيْدٌۢ بِمَا كَانُوْا يَمْكُرُوْنَ','Apabila datang sesuatu ayat kepada mereka, mereka berkata: "Kami tidak akan beriman sehingga diberikan kepada kami yang serupa dengan apa yang telah diberikan kepada utusan-utusan Allah". Allah lebih mengetahui di mana Dia menempatkan tugas kerasulan. Orang-orang yang berdosa, nanti akan ditimpa kehinaan di sisi Allah dan siksa yang keras disebabkan mereka selalu membuat tipu daya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,125,'فَمَنْ يُّرِدِ اللّٰهُ اَنْ يَّهْدِيَهٗ يَشْرَحْ صَدْرَهٗ لِلْاِسْلَامِۚ وَمَنْ يُّرِدْ اَنْ يُّضِلَّهٗ يَجْعَلْ صَدْرَهٗ ضَيِّقًا حَرَجًا كَاَنَّمَا يَصَّعَّدُ فِى السَّمَاۤءِۗ   كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يُؤْمِنُوْنَ ','Barangsiapa yang Allah menghendaki akan memberikan kepadanya petunjuk, niscaya Dia melapangkan dadanya untuk (memeluk agama) Islam. Dan barangsiapa yang dikehendaki Allah kesesatannya, niscaya Allah menjadikan dadanya sesak lagi sempit, seolah-olah ia sedang mendaki langit. Begitulah Allah menimpakan siksa kepada orang-orang yang tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,126,'وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيْمًاۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّذَّكَّرُوْنَ ','Dan inilah jalan Tuhanmu; (jalan) yang lurus. Sesungguhnya Kami telah menjelaskan ayat-ayat (Kami) kepada orang-orang yang mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,127,'۞ لَهُمْ دَارُ السَّلٰمِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ','Bagi mereka (disediakan) darussalam (surga) pada sisi Tuhannya dan Dialah Pelindung mereka disebabkan amal-amal saleh yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,128,'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًاۚ يٰمَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِّنَ الْاِنْسِ ۚوَقَالَ اَوْلِيَاۤؤُهُمْ مِّنَ الْاِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَّبَلَغْنَآ اَجَلَنَا الَّذِيْٓ اَجَّلْتَ لَنَا ۗقَالَ النَّارُ مَثْوٰىكُمْ خٰلِدِيْنَ فِيْهَآ اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ ','Dan (ingatlah) hari diwaktu Allah menghimpunkan mereka semuanya (dan Allah berfirman): "Hai golongan jin, sesungguhnya kamu telah banyak menyesatkan manusia", lalu berkatalah kawan-kawan meraka dari golongan manusia: "Ya Tuhan kami, sesungguhnya sebahagian daripada kami telah dapat kesenangan dari sebahagian (yang lain) dan kami telah sampai kepada waktu yang telah Engkau tentukan bagi kami". Allah berfirman: "Neraka itulah tempat diam kamu, sedang kamu kekal di dalamnya, kecuali kalau Allah menghendaki (yang lain)". Sesungguhnya Tuhanmu Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,129,'وَكَذٰلِكَ نُوَلِّيْ بَعْضَ الظّٰلِمِيْنَ بَعْضًاۢ بِمَا كَانُوْا يَكْسِبُوْنَ ࣖ ','Dan demikianlah Kami jadikan sebahagian orang-orang yang zalim itu menjadi teman bagi sebahagian yang lain disebabkan apa yang mereka usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,130,'يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۗ قَالُوْا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ ','Hai golongan jin dan manusia, apakah belum datang kepadamu rasul-rasul dari golongan kamu sendiri, yang menyampaikan kepadamu ayat-ayat-Ku dan memberi peringatan kepadamu terhadap pertemuanmu dengan hari ini? Mereka berkata: "Kami menjadi saksi atas diri kami sendiri", kehidupan dunia telah menipu mereka, dan mereka menjadi saksi atas diri mereka sendiri, bahwa mereka adalah orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,131,'ذٰلِكَ اَنْ لَّمْ يَكُنْ رَّبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا غٰفِلُوْنَ ','Yang demikian itu adalah karena Tuhanmu tidaklah membinasakan kota-kota secara aniaya, sedang penduduknya dalam keadaan lengah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,132,'وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ ','Dan masing-masing orang memperoleh derajat-derajat (seimbang) dengan apa yang dikerjakannya. Dan Tuhanmu tidak lengah dari apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,133,'وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۗاِنْ يَّشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْۢ  بَعْدِكُمْ مَّا يَشَاۤءُ كَمَآ اَنْشَاَكُمْ مِّنْ ذُرِّيَّةِ قَوْمٍ اٰخَرِيْنَ ','Dan Tuhanmu Maha Kaya lagi mempunyai rahmat. Jika Dia menghendaki niscaya Dia memusnahkan kamu dan menggantimu dengan siapa yang dikehendaki-Nya setelah kamu (musnah), sebagaimana Dia telah menjadikan kamu dari keturunan orang-orang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,134,'اِنَّ مَا تُوْعَدُوْنَ لَاٰتٍۙ وَّمَآ اَنْتُمْ بِمُعْجِزِيْنَ ','Sesungguhnya apa yang dijanjikan kepadamu pasti datang, dan kamu sekali-kali tidak sanggup menolaknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,135,'قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌۚ فَسَوْفَ تَعْلَمُوْنَۙ مَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ','Katakanlah: "Hai kaumku, berbuatlah sepenuh kemampuanmu, sesungguhnya akupun berbuat (pula). Kelak kamu akan mengetahui, siapakah (di antara kita) yang akan memperoleh hasil yang baik di dunia ini. Sesungguhnya orang-orang yang zalim itu tidak akan mendapatkan keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,136,'وَجَعَلُوْا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَصِيْبًا فَقَالُوْا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَاۤىِٕنَاۚ فَمَا كَانَ لِشُرَكَاۤىِٕهِمْ فَلَا يَصِلُ اِلَى اللّٰهِ ۚوَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَاۤىِٕهِمْۗ سَاۤءَ مَا يَحْكُمُوْنَ ','Dan mereka memperuntukkan bagi Allah satu bagian dari tanaman dan ternak yang telah diciptakan Allah, lalu mereka berkata sesuai dengan persangkaan mereka: "Ini untuk Allah dan ini untuk berhala-berhala kami". Maka saji-sajian yang diperuntukkan bagi berhala-berhala mereka tidak sampai kepada Allah; dan saji-sajian yang diperuntukkan bagi Allah, maka sajian itu sampai kepada berhala-berhala mereka. Amat buruklah ketetapan mereka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,137,'وَكَذٰلِكَ زَيَّنَ لِكَثِيْرٍ مِّنَ الْمُشْرِكِيْنَ قَتْلَ اَوْلَادِهِمْ شُرَكَاۤؤُهُمْ لِيُرْدُوْهُمْ وَلِيَلْبِسُوْا عَلَيْهِمْ دِيْنَهُمْۗ وَلَوْ شَاۤءَ اللّٰهُ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ ','Dan demikianlah pemimpin-pemimpin mereka telah menjadikan kebanyakan dari orang-orang musyrik itu memandang baik membunuh anak-anak mereka untuk membinasakan mereka dan untuk mengaburkan bagi mereka agama-Nya. Dan kalau Allah menghendaki, niscaya mereka tidak mengerjakannya, maka tinggallah mereka dan apa yang mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,138,'وَقَالُوْا هٰذِهٖٓ اَنْعَامٌ وَّحَرْثٌ حِجْرٌ لَّا يَطْعَمُهَآ اِلَّا مَنْ نَّشَاۤءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُوْرُهَا وَاَنْعَامٌ لَّا يَذْكُرُوْنَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَاۤءً عَلَيْهِۗ سَيَجْزِيْهِمْ بِمَا كَانُوْا يَفْتَرُوْنَ ','Dan mereka mengatakan: "Inilah hewan ternak dan tanaman yang dilarang; tidak boleh memakannya, kecuali orang yang kami kehendaki", menurut anggapan mereka, dan ada binatang ternak yang diharamkan menungganginya dan ada binatang ternak yang mereka tidak menyebut nama Allah waktu menyembelihnya, semata-mata membuat-buat kedustaan terhadap Allah. Kelak Allah akan membalas mereka terhadap apa yang selalu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,139,'وَقَالُوْا مَا فِيْ بُطُوْنِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِّذُكُوْرِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَّكُنْ مَّيْتَةً فَهُمْ فِيْهِ شُرَكَاۤءُ ۗسَيَجْزِيْهِمْ وَصْفَهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ','Dan mereka mengatakan: "Apa yang ada dalam perut binatang ternak ini adalah khusus untuk pria kami dan diharamkan atas wanita kami," dan jika yang dalam perut itu dilahirkan mati, maka pria dan wanita sama-sama boleh memakannya. Kelak Allah akan membalas mereka terhadap ketetapan mereka. Sesungguhnya Allah Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,140,'قَدْ خَسِرَ الَّذِيْنَ قَتَلُوْٓا اَوْلَادَهُمْ سَفَهًاۢ بِغَيْرِ عِلْمٍ وَّحَرَّمُوْا مَا رَزَقَهُمُ اللّٰهُ افْتِرَاۤءً عَلَى اللّٰهِ ۗقَدْ ضَلُّوْا وَمَا كَانُوْا مُهْتَدِيْنَ ࣖ ','Sesungguhnya rugilah orang yang membunuh anak-anak mereka, karena kebodohan lagi tidak mengetahui dan mereka mengharamkan apa yang Allah telah rezeki-kan pada mereka dengan semata-mata mengada-adakan terhadap Allah. Sesungguhnya mereka telah sesat dan tidaklah mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,141,'۞ وَهُوَ الَّذِيْٓ اَنْشَاَ جَنّٰتٍ مَّعْرُوْشٰتٍ وَّغَيْرَ مَعْرُوْشٰتٍ وَّالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا اُكُلُهٗ وَالزَّيْتُوْنَ وَالرُّمَّانَ مُتَشَابِهًا وَّغَيْرَ مُتَشَابِهٍۗ  كُلُوْا مِنْ ثَمَرِهٖٓ اِذَآ اَثْمَرَ وَاٰتُوْا حَقَّهٗ يَوْمَ حَصَادِهٖۖ وَلَا تُسْرِفُوْا ۗاِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَۙ','Dan Dialah yang menjadikan kebun-kebun yang berjunjung dan yang tidak berjunjung, pohon korma, tanam-tanaman yang bermacam-macam buahnya, zaitun dan delima yang serupa (bentuk dan warnanya) dan tidak sama (rasanya). Makanlah dari buahnya (yang bermacam-macam itu) bila dia berbuah, dan tunaikanlah haknya di hari memetik hasilnya (dengan disedekahkan kepada fakir miskin); dan janganlah kamu berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang yang berlebih-lebihan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,142,'وَمِنَ الْاَنْعَامِ حَمُوْلَةً وَّفَرْشًا ۗ كُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌۙ','Dan di antara hewan ternak itu ada yang dijadikan untuk pengangkutan dan ada yang untuk disembelih. Makanlah dari rezeki yang telah diberikan Allah kepadamu, dan janganlah kamu mengikuti langkah-langkah syaitan. Sesungguhnya syaitan itu musuh yang nyata bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,143,'ثَمٰنِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ نَبِّئُوْنِيْ بِعِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ ','(yaitu) delapan binatang yang berpasangan, sepasang domba, sepasang dari kambing. Katakanlah: "Apakah dua yang jantan yang diharamkan Allah ataukah dua yang betina, ataukah yang ada dalam kandungan dua betinanya?" Terangkanlah kepadaku dengan berdasar pengetahuan jika kamu memang orang-orang yang benar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,144,'وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ وَصّٰىكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا لِّيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ࣖ ','dan sepasang dari unta dan sepasang dari lembu. Katakanlah: "Apakah dua yang jantan yang diharamkan ataukah dua yang betina, ataukah yang ada dalam kandungan dua betinanya? Apakah kamu menyaksikan di waktu Allah menetapkan ini bagimu? Maka siapakah yang lebih zalim daripada orang-orang yang membuat-buat dusta terhadap Allah untuk menyesatkan manusia tanpa pengetahuan?" Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,145,'قُلْ لَّآ اَجِدُ فِيْ مَآ اُوْحِيَ اِلَيَّ مُحَرَّمًا عَلٰى طَاعِمٍ يَّطْعَمُهٗٓ اِلَّآ اَنْ يَّكُوْنَ مَيْتَةً اَوْ دَمًا مَّسْفُوْحًا اَوْ لَحْمَ خِنْزِيْرٍ فَاِنَّهٗ رِجْسٌ اَوْ فِسْقًا اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ رَبَّكَ غَفُوْرٌ رَّحِيْمٌ ','Katakanlah: "Tiadalah aku peroleh dalam wahyu yang diwahyukan kepadaku, sesuatu yang diharamkan bagi orang yang hendak memakannya, kecuali kalau makanan itu bangkai, atau darah yang mengalir atau daging babi -- karena sesungguhnya semua itu kotor -- atau binatang yang disembelih atas nama selain Allah. Barangsiapa yang dalam keadaan terpaksa, sedang dia tidak menginginkannya dan tidak (pula) melampaui batas, maka sesungguhnya Tuhanmu Maha Pengampun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,146,'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا كُلَّ ذِيْ ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُوْمَهُمَآ اِلَّا مَا حَمَلَتْ ظُهُوْرُهُمَآ اَوِ الْحَوَايَآ اَوْ مَا اخْتَلَطَ بِعَظْمٍۗ ذٰلِكَ جَزَيْنٰهُمْ بِبَغْيِهِمْۚ وَاِنَّا لَصٰدِقُوْنَ ','Dan kepada orang-orang Yahudi, Kami haramkan segala binatang yang berkuku dan dari sapi dan domba, Kami haramkan atas mereka lemak dari kedua binatang itu, selain lemak yang melekat di punggung keduanya atau yang di perut besar dan usus atau yang bercampur dengan tulang. Demikianlah Kami hukum mereka disebabkan kedurhakaan mereka; dan sesungguhnya Kami adalah Maha Benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,147,'فَاِنْ كَذَّبُوْكَ فَقُلْ رَّبُّكُمْ ذُوْ رَحْمَةٍ وَّاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهٗ عَنِ الْقَوْمِ الْمُجْرِمِيْنَ ','Maka jika mereka mendustakan kamu, katakanlah: "Tuhanmu mempunyai rahmat yang luas; dan siksa-Nya tidak dapat ditolak dari kaum yang berdosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,148,'سَيَقُوْلُ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكْنَا وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۗ  كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوْا بَأْسَنَاۗ قُلْ هَلْ عِنْدَكُمْ مِّنْ عِلْمٍ فَتُخْرِجُوْهُ لَنَاۗ اِنْ تَتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ اَنْتُمْ اِلَّا تَخْرُصُوْنَ','Orang-orang yang mempersekutukan Tuhan, akan mengatakan: "Jika Allah menghendaki, niscaya kami dan bapak-bapak kami tidak mempersekutukan-Nya dan tidak (pula) kami mengharamkan barang sesuatu apapun". Demikian pulalah orang-orang sebelum mereka telah mendustakan (para rasul) sampai mereka merasakan siksaan Kami. Katakanlah: "Adakah kamu mempunyai sesuatu pengetahuan sehingga dapat kamu mengemukakannya kepada Kami?" Kamu tidak mengikuti kecuali persangkaan belaka, dan kamu tidak lain hanyalah berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,149,'قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ','Katakanlah: "Allah mempunyai hujjah yang jelas lagi kuat; maka jika Dia menghendaki, pasti Dia memberi petunjuk kepada kamu semuanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,150,'قُلْ هَلُمَّ شُهَدَاۤءَكُمُ الَّذِيْنَ يَشْهَدُوْنَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوْا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُوْنَ ࣖ ','Katakanlah: "Bawalah kemari saksi-saksi kamu yang dapat mempersaksikan bahwasanya Allah telah mengharamkan (makanan yang kamu) haramkan ini" Jika mereka mempersaksikan, maka janganlah kamu ikut pula menjadi saksi bersama mereka; dan janganlah kamu mengikuti hawa nafsu orang-orang yang mendustakan ayat-ayat Kami, dan orang-orang yang tidak beriman kepada kehidupan akhirat, sedang mereka mempersekutukan Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,151,'۞ قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًاۚ وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ مِّنْ اِمْلَاقٍۗ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْ ۚوَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۚ وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ ','Katakanlah: "Marilah kubacakan apa yang diharamkan atas kamu oleh Tuhanmu yaitu: janganlah kamu mempersekutukan sesuatu dengan Dia, berbuat baiklah terhadap kedua orang ibu bapa, dan janganlah kamu membunuh anak-anak kamu karena takut kemiskinan, Kami akan memberi rezeki kepadamu dan kepada mereka, dan janganlah kamu mendekati perbuatan-perbuatan yang keji, baik yang nampak di antaranya maupun yang tersembunyi, dan janganlah kamu membunuh jiwa yang diharamkan Allah (membunuhnya) melainkan dengan sesuatu (sebab) yang benar". Demikian itu yang diperintahkan kepadamu supaya kamu memahami(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,152,'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗ ۚوَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ بِالْقِسْطِۚ  لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۚ وَاِذَا قُلْتُمْ فَاعْدِلُوْا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ اللّٰهِ اَوْفُوْاۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَذَكَّرُوْنَۙ','Dan janganlah kamu dekati harta anak yatim, kecuali dengan cara yang lebih bermanfaat, hingga sampai ia dewasa. Dan sempurnakanlah takaran dan timbangan dengan adil. Kami tidak memikulkan beban kepada sesorang melainkan sekedar kesanggupannya. Dan apabila kamu berkata, maka hendaklah kamu berlaku adil, kendatipun ia adalah kerabat(mu), dan penuhilah janji Allah. Yang demikian itu diperintahkan Allah kepadamu agar kamu ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,153,'وَاَنَّ هٰذَا صِرَاطِيْ مُسْتَقِيْمًا فَاتَّبِعُوْهُ ۚوَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيْلِهٖ ۗذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَتَّقُوْنَ ','dan bahwa (yang Kami perintahkan ini) adalah jalan-Ku yang lurus, maka ikutilah dia, dan janganlah kamu mengikuti jalan-jalan (yang lain), karena jalan-jalan itu mencerai beraikan kamu dari jalan-Nya. Yang demikian itu diperintahkan Allah agar kamu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,154,'ثُمَّ اٰتَيْنَا مُوْسَى الْكِتٰبَ تَمَامًا عَلَى الَّذِيْٓ اَحْسَنَ وَتَفْصِيْلًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لَّعَلَّهُمْ بِلِقَاۤءِ رَبِّهِمْ يُؤْمِنُوْنَ ࣖ ','Kemudian Kami telah memberikan Al Kitab (Taurat) kepada Musa untuk menyempurnakan (nikmat Kami) kepada orang yang berbuat kebaikan, dan untuk menjelaskan segala sesuatu dan sebagai petunjuk dan rahmat, agar mereka beriman (bahwa) mereka akan menemui Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,155,'وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ فَاتَّبِعُوْهُ وَاتَّقُوْا لَعَلَّكُمْ تُرْحَمُوْنَۙ ','Dan Al-Quran itu adalah kitab yang Kami turunkan yang diberkati, maka ikutilah dia dan bertakwalah agar kamu diberi rahmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,156,'اَنْ تَقُوْلُوْٓا اِنَّمَآ اُنْزِلَ الْكِتٰبُ عَلٰى طَاۤىِٕفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغٰفِلِيْنَۙ ','(Kami turunkan al-Quran itu) agar kamu (tidak) mengatakan: "Bahwa kitab itu hanya diturunkan kepada dua golongan saja sebelum kami, dan sesungguhnya kami tidak memperhatikan apa yang mereka baca.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,157,'اَوْ تَقُوْلُوْا لَوْ اَنَّآ اُنْزِلَ عَلَيْنَا الْكِتٰبُ لَكُنَّآ اَهْدٰى مِنْهُمْۚ فَقَدْ جَاۤءَكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ ۚفَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيٰتِ اللّٰهِ وَصَدَفَ عَنْهَا ۗسَنَجْزِى الَّذِيْنَ يَصْدِفُوْنَ عَنْ اٰيٰتِنَا سُوْۤءَ الْعَذَابِ بِمَا كَانُوْا يَصْدِفُوْنَ ','Atau agar kamu (tidak) mengatakan: "Sesungguhnya jikalau kitab ini diturunkan kepada kami, tentulah kami lebih mendapat petunjuk dari mereka". Sesungguhnya telah datang kepada kamu keterangan yang nyata dari Tuhanmu, petunjuk dan rahmat. Maka siapakah yang lebih zalim daripada orang yang mendustakan ayat-ayat Allah dan berpaling daripadanya? Kelak Kami akan memberi balasan kepada orang-orang yang berpaling dari ayat-ayat Kami dengan siksa yang buruk, disebabkan mereka selalu berpaling.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,158,'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيٰتِ رَبِّكَ ۗيَوْمَ يَأْتِيْ بَعْضُ اٰيٰتِ رَبِّكَ لَا يَنْفَعُ نَفْسًا اِيْمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ فِيْٓ اِيْمَانِهَا خَيْرًاۗ قُلِ انْتَظِرُوْٓا اِنَّا مُنْتَظِرُوْنَ ','Yang mereka nanti-nanti tidak lain hanyalah kedatangan malaikat kepada mereka (untuk mencabut nyawa mereka) atau kedatangan (siksa) Tuhanmu atau kedatangan beberapa ayat Tuhanmu. Pada hari datangnya ayat dari Tuhanmu, tidaklah bermanfaat lagi iman seseorang kepada dirinya sendiri yang belum beriman sebelum itu, atau dia (belum) mengusahakan kebaikan dalam masa imannya. Katakanlah: "Tunggulah olehmu sesungguhnya Kamipun menunggu (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,159,'اِنَّ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا لَّسْتَ مِنْهُمْ فِيْ شَيْءٍۗ اِنَّمَآ اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوْا يَفْعَلُوْنَ ','Sesungguhnya orang-orang yang memecah belah agama-Nya dan mereka menjadi bergolongan, tidak ada sedikitpun tanggung jawabmu kepada mereka. Sesungguhnya urusan mereka hanyalah terserah kepada Allah, kemudian Allah akan memberitahukan kepada mereka apa yang telah mereka perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,160,'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ عَشْرُ اَمْثَالِهَا ۚوَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُوْنَ','Barangsiapa membawa amal yang baik, maka baginya (pahala) sepuluh kali lipat amalnya; dan barangsiapa yang membawa perbuatan jahat maka dia tidak diberi pembalasan melainkan seimbang dengan kejahatannya, sedang mereka sedikitpun tidak dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,161,'قُلْ اِنَّنِيْ هَدٰىنِيْ رَبِّيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ەۚ دِيْنًا قِيَمًا مِّلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۚ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ','Katakanlah: "Sesungguhnya aku telah ditunjuki oleh Tuhanku kepada jalan yang lurus, (yaitu) agama yang benar, agama Ibrahim yang lurus, dan Ibrahim itu bukanlah termasuk orang-orang musyrik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,162,'قُلْ اِنَّ صَلَاتِيْ وَنُسُكِيْ وَمَحْيَايَ وَمَمَاتِيْ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ','Katakanlah: sesungguhnya sembahyangku, ibadatku, hidupku dan matiku hanyalah untuk Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,163,'لَا شَرِيْكَ لَهٗ ۚوَبِذٰلِكَ اُمِرْتُ وَاَنَا۠ اَوَّلُ الْمُسْلِمِيْنَ ','Tiada sekutu bagi-Nya; dan demikian itulah yang diperintahkan kepadaku dan aku adalah orang yang pertama-tama menyerahkan diri (kepada Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,164,'قُلْ اَغَيْرَ اللّٰهِ اَبْغِيْ رَبًّا وَّهُوَ رَبُّ كُلِّ شَيْءٍۗ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ','Katakanlah: "Apakah aku akan mencari Tuhan selain Allah, padahal Dia adalah Tuhan bagi segala sesuatu. Dan tidaklah seorang membuat dosa melainkan kemudharatannya kembali kepada dirinya sendiri; dan seorang yang berdosa tidak akan memikul dosa orang lain. Kemudian kepada Tuhanmulah kamu kembali, dan akan diberitakan-Nya kepadamu apa yang kamu perselisihkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,6,165,'وَهُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْۗ اِنَّ رَبَّكَ سَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ ࣖ','Dan Dialah yang menjadikan kamu penguasa-penguasa di bumi dan Dia meninggikan sebahagian kamu atas sebahagian (yang lain) beberapa derajat, untuk mengujimu tentang apa yang diberikan-Nya kepadamu. Sesungguhnya Tuhanmu amat cepat siksaan-Nya dan sesungguhnya Dia Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,1,'الۤمّۤصۤ ۚ ','Alif laam mim shaad.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,2,'كِتٰبٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ فِيْ صَدْرِكَ حَرَجٌ مِّنْهُ لِتُنْذِرَ بِهٖ وَذِكْرٰى لِلْمُؤْمِنِيْنَ ','Ini adalah sebuah kitab yang diturunkan kepadamu, maka janganlah ada kesempitan di dalam dadamu karenanya, supaya kamu memberi peringatan dengan kitab itu (kepada orang kafir), dan menjadi pelajaran bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,3,'اِتَّبِعُوْا مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ وَلَا تَتَّبِعُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۗ قَلِيْلًا مَّا تَذَكَّرُوْنَ ','Ikutilah apa yang diturunkan kepadamu dari Tuhanmu dan janganlah kamu mengikuti pemimpin-pemimpin selain-Nya. Amat sedikitlah kamu mengambil pelajaran (daripadanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,4,'وَكَمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا فَجَاۤءَهَا بَأْسُنَا بَيَاتًا اَوْ هُمْ قَاۤىِٕلُوْنَ','Betapa banyaknya negeri yang telah Kami binasakan, maka datanglah siksaan Kami (menimpa penduduk)nya di waktu mereka berada di malam hari, atau di waktu mereka beristirahat di tengah hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,5,'فَمَا كَانَ دَعْوٰىهُمْ اِذْ جَاۤءَهُمْ بَأْسُنَآ اِلَّآ اَنْ قَالُوْٓا اِنَّا كُنَّا ظٰلِمِيْنَ ','Maka tidak adalah keluhan mereka di waktu datang kepada mereka siksaan Kami, kecuali mengatakan: "Sesungguhnya kami adalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,6,'فَلَنَسْـَٔلَنَّ الَّذِيْنَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَلِيْنَۙ','Maka sesungguhnya Kami akan menanyai umat-umat yang telah diutus rasul-rasul kepada mereka dan sesungguhnya Kami akan menanyai (pula) rasul-rasul (Kami),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,7,'فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَّمَا كُنَّا غَاۤىِٕبِيْنَ ','maka sesungguhnya akan Kami kabarkan kepada mereka (apa-apa yang telah mereka perbuat), sedang (Kami) mengetahui (keadaan mereka), dan Kami sekali-kali tidak jauh (dari mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,8,'وَالْوَزْنُ يَوْمَىِٕذِ ِۨالْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Timbangan pada hari itu ialah kebenaran (keadilan), maka barangsiapa berat timbangan kebaikannya, maka mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,9,'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ بِمَا كَانُوْا بِاٰيٰتِنَا يَظْلِمُوْنَ ','Dan siapa yang ringan timbangan kebaikannya, maka itulah orang-orang yang merugikan dirinya sendiri, disebabkan mereka selalu mengingkari ayat-ayat Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,10,'وَلَقَدْ مَكَّنّٰكُمْ فِى الْاَرْضِ وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ࣖ ','Sesungguhnya Kami telah menempatkan kamu sekalian di muka bumi dan Kami adakan bagimu di muka bumi (sumber) penghidupan. Amat sedikitlah kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,11,'وَلَقَدْ خَلَقْنٰكُمْ ثُمَّ صَوَّرْنٰكُمْ ثُمَّ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ  لَمْ يَكُنْ مِّنَ السّٰجِدِيْنَ','Sesungguhnya Kami telah menciptakan kamu (Adam), lalu Kami bentuk tubuhmu, kemudian Kami katakan kepada para malaikat: "Bersujudlah kamu kepada Adam", maka merekapun bersujud kecuali iblis. Dia tidak termasuk mereka yang bersujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,12,'قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَ ۗقَالَ اَنَا۠ خَيْرٌ مِّنْهُۚ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ ','Allah berfirman: "Apakah yang menghalangimu untuk bersujud (kepada Adam) di waktu Aku menyuruhmu?" Menjawab iblis "Saya lebih baik daripadanya: Engkau ciptakan saya dari api sedang dia Engkau ciptakan dari tanah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,13,'قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُوْنُ لَكَ اَنْ تَتَكَبَّرَ فِيْهَا فَاخْرُجْ اِنَّكَ مِنَ الصّٰغِرِيْنَ ','Allah berfirman: "Turunlah kamu dari surga itu; karena kamu sepatutnya menyombongkan diri di dalamnya, maka keluarlah, sesungguhnya kamu termasuk orang-orang yang hina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,14,'قَالَ اَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ','Iblis menjawab: "Beri tangguhlah saya sampai waktu mereka dibangkitkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,15,'قَالَ اِنَّكَ مِنَ الْمُنْظَرِيْنَ ','Allah berfirman: "Sesungguhnya kamu termasuk mereka yang diberi tangguh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,16,'قَالَ فَبِمَآ اَغْوَيْتَنِيْ لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيْمَۙ','Iblis menjawab: "Karena Engkau telah menghukum saya tersesat, saya benar-benar akan (menghalang-halangi) mereka dari jalan Engkau yang lurus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,17,'ثُمَّ لَاٰتِيَنَّهُمْ مِّنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَاۤىِٕلِهِمْۗ وَلَا تَجِدُ اَكْثَرَهُمْ شٰكِرِيْنَ','kemudian saya akan mendatangi mereka dari muka dan dari belakang mereka, dari kanan dan dari kiri mereka. Dan Engkau tidak akan mendapati kebanyakan mereka bersyukur (taat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,18,'قَالَ اخْرُجْ مِنْهَا مَذْءُوْمًا مَّدْحُوْرًا ۗ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَعِيْنَ','Allah berfirman: "Keluarlah kamu dari surga itu sebagai orang terhina lagi terusir. Sesungguhnya barangsiapa di antara mereka mengikuti kamu, benar-benar Aku akan mengisi neraka Jahannam dengan kamu semuanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,19,'وَيٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ','(Dan Allah berfirman): "Hai Adam bertempat tinggallah kamu dan isterimu di surga serta makanlah olehmu berdua (buah-buahan) di mana saja yang kamu sukai, dan janganlah kamu berdua mendekati pohon ini, lalu menjadilah kamu berdua termasuk orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,20,'فَوَسْوَسَ لَهُمَا الشَّيْطٰنُ لِيُبْدِيَ لَهُمَا مَا وٗرِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰىكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ ِالَّآ اَنْ تَكُوْنَا مَلَكَيْنِ اَوْ تَكُوْنَا مِنَ الْخٰلِدِيْنَ ','Maka syaitan membisikkan pikiran jahat kepada keduanya untuk menampakkan kepada keduanya apa yang tertutup dari mereka yaitu auratnya dan syaitan berkata: "Tuhan kamu tidak melarangmu dan mendekati pohon ini, melainkan supaya kamu berdua tidak menjadi malaikat atau tidak menjadi orang-orang yang kekal (dalam surga)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,21,'وَقَاسَمَهُمَآ اِنِّيْ لَكُمَا لَمِنَ النّٰصِحِيْنَۙ','Dan dia (syaitan) bersumpah kepada keduanya. "Sesungguhnya saya adalah termasuk orang yang memberi nasehat kepada kamu berdua",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,22,'فَدَلّٰىهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۗ وَنَادٰىهُمَا رَبُّهُمَآ اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَّكُمَآ اِنَّ الشَّيْطٰنَ لَكُمَا عَدُوٌّ مُّبِيْنٌ ','maka syaitan membujuk keduanya (untuk memakan buah itu) dengan tipu daya. Tatkala keduanya telah merasai buah kayu itu, nampaklah bagi keduanya aurat-auratnya, dan mulailah keduanya menutupinya dengan daun-daun surga. Kemudian Tuhan mereka menyeru mereka: "Bukankah Aku telah melarang kamu berdua dari pohon kayu itu dan Aku katakan kepadamu: "Sesungguhnya syaitan itu adalah musuh yang nyata bagi kamu berdua?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,23,'قَالَا رَبَّنَا ظَلَمْنَآ اَنْفُسَنَا وَاِنْ لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ ','Keduanya berkata: "Ya Tuhan kami, kami telah menganiaya diri kami sendiri, dan jika Engkau tidak mengampuni kami dan memberi rahmat kepada kami, niscaya pastilah kami termasuk orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,24,'قَالَ اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚوَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ ','Allah berfirman: "Turunlah kamu sekalian, sebahagian kamu menjadi musuh bagi sebahagian yang lain. Dan kamu mempunyai tempat kediaman dan kesenangan (tempat mencari kehidupan) di muka bumi sampai waktu yang telah ditentukan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,25,'قَالَ فِيْهَا تَحْيَوْنَ وَفِيْهَا تَمُوْتُوْنَ وَمِنْهَا تُخْرَجُوْنَ ࣖ ','Allah berfirman: "Di bumi itu kamu hidup dan di bumi itu kamu mati, dan dari bumi itu (pula) kamu akan dibangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,26,'يَا بَنِيْٓ اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُّوَارِيْ سَوْاٰتِكُمْ وَرِيْشًاۗ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُوْنَ ','Hai anak Adam, sesungguhnya Kami telah menurunkan kepadamu pakaian untuk menutup auratmu dan pakaian indah untuk perhiasan. Dan pakaian takwa itulah yang paling baik. Yang demikian itu adalah sebahagian dari tanda-tanda kekuasaan Allah, mudah-mudahan mereka selalu ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,27,'يٰبَنِيْٓ اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطٰنُ كَمَآ اَخْرَجَ اَبَوَيْكُمْ مِّنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَا ۗاِنَّهٗ يَرٰىكُمْ هُوَ وَقَبِيْلُهٗ مِنْ حَيْثُ لَا تَرَوْنَهُمْۗ اِنَّا جَعَلْنَا الشَّيٰطِيْنَ اَوْلِيَاۤءَ لِلَّذِيْنَ لَا يُؤْمِنُوْنَ ','Hai anak Adam, janganlah sekali-kali kamu dapat ditipu oleh syaitan sebagaimana ia telah mengeluarkan kedua ibu bapamu dari surga, ia menanggalkan dari keduanya pakaiannya untuk memperlihatkan kepada keduanya auratnya. Sesungguhnya ia dan pengikut-pengikutnya melihat kamu dan suatu tempat yang kamu tidak bisa melihat mereka. Sesungguhnya Kami telah menjadikan syaitan-syaitan itu pemimpin-pemimpim bagi orang-orang yang tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,28,'وَاِذَا فَعَلُوْا فَاحِشَةً قَالُوْا وَجَدْنَا عَلَيْهَآ اٰبَاۤءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۗ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَاۤءِۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ','Dan apabila mereka melakukan perbuatan keji, mereka berkata: "Kami mendapati nenek moyang kami mengerjakan yang demikian itu, dan Allah menyuruh kami mengerjakannya". Katakanlah: "Sesungguhnya Allah tidak menyuruh (mengerjakan) perbuatan yang keji". Mengapa kamu mengada-adakan terhadap Allah apa yang tidak kamu ketahui?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,29,'قُلْ اَمَرَ رَبِّيْ بِالْقِسْطِۗ وَاَقِيْمُوْا وُجُوْهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۗ كَمَا بَدَاَكُمْ تَعُوْدُوْنَۗ','Katakanlah: "Tuhanku menyuruh menjalankan keadilan". Dan (katakanlah): "Luruskanlah muka (diri)mu di setiap sembahyang dan sembahlah Allah dengan mengikhlaskan ketaatanmu kepada-Nya. Sebagaimana Dia telah menciptakan kamu pada permulaan (demikian pulalah kamu akan kembali kepada-Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,30,'فَرِيْقًا هَدٰى وَفَرِيْقًا حَقَّ عَلَيْهِمُ الضَّلٰلَةُ  ۗاِنَّهُمُ اتَّخَذُوا الشَّيٰطِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ اللّٰهِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ ','Sebahagian diberi-Nya petunjuk dan sebahagian lagi telah pasti kesesatan bagi mereka. Sesungguhnya mereka menjadikan syaitan-syaitan pelindung (mereka) selain Allah, dan mereka mengira bahwa mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,31,'۞ يٰبَنِيْٓ اٰدَمَ خُذُوْا زِيْنَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّكُلُوْا وَاشْرَبُوْا وَلَا تُسْرِفُوْاۚ اِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَ ࣖ ','Hai anak Adam, pakailah pakaianmu yang indah di setiap (memasuki) mesjid, makan dan minumlah, dan janganlah berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang-orang yang berlebih-lebihan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,32,'قُلْ مَنْ حَرَّمَ زِيْنَةَ اللّٰهِ الَّتِيْٓ اَخْرَجَ لِعِبَادِهٖ وَالطَّيِّبٰتِ مِنَ الرِّزْقِۗ قُلْ هِيَ لِلَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَّوْمَ الْقِيٰمَةِۗ  كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ','Katakanlah: "Siapakah yang mengharamkan perhiasan dari Allah yang telah dikeluarkan-Nya untuk hamba-hamba-Nya dan (siapa pulakah yang mengharamkan) rezeki yang baik?" Katakanlah: "Semuanya itu (disediakan) bagi orang-orang yang beriman dalam kehidupan dunia, khusus (untuk mereka saja) di hari kiamat". Demikianlah Kami menjelaskan ayat-ayat itu bagi orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,33,'قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ','Katakanlah: "Tuhanku hanya mengharamkan perbuatan yang keji, baik yang nampak ataupun yang tersembunyi, dan perbuatan dosa, melanggar hak manusia tanpa alasan yang benar, (mengharamkan) mempersekutukan Allah dengan sesuatu yang Allah tidak menurunkan hujjah untuk itu dan (mengharamkan) mengada-adakan terhadap Allah apa yang tidak kamu ketahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,34,'وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ','Tiap-tiap umat mempunyai batas waktu; maka apabila telah datang waktunya mereka tidak dapat mengundurkannya barang sesaatpun dan tidak dapat (pula) memajukannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,35,'يٰبَنِيْٓ اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ','Hai anak-anak Adam, jika datang kepadamu rasul-rasul daripada kamu yang menceritakan kepadamu ayat-ayat-Ku, maka barangsiapa yang bertakwa dan mengadakan perbaikan, tidaklah ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,36,'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ','Dan orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, mereka itu penghuni-penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,37,'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اُولٰۤىِٕكَ يَنَالُهُمْ نَصِيْبُهُمْ مِّنَ الْكِتٰبِۗ حَتّٰٓى اِذَا جَاۤءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ  قَالُوْٓا اَيْنَ مَا كُنْتُمْ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ ','Maka siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah atau mendustakan ayat-ayat-Nya? Orang-orang itu akan memperoleh bahagian yang telah ditentukan untuknya dalam Kitab (Lauh Mahfuzh); hingga bila datang kepada mereka utusan-utusan Kami (malaikat) untuk mengambil nyawanya, (di waktu itu) utusan Kami bertanya: "Di mana (berhala-berhala) yang biasa kamu sembah selain Allah?" Orang-orang musyrik itu menjawab: "Berhala-berhala itu semuanya telah lenyap dari kami," dan mereka mengakui terhadap diri mereka bahwa mereka adalah orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,38,'قَالَ ادْخُلُوْا فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِّنَ الْجِنِّ وَالْاِنْسِ فِى النَّارِۙ كُلَّمَا دَخَلَتْ اُمَّةٌ لَّعَنَتْ اُخْتَهَا ۗحَتّٰٓى اِذَا ادَّارَكُوْا فِيْهَا جَمِيْعًا ۙقَالَتْ اُخْرٰىهُمْ لِاُوْلٰىهُمْ رَبَّنَا هٰٓؤُلَاۤءِ اَضَلُّوْنَا فَاٰتِهِمْ عَذَابًا ضِعْفًا مِّنَ النَّارِ ەۗ قَالَ لِكُلٍّ ضِعْفٌ وَّلٰكِنْ لَّا تَعْلَمُوْنَ','Allah berfirman: "Masuklah kamu sekalian ke dalam neraka bersama umat-umat jin dan manusia yang telah terdahulu sebelum kamu. Setiap suatu umat masuk (ke dalam neraka), dia mengutuk kawannya (menyesatkannya); sehingga apabila mereka masuk semuanya berkatalah orang-orang yang masuk kemudian di antara mereka kepada orang-orang yang masuk terdahulu: "Ya Tuhan kami, mereka telah menyesatkan kami, sebab itu datangkanlah kepada mereka siksaan yang berlipat ganda dari neraka". Allah berfirman: "Masing-masing mendapat (siksaan) yang berlipat ganda, akan tetapi kamu tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,39,'وَقَالَتْ اُوْلٰىهُمْ لِاُخْرٰىهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُوْنَ ࣖ','Dan berkata orang-orang yang masuk terdahulu di antara mereka kepada orang-orang yang masuk kemudian: "Kamu tidak mempunyai kelebihan sedikitpun atas kami, maka rasakanlah siksaan karena perbuatan yang telah kamu lakukan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,40,'اِنَّ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَاۤءِ وَلَا يَدْخُلُوْنَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ فِيْ سَمِّ الْخِيَاطِ ۗ وَكَذٰلِكَ نَجْزِى الْمُجْرِمِيْنَ ','Sesungguhnya orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, sekali-kali tidak akan dibukakan bagi mereka pintu-pintu langit dan tidak (pula) mereka masuk surga, hingga unta masuk ke lubang jarum. Demikianlah Kami memberi pembalasan kepada orang-orang yang berbuat kejahatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,41,'لَهُمْ مِّنْ جَهَنَّمَ مِهَادٌ وَّمِنْ فَوْقِهِمْ غَوَاشٍۗ وَكَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ ','Mereka mempunyai tikar tidur dari api neraka dan di atas mereka ada selimut (api neraka). Demikianlah Kami memberi balasan kepada orang-orang yang zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,42,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ ','dan orang-orang yang beriman dan mengerjakan amal-amal yang saleh, Kami tidak memikulkan kewajiban kepada diri seseorang melainkan sekedar kesanggupannya, mereka itulah penghuni-penghuni surga; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,43,'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ تَجْرِيْ مِنْ تَحْتِهِمُ الْانْهٰرُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ هَدٰىنَا لِهٰذَاۗ وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَآ اَنْ هَدٰىنَا اللّٰهُ ۚ لَقَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۗ وَنُوْدُوْٓا اَنْ تِلْكُمُ الْجَنَّةُ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ','Dan Kami cabut segala macam dendam yang berada di dalam dada mereka; mengalir di bawah mereka sungai-sungai dan mereka berkata: "Segala puji bagi Allah yang telah menunjuki kami kepada (surga) ini. Dan kami sekali-kali tidak akan mendapat petunjuk kalau Allah tidak memberi kami petunjuk. Sesungguhnya telah datang rasul-rasul Tuhan kami, membawa kebenaran". Dan diserukan kepada mereka: "ltulah surga yang diwariskan kepadamu, disebabkan apa yang dahulu kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,44,'وَنَادٰٓى اَصْحٰبُ الْجَنَّةِ اَصْحٰبَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُّمْ مَّا وَعَدَ رَبُّكُمْ حَقًّا ۗقَالُوْا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌۢ بَيْنَهُمْ اَنْ لَّعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ','Dan penghuni-penghuni surga berseru kepada Penghuni-penghuni neraka (dengan mengatakan): "Sesungguhnya kami dengan sebenarnya telah memperoleh apa yang Tuhan kami menjanjikannya kepada kami. Maka apakah kamu telah memperoleh dengan sebenarnya apa (azab) yang Tuhan kamu menjanjikannya (kepadamu)?" Mereka (penduduk neraka) menjawab: "Betul". Kemudian seorang penyeru (malaikat) mengumumkan di antara kedua golongan itu: "Kutukan Allah ditimpakan kepada orang-orang yang zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,45,'اَلَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۚ وَهُمْ بِالْاٰخِرَةِ كٰفِرُوْنَۘ','(yaitu) orang-orang yang menghalang-halangi (manusia) dari jalan Allah dan menginginkan agar jalan itu menjadi bengkok, dan mereka kafir kepada kehidupan akhirat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,46,'وَبَيْنَهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَّعْرِفُوْنَ كُلًّا ۢ بِسِيْمٰىهُمْۚ وَنَادَوْا اَصْحٰبَ الْجَنَّةِ اَنْ سَلٰمٌ عَلَيْكُمْۗ  لَمْ يَدْخُلُوْهَا وَهُمْ يَطْمَعُوْنَ ','Dan di antara keduanya (penghuni surga dan neraka) ada batas; dan di atas A´raaf itu ada orang-orang yang mengenal masing-masing dari dua golongan itu dengan tanda-tanda mereka. Dan mereka menyeru penduduk surga: "Salaamun ´alaikum". Mereka belum lagi memasukinya, sedang mereka ingin segera (memasukinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,47,'۞ وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَاۤءَ اَصْحٰبِ النَّارِۙ قَالُوْا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظّٰلِمِيْنَ ࣖ','Dan apabila pandangan mereka dialihkan ke arah penghuni neraka, mereka berkata: "Ya Tuhan kami, janganlah Engkau tempatkan kami bersama-sama orang-orang yang zalim itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,48,'وَنَادٰٓى اَصْحٰبُ الْاَعْرَافِ رِجَالًا يَّعْرِفُوْنَهُمْ بِسِيْمٰىهُمْ قَالُوْا مَآ اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُوْنَ ','Dan orang-orang yang di atas A´raaf memanggil beberapa orang (pemuka-pemuka orang kafir) yang mereka mengenalnya dengan tanda-tandanya dengan mengatakan: "Harta yang kamu kumpulkan dan apa yang selalu kamu sombongkan itu, tidaklah memberi manfaat kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,49,'اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۗ  اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَآ اَنْتُمْ تَحْزَنُوْنَ ','(Orang-orang di atas A´raaf bertanya kepada penghuni neraka): "Itukah orang-orang yang kamu telah bersumpah bahwa mereka tidak akan mendapat rahmat Allah?". (Kepada orang mukmin itu dikatakan): "Masuklah ke dalam surga, tidak ada kekhawatiran terhadapmu dan tidak (pula) kamu bersedih hati".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,50,'وَنَادٰٓى اَصْحٰبُ النَّارِ اَصْحٰبَ الْجَنَّةِ اَنْ اَفِيْضُوْا عَلَيْنَا مِنَ الْمَاۤءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُ ۗقَالُوْٓا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكٰفِرِيْنَۙ ','Dan penghuni neraka menyeru penghuni surga: "Limpahkanlah kepada kami sedikit air atau makanan yang telah direzekikan Allah kepadamu". Mereka (penghuni surga) menjawab: "Sesungguhnya Allah telah mengharamkan keduanya itu atas orang-orang kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,51,'الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَهْوًا وَّلَعِبًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰىهُمْ كَمَا نَسُوْا لِقَاۤءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ ','(yaitu) orang-orang yang menjadikan agama mereka sebagai main-main dan senda gurau, dan kehidupan dunia telah menipu mereka". Maka pada hari (kiamat) ini, Kami melupakan mereka sebagaimana mereka melupakan pertemuan mereka dengan hari ini, dan (sebagaimana) mereka selalu mengingkari ayat-ayat Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,52,'وَلَقَدْ جِئْنٰهُمْ بِكِتٰبٍ فَصَّلْنٰهُ عَلٰى عِلْمٍ هُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ','Dan sesungguhnya Kami telah mendatangkan sebuah Kitab (Al Quran) kepada mereka yang Kami telah menjelaskannya atas dasar pengetahuan Kami; menjadi petunjuk dan rahmat bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,53,'هَلْ يَنْظُرُوْنَ اِلَّا تَأْوِيْلَهٗۗ يَوْمَ يَأْتِيْ تَأْوِيْلُهٗ يَقُوْلُ الَّذِيْنَ نَسُوْهُ مِنْ قَبْلُ قَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَّنَا مِنْ شُفَعَاۤءَ فَيَشْفَعُوْا لَنَآ اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ قَدْ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ࣖ ','Tiadalah mereka menunggu-nunggu kecuali (terlaksananya kebenaran) Al Quran itu. Pada hari datangnya kebenaran pemberitaan Al Quran itu, berkatalah orang-orang yang melupakannya sebelum itu: "Sesungguhnya telah datang rasul-rasul Tuhan kami membawa yang hak, maka adakah bagi kami pemberi syafa´at yang akan memberi syafa´at bagi kami, atau dapatkah kami dikembalikan (ke dunia) sehingga kami dapat beramal yang lain dari yang pernah kami amalkan?". Sungguh mereka telah merugikan diri mereka sendiri dan telah lenyaplah dari mereka tuhan-tuhan yang mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,54,'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ يُغْشِى الَّيْلَ النَّهَارَ يَطْلُبُهٗ حَثِيْثًاۙ وَّالشَّمْسَ وَالْقَمَرَ وَالنُّجُوْمَ مُسَخَّرٰتٍۢ بِاَمْرِهٖٓ  ۙاَلَا لَهُ الْخَلْقُ وَالْاَمْرُۗ تَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ ','Sesungguhnya Tuhan kamu ialah Allah yang telah menciptakan langit dan bumi dalam enam masa, lalu Dia bersemayam di atas ´Arsy. Dia menutupkan malam kepada siang yang mengikutinya dengan cepat, dan (diciptakan-Nya pula) matahari, bulan dan bintang-bintang (masing-masing) tunduk kepada perintah-Nya. Ingatlah, menciptakan dan memerintah hanyalah hak Allah. Maha Suci Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,55,'اُدْعُوْا رَبَّكُمْ تَضَرُّعًا وَّخُفْيَةً ۗاِنَّهٗ لَا يُحِبُّ الْمُعْتَدِيْنَۚ ','Berdoalah kepada Tuhanmu dengan berendah diri dan suara yang lembut. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,56,'وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوْهُ خَوْفًا وَّطَمَعًاۗ اِنَّ رَحْمَتَ اللّٰهِ قَرِيْبٌ مِّنَ الْمُحْسِنِيْنَ ','Dan janganlah kamu membuat kerusakan di muka bumi, sesudah (Allah) memperbaikinya dan berdoalah kepada-Nya dengan rasa takut (tidak akan diterima) dan harapan (akan dikabulkan). Sesungguhnya rahmat Allah amat dekat kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,57,'وَهُوَ الَّذِيْ يُرْسِلُ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ حَتّٰٓى اِذَآ اَقَلَّتْ سَحَابًا ثِقَالًا سُقْنٰهُ لِبَلَدٍ مَّيِّتٍ فَاَنْزَلْنَا بِهِ الْمَاۤءَ فَاَخْرَجْنَا بِهٖ مِنْ كُلِّ الثَّمَرٰتِۗ  كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ تَذَكَّرُوْنَ ','Dan Dialah yang meniupkan angin sebagai pembawa berita gembira sebelum kedatangan rahmat-Nya (hujan); hingga apabila angin itu telah membawa awan mendung, Kami halau ke suatu daerah yang tandus, lalu Kami turunkan hujan di daerah itu, maka Kami keluarkan dengan sebab hujan itu pelbagai macam buah-buahan. Seperti itulah Kami membangkitkan orang-orang yang telah mati, mudah-mudahan kamu mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,58,'وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهٗ بِاِذْنِ رَبِّهٖۚ وَالَّذِيْ خَبُثَ لَا يَخْرُجُ اِلَّا نَكِدًاۗ  كَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ لِقَوْمٍ يَّشْكُرُوْنَ ࣖ ','Dan tanah yang baik, tanaman-tanamannya tumbuh subur dengan seizin Allah; dan tanah yang tidak subur, tanaman-tanamannya hanya tumbuh merana. Demikianlah Kami mengulangi tanda-tanda kebesaran (Kami) bagi orang-orang yang bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,59,'لَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ','Sesungguhnya Kami telah mengutus Nuh kepada kaumnya lalu ia berkata: "Wahai kaumku sembahlah Allah, sekali-kali tak ada Tuhan bagimu selain-Nya". Sesungguhnya (kalau kamu tidak menyembah Allah), aku takut kamu akan ditimpa azab hari yang besar (kiamat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,60,'قَالَ الْمَلَاُ مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Pemuka-pemuka dari kaumnya berkata: "Sesungguhnya kami memandang kamu berada dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,61,'قَالَ يٰقَوْمِ لَيْسَ بِيْ ضَلٰلَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ','Nuh menjawab: "Hai kaumku, tak ada padaku kesesatan sedikitpun tetapi aku adalah utusan dari Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,62,'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ ','"Aku sampaikan kepadamu amanat-amanat Tuhanku dan aku memberi nasehat kepadamu. dan aku mengetahui dari Allah apa yang tidak kamu ketahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,63,'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوْا وَلَعَلَّكُمْ تُرْحَمُوْنَ ','Dan apakah kamu (tidak percaya) dan heran bahwa datang kepada kamu peringatan dari Tuhanmu dengan perantaraan seorang laki-laki dari golonganmu agar dia memberi peringatan kepadamu dan mudah-mudahan kamu bertakwa dan supaya kamu mendapat rahmat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,64,'فَكَذَّبُوْهُ فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ فِى الْفُلْكِ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمًا عَمِيْنَ ࣖ ','Maka mereka mendustakan Nuh, kemudian Kami selamatkan dia dan orang-orang yang bersamanya dalam bahtera, dan Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang buta (mata hatinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,65,'۞ وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ','Dan (Kami telah mengutus) kepada kaum ´Aad saudara mereka, Hud. Ia berkata: "Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain dari-Nya. Maka mengapa kamu tidak bertakwa kepada-Nya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,66,'قَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ سَفَاهَةٍ وَّاِنَّا لَنَظُنُّكَ مِنَ الْكٰذِبِيْنَ ','Pemuka-pemuka yang kafir dari kaumnya berkata: "Sesungguhnya kami benar benar memandang kamu dalam keadaan kurang akal dan sesungguhnya kami menganggap kamu termasuk orang orang yang berdusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,67,'قَالَ يٰقَوْمِ لَيْسَ بِيْ سَفَاهَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ','Hud herkata "Hai kaumku, tidak ada padaku kekurangan akal sedikitpun, tetapi aku ini adalah utusan dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,68,'اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنَا۠ لَكُمْ نَاصِحٌ اَمِيْنٌ ','Aku menyampaikan amanat-amanat Tuhanku kepadamu dan aku hanyalah pemberi nasehat yang terpercaya bagimu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,69,'اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْۗ وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ قَوْمِ نُوْحٍ وَّزَادَكُمْ فِى الْخَلْقِ بَصْۣطَةً ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُوْنَ ','Apakah kamu (tidak percaya) dan heran bahwa datang kepadamu peringatan dari Tuhanmu yang dibawa oleh seorang laki-laki di antaramu untuk memberi peringatan kepadamu? Dan ingatlah oleh kamu sekalian di waktu Allah menjadikan kamu sebagai pengganti-pengganti (yang berkuasa) sesudah lenyapnya kaum Nuh, dan Tuhan telah melebihkan kekuatan tubuh dan perawakanmu (daripada kaum Nuh itu). Maka ingatlah nikmat-nikmat Allah supaya kamu mendapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,70,'قَالُوْٓا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهٗ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَاۤؤُنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ','Mereka berkata: "Apakah kamu datang kepada kami, agar kami hanya menyembah Allah saja dan meninggalkan apa yang biasa disembah oleh bapak-bapak kami? maka datangkanlah azab yang kamu ancamkan kepada kami jika kamu termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,71,'قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِّنْ رَّبِّكُمْ رِجْسٌ وَّغَضَبٌۗ  اَتُجَادِلُوْنَنِيْ فِيْٓ اَسْمَاۤءٍ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ','Ia berkata: "Sungguh sudah pasti kamu akan ditimpa azab dan kemarahan dari Tuhanmu". Apakah kamu sekalian hendak berbantah dengan aku tentang nama-nama (berhala) yang kamu beserta nenek moyangmu menamakannya, padahal Allah sekali-kali tidak menurunkan hujjah untuk itu? Maka tunggulah (azab itu), sesungguhnya aku juga termasuk orang yamg menunggu bersama kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,72,'فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ بِرَحْمَةٍ مِّنَّا وَقَطَعْنَا دَابِرَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَمَا كَانُوْا مُؤْمِنِيْنَ ࣖ ','Maka kami selamatkan Hud beserta orang-orang yang bersamanya dengan rahmat yang besar dari Kami, dan Kami tumpas orang-orang yang mendustakan ayat-ayat Kami, dan tiadalah mereka orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,73,'وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًاۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْۗ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ اَلِيْمٌ  ','Dan (Kami telah mengutus) kepada kaum Tsamud saudara mereka Shaleh. Ia berkata: "Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain-Nya. Sesungguhnya telah datang bukti yang nyata kepadamu dari Tuhanmu. Unta betina Allah ini menjadi tanda bagimu, maka biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apapun, (yang karenanya) kamu akan ditimpa siksaan yang pedih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,74,'وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ عَادٍ وَّبَوَّاَكُمْ فِى الْاَرْضِ تَتَّخِذُوْنَ مِنْ سُهُوْلِهَا قُصُوْرًا وَّتَنْحِتُوْنَ الْجِبَالَ بُيُوْتًا ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ','Dan ingatlah olehmu di waktu Tuhan menjadikam kamu pengganti-pengganti (yang berkuasa) sesudah kaum ´Aad dan memberikan tempat bagimu di bumi. Kamu dirikan istana-istana di tanah-tanahnya yang datar dan kamu pahat gunung-gunungnya untuk dijadikan rumah; maka ingatlah nikmat-nikmat Allah dan janganlah kamu merajalela di muka bumi membuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,75,'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لِلَّذِيْنَ اسْتُضْعِفُوْا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُوْنَ اَنَّ صٰلِحًا مُّرْسَلٌ مِّنْ رَّبِّهٖۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلَ بِهٖ مُؤْمِنُوْنَ ','Pemuka-pemuka yang menyombongkan diri di antara kaumnya berkata kepada orang-orang yang dianggap lemah yang telah beriman di antara mereka: "Tahukah kamu bahwa Shaleh di utus (menjadi rasul) oleh Tuhannya?". Mereka menjawab: "Sesungguhnya kami beriman kepada wahyu, yang Shaleh diutus untuk menyampaikannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,76,'قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا بِالَّذِيْٓ اٰمَنْتُمْ بِهٖ كٰفِرُوْنَ','Orang-orang yang menyombongkan diri berkata: "Sesungguhnya kami adalah orang yang tidak percaya kepada apa yang kamu imani itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,77,'فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوْا يٰصٰلِحُ ائْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الْمُرْسَلِيْنَ ','Kemudian mereka sembelih unta betina itu, dan mereka berlaku angkuh terhadap perintah Tuhan. Dan mereka berkata: "Hai Shaleh, datangkanlah apa yang kamu ancamkan itu kepada kami, jika (betul) kamu termasuk orang-orang yang diutus (Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,78,'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ','Karena itu mereka ditimpa gempa, maka jadilah mereka mayat-mayat yang bergelimpangan di tempat tinggal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,79,'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبِّيْ وَنَصَحْتُ لَكُمْ وَلٰكِنْ لَّا تُحِبُّوْنَ النّٰصِحِيْنَ ','Maka Shaleh meninggalkan mereka seraya berkata: "Hai kaumku sesungguhnya aku telah menyampaikan kepadamu amanat Tuhanku, dan aku telah memberi nasehat kepadamu, tetapi kamu tidak menyukai orang-orang yang memberi nasehat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,80,'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ','Dan (Kami juga telah mengutus) Luth (kepada kaumnya). (Ingatlah) tatkala dia berkata kepada mereka: "Mengapa kamu mengerjakan perbuatan faahisyah itu, yang belum pernah dikerjakan oleh seorangpun (di dunia ini) sebelummu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,81,'اِنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ ','Sesungguhnya kamu mendatangi lelaki untuk melepaskan nafsumu (kepada mereka), bukan kepada wanita, malah kamu ini adalah kaum yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,82,'وَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْهُمْ مِّنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ ','Jawab kaumnya tidak lain hanya mengatakan: "Usirlah mereka (Luth dan pengikut-pengikutnya) dari kotamu ini; sesungguhnya mereka adalah orang-orang yang berpura-pura mensucikan diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,83,'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ','Kemudian Kami selamatkan dia dan pengikut-pengikutnya kecuali isterinya; dia termasuk orang-orang yang tertinggal (dibinasakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,84,'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ࣖ ','Dan Kami turunkan kepada mereka hujan (batu); maka perhatikanlah bagaimana kesudahan orang-orang yang berdosa itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,85,'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ ','Dan (Kami telah mengutus) kepada penduduk Mad-yan saudara mereka, Syu´aib. Ia berkata: "Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain-Nya. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Maka sempurnakanlah takaran dan timbangan dan janganlah kamu kurangkan bagi manusia barang-barang takaran dan timbangannya, dan janganlah kamu membuat kerusakan di muka bumi sesudah Tuhan memperbaikinya. Yang demikian itu lebih baik bagimu jika betul-betul kamu orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,86,'وَلَا تَقْعُدُوْا بِكُلِّ صِرَاطٍ تُوْعِدُوْنَ وَتَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ بِهٖ وَتَبْغُوْنَهَا عِوَجًاۚ وَاذْكُرُوْٓا اِذْ كُنْتُمْ قَلِيْلًا فَكَثَّرَكُمْۖ وَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ','Dan janganlah kamu duduk di tiap-tiap jalan dengan menakut-nakuti dan menghalang-halangi orang yang beriman dari jalan Allah, dan menginginkan agar jalan Allah itu menjadi bengkok. Dan ingatlah di waktu dahulunya kamu berjumlah sedikit, lalu Allah memperbanyak jumlah kamu. Dan perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,8,7,87,'وَاِنْ كَانَ طَاۤىِٕفَةٌ مِّنْكُمْ اٰمَنُوْا بِالَّذِيْٓ اُرْسِلْتُ بِهٖ وَطَاۤىِٕفَةٌ لَّمْ يُؤْمِنُوْا فَاصْبِرُوْا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ  ۔','Jika ada segolongan daripada kamu beriman kepada apa yang aku diutus untuk menyampaikannya dan ada (pula) segolongan yang tidak beriman, maka bersabarlah, hingga Allah menetapkan hukumnya di antara kita; dan Dia adalah Hakim yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,88,'قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لَنُخْرِجَنَّكَ يٰشُعَيْبُ وَالَّذِيْنَ اٰمَنُوْا مَعَكَ مِنْ قَرْيَتِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ قَالَ اَوَلَوْ كُنَّا كَارِهِيْنَ','Pemuka-pemuka dan kaum Syu´aib yang menyombongkan dan berkata: "Sesungguhnya kami akan mengusir kamu hai Syu´aib dan orang-orang yang beriman bersamamu dari kota kami, atau kamu kembali kepada agama kami". Berkata Syu´aib: "Dan apakah (kamu akan mengusir kami), kendatipun kami tidak menyukainya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,89,'قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِبًا اِنْ عُدْنَا فِيْ مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰىنَا اللّٰهُ مِنْهَاۗ وَمَا يَكُوْنُ لَنَآ اَنْ نَّعُوْدَ فِيْهَآ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّنَاۗ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًاۗ عَلَى اللّٰهِ تَوَكَّلْنَاۗ رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَاَنْتَ خَيْرُ الْفَاتِحِيْنَ','Sungguh kami mengada-adakan kebohongan yang benar terhadap Allah, jika kami kembali kepada agamamu, sesudah Allah melepaskan kami dari padanya. Dan tidaklah patut kami kembali kepadanya, kecuali jika Allah, Tuhan kami menghendaki(nya). Pengetahuan Tuhan kami meliputi segala sesuatu. Kepada Allah sajalah kami bertawakkal. Ya Tuhan kami, berilah keputusan antara kami dan kaum kami dengan hak (adil) dan Engkaulah Pemberi keputusan yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,90,'وَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ لَىِٕنِ اتَّبَعْتُمْ شُعَيْبًا ِانَّكُمْ اِذًا لَّخٰسِرُوْنَ','Pemuka-pemuka kaum Syu´aib yang kafir berkata (kepada sesamanya): "Sesungguhnya jika kamu mengikuti Syu´aib, tentu kamu jika berbuat demikian (menjadi) orang-orang yang merugi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,91,'فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَۙ','Kemudian mereka ditimpa gempa, maka jadilah mereka mayat-mayat yang bergelimpangan di dalam rumah-rumah mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,92,'الَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَاَنْ لَّمْ يَغْنَوْا فِيْهَاۚ  اَلَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَانُوْا هُمُ الْخٰسِرِيْنَ','(yaitu) orang-orang yang mendustakan Syu´aib seolah-olah mereka belum pernah berdiam di kota itu; orang-orang yang mendustakan Syu´aib mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,93,'فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسٰلٰتِ رَبِّيْ وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كٰفِرِيْنَ ࣖ ','Maka Syu´aib meninggalkan mereka seraya berkata: "Hai kaumku, sesungguhnya aku telah menyampaikan kepadamu amanat-amanat Tuhanku dan aku telah memberi nasehat kepadamu. Maka bagaimana aku akan bersedih hati terhadap orang-orang yang kafir?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,94,'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّبِيٍّ اِلَّآ اَخَذْنَآ اَهْلَهَا بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَضَّرَّعُوْنَ','Kami tidaklah mengutus seseorang nabipun kepada sesuatu negeri, (lalu penduduknya mendustakan nabi itu), melainkan Kami timpakan kepada penduduknya kesempitan dan penderitaan supaya mereka tunduk dengan merendahkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,95,'ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَّقَالُوْا قَدْ مَسَّ اٰبَاۤءَنَا الضَّرَّاۤءُ وَالسَّرَّاۤءُ فَاَخَذْنٰهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ','Kemudian Kami ganti kesusahan itu dengan kesenangan hingga keturunan dan harta mereka bertambah banyak, dan mereka berkata: "Sesungguhnya nenek moyang kamipun telah merasai penderitaan dan kesenangan", maka Kami timpakan siksaan atas mereka dengan sekonyong-konyong sedang mereka tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,96,'وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوْا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكٰتٍ مِّنَ السَّمَاۤءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوْا فَاَخَذْنٰهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ','Jikalau sekiranya penduduk negeri-negeri beriman dan bertakwa, pastilah Kami akan melimpahkan kepada mereka berkah dari langit dan bumi, tetapi mereka mendustakan (ayat-ayat Kami) itu, maka Kami siksa mereka disebabkan perbuatannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,97,'اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَّهُمْ نَاۤىِٕمُوْنَۗ','Maka apakah penduduk negeri-negeri itu merasa aman dari kedatangan siksaan Kami kepada mereka di malam hari di waktu mereka sedang tidur?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,98,'اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا ضُحًى وَّهُمْ يَلْعَبُوْنَ','Atau apakah penduduk negeri-negeri itu merasa aman dari kedatangan siksaan Kami kepada mereka di waktu matahari sepenggalahan naik ketika mereka sedang bermain?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,99,'اَفَاَمِنُوْا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخٰسِرُوْنَ ࣖ ','Maka apakah mereka merasa aman dari azab Allah (yang tidak terduga-duga)? Tiada yang merasa aman dan azab Allah kecuali orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,100,'اَوَلَمْ يَهْدِ لِلَّذِيْنَ يَرِثُوْنَ الْاَرْضَ مِنْۢ بَعْدِ اَهْلِهَآ اَنْ لَّوْ نَشَاۤءُ اَصَبْنٰهُمْ بِذُنُوْبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَسْمَعُوْنَ','Dan apakah belum jelas bagi orang-orang yang mempusakai suatu negeri sesudah (lenyap) penduduknya, bahwa kalau Kami menghendaki tentu Kami azab mereka karena dosa-dosanya; dan Kami kunci mati hati mereka sehingga mereka tidak dapat mendengar (pelajaran lagi)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,101,'تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤىِٕهَاۚ وَلَقَدْ جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا مِنْ قَبْلُۗ  كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الْكٰفِرِيْنَ  ','Negeri-negeri (yang telah Kami binasakan) itu, Kami ceritakan sebagian dari berita-beritanya kepadamu. Dan sungguh telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata, maka mereka (juga) tidak beriman kepada apa yang dahulunya mereka telah mendustakannya. Demikianlah Allah mengunci mata hati orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,102,'وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِّنْ عَهْدٍۚ وَاِنْ وَّجَدْنَآ اَكْثَرَهُمْ لَفٰسِقِيْنَ','Dan Kami tidak mendapati kebanyakan mereka memenuhi janji. Sesungguhnya Kami mendapati kebanyakan mereka orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,103,'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَظَلَمُوْا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ  ','Kemudian Kami utus Musa sesudah rasul-rasul itu dengan membawa ayat-ayat Kami kepada Fir´aun dan pemuka-pemuka kaumnya, lalu mereka mengingkari ayat-ayat itu. Maka perhatikanlah bagaimana akibat orang-orang yang membuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,104,'وَقَالَ مُوْسٰى يٰفِرْعَوْنُ اِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَۙ','Dan Musa berkata: "Hai Fir´aun, sesungguhnya aku ini adalah seorang utusan dari Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,105,'حَقِيْقٌ عَلٰٓى اَنْ لَّآ اَقُوْلَ عَلَى اللّٰهِ اِلَّا الْحَقَّۗ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِّنْ رَّبِّكُمْ فَاَرْسِلْ مَعِيَ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ','wajib atasku tidak mengatakan sesuatu terhadap Allah, kecuali yang hak. Sesungguhnya aku datang kepadamu dengan membawa bukti yang nyata dari Tuhanmu, maka lepaskanlah Bani Israil (pergi) bersama aku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,106,'قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ','Fir´aun menjawab: "Jika benar kamu membawa sesuatu bukti, maka datangkanlah bukti itu jika (betul) kamu termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,107,'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۖ  ','Maka Musa menjatuhkan tongkat-nya, lalu seketika itu juga tongkat itu menjadi ular yang sebenarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,108,'وَّنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ ࣖ','Dan ia mengeluarkan tangannya, maka ketika itu juga tangan itu menjadi putih bercahaya (kelihatan) oleh orang-orang yang melihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,109,'قَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌۙ  ','Pemuka-pemuka kaum Fir´aun berkata: "Sesungguhnya Musa ini adalah ahli sihir yang pandai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,110,'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ ۚ فَمَاذَا تَأْمُرُوْنَ','yang bermaksud hendak mengeluarkan kamu dari negerimu". (Fir´aun berkata): "Maka apakah yang kamu anjurkan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,111,'قَالُوْآ اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَۙ ','Pemuka-pemuka itu menjawab: "Beri tangguhlah dia dan saudaranya serta kirimlah ke kota-kota beberapa orang yang akan mengumpulkan (ahli-ahli sihir),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,112,'يَأْتُوْكَ بِكُلِّ سٰحِرٍ عَلِيْمٍ','supaya mereka membawa kepadamu semua ahli sihir yang pandai".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,113,'وَجَاۤءَ السَّحَرَةُ فِرْعَوْنَ قَالُوْٓا اِنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ','Dan beberapa ahli sihir itu datang kepada Fir´aun mengatakan: "(Apakah) sesungguhnya kami akan mendapat upah, jika kamilah yang menang?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,114,'قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّبِيْنَ','Fir´aun menjawab: "Ya, dan sesungguhnya kamu benar-benar akan termasuk orang-orang yang dekat (kepadaku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,115,'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ نَحْنُ الْمُلْقِيْنَ','Ahli-ahli sihir berkata: "Hai Musa, kamukah yang akan melemparkan lebih dahulu, ataukah kami yang akan melemparkan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,116,'قَالَ اَلْقُوْاۚ فَلَمَّآ اَلْقَوْا سَحَرُوْٓا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوْهُمْ وَجَاۤءُوْ بِسِحْرٍ عَظِيْمٍ','Musa menjawab: "Lemparkanlah (lebih dahulu)!" Maka tatkala mereka melemparkan, mereka menyulap mata orang dan menjadikan orang banyak itu takut, serta mereka mendatangkan sihir yang besar (mena´jubkan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,117,'۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَۚ ','Dan Kami wahyukan kepada Musa: "Lemparkanlah tongkatmu!". Maka sekonyong-konyong tongkat itu menelan apa yang mereka sulapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,118,'فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوْا يَعْمَلُوْنَۚ ','Karena itu nyatalah yang benar dan batallah yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,119,'فَغُلِبُوْا هُنَالِكَ وَانْقَلَبُوْا صٰغِرِيْنَۚ ','Maka mereka kalah di tempat itu dan jadilah mereka orang-orang yang hina.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,120,'وَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَۙ ','Dan ahli-ahli sihir itu serta merta meniarapkan diri dengan bersujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,121,'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَۙ ','Mereka berkata: "Kami beriman kepada Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,122,'رَبِّ مُوْسٰى وَهٰرُوْنَ','"(yaitu) Tuhan Musa dan Harun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,123,'قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِهٖ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَّكَرْتُمُوْهُ فِى الْمَدِيْنَةِ لِتُخْرِجُوْا مِنْهَآ اَهْلَهَاۚ فَسَوْفَ تَعْلَمُوْنَ','Fir´aun berkata: "Apakah kamu beriman kepadanya sebelum aku memberi izin kepadamu?, sesungguhnya (perbuatan ini) adalah suatu muslihat yang telah kamu rencanakan di dalam kota ini, untuk mengeluarkan penduduknya dari padanya; maka kelak kamu akan mengetahui (akibat perbuatanmu ini);')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,124,'لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَ','demi, sesungguhnya aku akan memotong tangan dan kakimu dengan bersilang secara bertimbal balik, kemudian sungguh-sungguh aku akan menyalib kamu semuanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,125,'قَالُوْٓا اِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَۙ ','Ahli-ahli sihir itu menjawab: "Sesungguhnya kepada Tuhanlah kami kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,126,'وَمَا تَنْقِمُ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاٰيٰتِ رَبِّنَا لَمَّا جَاۤءَتْنَا ۗرَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّتَوَفَّنَا مُسْلِمِيْنَ ࣖ ','Dan kamu tidak menyalahkan kami, melainkan karena kami telah beriman kepada ayat-ayat Tuhan kami ketika ayat-ayat itu datang kepada kami". (Mereka berdoa): "Ya Tuhan kami, limpahkanlah kesabaran kepada kami dan wafatkanlah kami dalam keadaan berserah diri (kepada-Mu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,127,'وَقَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوْسٰى وَقَوْمَهٗ لِيُفْسِدُوْا فِى الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۗ قَالَ سَنُقَتِّلُ اَبْنَاۤءَهُمْ وَنَسْتَحْيٖ نِسَاۤءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُوْنَ','Berkatalah pembesar-pembesar dari kaum Fir´aun (kepada Fir´aun): "Apakah kamu membiarkan Musa dan kaumnya untuk membuat kerusakan di negeri ini (Mesir) dan meninggalkan kamu serta tuhan-tuhanmu?". Fir´aun menjawab: "Akan kita bunuh anak-anak lelaki mereka dan kita biarkan hidup perempuan-perempuan mereka; dan sesungguhnya kita berkuasa penuh di atas mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,128,'قَالَ مُوْسٰى لِقَوْمِهِ اسْتَعِيْنُوْا بِاللّٰهِ وَاصْبِرُوْاۚ اِنَّ الْاَرْضَ لِلّٰهِ ۗيُوْرِثُهَا مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَالْعَاقِبَةُ لِلْمُتَّقِيْنَ','Musa berkata kepada kaumnya: "Mohonlah pertolongan kepada Allah dan bersabarlah; sesungguhnya bumi (ini) kepunyaan Allah; dipusakakan-Nya kepada siapa yang dihendaki-Nya dari hamba-hamba-Nya. Dan kesudahan yang baik adalah bagi orang-orang yang bertakwa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,129,'قَالُوْٓا اُوْذِيْنَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْۢ بَعْدِ مَا جِئْتَنَا ۗقَالَ عَسٰى رَبُّكُمْ اَنْ يُّهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِى الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُوْنَ ࣖ ','Kaum Musa berkata: "Kami telah ditindas (oleh Fir´aun) sebelum kamu datang kepada kami dan sesudah kamu datang. Musa menjawab: "Mudah-mudahan Allah membinasakan musuhmu dan menjadikan kamu khalifah di bumi(Nya), maka Allah akan melihat bagaimana perbuatanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,130,'وَلَقَدْ اَخَذْنَآ اٰلَ فِرْعَوْنَ بِالسِّنِيْنَ وَنَقْصٍ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَذَّكَّرُوْنَ','Dan sesungguhnya Kami telah menghukum (Fir´aun dan) kaumnya dengan (mendatangkan) musim kemarau yang panjang dan kekurangan buah-buahan, supaya mereka mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,131,'فَاِذَا جَاۤءَتْهُمُ الْحَسَنَةُ قَالُوْا لَنَا هٰذِهٖ ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّطَّيَّرُوْا بِمُوْسٰى وَمَنْ مَّعَهٗۗ  اَلَآ اِنَّمَا طٰۤىِٕرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Kemudian apabila datang kepada mereka kemakmuran, mereka berkata: "Itu adalah karena (usaha) kami". Dan jika mereka ditimpa kesusahan, mereka lemparkan sebab kesialan itu kepada Musa dan orang-orang yang besertanya. Ketahuilah, sesungguhnya kesialan mereka itu adalah ketetapan dari Allah, akan tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,132,'وَقَالُوْا مَهْمَا تَأْتِنَا بِهٖ مِنْ اٰيَةٍ لِّتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ','Mereka berkata: "Bagaimanapun kamu mendatangkan keterangan kepada kami untuk menyihir kami dengan keterangan itu, maka kami sekali-kali tidak akan beriman kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,133,'فَاَرْسَلْنَا عَلَيْهِمُ الطُّوْفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيٰتٍ مُّفَصَّلٰتٍۗ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ','Maka Kami kirimkan kepada mereka taufan, belalang, kutu, katak dan darah sebagai bukti yang jelas, tetapi mereka tetap menyombongkan diri dan mereka adalah kaum yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,134,'وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوْا يٰمُوْسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ  لَىِٕنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِيْٓ اِسْرَاۤءِيْلَ ۚ','Dan ketika mereka ditimpa azab (yang telah diterangkan itu) merekapun berkata: "Hai Musa, mohonkanlah untuk kami kepada Tuhamnu dengan (perantaraan) kenabian yang diketahui Allah ada pada sisimu. Sesungguhnya jika kamu dapat menghilangkan azab itu dan pada kami, pasti kami akan beriman kepadamu dan akan kami biarkan Bani Israil pergi bersamamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,135,'فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوْهُ اِذَا هُمْ يَنْكُثُوْنَ','Maka setelah Kami hilangkan azab itu dari mereka hingga batas waktu yang mereka sampai kepadanya, tiba-tiba mereka mengingkarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,136,'فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ فِى الْيَمِّ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ ','Kemudian Kami menghukum mereka, maka Kami tenggelamkan mereka di laut disebabkan mereka mendustakan ayat-ayat Kami dan mereka adalah orang-orang yang melalaikan ayat-ayat Kami itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,137,'وَاَوْرَثْنَا الْقَوْمَ الَّذِيْنَ كَانُوْا يُسْتَضْعَفُوْنَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَۙ بِمَا صَبَرُوْاۗ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهٗ وَمَا كَانُوْا يَعْرِشُوْنَ','Dan Kami pusakakan kepada kaum yang telah ditindas itu, negeri-negeri bahagian timur bumi dan bahagian baratnya yang telah Kami beri berkah padanya. Dan telah sempurnalah perkataan Tuhanmu yang baik (sebagai janji) untuk Bani Israil disebabkan kesabaran mereka. Dan Kami hancurkan apa yang telah dibuat Fir´aun dan kaumnya dan apa yang telah dibangun mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,138,'وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَّعْكُفُوْنَ عَلٰٓى اَصْنَامٍ لَّهُمْ ۚقَالُوْا يٰمُوْسَى اجْعَلْ لَّنَآ اِلٰهًا كَمَا لَهُمْ اٰلِهَةٌ  ۗقَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُوْنَ','Dan Kami seberangkan Bani Israil ke seberang lautan itu, maka setelah mereka sampai kepada suatu kaum yang tetap menyembah berhala mereka, Bani lsrail berkata: "Hai Musa. buatlah untuk kami sebuah tuhan (berhala) sebagaimana mereka mempunyai beberapa tuhan (berhala)". Musa menjawab: "Sesungguh-nya kamu ini adalah kaum yang tidak mengetahui (sifat-sifat Tuhan)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,139,'اِنَّ هٰٓؤُلَاۤءِ مُتَبَّرٌ مَّا هُمْ فِيْهِ وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ','Sesungguhnya mereka itu akan dihancurkan kepercayaan yang dianutnya dan akan batal apa yang seIalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,140,'قَالَ اَغَيْرَ اللّٰهِ اَبْغِيْكُمْ اِلٰهًا وَّهُوَ فَضَّلَكُمْ عَلَى الْعٰلَمِيْنَ','Musa menjawab: "Patutkah aku mencari Tuhan untuk kamu yang selain dari pada Allah, padahal Dialah yang telah melebihkan kamu atas segala umat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,141,'وَاِذْ اَنْجَيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِۚ يُقَتِّلُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ࣖ ','Dan (ingatlah hai Bani Israil), ketika Kami menyelamatkan kamu dari (Fir´aun) dan kaumnya, yang mengazab kamu dengan azab yang sangat jahat, yaitu mereka membunuh anak-anak lelakimu dan membiarkan hidup wanita-wanitamu. Dan pada yang demikian itu cobaan yang besar dari Tuhanmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,142,'۞ وَوٰعَدْنَا مُوْسٰى ثَلٰثِيْنَ لَيْلَةً وَّاَتْمَمْنٰهَا بِعَشْرٍ فَتَمَّ مِيْقَاتُ رَبِّهٖٓ اَرْبَعِيْنَ لَيْلَةً ۚوَقَالَ مُوْسٰى لِاَخِيْهِ هٰرُوْنَ اخْلُفْنِيْ فِيْ قَوْمِيْ وَاَصْلِحْ وَلَا تَتَّبِعْ سَبِيْلَ الْمُفْسِدِيْنَ','Dan telah Kami janjikan kepada Musa (memberikan Taurat) sesudah berlalu waktu tiga puluh malam, dan Kami sempurnakan jumlah malam itu dengan sepuluh (malam lagi), maka sempurnalah waktu yang telah ditentukan Tuhannya empat puluh malam. Dan berkata Musa kepada saudaranya yaitu Harun: "Gantikanlah aku dalam (memimpin) kaumku, dan perbaikilah, dan janganlah kamu mengikuti jalan orang-orang yang membuat kerusakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,143,'وَلَمَّا جَاۤءَ مُوْسٰى لِمِيْقَاتِنَا وَكَلَّمَهٗ رَبُّهٗۙ قَالَ رَبِّ اَرِنِيْٓ اَنْظُرْ اِلَيْكَۗ قَالَ لَنْ تَرٰىنِيْ وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهٗ فَسَوْفَ تَرٰىنِيْۚ فَلَمَّا تَجَلّٰى رَبُّهٗ لِلْجَبَلِ جَعَلَهٗ دَكًّا وَّخَرَّ مُوْسٰى صَعِقًاۚ فَلَمَّآ اَفَاقَ قَالَ سُبْحٰنَكَ تُبْتُ اِلَيْكَ وَاَنَا۠ اَوَّلُ الْمُؤْمِنِيْنَ','Dan tatkala Musa datang untuk (munajat dengan Kami) pada waktu yang telah Kami tentukan dan Tuhan telah berfirman (langsung) kepadanya, berkatalah Musa: "Ya Tuhanku, nampakkanlah (diri Engkau) kepadaku agar aku dapat melihat kepada Engkau". Tuhan berfirman: "Kamu sekali-kali tidak sanggup melihat-Ku, tapi lihatlah ke bukit itu, maka jika ia tetap di tempatnya (sebagai sediakala) niscaya kamu dapat melihat-Ku". Tatkala Tuhannya menampakkan diri kepada gunung itu, dijadikannya gunung itu hancur luluh dan Musa pun jatuh pingsan. Maka setelah Musa sadar kembali, dia berkata: "Maha Suci Engkau, aku bertaubat kepada Engkau dan aku orang yang pertama-tama beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,144,'قَالَ يٰمُوْسٰٓى اِنِّى اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسٰلٰتِيْ وَبِكَلَامِيْ ۖفَخُذْ مَآ اٰتَيْتُكَ وَكُنْ مِّنَ الشّٰكِرِيْنَ','Allah berfirman: "Hai Musa, sesungguhnya Aku memilih (melebihkan) kamu dan manusia yang lain (di masamu) untuk membawa risalah-Ku dan untuk berbicara langsung dengan-Ku, sebab itu berpegang teguhlah kepada apa yang Aku berikan kepadamu dan hendaklah kamu termasuk orang-orang yang bersyukur".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,145,'وَكَتَبْنَا لَهٗ فِى الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَّوْعِظَةً وَّتَفْصِيْلًا لِّكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَّأْمُرْ قَوْمَكَ يَأْخُذُوْا بِاَحْسَنِهَا ۗسَاُورِيْكُمْ دَارَ الْفٰسِقِيْنَ','Dan telah Kami tuliskan untuk Musa pada luh-luh (Taurat) segala sesuatu sebagai pelajaran dan penjelasan bagi segala sesuatu; maka (Kami berfirman): "Berpeganglah kepadanya dengan teguh dan suruhlah kaummu berpegang kepada (perintah-perintahnya) dengan sebaik-baiknya, nanti Aku akan memperlihatkan kepadamu negeri orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,146,'سَاَصْرِفُ عَنْ اٰيٰتِيَ الَّذِيْنَ يَتَكَبَّرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ وَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَاۚ وَاِنْ يَّرَوْا سَبِيْلَ الرُّشْدِ لَا يَتَّخِذُوْهُ سَبِيْلًاۚ وَاِنْ يَّرَوْا سَبِيْلَ الْغَيِّ يَتَّخِذُوْهُ سَبِيْلًاۗ ذٰلِكَ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ','Aku akan memalingkan orang-orang yang menyombongkan dirinya di muka bumi tanpa alasan yang benar dari tanda-tanda kekuasaan-Ku. Mereka jika melihat tiap-tiap ayat(Ku), mereka tidak beriman kepadanya. Dan jika mereka melihat jalan yang membawa kepada petunjuk, mereka tidak mau menempuhnya, tetapi jika mereka melihat jalan kesesatan, mereka terus memenempuhnya. Yang demikian itu adalah karena mereka mendustakan ayat-ayat Kami dan mereka selalu lalai dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,147,'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ࣖ','Dan orang-orang yang mendustakan ayat-ayat Kami dan mendustakan akan menemui akhirat, sia-sialah perbuatan mereka. Mereka tidak diberi balasan selain dari apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,148,'وَاتَّخَذَ قَوْمُ مُوْسٰى مِنْۢ بَعْدِهٖ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌۗ  اَلَمْ يَرَوْا اَنَّهٗ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيْهِمْ سَبِيْلًاۘ اِتَّخَذُوْهُ وَكَانُوْا ظٰلِمِيْنَ','Dan kaum Musa, setelah kepergian Musa ke gunung Thur membuat dari perhiasan-perhiasan (emas) mereka anak lembu yang bertubuh dan bersuara. Apakah mereka tidak mengetahui bahwa anak lembu itu tidak dapat berbicara dengan mereka dan tidak dapat (pula) menunjukkan jalan kepada mereka? Mereka menjadikannya (sebagai sembahan) dan mereka adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,149,'وَلَمَّا سُقِطَ فِيْٓ اَيْدِيْهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّوْاۙ قَالُوْا لَىِٕنْ لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ','Dan setelah mereka sangat menyesali perbuatannya dan mengetahui bahwa mereka telah sesat, merekapun berkata: "Sungguh jika Tuhan kami tidak memberi rahmat kepada kami dan tidak mengampuni kami, pastilah kami menjadi orang-orang yang merugi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,150,'وَلَمَّا رَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًاۙ قَالَ بِئْسَمَا خَلَفْتُمُوْنِيْ مِنْۢ بَعْدِيْۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخِيْهِ يَجُرُّهٗٓ اِلَيْهِ ۗقَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُوْنِيْ وَكَادُوْا يَقْتُلُوْنَنِيْۖ فَلَا تُشْمِتْ بِيَ الْاَعْدَاۤءَ وَلَا تَجْعَلْنِيْ مَعَ الْقَوْمِ الظّٰلِمِيْنَ','Dan tatkala Musa telah kembali kepada kaumnya dengan marah dan sedih hati berkatalah dia: "Alangkah buruknya perbuatan yang kamu kerjakan sesudah kepergianku! Apakah kamu hendak mendahului janji Tuhanmu? Dan Musapun melemparkan luh-luh (Taurat) itu dan memegang (rambut) kepala saudaranya (Harun) sambil menariknya ke arahnya, Harun berkata: "Hai anak ibuku, sesungguhnya kaum ini telah menganggapku lemah dan hampir-hampir mereka membunuhku, sebab itu janganlah kamu menjadikan musuh-musuh gembira melihatku, dan janganlah kamu masukkan aku ke dalam golongan orang-orang yang zalim"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,151,'قَالَ رَبِّ اغْفِرْ لِيْ وَلِاَخِيْ وَاَدْخِلْنَا فِيْ رَحْمَتِكَ ۖوَاَنْتَ اَرْحَمُ الرّٰحِمِيْنَ ࣖ ','Musa berdoa: "Ya Tuhanku, ampunilah aku dan saudaraku dan masukkanlah kami ke dalam rahmat Engkau, dan Engkau adalah Maha Penyayang di antara para penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,152,'اِنَّ الَّذِيْنَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِّنْ رَّبِّهِمْ وَذِلَّةٌ فِى الْحَيٰوةِ الدُّنْيَاۗ وَكَذٰلِكَ نَجْزِى الْمُفْتَرِيْنَ','Sesungguhnya orang-orang yang menjadikan anak lembu (sebagai sembahannya), kelak akan menimpa mereka kemurkaan dari Tuhan mereka dan kehinaan dalam kehidupan di dunia. Demikianlah Kami memberi balasan kepada orang-orang yang membuat-buat kebohongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,153,'وَالَّذِيْنَ عَمِلُوا السَّيِّاٰتِ ثُمَّ تَابُوْا مِنْۢ بَعْدِهَا وَاٰمَنُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ','Orang-orang yang mengerjakan kejahatan, kemudian bertaubat sesudah itu dan beriman; sesungguhnya Tuhan kamu sesudah taubat yang disertai dengan iman itu adalah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,154,'وَلَمَّا سَكَتَ عَنْ مُّوْسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۖ وَفِيْ نُسْخَتِهَا هُدًى وَّرَحْمَةٌ لِّلَّذِيْنَ هُمْ لِرَبِّهِمْ يَرْهَبُوْنَ','Sesudah amarah Musa menjadi reda, lalu diambilnya (kembali) luh-luh (Taurat) itu; dan dalam tulisannya terdapat petunjuk dan rahmat untuk orang-orang yang takut kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,155,'وَاخْتَارَ مُوْسٰى قَوْمَهٗ سَبْعِيْنَ رَجُلًا لِّمِيْقَاتِنَا ۚفَلَمَّآ اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِّنْ قَبْلُ وَاِيَّايَۗ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاۤءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۗ تُضِلُّ بِهَا مَنْ تَشَاۤءُ وَتَهْدِيْ مَنْ تَشَاۤءُۗ اَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الْغَافِرِيْنَ','Dan Musa memilih tujuh puluh orang dari kaumnya untuk (memohonkan taubat kepada Kami) pada waktu yang telah Kami tentukan. Maka ketika mereka digoncang gempa bumi, Musa berkata: "Ya Tuhanku, kalau Engkau kehendaki, tentulah Engkau membinasakan mereka dan aku sebelum ini. Apakah Engkau membinasakan kami karena perbuatan orang-orang yang kurang akal di antara kami? Itu hanyalah cobaan dari Engkau, Engkau sesatkan dengan cobaan itu siapa yang Engkau kehendaki dan Engkau beri petunjuk kepada siapa yang Engkau kehendaki. Engkaulah Yang memimpin kami, maka ampunilah kami dan berilah kami rahmat dan Engkaulah Pemberi ampun yang sebaik-baiknya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,156,'۞ وَاكْتُبْ لَنَا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ اِنَّا هُدْنَآ اِلَيْكَۗ قَالَ عَذَابِيْٓ اُصِيْبُ بِهٖ مَنْ اَشَاۤءُۚ وَرَحْمَتِيْ وَسِعَتْ كُلَّ شَيْءٍۗ فَسَاَكْتُبُهَا لِلَّذِيْنَ يَتَّقُوْنَ وَيُؤْتُوْنَ الزَّكٰوةَ وَالَّذِيْنَ هُمْ بِاٰيٰتِنَا يُؤْمِنُوْنَۚ ','Dan tetapkanlah untuk kami kebajikan di dunia ini dan di akhirat; sesungguhnya kami kembali (bertaubat) kepada Engkau. Allah berfirman: "Siksa-Ku akan Kutimpakan kepada siapa yang Aku kehendaki dan rahmat-Ku meliputi segala sesuatu. Maka akan Aku tetapkan rahmat-Ku untuk orang-orang yang bertakwa, yang menunaikan zakat dan orang-orang yang beriman kepada ayat-ayat Kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,157,'اَلَّذِيْنَ يَتَّبِعُوْنَ الرَّسُوْلَ النَّبِيَّ الْاُمِّيَّ الَّذِيْ يَجِدُوْنَهٗ مَكْتُوْبًا عِنْدَهُمْ فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ يَأْمُرُهُمْ بِالْمَعْرُوْفِ وَيَنْهٰىهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبٰتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبٰۤىِٕثَ وَيَضَعُ عَنْهُمْ اِصْرَهُمْ وَالْاَغْلٰلَ الَّتِيْ كَانَتْ عَلَيْهِمْۗ فَالَّذِيْنَ اٰمَنُوْا بِهٖ وَعَزَّرُوْهُ وَنَصَرُوْهُ وَاتَّبَعُوا النُّوْرَ الَّذِيْٓ اُنْزِلَ مَعَهٗٓ  ۙاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ࣖ','(Yaitu) orang-orang yang mengikut Rasul, Nabi yang ummi yang (namanya) mereka dapati tertulis di dalam Taurat dan Injil yang ada di sisi mereka, yang menyuruh mereka mengerjakan yang ma´ruf dan melarang mereka dari mengerjakan yang mungkar dan menghalalkan bagi mereka segala yang baik dan mengharamkan bagi mereka segala yang buruk dan membuang dari mereka beban-beban dan belenggu-belenggu yang ada pada mereka. Maka orang-orang yang beriman kepadanya. memuliakannya, menolongnya dan mengikuti cahaya yang terang yang diturunkan kepadanya (Al Quran), mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,158,'قُلْ يٰٓاَيُّهَا النَّاسُ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ جَمِيْعًا ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ  لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُۖ فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهِ النَّبِيِّ الْاُمِّيِّ الَّذِيْ يُؤْمِنُ بِاللّٰهِ وَكَلِمٰتِهٖ وَاتَّبِعُوْهُ لَعَلَّكُمْ تَهْتَدُوْنَ','Katakanlah: "Hai manusia sesungguhnya aku adalah utusan Allah kepadamu semua, yaitu Allah Yang mempunyai kerajaan langit dan bumi; tidak ada Tuhan (yang berhak disembah) selain Dia, Yang menghidupkan dan mematikan, maka berimanlah kamu kepada Allah dan Rasul-Nya, Nabi yang ummi yang beriman kepada Allah dan kepada kalimat-kalimat-Nya (kitab-kitab-Nya) dan ikutilah dia, supaya kamu mendapat petunjuk".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,159,'وَمِنْ قَوْمِ مُوْسٰٓى اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ','Dan di antara kaum Musa itu terdapat suatu umat yang memberi petunjuk (kepada manusia) dengan hak dan dengan yang hak itulah mereka menjalankan keadilan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,160,'وَقَطَّعْنٰهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطًا اُمَمًاۗ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اِذِ اسْتَسْقٰىهُ قَوْمُهٗٓ اَنِ اضْرِبْ بِّعَصَاكَ الْحَجَرَۚ فَانْۢبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًاۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْۗ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوٰىۗ  كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ','Dan mereka Kami bagi menjadi dua belas suku yang masing-masingnya berjumlah besar dan Kami wahyukan kepada Musa ketika kaumnya meminta air kepadanya: "Pukullah batu itu dengan tongkatmu!". Maka memancarlah dari padanya duabelas mata air. Sesungguhnya tiap-tiap suku mengetahui tempat minum masing-masing. Dan Kami naungkan awan di atas mereka dan Kami turunkan kepada mereka manna dan salwa. (Kami berfirman): "Makanlah yang baik-baik dari apa yang telah Kami rezekikan kepadamu". Mereka tidak menganiaya Kami, tapi merekalah yang selalu menganiaya dirinya sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,161,'وَاِذْ قِيْلَ لَهُمُ اسْكُنُوْا هٰذِهِ الْقَرْيَةَ وَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ وَقُوْلُوْا حِطَّةٌ وَّادْخُلُوا الْبَابَ سُجَّدًا نَّغْفِرْ لَكُمْ خَطِيْۤـٰٔتِكُمْۗ سَنَزِيْدُ الْمُحْسِنِيْنَ','Dan (ingatlah), ketika dikatakan kepada mereka (Bani Israil): "Diamlah di negeri ini saja (Baitul Maqdis) dan makanlah dari (hasil bumi)nya di mana saja kamu kehendaki". Dan katakanlah: "Bebaskanlah kami dari dosa kami dan masukilah pintu gerbangnya sambil membungkuk, niscaya Kami ampuni kesalahan-kesalahanmu". Kelak akan Kami tambah (pahala) kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,162,'فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا مِنْهُمْ قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَظْلِمُوْنَ ࣖ ','Maka orang-orang yang zalim di antara mereka itu mengganti (perkataan itu) dengan perkataan yang tidak dikatakan kepada mereka, maka Kami timpakan kepada mereka azab dari langit disebabkan kezaliman mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,163,'وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّتِيْ كَانَتْ حَاضِرَةَ الْبَحْرِۘ اِذْ يَعْدُوْنَ فِى السَّبْتِ اِذْ تَأْتِيْهِمْ حِيْتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَّيَوْمَ لَا يَسْبِتُوْنَۙ لَا تَأْتِيْهِمْ ۛ كَذٰلِكَ ۛنَبْلُوْهُمْ بِمَا كَانُوْا يَفْسُقُوْنَ','Dan tanyakanlah kepada Bani Israil tentang negeri yang terletak di dekat laut ketika mereka melanggar aturan pada hari Sabtu, di waktu datang kepada mereka ikan-ikan (yang berada di sekitar) mereka terapung-apung di permukaan air, dan di hari-hari yang bukan Sabtu, ikan-ikan itu tidak datang kepada mereka. Demikianlah Kami mencoba mereka disebabkan mereka berlaku fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,164,'وَاِذْ قَالَتْ اُمَّةٌ مِّنْهُمْ لِمَ تَعِظُوْنَ قَوْمًاۙ ۨاللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيْدًاۗ قَالُوْا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُوْنَ','Dan (ingatlah) ketika suatu umat di antara mereka berkata: "Mengapa kamu menasehati kaum yang Allah akan membinasakan mereka atau mengazab mereka dengan azab yang amat keras?" Mereka menjawab: "Agar kami mempunyai alasan (pelepas tanggung jawab) kepada Tuhanmu, dan supaya mereka bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,165,'فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖٓ اَنْجَيْنَا الَّذِيْنَ يَنْهَوْنَ عَنِ السُّوْۤءِ وَاَخَذْنَا الَّذِيْنَ ظَلَمُوْا بِعَذَابٍۢ بَـِٔيْسٍۢ بِمَا كَانُوْا يَفْسُقُوْنَ','Maka tatkala mereka melupakan apa yang diperingatkan kepada mereka, Kami selamatkan orang-orang yang melarang dari perbuatan jahat dan Kami timpakan kepada orang-orang yang zalim siksaan yang keras, disebabkan mereka selalu berbuat fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,166,'فَلَمَّا عَتَوْا عَنْ مَّا نُهُوْا عَنْهُ قُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ','Maka tatkala mereka bersikap sombong terhadap apa yang dilarang mereka mengerjakannya, Kami katakan kepadanya: "Jadilah kamu kera yang hina.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,167,'وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَّسُوْمُهُمْ سُوْۤءَ الْعَذَابِۗ اِنَّ رَبَّكَ لَسَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ','Dan (ingatlah), ketika Tuhanmu memberitahukan, bahwa sesungguhnya Dia akan mengirim kepada mereka (orang-orang Yahudi) sampai hari kiamat orang-orang yang akan menimpakan kepada mereka azab yang seburuk-buruknya. Sesungguhnya Tuhanmu amat cepat siksa-Nya, dan sesungguhnya Dia adalah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,168,'وَقَطَّعْنٰهُمْ فِى الْاَرْضِ اُمَمًاۚ مِنْهُمُ الصّٰلِحُوْنَ وَمِنْهُمْ دُوْنَ ذٰلِكَ ۖوَبَلَوْنٰهُمْ بِالْحَسَنٰتِ وَالسَّيِّاٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ','Dan Kami bagi-bagi mereka di dunia ini menjadi beberapa golongan; di antaranya ada orang-orang yang saleh dan di antaranya ada yang tidak demikian. Dan Kami coba mereka dengan (nikmat) yang baik-baik dan (bencana) yang buruk-buruk, agar mereka kembali (kepada kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,169,'فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ وَّرِثُوا الْكِتٰبَ يَأْخُذُوْنَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُوْلُوْنَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَّأْتِهِمْ عَرَضٌ مِّثْلُهٗ يَأْخُذُوْهُۗ  اَلَمْ يُؤْخَذْ عَلَيْهِمْ مِّيْثَاقُ الْكِتٰبِ اَنْ لَّا يَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّ وَدَرَسُوْا مَا فِيْهِۗ وَالدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ','Maka datanglah sesudah mereka generasi (yang jahat) yang mewarisi Taurat, yang mengambil harta benda dunia yang rendah ini, dan berkata: "Kami akan diberi ampun". Dan kelak jika datang kepada mereka harta benda dunia sebanyak itu (pula), niscaya mereka akan mengambilnya (juga). Bukankah perjanjian Taurat sudah diambil dari mereka, yaitu bahwa mereka tidak akan mengatakan terhadap Allah kecuali yang benar, padahal mereka telah mempelajari apa yang tersebut di dalamnya?. Dan kampung akhirat itu lebih bagi mereka yang bertakwa. Maka apakah kamu sekalian tidak mengerti?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,170,'وَالَّذِيْنَ يُمَسِّكُوْنَ بِالْكِتٰبِ وَاَقَامُوا الصَّلٰوةَۗ اِنَّا لَا نُضِيْعُ اَجْرَ الْمُصْلِحِيْنَ','Dan orang-orang yang berpegang teguh dengan Al Kitab (Taurat) serta mendirikan shalat, (akan diberi pahala) karena sesungguhnya Kami tidak menyia-nyiakan pahala orang-orang yang mengadakan perbaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,171,'۞ وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهٗ ظُلَّةٌ وَّظَنُّوْٓا اَنَّهٗ وَاقِعٌۢ بِهِمْۚ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ ࣖ ','Dan (ingatlah), ketika Kami mengangkat bukit ke atas mereka seakan-akan bukit itu naungan awan dan mereka yakin bahwa bukit itu akan jatuh menimpa mereka. (Dan Kami katakan kepada mereka): "Peganglah dengan teguh apa yang telah Kami berikan kepadamu, serta ingatlah selalu (amalkanlah) apa yang tersebut di dalamnya supaya kamu menjadi orang-orang yang bertakwa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,172,'وَاِذْ اَخَذَ رَبُّكَ مِنْۢ بَنِيْٓ اٰدَمَ مِنْ ظُهُوْرِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ  اَلَسْتُ بِرَبِّكُمْۗ قَالُوْا بَلٰىۛ شَهِدْنَا ۛاَنْ تَقُوْلُوْا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غٰفِلِيْنَۙ ','Dan (ingatlah), ketika Tuhanmu mengeluarkan keturunan anak-anak Adam dari sulbi mereka dan Allah mengambil kesaksian terhadap jiwa mereka (seraya berfirman): "Bukankah Aku ini Tuhanmu?" Mereka menjawab: "Betul (Engkau Tuhan kami), kami menjadi saksi". (Kami lakukan yang demikian itu) agar di hari kiamat kamu tidak mengatakan: "Sesungguhnya kami (bani Adam) adalah orang-orang yang lengah terhadap ini (keesaan Tuhan)",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,173,'اَوْ تَقُوْلُوْٓا اِنَّمَآ اَشْرَكَ اٰبَاۤؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِّنْۢ بَعْدِهِمْۚ  اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُوْنَ','atau agar kamu tidak mengatakan: "Sesungguhnya orang-orang tua kami telah mempersekutukan Tuhan sejak dahulu, sedang kami ini adalah anak-anak keturunan yang (datang) sesudah mereka. Maka apakah Engkau akan membinasakan kami karena perbuatan orang-orang yang sesat dahulu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,174,'وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلَعَلَّهُمْ يَرْجِعُوْنَ','Dan demikianlah Kami menjelaskan ayat-ayat itu, agar mereka kembali (kepada kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,175,'وَاتْلُ عَلَيْهِمْ نَبَاَ الَّذِيْٓ اٰتَيْنٰهُ اٰيٰتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطٰنُ فَكَانَ مِنَ الْغٰوِيْنَ','Dan bacakanlah kepada mereka berita orang yang telah Kami berikan kepadanya ayat-ayat Kami (pengetahuan tentang isi Al Kitab), kemudian dia melepaskan diri dari pada ayat-ayat itu, lalu dia diikuti oleh syaitan (sampai dia tergoda), maka jadilah dia termasuk orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,176,'وَلَوْ شِئْنَا لَرَفَعْنٰهُ بِهَا وَلٰكِنَّهٗٓ اَخْلَدَ اِلَى الْاَرْضِ وَاتَّبَعَ هَوٰىهُۚ فَمَثَلُهٗ كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۗ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُوْنَ','Dan kalau Kami menghendaki, sesungguhnya Kami tinggikan (derajat)nya dengan ayat-ayat itu, tetapi dia cenderung kepada dunia dan menurutkan hawa nafsunya yang rendah, maka perumpamaannya seperti anjing jika kamu menghalaunya diulurkannya lidahnya dan jika kamu membiarkannya dia mengulurkan lidahnya (juga). Demikian itulah perumpamaan orang-orang yang mendustakan ayat-ayat Kami. Maka ceritakanlah (kepada mereka) kisah-kisah itu agar mereka berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,177,'سَاۤءَ مَثَلًا ۨالْقَوْمُ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاَنْفُسَهُمْ كَانُوْا يَظْلِمُوْنَ','Amat buruklah perumpamaan orang-orang yang mendustakan ayat-ayat Kami dan kepada diri mereka sendirilah mereka berbuat zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,178,'مَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِيْۚ وَمَنْ يُّضْلِلْ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ','Barangsiapa yang diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa yang disesatkan Allah, maka merekalah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,179,'وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيْرًا مِّنَ الْجِنِّ وَالْاِنْسِۖ  لَهُمْ قُلُوْبٌ لَّا يَفْقَهُوْنَ بِهَاۖ وَلَهُمْ اَعْيُنٌ لَّا يُبْصِرُوْنَ بِهَاۖ وَلَهُمْ اٰذَانٌ لَّا يَسْمَعُوْنَ بِهَاۗ اُولٰۤىِٕكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ  ۗ اُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ','Dan sesungguhnya Kami jadikan untuk (isi neraka Jahannam) kebanyakan dari jin dan manusia, mereka mempunyai hati, tetapi tidak dipergunakannya untuk memahami (ayat-ayat Allah) dan mereka mempunyai mata (tetapi) tidak dipergunakannya untuk melihat (tanda-tanda kekuasaan Allah), dan mereka mempunyai telinga (tetapi) tidak dipergunakannya untuk mendengar (ayat-ayat Allah). Mereka itu sebagai binatang ternak, bahkan mereka lebih sesat lagi. Mereka itulah orang-orang yang lalai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,180,'وَلِلّٰهِ الْاَسْمَاۤءُ الْحُسْنٰى فَادْعُوْهُ بِهَاۖ وَذَرُوا الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اَسْمَاۤىِٕهٖۗ سَيُجْزَوْنَ مَا كَانُوْا يَعْمَلُوْنَ ۖ','Hanya milik Allah asmaa-ul husna, maka bermohonlah kepada-Nya dengan menyebut asmaa-ul husna itu dan tinggalkanlah orang-orang yang menyimpang dari kebenaran dalam (menyebut) nama-nama-Nya. Nanti mereka akan mendapat balasan terhadap apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,181,'وَمِمَّنْ خَلَقْنَآ اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ ࣖ ','Dan di antara orang-orang yang Kami ciptakan ada umat yang memberi petunjuk dengan hak, dan dengan yang hak itu (pula) mereka menjalankan keadilan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,182,'وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَ','Dan orang-orang yang mendustakan ayat-ayat Kami, nanti Kami akan menarik mereka dengan berangaur-angsur (ke arah kebinasaan), dengan cara yang tidak mereka ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,183,'وَاُمْلِيْ لَهُمْ ۗاِنَّ كَيْدِيْ مَتِيْنٌ','Dan Aku memberi tangguh kepada mereka. Sesungguhnya rencana-Ku amat teguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,184,'اَوَلَمْ يَتَفَكَّرُوْا مَا بِصَاحِبِهِمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ مُّبِيْنٌ','Apakah (mereka lalai) dan tidak memikirkan bahwa teman mereka (Muhammad) tidak berpenyakit gila. Dia (Muhammad itu) tidak lain hanyalah seorang pemberi peringatan lagi pemberi penjelasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,185,'اَوَلَمْ يَنْظُرُوْا فِيْ مَلَكُوْتِ السَّمٰوٰتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ وَّاَنْ عَسٰٓى اَنْ يَّكُوْنَ قَدِ اقْتَرَبَ اَجَلُهُمْۖ فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ','Dan apakah mereka tidak memperhatikan kerajaan langit dan bumi dan segala sesuatu yang diciptakan Allah, dan kemungkinan telah dekatnya kebinasaan mereka? Maka kepada berita manakah lagi mereka akan beriman sesudah Al Quran itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,186,'مَنْ يُّضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهٗ  ۖوَيَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ','Barangsiapa yang Allah sesatkan, maka baginya tak ada orang yang akan memberi petunjuk. Dan Allah membiarkan mereka terombang-ambing dalam kesesatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,187,'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبِّيْۚ  لَا يُجَلِّيْهَا لِوَقْتِهَآ اِلَّا هُوَۘ ثَقُلَتْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ  لَا تَأْتِيْكُمْ اِلَّا بَغْتَةً ۗيَسْـَٔلُوْنَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ','Mereka menanyakan kepadamu tentang kiamat: "Bilakah terjadinya?" Katakanlah: "Sesungguhnya pengetahuan tentang kiamat itu adalah pada sisi Tuhanku; tidak seorangpun yang dapat menjelaskan waktu kedatangannya selain Dia. Kiamat itu amat berat (huru haranya bagi makhluk) yang di langit dan di bumi. Kiamat itu tidak akan datang kepadamu melainkan dengan tiba-tiba". Mereka bertanya kepadamu seakan-akan kamu benar-benar mengetahuinya. Katakanlah: "Sesungguhnya pengetahuan tentang bari kiamat itu adalah di sisi Allah, tetapi kebanyakan manusia tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,188,'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ نَفْعًا وَّلَا ضَرًّا اِلَّا مَا شَاۤءَ اللّٰهُ ۗوَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۛ وَمَا مَسَّنِيَ السُّوْۤءُ ۛاِنْ اَنَا۠ اِلَّا نَذِيْرٌ وَّبَشِيْرٌ لِّقَوْمٍ يُّؤْمِنُوْنَ ࣖ ','Katakanlah: "Aku tidak berkuasa menarik kemanfaatan bagi diriku dan tidak (pula) menolak kemudharatan kecuali yang dikehendaki Allah. Dan sekiranya aku mengetahui yang ghaib, tentulah aku membuat kebajikan sebanyak-banyaknya dan aku tidak akan ditimpa kemudharatan. Aku tidak lain hanyalah pemberi peringatan, dan pembawa berita gembira bagi orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,189,'۞ هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰىهَا حَمَلَتْ حَمْلًا خَفِيْفًا فَمَرَّتْ بِهٖ ۚفَلَمَّآ اَثْقَلَتْ دَّعَوَا اللّٰهَ رَبَّهُمَا لَىِٕنْ اٰتَيْتَنَا صَالِحًا لَّنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ','Dialah Yang menciptakan kamu dari diri yang satu dan dari padanya Dia menciptakan isterinya, agar dia merasa senang kepadanya. Maka setelah dicampurinya, isterinya itu mengandung kandungan yang ringan, dan teruslah dia merasa ringan (beberapa waktu). Kemudian tatkala dia merasa berat, keduanya (suami-isteri) bermohon kepada Allah, Tuhannya seraya berkata: "Sesungguhnya jika Engkau memberi kami anak yang saleh, tentulah kami termasuk orang-orang yang bersyukur".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,190,'فَلَمَّآ اٰتٰىهُمَا صَالِحًا جَعَلَا لَهٗ شُرَكَاۤءَ فِيْمَآ اٰتٰىهُمَا ۚفَتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ','Tatkala Allah memberi kepada keduanya seorang anak yang sempurna, maka keduanya menjadikan sekutu bagi Allah terhadap anak yang telah dianugerahkan-Nya kepada keduanya itu. Maka Maha Tinggi Allah dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,191,'اَيُشْرِكُوْنَ مَا لَا يَخْلُقُ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۖ ','Apakah mereka mempersekutukan (Allah dengan) berhada-berhala yang tak dapat menciptakan sesuatupun? Sedangkan berhala-berhala itu sendiri buatan orang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,192,'وَلَا يَسْتَطِيْعُوْنَ لَهُمْ نَصْرًا وَّلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ','Dan berhala-berhala itu tidak mampu memberi pertolongan kepada penyembah-penyembahnya dan kepada dirinya sendiripun berhala-berha]a itu tidak dapat memberi pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,193,'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوْكُمْۗ سَوَۤاءٌ عَلَيْكُمْ اَدَعَوْتُمُوْهُمْ اَمْ اَنْتُمْ صَامِتُوْنَ','Dan jika kamu (hai orang-orang musyrik) menyerunya (berhala) untuk memberi petunjuk kepadamu, tidaklah berhala-berhala itu dapat memperkenankan seruanmu; sama saja (hasilnya) buat kamu menyeru mereka ataupun kamu herdiam diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,194,'اِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوْهُمْ فَلْيَسْتَجِيْبُوْا لَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ','Sesungguhnya berhala-berhala yang kamu seru selain Allah itu adalah makhluk (yang lemah) yang serupa juga dengan kamu. Maka serulah berhala-berhala itu lalu biarkanlah mereka mmperkenankan permintaanmu, jika kamu memang orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,195,'اَلَهُمْ اَرْجُلٌ يَّمْشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَيْدٍ يَّبْطِشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَعْيُنٌ يُّبْصِرُوْنَ بِهَآ ۖ اَمْ لَهُمْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۗ قُلِ ادْعُوْا شُرَكَاۤءَكُمْ ثُمَّ كِيْدُوْنِ فَلَا تُنْظِرُوْنِ ','Apakah berhala-berhala mempunyai kaki yang dengan itu ia dapat berjalan, atau mempunyai tangan yang dengan itu ia dapat memegang dengan keras, atau mempunyai mata yang dengan itu ia dapat melihat, atau mempunyai telinga yang dengan itu ia dapat mendengar? Katakanlah: "Panggillah berhala-berhalamu yang kamu jadikan sekutu Allah, kemudian lakukanlah tipu daya (untuk mencelakakan)-ku. tanpa memberi tangguh (kepada-ku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,196,'اِنَّ وَلِيِّ َۧ اللّٰهُ الَّذِيْ نَزَّلَ الْكِتٰبَۖ وَهُوَ يَتَوَلَّى الصّٰلِحِيْنَ','Sesungguhnya pelindungku ialahlah Yang telah menurunkan Al Kitab (Al Quran) dan Dia melindungi orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,197,'وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَطِيْعُوْنَ نَصْرَكُمْ وَلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ','Dan berhala-berhala yang kamu seru selain Allah tidaklah sanggup menolongmu, bahkan tidak dapat menolong dirinya sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,198,'وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَسْمَعُوْاۗ وَتَرٰىهُمْ يَنْظُرُوْنَ اِلَيْكَ وَهُمْ لَا يُبْصِرُوْنَ','Dan jika kamu sekalian menyeru (berhala-berhala) untuk memberi petunjuk, niscaya berhala-herhala itu tidak dapat mendengarnya. Dan kamu melihat berhala-berhala itu memandang kepadamu padahal ia tidak melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,199,'خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِلِيْنَ','Jadilah engkau pemaaf dan suruhlah orang mengerjakan yang ma´ruf, serta berpalinglah dari pada orang-orang yang bodoh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,200,'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ سَمِيْعٌ عَلِيْمٌ','Dan jika kamu ditimpa sesuatu godaan syaitan maka berlindunglah kepada Allah')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,201,'اِنَّ الَّذِيْنَ اتَّقَوْا اِذَا مَسَّهُمْ طٰۤىِٕفٌ مِّنَ الشَّيْطٰنِ تَذَكَّرُوْا فَاِذَا هُمْ مُّبْصِرُوْنَۚ ','Sesungguhnya orang-orang yang bertakwa bila mereka ditimpa was-was dari syaitan, mereka ingat kepada Allah, maka ketika itu juga mereka melihat kesalahan-kesalahannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,202,'وَاِخْوَانُهُمْ يَمُدُّوْنَهُمْ فِى الْغَيِّ ثُمَّ لَا يُقْصِرُوْنَ','Dan teman-teman mereka (orang-orang kafir dan fasik) membantu syaitan-syaitan dalam menyesatkan dan mereka tidak henti-hentinya (menyesatkan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,203,'وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوْا لَوْلَا اجْتَبَيْتَهَاۗ قُلْ اِنَّمَآ اَتَّبِعُ مَا يُوْحٰٓى اِلَيَّ مِنْ رَّبِّيْۗ هٰذَا بَصَاۤىِٕرُ مِنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّؤْمِنُوْنَ','Dan apabila kamu tidak membawa suatu ayat Al Quran kepada mereka, mereka berkata: "Mengapa tidak kamu buat sendiri ayat itu?" Katakanlah: "Sesungguhnya aku hanya mengikut apa yang diwahyukan dari Tuhanku kepadaku. Al Quran ini adalah bukti-bukti yang nyata dari Tuhanmu, petunjuk dan rahmat bagi orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,204,'وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوْا لَهٗ وَاَنْصِتُوْا لَعَلَّكُمْ تُرْحَمُوْنَ','Dan apabila dibacakan Al Quran, maka dengarkanlah baik-baik, dan perhatikanlah dengan tenang agar kamu mendapat rahmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,205,'وَاذْكُرْ رَّبَّكَ فِيْ نَفْسِكَ تَضَرُّعًا وَّخِيْفَةً وَّدُوْنَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِّنَ الْغٰفِلِيْنَ','Dan sebutlah (nama) Tuhanmu dalam hatimu dengan merendahkan diri dan rasa takut, dan dengan tidak mengeraskan suara, di waktu pagi dan petang, dan janganlah kamu termasuk orang-orang yang lalai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,7,206,'اِنَّ الَّذِيْنَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَيُسَبِّحُوْنَهٗ وَلَهٗ يَسْجُدُوْنَ ࣖ ۩','Sesungguhnya malaikat-malaikat yang ada di sisi Tuhanmu tidaklah merasa enggan menyembah Allah dan mereka mentasbihkan-Nya dan hanya kepada-Nya-lah mereka bersujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,1,'يَسْـَٔلُوْنَكَ عَنِ الْاَنْفَالِۗ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُوْلِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوْا ذَاتَ بَيْنِكُمْ ۖوَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗٓ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ','Mereka menanyakan kepadamu tentang (pembagian) harta rampasan perang. Katakanlah: "Harta rampasan perang kepunyaan Allah dan Rasul, oleh sebab itu bertakwalah kepada Allah dan perbaikilah perhubungan di antara sesamamu; dan taatlah kepada Allah dan Rasul-Nya jika kamu adalah orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,2,'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيٰتُهٗ زَادَتْهُمْ اِيْمَانًا وَّعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۙ','Sesungguhnya orang-orang yang beriman ialah mereka yang bila disebut nama Allah gemetarlah hati mereka, dan apabila dibacakan ayat-ayat-Nya bertambahlah iman mereka (karenanya), dan hanya kepada Tuhanlah mereka bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,3,'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَۗ ','(yaitu) orang-orang yang mendirikan shalat dan yang menafkahkan sebagian dari rezeki yang Kami berikan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,4,'اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ  لَهُمْ دَرَجٰتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌۚ ','Itulah orang-orang yang beriman dengan sebenar-benarnya. Mereka akan memperoleh beberapa derajat ketinggian di sisi Tuhannya dan ampunan serta rezeki (nikmat) yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,5,'كَمَآ اَخْرَجَكَ رَبُّكَ مِنْۢ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ لَكٰرِهُوْنَ','Sebagaimana Tuhanmu menyuruhmu pergi dan rumahmu dengan kebenaran, padahal sesungguhnya sebagian dari orang-orang yang beriman itu tidak menyukainya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,6,'يُجَادِلُوْنَكَ فِى الْحَقِّ بَعْدَمَا تَبَيَّنَ كَاَنَّمَا يُسَاقُوْنَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُوْنَ ۗ ','mereka membantahmu tentang kebenaran sesudah nyata (bahwa mereka pasti menang), seolah-olah mereka dihalau kepada kematian, sedang mereka melihat (sebab-sebab kematian itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,7,'وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّاۤىِٕفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّوْنَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُوْنُ لَكُمْ وَيُرِيْدُ اللّٰهُ اَنْ يُّحِقَّ الْحَقَّ بِكَلِمٰتِهٖ وَيَقْطَعَ دَابِرَ الْكٰفِرِيْنَۙ ','Dan (ingatlah), ketika Allah menjanjikan kepadamu bahwa salah satu dari dua golongan (yang kamu hadapi) adalah untukmu, sedang kamu menginginkan bahwa yang tidak mempunyai kekekuatan senjatalah yang untukmu, dan Allah menghendaki untuk membenarkan yang benar dengan ayat-ayat-Nya dan memusnahkan orang-orang kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,8,'لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُوْنَۚ ','agar Allah menetapkan yang hak (Islam) dan membatalkan yang batil (syirik) walaupun orang-orang yang berdosa (musyrik) itu tidak menyukainya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,9,'اِذْ تَسْتَغِيْثُوْنَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنِّيْ مُمِدُّكُمْ بِاَلْفٍ مِّنَ الْمَلٰۤىِٕكَةِ مُرْدِفِيْنَ','(Ingatlah), ketika kamu memohon pertolongan kepada Tuhanmu, lalu diperkenankan-Nya bagimu: "Sesungguhnya Aku akan mendatangkan bala bantuan kepada kamu dengan seribu malaikat yang datang berturut-turut".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,10,'وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَىِٕنَّ بِهٖ قُلُوْبُكُمْۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ࣖ ','Dan Allah tidak menjadikannya (mengirim bala bantuan itu), melainkan sebagai kabar gembira dan agar hatimu menjadi tenteram karenanya. Dan kemenangan itu hanyalah dari sisi Allah. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,11,'اِذْ يُغَشِّيْكُمُ النُّعَاسَ اَمَنَةً مِّنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِّنَ السَّمَاۤءِ مَاۤءً لِّيُطَهِّرَكُمْ بِهٖ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطٰنِ وَلِيَرْبِطَ عَلٰى قُلُوْبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۗ ','(Ingatlah), ketika Allah menjadikan kamu mengantuk sebagai suatu penenteraman daripada-Nya, dan Allah menurunkan kepadamu hujan dari langit untuk mensucikan kamu dengan hujan itu dan menghilangkan dari kamu gangguan-gangguan syaitan dan untuk menguatkan hatimu dan mesmperteguh dengannya telapak kaki(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,12,'اِذْ يُوْحِيْ رَبُّكَ اِلَى الْمَلٰۤىِٕكَةِ اَنِّيْ مَعَكُمْ فَثَبِّتُوا الَّذِيْنَ اٰمَنُوْاۗ سَاُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ فَاضْرِبُوْا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوْا مِنْهُمْ كُلَّ بَنَانٍۗ ','(Ingatlah), ketika Tuhanmu mewahyukan kepada para malaikat: "Sesungguhnya Aku bersama kamu, maka teguhkan (pendirian) orang-orang yang telah beriman". Kelak akan Aku jatuhkan rasa ketakutan ke dalam hati orang-orang kafir, maka penggallah kepala mereka dan pancunglah tiap-tiap ujung jari mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,13,'ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗۚ وَمَنْ يُّشَاقِقِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ','(Ketentuan) yang demikian itu adalah karena sesungguhnya mereka menentang Allah dan Rasul-Nya; dan barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya Allah amat keras siksaan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,14,'ذٰلِكُمْ فَذُوْقُوْهُ وَاَنَّ لِلْكٰفِرِيْنَ عَذَابَ النَّارِ','Itulah (hukum dunia yang ditimpakan atasmu), maka rasakanlah hukuman itu. Sesungguhnya bagi orang-orang yang kafir itu ada (lagi) azab neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,15,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا زَحْفًا فَلَا تُوَلُّوْهُمُ الْاَدْبَارَۚ ','Hai orang-orang yang beriman, apabila kamu bertemu dengan orang-orang yang kafir yang sedang menyerangmu, maka janganlah kamu membelakangi mereka (mundur).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,16,'وَمَنْ يُّوَلِّهِمْ يَوْمَىِٕذٍ دُبُرَهٗٓ اِلَّا مُتَحَرِّفًا لِّقِتَالٍ اَوْ مُتَحَيِّزًا اِلٰى فِئَةٍ فَقَدْ بَاۤءَ بِغَضَبٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ','Barangsiapa yang membelakangi mereka (mundur) di waktu itu, kecuali berbelok untuk (sisat) perang atau hendak menggabungkan diri dengan pasukan yang lain, maka sesungguhnya orang itu kembali dengan membawa kemurkaan dari Allah, dan tempatnya ialah neraka Jahannam. Dan amat buruklah tempat kembalinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,17,'فَلَمْ تَقْتُلُوْهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِنِيْنَ مِنْهُ بَلَاۤءً حَسَنًاۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ','Maka (yang sebenarnya) bukan kamu yang membunuh mereka, akan tetapi Allahlah yang membunuh mereka, dan bukan kamu yang melempar ketika kamu melempar, tetapi Allah-lah yang melempar. (Allah berbuat demikian untuk membinasakan mereka) dan untuk memberi kemenangan kepada orang-orang mukmin, dengan kemenangan yang baik. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,18,'ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوْهِنُ كَيْدِ الْكٰفِرِيْنَ','Itulah (karunia Allah yang dilimpahkan kepadamu), dan sesungguhnya Allah melemahkan tipu daya orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,19,'اِنْ تَسْتَفْتِحُوْا فَقَدْ جَاۤءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوْا فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَعُوْدُوْا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـًٔا وَّلَوْ كَثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِنِيْنَ ࣖ ','Jika kamu (orang-orang musyrikin) mencari keputusan, maka telah datang keputusan kepadamu; dan jika kamu berhenti; maka itulah yang lehih baik bagimu; dan jika kamu kembali, niscaya Kami kembali (pula); dan angkatan perangmu sekali-kali tidak akan dapat menolak dari kamu sesuatu bahayapun, biarpun dia banyak dan sesungguhnya Allah beserta orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,20,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُوْنَ','Hai orang-orang yang beriman, taatlah kepada Allah dan Rasul-Nya, dan janganlah kamu berpaling dari pada-Nya, sedang kamu mendengar (perintah-perintah-Nya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,21,'وَلَا تَكُوْنُوْا كَالَّذِيْنَ قَالُوْا سَمِعْنَا وَهُمْ لَا يَسْمَعُوْنَۚ ','dan janganlah kamu menjadi seperti orang-orang (munafik) vang berkata "Kami mendengarkan, padahal mereka tidak mendengarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,22,'۞ اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذِيْنَ لَا يَعْقِلُوْنَ','Sesungguhnya binatang (makhluk) yang seburuk-buruknya pada sisi Allah ialah; orang-orang yang pekak dan tuli yang tidak mengerti apa-apapun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,23,'وَلَوْ عَلِمَ اللّٰهُ فِيْهِمْ خَيْرًا لَّاَسْمَعَهُمْۗ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ','Kalau sekiranya Allah mengetahui kebaikan ada pada mereka, tentulah Allah menjadikan mereka dapat mendengar. Dan jikalau Allah menjadikan mereka dapat mendengar, niscaya mereka pasti berpaling juga, sedang mereka memalingkan diri (dari apa yang mereka dengar itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,24,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَجِيْبُوْا لِلّٰهِ وَلِلرَّسُوْلِ اِذَا دَعَاكُمْ لِمَا يُحْيِيْكُمْۚ وَاعْلَمُوْٓا اَنَّ اللّٰهَ يَحُوْلُ بَيْنَ الْمَرْءِ وَقَلْبِهٖ وَاَنَّهٗٓ اِلَيْهِ تُحْشَرُوْنَ','Hai orang-orang yang beriman, penuhilah seruan Allah dan seruan Rasul apabila Rasul menyeru kamu kepada suatu yang memberi kehidupan kepada kamu, ketahuilah bahwa sesungguhnya Allah membatasi antara manusia dan hatinya dan sesungguhnya kepada-Nya-lah kamu akan dikumpulkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,25,'وَاتَّقُوْا فِتْنَةً لَّا تُصِيْبَنَّ الَّذِيْنَ ظَلَمُوْا مِنْكُمْ خَاۤصَّةً ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ','Dan peliharalah dirimu dari pada siksaan yang tidak khusus menimpa orang-orang yang zalim saja di antara kamu. Dan ketahuilah bahwa Allah amat keras siksaan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,26,'وَاذْكُرُوْٓا اِذْ اَنْتُمْ قَلِيْلٌ مُّسْتَضْعَفُوْنَ فِى الْاَرْضِ تَخَافُوْنَ اَنْ يَّتَخَطَّفَكُمُ النَّاسُ فَاٰوٰىكُمْ وَاَيَّدَكُمْ بِنَصْرِهٖ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ لَعَلَّكُمْ تَشْكُرُوْنَ','Dan ingatlah (hai para muhajirin) ketika kamu masih berjumlah sedikit, lagi tertindas di muka bumi (Mekah), kamu takut orang-orang (Mekah) akan menculik kamu, maka Allah memberi kamu tempat menetap (Madinah) dan dijadikan-Nya kamu kuat dengan pertolongan-Nya dan diberi-Nya kamu rezeki dari yang baik-baik agar kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,27,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَخُوْنُوا اللّٰهَ وَالرَّسُوْلَ وَتَخُوْنُوْٓا اَمٰنٰتِكُمْ وَاَنْتُمْ تَعْلَمُوْنَ','Hai orang-orang yang beriman, janganlah kamu mengkhianati Allah dan Rasul (Muhammad) dan (juga) janganlah kamu mengkhianati amanat-amanat yang dipercayakan kepadamu, sedang kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,28,'وَاعْلَمُوْٓا اَنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ  ۙوَّاَنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ࣖ','Dan ketahuilah, bahwa hartamu dan anak-anakmu itu hanyalah sebagai cobaan dan sesungguhnya di sisi Allah-lah pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,29,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَّكُمْ فُرْقَانًا وَّيُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ','Hai orang-orang beriman, jika kamu bertakwa kepada Allah, Kami akan memberikan kepadamu Furqaan. Dan kami akan jauhkan dirimu dari kesalahan-kesalahanmu, dan mengampuni (dosa-dosa)mu. Dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,30,'وَاِذْ يَمْكُرُ بِكَ الَّذِيْنَ كَفَرُوْا لِيُثْبِتُوْكَ اَوْ يَقْتُلُوْكَ اَوْ يُخْرِجُوْكَۗ وَيَمْكُرُوْنَ وَيَمْكُرُ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ','Dan (ingatlah), ketika orang-orang kafir (Quraisy) memikirkan daya upaya terhadapmu untuk menangkap dan memenjarakanmu atau membunuhmu, atau mengusirmu. Mereka memikirkan tipu daya dan Allah menggagalkan tipu daya itu. Dan Allah sebaik-baik Pembalas tipu daya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,31,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا قَالُوْا قَدْ سَمِعْنَا لَوْ نَشَاۤءُ لَقُلْنَا مِثْلَ هٰذَآ  ۙاِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ','Dan apabila dibacakan kepada mereka ayat-ayat Kami, mereka berkata: "Sesungguhnya kami telah mendengar (ayat-ayat yang seperti ini), kalau kami menhendaki niscaya kami dapat membacakan yang seperti ini, (Al Quran) ini tidak lain hanyalah dongeng-dongengan orang-orang purbakala".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,32,'وَاِذْ قَالُوا اللهم  اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِّنَ السَّمَاۤءِ اَوِ ائْتِنَا بِعَذَابٍ اَلِيْمٍ','Dan (ingatlah), ketika mereka (orang-orang musyrik) berkata: "Ya Allah, jika betul (Al Quran) ini, dialah yang benar dari sisi Engkau, maka hujanilah kami dengan batu dari langit, atau datangkanlah kepada kami azab yang pedih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,33,'وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ فِيْهِمْۚ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُوْنَ ','Dan Allah sekali-kali tidak akan mengazab mereka, sedang kamu berada di antara mereka. Dan tidaklah (pula) Allah akan mengazab mereka, sedang mereka meminta ampun')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,34,'وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّوْنَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوْٓا اَوْلِيَاۤءَهٗۗ اِنْ اَوْلِيَاۤؤُهٗٓ اِلَّا الْمُتَّقُوْنَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Kenapa Allah tidak mengazab mereka padahal mereka menghalangi orang untuk (mendatangi) Masjidilharam, dan mereka bukanlah orang-orang yang berhak menguasainya? Orang-orang yang berhak menguasai(nya) hanyalah orang-orang yang bertakwa. tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,35,'وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَاۤءً وَّتَصْدِيَةًۗ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ','Sembahyang mereka di sekitar Baitullah itu, lain tidak hanyalah siulan dan tepukan tangan. Maka rasakanlah azab disebabkan kekafiranmu itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,36,'اِنَّ الَّذِيْنَ كَفَرُوْا يُنْفِقُوْنَ اَمْوَالَهُمْ لِيَصُدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗفَسَيُنْفِقُوْنَهَا ثُمَّ تَكُوْنُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُوْنَ ەۗ وَالَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ يُحْشَرُوْنَۙ','Sesungguhnya orang-orang yang kafir menafkahkan harta mereka untuk menghalangi (orang) dari jalan Allah. Mereka akan menafkahkan harta itu, kemudian menjadi sesalan bagi mereka, dan mereka akan dikalahkan. Dan ke dalam Jahannamlah orang-orang yang kafir itu dikumpulkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,37,'لِيَمِيْزَ اللّٰهُ الْخَبِيْثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيْثَ بَعْضَهٗ عَلٰى بَعْضٍ فَيَرْكُمَهٗ جَمِيْعًا فَيَجْعَلَهٗ فِيْ جَهَنَّمَۗ  اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ࣖ ','supaya Allah memisahkan (golongan) yang buruk dari yang baik dan menjadikan (golongan) yang buruk itu sebagiannya di atas sebagian yang lain, lalu kesemuanya ditumpukkan-Nya, dan dimasukkan-Nya ke dalam neraka Jahannam. Mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,38,'قُلْ لِّلَّذِيْنَ كَفَرُوْٓا اِنْ يَّنْتَهُوْا يُغْفَرْ لَهُمْ مَّا قَدْ سَلَفَۚ وَاِنْ يَّعُوْدُوْا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّلِيْنَ','Katakanlah kepada orang-orang yang kafir itu: "Jika mereka berhenti (dari kekafirannya), niscaya Allah akan mengampuni mereka tentang dosa-dosa mereka yang sudah lalu; dan jika mereka kembali lagi sesungguhnya akan berlaku (kepada mereka) sunnah (Allah tenhadap) orang-orang dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,39,'وَقَاتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ كُلُّهٗ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ بَصِيْرٌ','Dan perangilah mereka, supaya jangan ada fitnah dan supaya agama itu semata-mata untuk Allah. Jika mereka berhenti (dari kekafiran), maka sesungguhnya Allah Maha Melihat apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,9,8,40,'وَاِنْ تَوَلَّوْا فَاعْلَمُوْٓا اَنَّ اللّٰهَ مَوْلٰىكُمْ ۗنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ  ۔','Dan jika mereka berpaling, maka ketahuilah bahwasanya Allah Pelindungmu. Dia adalah sebaik-baik Pelindung dan sebaik-baik Penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,41,'۞ وَاعْلَمُوْٓا اَنَّمَا غَنِمْتُمْ مِّنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهٗ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَآ اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعٰنِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Ketahuilah, sesungguhnya apa saja yang dapat kamu peroleh sebagai rampasan perang, maka sesungguhnya seperlima untuk Allah, Rasul, kerabat Rasul, anak-anak yatim, orang-orang miskin dan ibnussabil, jika kamu beriman kepada Allah dan kepada apa yang kami turunkan kepada hamba Kami (Muhammad) di hari Furqaan, yaitu di hari bertemunya dua pasukan. Dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,42,'اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۗ وَلَوْ تَوَاعَدْتُّمْ لَاخْتَلَفْتُمْ فِى الْمِيْعٰدِۙ وَلٰكِنْ لِّيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ەۙ لِّيَهْلِكَ مَنْ هَلَكَ عَنْۢ بَيِّنَةٍ وَّيَحْيٰى مَنْ حَيَّ عَنْۢ بَيِّنَةٍۗ وَاِنَّ اللّٰهَ لَسَمِيْعٌ عَلِيْمٌۙ','(Yaitu di hari) ketika kamu berada di pinggir lembah yang dekat dan mereka berada di pinggir lembah yang jauh sedang kafilah itu berada di bawah kamu. Sekiranya kamu mengadakan persetujuan (untuk menentukan hari pertempuran), pastilah kamu tidak sependapat dalam menentukan hari pertempuran itu, akan tetapi (Allah mempertemukan dua pasukan itu) agar Dia melakukan suatu urusan yang mesti dilaksanakan, yaitu agar orang yang binasa itu binasanya dengan keterangan yang nyata dan agar orang yang hidup itu hidupnya dengan keterangan yang nyata (pula). Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,43,'اِذْ يُرِيْكَهُمُ اللّٰهُ فِيْ مَنَامِكَ قَلِيْلًاۗ وَلَوْ اَرٰىكَهُمْ كَثِيْرًا لَّفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِى الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۗ اِنَّهٗ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ ','(yaitu) ketika Allah menampakkan mereka kepadamu di dalam mimpimu (berjumlah) sedikit. Dan sekiranya Allah memperlihatkan mereka kepada kamu (berjumlah) banyak tentu saja kamu menjadi gentar dan tentu saja kamu akan berbantah-bantahan dalam urusan itu, akan tetapi Allah telah menyelamatkan kamu. Sesungguhnya Allah Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,44,'وَاِذْ يُرِيْكُمُوْهُمْ اِذِ الْتَقَيْتُمْ فِيْٓ اَعْيُنِكُمْ قَلِيْلًا وَّيُقَلِّلُكُمْ فِيْٓ اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ࣖ ','Dan ketika Allah menampakkan mereka kepada kamu sekalian, ketika kamu berjumpa dengan mereka berjumlah sedikit pada penglihatan matamu dan kamu ditampakkan-Nya berjumlah sedikit pada penglihatan mata mereka, karena Allah hendak melakukan suatu urusan yang mesti dilaksanakan. Dan hanyalah kepada Allahlah dikembalikan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,45,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمْ فِئَةً فَاثْبُتُوْا وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَۚ ','Hai orang-orang yang beriman. apabila kamu memerangi pasukan (musuh), maka berteguh hatilah kamu dan sebutlah (nama) Allah sebanyak-banyaknya agar kamu beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,46,'وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَنَازَعُوْا فَتَفْشَلُوْا وَتَذْهَبَ رِيْحُكُمْ وَاصْبِرُوْاۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَۚ ','Dan taatlah kepada Allah dan Rasul-Nya dan janganlah kamu berbantah-bantahan, yang menyebabkan kamu menjadi gentar dan hilang kekuatanmu dan bersabarlah. Sesungguhnya Allah beserta orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,47,'وَلَا تَكُوْنُوْا كَالَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ بَطَرًا وَّرِئَاۤءَ النَّاسِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَاللّٰهُ بِمَايَعْمَلُوْنَ مُحِيْطٌ ','Dan janganlah kamu menjadi seperti orang-orang yang keluar dari kampungnya dengan rasa angkuh dan dengan maksud riya´ kepada manusia serta menghalangi (orang) dari jalan Allah. Dan (ilmu) Allah meliputi apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,48,'وَاِذْ زَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنِّيْ جَارٌ لَّكُمْۚ فَلَمَّا تَرَاۤءَتِ الْفِئَتٰنِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكُمْ اِنِّيْٓ اَرٰى مَا لَا تَرَوْنَ اِنِّيْٓ اَخَافُ اللّٰهَ ۗوَاللّٰهُ شَدِيْدُ الْعِقَابِ ࣖ ','Dan ketika syaitan menjadikan mereka memandang baik pekerjaan mereka dan mengatakan: "Tidak ada seorang manusiapun yang dapat menang terhadapmu pada hari ini, dan sesungguhnya saya ini adalah pelindungmu". Maka tatkala kedua pasukan itu telah dapat saling lihat melihat (berhadapan), syaitan itu balik ke belakang seraya berkata: "Sesungguhnya saya berlepas diri daripada kamu, sesungguhnya saya dapat melihat apa yang kamu sekalian tidak dapat melihat; sesungguhnya saya takut kepada Allah". Dan Allah sangat keras siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,49,'اِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ غَرَّ هٰٓؤُلَاۤءِ دِيْنُهُمْۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ','(Ingatlah), ketika orang-orang munafik dan orang-orang yang ada penyakit di dalam hatinya berkata: "Mereka itu (orang-orang mukmin) ditipu oleh agamanya". (Allah berfirman): "Barangsiapa yang bertawakkal kepada Allah, maka sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,50,'وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذِيْنَ كَفَرُوا الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْۚ وَذُوْقُوْا عَذَابَ الْحَرِيْقِ','Kalau kamu melihat ketika para malaikat mencabut jiwa orang-orang yang kafir seraya memukul muka dan belakang mereka (dan berkata): "Rasakanlah olehmu siksa neraka yang membakar", (tentulah kamu akan merasa ngeri).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,51,'ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۙ','Demikian itu disebabkan oleh perbuatan tanganmu sendiri. Sesungguhnya Allah sekali-kali tidak menganiaya hamba-Nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,52,'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَفَرُوْا بِاٰيٰتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْۗ اِنَّ اللّٰهَ قَوِيٌّ شَدِيْدُ الْعِقَابِ ','(keadaan mereka) serupa dengan keadaan Fir´aun dan pengikut-pengikutnya serta orang-orang yang sebelumnya. Mereka mengingkari ayat-ayat Allah, maka Allah menyiksa mereka disebabkan dosa-dosanya. Sesungguhnya Allah Maha Kuat lagi amat keras siksaan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,53,'ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّرًا نِّعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌۙ ','(Siksaan) yang demikian itu adalah karena sesungguhnya Allah sekali-kali tidak akan meubah sesuatu nikmat yang telah dianugerahkan-Nya kepada suatu kaum, hingga kaum itu meubah apa-apa yang ada pada diri mereka sendiri, dan sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,54,'كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۚ  كَذَّبُوْا بِاٰيٰتِ رَبِّهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوْا ظٰلِمِيْنَ ','(keadaan mereka) serupa dengan keadaan Fir´aun dan pengikut-pengikutnya serta orang-orang yang sebelumnya. Mereka mendustakan ayat-ayat Tuhannya maka Kami membinasakan mereka disebabkan dosa-dosanya dan Kami tenggelamkan Fir´aun dan pengikut-pengikutnya; dan kesemuanya adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,55,'اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الَّذِيْنَ كَفَرُوْا فَهُمْ لَا يُؤْمِنُوْنَۖ ','Sesungguhnya binatang (makhluk) yang paling buruk di sisi Allah ialah orang-orang yang kafir, karena mereka itu tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,56,'الَّذِيْنَ عَاهَدْتَّ مِنْهُمْ ثُمَّ يَنْقُضُوْنَ عَهْدَهُمْ فِيْ كُلِّ مَرَّةٍ وَّهُمْ لَا يَتَّقُوْنَ ','(Yaitu) orang-orang yang kamu telah mengambil perjanjian dari mereka, sesudah itu mereka mengkhianati janjinya pada setiap kalinya, dan mereka tidak takut (akibat-akibatnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,57,'فَاِمَّا تَثْقَفَنَّهُمْ فِى الْحَرْبِ فَشَرِّدْ بِهِمْ مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُوْنَ ','Jika kamu menemui mereka dalam peperangan, maka cerai beraikanlah orang-orang yang di belakang mereka dengan (menumpas) mereka, supaya mereka mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,58,'وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْۢبِذْ اِلَيْهِمْ عَلٰى سَوَاۤءٍۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَاۤىِٕنِيْنَ ࣖ ','Dan jika kamu khawatir akan (terjadinya) pengkhianatan dari suatu golongan, maka kembalikanlah perjanjian itu kepada mereka dengan cara yang jujur. Sesungguhnya Allah tidak menyukai orang-orang yang berkhianat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,59,'وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا سَبَقُوْاۗ اِنَّهُمْ لَا يُعْجِزُوْنَ ','Dan janganlah orang-orang yang kafir itu mengira, bahwa mereka akan dapat lolos (dari kekuasaan Allah). Sesungguhnya mereka tidak dapat melemahkan (Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,60,'وَاَعِدُّوْا لَهُمْ مَّا اسْتَطَعْتُمْ مِّنْ قُوَّةٍ وَّمِنْ رِّبَاطِ الْخَيْلِ تُرْهِبُوْنَ بِهٖ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَرِيْنَ مِنْ دُوْنِهِمْۚ  لَا تَعْلَمُوْنَهُمْۚ  اَللّٰهُ يَعْلَمُهُمْۗ وَمَا تُنْفِقُوْا مِنْ شَيْءٍ فِيْ سَبِيْلِ اللّٰهِ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ ','Dan siapkanlah untuk menghadapi mereka kekuatan apa saja yang kamu sanggupi dan dari kuda-kuda yang ditambat untuk berperang (yang dengan persiapan itu) kamu menggentarkan musuh Allah dan musuhmu dan orang orang selain mereka yang kamu tidak mengetahuinya; sedang Allah mengetahuinya. Apa saja yang kamu nafkahkan pada jalan Allah niscaya akan dibalasi dengan cukup kepadamu dan kamu tidak akan dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,61,'۞ وَاِنْ جَنَحُوْا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ','Dan jika mereka condong kepada perdamaian, maka condonglah kepadanya dan bertawakkallah kepada Allah. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,62,'وَاِنْ يُّرِيْدُوْٓا اَنْ يَّخْدَعُوْكَ فَاِنَّ حَسْبَكَ اللّٰهُ ۗهُوَ الَّذِيْٓ اَيَّدَكَ بِنَصْرِهٖ وَبِالْمُؤْمِنِيْنَۙ ','Dan jika mereka bermaksud menipumu, maka sesungguhnya cukuplah Allah (menjadi pelindungmu). Dialah yang memperkuatmu dengan pertolongan-Nya dan dengan para mukmin,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,63,'وَاَلَّفَ بَيْنَ قُلُوْبِهِمْۗ  لَوْاَنْفَقْتَ مَا فِى الْاَرْضِ جَمِيْعًا مَّآ اَلَّفْتَ بَيْنَ قُلُوْبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۗ اِنَّهٗ عَزِيْزٌ حَكِيْمٌ ','dan Yang mempersatukan hati mereka (orang-orang yang beriman). Walaupun kamu membelanjakan semua (kekayaan) yang berada di bumi, niscaya kamu tidak dapat mempersatukan hati mereka, akan tetapi Allah telah mempersatukan hati mereka. Sesungguhnya Dia Maha Gagah lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,64,'يٰٓاَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ࣖ ','Hai Nabi, cukuplah Allah (menjadi Pelindung) bagimu dan bagi orang-orang mukmin yang mengikutimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,65,'يٰٓاَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِيْنَ عَلَى الْقِتَالِۗ اِنْ يَّكُنْ مِّنْكُمْ عِشْرُوْنَ صَابِرُوْنَ يَغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ يَّغْلِبُوْٓا اَلْفًا مِّنَ الَّذِيْنَ كَفَرُوْا بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ','Hai Nabi, kobarkanlah semangat para mukmin untuk berperang. Jika ada dua puluh orang yang sabar diantaramu, niscaya mereka akan dapat mengalahkan dua ratus orang musuh. Dan jika ada seratus orang yang sabar diantaramu, niscaya mereka akan dapat mengalahkan seribu dari pada orang kafir, disebabkan orang-orang kafir itu kaum yang tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,66,'اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ فِيْكُمْ ضَعْفًاۗ فَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ صَابِرَةٌ يَّغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ اَلْفٌ يَّغْلِبُوْٓا اَلْفَيْنِ بِاِذْنِ اللّٰهِ ۗوَاللّٰهُ مَعَ الصّٰبِرِيْنَ ','Sekarang Allah telah meringankan kepadamu dan dia telah mengetahui bahwa padamu ada kelemahan. Maka jika ada diantaramu seratus orang yang sabar, niscaya mereka akan dapat mengalahkan dua ratus orang kafir; dan jika diantaramu ada seribu orang (yang sabar), niscaya mereka akan dapat mengalahkan dua ribu orang, dengan seizin Allah. Dan Allah beserta orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,67,'مَاكَانَ لِنَبِيٍّ اَنْ يَّكُوْنَ لَهٗٓ اَسْرٰى حَتّٰى يُثْخِنَ فِى الْاَرْضِۗ تُرِيْدُوْنَ عَرَضَ الدُّنْيَاۖ وَاللّٰهُ يُرِيْدُ الْاٰخِرَةَۗ وَاللّٰهُ عَزِيْزٌحَكِيْمٌ ','Tidak patut, bagi seorang Nabi mempunyai tawanan sebelum ia dapat melumpuhkan musuhnya di muka bumi. Kamu menghendaki harta benda duniawiyah sedangkan Allah menghendaki (pahala) akhirat (untukmu). Dan Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,68,'لَوْلَاكِتٰبٌ مِّنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ فِيْمَآ اَخَذْتُمْ عَذَابٌ عَظِيْمٌ ','Kalau sekiranya tidak ada ketetapan yang telah terdahulu dari Allah, niscaya kamu ditimpa siksaan yang besar karena tebusan yang kamu ambil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,69,'فَكُلُوْا مِمَّاغَنِمْتُمْ حَلٰلًا طَيِّبًاۖ وَّاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Maka makanlah dari sebagian rampasan perang yang telah kamu ambil itu, sebagai makanan yang halal lagi baik, dan bertakwalah kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,70,'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّمَنْ فِيْٓ اَيْدِيْكُمْ مِّنَ الْاَسْرٰٓىۙ اِنْ يَّعْلَمِ اللّٰهُ فِيْ قُلُوْبِكُمْ خَيْرًا يُّؤْتِكُمْ خَيْرًا مِّمَّآ اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ࣖ ','Hai Nabi, katakanlah kepada tawanan-tawanan yang ada di tanganmu: "Jika Allah mengetahui ada kebaikan dalam hatimu, niscaya Dia akan memberikan kepadamu yang lebih baik dari apa yang telah diambil daripadamu dan Dia akan mengampuni kamu". Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,71,'وَاِنْ يُّرِيْدُوْا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ','Akan tetapi jika mereka (tawanan-tawanan itu) bermaksud hendak berkhianat kepadamu, maka sesungguhnya mereka telah berkhianat kepada Allah sebelum ini, lalu Allah menjadikan(mu) berkuasa terhadap mereka. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,72,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَالَّذِيْنَ اٰمَنُوْا وَلَمْ يُهَاجِرُوْا مَا لَكُمْ مِّنْ وَّلَايَتِهِمْ مِّنْ شَيْءٍ حَتّٰى يُهَاجِرُوْاۚ وَاِنِ اسْتَنْصَرُوْكُمْ فِى الدِّيْنِ فَعَلَيْكُمُ النَّصْرُ اِلَّا عَلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ','Sesungguhnya orang-orang yang beriman dan berhijrah serta berjihad dengan harta dan jiwanya pada jalan Allah dan orang-orang yang memberikan tempat kediaman dan pertoIongan (kepada orang-orang muhajirin), mereka itu satu sama lain lindung-melindungi. Dan (terhadap) orang-orang yang beriman, tetapi belum berhijrah, maka tidak ada kewajiban sedikitpun atasmu melindungi mereka, sebelum mereka berhijrah. (Akan tetapi) jika mereka meminta pertolongan kepadamu dalam (urusan pembelaan) agama, maka kamu wajib memberikan pertolongan kecuali terhadap kaum yang telah ada perjanjian antara kamu dengan mereka. Dan Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,73,'وَالَّذِيْنَ كَفَرُوْا بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ اِلَّا تَفْعَلُوْهُ تَكُنْ فِتْنَةٌ فِى الْاَرْضِ وَفَسَادٌ كَبِيْرٌۗ','Adapun orang-orang yang kafir, sebagian mereka menjadi pelindung bagi sebagian yang lain. Jika kamu (hai para muslimin) tidak melaksanakan apa yang telah diperintahkan Allah itu, niscaya akan terjadi kekacauan di muka bumi dan kerusakan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,74,'وَالَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ  لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ','Dan orang-orang yang beriman dan berhijrah serta berjihad pada jalan Allah, dan orang-orang yang memberi tempat kediaman dan memberi pertolongan (kepada orang-orang muhajirin), mereka itulah orang-orang yang benar-benar beriman. Mereka memperoleh ampunan dan rezeki (nikmat) yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,8,75,'وَالَّذِيْنَ اٰمَنُوْا مِنْۢ بَعْدُ وَهَاجَرُوْا وَجَاهَدُوْا مَعَكُمْ فَاُولٰۤىِٕكَ مِنْكُمْۗ وَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ','Dan orang-orang yang beriman sesudah itu kemudian berhijrah serta berjihad bersamamu maka orang-orang itu termasuk golonganmu (juga). Orang-orang yang mempunyai hubungan kerabat itu sebagiannya lebih berhak terhadap sesamanya (daripada yang bukan kerabat) di dalam kitab Allah. Sesungguhnya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,1,'بَرَاۤءَةٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَۗ','(Inilah pernyataan) pemutusan hubungan dari Allah dan Rasul-Nya (yang dihadapkan) kepada orang-orang musyrikin yang kamu (kaum muslimin) telah mengadakan perjanjian (dengan mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,2,'فَسِيْحُوْا فِى الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَّاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۙوَاَنَّ اللّٰهَ مُخْزِى الْكٰفِرِيْنَ ','Maka berjalanlah kamu (kaum musyrikin) di muka bumi selama empat bulan dan ketahuilah bahwa sesungguhnya kamu tidak akan dapat melemahkan Allah, dan sesungguhnya Allah menghinakan orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,3,'وَاَذَانٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَرِيْۤءٌ مِّنَ الْمُشْرِكِيْنَ ەۙ وَرَسُوْلُهٗ ۗفَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۗوَبَشِّرِ الَّذِيْنَ كَفَرُوْا بِعَذَابٍ اَلِيْمٍۙ','Dan (inilah) suatu permakluman daripada Allah dan Rasul-Nya kepada umat manusia pada hari haji akbar bahwa sesungguhnya Allah dan Rasul-Nya berlepas diri dari orang-orang musyrikin. Kemudian jika kamu (kaum musyrikin) bertobat, maka bertaubat itu lebih baik bagimu; dan jika kamu berpaling, maka ketahuilah bahwa sesungguhnya kamu tidak dapat melemahkan Allah. Dan beritakanlah kepada orang-orang kafir (bahwa mereka akan mendapat) siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,4,'اِلَّا الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَ ثُمَّ لَمْ يَنْقُصُوْكُمْ شَيْـًٔا وَّلَمْ يُظَاهِرُوْا عَلَيْكُمْ اَحَدًا فَاَتِمُّوْٓا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ','kecuali orang-orang musyrikin yang kamu telah mengadakan perjanjian (dengan mereka) dan mereka tidak mengurangi sesuatu pun (dari isi perjanjian)mu dan tidak (pula) mereka membantu seseorang yang memusuhi kamu, maka terhadap mereka itu penuhilah janjinya sampai batas waktunya. Sesungguhnya Allah menyukai orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,5,'فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِيْنَ حَيْثُ وَجَدْتُّمُوْهُمْ وَخُذُوْهُمْ وَاحْصُرُوْهُمْ وَاقْعُدُوْا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوْا سَبِيْلَهُمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','Apabila sudah habis bulan-bulan Haram itu, maka bunuhlah orang-orang musyrikin itu dimana saja kamu jumpai mereka, dan tangkaplah mereka. Kepunglah mereka dan intailah ditempat pengintaian. Jika mereka bertaubat dan mendirikan sholat dan menunaikan zakat, maka berilah kebebasan kepada mereka untuk berjalan. Sesungguhnya Allah Maha Pengampun lagi maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,6,'وَاِنْ اَحَدٌ مِّنَ الْمُشْرِكِيْنَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلٰمَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهٗ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْلَمُوْنَ ࣖ','Dan jika seorang diantara orang-orang musyrikin itu meminta perlindungan kepadamu, maka lindungilah ia supaya ia sempat mendengar firman Allah, kemudian antarkanlah ia ketempat yang aman baginya. Demikian itu disebabkan mereka kaum yang tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,7,'كَيْفَ يَكُوْنُ لِلْمُشْرِكِيْنَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُوْلِهٖٓ اِلَّا الَّذِيْنَ عَاهَدْتُّمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوْا لَكُمْ فَاسْتَقِيْمُوْا لَهُمْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ','Bagaimana bisa ada perjanjian (aman) dari sisi Allah dan Rasul-Nya dengan orang-orang musyrikin, kecuali orang-orang yang kamu telah mengadakan perjanjian (dengan mereka) di dekat Masjidil haraam? maka selama mereka berlaku lurus terhadapmu, hendaklah kamu berlaku lurus (pula) terhadap mereka. Sesungguhnya Allah menyukai orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,8,'كَيْفَ وَاِنْ يَّظْهَرُوْا عَلَيْكُمْ لَا يَرْقُبُوْا فِيْكُمْ اِلًّا وَّلَا ذِمَّةً ۗيُرْضُوْنَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوْبُهُمْۚ وَاَكْثَرُهُمْ فٰسِقُوْنَۚ','Bagaimana bisa (ada perjanjian dari sisi Allah dan Rasul-Nya dengan orang-orang musyrikin), padahal jika mereka memperoleh kemenangan terhadap kamu, mereka tidak memelihara hubungan kekerabatan terhadap kamu dan tidak (pula mengindahkan) perjanjian. Mereka menyenangkan hatimu dengan mulutnya, sedang hatinya menolak. Dan kebanyakan mereka adalah orang-orang yang fasik (tidak menepati perjanjian).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,9,'اِشْتَرَوْا بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا فَصَدُّوْا عَنْ سَبِيْلِهٖۗ اِنَّهُمْ سَاۤءَ مَاكَانُوْا يَعْمَلُوْنَ ','Mereka menukarkan ayat-ayat Allah dengan harga yang sedikit, lalu mereka menghalangi (manusia) dari jalan Allah. Sesungguhnya amat buruklah apa yang mereka kerjakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,10,'لَا يَرْقُبُوْنَ فِيْ مُؤْمِنٍ اِلًّا وَّلَا ذِمَّةً ۗوَاُولٰۤىِٕكَ هُمُ الْمُعْتَدُوْنَ ','Mereka tidak memelihara (hubungan) kerabat terhadap orang-orang mukmin dan tidak (pula mengindahkan) perjanjian. Dan mereka itulah orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,11,'فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِى الدِّيْنِ ۗوَنُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ','Jika mereka bertaubat, mendirikan sholat dan menunaikan zakat, maka (mereka itu) adalah saudara-saudaramu seagama. Dan Kami menjelaskan ayat-ayat itu bagi kaum yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,12,'وَاِنْ نَّكَثُوْٓا اَيْمَانَهُمْ مِّنْۢ بَعْدِ عَهْدِهِمْ وَطَعَنُوْا فِيْ دِيْنِكُمْ فَقَاتِلُوْٓا اَىِٕمَّةَ الْكُفْرِۙ اِنَّهُمْ لَآ اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُوْنَ ','Jika mereka merusak sumpah (janji)nya sesudah mereka berjanji, dan mereka mencerca agamamu, maka perangilah pemimpin-pemimpin orang-orang kafir itu, karena sesungguhnya mereka itu adalah orang-orang (yang tidak dapat dipegang) janjinya, agar supaya mereka berhenti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,13,'اَلَا تُقَاتِلُوْنَ قَوْمًا نَّكَثُوْٓا اَيْمَانَهُمْ وَهَمُّوْا بِاِخْرَاجِ الرَّسُوْلِ وَهُمْ بَدَءُوْكُمْ اَوَّلَ مَرَّةٍۗ  اَتَخْشَوْنَهُمْ ۚفَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','Mengapakah kamu tidak memerangi orang-orang yang merusak sumpah (janjinya), padahal mereka telah keras kemauannya untuk mengusir Rasul dan merekalah yang pertama mulai memerangi kamu? Mengapakah kamu takut kepada mereka padahal Allah-lah yang berhak untuk kamu takuti, jika kamu benar-benar orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,14,'قَاتِلُوْهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْدِيْكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُوْرَ قَوْمٍ مُّؤْمِنِيْنَۙ','Perangilah mereka, niscaya Allah akan menghancurkan mereka dengan (perantaraan) tangan-tanganmu dan Allah akan menghinakan mereka dan menolong kamu terhadap mereka, serta melegakan hati orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,15,'وَيُذْهِبْ غَيْظَ قُلُوْبِهِمْۗ وَيَتُوْبُ اللّٰهُ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','dan menghilangkan panas hati orang-orang mukmin. Dan Allah menerima taubat orang yang dikehendaki-Nya. Allah maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,16,'اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوْا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَلَمْ يَتَّخِذُوْا مِنْ دُوْنِ اللّٰهِ وَلَا رَسُوْلِهٖ وَلَا الْمُؤْمِنِيْنَ وَلِيْجَةً ۗوَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ ࣖ','Apakah kamu mengira bahwa kamu akan dibiarkan, sedang Allah belum mengetahui (dalam kenyataan) orang-orang yang berjihad di antara kamu dan tidak mengambil menjadi teman yang setia selain Allah, Rasul-Nya dan orang-orang yang beriman. Dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,17,'مَا كَانَ لِلْمُشْرِكِيْنَ اَنْ يَّعْمُرُوْا مَسٰجِدَ اللّٰهِ شٰهِدِيْنَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْۚ وَ فِى النَّارِ هُمْ خٰلِدُوْنَ ','Tidaklah pantas orang-orang musyrik itu memakmurkan mesjid-mesjid Allah, sedang mereka mengakui bahwa mereka sendiri kafir. Itulah orang-orang yang sia-sia pekerjaannya, dan mereka kekal di dalam neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,18,'اِنَّمَا يَعْمُرُ مَسٰجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ ۗفَعَسٰٓى اُولٰۤىِٕكَ اَنْ يَّكُوْنُوْا مِنَ الْمُهْتَدِيْنَ ','Hanya yang memakmurkan masjid-masjid Allah ialah orang-orang yang beriman kepada Allah dan Hari kemudian, serta tetap mendirikan shalat, menunaikan zakat dan tidak takut (kepada siapapun) selain kepada Allah, maka merekalah orang-orang yang diharapkan termasuk golongan orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,19,'۞ اَجَعَلْتُمْ سِقَايَةَ الْحَاۤجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ فِيْ سَبِيْلِ اللّٰهِ ۗ لَا يَسْتَوٗنَ عِنْدَ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۘ','Apakah (orang-orang) yang memberi minuman orang-orang yang mengerjakan haji dan mengurus Masjidilharam kamu samakan dengan orang-orang yang beriman kepada Allah dan hari kemudian serta bejihad di jalan Allah? Mereka tidak sama di sisi Allah; dan Allah tidak memberi petunjuk kepada kaum yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,20,'اَلَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِ ۗوَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ','orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah dengan harta, benda dan diri mereka, adalah lebih tinggi derajatnya di sisi Allah; dan itulah orang-orang yang mendapat kemenangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,21,'يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِّنْهُ وَرِضْوَانٍ وَّجَنّٰتٍ لَّهُمْ فِيْهَا نَعِيْمٌ مُّقِيْمٌۙ','Tuhan mereka menggembirakan mereka dengan memberikan rahmat dari pada-Nya, keridhaan dan surga, mereka memperoleh didalamnya kesenangan yang kekal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,22,'خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗاِنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ','mereka kekal di dalamnya selama-lamanya. Sesungguhnya di sisi Allah-lah pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,23,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْٓا اٰبَاۤءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَاۤءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْاِيْمَانِۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ','Hai orang-orang beriman, janganlah kamu jadikan bapa-bapa dan saudara-saudaramu menjadi wali(mu), jika mereka lebih mengutamakan kekafiran atas keimanan dan siapa di antara kamu yang menjadikan mereka wali, maka mereka itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,24,'قُلْ اِنْ كَانَ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَشِيْرَتُكُمْ وَاَمْوَالُ ِۨاقْتَرَفْتُمُوْهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسٰكِنُ تَرْضَوْنَهَآ اَحَبَّ اِلَيْكُمْ مِّنَ اللّٰهِ وَرَسُوْلِهٖ وَجِهَادٍ فِيْ سَبِيْلِهٖ فَتَرَبَّصُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ࣖ','Katakanlah: "jika bapa-bapa, anak-anak, saudara-saudara, isteri-isteri, kaum keluargamu, harta kekayaan yang kamu usahakan, perniagaan yang kamu khawatiri kerugiannya, dan tempat tinggal yang kamu sukai, adalah lebih kamu cintai dari Allah dan Rasul-Nya dan dari berjihad di jalan-Nya, maka tunggulah sampai Allah mendatangkan keputusan-Nya". Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,25,'لَقَدْ نَصَرَكُمُ اللّٰهُ فِيْ مَوَاطِنَ كَثِيْرَةٍۙ وَّيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـًٔا وَّضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُّدْبِرِيْنَۚ','Sesungguhnya Allah telah menolong kamu (hai para mukminin) di medan peperangan yang banyak, dan (ingatlah) peperangan Hunain, yaitu diwaktu kamu menjadi congkak karena banyaknya jumlah(mu), maka jumlah yang banyak itu tidak memberi manfaat kepadamu sedikitpun, dan bumi yang luas itu telah terasa sempit olehmu, kemudian kamu lari kebelakang dengan bercerai-berai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,26,'ثُمَّ اَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَنْزَلَ جُنُوْدًا لَّمْ تَرَوْهَا وَعَذَّبَ الَّذِيْنَ كَفَرُوْاۗ وَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ ','Kemudian Allah menurunkan ketenangan kepada Rasul-Nya dan kepada orang-orang yang beriman, dan Allah menurunkan bala tentara yang kamu tiada melihatnya, dan Allah menimpakan bencana kepada orang-orang yang kafir, dan demikianlah pembalasan kepada orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,27,'ثُمَّ يَتُوْبُ اللّٰهُ مِنْۢ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ','Sesudah itu Allah menerima taubat dari orang-orang yang dikehendaki-Nya. Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,28,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْمُشْرِكُوْنَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَا ۚوَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيْكُمُ اللّٰهُ مِنْ فَضْلِهٖٓ اِنْ شَاۤءَۗ اِنَّ اللّٰهَ عَلِيْمٌ حَكِيْمٌ ','Hai orang-orang yang beriman, Sesungguhnya orang-orang yang musyrik itu najis, maka janganlah mereka mendekati Masjidilharam sesudah tahun ini. Dan jika kamu khawatir menjadi miskin, maka Allah nanti akan memberimu kekayaan kepadamu dari karunia-Nya, jika Dia menghendaki. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,29,'قَاتِلُوا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُوْنَ مَا حَرَّمَ اللّٰهُ وَرَسُوْلُهٗ وَلَا يَدِيْنُوْنَ دِيْنَ الْحَقِّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَّدٍ وَّهُمْ صَاغِرُوْنَ ࣖ','Perangilah orang-orang yang tidak beriman kepada Allah dan tidak (pula) kepada hari kemudian, dan mereka tidak mengharamkan apa yang diharamkan oleh Allah dan Rasul-Nya dan tidak beragama dengan agama yang benar (agama Allah), (yaitu orang-orang) yang diberikan Al-Kitab kepada mereka, sampai mereka membayar jizyah dengan patuh sedang mereka dalam keadaan tunduk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,30,'وَقَالَتِ الْيَهُوْدُ عُزَيْرُ ِۨابْنُ اللّٰهِ وَقَالَتِ النَّصٰرَى الْمَسِيْحُ ابْنُ اللّٰهِ ۗذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِـُٔوْنَ قَوْلَ الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۗقَاتَلَهُمُ اللّٰهُ ۚ اَنّٰى يُؤْفَكُوْنَ ','Orang-orang Yahudi berkata: "Uzair itu putera Allah" dan orang-orang Nasrani berkata: "Al Masih itu putera Allah". Demikianlah itu ucapan mereka dengan mulut mereka, mereka meniru perkataan orang-orang kafir yang terdahulu. Dilaknati Allah mereka , bagaimana mereka sampai berpaling?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,31,'اِتَّخَذُوْٓا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ وَالْمَسِيْحَ ابْنَ مَرْيَمَۚ وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوْٓا اِلٰهًا وَّاحِدًاۚ  لَآ اِلٰهَ اِلَّا هُوَۗ سُبْحٰنَهٗ عَمَّا يُشْرِكُوْنَ ','Mereka menjadikan orang-orang alimnya dan rahib-rahib mereka sebagai tuhan selain Allah dan (juga mereka mempertuhankan) Al Masih putera Maryam, padahal mereka hanya disuruh menyembah Tuhan yang Esa, tidak ada Tuhan (yang berhak disembah) selain Dia. Maha suci Allah dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,32,'يُرِيْدُوْنَ اَنْ يُّطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّآ اَنْ يُّتِمَّ نُوْرَهٗ وَلَوْ كَرِهَ الْكٰفِرُوْنَ ','Mereka berkehendak memadamkan cahaya (agama) Allah dengan mulut (ucapan-ucapan) mereka, dan Allah tidak menghendaki selain menyempurnakan cahaya-Nya, walaupun orang-orang yang kafir tidak menyukai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,33,'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ ','Dialah yang telah mengutus Rasul-Nya (dengan membawa) petunjuk (Al-Quran) dan agama yang benar untuk dimenangkan-Nya atas segala agama, walaupun orang-orang musyrikin tidak menyukai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,34,'۞ يٰٓاَيُّهَا الَّذِينَ اٰمَنُوْٓا اِنَّ كَثِيْرًا مِّنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُوْنَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَالَّذِيْنَ يَكْنِزُوْنَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُوْنَهَا فِيْ سَبِيْلِ اللّٰهِ ۙفَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ','Hai orang-orang yang beriman, sesungguhnya sebahagian besar dari orang-orang alim Yahudi dan rahib-rahib Nasrani benar-benar memakan harta orang dengan jalan batil dan mereka menghalang-halangi (manusia) dari jalan Allah. Dan orang-orang yang menyimpan emas dan perak dan tidak menafkahkannya pada jalan Allah, maka beritahukanlah kepada mereka, (bahwa mereka akan mendapat) siksa yang pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,35,'يَّوْمَ يُحْمٰى عَلَيْهَا فِيْ نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوْبُهُمْ وَظُهُوْرُهُمْۗ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوْقُوْا مَا كُنْتُمْ تَكْنِزُوْنَ ','pada hari dipanaskan emas perak itu dalam neraka jahannam, lalu dibakar dengannya dahi mereka, lambung dan punggung mereka (lalu dikatakan) kepada mereka: "Inilah harta bendamu yang kamu simpan untuk dirimu sendiri, maka rasakanlah sekarang (akibat dari) apa yang kamu simpan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,36,'اِنَّ عِدَّةَ الشُّهُوْرِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْرًا فِيْ كِتٰبِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ مِنْهَآ اَرْبَعَةٌ حُرُمٌ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ ەۙ فَلَا تَظْلِمُوْا فِيْهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِكِيْنَ كَاۤفَّةً كَمَا يُقَاتِلُوْنَكُمْ كَاۤفَّةً ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ','Sesungguhnya bilangan bulan pada sisi Allah adalah dua belas bulan, dalam ketetapan Allah di waktu Dia menciptakan langit dan bumi, di antaranya empat bulan haram. Itulah (ketetapan) agama yang lurus, maka janganlah kamu menganiaya diri kamu dalam bulan yang empat itu, dan perangilah kaum musyrikin itu semuanya sebagaimana merekapun memerangi kamu semuanya, dan ketahuilah bahwasanya Allah beserta orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,37,'اِنَّمَا النَّسِيْۤءُ زِيَادَةٌ فِى الْكُفْرِ يُضَلُّ بِهِ الَّذِيْنَ كَفَرُوْا يُحِلُّوْنَهٗ عَامًا وَّيُحَرِّمُوْنَهٗ عَامًا لِّيُوَاطِـُٔوْا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوْا مَا حَرَّمَ اللّٰهُ ۗزُيِّنَ لَهُمْ سُوْۤءُ اَعْمَالِهِمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ ࣖ','Sesungguhnya mengundur-undurkan bulan haram itu adalah menambah kekafiran. Disesatkan orang-orang yang kafir dengan mengundur-undurkan itu, mereka menghalalkannya pada suatu tahun dan mengharamkannya pada tahun yang lain, agar mereka dapat mempersesuaikan dengan bilangan yang Allah mengharamkannya, maka mereka menghalalkan apa yang diharamkan Allah. (Syaitan) menjadikan mereka memandang perbuatan mereka yang buruk itu. Dan Allah tidak memberi petunjuk kepada orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,38,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَا لَكُمْ اِذَا قِيْلَ لَكُمُ انْفِرُوْا فِيْ سَبِيْلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۗ اَرَضِيْتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا قَلِيْلٌ ','Hai orang-orang yang beriman, apakah sebabnya bila dikatakan kepadamu: "Berangkatlah (untuk berperang) pada jalan Allah" kamu merasa berat dan ingin tinggal di tempatmu? Apakah kamu puas dengan kehidupan di dunia sebagai ganti kehidupan di akhirat? Padahal kenikmatan hidup di dunia ini (dibandingkan dengan kehidupan) diakhirat hanyalah sedikit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,39,'اِلَّا تَنْفِرُوْا يُعَذِّبْكُمْ عَذَابًا اَلِيمًاۙ وَّيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوْهُ شَيْـًٔاۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Jika kamu tidak berangkat untuk berperang, niscaya Allah menyiksa kamu dengan siksa yang pedih dan digantinya (kamu) dengan kaum yang lain, dan kamu tidak akan dapat memberi kemudharatan kepada-Nya sedikitpun. Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,40,'اِلَّا تَنْصُرُوْهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذِيْنَ كَفَرُوْا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِى الْغَارِ اِذْ يَقُوْلُ لِصَاحِبِهٖ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلَيْهِ وَاَيَّدَهٗ بِجُنُوْدٍ لَّمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِيْنَ كَفَرُوا السُّفْلٰىۗ وَكَلِمَةُ اللّٰهِ هِيَ الْعُلْيَاۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ','Jikalau kamu tidak menolongnya (Muhammad) maka sesungguhnya Allah telah menolongnya (yaitu) ketika orang-orang kafir (musyrikin Mekah) mengeluarkannya (dari Mekah) sedang dia salah seorang dari dua orang ketika keduanya berada dalam gua, di waktu dia berkata kepada temannya: "Janganlah kamu berduka cita, sesungguhnya Allah beserta kita". Maka Allah menurunkan keterangan-Nya kepada (Muhammad) dan membantunya dengan tentara yang kamu tidak melihatnya, dan Al-Quran menjadikan orang-orang kafir itulah yang rendah. Dan kalimat Allah itulah yang tinggi. Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,41,'اِنْفِرُوْا خِفَافًا وَّثِقَالًا وَّجَاهِدُوْا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ فِيْ سَبِيْلِ اللّٰهِ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Berangkatlah kamu baik dalam keadaan merasa ringan maupun berat, dan berjihadlah kamu dengan harta dan dirimu di jalan Allah. Yang demikian itu adalah lebih baik bagimu, jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,42,'لَوْ كَانَ عَرَضًا قَرِيْبًا وَّسَفَرًا قَاصِدًا لَّاتَّبَعُوْكَ وَلٰكِنْۢ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۗ وَسَيَحْلِفُوْنَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُوْنَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكٰذِبُوْنَ ࣖ ','Kalau yang kamu serukan kepada mereka itu keuntungan yang mudah diperoleh dan perjalanan yang tidak seberapa jauh, pastilah mereka mengikutimu, tetapi tempat yang dituju itu amat jauh terasa oleh mereka. Mereka akan bersumpah dengan (nama) Allah: "Jikalau kami sanggup tentulah kami berangkat bersama-samamu". Mereka membinasakan diri mereka sendiri dan Allah mengetahui bahwa sesungguhnya mereka benar-benar orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,43,'عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذِيْنَ صَدَقُوْا وَتَعْلَمَ الْكٰذِبِيْنَ ','Semoga Allah memaafkanmu. Mengapa kamu memberi izin kepada mereka (untuk tidak pergi berperang), sebelum jelas bagimu orang-orang yang benar (dalam keuzurannya) dan sebelum kamu ketahui orang-orang yang berdusta?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,44,'لَا يَسْتَأْذِنُكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالْمُتَّقِيْنَ ','Orang-orang yang beriman kepada Allah dan hari kemudian, tidak akan meminta izin kepadamu untuk tidak ikut berjihad dengan harta dan diri mereka. Dan Allah mengetahui orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,45,'اِنَّمَا يَسْتَأْذِنُكَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوْبُهُمْ فَهُمْ فِيْ رَيْبِهِمْ يَتَرَدَّدُوْنَ ','Sesungguhnya yang akan meminta izin kepadamu, hanyalah orang-orang yang tidak beriman kepada Allah dan hari kemudian, dan hati mereka ragu-ragu, karena itu mereka selalu bimbang dalam keraguannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,46,'۞ وَلَوْ اَرَادُوا الْخُرُوْجَ لَاَعَدُّوْا لَهٗ عُدَّةً وَّلٰكِنْ كَرِهَ اللّٰهُ انْۢبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيْلَ اقْعُدُوْا مَعَ الْقٰعِدِيْنَ ','Dan jika mereka mau berangkat, tentulah mereka menyiapkan persiapan untuk keberangkatan itu, tetapi Allah tidak menyukai keberangkatan mereka, maka Allah melemahkan keinginan mereka. dan dikatakan kepada mereka: "Tinggallah kamu bersama orang-orang yang tinggal itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,47,'لَوْ خَرَجُوْا فِيْكُمْ مَّا زَادُوْكُمْ اِلَّا خَبَالًا وَّلَاَوْضَعُوْا خِلٰلَكُمْ يَبْغُوْنَكُمُ الْفِتْنَةَۚ وَفِيْكُمْ سَمّٰعُوْنَ لَهُمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ ','Jika mereka berangkat bersama-sama kamu, niscaya mereka tidak menambah kamu selain dari kerusakan belaka, dan tentu mereka akan bergegas maju ke muka di celah-celah barisanmu, untuk mengadakan kekacauan di antara kamu; sedang di antara kamu ada orang-orang yang amat suka mendengarkan perkataan mereka. Dan Allah mengetahui orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,48,'لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوْا لَكَ الْاُمُوْرَ حَتّٰى جَاۤءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كٰرِهُوْنَ ','Sesungguhnya dari dahulupun mereka telah mencari-cari kekacauan dan mereka mengatur pelbagai macam tipu daya untuk (merusakkan)mu, hingga datanglah kebenaran (pertolongan Allah) dan menanglah agama Allah, padahal mereka tidak menyukainya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,49,'وَمِنْهُمْ مَّنْ يَّقُوْلُ ائْذَنْ لِّيْ وَلَا تَفْتِنِّيْۗ  اَلَا فِى الْفِتْنَةِ سَقَطُوْاۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَ ','Di antara mereka ada orang yang berkata: "Berilah saya keizinan (tidak pergi berperang) dan janganlah kamu menjadikan saya terjerumus dalam fitnah". Ketahuilah bahwa mereka telah terjerumus ke dalam fitnah. Dan sesungguhnya Jahannam itu benar-benar meliputi orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,50,'اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُصِيْبَةٌ يَّقُوْلُوْا قَدْ اَخَذْنَآ اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَّهُمْ فَرِحُوْنَ ','Jika kamu mendapat suatu kebaikan, mereka menjadi tidak senang karenanya; dan jika kamu ditimpa oleh sesuatu bencana, mereka berkata: "Sesungguhnya kami sebelumnya telah memperhatikan urusan kami (tidak pergi perang)" dan mereka berpaling dengan rasa gembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,51,'قُلْ لَّنْ يُّصِيْبَنَآ اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰىنَا وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ','Katakanlah: "Sekali-kali tidak akan menimpa kami melainkan apa yang telah ditetapkan Allah untuk kami. Dialah Pelindung kami, dan hanya kepada Allah orang-orang yang beriman harus bertawakal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,52,'قُلْ هَلْ تَرَبَّصُوْنَ بِنَآ اِلَّآ اِحْدَى الْحُسْنَيَيْنِۗ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُّصِيْبَكُمُ اللّٰهُ بِعَذَابٍ مِّنْ عِنْدِهٖٓ اَوْ بِاَيْدِيْنَاۖ فَتَرَبَّصُوْٓا اِنَّا مَعَكُمْ مُّتَرَبِّصُوْنَ ','Katakanlah: "tidak ada yang kamu tunggu-tunggu bagi kami, kecuali salah satu dari dua kebaikan. Dan Kami menunggu-nunggu bagi kamu bahwa Allah akan menimpakan kepadamu azab (yang besar) dari sisi-Nya. Sebab itu tunggulah, sesungguhnya kami menunggu-nunggu bersamamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,53,'قُلْ اَنْفِقُوْا طَوْعًا اَوْ كَرْهًا لَّنْ يُّتَقَبَّلَ مِنْكُمْ ۗاِنَّكُمْ كُنْتُمْ قَوْمًا فٰسِقِيْنَ ','Katakanlah: "Nafkahkanlah hartamu, baik dengan sukarela ataupun dengan terpaksa, namun nafkah itu sekali-kali tidak akan diterima dari kamu. Sesungguhnya kamu adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,54,'وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقٰتُهُمْ اِلَّآ اَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَبِرَسُوْلِهٖ وَلَا يَأْتُوْنَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُوْنَ اِلَّا وَهُمْ كٰرِهُوْنَ ','Dan tidak ada yang menghalangi mereka untuk diterima dari mereka nafkah-nafkahnya melainkan karena mereka kafir kepada Allah dan Rasul-Nya dan mereka tidak mengerjakan sembahyang, melainkan dengan malas dan tidak (pula) menafkahkan (harta) mereka, melainkan dengan rasa enggan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,55,'فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِى الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ','Maka janganlah harta benda dan anak-anak mereka menarik hatimu. Sesungguhnya Allah menghendaki dengan (memberi) harta benda dan anak-anak itu untuk menyiksa mereka dalam kehidupan di dunia dan kelak akan melayang nyawa mereka, sedang mereka dalam keadaan kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,56,'وَيَحْلِفُوْنَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۗ وَمَا هُمْ مِّنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَّفْرَقُوْنَ ','Dan mereka (orang-orang munafik) bersumpah dengan (nama) Allah, bahwa sesungguhnya mereka termasuk golonganmu; padahal mereka bukanlah dari golonganmu, akan tetapi mereka adalah orang-orang yang sangat takut (kepadamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,57,'لَوْ يَجِدُوْنَ مَلْجَاً اَوْ مَغٰرٰتٍ اَوْ مُدَّخَلًا لَّوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُوْنَ ','Jikalau mereka memperoleh tempat perlindunganmu atau gua-gua atau lobang-lobang (dalam tanah) niscaya mereka pergi kepadanya dengan secepat-cepatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,58,'وَمِنْهُمْ مَّنْ يَّلْمِزُكَ فِى الصَّدَقٰتِۚ فَاِنْ اُعْطُوْا مِنْهَا رَضُوْا وَاِنْ لَّمْ يُعْطَوْا مِنْهَآ اِذَا هُمْ يَسْخَطُوْنَ ','Dan di antara mereka ada orang yang mencelamu tentang (distribusi) zakat; jika mereka diberi sebahagian dari padanya, mereka bersenang hati, dan jika mereka tidak diberi sebahagian dari padanya, dengan serta merta mereka menjadi marah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,59,'وَلَوْ اَنَّهُمْ رَضُوْا مَآ اٰتٰىهُمُ اللّٰهُ وَرَسُوْلُهٗۙ وَقَالُوْا حَسْبُنَا اللّٰهُ سَيُؤْتِيْنَا اللّٰهُ مِنْ فَضْلِهٖ وَرَسُوْلُهٗٓ اِنَّآ اِلَى اللّٰهِ رَاغِبُوْنَ ࣖ ','Jikalau mereka sungguh-sungguh ridha dengan apa yang diberikan Allah dan Rasul-Nya kepada mereka, dan berkata: "Cukuplah Allah bagi kami, Allah akan memberikan sebagian dari karunia-Nya dan demikian (pula) Rasul-Nya, sesungguhnya kami adalah orang-orang yang berharap kepada Allah," (tentulah yang demikian itu lebih baik bagi mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,60,'۞ اِنَّمَا الصَّدَقٰتُ لِلْفُقَرَاۤءِ وَالْمَسٰكِيْنِ وَالْعَامِلِيْنَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوْبُهُمْ وَفِى الرِّقَابِ وَالْغَارِمِيْنَ وَفِيْ سَبِيْلِ اللّٰهِ وَابْنِ السَّبِيْلِۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','Sesungguhnya zakat-zakat itu, hanyalah untuk orang-orang fakir, orang-orang miskin, pengurus-pengurus zakat, para mu´allaf yang dibujuk hatinya, untuk (memerdekakan) budak, orang-orang yang berhutang, untuk jalan Allah dan untuk mereka yuang sedang dalam perjalanan, sebagai suatu ketetapan yang diwajibkan Allah, dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,61,'وَمِنْهُمُ الَّذِيْنَ يُؤْذُوْنَ النَّبِيَّ وَيَقُوْلُوْنَ هُوَ اُذُنٌ ۗقُلْ اُذُنُ خَيْرٍ لَّكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِنِيْنَ وَرَحْمَةٌ لِّلَّذِيْنَ اٰمَنُوْا مِنْكُمْۗ وَالَّذِيْنَ يُؤْذُوْنَ رَسُوْلَ اللّٰهِ لَهُمْ عَذَابٌ اَلِيْمٌ ','Di antara mereka (orang-orang munafik) ada yang menyakiti Nabi dan mengatakan: "Nabi mempercayai semua apa yang didengarnya". Katakanlah: "Ia mempercayai semua yang baik bagi kamu, ia beriman kepada Allah, mempercayai orang-orang mukmin, dan menjadi rahmat bagi orang-orang yang beriman di antara kamu". Dan orang-orang yang menyakiti Rasulullah itu, bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,62,'يَحْلِفُوْنَ بِاللّٰهِ لَكُمْ لِيُرْضُوْكُمْ وَاللّٰهُ وَرَسُوْلُهٗٓ اَحَقُّ اَنْ يُّرْضُوْهُ اِنْ كَانُوْا مُؤْمِنِيْنَ ','Mereka bersumpah kepada kamu dengan (nama) Allah untuk mencari keridhaanmu, padahal Allah dan Rasul-Nya itulah yang lebih patut mereka cari keridhaannya jika mereka adalah orang-orang yang mukmin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,63,'اَلَمْ يَعْلَمُوْٓا اَنَّهٗ مَنْ يُّحَادِدِ اللّٰهَ وَرَسُوْلَهٗ فَاَنَّ لَهٗ نَارَ جَهَنَّمَ خَالِدًا فِيْهَاۗ ذٰلِكَ الْخِزْيُ الْعَظِيْمُ ','Tidaklah mereka (orang-orang munafik itu) mengetahui bahwasanya barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya nerakan jahannamlah baginya, kekal mereka di dalamnya. Itu adalah kehinaan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,64,'يَحْذَرُ الْمُنٰفِقُوْنَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُوْرَةٌ تُنَبِّئُهُمْ بِمَا فِيْ قُلُوْبِهِمْۗ قُلِ اسْتَهْزِءُوْاۚ اِنَّ اللّٰهَ مُخْرِجٌ مَّا تَحْذَرُوْنَ ','Orang-orang yang munafik itu takut akan diturunkan terhadap mereka sesuatu surat yang menerangkan apa yang tersembunyi dalam hati mereka. Katakanlah kepada mereka: "Teruskanlah ejekan-ejekanmu (terhadap Allah dan rasul-Nya)". Sesungguhnya Allah akan menyatakan apa yang kamu takuti itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,65,'وَلَىِٕنْ سَاَلْتَهُمْ لَيَقُوْلُنَّ اِنَّمَا كُنَّا نَخُوْضُ وَنَلْعَبُۗ  قُلْ اَبِاللّٰهِ وَاٰيٰتِهٖ وَرَسُوْلِهٖ كُنْتُمْ تَسْتَهْزِءُوْنَ ','Dan jika kamu tanyakan kepada mereka (tentang apa yang mereka lakukan itu), tentulah mereka akan manjawab, "Sesungguhnya kami hanyalah bersenda gurau dan bermain-main saja". Katakanlah: "Apakah dengan Allah, ayat-ayat-Nya dan Rasul-Nya kamu selalu berolok-olok?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,66,'لَا تَعْتَذِرُوْا قَدْ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ ۗ اِنْ نَّعْفُ عَنْ طَاۤىِٕفَةٍ مِّنْكُمْ نُعَذِّبْ طَاۤىِٕفَةً ۢ بِاَنَّهُمْ كَانُوْا مُجْرِمِيْنَ ࣖ','Tidak usah kamu minta maaf, karena kamu kafir sesudah beriman. Jika Kami memaafkan segolongan kamu (lantaran mereka taubat), niscaya Kami akan mengazab golongan (yang lain) disebabkan mereka adalah orang-orang yang selalu berbuat dosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,67,'اَلْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ بَعْضُهُمْ مِّنْۢ بَعْضٍۘ يَأْمُرُوْنَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوْفِ وَيَقْبِضُوْنَ اَيْدِيَهُمْۗ نَسُوا اللّٰهَ فَنَسِيَهُمْ ۗ اِنَّ الْمُنٰفِقِيْنَ هُمُ الْفٰسِقُوْنَ ','Orang-orang munafik laki-laki dan perempuan. sebagian dengan sebagian yang lain adalah sama, mereka menyuruh membuat yang munkar dan melarang berbuat yang ma´ruf dan mereka menggenggamkan tangannya. Mereka telah lupa kepada Allah, maka Allah melupakan mereka. Sesungguhnya orang-orang munafik itu adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,68,'وَعَدَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ هِيَ حَسْبُهُمْ ۚوَلَعَنَهُمُ اللّٰهُ ۚوَلَهُمْ عَذَابٌ مُّقِيْمٌۙ','Allah mengancam orang-orang munafik laki-laki dan perempuan dan orang-orang kafir dengan neraka Jahannam, mereka kekal di dalamnya. Cukuplah neraka itu bagi mereka, dan Allah melaknati mereka, dan bagi mereka azab yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,69,'كَالَّذِيْنَ مِنْ قَبْلِكُمْ كَانُوْٓا اَشَدَّ مِنْكُمْ قُوَّةً وَّاَكْثَرَ اَمْوَالًا وَّاَوْلَادًاۗ فَاسْتَمْتَعُوْا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِيْنَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِيْ خَاضُوْاۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ','(keadaan kamu hai orang-orang munafik dan musyrikin) adalah seperti keadaan orang-orang sebelum kamu, mereka lebih kuat daripada kamu, dan lebih banyak harta dan anak-anaknya dari kamu. Maka mereka telah menikmati bagian mereka, dan kamu telah menikmati bagian kamu sebagaimana orang-orang yang sebelummu menikmati bagiannya, dan kamu mempercakapkan (hal yang batil) sebagaimana mereka mempercakapkannya. Mereka itu amalannya menjadi sia-sia di dunia dan di akhirat; dan mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,70,'اَلَمْ يَأْتِهِمْ نَبَاُ الَّذِيْنَ مِنْ قَبْلِهِمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۙ وَقَوْمِ اِبْرٰهِيْمَ وَاَصْحٰبِ مَدْيَنَ وَالْمُؤْتَفِكٰتِۗ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ','Belumkah datang kepada mereka berita penting tentang orang-orang yang sebelum mereka, (yaitu) kaum Nuh, ´Aad, Tsamud, kaum Ibrahim, penduduk Madyan dan negeri-negeri yang telah musnah?. Telah datang kepada mereka rasul-rasul dengan membawa keterangan yang nyata, maka Allah tidaklah sekali-kali menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,71,'وَالْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۘ يَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَيُطِيْعُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاُولٰۤىِٕكَ سَيَرْحَمُهُمُ اللّٰهُ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ','Dan orang-orang yang beriman, lelaki dan perempuan, sebahagian mereka (adalah) menjadi penolong bagi sebahagian yang lain. Mereka menyuruh (mengerjakan) yang ma´ruf, mencegah dari yang munkar, mendirikan shalat, menunaikan zakat dan mereka taat pada Allah dan Rasul-Nya. Mereka itu akan diberi rahmat oleh Allah; sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,72,'وَعَدَ اللّٰهُ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍ ۗوَرِضْوَانٌ مِّنَ اللّٰهِ اَكْبَرُ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ࣖ ','Allah menjanjikan kepada orang-orang mukmin, lelaki dan perempuan, (akan mendapat) surga yang dibawahnya mengalir sungai-sungai, kekal mereka di dalamnya, dan (mendapat) tempat-tempat yang bagus di surga ´Adn. Dan keridhaan Allah adalah lebih besar; itu adalah keberuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,73,'يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْ ۗوَمَأْوٰىهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيْرُ ','Hai Nabi, berjihadlah (melawan) orang-orang kafir dan orang-orang munafik itu, dan bersikap keraslah terhadap mereka. Tempat mereka ialah jahannam. Dan itu adalah tempat kembali yang seburuk-buruknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,74,'يَحْلِفُوْنَ بِاللّٰهِ مَا قَالُوْا ۗوَلَقَدْ قَالُوْا كَلِمَةَ الْكُفْرِ وَكَفَرُوْا بَعْدَ اِسْلَامِهِمْ وَهَمُّوْا بِمَا لَمْ يَنَالُوْاۚ وَمَا نَقَمُوْٓا اِلَّآ اَنْ اَغْنٰىهُمُ اللّٰهُ وَرَسُوْلُهٗ مِنْ فَضْلِهٖ ۚفَاِنْ يَّتُوْبُوْا يَكُ خَيْرًا لَّهُمْ ۚوَاِنْ يَّتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذَابًا اَلِيْمًا فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَمَا لَهُمْ فِى الْاَرْضِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ','Mereka (orang-orang munafik itu) bersumpah dengan (nama) Allah, bahwa mereka tidak mengatakan (sesuatu yang menyakitimu). Sesungguhnya mereka telah mengucapkan perkataan kekafiran, dan telah menjadi kafir sesudah Islam dan mengingini apa yang mereka tidak dapat mencapainya, dan mereka tidak mencela (Allah dan Rasul-Nya), kecuali karena Allah dan Rasul-Nya telah melimpahkan karunia-Nya kepada mereka. Maka jika mereka bertaubat, itu adalah lebih baik bagi mereka, dan jika mereka berpaling, niscaya Allah akan mengazab mereka dengan azab yang pedih di dunia dan akhirat; dan mereka sekali-kali tidaklah mempunyai pelindung dan tidak (pula) penolong di muka bumi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,75,'۞ وَمِنْهُمْ مَّنْ عٰهَدَ اللّٰهَ لَىِٕنْ اٰتٰىنَا مِنْ فَضْلِهٖ لَنَصَّدَّقَنَّ وَلَنَكُوْنَنَّ مِنَ الصّٰلِحِيْنَ ','Dan diantara mereka ada orang yang telah berikrar kepada Allah: "Sesungguhnya jika Allah memberikan sebahagian karunia-Nya kepada kami, pastilah kami akan bersedekah dan pastilah kami termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,76,'فَلَمَّآ اٰتٰىهُمْ مِّنْ فَضْلِهٖ بَخِلُوْا بِهٖ وَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ ','Maka setelah Allah memberikan kepada mereka sebahagian dari karunia-Nya, mereka kikir dengan karunia itu, dan berpaling, dan mereka memanglah orang-orang yang selalu membelakangi (kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,77,'فَاَعْقَبَهُمْ نِفَاقًا فِيْ قُلُوْبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهٗ بِمَآ اَخْلَفُوا اللّٰهَ مَا وَعَدُوْهُ وَبِمَا كَانُوْا يَكْذِبُوْنَ ','Maka Allah menimbulkan kemunafikan pada hati mereka sampai kepada waktu mereka menemui Allah, karena mereka telah memungkiri terhadap Allah apa yang telah mereka ikrarkan kepada-Nya dan juga karena mereka selalu berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,78,'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰىهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوْبِ ','Tidaklah mereka tahu bahwasanya Allah mengetahui rahasia dan bisikan mereka, dan bahwasanya Allah amat mengetahui segala yang ghaib.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,79,'اَلَّذِيْنَ يَلْمِزُوْنَ الْمُطَّوِّعِيْنَ مِنَ الْمُؤْمِنِيْنَ فِى الصَّدَقٰتِ وَالَّذِيْنَ لَا يَجِدُوْنَ اِلَّا جُهْدَهُمْ فَيَسْخَرُوْنَ مِنْهُمْ ۗسَخِرَ اللّٰهُ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','(Orang-orang munafik itu) yaitu orang-orang yang mencela orang-orang mukmin yang memberi sedekah dengan sukarela dan (mencela) orang-orang yang tidak memperoleh (untuk disedekahkan) selain sekedar kesanggupannya, maka orang-orang munafik itu menghina mereka. Allah akan membalas penghinaan mereka itu, dan untuk mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,80,'اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۗ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِيْنَ مَرَّةً فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ۗذٰلِكَ بِاَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ࣖ ','Kamu memohonkan ampun bagi mereka atau tidak kamu mohonkan ampun bagi mereka (adalah sama saja). Kendatipun kamu memohonkan ampun bagi mereka tujuh puluh kali, namun Allah sekali-kali tidak akan memberi ampunan kepada mereka. Yang demikian itu adalah karena mereka kafir kepada Allah dan Rasul-Nya. Dan Allah tidak memberi petunjuk kepada kaum yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,81,'فَرِحَ الْمُخَلَّفُوْنَ بِمَقْعَدِهِمْ خِلٰفَ رَسُوْلِ اللّٰهِ وَكَرِهُوْٓا اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَقَالُوْا لَا تَنْفِرُوْا فِى الْحَرِّۗ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَرًّاۗ  لَوْ كَانُوْا يَفْقَهُوْنَ ','Orang-orang yang ditinggalkan (tidak ikut perang) itu, merasa gembira dengan tinggalnya mereka di belakang Rasulullah, dan mereka tidak suka berjihad dengan harta dan jiwa mereka pada jalan Allah dan mereka berkata: "Janganlah kamu berangkat (pergi berperang) dalam panas terik ini". Katakanlah: "Api neraka jahannam itu lebih sangat panas(nya)" jika mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,82,'فَلْيَضْحَكُوْا قَلِيْلًا وَّلْيَبْكُوْا كَثِيْرًاۚ جَزَاۤءًۢ بِمَا كَانُوْا يَكْسِبُوْنَ ','Maka hendaklah mereka tertawa sedikit dan menangis banyak, sebagai pembalasan dari apa yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,83,'فَاِنْ رَّجَعَكَ اللّٰهُ اِلٰى طَاۤىِٕفَةٍ مِّنْهُمْ فَاسْتَأْذَنُوْكَ لِلْخُرُوْجِ فَقُلْ لَّنْ تَخْرُجُوْا مَعِيَ اَبَدًا وَّلَنْ تُقَاتِلُوْا مَعِيَ عَدُوًّاۗ اِنَّكُمْ رَضِيْتُمْ بِالْقُعُوْدِ اَوَّلَ مَرَّةٍۗ فَاقْعُدُوْا مَعَ الْخَالِفِيْنَ ','Maka jika Allah mengembalikanmu kepada suatu golongan dari mereka, kemudian mereka minta izin kepadamu untuk keluar (pergi berperang), maka Katakanlah: "Kamu tidak boleh keluar bersamaku selama-lamanya dan tidak boleh memerangi musuh bersamaku. Sesungguhnya kamu telah rela tidak pergi berperang kali yang pertama. Karena itu duduklah bersama orang-orang yang tidak ikut berperang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,84,'وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِّنْهُمْ مَّاتَ اَبَدًا وَّلَا تَقُمْ عَلٰى قَبْرِهٖۗ اِنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَمَاتُوْا وَهُمْ فٰسِقُوْنَ ','Dan janganlah kamu sekali-kali menyembahyangkan (jenazah) seorang yang mati di antara mereka, dan janganlah kamu berdiri (mendoakan) di kuburnya. Sesungguhnya mereka telah kafir kepada Allah dan Rasul-Nya dan mereka mati dalam keadaan fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,85,'وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۗ اِنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّعَذِّبَهُمْ بِهَا فِى الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ','Dan janganlah harta benda dan anak-anak mereka menarik hatimu. Sesungguhnya Allah menghendaki akan mengazab mereka di dunia dengan harta dan anak-anak itu dan agar melayang nyawa mereka, dalam keadaan kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,86,'وَاِذَآ اُنْزِلَتْ سُوْرَةٌ اَنْ اٰمِنُوْا بِاللّٰهِ وَجَاهِدُوْا مَعَ رَسُوْلِهِ اسْتَأْذَنَكَ اُولُوا الطَّوْلِ مِنْهُمْ وَقَالُوْا ذَرْنَا نَكُنْ مَّعَ الْقٰعِدِيْنَ ','Dan apabila diturunkan suatu surat (yang memerintahkan kepada orang munafik itu): "Berimanlah kamu kepada Allah dan berjihadlah beserta Rasul-Nya", niscaya orang-orang yang sanggup di antara mereka meminta izin kepadamu (untuk tidak berjihad) dan mereka berkata: "Biarkanlah kami berada bersama orang-orang yang duduk".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,87,'رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِ وَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ ','Mereka rela berada bersama orang-orang yang tidak berperang, dan hati mereka telah dikunci mati maka mereka tidak mengetahui (kebahagiaan beriman dan berjihad).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,88,'لٰكِنِ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ جَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاُولٰۤىِٕكَ لَهُمُ الْخَيْرٰتُ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Tetapi Rasul dan orang-orang yang beriman bersama dia, mereka berjihad dengan harta dan diri mereka. Dan mereka itulah orang-orang yang memperoleh kebaikan, dan mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,89,'اَعَدَّ اللّٰهُ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ ࣖ ','Allah telah menyediakan bagi mereka surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,90,'وَجَاۤءَ الْمُعَذِّرُوْنَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِيْنَ كَذَبُوا اللّٰهَ وَرَسُوْلَهٗ ۗسَيُصِيْبُ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ ','Dan datang (kepada Nabi) orang-orang yang mengemukakan ´uzur, yaitu orang-orang Arab Baswi agar diberi izin bagi mereka (untuk tidak berjihad), sedang orang-orang yang mendustakan Allah dan Rasul-Nya, duduk berdiam diri saja. Kelak orang-orang yang kafir di antara mereka itu akan ditimpa azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,91,'لَيْسَ عَلَى الضُّعَفَاۤءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذِيْنَ لَا يَجِدُوْنَ مَا يُنْفِقُوْنَ حَرَجٌ اِذَا نَصَحُوْا لِلّٰهِ وَرَسُوْلِهٖۗ مَا عَلَى الْمُحْسِنِيْنَ مِنْ سَبِيْلٍ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ ','Tiada dosa (lantaran tidak pergi berjihad) atas orang-orang yang lemah, orang-orang yang sakit dan atas orang-orang yang tidak memperoleh apa yang akan mereka nafkahkan, apabila mereka berlaku ikhlas kepada Allah dan Rasul-Nya. Tidak ada jalan sedikitpun untuk menyalahkan orang-orang yang berbuat baik. Dan Allah Maha Pengampun lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,92,'وَّلَا عَلَى الَّذِيْنَ اِذَا مَآ اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَآ اَجِدُ مَآ اَحْمِلُكُمْ عَلَيْهِ ۖتَوَلَّوْا وَّاَعْيُنُهُمْ تَفِيْضُ مِنَ الدَّمْعِ حَزَنًا اَلَّا يَجِدُوْا مَا يُنْفِقُوْنَۗ ','dan tiada (pula) berdosa atas orang-orang yang apabila mereka datang kepadamu, supaya kamu memberi mereka kendaraan, lalu kamu berkata: "Aku tidak memperoleh kendaraan untuk membawamu". lalu mereka kembali, sedang mata mereka bercucuran air mata karena kesedihan, lantaran mereka tidak memperoleh apa yang akan mereka nafkahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,10,9,93,'اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَسْتَأْذِنُوْنَكَ وَهُمْ اَغْنِيَاۤءُۚ رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَعْلَمُوْنَ  ۔','Sesungguhnya jalan (untuk menyalahkan) hanyalah terhadap orang-orang yang meminta izin kepadamu, padahal mereka itu orang-orang kaya. Mereka rela berada bersama orang-orang yang tidak ikut berperang dan Allah telah mengunci mati hati mereka, maka mereka tidak mengetahui (akibat perbuatan mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,94,'يَعْتَذِرُوْنَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْ ۗ قُلْ لَّا تَعْتَذِرُوْا لَنْ نُّؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ ثُمَّ تُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ','Mereka (orang-orang munafik) mengemukakan ´uzurnya kepadamu, apabila kamu telah kembali kepada mereka (dari medan perang). Katakanlah: "Janganlah kamu mengemukakan ´uzur; kami tidak percaya lagi kepadamu, (karena) sesungguhnya Allah telah memberitahukan kepada kami beritamu yang sebenarnya. Dan Allah serta Rasul-Nya akan melihat pekerjaanmu, kemudian kamu dikembalikan kepada Yang mengetahui yang ghaib dan yang nyata, lalu Dia memberitahukan kepadamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,95,'سَيَحْلِفُوْنَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوْا عَنْهُمْ ۗ فَاَعْرِضُوْا عَنْهُمْ ۗ اِنَّهُمْ رِجْسٌۙ وَّمَأْوٰىهُمْ جَهَنَّمُ جَزَاۤءً ۢبِمَا كَانُوْا يَكْسِبُوْنَ','Kelak mereka akan bersumpah kepadamu dengan nama Allah, apabila kamu kembali kepada mereka, supaya kamu berpaling dari mereka. Maka berpalinglah dari mereka; karena sesungguhnya mereka itu adalah najis dan tempat mereka jahannam; sebagai balasan atas apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,96,'يَحْلِفُوْنَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۚفَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفٰسِقِيْنَ','Mereka akan bersumpah kepadamu, agar kamu ridha kepada mereka. Tetapi jika sekiranya kamu ridha kepada mereka, sesungguhnya Allah tidak ridha kepada orang-orang yang fasik itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,97,'اَلْاَعْرَابُ اَشَدُّ كُفْرًا وَّنِفَاقًا وَّاَجْدَرُ اَلَّا يَعْلَمُوْا حُدُوْدَ مَآ اَنْزَلَ اللّٰهُ عَلٰى رَسُوْلِهٖ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ','Orang-orang Arab Badwi itu, lebih sangat kekafiran dan kemunafikannya, dan lebih wajar tidak mengetahui hukum-hukum yang diturunkan Allah kepada Rasul-Nya. Dan Allah Maha mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,98,'وَمِنَ الْاَعْرَابِ مَنْ يَّتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَّيَتَرَبَّصُ بِكُمُ الدَّوَاۤىِٕرَ ۗعَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِ ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ  ','Di antara orang-orang Arab Badwi itu ada orang yang memandang apa yang dinafkahkannya (di jalan Allah), sebagi suatu kerugian, dan dia menanti-nanti marabahaya menimpamu, merekalah yang akan ditimpa marabahaya. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,99,'وَمِنَ الْاَعْرَابِ مَنْ يُّؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبٰتٍ عِنْدَ اللّٰهِ وَصَلَوٰتِ الرَّسُوْلِ ۗ اَلَآ اِنَّهَا قُرْبَةٌ لَّهُمْ ۗ سَيُدْخِلُهُمُ اللّٰهُ فِيْ رَحْمَتِهٖ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ','Di antara orang-orang Arab Badwi itu ada orang yang beriman kepada Allah dan hari kemudian, dan memandang apa yang dinafkahkannya (di jalan Allah) itu, sebagai jalan untuk mendekatkannya kepada Allah dan sebagai jalan untuk memperoleh doa Rasul. Ketahuilah, sesungguhnya nafkah itu adalah suatu jalan bagi mereka untuk mendekatkan diri (kepada Allah). Kelak Allah akan memasukan mereka kedalam rahmat (surga)Nya; Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,100,'وَالسّٰبِقُوْنَ الْاَوَّلُوْنَ مِنَ الْمُهٰجِرِيْنَ وَالْاَنْصَارِ وَالَّذِيْنَ اتَّبَعُوْهُمْ بِاِحْسَانٍۙ رَّضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ وَاَعَدَّ لَهُمْ جَنّٰتٍ تَجْرِيْ تَحْتَهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ ','Orang-orang yang terdahulu lagi yang pertama-tama (masuk Islam) dari golongan muhajirin dan anshar dan orang-orang yang mengikuti mereka dengan baik, Allah ridha kepada mereka dan merekapun ridha kepada Allah dan Allah menyediakan bagi mereka surga-surga yang mengalir sungai-sungai di dalamnya selama-lamanya. Mereka kekal di dalamnya. Itulah kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,101,'وَمِمَّنْ حَوْلَكُمْ مِّنَ الْاَعْرَابِ مُنٰفِقُوْنَ ۗوَمِنْ اَهْلِ الْمَدِيْنَةِ مَرَدُوْا عَلَى النِّفَاقِۗ  لَا تَعْلَمُهُمْۗ نَحْنُ نَعْلَمُهُمْۗ سَنُعَذِّبُهُمْ مَّرَّتَيْنِ ثُمَّ يُرَدُّوْنَ اِلٰى عَذَابٍ عَظِيْمٍ ۚ  ','Di antara orang-orang Arab Badwi yang di sekelilingmu itu, ada orang-orang munafik; dan (juga) di antara penduduk Madinah. Mereka keterlaluan dalam kemunafikannya. Kamu (Muhammad) tidak mengetahui mereka, (tetapi) Kamilah yang mengetahui mereka. Nanti mereka akan Kami siksa dua kali kemudian mereka akan dikembalikan kepada azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,102,'وَاٰخَرُوْنَ اعْتَرَفُوْا بِذُنُوْبِهِمْ خَلَطُوْا عَمَلًا صَالِحًا وَّاٰخَرَ سَيِّئًاۗ عَسَى اللّٰهُ اَنْ يَّتُوْبَ عَلَيْهِمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Dan (ada pula) orang-orang lain yang mengakui dosa-dosa mereka, mereka mencampurbaurkan pekerjaan yang baik dengan pekerjaan lain yang buruk. Mudah-mudahan Allah menerima taubat mereka. Sesungguhnya Allah Maha Pengampun lagi maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,103,'خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيْهِمْ بِهَا وَصَلِّ عَلَيْهِمْۗ اِنَّ صَلٰوتَكَ سَكَنٌ لَّهُمْۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ','Ambillah zakat dari sebagian harta mereka, dengan zakat itu kamu membersihkan dan mensucikan mereka dan mendoalah untuk mereka. Sesungguhnya doa kamu itu (menjadi) ketenteraman jiwa bagi mereka. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,104,'اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَأْخُذُ الصَّدَقٰتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ ','Tidaklah mereka mengetahui, bahwasanya Allah menerima taubat dari hamba-hamba-Nya dan menerima zakat dan bahwasanya Allah Maha Penerima taubat lagi Maha Penyayang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,105,'وَقُلِ اعْمَلُوْا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ وَالْمُؤْمِنُوْنَۗ وَسَتُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۚ  ','Dan Katakanlah: "Bekerjalah kamu, maka Allah dan Rasul-Nya serta orang-orang mukmin akan melihat pekerjaanmu itu, dan kamu akan dikembalikan kepada (Allah) Yang Mengetahui akan yang ghaib dan yang nyata, lalu diberitakan-Nya kepada kamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,106,'وَاٰخَرُوْنَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوْبُ عَلَيْهِمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','Dan ada (pula) orang-orang lain yang ditangguhkan sampai ada keputusan Allah; adakalanya Allah akan mengazab mereka dan adakalanya Allah akan menerima taubat mereka. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,107,'وَالَّذِيْنَ اتَّخَذُوْا مَسْجِدًا ضِرَارًا وَّكُفْرًا وَّتَفْرِيْقًاۢ بَيْنَ الْمُؤْمِنِيْنَ وَاِرْصَادًا لِّمَنْ حَارَبَ اللّٰهَ وَرَسُوْلَهٗ مِنْ قَبْلُ ۗوَلَيَحْلِفُنَّ اِنْ اَرَدْنَآ اِلَّا الْحُسْنٰىۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ ','Dan (di antara orang-orang munafik itu) ada orang-orang yang mendirikan masjid untuk menimbulkan kemudharatan (pada orang-orang mukmin), untuk kekafiran dan untuk memecah belah antara orang-orang mukmin serta menunggu kedatangan orang-orang yang telah memerangi Allah dan Rasul-Nya sejak dahulu. Mereka Sesungguhnya bersumpah: "Kami tidak menghendaki selain kebaikan". Dan Allah menjadi saksi bahwa sesungguhnya mereka itu adalah pendusta (dalam sumpahnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,108,'لَا تَقُمْ فِيْهِ اَبَدًاۗ  لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُوْمَ فِيْهِۗ فِيْهِ رِجَالٌ يُّحِبُّوْنَ اَنْ يَّتَطَهَّرُوْاۗ وَاللّٰهُ يُحِبُّ الْمُطَّهِّرِيْنَ ','Janganlah kamu bersembahyang dalam mesjid itu selama-lamanya. Sesungguhnya mesjid yang didirikan atas dasar takwa (mesjid Quba), sejak hari pertama adalah lebih patut kamu sholat di dalamnya. Di dalamnya mesjid itu ada orang-orang yang ingin membersihkan diri. Dan sesungguhnya Allah menyukai orang-orang yang bersih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,109,'اَفَمَنْ اَسَّسَ بُنْيَانَهٗ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَّنْ اَسَّسَ بُنْيَانَهٗ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهٖ فِيْ نَارِ جَهَنَّمَۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ','Maka apakah orang-orang yang mendirikan mesjidnya di atas dasar takwa kepada Allah dan keridhaan-(Nya) itu yang baik, ataukah orang-orang yang mendirikan bangunannya di tepi jurang yang runtuh, lalu bangunannya itu jatuh bersama-sama dengan dia ke dalam neraka Jahannam. Dan Allah tidak memberikan petunjuk kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,110,'لَا يَزَالُ بُنْيَانُهُمُ الَّذِيْ بَنَوْا رِيْبَةً فِيْ قُلُوْبِهِمْ اِلَّآ اَنْ تَقَطَّعَ قُلُوْبُهُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ࣖ ','Bangunan-bangunan yang mereka dirikan itu senantiasa menjadi pangkal keraguan dalam hati mereka, kecuali bila hati mereka itu telah hancur. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,111,'۞ اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِنِيْنَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۗ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ فَيَقْتُلُوْنَ وَيُقْتَلُوْنَ وَعْدًا عَلَيْهِ حَقًّا فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ وَالْقُرْاٰنِۗ وَمَنْ اَوْفٰى بِعَهْدِهٖ مِنَ اللّٰهِ فَاسْتَبْشِرُوْا بِبَيْعِكُمُ الَّذِيْ بَايَعْتُمْ بِهٖۗ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ','Sesungguhnya Allah telah membeli dari orang-orang mukmin diri dan harta mereka dengan memberikan surga untuk mereka. Mereka berperang pada jalan Allah; lalu mereka membunuh atau terbunuh. (Itu telah menjadi) janji yang benar dari Allah di dalam Taurat, Injil dan Al Quran. Dan siapakah yang lebih menepati janjinya (selain) daripada Allah? Maka bergembiralah dengan jual beli yang telah kamu lakukan itu, dan itulah kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,112,'اَلتَّاۤىِٕبُوْنَ الْعٰبِدُوْنَ الْحَامِدُوْنَ السَّاۤىِٕحُوْنَ الرَّاكِعُوْنَ السَّاجِدُوْنَ الْاٰمِرُوْنَ بِالْمَعْرُوْفِ وَالنَّاهُوْنَ عَنِ الْمُنْكَرِ وَالْحٰفِظُوْنَ لِحُدُوْدِ اللّٰهِ ۗوَبَشِّرِ الْمُؤْمِنِيْنَ ','Mereka itu adalah orang-orang yang bertaubat, yang beribadat, yang memuji, yang melawat, yang ruku´, yang sujud, yang menyuruh berbuat ma´ruf dan mencegah berbuat munkar dan yang memelihara hukum-hukum Allah. Dan gembirakanlah orang-orang mukmin itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,113,'مَا كَانَ لِلنَّبِيِّ وَالَّذِيْنَ اٰمَنُوْٓا اَنْ يَّسْتَغْفِرُوْا لِلْمُشْرِكِيْنَ وَلَوْ كَانُوْٓا اُولِيْ قُرْبٰى مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحٰبُ الْجَحِيْمِ ','Tiadalah sepatutnya bagi Nabi dan orang-orang yang beriman memintakan ampun (kepada Allah) bagi orang-orang musyrik, walaupun orang-orang musyrik itu adalah kaum kerabat(nya), sesudah jelas bagi mereka, bahwasanya orang-orang musyrik itu adalah penghuni neraka jahanam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,114,'وَمَا كَانَ اسْتِغْفَارُ اِبْرٰهِيْمَ لِاَبِيْهِ اِلَّا عَنْ مَّوْعِدَةٍ وَّعَدَهَآ اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَهٗٓ اَنَّهٗ عَدُوٌّ لِّلّٰهِ تَبَرَّاَ مِنْهُۗ اِنَّ اِبْرٰهِيْمَ لَاَوَّاهٌ حَلِيْمٌ ','Dan permintaan ampun dari Ibrahim (kepada Allah) untuk bapaknya tidak lain hanyalah karena suatu janji yang telah diikrarkannya kepada bapaknya itu. Maka, tatkala jelas bagi Ibrahim bahwa bapaknya itu adalah musuh Allah, maka Ibrahim berlepas diri dari padanya. Sesungguhnya Ibrahim adalah seorang yang sangat lembut hatinya lagi penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,115,'وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْمًاۢ بَعْدَ اِذْ هَدٰىهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَّا يَتَّقُوْنَۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Dan Allah sekali-kali tidak akan menyesatkan suatu kaum, sesudah Allah memberi petunjuk kepada mereka sehingga dijelaskan-Nya kepada mereka apa yang harus mereka jauhi. Sesungguhnya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,116,'اِنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُحْيٖ وَيُمِيْتُۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ','Sesungguhnya kepunyaan Allah-lah kerajaan langit dan bumi. Dia menghidupkan dan mematikan. Dan sekali-kali tidak ada pelindung dan penolong bagimu selain Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,117,'لَقَدْ تَّابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهٰجِرِيْنَ وَالْاَنْصَارِ الَّذِيْنَ اتَّبَعُوْهُ فِيْ سَاعَةِ الْعُسْرَةِ مِنْۢ بَعْدِ مَا كَادَ يَزِيْغُ قُلُوْبُ فَرِيْقٍ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۗ اِنَّهٗ بِهِمْ رَءُوْفٌ رَّحِيْمٌ ۙ','Sesungguhnya Allah telah menerima taubat Nabi, orang-orang muhajirin dan orang-orang anshar yang mengikuti Nabi dalam masa kesulitan, setelah hati segolongan dari mereka hampir berpaling, kemudian Allah menerima taubat mereka itu. Sesungguhnya Allah Maha Pengasih lagi Maha Penyayang kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,118,'وَّعَلَى الثَّلٰثَةِ الَّذِيْنَ خُلِّفُوْاۗ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّوْٓا اَنْ لَّا مَلْجَاَ مِنَ اللّٰهِ اِلَّآ اِلَيْهِۗ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوْبُوْاۗ اِنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ ࣖ ','dan terhadap tiga orang yang ditangguhkan (penerimaan taubat) mereka, hingga apabila bumi telah menjadi sempit bagi mereka, padahal bumi itu luas dan jiwa merekapun telah sempit (pula terasa) oleh mereka, serta mereka telah mengetahui bahwa tidak ada tempat lari dari (siksa) Allah, melainkan kepada-Nya saja. Kemudian Allah menerima taubat mereka agar mereka tetap dalam taubatnya. Sesungguhnya Allah-lah Yang maha Penerima taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,119,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُوْنُوْا مَعَ الصّٰدِقِيْنَ ','Hai orang-orang yang beriman bertakwalah kepada Allah, dan hendaklah kamu bersama orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,120,'مَا كَانَ لِاَهْلِ الْمَدِيْنَةِ وَمَنْ حَوْلَهُمْ مِّنَ الْاَعْرَابِ اَنْ يَّتَخَلَّفُوْا عَنْ رَّسُوْلِ اللّٰهِ وَلَا يَرْغَبُوْا بِاَنْفُسِهِمْ عَنْ نَّفْسِهٖۗ ذٰلِكَ بِاَنَّهُمْ لَا يُصِيْبُهُمْ ظَمَاٌ وَّلَا نَصَبٌ وَّلَا مَخْمَصَةٌ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَطَـُٔوْنَ مَوْطِئًا يَّغِيْظُ الْكُفَّارَ وَلَا يَنَالُوْنَ مِنْ عَدُوٍّ نَّيْلًا اِلَّا كُتِبَ لَهُمْ بِهٖ عَمَلٌ صَالِحٌۗ اِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ ','Tidaklah sepatutnya bagi penduduk Madinah dan orang-orang Arab Badwi yang berdiam di sekitar mereka, tidak turut menyertai Rasulullah (berperang) dan tidak patut (pula) bagi mereka lebih mencintai diri mereka daripada mencintai diri Rasul. Yang demikian itu ialah karena mereka tidak ditimpa kehausan, kepayahan dan kelaparan pada jalan Allah, dan tidak (pula) menginjak suatu tempat yang membangkitkan amarah orang-orang kafir, dan tidak menimpakan sesuatu bencana kepada musuh, melainkan dituliskanlah bagi mereka dengan yang demikian itu suatu amal saleh. Sesungguhnya Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,121,'وَلَا يُنْفِقُوْنَ نَفَقَةً صَغِيْرَةً وَّلَا كَبِيْرَةً وَّلَا يَقْطَعُوْنَ وَادِيًا اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوْا يَعْمَلُوْنَ ','dan mereka tiada menafkahkan suatu nafkah yang kecil dan tidak (pula) yang besar dan tidak melintasi suatu lembah, melainkan dituliskan bagi mereka (amal saleh pula) karena Allah akan memberi balasan kepada mereka yang lebih baik dari apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,122,'۞ وَمَا كَانَ الْمُؤْمِنُوْنَ لِيَنْفِرُوْا كَاۤفَّةًۗ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِّنْهُمْ طَاۤىِٕفَةٌ لِّيَتَفَقَّهُوْا فِى الدِّيْنِ وَلِيُنْذِرُوْا قَوْمَهُمْ اِذَا رَجَعُوْٓا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُوْنَ ࣖ ','Tidak sepatutnya bagi mukminin itu pergi semuanya (ke medan perang). Mengapa tidak pergi dari tiap-tiap golongan di antara mereka beberapa orang untuk memperdalam pengetahuan mereka tentang agama dan untuk memberi peringatan kepada kaumnya apabila mereka telah kembali kepadanya, supaya mereka itu dapat menjaga dirinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,123,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قَاتِلُوا الَّذِيْنَ يَلُوْنَكُمْ مِّنَ الْكُفَّارِ وَلْيَجِدُوْا فِيْكُمْ غِلْظَةًۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ','Hai orang-orang yang beriman, perangilah orang-orang kafir yang di sekitar kamu itu, dan hendaklah mereka menemui kekerasan daripadamu, dan ketahuilah, bahwasanya Allah bersama orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,124,'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ فَمِنْهُمْ مَّنْ يَّقُوْلُ اَيُّكُمْ زَادَتْهُ هٰذِهٖٓ اِيْمَانًاۚ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَزَادَتْهُمْ اِيْمَانًا وَّهُمْ يَسْتَبْشِرُوْنَ','Dan apabila diturunkan suatu surat, maka di antara mereka (orang-orang munafik) ada yang berkata: "Siapakah di antara kamu yang bertambah imannya dengan (turannya) surat ini?" Adapun orang-orang yang beriman, maka surat ini menambah imannya, dan mereka merasa gembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,125,'وَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ فَزَادَتْهُمْ رِجْسًا اِلٰى رِجْسِهِمْ وَمَاتُوْا وَهُمْ كٰفِرُوْنَ ','Dan adapun orang-orang yang di dalam hati mereka ada penyakit, maka dengan surat itu bertambah kekafiran mereka, disamping kekafirannya (yang telah ada) dan mereka mati dalam keadaan kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,126,'اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُوْنَ فِيْ كُلِّ عَامٍ مَّرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوْبُوْنَ وَلَا هُمْ يَذَّكَّرُوْنَ ','Dan tidaklah mereka (orang-orang munafik) memperhatikan bahwa mereka diuji sekali atau dua kali setiap tahun, dan mereka tidak (juga) bertaubat dan tidak (pula) mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,127,'وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ نَّظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۗ هَلْ يَرٰىكُمْ مِّنْ اَحَدٍ ثُمَّ انْصَرَفُوْاۗ صَرَفَ اللّٰهُ قُلُوْبَهُمْ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ','Dan apabila diturunkan satu surat, sebagian mereka memandang kepada yang lain (sambil berkata): "Adakah seorang dari (orang-orang muslimin) yang melihat kamu?" Sesudah itu merekapun pergi. Allah telah memalingkan hati mereka disebabkan mereka adalah kaum yang tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,128,'لَقَدْ جَاۤءَكُمْ رَسُوْلٌ مِّنْ اَنْفُسِكُمْ عَزِيْزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيْصٌ عَلَيْكُمْ بِالْمُؤْمِنِيْنَ رَءُوْفٌ رَّحِيْمٌ ','Sungguh telah datang kepadamu seorang Rasul dari kaummu sendiri, berat terasa olehnya penderitaanmu, sangat menginginkan (keimanan dan keselamatan) bagimu, amat belas kasihan lagi penyayang terhadap orang-orang mukmin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,9,129,'فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيْمِ ࣖ','Jika mereka berpaling (dari keimanan), maka katakanlah: "Cukuplah Allah bagiku; tidak ada Tuhan selain Dia. Hanya kepada-Nya aku bertawakkal dan Dia adalah Tuhan yang memiliki ´Arsy yang agung".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,1,'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِ ','Alif laam raa. Inilah ayat-ayat Al Quran yang mengandung hikmah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,2,'اَكَانَ لِلنَّاسِ عَجَبًا اَنْ اَوْحَيْنَآ اِلٰى رَجُلٍ مِّنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِيْنَ اٰمَنُوْٓا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ ۗ قَالَ الْكٰفِرُوْنَ اِنَّ هٰذَا لَسٰحِرٌ مُّبِيْنٌ ','Patutkah menjadi keheranan bagi manusia bahwa Kami mewahyukan kepada seorang laki-laki di antara mereka: "Berilah peringatan kepada manusia dan gembirakanlah orang-orang beriman bahwa mereka mempunyai kedudukan yang tinggi di sisi Tuhan mereka". Orang-orang kafir berkata: "Sesungguhnya orang ini (Muhammad) benar-benar adalah tukang sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,3,'اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۗ مَا مِنْ شَفِيْعٍ اِلَّا مِنْۢ بَعْدِ اِذْنِهٖۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوْهُۗ اَفَلَا تَذَكَّرُوْنَ ','Sesungguhnya Tuhan kamu ialah Allah Yang menciptakan langit dan bumi dalam enam masa, kemudian Dia bersemayam di atas ´Arsy untuk mengatur segala urusan. Tiada seorangpun yang akan memberi syafa´at kecuali sesudah ada izin-Nya. (Dzat) yang demikian itulah Allah, Tuhan kamu, maka sembahlah Dia. Maka apakah kamu tidak mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,4,'اِلَيْهِ مَرْجِعُكُمْ جَمِيْعًاۗ وَعْدَ اللّٰهِ حَقًّاۗ اِنَّهٗ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ بِالْقِسْطِۗ وَالَّذِيْنَ كَفَرُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ ','Hanya kepada-Nya-lah kamu semuanya akan kembali; sebagai janji yang benar daripada Allah, sesungguhnya Allah menciptakan makhluk pada permulaannya kemudian mengulanginya (menghidupkannya) kembali (sesudah berbangkit), agar Dia memberi pembalasan kepada orang-orang yang beriman dan yang mengerjakan amal saleh dengan adil. Dan untuk orang-orang kafir disediakan minuman air yang panas dan azab yang pedih disebabkan kekafiran mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,5,'هُوَ الَّذِيْ جَعَلَ الشَّمْسَ ضِيَاۤءً وَّالْقَمَرَ نُوْرًا وَّقَدَّرَهٗ مَنَازِلَ لِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۗ يُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ','Dialah yang menjadikan matahari bersinar dan bulan bercahaya dan ditetapkan-Nya manzilah-manzilah (tempat-tempat) bagi perjalanan bulan itu, supaya kamu mengetahui bilangan tahun dan perhitungan (waktu). Allah tidak menciptakan yang demikian itu melainkan dengan hak. Dia menjelaskan tanda-tanda (kebesaran-Nya) kepada orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,6,'اِنَّ فِى اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّتَّقُوْنَ ','Sesungguhnya pada pertukaran malam dan siang itu dan pada apa yang diciptakan Allah di langit dan di bumi, benar-benar terdapat tanda-tanda (kekuasaan-Nya) bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,7,'اِنَّ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا وَرَضُوْا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَـَٔنُّوْا بِهَا وَالَّذِيْنَ هُمْ عَنْ اٰيٰتِنَا غٰفِلُوْنَۙ','Sesungguhnya orang-orang yang tidak mengharapkan (tidak percaya akan) pertemuan dengan Kami, dan merasa puas dengan kehidupan dunia serta merasa tenteram dengan kehidupan itu dan orang-orang yang melalaikan ayat-ayat Kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,8,'اُولٰۤىِٕكَ مَأْوٰىهُمُ النَّارُ بِمَا كَانُوْا يَكْسِبُوْنَ ','mereka itu tempatnya ialah neraka, disebabkan apa yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,9,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ يَهْدِيْهِمْ رَبُّهُمْ بِاِيْمَانِهِمْۚ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ فِيْ جَنّٰتِ النَّعِيْمِ','Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh, mereka diberi petunjuk oleh Tuhan mereka karena keimanannya, di bawah mereka mengalir sungai-sungai di dalam surga yang penuh kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,10,'دَعْوٰىهُمْ فِيْهَا سُبْحٰنَكَ اللهم وَتَحِيَّتُهُمْ فِيْهَا سَلٰمٌۚ وَاٰخِرُ دَعْوٰىهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ','Do´a mereka di dalamnya ialah: "Subhanakallahumma", dan salam penghormatan mereka ialah: "Salam". Dan penutup doa mereka ialah: "Alhamdulilaahi Rabbil ´aalamin".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,11,'۞ وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۗ فَنَذَرُ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ','Dan kalau sekiranya Allah menyegerakan kejahatan bagi manusia seperti permintaan mereka untuk menyegerakan kebaikan, pastilah diakhiri umur mereka. Maka Kami biarkan orang-orang yang tidak mengharapkan pertemuan dengan Kami, bergelimangan di dalam kesesatan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,12,'وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْۢبِهٖٓ اَوْ قَاعِدًا اَوْ قَاۤىِٕمًا ۚفَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهٗ مَرَّ كَاَنْ لَّمْ يَدْعُنَآ اِلٰى ضُرٍّ مَّسَّهٗۗ  كَذٰلِكَ زُيِّنَ لِلْمُسْرِفِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ','Dan apabila manusia ditimpa bahaya dia berdoa kepada Kami dalam keadaan berbaring, duduk atau berdiri, tetapi setelah Kami hilangkan bahaya itu daripadanya, dia (kembali) melalui (jalannya yang sesat), seolah-olah dia tidak pernah berdoa kepada Kami untuk (menghilangkan) bahaya yang telah menimpanya. Begitulah orang-orang yang melampaui batas itu memandang baik apa yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,13,'وَلَقَدْ اَهْلَكْنَا الْقُرُوْنَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوْاۙ وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَمَا كَانُوْا لِيُؤْمِنُوْا ۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ ','Dan sesungguhnya Kami telah membinasakan umat-umat sebelum kamu, ketika mereka berbuat kezaliman, padahal rasul-rasul mereka telah datang kepada mereka dengan membawa keterangan-keterangan yang nyata, tetapi mereka sekali-kali tidak hendak beriman. Demikianlah Kami memberi pembalasan kepada orang-orang yang berbuat dosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,14,'ثُمَّ جَعَلْنٰكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِ مِنْۢ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُوْنَ ','Kemudian Kami jadikan kamu pengganti-pengganti (mereka) di muka bumi sesudah mereka, supaya Kami memperhatikan bagaimana kamu berbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,15,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنٰتٍۙ قَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَآ اَوْ بَدِّلْهُ ۗ قُلْ مَا يَكُوْنُ لِيْٓ اَنْ اُبَدِّلَهٗ مِنْ تِلْقَاۤئِ نَفْسِيْ ۚاِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ ۚ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ ','Dan apabila dibacakan kepada mereka ayat-ayat Kami yang nyata, orang-orang yang tidak mengharapkan pertemuan dengan Kami berkata: "Datangkanlah Al Quran yang lain dari ini atau gantilah dia". Katakanlah: "Tidaklah patut bagiku menggantinya dari pihak diriku sendiri. Aku tidak mengikut kecuali apa yang diwahyukan kepadaku. Sesungguhnya aku takut jika mendurhakai Tuhanku kepada siksa hari yang besar (kiamat)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,16,'قُلْ لَّوْ شَاۤءَ اللّٰهُ مَا تَلَوْتُهٗ عَلَيْكُمْ وَلَآ اَدْرٰىكُمْ بِهٖ ۖفَقَدْ لَبِثْتُ فِيْكُمْ عُمُرًا مِّنْ قَبْلِهٖۗ اَفَلَا تَعْقِلُوْنَ','Katakanlah: "Jikalau Allah menghendaki, niscaya aku tidak membacakannya kepadamu dan Allah tidak (pula) memberitahukannya kepadamu". Sesungguhnya aku telah tinggal bersamamu beberapa lama sebelumnya. Maka apakah kamu tidak memikirkannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,17,'فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الْمُجْرِمُوْنَ ','Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kedustaan terhadap Allah atau mendustakan ayat-ayat-Nya? Sesungguhnya, tiadalah beruntung orang-orang yang berbuat dosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,18,'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُوْلُوْنَ هٰٓؤُلَاۤءِ شُفَعَاۤؤُنَا عِنْدَ اللّٰهِ ۗقُلْ اَتُنَبِّـُٔوْنَ اللّٰهَ بِمَا لَا يَعْلَمُ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ','Dan mereka menyembah selain daripada Allah apa yang tidak dapat mendatangkan kemudharatan kepada mereka dan tidak (pula) kemanfaatan, dan mereka berkata: "Mereka itu adalah pemberi syafa´at kepada kami di sisi Allah". Katakanlah: "Apakah kamu mengabarkan kepada Allah apa yang tidak diketahui-Nya baik di langit dan tidak (pula) dibumi?" Maha Suci Allah dan Maha Tinggi dan apa yang mereka mempersekutukan (itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,19,'وَمَا كَانَ النَّاسُ اِلَّآ اُمَّةً وَّاحِدَةً فَاخْتَلَفُوْاۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيْمَا فِيْهِ يَخْتَلِفُوْنَ ','Manusia dahulunya hanyalah satu umat, kemudian mereka berselisih. Kalau tidaklah karena suatu ketetapan yang telah ada dari Tuhanmu dahulu, pastilah telah diberi keputusan di antara mereka, tentang apa yang mereka perselisihkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,20,'وَيَقُوْلُوْنَ لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُوْاۚ اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ࣖ ','Dan mereka berkata: "Mepada tidak diturunkan kepadanya (Muhammad) suatu keterangan (mukjizat) dari Tuhannya?" Maka katakanlah: "Sesungguhnya yang ghaib itu kepunyaan Allah, sebab itu tunggu (sajalah) olehmu, sesungguhnya aku bersama kamu termasuk orang-orang yang manunggu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,21,'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً مِّنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُمْ اِذَا لَهُمْ مَّكْرٌ فِيْٓ اٰيٰتِنَاۗ قُلِ اللّٰهُ اَسْرَعُ مَكْرًاۗ اِنَّ رُسُلَنَا يَكْتُبُوْنَ مَا تَمْكُرُوْنَ ','Dan apabila Kami merasakan kepada manusia suatu rahmat, sesudah (datangnya) bahaya menimpa mereka, tiba-tiba mereka mempunyai tipu daya dalam (menentang) tanda-tanda kekuasaan Kami. Katakanlah: "Allah lebih cepat pembalasannya (atas tipu daya itu)". Sesungguhnya malaikat-malaikat Kami menuliskan tipu dayamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,22,'هُوَ الَّذِيْ يُسَيِّرُكُمْ فِى الْبَرِّ وَالْبَحْرِۗ حَتّٰٓى اِذَا كُنْتُمْ فِىْ الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِرِيْحٍ طَيِّبَةٍ وَّفَرِحُوْا بِهَا جَاۤءَتْهَا رِيْحٌ عَاصِفٌ وَّجَاۤءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَّظَنُّوْٓا اَنَّهُمْ اُحِيْطَ بِهِمْۙ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ لَىِٕنْ اَنْجَيْتَنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ','Dialah Tuhan yang menjadikan kamu dapat berjalan di daratan, (berlayar) di lautan. Sehingga apabila kamu berada di dalam bahtera, dan meluncurlah bahtera itu membawa orang-orang yang ada di dalamnya dengan tiupan angin yang baik, dan mereka bergembira karenanya, datanglah angin badai, dan (apabila) gelombang dari segenap penjuru menimpanya, dan mereka yakin bahwa mereka telah terkepung (bahaya), maka mereka berdoa kepada Allah dengan mengikhlaskan ketaatan kepada-Nya semata-mata. (Mereka berkata): "Sesungguhnya jika Engkau menyelamatkan kami dari bahaya ini, pastilah kami akan termasuk orang-orang yang bersyukur".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,23,'فَلَمَّآ اَنْجٰىهُمْ اِذَا هُمْ يَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ ۗيٰٓاَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْ مَّتَاعَ الْحَيٰوةِ الدُّنْيَاۖ ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ','Maka tatkala Allah menyelamatkan mereka, tiba-tiba mereka membuat kezaliman di muka bumi tanpa (alasan) yang benar. Hai manusia, sesungguhnya (bencana) kezalimanmu akan menimpa dirimu sendiri; (hasil kezalimanmu) itu hanyalah kenikmatan hidup duniawi, kemudian kepada Kami-lah kembalimu, lalu Kami kabarkan kepadamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,24,'اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُ ۗحَتّٰٓى اِذَآ اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ اَهْلُهَآ اَنَّهُمْ قٰدِرُوْنَ عَلَيْهَآ اَتٰىهَآ اَمْرُنَا لَيْلًا اَوْ نَهَارًا فَجَعَلْنٰهَا حَصِيْدًا كَاَنْ لَّمْ تَغْنَ بِالْاَمْسِۗ  كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّتَفَكَّرُوْنَ ','Sesungguhnya perumpamaan kehidupan duniawi itu, adalah seperti air (hujan) yang Kami turunkan dan langit, lalu tumbuhlah dengan suburnya karena air itu tanam-tanaman bumi, di antaranya ada yang dimakan manusia dan binatang ternak. Hingga apabila bumi itu telah sempurna keindahannya, dan memakai (pula) perhiasannya, dan pemilik-permliknya mengira bahwa mereka pasti menguasasinya, tiba-tiba datanglah kepadanya azab Kami di waktu malam atau siang, lalu Kami jadikan (tanam-tanamannya) laksana tanam-tanaman yang sudah disabit, seakan-akan belum pernah tumbuh kemarin. Demikianlah Kami menjelaskan tanda-tanda kekuasaan (Kami) kepada orang-orang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,25,'وَاللّٰهُ يَدْعُوْٓ اِلٰى دَارِ السَّلٰمِ ۚوَيَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Allah menyeru (manusia) ke Darussalam (surga), dan menunjuki orang yang dikehendaki-Nya kepada jalan yang lurus (Islam).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,26,'۞ لِلَّذِيْنَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌ  ۗوَلَا يَرْهَقُ وُجُوْهَهُمْ قَتَرٌ وَّلَا ذِلَّةٌ  ۗاُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ هُمْ فِيْهَا خٰلِدُوْنَ ','Bagi orang-orang yang berbuat baik, ada pahala yang terbaik (surga) dan tambahannya. Dan muka mereka tidak ditutupi debu hitam dan tidak (pula) kehinaan. Mereka itulah penghuni surga, mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,27,'وَالَّذِيْنَ كَسَبُوا السَّيِّاٰتِ جَزَاۤءُ سَيِّئَةٍ ۢبِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌ  ۗمَا لَهُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ  كَاَنَّمَآ اُغْشِيَتْ وُجُوْهُهُمْ قِطَعًا مِّنَ الَّيْلِ مُظْلِمًاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚهُمْ فِيْهَا خٰلِدُوْنَ ','Dan orang-orang yang mengerjakan kejahatan (mendapat) balasan yang setimpal dan mereka ditutupi kehinaan. Tidak ada bagi mereka seorang pelindungpun dari (azab) Allah, seakan-akan muka mereka ditutupi dengan kepingan-kepingan malam yang gelap gelita. Mereka itulah penghuni neraka; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,28,'وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْا مَكَانَكُمْ اَنْتُمْ وَشُرَكَاۤؤُكُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَاۤؤُهُمْ مَّا كُنْتُمْ اِيَّانَا تَعْبُدُوْنَ','(Ingatlah) suatu hari (ketika itu). Kami mengumpulkan mereka semuanya, kemudian Kami berkata kepada orang-orang yang mempersekutukan (Tuhan): "Tetaplah kamu dan sekutu-sekutumu di tempatmu itu". Lalu Kami pisahkan mereka dan berkatalah sekutu-sekutu mereka: "Kamu sekali-kali tidak pernah menyembah kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,29,'فَكَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغٰفِلِيْنَ ','Dan cukuplah Allah menjadi saksi antara kami dengan kamu, bahwa kami tidak tahu-menahu tentang penyembahan kamu (kepada kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,30,'هُنَالِكَ تَبْلُوْا كُلُّ نَفْسٍ مَّآ اَسْلَفَتْ وَرُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ࣖ ','Di tempat itu (padang Mahsyar), tiap-tiap diri merasakan pembalasan dari apa yang telah dikerjakannya dahulu dan mereka dikembalikan kepada Allah Pelindung mereka yang sebenarnya dan lenyaplah dari mereka apa yang mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,31,'قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِ اَمَّنْ يَّمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُّخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُّدَبِّرُ الْاَمْرَۗ فَسَيَقُوْلُوْنَ اللّٰهُ ۚفَقُلْ اَفَلَا تَتَّقُوْنَ ','Katakanlah: "Siapakah yang memberi rezeki kepadamu dari langit dan bumi, atau siapakah yang kuasa (menciptakan) pendengaran dan penglihatan, dan siapakah yang mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan siapakah yang mengatur segala urusan?" Maka mereka akan menjawab: "Allah". Maka katakanlah "Mangapa kamu tidak bertakwa kepada-Nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,32,'فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلٰلُ  ۖفَاَنّٰى تُصْرَفُوْنَ ','Maka (Zat yang demikian) itulah Allah Tuhan kamu yang sebenarnya; maka tidak ada sesudah kebenaran itu, melainkan kesesatan. Maka bagaimanakah kamu dipalingkan (dari kebenaran)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,33,'كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ فَسَقُوْٓا اَنَّهُمْ لَا يُؤْمِنُوْنَ ','Demikianlah telah tetap hukuman Tuhanmu terhadap orang-orang yang fasik, karena sesungguhnya mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,34,'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗۗ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ فَاَنّٰى تُؤْفَكُوْنَ ','Katakanlah: "Apakah di antara sekutu-sekutumu ada yang dapat memulai penciptaan makhluk, kemudian mengulanginya (menghidupkannya) kembali?" katakanlah: "Allah-lah yang memulai penciptaan makhluk, kemudian mengulanginya (menghidupkannya) kembali; maka bagaimanakah kamu dipalingkan (kepada menyembah yang selain Allah)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,35,'قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّهْدِيْٓ اِلَى الْحَقِّۗ قُلِ اللّٰهُ يَهْدِيْ لِلْحَقِّۗ اَفَمَنْ يَّهْدِيْٓ اِلَى الْحَقِّ اَحَقُّ اَنْ يُّتَّبَعَ اَمَّنْ لَّا يَهِدِّيْٓ اِلَّآ اَنْ يُّهْدٰىۚ فَمَا لَكُمْۗ  كَيْفَ تَحْكُمُوْنَ ','Katakanlah: "Apakah di antara sekutu-sekuturmu ada yang menunjuki kepada kebenaran?" Katakanlah "Allah-lah yang menunjuki kepada kebenaran". Maka apakah orang-orang yang menunjuki kepada kebenaran itu lebih berhak diikuti ataukah orang yang tidak dapat memberi petunjuk kecuali (bila) diberi petunjuk? Mengapa kamu (berbuat demikian)? Bagaimanakah kamu mengambil keputusan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,36,'وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَنًّاۗ اِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَفْعَلُوْنَ ','Dan kebanyakan mereka tidak mengikuti kecuali persangkaan saja. Sesungguhnya persangkaan itu tidak sedikitpun berguna untuk mencapai kebenaran. Sesungguhnya Allah Maha Mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,37,'وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُّفْتَرٰى مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ  ','Tidaklah mungkin Al Quran ini dibuat oleh selain Allah; akan tetapi (Al Quran itu) membenarkan kitab-kitab yang sebelumnya dan menjelaskan hukum-hukum yang telah ditetapkannya, tidak ada keraguan di dalamnya, (diturunkan) dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,38,'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ فَأْتُوْا بِسُوْرَةٍ مِّثْلِهٖ وَادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ','Atau (patutkah) mereka mengatakan "Muhammad membuat-buatnya". Katakanlah: "(Kalau benar yang kamu katakan itu), maka cobalah datangkan sebuah surat seumpamanya dan panggillah siapa-siapa yang dapat kamu panggil (untuk membuatnya) selain Allah, jika kamu orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,39,'بَلْ كَذَّبُوْا بِمَا لَمْ يُحِيْطُوْا بِعِلْمِهٖ وَلَمَّا يَأْتِهِمْ تَأْوِيْلُهٗۗ  كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ','Bahkan yang sebenarnya, mereka mendustakan apa yang mereka belum mengetahuinya dengan sempurna padahal belum datang kepada mereka penjelasannya. Demikianlah orang-orang yang sebelum mereka telah mendustakan (rasul). Maka perhatikanlah bagaimana akibat orang-orang yang zalim itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,40,'وَمِنْهُمْ مَّنْ يُّؤْمِنُ بِهٖ وَمِنْهُمْ مَّنْ لَّا يُؤْمِنُ بِهٖۗ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِدِيْنَ ࣖ ','Di antara mereka ada orang-orang yang beriman kepada Al Quran, dan di antaranya ada (pula) orang-orang yang tidak beriman kepadanya. Tuhanmu lebih mengetahui tentang orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,41,'وَاِنْ كَذَّبُوْكَ فَقُلْ لِّيْ عَمَلِيْ وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَرِيْۤـُٔوْنَ مِمَّآ اَعْمَلُ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ','Jika mereka mendustakan kamu, maka katakanlah: "Bagiku pekerjaanku dan bagimu pekerjaanmu. Kamu berlepas diri terhadap apa yang aku kerjakan dan akupun berlepas diri terhadap apa yang kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,42,'وَمِنْهُمْ مَّنْ يَّسْتَمِعُوْنَ اِلَيْكَۗ اَفَاَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوْا لَا يَعْقِلُوْنَ ','Dan di antara mereka ada orang yang mendengarkanmu. Apakah kamu dapat menjadikan orang-orang tuli itu mendengar walaupun mereka tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,43,'وَمِنْهُمْ مَّنْ يَّنْظُرُ اِلَيْكَۗ اَفَاَنْتَ تَهْدِى الْعُمْيَ وَلَوْ كَانُوْا لَا يُبْصِرُوْنَ ','Dan di antara mereka ada orang yang melihat kepadamu, apakah dapat kamu memberi petunjuk kepada orang-orang yang buta, walaupun mereka tidak dapat memperhatikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,44,'اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـًٔا وَّلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُوْنَ','Sesungguhnya Allah tidak berbuat zalim kepada manusia sedikitpun, akan tetapi manusia itulah yang berbuat zalim kepada diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,45,'وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَّمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنَ النَّهَارِ يَتَعَارَفُوْنَ بَيْنَهُمْۗ قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ وَمَا كَانُوْا مُهْتَدِيْنَ ','Dan (ingatlah) akan hari (yang di waktu itu) Allah mengumpulkan mereka, (mereka merasa di hari itu) seakan-akan mereka tidak pernah berdiam (di dunia) hanya sesaat di siang hari, (di waktu itu) mereka saling berkenalan. Sesungguhnya rugilah orang-orang yang mendustakan pertemuan mereka dengan Allah dan mereka tidak mendapat petunjuk')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,46,'وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَهِيْدٌ عَلٰى مَا يَفْعَلُوْنَ ','Dan jika Kami perlihatkan kepadamu sebahagian dari (siksa) yang Kami ancamkan kepada mereka, (tentulah kamu akan melihatnya) atau (jika) Kami wafatkan kamu (sebelum itu), maka kepada Kami jualah mereka kembali, dan Allah menjadi saksi atas apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,47,'وَلِكُلِّ اُمَّةٍ رَّسُوْلٌ ۚفَاِذَا جَاۤءَ رَسُوْلُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ','Tiap-tiap umat mempunyai rasul; maka apabila telah datang rasul mereka, diberikanlah keputusan antara mereka dengan adil dan mereka (sedikitpun) tidak dianiaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,48,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Mereka mengatakan: "Bilakah (datangnya) ancaman itu, jika memang kamu orang-orang yang benar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,49,'قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ ضَرًّا وَّلَا نَفْعًا اِلَّا مَا شَاۤءَ اللّٰهُ ۗ لِكُلِّ اُمَّةٍ اَجَلٌ ۚاِذَا جَاۤءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ','Katakanlah: "Aku tidak berkuasa mendatangkan kemudharatan dan tidak (pula) kemanfaatan kepada diriku, melainkan apa yang dikehendaki Allah". Tiap-tiap umat mempunyai ajal. Apabila telah datang ajal mereka, maka mereka tidak dapat mengundurkannya barang sesaatpun dan tidak (pula) mendahulukan(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,50,'قُلْ اَرَءَيْتُمْ اِنْ اَتٰىكُمْ عَذَابُهٗ بَيَاتًا اَوْ نَهَارًا مَّاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُوْنَ','Katakanlah: "Terangkan kepadaku, jika datang kepada kamu sekalian sikaaan-Nya di waktu malam atau di siang hari, apakah orang-orang yang berdosa itu meminta disegerakan juga?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,51,'اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِهٖۗ  اٰۤلْـٰٔنَ وَقَدْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ ','Kemudian apakah setelah terjadinya (azab itu), kemudian itu kamu baru mempercayainya? Apakah sekarang (baru kamu mempercayai), padahal sebelumnya kamu selalu meminta supaya disegerakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,52,'ثُمَّ قِيْلَ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُوْنَ ','Kemudian dikatakan kepada orang-orang yang zalim (musyrik) itu: "Rasakanlah olehmu siksaan yang kekal; kamu tidak diberi balasan melainkan dengan apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,53,'وَيَسْتَنْۢبِـُٔوْنَكَ اَحَقٌّ هُوَ ۗ قُلْ اِيْ وَرَبِّيْٓ اِنَّهٗ لَحَقٌّ ۗوَمَآ اَنْتُمْ بِمُعْجِزِيْنَ ࣖ ','Dan mereka menanyakan kepadamu: "Benarkah (azab yang dijanjikan) itu? Katakanlah: "Ya, demi Tuhanku, sesungguhnya azab itu adalah benar dan kamu sekali-kali tidak bisa luput (daripadanya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,54,'وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِى الْاَرْضِ لَافْتَدَتْ بِهٖۗ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ','Dan kalau setiap diri yang zalim (muayrik) itu mempunyai segala apa yang ada di bumi ini, tentu dia menebus dirinya dengan itu, dan mereka membunyikan penyesalannya ketika mereka telah menyaksikan azab itu. Dan telah diberi keputusan di antara mereka dengan adil, sedang mereka tidak dianiaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,55,'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ  اَلَآ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Ingatlah, sesungguhnya kepunyaan Allah apa yang ada di langit dan di bumi. Ingatlah, sesungguhnya janji Allah itu benar, tetapi kebanyakan mereka tidak mengetahui(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,56,'هُوَ يُحْيٖ وَيُمِيْتُ وَاِلَيْهِ تُرْجَعُوْنَ ','Dialah yang menghidupkan dan mematikan dan hanya kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,57,'يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَتْكُمْ مَّوْعِظَةٌ مِّنْ رَّبِّكُمْ وَشِفَاۤءٌ لِّمَا فِى الصُّدُوْرِۙ وَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ','Hai manusia, sesungguhnya telah datang kepadamu pelajaran dari Tuhanmu dan penyembuh bagi penyakit-penyakit (yang berada) dalam dada dan petunjuk serta rahmat bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,58,'قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِهٖ فَبِذٰلِكَ فَلْيَفْرَحُوْاۗ هُوَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ','Katakanlah: "Dengan kurnia Allah dan rahmat-Nya, hendaklah dengan itu mereka bergembira. Kurnia Allah dan rahmat-Nya itu adalah lebih baik dari apa yang mereka kumpulkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,59,'قُلْ اَرَءَيْتُمْ مَّآ اَنْزَلَ اللّٰهُ لَكُمْ مِّنْ رِّزْقٍ فَجَعَلْتُمْ مِّنْهُ حَرَامًا وَّحَلٰلًا ۗ قُلْ اٰۤللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُوْنَ','Katakanlah: "Terangkanlah kepadaku tentang rezeki yang diturunkan Allah kepadamu, lalu kamu jadikan sebagiannya haram dan (sebagiannya) halal". Katakanlah: "Apakah Allah telah memberikan izin kepadamu (tentang ini) atau kamu mengada-adakan saja terhadap Allah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,60,'وَمَا ظَنُّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِ ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ ࣖ ','Apakah dugaan orang-orang yang mengada-adakan kebohongan terhadap Allah pada hari kiamat? Sesungguhnya Allah benar-benar mempunyai karunia (yang dilimpahkan) atas manusia, tetapi kebanyakan mereka tidak mensyukuri(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,61,'وَمَا تَكُوْنُ فِيْ شَأْنٍ وَّمَا تَتْلُوْا مِنْهُ مِنْ قُرْاٰنٍ وَّلَا تَعْمَلُوْنَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوْدًا اِذْ تُفِيْضُوْنَ فِيْهِۗ وَمَا يَعْزُبُ عَنْ رَّبِّكَ مِنْ مِّثْقَالِ ذَرَّةٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ وَلَآ اَصْغَرَ مِنْ ذٰلِكَ وَلَآ اَكْبَرَ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ','Kamu tidak berada dalam suatu keadaan dan tidak membaca suatu ayat dari Al Quran dan kamu tidak mengerjakan suatu pekerjaan, melainkan Kami menjadi saksi atasmu di waktu kamu melakukannya. Tidak luput dari pengetahuan Tuhanmu biarpun sebesar zarrah (atom) di bumi ataupun di langit. Tidak ada yang lebih kecil dan tidak (pula) yang lebih besar dari itu, melainkan (semua tercatat) dalam kitab yang nyata (Lauh Mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,62,'اَلَآ اِنَّ اَوْلِيَاۤءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ  ','Ingatlah, sesungguhnya wali-wali Allah itu, tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,63,'اَلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَۗ  ','(Yaitu) orang-orang yang beriman dan mereka selalu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,64,'لَهُمُ الْبُشْرٰى فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۗ  لَا تَبْدِيْلَ لِكَلِمٰتِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۗ  ','Bagi mereka berita gembira di dalam kehidupan di dunia dan (dalam kehidupan} di akhirat. Tidak ada perubahan bagi kalimat-kalimat (janji-janji) Allah. Yang demikian itu adalah kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,65,'وَلَا يَحْزُنْكَ قَوْلُهُمْۘ اِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ هُوَ السَّمِيْعُ الْعَلِيْمُ','Janganlah kamu sedih oleh perkataan mereka. Sesungguhnya kekuasaan itu seluruhnya adalah kepunyaan Allah. Dialah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,66,'اَلَآ اِنَّ لِلّٰهِ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِۗ وَمَا يَتَّبِعُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ شُرَكَاۤءَ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ','Ingatlah, sesungguhnya kepunyaan Allah semua yang ada di langit dan semua yang ada di bumi. Dan orang-orang yang menyeru sekutu-sekutu selain Allah, tidaklah mengikuti (suatu keyakinan). Mereka tidak mengikuti kecuali prasangka belaka, dan mereka hanyalah menduga-duga.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,67,'هُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ ','Dialah yang menjadikan malam bagi kamu supaya kamu beristirahat padanya dan (menjadikan) siang terang benderang (supaya kamu mencari karunia Allah). Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang mendengar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,68,'قَالُوا اتَّخَذَ اللّٰهُ وَلَدًا سُبْحٰنَهٗ ۗ هُوَ الْغَنِيُّ ۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اِنْ عِنْدَكُمْ مِّنْ سُلْطٰنٍۢ بِهٰذَاۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ','Mereka (orang-orang Yahudi dan Nasrani) berkata: "Allah mempuyai anak". Maha Suci Allah; Dialah Yang Maha Kaya; kepunyaan-Nya apa yang ada di langit dan apa yang di bumi. Kamu tidak mempunyai hujjah tentang ini. Pantaskah kamu mengatakan terhadap Allah apa yang tidak kamu ketahui?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,69,'قُلْ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ  ','Katakanlah: "Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tidak beruntung".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,70,'مَتَاعٌ فِى الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيْقُهُمُ الْعَذَابَ الشَّدِيْدَ بِمَا كَانُوْا يَكْفُرُوْنَ ࣖ ','(Bagi mereka) kesenangan (sementara) di dunia, kemudian kepada Kami-lah mereka kembali, kemudian Kami rasakan kepada mereka siksa yang berat, disebabkan kekafiran mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,71,'۞ وَاتْلُ عَلَيْهِمْ نَبَاَ نُوْحٍۘ اِذْ قَالَ لِقَوْمِهٖ يٰقَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَّقَامِيْ وَتَذْكِيْرِيْ بِاٰيٰتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُوْٓا اَمْرَكُمْ وَشُرَكَاۤءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوْٓا اِلَيَّ وَلَا تُنْظِرُوْنِ ','Dan bacakanIah kepada mereka berita penting tentang Nuh di waktu dia berkata kepada kaumnya: "Hai kaumku, jika terasa berat bagimu tinggal (bersamaku) dan peringatanku (kepadamu) dengan ayat-ayat Allah, maka kepada Allah-lah aku bertawakal, karena itu bulatkanlah keputusanmu dan (kumpulkanlah) sekutu-sekutumu (untuk membinasakanku). Kemudian janganlah keputusanmu itu dirahasiakan, lalu lakukanlah terhadap diriku, dan janganlah kamu memberi tangguh kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,72,'فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِّنْ اَجْرٍۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۙوَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ','Jika kamu berpaling (dari peringatanku), aku tidak meminta upah sedikitpun dari padamu. Upahku tidak lain hanyalah dari Allah belaka, dan aku disuruh supaya aku termasuk golongan orang-orang yang berserah diri (kepada-Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,73,'فَكَذَّبُوْهُ فَنَجَّيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ وَجَعَلْنٰهُمْ خَلٰۤىِٕفَ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَ ','Lalu mereka mendustakan Nuh, maka Kami selamatkan dia dan orang-orang yang bersamanya di dalam bahtera, dan Kami jadikan mereka itu pemegang kekuasaan dan Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Maka perhatikanlah bagaimana kesesudahan orang-orang yang diberi peringatan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,74,'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهٖ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا بِهٖ مِنْ قَبْلُ ۗ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوْبِ الْمُعْتَدِيْنَ ','Kemudian sesudah Nuh, Kami utus beberapa rasul kepada kaum mereka (masing-masing), maka rasul-rasul itu datang kepada mereka dengan membawa keterangan-keterangan yang nyata, tetapi mereka tidak hendak beriman karena mereka dahulu telah (biasa) mendustakannya. Demikianlah Kami mengunci mati hati orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,75,'ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى وَهٰرُوْنَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ بِاٰيٰتِنَا فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ ','Kemudian sesudah rasul-rasul itu, Kami utus Musa dan Harun kepada Fir´aun dan pemuka-pemuka kaumnya, dengan (membawa) tanda-tanda (mukjizat-mukjizat) Kami, maka mereka menyombongkan diri dan mereka adalah orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,76,'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْٓا اِنَّ هٰذَا لَسِحْرٌ مُّبِيْنٌ ','Dan tatkala telah datang kepada mereka kebenaran dari sisi Kami, mereka berkata: "Sesungguhnya ini adalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,77,'قَالَ مُوْسٰٓى اَتَقُوْلُوْنَ لِلْحَقِّ لَمَّا جَاۤءَكُمْ ۗ اَسِحْرٌ هٰذَاۗ وَلَا يُفْلِحُ السَّاحِرُوْنَ ','Musa berkata: "Apakah kamu mengatakan terhadap kebenaran waktu ia datang kepadamu, sihirkah ini?" padahal ahli-ahli sihir itu tidaklah mendapat kemenangan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,78,'قَالُوْٓا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا وَتَكُوْنَ لَكُمَا الْكِبْرِيَاۤءُ فِى الْاَرْضِۗ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِيْنَ ','Mereka berkata: "Apakah kamu datang kepada kami untuk memalingkan kami dari apa yang kami dapati nenek moyang kami mengerjakannya, dan supaya kamu berdua mempunyai kekuasaan di muka bumi? Kami tidak akan mempercayai kamu berdua".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,79,'وَقَالَ فِرْعَوْنُ ائْتُوْنِيْ بِكُلِّ سٰحِرٍ عَلِيْمٍ ','Fir´aun berkata (kepada pemuka kaumnya): "Datangkanlah kepadaku semua ahli-ahli sihir yang pandai!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,80,'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ ','Maka tatkala ahli-ahli sihir itu datang, Musa berkata kepada mereka: "Lemparkanlah apa yang hendak kamu lemparkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,81,'فَلَمَّآ اَلْقَوْا قَالَ مُوْسٰى مَا جِئْتُمْ بِهِ ۙالسِّحْرُۗ اِنَّ اللّٰهَ سَيُبْطِلُهٗۗ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِيْنَ ࣖ ','Maka setelah mereka lemparkan, Musa berkata: "Apa yang kamu lakukan itu, itulah yang sihir, sesungguhnya Allah akan menampakkan ketidak benarannya" Sesungguhnya Allah tidak akan membiarkan terus berlangsungnya pekerjaan orang-yang membuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,82,'وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمٰتِهٖ وَلَوْ كَرِهَ الْمُجْرِمُوْنَ ','Dan Allah akan mengokohkan yang benar dengan ketetapan-Nya, walaupun orang-orang yang berbuat dosa tidak menyukai(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,83,'فَمَآ اٰمَنَ لِمُوْسٰىٓ اِلَّا ذُرِّيَّةٌ مِّنْ قَوْمِهٖ عَلٰى خَوْفٍ مِّنْ فِرْعَوْنَ وَمَلَا۟ىِٕهِمْ اَنْ يَّفْتِنَهُمْ ۗوَاِنَّ فِرْعَوْنَ لَعَالٍ فِى الْاَرْضِۚ وَاِنَّهٗ لَمِنَ الْمُسْرِفِيْنَ ','Maka tidak ada yang beriman kepada Musa, melainkan pemuda-pemuda dari kaumnya (Musa) dalam keadaan takut bahwa Fir´aun dan pemuka-pemuka kaumnya akan menyiksa mereka. Sesungguhnya Fir´aun itu berbuat sewenang-wenang di muka bumi. Dan sesungguhnya dia termasuk orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,84,'وَقَالَ مُوْسٰى يٰقَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّسْلِمِيْنَ ','Berkata Musa: "Hai kaumku, jika kamu beriman kepada Allah, maka bertawakkallah kepada-Nya saja, jika kamu benar-benar orang yang berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,85,'فَقَالُوْا عَلَى اللّٰهِ تَوَكَّلْنَا ۚرَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلْقَوْمِ الظّٰلِمِيْنَ ','Lalu mereka berkata: "Kepada Allahlah kami bertawakkal! Ya Tuhan kami; janganlah Engkau jadikan kami sasaran fitnah bagi kaum yang´zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,86,'وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكٰفِرِيْنَ ','dan selamatkanlah kami dengan rahmat Engkau dari (tipu daya) orang-orang yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,87,'وَاَوْحَيْنَآ اِلٰى مُوْسٰى وَاَخِيْهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوْتًا وَّاجْعَلُوْا بُيُوْتَكُمْ قِبْلَةً وَّاَقِيْمُوا الصَّلٰوةَۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ','Dan Kami wahyukan kepada Musa dan saudaranya: "Ambillah olehmu berdua beberapa buah rumah di Mesir untuk tempat tinggal bagi kaummu dan jadikanlah olehmu rumah-rumahmu itu tempat shalat dan dirikanlah olehmu sembahyang serta gembirakanlah orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,88,'وَقَالَ مُوْسٰى رَبَّنَآ اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهٗ زِيْنَةً وَّاَمْوَالًا فِى الْحَيٰوةِ الدُّنْيَاۗ رَبَّنَا لِيُضِلُّوْا عَنْ سَبِيْلِكَ ۚرَبَّنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوْبِهِمْ فَلَا يُؤْمِنُوْا حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ','Musa berkata: "Ya Tuhan kami, sesungguhnya Engkau telah memberi kepada Fir´aun dan pemuka-pemuka kaumnya perhiasan dan harta kekayaan dalam kehidupan dunia, ya Tuhan Kami -- akibatnya mereka menyesatkan (manusia) dari jalan Engkau. Ya Tuhan kami, binasakanlah harta benda mereka, dan kunci matilah hati mereka, maka mereka tidak beriman hingga mereka melihat siksaan yang pedih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,89,'قَالَ قَدْ اُجِيْبَتْ دَّعْوَتُكُمَا فَاسْتَقِيْمَا وَلَا تَتَّبِعٰۤنِّ سَبِيْلَ الَّذِيْنَ لَا يَعْلَمُوْنَ ','AlIah berfirman: "Sesungguhnya telah diperkenankan permohonan kamu berdua, sebab itu tetaplah kamu berdua pada jalan yang lurus dan janganlah sekali-kali kamu mengikuti jalan orang-orang yang tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,90,'۞ وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُوْدُهٗ بَغْيًا وَّعَدْوًا ۗحَتّٰىٓ اِذَآ اَدْرَكَهُ الْغَرَقُ قَالَ اٰمَنْتُ اَنَّهٗ لَآ اِلٰهَ اِلَّا الَّذِيْٓ اٰمَنَتْ بِهٖ بَنُوْٓا اِسْرَاۤءِيْلَ وَاَنَا۠ مِنَ الْمُسْلِمِيْنَ','Dan Kami memungkinkan Bani Israil melintasi laut, lalu mereka diikuti oleh Fir´aun dan bala tentaranya, karena hendak menganiaya dan menindas (mereka); hingga bila Fir´aun itu telah hampir tenggelam berkatalah dia: "Saya percaya bahwa tidak ada Tuhan melainkan Tuhan yang dipercayai oleh Bani Israil, dan saya termasuk orang-orang yang berserah diri (kepada Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,91,'اٰۤلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِيْنَ','Apakah sekarang (baru kamu percaya), padahal sesungguhnya kamu telah durhaka sejak dahulu, dan kamu termasuk orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,92,'فَالْيَوْمَ نُنَجِّيْكَ بِبَدَنِكَ لِتَكُوْنَ لِمَنْ خَلْفَكَ اٰيَةً ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ عَنْ اٰيٰتِنَا لَغٰفِلُوْنَ ','Maka pada hari ini Kami selamatkan badanmu supaya kamu dapat menjadi pelajaran bagi orang-orang yang datang sesudahmu dan sesungguhnya kebanyakan dari manusia lengah dari tanda-tanda kekuasaan Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,93,'وَلَقَدْ بَوَّأْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مُبَوَّاَ صِدْقٍ وَّرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ ۚفَمَا اخْتَلَفُوْا حَتّٰى جَاۤءَهُمُ الْعِلْمُ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ','Dan sesungguhnya Kami telah menempatkan Bani Israil di ternpat kediaman yang bagus dan Kami beri mereka rezeki dari yang baik-baik. Maka mereka tidak berselisih, kecuali setelah datang kepada mereka pengetahuan (yang tersebut dalam Taurat). Sesungguhnya Tuhan kamu akan memutuskan antara mereka di hari kiamat tentang apa yang mereka perselisihkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,94,'فَاِنْ كُنْتَ فِيْ شَكٍّ مِّمَّآ اَنْزَلْنَآ اِلَيْكَ فَسْـَٔلِ الَّذِيْنَ يَقْرَءُوْنَ الْكِتٰبَ مِنْ قَبْلِكَ ۚ لَقَدْ جَاۤءَكَ الْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَۙ','Maka jika kamu (Muhammad) berada dalam keragu-raguan tentang apa yang Kami turunkan kepadamu, maka tanyakanlah kepada orang-orang yang membaca kitab sebelum kamu. Sesungguhnya telah datang kebenaran kepadamu dari Tuhanmu, sebab itu janganlah sekali-kali kamu temasuk orang-orang yang ragu-ragu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,95,'وَلَا تَكُوْنَنَّ مِنَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ فَتَكُوْنَ مِنَ الْخٰسِرِيْنَ ','Dan sekali-kali janganlah kamu termasuk orang-orang yang mendustakan ayat-ayat Allah yang menyebabkan kamu termasuk orang-orang yang rugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,96,'اِنَّ الَّذِيْنَ حَقَّتْ عَلَيْهِمْ كَلِمَةُ رَبِّكَ لَا يُؤْمِنُوْنَ ','Sesungguhnya orang-orang yang telah pasti terhadap mereka kalimat Tuhanmu, tidaklah akan beriman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,97,'وَلَوْ جَاۤءَتْهُمْ كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ','meskipun datang kepada mereka segala macam keterangan, hingga mereka menyaksikan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,98,'فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَآ اِيْمَانُهَآ اِلَّا قَوْمَ يُوْنُسَۗ  لَمَّآ اٰمَنُوْا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ ','Dan mengapa tidak ada (penduduk) suatu kota yang beriman, lalu imannya itu bermanfaat kepadanya selain kaum Yunus? Tatkala mereka (kaum Yunus itu), beriman, Kami hilangkan dari mereka azab yang menghinakan dalam kehidupan dunia, dan Kami beri kesenangan kepada mereka sampai kepada waktu yang tertentu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,99,'وَلَوْ شَاۤءَ رَبُّكَ لَاٰمَنَ مَنْ فِى الْاَرْضِ كُلُّهُمْ جَمِيْعًاۗ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُوْنُوْا مُؤْمِنِيْنَ ','Dan jikalau Tuhanmu menghendaki, tentulah beriman semua orang yang di muka bumi seluruhnya. Maka apakah kamu (hendak) memaksa manusia supaya mereka menjadi orang-orang yang beriman semuanya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,100,'وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِ ۗوَيَجْعَلُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يَعْقِلُوْنَ ','Dan tidak ada seorangpun akan beriman kecuali dengan izin Allah; dan Allah menimpakan kemurkaan kepada orang-orang yang tidak mempergunakan akalnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,101,'قُلِ انْظُرُوْا مَاذَا فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَمَا تُغْنِى الْاٰيٰتُ وَالنُّذُرُ عَنْ قَوْمٍ لَّا يُؤْمِنُوْنَ ','Katakanlah: "Perhatikanlah apa yaag ada di langit dan di bumi. Tidaklah bermanfaat tanda kekuasaan Allah dan rasul-rasul yang memberi peringatan bagi orang-orang yang tidak beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,102,'فَهَلْ يَنْتَظِرُوْنَ اِلَّا مِثْلَ اَيَّامِ الَّذِيْنَ خَلَوْا مِنْ قَبْلِهِمْۗ قُلْ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ','Mereka tidak menunggu-nunggu kecuali (kejadian-kejadian) yang sama dengan kejadian-kejadian (yang menimpa) orang-orang yang telah terdahulu sebelum mereka. Katakanlah: "Maka tunggulah, sesungguhnya akupun termasuk orang-orang yang menunggu bersama kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,103,'ثُمَّ نُنَجِّيْ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا كَذٰلِكَ ۚحَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِيْنَ ࣖ ','Kemudian Kami selamatkan rasul-rasul Kami dan orang-orang yang beriman, demikianlah menjadi kewajiban atas Kami menyelamatkan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,104,'قُلْ يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ شَكٍّ مِّنْ دِيْنِيْ فَلَآ اَعْبُدُ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذِيْ يَتَوَفّٰىكُمْ ۖ وَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُؤْمِنِيْنَ','Katakanlah: "Hai manusia, jika kamu masih dalam keragu-raguan tentang agamaku, maka (ketahuilah) aku tidak menyembah yang kamu sembah selain Allah, tetapi aku menyembah Allah yang akan mematikan kamu dan aku telah diperintah supaya termasuk orang-orang yang beriman",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,105,'وَاَنْ اَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۚ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ','dan (aku telah diperintah): "Hadapkanlah mukamu kepada agama dengan tulus dan ikhlas dan janganlah kamu termasuk orang-orang yang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,106,'وَلَا تَدْعُ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَ ۚفَاِنْ فَعَلْتَ فَاِنَّكَ اِذًا مِّنَ الظّٰلِمِيْنَ ','Dan janganlah kamu menyembah apa-apa yang tidak memberi manfaat dan tidak (pula) memberi mudharat kepadamu selain Allah; sebab jika kamu berbuat (yang demikian), itu, maka sesungguhnya kamu kalau begitu termasuk orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,107,'وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗ ٓاِلَّا هُوَ ۚوَاِنْ يُّرِدْكَ بِخَيْرٍ فَلَا رَاۤدَّ لِفَضْلِهٖۗ يُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَهُوَ الْغَفُوْرُ الرَّحِيْمُ ','Jika Allah menimpakan sesuatu kemudharatan kepadamu, maka tidak ada yang dapat menghilangkannya kecuali Dia. Dan jika Allah menghendaki kebaikan bagi kamu, maka tak ada yang dapat menolak kurnia-Nya. Dia memberikan kebaikan itu kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan Dialah Yang Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,108,'قُلْ يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الْحَقُّ مِنْ رَّبِّكُمْ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖ ۚوَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنَا۠ عَلَيْكُمْ بِوَكِيْلٍۗ  ','Katakanlah: "Hai manusia, sesungguhnya teIah datang kepadamu kebenaran (Al Quran) dari Tuhanmu, sebab itu barangsiapa yang mendapat petunjuk maka sesungguhnya (petunjuk itu) untuk kebaikan dirinya sendiri. Dan barangsiapa yang sesat, maka sesungguhnya kesesatannya itu mencelakakan dirinya sendiri. Dan aku bukanlah seorang penjaga terhadap dirimu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,10,109,'وَاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُ ۚوَهُوَ خَيْرُ الْحٰكِمِيْنَ ࣖ ','Dan ikutilah apa yang diwahyukan kepadamu, dan bersabarlah hingga Allah memberi keputusan dan Dia adalah Hakim yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,11,1,'الۤرٰ ۗ كِتٰبٌ اُحْكِمَتْ اٰيٰتُهٗ ثُمَّ فُصِّلَتْ مِنْ لَّدُنْ حَكِيْمٍ خَبِيْرٍۙ','Alif laam raa, (inilah) suatu kitab yang ayat-ayatnya disusun dengan rapi serta dijelaskan secara terperinci, yang diturunkan dari sisi (Allah) Yang Maha Bijaksana lagi Maha Tahu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,11,2,'اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنَّنِيْ لَكُمْ مِّنْهُ نَذِيْرٌ وَّبَشِيْرٌۙ ','agar kamu tidak menyembah selain Allah. Sesungguhnya aku (Muhammad) adalah pemberi peringatan dan pembawa khabar gembira kepadamu daripada-Nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,11,3,'وَّاَنِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُمَتِّعْكُمْ مَّتَاعًا حَسَنًا اِلٰٓى اَجَلٍ مُّسَمًّى وَّيُؤْتِ كُلَّ ذِيْ فَضْلٍ فَضْلَهٗ ۗوَاِنْ تَوَلَّوْا فَاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيْرٍ','dan hendaklah kamu meminta ampun kepada Tuhanmu dan bertaubat kepada-Nya. (Jika kamu mengerjakan yang demikian), niscaya Dia akan memberi kenikmatan yang baik (terus menerus) kepadamu sampai kepada waktu yang telah ditentukan dan Dia akan memberikan kepada tiap-tiap orang yang mempunyai keutamaan (balasan) keutamaannya. Jika kamu berpaling, maka sesungguhnya aku takut kamu akan ditimpa siksa hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,11,4,'اِلَى اللّٰهِ مَرْجِعُكُمْ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Kepada Allah-lah kembalimu, dan Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,11,11,5,'اَلَآ اِنَّهُمْ يَثْنُوْنَ صُدُوْرَهُمْ لِيَسْتَخْفُوْا مِنْهُۗ  اَلَا حِيْنَ يَسْتَغْشُوْنَ ثِيَابَهُمْ ۙيَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَۚ اِنَّهٗ عَلِيْمٌ ۢ بِذَاتِ الصُّدُوْرِ  ۔','Ingatlah, sesungguhnya (orang munafik itu) memalingkan dada mereka untuk menyembunyikan diri daripadanya (Muhammad). Ingatlah, di waktu mereka menyelimuti dirinya dengan kain, Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka lahirkan, sesungguhnya Allah Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,6,'۞ وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۗ كُلٌّ فِيْ كِتٰبٍ مُّبِيْنٍ','Dan tidak ada suatu binatang melata pun di bumi melainkan Allah-lah yang memberi rezekinya, dan Dia mengetahui tempat berdiam binatang itu dan tempat penyimpanannya. Semuanya tertulis dalam Kitab yang nyata (Lauh mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,7,'وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ وَّكَانَ عَرْشُهٗ عَلَى الْمَاۤءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًا ۗوَلَىِٕنْ قُلْتَ اِنَّكُمْ مَّبْعُوْثُوْنَ مِنْۢ بَعْدِ الْمَوْتِ لَيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُّبِيْنٌ','Dan Dialah yang menciptakan langit dan bumi dalam enam masa, dan adalah singgasana-Nya (sebelum itu) di atas air, agar Dia menguji siapakah di antara kamu yang lebih baik amalnya, dan jika kamu berkata (kepada penduduk Mekah): "Sesungguhnya kamu akan dibangkitkan sesudah mati", niscaya orang-orang yang kafir itu akan berkata: "Ini tidak lain hanyalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,8,'وَلَىِٕنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَّعْدُوْدَةٍ لَّيَقُوْلُنَّ مَا يَحْبِسُهٗ ۗ اَلَا يَوْمَ يَأْتِيْهِمْ لَيْسَ مَصْرُوْفًا عَنْهُمْ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ ','Dan sesungguhnya jika Kami undurkan azab dari mereka sampai kepada suatu waktu yang ditentukan. niscaya mereka akan berkata: "Apakah yang menghalanginya?" lngatlah, diwaktu azab itu datang kepada mereka tidaklah dapat dipalingkan dari mereka dan mereka diliputi oleh azab yang dahulunya mereka selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,9,'وَلَىِٕنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنٰهَا مِنْهُۚ اِنَّهٗ لَيَـُٔوْسٌ كَفُوْرٌ','Dan jika Kami rasakan kepada manusia suatu rahmat (nikmat) dari Kami, kemudian rahmat itu Kami cabut daripadanya, pastilah dia menjadi putus asa lagi tidak berterima kasih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,10,'وَلَىِٕنْ اَذَقْنٰهُ نَعْمَاۤءَ بَعْدَ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ ذَهَبَ السَّيِّاٰتُ عَنِّيْ ۗاِنَّهٗ لَفَرِحٌ فَخُوْرٌۙ  ','Dan jika Kami rasakan kepadanya kebahagiaan sesudah bencana yang menimpanya, niscaya dia akan berkata: "Telah hilang bencana-bencana itu daripadaku"; sesungguhnya dia sangat gembira lagi bangga,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,11,'اِلَّا الَّذِيْنَ صَبَرُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ','kecuali orang-orang yang sabar (terhadap bencana), dan mengerjakan amal-amal saleh; mereka itu beroleh ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,12,'فَلَعَلَّكَ تَارِكٌۢ بَعْضَ مَا يُوْحٰىٓ اِلَيْكَ وَضَاۤىِٕقٌۢ بِهٖ صَدْرُكَ اَنْ يَّقُوْلُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَاۤءَ مَعَهٗ مَلَكٌ  ۗاِنَّمَآ اَنْتَ نَذِيْرٌ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ  ۗ','Maka boleh jadi kamu hendak meninggalkan sebahagian dari apa yang diwahyukan kepadamu dan sempit karenanya dadamu, karena khawatir bahwa mereka akan mengatakan: "Mengapa tidak diturunkan kepadanya perbendaharaan (kekayaan) atau datang bersama-sama dengan dia seorang malaikat?" Sesungguhnya kamu hanyalah seorang pemberi peringatan dan Allah Pemelihara segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,13,'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗقُلْ فَأْتُوْا بِعَشْرِ سُوَرٍ مِّثْلِهٖ مُفْتَرَيٰتٍ وَّادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Bahkan mereka mengatakan: "Muhammad telah membuat-buat Al Quran itu", Katakanlah: "(Kalau demikian), maka datangkanlah sepuluh surat-surat yang dibuat-buat yang menyamainya, dan panggillah orang-orang yang kamu sanggup (memanggilnya) selain Allah, jika kamu memang orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,14,'فَاِلَّمْ يَسْتَجِيْبُوْا لَكُمْ فَاعْلَمُوْٓا اَنَّمَآ اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَّآ اِلٰهَ اِلَّا هُوَ ۚفَهَلْ اَنْتُمْ مُّسْلِمُوْنَ','Jika mereka yang kamu seru itu tidak menerima seruanmu (ajakanmu) itu maka ketahuilah, sesungguhnya Al Quran itu diturunkan dengan ilmu Allah, dan bahwasanya tidak ada Tuhan selain Dia, maka maukah kamu berserah diri (kepada Allah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,15,'مَنْ كَانَ يُرِيْدُ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ فِيْهَا وَهُمْ فِيْهَا لَا يُبْخَسُوْنَ','Barangsiapa yang menghendaki kehidupan dunia dan perhiasannya, niscaya Kami berikan kepada mereka balasan pekerjaan mereka di dunia dengan sempurna dan mereka di dunia itu tidak akan dirugikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,16,'اُولٰۤىِٕكَ الَّذِيْنَ لَيْسَ لَهُمْ فِى الْاٰخِرَةِ اِلَّا النَّارُ ۖوَحَبِطَ مَا صَنَعُوْا فِيْهَا وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ','Itulah orang-orang yang tidak memperoleh di akhirat, kecuali neraka dan lenyaplah di akhirat itu apa yang telah mereka usahakan di dunia dan sia-sialah apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,17,'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ وَيَتْلُوْهُ شَاهِدٌ مِّنْهُ وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰىٓ اِمَامًا وَّرَحْمَةًۗ  اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗوَمَنْ يَّكْفُرْ بِهٖ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهٗ فَلَا تَكُ فِيْ مِرْيَةٍ مِّنْهُ اِنَّهُ الْحَقُّ مِنْ رَّبِّكَ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ','Apakah (orang-orang kafir itu sama dengan) orang-orang yang ada mempunyai bukti yang nyata (Al Quran) dari Tuhannya, dan diikuti pula oleh seorang saksi (Muhammad) dari Allah dan sebelum Al Quran itu telah ada Kitab Musa yang menjadi pedoman dan rahmat? Mereka itu beriman kepada Al Quran. Dan barangsiapa di antara mereka (orang-orang Quraisy) dan sekutu-sekutunya yang kafir kepada Al Quran, maka nerakalah tempat yang diancamkan baginya, karena itu janganlah kamu ragu-ragu terhadap Al Quran itu. Sesungguhnya (Al Quran) itu benar-benar dari Tuhanmu, tetapi kebanyakan manusia tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,18,'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ اُولٰۤىِٕكَ يُعْرَضُوْنَ عَلٰى رَبِّهِمْ وَيَقُوْلُ الْاَشْهَادُ هٰٓؤُلَاۤءِ الَّذِيْنَ كَذَبُوْا عَلٰى رَبِّهِمْۚ  اَلَا لَعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ۙ  ','Dan siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah? Mereka itu akan dihadapkan kepada Tuhan mereka, dan para saksi akan berkata: "Orang-orang inilah yang telah berdusta terhadap Tuhan mereka". Ingatlah, kutukan Allah (ditimpakan) atas orang-orang yang zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,19,'الَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۗ وَهُمْ بِالْاٰخِرَةِ هُمْ كفِٰرُوْنَ','(yaitu) orang-orang yang menghalangi (manusia) dari jalan Allah dan menghendaki (supaya) jalan itu bengkok. Dan mereka itulah orang-orang yang tidak percaya akan adanya hari akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,20,'اُولٰۤىِٕكَ لَمْ يَكُوْنُوْا مُعْجِزِيْنَ فِى الْاَرْضِ وَمَا كَانَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ۘ يُضٰعَفُ لَهُمُ الْعَذَابُ ۗمَا كَانُوْا يَسْتَطِيْعُوْنَ السَّمْعَ وَمَا كَانُوْا يُبْصِرُوْنَ','Orang-orang itu tidak mampu menghalang-halangi Allah untuk (mengazab mereka) di bumi ini, dan sekali-kali tidak adalah bagi mereka penolong selain Allah. Siksaan itu dilipat gandakan kepada mereka. Mereka selalu tidak dapat mendengar (kebenaran) dan mereka selalu tidak dapat melihat(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,21,'اُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ','Mereka itulah orang-orang yang merugikan dirinya sendiri, dan lenyaplah dari mereka apa yang selalu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,22,'لَاجَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ','Pasti mereka itu di akhirat menjadi orang-orang yang paling merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,23,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَخْبَتُوْٓا اِلٰى رَبِّهِمْۙ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ','Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh dan merendahkan diri kepada Tuhan mereka, mereka itu adalah penghuni-penghuni surga; mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,24,'۞ مَثَلُ الْفَرِيْقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَصِيْرِ وَالسَّمِيْعِۗ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَفَلَا تَذَكَّرُوْنَ ࣖ ','Perbandingan kedua golongan itu (orang-orang kafir dan orang-orang mukmin), seperti orang buta dan tuli dengan orang yang dapat melihat dan dapat mendengar. Adakah kedua golongan itu sama keadaan dan sifatnya? Maka tidakkah kamu mengambil pelajaran (daripada perbandingan itu)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,25,'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۙ','Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, (dia berkata): "Sesungguhnya aku adalah pemberi peringatan yang nyata bagi kamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,26,'اَنْ لَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۖاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَلِيْمٍ','agar kamu tidak menyembah selain Allah. Sesungguhnya aku takut kamu akan ditimpa azab (pada) hari yang sangat menyedihkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,27,'فَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا نَرٰىكَ اِلَّا بَشَرًا مِّثْلَنَا وَمَا نَرٰىكَ اتَّبَعَكَ اِلَّا الَّذِيْنَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍۢ بَلْ نَظُنُّكُمْ كٰذِبِيْنَ','Maka berkatalah pemimpin-pemimpin yang kafir dari kaumnya: "Kami tidak melihat kamu, melainkan (sebagai) seorang manusia (biasa) seperti kami, dan kami tidak melihat orang-orang yang mengikuti kamu, melainkan orang-orang yang hina dina di antara kami yang lekas percaya saja, dan kami tidak melihat kamu memiliki sesuatu kelebihan apapun atas kami, bahkan kami yakin bahwa kamu adalah orang-orang yang dusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,28,'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَاٰتٰىنِيْ رَحْمَةً مِّنْ عِنْدِهٖ فَعُمِّيَتْ عَلَيْكُمْۗ اَنُلْزِمُكُمُوْهَا وَاَنْتُمْ لَهَا كٰرِهُوْنَ','Berkata Nuh: "Hai kaumku, bagaimana pikiranmu, jika aku ada mempunyai bukti yang nyata dari Tuhanku, dan diberinya aku rahmat dari sisi-Nya, tetapi rahmat itu disamarkan bagimu. Apa akan kami paksakankah kamu menerimanya, padahal kamu tiada menyukainya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,29,'وَيٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ مَالًاۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَآ اَنَا۠ بِطَارِدِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ','Dan (dia berkata): "Hai kaumku, aku tiada meminta harta benda kepada kamu (sebagai upah) bagi seruanku. Upahku hanyalah dari Allah dan aku sekali-kali tidak akan mengusir orang-orang yang telah beriman. Sesungguhnya mereka akan bertemu dengan Tuhannya, akan tetapi aku memandangmu suatu kaum yang tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,30,'وَيٰقَوْمِ مَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ طَرَدْتُّهُمْ ۗ اَفَلَا تَذَكَّرُوْنَ','Dan (dia berkata): "Hai kaumku, siapakah yang akan menolongku dari (azab) Allah jika aku mengusir mereka. Maka tidakkah kamu mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,31,'وَلَآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ اِنِّيْ مَلَكٌ وَّلَآ اَقُوْلُ لِلَّذِيْنَ تَزْدَرِيْٓ اَعْيُنُكُمْ لَنْ يُّؤْتِيَهُمُ اللّٰهُ خَيْرًا ۗ اَللّٰهُ اَعْلَمُ بِمَا فِيْٓ اَنْفُسِهِمْ ۚاِنِّيْٓ اِذًا لَّمِنَ الظّٰلِمِيْنَ','Dan aku tidak mengatakan kepada kamu (bahwa): "Aku mempunyai gudang-gudang rezeki dan kekayaan dari Allah, dan aku tiada mengetahui yang ghaib", dan tidak (pula) aku mengatakan: "Bahwa sesungguhnya aku adalah malaikat", dan tidak juga aku mengatakan kepada orang-orang yang dipandang hina oleh penglihatanmu: "Sekali-kali Allah tidak akan mendatangkan kebaikan kepada mereka". Allah lebih mengetahui apa yang ada pada diri mereka; sesungguhnya aku, kalau begitu benar-benar termasuk orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,32,'قَالُوْا يٰنُوْحُ قَدْ جَادَلْتَنَا فَاَ كْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ','Mereka berkata "Hai Nuh, sesungguhnya kamu telah berbantah dengan kami, dan kamu telah memperpanjang bantahanmu terhadap kami, maka datangkanlah kepada kami azab yang kamu ancamkan kepada kami, jika kamu termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,33,'قَالَ اِنَّمَا يَأْتِيْكُمْ بِهِ اللّٰهُ اِنْ شَاۤءَ وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ','Nuh menjawab: "Hanyalah Allah yang akan mendatangkan azab itu kepadamu jika Dia menghendaki, dan kamu sekali-kali tidak dapat melepaskan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,34,'وَلَا يَنْفَعُكُمْ نُصْحِيْٓ اِنْ اَرَدْتُّ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُرِيْدُ اَنْ يُّغْوِيَكُمْ ۗهُوَ رَبُّكُمْ ۗوَاِلَيْهِ تُرْجَعُوْنَۗ ','Dan tidaklah bermanfaat kepadamu nasehatku jika aku hendak memberi nasehat kepada kamu, sekiranya Allah hendak menyesatkan kamu, Dia adalah Tuhanmu, dan kepada-Nya-lah kamu dikembalikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,35,'اَمْ يَقُوْلُوْنَ افْتَرٰىهُۗ قُلْ اِنِ افْتَرَيْتُهٗ فَعَلَيَّ اِجْرَامِيْ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تُجْرِمُوْنَ ࣖ ','Malahan kaum Nuh itu berkata: "Dia cuma membuat-buat nasihatnya saja". Katakanlah: "Jika aku membuat-buat nasihat itu, maka hanya akulah yang memikul dosaku, dan aku berlepas diri dari dosa yang kamu perbuat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,36,'وَاُوْحِيَ اِلٰى نُوْحٍ اَنَّهٗ لَنْ يُّؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَفْعَلُوْنَۖ  ','Dan diwahyukan kepada Nuh, bahwasanya sekali-kali tidak akan beriman di antara kaummu, kecuali orang yang telah beriman (saja), karena itu janganlah kamu bersedih hati tentang apa yang selalu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,37,'وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْا ۚاِنَّهُمْ مُّغْرَقُوْنَ','Dan buatlah bahtera itu dengan pengawasan dan petunjuk wahyu Kami, dan janganlah kamu bicarakan dengan Aku tentang orang-orang yang zalim itu; sesungguhnya mereka itu akan ditenggelamkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,38,'وَيَصْنَعُ الْفُلْكَۗ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِّنْ قَوْمِهٖ سَخِرُوْا مِنْهُ ۗقَالَ اِنْ تَسْخَرُوْا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُوْنَۗ ','Dan mulailah Nuh membuat bahtera. Dan setiap kali pemimpin kaumnya berjalan meliwati Nuh, mereka mengejeknya. Berkatalah Nuh: "Jika kamu mengejek kami, maka sesungguhnya kami (pun) mengejekmu sebagaimana kamu sekalian mengejek (kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,39,'فَسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ','Kelak kamu akan mengetahui siapa yang akan ditimpa oleh azab yang menghinakannya dan yang akan ditimpa azab yang kekal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,40,'حَتّٰىٓ اِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ قُلْنَا احْمِلْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَ ۗوَمَآ اٰمَنَ مَعَهٗٓ اِلَّا قَلِيْلٌ','Hingga apabila perintah Kami datang dan dapur telah memancarkan air, Kami berfirman: "Muatkanlah ke dalam bahtera itu dari masing-masing binatang sepasang (jantan dan betina), dan keluargamu kecuali orang yang telah terdahulu ketetapan terhadapnya dan (muatkan pula) orang-orang yang beriman". Dan tidak beriman bersama dengan Nuh itu kecuali sedikit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,41,'۞ وَقَالَ ارْكَبُوْا فِيْهَا بِسْمِ اللّٰهِ مَجْرٰ۪ىهَا وَمُرْسٰىهَا ۗاِنَّ رَبِّيْ لَغَفُوْرٌ رَّحِيْمٌ','Dan Nuh berkata: "Naiklah kamu sekalian ke dalamnya dengan menyebut nama Allah di waktu berlayar dan berlabuhnya". Sesungguhnya Tuhanku benar-benar Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,42,'وَهِيَ تَجْرِيْ بِهِمْ فِيْ مَوْجٍ كَالْجِبَالِۗ وَنَادٰى نُوْحُ ِۨابْنَهٗ وَكَانَ فِيْ مَعْزِلٍ يّٰبُنَيَّ ارْكَبْ مَّعَنَا وَلَا تَكُنْ مَّعَ الْكٰفِرِيْنَ','Dan bahtera itu berlayar membawa mereka dalam gelombang laksana gunung. Dan Nuh memanggil anaknya, sedang anak itu berada di tempat yang jauh terpencil: "Hai anakku, naiklah (ke kapal) bersama kami dan janganlah kamu berada bersama orang-orang yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,43,'قَالَ سَاٰوِيْٓ اِلٰى جَبَلٍ يَّعْصِمُنِيْ مِنَ الْمَاۤءِ ۗقَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَّحِمَ ۚوَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِيْنَ','Anaknya menjawab: "Aku akan mencari perlindungan ke gunung yang dapat memeliharaku dari air bah!" Nuh berkata: "Tidak ada yang melindungi hari ini dari azab Allah selain Allah (saja) Yang Maha Penyayang". Dan gelombang menjadi penghalang antara keduanya; maka jadilah anak itu termasuk orang-orang yang ditenggelamkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,44,'وَقِيْلَ يٰٓاَرْضُ ابْلَعِيْ مَاۤءَكِ وَيَا سَمَاۤءُ اَقْلِعِيْ وَغِيْضَ الْمَاۤءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُوْدِيِّ وَقِيْلَ بُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ','Dan difirmankan: "Hai bumi telanlah airmu, dan hai langit (hujan) berhentilah," dan airpun disurutkan, perintahpun diselesaikan dan bahtera itupun berlabuh di atas bukit Judi, dan dikatakan: "Binasalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,45,'وَنَادٰى نُوْحٌ رَّبَّهٗ فَقَالَ رَبِّ اِنَّ ابْنِيْ مِنْ اَهْلِيْۚ وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحٰكِمِيْنَ','Dan Nuh berseru kepada Tuhannya sambil berkata: "Ya Tuhanku, sesungguhnya anakku termasuk keluargaku, dan sesungguhnya janji Engkau itulah yang benar. Dan Engkau adalah Hakim yang seadil-adilnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,46,'قَالَ يٰنُوْحُ اِنَّهٗ لَيْسَ مِنْ اَهْلِكَ ۚاِنَّهٗ عَمَلٌ غَيْرُ صَالِحٍ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنِّيْٓ اَعِظُكَ اَنْ تَكُوْنَ مِنَ الْجٰهِلِيْنَ','Allah berfirman: "Hai Nuh, sesungguhnya dia bukanlah termasuk keluargamu (yang dijanjikan akan diselamatkan), sesungguhnya (perbuatan)nya perbuatan yang tidak baik. Sebab itu janganlah kamu memohon kepada-Ku sesuatu yang kamu tidak mengetahui (hakekat)nya. Sesungguhnya Aku memperingatkan kepadamu supaya kamu jangan termasuk orang-orang yang tidak berpengetahuan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,47,'قَالَ رَبِّ اِنِّيْٓ اَعُوْذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ ۗوَاِلَّا تَغْفِرْ لِيْ وَتَرْحَمْنِيْٓ اَكُنْ مِّنَ الْخٰسِرِيْنَ','Nuh berkata: Ya Tuhanku, sesungguhnya aku berlindung kepada Engkau dari memohon kepada Engkau sesuatu yang aku tiada mengetahui (hakekat)nya. Dan sekiranya Engkau tidak memberi ampun kepadaku, dan (tidak) menaruh belas kasihan kepadaku, niscaya aku akan termasuk orang-orang yang merugi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,48,'قِيْلَ يٰنُوْحُ اهْبِطْ بِسَلٰمٍ مِّنَّا وَبَرَكٰتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِّمَّنْ مَّعَكَ ۗوَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِّنَّا عَذَابٌ اَلِيْمٌ','Difirmankan: "Hai Nuh, turunlah dengan selamat sejahtera dan penuh keberkatan dari Kami atasmu dan atas umat-umat (yang mukmin) dari orang-orang yang bersamamu. Dan ada (pula) umat-umat yang Kami beri kesenangan pada mereka (dalam kehidupan dunia), kemudian mereka akan ditimpa azab yang pedih dari Kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,49,'تِلْكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهَآ اِلَيْكَ ۚمَا كُنْتَ تَعْلَمُهَآ اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۚ فَاصْبِرْۚ اِنَّ الْعَاقِبَةَ لِلْمُتَّقِيْنَ ࣖ ','Itu adalah di antara berita-berita penting tentang yang ghaib yang Kami wahyukan kepadamu (Muhammad); tidak pernah kamu mengetahuinya dan tidak (pula) kaummu sebelum ini. Maka bersabarlah; sesungguhnya kesudahan yang baik adalah bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,50,'وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗاِنْ اَنْتُمْ اِلَّا مُفْتَرُوْنَ','Dan kepada kaum ´Ad (Kami utus) saudara mereka, Huud. Ia berkata: "Hai kaumku, sembahlah Allah, sekali-kali tidak ada bagimu Tuhan selain Dia. Kamu hanyalah mengada-adakan saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,51,'يٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا ۗاِنْ اَجْرِيَ اِلَّا عَلَى الَّذِيْ فَطَرَنِيْ ۗ اَفَلَا تَعْقِلُوْنَ ','Hai kaumku, aku tidak meminta upah kepadamu bagi seruanku ini. Upahku tidak lain hanyalah dari Allah yang telah menciptakanku. Maka tidakkah kamu memikirkan(nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,52,'وَيٰقَوْمِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًا وَّيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِيْنَ','Dan (dia berkata): "Hai kaumku, mohonlah ampun kepada Tuhanmu lalu bertobatlah kepada-Nya, niscaya Dia menurunkan hujan yang sangat deras atasmu, dan Dia akan menambahkan kekuatan kepada kekuatanmu, dan janganlah kamu berpaling dengan berbuat dosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,53,'قَالُوْا يٰهُوْدُ مَاجِئْتَنَا بِبَيِّنَةٍ وَّمَا نَحْنُ بِتَارِكِيْٓ  اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ','Kaum ´Ad berkata: "Hai Huud, kamu tidak mendatangkan kepada kami suatu bukti yang nyata, dan kami sekali-kali tidak akan meninggalkan sembahan-sembahan kami karena perkataanmu, dan kami sekali-kali tidak akan mempercayai kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,54,'اِنْ نَّقُوْلُ اِلَّا اعْتَرٰىكَ بَعْضُ اٰلِهَتِنَا بِسُوْۤءٍ ۗقَالَ اِنِّيْٓ اُشْهِدُ اللّٰهَ وَاشْهَدُوْٓا اَنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ','Kami tidak mengatakan melainkan bahwa sebagian sembahan kami telah menimpakan penyakit gila atas dirimu". Huud menjawab: "Sesungguhnya aku bersaksi kepada Allah dan saksikanlah olehmu sekalian bahwa sesungguhnya aku berlepas diri dari apa yang kamu persekutukan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,55,'مِنْ دُوْنِهٖ فَكِيْدُوْنِيْ جَمِيْعًا ثُمَّ لَا تُنْظِرُوْنِ','dari selain-Nya, sebab itu jalankanlah tipu dayamu semuanya terhadapku dan janganlah kamu memberi tangguh kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,56,'اِنِّيْ تَوَكَّلْتُ عَلَى اللّٰهِ رَبِّيْ وَرَبِّكُمْ ۗمَا مِنْ دَاۤبَّةٍ اِلَّا هُوَ اٰخِذٌۢ بِنَاصِيَتِهَا ۗاِنَّ رَبِّيْ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ','Sesungguhnya aku bertawakkal kepada Allah Tuhanku dan Tuhanmu. Tidak ada suatu binatang melatapun melainkan Dialah yang memegang ubun-ubunnya. Sesungguhnya Tuhanku di atas jalan yang lurus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,57,'فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَّآ اُرْسِلْتُ بِهٖٓ اِلَيْكُمْ ۗوَيَسْتَخْلِفُ رَبِّيْ قَوْمًا غَيْرَكُمْۗ وَلَا تَضُرُّوْنَهٗ شَيْـًٔا ۗاِنَّ رَبِّيْ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ','Jika kamu berpaling, maka sesungguhnya aku telah menyampaikan kepadamu apa (amanat) yang aku diutus (untuk menyampaikan)nya kepadamu. Dan Tuhanku akan mengganti (kamu) dengan kaum yang lain (dari) kamu; dan kamu tidak dapat membuat mudharat kepada-Nya sedikitpun. Sesungguhnya Tuhanku adalah Maha Pemelihara segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,58,'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا هُوْدًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَنَجَّيْنٰهُمْ مِّنْ عَذَابٍ غَلِيْظٍ','Dan tatkala datang azab Kami, Kami selamatkan Huud dan orang-orang yang beriman bersama dia dengan rahmat dari Kami; dan Kami selamatkan (pula) mereka (di akhirat) dari azab yang berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,59,'وَتِلْكَ عَادٌ  ۖجَحَدُوْا بِاٰيٰتِ رَبِّهِمْ وَعَصَوْا رُسُلَهٗ وَاتَّبَعُوْٓا اَمْرَ كُلِّ جَبَّارٍ عَنِيْدٍ','Dan itulah (kisah) kaum ´Ad yang mengingkari tanda-tanda kekuasaan Tuhan mereka, dan mendurhakai rasul-rasul Allah dan mereka menuruti perintah semua penguasa yang sewenang-wenang lagi menentang (kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,60,'وَاُتْبِعُوْا فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً وَّيَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ عَادًا كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّعَادٍ قَوْمِ هُوْدٍ ࣖ ','Dan mereka selalu diikuti dengan kutukan di dunia ini dan (begitu pula) di hari kiamat. Ingatlah, sesungguhnya kaum ´Ad itu kafir kepada Tuhan mereka. Ingatlah kebinasaanlah bagi kaum ´Ad (yaitu) kaum Huud itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,61,'۞ وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا ۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗهُوَ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ فِيْهَا فَاسْتَغْفِرُوْهُ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ قَرِيْبٌ مُّجِيْبٌ','Dan kepada Tsamud (Kami utus) saudara mereka Shaleh. Shaleh berkata: "Hai kaumku, sembahlah Allah, sekali-kali tidak ada bagimu Tuhan selain Dia. Dia telah menciptakan kamu dari bumi (tanah) dan menjadikan kamu pemakmurnya, karena itu mohonlah ampunan-Nya, kemudian bertobatlah kepada-Nya, Sesungguhnya Tuhanku amat dekat (rahmat-Nya) lagi memperkenankan (doa hamba-Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,62,'قَالُوْا يٰصٰلِحُ قَدْ كُنْتَ فِيْنَا مَرْجُوًّا قَبْلَ هٰذَآ اَتَنْهٰىنَآ اَنْ نَّعْبُدَ مَا يَعْبُدُ اٰبَاۤؤُنَا وَاِنَّنَا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَآ اِلَيْهِ مُرِيْبٍ','Kaum Tsamud berkata: "Hai Shaleh, sesungguhnya kamu sebelum ini adalah seorang di antara kami yang kami harapkan, apakah kamu melarang kami untuk menyembah apa yang disembah oleh bapak-bapak kami? dan sesungguhnya kami betul-betul dalam keraguan yang menggelisahkan terhadap agama yang kamu serukan kepada kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,63,'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْۗ وَاٰتٰىنِيْ مِنْهُ رَحْمَةً فَمَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ عَصَيْتُهٗ ۗفَمَا تَزِيْدُوْنَنِيْ غَيْرَ تَخْسِيْرٍ','Shaleh berkata: "Hai kaumku, bagaimana pikiranmu jika aku mempunyai bukti yang nyata dari Tuhanku dan diberi-Nya aku rahmat (kenabian) dari-Nya, maka siapakah yang akan menolong aku dari (azab) Allah jika aku mendurhakai-Nya. Sebab itu kamu tidak menambah apapun kepadaku selain daripada kerugian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,64,'وَيٰقَوْمِ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيْبٌ','Hai kaumku, inilah unta betina dari Allah, sebagai mukjizat (yang menunjukkan kebenaran) untukmu, sebab itu biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apapun yang akan menyebabkan kamu ditimpa azab yang dekat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,65,'فَعَقَرُوْهَا فَقَالَ تَمَتَّعُوْا فِيْ دَارِكُمْ ثَلٰثَةَ اَيَّامٍ ۗذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوْبٍ','Mereka membunuh unta itu, maka berkata Shaleh: "Bersukarialah kamu sekalian di rumahmu selama tiga hari, itu adalah janji yang tidak dapat didustakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,66,'فَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا صٰلِحًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّا وَمِنْ خِزْيِ يَوْمِىِٕذٍ ۗاِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيْزُ','Maka tatkala datang azab Kami, Kami selamatkan Shaleh beserta orang-orang yang beriman bersama dia dengan rahmat dari Kami dan dari kehinaan di hari itu. Sesungguhnya Tuhanmu Dia-Lah yang Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,67,'وَاَخَذَ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ  ','Dan satu suara keras yang mengguntur menimpa orang-orang yang zalim itu, lalu mereka mati bergelimpangan di rumahnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,68,'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَآ اِنَّ ثَمُوْدَا۠ كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّثَمُوْدَ ࣖ ','seolah-olah mereka belum pernah berdiam di tempat itu. Ingatlah, sesungguhnya kaum Tsamud mengingkari Tuhan mereka. Ingatlah, kebinasaanlah bagi kaum Tsamud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,69,'وَلَقَدْ جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰى قَالُوْا سَلٰمًا ۖقَالَ سَلٰمٌ فَمَا لَبِثَ اَنْ جَاۤءَ بِعِجْلٍ حَنِيْذٍ','Dan sesungguhnya utusan-utusan Kami (malaikat-malaikat) telah datang kepada lbrahim dengan membawa kabar gembira, mereka mengucapkan: "Selamat". Ibrahim menjawab: "Selamatlah," maka tidak lama kemudian Ibrahim menyuguhkan daging anak sapi yang dipanggang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,70,'فَلَمَّا رَاٰىٓ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمِ لُوْطٍۗ ','Maka tatkala dilihatnya tangan mereka tidak menjamahnya, Ibrahim memandang aneh perbuatan mereka, dan merasa takut kepada mereka. Malaikat itu berkata: "Jangan kamu takut, sesungguhnya kami adalah (malaikat-ma]aikat) yang diutus kepada kaum Luth".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,71,'وَامْرَاَتُهٗ قَاۤىِٕمَةٌ فَضَحِكَتْ فَبَشَّرْنٰهَا بِاِسْحٰقَۙ وَمِنْ وَّرَاۤءِ اِسْحٰقَ يَعْقُوْبَ','Dan isterinya berdiri (dibalik tirai) lalu dia tersenyum, maka Kami sampaikan kepadanya berita gembira tentang (kelahiran) Ishak dan dari Ishak (akan lahir puteranya) Ya´qub.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,72,'قَالَتْ يٰوَيْلَتٰىٓ ءَاَلِدُ وَاَنَا۠ عَجُوْزٌ وَّهٰذَا بَعْلِيْ شَيْخًا ۗاِنَّ هٰذَا لَشَيْءٌ عَجِيْبٌ','Isterinya berkata: "Sungguh mengherankan, apakah aku akan melahirkan anak padahal aku adalah seorang perempuan tua, dan ini suamikupun dalam keadaan yang sudah tua pula?. Sesungguhnya ini benar-benar suatu yang sangat aneh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,73,'قَالُوْٓا اَتَعْجَبِيْنَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكٰتُهٗ عَلَيْكُمْ اَهْلَ الْبَيْتِۗ اِنَّهُ حَمِيْدٌ مَّجِيْدٌ','Para malaikat itu berkata: "Apakah kamu merasa heran tentang ketetapan Allah? (Itu adalah) rahmat Allah dan keberkatan-Nya, dicurahkan atas kamu, hai ahlulbait! Sesungguhnya Allah Maha Terpuji lagi Maha Pemurah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,74,'فَلَمَّا ذَهَبَ عَنْ اِبْرٰهِيْمَ الرَّوْعُ وَجَاۤءَتْهُ الْبُشْرٰى يُجَادِلُنَا فِيْ قَوْمِ لُوْطٍ','Maka tatkala rasa takut hilang dari Ibrahim dan berita gembira telah datang kepadanya, diapun bersoal jawab dengan (malaikat-malaikat) Kami tentang kaum Luth.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,75,'اِنَّ اِبْرٰهِيْمَ لَحَلِيْمٌ اَوَّاهٌ مُّنِيْبٌ','Sesungguhnya Ibrahim itu benar-benar seorang yang penyantun lagi penghiba dan suka kembali kepada Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,76,'يٰٓاِبْرٰهِيْمُ اَعْرِضْ عَنْ هٰذَا ۚاِنَّهٗ قَدْ جَاۤءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰتِيْهِمْ عَذَابٌ غَيْرُ مَرْدُوْدٍ','Hai Ibrahim, tinggalkanlah soal jawab ini, sesungguhnya telah datang ketetapan Tuhanmu, dan sesungguhnya mereka itu akan didatangi azab yang tidak dapat ditolak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,77,'وَلَمَّا جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالَ هٰذَا يَوْمٌ عَصِيْبٌ','Dan tatkala datang utusan-utusan Kami (para malaikat) itu kepada Luth, dia merasa susah dan merasa sempit dadanya karena kedatangan mereka, dan dia berkata: "Ini adalah hari yang amat sulit".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,78,'وَجَاۤءَهٗ قَوْمُهٗ يُهْرَعُوْنَ اِلَيْهِۗ وَمِنْ قَبْلُ كَانُوْا يَعْمَلُوْنَ السَّيِّاٰتِۗ قَالَ يٰقَوْمِ هٰٓؤُلَاۤءِ بَنَاتِيْ هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ فِيْ ضَيْفِيْۗ  اَلَيْسَ مِنْكُمْ رَجُلٌ رَّشِيْدٌ','Dan datanglah kepadanya kaumnya dengan bergegas-gegas. Dan sejak dahulu mereka selalu melakukan perbuatan-perbuatan yang keji. Luth berkata: "Hai kaumku, inilah puteri-puteriku, mereka lebih suci bagimu, maka bertakwalah kepada Allah dan janganlah kamu mencemarkan (nama)ku terhadap tamuku ini. Tidak adakah di antaramu seorang yang berakal?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,79,'قَالُوْا لَقَدْ عَلِمْتَ مَا لَنَا فِيْ بَنٰتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُرِيْدُ','Mereka menjawab: "Sesungguhnya kamu telah tahu bahwa kami tidak mempunyai keinginan terhadap puteri-puterimu; dan sesungguhnya kamu tentu mengetahui apa yang sebenarnya kami kehendaki".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,80,'قَالَ لَوْ اَنَّ لِيْ بِكُمْ قُوَّةً اَوْ اٰوِيْٓ اِلٰى رُكْنٍ شَدِيْدٍ','Luth berkata: "Seandainya aku ada mempunyai kekuatan (untuk menolakmu) atau kalau aku dapat berlindung kepada keluarga yang kuat (tentu aku lakukan)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,81,'قَالُوْا يٰلُوْطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَّصِلُوْٓا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۗ اِنَّهٗ مُصِيْبُهَا مَآ اَصَابَهُمْ  ۗاِنَّ مَوْعِدَهُمُ الصُّبْحُ ۗ اَلَيْسَ الصُّبْحُ بِقَرِيْبٍ','Para utusan (malaikat) berkata: "Hai Luth, sesungguhnya kami adalah utusan-utusan Tuhanmu, sekali-kali mereka tidak akan dapat mengganggu kamu, sebab itu pergilah dengan membawa keluarga dan pengikut-pengikut kamu di akhir malam dan janganlah ada seorangpun di antara kamu yang tertinggal, kecuali isterimu. Sesungguhnya dia akan ditimpa azab yang menimpa mereka karena sesungguhnya saat jatuhnya azab kepada mereka ialah di waktu subuh; bukankah subuh itu sudah dekat?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,82,'فَلَمَّا جَاۤءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِّنْ سِجِّيْلٍ مَّنْضُوْدٍ','Maka tatkala datang azab Kami, Kami jadikan negeri kaum Luth itu yang di atas ke bawah (Kami balikkan), dan Kami hujani mereka dengan batu dari tanah yang terbakar dengan bertubi-tubi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,83,'مُسَوَّمَةً عِنْدَ رَبِّكَۗ وَمَا هِيَ مِنَ الظّٰلِمِيْنَ بِبَعِيْدٍ ࣖ ','Yang diberi tanda oleh Tuhanmu, dan siksaan itu tiadalah jauh dari orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,84,'۞ وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗوَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيْزَانَ اِنِّيْٓ اَرٰىكُمْ بِخَيْرٍ وَّاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُّحِيْطٍ','Dan kepada (penduduk) Mad-yan (Kami utus) saudara mereka, Syu´aib. Ia berkata: "Hai kaumku, sembahlah Allah, sekali-kali tiada Tuhan bagimu selain Dia. Dan janganlah kamu kurangi takaran dan timbangan, sesungguhnya aku melihat kamu dalam keadaan yang baik (mampu) dan sesungguhnya aku khawatir terhadapmu akan azab hari yang membinasakan (kiamat)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,85,'وَيٰقَوْمِ اَوْفُوا الْمِكْيَالَ وَالْمِيْزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ','Dan Syu´aib berkata: "Hai kaumku, cukupkanlah takaran dan timbangan dengan adil, dan janganlah kamu merugikan manusia terhadap hak-hak mereka dan janganlah kamu membuat kejahatan di muka bumi dengan membuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,86,'بَقِيَّتُ اللّٰهِ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ەۚ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ','Sisa (keuntungan) dari Allah adalah lebih baik bagimu jika kamu orang-orang yang beriman. Dan aku bukanlah seorang penjaga atas dirimu"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,87,'قَالُوْا يٰشُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَّتْرُكَ مَا يَعْبُدُ اٰبَاۤؤُنَآ اَوْ اَنْ نَّفْعَلَ فِيْٓ اَمْوَالِنَا مَا نَشٰۤؤُا ۗاِنَّكَ لَاَنْتَ الْحَلِيْمُ الرَّشِيْدُ','Mereka berkata: "Hai Syu´aib, apakah sembahyangmu menyuruh kamu agar kami meninggalkan apa yang disembah oleh bapak-bapak kami atau melarang kami memperbuat apa yang kami kehendaki tentang harta kami. Sesungguhnya kamu adalah orang yang sangat penyantun lagi berakal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,88,'قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَرَزَقَنِيْ مِنْهُ رِزْقًا حَسَنًا وَّمَآ اُرِيْدُ اَنْ اُخَالِفَكُمْ اِلٰى مَآ اَنْهٰىكُمْ عَنْهُ ۗاِنْ اُرِيْدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۗ وَمَا تَوْفِيْقِيْٓ اِلَّا بِاللّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ اُنِيْبُ ','Syu´aib berkata: "Hai kaumku, bagaimana pikiranmu jika aku mempunyai bukti yang nyata dari Tuhanku dan dianugerahi-Nya aku dari pada-Nya rezeki yang baik (patutkah aku menyalahi perintah-Nya)? Dan aku tidak berkehendak menyalahi kamu (dengan mengerjakan) apa yang aku larang. Aku tidak bermaksud kecuali (mendatangkan) perbaikan selama aku masih berkesanggupan. Dan tidak ada taufik bagiku melainkan dengan (pertolongan) Allah. Hanya kepada Allah aku bertawakkal dan hanya kepada-Nya-lah aku kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,89,'وَيٰقَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِيْٓ اَنْ يُّصِيْبَكُمْ مِّثْلُ مَآ اَصَابَ قَوْمَ نُوْحٍ اَوْ قَوْمَ هُوْدٍ اَوْ قَوْمَ صٰلِحٍ ۗوَمَا قَوْمُ لُوْطٍ مِّنْكُمْ بِبَعِيْدٍ','Hai kaumku, janganlah hendaknya pertentangan antara aku (dengan kamu) menyebabkan kamu menjadi jahat hingga kamu ditimpa azab seperti yang menimpa kaum Nuh atau kaum Hud atau kaum Shaleh, sedang kaum Luth tidak (pula) jauh (tempatnya) dari kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,90,'وَاسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ رَحِيْمٌ وَّدُوْدٌ','Dan mohonlah ampun kepada Tuhanmu kemudian bertaubatlah kepada-Nya. Sesungguhnya Tuhanku Maha Penyayang lagi Maha Pengasih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,91,'قَالُوْا يٰشُعَيْبُ مَا نَفْقَهُ كَثِيْرًا مِّمَّا تَقُوْلُ وَاِنَّا لَنَرٰىكَ فِيْنَا ضَعِيْفًا ۗوَلَوْلَا رَهْطُكَ لَرَجَمْنٰكَ ۖوَمَآ اَنْتَ عَلَيْنَا بِعَزِيْزٍ ','Mereka berkata: "Hai Syu´aib, kami tidak banyak mengerti tentang apa yang kamu katakan itu dan sesungguhnya kami benar-benar melihat kamu seorang yang lemah di antara kami; kalau tidaklah karena keluargamu tentulah kami telah merajam kamu, sedang kamupun bukanlah seorang yang berwibawa di sisi kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,92,'قَالَ يٰقَوْمِ اَرَهْطِيْٓ اَعَزُّ عَلَيْكُمْ مِّنَ اللّٰهِ ۗوَاتَّخَذْتُمُوْهُ وَرَاۤءَكُمْ ظِهْرِيًّا ۗاِنَّ رَبِّيْ بِمَا تَعْمَلُوْنَ مُحِيْطٌ','Syu´aib menjawab: "Hai kaumku, apakah keluargaku lebih terhormat menurut pandanganmu daripada Allah, sedang Allah kamu jadikan sesuatu yang terbuang di belakangmu?. Sesungguhnya (pengetahuan) Tuhanku meliputi apa yang kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,93,'وَيٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ  ۗسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَمَنْ هُوَ كَاذِبٌۗ وَارْتَقِبُوْٓا اِنِّيْ مَعَكُمْ رَقِيْبٌ','Dan (dia berkata): "Hai kaumku, berbuatlah menurut kemampuanmu, sesungguhnya akupun berbuat (pula). Kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakannya dan siapa yang berdusta. Dan tunggulah azab (Tuhan), sesungguhnya akupun menunggu bersama kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,94,'وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا شُعَيْبًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَاَخَذَتِ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ  ','Dan tatkala datang azab Kami, Kami selamatkan Syu´aib dan orang-orang yang beriman bersama-sama dengan dia dengan rahmat dari Kami, dan orang-orang yang zalim dibinasakan oleh satu suara yang mengguntur, lalu jadilah mereka mati bergelimpangan di rumahnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,95,'كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَا بُعْدًا لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُوْدُ ࣖ ','Seolah-olah mereka belum pernah berdiam di tempat itu. Ingatlah, kebinasaanlah bagi penduduk Mad-yan sebagaimana kaum Tsamud telah binasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,96,'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ ','Dan sesungguhnya Kami telah mengutus Musa dengan tanda-tanda (kekuasaan) Kami dan mukjizat yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,97,'اِلٰى فِرْعَوْنَ وَملَا۟ىِٕهٖ فَاتَّبَعُوْٓا اَمْرَ فِرْعَوْنَ ۚوَمَآ اَمْرُ فِرْعَوْنَ بِرَشِيْدٍ','kepada Fir´aun dan pemimpin-pemimpin kaumnya, tetapi mereka mengikut perintah Fir´aun, padahal perintah Fir´aun sekali-kali bukanlah (perintah) yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,98,'يَقْدُمُ قَوْمَهٗ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَ ۗوَبِئْسَ الْوِرْدُ الْمَوْرُوْدُ','Ia berjalan di muka kaumnya di hari kiamat lalu memasukkan mereka ke dalam neraka. Neraka itu seburuk-buruk tempat yang didatangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,99,'وَاُتْبِعُوْا فِيْ هٰذِهٖ لَعْنَةً وَّيَوْمَ الْقِيٰمَةِۗ بِئْسَ الرِّفْدُ الْمَرْفُوْدُ','Dan mereka selalu diikuti dengan kutukan di dunia ini dan (begitu pula) di hari kiamat. La´nat itu seburuk-buruk pemberian yang diberikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,100,'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْقُرٰى نَقُصُّهٗ عَلَيْكَ مِنْهَا قَاۤىِٕمٌ وَّحَصِيْدٌ','Itu adalah sebahagian dan berita-berita negeri (yang telah dibinasakan) yang Kami ceritakan kepadamu (Muhammad); di antara negeri-negeri itu ada yang masih kedapatan bekas-bekasnya dan ada (pula) yang telah musnah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,101,'وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ ظَلَمُوْٓا اَنْفُسَهُمْ فَمَآ اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّتِيْ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ مِنْ شَيْءٍ لَّمَّا جَاۤءَ اَمْرُ رَبِّكَۗ وَمَا زَادُوْهُمْ غَيْرَ تَتْبِيْبٍ ','Dan Kami tidaklah menganiaya mereka tetapi merekalah yang menganiaya diri mereka sendiri, karena itu tiadalah bermanfaat sedikitpun kepada mereka sembahan-sembahan yang mereka seru selain Allah, di waktu azab Tuhanmu datang. Dan sembahan-sembahan itu tidaklah menambah kepada mereka kecuali kebinasaan belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,102,'وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَآ اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌ  ۗاِنَّ اَخْذَهٗٓ اَلِيْمٌ شَدِيْدٌ','Dan begitulah azab Tuhanmu, apabila Dia mengazab penduduk negeri-negeri yang berbuat zalim. Sesungguhnya azab-Nya itu adalah sangat pedih lagi keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,103,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّمَنْ خَافَ عَذَابَ الْاٰخِرَةِ ۗذٰلِكَ يَوْمٌ مَّجْمُوْعٌۙ لَّهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَّشْهُوْدٌ','Sesungguhnya pada yang demikian itu benar-benar terdapat pelajaran bagi orang-orang yang takut kepada azab akhirat. Hari kiamat itu adalah suatu hari yang semua manusia dikumpulkan untuk (menghadapi)nya, dan hari itu adalah suatu hari yang disaksikan (oleh segala makhluk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,104,'وَمَا نُؤَخِّرُهٗٓ اِلَّا لِاَجَلٍ مَّعْدُوْدٍۗ ','Dan Kami tiadalah mengundurkannya, melainkan sampai waktu yang tertentu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,105,'يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِاِذْنِهٖۚ فَمِنْهُمْ شَقِيٌّ وَّسَعِيْدٌ','Di kala datang hari itu, tidak ada seorangun yang berbicara, melainkan dengan izin-Nya; maka di antara mereka ada yang celaka dan ada yang berbahagia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,106,'فَاَمَّا الَّذِيْنَ شَقُوْا فَفِى النَّارِ لَهُمْ فِيْهَا زَفِيْرٌ وَّشَهِيْقٌۙ  ','Adapun orang-orang yang celaka, maka (tempatnya) di dalam neraka, di dalamnya mereka mengeluarkan dan menarik nafas (dengan merintih),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,107,'خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ اِنَّ رَبَّكَ فَعَّالٌ لِّمَا يُرِيْدُ','mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain). Sesungguhnya Tuhanmu Maha Pelaksana terhadap apa yang Dia kehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,108,'۞ وَاَمَّا الَّذِيْنَ سُعِدُوْا فَفِى الْجَنَّةِ خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ عَطَاۤءً غَيْرَ مَجْذُوْذٍ','Adapun orang-orang yang berbahagia, maka tempatnya di dalam surga, mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain); sebagai karunia yang tiada putus-putusnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,109,'فَلَا تَكُ فِيْ مِرْيَةٍ مِّمَّا يَعْبُدُ هٰٓؤُلَاۤءِ ۗمَا يَعْبُدُوْنَ اِلَّا كَمَا يَعْبُدُ اٰبَاۤؤُهُمْ مِّنْ قَبْلُ ۗوَاِنَّا لَمُوَفُّوْهُمْ نَصِيْبَهُمْ غَيْرَ مَنْقُوْصٍ ࣖ ','Maka janganlah kamu berada dalam keragu-raguan tentang apa yang disembah oleh mereka. Mereka tidak menyembah melainkan sebagaimana nenek moyang mereka menyembah dahulu. Dan sesungguhnya Kami pasti akan menyempurnakan dengan secukup-cukupnya pembalasan (terhadap) mereka dengan tidak dikurangi sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,110,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ','Dan sesungguhnya Kami telah memberikan Kitab (Taurat) kepada Musa, lalu diperselisihkan tentang Kitab itu. Dan seandainya tidak ada ketetapan yang telah terdahulu dari Tuhanmu, niscaya telah ditetapkan hukuman di antara mereka. Dan sesungguhnya mereka (orang-orang kafir Mekah) dalam keraguan yang menggelisahkan terhadap Al Quran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,111,'وَاِنَّ كُلًّا لَّمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْ ۗاِنَّهٗ بِمَا يَعْمَلُوْنَ خَبِيْرٌ','Dan sesungguhnya kepada masing-masing (mereka yang berselisih itu) pasti Tuhanmu akan menyempurnakan dengan cukup, (balasan) pekerjaan mereka. Sesungguhnya Dia Maha Mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,112,'فَاسْتَقِمْ كَمَآ اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۗ اِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Maka tetaplah kamu pada jalan yang benar, sebagaimana diperintahkan kepadamu dan (juga) orang yang telah taubat beserta kamu dan janganlah kamu melampaui batas. Sesungguhnya Dia Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,113,'وَلَا تَرْكَنُوْٓا اِلَى الَّذِيْنَ ظَلَمُوْا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ثُمَّ لَا تُنْصَرُوْنَ','Dan janganlah kamu cenderung kepada orang-orang yang zalim yang menyebabkan kamu disentuh api neraka, dan sekali-kali kamu tiada mempunyai seorang penolongpun selain daripada Allah, kemudian kamu tidak akan diberi pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,114,'وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِّنَ الَّيْلِ ۗاِنَّ الْحَسَنٰتِ يُذْهِبْنَ السَّيِّاٰتِۗ ذٰلِكَ ذِكْرٰى لِلذَّاكِرِيْنَ','Dan dirikanlah sembahyang itu pada kedua tepi siang (pagi dan petang) dan pada bahagian permulaan daripada malam. Sesungguhnya perbuatan-perbuatan yang baik itu menghapuskan (dosa) perbuatan-perbuatan yang buruk. Itulah peringatan bagi orang-orang yang ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,115,'وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ','Dan bersabarlah, karena sesungguhnya Allah tiada menyia-nyiakan pahala orang-orang yang berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,116,'فَلَوْلَا كَانَ مِنَ الْقُرُوْنِ مِنْ قَبْلِكُمْ اُولُوْا بَقِيَّةٍ يَّنْهَوْنَ عَنِ الْفَسَادِ فِى الْاَرْضِ اِلَّا قَلِيْلًا مِّمَّنْ اَنْجَيْنَا مِنْهُمْ ۚوَاتَّبَعَ الَّذِيْنَ ظَلَمُوْا مَآ اُتْرِفُوْا فِيْهِ وَكَانُوْا مُجْرِمِيْنَ','Maka mengapa tidak ada dari umat-umat yang sebelum kamu orang-orang yang mempunyai keutamaan yang melarang daripada (mengerjakan) kerusakan di muka bumi, kecuali sebahagian kecil di antara orang-orang yang telah Kami selamatkan di antara mereka, dan orang-orang yang zalim hanya mementingkan kenikmatan yang mewah yang ada pada mereka, dan mereka adalah orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,117,'وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا مُصْلِحُوْنَ','Dan Tuhanmu sekali-kali tidak akan membinasakan negeri-negeri secara zalim, sedang penduduknya orang-orang yang berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,118,'وَلَوْ شَاۤءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَّاحِدَةً وَّلَا يَزَالُوْنَ مُخْتَلِفِيْنَۙ  ','Jikalau Tuhanmu menghendaki, tentu Dia menjadikan manusia umat yang satu, tetapi mereka senantiasa berselisih pendapat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,119,'اِلَّا مَنْ رَّحِمَ رَبُّكَ ۗوَلِذٰلِكَ خَلَقَهُمْ ۗوَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ','kecuali orang-orang yang diberi rahmat oleh Tuhanmu. Dan untuk itulah Allah menciptakan mereka. Kalimat Tuhanmu (keputusan-Nya) telah ditetapkan: sesungguhnya Aku akan memenuhi neraka Jahannam dengan jin dan manusia (yang durhaka) semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,120,'وَكُلًّا نَّقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ الرُّسُلِ مَا نُثَبِّتُ بِهٖ فُؤَادَكَ وَجَاۤءَكَ فِيْ هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَّذِكْرٰى لِلْمُؤْمِنِيْنَ ','Dan semua kisah dari rasul-rasul Kami ceritakan kepadamu, ialah kisah-kisah yang dengannya Kami teguhkan hatimu; dan dalam surat ini telah datang kepadamu kebenaran serta pengajaran dan peringatan bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,121,'وَقُلْ لِّلَّذِيْنَ لَا يُؤْمِنُوْنَ اعْمَلُوْا عَلٰى مَكَانَتِكُمْۗ اِنَّا عٰمِلُوْنَۙ  ','Dan katakanlah kepada orang-orang yang tidak beriman: "Berbuatlah menurut kemampuanmu; sesungguhnya Kami-pun berbuat (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,122,'وَانْتَظِرُوْاۚ اِنَّا مُنْتَظِرُوْنَ','Dan tunggulah (akibat perbuatanmu); sesungguhnya kamipun menunggu (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,11,123,'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهٗ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ࣖ ','Dan kepunyaan Allah-lah apa yang ghaib di langit dan di bumi dan kepada-Nya-lah dikembalikan urusan-urusan semuanya, maka sembahlah Dia, dan bertawakkallah kepada-Nya. Dan sekali-kali Tuhanmu tidak lalai dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,1,'الۤرٰ ۗ تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِۗ  ','Alif, laam, raa. Ini adalah ayat-ayat Kitab (Al Quran) yang nyata (dari Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,2,'اِنَّآ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَ','Sesungguhnya Kami menurunkannya berupa Al Quran dengan berbahasa Arab, agar kamu memahaminya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,3,'نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَآ اَوْحَيْنَآ اِلَيْكَ هٰذَا الْقُرْاٰنَۖ وَاِنْ كُنْتَ مِنْ قَبْلِهٖ لَمِنَ الْغٰفِلِيْنَ','Kami menceritakan kepadamu kisah yang paling baik dengan mewahyukan Al Quran ini kepadamu, dan sesungguhnya kamu sebelum (Kami mewahyukan)nya adalah termasuk orang-orang yang belum mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,4,'اِذْ قَالَ يُوْسُفُ لِاَبِيْهِ يٰٓاَبَتِ اِنِّيْ رَاَيْتُ اَحَدَ عَشَرَ كَوْكَبًا وَّالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ لِيْ سٰجِدِيْنَ','(Ingatlah), ketika Yusuf berkata kepada ayahnya: "Wahai ayahku, sesungguhnya aku bermimpi melihat sebelas bintang, matahari dan bulan; kulihat semuanya sujud kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,5,'قَالَ يٰبُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَكِيْدُوْا لَكَ كَيْدًا ۗاِنَّ الشَّيْطٰنَ لِلْاِنْسَانِ عَدُوٌّ مُّبِيْنٌ','Ayahnya berkata: "Hai anakku, janganlah kamu ceritakan mimpimu itu kepada saudara-saudaramu, maka mereka membuat makar (untuk membinasakan)mu. Sesungguhnya syaitan itu adalah musuh yang nyata bagi manusia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,6,'وَكَذٰلِكَ يَجْتَبِيْكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيْلِ الْاَحَادِيْثِ وَيُتِمُّ نِعْمَتَهٗ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوْبَ كَمَآ اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰهِيْمَ وَاِسْحٰقَۗ اِنَّ رَبَّكَ عَلِيْمٌ حَكِيْمٌ ࣖ','Dan demikianlah Tuhanmu, memilih kamu (untuk menjadi Nabi) dan diajarkan-Nya kepadamu sebahagian dari ta´bir mimpi-mimpi dan disempurnakan-Nya nikmat-Nya kepadamu dan kepada keluarga Ya´qub, sebagaimana Dia telah menyempurnakan nikmat-Nya kepada dua orang bapakmu sebelum itu, (yaitu) Ibrahim dan Ishak. Sesungguhnya Tuhanmu Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,7,'۞ لَقَدْ كَانَ فِيْ يُوْسُفَ وَاِخْوَتِهٖٓ اٰيٰتٌ لِّلسَّاۤىِٕلِيْنَ ','Sesungguhnya ada beberapa tanda-tanda kekuasaan Allah pada (kisah) Yusuf dan saudara-saudaranya bagi orang-orang yang bertanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,8,'اِذْ قَالُوْا لَيُوْسُفُ وَاَخُوْهُ اَحَبُّ اِلٰٓى اَبِيْنَا مِنَّا وَنَحْنُ عُصْبَةٌ  ۗاِنَّ اَبَانَا لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ  ','(Yaitu) ketika mereka berkata: "Sesungguhnya Yusuf dan saudara kandungnya (Bunyamin) lebih dicintai oleh ayah kita dari pada kita sendiri, padahal kita (ini) adalah satu golongan (yang kuat). Sesungguhnya ayah kita adalah dalam kekeliruan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,9,' ۨاقْتُلُوْا يُوْسُفَ اَوِ اطْرَحُوْهُ اَرْضًا يَّخْلُ لَكُمْ وَجْهُ اَبِيْكُمْ وَتَكُوْنُوْا مِنْۢ بَعْدِهٖ قَوْمًا صٰلِحِيْنَ','Bunuhlah Yusuf atau buanglah dia kesuatu daerah (yang tak dikenal) supaya perhatian ayahmu tertumpah kepadamu saja, dan sesudah itu hendaklah kamu menjadi orang-orang yang baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,10,'قَالَ قَاۤئِلٌ مِّنْهُمْ لَا تَقْتُلُوْا يُوْسُفَ وَاَلْقُوْهُ فِيْ غَيٰبَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فٰعِلِيْنَ','Seorang diantara mereka berkata: "Janganlah kamu bunuh Yusuf, tetapi masukkanlah dia ke dasar sumur supaya dia dipungut oleh beberapa orang musafir, jika kamu hendak berbuat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,11,'قَالُوْا يٰٓاَبَانَا مَا لَكَ لَا تَأْمَنَّ۫ا عَلٰى يُوْسُفَ وَاِنَّا لَهٗ لَنَاصِحُوْنَ','Mereka berkata: "Wahai ayah kami, apa sebabnya kamu tidak mempercayai kami terhadap Yusuf, padahal sesungguhnya kami adalah orang-orang yang mengingini kebaikan baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,12,'اَرْسِلْهُ مَعَنَا غَدًا يَّرْتَعْ وَيَلْعَبْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ','Biarkanlah dia pergi bersama kami besok pagi, agar dia (dapat) bersenang-senang dan (dapat) bermain-main, dan sesungguhnya kami pasti menjaganya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,13,'قَالَ اِنِّيْ لَيَحْزُنُنِيْٓ اَنْ تَذْهَبُوْا بِهٖ وَاَخَافُ اَنْ يَّأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غٰفِلُوْنَ ','Berkata Ya´qub: "Sesungguhnya kepergian kamu bersama Yusuf amat menyedihkanku dan aku khawatir kalau-kalau dia dimakan serigala, sedang kamu lengah dari padanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,14,'قَالُوْا لَىِٕنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّآ اِذًا لَّخٰسِرُوْنَ','Mereka berkata: "Jika ia benar-benar dimakan serigala, sedang kami golongan (yang kuat), sesungguhnya kami kalau demikian adalah orang-orang yang merugi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,15,'فَلَمَّا ذَهَبُوْا بِهٖ وَاَجْمَعُوْٓا اَنْ يَّجْعَلُوْهُ فِيْ غَيٰبَتِ الْجُبِّۚ وَاَوْحَيْنَآ اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُوْنَ','Maka tatkala mereka membawanya dan sepakat memasukkannya ke dasar sumur (lalu mereka masukkan dia), dan (di waktu dia sudah dalam sumur) Kami wahyukan kepada Yusuf: "Sesungguhnya kamu akan menceritakan kepada mereka perbuatan mereka ini, sedang mereka tiada ingat lagi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,16,'وَجَاۤءُوْٓ اَبَاهُمْ عِشَاۤءً يَّبْكُوْنَۗ','Kemudian mereka datang kepada ayah mereka di sore hari sambil menangis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,17,'قَالُوْا يٰٓاَبَانَآ اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوْسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَآ اَنْتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صٰدِقِيْنَ','Mereka berkata: "Wahai ayah kami, sesungguhnya kami pergi berlomba-lomba dan kami tinggalkan Yusuf di dekat barang-barang kami, lalu dia dimakan serigala; dan kamu sekali-kali tidak akan percaya kepada kami, sekalipun kami adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,18,'وَجَاۤءُوْ عَلٰى قَمِيْصِهٖ بِدَمٍ كَذِبٍۗ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ  ۗوَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ','Mereka datang membawa baju gamisnya (yang berlumuran) dengan darah palsu. Ya´qub berkata: "Sebenarnya dirimu sendirilah yang memandang baik perbuatan (yang buruk) itu; maka kesabaran yang baik itulah (kesabaranku). Dan Allah sajalah yang dimohon pertolongan-Nya terhadap apa yang kamu ceritakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,19,'وَجَاۤءَتْ سَيَّارَةٌ فَاَرْسَلُوْا وَارِدَهُمْ فَاَدْلٰى دَلْوَهٗ ۗقَالَ يٰبُشْرٰى هٰذَا غُلٰمٌ ۗوَاَسَرُّوْهُ بِضَاعَةً ۗوَاللّٰهُ عَلِيْمٌ ۢبِمَا يَعْمَلُوْنَ','Kemudian datanglah kelompok orang-orang musafir, lalu mereka menyuruh seorang pengambil air, maka dia menurunkan timbanya, dia berkata: "Oh; kabar gembira, ini seorang anak muda!" Kemudian mereka menyembunyikan dia sebagai barang dagangan. Dan Allah Maha Mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,20,'وَشَرَوْهُ بِثَمَنٍۢ بَخْسٍ دَرَاهِمَ مَعْدُوْدَةٍ ۚوَكَانُوْا فِيْهِ مِنَ الزَّاهِدِيْنَ ࣖ ','Dan mereka menjual Yusuf dengan harga yang murah, yaitu beberapa dirham saja, dan mereka merasa tidak tertarik hatinya kepada Yusuf.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,21,'وَقَالَ الَّذِى اشْتَرٰىهُ مِنْ مِّصْرَ لِامْرَاَتِهٖٓ اَكْرِمِيْ مَثْوٰىهُ عَسٰىٓ اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا ۗوَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِۖ وَلِنُعَلِّمَهٗ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۗ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِهٖ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ','Dan orang Mesir yang membelinya berkata kepada isterinya: "Berikanlah kepadanya tempat (dan layanan) yang baik, boleh jadi dia bermanfaat kepada kita atau kita pungut dia sebagai anak". Dan demikian pulalah Kami memberikan kedudukan yang baik kepada Yusuf di muka bumi (Mesir), dan agar Kami ajarkan kepadanya ta´bir mimpi. Dan Allah berkuasa terhadap urusan-Nya, tetapi kebanyakan manusia tiada mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,22,'وَلَمَّا بَلَغَ اَشُدَّهٗٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','Dan tatkala dia cukup dewasa Kami berikan kepadanya hikmah dan ilmu. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,23,'وَرَاوَدَتْهُ الَّتِيْ هُوَ فِيْ بَيْتِهَا عَنْ نَّفْسِهٖ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۗقَالَ مَعَاذَ اللّٰهِ اِنَّهٗ رَبِّيْٓ اَحْسَنَ مَثْوَايَۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ','Dan wanita (Zulaikha) yang Yusuf tinggal di rumahnya menggoda Yusuf untuk menundukkan dirinya (kepadanya) dan dia menutup pintu-pintu, seraya berkata: "Marilah ke sini". Yusuf berkata: "Aku berlindung kepada Allah, sungguh tuanku telah memperlakukan aku dengan baik". Sesungguhnya orang-orang yang zalim tiada akan beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,24,'وَلَقَدْ هَمَّتْ بِهٖۙ وَهَمَّ بِهَا ۚ لَوْلَآ اَنْ رَّاٰى بُرْهَانَ رَبِّهٖۗ  كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّوْۤءَ وَالْفَحْشَاۤءَۗ اِنَّهٗ مِنْ عِبَادِنَا الْمُخْلَصِيْنَ ','Sesungguhnya wanita itu telah bermaksud (melakukan perbuatan itu) dengan Yusuf, dan Yusufpun bermaksud (melakukan pula) dengan wanita itu andaikata dia tidak melihat tanda (dari) Tuhannya. Demikianlah, agar Kami memalingkan dari padanya kemungkaran dan kekejian. Sesungguhnya Yusuf itu termasuk hamba-hamba Kami yang terpilih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,25,'وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيْصَهٗ مِنْ دُبُرٍ وَّاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۗ قَالَتْ مَا جَزَاۤءُ مَنْ اَرَادَ بِاَهْلِكَ سُوْۤءًا اِلَّآ اَنْ يُّسْجَنَ اَوْ عَذَابٌ اَلِيْمٌ ','Dan keduanya berlomba-lomba menuju pintu dan wanita itu menarik baju gamis Yusuf dari belakang hingga koyak dan kedua-duanya mendapati suami wanita itu di muka pintu. Wanita itu berkata: "Apakah pembalasan terhadap orang yang bermaksud berbuat serong dengan isterimu, selain dipenjarakan atau (dihukum) dengan azab yang pedih?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,26,'قَالَ هِيَ رَاوَدَتْنِيْ عَنْ نَّفْسِيْ وَشَهِدَ شَاهِدٌ مِّنْ اَهْلِهَاۚ اِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكٰذِبِيْنَ ','Yusuf berkata: "Dia menggodaku untuk menundukkan diriku (kepadanya)", dan seorang saksi dari keluarga wanita itu memberikan kesaksiannya: "Jika baju gamisnya koyak di muka, maka wanita itu benar dan Yusuf termasuk orang-orang yang dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,27,'وَاِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصّٰدِقِيْنَ ','Dan jika baju gamisnya koyak di belakang, maka wanita itulah yang dusta, dan Yusuf termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,28,'فَلَمَّا رَاٰى قَمِيْصَهٗ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهٗ مِنْ كَيْدِكُنَّ ۗاِنَّ كَيْدَكُنَّ عَظِيْمٌ ','Maka tatkala suami wanita itu melihat baju gamis Yusuf koyak di belakang berkatalah dia: "Sesungguhnya (kejadian) itu adalah diantara tipu daya kamu, sesungguhnya tipu daya kamu adalah besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,29,'يُوْسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِرِيْ لِذَنْۢبِكِۖ اِنَّكِ كُنْتِ مِنَ الْخٰطِـِٕيْنَ ࣖ ','(Hai) Yusuf: "Berpalinglah dari ini, dan (kamu hai isteriku) mohon ampunlah atas dosamu itu, karena kamu sesungguhnya termasuk orang-orang yang berbuat salah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,30,'۞ وَقَالَ نِسْوَةٌ فِى الْمَدِيْنَةِ امْرَاَتُ الْعَزِيْزِ تُرَاوِدُ فَتٰىهَا عَنْ نَّفْسِهٖۚ قَدْ شَغَفَهَا حُبًّاۗ اِنَّا لَنَرٰىهَا فِيْ ضَلٰلٍ مُّبِيْنٍ ','Dan wanita-wanita di kota berkata: "Isteri Al Aziz menggoda bujangnya untuk menundukkan dirinya (kepadanya), sesungguhnya cintanya kepada bujangnya itu adalah sangat mendalam. Sesungguhnya kami memandangnya dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,31,'فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَاً وَّاٰتَتْ كُلَّ وَاحِدَةٍ مِّنْهُنَّ سِكِّيْنًا وَّقَالَتِ اخْرُجْ عَلَيْهِنَّ ۚ فَلَمَّا رَاَيْنَهٗٓ اَكْبَرْنَهٗ وَقَطَّعْنَ اَيْدِيَهُنَّۖ وَقُلْنَ حَاشَ لِلّٰهِ مَا هٰذَا بَشَرًاۗ اِنْ هٰذَآ اِلَّا مَلَكٌ كَرِيْمٌ ','Maka tatkala wanita itu (Zulaikha) mendengar cercaan mereka, diundangnyalah wanita-wanita itu dan disediakannya bagi mereka tempat duduk, dan diberikannya kepada masing-masing mereka sebuah pisau (untuk memotong jamuan), kemudian dia berkata (kepada Yusuf): "Keluarlah (nampakkanlah dirimu) kepada mereka". Maka tatkala wanita-wanita itu melihatnya, mereka kagum kepada (keelokan rupa)nya, dan mereka melukai (jari) tangannya dan berkata: "Maha sempurna Allah, ini bukanlah manusia. Sesungguhnya ini tidak lain hanyalah malaikat yang mulia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,32,'قَالَتْ فَذٰلِكُنَّ الَّذِيْ لُمْتُنَّنِيْ فِيْهِ ۗوَلَقَدْ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ فَاسْتَعْصَمَ ۗوَلَىِٕنْ لَّمْ يَفْعَلْ مَآ اٰمُرُهٗ لَيُسْجَنَنَّ وَلَيَكُوْنًا مِّنَ الصّٰغِرِيْنَ ','Wanita itu berkata: "Itulah dia orang yang kamu cela aku karena (tertarik) kepadanya, dan sesungguhnya aku telah menggoda dia untuk menundukkan dirinya (kepadaku) akan tetapi dia menolak. Dan sesungguhnya jika dia tidak mentaati apa yang aku perintahkan kepadanya, niscaya dia akan dipenjarakan dan dia akan termasuk golongan orang-orang yang hina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,33,'قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُوْنَنِيْٓ اِلَيْهِ ۚوَاِلَّا تَصْرِفْ عَنِّيْ كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِّنَ الْجٰهِلِيْنَ ','Yusuf berkata: "Wahai Tuhanku, penjara lebih aku sukai daripada memenuhi ajakan mereka kepadaku. Dan jika tidak Engkau hindarkan dari padaku tipu daya mereka, tentu aku akan cenderung untuk (memenuhi keinginan mereka) dan tentulah aku termasuk orang-orang yang bodoh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,34,'فَاسْتَجَابَ لَهٗ رَبُّهٗ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ','Maka Tuhannya memperkenankan doa Yusuf dan Dia menghindarkan Yusuf dari tipu daya mereka. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,35,'ثُمَّ بَدَا لَهُمْ مِّنْۢ بَعْدِ مَا رَاَوُا الْاٰيٰتِ لَيَسْجُنُنَّهٗ حَتّٰى حِيْنٍ ࣖ ','Kemudian timbul pikiran pada mereka setelah melihat tanda-tanda (kebenaran Yusuf) bahwa mereka harus memenjarakannya sampai sesuatu waktu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,36,'وَدَخَلَ مَعَهُ السِّجْنَ فَتَيٰنِ ۗقَالَ اَحَدُهُمَآ اِنِّيْٓ اَرٰىنِيْٓ اَعْصِرُ خَمْرًا ۚوَقَالَ الْاٰخَرُ اِنِّيْٓ اَرٰىنِيْٓ اَحْمِلُ فَوْقَ رَأْسِيْ خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۗنَبِّئْنَا بِتَأْوِيْلِهٖ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ','Dan bersama dengan dia masuk pula ke dalam penjara dua orang pemuda. Berkatalah salah seorang diantara keduanya: "Sesungguhnya aku bermimpi, bahwa aku memeras anggur". Dan yang lainnya berkata: "Sesungguhnya aku bermimpi, bahwa aku membawa roti di atas kepalaku, sebahagiannya dimakan burung". Berikanlah kepada kami ta´birnya; sesungguhnya kami memandang kamu termasuk orang-orang yang pandai (mena´birkan mimpi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,37,'قَالَ لَا يَأْتِيْكُمَا طَعَامٌ تُرْزَقٰنِهٖٓ اِلَّا نَبَّأْتُكُمَا بِتَأْوِيْلِهٖ قَبْلَ اَنْ يَّأْتِيَكُمَا ۗذٰلِكُمَا مِمَّا عَلَّمَنِيْ رَبِّيْۗ اِنِّيْ تَرَكْتُ مِلَّةَ قَوْمٍ لَّا يُؤْمِنُوْنَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَۙ ','Yusuf berkata: "Tidak disampaikan kepada kamu berdua makanan yang akan diberikan kepadamu melainkan aku telah dapat menerangkan jenis makanan itu, sebelum makanan itu sampai kepadamu. Yang demikian itu adalah sebagian dari apa yang diajarkan kepadaku oleh Tuhanku. Sesungguhnya aku telah meninggalkan agama orang-orang yang tidak beriman kepada Allah, sedang mereka ingkar kepada hari kemudian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,38,'وَاتَّبَعْتُ مِلَّةَ اٰبَاۤءِيْٓ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَۗ مَا كَانَ لَنَآ اَنْ نُّشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۗ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ','Dan aku pengikut agama bapak-bapakku yaitu Ibrahim, Ishak dan Ya´qub. Tiadalah patut bagi kami (para Nabi) mempersekutukan sesuatu apapun dengan Allah. Yang demikian itu adalah dari karunia Allah kepada kami dan kepada manusia (seluruhnya); tetapi kebanyakan manusia tidak mensyukuri (Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,39,'يٰصَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُوْنَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۗ  ','Hai kedua penghuni penjara, manakah yang baik, tuhan-tuhan yang bermacam-macam itu ataukah Allah Yang Maha Esa lagi Maha Perkasa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,40,'مَا تَعْبُدُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اَسْمَاۤءً سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗاَمَرَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ','Kamu tidak menyembah yang selain Allah kecuali hanya (menyembah) nama-nama yang kamu dan nenek moyangmu membuat-buatnya. Allah tidak menurunkan suatu keteranganpun tentang nama-nama itu. Keputusan itu hanyalah kepunyaan Allah. Dia telah memerintahkan agar kamu tidak menyembah selain Dia. Itulah agama yang lurus, tetapi kebanyakan manusia tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,41,'يٰصَاحِبَيِ السِّجْنِ اَمَّآ اَحَدُكُمَا فَيَسْقِيْ رَبَّهٗ خَمْرًا ۗوَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَّأْسِهٖ ۗ قُضِيَ الْاَمْرُ الَّذِيْ فِيْهِ تَسْتَفْتِيٰنِۗ ','Hai kedua penghuni penjara: "Adapun salah seorang diantara kamu berdua, akan memberi minuman tuannya dengan khamar; adapun yang seorang lagi maka ia akan disalib, lalu burung memakan sebagian dari kepalanya. Telah diputuskan perkara yang kamu berdua menanyakannya (kepadaku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,42,'وَقَالَ لِلَّذِيْ ظَنَّ اَنَّهٗ نَاجٍ مِّنْهُمَا اذْكُرْنِيْ عِنْدَ رَبِّكَۖ فَاَنْسٰىهُ الشَّيْطٰنُ ذِكْرَ رَبِّهٖ فَلَبِثَ فِى السِّجْنِ بِضْعَ سِنِيْنَ ࣖ ','Dan Yusuf berkata kepada orang yang diketahuinya akan selamat diantara mereka berdua: "Terangkanlah keadaanku kepada tuanmu". Maka syaitan menjadikan dia lupa menerangkan (keadaan Yusuf) kepada tuannya. Karena itu tetaplah dia (Yusuf) dalam penjara beberapa tahun lamanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,43,'وَقَالَ الْمَلِكُ اِنِّيْٓ اَرٰى سَبْعَ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعَ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۗ  يٰٓاَيُّهَا الْمَلَاُ اَفْتُوْنِيْ فِيْ رُؤْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُوْنَ','Raja berkata (kepada orang-orang terkemuka dari kaumnya): "Sesungguhnya aku bermimpi melihat tujuh ekor sapi betina yang gemuk-gemuk dimakan oleh tujuh ekor sapi betina yang kurus-kurus dan tujuh bulir (gandum) yang hijau dan tujuh bulir lainnya yang kering". Hai orang-orang yang terkemuka: "Terangkanlah kepadaku tentang ta´bir mimpiku itu jika kamu dapat mena´birkan mimpi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,44,'قَالُوْٓا اَضْغَاثُ اَحْلَامٍ ۚوَمَا نَحْنُ بِتَأْوِيْلِ الْاَحْلَامِ بِعٰلِمِيْنَ ','Mereka menjawab: "(Itu) adalah mimpi-mimpi yang kosong dan kami sekali-kali tidak tahu menta´birkan mimpi itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,45,'وَقَالَ الَّذِيْ نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۠ اُنَبِّئُكُمْ بِتَأْوِيْلِهٖ فَاَرْسِلُوْنِ ','Dan berkatalah orang yang selamat diantara mereka berdua dan teringat (kepada Yusuf) sesudah beberapa waktu lamanya: "Aku akan memberitakan kepadamu tentang (orang yang pandai) mena´birkan mimpi itu, maka utuslah aku (kepadanya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,46,'يُوْسُفُ اَيُّهَا الصِّدِّيْقُ اَفْتِنَا فِيْ سَبْعِ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعِ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۙ  لَّعَلِّيْٓ اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُوْنَ ','(Setelah pelayan itu berjumpa dengan Yusuf dia berseru): "Yusuf, hai orang yang amat dipercaya, terangkanlah kepada kami tentang tujuh ekor sapi betina yang gemuk-gemuk yang dimakan oleh tujuh ekor sapi betina yang kurus-kurus dan tujuh bulir (gandum) yang hijau dan (tujuh) lainnya yang kering agar aku kembali kepada orang-orang itu, agar mereka mengetahuinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,47,'قَالَ تَزْرَعُوْنَ سَبْعَ سِنِيْنَ دَاَبًاۚ فَمَا حَصَدْتُّمْ فَذَرُوْهُ فِيْ سُنْۢبُلِهٖٓ اِلَّا قَلِيْلًا مِّمَّا تَأْكُلُوْنَ ','Yusuf berkata: "Supaya kamu bertanam tujuh tahun (lamanya) sebagaimana biasa; maka apa yang kamu tuai hendaklah kamu biarkan dibulirnya kecuali sedikit untuk kamu makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,48,'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَّأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَلِيْلًا مِّمَّا تُحْصِنُوْنَ','Kemudian sesudah itu akan datang tujuh tahun yang amat sulit, yang menghabiskan apa yang kamu simpan untuk menghadapinya (tahun sulit), kecuali sedikit dari (bibit gandum) yang kamu simpan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,49,'ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ عَامٌ فِيْهِ يُغَاثُ النَّاسُ وَفِيْهِ يَعْصِرُوْنَ ࣖ','Kemudian setelah itu akan datang tahun yang padanya manusia diberi hujan (dengan cukup) dan dimasa itu mereka memeras anggur".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,50,'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖ ۚفَلَمَّا جَاۤءَهُ الرَّسُوْلُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰتِيْ قَطَّعْنَ اَيْدِيَهُنَّ ۗاِنَّ رَبِّيْ بِكَيْدِهِنَّ عَلِيْمٌ ','Raja berkata: "Bawalah dia kepadaku". Maka tatkala utusan itu datang kepada Yusuf, berkatalah Yusuf: "Kembalilah kepada tuanmu dan tanyakanlah kepadanya bagaimana halnya wanita-wanita yang telah melukai tangannya. Sesungguhnya Tuhanku, Maha Mengetahui tipu daya mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,51,'قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُّنَّ يُوْسُفَ عَنْ نَّفْسِهٖۗ  قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوْۤءٍ ۗقَالَتِ امْرَاَتُ الْعَزِيْزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۖ اَنَا۠ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ وَاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ ','Raja berkata (kepada wanita-wanita itu): "Bagaimana keadaanmu ketika kamu menggoda Yusuf untuk menundukkan dirinya (kepadamu)?" Mereka berkata: "Maha Sempurna Allah, kami tiada mengetahui sesuatu keburukan dari padanya". Berkata isteri Al Aziz: "Sekarang jelaslah kebenaran itu, akulah yang menggodanya untuk menundukkan dirinya (kepadaku), dan sesungguhnya dia termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,12,12,52,'ذٰلِكَ لِيَعْلَمَ اَنِّيْ لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْدِيْ كَيْدَ الْخَاۤىِٕنِيْنَ  ۔','(Yusuf berkata): "Yang demikian itu agar dia (Al Aziz) mengetahui bahwa sesungguhnya aku tidak berkhianat kepadanya di belakangnya, dan bahwasanya Allah tidak meridhai tipu daya orang-orang yang berkhianat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,53,'۞ وَمَآ اُبَرِّئُ نَفْسِيْۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ ۢ بِالسُّوْۤءِ اِلَّا مَا رَحِمَ رَبِّيْۗ اِنَّ رَبِّيْ غَفُوْرٌ رَّحِيْمٌ','Dan aku tidak membebaskan diriku (dari kesalahan), karena sesungguhnya nafsu itu selalu menyuruh kepada kejahatan, kecuali nafsu yang diberi rahmat oleh Tuhanku. Sesungguhnya Tuhanku Maha Pengampun lagi Maha Penyanyang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,54,'وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖٓ اَسْتَخْلِصْهُ لِنَفْسِيْۚ فَلَمَّا كَلَّمَهٗ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَكِيْنٌ اَمِيْنٌ','Dan raja berkata: "Bawalah Yusuf kepadaku, agar aku memilih dia sebagai orang yang rapat kepadaku". Maka tatkala raja telah bercakap-cakap dengan dia, dia berkata: "Sesungguhnya kamu (mulai) hari ini menjadi seorang yang berkedudukan tinggi lagi dipercayai pada sisi kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,55,'قَالَ اجْعَلْنِيْ عَلٰى خَزَاۤىِٕنِ الْاَرْضِۚ اِنِّيْ حَفِيْظٌ عَلِيْمٌ','Berkata Yusuf: "Jadikanlah aku bendaharawan negara (Mesir); sesungguhnya aku adalah orang yang pandai menjaga, lagi berpengetahuan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,56,'وَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَاۤءُۗ نُصِيْبُ بِرَحْمَتِنَا مَنْ نَّشَاۤءُ وَلَا نُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ','Dan demikianlah Kami memberi kedudukan kepada Yusuf di negeri Mesir; (dia berkuasa penuh) pergi menuju kemana saja ia kehendaki di bumi Mesir itu. Kami melimpahkan rahmat Kami kepada siapa yang Kami kehendaki dan Kami tidak menyia-nyiakan pahala orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,57,'وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ ࣖ','Dan sesungguhnya pahala di akhirat itu lebih baik, bagi orang-orang yang beriman dan selalu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,58,'وَجَاۤءَ اِخْوَةُ يُوْسُفَ فَدَخَلُوْا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهٗ مُنْكِرُوْنَ','Dan saudara-saudara Yusuf datang (ke Mesir} lalu mereka masuk ke (tempat)nya. Maka Yusuf mengenal mereka, sedang mereka tidak kenal (lagi) kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,59,'وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُوْنِيْ بِاَخٍ لَّكُمْ مِّنْ اَبِيْكُمْ ۚ اَلَا تَرَوْنَ اَنِّيْٓ اُوْفِى الْكَيْلَ وَاَنَا۠ خَيْرُ الْمُنْزِلِيْنَ ','Dan tatkala Yusuf menyiapkan untuk mereka bahan makanannya, ia berkata: "Bawalah kepadaku saudaramu yang seayah dengan kamu (Bunyamin), tidakkah kamu melihat bahwa aku menyempurnakan sukatan dan aku adalah sebaik-baik penerima tamu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,60,'فَاِنْ لَّمْ تَأْتُوْنِيْ بِهٖ فَلَا كَيْلَ لَكُمْ عِنْدِيْ وَلَا تَقْرَبُوْنِ','Jika kamu tidak membawanya kepadaku, maka kamu tidak akan mendapat sukatan lagi dari padaku dan jangan kamu mendekatiku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,61,'قَالُوْا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُوْنَ','Mereka berkata: "Kami akan membujuk ayahnya untuk membawanya (ke mari) dan sesungguhnya kami benar-benar akan melaksanakannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,62,'وَقَالَ لِفِتْيٰنِهِ اجْعَلُوْا بِضَاعَتَهُمْ فِيْ رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُوْنَهَآ اِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُوْنَ','Yusuf berkata kepada bujang-bujangnya: "Masukkanlah barang-barang (penukar kepunyaan mereka) ke dalam karung-karung mereka, supaya mereka mengetahuinya apabila mereka telah kembali kepada keluarganya, mudah-mudahan mereka kembali lagi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,63,'فَلَمَّا رَجَعُوْٓا اِلٰٓى اَبِيْهِمْ قَالُوْا يٰٓاَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَآ اَخَانَا نَكْتَلْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ','Maka tatkala mereka telah kembali kepada ayah mereka (Ya´qub) mereka berkata: "Wahai ayah kami, kami tidak akan mendapat sukatan (gandum) lagi, (jika tidak membawa saudara kami), sebab itu biarkanlah saudara kami pergi bersama-sama kami supaya kami mendapat sukatan, dan sesungguhnya kami benar benar akan menjaganya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,64,'قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَآ اَمِنْتُكُمْ عَلٰٓى اَخِيْهِ مِنْ قَبْلُۗ فَاللّٰهُ خَيْرٌ حٰفِظًا وَّهُوَ اَرْحَمُ الرّٰحِمِيْنَ','Berkata Ya´qub: "Bagaimana aku akan mempercayakannya (Bunyamin) kepadamu, kecuali seperti aku telah mempercayakan saudaranya (Yusuf) kepada kamu dahulu?". Maka Allah adalah sebaik-baik Penjaga dan Dia adalah Maha Penyanyang diantara para penyanyang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,65,'وَلَمَّا فَتَحُوْا مَتَاعَهُمْ وَجَدُوْا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۗ قَالُوْا يٰٓاَبَانَا مَا نَبْغِيْۗ هٰذِهٖ بِضَاعَتُنَا رُدَّتْ اِلَيْنَا وَنَمِيْرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَعِيْرٍۗ ذٰلِكَ كَيْلٌ يَّسِيْرٌ','Tatkala mereka membuka barang-barangnya, mereka menemukan kembali barang-barang (penukaran) mereka, dikembalikan kepada mereka. Mereka berkata: "Wahai ayah kami apa lagi yang kita inginkan. Ini barang-barang kita dikembalikan kepada kita, dan kami akan dapat memberi makan keluarga kami, dan kami akan dapat memelihara saudara kami, dan kami akan mendapat tambahan sukatan (gandum) seberat beban seekor unta. Itu adalah sukatan yang mudah (bagi raja Mesir)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,66,'قَالَ لَنْ اُرْسِلَهٗ مَعَكُمْ حَتّٰى تُؤْتُوْنِ مَوْثِقًا مِّنَ اللّٰهِ لَتَأْتُنَّنِيْ بِهٖٓ اِلَّآ اَنْ يُّحَاطَ بِكُمْۚ فَلَمَّآ اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ','Ya´qub berkata: "Aku sekali-kali tidak akan melepaskannya (pergi) bersama-sama kamu, sebelum kamu memberikan kepadaku janji yang teguh atas nama Allah, bahwa kamu pasti akan membawanya kepadaku kembali, kecuali jika kamu dikepung musuh". Tatkala mereka memberikan janji mereka, maka Ya´qub berkata: "Allah adalah saksi terhadap apa yang kita ucapkan (ini)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,67,'وَقَالَ يٰبَنِيَّ لَا تَدْخُلُوْا مِنْۢ بَابٍ وَّاحِدٍ وَّادْخُلُوْا مِنْ اَبْوَابٍ مُّتَفَرِّقَةٍۗ وَمَآ اُغْنِيْ عَنْكُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ','Dan Ya´qub berkata: "Hai anak-anakku janganlah kamu (bersama-sama) masuk dari satu pintu gerbang, dan masuklah dari pintu-pintu gerbang yang berlain-lain; namun demikian aku tiada dapat melepaskan kamu barang sedikitpun dari pada (takdir) Allah. Keputusan menetapkan (sesuatu) hanyalah hak Allah; kepada-Nya-lah aku bertawakkal dan hendaklah kepada-Nya saja orang-orang yang bertawakkal berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,68,'وَلَمَّا دَخَلُوْا مِنْ حَيْثُ اَمَرَهُمْ اَبُوْهُمْۗ مَا كَانَ يُغْنِيْ عَنْهُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً فِيْ نَفْسِ يَعْقُوْبَ قَضٰىهَاۗ وَاِنَّهٗ لَذُوْ عِلْمٍ لِّمَا عَلَّمْنٰهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ࣖ','Dan tatkala mereka masuk menurut yang diperintahkan ayah mereka, maka (cara yang mereka lakukan itu) tiadalah melepaskan mereka sedikitpun dari takdir Allah, akan tetapi itu hanya suatu keinginan pada diri Ya´qub yang telah ditetapkannya. Dan sesungguhnya dia mempunyai pengetahuan, karena Kami telah mengajarkan kepadanya. Akan tetapi kebanyakan manusia tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,69,'وَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنِّيْٓ اَنَا۠ اَخُوْكَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَعْمَلُوْنَ','Dan tatkala mereka masuk ke (tempat) Yusuf. Yusuf membawa saudaranya (Bunyamin) ke tempatnya, Yusuf berkata: "Sesungguhnya aku (ini) adalah saudaramu, maka janganlah kamu berdukacita terhadap apa yang telah mereka kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,70,'فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِيْ رَحْلِ اَخِيْهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْعِيْرُ اِنَّكُمْ لَسَارِقُوْنَ','Maka tatkala telah disiapkan untuk mereka bahan makanan mereka, Yusuf memasukkan piala (tempat minum) ke dalam karung saudaranya. Kemudian berteriaklah seseorang yang menyerukan: "Hai kafilah, sesungguhnya kamu adalah orang-orang yang mencuri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,71,'قَالُوْا وَاَقْبَلُوْا عَلَيْهِمْ مَّاذَا تَفْقِدُوْنَ','Mereka menjawab, sambil menghadap kepada penyeru-penyeru itu: "Barang apakah yang hilang dari pada kamu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,72,'قَالُوْا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاۤءَ بِهٖ حِمْلُ بَعِيْرٍ وَّاَنَا۠ بِهٖ زَعِيْمٌ','Penyeru-penyeru itu berkata: "Kami kehilangan piala raja, dan siapa yang dapat mengembalikannya akan memperoleh bahan makanan (seberat) beban unta, dan aku menjamin terhadapnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,73,'قَالُوْا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَّا جِئْنَا لِنُفْسِدَ فِى الْاَرْضِ وَمَا كُنَّا سَارِقِيْنَ','Saudara-saudara Yusuf menjawab "Demi Allah sesungguhnya kamu mengetahui bahwa kami datang bukan untuk membuat kerusakan di negeri (ini) dan kami bukanlah para pencuri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,74,'قَالُوْا فَمَا جَزَاۤؤُهٗٓ اِنْ كُنْتُمْ كٰذِبِيْنَ','Mereka berkata: "Tetapi apa balasannya jikalau kamu betul-betul pendusta? "')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,75,'قَالُوْا جَزَاۤؤُهٗ مَنْ وُّجِدَ فِيْ رَحْلِهٖ فَهُوَ جَزَاۤؤُهٗ ۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ','Mereka menjawab: "Balasannya, ialah pada siapa diketemukan (barang yang hilang) dalam karungnya, maka dia sendirilah balasannya (tebusannya)". Demikianlah kami memberi pembalasan kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,76,'فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَاۤءِ اَخِيْهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِّعَاۤءِ اَخِيْهِۗ  كَذٰلِكَ كِدْنَا لِيُوْسُفَۗ مَا كَانَ لِيَأْخُذَ اَخَاهُ فِيْ دِيْنِ الْمَلِكِ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗنَرْفَعُ دَرَجَاتٍ مَّنْ نَّشَاۤءُۗ وَفَوْقَ كُلِّ ذِيْ عِلْمٍ عَلِيْمٌ','Maka mulailah Yusuf (memeriksa) karung-karung mereka sebelum (memeriksa) karung saudaranya sendiri, kemudian dia mengeluarkan piala raja itu dari karung saudaranya. Demikianlah Kami atur untuk (mencapai maksud) Yusuf. Tiadalah patut Yusuf menghukum saudaranya menurut undang-undang raja, kecuali Allah menghendaki-Nya. Kami tinggikan derajat orang yang Kami kehendaki; dan di atas tiap-tiap orang yang berpengetahuan itu ada lagi Yang Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,77,'۞ قَالُوْٓا اِنْ يَّسْرِقْ فَقَدْ سَرَقَ اَخٌ لَّهٗ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوْسُفُ فِيْ نَفْسِهٖ وَلَمْ يُبْدِهَا لَهُمْۚ قَالَ اَنْتُمْ شَرٌّ مَّكَانًا ۚوَاللّٰهُ اَعْلَمُ بِمَا تَصِفُوْنَ','Mereka berkata: "Jika ia mencuri, maka sesungguhnya, telah pernah mencuri pula saudaranya sebelum itu". Maka Yusuf menyembunyikan kejengkelan itu pada dirinya dan tidak menampakkannya kepada mereka. Dia berkata (dalam hatinya): "Kamu lebih buruk kedudukanmu (sifat-sifatmu) dan Allah Maha Mengetahui apa yang kamu terangkan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,78,'قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ اِنَّ لَهٗٓ اَبًا شَيْخًا كَبِيْرًا فَخُذْ اَحَدَنَا مَكَانَهٗ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ','Mereka berkata: "Wahai Al Aziz, sesungguhnya ia mempunyai ayah yang sudah lanjut usianya, lantaran itu ambillah salah seorang diantara kami sebagai gantinya, sesungguhnya kami melihat kamu termasuk oranng-orang yang berbuat baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,79,'قَالَ مَعَاذَ اللّٰهِ اَنْ نَّأْخُذَ اِلَّا مَنْ وَّجَدْنَا مَتَاعَنَا عِنْدَهٗٓ  ۙاِنَّآ اِذًا لَّظٰلِمُوْنَ ࣖ','Berkata Yusuf: "Aku mohon perlindungan kepada Allah daripada menahan seorang, kecuali orang yang kami ketemukan harta benda kami padanya, jika kami berbuat demikian, maka benar-benarlah kami orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,80,'فَلَمَّا اسْتَا۟يْـَٔسُوْا مِنْهُ خَلَصُوْا نَجِيًّاۗ قَالَ كَبِيْرُهُمْ اَلَمْ تَعْلَمُوْٓا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَّوْثِقًا مِّنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُّمْ فِيْ يُوْسُفَ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ لِيْٓ اَبِيْٓ اَوْ يَحْكُمَ اللّٰهُ لِيْۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ','Maka tatkala mereka berputus asa dari pada (putusan) Yusuf mereka menyendiri sambil berunding dengan berbisik-bisik. Berkatalah yang tertua diantara mereka: "Tidakkah kamu ketahui bahwa sesungguhnya ayahmu telah mengambil janji dari kamu dengan nama Allah dan sebelum itu kamu telah menyia-nyiakan Yusuf. Sebab itu aku tidak akan meninggalkan negeri Mesir, sampai ayahku mengizinkan kepadaku (untuk kembali), atau Allah memberi keputusan terhadapku. Dan Dia adalah Hakim yang sebaik-baiknya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,81,'اِرْجِعُوْٓا اِلٰٓى اَبِيْكُمْ فَقُوْلُوْا يٰٓاَبَانَآ اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَآ اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حٰفِظِيْنَ','Kembalilah kepada ayahmu dan katakanlah: "Wahai ayah kami! Sesungguhnya anakmu telah mencuri, dan kami hanya menyaksikan apa yang kami ketahui, dan sekali-kali kami tidak dapat menjaga (mengetahui) barang yang ghaib.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,82,'وَسْـَٔلِ الْقَرْيَةَ الَّتِيْ كُنَّا فِيْهَا وَالْعِيْرَ الَّتِيْٓ اَقْبَلْنَا فِيْهَاۗ وَاِنَّا لَصٰدِقُوْنَ','Dan tanyalah (penduduk) negeri yang kami berada disitu, dan kafilah yang kami datang bersamanya, dan sesungguhnya kami adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,83,'قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ  ۗعَسَى اللّٰهُ اَنْ يَّأْتِيَنِيْ بِهِمْ جَمِيْعًاۗ اِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ','Ya´qub berkata: "Hanya dirimu sendirilah yang memandang baik perbuatan (yang buruk) itu. Maka kesabaran yang baik itulah (kesabaranku). Mudah-mudahan Allah mendatangkan mereka semuanya kepadaku; sesungguhnya Dialah Yang Maha Mengetahui lagi Maha Bijaksana".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,84,'وَتَوَلّٰى عَنْهُمْ وَقَالَ يٰٓاَسَفٰى عَلٰى يُوْسُفَ وَابْيَضَّتْ عَيْنٰهُ مِنَ الْحُزْنِ فَهُوَ كَظِيْمٌ ','Dan Ya´qub berpaling dari mereka (anak-anaknya) seraya berkata: "Aduhai duka citaku terhadap Yusuf", dan kedua matanya menjadi putih karena kesedihan dan dia adalah seorang yang menahan amarahnya (terhadap anak-anaknya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,85,'قَالُوْا تَاللّٰهِ تَفْتَؤُا تَذْكُرُ يُوْسُفَ حَتّٰى تَكُوْنَ حَرَضًا اَوْ تَكُوْنَ مِنَ الْهَالِكِيْنَ','Mereka berkata: "Demi Allah, senantiasa kamu mengingati Yusuf, sehingga kamu mengidapkan penyakit yang berat atau termasuk orang-orang yang binasa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,86,'قَالَ اِنَّمَآ اَشْكُوْا بَثِّيْ وَحُزْنِيْٓ اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ','Ya´qub menjawab: "Sesungguhnya hanyalah kepada Allah aku mengadukan kesusahan dan kesedihanku, dan aku mengetahui dari Allah apa yang kamu tiada mengetahuinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,87,'يٰبَنِيَّ اذْهَبُوْا فَتَحَسَّسُوْا مِنْ يُّوْسُفَ وَاَخِيْهِ وَلَا تَا۟يْـَٔسُوْا مِنْ رَّوْحِ اللّٰهِ ۗاِنَّهٗ لَا يَا۟يْـَٔسُ مِنْ رَّوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكٰفِرُوْنَ','Hai anak-anakku, pergilah kamu, maka carilah berita tentang Yusuf dan saudaranya dan jangan kamu berputus asa dari rahmat Allah. Sesungguhnya tiada berputus asa dari rahmat Allah, melainkan kaum yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,88,'فَلَمَّا دَخَلُوْا عَلَيْهِ قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُّزْجٰىةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۗ اِنَّ اللّٰهَ يَجْزِى الْمُتَصَدِّقِيْنَ','Maka ketika mereka masuk ke (tempat) Yusuf, mereka berkata: "Hai Al Aziz, kami dan keluarga kami telah ditimpa kesengsaraan dan kami datang membawa barang-barang yang tak berharga, maka sempurnakanlah sukatan untuk kami, dan bersedekahlah kepada kami, sesungguhnya Allah memberi balasan kepada orang-orang yang bersedekah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,89,'قَالَ هَلْ عَلِمْتُمْ مَّا فَعَلْتُمْ بِيُوْسُفَ وَاَخِيْهِ اِذْ اَنْتُمْ جَاهِلُوْنَ','Yusuf berkata: "Apakah kamu mengetahui (kejelekan) apa yang telah kamu lakukan terhadap Yusuf dan saudaranya ketika kamu tidak mengetahui (akibat) perbuatanmu itu?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,90,'قَالُوْٓا ءَاِنَّكَ لَاَنْتَ يُوْسُفُۗ قَالَ اَنَا۠ يُوْسُفُ وَهٰذَآ اَخِيْ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۗ اِنَّهٗ مَنْ يَّتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ','Mereka berkata: "Apakah kamu ini benar-benar Yusuf?". Yusuf menjawab: "Akulah Yusuf dan ini saudaraku. Sesungguhnya Allah telah melimpahkan karunia-Nya kepada kami". Sesungguhnya barang siapa yang bertakwa dan bersabar, maka sesungguhnya Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,91,'قَالُوْا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخٰطِـِٕيْنَ','Mereka berkata: "Demi Allah, sesungguhnya Allah telah melebihkan kamu atas kami, dan sesungguhnya kami adalah orang-orang yang bersalah (berdosa)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,92,'قَالَ لَا تَثْرِيْبَ عَلَيْكُمُ الْيَوْمَۗ يَغْفِرُ اللّٰهُ لَكُمْ ۖوَهُوَ اَرْحَمُ الرّٰحِمِيْنَ','Dia (Yusuf) berkata: "Pada hari ini tak ada cercaan terhadap kamu, mudah-mudahan Allah mengampuni (kamu), dan Dia adalah Maha Penyayang diantara para penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,93,'اِذْهَبُوْا بِقَمِيْصِيْ هٰذَا فَاَلْقُوْهُ عَلٰى وَجْهِ اَبِيْ يَأْتِ بَصِيْرًا ۚوَأْتُوْنِيْ بِاَهْلِكُمْ اَجْمَعِيْنَ ࣖ','Pergilah kamu dengan membawa baju gamisku ini, lalu letakkanlah dia kewajah ayahku, nanti ia akan melihat kembali; dan bawalah keluargamu semuanya kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,94,'وَلَمَّا فَصَلَتِ الْعِيْرُ قَالَ اَبُوْهُمْ اِنِّيْ لَاَجِدُ رِيْحَ يُوْسُفَ لَوْلَآ اَنْ تُفَنِّدُوْنِ','Tatkala kafilah itu telah ke luar (dari negeri Mesir) berkata ayah mereka: "Sesungguhnya aku mencium bau Yusuf, sekiranya kamu tidak menuduhku lemah akal (tentu kamu membenarkan aku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,95,'قَالُوْا تَاللّٰهِ اِنَّكَ لَفِيْ ضَلٰلِكَ الْقَدِيْمِ','Keluarganya berkata: "Demi Allah, sesungguhnya kamu masih dalam kekeliruanmu yang dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,96,'فَلَمَّآ اَنْ جَاۤءَ الْبَشِيْرُ اَلْقٰىهُ عَلٰى وَجْهِهٖ فَارْتَدَّ بَصِيْرًاۗ قَالَ اَلَمْ اَقُلْ لَّكُمْۙ اِنِّيْٓ اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ','Tatkala telah tiba pembawa kabar gembira itu, maka diletakkannya baju gamis itu ke wajah Ya´qub, lalu kembalilah dia dapat melihat. Berkata Ya´qub: "Tidakkah aku katakan kepadamu, bahwa aku mengetahui dari Allah apa yang kamu tidak mengetahuinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,97,'قَالُوْا يٰٓاَبَانَا اسْتَغْفِرْ لَنَا ذُنُوْبَنَآ اِنَّا كُنَّا خٰطِـِٕيْنَ','Mereka berkata: "Wahai ayah kami, mohonkanlah ampun bagi kami terhadap dosa-dosa kami, sesungguhnya kami adalah orang-orang yang bersalah (berdosa)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,98,'قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبِّيْ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ','Ya´qub berkata: "Aku akan memohonkan ampun bagimu kepada Tuhanku. Sesungguhnya Dialah Yang Maha Pengampun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,99,'فَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوْا مِصْرَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَ ۗ ','Maka tatkala mereka masuk ke (tempat) Yusuf: Yusuf merangkul ibu bapanya dan dia berkata: "Masuklah kamu ke negeri Mesir, insya Allah dalam keadaan aman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,100,'وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوْا لَهٗ سُجَّدًاۚ وَقَالَ يٰٓاَبَتِ هٰذَا تَأْوِيْلُ رُءْيَايَ مِنْ قَبْلُ  ۖقَدْ جَعَلَهَا رَبِّيْ حَقًّاۗ وَقَدْ اَحْسَنَ بِيْٓ اِذْ اَخْرَجَنِيْ مِنَ السِّجْنِ وَجَاۤءَ بِكُمْ مِّنَ الْبَدْوِ مِنْۢ بَعْدِ اَنْ نَّزَغَ الشَّيْطٰنُ بَيْنِيْ وَبَيْنَ اِخْوَتِيْۗ اِنَّ رَبِّيْ لَطِيْفٌ لِّمَا يَشَاۤءُ ۗاِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ','Dan ia menaikkan kedua ibu-bapanya ke atas singgasana. Dan mereka (semuanya) merebahkan diri seraya sujud kepada Yusuf. Dan berkata Yusuf: "Wahai ayahku inilah ta´bir mimpiku yang dahulu itu; sesungguhnya Tuhanku telah menjadikannya suatu kenyataan. Dan sesungguhnya Tuhanku telah berbuat baik kepadaku, ketika Dia membebaskan aku dari rumah penjara dan ketika membawa kamu dari dusun padang pasir, setelah syaitan merusakkan (hubungan) antaraku dan saudara-saudaraku. Sesungguhnya Tuhanku Maha Lembut terhadap apa yang Dia kehendaki. Sesungguhnya Dialah Yang Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,101,'۞ رَبِّ قَدْ اٰتَيْتَنِيْ مِنَ الْمُلْكِ وَعَلَّمْتَنِيْ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۚ فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِۗ اَنْتَ وَلِيّٖ فِى الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّنِيْ مُسْلِمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ','Ya Tuhanku, sesungguhnya Engkau telah menganugerahkan kepadaku sebahagian kerajaan dan telah mengajarkan kepadaku sebahagian ta´bir mimpi. (Ya Tuhan) Pencipta langit dan bumi. Engkaulah Pelindungku di dunia dan di akhirat, wafatkanlah aku dalam keadaan Islam dan gabungkanlah aku dengan orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,102,'ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُوْٓا اَمْرَهُمْ وَهُمْ يَمْكُرُوْنَ','Demikian itu (adalah) diantara berita-berita yang ghaib yang Kami wahyukan kepadamu (Muhammad); padahal kamu tidak berada pada sisi mereka, ketika mereka memutuskan rencananya (untuk memasukkan Yusuf ke dalam sumur) dan mereka sedang mengatur tipu daya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,103,'وَمَآ اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِيْنَ','Dan sebahagian besar manusia tidak akan beriman -- walaupun kamu sangat menginginkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,104,'وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۗ اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ ࣖ','Dan kamu sekali-kali tidak meminta upah kepada mereka (terhadap seruanmu ini), itu tidak lain hanyalah pengajaran bagi semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,105,'وَكَاَيِّنْ مِّنْ اٰيَةٍ فِى السَّمٰوٰتِ وَالْاَرْضِ يَمُرُّوْنَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُوْنَ','Dan banyak sekali tanda-tanda (kekuasaan Allah) di langit dan di bumi yang mereka melaluinya, sedang mereka berpaling dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,106,'وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُّشْرِكُوْنَ','Dan sebahagian besar dari mereka tidak beriman kepada Allah, melainkan dalam keadaan mempersekutukan Allah (dengan sembahan-sembahan lain).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,107,'اَفَاَمِنُوْٓا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِّنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ','Apakah mereka merasa aman dari kedatangan siksa Allah yang meliputi mereka, atau kedatangan kiamat kepada mereka secara mendadak, sedang mereka tidak menyadarinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,108,'قُلْ هٰذِهٖ سَبِيْلِيْٓ اَدْعُوْٓا اِلَى اللّٰهِ ۗعَلٰى بَصِيْرَةٍ اَنَا۠ وَمَنِ اتَّبَعَنِيْ ۗوَسُبْحٰنَ اللّٰهِ وَمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَ','Katakanlah: "Inilah jalan (agama)ku, aku dan orang-orang yang mengikutiku mengajak (kamu) kepada Allah dengan hujjah yang nyata, Maha Suci Allah, dan aku tiada termasuk orang-orang yang musyrik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,109,'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ مِّنْ اَهْلِ الْقُرٰىۗ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اتَّقَوْاۗ اَفَلَا تَعْقِلُوْنَ','Kami tidak mengutus sebelum kamu, melainkan orang laki-laki yang Kami berikan wahyu kepadanya diantara penduduk negeri. Maka tidakkah mereka bepergian di muka bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul) dan sesungguhnya kampung akhirat adalah lebih baik bagi orang-orang yang bertakwa. Maka tidakkah kamu memikirkannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,110,'حَتّٰٓى اِذَا اسْتَا۟يْـَٔسَ الرُّسُلُ وَظَنُّوْٓا اَنَّهُمْ قَدْ كُذِبُوْا جَاۤءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَّشَاۤءُ ۗوَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِيْنَ','Sehingga apabila para rasul tidak mempunyai harapan lagi (tentang keimanan mereka) dan telah meyakini bahwa mereka telah didustakan, datanglah kepada para rasul itu pertolongan Kami, lalu diselamatkan orang-orang yang Kami kehendaki. Dan tidak dapat ditolak siksa Kami dari pada orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,12,111,'لَقَدْ كَانَ فِيْ قَصَصِهِمْ عِبْرَةٌ لِّاُولِى الْاَلْبَابِۗ مَا كَانَ حَدِيْثًا يُّفْتَرٰى وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ كُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ࣖ','Sesungguhnya pada kisah-kisah mereka itu terdapat pengajaran bagi orang-orang yang mempunyai akal. Al Quran itu bukanlah cerita yang dibuat-buat, akan tetapi membenarkan (kitab-kitab) yang sebelumnya dan menjelaskan segala sesuatu, dan sebagai petunjuk dan rahmat bagi kaum yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,1,'الۤمّۤرٰۗ تِلْكَ اٰيٰتُ الْكِتٰبِۗ وَالَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ ','Alif laam miim raa. Ini adalah ayat-ayat Al Kitab (Al Quran). Dan Kitab yang diturunkan kepadamu daripada Tuhanmu itu adalah benar: akan tetapi kebanyakan manusia tidak beriman (kepadanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,2,'اَللّٰهُ الَّذِيْ رَفَعَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ  كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيٰتِ لَعَلَّكُمْ بِلِقَاۤءِ رَبِّكُمْ تُوْقِنُوْنَ ','Allah-lah Yang meninggikan langit tanpa tiang (sebagaimana) yang kamu lihat, kemudian Dia bersemayam di atas ´Arasy, dan menundukkan matahari dan bulan. Masing-masing beredar hingga waktu yang ditentukan. Allah mengatur urusan (makhluk-Nya), menjelaskan tanda-tanda (kebesaran-Nya), supaya kamu meyakini pertemuan(mu) dengan Tuhanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,3,'وَهُوَ الَّذِيْ مَدَّ الْاَرْضَ وَجَعَلَ فِيْهَا رَوَاسِيَ وَاَنْهٰرًا ۗوَمِنْ كُلِّ الثَّمَرٰتِ جَعَلَ فِيْهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِى الَّيْلَ النَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ ','Dan Dialah Tuhan yang membentangkan bumi dan menjadikan gunung-gunung dan sungai-sungai padanya. Dan menjadikan padanya semua buah-buahan berpasang-pasangan, Allah menutupkan malam kepada siang. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi kaum yang memikirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,4,'وَفِى الْاَرْضِ قِطَعٌ مُّتَجٰوِرٰتٌ وَّجَنّٰتٌ مِّنْ اَعْنَابٍ وَّزَرْعٌ وَّنَخِيْلٌ صِنْوَانٌ وَّغَيْرُ صِنْوَانٍ يُّسْقٰى بِمَاۤءٍ وَّاحِدٍۙ وَّنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِى الْاُكُلِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ','Dan di bumi ini terdapat bagian-bagian yang berdampingan, dan kebun-kebun anggur, tanaman-tanaman dan pohon korma yang bercabang dan yang tidak bercabang, disirami dengan air yang sama. Kami melebihkan sebahagian tanam-tanaman itu atas sebahagian yang lain tentang rasanya. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi kaum yang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,5,'۞ وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَابًا ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْۚ وَاُولٰۤىِٕكَ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ','Dan jika (ada sesuatu) yang kamu herankan, maka yang patut mengherankan adalah ucapan mereka: "Apabila kami telah menjadi tanah, apakah kami sesungguhnya akan (dikembalikan) menjadi makhluk yang baru?" Orang-orang itulah yang kafir kepada Tuhannya; dan orang-orang itulah (yang dilekatkan) belenggu di lehernya; mereka itulah penghuni neraka, mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,6,'وَيَسْتَعْجِلُوْنَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلٰتُۗ وَاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ لِّلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَدِيْدُ الْعِقَابِ ','Mereka meminta kepadamu supaya disegerakan (datangnya) siksa, sebelum (mereka meminta) kebaikan, padahal telah terjadi bermacam-macam contoh siksa sebelum mereka. Sesungguhnya Tuhanmu benar-benar mempunyai ampunan (yang luas) bagi manusia sekalipun mereka zalim, dan sesungguhnya Tuhanmu benar-benar sangat keras siksanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,7,'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ اِنَّمَآ اَنْتَ مُنْذِرٌ وَّلِكُلِّ قَوْمٍ هَادٍ ࣖ ','Orang-orang yang kafir berkata: "Mengapa tidak diturunkan kepadanya (Muhammad) suatu tanda (kebesaran) dari Tuhannya?" Sesungguhnya kamu hanyalah seorang pemberi peringatan; dan bagi tiap-tiap kaum ada orang yang memberi petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,8,'اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغِيْضُ الْاَرْحَامُ وَمَا تَزْدَادُ ۗوَكُلُّ شَيْءٍ عِنْدَهٗ بِمِقْدَارٍ ','Allah mengetahui apa yang dikandung oleh setiap perempuan, dan kandungan rahim yang kurang sempurna dan yang bertambah. Dan segala sesuatu pada sisi-Nya ada ukurannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,9,'عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيْرُ الْمُتَعَالِ ','Yang mengetahui semua yang ghaib dan yang nampak; Yang Maha Besar lagi Maha Tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,10,'سَوَاۤءٌ مِّنْكُمْ مَّنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهٖ وَمَنْ هُوَ مُسْتَخْفٍۢ بِالَّيْلِ وَسَارِبٌۢ بِالنَّهَارِ ','Sama saja (bagi Tuhan), siapa diantaramu yang merahasiakan ucapannya, dan siapa yang berterus-terang dengan ucapan itu, dan siapa yang bersembunyi di malam hari dan yang berjalan (menampakkan diri) di siang hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,11,'لَهٗ مُعَقِّبٰتٌ مِّنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ يَحْفَظُوْنَهٗ مِنْ اَمْرِ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۗ وَاِذَآ اَرَادَ اللّٰهُ بِقَوْمٍ سُوْۤءًا فَلَا مَرَدَّ لَهٗ ۚوَمَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّالٍ ','Bagi manusia ada malaikat-malaikat yang selalu mengikutinya bergiliran, di muka dan di belakangnya, mereka menjaganya atas perintah Allah. Sesungguhnya Allah tidak merubah keadaan sesuatu kaum sehingga mereka merubah keadaan yang ada pada diri mereka sendiri. Dan apabila Allah menghendaki keburukan terhadap sesuatu kaum, maka tak ada yang dapat menolaknya; dan sekali-kali tak ada pelindung bagi mereka selain Dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,12,'هُوَ الَّذِيْ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنْشِئُ السَّحَابَ الثِّقَالَۚ  ','Dialah Tuhan yang memperlihatkan kilat kepadamu untuk menimbulkan ketakutan dan harapan, dan Dia mengadakan awan mendung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,13,'وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهٖ وَالْمَلٰۤىِٕكَةُ مِنْ خِيْفَتِهٖۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيْبُ بِهَا مَنْ يَّشَاۤءُ وَهُمْ يُجَادِلُوْنَ فِى اللّٰهِ ۚوَهُوَ شَدِيْدُ الْمِحَالِۗ  ','Dan guruh itu bertasbih dengan memuji Allah, (demikian pula) para malaikat karena takut kepada-Nya, dan Allah melepaskan halilintar, lalu menimpakannya kepada siapa yang Dia kehendaki, dan mereka berbantah-bantahan tentang Allah, dan Dialah Tuhan Yang Maha keras siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,14,'لَهٗ دَعْوَةُ الْحَقِّۗ وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَجِيْبُوْنَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَاۤءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهٖۗ وَمَا دُعَاۤءُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ ','Hanya bagi Allah-lah (hak mengabulkan) doa yang benar. Dan berhala-berhala yang mereka sembah selain Allah tidak dapat memperkenankan sesuatupun bagi mereka, melainkan seperti orang yang membukakan kedua telapak tangannya ke dalam air supaya sampai air ke mulutnya, padahal air itu tidak dapat sampai ke mulutnya. Dan doa (ibadat) orang-orang kafir itu, hanyalah sia-sia belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,15,'وَلِلّٰهِ يَسْجُدُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّظِلٰلُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ ۩','Hanya kepada Allah-lah sujud (patuh) segala apa yang di langit dan di bumi, baik dengan kemauan sendiri ataupun terpaksa (dan sujud pula) bayang-bayangnya di waktu pagi dan petang hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,16,'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۗقُلْ اَفَاتَّخَذْتُمْ مِّنْ دُوْنِهٖٓ اَوْلِيَاۤءَ لَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ نَفْعًا وَّلَا ضَرًّاۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ اَمْ هَلْ تَسْتَوِى الظُّلُمٰتُ وَالنُّوْرُ ەۚ اَمْ جَعَلُوْا لِلّٰهِ شُرَكَاۤءَ خَلَقُوْا كَخَلْقِهٖ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْۗ قُلِ اللّٰهُ خَالِقُ كُلِّ شَيْءٍ وَّهُوَ الْوَاحِدُ الْقَهَّارُ','Katakanlah: "Siapakah Tuhan langit dan bumi?" Jawabnya: "Allah". Katakanlah: "Maka patutkah kamu mengambil pelindung-pelindungmu dari selain Allah, padahal mereka tidak menguasai kemanfaatan dan tidak (pula) kemudharatan bagi diri mereka sendiri?". Katakanlah: "Adakah sama orang buta dan yang dapat melihat, atau samakah gelap gulita dan terang benderang; apakah mereka menjadikan beberapa sekutu bagi Allah yang dapat menciptakan seperti ciptaan-Nya sehingga kedua ciptaan itu serupa menurut pandangan mereka?" Katakanlah: "Allah adalah Pencipta segala sesuatu dan Dialah Tuhan Yang Maha Esa lagi Maha Perkasa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,17,'اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَالَتْ اَوْدِيَةٌ ۢ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَّابِيًا ۗوَمِمَّا يُوْقِدُوْنَ عَلَيْهِ فِى النَّارِ ابْتِغَاۤءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِّثْلُهٗ  ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَ ەۗ فَاَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاۤءً ۚوَاَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِى الْاَرْضِۗ  كَذٰلِكَ يَضْرِبُ اللّٰهُ الْاَمْثَالَ ۗ','Allah telah menurunkan air (hujan) dari langit, maka mengalirlah air di lembah-lembah menurut ukurannya, maka arus itu membawa buih yang mengambang. Dan dari apa (logam) yang mereka lebur dalam api untuk membuat perhiasan atau alat-alat, ada (pula) buihnya seperti buih arus itu. Demikianlah Allah membuat perumpamaan (bagi) yang benar dan yang bathil. Adapun buih itu, akan hilang sebagai sesuatu yang tak ada harganya; adapun yang memberi manfaat kepada manusia, maka ia tetap di bumi. Demikianlah Allah membuat perumpamaan-perumpamaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,18,'لِلَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمُ الْحُسْنٰىۗ وَالَّذِيْنَ لَمْ يَسْتَجِيْبُوْا لَهٗ لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ ۗ اُولٰۤىِٕكَ لَهُمْ سُوْۤءُ الْحِسَابِ ەۙ وَمَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ ࣖ','Bagi orang-orang yang memenuhi seruan Tuhannya, (disediakan) pembalasan yang baik. Dan orang-orang yang tidak memenuhi seruan Tuhan, sekiranya mereka mempunyai semua (kekayaan) yang ada di bumi dan (ditambah) sebanyak isi bumi itu lagi besertanya, niscaya mereka akan menebus dirinya dengan kekayaan itu. Orang-orang itu disediakan baginya hisab yang buruk dan tempat kediaman mereka ialah Jahanam dan itulah seburuk-buruk tempat kediaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,19,'۞ اَفَمَنْ يَّعْلَمُ اَنَّمَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِۙ  ','Adakah orang yang mengetahui bahwasanya apa yang diturunkan kepadamu dari Tuhanmu itu benar sama dengan orang yang buta? Hanyalah orang-orang yang berakal saja yang dapat mengambil pelajaran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,20,'الَّذِيْنَ يُوْفُوْنَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُوْنَ الْمِيْثَاقَۙ  ','(yaitu) orang-orang yang memenuhi janji Allah dan tidak merusak perjanjian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,21,'وَالَّذِيْنَ يَصِلُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُوْنَ سُوْۤءَ الْحِسَابِ ۗ   ','dan orang-orang yang menghubungkan apa-apa yang Allah perintahkan supaya dihubungkan, dan mereka takut kepada Tuhannya dan takut kepada hisab yang buruk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,22,'وَالَّذِيْنَ صَبَرُوا ابْتِغَاۤءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً وَّيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ اُولٰۤىِٕكَ لَهُمْ عُقْبَى الدَّارِۙ  ','Dan orang-orang yang sabar karena mencari keridhaan Tuhannya, mendirikan shalat, dan menafkahkan sebagian rezeki yang Kami berikan kepada mereka, secara sembunyi atau terang-terangan serta menolak kejahatan dengan kebaikan; orang-orang itulah yang mendapat tempat kesudahan (yang baik),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,23,'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ وَالْمَلٰۤىِٕكَةُ يَدْخُلُوْنَ عَلَيْهِمْ مِّنْ كُلِّ بَابٍۚ ','(yaitu) surga ´Adn yang mereka masuk ke dalamnya bersama-sama dengan orang-orang yang saleh dari bapak-bapaknya, isteri-isterinya dan anak cucunya, sedang malaikat-malaikat masuk ke tempat-tempat mereka dari semua pintu;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,24,'سَلٰمٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۗ   ','(sambil mengucapkan): "Salamun ´alaikum bima shabartum". Maka alangkah baiknya tempat kesudahan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,25,'وَالَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْ ۢ بَعْدِ مِيْثَاقِهٖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۙ اُولٰۤىِٕكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ ','Orang-orang yang merusak janji Allah setelah diikrarkan dengan teguh dan memutuskan apa-apa yang Allah perintahkan supaya dihubungkan dan mengadakan kerusakan di bumi, orang-orang itulah yang memperoleh kutukan dan bagi mereka tempat kediaman yang buruk (Jahannam).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,26,'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗوَفَرِحُوْا بِالْحَيٰوةِ الدُّنْيَاۗ وَمَا الْحَيٰوةُ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا مَتَاعٌ  ࣖ  ','Allah meluaskan rezeki dan menyempitkannya bagi siapa yang Dia kehendaki. Mereka bergembira dengan kehidupan di dunia, padahal kehidupan dunia itu (dibanding dengan) kehidupan akhirat, hanyalah kesenangan (yang sedikit).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,27,'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ  قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ اَنَابَۖ  ','Orang-orang kafir berkata: "Mengapa tidak diturunkan kepadanya (Muhammad) tanda (mukjizat) dari Tuhannya?" Katakanlah: "Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan menunjuki orang-orang yang bertaubat kepada-Nya",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,28,'الَّذِيْنَ اٰمَنُوْا وَتَطْمَىِٕنُّ قُلُوْبُهُمْ بِذِكْرِ اللّٰهِ ۗ اَلَا بِذِكْرِ اللّٰهِ تَطْمَىِٕنُّ الْقُلُوْبُ ۗ   ','(yaitu) orang-orang yang beriman dan hati mereka manjadi tenteram dengan mengingat Allah. Ingatlah, hanya dengan mengingati Allah-lah hati menjadi tenteram.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,29,'اَلَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ طُوْبٰى لَهُمْ وَحُسْنُ مَاٰبٍ ','Orang-orang yang beriman dan beramal saleh, bagi mereka kebahagiaan dan tempat kembali yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,30,'كَذٰلِكَ اَرْسَلْنٰكَ فِيْٓ اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَآ اُمَمٌ لِّتَتْلُوَا۟ عَلَيْهِمُ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَهُمْ يَكْفُرُوْنَ بِالرَّحْمٰنِۗ قُلْ هُوَ رَبِّيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ ','Demikianlah, Kami telah mengutus kamu pada suatu umat yang sungguh telah berlalu beberapa umat sebelumnya, supaya kamu membacakan kepada mereka (Al Quran) yang Kami wahyukan kepadamu, padahal mereka kafir kepada Tuhan Yang Maha Pemurah. Katakanlah: "Dialah Tuhanku tidak ada Tuhan selain Dia; hanya kepada-Nya aku bertawakkal dan hanya kepada-Nya aku bertaubat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,31,'وَلَوْ اَنَّ قُرْاٰنًا سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۗ بَلْ لِّلّٰهِ الْاَمْرُ جَمِيْعًاۗ اَفَلَمْ يَا۟يْـَٔسِ الَّذِيْنَ اٰمَنُوْٓا اَنْ لَّوْ يَشَاۤءُ اللّٰهُ لَهَدَى النَّاسَ جَمِيْعًاۗ وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا تُصِيْبُهُمْ بِمَا صَنَعُوْا قَارِعَةٌ اَوْ تَحُلُّ قَرِيْبًا مِّنْ دَارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ ࣖ ','Dan sekiranya ada suatu bacaan (kitab suci) yang dengan bacaan itu gunung-gunung dapat digoncangkan atau bumi jadi terbelah atau oleh karenanya orang-orang yang sudah mati dapat berbicara, (tentulah Al Quran itulah dia). Sebenarnya segala urusan itu adalah kepunyaan Allah. Maka tidakkah orang-orang yang beriman itu mengetahui bahwa seandainya Allah menghendaki (semua manusia beriman), tentu Allah memberi petunjuk kepada manusia semuanya. Dan orang-orang yang kafir senantiasa ditimpa bencana disebabkan perbuatan mereka sendiri atau bencana itu terjadi dekat tempat kediaman mereka, sehingga datanglah janji Allah. Sesungguhnya Allah tidak menyalahi janji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,32,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذِيْنَ كَفَرُوْا ثُمَّ اَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ ','Dan sesungguhnya telah diperolok-olokkan beberapa rasul sebelum kamu, maka Aku beri tangguh kepada orang-orang kafir itu kemudian Aku binasakan mereka. Alangkah hebatnya siksaan-Ku itu!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,33,'اَفَمَنْ هُوَ قَاۤىِٕمٌ عَلٰى كُلِّ نَفْسٍۢ بِمَا كَسَبَتْۚ وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ ۗ قُلْ سَمُّوْهُمْۗ اَمْ تُنَبِّـُٔوْنَهٗ بِمَا لَا يَعْلَمُ فِى الْاَرْضِ اَمْ بِظَاهِرٍ مِّنَ الْقَوْلِ ۗبَلْ زُيِّنَ لِلَّذِيْنَ كَفَرُوْا مَكْرُهُمْ وَصُدُّوْا عَنِ السَّبِيْلِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ ','Maka apakah Tuhan yang menjaga setiap diri terhadap apa yang diperbuatnya (sama dengan yang tidak demikian sifatnya)? Mereka menjadikan beberapa sekutu bagi Allah. Katakanlah: "Sebutkanlah sifat-sifat mereka itu". Atau apakah kamu hendak memberitakan kepada Allah apa yang tidak diketahui-Nya di bumi, atau kamu mengatakan (tentang hal itu) sekadar perkataan pada lahirnya saja. Sebenarnya orang-orang kafir itu dijadikan (oleh syaitan) memandang baik tipu daya mereka dan dihalanginya dari jalan (yang benar). Dan barangsiapa yang disesatkan Allah, maka baginya tak ada seorangpun yang akan memberi petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,34,'لَهُمْ عَذَابٌ فِى الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ ','Bagi mereka azab dalam kehidupan dunia dan sesungguhnya azab akhirat adalah lebih keras dan tak ada bagi mereka seorang pelindungpun dari (azab) Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,35,'۞ مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ  اُكُلُهَا دَاۤىِٕمٌ وَّظِلُّهَاۗ تِلْكَ عُقْبَى الَّذِيْنَ اتَّقَوْا ۖوَّعُقْبَى الْكٰفِرِيْنَ النَّارُ ','Perumpamaan surga yang dijanjikan kepada orang-orang yang takwa ialah (seperti taman); mengalir sungai-sungai di dalamnya; buahnya tak henti-henti sedang naungannya (demikian pula). Itulah tempat kesudahan bagi orang-orang yang bertakwa, sedang tempat kesudahan bagi orang-orang kafir ialah neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,36,'وَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَفْرَحُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُّنْكِرُ بَعْضَهٗ ۗ قُلْ اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَآ اُشْرِكَ بِهٖ ۗاِلَيْهِ اَدْعُوْا وَاِلَيْهِ مَاٰبِ ','Orang-orang yang telah Kami berikan kitab kepada mereka bergembira dengan kitab yang diturunkan kepadamu, dan di antara golongan-golongan (Yahudi dan Nasrani) yang bersekutu, ada yang mengingkari sebahagiannya. Katakanlah "Sesungguhnya aku hanya diperintah untuk menyembah Allah dan tidak mempersekutukan sesuatupun dengan Dia. Hanya kepada-Nya aku seru (manusia) dan hanya kepada-Nya aku kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,37,'وَكَذٰلِكَ اَنْزَلْنٰهُ حُكْمًا عَرَبِيًّاۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَمَا جَاۤءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا وَاقٍ ࣖ ','Dan demikianlah, Kami telah menurunkan Al Quran itu sebagai peraturan (yang benar) dalam bahasa Arab. Dan seandainya kamu mengikuti hawa nafsu mereka setelah datang pengetahuan kepadamu, maka sekali-kali tidak ada pelindung dan pemelihara bagimu terhadap (siksa) Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,38,'وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجًا وَّذُرِّيَّةً ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗلِكُلِّ اَجَلٍ كِتَابٌ ','Dan sesungguhnya Kami telah mengutus beberapa Rasul sebelum kamu dan Kami memberikan kepada mereka isteri-isteri dan keturunan. Dan tidak ada hak bagi seorang Rasul mendatangkan sesuatu ayat (mukjizat) melainkan dengan izin Allah. Bagi tiap-tiap masa ada Kitab (yang tertentu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,39,'يَمْحُوا اللّٰهُ مَا يَشَاۤءُ وَيُثْبِتُ ۚوَعِنْدَهٗٓ اُمُّ الْكِتٰبِ ','Allah menghapuskan apa yang Dia kehendaki dan menetapkan (apa yang Dia kehendaki), dan di sisi-Nya-lah terdapat Ummul-Kitab (Lauh mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,40,'وَاِنْ مَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلٰغُ وَعَلَيْنَا الْحِسَابُ ','Dan jika Kami perlihatkan kepadamu sebahagian (siksa) yang Kami ancamkan kepada mereka atau Kami wafatkan kamu (hal itu tidak penting bagimu) karena sesungguhnya tugasmu hanya menyampaikan saja, sedang Kami-lah yang menghisab amalan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,41,'اَوَلَمْ يَرَوْا اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهٖۗ وَهُوَ سَرِيْعُ الْحِسَابِ ','Dan apakah mereka tidak melihat bahwa sesungguhnya Kami mendatangi daerah-daerah (orang-orang kafir), lalu Kami kurangi daerah-daerah itu (sedikit demi sedikit) dari tepi-tepinya? Dan Allah menetapkan hukum (menurut kehendak-Nya), tidak ada yang dapat menolak ketetapan-Nya; dan Dialah Yang Maha cepat hisab-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,42,'وَقَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَمِيْعًا ۗيَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۗ وَسَيَعْلَمُ الْكُفّٰرُ لِمَنْ عُقْبَى الدَّارِ ','Dan sungguh orang-orang kafir yang sebelum mereka (kafir Mekah) telah mengadakan tipu daya, tetapi semua tipu daya itu adalah dalam kekuasaan Allah. Dia mengetahui apa yang diusahakan oleh setiap diri, dan orang-orang kafir akan mengetahui untuk siapa tempat kesudahan (yang baik) itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,13,43,'وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَسْتَ مُرْسَلًا ۗ قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۙ وَمَنْ عِنْدَهٗ عِلْمُ الْكِتٰبِ ࣖ ','Berkatalah orang-orang kafir: "Kamu bukan seorang yang dijadikan Rasul". Katakanlah: "Cukuplah Allah menjadi saksi antaraku dan kamu, dan antara orang yang mempunyai ilmu Al Kitab".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,1,'الۤرٰ ۗ كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ بِاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِۙ','Alif, laam raa. (Ini adalah) Kitab yang Kami turunkan kepadamu supaya kamu mengeluarkan manusia dari gelap gulita kepada cahaya terang benderang dengan izin Tuhan mereka, (yaitu) menuju jalan Tuhan Yang Maha Perkasa lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,2,'اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَوَيْلٌ لِّلْكٰفِرِيْنَ مِنْ عَذَابٍ شَدِيْدٍۙ  ','Allah-lah yang memiliki segala apa yang di langit dan di bumi. Dan kecelakaanlah bagi orang-orang kafir karena siksaan yang sangat pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,3,' ۨالَّذِيْنَ يَسْتَحِبُّوْنَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًا ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍۢ بَعِيْدٍ','(yaitu) orang-orang yang lebih menyukai kehidupan dunia dari pada kehidupan akhirat, dan menghalang-halangi (manusia) dari jalan Allah dan menginginkan agar jalan Allah itu bengkok. Mereka itu berada dalam kesesatan yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,4,'وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا بِلِسَانِ قَوْمِهٖ لِيُبَيِّنَ لَهُمْ ۗفَيُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Kami tidak mengutus seorang rasulpun, melainkan dengan bahasa kaumnya, supaya ia dapat memberi penjelasan dengan terang kepada mereka. Maka Allah menyesatkan siapa yang Dia kehendaki, dan memberi petunjuk kepada siapa yang Dia kehendaki. Dan Dialah Tuhan Yang Maha Kuasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,5,'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ وَذَكِّرْهُمْ بِاَيّٰىمِ اللّٰهِ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ','Dan sesungguhnya Kami telah mengutus Musa dengan membawa ayat-ayat Kami, (dan Kami perintahkan kepadanya): "Keluarkanlah kaummu dari gelap gulita kepada cahaya terang benderang dan ingatkanlah mereka kepada hari-hari Allah". Sesunguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang penyabar dan banyak bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,6,'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰىكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ وَيُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗوَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ࣖ','Dan (ingatlah), ketika Musa berkata kepada kaumnya: "Ingatlah nikmat Allah atasmu ketika Dia menyelamatkan kamu dari (Fir´aun dan) pengikut-pengikutnya, mereka menyiksa kamu dengan siksa yang pedih, mereka menyembelih anak-anak laki-lakimu, membiarkan hidup anak-anak perempuanmu; dan pada yang demikian itu ada cobaan yang besar dari Tuhanmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,7,'وَاِذْ تَاَذَّنَ رَبُّكُمْ لَىِٕنْ شَكَرْتُمْ لَاَزِيْدَنَّكُمْ وَلَىِٕنْ كَفَرْتُمْ اِنَّ عَذَابِيْ لَشَدِيْدٌ ','Dan (ingatlah juga), tatkala Tuhanmu memaklumkan; "Sesungguhnya jika kamu bersyukur, pasti Kami akan menambah (nikmat) kepadamu, dan jika kamu mengingkari (nikmat-Ku), maka sesungguhnya azab-Ku sangat pedih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,8,'وَقَالَ مُوْسٰٓى اِنْ تَكْفُرُوْٓا اَنْتُمْ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۙفَاِنَّ اللّٰهَ لَغَنِيٌّ حَمِيْدٌ ','Dan Musa berkata: "Jika kamu dan orang-orang yang ada di muka bumi semuanya mengingkari (nikmat Allah) maka sesungguhnya Allah Maha Kaya lagi Maha Terpuji".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,9,'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ مِنْ قَبْلِكُمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۗ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُ  ۗجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرَدُّوْٓا اَيْدِيَهُمْ فِيْٓ اَفْوَاهِهِمْ وَقَالُوْٓا اِنَّا كَفَرْنَا بِمَآ اُرْسِلْتُمْ بِهٖ وَاِنَّا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَنَآ اِلَيْهِ مُرِيْبٍ','Belumkah sampai kepadamu berita orang-orang sebelum kamu (yaitu) kaum Nuh, ´Ad, Tsamud dan orang-orang sesudah mereka. Tidak ada yang mengetahui mereka selain Allah. Telah datang rasul-rasul kepada mereka (membawa) bukti-bukti yang nyata lalu mereka menutupkan tangannya ke mulutnya (karena kebencian), dan berkata: "Sesungguhnya kami mengingkari apa yang kamu disuruh menyampaikannya (kepada kami), dan sesungguhnya kami benar-benar dalam keragu-raguan yang menggelisahkan terhadap apa yang kamu ajak kami kepadanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,10,'۞ قَالَتْ رُسُلُهُمْ اَفِى اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِۗ يَدْعُوْكُمْ لِيَغْفِرَ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ قَالُوْٓا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَا ۗ تُرِيْدُوْنَ اَنْ تَصُدُّوْنَا عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُنَا فَأْتُوْنَا بِسُلْطٰنٍ مُّبِيْنٍ ','Berkata rasul-rasul mereka: "Apakah ada keragu-raguan terhadap Allah, Pencipta langit dan bumi? Dia menyeru kamu untuk memberi ampunan kepadamu dari dosa-dosamu dan menangguhkan (siksaan)mu sampai masa yang ditentukan?" Mereka berkata: "Kamu tidak lain hanyalah manusia seperti kami juga. Kamu menghendaki untuk menghalang-halangi (membelokkan) kami dari apa yang selalu disembah nenek moyang kami, karena itu datangkanlah kepada kami, bukti yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,11,'قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَّحْنُ اِلَّا بَشَرٌ مِّثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَمَا كَانَ لَنَآ اَنْ نَّأْتِيَكُمْ بِسُلْطٰنٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ','Rasul-rasul mereka berkata kepada mereka: "Kami tidak lain hanyalah manusia seperti kamu, akan tetapi Allah memberi karunia kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Dan tidak patut bagi kami mendatangkan suatu bukti kepada kamu melainkan dengan izin Allah. Dan hanya kepada Allah sajalah hendaknya orang-orang mukmin bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,12,'وَمَا لَنَآ اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰىنَا سُبُلَنَاۗ وَلَنَصْبِرَنَّ عَلٰى مَآ اٰذَيْتُمُوْنَاۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ ࣖ ','Mengapa kami tidak akan bertawakkal kepada Allah padahal Dia telah menunjukkan jalan kepada kami, dan kami sungguh-sungguh akan bersabar terhadap gangguan-gangguan yang kamu lakukan kepada kami. Dan hanya kepada Allah saja orang-orang yang bertawakkal itu, berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,13,'وَقَالَ الَّذِيْنَ كَفَرُوْا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِّنْ اَرْضِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظّٰلِمِيْنَ ۗ   ','Orang-orang kafir berkata kepada Rasul-rasul mereka: "Kami sungguh-sungguh akan mengusir kamu dari negeri kami atau kamu kembali kepada agama kami". Maka Tuhan mewahyukan kepada mereka: "Kami pasti akan membinasakan orang-orang yang zalim itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,14,'وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْۢ بَعْدِهِمْ ۗذٰلِكَ لِمَنْ خَافَ مَقَامِيْ وَخَافَ وَعِيْدِ ','dan Kami pasti akan menempatkan kamu di negeri-negeri itu sesudah mereka. Yang demikian itu (adalah untuk) orang-orang yang takut (akan menghadap) kehadirat-Ku dan yang takut kepada ancaman-Ku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,15,'وَاسْتَفْتَحُوْا وَخَابَ كُلُّ جَبَّارٍ عَنِيْدٍۙ  ','Dan mereka memohon kemenangan (atas musuh-musuh mereka) dan binasalah semua orang yang berlaku sewenang-wenang lagi keras kepala,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,16,'مِّنْ وَّرَاۤىِٕهٖ جَهَنَّمُ وَيُسْقٰى مِنْ مَّاۤءٍ صَدِيْدٍۙ ','di hadapannya ada Jahannam dan dia akan diberi minuman dengan air nanah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,17,'يَّتَجَرَّعُهٗ وَلَا يَكَادُ يُسِيْغُهٗ وَيَأْتِيْهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَّمَا هُوَ بِمَيِّتٍۗ وَمِنْ وَّرَاۤىِٕهٖ عَذَابٌ غَلِيْظٌ ','diminumnnya air nanah itu dan hampir dia tidak bisa menelannya dan datanglah (bahaya) maut kepadanya dari segenap penjuru, tetapi dia tidak juga mati, dan dihadapannya masih ada azab yang berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,18,'مَثَلُ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادِ ِۨاشْتَدَّتْ بِهِ الرِّيْحُ فِيْ يَوْمٍ عَاصِفٍۗ  لَا يَقْدِرُوْنَ مِمَّا كَسَبُوْا عَلٰى شَيْءٍ ۗذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ','Orang-orang yang kafir kepada Tuhannya, amalan-amalan mereka adalah seperti abu yang ditiup angin dengan keras pada suatu hari yang berangin kencang. Mereka tidak dapat mengambil manfaat sedikitpun dari apa yang telah mereka usahakan (di dunia). Yang demikian itu adalah kesesatan yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,19,'اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۙ  ','Tidakkah kamu perhatikan, bahwa sesungguhnya Allah telah menciptakan langit dan bumi dengan hak? Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mengganti(mu) dengan makhluk yang baru,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,20,'وَّمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ','dan yang demikian itu sekali-kali tidak sukar bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,21,'وَبَرَزُوْا لِلّٰهِ جَمِيْعًا فَقَالَ الضُّعَفٰۤؤُا لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍ ۗقَالُوْا لَوْ هَدٰىنَا اللّٰهُ لَهَدَيْنٰكُمْۗ سَوَاۤءٌ عَلَيْنَآ اَجَزِعْنَآ اَمْ صَبَرْنَا مَا لَنَا مِنْ مَّحِيْصٍ ࣖ  ','Dan mereka semuanya (di padang Mahsyar) akan berkumpul menghadap ke hadirat Allah, lalu berkatalah orang-orang yang lemah kepada orang-orang yang sombong: "Sesungguhnya kami dahulu adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan daripada kami azab Allah (walaupun) sedikit saja? Mereka menjawab: "Seandainya Allah memberi petunjuk kepada kami, niscaya kami dapat memberi petunjuk kepadamu. Sama saja bagi kita, apakah kita mengeluh ataukah bersabar. Sekali-kali kita tidak mempunyai tempat untuk melarikan diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,22,'وَقَالَ الشَّيْطٰنُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُّكُمْ فَاَخْلَفْتُكُمْۗ وَمَا كَانَ لِيَ عَلَيْكُمْ مِّنْ سُلْطٰنٍ اِلَّآ اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِيْ ۚفَلَا تَلُوْمُوْنِيْ وَلُوْمُوْٓا اَنْفُسَكُمْۗ مَآ اَنَا۠ بِمُصْرِخِكُمْ وَمَآ اَنْتُمْ بِمُصْرِخِيَّۗ اِنِّيْ كَفَرْتُ بِمَآ اَشْرَكْتُمُوْنِ مِنْ قَبْلُ ۗاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ ','Dan berkatalah syaitan tatkala perkara (hisab) telah diselesaikan: "Sesungguhnya Allah telah menjanjikan kepadamu janji yang benar, dan akupun telah menjanjikan kepadamu tetapi aku menyalahinya. Sekali-kali tidak ada kekuasaan bagiku terhadapmu, melainkan (sekedar) aku menyeru kamu lalu kamu mematuhi seruanku, oleh sebab itu janganlah kamu mencerca aku akan tetapi cercalah dirimu sendiri. Aku sekali-kali tidak dapat menolongmu dan kamupun sekali-kali tidak dapat menolongku. Sesungguhnya aku tidak membenarkan perbuatanmu mempersekutukan aku (dengan Allah) sejak dahulu". Sesungguhnya orang-orang yang zalim itu mendapat siksaan yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,23,'وَاُدْخِلَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا بِاِذْنِ رَبِّهِمْۗ تَحِيَّتُهُمْ فِيْهَا سَلٰمٌ ','Dan dimasukkanlah orang-orang yang beriman dan beramal saleh ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya dengan seizin Tuhan mereka. Ucapan penghormatan mereka dalam surga itu ialah "salaam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,24,'اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَّفَرْعُهَا فِى السَّمَاۤءِۙ ','Tidakkah kamu perhatikan bagaimana Allah telah membuat perumpamaan kalimat yang baik seperti pohon yang baik, akarnya teguh dan cabangnya (menjulang) ke langit,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,25,'تُؤْتِيْٓ اُكُلَهَا كُلَّ حِيْنٍ ۢبِاِذْنِ رَبِّهَاۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ','pohon itu memberikan buahnya pada setiap musim dengan seizin Tuhannya. Allah membuat perumpamaan-perumpamaan itu untuk manusia supaya mereka selalu ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,26,'وَمَثَلُ كَلِمَةٍ خَبِيْثَةٍ كَشَجَرَةٍ خَبِيْثَةِ ِۨاجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ ','Dan perumpamaan kalimat yang buruk seperti pohon yang buruk, yang telah dicabut dengan akar-akarnya dari permukaan bumi; tidak dapat tetap (tegak) sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,27,'يُثَبِّتُ اللّٰهُ الَّذِيْنَ اٰمَنُوْا بِالْقَوْلِ الثَّابِتِ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظّٰلِمِيْنَۗ وَيَفْعَلُ اللّٰهُ مَا يَشَاۤءُ ࣖ   ','Allah meneguhkan (iman) orang-orang yang beriman dengan ucapan yang teguh itu dalam kehidupan di dunia dan di akhirat; dan Allah menyesatkan orang-orang yang zalim dan memperbuat apa yang Dia kehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,28,'۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ بَدَّلُوْا نِعْمَتَ اللّٰهِ كُفْرًا وَّاَحَلُّوْا قَوْمَهُمْ دَارَ الْبَوَارِۙ  ','Tidakkah kamu perhatikan orang-orang yang telah menukar nikmat Allah dengan kekafiran dan menjatuhkan kaumnya ke lembah kebinasaan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,29,'جَهَنَّمَ ۚيَصْلَوْنَهَاۗ وَبِئْسَ الْقَرَارُ ','yaitu neraka jahannam; mereka masuk kedalamnya; dan itulah seburuk-buruk tempat kediaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,30,'وَجَعَلُوْا لِلّٰهِ اَنْدَادًا لِّيُضِلُّوْا عَنْ سَبِيْلِهٖۗ قُلْ تَمَتَّعُوْا فَاِنَّ مَصِيْرَكُمْ اِلَى النَّارِ ','Orang-orang kafir itu telah menjadikan sekutu-sekutu bagi Allah supaya mereka menyesatkan (manusia) dari jalan-Nya. Katakanlah: "Bersenang-senanglah kamu, karena sesungguhnya tempat kembalimu ialah neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,31,'قُلْ لِّعِبَادِيَ الَّذِيْنَ اٰمَنُوْا يُقِيْمُوا الصَّلٰوةَ وَيُنْفِقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خِلٰلٌ ','Katakanlah kepada hamba-hamba-Ku yang telah beriman: "Hendaklah mereka mendirikan shalat, menafkahkan sebahagian rezeki yang Kami berikan kepada mereka secara sembunyi ataupun terang-terangan sebelum datang hari (kiamat) yang pada bari itu tidak ada jual beli dan persahabatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,32,'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚوَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِى الْبَحْرِ بِاَمْرِهٖ ۚوَسَخَّرَ لَكُمُ الْاَنْهٰرَ ','Allah-lah yang telah menciptakan langit dan bumi dan menurunkan air hujan dari langit, kemudian Dia mengeluarkan dengan air hujan itu berbagai buah-buahan menjadi rezeki untukmu; dan Dia telah menundukkan bahtera bagimu supaya bahtera itu, berlayar di lautan dengan kehendak-Nya, dan Dia telah menundukkan (pula) bagimu sungai-sungai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,33,'وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَاۤىِٕبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَ ۚ  ','Dan Dia telah menundukkan (pula) bagimu matahari dan bulan yang terus menerus beredar (dalam orbitnya); dan telah menundukkan bagimu malam dan siang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,34,'وَاٰتٰىكُمْ مِّنْ كُلِّ مَا سَاَلْتُمُوْهُۗ وَاِنْ تَعُدُّوْا نِعْمَتَ اللّٰهِ لَا تُحْصُوْهَاۗ اِنَّ الْاِنْسَانَ لَظَلُوْمٌ كَفَّارٌ ࣖ  ','Dan Dia telah memberikan kepadamu (keperluanmu) dan segala apa yang kamu mohonkan kepadanya. Dan jika kamu menghitung nikmat Allah, tidaklah dapat kamu menghinggakannya. Sesungguhnya manusia itu, sangat zalim dan sangat mengingkari (nikmat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,35,'وَاِذْ قَالَ اِبْرٰهِيْمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِنًا وَّاجْنُبْنِيْ وَبَنِيَّ اَنْ نَّعْبُدَ الْاَصْنَامَ ۗ   ','Dan (ingatlah), ketika Ibrahim berkata: "Ya Tuhanku, jadikanlah negeri ini (Mekah), negeri yang aman, dan jauhkanlah aku beserta anak cucuku daripada menyembah berhala-berhala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,36,'رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَثِيْرًا مِّنَ النَّاسِۚ فَمَنْ تَبِعَنِيْ فَاِنَّهٗ مِنِّيْۚ وَمَنْ عَصَانِيْ فَاِنَّكَ غَفُوْرٌ رَّحِيْمٌ ','Ya Tuhanku, sesungguhnya berhala-berhala itu telah menyesatkan kebanyakan daripada manusia, maka barangsiapa yang mengikutiku, maka sesungguhnya orang itu termasuk golonganku, dan barangsiapa yang mendurhakai aku, maka sesungguhnya Engkau, Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,37,'رَبَّنَآ اِنِّيْٓ اَسْكَنْتُ مِنْ ذُرِّيَّتِيْ بِوَادٍ غَيْرِ ذِيْ زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّنَا لِيُقِيْمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٕدَةً مِّنَ النَّاسِ تَهْوِيْٓ اِلَيْهِمْ وَارْزُقْهُمْ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَشْكُرُوْنَ','Ya Tuhan kami, sesungguhnya aku telah menempatkan sebahagian keturunanku di lembah yang tidak mempunyai tanam-tanaman di dekat rumah Engkau (Baitullah) yang dihormati, ya Tuhan kami (yang demikian itu) agar mereka mendirikan shalat, maka jadikanlah hati sebagian manusia cenderung kepada mereka dan beri rezekilah mereka dari buah-buahan, mudah-mudahan mereka bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,38,'رَبَّنَآ اِنَّكَ تَعْلَمُ مَا نُخْفِيْ وَمَا نُعْلِنُۗ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ','Ya Tuhan kami, sesungguhnya Engkau mengetahui apa yang kami sembunyikan dan apa yang kami lahirkan; dan tidak ada sesuatupun yang tersembunyi bagi Allah, baik yang ada di bumi maupun yang ada di langit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,39,'اَلْحَمْدُ لِلّٰهِ الَّذِيْ وَهَبَ لِيْ عَلَى الْكِبَرِ اِسْمٰعِيْلَ وَاِسْحٰقَۗ اِنَّ رَبِّيْ لَسَمِيْعُ الدُّعَاۤءِ ','Segala puji bagi Allah yang telah menganugerahkan kepadaku di hari tua(ku) Ismail dan Ishaq. Sesungguhnya Tuhanku, benar-benar Maha Mendengar (memperkenankan) doa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,40,'رَبِّ اجْعَلْنِيْ مُقِيْمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّتِيْۖ رَبَّنَا وَتَقَبَّلْ دُعَاۤءِ ','Ya Tuhanku, jadikanlah aku dan anak cucuku orang-orang yang tetap mendirikan shalat, ya Tuhan kami, perkenankanlah doaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,41,'رَبَّنَا اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِلْمُؤْمِنِيْنَ يَوْمَ يَقُوْمُ الْحِسَابُ ࣖ ','Ya Tuhan kami, beri ampunlah aku dan kedua ibu bapaku dan sekalian orang-orang mukmin pada hari terjadinya hisab (hari kiamat)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,42,'وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلًا عَمَّا يَعْمَلُ الظّٰلِمُوْنَ ەۗ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيْهِ الْاَبْصَارُۙ','Dan janganlah sekali-kali kamu (Muhammad) mengira, bahwa Allah lalai dari apa yang diperbuat oleh orang-orang yang zalim. Sesungguhnya Allah memberi tangguh kepada mereka sampai hari yang pada waktu itu mata (mereka) terbelalak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,43,'مُهْطِعِيْنَ مُقْنِعِيْ رُءُوْسِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْ ۚوَاَفْـِٕدَتُهُمْ هَوَاۤءٌ ۗ  ','mereka datang bergegas-gegas memenuhi panggilan dengan mangangkat kepalanya, sedang mata mereka tidak berkedip-kedip dan hati mereka kosong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,44,'وَاَنْذِرِ النَّاسَ يَوْمَ يَأْتِيْهِمُ الْعَذَابُۙ فَيَقُوْلُ الَّذِيْنَ ظَلَمُوْا رَبَّنَآ اَخِّرْنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۙ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَۗ اَوَلَمْ تَكُوْنُوْٓا اَقْسَمْتُمْ مِّنْ قَبْلُ مَا لَكُمْ مِّنْ زَوَالٍۙ  ','Dan berikanlah peringatan kepada manusia terhadap hari (yang pada waktu itu) datang azab kepada mereka, maka berkatalah orang-orang yang zalim: "Ya Tuhan kami, beri tangguhlah kami (kembalikanlah kami ke dunia) walaupun dalam waktu yang sedikit, niscaya kami akan mematuhi seruan Engkau dan akan mengikuti rasul-rasul". (Kepada mereka dikatakan): "Bukankah kamu telah bersumpah dahulu (di dunia) bahwa sekali-kali kamu tidak akan binasa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,45,'وَّسَكَنْتُمْ فِيْ مَسٰكِنِ الَّذِيْنَ ظَلَمُوْٓا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ ','dan kamu telah berdiam di tempat-tempat kediaman orang-orang yang menganiaya diri mereka sendiri, dan telah nyata bagimu bagaimana Kami telah berbuat terhadap mereka dan telah Kami berikan kepadamu beberapa perumpamaan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,46,'وَقَدْ مَكَرُوْا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۗ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُوْلَ مِنْهُ الْجِبَالُ ','Dan sesungguhnya mereka telah membuat makar yang besar padahal di sisi Allah-lah (balasan) makar mereka itu. Dan sesungguhnya makar mereka itu (amat besar) sehingga gunung-gunung dapat lenyap karenanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,47,'فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِهٖ رُسُلَهٗ ۗاِنَّ اللّٰهَ عَزِيْزٌ ذُو انْتِقَامٍۗ   ','Karena itu janganlah sekali-kali kamu mengira Allah akan menyalahi janji-Nya kepada rasul-raaul-Nya; sesungguhnya Allah Maha Perkasa, lagi mempunyai pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,48,'يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوٰتُ وَبَرَزُوْا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ ','(Yaitu) pada hari (ketika) bumi diganti dengan bumi yang lain dan (demikian pula) langit, dan meraka semuanya (di padang Mahsyar) berkumpul menghadap ke hadirat Allah yang Maha Esa lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,49,'وَتَرَى الْمُجْرِمِيْنَ يَوْمَىِٕذٍ مُّقَرَّنِيْنَ فِى الْاَصْفَادِۚ  ','Dan kamu akan melihat orang-orang yang berdosa pada hari itu diikat bersama-sama dengan belenggu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,50,'سَرَابِيْلُهُمْ مِّنْ قَطِرَانٍ وَّتَغْشٰى وُجُوْهَهُمُ النَّارُۙ  ','Pakaian mereka adalah dari pelangkin (ter) dan muka mereka ditutup oleh api neraka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,51,'لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَّا كَسَبَتْۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ ','agar Allah memberi pembalasan kepada tiap-tiap orang terhadap apa yang ia usahakan. Sesungguhnya Allah Maha cepat hisab-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,14,52,'هٰذَا بَلٰغٌ لِّلنَّاسِ وَلِيُنْذَرُوْا بِهٖ وَلِيَعْلَمُوْٓا اَنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّلِيَذَّكَّرَ اُولُوا الْاَلْبَابِ ࣖ ','(Al Quran) ini adalah penjelasan yang sempurna bagi manusia, dan supaya mereka diberi peringatan dengan-Nya, dan supaya mereka mengetahui bahwasanya Dia adalah Tuhan Yang Maha Esa dan agar orang-orang yang berakal mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,13,15,1,'الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ وَقُرْاٰنٍ مُّبِيْنٍ  ۔','Alif, laam, raa. (Surat) ini adalah (sebagian dari) ayat-ayat Al-Kitab (yang sempurna), yaitu (ayat-ayat) Al Quran yang memberi penjelasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,2,'رُبَمَا يَوَدُّ الَّذِيْنَ كَفَرُوْا لَوْ كَانُوْا مُسْلِمِيْنَ ','Orang-orang yang kafir itu seringkali (nanti di akhirat) menginginkan, kiranya mereka dahulu (di dunia) menjadi orang-orang muslim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,3,'ذَرْهُمْ يَأْكُلُوْا وَيَتَمَتَّعُوْا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُوْنَ ','Biarkanlah mereka (di dunia ini) makan dan bersenang-senang dan dilalaikan oleh angan-angan (kosong), maka kelak mereka akan mengetahui (akibat perbuatan mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,4,'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَّعْلُوْمٌ ','Dan Kami tiada membinasakan sesuatu negeripun, melainkan ada baginya ketentuan masa yang telah ditetapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,5,'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ','Tidak ada suatu umatpun yang dapat mendahului ajalnya, dan tidak (pula) dapat mengundurkan(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,6,'وَقَالُوْا يٰٓاَيُّهَا الَّذِيْ نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُوْنٌ  ۗ  ','Mereka berkata: "Hai orang yang diturunkan Al Quran kepadanya, sesungguhnya kamu benar-benar orang yang gila.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,7,'لَوْمَا تَأْتِيْنَا بِالْمَلٰۤىِٕكَةِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ','Mengapa kamu tidak mendatangkan malaikat kepada kami, jika kamu termasuk orang-orang yang benar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,8,'مَا نُنَزِّلُ الْمَلٰۤىِٕكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُوْٓا اِذًا مُّنْظَرِيْنَ ','Kami tidak menurunkan malaikat melainkan dengan benar (untuk membawa azab) dan tiadalah mereka ketika itu diberi tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,9,'اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهٗ لَحٰفِظُوْنَ ','Sesungguhnya Kami-lah yang menurunkan Al Quran, dan sesungguhnya Kami benar-benar memeliharanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,10,'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ شِيَعِ الْاَوَّلِيْنَ ','Dan sesungguhnya Kami telah mengutus (beberapa rasul) sebelum kamu kepada umat-umat yang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,11,'وَمَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ','Dan tidak datang seorang rasulpun kepada mereka, melainkan mereka selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,12,'كَذٰلِكَ نَسْلُكُهٗ فِيْ قُلُوْبِ الْمُجْرِمِيْنَۙ','Demikianlah, Kami mamasukkan (rasa ingkar dan memperolok-olokkan itu) kedalam hati orang-orang yang berdosa (orang-orang kafir),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,13,'لَا يُؤْمِنُوْنَ بِهٖ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّلِيْنَ ','mereka tidak beriman kepadanya (Al Quran) dan sesungguhnya telah berlalu sunnatullah terhadap orang-orang dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,14,'وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِّنَ السَّمَاۤءِ فَظَلُّوْا فِيْهِ يَعْرُجُوْنَۙ','Dan jika seandainya Kami membukakan kepada mereka salah satu dari (pintu-pintu) langit, lalu mereka terus menerus naik ke atasnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,15,'لَقَالُوْٓا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَّسْحُوْرُوْنَ ࣖ ','tentulah mereka berkata: "Sesungguhnya pandangan kamilah yang dikaburkan, bahkan kami adalah orang orang yang kena sihir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,16,'وَلَقَدْ جَعَلْنَا فِى السَّمَاۤءِ بُرُوْجًا وَّزَيَّنّٰهَا لِلنّٰظِرِيْنَۙ','Dan sesungguhnya Kami telah menciptakan gugusan bintang-bintang (di langit) dan Kami telah menghiasi langit itu bagi orang-orang yang memandang(nya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,17,'وَحَفِظْنٰهَا مِنْ كُلِّ شَيْطٰنٍ رَّجِيْمٍۙ','dan Kami menjaganya dari tiap-tiap syaitan yang terkutuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,18,'اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهٗ شِهَابٌ مُّبِيْنٌ ','kecuali syaitan yang mencuri-curi (berita) yang dapat didengar (dari malaikat) lalu dia dikejar oleh semburan api yang terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,19,'وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ شَيْءٍ مَّوْزُوْنٍ','Dan Kami telah menghamparkan bumi dan menjadikan padanya gunung-gunung dan Kami tumbuhkan padanya segala sesuatu menurut ukuran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,20,'وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَ وَمَنْ لَّسْتُمْ لَهٗ بِرَازِقِيْنَ ','Dan Kami telah menjadikan untukmu di bumi keperluan-keperluan hidup, dan (Kami menciptakan pula) makhluk-makhluk yang kamu sekali-kali bukan pemberi rezeki kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,21,'وَاِنْ مِّنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَاۤىِٕنُهٗ وَمَا نُنَزِّلُهٗٓ اِلَّا بِقَدَرٍ مَّعْلُوْمٍ ','Dan tidak ada sesuatupun melainkan pada sisi Kami-lah khazanahnya; dan Kami tidak menurunkannya melainkan dengan ukuran yang tertentu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,22,'وَاَرْسَلْنَا الرِّيٰحَ لَوَاقِحَ فَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَسْقَيْنٰكُمُوْهُۚ وَمَآ اَنْتُمْ لَهٗ بِخَازِنِيْنَ','Dan Kami telah meniupkan angin untuk mengawinkan (tumbuh-tumbuhan) dan Kami turunkan hujan dari langit, lalu Kami beri minum kamu dengan air itu, dan sekali-kali bukanlah kamu yang menyimpannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,23,'وَاِنَّا لَنَحْنُ نُحْيٖ وَنُمِيْتُ وَنَحْنُ الْوَارِثُوْنَ ','Dan sesungguhnya benar-benar Kami-lah yang menghidupkan dan mematikan dan Kami (pulalah) yang mewarisi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,24,'وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِيْنَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِيْنَ ','Dan sesungguhnya Kami telah mengetahui orang-orang yang terdahulu daripada-mu dan sesungguhnya Kami mengetahui pula orang-orang yang terkemudian (daripadamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,25,'وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ࣖ ','Sesungguhnya Tuhanmu, Dialah yang akan menghimpunkan mereka. Sesungguhnya Dia adalah Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,26,'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ  ','Dan sesungguhnya Kami telah menciptakan manusia (Adam) dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,27,'وَالْجَاۤنَّ خَلَقْنٰهُ مِنْ قَبْلُ مِنْ نَّارِ السَّمُوْمِ  ','Dan Kami telah menciptakan jin sebelum (Adam) dari api yang sangat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,28,'وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ  ','Dan (ingatlah), ketika Tuhanmu berfirman kepada para malaikat: "Sesungguhnya Aku akan menciptakan seorang manusia dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,29,'فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ ','Maka apabila Aku telah menyempurnakan kejadiannya, dan telah meniup kan kedalamnya ruh (ciptaan)-Ku, maka tunduklah kamu kepadanya dengan bersujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,30,'فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ','Maka bersujudlah para malaikat itu semuanya bersama-sama,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,31,'اِلَّآ اِبْلِيْسَۗ اَبٰىٓ اَنْ يَّكُوْنَ مَعَ السّٰجِدِيْنَ ','kecuali iblis. Ia enggan ikut besama-sama (malaikat) yang sujud itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,32,'قَالَ يٰٓاِبْلِيْسُ مَا لَكَ اَلَّا تَكُوْنَ مَعَ السّٰجِدِيْنَ ','Allah berfirman: "Hai iblis, apa sebabnya kamu tidak (ikut sujud) bersama-sama mereka yang sujud itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,33,'قَالَ لَمْ اَكُنْ لِّاَسْجُدَ لِبَشَرٍ خَلَقْتَهٗ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍ ','Berkata Iblis: "Aku sekali-kali tidak akan sujud kepada manusia yang Engkau telah menciptakannya dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,34,'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۙ','Allah berfirman: "Keluarlah dari surga, karena sesungguhnya kamu terkutuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,35,'وَّاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدِّيْنِ ','dan sesungguhnya kutukan itu tetap menimpamu sampai hari kiamat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,36,'قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ','Berkata iblis: "Ya Tuhanku, (kalau begitu) maka beri tangguhlah kepadaku sampai hari (manusia) dibangkitkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,37,'قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ','Allah berfirman: "(Kalau begitu) maka sesungguhnya kamu termasuk orang-orang yang diberi tangguh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,38,'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ ','sampai hari (suatu) waktu yang telah ditentukan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,39,'قَالَ رَبِّ بِمَآ اَغْوَيْتَنِيْ لَاُزَيِّنَنَّ لَهُمْ فِى الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ  ','Iblis berkata: "Ya Tuhanku, oleh sebab Engkau telah memutuskan bahwa aku sesat, pasti aku akan menjadikan mereka memandang baik (perbuatan maksiat) di muka bumi, dan pasti aku akan menyesatkan mereka semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,40,'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ ','kecuali hamba-hamba Engkau yang mukhlis di antara mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,41,'قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيْمٌ ','Allah berfirman: "Ini adalah jalan yang lurus, kewajiban Aku-lah (menjaganya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,42,'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغٰوِيْنَ ','Sesungguhnya hamba-hamba-Ku tidak ada kekuasaan bagimu terhadap mereka, kecuali orang-orang yang mengikut kamu, yaitu orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,43,'وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَعِيْنَۙ','Dan sesungguhnya Jahannam itu benar-benar tempat yang telah diancamkan kepada mereka (pengikut-pengikut syaitan) semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,44,'لَهَا سَبْعَةُ اَبْوَابٍۗ لِكُلِّ بَابٍ مِّنْهُمْ جُزْءٌ مَّقْسُوْمٌ ࣖ  ','Jahannam itu mempunyai tujuh pintu. Tiap-tiap pintu (telah ditetapkan) untuk golongan yang tertentu dari mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,45,'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۗ  ','Sesungguhnya orang-orang yang bertakwa itu berada dalam surga (taman-taman) dan (di dekat) mata air-mata air (yang mengalir).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,46,'اُدْخُلُوْهَا بِسَلٰمٍ اٰمِنِيْنَ ','(Dikatakan kepada mereka): "Masuklah ke dalamnya dengan sejahtera lagi aman"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,47,'وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ اِخْوَانًا عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ ','Dan Kami lenyapkan segala rasa dendam yang berada dalam hati mereka, sedang mereka merasa bersaudara duduk berhadap-hadapan di atas dipan-dipan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,48,'لَا يَمَسُّهُمْ فِيْهَا نَصَبٌ وَّمَا هُمْ مِّنْهَا بِمُخْرَجِيْنَ ','Mereka tidak merasa lelah di dalamnya dan mereka sekali-kali tidak akan dikeluarkan daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,49,'۞ نَبِّئْ عِبَادِيْٓ اَنِّيْٓ اَنَا الْغَفُوْرُ الرَّحِيْمُۙ','Kabarkanlah kepada hamba-hamba-Ku, bahwa sesungguhnya Aku-lah Yang Maha Pengampun lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,50,'وَاَنَّ عَذَابِيْ هُوَ الْعَذَابُ الْاَلِيْمُ ','dan bahwa sesungguhnya azab-Ku adalah azab yang sangat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,51,'وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰهِيْمَۘ ','Dan kabarkanlah kepada mereka tentang tamu-tamu Ibrahim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,52,'اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًاۗ قَالَ اِنَّا مِنْكُمْ وَجِلُوْنَ ','Ketika mereka masuk ke tempatnya, lalu mereka mengucapkan: "Salaam". Berkata Ibrahim: "Sesungguhnya kami merasa takut kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,53,'قَالُوْا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلٰمٍ عَلِيْمٍ ','Mereka berkata: "Janganlah kamu merasa takut, sesungguhnya kami memberi kabar gembira kepadamu dengan (kelahiran seorang) anak laki-laki (yang akan menjadi) orang yang alim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,54,'قَالَ اَبَشَّرْتُمُوْنِيْ عَلٰٓى اَنْ مَّسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُوْنَ ','Berkata Ibrahim: "Apakah kamu memberi kabar gembira kepadaku padahal usiaku telah lanjut, maka dengan cara bagaimanakah (terlaksananya) berita gembira yang kamu kabarkan ini?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,55,'قَالُوْا بَشَّرْنٰكَ بِالْحَقِّ فَلَا تَكُنْ مِّنَ الْقٰنِطِيْنَ ','Mereka menjawab: "Kami menyampaikan kabar gembira kepadamu dengan benar, maka janganlah kamu termasuk orang-orang yang berputus asa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,56,'قَالَ وَمَنْ يَّقْنَطُ مِنْ رَّحْمَةِ رَبِّهٖٓ اِلَّا الضَّاۤلُّوْنَ ','Ibrahim berkata: "Tidak ada orang yang berputus asa dari rahmat Tuhan-nya, kecuali orang-orang yang sesat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,57,'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَ ','Berkata (pula) Ibrahim: "Apakah urusanmu yang penting (selain itu), hai para utusan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,58,'قَالُوْٓا اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ','Mereka menjawab: "Kami sesungguhnya diutus kepada kaum yang berdosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,59,'اِلَّآ اٰلَ لُوْطٍۗ اِنَّا لَمُنَجُّوْهُمْ اَجْمَعِيْنَۙ','kecuali Luth beserta pengikut-pengikutnya. Sesungguhnya Kami akan menyelamatkan mereka semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,60,'اِلَّا امْرَاَتَهٗ قَدَّرْنَآ اِنَّهَا لَمِنَ الْغٰبِرِيْنَ ࣖ ','kecuali istrinya. Kami telah menentukan, bahwa sesungguhnya ia itu termasuk orang-orang yang tertinggal (bersama-sama dengan orang kafir lainnya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,61,'فَلَمَّا جَاۤءَ اٰلَ لُوْطِ ِۨالْمُرْسَلُوْنَۙ','Maka tatkala para utusan itu datang kepada kaum Luth, beserta pengikut pengikutnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,62,'قَالَ اِنَّكُمْ قَوْمٌ مُّنْكَرُوْنَ ','ia berkata: "Sesungguhnya kamu adalah orang-orang yang tidak dikenal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,63,'قَالُوْا بَلْ جِئْنٰكَ بِمَا كَانُوْا فِيْهِ يَمْتَرُوْنَ ','Para utusan menjawab: "Sebenarnya kami ini datang kepadamu dengan membawa azab yang selalu mereka dustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,64,'وَاَتَيْنٰكَ بِالْحَقِّ وَاِنَّا لَصٰدِقُوْنَ ','Dan kami datang kepadamu membawa kebenaran dan sesungguhnya kami betul-betul orang-orang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,65,'فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَاتَّبِعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَّامْضُوْا حَيْثُ تُؤْمَرُوْنَ ','Maka pergilah kamu di akhir malam dengan membawa keluargamu, dan ikutlah mereka dari belakang dan janganlah seorangpun di antara kamu menoleh kebelakang dan teruskanlah perjalanan ke tempat yang di perintahkan kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,66,'وَقَضَيْنَآ اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُلَاۤءِ مَقْطُوْعٌ مُّصْبِحِيْنَ ','Dan telah Kami wahyukan kepadanya (Luth) perkara itu, yaitu bahwa mereka akan ditumpas habis di waktu subuh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,67,'وَجَاۤءَ اَهْلُ الْمَدِيْنَةِ يَسْتَبْشِرُوْنَ ','Dan datanglah penduduk kota itu (ke rumah Luth) dengan gembira (karena) kedatangan tamu-tamu itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,68,'قَالَ اِنَّ هٰٓؤُلَاۤءِ ضَيْفِيْ فَلَا تَفْضَحُوْنِۙ','Luth berkata: "Sesungguhnya mereka adalah tamuku; maka janganlah kamu memberi malu (kepadaku),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,69,'وَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ ','dan bertakwalah kepada Allah dan janganlah kamu membuat aku terhina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,70,'قَالُوْٓا اَوَلَمْ نَنْهَكَ عَنِ الْعٰلَمِيْنَ ','Mereka berkata: "Dan bukankah kami telah melarangmu dari (melindungi) manusia?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,71,'قَالَ هٰٓؤُلَاۤءِ بَنٰتِيْٓ اِنْ كُنْتُمْ فٰعِلِيْنَۗ ','Luth berkata: "Inilah puteri-puteriku (kawinlah dengan mereka), jika kamu hendak berbuat (secara yang halal)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,72,'لَعَمْرُكَ اِنَّهُمْ لَفِيْ سَكْرَتِهِمْ يَعْمَهُوْنَ ','(Allah berfirman): "Demi umurmu (Muhammad), sesungguhnya mereka terombang-ambing di dalam kemabukan (kesesatan)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,73,'فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِيْنَۙ','Maka mereka dibinasakan oleh suara keras yang mengguntur, ketika matahari akan terbit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,74,'فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِّنْ سِجِّيْلٍ ','Maka Kami jadikan bahagian atas kota itu terbalik ke bawah dan Kami hujani mereka dengan batu dari tanah yang keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,75,'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْمُتَوَسِّمِيْنَۙ','Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang memperhatikan tanda-tanda.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,76,'وَاِنَّهَا لَبِسَبِيْلٍ مُّقِيْمٍ ','Dan sesungguhnya kota itu benar-benar terletak di jalan yang masih tetap (dilalui manusia).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,77,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَۗ  ','Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,78,'وَاِنْ كَانَ اَصْحٰبُ الْاَيْكَةِ لَظٰلِمِيْنَۙ','Dan sesungguhnya adalah penduduk Aikah itu benar-benar kaum yang zalim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,79,'فَانْتَقَمْنَا مِنْهُمْۘ وَاِنَّهُمَا لَبِاِمَامٍ مُّبِيْنٍۗ  ࣖ  ','maka Kami membinasakan mereka. Dan sesungguhnya kedua kota itu benar-benar terletak di jalan umum yang terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,80,'وَلَقَدْ كَذَّبَ اَصْحٰبُ الْحِجْرِ الْمُرْسَلِيْنَۙ','Dan sesungguhnya penduduk-penduduk kota Al Hijr telah mendustakan rasul-rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,81,'وَاٰتَيْنٰهُمْ اٰيٰتِنَا فَكَانُوْا عَنْهَا مُعْرِضِيْنَۙ','dan Kami telah mendatangkan kepada mereka tanda-tanda (kekuasaan) Kami, tetapi mereka selalu berpaling daripadanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,82,'وَكَانُوْا يَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا اٰمِنِيْنَ ','dan mereka memahat rumah-rumah dari gunung-gunung batu (yang didiami) dengan aman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,83,'فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِيْنَۙ','Maka mereka dibinasakan oleh suara keras yang mengguntur di waktu pagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,84,'فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَۗ  ','maka tak dapat menolong mereka, apa yang telah mereka usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,85,'وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّۗ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيْلَ ','Dan tidaklah Kami ciptakan langit dan bumi dan apa yang ada di antara keduanya, melainkan dengan benar. Dan sesungguhnya saat (kiamat) itu pasti akan datang, maka maafkanlah (mereka) dengan cara yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,86,'اِنَّ رَبَّكَ هُوَ الْخَلّٰقُ الْعَلِيْمُ ','Sesungguhnya Tuhanmu, Dialah Yang Maha Pencipta lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,87,'وَلَقَدْ اٰتَيْنٰكَ سَبْعًا مِّنَ الْمَثَانِيْ وَالْقُرْاٰنَ الْعَظِيْمَ ','Dan sesungguhnya Kami telah berikan kepadamu tujuh ayat yang dibaca berulang-ulang dan Al Quran yang agung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,88,'لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِيْنَ ','Janganlah sekali-kali kamu menunjukkan pandanganmu kepada kenikmatan hidup yang telah Kami berikan kepada beberapa golongan di antara mereka (orang-orang kafir itu), dan janganlah kamu bersedih hati terhadap mereka dan berendah dirilah kamu terhadap orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,89,'وَقُلْ اِنِّيْٓ اَنَا النَّذِيْرُ الْمُبِيْنُۚ  ','Dan katakanlah: "Sesungguhnya aku adalah pemberi peringatan yang menjelaskan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,90,'كَمَآ اَنْزَلْنَا عَلَى الْمُقْتَسِمِيْنَۙ','Sebagaimana (Kami telah memberi peringatan), Kami telah menurunkan (azab) kepada orang-orang yang membagi-bagi (Kitab Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,91,'الَّذِيْنَ جَعَلُوا الْقُرْاٰنَ عِضِيْنَ ','(yaitu) orang-orang yang telah menjadikan Al Quran itu terbagi-bagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,92,'فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَعِيْنَۙ','Maka demi Tuhanmu, Kami pasti akan menanyai mereka semua,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,93,'عَمَّا كَانُوْا يَعْمَلُوْنَ ','tentang apa yang telah mereka kerjakan dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,94,'فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ ','Maka sampaikanlah olehmu secara terang-terangan segala apa yang diperintahkan (kepadamu) dan berpalinglah dari orang-orang yang musyrik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,95,'اِنَّا كَفَيْنٰكَ الْمُسْتَهْزِءِيْنَۙ','Sesungguhnya Kami memelihara kamu daripada (kejahatan) orang-orang yang memperolok-olokkan (kamu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,96,'الَّذِيْنَ يَجْعَلُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۚ فَسَوْفَ يَعْلَمُوْنَ ','(Yaitu) orang-orang yang menganggap adanya tuhan yang lain di samping Allah; maka mereka kelak akan mengetahui (akibat-akibatnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,97,'وَلَقَدْ نَعْلَمُ اَنَّكَ يَضِيْقُ صَدْرُكَ بِمَا يَقُوْلُوْنَۙ','Dan Kami sungguh-sungguh mengetahui, bahwa dadamu menjadi sempit disebabkan apa yang mereka ucapkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,98,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُنْ مِّنَ السَّاجِدِيْنَۙ','maka bertasbihlah dengan memuji Tuhanmu dan jadilah kamu di antara orang-orang yang bersujud (shalat),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,15,99,'وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَقِيْنُ ࣖࣖ ','dan sembahlah Tuhanmu sampai datang kepadamu yang diyakini (ajal).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,1,'اَتٰىٓ اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوْهُ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ','Telah pasti datangnya ketetapan Allah maka janganlah kamu meminta agar disegerakan (datang)nya. Maha Suci Allah dan Maha Tinggi dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,2,'يُنَزِّلُ الْمَلٰۤىِٕكَةَ بِالرُّوْحِ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اَنْ اَنْذِرُوْٓا اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاتَّقُوْنِ ','Dia menurunkan para malaikat dengan (membawa) wahyu dengan perintah-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, yaitu: "Peringatkanlah olehmu sekalian, bahwasanya tidak ada Tuhan (yang hak) melainkan Aku, maka hendaklah kamu bertakwa kepada-Ku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,3,'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ تَعٰلٰى عَمَّا يُشْرِكُوْنَ ','Dia menciptakan langit dan bumi dengan hak. Maha Tinggi Allah daripada apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,4,'خَلَقَ الْاِنْسَانَ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ ','Dia telah menciptakan manusia dari mani, tiba-tiba ia menjadi pembantah yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,5,'وَالْاَنْعَامَ خَلَقَهَا لَكُمْ فِيْهَا دِفْءٌ وَّمَنَافِعُ وَمِنْهَا تَأْكُلُوْنَ ','Dan Dia telah menciptakan binatang ternak untuk kamu; padanya ada (bulu) yang menghangatkan dan berbagai-bagai manfaat, dan sebahagiannya kamu makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,6,'وَلَكُمْ فِيْهَا جَمَالٌ حِيْنَ تُرِيْحُوْنَ وَحِيْنَ تَسْرَحُوْنَۖ','Dan kamu memperoleh pandangan yang indah padanya, ketika kamu membawanya kembali ke kandang dan ketika kamu melepaskannya ke tempat penggembalaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,7,'وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَّمْ تَكُوْنُوْا بٰلِغِيْهِ اِلَّا بِشِقِّ الْاَنْفُسِۗ اِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌۙ','Dan ia memikul beban-bebanmu ke suatu negeri yang kamu tidak sanggup sampai kepadanya, melainkan dengan kesukaran-kesukaran (yang memayahkan) diri. Sesungguhnya Tuhanmu benar-benar Maha Pengasih lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,8,'وَّالْخَيْلَ وَالْبِغَالَ وَالْحَمِيْرَ لِتَرْكَبُوْهَا وَزِيْنَةًۗ وَيَخْلُقُ مَا لَا تَعْلَمُوْنَ','dan (Dia telah menciptakan) kuda, bagal dan keledai, agar kamu menungganginya dan (menjadikannya) perhiasan. Dan Allah menciptakan apa yang kamu tidak mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,9,'وَعَلَى اللّٰهِ قَصْدُ السَّبِيْلِ وَمِنْهَا جَاۤىِٕرٌ ۗوَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ࣖ  ','Dan hak bagi Allah (menerangkan) jalan yang lurus, dan di antara jalan-jalan ada yang bengkok. Dan jikalau Dia menghendaki, tentulah Dia memimpin kamu semuanya (kepada jalan yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,10,'هُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً لَّكُمْ مِّنْهُ شَرَابٌ وَّمِنْهُ شَجَرٌ فِيْهِ تُسِيْمُوْنَ   ','Dialah, Yang telah menurunkan air hujan dari langit untuk kamu, sebahagiannya menjadi minuman dan sebahagiannya (menyuburkan) tumbuh-tumbuhan, yang pada (tempat tumbuhnya) kamu menggembalakan ternakmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,11,'يُنْۢبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُوْنَ وَالنَّخِيْلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرٰتِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ','Dia menumbuhkan bagi kamu dengan air hujan itu tanam-tanaman; zaitun, korma, anggur dan segala macam buah-buahan. Sesungguhnya pada yang demikian itu benar-benar ada tanda (kekuasaan Allah) bagi kaum yang memikirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,12,'وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَ ۗوَالنُّجُوْمُ مُسَخَّرٰتٌۢ بِاَمْرِهٖ ۗاِنَّ فِي ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَۙ','Dan Dia menundukkan malam dan siang, matahari dan bulan untukmu. Dan bintang-bintang itu ditundukkan (untukmu) dengan perintah-Nya. Sesungguhnya pada yang demikian itu benar-benar ada tanda-tanda (kekuasaan Allah) bagi kaum yang memahami(nya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,13,'وَمَا ذَرَاَ لَكُمْ فِى الْاَرْضِ مُخْتَلِفًا اَلْوَانُهٗ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّذَّكَّرُوْنَ ','dan Dia (menundukkan pula) apa yang Dia ciptakan untuk kamu di bumi ini dengan berlain-lainan macamnya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi kaum yang mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,14,'وَهُوَ الَّذِيْ سَخَّرَ الْبَحْرَ لِتَأْكُلُوْا مِنْهُ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْا مِنْهُ حِلْيَةً تَلْبَسُوْنَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ','Dan Dialah, Allah yang menundukkan lautan (untukmu), agar kamu dapat memakan daripadanya daging yang segar (ikan), dan kamu mengeluarkan dari lautan itu perhiasan yang kamu pakai; dan kamu melihat bahtera berlayar padanya, dan supaya kamu mencari (keuntungan) dari karunia-Nya, dan supaya kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,15,'وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَاَنْهٰرًا وَّسُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَۙ','Dan Dia menancapkan gunung-gunung di bumi supaya bumi itu tidak goncang bersama kamu, (dan Dia menciptakan) sungai-sungai dan jalan-jalan agar kamu mendapat petunjuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,16,'وَعَلٰمٰتٍۗ وَبِالنَّجْمِ هُمْ يَهْتَدُوْنَ  ','dan (Dia ciptakan) tanda-tanda (penunjuk jalan). Dan dengan bintang-bintang itulah mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,17,'اَفَمَنْ يَّخْلُقُ كَمَنْ لَّا يَخْلُقُۗ اَفَلَا تَذَكَّرُوْنَ ','Maka apakah (Allah) yang menciptakan itu sama dengan yang tidak dapat menciptakan (apa-apa)?. Maka mengapa kamu tidak mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,18,'وَاِنْ تَعُدُّوْا نِعْمَةَ اللّٰهِ لَا تُحْصُوْهَا ۗاِنَّ اللّٰهَ لَغَفُوْرٌ رَّحِيْمٌ ','Dan jika kamu menghitung-hitung nikmat Allah, niscaya kamu tak dapat menentukan jumlahnya. Sesungguhnya Allah benar-benar Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,19,'وَاللّٰهُ يَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَ ','Dan Allah mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,20,'وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۗ ','Dan berhala-berhala yang mereka seru selain Allah, tidak dapat membuat sesuatu apapun, sedang berhala-berhala itu (sendiri) dibuat orang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,21,'اَمْوَاتٌ غَيْرُ اَحْيَاۤءٍ ۗوَمَا يَشْعُرُوْنَۙ اَيَّانَ يُبْعَثُوْنَ ࣖ ','(Berhala-berhala itu) benda mati tidak hidup, dan berhala-berhala tidak mengetahui bilakah penyembah-penyembahnya akan dibangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,22,'اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ ۚفَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ قُلُوْبُهُمْ مُّنْكِرَةٌ وَّهُمْ مُّسْتَكْبِرُوْنَ ','Tuhan kamu adalah Tuhan Yang Maha Esa. Maka orang-orang yang tidak beriman kepada akhirat, hati mereka mengingkari (keesaaan Allah), sedangkan mereka sendiri adalah orang-orang yang sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,23,'لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ۗاِنَّهٗ لَا يُحِبُّ الْمُسْتَكْبِرِيْنَ ','Tidak diragukan lagi bahwa sesungguhnya Allah mengetahui apa yang mereka rahasiakan dan apa yang mereka lahirkan. Sesungguhnya Allah tidak menyukai orang-orang yang sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,24,'وَاِذَا قِيْلَ لَهُمْ مَّاذَآ اَنْزَلَ رَبُّكُمْ ۙقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ ','Dan apabila dikatakan kepada mereka "Apakah yang telah diturunkan Tuhanmu?" Mereka menjawab: "Dongeng-dongengan orang-orang dahulu",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,25,'لِيَحْمِلُوْٓا اَوْزَارَهُمْ كَامِلَةً يَّوْمَ الْقِيٰمَةِ ۙوَمِنْ اَوْزَارِ الَّذِيْنَ يُضِلُّوْنَهُمْ بِغَيْرِ عِلْمٍ ۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ ࣖ ','(ucapan mereka) menyebabkan mereka memikul dosa-dosanya dengan sepenuh-penuhnya pada hari kiamat, dan sebahagian dosa-dosa orang yang mereka sesatkan yang tidak mengetahui sedikitpun (bahwa mereka disesatkan). Ingatlah, amat buruklah dosa yang mereka pikul itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,26,'قَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِّنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ ','Sesungguhnya orang-orang yang sebelum mereka telah mengadakan makar, maka Allah menghancurkan rumah-rumah mereka dari fondasinya, lalu atap (rumah itu) jatuh menimpa mereka dari atas, dan datanglah azab itu kepada mereka dari tempat yang tidak mereka sadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,27,'ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْزِيْهِمْ وَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تُشَاۤقُّوْنَ فِيْهِمْ ۗقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوْۤءَ عَلَى الْكٰفِرِيْنَۙ','Kemudian Allah menghinakan mereka di hari kiamat, dan berfirman: "Di manakah sekutu-sekutu-Ku itu (yang karena membelanya) kamu selalu memusuhi mereka (nabi-nabi dan orang-orang mukmin)?" Berkatalah orang-orang yang telah diberi ilmu: "Sesungguhnya kehinaan dan azab hari ini ditimpakan atas orang-orang yang kafir",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,28,'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ ۖفَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوْۤءٍ ۗبَلٰىٓ اِنَّ اللّٰهَ عَلِيْمٌۢ بِمَا كُنْتُمْ تَعْمَلُوْنَ ','(yaitu) orang-orang yang dimatikan oleh para malaikat dalam keadaan berbuat zalim kepada diri mereka sendiri, lalu mereka menyerah diri (sambil berkata); "Kami sekali-kali tidak ada mengerjakan sesuatu kejahatanpun". (Malaikat menjawab): "Ada, sesungguhnya Allah Maha Mengetahui apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,29,'فَادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۗفَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ ','Maka masukilah pintu-pintu neraka Jahannam, kamu kekal di dalamnya. Maka amat buruklah tempat orang-orang yang menyombongkan diri itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,30,'۞ وَقِيْلَ لِلَّذِيْنَ اتَّقَوْا مَاذَآ اَنْزَلَ رَبُّكُمْ ۗقَالُوْا خَيْرًا ۚلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ  ۗوَلَدَارُ الْاٰخِرَةِ خَيْرٌ ۗوَلَنِعْمَ دَارُ الْمُتَّقِيْنَۙ','Dan dikatakan kepada orang-orang yang bertakwa: "Apakah yang telah diturunkan oleh Tuhanmu?" Mereka menjawab: "(Allah telah menurunkan) kebaikan". Orang-orang yang berbuat baik di dunia ini mendapat (pembalasan) yang baik. Dan sesungguhnya kampung akhirat adalah lebih baik dan itulah sebaik-baik tempat bagi orang yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,31,'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ ۗ كَذٰلِكَ يَجْزِى اللّٰهُ الْمُتَّقِيْنَۙ','(yaitu) surga ´Adn yang mereka masuk ke dalamnya, mengalir di bawahnya sungai-sungai, di dalam surga itu mereka mendapat segala apa yang mereka kehendaki. Demikianlah Allah memberi balasan kepada orang-orang yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,32,'الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ طَيِّبِيْنَ ۙيَقُوْلُوْنَ سَلٰمٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُوْنَ ','(yaitu) orang-orang yang diwafatkan dalam keadaan baik oleh para malaikat dengan mengatakan (kepada mereka): "Salaamun´alaikum, masuklah kamu ke dalam surga itu disebabkan apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,33,'هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗوَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ','Tidak ada yang ditunggu-tunggu orang kafir selain dari datangnya para malaikat kepada mereka atau datangnya perintah Tuhanmu. Demikianlah yang telah diperbuat oleh orang-orang (kafir) sebelum mereka. Dan Allah tidak menganiaya mereka, akan tetapi merekalah yang selalu menganiaya diri mereka sendiri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,34,'فَاَصَابَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ ','Maka mereka ditimpa oleh (akibat) kejahatan perbuatan mereka dan mereka diliputi oleh azab yang selalu mereka perolok-olokan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,35,'وَقَالَ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ نَّحْنُ وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚفَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ','Dan berkatalah orang-orang musyrik: "Jika Allah menghendaki, niscaya kami tidak akan menyembah sesuatu apapun selain Dia, baik kami maupun bapak-bapak kami, dan tidak pula kami mengharamkan sesuatupun tanpa (izin)-Nya". Demikianlah yang diperbuat orang-orang sebelum mereka; maka tidak ada kewajiban atas para rasul, selain dari menyampaikan (amanat Allah) dengan terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,36,'وَلَقَدْ بَعَثْنَا فِيْ كُلِّ اُمَّةٍ رَّسُوْلًا اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوْتَۚ فَمِنْهُمْ مَّنْ هَدَى اللّٰهُ وَمِنْهُمْ مَّنْ حَقَّتْ عَلَيْهِ الضَّلٰلَةُ ۗ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ','Dan sungguhnya Kami telah mengutus rasul pada tiap-tiap umat (untuk menyerukan): "Sembahlah Allah (saja), dan jauhilah Thaghut itu", maka di antara umat itu ada orang-orang yang diberi petunjuk oleh Allah dan ada pula di antaranya orang-orang yang telah pasti kesesatan baginya. Maka berjalanlah kamu dimuka bumi dan perhatikanlah bagaimana kesudahan orang-orang yang mendustakan (rasul-rasul).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,37,'اِنْ تَحْرِصْ عَلٰى هُدٰىهُمْ فَاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ يُّضِلُّ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ','Jika kamu sangat mengharapkan agar mereka dapat petunjuk, maka sesungguhnya Allah tiada memberi petunjuk kepada orang yang disesatkan-Nya, dan sekali-kali mereka tiada mempunyai penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,38,'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ  لَا يَبْعَثُ اللّٰهُ مَنْ يَّمُوْتُۗ بَلٰى وَعْدًا عَلَيْهِ حَقًّا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ','Mereka bersumpah dengan nama Allah dengan sumpahnya yang sungguh-sungguh: "Allah tidak akan akan membangkitkan orang yang mati". (Tidak demikian), bahkan (pasti Allah akan membangkitnya), sebagai suatu janji yang benar dari Allah, akan tetapi kebanyakan manusia tiada mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,39,'لِيُبَيِّنَ لَهُمُ الَّذِيْ يَخْتَلِفُوْنَ فِيْهِ وَلِيَعْلَمَ الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ كَانُوْا كٰذِبِيْنَ  ','agar Allah menjelaskan kepada mereka apa yang mereka perselisihkan itu, agar orang-orang kafir itu mengetahui bahwasanya mereka adalah orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,40,'اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَآ اَرَدْنٰهُ اَنْ نَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ ࣖ ','Sesungguhnya perkataan Kami terhadap sesuatu apabila Kami menghendakinya, Kami hanya mengatakan kepadanya: "kun (jadilah)", maka jadilah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,41,'وَالَّذِيْنَ هَاجَرُوْا فِى اللّٰهِ مِنْۢ بَعْدِ مَا ظُلِمُوْا لَنُبَوِّئَنَّهُمْ فِى الدُّنْيَا حَسَنَةً  ۗوَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَۙ','Dan orang-orang yang berhijrah karena Allah sesudah mereka dianiaya, pasti Kami akan memberikan tempat yang bagus kepada mereka di dunia. Dan sesungguhnya pahala di akhirat adalah lebih besar, kalau mereka mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,42,'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ','(yaitu) orang-orang yang sabar dan hanya kepada Tuhan saja mereka bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,43,'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ  فَاسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَۙ','Dan Kami tidak mengutus sebelum kamu, kecuali orang-orang lelaki yang Kami beri wahyu kepada mereka; maka bertanyalah kepada orang yang mempunyai pengetahuan jika kamu tidak mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,44,'بِالْبَيِّنٰتِ وَالزُّبُرِۗ وَاَنْزَلْنَآ اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُوْنَ ','keterangan-keterangan (mukjizat) dan kitab-kitab. Dan Kami turunkan kepadamu Al Quran, agar kamu menerangkan pada umat manusia apa yang telah diturunkan kepada mereka dan supaya mereka memikirkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,45,'اَفَاَمِنَ الَّذِيْنَ مَكَرُوا السَّيِّاٰتِ اَنْ يَّخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَۙ','maka apakah orang-orang yang membuat makar yang jahat itu, merasa aman (dari bencana) ditenggelamkannya bumi oleh Allah bersama mereka, atau datangnya azab kepada mereka dari tempat yang tidak mereka sadari,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,46,'اَوْ يَأْخُذَهُمْ فِيْ تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِيْنَۙ','atau Allah mengazab mereka diwaktu mereka dalam perjalanan, maka sekali-kali mereka tidak dapat menolak (azab itu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,47,'اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۗ فَاِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌ ','atau Allah mengazab mereka dengan berangsur-angsur (sampai binasa). Maka sesungguhnya Tuhanmu adalah Maha Pengasih lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,48,'اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَّتَفَيَّؤُا ظِلٰلُهٗ عَنِ الْيَمِيْنِ وَالشَّمَاۤىِٕلِ سُجَّدًا لِّلّٰهِ وَهُمْ دَاخِرُوْنَ ','Dan apakah mereka tidak memperhatikan segala sesuatu yang telah diciptakan Allah yang bayangannya berbolak-balik ke kanan dan ke kiri dalam keadaan sujud kepada Allah, sedang mereka berendah diri?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,49,'وَلِلّٰهِ يَسْجُدُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ مِنْ دَاۤبَّةٍ وَّالْمَلٰۤىِٕكَةُ وَهُمْ لَا يَسْتَكْبِرُوْنَ ','Dan kepada Allah sajalah bersujud segala apa yang berada di langit dan semua makhluk yang melata di bumi dan (juga) para ma]aikat, sedang mereka (malaikat) tidak menyombongkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,50,'يَخَافُوْنَ رَبَّهُمْ مِّنْ فَوْقِهِمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ࣖ ۩ ','Mereka takut kepada Tuhan mereka yang di atas mereka dan melaksanakan apa yang diperintahkan (kepada mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,51,'۞ وَقَالَ اللّٰهُ لَا تَتَّخِذُوْٓا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ فَاِيَّايَ فَارْهَبُوْنِ ','Allah berfirman: "Janganlah kamu menyembah dua tuhan; sesungguhnya Dialah Tuhan Yang Maha Esa, maka hendaklah kepada-Ku saja kamu takut".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,52,'وَلَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَلَهُ الدِّيْنُ وَاصِبًاۗ اَفَغَيْرَ اللّٰهِ تَتَّقُوْنَ ','Dan kepunyaan-Nya-lah segala apa yang ada di langit dan di bumi, dan untuk-Nya-lah ketaatan itu selama-lamanya. Maka mengapa kamu bertakwa kepada selain Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,53,'وَمَا بِكُمْ مِّنْ نِّعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُوْنَۚ  ','Dan apa saja nikmat yang ada pada kamu, maka dari Allah-lah (datangnya), dan bila kamu ditimpa oleh kemudharatan, maka hanya kepada-Nya-lah kamu meminta pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,54,'ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَرِيْقٌ مِّنْكُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ','Kemudian apabila Dia telah menghilangkan kemudharatan itu dari pada kamu, tiba-tiba sebahagian dari pada kamu mempersekutukan Tuhannya dengan (yang lain),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,55,'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ  ','Biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka; maka bersenang-senanglah kamu. Kelak kamu akan mengetahui (akibatnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,56,'وَيَجْعَلُوْنَ لِمَا لَا يَعْلَمُوْنَ نَصِيْبًا مِّمَّا رَزَقْنٰهُمْۗ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُوْنَ ','Dan mereka sediakan untuk berhala-berhala yang mereka tiada mengetahui (kekuasaannya), satu bahagian dari rezeki yang telah Kami berikan kepada mereka. Demi Allah, sesungguhnya kamu akan ditanyai tentang apa yang telah kamu ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,57,'وَيَجْعَلُوْنَ لِلّٰهِ الْبَنٰتِ سُبْحٰنَهٗۙ وَلَهُمْ مَّا يَشْتَهُوْنَ ','Dan mereka menetapkan bagi Allah anak-anak perempuan. Maha Suci Allah, sedang untuk mereka sendiri (mereka tetapkan) apa yang mereka sukai (yaitu anak-anak laki-laki).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,58,'وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌۚ ','Dan apabila seseorang dari mereka diberi kabar dengan (kelahiran) anak perempuan, hitamlah (merah padamlah) mukanya, dan dia sangat marah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,59,'يَتَوٰرٰى مِنَ الْقَوْمِ مِنْ سُوْۤءِ مَا بُشِّرَ بِهٖۗ اَيُمْسِكُهٗ عَلٰى هُوْنٍ اَمْ يَدُسُّهٗ فِى التُّرَابِۗ  اَلَا سَاۤءَ مَا يَحْكُمُوْنَ ','Ia menyembunyikan dirinya dari orang banyak, disebabkan buruknya berita yang disampaikan kepadanya. Apakah dia akan memeliharanya dengan menanggung kehinaan ataukah akan menguburkannya ke dalam tanah (hidup-hidup)?. Ketahuilah, alangkah buruknya apa yang mereka tetapkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,60,'لِلَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ','Orang-orang yang tidak beriman kepada kehidupan akhirat, mempunyai sifat yang buruk; dan Allah mempunyai sifat yang Maha Tinggi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,61,'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَّا تَرَكَ عَلَيْهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً  وَّلَا يَسْتَقْدِمُوْنَ ','Jikalau Allah menghukum manusia karena kezalimannya, niscaya tidak akan ditinggalkan-Nya di muka bumi sesuatupun dari makhluk yang melata, tetapi Allah menangguhkan mereka sampai kepada waktu yang ditentukan. Maka apabila telah tiba waktunya (yang ditentukan) bagi mereka, tidaklah mereka dapat mengundurkannya barang sesaatpun dan tidak (pula) mendahulukannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,62,'وَيَجْعَلُوْنَ لِلّٰهِ مَا يَكْرَهُوْنَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰى لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُّفْرَطُوْنَ ','Dan mereka menetapkan bagi Allah apa yang mereka sendiri membencinya, dan lidah mereka mengucapkan kedustaan, yaitu bahwa sesungguhnya merekalah yang akan mendapat kebaikan. Tiadalah diragukan bahwa nerakalah bagi mereka, dan sesungguhnya mereka segera dimasukkan (ke dalamnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,63,'تَاللّٰهِ لَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','Demi Allah, sesungguhnya Kami telah mengutus rasul-rasul Kami kepada umat-umat sebelum kamu, tetapi syaitan menjadikan umat-umat itu memandang baik perbuatan mereka (yang buruk), maka syaitan menjadi pemimpin mereka di hari itu dan bagi mereka azab yang sangat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,64,'وَمَآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِى اخْتَلَفُوْا فِيْهِۙ وَهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ','Dan Kami tidak menurunkan kepadamu Al-Kitab (Al Quran) ini, melainkan agar kamu dapat menjelaskan kepada mereka apa yang mereka perselisihkan itu dan menjadi petunjuk dan rahmat bagi kaum yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,65,'وَاللّٰهُ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّسْمَعُوْنَ ࣖ ','Dan Allah menurunkan dari langit air (hujan) dan dengan air itu dihidupkan-Nya bumi sesudah matinya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Tuhan) bagi orang-orang yang mendengarkan (pelajaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,66,'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةً ۚ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهٖ مِنْۢ بَيْنِ فَرْثٍ وَّدَمٍ لَّبَنًا خَالِصًا سَاۤىِٕغًا لِّلشّٰرِبِيْنَ ','Dan sesungguhnya pada binatang ternak itu benar-benar terdapat pelajaran bagi kamu. Kami memberimu minum dari pada apa yang berada dalam perutnya (berupa) susu yang bersih antara tahi dan darah, yang mudah ditelan bagi orang-orang yang meminumnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,67,'وَمِنْ ثَمَرٰتِ النَّخِيْلِ وَالْاَعْنَابِ تَتَّخِذُوْنَ مِنْهُ سَكَرًا وَّرِزْقًا حَسَنًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْقِلُوْنَ ','Dan dari buah korma dan anggur, kamu buat minimuman yang memabukkan dan rezeki yang baik. Sesunggguhnya pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang memikirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,68,'وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذِيْ مِنَ الْجِبَالِ بُيُوْتًا وَّمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُوْنَۙ','Dan Tuhanmu mewahyukan kepada lebah: "Buatlah sarang-sarang di bukit-bukit, di pohon-pohon kayu, dan di tempat-tempat yang dibikin manusia",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,69,'ثُمَّ كُلِيْ مِنْ كُلِّ الثَّمَرٰتِ فَاسْلُكِيْ سُبُلَ رَبِّكِ ذُلُلًاۗ يَخْرُجُ مِنْ بُطُوْنِهَا شَرَابٌ مُّخْتَلِفٌ اَلْوَانُهٗ ۖفِيْهِ شِفَاۤءٌ لِّلنَّاسِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ ','kemudian makanlah dari tiap-tiap (macam) buah-buahan dan tempuhlah jalan Tuhanmu yang telah dimudahkan (bagimu). Dari perut lebah itu ke luar minuman (madu) yang bermacam-macam warnanya, di dalamnya terdapat obat yang menyembuhkan bagi manusia. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kebesaran Tuhan) bagi orang-orang yang memikirkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,70,'وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰىكُمْ وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ قَدِيْرٌ ࣖ  ','Allah menciptakan kamu, kemudian mewafatkan kamu; dan di antara kamu ada yang dikembalikan kepada umur yang paling lemah (pikun), supaya dia tidak mengetahui lagi sesuatupun yang pernah diketahuinya. Sesungguhnya Allah Maha Mengetahui lagi Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,71,'وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِى الرِّزْقِۚ فَمَا الَّذِيْنَ فُضِّلُوْا بِرَاۤدِّيْ رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ فِيْهِ سَوَاۤءٌۗ اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُوْنَ  ','Dan Allah melebihkan sebahagian kamu dari sebagian yang lain dalam hal rezeki, tetapi orang-orang yang dilebihkan (rezekinya itu) tidak mau memberikan rezeki mereka kepada budak-budak yang mereka miliki, agar mereka sama (merasakan) rezeki itu. Maka mengapa mereka mengingkari nikmat Allah?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,72,'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّجَعَلَ لَكُمْ مِّنْ اَزْوَاجِكُمْ بَنِيْنَ وَحَفَدَةً وَّرَزَقَكُمْ مِّنَ الطَّيِّبٰتِۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُوْنَۙ','Allah menjadikan bagi kamu isteri-isteri dari jenis kamu sendiri dan menjadikan bagimu dari isteri-isteri kamu itu, anak-anak dan cucu-cucu, dan memberimu rezeki dari yang baik-baik. Maka mengapakah mereka beriman kepada yang bathil dan mengingkari nikmat Allah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,73,'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِّنَ السَّمٰوٰتِ وَالْاَرْضِ شَيْـًٔا وَّلَا يَسْتَطِيْعُوْنَ ۚ  ','Dan mereka menyembah selain Allah, sesuatu yang tidak dapat memberikan rezeki kepada mereka sedikitpun dari langit dan bumi, dan tidak berkuasa (sedikit juapun).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,74,'فَلَا تَضْرِبُوْا لِلّٰهِ الْاَمْثَالَ ۗاِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ','Maka janganlah kamu mengadakan sekutu-sekutu bagi Allah. Sesungguhnya Allah mengetahui, sedang kamu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,75,'۞ ضَرَبَ اللّٰهُ مَثَلًا عَبْدًا مَّمْلُوْكًا لَّا يَقْدِرُ عَلٰى شَيْءٍ وَّمَنْ رَّزَقْنٰهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَّجَهْرًاۗ هَلْ يَسْتَوٗنَ ۚ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ','Allah membuat perumpamaan dengan seorang hamba sahaya yang dimiliki yang tidak dapat bertindak terhadap sesuatupun dan seorang yang Kami beri rezeki yang baik dari Kami, lalu dia menafkahkan sebagian dari rezeki itu secara sembunyi dan secara terang-terangan, adakah mereka itu sama? Segala puji hanya bagi Allah, tetapi kebanyakan mereka tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,76,'وَضَرَبَ اللّٰهُ مَثَلًا رَّجُلَيْنِ اَحَدُهُمَآ اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَّهُوَ كَلٌّ عَلٰى مَوْلٰىهُ ۗ اَيْنَمَا يُوَجِّهْهُّ لَا يَأْتِ بِخَيْرٍ ۖهَلْ يَسْتَوِيْ هُوَۙ وَمَنْ يَّأْمُرُ بِالْعَدْلِ وَهُوَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ࣖ ','Dan Allah membuat (pula) perumpamaan: dua orang lelaki yang seorang bisu, tidak dapat berbuat sesuatupun dan dia menjadi beban atas penanggungnya, ke mana saja dia disuruh oleh penanggungnya itu, dia tidak dapat mendatangkan suatu kebajikanpun. Samakah orang itu dengan orang yang menyuruh berbuat keadilan, dan dia berada pula di atas jalan yang lurus?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,77,'وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ وَمَآ اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan kepunyaan Allah-lah segala apa yang tersembunyi di langit dan di bumi. Tidak adalah kejadian kiamat itu, melainkan seperti sekejap mata atau lebih cepat (lagi). Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,78,'وَاللّٰهُ اَخْرَجَكُمْ مِّنْۢ بُطُوْنِ اُمَّهٰتِكُمْ لَا تَعْلَمُوْنَ شَيْـًٔاۙ وَّجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَ ۙ لَعَلَّكُمْ تَشْكُرُوْنَ ','Dan Allah mengeluarkan kamu dari perut ibumu dalam keadaan tidak mengetahui sesuatupun, dan Dia memberi kamu pendengaran, penglihatan dan hati, agar kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,79,'اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرٰتٍ فِيْ جَوِّ السَّمَاۤءِ ۗمَا يُمْسِكُهُنَّ اِلَّا اللّٰهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ','Tidakkah mereka memperhatikan burung-burung yang dimudahkan terbang diangkasa bebas. Tidak ada yang menahannya selain daripada Allah. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Tuhan) bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,80,'وَاللّٰهُ جَعَلَ لَكُمْ مِّنْۢ بُيُوْتِكُمْ سَكَنًا وَّجَعَلَ لَكُمْ مِّنْ جُلُوْدِ الْاَنْعَامِ بُيُوْتًا تَسْتَخِفُّوْنَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْ ۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَآ اَثَاثًا وَّمَتَاعًا اِلٰى حِيْنٍ ','Dan Allah menjadikan bagimu rumah-rumahmu sebagai tempat tinggal dan Dia menjadikan bagi kamu rumah-rumah (kemah-kemah) dari kulit binatang ternak yang kamu merasa ringan (membawa)nya di waktu kamu berjalan dan waktu kamu bermukim dan (dijadikan-Nya pula) dari bulu domba, bulu unta dan bulu kambing, alat-alat rumah tangga dan perhiasan (yang kamu pakai) sampai waktu (tertentu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,81,'وَاللّٰهُ جَعَلَ لَكُمْ مِّمَّا خَلَقَ ظِلٰلًا وَّجَعَلَ لَكُمْ مِّنَ الْجِبَالِ اَكْنَانًا وَّجَعَلَ لَكُمْ سَرَابِيْلَ تَقِيْكُمُ الْحَرَّ وَسَرَابِيْلَ تَقِيْكُمْ بَأْسَكُمْ ۚ  كَذٰلِكَ يُتِمُّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُوْنَ','Dan Allah menjadikan bagimu tempat bernaung dari apa yang telah Dia ciptakan, dan Dia jadikan bagimu tempat-tempat tinggal di gunung-gunung, dan Dia jadikan bagimu pakaian yang memeliharamu dari panas dan pakaian (baju besi) yang memelihara kamu dalam peperangan. Demikianlah Allah menyempurnakan nikmat-Nya atasmu agar kamu berserah diri (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,82,'فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ الْمُبِيْنُ ','Jika mereka tetap berpaling, maka sesungguhnya kewajiban yang dibebankan atasmu (Muhammad) hanyalah menyampaikan (amanat Allah) dengan terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,83,'يَعْرِفُوْنَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُوْنَهَا وَاَكْثَرُهُمُ الْكٰفِرُوْنَ ࣖ ','Mereka mengetahui nikmat Allah, kemudian mereka mengingkarinya dan kebanyakan mereka adalah orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,84,'وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَهِيْدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِيْنَ كَفَرُوْا وَلَا هُمْ يُسْتَعْتَبُوْنَ ','Dan (ingatlah) akan hari (ketika) Kami bangkitkan dari tiap-tiap umat seorang saksi (rasul), kemudian tidak diizinkan kepada orang-orang yang kafir (untuk membela diri) dan tidak (pula) mereka dibolehkan meminta maaf.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,85,'وَاِذَا رَاَ الَّذِيْنَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُوْنَ','Dan apabila orang-orang zalim telah menyaksikan azab, maka tidaklah diringankan azab bagi mereka dan tidak puIa mereka diberi tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,86,'وَاِذَا رَاَ الَّذِيْنَ اَشْرَكُوْا شُرَكَاۤءَهُمْ قَالُوْا رَبَّنَا هٰٓؤُلَاۤءِ شُرَكَاۤؤُنَا الَّذِيْنَ كُنَّا نَدْعُوْا مِنْ دُوْنِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكٰذِبُوْنَۚ ','Dan apabila orang-orang yang mempersekutukan (Allah) melihat sekutu-sekutu mereka, mereka berkata: "Ya Tuhan kami mereka inilah sekutu-sekutu kami yang dahulu kami sembah selain dari Engkau". Lalu sekutu-sekutu mereka mengatakan kepada mereka: "Sesungguhnya kamu benar-benar orang-orang yang dusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,87,'وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَىِٕذِ ِۨالسَّلَمَ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ  ','Dan mereka menyatakan ketundukannya kepada Allah pada hari itu dan hilanglah dari mereka apa yang selalu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,88,'اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ زِدْنٰهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوْا يُفْسِدُوْنَ ','Orang-orang yang kafir dan menghalangi (manusia) dari jalan Allah, Kami tambahkan kepada mereka siksaan di atas siksaan disebabkan mereka selalu berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,89,'وَيَوْمَ نَبْعَثُ فِيْ كُلِّ اُمَّةٍ شَهِيْدًا عَلَيْهِمْ مِّنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَهِيْدًا عَلٰى هٰٓؤُلَاۤءِۗ وَنَزَّلْنَا عَلَيْكَ الْكِتٰبَ تِبْيَانًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً وَّبُشْرٰى لِلْمُسْلِمِيْنَ ࣖ ','(Dan ingatlah) akan hari (ketika) Kami bangkitkan pada tiap-tiap umat seorang saksi atas mereka dari mereka sendiri dan Kami datangkan kamu (Muhammad) menjadi saksi atas seluruh umat manusia. Dan Kami turunkan kepadamu Al Kitab (Al Quran) untuk menjelaskan segala sesuatu dan petunjuk serta rahmat dan kabar gembira bagi orang-orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,90,'۞ اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَاِيْتَاۤئِ ذِى الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ','Sesungguhnya Allah menyuruh (kamu) berlaku adil dan berbuat kebajikan, memberi kepada kaum kerabat, dan Allah melarang dari perbuatan keji, kemungkaran dan permusuhan. Dia memberi pengajaran kepadamu agar kamu dapat mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,91,'وَاَوْفُوْا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُّمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْكِيْدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَفِيْلًا ۗاِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُوْنَ ','Dan tepatilah perjanjian dengan Allah apabila kamu berjanji dan janganlah kamu membatalkan sumpah-sumpah(mu) itu, sesudah meneguhkannya, sedang kamu telah menjadikan Allah sebagai saksimu (terhadap sumpah-sumpahmu itu). Sesungguhnya Allah mengetahui apa yang kamu perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,92,'وَلَا تَكُوْنُوْا كَالَّتِيْ نَقَضَتْ غَزْلَهَا مِنْۢ بَعْدِ قُوَّةٍ اَنْكَاثًاۗ تَتَّخِذُوْنَ اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ اَنْ تَكُوْنَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍ ۗاِنَّمَا يَبْلُوْكُمُ اللّٰهُ بِهٖۗ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَةِ مَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ','Dan janganlah kamu seperti seorang perempuan yang menguraikan benangnya yang sudah dipintal dengan kuat, menjadi cerai berai kembali, kamu menjadikan sumpah (perjanjian)mu sebagai alat penipu di antaramu, disebabkan adanya satu golongan yang lebih banyak jumlahnya dari golongan yang lain. Sesungguhnya Allah hanya menguji kamu dengan hal itu. Dan sesungguhnya di hari kiamat akan dijelaskan-Nya kepadamu apa yang dahulu kamu perselisihkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,93,'وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُوْنَ ','Dan kalau Allah menghendaki, niscaya Dia menjadikan kamu satu umat (saja), tetapi Allah menyesatkan siapa yang dikehendaki-Nya dan memberi petunjuk kepada siapa yang dikehendaki-Nya. Dan sesungguhnya kamu akan ditanya tentang apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,94,'وَلَا تَتَّخِذُوْٓا اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ فَتَزِلَّ قَدَمٌۢ بَعْدَ ثُبُوْتِهَا وَتَذُوْقُوا السُّوْۤءَ بِمَا صَدَدْتُّمْ عَنْ سَبِيْلِ اللّٰهِ ۚوَلَكُمْ عَذَابٌ عَظِيْمٌ ','Dan janganlah kamu jadikan sumpah-sumpahmu sebagai alat penipu di antaramu, yang menyebabkan tergelincir kaki(mu) sesudah kokoh tegaknya, dan kamu rasakan kemelaratan (di dunia) karena kamu menghalangi (manusia) dari jalan Allah; dan bagimu azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,95,'وَلَا تَشْتَرُوْا بِعَهْدِ اللّٰهِ ثَمَنًا قَلِيْلًاۗ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Dan janganlah kamu tukar perjanjianmu dengan Allah dengan harga yang sedikit (murah), sesungguhnya apa yang ada di sisi Allah, itulah yang lebih baik bagimu jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,96,'مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۗ وَلَنَجْزِيَنَّ الَّذِيْنَ صَبَرُوْٓا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ ','Apa yang di sisimu akan lenyap, dan apa yang ada di sisi Allah adalah kekal. Dan sesungguhnya Kami akan memberi balasan kepada orang-orang yang sabar dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,97,'مَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهٗ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ  ','Barangsiapa yang mengerjakan amal saleh, baik laki-laki maupun perempuan dalam keadaan beriman, maka sesungguhnya akan Kami berikan kepadanya kehidupan yang baik dan sesungguhnya akan Kami beri balasan kepada mereka dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,98,'فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطٰنِ الرَّجِيْمِ ','Apabila kamu membaca Al Quran hendaklah kamu meminta perlindungan kepada Allah dari syaitan yang terkutuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,99,'اِنَّهٗ لَيْسَ لَهٗ سُلْطٰنٌ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ','Sesungguhnya syaitan itu tidak ada kekuasaannya atas orang-orang yang beriman dan bertawakkal kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,100,'اِنَّمَا سُلْطٰنُهٗ عَلَى الَّذِيْنَ يَتَوَلَّوْنَهٗ وَالَّذِيْنَ هُمْ بِهٖ مُشْرِكُوْنَ ࣖ','Sesungguhnya kekuasaannya (syaitan) hanyalah atas orang-orang yang mengambilnya jadi pemimpin dan atas orang-orang yang mempersekutukannya dengan Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,101,'وَاِذَا بَدَّلْنَآ اٰيَةً مَّكَانَ اٰيَةٍ ۙوَّاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُوْٓا اِنَّمَآ اَنْتَ مُفْتَرٍۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ','Dan apabila Kami letakkan suatu ayat di tempat ayat yang lain sebagai penggantinya padahal Allah lebih mengetahui apa yang diturunkan-Nya, mereka berkata: "Sesungguhnya kamu adalah orang yang mengada-adakan saja". Bahkan kebanyakan mereka tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,102,'قُلْ نَزَّلَهٗ رُوْحُ الْقُدُسِ مِنْ رَّبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِيْنَ اٰمَنُوْا وَهُدًى وَّبُشْرٰى لِلْمُسْلِمِيْنَ ','Katakanlah: "Ruhul Qudus (Jibril) menurunkan Al Quran itu dari Tuhanmu dengan benar, untuk meneguhkan (hati) orang-orang yang telah beriman, dan menjadi petunjuk serta kabar gembira bagi orang-orang yang berserah diri (kepada Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,103,'وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُوْلُوْنَ اِنَّمَا يُعَلِّمُهٗ بَشَرٌۗ لِسَانُ الَّذِيْ يُلْحِدُوْنَ اِلَيْهِ اَعْجَمِيٌّ وَّهٰذَا لِسَانٌ عَرَبِيٌّ مُّبِيْنٌ ','Dan sesungguhnya Kami mengetahui bahwa mereka berkata: "Sesungguhnya Al Quran itu diajarkan oleh seorang manusia kepadanya (Muhammad)". Padahal bahasa orang yang mereka tuduhkan (bahwa) Muhammad belajar kepadanya bahasa ´Ajam, sedang Al Quran adalah dalam bahasa Arab yang terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,104,'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۙ لَا يَهْدِيْهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','Sesungguhnya orang-orang yang tidak beriman kepada ayat-ayat Allah (Al Quran), Allah tidak akan memberi petunjuk kepada mereka dan bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,105,'اِنَّمَا يَفْتَرِى الْكَذِبَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۚ وَاُولٰۤىِٕكَ هُمُ الْكٰذِبُوْنَ ','Sesungguhnya yang mengada-adakan kebohongan, hanyalah orang-orang yang tidak beriman kepada ayat-ayat Allah, dan mereka itulah orang-orang pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,106,'مَنْ كَفَرَ بِاللّٰهِ مِنْۢ بَعْدِ اِيْمَانِهٖٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهٗ مُطْمَىِٕنٌّۢ بِالْاِيْمَانِ وَلٰكِنْ مَّنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِّنَ اللّٰهِ ۗوَلَهُمْ عَذَابٌ عَظِيْمٌ ','Barangsiapa yang kafir kepada Allah sesudah dia beriman (dia mendapat kemurkaan Allah), kecuali orang yang dipaksa kafir padahal hatinya tetap tenang dalam beriman (dia tidak berdosa), akan tetapi orang yang melapangkan dadanya untuk kekafiran, maka kemurkaan Allah menimpanya dan baginya azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,107,'ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ ','Yang demikian itu disebabkan karena sesungguhnya mereka mencintai kehidupan di dunia lebih dari akhirat, dan bahwasanya Allah tiada memberi petunjuk kepada kaum yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,108,'اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۗ وَاُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ ','Mereka itulah orang-orang yang hati, pendengaran dan penglihatannya telah dikunci mati oleh Allah, dan mereka itulah orang-orang yang lalai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,109,'لَا جَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْخٰسِرُوْنَ ','Pastilah bahwa mereka di akhirat nanti adalah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,110,'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ هَاجَرُوْا مِنْۢ بَعْدِ مَا فُتِنُوْا ثُمَّ جَاهَدُوْا وَصَبَرُوْاۚ اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ࣖ  ','Dan sesungguhnya Tuhanmu (pelindung) bagi orang-orang yang berhijrah sesudah menderita cobaan, kemudian mereka berjihad dan sabar; sesungguhnya Tuhanmu sesudah itu benar-benar Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,111,'۞ يَوْمَ تَأْتِيْ كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَّفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُمْ لَا يُظْلَمُوْنَ ','(Ingatlah) suatu hari (ketika) tiap-tiap diri datang untuk membela dirinya sendiri dan bagi tiap-tiap diri disempurnakan (balasan) apa yang telah dikerjakannya, sedangkan mereka tidak dianiaya (dirugikan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,112,'وَضَرَبَ اللّٰهُ مَثَلًا قَرْيَةً كَانَتْ اٰمِنَةً مُّطْمَىِٕنَّةً يَّأْتِيْهَا رِزْقُهَا رَغَدًا مِّنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوْعِ وَالْخَوْفِ بِمَا كَانُوْا يَصْنَعُوْنَ ','Dan Allah telah membuat suatu perumpamaan (dengan) sebuah negeri yang dahulunya aman lagi tenteram, rezekinya datang kepadanya melimpah ruah dari segenap tempat, tetapi (penduduk)nya mengingkari nikmat-nikmat Allah; karena itu Allah merasakan kepada mereka pakaian kelaparan dan ketakutan, disebabkan apa yang selalu mereka perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,113,'وَلَقَدْ جَاۤءَهُمْ رَسُوْلٌ مِّنْهُمْ فَكَذَّبُوْهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظٰلِمُوْنَ ','Dan sesungguhnya telah datang kepada mereka seorang rasul dari mereka sendiri, tetapi mereka mendustakannya; karena itu mereka dimusnahkan azab dan mereka adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,114,'فَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًاۖ وَّاشْكُرُوْا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ','Maka makanlah yang halal lagi baik dari rezeki yang telah diberikan Allah kepadamu; dan syukurilah nikmat Allah, jika kamu hanya kepada-Nya saja menyembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,115,'اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Sesungguhnya Allah hanya mengharamkan atasmu (memakan) bangkai, darah, daging babi dan apa yang disembelih dengan menyebut nama selain Allah; tetapi barangsiapa yang terpaksa memakannya dengan tidak menganiaya dan tidak pula melampaui batas, maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,116,'وَلَا تَقُوْلُوْا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلٰلٌ وَّهٰذَا حَرَامٌ لِّتَفْتَرُوْا عَلَى اللّٰهِ الْكَذِبَۗ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ ','Dan janganlah kamu mengatakan terhadap apa yang disebut-sebut oleh lidahmu secara dusta "ini halal dan ini haram", untuk mengada-adakan kebohongan terhadap Allah. Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tiadalah beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,117,'مَتَاعٌ قَلِيْلٌ  ۖوَّلَهُمْ عَذَابٌ اَلِيْمٌ ','(Itu adalah) kesenangan yang sedikit, dan bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,118,'وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ ۗوَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ','Dan terhadap orang-orang Yahudi, Kami haramkan apa yang telah Kami ceritakan dahulu kepadamu; dan Kami tiada menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,119,'ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ عَمِلُوا السُّوْۤءَ بِجَهَالَةٍ ثُمَّ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ࣖ ','Kemudian, sesungguhnya Tuhanmu (mengampuni) bagi orang-orang yang mengerjakan kesalahan karena kebodohannya, kemudian mereka bertaubat sesudah itu dan memperbaiki (dirinya), sesungguhnya Tuhanmu sesudah itu benar-benar Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,120,'اِنَّ اِبْرٰهِيْمَ كَانَ اُمَّةً قَانِتًا لِّلّٰهِ حَنِيْفًاۗ وَلَمْ يَكُ مِنَ الْمُشْرِكِيْنَۙ','Sesungguhnya Ibrahim adalah seorang imam yang dapat dijadikan teladan lagi patuh kepada Allah dan hanif. Dan sekali-kali bukanlah dia termasuk orang-orang yang mempersekutukan (Tuhan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,121,'شَاكِرًا لِّاَنْعُمِهِ ۖاجْتَبٰىهُ وَهَدٰىهُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','(lagi) yang mensyukuri nikmat-nikmat Allah. Allah telah memilihnya dan menunjukinya kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,122,'وَاٰتَيْنٰهُ فِى الدُّنْيَا حَسَنَةً ۗوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ۗ  ','Dan Kami berikan kepadanya kebaikan di dunia. Dan sesungguhnya dia di akhirat benar-benar termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,123,'ثُمَّ اَوْحَيْنَآ اِلَيْكَ اَنِ اتَّبِعْ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ','Kemudian Kami wahyukan kepadamu (Muhammad): "Ikutilah agama Ibrahim seorang yang hanif" dan bukanlah dia termasuk orang-orang yang mempersekutukan Tuhan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,124,'اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِيْنَ اخْتَلَفُوْا فِيْهِۗ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ','Sesungguhnya diwajibkan (menghormati) hari Sabtu atas orang-orang (Yahudi) yang berselisih padanya. Dan sesungguhnya Tuhanmu benar-benar akan memberi putusan di antara mereka di hari kiamat terhadap apa yang telah mereka perselisihkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,125,'اُدْعُ اِلٰى سَبِيْلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّتِيْ هِيَ اَحْسَنُۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ','Serulah (manusia) kepada jalan Tuhan-mu dengan hikmah dan pelajaran yang baik dan bantahlah mereka dengan cara yang baik. Sesungguhnya Tuhanmu Dialah yang lebih mengetahui tentang siapa yang tersesat dari jalan-Nya dan Dialah yang lebih mengetahui orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,126,'وَاِنْ عَاقَبْتُمْ فَعَاقِبُوْا بِمِثْلِ مَا عُوْقِبْتُمْ بِهٖۗ وَلَىِٕنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِّلصّٰبِرِيْنَ ','Dan jika kamu memberikan balasan, maka balaslah dengan balasan yang sama dengan siksaan yang ditimpakan kepadamu. Akan tetapi jika kamu bersabar, sesungguhnya itulah yang lebih baik bagi orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,127,'وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ','Bersabarlah (hai Muhammad) dan tiadalah kesabaranmu itu melainkan dengan pertolongan Allah dan janganlah kamu bersedih hati terhadap (kekafiran) mereka dan janganlah kamu bersempit dada terhadap apa yang mereka tipu dayakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,14,16,128,'اِنَّ اللّٰهَ مَعَ الَّذِيْنَ اتَّقَوْا وَّالَّذِيْنَ هُمْ مُّحْسِنُوْنَ ࣖ   ۔','Sesungguhnya Allah beserta orang-orang yang bertakwa dan orang-orang yang berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,1,'سُبْحٰنَ الَّذِيْٓ اَسْرٰى بِعَبْدِهٖ لَيْلًا مِّنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذِيْ بٰرَكْنَا حَوْلَهٗ لِنُرِيَهٗ مِنْ اٰيٰتِنَاۗ اِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ','Maha Suci Allah, yang telah memperjalankan hamba-Nya pada suatu malam dari Al Masjidil Haram ke Al Masjidil Aqsha yang telah Kami berkahi sekelilingnya agar Kami perlihatkan kepadanya sebagian dari tanda-tanda (kebesaran) Kami. Sesungguhnya Dia adalah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,2,'وَاٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَ اَلَّا تَتَّخِذُوْا مِنْ دُوْنِيْ وَكِيْلًاۗ ','Dan Kami berikan kepada Musa kitab (Taurat) dan Kami jadikan kitab Taurat itu petunjuk bagi Bani Israil (dengan firman): "Janganlah kamu mengambil penolong selain Aku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,3,'ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوْحٍۗ اِنَّهٗ كَانَ عَبْدًا شَكُوْرًا','(yaitu) anak cucu dari orang-orang yang Kami bawa bersama-sama Nuh. Sesungguhnya dia adalah hamba (Allah) yang banyak bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,4,'وَقَضَيْنَآ اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ فِى الْكِتٰبِ لَتُفْسِدُنَّ فِى الْاَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيْرًا','Dan telah Kami tetapkan terhadap Bani Israil dalam Kitab itu: "Sesungguhnya kamu akan membuat kerusakan di muka bumi ini dua kali dan pasti kamu akan menyombongkan diri dengan kesombongan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,5,'فَاِذَا جَاۤءَ وَعْدُ اُوْلٰىهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَّنَآ اُولِيْ بَأْسٍ شَدِيْدٍ فَجَاسُوْا خِلٰلَ الدِّيَارِۗ وَكَانَ وَعْدًا مَّفْعُوْلًا','Maka apabila datang saat hukuman bagi (kejahatan) pertama dari kedua (kejahatan) itu, Kami datangkan kepadamu hamba-hamba Kami yang mempunyai kekuatan yang besar, lalu mereka merajalela di kampung-kampung, dan itulah ketetapan yang pasti terlaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,6,'ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنٰكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَجَعَلْنٰكُمْ اَكْثَرَ نَفِيْرًا','Kemudian Kami berikan kepadamu giliran untuk mengalahkan mereka kembali dan Kami membantumu dengan harta kekayaan dan anak-anak dan Kami jadikan kamu kelompok yang lebih besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,7,'اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ ۗوَاِنْ اَسَأْتُمْ فَلَهَاۗ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ لِيَسٗۤـُٔوْا وُجُوْهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوْهُ اَوَّلَ مَرَّةٍ وَّلِيُتَبِّرُوْا مَا عَلَوْا تَتْبِيْرًا','Jika kamu berbuat baik (berarti) kamu berbuat baik bagi dirimu sendiri dan jika kamu berbuat jahat, maka (kejahatan) itu bagi dirimu sendiri, dan apabila datang saat hukuman bagi (kejahatan) yang kedua, (Kami datangkan orang-orang lain) untuk menyuramkan muka-muka kamu dan mereka masuk ke dalam mesjid, sebagaimana musuh-musuhmu memasukinya pada kali pertama dan untuk membinasakan sehabis-habisnya apa saja yang mereka kuasai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,8,'عَسٰى رَبُّكُمْ اَنْ يَّرْحَمَكُمْۚ وَاِنْ عُدْتُّمْ عُدْنَاۘ وَجَعَلْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ حَصِيْرًا ','Mudah-mudahan Tuhanmu akan melimpahkan rahmat(Nya) kepadamu; dan sekiranya kamu kembali kepada (kedurhakaan) niscaya Kami kembali (mengazabmu) dan Kami jadikan neraka Jahannam penjara bagi orang-orang yang tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,9,'اِنَّ هٰذَا الْقُرْاٰنَ يَهْدِيْ لِلَّتِيْ هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا كَبِيْرًاۙ  ','Sesungguhnya Al Quran ini memberikan petunjuk kepada (jalan) yang lebih lurus dan memberi khabar gembira kepada orang-orang Mu´min yang mengerjakan amal saleh bahwa bagi mereka ada pahala yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,10,'وَّاَنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا ࣖ','dan sesungguhnya orang-orang yang tidak beriman kepada kehidupan akhirat, Kami sediakan bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,11,'وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَاۤءَهٗ بِالْخَيْرِۗ وَكَانَ الْاِنْسَانُ عَجُوْلًا  ','Dan manusia mendoa untuk kejahatan sebagaimana ia mendoa untuk kebaikan. Dan adalah manusia bersifat tergesa-gesa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,12,'وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَآ اٰيَةَ الَّيْلِ وَجَعَلْنَآ اٰيَةَ النَّهَارِ مُبْصِرَةً لِّتَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ وَلِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ وَكُلَّ شَيْءٍ فَصَّلْنٰهُ تَفْصِيْلًا','Dan Kami jadikan malam dan siang sebagai dua tanda, lalu Kami hapuskan tanda malam dan Kami jadikan tanda siang itu terang, agar kamu mencari kurnia dari Tuhanmu, dan supaya kamu mengetahui bilangan tahun-tahun dan perhitungan. Dan segala sesuatu telah Kami terangkan dengan jelas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,13,'وَكُلَّ اِنْسَانٍ اَلْزَمْنٰهُ طٰۤىِٕرَهٗ فِيْ عُنُقِهٖۗ وَنُخْرِجُ لَهٗ يَوْمَ الْقِيٰمَةِ كِتٰبًا يَّلْقٰىهُ مَنْشُوْرًا','Dan tiap-tiap manusia itu telah Kami tetapkan amal perbuatannya (sebagaimana tetapnya kalung) pada lehernya. Dan Kami keluarkan baginya pada hari kiamat sebuah kitab yang dijumpainya terbuka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,14,'اِقْرَأْ كِتَابَكَۗ  كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيْبًاۗ','"Bacalah kitabmu, cukuplah dirimu sendiri pada waktu ini sebagai penghisab terhadapmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,15,'مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ وَمَا كُنَّا مُعَذِّبِيْنَ حَتّٰى نَبْعَثَ رَسُوْلًا','Barangsiapa yang berbuat sesuai dengan hidayah (Allah), maka sesungguhnya dia berbuat itu untuk (keselamatan) dirinya sendiri; dan barangsiapa yang sesat maka sesungguhnya dia tersesat bagi (kerugian) dirinya sendiri. Dan seorang yang berdosa tidak dapat memikul dosa orang lain, dan Kami tidak akan mengazab sebelum Kami mengutus seorang rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,16,'وَاِذَآ اَرَدْنَآ اَنْ نُّهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَفِيْهَا فَفَسَقُوْا فِيْهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنٰهَا تَدْمِيْرًا ','Dan jika Kami hendak membinasakan suatu negeri, maka Kami perintahkan kepada orang-orang yang hidup mewah di negeri itu (supaya mentaati Allah) tetapi mereka melakukan kedurhakaan dalam negeri itu, maka sudah sepantasnya berlaku terhadapnya perkataan (ketentuan Kami), kemudian Kami hancurkan negeri itu sehancur-hancurnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,17,'وَكَمْ اَهْلَكْنَا مِنَ الْقُرُوْنِ مِنْۢ بَعْدِ نُوْحٍۗ وَكَفٰى بِرَبِّكَ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا','Dan berapa banyaknya kaum sesudah Nuh telah Kami binasakan. Dan cukuplah Tuhanmu Maha Mengetahui lagi Maha Melihat dosa hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,18,'مَنْ كَانَ يُرِيْدُ الْعَاجِلَةَ عَجَّلْنَا لَهٗ فِيْهَا مَا نَشَاۤءُ لِمَنْ نُّرِيْدُ ثُمَّ جَعَلْنَا لَهٗ جَهَنَّمَۚ يَصْلٰىهَا مَذْمُوْمًا مَّدْحُوْرًا','Barangsiapa menghendaki kehidupan sekarang (duniawi), maka Kami segerakan baginya di dunia itu apa yang kami kehendaki bagi orang yang kami kehendaki dan Kami tentukan baginya neraka jahannam; ia akan memasukinya dalam keadaan tercela dan terusir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,19,'وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ كَانَ سَعْيُهُمْ مَّشْكُوْرًا','Dan barangsiapa yang menghendaki kehidupan akhirat dan berusaha ke arah itu dengan sungguh-sungguh sedang ia adalah mukmin, maka mereka itu adalah orang-orang yang usahanya dibalasi dengan baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,20,'كُلًّا نُّمِدُّ هٰٓؤُلَاۤءِ وَهٰٓؤُلَاۤءِ مِنْ عَطَاۤءِ رَبِّكَ ۗوَمَا كَانَ عَطَاۤءُ رَبِّكَ مَحْظُوْرًا ','Kepada masing-masing golongan baik golongan ini maupun golongan itu Kami berikan bantuan dari kemurahan Tuhanmu. Dan kemurahan Tuhanmu tidak dapat dihalangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,21,'اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۗ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجٰتٍ وَّاَكْبَرُ تَفْضِيْلًا','Perhatikanlah bagaimana Kami lebihkan sebagian dari mereka atas sebagian (yang lain). Dan pasti kehidupan akhirat lebih tinggi tingkatnya dan lebih besar keutamaannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,22,'لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَقْعُدَ مَذْمُوْمًا مَّخْذُوْلًا ࣖ ','Janganlah kamu adakan tuhan yang lain di samping Allah, agar kamu tidak menjadi tercela dan tidak ditinggalkan (Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,23,'۞ وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسٰنًاۗ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَآ اَوْ كِلٰهُمَا فَلَا تَقُلْ لَّهُمَآ اُفٍّ وَّلَا تَنْهَرْهُمَا وَقُلْ لَّهُمَا قَوْلًا كَرِيْمًا','Dan Tuhanmu telah memerintahkan supaya kamu jangan menyembah selain Dia dan hendaklah kamu berbuat baik pada ibu bapakmu dengan sebaik-baiknya. Jika salah seorang di antara keduanya atau kedua-duanya sampai berumur lanjut dalam pemeliharaanmu, maka sekali-kali janganlah kamu mengatakan kepada keduanya perkataan "ah" dan janganlah kamu membentak mereka dan ucapkanlah kepada mereka perkataan yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,24,'وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَّبِّ ارْحَمْهُمَا كَمَا رَبَّيٰنِيْ صَغِيْرًاۗ','Dan rendahkanlah dirimu terhadap mereka berdua dengan penuh kesayangan dan ucapkanlah: "Wahai Tuhanku, kasihilah mereka keduanya, sebagaimana mereka berdua telah mendidik aku waktu kecil".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,25,'رَبُّكُمْ اَعْلَمُ بِمَا فِيْ نُفُوْسِكُمْ ۗاِنْ تَكُوْنُوْا صٰلِحِيْنَ فَاِنَّهٗ كَانَ لِلْاَوَّابِيْنَ غَفُوْرًا','Tuhanmu lebih mengetahui apa yang ada dalam hatimu; jika kamu orang-orang yang baik, maka sesungguhnya Dia Maha Pengampun bagi orang-orang yang bertaubat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,26,'وَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِ وَلَا تُبَذِّرْ تَبْذِيْرًا ','Dan berikanlah kepada keluarga-keluarga yang dekat akan haknya, kepada orang miskin dan orang yang dalam perjalanan dan janganlah kamu menghambur-hamburkan (hartamu) secara boros.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,27,'اِنَّ الْمُبَذِّرِيْنَ كَانُوْٓا اِخْوَانَ الشَّيٰطِيْنِ ۗوَكَانَ الشَّيْطٰنُ لِرَبِّهٖ كَفُوْرًا','Sesungguhnya pemboros-pemboros itu adalah saudara-saudara syaitan dan syaitan itu adalah sangat ingkar kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,28,'وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاۤءَ رَحْمَةٍ مِّنْ رَّبِّكَ تَرْجُوْهَا فَقُلْ لَّهُمْ قَوْلًا مَّيْسُوْرًا','Dan jika kamu berpaling dari mereka untuk memperoleh rahmat dari Tuhanmu yang kamu harapkan, maka katakanlah kepada mereka ucapan yang pantas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,29,'وَلَا تَجْعَلْ يَدَكَ مَغْلُوْلَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوْمًا مَّحْسُوْرًا ','Dan janganlah kamu jadikan tanganmu terbelenggu pada lehermu dan janganlah kamu terlalu mengulurkannya karena itu kamu menjadi tercela dan menyesal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,30,'اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا ࣖ','Sesungguhnya Tuhanmu melapangkan rezeki kepada siapa yang Dia kehendaki dan menyempitkannya; sesungguhnya Dia Maha Mengetahui lagi Maha Melihat akan hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,31,'وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۗ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۗ اِنَّ قَتْلَهُمْ كَانَ خِطْـًٔا كَبِيْرًا ','Dan janganlah kamu membunuh anak-anakmu karena takut kemiskinan. Kamilah yang akan memberi rezeki kepada mereka dan juga kepadamu. Sesungguhnya membunuh mereka adalah suatu dosa yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,32,'وَلَا تَقْرَبُوا الزِّنٰىٓ اِنَّهٗ كَانَ فَاحِشَةً ۗوَسَاۤءَ سَبِيْلًا ','Dan janganlah kamu mendekati zina; sesungguhnya zina itu adalah suatu perbuatan yang keji. Dan suatu jalan yang buruk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,33,'وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ وَمَنْ قُتِلَ مَظْلُوْمًا فَقَدْ جَعَلْنَا لِوَلِيِّهٖ سُلْطٰنًا فَلَا يُسْرِفْ فِّى الْقَتْلِۗ اِنَّهٗ كَانَ مَنْصُوْرًا','Dan janganlah kamu membunuh jiwa yang diharamkan Allah (membunuhnya), melainkan dengan suatu (alasan) yang benar. Dan barangsiapa dibunuh secara zalim, maka sesungguhnya Kami telah memberi kekuasaan kepada ahli warisnya, tetapi janganlah ahli waris itu melampaui batas dalam membunuh. Sesungguhnya ia adalah orang yang mendapat pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,34,'وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗۖ وَاَوْفُوْا بِالْعَهْدِۖ اِنَّ الْعَهْدَ كَانَ مَسْـُٔوْلًا','Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih baik (bermanfaat) sampai ia dewasa dan penuhilah janji; sesungguhnya janji itu pasti diminta pertanggungan jawabnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,35,'وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا','Dan sempurnakanlah takaran apabila kamu menakar, dan timbanglah dengan neraca yang benar. Itulah yang lebih utama (bagimu) dan lebih baik akibatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,36,'وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ اُولٰۤىِٕكَ كَانَ عَنْهُ مَسْـُٔوْلًا ','Dan janganlah kamu mengikuti apa yang kamu tidak mempunyai pengetahuan tentangnya. Sesungguhnya pendengaran, penglihatan dan hati, semuanya itu akan diminta pertanggungan jawabnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,37,'وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُوْلًا','Dan janganlah kamu berjalan di muka bumi ini dengan sombong, karena sesungguhnya kamu sekali-kali tidak dapat menembus bumi dan sekali-kali kamu tidak akan sampai setinggi gunung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,38,'كُلُّ ذٰلِكَ كَانَ سَيِّئُهٗ عِنْدَ رَبِّكَ مَكْرُوْهًا ','Semua itu kejahatannya amat dibenci di sisi Tuhanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,39,'ذٰلِكَ مِمَّآ اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۗ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتُلْقٰى فِيْ جَهَنَّمَ مَلُوْمًا مَّدْحُوْرًا ','Itulah sebagian hikmah yang diwahyukan Tuhanmu kepadamu. Dan janganlah kamu mengadakan tuhan yang lain di samping Allah, yang menyebabkan kamu dilemparkan ke dalam neraka dalam keadaan tercela lagi dijauhkan (dari rahmat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,40,'اَفَاَصْفٰىكُمْ رَبُّكُمْ بِالْبَنِيْنَ وَاتَّخَذَ مِنَ الْمَلٰۤىِٕكَةِ اِنَاثًاۗ اِنَّكُمْ لَتَقُوْلُوْنَ قَوْلًا عَظِيْمًا ࣖ ','Maka apakah patut Tuhan memilihkan bagimu anak-anak laki-laki sedang Dia sendiri mengambil anak-anak perempuan di antara para malaikat? Sesungguhnya kamu benar-benar mengucapkan kata-kata yang besar (dosanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,41,'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُوْاۗ وَمَا يَزِيْدُهُمْ اِلَّا نُفُوْرًا','Dan sesungguhnya dalam Al Quran ini Kami telah ulang-ulangi (peringatan-peringatan), agar mereka selalu ingat. Dan ulangan peringatan itu tidak lain hanyalah menambah mereka lari (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,42,'قُلْ لَّوْ كَانَ مَعَهٗ ٓ اٰلِهَةٌ كَمَا يَقُوْلُوْنَ اِذًا لَّابْتَغَوْا اِلٰى ذِى الْعَرْشِ سَبِيْلًا','Katakanlah: "Jikalau ada tuhan-tuhan di samping-Nya, sebagaimana yang mereka katakan, niscaya tuhan-tuhan itu mencari jalan kepada Tuhan yang mempunyai ´Arsy".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,43,' سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَقُوْلُوْنَ عُلُوًّا كَبِيْرًا','Maha Suci dan Maha Tinggi Dia dari apa yang mereka katakan dengan ketinggian yang sebesar-besarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,44,'تُسَبِّحُ لَهُ السَّمٰوٰتُ السَّبْعُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ وَاِنْ مِّنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِهٖ وَلٰكِنْ لَّا تَفْقَهُوْنَ تَسْبِيْحَهُمْۗ اِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا','Langit yang tujuh, bumi dan semua yang ada di dalamnya bertasbih kepada Allah. Dan tak ada suatupun melainkan bertasbih dengan memuji-Nya, tetapi kamu sekalian tidak mengerti tasbih mereka. Sesungguhnya Dia adalah Maha Penyantun lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,45,'وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ حِجَابًا مَّسْتُوْرًاۙ  ','Dan apabila kamu membaca Al Quran niscaya Kami adakan antara kamu dan orang-orang yang tidak beriman kepada kehidupan akhirat, suatu dinding yang tertutup,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,46,'وَّجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِذَا ذَكَرْتَ رَبَّكَ فِى الْقُرْاٰنِ وَحْدَهٗ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوْرًا','dan Kami adakan tutupan di atas hati mereka dan sumbatan di telinga mereka, agar mereka tidak dapat memahaminya. Dan apabila kamu menyebut Tuhanmu saja dalam Al Quran, niscaya mereka berpaling ke belakang karena bencinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,47,'نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُوْنَ بِهٖٓ اِذْ يَسْتَمِعُوْنَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُوْلُ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا ','Kami lebih mengetahui dalam keadaan bagaimana mereka mendengarkan sewaktu mereka mendengarkan kamu, dan sewaktu mereka berbisik-bisik (yaitu) ketika orang-orang zalim itu berkata: "Kamu tidak lain hanyalah mengikuti seorang laki-laki yang kena sihir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,48,'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا','Lihatlah bagaimana mereka membuat perumpamaan-perumpamaan terhadapmu; karena itu mereka menjadi sesat dan tidak dapat lagi menemukan jalan (yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,49,'وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ','Dan mereka berkata: "Apakah bila kami telah menjadi tulang belulang dan benda-benda yang hancur, apa benar-benarkah kami akan dibangkitkan kembali sebagai makhluk yang baru?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,50,'۞ قُلْ كُوْنُوْا حِجَارَةً اَوْ حَدِيْدًاۙ ','Katakanlah: "Jadilah kamu sekalian batu atau besi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,51,'اَوْ خَلْقًا مِّمَّا يَكْبُرُ فِيْ صُدُوْرِكُمْ ۚفَسَيَقُوْلُوْنَ مَنْ يُّعِيْدُنَاۗ قُلِ الَّذِيْ فَطَرَكُمْ اَوَّلَ مَرَّةٍۗ فَسَيُنْغِضُوْنَ اِلَيْكَ رُءُوْسَهُمْ وَيَقُوْلُوْنَ مَتٰى هُوَۗ قُلْ عَسٰٓى اَنْ يَّكُوْنَ قَرِيْبًا','atau suatu makhluk dari makhluk yang tidak mungkin (hidup) menurut pikiranmu". Maka mereka akan bertanya: "Siapa yang akan menghidupkan kami kembali?" Katakanlah: "Yang telah menciptakan kamu pada kali yang pertama". Lalu mereka akan menggeleng-gelengkan kepala mereka kepadamu dan berkata: "Kapan itu (akan terjadi)?" Katakanlah: "Mudah-mudahan waktu berbangkit itu dekat",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,52,'يَوْمَ يَدْعُوْكُمْ فَتَسْتَجِيْبُوْنَ بِحَمْدِهٖ وَتَظُنُّوْنَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا ࣖ ','yaitu pada hari Dia memanggil kamu, lalu kamu mematuhi-Nya sambil memuji-Nya dan kamu mengira, bahwa kamu tidak berdiam (di dalam kubur) kecuali sebentar saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,53,'وَقُلْ لِّعِبَادِيْ يَقُوْلُوا الَّتِيْ هِيَ اَحْسَنُۗ اِنَّ الشَّيْطٰنَ يَنْزَغُ بَيْنَهُمْۗ اِنَّ الشَّيْطٰنَ كَانَ لِلْاِنْسَانِ عَدُوًّا مُّبِيْنًا','Dan katakanlah kepada hamha-hamba-Ku: "Hendaklah mereka mengucapkan perkataan yang lebih baik (benar). Sesungguhnya syaitan itu menimbulkan perselisihan di antara mereka. Sesungguhnya syaitan itu adalah musuh yang nyata bagi manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,54,'رَبُّكُمْ اَعْلَمُ بِكُمْ اِنْ يَّشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَّشَأْ يُعَذِّبْكُمْۗ وَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ وَكِيْلًا','Tuhanmu lebih mengetahui tentang kamu. Dia akan memberi rahmat kepadamu jika Dia menghendaki dan Dia akan mengazabmu, jika Dia menghendaki. Dan, Kami tidaklah mengutusmu untuk menjadi penjaga bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,55,'وَرَبُّكَ اَعْلَمُ بِمَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّٖنَ عَلٰى بَعْضٍ وَّاٰتَيْنَا دَاوٗدَ زَبُوْرًا','Dan Tuhan-mu lebih mengetahui siapa yang (ada) di langit dan di bumi. Dan sesungguhnya telah Kami lebihkan sebagian nabi-nabi itu atas sebagian (yang lain), dan Kami berikan Zabur kepada Daud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,56,'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِهٖ فَلَا يَمْلِكُوْنَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْوِيْلًا','Katakanlah: "Panggillah mereka yang kamu anggap (tuhan) selain Allah, maka mereka tidak akan mempunyai kekuasaan untuk menghilangkan bahaya daripadamu dan tidak pula memindahkannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,57,'اُولٰۤىِٕكَ الَّذِيْنَ يَدْعُوْنَ يَبْتَغُوْنَ اِلٰى رَبِّهِمُ الْوَسِيْلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُوْنَ رَحْمَتَهٗ وَيَخَافُوْنَ عَذَابَهٗۗ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوْرًا','Orang-orang yang mereka seru itu, mereka sendiri mencari jalan kepada Tuhan mereka siapa di antara mereka yang lebih dekat (kepada Allah) dan mengharapkan rahmat-Nya dan takut akan azab-Nya; sesungguhnya azab Tuhanmu adalah suatu yang (harus) ditakuti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,58,'وَاِنْ مِّنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوْهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوْهَا عَذَابًا شَدِيْدًاۗ كَانَ ذٰلِكَ فىِ الْكِتٰبِ مَسْطُوْرًا','Tak ada suatu negeripun (yang durhaka penduduknya), melainkan Kami membinasakannya sebelum hari kiamat atau Kami azab (penduduknya) dengan azab yang sangat keras. Yang demikian itu telah tertulis di dalam kitab (Lauh Mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,59,'وَمَا مَنَعَنَآ اَنْ نُّرْسِلَ بِالْاٰيٰتِ اِلَّآ اَنْ كَذَّبَ بِهَا الْاَوَّلُوْنَۗ وَاٰتَيْنَا ثَمُوْدَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوْا بِهَاۗ وَمَا نُرْسِلُ بِالْاٰيٰتِ اِلَّا تَخْوِيْفًا','Dan sekali-kali tidak ada yang menghalangi Kami untuk mengirimkan (kepadamu) tanda-tanda (kekuasan Kami), melainkan karena tanda-tanda itu telah didustakan oleh orang-orang dahulu. Dan telah Kami berikan kepada Tsamud unta betina itu (sebagai mukjizat) yang dapat dilihat, tetapi mereka menganiaya unta betina itu. Dan Kami tidak memberi tanda-tanda itu melainkan untuk menakuti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,60,'وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۗ وَمَا جَعَلْنَا الرُّءْيَا الَّتِيْٓ اَرَيْنٰكَ اِلَّا فِتْنَةً لِّلنَّاسِ وَالشَّجَرَةَ الْمَلْعُوْنَةَ فِى الْقُرْاٰنِ ۗ وَنُخَوِّفُهُمْۙ فَمَا يَزِيْدُهُمْ اِلَّا طُغْيَانًا كَبِيْرًا ࣖ ','Dan (ingatlah), ketika Kami wahyukan kepadamu: "Sesungguhnya (ilmu) Tuhanmu meliputi segala manusia". Dan Kami tidak menjadikan mimpi yang telah Kami perlihatkan kepadamu, melainkan sebagai ujian bagi manusia dan (begitu pula) pohon kayu yang terkutuk dalam Al Quran. Dan Kami menakut-nakuti mereka, tetapi yang demikian itu hanyalah menambah besar kedurhakaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,61,'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ طِيْنًاۚ ','Dan (ingatlah), tatkala Kami berfirman kepada para malaikat: "Sujudlah kamu semua kepada Adam", lalu mereka sujud kecuali iblis. Dia berkata: "Apakah aku akan sujud kepada orang yang Engkau ciptakan dari tanah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,62,'قَالَ اَرَاَيْتَكَ هٰذَا الَّذِيْ كَرَّمْتَ عَلَيَّ لَىِٕنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهٗٓ اِلَّا قَلِيْلًا','Dia (iblis) berkata: "Terangkanlah kepadaku inikah orangnya yang Engkau muliakan atas diriku? Sesungguhnya jika Engkau memberi tangguh kepadaku sampai hari kiamat, niscaya benar-benar akan aku sesatkan keturunannya, kecuali sebahagian kecil".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,63,'قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَاۤؤُكُمْ جَزَاۤءً مَّوْفُوْرًا','Tuhan berfirman: "Pergilah, barangsiapa di antara mereka yang mengikuti kamu, maka sesungguhnya neraka Jahannam adalah balasanmu semua, sebagai suatu pembalasan yang cukup.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,64,'وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِى الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا','Dan hasunglah siapa yang kamu sanggupi di antara mereka dengan ajakanmu, dan kerahkanlah terhadap mereka pasukan berkuda dan pasukanmu yang berjalan kaki dan berserikatlah dengan mereka pada harta dan anak-anak dan beri janjilah mereka. Dan tidak ada yang dijanjikan oleh syaitan kepada mereka melainkan tipuan belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,65,'اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌۗ وَكَفٰى بِرَبِّكَ وَكِيْلًا','Sesungguhnya hamba-hamba-Ku, kamu tidak dapat berkuasa atas mereka. Dan cukuplah Tuhan-mu sebagai Penjaga".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,66,'رَبُّكُمُ الَّذِيْ يُزْجِيْ لَكُمُ الْفُلْكَ فِى الْبَحْرِ لِتَبْتَغُوْا مِنْ فَضْلِهٖۗ اِنَّهٗ كَانَ بِكُمْ رَحِيْمًا','Tuhan-mu adalah yang melayarkan kapal-kapal di lautan untukmu, agar kamu mencari sebahagian dari karunia-Nya. Sesungguhnya Dia adalah Maha Penyayang terhadapmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,67,'وَاِذَا مَسَّكُمُ الضُّرُّ فِى الْبَحْرِ ضَلَّ مَنْ تَدْعُوْنَ اِلَّآ اِيَّاهُۚ فَلَمَّا نَجّٰىكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۗ وَكَانَ الْاِنْسَانُ كَفُوْرًا','Dan apabila kamu ditimpa bahaya di lautan, niscaya hilanglah siapa yang kamu seru kecuali Dia, Maka tatkala Dia menyelamatkan kamu ke daratan, kamu berpaling. Dan manusia itu adalah selalu tidak berterima kasih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,68,'اَفَاَمِنْتُمْ اَنْ يَّخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوْا لَكُمْ وَكِيْلًا ۙ ','Maka apakah kamu merasa aman (dari hukuman Tuhan) yang menjungkir balikkan sebagian daratan bersama kamu atau Dia meniupkan (angin keras yang membawa) batu-batu kecil? dan kamu tidak akan mendapat seorang pelindungpun bagi kamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,69,'اَمْ اَمِنْتُمْ اَنْ يُّعِيْدَكُمْ فِيْهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِّنَ الرِّيْحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوْا لَكُمْ عَلَيْنَا بِهٖ تَبِيْعًا','atau apakah kamu merasa aman dari dikembalikan-Nya kamu ke laut sekali lagi, lalu Dia meniupkan atas kamu angin taupan dan ditenggelamkan-Nya kamu disebabkan kekafiranmu. Dan kamu tidak akan mendapat seorang penolongpun dalam hal ini terhadap (siksaan) Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,70,'۞ وَلَقَدْ كَرَّمْنَا بَنِيْٓ اٰدَمَ وَحَمَلْنٰهُمْ فِى الْبَرِّ وَالْبَحْرِ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلٰى كَثِيْرٍ مِّمَّنْ خَلَقْنَا تَفْضِيْلًا ࣖ ','Dan sesungguhnya telah Kami muliakan anak-anak Adam, Kami angkut mereka di daratan dan di lautan, Kami beri mereka rezeki dari yang baik-baik dan Kami lebihkan mereka dengan kelebihan yang sempurna atas kebanyakan makhluk yang telah Kami ciptakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,71,'يَوْمَ نَدْعُوْا كُلَّ اُنَاسٍۢ بِاِمَامِهِمْۚ فَمَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَاُولٰۤىِٕكَ يَقْرَءُوْنَ كِتٰبَهُمْ وَلَا يُظْلَمُوْنَ فَتِيْلًا','(Ingatlah) suatu hari (yang di hari itu) Kami panggil tiap umat dengan pemimpinnya; dan barangsiapa yang diberikan kitab amalannya di tangan kanannya maka mereka ini akan membaca kitabnya itu, dan mereka tidak dianiaya sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,72,'وَمَنْ كَانَ فِيْ هٰذِهٖٓ اَعْمٰى فَهُوَ فِى الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَبِيْلًا','Dan barangsiapa yang buta (hatinya) di dunia ini, niscaya di akhirat (nanti) ia akan lebih buta (pula) dan lebih tersesat dari jalan (yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,73,'وَاِنْ كَادُوْا لَيَفْتِنُوْنَكَ عَنِ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهٗۖ وَاِذًا لَّاتَّخَذُوْكَ خَلِيْلًا','Dan sesungguhnya mereka hampir memalingkan kamu dari apa yang telah Kami wahyukan kepadamu, agar kamu membuat yang lain secara bohong terhadap Kami; dan kalau sudah begitu tentu|ah mereka mengambil kamu jadi sahabat yang setia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,74,'وَلَوْلَآ اَنْ ثَبَّتْنٰكَ لَقَدْ كِدْتَّ تَرْكَنُ اِلَيْهِمْ شَيْـًٔا قَلِيْلًا ۙ  ','Dan kalau Kami tidak memperkuat (hati)mu, niscaya kamu hampir-hampir condong sedikit kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,75,'اِذًا لَّاَذَقْنٰكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيْرًا','kalau terjadi demikian, benar-benarlah Kami akan rasakan kepadamu (siksaan) berlipat ganda di dunia ini dan begitu (pula siksaan) berlipat ganda sesudah mati, dan kamu tidak akan mendapat seorang penolongpun terhadap Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,76,'وَاِنْ كَادُوْا لَيَسْتَفِزُّوْنَكَ مِنَ الْاَرْضِ لِيُخْرِجُوْكَ مِنْهَا وَاِذًا لَّا يَلْبَثُوْنَ خِلٰفَكَ اِلَّا قَلِيْلًا','Dan sesungguhnya benar-benar mereka hampir membuatmu gelisah di negeri (Mekah) untuk mengusirmu daripadanya dan kalau terjadi demikian, niscaya sepeninggalmu mereka tidak tinggal, melainkan sebentar saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,77,'سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُّسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيْلًا ࣖ ','(Kami menetapkan yang demikian) sebagai suatu ketetapan terhadap rasul-rasul Kami yang Kami utus sebelum kamu dan tidak akan kamu dapati perubahan bagi ketetapan Kami itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,78,'اَقِمِ الصَّلٰوةَ لِدُلُوْكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۗ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوْدًا ','Dirikanlah shalat dari sesudah matahari tergelincir sampai gelap malam dan (dirikanlah pula shalat) subuh. Sesungguhnya shalat subuh itu disaksikan (oleh malaikat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,79,'وَمِنَ الَّيْلِ فَتَهَجَّدْ بِهٖ نَافِلَةً لَّكَۖ عَسٰٓى اَنْ يَّبْعَثَكَ رَبُّكَ مَقَامًا مَّحْمُوْدًا','Dan pada sebahagian malam hari bersembahyang tahajudlah kamu sebagai suatu ibadah tambahan bagimu; mudah-mudahan Tuhan-mu mengangkat kamu ke tempat yang terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,80,'وَقُلْ رَّبِّ اَدْخِلْنِيْ مُدْخَلَ صِدْقٍ وَّاَخْرِجْنِيْ مُخْرَجَ صِدْقٍ وَّاجْعَلْ لِّيْ مِنْ لَّدُنْكَ سُلْطٰنًا نَّصِيْرًا','Dan katakanlah: "Ya Tuhan-ku, masukkanlah aku secara masuk yang benar dan keluarkanlah (pula) aku secara keluar yang benar dan berikanlah kepadaku dari sisi Engkau kekuasaan yang menolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,81,'وَقُلْ جَاۤءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ  ۖاِنَّ الْبَاطِلَ كَانَ زَهُوْقًا','Dan katakanlah: "Yang benar telah datang dan yang batil telah lenyap". Sesungguhnya yang batil itu adalah sesuatu yang pasti lenyap.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,82,'وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَاۤءٌ وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَۙ وَلَا يَزِيْدُ الظّٰلِمِيْنَ اِلَّا خَسَارًا','Dan Kami turunkan dari Al Quran suatu yang menjadi penawar dan rahmat bagi orang-orang yang beriman dan Al Quran itu tidaklah menambah kepada orang-orang yang zalim selain kerugian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,83,'وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَـُٔوْسًا','Dan apabila Kami berikan kesenangan kepada manusia niscaya berpalinglah dia; dan membelakang dengan sikap yang sombong; dan apabila dia ditimpa kesusahan niscaya dia berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,84,'قُلْ كُلٌّ يَّعْمَلُ عَلٰى شَاكِلَتِهٖۗ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَبِيْلًا ࣖ','Katakanlah: "Tiap-tiap orang berbuat menurut keadaannya masing-masing". Maka Tuhanmu lebih mengetahui siapa yang lebih benar jalannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,85,'وَيَسْـَٔلُوْنَكَ عَنِ الرُّوْحِۗ قُلِ الرُّوْحُ مِنْ اَمْرِ رَبِّيْ وَمَآ اُوْتِيْتُمْ مِّنَ الْعِلْمِ اِلَّا قَلِيْلًا ','Dan mereka bertanya kepadamu tentang roh. Katakanlah: "Roh itu termasuk urusan Tuhan-ku, dan tidaklah kamu diberi pengetahuan melainkan sedikit".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,86,'وَلَىِٕنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهٖ عَلَيْنَا وَكِيْلًاۙ؉؉','Dan sesungguhnya jika Kami menghendaki, niscaya Kami lenyapkan apa yang telah Kami wahyukan kepadamu, dan dengan pelenyapan itu, kamu tidak akan mendapatkan seorang pembelapun terhadap Kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,87,'اِلَّا رَحْمَةً مِّنْ رَّبِّكَۗ اِنَّ فَضْلَهٗ كَانَ عَلَيْكَ كَبِيْرًا','kecuali karena rahmat dari Tuhanmu. Sesungguhnya karunia-Nya atasmu adalah besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,88,'قُلْ لَّىِٕنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَّأْتُوْا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُوْنَ بِمِثْلِهٖ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيْرًا','Katakanlah: "Sesungguhnya jika manusia dan jin berkumpul untuk membuat yang serupa Al Quran ini, niscaya mereka tidak akan dapat membuat yang serupa dengan dia, sekalipun sebagian mereka menjadi pembantu bagi sebagian yang lain".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,89,'وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا','Dan sesungguhnya Kami telah mengulang-ulang kepada manusia dalam Al Quran ini tiap-tiap macam perumpamaan, tapi kebanyakan manusia tidak menyukai kecuali mengingkari(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,90,'وَقَالُوْا لَنْ نُّؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْۢبُوْعًاۙ  ','Dan mereka berkata: "Kami sekali-kali tidak percaya kepadamu hingga kamu memancarkan mata air dan bumi untuk kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,91,'اَوْ تَكُوْنَ لَكَ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّعِنَبٍ فَتُفَجِّرَ الْاَنْهٰرَ خِلٰلَهَا تَفْجِيْرًاۙ  ','atau kamu mempunyai sebuah kebun korma dan anggur, lalu kamu alirkan sungai-sungai di celah kebun yang deras alirannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,92,'اَوْ تُسْقِطَ السَّمَاۤءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰۤىِٕكَةِ قَبِيْلًاۙ  ','atau kamu jatuhkan langit berkeping-keping atas kami, sebagaimana kamu katakan atau kamu datangkan Allah dan malaikat-malaikat berhadapan muka dengan kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,93,'اَوْ يَكُوْنَ لَكَ بَيْتٌ مِّنْ زُخْرُفٍ اَوْ تَرْقٰى فِى السَّمَاۤءِ ۗوَلَنْ نُّؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتٰبًا نَّقْرَؤُهٗۗ قُلْ سُبْحَانَ رَبِّيْ هَلْ كُنْتُ اِلَّا بَشَرًا رَّسُوْلًا ࣖ  ','Atau kamu mempunyai sebuah rumah dari emas, atau kamu naik ke langit. Dan kami sekali-kali tidak akan mempercayai kenaikanmu itu hingga kamu turunkan atas kami sebuah kitab yang kami baca". Katakanlah: "Maha Suci Tuhanku, bukankah aku ini hanya seorang manusia yang menjadi rasul?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,94,'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰٓى اِلَّآ اَنْ قَالُوْٓا اَبَعَثَ اللّٰهُ بَشَرًا رَّسُوْلًا','Dan tidak ada sesuatu yang menghalangi manusia untuk beriman tatkala datang petunjuk kepadanya, kecuali perkataan mereka: "Adakah Allah mengutus seorang manusia menjadi rasuI?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,95,'قُلْ لَّوْ كَانَ فِى الْاَرْضِ مَلٰۤىِٕكَةٌ يَّمْشُوْنَ مُطْمَىِٕنِّيْنَ لَنَزَّلْنَا عَلَيْهِمْ مِّنَ السَّمَاۤءِ مَلَكًا رَّسُوْلًا','Katakanlah: "Kalau seandainya ada malaikat-malaikat yang berjalan-jalan sebagai penghuni di bumi, niscaya Kami turunkan dari langit kepada mereka seorang malaikat menjadi rasul".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,96,'قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۗ اِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا','Katakanlah: "Cukuplah Allah menjadi saksi antara aku dan kamu sekalian. Sesungguhnya Dia adalah Maha Mengetahui lagi Maha Melihat akan hamba-hamba-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,97,'وَمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَاۤءَ مِنْ دُوْنِهٖۗ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوْهِهِمْ عُمْيًا وَّبُكْمًا وَّصُمًّاۗ مَأْوٰىهُمْ جَهَنَّمُۗ  كُلَّمَا خَبَتْ زِدْنٰهُمْ سَعِيْرًا','Dan barangsiapa yang ditunjuki Allah, dialah yang mendapat petunjuk dan barangsiapa yang Dia sesatkan maka sekali-kali kamu tidak akan mendapat penolong-penolong bagi mereka selain dari Dia. Dan Kami akan mengumpulkan mereka pada hari kiamat (diseret) atas muka mereka dalam keadaan buta, bisu dan pekak. Tempat kediaman mereka adalah neraka jahannam. Tiap-tiap kali nyala api Jahannam itu akan padam, Kami tambah lagi bagi mereka nyalanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,98,'ذٰلِكَ جَزَاۤؤُهُمْ بِاَنَّهُمْ كَفَرُوْا بِاٰيٰتِنَا وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ','Itulah balasan bagi mereka, karena sesungguhnya mereka kafir kepada ayat-ayat Kami dan (karena mereka) berkata: "Apakah bila kami telah menjadi tulang belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk baru?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,99,'۞ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلًا لَّا رَيْبَ فِيْهِۗ فَاَبَى الظّٰلِمُوْنَ اِلَّا كُفُوْرًا','Dan apakah mereka tidak memperhatikan bahwasanya Allah yang menciptakan langit dan bumi adalah kuasa (pula) menciptakan yang serupa dengan mereka, dan telah menetapkan waktu yang tertentu bagi mereka yang tidak ada keraguan padanya? Maka orang-orang zalim itu tidak menghendaki kecuali kekafiran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,100,'قُلْ لَّوْ اَنْتُمْ تَمْلِكُوْنَ خَزَاۤىِٕنَ رَحْمَةِ رَبِّيْٓ اِذًا لَّاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۗ وَكَانَ الْاِنْسَانُ قَتُوْرًا ࣖ','Katakanlah: "Kalau seandainya kamu menguasai perbendaharaan-perbendaharaan rahmat Tuhanku, niscaya perbendaharaan itu kamu tahan, karena takut membelanjakannya". Dan adalah manusia itu sangat kikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,101,'وَلَقَدْ اٰتَيْنَا مُوْسٰى تِسْعَ اٰيٰتٍۢ بَيِّنٰتٍ فَسْـَٔلْ بَنِيْٓ اِسْرَاۤءِيْلَ اِذْ جَاۤءَهُمْ فَقَالَ لَهٗ فِرْعَوْنُ اِنِّيْ لَاَظُنُّكَ يٰمُوْسٰى مَسْحُوْرًا ','Dan sesungguhnya Kami telah memberikan kepada Musa sembilan buah mukjizat yang nyata, maka tanyakanlah kepada Bani Israil, tatkala Musa datang kepada mereka lalu Fir´aun berkata kepadanya: "Sesungguhnya aku sangka kamu, hai Musa, seorang yang kena sihir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,102,'قَالَ لَقَدْ عَلِمْتَ مَآ اَنْزَلَ هٰٓؤُلَاۤءِ اِلَّا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ بَصَاۤىِٕرَۚ وَاِنِّيْ لَاَظُنُّكَ يٰفِرْعَوْنُ مَثْبُوْرًا ','Musa menjawab: "Sesungguhnya kamu telah mengetahui, bahwa tiada yang menurunkan mukjizat-mukjizat itu kecuali Tuhan Yang memelihara langit dan bumi sebagai bukti-bukti yang nyata; dan sesungguhnya aku mengira kamu, hai Fir´aun, seorang yang akan binasa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,103,'فَاَرَادَ اَنْ يَّسْتَفِزَّهُمْ مِّنَ الْاَرْضِ فَاَغْرَقْنٰهُ وَمَنْ مَّعَهٗ جَمِيْعًاۙ ','Kemudian (Fir´aun) hendak mengusir mereka (Musa dan pengikut-pengikutnya) dari bumi (Mesir) itu, maka Kami tenggelamkan dia (Fir´aun) serta orang-orang yang bersama-sama dia seluruhnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,104,'وَّقُلْنَا مِنْۢ بَعْدِهٖ لِبَنِيْٓ اِسْرَاۤءِيْلَ اسْكُنُوا الْاَرْضَ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَفِيْفًاۗ   ','dan Kami berfirman sesudah itu kepada Bani Israil: "Diamlah di negeri ini, maka apabila datang masa berbangkit, niscaya Kami datangkan kamu dalam keadaan bercampur baur (dengan musuhmu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,105,'وَبِالْحَقِّ اَنْزَلْنٰهُ وَبِالْحَقِّ نَزَلَۗ وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًاۘ','Dan Kami turunkan (Al Quran) itu dengan sebenar-benarnya dan Al Quran itu telah turun dengan (membawa) kebenaran. Dan Kami tidak mengutus kamu, melainkan sebagai pembawa berita gembira dan pemberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,106,'وَقُرْاٰنًا فَرَقْنٰهُ لِتَقْرَاَهٗ عَلَى النَّاسِ عَلٰى مُكْثٍ وَّنَزَّلْنٰهُ تَنْزِيْلًا ','Dan Al Quran itu telah Kami turunkan dengan berangsur-angsur agar kamu membacakannya perlahan-lahan kepada manusia dan Kami menurunkannya bagian demi bagian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,107,'قُلْ اٰمِنُوْا بِهٖٓ اَوْ لَا تُؤْمِنُوْاۗ اِنَّ الَّذِيْنَ اُوْتُوا الْعِلْمَ مِنْ قَبْلِهٖٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّوْنَ لِلْاَذْقَانِ سُجَّدًاۙ','Katakanlah: "Berimanlah kamu kepadanya atau tidak usah beriman (sama saja bagi Allah). Sesungguhnya orang-orang yang diberi pengetahuan sebelumnya apabila Al Quran dibacakan kepada mereka, mereka menyungkur atas muka mereka sambil bersujud,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,108,'وَّيَقُوْلُوْنَ سُبْحٰنَ رَبِّنَآ اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُوْلًا','dan mereka berkata: "Maha Suci Tuhan kami, sesungguhnya janji Tuhan kami pasti dipenuhi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,109,'وَيَخِرُّوْنَ لِلْاَذْقَانِ يَبْكُوْنَ وَيَزِيْدُهُمْ خُشُوْعًا ۩','Dan mereka menyungkur atas muka mereka sambil menangis dan mereka bertambah khusyu´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,110,'قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۗ اَيًّا مَّا تَدْعُوْا فَلَهُ الْاَسْمَاۤءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَبِيْلًا','Katakanlah: "Serulah Allah atau serulah Ar-Rahman. Dengan nama yang mana saja kamu seru, Dia mempunyai al asmaaul husna (nama-nama yang terbaik) dan janganlah kamu mengeraskan suaramu dalam shalatmu dan janganlah pula merendahkannya dan carilah jalan tengah di antara kedua itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,17,111,'وَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ لَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَلَمْ يَكُنْ لَّهٗ وَلِيٌّ مِّنَ الذُّلِّ وَكَبِّرْهُ تَكْبِيْرًا ࣖ','Dan katakanlah: "Segala puji bagi Allah Yang tidak mempunyai anak dan tidak mempunyai sekutu dalam kerajaan-Nya dan Dia bukan pula hina yang memerlukan penolong dan agungkanlah Dia dengan pengagungan yang sebesar-besarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,1,'اَلْحَمْدُ لِلّٰهِ الَّذِيْٓ اَنْزَلَ عَلٰى عَبْدِهِ الْكِتٰبَ وَلَمْ يَجْعَلْ لَّهٗ عِوَجًا ۜ','Segala puji bagi Allah yang telah menurunkan kepada hamba-Nya Al Kitab (Al-Quran) dan Dia tidak mengadakan kebengkokan di dalamnya;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,2,'قَيِّمًا لِّيُنْذِرَ بَأْسًا شَدِيْدًا مِّنْ لَّدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا حَسَنًاۙ ','sebagai bimbingan yang lurus, untuk memperingatkan siksaan yang sangat pedih dari sisi Allah dan memberi berita gembira kepada orang-orang yang beriman, yang mengerjakan amal saleh, bahwa mereka akan mendapat pembalasan yang baik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,3,'مَّاكِثِيْنَ فِيْهِ اَبَدًاۙ ','mereka kekal di dalamnya untuk selama-lamanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,4,'وَّيُنْذِرَ الَّذِيْنَ قَالُوا اتَّخَذَ اللّٰهُ وَلَدًاۖ  ','Dan untuk memperingatkan kepada orang-orang yang berkata: "Allah mengambil seorang anak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,5,'مَّا لَهُمْ بِهٖ مِنْ عِلْمٍ وَّلَا لِاٰبَاۤىِٕهِمْۗ  كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۗ اِنْ يَّقُوْلُوْنَ اِلَّا كَذِبًا','Mereka sekali-kali tidak mempunyai pengetahuan tentang hal itu, begitu pula nenek moyang mereka. Alangkah buruknya kata-kata yang keluar dari mulut mereka; mereka tidak mengatakan (sesuatu) kecuali dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,6,'فَلَعَلَّكَ بَاخِعٌ نَّفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَّمْ يُؤْمِنُوْا بِهٰذَا الْحَدِيْثِ اَسَفًا','Maka (apakah) barangkali kamu akan membunuh dirimu karena bersedih hati setelah mereka berpaling, sekiranya mereka tidak beriman kepada keterangan ini (Al-Quran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,7,'اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ زِيْنَةً لَّهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلًا','Sesungguhnya Kami telah menjadikan apa yang di bumi sebagai perhiasan baginya, agar Kami menguji mereka siapakah di antara mereka yang terbaik perbuatannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,8,'وَاِنَّا لَجَاعِلُوْنَ مَا عَلَيْهَا صَعِيْدًا جُرُزًاۗ ','Dan sesungguhnya Kami benar-benar akan menjadikan (pula) apa yang di atasnya menjadi tanah rata lagi tandus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,9,'اَمْ حَسِبْتَ اَنَّ اَصْحٰبَ الْكَهْفِ وَالرَّقِيْمِ كَانُوْا مِنْ اٰيٰتِنَا عَجَبًا','Atau kamu mengira bahwa orang-orang yang mendiami gua dan (yang mempunyai) raqim itu, mereka termasuk tanda-tanda kekuasaan Kami yang mengherankan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,10,'اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوْا رَبَّنَآ اٰتِنَا مِنْ لَّدُنْكَ رَحْمَةً وَّهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَدًا','(Ingatlah) tatkala para pemuda itu mencari tempat berlindung ke dalam gua, lalu mereka berdoa: "Wahai Tuhan kami, berikanlah rahmat kepada kami dari sisi-Mu dan sempurnakanlah bagi kami petunjuk yang lurus dalam urusan kami (ini)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,11,'فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِى الْكَهْفِ سِنِيْنَ عَدَدًاۙ  ','Maka Kami tutup telinga mereka beberapa tahun dalam gua itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,12,'ثُمَّ بَعَثْنٰهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُوْٓا اَمَدًا ࣖ','Kemudian Kami bangunkan mereka, agar Kami mengetahui manakah di antara kedua golongan itu] yang lebih tepat dalam menghitung berapa lama mereka tinggal (dalam gua itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,13,'نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۗ اِنَّهُمْ فِتْيَةٌ اٰمَنُوْا بِرَبِّهِمْ وَزِدْنٰهُمْ هُدًىۖ   ','Kami kisahkan kepadamu (Muhammad) cerita ini dengan benar. Sesungguhnya mereka adalah pemuda-pemuda yang beriman kepada Tuhan mereka, dan Kami tambah pula untuk mereka petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,14,'وَّرَبَطْنَا عَلٰى قُلُوْبِهِمْ اِذْ قَامُوْا فَقَالُوْا رَبُّنَا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ لَنْ نَّدْعُوَا۟ مِنْ دُوْنِهٖٓ اِلٰهًا لَّقَدْ قُلْنَآ اِذًا شَطَطًا','Dan Kami meneguhkan hati mereka diwaktu mereka berdiri, lalu mereka pun berkata, "Tuhan kami adalah Tuhan seluruh langit dan bumi; kami sekali-kali tidak menyeru Tuhan selain Dia, sesungguhnya kami kalau demikian telah mengucapkan perkataan yang amat jauh dari kebenaran".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,15,'هٰٓؤُلَاۤءِ قَوْمُنَا اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةًۗ  لَوْلَا يَأْتُوْنَ عَلَيْهِمْ بِسُلْطٰنٍۢ بَيِّنٍۗ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ  ','Kaum kami ini telah menjadikan selain Dia sebagai tuhan-tuhan (untuk disembah). Mengapa mereka tidak mengemukakan alasan yang terang (tentang kepercayaan mereka)? Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kebohongan terhadap Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,16,'وَاِذِ اعْتَزَلْتُمُوْهُمْ وَمَا يَعْبُدُوْنَ اِلَّا اللّٰهَ فَأْوٗٓا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِّنْ رَّحْمَتِهٖ وَيُهَيِّئْ لَكُمْ مِّنْ اَمْرِكُمْ مِّرْفَقًا','Dan apabila kamu meninggalkan mereka dan apa yang mereka sembah selain Allah, maka carilah tempat berlindung ke dalam gua itu, niscaya Tuhanmu akan melimpahkan sebagian rahmat-Nya kepadamu dan menyediakan sesuatu yang berguna bagimu dalam urusan kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,17,'۞ وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَّزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِيْنِ وَاِذَا غَرَبَتْ تَّقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِيْ فَجْوَةٍ مِّنْهُۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ ۗمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهٗ وَلِيًّا مُّرْشِدًا ࣖ ','Dan kamu akan melihat matahari ketika terbit, condong dari gua mereka ke sebelah kanan, dan bila matahari terbenam menjauhi mereka ke sebelah kiri sedang mereka berada dalam tempat yang luas dalam gua itu. Itu adalah sebagian dari tanda-tanda (kebesaran) Allah. Barangsiapa yang diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa yang disesatkan-Nya, maka kamu tidak akan mendapatkan seorang pemimpinpun yang dapat memberi petunjuk kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,18,'وَتَحْسَبُهُمْ اَيْقَاظًا وَّهُمْ رُقُوْدٌ  ۖوَّنُقَلِّبُهُمْ ذَاتَ الْيَمِيْنِ وَذَاتَ الشِّمَالِ ۖوَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيْدِۗ  لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَّلَمُلِئْتَ مِنْهُمْ رُعْبًا','Dan kamu mengira mereka itu bangun, padahal mereka tidur; Dan kami balik-balikkan mereka ke kanan dan ke kiri, sedang anjing mereka mengunjurkan kedua lengannya di muka pintu gua. Dan jika kamu menyaksikan mereka tentulah kamu akan berpaling dari mereka dengan melarikan diri dan tentulah (hati) kamu akan dipenuhi oleh ketakutan terhadap mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,19,'وَكَذٰلِكَ بَعَثْنٰهُمْ لِيَتَسَاۤءَلُوْا بَيْنَهُمْۗ قَالَ قَاۤىِٕلٌ مِّنْهُمْ كَمْ لَبِثْتُمْۗ قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالُوْا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْۗ فَابْعَثُوْٓا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِهٖٓ اِلَى الْمَدِيْنَةِ فَلْيَنْظُرْ اَيُّهَآ اَزْكٰى طَعَامًا فَلْيَأْتِكُمْ بِرِزْقٍ مِّنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ اَحَدًا ','Dan demikianlah Kami bangunkan mereka agar mereka saling bertanya di antara mereka sendiri. Berkatalah salah seorang di antara mereka: Sudah berapa lamakah kamu berada (disini?)". Mereka menjawab: "Kita berada (disini) sehari atau setengah hari". Berkata (yang lain lagi): "Tuhan kamu lebih mengetahui berapa lamanya kamu berada (di sini). Maka suruhlah salah seorang di antara kamu untuk pergi ke kota dengan membawa uang perakmu ini, dan hendaklah dia lihat manakah makanan yang lebih baik, maka hendaklah ia membawa makanan itu untukmu, dan hendaklah ia berlaku lemah-lembut dan janganlah sekali-kali menceritakan halmu kepada seorangpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,20,'اِنَّهُمْ اِنْ يَّظْهَرُوْا عَلَيْكُمْ يَرْجُمُوْكُمْ اَوْ يُعِيْدُوْكُمْ فِيْ مِلَّتِهِمْ وَلَنْ تُفْلِحُوْٓا اِذًا اَبَدًا','Sesungguhnya jika mereka dapat mengetahui tempatmu, niscaya mereka akan melempar kamu dengan batu, atau memaksamu kembali kepada agama mereka, dan jika demikian niscaya kamu tidak akan beruntung selama lamanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,21,'وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوْٓا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاَنَّ السَّاعَةَ لَا رَيْبَ فِيْهَاۚ اِذْ يَتَنَازَعُوْنَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوْا عَلَيْهِمْ بُنْيَانًاۗ رَبُّهُمْ اَعْلَمُ بِهِمْۗ قَالَ الَّذِيْنَ غَلَبُوْا عَلٰٓى اَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَّسْجِدًا','Dan demikian (pula) Kami mempertemukan (manusia) dengan mereka, agar manusia itu mengetahui, bahwa janji Allah itu benar, dan bahwa kedatangan hari kiamat tidak ada keraguan padanya. Ketika orang-orang itu berselisih tentang urusan mereka, orang-orang itu berkata: "Dirikan sebuah bangunan di atas (gua) mereka, Tuhan mereka lebih mengetahui tentang mereka". Orang-orang yang berkuasa atas urusan mereka berkata: "Sesungguhnya kami akan mendirikan sebuah rumah peribadatan di atasnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,22,'سَيَقُوْلُوْنَ ثَلٰثَةٌ رَّابِعُهُمْ كَلْبُهُمْۚ وَيَقُوْلُوْنَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًاۢ بِالْغَيْبِۚ وَيَقُوْلُوْنَ سَبْعَةٌ وَّثَامِنُهُمْ كَلْبُهُمْ ۗقُلْ رَّبِّيْٓ اَعْلَمُ بِعِدَّتِهِمْ مَّا يَعْلَمُهُمْ اِلَّا قَلِيْلٌ ەۗ فَلَا تُمَارِ فِيْهِمْ اِلَّا مِرَاۤءً ظَاهِرًا ۖوَّلَا تَسْتَفْتِ فِيْهِمْ مِّنْهُمْ اَحَدًا ࣖ','Nanti (ada orang yang akan) mengatakan (jumlah mereka) adalah tiga orang yang keempat adalah anjingnya, dan (yang lain) mengatakan: "(jumlah mereka) adalah lima orang yang keenam adalah anjing nya", sebagai terkaan terhadap barang yang gaib; dan (yang lain lagi) mengatakan: "(jumlah mereka) tujuh orang, yang ke delapan adalah anjingnya". Katakanlah: "Tuhanku lebih mengetahui jumlah mereka; tidak ada orang yang mengetahui (bilangan) mereka kecuali sedikit". Karena itu janganlah kamu (Muhammad) bertengkar tentang hal mereka, kecuali pertengkaran lahir saja dan jangan kamu menanyakan tentang mereka (pemuda-pemuda itu) kepada seorangpun di antara mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,23,'وَلَا تَقُوْلَنَّ لِشَا۟يْءٍ اِنِّيْ فَاعِلٌ ذٰلِكَ غَدًاۙ  ','Dan jangan sekali-kali kamu mengatakan tentang sesuatu: "Sesungguhnya aku akan mengerjakan ini besok pagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,24,'اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۖوَاذْكُرْ رَّبَّكَ اِذَا نَسِيْتَ وَقُلْ عَسٰٓى اَنْ يَّهْدِيَنِ رَبِّيْ لِاَقْرَبَ مِنْ هٰذَا رَشَدًا','kecuali (dengan menyebut): "Insya Allah". Dan ingatlah kepada Tuhanmu jika kamu lupa dan katakanlah: "Mudah-mudahan Tuhanku akan memberiku petunjuk kepada yang lebih dekat kebenarannya dari pada ini".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,25,'وَلَبِثُوْا فِيْ كَهْفِهِمْ ثَلٰثَ مِائَةٍ سِنِيْنَ وَازْدَادُوْا تِسْعًا','Dan mereka tinggal dalam gua mereka tiga ratus tahun dan ditambah sembilan tahun (lagi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,26,'قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُوْا ۚ لَهٗ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ اَبْصِرْ بِهٖ وَاَسْمِعْۗ مَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّۗ وَلَا يُشْرِكُ فِيْ حُكْمِهٖٓ اَحَدًا','Katakanlah: "Allah lebih mengetahui berapa lamanya mereka tinggal (di gua); kepunyaan-Nya-lah semua yang tersembunyi di langit dan di bumi. Alangkah terang penglihatan-Nya dan alangkah tajam pendengaran-Nya; tak ada seorang pelindungpun bagi mereka selain dari pada-Nya; dan Dia tidak mengambil seorangpun menjadi sekutu-Nya dalam menetapkan keputusan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,27,'وَاتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۗ  لَا مُبَدِّلَ لِكَلِمٰتِهٖۗ وَلَنْ تَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا','Dan bacakanlah apa yang diwahyukan kepadamu, yaitu kitab Tuhanmu (Al Quran). Tidak ada (seorangpun) yang dapat merubah kalimat-kalimat-Nya. Dan kamu tidak akan dapat menemukan tempat berlindung selain dari pada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,28,'وَاصْبِرْ نَفْسَكَ مَعَ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ وَلَا تَعْدُ عَيْنٰكَ عَنْهُمْۚ تُرِيْدُ زِيْنَةَ الْحَيٰوةِ الدُّنْيَاۚ وَلَا تُطِعْ مَنْ اَغْفَلْنَا قَلْبَهٗ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰىهُ وَكَانَ اَمْرُهٗ فُرُطًا','Dan bersabarlah kamu bersama-sama dengan orang-orang yang menyeru Tuhannya di pagi dan senja hari dengan mengharap keridhaan-Nya; dan janganlah kedua matamu berpaling dari mereka (karena) mengharapkan perhiasan dunia ini; dan janganlah kamu mengikuti orang yang hatinya telah Kami lalaikan dari mengingati Kami, serta menuruti hawa nafsunya dan adalah keadaannya itu melewati batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,29,'وَقُلِ الْحَقُّ مِنْ رَّبِّكُمْۗ فَمَنْ شَاۤءَ فَلْيُؤْمِنْ وَّمَنْ شَاۤءَ فَلْيَكْفُرْۚ اِنَّآ اَعْتَدْنَا لِلظّٰلِمِيْنَ نَارًاۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۗ وَاِنْ يَّسْتَغِيْثُوْا يُغَاثُوْا بِمَاۤءٍ كَالْمُهْلِ يَشْوِى الْوُجُوْهَۗ بِئْسَ الشَّرَابُۗ وَسَاۤءَتْ مُرْتَفَقًا','Dan katakanlah: "Kebenaran itu datangnya dari Tuhanmu; maka barangsiapa yang ingin (beriman) hendaklah ia beriman, dan barangsiapa yang ingin (kafir) biarlah ia kafir". Sesungguhnya Kami telah sediakan bagi orang orang zalim itu neraka, yang gejolaknya mengepung mereka. Dan jika mereka meminta minum, niscaya mereka akan diberi minum dengan air seperti besi yang mendidih yang menghanguskan muka. Itulah minuman yang paling buruk dan tempat istirahat yang paling jelek.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,30,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اِنَّا لَا نُضِيْعُ اَجْرَ مَنْ اَحْسَنَ عَمَلًاۚ  ','Sesunggunya mereka yang beriman dan beramal saleh, tentulah Kami tidak akan menyia-nyiakan pahala orang-orang yang mengerjakan amalan(nya) dengan yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,31,'اُولٰۤىِٕكَ لَهُمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّيَلْبَسُوْنَ ثِيَابًا خُضْرًا مِّنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَّكِىِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۗ نِعْمَ الثَّوَابُۗ وَحَسُنَتْ مُرْتَفَقًا','Mereka itulah (orang-orang yang) bagi mereka surga ´Adn, mengalir sungai-sungai di bawahnya; dalam surga itu mereka dihiasi dengan gelang mas dan mereka memakai pakaian hijau dari sutera halus dan sutera tebal, sedang mereka duduk sambil bersandar di atas dipan-dipan yang indah. Itulah pahala yang sebaik-baiknya, dan tempat istirahat yang indah;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,32,'۞ وَاضْرِبْ لَهُمْ مَّثَلًا رَّجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَّحَفَفْنٰهُمَا بِنَخْلٍ وَّجَعَلْنَا بَيْنَهُمَا زَرْعًاۗ ','Dan berikanlah kepada mereka sebuah perumpamaan dua orang laki-laki, Kami jadikan bagi seorang di antara keduanya (yang kafir) dua buah kebun anggur dan kami kelilingi kedua kebun itu dengan pohon-pohon korma dan di antara kedua kebun itu Kami buatkan ladang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,33,'كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِّنْهُ شَيْـًٔاۙ وَّفَجَّرْنَا خِلٰلَهُمَا نَهَرًاۙ  ','Kedua buah kebun itu menghasilkan buahnya, dan kebun itu tiada kurang buahnya sedikitpun, dan Kami alirkan sungai di celah-celah kedua kebun itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,34,'وَّكَانَ لَهٗ ثَمَرٌۚ فَقَالَ لِصَاحِبِهٖ وَهُوَ يُحَاوِرُهٗٓ اَنَا۠ اَكْثَرُ مِنْكَ مَالًا وَّاَعَزُّ نَفَرًا','dan dia mempunyai kekayaan besar, maka ia berkata kepada kawannya (yang mukmin) ketika bercakap-cakap dengan dia: "Hartaku lebih banyak dari pada hartamu dan pengikut-pengikutku lebih kuat"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,35,'وَدَخَلَ جَنَّتَهٗ وَهُوَ ظَالِمٌ لِّنَفْسِهٖۚ قَالَ مَآ اَظُنُّ اَنْ تَبِيْدَ هٰذِهٖٓ اَبَدًاۙ  ','Dan dia memasuki kebunnya sedang dia zalim terhadap dirinya sendiri; ia berkata: "Aku kira kebun ini tidak akan binasa selama-lamanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,36,'وَّمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةً وَّلَىِٕنْ رُّدِدْتُّ اِلٰى رَبِّيْ لَاَجِدَنَّ خَيْرًا مِّنْهَا مُنْقَلَبًا ','dan aku tidak mengira hari kiamat itu akan datang, dan jika sekiranya aku kembalikan kepada Tuhanku, pasti aku akan mendapat tempat kembali yang lebih baik dari pada kebun-kebun itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,37,'قَالَ لَهٗ صَاحِبُهٗ وَهُوَ يُحَاوِرُهٗٓ اَكَفَرْتَ بِالَّذِيْ خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ سَوّٰىكَ رَجُلًاۗ  ','Kawannya (yang mukmin) berkata kepadanya -- sedang dia bercakap-cakap dengannya: "Apakah kamu kafir kepada (Tuhan) yang menciptakan kamu dari tanah, kemudian dari setetes air mani, lalu Dia menjadikan kamu seorang laki-laki yang sempurna?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,38,'لٰكِنَّا۠ هُوَ اللّٰهُ رَبِّيْ وَلَآ اُشْرِكُ بِرَبِّيْٓ اَحَدًا ','Tetapi aku (percaya bahwa): Dialah Allah, Tuhanku, dan aku tidak mempersekutukan seorangpun dengan Tuhanku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,39,'وَلَوْلَآ اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاۤءَ اللّٰهُ ۙ لَا قُوَّةَ اِلَّا بِاللّٰهِ ۚاِنْ تَرَنِ اَنَا۠ اَقَلَّ مِنْكَ مَالًا وَّوَلَدًاۚ ','Dan mengapa kamu tidak mengatakan waktu kamu memasuki kebunmu "maasyaallaah, laa quwwata illaa billaah (sungguh atas kehendak Allah semua ini terwujud, tiada kekuatan kecuali dengan pertolongan Allah). Sekiranya kamu anggap aku lebih sedikit darimu dalam hal harta dan keturunan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,40,'فَعَسٰى رَبِّيْٓ اَنْ يُّؤْتِيَنِ خَيْرًا مِّنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِّنَ السَّمَاۤءِ فَتُصْبِحَ صَعِيْدًا زَلَقًاۙ ','maka mudah-mudahan Tuhanku, akan memberi kepadaku (kebun) yang lebih baik dari pada kebunmu (ini); dan mudah-mudahan Dia mengirimkan ketentuan (petir) dari langit kepada kebunmu; hingga (kebun itu) menjadi tanah yang licin;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,41,'اَوْ يُصْبِحَ مَاۤؤُهَا غَوْرًا فَلَنْ تَسْتَطِيْعَ لَهٗ طَلَبًا ','atau airnya menjadi surut ke dalam tanah, maka sekali-kali kamu tidak dapat menemukannya lagi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,42,'وَاُحِيْطَ بِثَمَرِهٖ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَآ اَنْفَقَ فِيْهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَا وَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُشْرِكْ بِرَبِّيْٓ اَحَدًا','Dan harta kekayaannya dibinasakan; lalu ia membulak-balikkan kedua tangannya (tanda menyesal) terhadap apa yang ia telah belanjakan untuk itu, sedang pohon anggur itu roboh bersama para-paranya dan dia berkata: "Aduhai kiranya dulu aku tidak mempersekutukan seorangpun dengan Tuhanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,43,'وَلَمْ تَكُنْ لَّهٗ فِئَةٌ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ وَمَا كَانَ مُنْتَصِرًاۗ  ','Dan tidak ada bagi dia segolonganpun yang akan menolongnya selain Allah; dan sekali-kali ia tidak dapat membela dirinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,44,'هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۗ هُوَ خَيْرٌ ثَوَابًا وَّخَيْرٌ عُقْبًا ࣖ','Di sana pertolongan itu hanya dari Allah Yang Hak. Dia adalah sebaik-baik Pemberi pahala dan sebaik-baik Pemberi balasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,45,'وَاضْرِبْ لَهُمْ مَّثَلَ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَشِيْمًا تَذْرُوْهُ الرِّيٰحُ ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقْتَدِرًا','Dan berilah perumpamaan kepada mereka (manusia), kehidupan dunia sebagai air hujan yang Kami turunkan dari langit, maka menjadi subur karenanya tumbuh-tumbuhan di muka bumi, kemudian tumbuh-tumbuhan itu menjadi kering yang diterbangkan oleh angin. Dan adalah Allah, Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,46,'اَلْمَالُ وَالْبَنُوْنَ زِيْنَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ اَمَلًا','Harta dan anak-anak adalah perhiasan kehidupan dunia tetapi amalan-amalan yang kekal lagi saleh adalah lebih baik pahalanya di sisi Tuhanmu serta lebih baik untuk menjadi harapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,47,'وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَّحَشَرْنٰهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَدًاۚ ','Dan (ingatlah) akan hari (yang ketika itu) Kami perjalankan gunung-gunung dan kamu akan dapat melihat bumi itu datar dan Kami kumpulkan seluruh manusia, dan tidak kami tinggalkan seorangpun dari mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,48,'وَعُرِضُوْا عَلٰى رَبِّكَ صَفًّاۗ  لَقَدْ جِئْتُمُوْنَا كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍۢ  ۖبَلْ زَعَمْتُمْ اَلَّنْ نَّجْعَلَ لَكُمْ مَّوْعِدًا','Dan mereka akan dibawa ke hadapan Tuhanmu dengan berbaris. Sesungguhnya kamu datang kepada Kami, sebagaimana Kami menciptakan kamu pada kali yang pertama; bahkan kamu mengatakan bahwa Kami sekali-kali tidak akan menetapkan bagi kamu waktu (memenuhi) perjanjian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,49,'وَوُضِعَ الْكِتٰبُ فَتَرَى الْمُجْرِمِيْنَ مُشْفِقِيْنَ مِمَّا فِيْهِ وَيَقُوْلُوْنَ يٰوَيْلَتَنَا مَالِ هٰذَا الْكِتٰبِ لَا يُغَادِرُ صَغِيْرَةً وَّلَا كَبِيْرَةً اِلَّآ اَحْصٰىهَاۚ وَوَجَدُوْا مَا عَمِلُوْا حَاضِرًاۗ وَلَا يَظْلِمُ رَبُّكَ اَحَدًا ࣖ ','Dan diletakkanlah kitab, lalu kamu akan melihat orang-orang bersalah ketakutan terhadap apa yang (tertulis) di dalamnya, dan mereka berkata: "Aduhai celaka kami, kitab apakah ini yang tidak meninggalkan yang kecil dan tidak (pula) yang besar, melainkan ia mencatat semuanya; dan mereka dapati apa yang telah mereka kerjakan ada (tertulis). Dan Tuhanmu tidak menganiaya seorang juapun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,50,'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّهٖۗ اَفَتَتَّخِذُوْنَهٗ وَذُرِّيَّتَهٗٓ اَوْلِيَاۤءَ مِنْ دُوْنِيْ وَهُمْ لَكُمْ عَدُوٌّۗ بِئْسَ لِلظّٰلِمِيْنَ بَدَلًا ','Dan (ingatlah) ketika Kami berfirman kepada para malaikat: "Sujudlah kamu kepada Adam, maka sujudlah mereka kecuali Iblis. Dia adalah dari golongan jin, maka ia mendurhakai perintah Tuhannya. Patutkah kamu mengambil dia dan turanan-turunannya sebagai pemimpin selain daripada-Ku, sedang mereka adalah musuhmu? Amat buruklah iblis itu sebagai pengganti (dari Allah) bagi orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,51,'۞ مَآ اَشْهَدْتُّهُمْ خَلْقَ السَّمٰوٰتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّيْنَ عَضُدًا','Aku tidak menghadirkan mereka (iblis dan anak cucunya) untuk menyaksikan penciptaan langit dan bumi dan tidak (pula) penciptaan diri mereka sendiri; dan tidaklah Aku mengambil orang-orang yang menyesatkan itu sebagai penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,52,'وَيَوْمَ يَقُوْلُ نَادُوْا شُرَكَاۤءِيَ الَّذِيْنَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَّوْبِقًا','Dan (ingatlah) akan hari (yang ketika itu) Dia berfirman: "Serulah olehmu sekalian sekutu-sekutu-Ku yang kamu katakan itu". Mereka lalu memanggilnya tetapi sekutu-sekutu itu tidak membalas seruan mereka dan Kami adakan untuk mereka tempat kebinasaan (neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,53,'وَرَاَ الْمُجْرِمُوْنَ النَّارَ فَظَنُّوْٓا اَنَّهُمْ مُّوَاقِعُوْهَا وَلَمْ يَجِدُوْا عَنْهَا مَصْرِفًا ࣖ ','Dan orang-orang yang berdosa melihat neraka, maka mereka meyakini, bahwa mereka akan jatuh ke dalamnya dan mereka tidak menemukan tempat berpaling dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,54,'وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۗ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلًا ','Dan sesungguhnya Kami telah mengulang-ulangi bagi manusia dalam Al Quran ini bermacam-macam perumpamaan. Dan manusia adalah makhluk yang paling banyak membantah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,55,'وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوْا رَبَّهُمْ اِلَّآ اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّلِيْنَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا','Dam tidak ada sesuatupun yang menghalangi manusia dari beriman, ketika petunjuk telah datang kepada mereka, dan dari memohon ampun kepada Tuhannya, kecuali (keinginan menanti) datangnya hukum (Allah yang telah berlalu pada) umat-umat yang dahulu atau datangnya azab atas mereka dengan nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,56,'وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ وَيُجَادِلُ الَّذِيْنَ كَفَرُوْا بِالْبَاطِلِ لِيُدْحِضُوْا بِهِ الْحَقَّ وَاتَّخَذُوْٓا اٰيٰتِيْ وَمَآ اُنْذِرُوْا هُزُوًا','Dan tidaklah Kami mengutus rasul-rasul hanyalah sebagai pembawa berita gembira dan sebagai pemberi peringatan; tetapi orang-orang yang kafir membantah dengan yang batil agar dengan demikian mereka dapat melenyap kan yang hak, dan mereka menganggap ayat-ayat kami dan peringatan-peringatan terhadap mereka sebagai olok-olokan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,57,'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۗ اِنَّا جَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَّهْتَدُوْٓا اِذًا اَبَدًا ','Dan siapakah yang lebih zalim dari pada orang yang telah diperingatkan dengan ayat-ayat Tuhannya lalu dia berpaling dari padanya dan melupakan apa yang telah dikerjakan oleh kedua tangannya? Sesungguhnya Kami telah meletakkan tutupan di atas hati mereka, (sehingga mereka tidak) memahaminya, dan (Kami letakkan pula) sumbatan di telinga mereka; dan kendatipun kamu menyeru mereka kepada petunjuk, niscaya mereka tidak akan mendapat petunjuk selama-lamanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,58,'وَرَبُّكَ الْغَفُوْرُ ذُو الرَّحْمَةِۗ  لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوْا لَعَجَّلَ لَهُمُ الْعَذَابَۗ بَلْ لَّهُمْ مَّوْعِدٌ لَّنْ يَّجِدُوْا مِنْ دُوْنِهٖ مَوْىِٕلًا','Dan Tuhanmulah yang Maha Pengampun, lagi mempunyai rahmat. Jika Dia mengazab mereka karena perbuatan mereka, tentu Dia akan menyegerakan azab bagi mereka. Tetapi bagi mereka ada waktu yang tertentu (untuk mendapat azab) yang mereka sekali-kali tidak akan menemukan tempat berlindung dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,59,'وَتِلْكَ الْقُرٰٓى اَهْلَكْنٰهُمْ لَمَّا ظَلَمُوْا وَجَعَلْنَا لِمَهْلِكِهِمْ مَّوْعِدًا ࣖ','Dan (penduduk) negeri telah Kami binasakan ketika mereka berbuat zalim, dan telah Kami tetapkan waktu tertentu bagi kebinasaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,60,'وَاِذْ قَالَ مُوْسٰى لِفَتٰىهُ لَآ اَبْرَحُ حَتّٰٓى  اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُبًا','Dan (ingatlah) ketika Musa berkata kepada muridnya: "Aku tidak akan berhenti (berjalan) sebelum sampai ke pertemuan dua buah lautan; atau aku akan berjalan sampai bertahun-tahun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,61,'فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوْتَهُمَا فَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ سَرَبًا ','Maka tatkala mereka sampai ke pertemuan dua buah laut itu, mereka lalai akan ikannya, lalu ikan itu melompat mengambil jalannya ke laut itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,62,'فَلَمَّا جَاوَزَا قَالَ لِفَتٰىهُ اٰتِنَا غَدَاۤءَنَاۖ  لَقَدْ لَقِيْنَا مِنْ سَفَرِنَا هٰذَا نَصَبًا ','Maka tatkala mereka berjalan lebih jauh, berkatalah Musa kepada muridnya: "Bawalah kemari makanan kita; sesungguhnya kita telah merasa letih karena perjalanan kita ini".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,63,'قَالَ اَرَاَيْتَ اِذْ اَوَيْنَآ اِلَى الصَّخْرَةِ فَاِنِّيْ نَسِيْتُ الْحُوْتَۖ وَمَآ اَنْسٰىنِيْهُ اِلَّا الشَّيْطٰنُ اَنْ اَذْكُرَهٗۚ وَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ عَجَبًا ','Muridnya menjawab: "Tahukah kamu tatkala kita mencari tempat berlindung di batu tadi, maka sesungguhnya aku lupa (menceritakan tentang) ikan itu dan tidak adalah yang melupakan aku untuk menceritakannya kecuali syaitan dan ikan itu mengambil jalannya ke laut dengan cara yang aneh sekali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,64,'قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۖ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصًاۙ  ','Musa berkata: "Itulah (tempat) yang kita cari". Lalu keduanya kembali, mengikuti jejak mereka semula.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,65,'فَوَجَدَا عَبْدًا مِّنْ عِبَادِنَآ اٰتَيْنٰهُ رَحْمَةً مِّنْ عِنْدِنَا وَعَلَّمْنٰهُ مِنْ لَّدُنَّا عِلْمًا','Lalu mereka bertemu dengan seorang hamba di antara hamba-hamba Kami, yang telah Kami berikan kepadanya rahmat dari sisi Kami, dan yang telah Kami ajarkan kepadanya ilmu dari sisi Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,66,'قَالَ لَهٗ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا','Musa berkata kepada Khidhr: "Bolehkah aku mengikutimu supaya kamu mengajarkan kepadaku ilmu yang benar di antara ilmu-ilmu yang telah diajarkan kepadamu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,67,'قَالَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ','Dia menjawab: "Sesungguhnya kamu sekali-kali tidak akan sanggup sabar bersama aku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,68,'وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِهٖ خُبْرًا','Dan bagaimana kamu dapat sabar atas sesuatu, yang kamu belum mempunyai pengetahuan yang cukup tentang hal itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,69,'قَالَ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ صَابِرًا وَّلَآ اَعْصِيْ لَكَ اَمْرًا','Musa berkata: "Insya Allah kamu akan mendapati aku sebagai orang yang sabar, dan aku tidak akan menentangmu dalam sesuatu urusanpun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,70,'قَالَ فَاِنِ اتَّبَعْتَنِيْ فَلَا تَسْـَٔلْنِيْ عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْرًا ࣖ ','Dia berkata: "Jika kamu mengikutiku, maka janganlah kamu menanyakan kepadaku tentang sesuatu apapun, sampai aku sendiri menerangkannya kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,71,'فَانْطَلَقَاۗ حَتّٰٓى اِذَا رَكِبَا فِى السَّفِيْنَةِ خَرَقَهَاۗ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ  لَقَدْ جِئْتَ شَيْـًٔا اِمْرًا ','Maka berjalanlah keduanya, hingga tatkala keduanya menaiki perahu lalu Khidhr melobanginya. Musa berkata: "Mengapa kamu melobangi perahu itu akibatnya kamu menenggelamkan penumpangnya?" Sesungguhnya kamu telah berbuat sesuatu kesalahan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,72,'قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ','Dia (Khidhr) berkata: "Bukankah aku telah berkata: "Sesungguhnya kamu sekali-kali tidak akan sabar bersama dengan aku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,73,'قَالَ لَا تُؤَاخِذْنِيْ بِمَا نَسِيْتُ وَلَا تُرْهِقْنِيْ مِنْ اَمْرِيْ عُسْرًا','Musa berkata: "Janganlah kamu menghukum aku karena kelupaanku dan janganlah kamu membebani aku dengan sesuatu kesulitan dalam urusanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,15,18,74,'فَانْطَلَقَا ۗحَتّٰٓى اِذَا لَقِيَا غُلٰمًا فَقَتَلَهٗ ۙقَالَ اَقَتَلْتَ نَفْسًا زَكِيَّةً؈ۢبِغَيْرِ نَفْسٍۗ  لَقَدْ جِئْتَ شَيْـًٔا نُكْرًا   ۔','Maka berjalanlah keduanya; hingga tatkala keduanya berjumpa dengan seorang anak, maka Khidhr membunuhnya. Musa berkata: "Mengapa kamu membunuh jiwa yang bersih, bukan karena dia membunuh orang lain? Sesungguhnya kamu telah melakukan suatu yang mungkar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,75,'۞ قَالَ اَلَمْ اَقُلْ لَّكَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ','Khidhr berkata: "Bukankah sudah kukatakan kepadamu, bahwa sesungguhnya kamu tidak akan dapat sabar bersamaku?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,76,'قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍۢ بَعْدَهَا فَلَا تُصٰحِبْنِيْۚ قَدْ بَلَغْتَ مِنْ لَّدُنِّيْ عُذْرًا ','Musa berkata: "Jika aku bertanya kepadamu tentang sesuatu sesudah (kali) ini, maka janganlah kamu memperbolehkan aku menyertaimu, sesungguhnya kamu sudah cukup memberikan uzur padaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,77,'فَانْطَلَقَا ۗحَتّٰىٓ اِذَآ اَتَيَآ اَهْلَ قَرْيَةِ ِۨاسْتَطْعَمَآ اَهْلَهَا فَاَبَوْا اَنْ يُّضَيِّفُوْهُمَا فَوَجَدَا فِيْهَا جِدَارًا يُّرِيْدُ اَنْ يَّنْقَضَّ فَاَقَامَهٗ ۗقَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْرًا ','Maka keduanya berjalan; hingga tatkala keduanya sampai kepada penduduk suatu negeri, mereka minta dijamu kepada penduduk negeri itu, tetapi penduduk negeri itu tidak mau menjamu mereka, kemudian keduanya mendapatkan dalam negeri itu dinding rumah yang hampir roboh, maka Khidhr menegakkan dinding itu. Musa berkata: "Jikalau kamu mau, niscaya kamu mengambil upah untuk itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,78,'قَالَ هٰذَا فِرَاقُ بَيْنِيْ وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْوِيْلِ مَا لَمْ تَسْتَطِعْ عَّلَيْهِ صَبْرًا ','Khidhr berkata: "Inilah perpisahan antara aku dengan kamu; kelak akan kuberitahukan kepadamu tujuan perbuatan-perbuatan yang kamu tidak dapat sabar terhadapnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,79,'اَمَّا السَّفِيْنَةُ فَكَانَتْ لِمَسٰكِيْنَ يَعْمَلُوْنَ فِى الْبَحْرِ فَاَرَدْتُّ اَنْ اَعِيْبَهَاۗ وَكَانَ وَرَاۤءَهُمْ مَّلِكٌ يَّأْخُذُ كُلَّ سَفِيْنَةٍ غَصْبًا ','Adapun bahtera itu adalah kepunyaan orang-orang miskin yang bekerja di laut, dan aku bertujuan merusakkan bahtera itu, karena di hadapan mereka ada seorang raja yang merampas tiap-tiap bahtera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,80,'وَاَمَّا الْغُلٰمُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَشِيْنَآ اَنْ يُّرْهِقَهُمَا طُغْيَانًا وَّكُفْرًا ۚ','Dan adapun anak muda itu, maka keduanya adalah orang-orang mukmin, dan kami khawatir bahwa dia akan mendorong kedua orang tuanya itu kepada kesesatan dan kekafiran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,81,'فَاَرَدْنَآ اَنْ يُّبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِّنْهُ زَكٰوةً وَّاَقْرَبَ رُحْمًا ','Dan kami menghendaki, supaya Tuhan mereka mengganti bagi mereka dengan anak lain yang lebih baik kesuciannya dari anaknya itu dan lebih dalam kasih sayangnya (kepada ibu bapaknya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,82,'وَاَمَّا الْجِدَارُ فَكَانَ لِغُلٰمَيْنِ يَتِيْمَيْنِ فِى الْمَدِيْنَةِ وَكَانَ تَحْتَهٗ كَنْزٌ لَّهُمَا وَكَانَ اَبُوْهُمَا صَالِحًا ۚفَاَرَادَ رَبُّكَ اَنْ يَّبْلُغَآ اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِّنْ رَّبِّكَۚ وَمَا فَعَلْتُهٗ عَنْ اَمْرِيْۗ ذٰلِكَ تَأْوِيْلُ مَا لَمْ تَسْطِعْ عَّلَيْهِ صَبْرًاۗ  ࣖ','Adapun dinding rumah adalah kepunyaan dua orang anak yatim di kota itu, dan di bawahnya ada harta benda simpanan bagi mereka berdua, sedang ayahnya adalah seorang yang saleh, maka Tuhanmu menghendaki agar supaya mereka sampai kepada kedewasaannya dan mengeluarkan simpanannya itu, sebagai rahmat dari Tuhanmu; dan bukanlah aku melakukannya itu menurut kemauanku sendiri. Demikian itu adalah tujuan perbuatan-perbuatan yang kamu tidak dapat sabar terhadapnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,83,'وَيَسْـَٔلُوْنَكَ عَنْ ذِى الْقَرْنَيْنِۗ قُلْ سَاَتْلُوْا عَلَيْكُمْ مِّنْهُ ذِكْرًا ۗ','Mereka akan bertanya kepadamu (Muhammad) tentang Dzulkarnain. Katakanlah: "Aku akan bacakan kepadamu cerita tantangnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,84,'اِنَّا مَكَّنَّا لَهٗ فِى الْاَرْضِ وَاٰتَيْنٰهُ مِنْ كُلِّ شَيْءٍ سَبَبًا ۙ','Sesungguhnya Kami telah memberi kekuasaan kepadanya di (muka) bumi, dan Kami telah memberikan kepadanya jalan (untuk mencapai) segala sesuatu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,85,'فَاَتْبَعَ سَبَبًا','maka diapun menempuh suatu jalan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,86,'حَتّٰىٓ اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِيْ عَيْنٍ حَمِئَةٍ وَّوَجَدَ عِنْدَهَا قَوْمًا ەۗ قُلْنَا يٰذَا الْقَرْنَيْنِ اِمَّآ اَنْ تُعَذِّبَ وَاِمَّآ اَنْ تَتَّخِذَ فِيْهِمْ حُسْنًا','Hingga apabila dia telah sampai ketempat terbenam matahari, dia melihat matahari terbenam di dalam laut yang berlumpur hitam, dan dia mendapati di situ segolongan umat. Kami berkata: "Hai Dzulkarnain, kamu boleh menyiksa atau boleh berbuat kebaikan terhadap mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,87,'قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهٗ ثُمَّ يُرَدُّ اِلٰى رَبِّهٖ فَيُعَذِّبُهٗ عَذَابًا نُّكْرًا ','Berkata Dzulkarnain: "Adapun orang yang aniaya, maka kami kelak akan mengazabnya, kemudian dia kembalikan kepada Tuhannya, lalu Tuhan mengazabnya dengan azab yang tidak ada taranya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,88,'وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًا فَلَهٗ جَزَاۤءً ۨالْحُسْنٰىۚ وَسَنَقُوْلُ لَهٗ مِنْ اَمْرِنَا يُسْرًا ۗ','Adapun orang-orang yang beriman dan beramal saleh, maka baginya pahala yang terbaik sebagai balasan, dan akan kami titahkan kepadanya (perintah) yang mudah dari perintah-perintah kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,89,'ثُمَّ اَتْبَعَ سَبَبًا ','Kemudian dia menempuh jalan (yang lain).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,90,'حَتّٰىٓ اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَّمْ نَجْعَلْ لَّهُمْ مِّنْ دُوْنِهَا سِتْرًا ۙ','Hingga apabila dia telah sampai ke tempat terbit matahari (sebelah Timur) dia mendapati matahari itu menyinari segolongan umat yang Kami tidak menjadikan bagi mereka sesuatu yang melindunginya dari (cahaya) matahari itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,91,'كَذٰلِكَۗ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْرًا ','demikianlah. dan sesungguhnya ilmu Kami meliputi segala apa yang ada padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,92,'ثُمَّ اَتْبَعَ سَبَبًا ','Kemudian dia menempuh suatu jalan (yang lain lagi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,93,'حَتّٰىٓ اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُوْنِهِمَا قَوْمًاۙ  لَّا يَكَادُوْنَ يَفْقَهُوْنَ قَوْلًا ','Hingga apabila dia telah sampai di antara dua buah gunung, dia mendapati di hadapan kedua bukit itu suatu kaum yang hampir tidak mengerti pembicaraan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,94,'قَالُوْا يٰذَا الْقَرْنَيْنِ اِنَّ يَأْجُوْجَ وَمَأْجُوْجَ مُفْسِدُوْنَ فِى الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا ','Mereka berkata: "Hai Dzulkarnain, sesungguhnya Ya´juj dan Ma´juj itu orang-orang yang membuat kerusakan di muka bumi, maka dapatkah kami memberikan sesuatu pembayaran kepadamu, supaya kamu membuat dinding antara kami dan mereka?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,95,'قَالَ مَا مَكَّنِّيْ فِيْهِ رَبِّيْ خَيْرٌ فَاَعِيْنُوْنِيْ بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا ۙ','Dzulkarnain berkata: "Apa yang telah dikuasakan oleh Tuhanku kepadaku terhadapnya adalah lebih baik, maka tolonglah aku dengan kekuatan (manusia dan alat-alat), agar aku membuatkan dinding antara kamu dan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,96,'اٰتُوْنِيْ زُبَرَ الْحَدِيْدِۗ حَتّٰىٓ اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوْا ۗحَتّٰىٓ اِذَا جَعَلَهٗ نَارًاۙ قَالَ اٰتُوْنِيْٓ اُفْرِغْ عَلَيْهِ قِطْرًا ۗ','berilah aku potongan-potongan besi". Hingga apabila besi itu telah sama rata dengan kedua (puncak) gunung itu, berkatalah Dzulkarnain: "Tiuplah (api itu)". Hingga apabila besi itu sudah menjadi (merah seperti) api, diapun berkata: "Berilah aku tembaga (yang mendidih) agar aku kutuangkan ke atas besi panas itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,97,'فَمَا اسْطَاعُوْٓا اَنْ يَّظْهَرُوْهُ وَمَا اسْتَطَاعُوْا لَهٗ نَقْبًا ','Maka mereka tidak bisa mendakinya dan mereka tidak bisa (pula) melobanginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,98,'قَالَ هٰذَا رَحْمَةٌ مِّنْ رَّبِّيْۚ فَاِذَا جَاۤءَ وَعْدُ رَبِّيْ جَعَلَهٗ دَكَّاۤءَۚ وَكَانَ وَعْدُ رَبِّيْ حَقًّا ۗ','Dzulkarnain berkata: "Ini (dinding) adalah rahmat dari Tuhanku, maka apabila sudah datang janji Tuhanku, Dia akan menjadikannya hancur luluh; dan janji Tuhanku itu adalah benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,99,'۞ وَتَرَكْنَا بَعْضَهُمْ يَوْمَىِٕذٍ يَّمُوْجُ فِيْ بَعْضٍ وَّنُفِخَ فِى الصُّوْرِ فَجَمَعْنٰهُمْ جَمْعًا ۙ','Kami biarkan mereka di hari itu bercampur aduk antara satu dengan yang lain, kemudian ditiup lagi sangkakala, lalu Kami kumpulkan mereka itu semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,100,'وَّعَرَضْنَا جَهَنَّمَ يَوْمَىِٕذٍ لِّلْكٰفِرِيْنَ عَرْضًا ۙ','dan Kami nampakkan Jahannam pada hari itu kepada orang-orang kafir dengan jelas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,101,' ۨالَّذِيْنَ كَانَتْ اَعْيُنُهُمْ فِيْ غِطَاۤءٍ عَنْ ذِكْرِيْ وَكَانُوْا لَا يَسْتَطِيْعُوْنَ سَمْعًا ࣖ','yaitu orang-orang yang matanya dalam keadaan tertutup dari memperhatikan tanda-tanda kebesaran-Ku, dan adalah mereka tidak sanggup mendengar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,102,'اَفَحَسِبَ الَّذِيْنَ كَفَرُوْٓا اَنْ يَّتَّخِذُوْا عِبَادِيْ مِنْ دُوْنِيْٓ اَوْلِيَاۤءَ ۗاِنَّآ اَعْتَدْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ نُزُلًا ','maka apakah orang-orang kafir menyangka bahwa mereka (dapat) mengambil hamba-hamba-Ku menjadi penolong selain Aku? Sesungguhnya Kami telah menyediakan neraka Jahannam tempat tinggal bagi orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,103,'قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَرِيْنَ اَعْمَالًا ۗ','Katakanlah: "Apakah akan Kami beritahukan kepadamu tentang orang-orang yang paling merugi perbuatannya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,104,'اَلَّذِيْنَ ضَلَّ سَعْيُهُمْ فِى الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُوْنَ اَنَّهُمْ يُحْسِنُوْنَ صُنْعًا ','Yaitu orang-orang yang telah sia-sia perbuatannya dalam kehidupan dunia ini, sedangkan mereka menyangka bahwa mereka berbuat sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,105,'اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ وَلِقَاۤىِٕهٖ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُقِيْمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْنًا ','Mereka itu orang-orang yang telah kufur terhadap ayat-ayat Tuhan mereka dan (kufur terhadap) perjumpaan dengan Dia, maka hapuslah amalan-amalan mereka, dan Kami tidak mengadakan suatu penilaian bagi (amalan) mereka pada hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,106,'ذٰلِكَ جَزَاۤؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوْا وَاتَّخَذُوْٓا اٰيٰتِيْ وَرُسُلِيْ هُزُوًا ','Demikianlah balasan mereka itu neraka Jahannam, disebabkan kekafiran mereka dan disebabkan mereka menjadikan ayat-ayat-Ku dan rasul-rasul-Ku sebagai olok-olok.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,107,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَانَتْ لَهُمْ جَنّٰتُ الْفِرْدَوْسِ نُزُلًا ۙ','Sesungguhnya orang-orang yang beriman dan beramal saleh, bagi mereka adalah surga Firdaus menjadi tempat tinggal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,108,'خٰلِدِيْنَ فِيْهَا لَا يَبْغُوْنَ عَنْهَا حِوَلًا ','mereka kekal di dalamnya, mereka tidak ingin berpindah dari padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,109,'قُلْ لَّوْ كَانَ الْبَحْرُ مِدَادًا لِّكَلِمٰتِ رَبِّيْ لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمٰتُ رَبِّيْ وَلَوْ جِئْنَا بِمِثْلِهٖ مَدَدًا ','Katakanlah: Sekiranya lautan menjadi tinta untuk (menulis) kalimat-kalimat Tuhanku, sungguh habislah lautan itu sebelum habis (ditulis) kalimat-kalimat Tuhanku, meskipun Kami datangkan tambahan sebanyak itu (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,18,110,'قُلْ اِنَّمَآ اَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَمَنْ كَانَ يَرْجُوْا لِقَاۤءَ رَبِّهٖ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَّلَا يُشْرِكْ بِعِبَادَةِ رَبِّهٖٓ اَحَدًا ࣖ','Katakanlah: Sesungguhnya aku ini manusia biasa seperti kamu, yang diwahyukan kepadaku: "Bahwa sesungguhnya Tuhan kamu itu adalah Tuhan yang Esa". Barangsiapa mengharap perjumpaan dengan Tuhannya, maka hendaklah ia mengerjakan amal yang saleh dan janganlah ia mempersekutukan seorangpun dalam beribadat kepada Tuhannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,1,'كۤهٰيٰعۤصۤ ۚ ','Kaaf Haa Yaa ´Ain Shaad.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,2,'ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهٗ زَكَرِيَّا ۚ','(Yang dibacakan ini adalah) penjelasan tentang rahmat Tuhan kamu kepada hamba-Nya, Zakaria,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,3,'اِذْ نَادٰى رَبَّهٗ نِدَاۤءً خَفِيًّا ','yaitu tatkala ia berdoa kepada Tuhannya dengan suara yang lembut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,4,'قَالَ رَبِّ اِنِّيْ وَهَنَ الْعَظْمُ مِنِّيْ وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَّلَمْ اَكُنْۢ بِدُعَاۤىِٕكَ رَبِّ شَقِيًّا ','Ia berkata "Ya Tuhanku, sesungguhnya tulangku telah lemah dan kepalaku telah ditumbuhi uban, dan aku belum pernah kecewa dalam berdoa kepada Engkau, ya Tuhanku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,5,'وَاِنِّيْ خِفْتُ الْمَوَالِيَ مِنْ وَّرَاۤءِيْ وَكَانَتِ امْرَاَتِيْ عَاقِرًا فَهَبْ لِيْ مِنْ لَّدُنْكَ وَلِيًّا ۙ','Dan sesungguhnya aku khawatir terhadap mawaliku sepeninggalku, sedang isteriku adalah seorang yang mandul, maka anugerahilah aku dari sisi Engkau seorang putera,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,6,'يَّرِثُنِيْ وَيَرِثُ مِنْ اٰلِ يَعْقُوْبَ وَاجْعَلْهُ رَبِّ رَضِيًّا ','yang akan mewarisi aku dan mewarisi sebahagian keluarga Ya´qub; dan jadikanlah ia, ya Tuhanku, seorang yang diridhai".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,7,'يٰزَكَرِيَّآ اِنَّا نُبَشِّرُكَ بِغُلٰمِ ِۨاسْمُهٗ يَحْيٰىۙ لَمْ نَجْعَلْ لَّهٗ مِنْ قَبْلُ سَمِيًّا ','Hai Zakaria, sesungguhnya Kami memberi kabar gembira kepadamu akan (beroleh) seorang anak yang namanya Yahya, yang sebelumnya Kami belum pernah menciptakan orang yang serupa dengan dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,8,'قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّكَانَتِ امْرَاَتِيْ عَاقِرًا وَّقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا ','Zakaria berkata: "Ya Tuhanku, bagaimana akan ada anak bagiku, padahal isteriku adalah seorang yang mandul dan aku (sendiri) sesungguhnya sudah mencapai umur yang sangat tua".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,9,'قَالَ كَذٰلِكَۗ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَّقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـًٔا ','Tuhan berfirman: "Demikianlah". Tuhan berfirman: "Hal itu adalah mudah bagi-Ku; dan sesunguhnya telah Aku ciptakan kamu sebelum itu, padahal kamu (di waktu itu) belum ada sama sekali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,10,'قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗقَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِيًّا ','Zakaria berkata: "Ya Tuhanku, berilah aku suatu tanda". Tuhan berfirman: "Tanda bagimu ialah bahwa kamu tidak dapat bercakap-cakap dengan manusia selama tiga malam, padahal kamu sehat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,11,'فَخَرَجَ عَلٰى قَوْمِهٖ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوْا بُكْرَةً وَّعَشِيًّا ','Maka ia keluar dari mihrab menuju kaumnya, lalu ia memberi isyarat kepada mereka; hendaklah kamu bertasbih di waktu pagi dan petang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,12,'يٰيَحْيٰى خُذِ الْكِتٰبَ بِقُوَّةٍ ۗوَاٰتَيْنٰهُ الْحُكْمَ صَبِيًّاۙ ','Hai Yahya, ambillah Al Kitab (Taurat) itu dengan sungguh-sungguh. Dan kami berikan kepadanya hikmah selagi ia masih kanak-kanak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,13,'وَّحَنَانًا مِّنْ لَّدُنَّا وَزَكٰوةً ۗوَكَانَ تَقِيًّا ۙ','dan rasa belas kasihan yang mendalam dari sisi Kami dan kesucian (dan dosa). Dan ia adalah seorang yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,14,'وَّبَرًّاۢ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا ','dan seorang yang berbakti kepada kedua orang tuanya, dan bukanlah ia orang yang sombong lagi durhaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,15,'وَسَلٰمٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوْتُ وَيَوْمَ يُبْعَثُ حَيًّا ࣖ','Kesejahteraan atas dirinya pada hari ia dilahirkan dan pada hari ia meninggal dan pada hari ia dibangkitkan hidup kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,16,'وَاذْكُرْ فِى الْكِتٰبِ مَرْيَمَۘ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَانًا شَرْقِيًّا ۙ','Dan ceritakanlah (kisah) Maryam di dalam Al Quran, yaitu ketika ia menjauhkan diri dari keluarganya ke suatu tempat di sebelah timur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,17,'فَاتَّخَذَتْ مِنْ دُوْنِهِمْ حِجَابًاۗ فَاَرْسَلْنَآ اِلَيْهَا رُوْحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا ','maka ia mengadakan tabir (yang melindunginya) dari mereka; lalu Kami mengutus roh Kami kepadanya, maka ia menjelma di hadapannya (dalam bentuk) manusia yang sempurna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,18,'قَالَتْ اِنِّيْٓ اَعُوْذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِيًّا ','Maryam berkata: "Sesungguhnya aku berlindung dari padamu kepada Tuhan Yang Maha pemurah, jika kamu seorang yang bertakwa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,19,'قَالَ اِنَّمَآ اَنَا۠ رَسُوْلُ رَبِّكِۖ لِاَهَبَ لَكِ غُلٰمًا زَكِيًّا ','Ia (jibril) berkata: "Sesungguhnya aku ini hanyalah seorang utusan Tuhanmu, untuk memberimu seorang anak laki-laki yang suci".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,20,'قَالَتْ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ وَّلَمْ اَكُ بَغِيًّا ','Maryam berkata: "Bagaimana akan ada bagiku seorang anak laki-laki, sedang tidak pernah seorang manusiapun menyentuhku dan aku bukan (pula) seorang pezina!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,21,'قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَهٗٓ اٰيَةً لِّلنَّاسِ وَرَحْمَةً مِّنَّاۚ وَكَانَ اَمْرًا مَّقْضِيًّا ','Jibril berkata: "Demikianlah". Tuhanmu berfirman: "Hal itu adalah mudah bagi-Ku; dan agar dapat Kami menjadikannya suatu tanda bagi manusia dan sebagai rahmat dari Kami; dan hal itu adalah suatu perkara yang sudah diputuskan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,22,'۞ فَحَمَلَتْهُ فَانْتَبَذَتْ بِهٖ مَكَانًا قَصِيًّا ','Maka Maryam mengandungnya, lalu ia menyisihkan diri dengan kandungannya itu ke tempat yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,23,'فَاَجَاۤءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يٰلَيْتَنِيْ مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْيًا مَّنْسِيًّا ','Maka rasa sakit akan melahirkan anak memaksa ia (bersandar) pada pangkal pohon kurma, dia berkata: "Aduhai, alangkah baiknya aku mati sebelum ini, dan aku menjadi barang yang tidak berarti, lagi dilupakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,24,'فَنَادٰىهَا مِنْ تَحْتِهَآ اَلَّا تَحْزَنِيْ قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا ','Maka Jibril menyerunya dari tempat yang rendah: "Janganlah kamu bersedih hati, sesungguhnya Tuhanmu telah menjadikan anak sungai di bawahmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,25,'وَهُزِّيْٓ اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسٰقِطْ عَلَيْكِ رُطَبًا جَنِيًّا ۖ','Dan goyanglah pangkal pohon kurma itu ke arahmu, niscaya pohon itu akan menggugurkan buah kurma yang masak kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,26,'فَكُلِيْ وَاشْرَبِيْ وَقَرِّيْ عَيْنًا ۚفَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَدًاۙ فَقُوْلِيْٓ اِنِّيْ نَذَرْتُ لِلرَّحْمٰنِ صَوْمًا فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِيًّا ۚ','maka makan, minum dan bersenang hatilah kamu. Jika kamu melihat seorang manusia, maka katakanlah: "Sesungguhnya aku telah bernazar berpuasa untuk Tuhan Yang Maha Pemurah, maka aku tidak akan berbicara dengan seorang manusiapun pada hari ini".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,27,'فَاَتَتْ بِهٖ قَوْمَهَا تَحْمِلُهٗ ۗقَالُوْا يٰمَرْيَمُ لَقَدْ جِئْتِ شَيْـًٔا فَرِيًّا ','Maka Maryam membawa anak itu kepada kaumnya dengan menggendongnya. Kaumnya berkata: "Hai Maryam, sesungguhnya kamu telah melakukan sesuatu yang amat mungkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,28,'يٰٓاُخْتَ هٰرُوْنَ مَا كَانَ اَبُوْكِ امْرَاَ سَوْءٍ وَّمَا كَانَتْ اُمُّكِ بَغِيًّا ۖ','Hai saudara perempuan Harun, ayahmu sekali-kali bukanlah seorang yang jahat dan ibumu sekali-kali bukanlah seorang pezina",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,29,'فَاَشَارَتْ اِلَيْهِۗ قَالُوْا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِى الْمَهْدِ صَبِيًّا ','maka Maryam menunjuk kepada anaknya. Mereka berkata: "Bagaimana kami akan berbicara dengan anak kecil yang masih di dalam ayunan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,30,'قَالَ اِنِّيْ عَبْدُ اللّٰهِ ۗاٰتٰنِيَ الْكِتٰبَ وَجَعَلَنِيْ نَبِيًّا ۙ','Berkata Isa: "Sesungguhnya aku ini hamba Allah, Dia memberiku Al Kitab (Injil) dan Dia menjadikan aku seorang nabi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,31,'وَّجَعَلَنِيْ مُبٰرَكًا اَيْنَ مَا كُنْتُۖ وَاَوْصٰنِيْ بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَيًّا ۖ','dan Dia menjadikan aku seorang yang diberkati di mana saja aku berada, dan Dia memerintahkan kepadaku (mendirikan) shalat dan (menunaikan) zakat selama aku hidup;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,32,'وَّبَرًّاۢ بِوَالِدَتِيْ وَلَمْ يَجْعَلْنِيْ جَبَّارًا شَقِيًّا ','dan berbakti kepada ibuku, dan Dia tidak menjadikan aku seorang yang sombong lagi celaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,33,'وَالسَّلٰمُ عَلَيَّ يَوْمَ وُلِدْتُّ وَيَوْمَ اَمُوْتُ وَيَوْمَ اُبْعَثُ حَيًّا ','Dan kesejahteraan semoga dilimpahkan kepadaku, pada hari aku dilahirkan, pada hari aku meninggal dan pada hari aku dibangkitkan hidup kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,34,'ذٰلِكَ عِيْسَى ابْنُ مَرْيَمَ ۚقَوْلَ الْحَقِّ الَّذِيْ فِيْهِ يَمْتَرُوْنَ ','Itulah Isa putera Maryam, yang mengatakan perkataan yang benar, yang mereka berbantah-bantahan tentang kebenarannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,35,'مَا كَانَ لِلّٰهِ اَنْ يَّتَّخِذَ مِنْ وَّلَدٍ سُبْحٰنَهٗ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ۗ','Tidak layak bagi Allah mempunyai anak, Maha Suci Dia. Apabila Dia telah menetapkan sesuatu, maka Dia hanya berkata kepadanya: "Jadilah", maka jadilah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,36,'وَاِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ','Sesungguhnya Allah adalah Tuhanku dan Tuhanmu, maka sembahIah Dia oleh kamu sekalian. Ini adalah jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,37,'فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْۚ فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ مَّشْهَدِ يَوْمٍ عَظِيْمٍ ','Maka berselisihlah golongan-golongan (yang ada) di antara mereka. Maka kecelakaanlah bagi orang-orang kafir pada waktu menyaksikan hari yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,38,'اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُوْنَنَا لٰكِنِ الظّٰلِمُوْنَ الْيَوْمَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Alangkah terangnya pendengaran mereka dan alangkah tajamnya penglihatan mereka pada hari mereka datang kepada Kami. Tetapi orang-orang yang zalim pada hari ini (di dunia) berada dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,39,'وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۘ وَهُمْ فِيْ غَفْلَةٍ وَّهُمْ لَا يُؤْمِنُوْنَ ','Dan berilah mereka peringatan tentang hari penyesalan, (yaitu) ketika segala perkara telah diputus. Dan mereka dalam kelalaian dan mereka tidak (pula) beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,40,'اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُوْنَ ࣖ','Sesungguhnya Kami mewarisi bumi dan semua orang-orang yang ada di atasnya, dan hanya kepada Kamilah mereka dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,41,'وَاذْكُرْ فِى الْكِتٰبِ اِبْرٰهِيْمَ ەۗ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا','Ceritakanlah (Hai Muhammad) kisah Ibrahim di dalam Al Kitab (Al Quran) ini. Sesungguhnya ia adalah seorang yang sangat membenarkan lagi seorang Nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,42,'اِذْ قَالَ لِاَبِيْهِ يٰٓاَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِيْ عَنْكَ شَيْـًٔا ','Ingatlah ketika ia berkata kepada bapaknya; "Wahai bapakku, mengapa kamu menyembah sesuatu yang tidak mendengar, tidak melihat dan tidak dapat menolong kamu sedikitpun?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,43,'يٰٓاَبَتِ اِنِّي قَدْ جَاۤءَنِيْ مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِيْٓ اَهْدِكَ صِرَاطًا سَوِيًّا ','Wahai bapakku, sesungguhnya telah datang kepadaku sebahagian ilmu pengetahuan yang tidak datang kepadamu, maka ikutilah aku, niscaya aku akan menunjukkan kepadamu jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,44,'يٰٓاَبَتِ لَا تَعْبُدِ الشَّيْطٰنَۗ اِنَّ الشَّيْطٰنَ كَانَ لِلرَّحْمٰنِ عَصِيًّا ','Wahai bapakku, janganlah kamu menyembah syaitan. Sesungguhnya syaitan itu durhaka kepada Tuhan Yang Maha Pemurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,45,'يٰٓاَبَتِ اِنِّيْٓ اَخَافُ اَنْ يَّمَسَّكَ عَذَابٌ مِّنَ الرَّحْمٰنِ فَتَكُوْنَ لِلشَّيْطٰنِ وَلِيًّا ','Wahai bapakku, sesungguhnya aku khawatir bahwa kamu akan ditimpa azab dari Tuhan Yang Maha Pemurah, maka kamu menjadi kawan bagi syaitan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,46,'قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَتِيْ يٰٓاِبْرٰهِيْمُ ۚ لَىِٕنْ لَّمْ تَنْتَهِ لَاَرْجُمَنَّكَ وَاهْجُرْنِيْ مَلِيًّا ','Berkata bapaknya: "Bencikah kamu kepada tuhan-tuhanku, hai Ibrahim? Jika kamu tidak berhenti, maka niscaya kamu akan kurajam, dan tinggalkanlah aku buat waktu yang lama".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,47,'قَالَ سَلٰمٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبِّيْۗ اِنَّهٗ كَانَ بِيْ حَفِيًّا ','Berkata Ibrahim: "Semoga keselamatan dilimpahkan kepadamu, aku akan memintakan ampun bagimu kepada Tuhanku. Sesungguhnya Dia sangat baik kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,48,'وَاَعْتَزِلُكُمْ وَمَا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ وَاَدْعُوْ رَبِّيْۖ عَسٰٓى اَلَّآ اَكُوْنَ بِدُعَاۤءِ رَبِّيْ شَقِيًّا ','Dan aku akan menjauhkan diri darimu dan dari apa yang kamu seru selain Allah, dan aku akan berdoa kepada Tuhanku, mudah-mudahan aku tidak akan kecewa dengan berdoa kepada Tuhanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,49,'فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۙوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ وَكُلًّا جَعَلْنَا نَبِيًّا ','Maka ketika Ibrahim sudah menjauhkan diri dari mereka dan dari apa yang mereka sembah selain Allah, Kami anugerahkan kepadanya Ishak, dan Ya´qub. Dan masing-masingnya Kami angkat menjadi nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,50,'وَوَهَبْنَا لَهُمْ مِّنْ رَّحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا ࣖ','Dan Kami anugerahkan kepada mereka sebagian dari rahmat Kami dan Kami jadikan mereka buah tutur yang baik lagi tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,51,'وَاذْكُرْ فِى الْكِتٰبِ مُوْسٰٓىۖ اِنَّهٗ كَانَ مُخْلَصًا وَّكَانَ رَسُوْلًا نَّبِيًّا ','Dan ceritakanlah (hai Muhammad kepada mereka), kisah Musa di dalam Al Kitab (Al Quran) ini. Sesungguhnya ia adalah seorang yang dipilih dan seorang rasul dan nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,52,'وَنَادَيْنٰهُ مِنْ جَانِبِ الطُّوْرِ الْاَيْمَنِ وَقَرَّبْنٰهُ نَجِيًّا ','Dan Kami telah memanggilnya dari sebelah kanan gunung Thur dan Kami telah mendekatkannya kepada Kami di waktu dia munajat (kepada Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,53,'وَوَهَبْنَا لَهٗ مِنْ رَّحْمَتِنَآ اَخَاهُ هٰرُوْنَ نَبِيًّا ','Dan Kami telah menganugerahkan kepadanya sebagian rahmat Kami, yaitu saudaranya, Harun menjadi seorang nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,54,'وَاذْكُرْ فِى الْكِتٰبِ اِسْمٰعِيْلَ ۖاِنَّهٗ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُوْلًا نَّبِيًّا ۚ','Dan ceritakanlah (hai Muhammad kepada mereka) kisah Ismail (yang tersebut) di dalam Al Quran. Sesungguhnya ia adalah seorang yang benar janjinya, dan dia adalah seorang rasul dan nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,55,'وَكَانَ يَأْمُرُ اَهْلَهٗ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّهٖ مَرْضِيًّا ','Dan ia menyuruh ahlinya untuk bersembahyang dan menunaikan zakat, dan ia adalah seorang yang diridhai di sisi Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,56,'وَاذْكُرْ فِى الْكِتٰبِ اِدْرِيْسَۖ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا ۙ','Dan ceritakanlah (hai Muhammad kepada mereka, kisah) Idris (yang tersebut) di dalam Al Quran. Sesungguhnya ia adalah seorang yang sangat membenarkan dan seorang nabi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,57,'وَّرَفَعْنٰهُ مَكَانًا عَلِيًّا ','Dan Kami telah mengangkatnya ke martabat yang tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,58,'اُولٰۤىِٕكَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوْحٍۖ وَّمِنْ ذُرِّيَّةِ اِبْرٰهِيْمَ وَاِسْرَاۤءِيْلَ ۖوَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۗ اِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُ الرَّحْمٰنِ خَرُّوْا سُجَّدًا وَّبُكِيًّا ۩','Mereka itu adalah orang-orang yang telah diberi nikmat oleh Allah, yaitu para nabi dari keturunan Adam, dan dari orang-orang yang Kami angkat bersama Nuh, dan dari keturunan Ibrahim dan Israil, dan dari orang-orang yang telah Kami beri petunjuk dan telah Kami pilih. Apabila dibacakan ayat-ayat Allah Yang Maha Pemurah kepada mereka, maka mereka menyungkur dengan bersujud dan menangis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,59,'۞ فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوٰتِ فَسَوْفَ يَلْقَوْنَ غَيًّا ۙ','Maka datanglah sesudah mereka, pengganti (yang jelek) yang menyia-nyiakan shalat dan memperturutkan hawa nafsunya, maka mereka kelak akan menemui kesesatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,60,'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ شَيْـًٔا ۙ','kecuali orang yang bertaubat, beriman dan beramal saleh, maka mereka itu akan masuk surga dan tidak dianiaya (dirugikan) sedikitpun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,61,'جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدَ الرَّحْمٰنُ عِبَادَهٗ بِالْغَيْبِۗ اِنَّهٗ كَانَ وَعْدُهٗ مَأْتِيًّا ','yaitu surga ´Adn yang telah dijanjikan oleh Tuhan Yang Maha Pemurah kepada hamba-hamba-Nya, sekalipun (surga itu) tidak nampak. Sesungguhnya janji Allah itu pasti akan ditepati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,62,'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا اِلَّا سَلٰمًاۗ وَلَهُمْ رِزْقُهُمْ فِيْهَا بُكْرَةً وَّعَشِيًّا ','Mereka tidak mendengar perkataan yang tak berguna di dalam surga, kecuali ucapan salam. Bagi mereka rezekinya di surga itu tiap-tiap pagi dan petang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,63,'تِلْكَ الْجَنَّةُ الَّتِيْ نُوْرِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا ','Itulah surga yang akan Kami wariskan kepada hamba-hamba Kami yang selalu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,64,'وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ  لَهٗ مَا بَيْنَ اَيْدِيْنَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَ وَمَا كَانَ رَبُّكَ نَسِيًّا ۚ','Dan tidaklah kami (Jibril) turun, kecuali dengan perintah Tuhanmu. Kepunyaan-Nya-lah apa-apa yang ada di hadapan kita, apa-apa yang ada di belakang kita dan apa-apa yang ada di antara keduanya, dan tidaklah Tuhanmu lupa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,65,'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهٖۗ هَلْ تَعْلَمُ لَهٗ سَمِيًّا ࣖ','Tuhan (yang menguasai) langit dan bumi dan apa-apa yang ada di antara keduanya, maka sembahlah Dia dan berteguh hatilah dalam beribadat kepada-Nya. Apakah kamu mengetahui ada seorang yang sama dengan Dia (yang patut disembah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,66,'وَيَقُوْلُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَيًّا ','Dan berkata manusia: "Betulkah apabila aku telah mati, bahwa aku sungguh-sungguh akan dibangkitkan menjadi hidup kembali?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,67,'اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـًٔا ','Dan tidakkah manusia itu memikirkan bahwa sesungguhnya Kami telah menciptakannya dahulu, sedang ia tidak ada sama sekali?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,68,'فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيٰطِيْنَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا ','Demi Tuhanmu, sesungguhnya akan Kami bangkitkan mereka bersama syaitan, kemudian akan Kami datangkan mereka ke sekeliling Jahannam dengan berlutut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,69,'ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيْعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِيًّا ۚ','Kemudian pasti akan Kami tarik dari tiap-tiap golongan siapa di antara mereka yang sangat durhaka kepada Tuhan Yang Maha Pemurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,70,'ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذِيْنَ هُمْ اَوْ لٰى بِهَا صِلِيًّا ','Dan kemudian Kami sungguh lebih mengetahui orang-orang yang seharusnya dimasukkan ke dalam neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,71,'وَاِنْ مِّنْكُمْ اِلَّا وَارِدُهَا ۚ كَانَ عَلٰى رَبِّكَ حَتْمًا مَّقْضِيًّا ۚ','Dan tidak ada seorangpun dari padamu, melainkan mendatangi neraka itu. Hal itu bagi Tuhanmu adalah suatu kemestian yang sudah ditetapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,72,'ثُمَّ نُنَجِّى الَّذِيْنَ اتَّقَوْا وَّنَذَرُ الظّٰلِمِيْنَ فِيْهَا جِثِيًّا ','Kemudian Kami akan menyelamatkan orang-orang yang bertakwa dan membiarkan orang-orang yang zalim di dalam neraka dalam keadaan berlutut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,73,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓاۙ اَيُّ الْفَرِيْقَيْنِ خَيْرٌ مَّقَامًا وَّاَحْسَنُ نَدِيًّا ','Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang (maksudnya), niscaya orang-orang yang kafir berkata kepada orang-orang yang beriman: "Manakah di antara kedua golongan (kafir dan mukmin) yang lebih baik tempat tinggalnya dan lebih indah tempat pertemuan(nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,74,'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثًا وَّرِءْيًا ','Berapa banyak umat yang telah Kami binasakan sebelum mereka, sedang mereka adalah lebih bagus alat rumah tangganya dan lebih sedap di pandang mata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,75,'قُلْ مَنْ كَانَ فِى الضَّلٰلَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَدًّا ەۚ حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَ ۗفَسَيَعْلَمُوْنَ مَنْ هُوَ شَرٌّ مَّكَانًا وَّاَضْعَفُ جُنْدًا','Katakanlah: "Barang siapa yang berada di dalam kesesatan, maka biarlah Tuhan yang Maha Pemurah memperpanjang tempo baginya; sehingga apabila mereka telah melihat apa yang diancamkan kepadanya, baik siksa maupun kiamat, maka mereka akan mengetahui siapa yang lebih jelek kedudukannya dan lebih lemah penolong-penolongnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,76,'وَيَزِيْدُ اللّٰهُ الَّذِيْنَ اهْتَدَوْا هُدًىۗ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ مَّرَدًّا ','Dan Allah akan menambah petunjuk kepada mereka yang telah mendapat petunjuk. Dan amal-amal saleh yang kekal itu lebih baik pahalanya di sisi Tuhanmu dan lebih baik kesudahannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,77,'اَفَرَاَيْتَ الَّذِيْ كَفَرَ بِاٰيٰتِنَا وَقَالَ لَاُوْتَيَنَّ مَالًا وَّوَلَدًا ۗ','Maka apakah kamu telah melihat orang yang kafir kepada ayat-ayat Kami dan ia mengatakan: "Pasti aku akan diberi harta dan anak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,78,'اَطَّلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۙ','Adakah ia melihat yang ghaib atau ia telah membuat perjanjian di sisi Tuhan Yang Maha Pemurah?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,79,'كَلَّاۗ سَنَكْتُبُ مَا يَقُوْلُ وَنَمُدُّ لَهٗ مِنَ الْعَذَابِ مَدًّا ۙ','sekali-kali tidak, Kami akan menulis apa yang ia katakan, dan benar-benar Kami akan memperpanjang azab untuknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,80,'وَّنَرِثُهٗ مَا يَقُوْلُ وَيَأْتِيْنَا فَرْدًا ','dan Kami akan mewarisi apa yang ia katakan itu, dan ia akan datang kepada Kami dengan seorang diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,81,'وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لِّيَكُوْنُوْا لَهُمْ عِزًّا ۙ','Dan mereka telah mengambil sembahan-sembahan selain Allah, agar sembahan-sembahan itu menjadi pelindung bagi mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,82,'كَلَّا ۗسَيَكْفُرُوْنَ بِعِبَادَتِهِمْ وَيَكُوْنُوْنَ عَلَيْهِمْ ضِدًّا ࣖ','sekali-kali tidak. Kelak mereka (sembahan-sembahan) itu akan mengingkari penyembahan (pengikut-pengikutnya) terhadapnya, dan mereka (sembahan-sembahan) itu akan menjadi musuh bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,83,'اَلَمْ تَرَ اَنَّآ اَرْسَلْنَا الشَّيٰطِيْنَ عَلَى الْكٰفِرِيْنَ تَؤُزُّهُمْ اَزًّا ۙ','Tidakkah kamu lihat, bahwasanya Kami telah mengirim syaitan-syaitan itu kepada orang-orang kafir untuk menghasung mereka berbuat maksiat dengan sungguh-sungguh?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,84,'فَلَا تَعْجَلْ عَلَيْهِمْۗ اِنَّمَا نَعُدُّ لَهُمْ عَدًّا ۗ','maka janganlah kamu tergesa-gesa memintakan siksa terhadap mereka, karena sesungguhnya Kami hanya menghitung datangnya (hari siksaan) untuk mereka dengan perhitungan yang teliti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,85,'يَوْمَ نَحْشُرُ الْمُتَّقِيْنَ اِلَى الرَّحْمٰنِ وَفْدًا ','(Ingatlah) hari (ketika) Kami mengumpulkan orang-orang yang takwa kepada Tuhan Yang Maha Pemurah sebagai perutusan yang terhormat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,86,'وَنَسُوْقُ الْمُجْرِمِيْنَ اِلٰى جَهَنَّمَ وِرْدًا ۘ','dan Kami akan menghalau orang-orang yang durhaka ke neraka Jahannam dalam keadaan dahaga.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,87,'لَا يَمْلِكُوْنَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۘ','Mereka tidak berhak mendapat syafa´at kecuali orang yang telah mengadakan perjanjian di sisi Tuhan Yang Maha Pemurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,88,'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا ۗ','Dan mereka berkata: "Tuhan Yang Maha Pemurah mengambil (mempunyai) anak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,89,'لَقَدْ جِئْتُمْ شَيْـًٔا اِدًّا ۙ','Sesungguhnya kamu telah mendatangkan sesuatu perkara yang sangat mungkar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,90,'تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا ۙ','hampir-hampir langit pecah karena ucapan itu, dan bumi belah, dan gunung-gunung runtuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,91,'اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَدًا ۚ','karena mereka mendakwakan Allah Yang Maha Pemurah mempunyai anak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,92,'وَمَا يَنْۢبَغِيْ لِلرَّحْمٰنِ اَنْ يَّتَّخِذَ وَلَدًا ۗ','Dan tidak layak bagi Tuhan Yang Maha Pemurah mengambil (mempunyai) anak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,93,'اِنْ كُلُّ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ اِلَّآ اٰتِى الرَّحْمٰنِ عَبْدًا ۗ','Tidak ada seorangpun di langit dan di bumi, kecuali akan datang kepada Tuhan Yang Maha Pemurah selaku seorang hamba.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,94,'لَقَدْ اَحْصٰىهُمْ وَعَدَّهُمْ عَدًّا ۗ','Sesungguhnya Allah telah menentukan jumlah mereka dan menghitung mereka dengan hitungan yang teliti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,95,'وَكُلُّهُمْ اٰتِيْهِ يَوْمَ الْقِيٰمَةِ فَرْدًا ','Dan tiap-tiap mereka akan datang kepada Allah pada hari kiamat dengan sendiri-sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,96,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُدًّا ','Sesungguhnya orang-orang yang beriman dan beramal saleh, kelak Allah Yang Maha Pemurah akan menanamkan dalam (hati) mereka rasa kasih sayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,97,'فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِيْنَ وَتُنْذِرَ بِهٖ قَوْمًا لُّدًّا ','Maka sesungguhnya telah Kami mudahkan Al Quran itu dengan bahasamu, agar kamu dapat memberi kabar gembira dengan Al Quran itu kepada orang-orang yang bertakwa, dan agar kamu memberi peringatan dengannya kepada kaum yang membangkang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,19,98,'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍۗ هَلْ تُحِسُّ مِنْهُمْ مِّنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزًا ࣖ','Dan berapa banyak telah Kami binasakan umat-umat sebelum mereka. Adakah kamu melihat seorangpun dari mereka atau kamu dengar suara mereka yang samar-samar?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,1,'طٰهٰ ۚ','Thaahaa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,2,'مَآ اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰٓى ۙ','Kami tidak menurunkan Al Quran ini kepadamu agar kamu menjadi susah;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,3,'اِلَّا تَذْكِرَةً لِّمَنْ يَّخْشٰى ۙ','tetapi sebagai peringatan bagi orang yang takut (kepada Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,4,'تَنْزِيْلًا مِّمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوٰتِ الْعُلٰى ۗ','yaitu diturunkan dari Allah yang menciptakan bumi dan langit yang tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,5,'اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى ','(Yaitu) Tuhan Yang Maha Pemurah. Yang bersemayam di atas ´Arsy.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,6,'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى ','Kepunyaan-Nya-lah semua yang ada di langit, semua yang di bumi, semua yang di antara keduanya dan semua yang di bawah tanah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,7,'وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهٗ يَعْلَمُ السِّرَّ وَاَخْفٰى ','Dan jika kamu mengeraskan ucapanmu, maka sesungguhnya Dia mengetahui rahasia dan yang lebih tersembunyi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,8,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ  لَهُ الْاَسْمَاۤءُ الْحُسْنٰى ','Dialah Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia. Dia mempunyai al asmaaul husna (nama-nama yang baik),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,9,'وَهَلْ اَتٰىكَ حَدِيْثُ مُوْسٰى ۘ','Apakah telah sampai kepadamu kisah Musa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,10,'اِذْ رَاٰ نَارًا فَقَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى ','Ketika ia melihat api, lalu berkatalah ia kepada keluarganya: "Tinggallah kamu (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa sedikit daripadanya kepadamu atau aku akan mendapat petunjuk di tempat api itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,11,'فَلَمَّآ اَتٰىهَا نُوْدِيَ يٰمُوْسٰٓى ۙ','Maka ketika ia datang ke tempat api itu ia dipanggil: "Hai Musa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,12,'اِنِّيْٓ اَنَا۠ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى ۗ','Sesungguhnya Aku inilah Tuhanmu, maka tanggalkanlah kedua terompahmu; sesungguhnya kamu berada dilembah yang suci, Thuwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,13,'وَاَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوْحٰى ','Dan Aku telah memilih kamu, maka dengarkanlah apa yang akan diwahyukan (kepadamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,14,'اِنَّنِيْٓ اَنَا اللّٰهُ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدْنِيْۙ وَاَقِمِ الصَّلٰوةَ لِذِكْرِيْ ','Sesungguhnya Aku ini adalah Allah, tidak ada Tuhan (yang hak) selain Aku, maka sembahlah Aku dan dirikanlah shalat untuk mengingat Aku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,15,'اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْفِيْهَا لِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا تَسْعٰى ','Segungguhnya hari kiamat itu akan datang Aku merahasiakan (waktunya) agar supaya tiap-tiap diri itu dibalas dengan apa yang ia usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,16,'فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَّا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوٰىهُ فَتَرْدٰى ','Maka sekali-kali janganlah kamu dipalingkan daripadanya oleh orang yang tidak beriman kepadanya dan oleh orang yang mengikuti hawa nafsunya, yang menyebabkan kamu jadi binasa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,17,'وَمَا تِلْكَ بِيَمِيْنِكَ يٰمُوْسٰى ','Apakah itu yang di tangan kananmu, hai Musa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,18,'قَالَ هِيَ عَصَايَۚ اَتَوَكَّؤُا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَمِيْ وَلِيَ فِيْهَا مَاٰرِبُ اُخْرٰى ','Berkata Musa: "Ini adalah tongkatku, aku bertelekan padanya, dan aku pukul (daun) dengannya untuk kambingku, dan bagiku ada lagi keperluan yang lain padanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,19,'قَالَ اَلْقِهَا يٰمُوْسٰى ','Allah berfirman: "Lemparkanlah ia, hai Musa!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,20,'فَاَلْقٰىهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى ','Lalu dilemparkannyalah tongkat itu, maka tiba-tiba ia menjadi seekor ular yang merayap dengan cepat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,21,'قَالَ خُذْهَا وَلَا تَخَفْۗ سَنُعِيْدُهَا سِيْرَتَهَا الْاُوْلٰى ','Allah berfirman: "Peganglah ia dan jangan takut, Kami akan mengembalikannya kepada keadaannya semula,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,22,'وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ اٰيَةً اُخْرٰىۙ ','dan kepitkanlah tanganmu ke ketiakmu, niscaya ia ke luar menjadi putih cemerlang tanpa cacad, sebagai mukjizat yang lain (pula),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,23,'لِنُرِيَكَ مِنْ اٰيٰتِنَا الْكُبْرٰى ۚ','untuk Kami perlihatkan kepadamu sebahagian dari tanda-tanda kekuasaan Kami yang sangat besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,24,'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰى ࣖ','Pergilah kepada Fir´aun; sesungguhnya ia telah melampaui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,25,'قَالَ رَبِّ اشْرَحْ لِيْ صَدْرِيْ ۙ','Berkata Musa: "Ya Tuhanku, lapangkanlah untukku dadaku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,26,'وَيَسِّرْ لِيْٓ اَمْرِيْ ۙ','dan mudahkanlah untukku urusanku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,27,'وَاحْلُلْ عُقْدَةً مِّنْ لِّسَانِيْ ۙ','dan lepaskanlah kekakuan dari lidahku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,28,'يَفْقَهُوْا قَوْلِيْ  ۖ','supaya mereka mengerti perkataanku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,29,'وَاجْعَلْ لِّيْ وَزِيْرًا مِّنْ اَهْلِيْ ۙ','dan jadikanlah untukku seorang pembantu dari keluargaku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,30,'هٰرُوْنَ اَخِى ۙ','(yaitu) Harun, saudaraku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,31,'اشْدُدْ بِهٖٓ اَزْرِيْ ۙ','teguhkanlah dengan dia kekuatanku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,32,'وَاَشْرِكْهُ فِيْٓ اَمْرِيْ ۙ','dan jadikankanlah dia sekutu dalam urusanku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,33,'كَيْ نُسَبِّحَكَ كَثِيْرًا ۙ','supaya kami banyak bertasbih kepada Engkau,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,34,'وَّنَذْكُرَكَ كَثِيْرًا ۗ','dan banyak mengingat Engkau.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,35,'اِنَّكَ كُنْتَ بِنَا بَصِيْرًا ','Sesungguhnya Engkau adalah Maha Melihat (keadaan) kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,36,'قَالَ قَدْ اُوْتِيْتَ سُؤْلَكَ يٰمُوْسٰى ','Allah berfirman: "Sesungguhnya telah diperkenankan permintaanmu, hai Musa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,37,'وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىٓ ۙ','Dan sesungguhnya Kami telah memberi nikmat kepadamu pada kali yang lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,38,'اِذْ اَوْحَيْنَآ اِلٰٓى اُمِّكَ مَا يُوْحٰىٓ ۙ','yaitu ketika Kami mengilhamkan kepada ibumu suatu yang diilhamkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,39,'اَنِ اقْذِفِيْهِ فِى التَّابُوْتِ فَاقْذِفِيْهِ فِى الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِّيْ وَعَدُوٌّ لَّهٗ  ۗوَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِّنِّيْ ەۚ وَلِتُصْنَعَ عَلٰى عَيْنِيْ ۘ','Yaitu: "Letakkanlah ia (Musa) didalam peti, kemudian lemparkanlah ia ke sungai (Nil), maka pasti sungai itu membawanya ke tepi, supaya diambil oleh (Fir´aun) musuh-Ku dan musuhnya. Dan Aku telah melimpahkan kepadamu kasih sayang yang datang dari-Ku; dan supaya kamu diasuh di bawah pengawasan-Ku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,40,'اِذْ تَمْشِيْٓ اُخْتُكَ فَتَقُوْلُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَّكْفُلُهٗ ۗفَرَجَعْنٰكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ەۗ وَقَتَلْتَ نَفْسًا فَنَجَّيْنٰكَ مِنَ الْغَمِّ وَفَتَنّٰكَ فُتُوْنًا ەۗ فَلَبِثْتَ سِنِيْنَ فِيْٓ اَهْلِ مَدْيَنَ ەۙ ثُمَّ جِئْتَ عَلٰى قَدَرٍ يّٰمُوْسٰى','(yaitu) ketika saudaramu yang perempuan berjalan, lalu ia berkata kepada (keluarga Fir´aun): "Bolehkah saya menunjukkan kepadamu orang yang akan memeliharanya?" Maka Kami mengembalikanmu kepada ibumu, agar senang hatinya dan tidak berduka cita. Dan kamu pernah membunuh seorang manusia, lalu Kami selamatkan kamu dari kesusahan dan Kami telah mencobamu dengan beberapa cobaan; maka kamu tinggal beberapa tahun diantara penduduk Madyan, kemudian kamu datang menurut waktu yang ditetapkan hai Musa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,41,'وَاصْطَنَعْتُكَ لِنَفْسِيْۚ  ','dan Aku telah memilihmu untuk diri-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,42,'اِذْهَبْ اَنْتَ وَاَخُوْكَ بِاٰيٰتِيْ وَلَا تَنِيَا فِيْ ذِكْرِيْۚ  ','Pergilah kamu beserta saudaramu dengan membawa ayat-ayat-Ku, dan janganlah kamu berdua lalai dalam mengingat-Ku;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,43,'اِذْهَبَآ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۚ ','Pergilah kamu berdua kepada Fir´aun, sesungguhnya dia telah melampaui batas;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,44,'فَقُوْلَا لَهٗ قَوْلًا لَّيِّنًا لَّعَلَّهٗ يَتَذَكَّرُ اَوْ يَخْشٰى ','maka berbicaralah kamu berdua kepadanya dengan kata-kata yang lemah lembut, mudah-mudahan ia ingat atau takut".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,45,'قَالَا رَبَّنَآ اِنَّنَا نَخَافُ اَنْ يَّفْرُطَ عَلَيْنَآ اَوْ اَنْ يَّطْغٰى ','Berkatalah mereka berdua: "Ya Tuhan kami, sesungguhnya kami khawatir bahwa ia segera menyiksa kami atau akan bertambah melampaui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,46,'قَالَ لَا تَخَافَآ اِنَّنِيْ مَعَكُمَآ اَسْمَعُ وَاَرٰى ','Allah berfirman: "Janganlah kamu berdua khawatir, sesungguhnya Aku beserta kamu berdua, Aku mendengar dan melihat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,47,'فَأْتِيٰهُ فَقُوْلَآ اِنَّا رَسُوْلَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ وَلَا تُعَذِّبْهُمْۗ قَدْ جِئْنٰكَ بِاٰيَةٍ مِّنْ رَّبِّكَ ۗوَالسَّلٰمُ عَلٰى مَنِ اتَّبَعَ الْهُدٰى','Maka datanglah kamu berdua kepadanya (Fir´aun) dan katakanlah: "Sesungguhnya kami berdua adalah utusan Tuhanmu, maka lepaskanlah Bani Israil bersama kami dan janganlah kamu menyiksa mereka. Sesungguhnya kami telah datang kepadamu dengan membawa bukti (atas kerasulan kami) dari Tuhanmu. Dan keselamatan itu dilimpahkan kepada orang yang mengikuti petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,48,'اِنَّا قَدْ اُوْحِيَ اِلَيْنَآ اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى ','Sesungguhnya telah diwahyukan kepada kami bahwa siksa itu (ditimpakan) atas orang-orang yang mendustakan dan berpaling.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,49,'قَالَ فَمَنْ رَّبُّكُمَا يٰمُوْسٰى ','Berkata Fir´aun: "Maka siapakah Tuhanmu berdua, hai Musa?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,50,'قَالَ رَبُّنَا الَّذِيْٓ اَعْطٰى كُلَّ شَيْءٍ خَلْقَهٗ ثُمَّ هَدٰى ','Musa berkata: "Tuhan kami ialah (Tuhan) yang telah memberikan kepada tiap-tiap sesuatu bentuk kejadiannya, kemudian memberinya petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,51,'قَالَ فَمَا بَالُ الْقُرُوْنِ الْاُوْلٰى ','Berkata Fir´aun: "Maka bagaimanakah keadaan umat-umat yang dahulu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,52,'قَالَ عِلْمُهَا عِنْدَ رَبِّيْ فِيْ كِتٰبٍۚ  لَا يَضِلُّ رَبِّيْ وَلَا يَنْسَىۖ ','Musa menjawab: "Pengetahuan tentang itu ada di sisi Tuhanku, di dalam sebuah kitab, Tuhan kami tidak akan salah dan tidak (pula) lupa;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,53,'الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّسَلَكَ لَكُمْ فِيْهَا سُبُلًا وَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۗ فَاَخْرَجْنَا بِهٖٓ اَزْوَاجًا مِّنْ نَّبَاتٍ شَتّٰى ','Yang telah menjadikan bagimu bumi sebagai hamparan dan Yang telah menjadikan bagimu di bumi itu jalan-ja]an, dan menurunkan dari langit air hujan. Maka Kami tumbuhkan dengan air hujan itu berjenis-jenis dari tumbuh-tumbuhan yang bermacam-macam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,54,'كُلُوْا وَارْعَوْا اَنْعَامَكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى ࣖ','Makanlah dan gembalakanlah binatang-binatangmu. Sesungguhnya pada yang demikian itu, terdapat tanda-tanda kekuasaan Allah bagi orang-orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,55,'۞ مِنْهَا خَلَقْنٰكُمْ وَفِيْهَا نُعِيْدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى ','Dari bumi (tanah) itulah Kami menjadikan kamu dan kepadanya Kami akan mengembalikan kamu dan daripadanya Kami akan mengeluarkan kamu pada kali yang lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,56,'وَلَقَدْ اَرَيْنٰهُ اٰيٰتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى ','Dan sesungguhnya Kami telah perlihatkan kepadanya (Fir´aun) tanda-tanda kekuasaan Kami semuanya maka ia mendustakan dan enggan (menerima kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,57,'قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يٰمُوْسٰى ','Berkata Fir´aun: "Adakah kamu datang kepada kami untuk mengusir kami dari negeri kami (ini) dengan sihirmu, hai Musa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,58,'فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِّثْلِهٖ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَّا نُخْلِفُهٗ نَحْنُ وَلَآ اَنْتَ مَكَانًا سُوًى ','Dan kamipun pasti akan mendatangkan (pula) kepadamu sihir semacam itu, maka buatlah suatu waktu untuk pertemuan antara kami dan kamu, yang kami tidak akan menyalahinya dan tidak (pula) kamu di suatu tempat yang pertengahan (letaknya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,59,'قَالَ مَوْعِدُكُمْ يَوْمُ الزِّيْنَةِ وَاَنْ يُّحْشَرَ النَّاسُ ضُحًى ','Berkata Musa: "Waktu untuk pertemuan (kami dengan) kamu itu ialah di hari raya dan hendaklah dikumpulkan manusia pada waktu matahari sepenggalahan naik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,60,'فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهٗ ثُمَّ اَتٰى ','Maka Fir´aun meninggalkan (tempat itu), lalu mengatur tipu dayanya, kemudian dia datang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,61,'قَالَ لَهُمْ مُّوْسٰى وَيْلَكُمْ لَا تَفْتَرُوْا عَلَى اللّٰهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى ','Berkata Musa kepada mereka: "Celakalah kamu, janganlah kamu mengada-adakan kedustaan terhadap Allah, maka Dia membinasakan kamu dengan siksa". Dan sesungguhnya telah merugi orang yang mengada-adakan kedustaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,62,'فَتَنَازَعُوْٓا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى ','Maka mereka berbantah-bantahan tentang urusan mereka di antara mereka dan mereka merahasiakan percakapan (mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,63,'قَالُوْٓا اِنْ هٰذٰنِ لَسٰحِرَانِ يُرِيْدَانِ اَنْ يُّخْرِجٰكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيْقَتِكُمُ الْمُثْلٰى ','Mereka berkata: "Sesungguhnya dua orang ini adalah benar-benar ahli sihir yang hendak mengusir kamu dari negeri kamu dengan sihirnya dan hendak melenyapkan kedudukan kamu yang utama.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,64,'فَاَجْمِعُوْا كَيْدَكُمْ ثُمَّ ائْتُوْا صَفًّاۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى ','Maka himpunkanlah segala daya (sihir) kamu sekalian, kemudian datanglah dengan berbaris. dan sesungguhnya beruntunglah oran yang menang pada hari ini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,65,'قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ اَوَّلَ مَنْ اَلْقٰى ','(Setelah mereka berkumpul) mereka berkata: "Hai Musa (pilihlah), apakah kamu yang melemparkan (dahulu) atau kamikah orang yang mula-mula melemparkan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,66,'قَالَ بَلْ اَلْقُوْاۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى','Berkata Musa: "Silahkan kamu sekalian melemparkan". Maka tiba-tiba tali-tali dan tongkat-tongkat mereka, terbayang kepada Musa seakan-akan ia merayap cepat, lantaran sihir mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,67,'فَاَوْجَسَ فِيْ نَفْسِهٖ خِيْفَةً مُّوْسٰى ','Maka Musa merasa takut dalam hatinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,68,'قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى ','Kami berkata: "janganlah kamu takut, sesungguhnya kamulah yang paling unggul (menang).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,69,'وَاَلْقِ مَا فِيْ يَمِيْنِكَ تَلْقَفْ مَا صَنَعُوْاۗ اِنَّمَا صَنَعُوْا كَيْدُ سٰحِرٍۗ وَلَا يُفْلِحُ السّٰحِرُ حَيْثُ اَتٰى ','Dan lemparkanlah apa yang ada ditangan kananmu, niscaya ia akan menelan apa yang mereka perbuat. "Sesungguhnya apa yang mereka perbuat itu adalah tipu daya tukang sihir (belaka). Dan tidak akan menang tukang sihir itu, dari mana saja ia datang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,70,'فَاُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوْٓا اٰمَنَّا بِرَبِّ هٰرُوْنَ وَمُوْسٰى ','Lalu tukang-tukang sihir itu tersungkur dengan bersujud, seraya berkata: "Kami telah percaya kepada Tuhan Harun dan Musa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,71,'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۗ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ فِيْ جُذُوْعِ النَّخْلِۖ وَلَتَعْلَمُنَّ اَيُّنَآ اَشَدُّ عَذَابًا وَّاَبْقٰى ','Berkata Fir´aun: "Apakah kamu telah beriman kepadanya (Musa) sebelum aku memberi izin kepadamu sekalian. Sesungguhnya ia adalah pemimpinmu yang mengajarkan sihir kepadamu sekalian. Maka sesungguhnya aku akan memotong tangan dan kaki kamu sekalian dengan bersilang secara bertimbal balik, dan sesungguhnya aku akan menyalib kamu sekalian pada pangkal pohon kurma dan sesungguhnya kamu akan mengetahui siapa di antara kita yang lebih pedih dan lebih kekal siksanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,72,'قَالُوْا لَنْ نُّؤْثِرَكَ عَلٰى مَا جَاۤءَنَا مِنَ الْبَيِّنٰتِ وَالَّذِيْ فَطَرَنَا فَاقْضِ مَآ اَنْتَ قَاضٍۗ اِنَّمَا تَقْضِيْ هٰذِهِ الْحَيٰوةَ الدُّنْيَا ۗ','Mereka berkata: "Kami sekali-kali tidak akan mengutamakan kamu daripada bukti-bukti yang nyata (mukjizat), yang telah datang kepada kami dan daripada Tuhan yang telah menciptakan kami; maka putuskanlah apa yang hendak kamu putuskan. Sesungguhnya kamu hanya akan dapat memutuskan pada kehidupan di dunia ini saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,73,'اِنَّآ اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطٰيٰنَا وَمَآ اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۗ وَاللّٰهُ خَيْرٌ وَّاَبْقٰى ','Sesungguhnya kami telah beriman kepada Tuhan kami, agar Dia mengampuni kesalahan-kesalahan kami dan sihir yang telah kamu paksakan kepada kami melakukannya. Dan Allah lebih baik (pahala-Nya) dan lebih kekal (azab-Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,74,'اِنَّهٗ مَنْ يَّأْتِ رَبَّهٗ مُجْرِمًا فَاِنَّ لَهٗ جَهَنَّمَ ۗ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰى ','Sesungguhnya barangsiapa datang kepada Tuhannya dalam keadaan berdosa, maka sesungguhnya baginya neraka Jahannam. Ia tidak mati di dalamnya dan tidak (pula) hidup.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,75,'وَمَنْ يَّأْتِهٖ مُؤْمِنًا قَدْ عَمِلَ الصّٰلِحٰتِ فَاُولٰۤىِٕكَ لَهُمُ الدَّرَجٰتُ الْعُلٰى ۙ','Dan barangsiapa datang kepada Tuhannya dalam keadaan beriman, lagi sungguh-sungguh telah beramal saleh, maka mereka itulah orang-orang yang memperoleh tempat-tempat yang tinggi (mulia),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,76,'جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗوَذٰلِكَ جَزٰۤؤُا مَنْ تَزَكّٰى ࣖ','(yaitu) surga ´Adn yang mengalir sungai-sungai di bawahnya, mereka kekal di dalamnya. Dan itu adalah balasan bagi orang yang bersih (dari kekafiran dan kemaksiatan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,77,'وَلَقَدْ اَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْ فَاضْرِبْ لَهُمْ طَرِيْقًا فِى الْبَحْرِ يَبَسًاۙ  لَّا تَخٰفُ دَرَكًا وَّلَا تَخْشٰى ','Dan sesungguhnya telah Kami wahyukan kepada Musa: "Pergilah kamu dengan hamba-hamba-Ku (Bani Israil) di malam hari, maka buatlah untuk mereka jalan yang kering dilaut itu, kamu tak usah khawatir akan tersusul dan tidak usah takut (akan tenggelam)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,78,'فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُوْدِهٖ فَغَشِيَهُمْ مِّنَ الْيَمِّ مَا غَشِيَهُمْ ۗ','Maka Fir´aun dengan bala tentaranya mengejar mereka, lalu mereka ditutup oleh laut yang menenggelamkan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,79,'وَاَضَلَّ فِرْعَوْنُ قَوْمَهٗ وَمَا هَدٰى ','Dan Fir´aun telah menyesatkan kaumnya dan tidak memberi petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,80,'يٰبَنِيْٓ اِسْرَاۤءِيْلَ قَدْ اَنْجَيْنٰكُمْ مِّنْ عَدُوِّكُمْ وَوٰعَدْنٰكُمْ جَانِبَ الطُّوْرِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ','Hai Bani Israil, sesungguhnya Kami telah menyelamatkan kamu sekalian dari musuhmu, dan Kami telah mengadakan perjanjian dengan kamu sekalian (untuk munajat) di sebelah kanan gunung itu dan Kami telah menurunkan kepada kamu sekalian manna dan salwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,81,'كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۙ وَلَا تَطْغَوْا فِيْهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِيْۚ وَمَنْ يَّحْلِلْ عَلَيْهِ غَضَبِيْ فَقَدْ هَوٰى ','Makanlah di antara rezeki yang baik yang telah Kami berikan kepadamu, dan janganlah melampaui batas padanya, yang menyebabkan kemurkaan-Ku menimpamu. Dan barangsiapa ditimpa oleh kemurkaan-Ku, maka sesungguhnya binasalah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,82,'وَاِنِّي لَغَفَّارٌ لِّمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدٰى ','Dan sesungguhnya Aku Maha Pengampun bagi orang yang bertaubat, beriman, beramal saleh, kemudian tetap di jalan yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,83,'۞ وَمَآ اَعْجَلَكَ عَنْ قَوْمِكَ يٰمُوْسٰى ','Mengapa kamu datang lebih cepat daripada kaummu, hai Musa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,84,'قَالَ هُمْ اُولَاۤءِ عَلٰٓى اَثَرِيْ وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى ','Berkata, Musa: "Itulah mereka sedang menyusuli aku dan aku bersegera kepada-Mu. Ya Tuhanku, agar supaya Engkau ridha (kepadaku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,85,'قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْۢ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ ','Allah berfirman: "Maka sesungguhnya Kami telah menguji kaummu sesudah kamu tinggalkan, dan mereka telah disesatkan oleh Samiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,86,'فَرَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًا ەۚ قَالَ يٰقَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ەۗ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُّمْ اَنْ يَّحِلَّ عَلَيْكُمْ غَضَبٌ مِّنْ رَّبِّكُمْ فَاَخْلَفْتُمْ مَّوْعِدِيْ','Kemudian Musa kembali kepada kaumnya dengan marah dan bersedih hati. Berkata Musa: "Hai kaumku, bukankah Tuhanmu telah menjanjikan kepadamu suatu janji yang baik? Maka apakah terasa lama masa yang berlalu itu bagimu atau kamu menghendaki agar kemurkaan dari Tuhanmu menimpamu, dan kamu melanggar perjanjianmu dengan aku?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,87,'قَالُوْا مَآ اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْنَآ اَوْزَارًا مِّنْ زِيْنَةِ الْقَوْمِ فَقَذَفْنٰهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّ ۙ','Mereka berkata: "Kami sekali-kali tidak melanggar perjanjianmu dengan kemauan kami sendiri, tetapi kami disuruh membawa beban-beban dari perhiasan kaum itu, maka kami telah melemparkannya, dan demikian pula Samiri melemparkannya",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,88,'فَاَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌ فَقَالُوْا هٰذَآ اِلٰهُكُمْ وَاِلٰهُ مُوْسٰى ەۙ فَنَسِيَ ۗ','kemudian Samiri mengeluarkan untuk mereka (dari lobang itu) anak lembu yang bertubuh dan bersuara, maka mereka berkata: "Inilah Tuhanmu dan Tuhan Musa, tetapi Musa telah lupa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,89,'اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلًا ەۙ وَّلَا يَمْلِكُ لَهُمْ ضَرًّا وَّلَا نَفْعًا ࣖ','Maka apakah mereka tidak memperhatikan bahwa patung anak lembu itu tidak dapat memberi jawaban kepada mereka, dan tidak dapat memberi kemudharatan kepada mereka dan tidak (pula) kemanfaatan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,90,'وَلَقَدْ قَالَ لَهُمْ هٰرُوْنُ مِنْ قَبْلُ يٰقَوْمِ اِنَّمَا فُتِنْتُمْ بِهٖۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُوْنِيْ وَاَطِيْعُوْٓا اَمْرِيْ ','Dan sesungguhnya Harun telah berkata kepada mereka sebelumnya: "Hai kaumku, sesungguhnya kamu hanya diberi cobaan dengan anak lembu. itu dan sesungguhnya Tuhanmu ialah (Tuhan) Yang Maha Pemurah, maka ikutilah aku dan taatilah perintahku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,91,'قَالُوْا لَنْ نَّبْرَحَ عَلَيْهِ عٰكِفِيْنَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوْسٰى ','Mereka menjawab: "Kami akan tetap menyembah patung anak lembu ini, hingga Musa kembali kepada kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,92,'قَالَ يٰهٰرُوْنُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّوْٓا ۙ','Berkata Musa: "Hai Harun, apa yang menghalangi kamu ketika kamu melihat mereka telah sesat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,93,'اَلَّا تَتَّبِعَنِۗ اَفَعَصَيْتَ اَمْرِيْ ','(sehingga) kamu tidak mengikuti aku? Maka apakah kamu telah (sengaja) mendurhakai perintahku?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,94,'قَالَ يَبْنَؤُمَّ لَا تَأْخُذْ بِلِحْيَتِيْ وَلَا بِرَأْسِيْۚ اِنِّيْ خَشِيْتُ اَنْ تَقُوْلَ فَرَّقْتَ بَيْنَ بَنِيْٓ اِسْرَاۤءِيْلَ وَلَمْ تَرْقُبْ قَوْلِيْ ','Harun menjawab´ "Hai putera ibuku, janganlah kamu pegang janggutku dan jangan (pula) kepalaku; sesungguhnya aku khawatir bahwa kamu akan berkata (kepadaku): "Kamu telah memecah antara Bani Israil dan kamu tidak memelihara amanatku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,95,'قَالَ فَمَا خَطْبُكَ يٰسَامِرِيُّ ','Berkata Musa: "Apakah yang mendorongmu (berbuat demikian) hai Samiri?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,96,'قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوْا بِهٖ فَقَبَضْتُ قَبْضَةً مِّنْ اَثَرِ الرَّسُوْلِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ لِيْ نَفْسِيْ ','Samiri menjawab: "Aku mengetahui sesuatu yang mereka tidak mengetahuinya, maka aku ambil segenggam dari jejak rasul lalu aku melemparkannya, dan demikianlah nafsuku membujukku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,97,'قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِى الْحَيٰوةِ اَنْ تَقُوْلَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِدًا لَّنْ تُخْلَفَهٗۚ وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذِيْ ظَلْتَ عَلَيْهِ عَاكِفًا ۗ  لَنُحَرِّقَنَّهٗ ثُمَّ لَنَنْسِفَنَّهٗ فِى الْيَمِّ نَسْفًا ','Berkata Musa: "Pergilah kamu, maka sesungguhnya bagimu di dalam kehidupan di dunia ini (hanya dapat) mengatakan: "Janganlah menyentuh (aku)". Dan sesungguhnya bagimu hukuman (di akhirat) yang kamu sekali-kali tidak dapat menghindarinya, dan lihatlah tuhanmu itu yang kamu tetap menyembahnya. Sesungguhnya kami akan membakarnya, kemudian kami sungguh-sungguh akan menghamburkannya ke dalam laut (berupa abu yang berserakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,98,'اِنَّمَآ اِلٰهُكُمُ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۗ وَسِعَ كُلَّ شَيْءٍ عِلْمًا ','Sesungguhnya Tuhanmu hanyalah Allah, yang tidak ada Tuhan selain Dia. Pengetahuan-Nya meliputi segala sesuatu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,99,'كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنٰكَ مِنْ لَّدُنَّا ذِكْرًا ۚ','Demikianlah kami kisahkan kepadamu (Muhammad) sebagian kisah umat yang telah lalu, dan sesungguhnya telah Kami berikan kepadamu dari sisi Kami suatu peringatan (Al Quran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,100,'مَنْ اَعْرَضَ عَنْهُ فَاِنَّهٗ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْرًا ','Barangsiapa berpaling dari pada Al qur´an maka sesungguhnya ia akan memikul dosa yang besar di hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,101,'خٰلِدِيْنَ فِيْهِ ۗوَسَاۤءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلًاۙ ','mereka kekal di dalam keadaan itu. Dan amat buruklah dosa itu sebagai beban bagi mereka di hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,102,'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ وَنَحْشُرُ الْمُجْرِمِيْنَ يَوْمَىِٕذٍ زُرْقًا ۖ','(yaitu) di hari (yang di waktu itu) ditiup sangkakala dan Kami akan mengumpulkan pada hari itu orang-orang yang berdosa dengan muka yang biru muram;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,103,'يَّتَخَافَتُوْنَ بَيْنَهُمْ اِنْ لَّبِثْتُمْ اِلَّا عَشْرًا ','mereka berbisik-bisik di antara mereka: "Kamu tidak berdiam (di dunia) melainkan hanyalah sepuluh (hari)"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,104,'نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ اِذْ يَقُوْلُ اَمْثَلُهُمْ طَرِيْقَةً اِنْ لَّبِثْتُمْ اِلَّا يَوْمًا ࣖ','Kami lebih mengetahui apa yang mereka katakan, ketika berkata orang yang paling lurus jalannya di antara mereka: "Kamu tidak berdiam (di dunia), melainkan hanyalah sehari saja".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,105,'وَيَسْـَٔلُوْنَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّيْ نَسْفًا ۙ','Dan mereka bertanya kepadamu tentang gunung-gunung, maka katakanlah: "Tuhanku akan menghancurkannya (di hari kiamat) sehancur-hancurnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,106,'فَيَذَرُهَا قَاعًا صَفْصَفًا ۙ','maka Dia akan menjadikan (bekas) gunung-gunung itu datar sama sekali,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,107,'لَّا تَرٰى فِيْهَا عِوَجًا وَّلَآ اَمْتًا ۗ','tidak ada sedikitpun kamu lihat padanya tempat yang rendah dan yang tinggi-tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,108,'يَوْمَىِٕذٍ يَّتَّبِعُوْنَ الدَّاعِيَ لَا عِوَجَ لَهٗ ۚوَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْسًا ','Pada hari itu manusia mengikuti (menuju kepada suara) penyeru dengan tidak berbelok-belok; dan merendahlah semua suara kepada Tuhan Yang Maha Pemurah, maka kamu tidak mendengar kecuali bisikan saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,109,'يَوْمَىِٕذٍ لَّا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهٗ قَوْلًا ','Pada hari itu tidak berguna syafa´at, kecuali (syafa´at) orang yang Allah Maha Pemurah telah memberi izin kepadanya, dan Dia telah meridhai perkataannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,110,'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيْطُوْنَ بِهٖ عِلْمًا ','Dia mengetahui apa yang ada di hadapan mereka dan apa yang ada di belakang mereka, sedang ilmu mereka tidak dapat meliputi ilmu-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,111,'۞ وَعَنَتِ الْوُجُوْهُ لِلْحَيِّ الْقَيُّوْمِۗ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا ','Dan tunduklah semua muka (dengan berendah diri) kepada Tuhan Yang Hidup Kekal lagi senantiasa mengurus (makhluk-Nya). Dan sesungguhnya telah merugilah orang yang melakukan kezaliman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,112,'وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا يَخٰفُ ظُلْمًا وَّلَا هَضْمًا ','Dan barangsiapa mengerjakan amal-amal yang saleh dan ia dalam keadaan beriman, maka ia tidak khawatir akan perlakuan yang tidak adil (terhadapnya) dan tidak (pula) akan pengurangan haknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,113,'وَكَذٰلِكَ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا وَّصَرَّفْنَا فِيْهِ مِنَ الْوَعِيْدِ لَعَلَّهُمْ يَتَّقُوْنَ اَوْ يُحْدِثُ لَهُمْ ذِكْرًا ','Dan demikianlah Kami menurunkan Al Quran dalam bahasa Arab, dan Kami telah menerangkan dengan berulang kali, di dalamnya sebahagian dari ancaman, agar mereka bertakwa atau (agar) Al Quran itu menimbulkan pengajaran bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,114,'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُّقْضٰٓى اِلَيْكَ وَحْيُهٗ ۖوَقُلْ رَّبِّ زِدْنِيْ عِلْمًا ','Maka Maha Tinggi Allah Raja Yang sebenar-benarnya, dan janganlah kamu tergesa-gesa membaca Al qur´an sebelum disempurnakan mewahyukannya kepadamu, dan katakanlah: "Ya Tuhanku, tambahkanlah kepadaku ilmu pengetahuan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,115,'وَلَقَدْ عَهِدْنَآ اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهٗ عَزْمًا ࣖ','Dan sesungguhnya telah Kami perintahkan kepada Adam dahulu, maka ia lupa (akan perintah itu), dan tidak Kami dapati padanya kemauan yang kuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,116,'وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَ اَبٰى ۗ','Dan (ingatlah) ketika Kami berkata kepada malaikat: "Sujudlah kamu kepada Adam", maka mereka sujud kecuali iblis. Ia membangkang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,117,'فَقُلْنَا يٰٓاٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى ','Maka Kami berkata: "Hai Adam, sesungguhnya ini (iblis) adalah musuh bagimu dan bagi isterimu, maka sekali-kali janganlah sampai ia mengeluarkan kamu berdua dari surga, yang menyebabkan kamu menjadi celaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,118,'اِنَّ لَكَ اَلَّا تَجُوْعَ فِيْهَا وَلَا تَعْرٰى ۙ','Sesungguhnya kamu tidak akan kelaparan di dalamnya dan tidak akan telanjang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,119,'وَاَنَّكَ لَا تَظْمَؤُا فِيْهَا وَلَا تَضْحٰى ','dan sesungguhnya kamu tidak akan merasa dahaga dan tidak (pula) akan ditimpa panas matahari di dalamnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,120,'فَوَسْوَسَ اِلَيْهِ الشَّيْطٰنُ قَالَ يٰٓاٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَّا يَبْلٰى ','Kemudian syaitan membisikkan pikiran jahat kepadanya, dengan berkata: "Hai Adam, maukah saya tunjukkan kepada kamu pohon khuldi dan kerajaan yang tidak akan binasa?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,121,'فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۚ وَعَصٰٓى اٰدَمُ رَبَّهٗ فَغَوٰى ۖ','Maka keduanya memakan dari buah pohon itu, lalu nampaklah bagi keduanya aurat-auratnya dan mulailah keduanya menutupinya dengan daun-daun (yang ada di) surga, dan durhakalah Adam kepada Tuhan dan sesatlah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,122,'ثُمَّ اجْتَبٰىهُ رَبُّهٗ فَتَابَ عَلَيْهِ وَهَدٰى ','Kemudian Tuhannya memilihnya maka Dia menerima taubatnya dan memberinya petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,123,'قَالَ اهْبِطَا مِنْهَا جَمِيعًاۢ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚفَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى ەۙ فَمَنِ اتَّبَعَ هُدٰيَ فَلَا يَضِلُّ وَلَا يَشْقٰى','Allah berfirman: "Turunlah kamu berdua dari surga bersama-sama, sebagian kamu menjadi musuh bagi sebagian yang lain. Maka jika datang kepadamu petunjuk daripada-Ku, lalu barangsiapa yang mengikut petunjuk-Ku, ia tidak akan sesat dan tidak akan celaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,124,'وَمَنْ اَعْرَضَ عَنْ ذِكْرِيْ فَاِنَّ لَهٗ مَعِيْشَةً ضَنْكًا وَّنَحْشُرُهٗ يَوْمَ الْقِيٰمَةِ اَعْمٰى ','Dan barangsiapa berpaling dari peringatan-Ku, maka sesungguhnya baginya penghidupan yang sempit, dan Kami akan menghimpunkannya pada hari kiamat dalam keadaan buta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,125,'قَالَ رَبِّ لِمَ حَشَرْتَنِيْٓ اَعْمٰى وَقَدْ كُنْتُ بَصِيْرًا ','Berkatalah ia: "Ya Tuhanku, mengapa Engkau menghimpunkan aku dalam keadaan buta, padahal aku dahulunya adalah seorang yang melihat?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,126,'قَالَ كَذٰلِكَ اَتَتْكَ اٰيٰتُنَا فَنَسِيْتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى ','Allah berfirman: "Demikianlah, telah datang kepadamu ayat-ayat Kami, maka kamu melupakannya, dan begitu (pula) pada hari ini kamupun dilupakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,127,'وَكَذٰلِكَ نَجْزِيْ مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْۢ بِاٰيٰتِ رَبِّهٖۗ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى ','Dan demikianlah Kami membalas orang yang melampaui batas dan tidak percaya kepada ayat-ayat Tuhannya. Dan sesungguhnya azab di akhirat itu lebih berat dan lebih kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,128,'اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى ࣖ','Maka tidakkah menjadi petunjuk bagi mereka (kaum musyrikin) berapa banyaknya Kami membinasakan umat-umat sebelum mereka, padahal mereka berjalan (di bekas-bekas) tempat tinggal umat-umat itu? Sesungguhnya pada yang demikian itu terdapat tanda-tanda bagi orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,129,'وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَكَانَ لِزَامًا وَّاَجَلٌ مُّسَمًّى ۗ','Dan sekiranya tidak ada suatu ketetapan dari Allah yang telah terdahulu atau tidak ada ajal yang telah ditentukan, pasti (azab itu) menimpa mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,130,'فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ غُرُوْبِهَا ۚوَمِنْ اٰنَاۤئِ الَّيْلِ فَسَبِّحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى','Maka sabarlah kamu atas apa yang mereka katakan, dan bertasbihlah dengan memuji Tuhanmu, sebelum terbit matahari dan sebelum terbenamnya dan bertasbih pulalah pada waktu-waktu di malam hari dan pada waktu-waktu di siang hari, supaya kamu merasa senang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,131,'وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا ەۙ لِنَفْتِنَهُمْ فِيْهِ ۗوَرِزْقُ رَبِّكَ خَيْرٌ وَّاَبْقٰى','Dan janganlah kamu tujukan kedua matamu kepada apa yang telah Kami berikan kepada golongan-golongan dari mereka, sebagai bunga kehidupan dunia untuk Kami cobai mereka dengannya. Dan karunia Tuhan kamu adalah lebih baik dan lebih kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,132,'وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۗ  لَا نَسْـَٔلُكَ رِزْقًاۗ نَحْنُ نَرْزُقُكَۗ وَالْعَاقِبَةُ لِلتَّقْوٰى ','Dan perintahkanlah kepada keluargamu mendirikan shalat dan bersabarlah kamu dalam mengerjakannya. Kami tidak meminta rezeki kepadamu, Kamilah yang memberi rezeki kepadamu. Dan akibat (yang baik) itu adalah bagi orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,133,'وَقَالُوْا لَوْلَا يَأْتِيْنَا بِاٰيَةٍ مِّنْ رَّبِّهٖۗ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِى الصُّحُفِ الْاُولٰى ','Dan mereka berkata: "Mengapa ia tidak membawa bukti kepada kami dari Tuhannya?" Dan apakah belum datang kepada mereka bukti yang nyata dari apa yang tersebut di dalam kitab-kitab yang dahulu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,134,'وَلَوْ اَنَّآ اَهْلَكْنٰهُمْ بِعَذَابٍ مِّنْ قَبْلِهٖ لَقَالُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ مِنْ قَبْلِ اَنْ نَّذِلَّ وَنَخْزٰى ','Dan sekiranya Kami binasakan mereka dengan suatu azab sebelum Al Quran itu (diturunkan), tentulah mereka berkata: "Ya Tuhan kami, mengapa tidak Engkau utus seorang rasul kepada kami, lalu kami mengikuti ayat-ayat Engkau sebelum kami menjadi hina dan rendah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,16,20,135,'قُلْ كُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوْاۚ فَسَتَعْلَمُوْنَ مَنْ اَصْحٰبُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى ࣖ    ۔','Katakanlah: "Masing-masing (kita) menanti, maka nantikanlah oleh kamu sekalian! Maka kamu kelak akan mengetahui, siapa yang menempuh jalan yang lurus dan siapa yang telah mendapat petunjuk".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,1,'اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِيْ غَفْلَةٍ مُّعْرِضُوْنَ ۚ ','Telah dekat kepada manusia hari menghisab segala amalan mereka, sedang mereka berada dalam kelalaian lagi berpaling (daripadanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,2,'مَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنْ رَّبِّهِمْ مُّحْدَثٍ اِلَّا اسْتَمَعُوْهُ وَهُمْ يَلْعَبُوْنَ ۙ','Tidak datang kepada mereka suatu ayat Al Quran pun yang baru (di-turunkan) dari Tuhan mereka, melainkan mereka mendengarnya, sedang mereka bermain-main,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,3,'لَاهِيَةً قُلُوْبُهُمْۗ وَاَسَرُّوا النَّجْوَىۖ الَّذِيْنَ ظَلَمُوْاۖ هَلْ هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۚ اَفَتَأْتُوْنَ السِّحْرَ وَاَنْتُمْ تُبْصِرُوْنَ ','(lagi) hati mereka dalam keadaan lalai. Dan mereka yang zalim itu merahasiakan pembicaraan mereka: "Orang ini tidak lain hanyalah seorang manusia (jua) seperti kamu, maka apakah kamu menerima sihir itu, padahal kamu menyaksikannya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,4,'قٰلَ رَبِّيْ يَعْلَمُ الْقَوْلَ فِى السَّمَاۤءِ وَالْاَرْضِۖ وَهُوَ السَّمِيْعُ الْعَلِيْمُ ','Berkatalah Muhammad (kepada mereka): "Tuhanku mengetahui semua perkataan di langit dan di bumi dan Dialah Yang Maha Mendengar lagi Maha Mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,5,'بَلْ قَالُوْٓا اَضْغَاثُ اَحْلَامٍۢ بَلِ افْتَرٰىهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَآ اُرْسِلَ الْاَوَّلُوْنَ ','Bahkan mereka berkata (pula): "(Al Quran itu adalah) mimpi-mimpi yang kalut, malah diada-adakannya, bahkan dia sendiri seorang penyair, maka hendaknya ia mendatangkan kepada kita suatu mukjizat, sebagai-mana rasul-rasul yang telah lalu di-utus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,6,'مَآ اٰمَنَتْ قَبْلَهُمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَاۚ اَفَهُمْ يُؤْمِنُوْنَ ','Tidak ada (penduduk) suatu negeripun yang beriman yang Kami telah membinasakannya sebeIum mereka; maka apakah mereka akan beriman?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,7,'وَمَآ اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَ ','Kami tiada mengutus rasul rasul sebelum kamu (Muhammad), melainkan beberapa orang-laki-laki yang Kami beri wahyu kepada mereka, maka tanyakanlah olehmu kepada orang-orang yang berilmu, jika kamu tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,8,'وَمَا جَعَلْنٰهُمْ جَسَدًا لَّا يَأْكُلُوْنَ الطَّعَامَ وَمَا كَانُوْا خٰلِدِيْنَ ','Dan tidaklah Kami jadikan mereka tubuh-tubuh yang tiada memakan makanan, dan tidak (pula) mereka itu orang-orang yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,9,'ثُمَّ صَدَقْنٰهُمُ الْوَعْدَ فَاَنْجَيْنٰهُمْ وَمَنْ نَّشَاۤءُ وَاَهْلَكْنَا الْمُسْرِفِيْنَ ','Kemudian Kami tepati janji (yang telah Kami janjikan) kepada mereka. Maka Kami selamatkan mereka dan orang-orang yang Kami kehendaki dan Kami binasakan orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,10,'لَقَدْ اَنْزَلْنَآ اِلَيْكُمْ كِتٰبًا فِيْهِ ذِكْرُكُمْۗ اَفَلَا تَعْقِلُوْنَ ࣖ','Sesungguhnya telah Kami turunkan kepada kamu sebuah kitab yang di dalamnya terdapat sebab-sebab kemuliaan bagimu. Maka apakah kamu tiada memahaminya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,11,'وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَّاَنْشَأْنَا بَعْدَهَا قَوْمًا اٰخَرِيْنَ ','Dan berapa banyaknya (penduduk) negeri yang zalim yang teIah Kami binasakan, dan Kami adakan sesudah mereka itu kaum yang lain (sebagai penggantinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,12,'فَلَمَّآ اَحَسُّوْا بَأْسَنَآ اِذَا هُمْ مِّنْهَا يَرْكُضُوْنَ ۗ','Maka tatkala mereka merasakan azab Kami, tiba-tiba mereka melarikan diri dari negerinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,13,'لَا تَرْكُضُوْا وَارْجِعُوْٓا اِلٰى مَآ اُتْرِفْتُمْ فِيْهِ وَمَسٰكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُوْنَ ','Janganlah kamu lari tergesa-gesa; kembalilah kamu kepada nikmat yang telah kamu rasakan dan kepada tempat-tempat kediamanmu (yang baik), supaya kamu ditanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,14,'قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ','Mereka berkata: "Aduhai, celaka kami, sesungguhnya kami adalah orang-orang yang zaIim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,15,'فَمَا زَالَتْ تِّلْكَ دَعْوٰىهُمْ حَتّٰى جَعَلْنٰهُمْ حَصِيْدًا خَامِدِيْنَ ','Maka tetaplah demikian keluhan mereka, sehingga Kami jadikan mereka sebagai tanaman yang telah dituai, yang tidak dapat hidup lagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,16,'وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ ','Dan tidaklah Kami ciptakan Iangit dan bumi dan segala yang ada di antara keduanya dengan bermain-main.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,17,'لَوْ اَرَدْنَآ اَنْ نَّتَّخِذَ لَهْوًا لَّاتَّخَذْنٰهُ مِنْ لَّدُنَّآ  ۖاِنْ كُنَّا فٰعِلِيْنَ ','Sekiranya Kami hendak membuat sesuatu permainan, (isteri dan anak), tentulah Kami membuatnya dari sisi Kami. Jika Kami menghendaki berbuat demikian, (tentulah Kami telah melakukannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,18,'بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهٗ فَاِذَا هُوَ زَاهِقٌۗ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُوْنَ ','Sebenarya Kami melontarkan yang hak kepada yang batil lalu yang hak itu menghancurkannya, maka dengan serta merta yang batil itu lenyap. Dan kecelakaanlah bagimu disebabkan kamu mensifati (Allah dengan sifat-sifat yang tidak layak bagi-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,19,'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَمَنْ عِنْدَهٗ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَلَا يَسْتَحْسِرُوْنَ ۚ','Dan kepunyaan-Nya-lah segala yang di langit dan di bumi. Dan malaikat-malaikat yang di sisi-Nya, mereka tiada mempunyai rasa angkuh untuk menyembah-Nya dan tiada (pula) merasa letih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,20,'يُسَبِّحُوْنَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُوْنَ ','Mereka selalu bertasbih malam dan siang tiada henti-hentinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,21,'اَمِ اتَّخَذُوْٓا اٰلِهَةً مِّنَ الْاَرْضِ هُمْ يُنْشِرُوْنَ ','Apakah mereka mengambil tuhan-tuhan dari bumi, yang dapat menghidupkan (orang-orang mati)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,22,'لَوْ كَانَ فِيْهِمَآ اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحٰنَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ ','Sekiranya ada di langit dan di bumi tuhan-tuhan selain Allah, tentulah keduanya itu telah rusak binasa. Maka Maha Suci Allah yang mempunyai ´Arsy daripada apa yang mereka sifatkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,23,'لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُوْنَ ','Dia tidak ditanya tentang apa yang diperbuat-Nya dan merekalah yang akan ditanyai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,24,'اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً ۗقُلْ هَاتُوْا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَّعِيَ وَذِكْرُ مَنْ قَبْلِيْۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَۙ الْحَقَّ فَهُمْ مُّعْرِضُوْنَ ','Apakah mereka mengambil tuhan-tuhan selain-Nya? Katakanlah: "Unjukkanlah hujjahmu! (Al Quran) ini adalah peringatan bagi orang-orang yang bersamaku, dan peringatan bagi orang-orang yang sebelumku". Sebenarnya kebanyakan mereka tiada mengetahui yang hak, karena itu mereka berpaling.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,25,'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ اِلَّا نُوْحِيْٓ اِلَيْهِ اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدُوْنِ ','Dan Kami tidak mengutus seorang rasulpun sebelum kamu melainkan Kami wahyukan kepadanya: "Bahwasanya tidak ada Tuhan (yang hak) melainkan Aku, maka sembahlah olehmu sekalian akan Aku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,26,'وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا سُبْحٰنَهٗ ۗبَلْ عِبَادٌ مُّكْرَمُوْنَ ۙ','Dan mereka berkata: "Tuhan Yang Maha Pemurah telah mengambil (mempunyai) anak", Maha Suci Allah. Sebenarnya (malaikat-malaikat itu), adalah hamba-hamba yang dimuliakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,27,'لَا يَسْبِقُوْنَهٗ بِالْقَوْلِ وَهُمْ بِاَمْرِهٖ يَعْمَلُوْنَ ','mereka itu tidak mendahului-Nya dengan perkataan dan mereka mengerjakan perintah-perintah-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,28,'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُوْنَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِّنْ خَشْيَتِهٖ مُشْفِقُوْنَ ','Allah mengetahui segala sesuatu yang dihadapan mereka (malaikat) dan yang di belakang mereka, dan mereka tiada memberi syafa´at melainkan kepada orang yang diridhai Allah, dan mereka itu selalu berhati-hati karena takut kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,29,'۞ وَمَنْ يَّقُلْ مِنْهُمْ اِنِّيْٓ اِلٰهٌ مِّنْ دُوْنِهٖ فَذٰلِكَ نَجْزِيْهِ جَهَنَّمَۗ  كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ ࣖ','Dan barangsiapa di antara mereka, mengatakan: "Sesungguhnya Aku adalah tuhan selain daripada Allah", maka orang itu Kami beri balasan dengan Jahannam, demikian Kami memberikan pembalasan kepada orang-orang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,30,'اَوَلَمْ يَرَ الَّذِيْنَ كَفَرُوْٓا اَنَّ السَّمٰوٰتِ وَالْاَرْضَ كَانَتَا رَتْقًا فَفَتَقْنٰهُمَاۗ وَجَعَلْنَا مِنَ الْمَاۤءِ كُلَّ شَيْءٍ حَيٍّۗ  اَفَلَا يُؤْمِنُوْنَ ','Dan apakah orang-orang yang kafir tidak mengetahui bahwasanya langit dan bumi itu keduanya dahulu adalah suatu yang padu, kemudian Kami pisahkan antara keduanya. Dan dari air Kami jadikan segala sesuatu yang hidup. Maka mengapakah mereka tiada juga beriman?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,31,'وَجَعَلْنَا فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِهِمْۖ وَجَعَلْنَا فِيْهَا فِجَاجًا سُبُلًا لَّعَلَّهُمْ يَهْتَدُوْنَ ','Dan telah Kami jadikan di bumi ini gunung-gunung yang kokoh supaya bumi itu (tidak) goncang bersama mereka dan telah Kami jadikan (pula) di bumi itu jalan-jalan yang luas, agar mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,32,'وَجَعَلْنَا السَّمَاۤءَ سَقْفًا مَّحْفُوْظًاۚ وَهُمْ عَنْ اٰيٰتِهَا مُعْرِضُوْنَ ','Dan Kami menjadikan langit itu sebagai atap yang terpelihara, sedang mereka berpaling dari segala tanda-tanda (kekuasaan Allah) yang terdapat padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,33,'وَهُوَ الَّذِيْ خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۗ  كُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ ','Dan Dialah yang telah menciptakan malam dan siang, matahari dan bulan. Masing-masing dari keduanya itu beredar di dalam garis edarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,34,'وَمَا جَعَلْنَا لِبَشَرٍ مِّنْ قَبْلِكَ الْخُلْدَۗ اَفَا۟ىِٕنْ مِّتَّ فَهُمُ الْخٰلِدُوْنَ ','Kami tidak menjadikan hidup abadi bagi seorang manusiapun sebelum kamu (Muhammad); maka jikalau kamu mati, apakah mereka akan kekal?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,35,'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَنَبْلُوْكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۗوَاِلَيْنَا تُرْجَعُوْنَ ','Tiap-tiap yang berjiwa akan merasakan mati. Kami akan menguji kamu dengan keburukan dan kebaikan sebagai cobaan (yang sebenar-benarnya). Dan hanya kepada Kamilah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,36,'وَاِذَا رَاٰكَ الَّذِيْنَ كَفَرُوْٓا اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كٰفِرُوْنَ  ','Dan apahila orang-orang kafir itu melihat kamu, mereka hanya membuat kamu menjadi olok-olok. (Mereka mengatakan): "Apakah ini orang yang mencela tuhan-tuhan-mu?", padahal mereka adaIah orang-orang yang ingkar mengingat Allah Yang Maha Pemurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,37,'خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۗ سَاُورِيْكُمْ اٰيٰتِيْ فَلَا تَسْتَعْجِلُوْنِ ','Manusia telah dijadikan (bertabiat) tergesa-gesa. Kelak akan Aku perIihatkan kepadamu tanda-tanda azab-Ku. Maka janganlah kamu minta kepada-Ku mendatangkannya dengan segera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,38,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Mereka berkata: "Kapankah janji itu akan datang, jika kamu sekaIian adalah orang-orang yang benar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,39,'لَوْ يَعْلَمُ الَّذِيْنَ كَفَرُوْا حِيْنَ لَا يَكُفُّوْنَ عَنْ وُّجُوْهِهِمُ النَّارَ وَلَا عَنْ ظُهُوْرِهِمْ وَلَا هُمْ يُنْصَرُوْنَ ','Andaikata orang-orang kafir itu mengetahui, waktu (di mana) mereka itu tidak mampu mengelakkan api neraka dari muka mereka dan (tidak pula) dari punggung mereka, sedang mereka (tidak pula) mendapat pertolongan, (tentulah mereka tiada meminta disegerakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,40,'بَلْ تَأْتِيْهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيْعُوْنَ رَدَّهَا وَلَا هُمْ يُنْظَرُوْنَ ','Sebenarnya (azab) itu akan datang kepada mereka dengan sekonyong-konyong lalu membuat mereka menjadi panik, maka mereka tidak sanggup menolaknya dan tidak (pula) mereka diberi tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,41,'وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ','Dan sungguh telah diperolok-olokkan beberapa orang rasul sebelum kamu maka turunlah kepada orang yang mencemoohkan rasul-rasul itu azab yang selalu mereka perolok-olokkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,42,'قُلْ مَنْ يَّكْلَؤُكُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۗ  بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُّعْرِضُوْنَ ','Katakanlah: "Siapakah yang dapat memelihara kamu di waktu malam dan siang hari dari (azab Allah) Yang Maha Pemurah?" Sebenarnya mereka adalah orang-orang yang berpaling dari mengingati Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,43,'اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِّنْ دُوْنِنَاۗ  لَا يَسْتَطِيْعُوْنَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِّنَّا يُصْحَبُوْنَ','Atau adakah mereka mempunyai tuhan-tuhan yang dapat memelihara mereka dari (azab) Kami. Tuhan-tuhan itu tidak sanggup menolong diri mereka sendiri dan tidak (pula) mereka dilindungi dari (azab) Kami itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,44,'بَلْ مَتَّعْنَا هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۗ اَفَلَا يَرَوْنَ اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ اَفَهُمُ الْغٰلِبُوْنَ ','Sebenarnya Kami telah memberi mereka dan bapak-bapak mereka kenikmatan (hidup di dunia) hingga panjanglah umur mereka. Maka apakah mereka tidak melihat bahwasanya Kami mendatangi negeri (orang kafir), lalu Kami kurangi luasnya dari segala penjurunya. Maka apakah mereka yang menang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,45,'قُلْ اِنَّمَآ اُنْذِرُكُمْ بِالْوَحْيِۖ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاۤءَ اِذَا مَا يُنْذَرُوْنَ ','Katakanlah (hai Muhammad): "Sesungguhnya aku hanya memberi peringatan kepada kamu sekalian dengan wahyu dan tiadalah orang-orang yang tuli mendengar seruan, apabila mereka diberi peringatan"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,46,'وَلَىِٕنْ مَّسَّتْهُمْ نَفْحَةٌ مِّنْ عَذَابِ رَبِّكَ لَيَقُوْلُنَّ يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ','Dan sesungguhnya, jika mereka ditimpa sedikit saja dari azab Tuhan-mu, pastilah mereka berkata: "Aduhai, celakalah kami, bahwasanya kami adalah orang yang menganiaya diri sendiri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,47,'وَنَضَعُ الْمَوَازِيْنَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـًٔاۗ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ اَتَيْنَا بِهَاۗ وَكَفٰى بِنَا حَاسِبِيْنَ ','Kami akan memasang timbangan yang tepat pada hari kiamat, maka tiadalah dirugikan seseorang barang sedikitpun. Dan jika (amalan itu) hanya seberat biji sawipun pasti Kami mendatangkan (pahala)nya. Dan cukuplah Kami sebagai pembuat perhitungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,48,'وَلَقَدْ اٰتَيْنَا مُوْسٰى وَهٰرُوْنَ الْفُرْقَانَ وَضِيَاۤءً وَّذِكْرًا لِّلْمُتَّقِيْنَ ۙ','Dan sesungguhnya telah Kami berikan kepada Musa dan Harun Kitab Taurat dan penerangan serta pengajaran bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,49,'الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِّنَ السَّاعَةِ مُشْفِقُوْنَ ','(yaitu) orang-orang yang takut akan (azab) Tuhan mereka, sedang mereka tidak melihat-Nya, dan mereka merasa takut akan (tibanya) hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,50,'وَهٰذَا ذِكْرٌ مُّبٰرَكٌ اَنْزَلْنٰهُۗ اَفَاَنْتُمْ لَهٗ مُنْكِرُوْنَ ࣖ','Dan Al Quran ini adalah suatu kitab (peringatan) yang mempunyai berkah yang telah Kami turunkan. Maka mengapakah kamu mengingkarinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,51,'۞ وَلَقَدْ اٰتَيْنَآ اِبْرٰهِيْمَ رُشْدَهٗ مِنْ قَبْلُ وَكُنَّا بِهٖ عٰلِمِيْنَ ','Dan sesungguhnya telah Kami anugerahkan kepada Ibrahim hidayah kebenaran sebelum (Musa dan Harun), dan adalah Kami mengetahui (keadaan)nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,52,'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا هٰذِهِ التَّمَاثِيْلُ الَّتِيْٓ اَنْتُمْ لَهَا عَاكِفُوْنَ ','(Ingatlah), ketika Ibrahim berkata kepada bapaknya dan kaumnya: "Patung-patung apakah ini yang kamu tekun beribadat kepadanya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,53,'قَالُوْا وَجَدْنَآ اٰبَاۤءَنَا لَهَا عٰبِدِيْنَ ','Mereka menjawab: "Kami mendapati bapak-bapak kami menyembahnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,54,'قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَاۤؤُكُمْ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Ibrahim berkata: "Sesungguhnya kamu dan bapak-bapakmu berada dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,55,'قَالُوْٓا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللّٰعِبِيْنَ ','Mereka menjawab: "Apakah kamu datang kepada kami dengan sungguh-sungguh ataukah kamu termasuk orang-orang yang bermain-main?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,56,'قَالَ بَلْ رَّبُّكُمْ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ الَّذِيْ فَطَرَهُنَّۖ وَاَنَا۠ عَلٰى ذٰلِكُمْ مِّنَ الشّٰهِدِيْنَ ','Ibrahim berkata: "Sebenarnya Tuhan kamu ialah Tuhan langit dan bumi yang telah menciptakannya: dan aku termasuk orang-orang yang dapat memberikan bukti atas yang demikian itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,57,'وَتَاللّٰهِ لَاَكِيْدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوْا مُدْبِرِيْنَ ','Demi Allah, sesungguhnya aku akan melakukan tipu daya terhadap berhala-berhalamu sesudah kamu pergi meninggalkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,58,'فَجَعَلَهُمْ جُذَاذًا اِلَّا كَبِيْرًا لَّهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُوْنَ ','Maka Ibrahim membuat berhala-berhala itu hancur berpotong-potong, kecuali yang terbesar (induk) dari patung-patung yang lain; agar mereka kembali (untuk bertanya) kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,59,'قَالُوْا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَآ اِنَّهٗ لَمِنَ الظّٰلِمِيْنَ ','Mereka berkata: "Siapakah yang melakukan perbuatan ini terhadap tuhan-tuhan kami, sesungguhnya dia termasuk orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,60,'قَالُوْا سَمِعْنَا فَتًى يَّذْكُرُهُمْ يُقَالُ لَهٗٓ اِبْرٰهِيْمُ ۗ','Mereka berkata: "Kami dengar ada seorang pemuda yang mencela berhala-berhala ini yang bernama Ibrahim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,61,'قَالُوْا فَأْتُوْا بِهٖ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُوْنَ ','Mereka berkata: "(Kalau demikian) bawalah dia dengan cara yang dapat dilihat orang banyak, agar mereka menyaksikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,62,'قَالُوْٓا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يٰٓاِبْرٰهِيْمُ ۗ','Mereka bertanya: "Apakah kamu, yang melakukan perbuatan ini terhadap tuhan-tuhan kami, hai Ibrahim?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,63,'قَالَ بَلْ فَعَلَهٗ كَبِيْرُهُمْ هٰذَا فَسْـَٔلُوْهُمْ اِنْ كَانُوْا يَنْطِقُوْنَ ','Ibrahim menjawab: "Sebenarnya patung yang besar itulah yang melakukannya, maka tanyakanlah kepada berhala itu, jika mereka dapat berbicara".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,64,'فَرَجَعُوْٓا اِلٰٓى اَنْفُسِهِمْ فَقَالُوْٓا اِنَّكُمْ اَنْتُمُ الظّٰلِمُوْنَ ۙ','Maka mereka telah kembali kepada kesadaran dan lalu berkata: "Sesungguhnya kamu sekalian adalah orang-orang yang menganiaya (diri sendiri)",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,65,'ثُمَّ نُكِسُوْا عَلٰى رُءُوْسِهِمْۚ  لَقَدْ عَلِمْتَ مَا هٰٓؤُلَاۤءِ يَنْطِقُوْنَ ','kemudian kepala mereka jadi tertunduk (lalu berkata): "Sesungguhnya kamu (hai Ibrahim) telah mengetahui bahwa berhala-berhala itu tidak dapat berbicara".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,66,'قَالَ اَفَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـًٔا وَّلَا يَضُرُّكُمْ ۗ','Ibrahim berkata: Maka mengapakah kamu menyembah selain Allah sesuatu yang tidak dapat memberi manfaat sedikitpun dan tidak (pula) memberi mudharat kepada kamu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,67,'اُفٍّ لَّكُمْ وَلِمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَفَلَا تَعْقِلُوْنَ ','Ah (celakalah) kamu dan apa yang kamu sembah selain Allah. Maka apakah kamu tidak memahami?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,68,'قَالُوْا حَرِّقُوْهُ وَانْصُرُوْٓا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فٰعِلِيْنَ ','Mereka berkata: "Bakarlah dia dan bantulah tuhan-tuhan kamu, jika kamu benar-benar hendak bertindak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,69,'قُلْنَا يَا نَارُ كُوْنِيْ بَرْدًا وَّسَلٰمًا عَلٰٓى اِبْرٰهِيْمَ ۙ','Kami berfirman: "Hai api menjadi dinginlah, dan menjadi keselamatanlah bagi Ibrahim",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,70,'وَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَخْسَرِيْنَ ۚ','mereka hendak berbuat makar terhadap Ibrahim, maka Kami menjadikan mereka itu orang-orang yang paling merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,71,'وَنَجَّيْنٰهُ وَلُوْطًا اِلَى الْاَرْضِ الَّتِيْ بٰرَكْناَ فِيْهَا لِلْعٰلَمِيْنَ ','Dan Kami seIamatkan Ibrahim dan Luth ke sebuah negeri yang Kami telah memberkahinya untuk sekalian manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,72,'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ نَافِلَةً ۗوَكُلًّا جَعَلْنَا صٰلِحِيْنَ ','Dan Kami telah memberikan kepada-nya (Ibrahim) lshak dan Ya´qub, sebagai suatu anugerah (daripada Kami). Dan masing-masingnya Kami jadikan orang-orang yang saleh')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,73,'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا وَاَوْحَيْنَآ اِلَيْهِمْ فِعْلَ الْخَيْرٰتِ وَاِقَامَ الصَّلٰوةِ وَاِيْتَاۤءَ الزَّكٰوةِۚ وَكَانُوْا لَنَا عٰبِدِيْنَ ۙ','Kami telah menjadikan mereka itu sebagai pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami dan telah Kami wahyukan kepada, mereka mengerjakan kebajikan, mendirikan sembahyang, menunaikan zakat, dan hanya kepada Kamilah mereka selalu menyembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,74,'وَلُوْطًا اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا وَّنَجَّيْنٰهُ مِنَ الْقَرْيَةِ الَّتِيْ كَانَتْ تَّعْمَلُ الْخَبٰۤىِٕثَ ۗاِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فٰسِقِيْنَۙ','dan kepada Luth, Kami telah berikan hikmah dan ilmu, dan telah Kami selamatkan dia dari (azab yang telah menimpa penduduk) kota yang mengerjakan perbuatan keji. Sesungguhnya mereka adalah kaum yang jahat lagi fasik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,75,'وَاَدْخَلْنٰهُ فِيْ رَحْمَتِنَاۗ اِنَّهٗ مِنَ الصّٰلِحِيْنَ ࣖ ','dan Kami masukkan dia ke dalam rahmat Kami; karena sesungguhnya dia termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,76,'وَنُوْحًا اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهٗ فَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِ ۚ','Dan (ingatlah kisah) Nuh, sebelum itu ketika dia berdoa, dan Kami memperkenankan doanya, lalu Kami selamatkan dia beserta keluarganya dari bencana yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,77,'وَنَصَرْنٰهُ مِنَ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَ ','Dan Kami telah menolongnya dari kaum yang telah mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang jahat, maka Kami tenggelamkan mereka semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,78,'وَدَاوٗدَ وَسُلَيْمٰنَ اِذْ يَحْكُمٰنِ فِى الْحَرْثِ اِذْ نَفَشَتْ فِيْهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شٰهِدِيْنَ ۖ','Dan (ingatlah kisah) Daud dan Sulaiman, di waktu keduanya memberikan keputusan mengenai tanaman, karena tanaman itu dirusak oleh kambing-kambing kepunyaan kaumnya. Dan adalah Kami menyaksikan keputusan yang diberikan oleh mereka itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,79,'فَفَهَّمْنٰهَا سُلَيْمٰنَۚ وَكُلًّا اٰتَيْنَا حُكْمًا وَّعِلْمًاۖ وَّسَخَّرْنَا مَعَ دَاوٗدَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۗ وَكُنَّا فٰعِلِيْنَ','maka Kami telah memberikan pengertian kepada Sulaiman tentang hukum (yang lebih tepat); dan kepada masing-masing mereka telah Kami berikan hikmah dan ilmu dan telah Kami tundukkan gunung-gunung dan burung-burung, semua bertasbih bersama Daud. Dan kamilah yang melakukannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,80,'وَعَلَّمْنٰهُ صَنْعَةَ لَبُوْسٍ لَّكُمْ لِتُحْصِنَكُمْ مِّنْۢ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُوْنَ','Dan telah Kami ajarkan kepada Daud membuat baju besi untuk kamu, guna memelihara kamu dalam peperanganmu; Maka hendaklah kamu bersyukur (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,81,'وَلِسُلَيْمٰنَ الرِّيْحَ عَاصِفَةً تَجْرِيْ بِاَمْرِهٖٓ اِلَى الْاَرْضِ الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَكُنَّا بِكُلِّ شَيْءٍ عٰلِمِيْنَ','Dan (telah Kami tundukkan) untuk Sulaiman angin yang sangat kencang tiupannya yang berhembus dengan perintahnya ke negeri yang kami telah memberkatinya. Dan adalah Kami Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,82,'وَمِنَ الشَّيٰطِيْنِ مَنْ يَّغُوْصُوْنَ لَهٗ وَيَعْمَلُوْنَ عَمَلًا دُوْنَ ذٰلِكَۚ وَكُنَّا لَهُمْ حٰفِظِيْنَ ۙ','Dan Kami telah tundukkan (pula kepada Sulaiman) segolongan syaitan-syaitan yang menyelam (ke dalam laut) untuknya dan mengerjakan pekerjaan selain daripada itu, dan adalah Kami memelihara mereka itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,83,'۞ وَاَيُّوْبَ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِمِيْنَ ۚ','dan (ingatlah kisah) Ayub, ketika ia menyeru Tuhannya: "(Ya Tuhanku), sesungguhnya aku telah ditimpa penyakit dan Engkau adalah Tuhan Yang Maha Penyayang di antara semua penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,84,'فَاسْتَجَبْنَا لَهٗ فَكَشَفْنَا مَا بِهٖ مِنْ ضُرٍّ وَّاٰتَيْنٰهُ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنْ عِنْدِنَا وَذِكْرٰى لِلْعٰبِدِيْنَ ۚ','Maka Kamipun memperkenankan seruannya itu, lalu Kami lenyapkan penyakit yang ada padanya dan Kami kembalikan keluarganya kepadanya, dan Kami lipat gandakan bilangan mereka, sebagai suatu rahmat dari sisi Kami dan untuk menjadi peringatan bagi semua yang menyembah Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,85,'وَاِسْمٰعِيْلَ وَاِدْرِيْسَ وَذَا الْكِفْلِۗ  كُلٌّ مِّنَ الصّٰبِرِيْنَ ۙ','Dan (ingatlah kisah) Ismail, Idris dan Dzulkifli. Semua mereka termasuk orang-orang yang sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,86,'وَاَدْخَلْنٰهُمْ فِيْ رَحْمَتِنَاۗ اِنَّهُمْ مِّنَ الصّٰلِحِيْنَ ','Kami telah memasukkan mereka kedalam rahmat Kami. Sesungguhnya mereka termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,87,'وَذَا النُّوْنِ اِذْ ذَّهَبَ مُغَاضِبًا فَظَنَّ اَنْ لَّنْ نَّقْدِرَ عَلَيْهِ فَنَادٰى فِى الظُّلُمٰتِ اَنْ لَّآ اِلٰهَ اِلَّآ اَنْتَ سُبْحٰنَكَ اِنِّيْ كُنْتُ مِنَ الظّٰلِمِيْنَ ۚ','Dan (ingatlah kisah) Dzun Nun (Yunus), ketika ia pergi dalam keadaan marah, lalu ia menyangka bahwa Kami tidak akan mempersempitnya (menyulitkannya), maka ia menyeru dalam keadaan yang sangat gelap: "Bahwa tidak ada Tuhan selain Engkau. Maha Suci Engkau, sesungguhnya aku adalah termasuk orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,88,'فَاسْتَجَبْنَا لَهٗۙ وَنَجَّيْنٰهُ مِنَ الْغَمِّۗ وَكَذٰلِكَ نُـْۨجِى الْمُؤْمِنِيْنَ ','Maka Kami telah memperkenankan doanya dan menyelamatkannya dari pada kedukaan. Dan demikianlah Kami selamatkan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,89,'وَزَكَرِيَّآ اِذْ نَادٰى رَبَّهٗ رَبِّ لَا تَذَرْنِيْ فَرْدًا وَّاَنْتَ خَيْرُ الْوٰرِثِيْنَ ۚ','Dan (ingatlah kisah) Zakaria, tatkala ia menyeru Tuhannya: "Ya Tuhanku janganlah Engkau membiarkan aku hidup seorang diri dan Engkaulah Waris Yang Paling Baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,90,'فَاسْتَجَبْنَا لَهٗ  ۖوَوَهَبْنَا لَهٗ يَحْيٰى وَاَصْلَحْنَا لَهٗ زَوْجَهٗۗ اِنَّهُمْ كَانُوْا يُسٰرِعُوْنَ فِى الْخَيْرٰتِ وَيَدْعُوْنَنَا رَغَبًا وَّرَهَبًاۗ وَكَانُوْا لَنَا خٰشِعِيْنَ ','Maka Kami memperkenankan doanya, dan Kami anugerahkan kepada nya Yahya dan Kami jadikan isterinya dapat mengandung. Sesungguhnya mereka adalah orang-orang yang selalu bersegera dalam (mengerjakan) perbuatan-perbuatan yang baik dan mereka berdoa kepada Kami dengan harap dan cemas. Dan mereka adalah orang-orang yang khusyu´ kepada Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,91,'وَالَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهَا مِنْ رُّوْحِنَا وَجَعَلْنٰهَا وَابْنَهَآ اٰيَةً لِّلْعٰلَمِيْنَ ','Dan (ingatlah kisah) Maryam yang telah memelihara kehormatannya, lalu Kami tiupkan ke dalam (tubuh)nya ruh dari Kami dan Kami jadikan dia dan anaknya tanda (kekuasaan Allah) yang besar bagi semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,92,'اِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةًۖ وَّاَنَا۠ رَبُّكُمْ فَاعْبُدُوْنِ ','Sesungguhnya (agama Tauhid) ini adalah agama kamu semua; agama yang satu dan Aku adalah Tuhanmu, maka sembahlah Aku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,93,'وَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْۗ كُلٌّ اِلَيْنَا رَاجِعُوْنَ ࣖ','Dan mereka telah memotong-motong urusan (agama) mereka di antara mereka. Kepada Kamilah masing-masing golongan itu akan kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,94,'فَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهٖۚ وَاِنَّا لَهٗ كَاتِبُوْنَ ','Maka barang siapa yang mengerjakan amal saleh, sedang ia beriman, maka tidak ada pengingkaran terhadap amalannya itu dan sesungguhnya Kami menuliskan amalannya itu untuknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,95,'وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنٰهَآ اَنَّهُمْ لَا يَرْجِعُوْنَ ','Sungguh tidak mungkin atas (penduduk) suatu negeri yang telah Kami binasakan, bahwa mereka tidak akan kembali (kepada Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,96,'حَتّٰىٓ اِذَا فُتِحَتْ يَأْجُوْجُ وَمَأْجُوْجُ وَهُمْ مِّنْ كُلِّ حَدَبٍ يَّنْسِلُوْنَ ','Hingga apabila dibukakan (tembok) Ya´juj dan Ma´juj, dan mereka turun dengan cepat dari seluruh tempat yang tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,97,'وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذِيْنَ كَفَرُوْاۗ يٰوَيْلَنَا قَدْ كُنَّا فِيْ غَفْلَةٍ مِّنْ هٰذَا بَلْ كُنَّا ظٰلِمِيْنَ ','Dan telah dekatlah kedatangan janji yang benar (hari berbangkit), maka tiba-tiba terbelalaklah mata orang-orang yang kafir. (Mereka berkata): "Aduhai, celakalah kami, sesungguhnya kami adalah dalam kelalaian tentang ini, bahkan kami adalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,98,'اِنَّكُمْ وَمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ حَصَبُ جَهَنَّمَۗ اَنْتُمْ لَهَا وَارِدُوْنَ ','Sesungguhnya kamu dan apa yang kamu sembah selain Allah, adalah umpan Jahannam, kamu pasti masuk ke dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,99,'لَوْ كَانَ هٰٓؤُلَاۤءِ اٰلِهَةً مَّا وَرَدُوْهَاۗ وَكُلٌّ فِيْهَا خٰلِدُوْنَ ','Andaikata berhala-berhala itu Tuhan, tentulah mereka tidak masuk neraka. Dan semuanya akan kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,100,'لَهُمْ فِيْهَا زَفِيْرٌ وَّهُمْ فِيْهَا لَا يَسْمَعُوْنَ ','Mereka merintih di dalam api dan mereka di dalamnya tidak bisa mendengar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,101,'اِنَّ الَّذِيْنَ سَبَقَتْ لَهُمْ مِّنَّا الْحُسْنٰىٓۙ اُولٰۤىِٕكَ عَنْهَا مُبْعَدُوْنَ ۙ','Bahwasanya orang-orang yang telah ada untuk mereka ketetapan yang baik dari Kami, mereka itu dijauhkan dari neraka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,102,'لَا يَسْمَعُوْنَ حَسِيْسَهَاۚ وَهُمْ فِيْ مَا اشْتَهَتْ اَنْفُسُهُمْ خٰلِدُوْنَ ۚ','mereka tidak mendengar sedikitpun suara api neraka, dan mereka kekal dalam menikmati apa yang diingini oleh mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,103,'لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰىهُمُ الْمَلٰۤىِٕكَةُۗ هٰذَا يَوْمُكُمُ الَّذِيْ كُنْتُمْ تُوْعَدُوْنَ ','Mereka tidak disusahkan oleh kedahsyatan yang besar (pada hari kiamat), dan mereka disambut oleh para malaikat. (Malaikat berkata): "Inilah harimu yang telah dijanjikan kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,104,'يَوْمَ نَطْوِى السَّمَاۤءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۗ  كَمَا بَدَأْنَآ اَوَّلَ خَلْقٍ نُّعِيْدُهٗۗ وَعْدًا عَلَيْنَاۗ اِنَّا كُنَّا فٰعِلِيْنَ ','(Yaitu) pada hari Kami gulung langit sebagai menggulung lembaran-lembaran kertas. Sebagaimana Kami telah memulai panciptaan pertama begitulah Kami akan mengulanginya. Itulah suatu janji yang pasti Kami tepati; sesungguhnya Kamilah yang akan melaksanakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,105,'وَلَقَدْ كَتَبْنَا فِى الزَّبُوْرِ مِنْۢ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصّٰلِحُوْنَ ','Dan sungguh telah Kami tulis didalam Zabur sesudah (Kami tulis dalam) Lauh Mahfuzh, bahwasanya bumi ini dipusakai hamba-hamba-Ku yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,106,'اِنَّ فِيْ هٰذَا لَبَلٰغًا لِّقَوْمٍ عٰبِدِيْنَ ۗ','Sesungguhnya (apa yang disebutkan) dalam (surat) ini, benar-benar menjadi peringatan bagi kaum yang menyembah (Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,107,'وَمَآ اَرْسَلْنٰكَ اِلَّا رَحْمَةً لِّلْعٰلَمِيْنَ ','Dan tiadalah Kami mengutus kamu, melainkan untuk (menjadi) rahmat bagi semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,108,'قُلْ اِنَّمَا يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَهَلْ اَنْتُمْ مُّسْلِمُوْنَ','Katakanlah: "Sesungguhnya yang diwahyukan kepadaku adalah: "Bahwasanya Tuhanmu adalah Tuhan Yang Esa. maka hendaklah kamu berserah diri (kepada-Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,109,'فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَاۤءٍۗ وَاِنْ اَدْرِيْٓ اَقَرِيْبٌ اَمْ بَعِيْدٌ مَّا تُوْعَدُوْنَ ','Jika mereka berpaling, maka katakanlah: "Aku telah menyampaikan kepada kamu sekalian (ajaran) yang sama (antara kita) dan aku tidak mengetahui apakah yang diancamkan kepadamu itu sudah dekat atau masih jauh?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,110,'اِنَّهٗ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُوْنَ ','Sesungguhnya Dia mengetahui perkataan (yang kamu ucapkan) dengan terang-terangan dan Dia mengetahui apa yang kamu rahasiakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,111,'وَاِنْ اَدْرِيْ لَعَلَّهٗ فِتْنَةٌ لَّكُمْ وَمَتَاعٌ اِلٰى حِيْنٍ ','Dan aku tiada mengetahui, boleh jadi hal itu cobaan bagi kamu dan kesenangan sampai kepada suatu waktu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,21,112,'قَالَ رَبِّ احْكُمْ بِالْحَقِّۗ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ ࣖ','(Muhammad) berkata: "Ya Tuhanku, berilah keputusan dengan adil. Dan Tuhan kami ialah Tuhan Yang Maha Pemurah lagi Yang dimohonkan pertolongan-Nya terhadap apa yang kamu katakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,1,'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيْمٌ ','Hai manusia, bertakwalah kepada Tuhanmu; sesungguhnya kegoncangan hari kiamat itu adalah suatu kejadian yang sangat besar (dahsyat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,2,'يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّآ اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكٰرٰى وَمَا هُمْ بِسُكٰرٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَدِيْدٌ ','(Ingatlah) pada hari (ketika) kamu melihat kegoncangan itu, lalailah semua wanita yang menyusui anaknya dari anak yang disusuinya dan gugurlah kandungan segala wanita yang hamil, dan kamu lihat manusia dalam keadaan mabuk, padahal sebenarnya mereka tidak mabuk, akan tetapi azab Allah itu sangat kerasnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,3,'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّيَتَّبِعُ كُلَّ شَيْطٰنٍ مَّرِيْدٍۙ ','Di antara manusia ada orang yang membantah tentang Allah tanpa ilmu pengetahuan dan mengikuti setiap syaitan yang jahat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,4,'كُتِبَ عَلَيْهِ اَنَّهٗ مَنْ تَوَلَّاهُ فَاَنَّهٗ يُضِلُّهٗ وَيَهْدِيْهِ اِلٰى عَذَابِ السَّعِيْرِ ','yang telah ditetapkan terhadap syaitan itu, bahwa barangsiapa yang berkawan dengan dia, tentu dia akan menyesatkannya, dan membawanya ke azab neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,5,'يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ رَيْبٍ مِّنَ الْبَعْثِ فَاِنَّا خَلَقْنٰكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُّضْغَةٍ مُّخَلَّقَةٍ وَّغَيْرِ مُخَلَّقَةٍ لِّنُبَيِّنَ لَكُمْۗ وَنُقِرُّ فِى الْاَرْحَامِ مَا نَشَاۤءُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْۚ وَمِنْكُمْ مَّنْ يُّتَوَفّٰى وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْۢ بَعْدِ عِلْمٍ شَيْـًٔاۗ وَتَرَى الْاَرْضَ هَامِدَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْ وَاَنْۢبَتَتْ مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍ','Hai manusia, jika kamu dalam keraguan tentang kebangkitan (dari kubur), maka (ketahuilah) sesungguhnya Kami telah menjadikan kamu dari tanah, kemudian dari setetes mani, kemudian dari segumpal darah, kemudian dari segumpal daging yang sempurna kejadiannya dan yang tidak sempurna, agar Kami jelaskan kepada kamu dan Kami tetapkan dalam rahim, apa yang Kami kehendaki sampai waktu yang sudah ditentukan, kemudian Kami keluarkan kamu sebagai bayi, kemudian (dengan berangsur-angsur) kamu sampailah kepada kedewasaan, dan di antara kamu ada yang diwafatkan dan (adapula) di antara kamu yang dipanjangkan umurnya sampai pikun, supaya dia tidak mengetahui lagi sesuatupun yang dahulunya telah diketahuinya. Dan kamu lihat bumi ini kering, kemudian apabila telah Kami turunkan air di atasnya, hiduplah bumi itu dan suburlah dan menumbuhkan berbagai macam tumbuh-tumbuhan yang indah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,6,'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهٗ يُحْيِ الْمَوْتٰى وَاَنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۙ','Yang demikian itu, karena sesungguhnya Allah, Dialah yang haq dan sesungguhnya Dialah yang menghidupkan segala yang mati dan sesungguhnya Allah Maha Kuasa atas segala sesuatu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,7,'وَّاَنَّ السَّاعَةَ اٰتِيَةٌ لَّا رَيْبَ فِيْهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِى الْقُبُوْرِ ','dan sesungguhnya hari kiamat itu pastilah datang, tak ada keraguan padanya; dan bahwasanya Allah membangkitkan semua orang di dalam kubur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,8,'وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ ۙ','Dan di antara manusia ada orang-orang yang membantah tentang Allah tanpa ilmu pengetahuan, tanpa petunjuk dan tanpa kitab (wahyu) yang bercahaya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,9,'ثَانِيَ عِطْفِهٖ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ ۗ لَهٗ فِى الدُّنْيَا خِزْيٌ وَّنُذِيْقُهٗ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَرِيْقِ','dengan memalingkan lambungnya untuk menyesatkan manusia dari jalan Allah. Ia mendapat kehinaan di dunia dan dihari kiamat Kami merasakan kepadanya azab neraka yang membakar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,10,'ذٰلِكَ بِمَا قَدَّمَتْ يَدٰكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِ ࣖ','(Akan dikatakan kepadanya): "Yang demikian itu, adalah disebabkan perbuatan yang dikerjakan oleh kedua tangan kamu dahulu dan sesungguhnya Allah sekali-kali bukanlah penganiaya hamba-hamba-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,11,'وَمِنَ النَّاسِ مَنْ يَّعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهٗ خَيْرُ ِۨاطْمَـَٔنَّ بِهٖۚ وَاِنْ اَصَابَتْهُ فِتْنَةُ ِۨانْقَلَبَ عَلٰى وَجْهِهٖۗ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۗ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ ','Dan di antara manusia ada orang yang menyembah Allah dengan berada di tepi; maka jika ia memperoleh kebajikan, tetaplah ia dalam keadaan itu, dan jika ia ditimpa oleh suatu bencana, berbaliklah ia ke belakang. Rugilah ia di dunia dan di akhirat. Yang demikian itu adalah kerugian yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,12,'يَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهٗ وَمَا لَا يَنْفَعُهٗۗ ذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ۚ','Ia menyeru selain Allah, sesuatu yang tidak dapat memberi mudharat dan tidak (pula) memberi manfaat kepadanya. Yang demikian itu adalah kesesatan yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,13,'يَدْعُوْا لَمَنْ ضَرُّهٗٓ اَقْرَبُ مِنْ نَّفْعِهٖۗ  لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَشِيْرُ ','Ia menyeru sesuatu yang sebenarnya mudharatnya lebih dekat dari manfaatnya. Sesungguhnya yang diserunya itu adalah sejahat-jahat kawan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,14,'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ ','Sesungguhnya Allah memasukkan orang-orang yang beriman dan mengerjakan amal yang saleh ke dalam surga-surga yang di bawahnya mengalir sungai-sungai. Sesungguhnya Allah berbuat apa yang Dia kehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,15,'مَنْ كَانَ يَظُنُّ اَنْ لَّنْ يَّنْصُرَهُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَاۤءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهٗ مَا يَغِيْظُ ','Barangsiapa yang menyangka bahwa Allah sekali-kali tiada menolongnya (Muhammad) di dunia dan akhirat, maka hendaklah ia merentangkan tali ke langit, kemudian hendaklah ia melaluinya, kemudian hendaklah ia pikirkan apakah tipu dayanya itu dapat melenyapkan apa yang menyakitkan hatinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,16,'وَكَذٰلِكَ اَنْزَلْنٰهُ اٰيٰتٍۢ بَيِّنٰتٍۙ وَّاَنَّ اللّٰهَ يَهْدِيْ مَنْ يُّرِيْدُ ','Dan demikianlah Kami telah menurunkan Al Quran yang merupakan ayat-ayat yang nyata, dan bahwasanya Allah memberikan petunjuk kepada siapa yang Dia kehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,17,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـِٕيْنَ وَالنَّصٰرٰى وَالْمَجُوْسَ وَالَّذِيْنَ اَشْرَكُوْٓا ۖاِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ','Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, orang-orang Shaabi-iin orang-orang Nasrani, orang-orang Majusi dan orang-orang musyrik, Allah akan memberi keputusan di antara mereka pada hari kiamat. Sesungguhnya Allah menyaksikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,18,'اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُوْمُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَاۤبُّ وَكَثِيْرٌ مِّنَ النَّاسِۗ وَكَثِيْرٌ حَقَّ عَلَيْهِ الْعَذَابُۗ وَمَنْ يُّهِنِ اللّٰهُ فَمَا لَهٗ مِنْ مُّكْرِمٍۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يَشَاۤءُ ۩ۗ','Apakah kamu tiada mengetahui, bahwa kepada Allah bersujud apa yang ada di langit, di bumi, matahari, bulan, bintang, gunung, pohon-pohonan, binatang-binatang yang melata dan sebagian besar daripada manusia? Dan banyak di antara manusia yang telah ditetapkan azab atasnya. Dan barangsiapa yang dihinakan Allah maka tidak seorangpun yang memuliakannya. Sesungguhnya Allah berbuat apa yang Dia kehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,19,'۞ هٰذَانِ خَصْمٰنِ اخْتَصَمُوْا فِيْ رَبِّهِمْ  فَالَّذِيْنَ كَفَرُوْا قُطِّعَتْ لَهُمْ ثِيَابٌ مِّنْ نَّارٍۗ يُصَبُّ مِنْ فَوْقِ رُءُوْسِهِمُ الْحَمِيْمُ ۚ','Inilah dua golongan (golongan mukmin dan golongan kafir) yang bertengkar, mereka saling bertengkar mengenai Tuhan mereka. Maka orang kafir akan dibuatkan untuk mereka pakaian-pakaian dari api neraka. Disiramkan air yang sedang mendidih ke atas kepala mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,20,'يُصْهَرُ بِهٖ مَا فِيْ بُطُوْنِهِمْ وَالْجُلُوْدُ ۗ','Dengan air itu dihancur luluhkan segala apa yang ada dalam perut mereka dan juga kulit (mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,21,'وَلَهُمْ مَّقَامِعُ مِنْ حَدِيْدٍ ','Dan untuk mereka cambuk-cambuk dari besi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,22,'كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَا مِنْ غَمٍّ اُعِيْدُوْا فِيْهَا وَذُوْقُوْا عَذَابَ الْحَرِيْقِ ࣖ','Setiap kali mereka hendak ke luar dari neraka lantaran kesengsaraan mereka, niscaya mereka dikembalikan ke dalamnya. (Kepada mereka dikatakan), "Rasailah azab yang membakar ini".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,23,'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًاۗ وَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ','Sesungguhnya Allah memasukkan orang-orang beriman dan mengerjakan amal yang saleh ke dalam surga-surga yang di bawahnya mengalir sungai-sungai. Di surga itu mereka diberi perhiasan dengan gelang-gelang dari emas dan mutiara, dan pakaian mereka adalah sutera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,24,'وَهُدُوْٓا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۚ وَهُدُوْٓا اِلٰى صِرَاطِ الْحَمِيْدِ ','Dan mereka diberi petunjuk kepada ucapan-ucapan yang baik dan ditunjuki (pula) kepada jalan (Allah) yang terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,25,'اِنَّ الَّذِيْنَ كَفَرُوْا وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِيْ جَعَلْنٰهُ لِلنَّاسِ سَوَاۤءً ۨالْعَاكِفُ فِيْهِ وَالْبَادِۗ وَمَنْ يُّرِدْ فِيْهِ بِاِلْحَادٍۢ بِظُلْمٍ نُّذِقْهُ مِنْ عَذَابٍ اَلِيْمٍ ࣖ','Sesungguhnya orang-orang yang kafir dan menghalangi manusia dari jalan Allah dan Masjidilharam yang telah Kami jadikan untuk semua manusia, baik yang bermukim di situ maupun di padang pasir dan siapa yang bermaksud di dalamnya melakukan kejahatan secara zalim, niscaya akan Kami rasakan kepadanya sebahagian siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,26,'وَاِذْ بَوَّأْنَا لِاِبْرٰهِيْمَ مَكَانَ الْبَيْتِ اَنْ لَّا تُشْرِكْ بِيْ شَيْـًٔا وَّطَهِّرْ بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْقَاۤىِٕمِيْنَ وَالرُّكَّعِ السُّجُوْدِ ','Dan (ingatlah), ketika Kami memberikan tempat kepada Ibrahim di tempat Baitullah (dengan mengatakan): "Janganlah kamu memperserikatkan sesuatupun dengan Aku dan sucikanlah rumah-Ku ini bagi orang-orang yang thawaf, dan orang-orang yang beribadat dan orang-orang yang ruku´ dan sujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,27,'وَاَذِّنْ فِى النَّاسِ بِالْحَجِّ يَأْتُوْكَ رِجَالًا وَّعَلٰى كُلِّ ضَامِرٍ يَّأْتِيْنَ مِنْ كُلِّ فَجٍّ عَمِيْقٍ ۙ','Dan berserulah kepada manusia untuk mengerjakan haji, niscaya mereka akan datang kepadamu dengan berjalan kaki, dan mengendarai unta yang kurus yang datang dari segenap penjuru yang jauh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,28,'لِّيَشْهَدُوْا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ فِيْٓ اَيَّامٍ مَّعْلُوْمٰتٍ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۚ فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْبَاۤىِٕسَ الْفَقِيْرَ ۖ','supaya mereka menyaksikan berbagai manfaat bagi mereka dan supaya mereka menyebut nama Allah pada hari yang telah ditentukan atas rezeki yang Allah telah berikan kepada mereka berupa binatang ternak. Maka makanlah sebahagian daripadanya dan (sebahagian lagi) berikanlah untuk dimakan orang-orang yang sengsara dan fakir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,29,'ثُمَّ لْيَقْضُوْا تَفَثَهُمْ وَلْيُوْفُوْا نُذُوْرَهُمْ وَلْيَطَّوَّفُوْا بِالْبَيْتِ الْعَتِيْقِ ','Kemudian, hendaklah mereka menghilangkan kotoran yang ada pada badan mereka dan hendaklah mereka menyempurnakan nazar-nazar mereka dan hendaklah mereka melakukan melakukan thawaf sekeliling rumah yang tua itu (Baitullah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,30,'ذٰلِكَ وَمَنْ يُّعَظِّمْ حُرُمٰتِ اللّٰهِ فَهُوَ خَيْرٌ لَّهٗ عِنْدَ رَبِّهٖۗ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوْا قَوْلَ الزُّوْرِ ۙ','Demikianlah (perintah Allah). Dan barangsiapa mengagungkan apa-apa yang terhormat di sisi Allah maka itu adalah lebih baik baginya di sisi Tuhannya. Dan telah dihalalkan bagi kamu semua binatang ternak, terkecuali yang diterangkan kepadamu keharamannya, maka jauhilah olehmu berhala-berhala yang najis itu dan jauhilah perkataan-perkataan dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,31,'حُنَفَاۤءَ لِلّٰهِ غَيْرَ مُشْرِكِيْنَ بِهٖۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَاۤءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْوِيْ بِهِ الرِّيْحُ فِيْ مَكَانٍ سَحِيْقٍ ','dengan ikhlas kepada Allah, tidak mempersekutukan sesuatu dengan Dia. Barangsiapa mempersekutukan sesuatu dengan Allah, maka adalah ia seolah-olah jatuh dari langit lalu disambar oleh burung, atau diterbangkan angin ke tempat yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,32,'ذٰلِكَ وَمَنْ يُّعَظِّمْ شَعَاۤىِٕرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوْبِ ','Demikianlah (perintah Allah). Dan barangsiapa mengagungkan syi´ar-syi´ar Allah, maka sesungguhnya itu timbul dari ketakwaan hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,33,'لَكُمْ فِيْهَا مَنَافِعُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ مَحِلُّهَآ اِلَى الْبَيْتِ الْعَتِيْقِ ࣖ','Bagi kamu pada binatang-binatang hadyu itu ada beberapa manfaat, sampai kepada waktu yang ditentukan, kemudian tempat wajib (serta akhir masa) menyembelihnya ialah setelah sampai ke Baitul Atiq (Baitullah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,34,'وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا لِّيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۗ فَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَلَهٗٓ اَسْلِمُوْاۗ وَبَشِّرِ الْمُخْبِتِيْنَ ۙ','Dan bagi tiap-tiap umat telah Kami syariatkan penyembelihan (kurban), supaya mereka menyebut nama Allah terhadap binatang ternak yang telah direzekikan Allah kepada mereka, maka Tuhanmu ialah Tuhan Yang Maha Esa, karena itu berserah dirilah kamu kepada-Nya. Dan berilah kabar gembira kepada orang-orang yang tunduk patuh (kepada Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,35,'الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَالصَّابِرِيْنَ عَلٰى مَآ اَصَابَهُمْ وَالْمُقِيْمِى الصَّلٰوةِۙ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ','(yaitu) orang-orang yang apabila disebut nama Allah gemetarlah hati mereka, orang-orang yang sabar terhadap apa yang menimpa mereka, orang-orang yang mendirikan sembahyang dan orang-orang yang menafkahkan sebagian dari apa yang telah Kami rezekikan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,36,'وَالْبُدْنَ جَعَلْنٰهَا لَكُمْ مِّنْ شَعَاۤىِٕرِ اللّٰهِ لَكُمْ فِيْهَا خَيْرٌۖ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَاۤفَّۚ فَاِذَا وَجَبَتْ جُنُوْبُهَا فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّۗ  كَذٰلِكَ سَخَّرْنٰهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ ','Dan telah Kami jadikan untuk kamu unta-unta itu sebahagian dari syi´ar Allah, kamu memperoleh kebaikan yang banyak padanya, maka sebutlah olehmu nama Allah ketika kamu menyembelihnya dalam keadaan berdiri (dan telah terikat). Kemudian apabila telah roboh (mati), maka makanlah sebahagiannya dan beri makanlah orang yang rela dengan apa yang ada padanya (yang tidak meminta-minta) dan orang yang meminta. Demikianlah Kami telah menundukkan untua-unta itu kepada kamu, mudah-mudahan kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,37,'لَنْ يَّنَالَ اللّٰهَ لُحُوْمُهَا وَلَا دِمَاۤؤُهَا وَلٰكِنْ يَّنَالُهُ التَّقْوٰى مِنْكُمْۗ  كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ ۗ وَبَشِّرِ الْمُحْسِنِيْنَ ','Daging-daging unta dan darahnya itu sekali-kali tidak dapat mencapai (keridhaan) Allah, tetapi ketakwaan dari kamulah yang dapat mencapainya. Demikianlah Allah telah menundukkannya untuk kamu supaya kamu mengagungkan Allah terhadap hidayah-Nya kepada kamu. Dan berilah kabar gembira kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,38,'۞ اِنَّ اللّٰهَ يُدَافِعُ عَنِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُوْرٍ ࣖ','Sesungguhnya Allah membela orang-orang yang telah beriman. Sesungguhnya Allah tidak menyukai tiap-tiap orang yang berkhianat lagi mengingkari nikmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,39,'اُذِنَ لِلَّذِيْنَ يُقَاتَلُوْنَ بِاَنَّهُمْ ظُلِمُوْاۗ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَدِيْرٌ ۙ','Telah diizinkan (berperang) bagi orang-orang yang diperangi, karena sesungguhnya mereka telah dianiaya. Dan sesungguhnya Allah, benar-benar Maha Kuasa menolong mereka itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,40,' ۨالَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّآ اَنْ يَّقُوْلُوْا رَبُّنَا اللّٰهُ ۗوَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَّصَلَوٰتٌ وَّمَسٰجِدُ يُذْكَرُ فِيْهَا اسْمُ اللّٰهِ كَثِيْرًاۗ وَلَيَنْصُرَنَّ اللّٰهُ مَنْ يَّنْصُرُهٗۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ ','(yaitu) orang-orang yang telah diusir dari kampung halaman mereka tanpa alasan yang benar, kecuali karena mereka berkata: "Tuhan kami hanyalah Allah". Dan sekiranya Allah tiada menolak (keganasan) sebagian manusia dengan sebagian yang lain, tentulah telah dirobohkan biara-biara Nasrani, gereja-gereja, rumah-rumah ibadat orang Yahudi dan masjid-masjid, yang di dalamnya banyak disebut nama Allah. Sesungguhnya Allah pasti menolong orang yang menolong (agama)-Nya. Sesungguhnya Allah benar-benar Maha Kuat lagi Maha Perkasa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,41,'اَلَّذِيْنَ اِنْ مَّكَّنّٰهُمْ فِى الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوْا بِالْمَعْرُوْفِ وَنَهَوْا عَنِ الْمُنْكَرِۗ وَلِلّٰهِ عَاقِبَةُ الْاُمُوْرِ ','(yaitu) orang-orang yang jika Kami teguhkan kedudukan mereka di muka bumi niscaya mereka mendirikan sembahyang, menunaikan zakat, menyuruh berbuat ma´ruf dan mencegah dari perbuatan yang mungkar; dan kepada Allah-lah kembali segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,42,'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّثَمُوْدُ ۙ','Dan jika mereka (orang-orang musyrik) mendustakan kamu, maka sesungguhnya telah mendustakan juga sebelum mereka kaum Nuh, ´Aad dan Tsamud,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,43,'وَقَوْمُ اِبْرٰهِيْمَ وَقَوْمُ لُوْطٍ ۙ','dan kaum Ibrahim dan kaum Luth,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,44,'وَّاَصْحٰبُ مَدْيَنَۚ وَكُذِّبَ مُوْسٰى فَاَمْلَيْتُ لِلْكٰفِرِيْنَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَكِيْرِ ','dan penduduk Madyan, dan telah didustakan Musa, lalu Aku tangguhkan (azab-Ku) untuk orang-orang kafir, kemudian Aku azab mereka, maka (lihatlah) bagaimana besarnya kebencian-Ku (kepada mereka itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,45,'فَكَاَيِّنْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۖ وَبِئْرٍ مُّعَطَّلَةٍ وَّقَصْرٍ مَّشِيْدٍ ','Berapalah banyaknya kota yang Kami telah membinasakannya, yang penduduknya dalam keadaan zalim, maka (tembok-tembok) kota itu roboh menutupi atap-atapnya dan (berapa banyak pula) sumur yang telah ditinggalkan dan istana yang tinggi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,46,'اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَتَكُوْنَ لَهُمْ قُلُوْبٌ يَّعْقِلُوْنَ بِهَآ اَوْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوْبُ الَّتِيْ فِى الصُّدُوْرِ ','maka apakah mereka tidak berjalan di muka bumi, lalu mereka mempunyai hati yang dengan itu mereka dapat memahami atau mempunyai telinga yang dengan itu mereka dapat mendengar? Karena sesungguhnya bukanlah mata itu yang buta, tetapi yang buta, ialah hati yang di dalam dada.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,47,'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِ وَلَنْ يُّخْلِفَ اللّٰهُ وَعْدَهٗۗ وَاِنَّ يَوْمًا عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِّمَّا تَعُدُّوْنَ ','Dan mereka meminta kepadamu agar azab itu disegerakan, padahal Allah sekali-kali tidak akan menyalahi janji-Nya. Sesungguhnya sehari disisi Tuhanmu adalah seperti seribu tahun menurut perhitunganmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,48,'وَكَاَيِّنْ مِّنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَصِيْرُ ࣖ','Dan berapalah banyaknya kota yang Aku tangguhkan (azab-Ku) kepadanya, yang penduduknya berbuat zalim, kemudian Aku azab mereka, dan hanya kepada-Kulah kembalinya (segala sesuatu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,49,'قُلْ يٰٓاَيُّهَا النَّاسُ اِنَّمَآ اَنَا۠ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۚ','Katakanlah: "Hai manusia, sesungguhnya aku adalah seorang pemberi peringatan yang nyata kepada kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,50,'فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ','Maka orang-orang yang beriman dan beramal saleh, bagi mereka ampunan dan rezeki yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,51,'وَالَّذِيْنَ سَعَوْا فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ','Dan orang-orang yang berusaha dengan maksud menentang ayat-ayat Kami dengan melemahkan (kemauan untuk beriman); mereka itu adalah penghuni-penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,52,'وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ وَّلَا نَبِيٍّ اِلَّآ اِذَا تَمَنّٰىٓ اَلْقَى الشَّيْطٰنُ فِيْٓ اُمْنِيَّتِهٖۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِى الشَّيْطٰنُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ۙ','Dan Kami tidak mengutus sebelum kamu seorang rasulpun dan tidak (pula) seorang nabi, melainkan apabila ia mempunyai sesuatu keinginan, syaitanpun memasukkan godaan-godaan terhadap keinginan itu, Allah menghilangkan apa yang dimasukkan oleh syaitan itu, dan Allah menguatkan ayat-ayat-Nya. Dan Allah Maha Mengetahui lagi Maha Bijaksana,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,53,'لِّيَجْعَلَ مَا يُلْقِى الشَّيْطٰنُ فِتْنَةً لِّلَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْقَاسِيَةِ قُلُوْبُهُمْۗ وَاِنَّ الظّٰلِمِيْنَ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ۙ','agar Dia menjadikan apa yang dimasukkan oleh syaitan itu, sebagai cobaan bagi orang-orang yang di dalam hatinya ada penyakit dan yang kasar hatinya. Dan sesungguhnya orang-orang yang zalim itu, benar-benar dalam permusuhan yang sangat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,54,'وَّلِيَعْلَمَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَّبِّكَ فَيُؤْمِنُوْا بِهٖ فَتُخْبِتَ لَهٗ قُلُوْبُهُمْۗ وَاِنَّ اللّٰهَ لَهَادِ الَّذِيْنَ اٰمَنُوْٓا اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','dan agar orang-orang yang telah diberi ilmu, meyakini bahwasanya Al Quran itulah yang hak dari Tuhan-mu lalu mereka beriman dan tunduk hati mereka kepadanya dan sesungguhnya Allah adalah Pemberi Petunjuk bagi orang-orang yang beriman kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,55,'وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا فِيْ مِرْيَةٍ مِّنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيْمٍ ','Dan senantiasalah orang-orang kafir itu berada dalam keragu-raguan terhadap Al Quran, hingga datang kepada mereka saat (kematiannya) dengan tiba-tiba atau datang kepada mereka azab hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,56,'اَلْمُلْكُ يَوْمَىِٕذٍ لِّلّٰهِ ۗيَحْكُمُ بَيْنَهُمْۗ فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ جَنّٰتِ النَّعِيْمِ ','Kekuasaan di hari itu ada pada Allah, Dia memberi keputusan di antara mereka. Maka orang-orang yang beriman dan beramal saleh adalah di dalam surga yang penuh kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,57,'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا فَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ ࣖ','Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, maka bagi mereka azab yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,58,'وَالَّذِيْنَ هَاجَرُوْا فِيْ سَبِيْلِ اللّٰهِ ثُمَّ قُتِلُوْٓا اَوْ مَاتُوْا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقًا حَسَنًاۗ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرّٰزِقِيْنَ ','Dan orang-orang yang berhijrah di jalan Allah, kemudian mereka di bunuh atau mati, benar-benar Allah akan memberikan kepada mereka rezeki yang baik (surga). Dan sesungguhnya Allah adalah sebaik-baik pemberi rezeki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,59,'لَيُدْخِلَنَّهُمْ مُّدْخَلًا يَّرْضَوْنَهٗۗ وَاِنَّ اللّٰهَ لَعَلِيْمٌ حَلِيْمٌ','Sesungguhnya Allah akan memasukkan mereka ke dalam suatu tempat (surga) yang mereka menyukainya. Dan sesungguhnya Allah Maha mengetahui lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,60,'۞ ذٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوْقِبَ بِهٖ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُ ۗاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ ','Demikianlah, dan barangsiapa membalas seimbang dengan penganiayaan yang pernah ia derita kemudian ia dianiaya (lagi), pasti Allah akan menolongnya. Sesungguhnya Allah benar-benar Maha Pemaaf lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,61,'ذٰلِكَ بِاَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَاَنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ','Yang demikian itu, adalah karena sesungguhnya Allah (kuasa) memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan bahwasanya Allah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,62,'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ ','(Kuasa Allah) yang demikian itu, adalah karena sesungguhnya Allah, Dialah (Tuhan) Yang Haq dan sesungguhnya apa saja yang mereka seru selain dari Allah, itulah yang batil, dan sesungguhnya Allah, Dialah Yang Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,63,'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۖ فَتُصْبِحُ الْاَرْضُ مُخْضَرَّةًۗ اِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ ۚ','Apakah kamu tiada melihat, bahwasanya Allah menurunkan air dari langit, lalu jadilah bumi itu hijau? Sesungguhnya Allah Maha Halus lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,64,'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَمِيْدُ ࣖ','Kepunyaan Allah-lah segala yang ada di langit dan segala yang ada di bumi. Dan sesungguhnya Allah benar-benar Maha Kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,65,'اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى الْاَرْضِ وَالْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِاَمْرِهٖۗ وَيُمْسِكُ السَّمَاۤءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِهٖۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ ','Apakah kamu tiada melihat bahwasanya Allah menundukkan bagimu apa yang ada di bumi dan bahtera yang berlayar di lautan dengan perintah-Nya. Dan Dia menahan (benda-benda) langit jatuh ke bumi, melainkan dengan izin-Nya? Sesungguhnya Allah benar-benar Maha Pengasih lagi Maha Penyayang kepada Manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,66,'وَهُوَ الَّذِيْٓ اَحْيَاكُمْ ۖ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ اِنَّ الْاِنْسَانَ لَكَفُوْرٌ ','Dan Dialah Allah yang telah menghidupkan kamu, kemudian mematikan kamu, kemudian menghidupkan kamu (lagi), sesungguhnya manusia itu, benar-benar sangat mengingkari nikmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,67,'لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوْهُ فَلَا يُنَازِعُنَّكَ فِى الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۗ اِنَّكَ لَعَلٰى هُدًى مُّسْتَقِيْمٍ ','Bagi tiap-tiap umat telah Kami tetapkan syari´at tertentu yang mereka lakukan, maka janganlah sekali-kali mereka membantah kamu dalam urusan (syari´at) ini dan serulah kepada (agama) Tuhanmu. Sesungguhnya kamu benar-benar berada pada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,68,'وَاِنْ جَادَلُوْكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُوْنَ ','Dan jika mereka membantah kamu, maka katakanlah: "Allah lebih mengetahui tentang apa yang kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,69,'اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ','Allah akan mengadili di antara kamu pada hari kiamat tentang apa yang kamu dahulu selalu berselisih padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,70,'اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمَاۤءِ وَالْاَرْضِۗ اِنَّ ذٰلِكَ فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ','Apakah kamu tidak mengetahui bahwa sesungguhnya Allah mengetahui apa saja yang ada di langit dan di bumi?; bahwasanya yang demikian itu terdapat dalam sebuah kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu amat mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,71,'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّمَا لَيْسَ لَهُمْ بِهٖ عِلْمٌ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ','Dan mereka menyembah selain Allah, apa yang Allah tidak menurunkan keterangan tentang itu, dan apa yang mereka sendiri tiada mempunyai pengetahuan terhadapnya. Dan bagi orang-orang yang zalim sekali-kali tidak ada seorang penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,72,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ تَعْرِفُ فِيْ وُجُوْهِ الَّذِيْنَ كَفَرُوا الْمُنْكَرَۗ يَكَادُوْنَ يَسْطُوْنَ بِالَّذِيْنَ يَتْلُوْنَ عَلَيْهِمْ اٰيٰتِنَاۗ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكُمْۗ  اَلنَّارُۗ وَعَدَهَا اللّٰهُ الَّذِيْنَ كَفَرُوْاۗ وَبِئْسَ الْمَصِيْرُ ࣖ','Dan apabila dibacakan di hadapan mereka ayat-ayat Kami yang terang, niscaya kamu melihat tanda-tanda keingkaran pada muka orang-orang yang kafir itu. Hampir-hampir mereka menyerang orang-orang yang membacakan ayat-ayat Kami di hadapan mereka. Katakanlah: "Apakah akan aku kabarkan kepadamu yang lebih buruk daripada itu, yaitu neraka?" Allah telah mengancamkannya kepada orang-orang yang kafir. Dan neraka itu adalah seburuk-buruknya tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,73,'يٰٓاَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوْا لَهٗ ۗاِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَنْ يَّخْلُقُوْا ذُبَابًا وَّلَوِ اجْتَمَعُوْا لَهٗ ۗوَاِنْ يَّسْلُبْهُمُ الذُّبَابُ شَيْـًٔا لَّا يَسْتَنْقِذُوْهُ مِنْهُۗ ضَعُفَ الطَّالِبُ وَالْمَطْلُوْبُ','Hai manusia, telah dibuat perumpamaan, maka dengarkanlah olehmu perumpamaan itu. Sesungguhnya segala yang kamu seru selain Allah sekali-kali tidak dapat menciptakan seekor lalatpun, walaupun mereka bersatu menciptakannya. Dan jika lalat itu merampas sesuatu dari mereka, tiadalah mereka dapat merebutnya kembali dari lalat itu. Amat lemahlah yang menyembah dan amat lemah (pulalah) yang disembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,74,'مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ ','Mereka tidak mengenal Allah dengan sebenar-benarnya. Sesungguhnya Allah benar-benar Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,75,'اَللّٰهُ يَصْطَفِيْ مِنَ الْمَلٰۤىِٕكَةِ رُسُلًا وَّمِنَ النَّاسِۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ۚ','Allah memilih utusan-utusan-(Nya) dari malaikat dan dari manusia; sesungguhnya Allah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,76,'يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ','Allah mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka. Dan hanya kepada Allah dikembalikan semua urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,77,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ارْكَعُوْا وَاسْجُدُوْا وَاعْبُدُوْا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُوْنَ ۚ۩','Hai orang-orang yang beriman, ruku´lah kamu, sujudlah kamu, sembahlah Tuhanmu dan perbuatlah kebajikan, supaya kamu mendapat kemenangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,17,22,78,'وَجَاهِدُوْا فِى اللّٰهِ حَقَّ جِهَادِهٖۗ هُوَ اجْتَبٰىكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِى الدِّيْنِ مِنْ حَرَجٍۗ مِلَّةَ اَبِيْكُمْ اِبْرٰهِيْمَۗ هُوَ سَمّٰىكُمُ الْمُسْلِمِيْنَ ەۙ مِنْ قَبْلُ وَفِيْ هٰذَا لِيَكُوْنَ الرَّسُوْلُ شَهِيْدًا عَلَيْكُمْ وَتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِۖ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاعْتَصِمُوْا بِاللّٰهِ ۗهُوَ مَوْلٰىكُمْۚ فَنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ ࣖ   ۔','Dan berjihadlah kamu pada jalan Allah dengan jihad yang sebenar-benarnya. Dia telah memilih kamu dan Dia sekali-kali tidak menjadikan untuk kamu dalam agama suatu kesempitan. (Ikutilah) agama orang tuamu Ibrahim. Dia (Allah) telah menamai kamu sekalian orang-orang muslim dari dahulu, dan (begitu pula) dalam (Al Quran) ini, supaya Rasul itu menjadi saksi atas dirimu dan supaya kamu semua menjadi saksi atas segenap manusia, maka dirikanlah sembahyang, tunaikanlah zakat dan berpeganglah kamu pada tali Allah. Dia adalah Pelindungmu, maka Dialah sebaik-baik Pelindung dan sebaik-baik Penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,1,' قَدْ اَفْلَحَ الْمُؤْمِنُوْنَ ۙ','Sesungguhnya beruntunglah orang-orang yang beriman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,2,' الَّذِيْنَ هُمْ فِيْ صَلٰو تِهِمْ خَاشِعُوْنَ','(yaitu) orang-orang yang khusyu´ dalam sembahyangnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,3,'وَالَّذِيْنَ هُمْ عَنِ اللَّغْوِ مُعْرِضُوْنَ ۙ','dan orang-orang yang menjauhkan diri dari (perbuatan dan perkataan) yang tiada berguna,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,4,'وَالَّذِيْنَ هُمْ لِلزَّكٰوةِ فَاعِلُوْنَ ۙ','dan orang-orang yang menunaikan zakat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,5,' وَالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَ ۙ','dan orang-orang yang menjaga kemaluannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,6,'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ','kecuali terhadap isteri-isteri mereka atau budak yang mereka miliki; maka sesungguhnya mereka dalam hal ini tiada tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,7,'فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَ ۚ','Barangsiapa mencari yang di balik itu maka mereka itulah orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,8,'وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَ ۙ','Dan orang-orang yang memelihara amanat-amanat (yang dipikulnya) dan janjinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,9,'وَالَّذِيْنَ هُمْ عَلٰى صَلَوٰتِهِمْ يُحَافِظُوْنَ ۘ','dan orang-orang yang memelihara sembahyangnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,10,'اُولٰۤىِٕكَ هُمُ الْوَارِثُوْنَ ۙ','Mereka itulah orang-orang yang akan mewarisi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,11,'الَّذِيْنَ يَرِثُوْنَ الْفِرْدَوْسَۗ هُمْ فِيْهَا خٰلِدُوْنَ ','(yakni) yang akan mewarisi surga Firdaus. Mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,12,'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلٰلَةٍ مِّنْ طِيْنٍ ۚ','Dan sesungguhnya Kami telah menciptakan manusia dari suatu saripati (berasal) dari tanah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,13,'ثُمَّ جَعَلْنٰهُ نُطْفَةً فِيْ قَرَارٍ مَّكِيْنٍ ۖ','Kemudian Kami jadikan saripati itu air mani (yang disimpan) dalam tempat yang kokoh (rahim).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,14,'ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظٰمًا فَكَسَوْنَا الْعِظٰمَ لَحْمًا ثُمَّ اَنْشَأْنٰهُ خَلْقًا اٰخَرَۗ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِقِيْنَۗ ','Kemudian air mani itu Kami jadikan segumpal darah, lalu segumpal darah itu Kami jadikan segumpal daging, dan segumpal daging itu Kami jadikan tulang belulang, lalu tulang belulang itu Kami bungkus dengan daging. Kemudian Kami jadikan dia makhluk yang (berbentuk) lain. Maka Maha sucilah Allah, Pencipta Yang Paling Baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,15,' ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُوْنَ ۗ','Kemudian, sesudah itu, sesungguhnya kamu sekalian benar-benar akan mati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,16,'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُوْنَ','Kemudian, sesungguhnya kamu sekalian akan dibangkitkan (dari kuburmu) di hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,17,'وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَاۤىِٕقَۖ وَمَا كُنَّا عَنِ الْخَلْقِ غٰفِلِيْنَ','Dan sesungguhnya Kami telah menciptakan di atas kamu tujuh buah jalan (tujuh buah langit); dan Kami tidaklah lengah terhadap ciptaan (Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,18,'وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍ فَاَسْكَنّٰهُ فِى الْاَرْضِۖ وَاِنَّا عَلٰى ذَهَابٍۢ بِهٖ لَقٰدِرُوْنَ ۚ','Dan Kami turunkan air dari langit menurut suatu ukuran; lalu Kami jadikan air itu menetap di bumi, dan sesungguhnya Kami benar-benar berkuasa menghilangkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,19,'فَاَنْشَأْنَا لَكُمْ بِهٖ جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍۘ لَكُمْ فِيْهَا فَوَاكِهُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ','Lalu dengan air itu, Kami tumbuhkan untuk kamu kebun-kebun kurma dan anggur; di dalam kebun-kebun itu kamu peroleh buah-buahan yang banyak dan sebahagian dari buah-buahan itu kamu makan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,20,'وَشَجَرَةً تَخْرُجُ مِنْ طُوْرِ سَيْنَاۤءَ تَنْۢبُتُ بِالدُّهْنِ وَصِبْغٍ لِّلْاٰكِلِيْنَ ','dan pohon kayu keluar dari Thursina (pohon zaitun), yang menghasilkan minyak, dan pemakan makanan bagi orang-orang yang makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,21,'وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةًۗ  نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهَا وَلَكُمْ فِيْهَا مَنَافِعُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ ','Dan sesungguhnya pada binatang-binatang ternak, benar-benar terdapat pelajaran yang penting bagi kamu, Kami memberi minum kamu dari air susu yang ada dalam perutnya, dan (juga) pada binatang-binatang ternak itu terdapat faedah yang banyak untuk kamu, dan sebagian daripadanya kamu makan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,22,'وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَ ࣖ','dan di atas punggung binatang-binatang ternak itu dan (juga) di atas perahu-perahu kamu diangkut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,23,'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ','Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, lalu ia berkata: "Hai kaumku, sembahlah oleh kamu Allah, (karena) sekali-kali tidak ada Tuhan bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,24,'فَقَالَ الْمَلَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يُرِيْدُ اَنْ يَّتَفَضَّلَ عَلَيْكُمْۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَنْزَلَ مَلٰۤىِٕكَةً  ۖمَّا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ۚ','Maka pemuka-pemuka orang yang kafir di antara kaumnya menjawab: "Orang ini tidak lain hanyalah manusia seperti kamu, yang bermaksud hendak menjadi seorang yang lebih tinggi dari kamu. Dan kalau Allah menghendaki, tentu Dia mengutus beberapa orang malaikat. Belum pernah kami mendengar (seruan yang seperti) ini pada masa nenek moyang kami yang dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,25,'اِنْ هُوَ اِلَّا رَجُلٌۢ بِهٖ جِنَّةٌ فَتَرَبَّصُوْا بِهٖ حَتّٰى حِيْنٍ','la tidak lain hanyalah seorang laki-laki yang berpenyakit gila, maka tunggulah (sabarlah) terhadapnya sampai suatu waktu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,26,'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ ','Nuh berdoa: "Ya Tuhanku, tolonglah aku, karena mereka mendustakan aku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,27,'فَاَوْحَيْنَآ اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ فَاسْلُكْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْاۚ اِنَّهُمْ مُّغْرَقُوْنَ','Lalu Kami wahyukan kepadanya: "Buatlah bahtera di bawah penilikan dan petunjuk Kami, maka apabila perintah Kami telah datang dan tanur telah memancarkan air, maka masukkanlah ke dalam bahtera itu sepasang dari tiap-tiap (jenis), dan (juga) keluargamu, kecuali orang yang telah lebih dahulu ditetapkan (akan ditimpa azab) di antara mereka. Dan janganlah kamu bicarakan dengan Aku tentang orang-orang yang zalim, karena sesungguhnya mereka itu akan ditenggelamkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,28,'فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَّعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ نَجّٰىنَا مِنَ الْقَوْمِ الظّٰلِمِيْنَ','Apabila kamu dan orang-orang yang bersamamu telah berada di atas bahtera itu, maka ucapkanlah: "Segala puji bagi Allah yang telah menyelamatkan kami dari orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,29,'وَقُلْ رَّبِّ اَنْزِلْنِيْ مُنْزَلًا مُّبٰرَكًا وَّاَنْتَ خَيْرُ الْمُنْزِلِيْنَ','Dan berdoalah: Ya Tuhanku, tempatkanlah aku pada tempat yang diberkati, dan Engkau adalah sebaik-baik Yang memberi tempat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,30,'اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ وَّاِنْ كُنَّا لَمُبْتَلِيْنَ','Sesungguhnya pada (kejadian) itu benar-benar terdapat beberapa tanda (kebesaran Allah), dan sesungguhnya Kami menimpakan azab (kepada kaum Nuh itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,31,'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ ۚ','Kemudian, Kami jadikan sesudah mereka umat yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,32,'فَاَرْسَلْنَا فِيْهِمْ رَسُوْلًا مِّنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ࣖ ','Lalu Kami utus kepada mereka, seorang rasul dari kalangan mereka sendiri (yang berkata): "Sembahlah Allah oleh kamu sekalian, sekali-kali tidak ada Tuhan selain daripada-Nya. Maka mengapa kamu tidak bertakwa (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,33,'وَقَالَ الْمَلَاُ مِنْ قَوْمِهِ الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِلِقَاۤءِ الْاٰخِرَةِ وَاَتْرَفْنٰهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُوْنَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُوْنَ ','Dan berkatalah pemuka-pemuka yang kafir di antara kaumnya dan yang mendustakan akan menemui hari akhirat (kelak) dan yang telah Kami mewahkan mereka dalam kehidupan di dunia: "(Orang) ini tidak lain hanyalah manusia seperti kamu, dia makan dari apa yang kamu makan, dan meminum dari apa yang kamu minum.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,34,'وَلَىِٕنْ اَطَعْتُمْ بَشَرًا مِّثْلَكُمْ اِنَّكُمْ اِذًا لَّخٰسِرُوْنَ ۙ','Dan sesungguhnya jika kamu sekalian mentaati manusia yang seperti kamu, niscaya bila demikian, kamu benar-benar (menjadi) orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,35,'اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَّعِظَامًا اَنَّكُمْ مُّخْرَجُوْنَ ۖ ','Apakah ia menjanjikan kepada kamu sekalian, bahwa bila kamu telah mati dan telah menjadi tanah dan tulang belulang, kamu sesungguhnya akan dikeluarkan (dari kuburmu)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,36,'۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوْعَدُوْنَ ۖ ','jauh, jauh sekali (dari kebenaran) apa yang diancamkan kepada kamu itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,37,'اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ۖ ','kehidupan itu tidak lain hanyalah kehidupan kita di dunia ini, kita mati dan kita hidup dan sekali-kali tidak akan dibangkitkan lagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,38,'اِنْ هُوَ اِلَّا رَجُلُ ِۨافْتَرٰى عَلَى اللّٰهِ كَذِبًا وَّمَا نَحْنُ لَهٗ بِمُؤْمِنِيْنَ','Ia tidak lain hanyalah seorang yang mengada-adakan kebohongan terhadap Allah, dan kami sekali-kali tidak akan beriman kepadanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,39,'قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ','Rasul itu berdoa: "Ya Tuhanku, tolonglah aku karena mereka mendustakanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,40,'قَالَ عَمَّا قَلِيْلٍ لَّيُصْبِحُنَّ نٰدِمِيْنَ ۚ ','Allah berfirman: "Dalam sedikit waktu lagi pasti mereka akan menjadi orang-orang yang menyesal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,41,'فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنٰهُمْ غُثَاۤءًۚ فَبُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ','Maka dimusnahkanlah mereka oleh suara yang mengguntur dengan hak dan Kami jadikan mereka (sebagai) sampah banjir maka kebinasaanlah bagi orang-orang yang zalim itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,42,'ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قُرُوْنًا اٰخَرِيْنَ ۗ','Kemudian Kami ciptakan sesudah mereka umat-umat yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,43,'مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ۗ','Tidak (dapat) sesuatu umatpun mendahului ajalnya, dan tidak (dapat pula) mereka terlambat (dari ajalnya itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,44,'ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۗ  كُلَّمَا جَاۤءَ اُمَّةً رَّسُوْلُهَا كَذَّبُوْهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضًا وَّجَعَلْنٰهُمْ اَحَادِيْثَۚ فَبُعْدًا لِّقَوْمٍ لَّا يُؤْمِنُوْنَ ','Kemudian Kami utus (kepada umat-umat itu) rasul-rasul Kami berturut-turut. Tiap-tiap seorang rasul datang kepada umatnya, umat itu mendustakannya, maka Kami perikutkan sebagian mereka dengan sebagian yang lain. Dan Kami jadikan mereka buah tutur (manusia), maka kebinasaanlah bagi orang-orang yang tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,45,'ثُمَّ اَرْسَلْنَا مُوْسٰى وَاَخَاهُ هٰرُوْنَ ەۙ بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ','Kemudian Kami utus Musa dan saudaranya Harun dengan membawa tanda-tanda (Kebesaran) Kami, dan bukti yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,46,'اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا عَالِيْنَ ۚ','kepada Fir´aun dan pembesar-pembesar kaumnya, maka mereka ini takbur dan mereka adalah orang-orang yang sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,47,'فَقَالُوْٓا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عٰبِدُوْنَ ۚ','Dan mereka berkata: "Apakah (patut) kita percaya kepada dua orang manusia seperti kita (juga), padahal kaum mereka (Bani Israil) adalah orang-orang yang menghambakan diri kepada kita?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,48,'فَكَذَّبُوْهُمَا فَكَانُوْا مِنَ الْمُهْلَكِيْنَ ','Maka (tetaplah) mereka mendustakan keduanya, sebab itu mereka adalah termasuk orang-orang yang dibinasakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,49,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ لَعَلَّهُمْ يَهْتَدُوْنَ ','Dan sesungguhnya telah Kami berikan Al Kitab (Taurat) kepada Musa, agar mereka (Bani Israil) mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,50,'وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهٗٓ اٰيَةً وَّاٰوَيْنٰهُمَآ اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَّمَعِيْنٍ ࣖ','Dan telah Kami jadikan (Isa) putera Maryam beserta ibunya suatu bukti yang nyata bagi (kekuasaan Kami), dan Kami melindungi mereka di suatu tanah tinggi yang datar yang banyak terdapat padang-padang rumput dan sumber-sumber air bersih yang mengalir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,51,'يٰٓاَيُّهَا الرُّسُلُ كُلُوْا مِنَ الطَّيِّبٰتِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ۗ','Hai rasul-rasul, makanlah dari makanan yang baik-baik, dan kerjakanlah amal yang saleh. Sesungguhnya Aku Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,52,'وَاِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةً وَّاَنَا۠ رَبُّكُمْ فَاتَّقُوْنِ','Sesungguhnya (agama tauhid) ini, adalah agama kamu semua, agama yang satu, dan Aku adalah Tuhanmu, maka bertakwalah kepada-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,53,'فَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْ زُبُرًاۗ  كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ ','Kemudian mereka (pengikut-pengikut rasul itu) menjadikan agama mereka terpecah belah menjadi beberapa pecahan. Tiap-tiap golongan merasa bangga dengan apa yang ada pada sisi mereka (masing-masing).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,54,'فَذَرْهُمْ فِيْ غَمْرَتِهِمْ حَتّٰى حِيْنٍ ','Maka biarkanlah mereka dalam kesesatannya sampai suatu waktu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,55,'اَيَحْسَبُوْنَ اَنَّمَا نُمِدُّهُمْ بِهٖ مِنْ مَّالٍ وَّبَنِيْنَ ۙ','Apakah mereka mengira bahwa harta dan anak-anak yang Kami berikan kepada mereka itu (berarti bahwa),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,56,'نُسَارِعُ لَهُمْ فِى الْخَيْرٰتِۗ بَلْ لَّا يَشْعُرُوْنَ','Kami bersegera memberikan kebaikan-kebaikan kepada mereka? Tidak, sebenarnya mereka tidak sadar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,57,'اِنَّ الَّذِيْنَ هُمْ مِّنْ خَشْيَةِ رَبِّهِمْ مُّشْفِقُوْنَ ۙ','Sesungguhnya orang-orang yang berhati-hati karena takut akan (azab) Tuhan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,58,'وَالَّذِيْنَ هُمْ بِاٰيٰتِ رَبِّهِمْ يُؤْمِنُوْنَ ۙ','Dan orang-orang yang beriman dengan ayat-ayat Tuhan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,59,'وَالَّذِيْنَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُوْنَ ۙ','Dan orang-orang yang tidak mempersekutukan dengan Tuhan mereka (sesuatu apapun),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,60,'وَالَّذِيْنَ يُؤْتُوْنَ مَآ اٰتَوْا وَّقُلُوْبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رٰجِعُوْنَ ۙ','Dan orang-orang yang memberikan apa yang telah mereka berikan, dengan hati yang takut, (karena mereka tahu bahwa) sesungguhnya mereka akan kembali kepada Tuhan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,61,'اُولٰۤىِٕكَ يُسَارِعُوْنَ فِى الْخَيْرٰتِ وَهُمْ لَهَا سٰبِقُوْنَ','mereka itu bersegera untuk mendapat kebaikan-kebaikan, dan merekalah orang-orang yang segera memperolehnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,62,'وَلَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۖ وَلَدَيْنَا كِتٰبٌ يَّنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ ','Kami tiada membebani seseorang melainkan menurut kesanggupannya, dan pada sisi Kami ada suatu kitab yang membicarakan kebenaran, dan mereka tidak dianiaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,63,'بَلْ قُلُوْبُهُمْ فِيْ غَمْرَةٍ مِّنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِّنْ دُوْنِ ذٰلِكَ هُمْ لَهَا عَامِلُوْنَ','Tetapi hati orang-orang kafir itu dalam kesesatan dari (memahami kenyataan) ini, dan mereka banyak mengerjakan perbuatan-perbuatan (buruk) selain daripada itu, mereka tetap mengerjakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,64,'حَتّٰٓى اِذَآ اَخَذْنَا مُتْرَفِيْهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُوْنَ ۗ','Hingga apabila Kami timpakan azab, kepada orang-orang yang hidup mewah di antara mereka, dengan serta merta mereka memekik minta tolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,65,'لَا تَجْـَٔرُوا الْيَوْمَۖ اِنَّكُمْ مِّنَّا لَا تُنْصَرُوْنَ ','Janganlah kamu memekik minta tolong pada hari ini. Sesungguhnya kamu tiada akan mendapat pertolongan dari Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,66,'قَدْ كَانَتْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُوْنَ ۙ','Sesungguhnya ayat-ayat-Ku (Al Quran) selalu dibacakan kepada kamu sekalian, maka kamu selalu berpaling ke belakang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,67,'مُسْتَكْبِرِيْنَۙ بِهٖ سٰمِرًا تَهْجُرُوْنَ ','dengan menyombongkan diri terhadap Al Quran itu dan mengucapkan perkataan-perkataan keji terhadapnya di waktu kamu bercakap-cakap di malam hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,68,'اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَاۤءَهُمْ مَّا لَمْ يَأْتِ اٰبَاۤءَهُمُ الْاَوَّلِيْنَ ۖ','Maka apakah mereka tidak memperhatikan perkataan (Kami), atau apakah telah datang kepada mereka apa yang tidak pernah datang kepada nenek moyang mereka dahulu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,69,'اَمْ لَمْ يَعْرِفُوْا رَسُوْلَهُمْ فَهُمْ لَهٗ مُنْكِرُوْنَ ۖ','Ataukah mereka tidak mengenal rasul mereka, karena itu mereka memungkirinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,70,'اَمْ يَقُوْلُوْنَ بِهٖ جِنَّةٌ ۗ بَلْ جَاۤءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كٰرِهُوْنَ ','Atau (apakah patut) mereka berkata: "Padanya (Muhammad) ada penyakit gila". Sebenarnya dia telah membawa kebenaran kepada mereka, dan kebanyakan mereka benci kepada kebenaran itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,71,'وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَاۤءَهُمْ لَفَسَدَتِ السَّمٰوٰتُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ بَلْ اَتَيْنٰهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُّعْرِضُوْنَ ۗ','Andaikata kebenaran itu menuruti hawa nafsu mereka, pasti binasalah langit dan bumi ini, dan semua yang ada di dalamnya. Sebenarnya Kami telah mendatangkan kepada mereka kebanggaan (Al Quran) mereka tetapi mereka berpaling dari kebanggaan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,72,'اَمْ تَسْـَٔلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖوَّهُوَ خَيْرُ الرّٰزِقِيْنَ','Atau kamu meminta upah kepada mereka?", maka upah dari Tuhanmu adalah lebih baik, dan Dia adalah Pemberi rezeki Yang Paling Baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,73,'وَاِنَّكَ لَتَدْعُوْهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Dan sesungguhnya kamu benar-benar menyeru mereka kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,74,'وَاِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُوْنَ ','Dan sesungguhnya orang-orang yang tidak beriman kepada negeri akhirat benar-benar menyimpang dari jalan (yang lurus).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,75,'۞ وَلَوْ رَحِمْنٰهُمْ وَكَشَفْنَا مَا بِهِمْ مِّنْ ضُرٍّ لَّلَجُّوْا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ','Andaikata mereka Kami belas kasihani, dan Kami lenyapkan kemudharatan yang mereka alami, benar-benar mereka akan terus menerus terombang-ambing dalam keterlaluan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,76,'وَلَقَدْ اَخَذْنٰهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوْا لِرَبِّهِمْ وَمَا يَتَضَرَّعُوْنَ','Dan sesungguhnya Kami telah pernah menimpakan azab kepada mereka, maka mereka tidak tunduk kepada Tuhan mereka, dan (juga) tidak memohon (kepada-Nya) dengan merendahkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,77,'حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيْدٍ اِذَا هُمْ فِيْهِ مُبْلِسُوْنَ ࣖ ','Hingga apabila Kami bukakan untuk mereka suatu pintu tempat azab yang amat sangat (di waktu itulah) tiba-tiba mereka menjadi putus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,78,'وَهُوَ الَّذِيْٓ اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ','Dan Dialah yang telah menciptakan bagi kamu sekalian, pendengaran, penglihatan dan hati. Amat sedikitlah kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,79,'وَهُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ','Dan Dialah yang menciptakan serta mengembang biakkan kamu di bumi ini dan kepada-Nya-lah kamu akan dihimpunkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,80,'وَهُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۗ اَفَلَا تَعْقِلُوْنَ','Dan Dialah yang menghidupkan dan mematikan, dan Dialah yang (mengatur) pertukaran malam dan siang. Maka apakah kamu tidak memahaminya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,81,'بَلْ قَالُوْا مِثْلَ مَا قَالَ الْاَوَّلُوْنَ','Sebenarnya mereka mengucapkan perkataan yang serupa dengan perkataan yang diucapkan oleh orang-orang dahulu kala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,82,'قَالُوْٓا ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَ','Mereka berkata: "Apakah betul, apabila kami telah mati dan kami telah menjadi tanah dan tulang belulang, apakah sesungguhnya kami benar-benar akan dibangkitkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,83,'لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَاۤؤُنَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ','Sesungguhnya kami dan bapak-bapak kami telah diberi ancaman (dengan) ini dahulu, ini tidak lain hanyalah dongengan orang-orang dahulu kala!".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,84,'قُلْ لِّمَنِ الْاَرْضُ وَمَنْ فِيْهَآ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Katakanlah: "Kepunyaan siapakah bumi ini, dan semua yang ada padanya, jika kamu mengetahui?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,85,'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَذَكَّرُوْنَ ','Mereka akan menjawab: "Kepunyaan Allah". Katakanlah: "Maka apakah kamu tidak ingat?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,86,'قُلْ مَنْ رَّبُّ السَّمٰوٰتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيْمِ','Katakanlah: "Siapakah Yang Empunya langit yang tujuh dan Yang Empunya ´Arsy yang besar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,87,'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَتَّقُوْنَ','Mereka akan menjawab: "Kepunyaan Allah". Katakanlah: "Maka apakah kamu tidak bertakwa?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,88,'قُلْ مَنْۢ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّهُوَ يُجِيْرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Katakanlah: "Siapakah yang di tangan-Nya berada kekuasaan atas segala sesuatu sedang Dia melindungi, tetapi tidak ada yang dapat dilindungi dari (azab)-Nya, jika kamu mengetahui?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,89,'سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ فَاَنّٰى تُسْحَرُوْنَ','Mereka akan menjawab: "Kepunyaan Allah". Katakanlah: "(Kalau demikian), maka dari jalan manakah kamu ditipu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,90,'بَلْ اَتَيْنٰهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكٰذِبُوْنَ','Sebenarnya Kami telah membawa kebenaran kepada mereka, dan sesungguhnya mereka benar-benar orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,91,'مَا اتَّخَذَ اللّٰهُ مِنْ وَّلَدٍ وَّمَا كَانَ مَعَهٗ مِنْ اِلٰهٍ اِذًا لَّذَهَبَ كُلُّ اِلٰهٍۢ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۗ سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَ ۙ','Allah sekali-kali tidak mempunyai anak, dan sekali-kali tidak ada tuhan (yang lain) beserta-Nya, kalau ada tuhan beserta-Nya, masing-masing tuhan itu akan membawa makhluk yang diciptakannya, dan sebagian dari tuhan-tuhan itu akan mengalahkan sebagian yang lain. Maha Suci Allah dari apa yang mereka sifatkan itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,92,'عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ࣖ','Yang mengetahui semua yang ghaib dan semua yang nampak, maka Maha Tinggilah Dia dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,93,'قُلْ رَّبِّ اِمَّا تُرِيَنِّيْ مَا يُوْعَدُوْنَ ۙ','Katakanlah: "Ya Tuhanku, jika Engkau sungguh-sungguh hendak memperlihatkan kepadaku azab yang diancamkan kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,94,'رَبِّ فَلَا تَجْعَلْنِيْ فِى الْقَوْمِ الظّٰلِمِيْنَ','ya Tuhanku, maka janganlah Engkau jadikan aku berada di antara orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,95,'وَاِنَّا عَلٰٓى اَنْ نُّرِيَكَ مَا نَعِدُهُمْ لَقٰدِرُوْنَ','Dan sesungguhnya Kami benar-benar kuasa untuk memperlihatkan kepadamu apa yang Kami ancamkan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,96,'اِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ السَّيِّئَةَۗ  نَحْنُ اَعْلَمُ بِمَا يَصِفُوْنَ ','Tolaklah perbuatan buruk mereka dengan yang lebih baik. Kami lebih mengetahui apa yang mereka sifatkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,97,'وَقُلْ رَّبِّ اَعُوْذُ بِكَ مِنْ هَمَزٰتِ الشَّيٰطِيْنِ ۙ','Dan katakanlah: "Ya Tuhanku aku berlindung kepada Engkau dari bisikan-bisikan syaitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,98,'وَاَعُوْذُ بِكَ رَبِّ اَنْ يَّحْضُرُوْنِ ','Dan aku berlindung (pula) kepada Engkau ya Tuhanku, dari kedatangan mereka kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,99,'حَتّٰٓى اِذَا جَاۤءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُوْنِ ۙ','(Demikianlah keadaan orang-orang kafir itu), hingga apabila datang kematian kepada seseorang dari mereka, dia berkata: "Ya Tuhanku kembalikanlah aku (ke dunia),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,100,'لَعَلِّيْٓ اَعْمَلُ صَالِحًا فِيْمَا تَرَكْتُ كَلَّاۗ اِنَّهَا كَلِمَةٌ هُوَ قَاۤىِٕلُهَاۗ وَمِنْ وَّرَاۤىِٕهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُوْنَ ','agar aku berbuat amal yang saleh terhadap yang telah aku tinggalkan. Sekali-kali tidak. Sesungguhnya itu adalah perkataan yang diucapkannya saja. Dan di hadapan mereka ada dinding sampal hari mereka dibangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,101,'فَاِذَا نُفِخَ فِى الصُّوْرِ فَلَآ اَنْسَابَ بَيْنَهُمْ يَوْمَىِٕذٍ وَّلَا يَتَسَاۤءَلُوْنَ','Apabila sangkakala ditiup maka tidaklah ada lagi pertalian nasab di antara mereka pada hari itu, dan tidak ada pula mereka saling bertanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,102,'فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Barangsiapa yang berat timbangan (kebaikan)nya, maka mereka itulah orang-orang yang dapat keberuntungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,103,'وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فِيْ جَهَنَّمَ خٰلِدُوْنَ ۚ','Dan barangsiapa yang ringan timbangannya, maka mereka itulah orang-orang yang merugikan dirinya sendiri, mereka kekal di dalam neraka Jahannam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,104,'تَلْفَحُ وُجُوْهَهُمُ النَّارُ وَهُمْ فِيْهَا كَالِحُوْنَ ','Muka mereka dibakar api neraka, dan mereka di dalam neraka itu dalam keadaan cacat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,105,'اَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُوْنَ','Bukankah ayat-ayat-Ku telah dibacakan kepadamu sekalian, tetapi kamu selalu mendustakannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,106,'قَالُوْا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَاۤلِّيْنَ','Mereka berkata: "Ya Tuhan kami, kami telah dikuasai oleh kejahatan kami, dan adalah kami orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,107,'رَبَّنَآ اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظٰلِمُوْنَ','Ya Tuhan kami, keluarkanlah kami daripadanya (dan kembalikanlah kami ke dunia), maka jika kami kembali (juga kepada kekafiran), sesungguhnya kami adalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,108,'قَالَ اخْسَـُٔوْا فِيْهَا وَلَا تُكَلِّمُوْنِ','Allah berfirman: "Tinggallah dengan hina di dalamnya, dan janganlah kamu berbicara dengan Aku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,109,'اِنَّهٗ كَانَ فَرِيْقٌ مِّنْ عِبَادِيْ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ۚ','Sesungguhnya, ada segolongan dari hamba-hamba-Ku berdoa (di dunia): "Ya Tuhan kami, kami telah beriman, maka ampunilah kami dan berilah kami rahmat dan Engkau adalah Pemberi rahmat Yang Paling Baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,110,'فَاتَّخَذْتُمُوْهُمْ سِخْرِيًّا حَتّٰٓى اَنْسَوْكُمْ ذِكْرِيْ وَكُنْتُمْ مِّنْهُمْ تَضْحَكُوْنَ','Lalu kamu menjadikan mereka buah ejekan, sehingga (kesibukan) kamu mengejek mereka, menjadikan kamu lupa mengingat Aku, dan adalah kamu selalu mentertawakan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,111,'اِنِّيْ جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوْٓاۙ اَنَّهُمْ هُمُ الْفَاۤىِٕزُوْنَ','Sesungguhnya Aku memberi balasan kepada mereka di hari ini, karena kesabaran mereka; sesungguhnya mereka itulah orang-orang yang menang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,112,'قٰلَ كَمْ لَبِثْتُمْ فِى الْاَرْضِ عَدَدَ سِنِيْنَ','Allah bertanya: "Berapa tahunkah lamanya kamu tinggal di bumi?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,113,'قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَاۤدِّيْنَ ','Mereka menjawab: "Kami tinggal (di bumi) sehari atau setengah hari, maka tanyakanlah kepada orang-orang yang menghitung".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,114,'قٰلَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا لَّوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُوْنَ','Allah berfirman: "Kamu tidak tinggal (di bumi) melainkan sebentar saja, kalau kamu sesungguhnya mengetahui"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,115,'اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنٰكُمْ عَبَثًا وَّاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُوْنَ','Maka apakah kamu mengira, bahwa sesungguhnya Kami menciptakan kamu secara main-main (saja), dan bahwa kamu tidak akan dikembalikan kepada Kami?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,116,'فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ  لَآ اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَرِيْمِ','Maka Maha Tinggi Allah, Raja Yang Sebenarnya; tidak ada Tuhan selain Dia, Tuhan (Yang mempunyai) ´Arsy yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,117,'وَمَنْ يَّدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ لَا بُرْهَانَ لَهٗ بِهٖۙ فَاِنَّمَا حِسَابُهٗ عِنْدَ رَبِّهٖۗ اِنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ','Dan barangsiapa menyembah tuhan yang lain di samping Allah, padahal tidak ada suatu dalilpun baginya tentang itu, maka sesungguhnya perhitungannya di sisi Tuhannya. Sesungguhnya orang-orang yang kafir itu tiada beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,23,118,'وَقُلْ رَّبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ࣖ','Dan katakanlah: "Ya Tuhanku berilah ampun dan berilah rahmat, dan Engkau adalah Pemberi rahmat Yang Paling baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,1,'سُوْرَةٌ اَنْزَلْنٰهَا وَفَرَضْنٰهَا وَاَنْزَلْنَا فِيْهَآ اٰيٰتٍۢ بَيِّنٰتٍ لَّعَلَّكُمْ تَذَكَّرُوْنَ','(Ini adalah) satu surat yang Kami turunkan dan Kami wajibkan (menjalankan hukum-hukum yang ada di dalam)nya, dan Kami turunkan di dalamnya ayat ayat yang jelas, agar kamu selalu mengingatinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,2,'اَلزَّانِيَةُ وَالزَّانِيْ فَاجْلِدُوْا كُلَّ وَاحِدٍ مِّنْهُمَا مِائَةَ جَلْدَةٍ ۖوَّلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِيْ دِيْنِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَاۤىِٕفَةٌ مِّنَ الْمُؤْمِنِيْنَ','Perempuan yang berzina dan laki-laki yang berzina, maka deralah tiap-tiap seorang dari keduanya seratus dali dera, dan janganlah belas kasihan kepada keduanya mencegah kamu untuk (menjalankan) agama Allah, jika kamu beriman kepada Allah, dan hari akhirat, dan hendaklah (pelaksanaan) hukuman mereka disaksikan oleh sekumpulan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,3,'اَلزَّانِيْ لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةً ۖوَّالزَّانِيَةُ لَا يَنْكِحُهَآ اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِنِيْنَ','Laki-laki yang berzina tidak mengawini melainkan perempuan yang berzina, atau perempuan yang musyrik; dan perempuan yang berzina tidak dikawini melainkan oleh laki-laki yang berzina atau laki-laki musyrik, dan yang demikian itu diharamkan atas oran-orang yang mukmin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,4,'وَالَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ ثُمَّ لَمْ يَأْتُوْا بِاَرْبَعَةِ شُهَدَاۤءَ فَاجْلِدُوْهُمْ ثَمٰنِيْنَ جَلْدَةً وَّلَا تَقْبَلُوْا لَهُمْ شَهَادَةً اَبَدًاۚ وَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ۙ','Dan orang-orang yang menuduh wanita-wanita yang baik-baik (berbuat zina) dan mereka tidak mendatangkan empat orang saksi, maka deralah mereka (yang menuduh itu) delapan puluh kali dera, dan janganlah kamu terima kesaksian mereka buat selama-lamanya. Dan mereka itulah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,5,'اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۚ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','kecuali orang-orang yang bertaubat sesudah itu dan memperbaiki (dirinya), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,6,'وَالَّذِيْنَ يَرْمُوْنَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَّهُمْ شُهَدَاۤءُ اِلَّآ اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهٰدٰتٍۢ بِاللّٰهِ ۙاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ','Dan orang-orang yang menuduh isterinya (berzina), padahal mereka tidak ada mempunyai saksi-saksi selain diri mereka sendiri, maka persaksian orang itu ialah empat kali bersumpah dengan nama Allah, sesungguhnya dia adalah termasuk orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,7,'وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ كَانَ مِنَ الْكٰذِبِيْنَ','Dan (sumpah) yang kelima: bahwa laknat Allah atasnya, jika dia termasuk orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,8,'وَيَدْرَؤُا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهٰدٰتٍۢ بِاللّٰهِ اِنَّهٗ لَمِنَ الْكٰذِبِيْنَ ۙ','Istrinya itu dihindarkan dari hukuman oleh sumpahnya empat kali atas nama Allah sesungguhnya suaminya itu benar-benar termasuk orang-orang yang dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,9,'وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَآ اِنْ كَانَ مِنَ الصّٰدِقِيْنَ ','dan (sumpah) yang kelima: bahwa laknat Allah atasnya jika suaminya itu termasuk orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,10,'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ تَوَّابٌ حَكِيْمٌ ࣖ','Dan andaikata tidak ada kurnia Allah dan rahmat-Nya atas dirimu dan (andaikata) Allah bukan Penerima Taubat lagi Maha Bijaksana, (niscaya kamu akan mengalami kesulitan-kesulitan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,11,'اِنَّ الَّذِيْنَ جَاۤءُوْ بِالْاِفْكِ عُصْبَةٌ مِّنْكُمْۗ  لَا تَحْسَبُوْهُ شَرًّا لَّكُمْۗ بَلْ هُوَ خَيْرٌ لَّكُمْۗ لِكُلِّ امْرِئٍ مِّنْهُمْ مَّا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذِيْ تَوَلّٰى كِبْرَهٗ مِنْهُمْ لَهٗ عَذَابٌ عَظِيْمٌ','Sesungguhnya orang-orang yang membawa berita bohong itu adalah dari golongan kamu juga. Janganlah kamu kira bahwa berita bohong itu buruk bagi kamu bahkan ia adalah baik bagi kamu. Tiap-tiap seseorang dari mereka mendapat balasan dari dosa yang dikerjakannya. Dan siapa di antara mereka yang mengambil bahagian yang terbesar dalam penyiaran berita bohong itu baginya azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,12,'لَوْلَآ اِذْ سَمِعْتُمُوْهُ ظَنَّ الْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بِاَنْفُسِهِمْ خَيْرًاۙ وَّقَالُوْا هٰذَآ اِفْكٌ مُّبِيْنٌ','Mengapa di waktu kamu mendengar berita bohon itu orang-orang mukminin dan mukminat tidak bersangka baik terhadap diri mereka sendiri, dan (mengapa tidak) berkata: "Ini adalah suatu berita bohong yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,13,'لَوْلَا جَاۤءُوْ عَلَيْهِ بِاَرْبَعَةِ شُهَدَاۤءَۚ فَاِذْ لَمْ يَأْتُوْا بِالشُّهَدَاۤءِ فَاُولٰۤىِٕكَ عِنْدَ اللّٰهِ هُمُ الْكٰذِبُوْنَ','Mengapa mereka (yang menuduh itu) tidak mendatangkan empat orang saksi atas berita bohong itu? Olah karena mereka tidak mendatangkan saksi-saksi maka mereka itulah pada sisi Allah orang-orang yang dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,14,'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ فِى الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ فِيْ مَآ اَفَضْتُمْ فِيْهِ عَذَابٌ عَظِيْمٌ ','Sekiranya tidak ada kurnia Allah dan rahmat-Nya kepada kamu semua di dunia dan di akhirat, niscaya kamu ditimpa azab yang besar, karena pembicaraan kamu tentang berita bohong itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,15,'اِذْ تَلَقَّوْنَهٗ بِاَلْسِنَتِكُمْ وَتَقُوْلُوْنَ بِاَفْوَاهِكُمْ مَّا لَيْسَ لَكُمْ بِهٖ عِلْمٌ وَّتَحْسَبُوْنَهٗ هَيِّنًاۙ وَّهُوَ عِنْدَ اللّٰهِ عَظِيْمٌ ۚ','(Ingatlah) di waktu kamu menerima berita bohong itu dari mulut ke mulut dan kamu katakan dengan mulutmu apa yang tidak kamu ketahui sedikit juga, dan kamu menganggapnya suatu yang ringan saja. Padahal dia pada sisi Allah adalah besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,16,'وَلَوْلَآ اِذْ سَمِعْتُمُوْهُ قُلْتُمْ مَّا يَكُوْنُ لَنَآ اَنْ نَّتَكَلَّمَ بِهٰذَاۖ سُبْحٰنَكَ هٰذَا بُهْتَانٌ عَظِيْمٌ','Dan mengapa kamu tidak berkata, diwaktu mendengar berita bohong itu: "Sekali-kali tidaklah pantas bagi kita memperkatakan ini, Maha Suci Engkau (Ya Tuhan kami), ini adalah dusta yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,17,'يَعِظُكُمُ اللّٰهُ اَنْ تَعُوْدُوْا لِمِثْلِهٖٓ اَبَدًا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ۚ','Allah memperingatkan kamu agar (jangan) kembali memperbuat yang seperti itu selama-lamanya, jika kamu orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,18,'وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ','dan Allah menerangkan ayat-ayat-Nya kepada kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,19,'اِنَّ الَّذِيْنَ يُحِبُّوْنَ اَنْ تَشِيْعَ الْفَاحِشَةُ فِى الَّذِيْنَ اٰمَنُوْا لَهُمْ عَذَابٌ اَلِيْمٌۙ فِى الدُّنْيَا وَالْاٰخِرَةِۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ','Sesungguhnya orang-orang yang ingin agar (berita) perbuatan yang amat keji itu tersiar di kalangan orang-orang yang beriman, bagi mereka azab yang pedih di dunia dan di akhirat. Dan Allah mengetahui, sedang, kamu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,20,'وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ رَءُوْفٌ رَّحِيْمٌ ࣖ ','Dan sekiranya tidaklah karena kurnia Allah dan rahmat-Nya kepada kamu semua, dan Allah Maha Penyantun dan Maha Penyayang, (niscaya kamu akan ditimpa azab yang besar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,21,'۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ وَمَنْ يَّتَّبِعْ خُطُوٰتِ الشَّيْطٰنِ فَاِنَّهٗ يَأْمُرُ بِالْفَحْشَاۤءِ وَالْمُنْكَرِۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ مَا زَكٰى مِنْكُمْ مِّنْ اَحَدٍ اَبَدًاۙ وَّلٰكِنَّ اللّٰهَ يُزَكِّيْ مَنْ يَّشَاۤءُۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ','Hai orang-orang yang beriman, janganlah kamu mengikuti langkah-langkah syaitan. Barangsiapa yang mengikuti langkah-langkah syaitan, maka sesungguhnya syaitan itu menyuruh mengerjakan perbuatan yang keji dan yang mungkar. Sekiranya tidaklah karena kurnia Allah dan rahmat-Nya kepada kamu sekalian, niscaya tidak seorangpun dari kamu bersih (dari perbuatan-perbuatan keji dan mungkar itu) selama-lamanya, tetapi Allah membersihkan siapa yang dikehendaki-Nya. Dan Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,22,'وَلَا يَأْتَلِ اُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُّؤْتُوْٓا اُولِى الْقُرْبٰى وَالْمَسٰكِيْنَ وَالْمُهٰجِرِيْنَ فِيْ سَبِيْلِ اللّٰهِ ۖوَلْيَعْفُوْا وَلْيَصْفَحُوْاۗ  اَلَا تُحِبُّوْنَ اَنْ يَّغْفِرَ اللّٰهُ لَكُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ','Dan janganlah orang-orang yang mempunyai kelebihan dan kelapangan di antara kamu bersumpah bahwa mereka (tidak) akan memberi (bantuan) kepada kaum kerabat(nya), orang-orang yang miskin dan orang-orang yang berhijrah pada jalan Allah, dan hendaklah mereka memaafkan dan berlapang dada. Apakah kamu tidak ingin bahwa Allah mengampunimu? Dan Allah adalah Maha Pengampun lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,23,'اِنَّ الَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ الْغٰفِلٰتِ الْمُؤْمِنٰتِ لُعِنُوْا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظِيْمٌ ۙ','Sesungguhnya orang-orang yang menuduh wanita yang baik-baik, yang lengah lagi beriman (berbuat zina), mereka kena laknat di dunia dan akhirat, dan bagi mereka azab yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,24,'يَّوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْدِيْهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ','pada hari (ketika), lidah, tangan dan kaki mereka menjadi saksi atas mereka terhadap apa yang dahulu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,25,'يَوْمَىِٕذٍ يُّوَفِّيْهِمُ اللّٰهُ دِيْنَهُمُ الْحَقَّ وَيَعْلَمُوْنَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُبِيْنُ','Di hari itu, Allah akan memberi mereka balasan yag setimpal menurut semestinya, dan tahulah mereka bahwa Allah-lah yang Benar, lagi Yang menjelaskan (segala sesutatu menurut hakikat yang sebenarnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,26,'اَلْخَبِيْثٰتُ لِلْخَبِيْثِيْنَ وَالْخَبِيْثُوْنَ لِلْخَبِيْثٰتِۚ وَالطَّيِّبٰتُ لِلطَّيِّبِيْنَ وَالطَّيِّبُوْنَ لِلطَّيِّبٰتِۚ اُولٰۤىِٕكَ مُبَرَّءُوْنَ مِمَّا يَقُوْلُوْنَۗ  لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ࣖ','Wanita-wanita yang keji adalah untuk laki-laki yang keji, dan laki-laki yang keji adalah buat wanita-wanita yang keji (pula), dan wanita-wanita yang baik adalah untuk laki-laki yang baik dan laki-laki yang baik adalah untuk wanita-wanita yang baik (pula). Mereka (yang dituduh) itu bersih dari apa yang dituduhkan oleh mereka (yang menuduh itu). Bagi mereka ampunan dan rezeki yang mulia (surga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,27,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتًا غَيْرَ بُيُوْتِكُمْ حَتّٰى تَسْتَأْنِسُوْا وَتُسَلِّمُوْا عَلٰٓى اَهْلِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ','Hai orang-orang yang beriman, janganlah kamu memasuki rumah yang bukan rumahmu sebelum meminta izin dan memberi salam kepada penghuninya. Yang demikian itu lebih baik bagimu, agar kamu (selalu) ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,28,'فَاِنْ لَّمْ تَجِدُوْا فِيْهَآ اَحَدًا فَلَا تَدْخُلُوْهَا حَتّٰى يُؤْذَنَ لَكُمْ وَاِنْ قِيْلَ لَكُمُ ارْجِعُوْا فَارْجِعُوْا هُوَ اَزْكٰى لَكُمْ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ','Jika kamu tidak menemui seorangpun didalamnya, maka janganlah kamu masuk sebelum kamu mendapat izin. Dan jika dikatakan kepadamu: "Kembali (saja)lah, maka hendaklah kamu kembali. Itu bersih bagimu dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,29,'لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوْا بُيُوْتًا غَيْرَ مَسْكُوْنَةٍ فِيْهَا مَتَاعٌ لَّكُمْۗ وَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ','Tidak ada dosa atasmu memasuki rumah yang tidak disediakan untuk didiami, yang di dalamnya ada keperluanmu, dan Allah mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,30,'قُلْ لِّلْمُؤْمِنِيْنَ يَغُضُّوْا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوْا فُرُوْجَهُمْۗ ذٰلِكَ اَزْكٰى لَهُمْۗ اِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا يَصْنَعُوْنَ','Katakanlah kepada orang laki-laki yang beriman: "Hendaklah mereka menahan pandanganya, dan memelihara kemaluannya; yang demikian itu adalah lebih suci bagi mereka, sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,31,'وَقُلْ لِّلْمُؤْمِنٰتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوْجَهُنَّ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوْبِهِنَّۖ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا لِبُعُوْلَتِهِنَّ اَوْ اٰبَاۤىِٕهِنَّ اَوْ اٰبَاۤءِ بُعُوْلَتِهِنَّ اَوْ اَبْنَاۤىِٕهِنَّ اَوْ اَبْنَاۤءِ بُعُوْلَتِهِنَّ اَوْ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اَخَوٰتِهِنَّ اَوْ نِسَاۤىِٕهِنَّ اَوْ مَا مَلَكَتْ اَيْمَانُهُنَّ اَوِ التَّابِعِيْنَ غَيْرِ اُولِى الْاِرْبَةِ مِنَ الرِّجَالِ اَوِ الطِّفْلِ الَّذِيْنَ لَمْ يَظْهَرُوْا عَلٰى عَوْرٰتِ النِّسَاۤءِ ۖوَلَا يَضْرِبْنَ بِاَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِيْنَ مِنْ زِيْنَتِهِنَّۗ وَتُوْبُوْٓا اِلَى اللّٰهِ جَمِيْعًا اَيُّهَ الْمُؤْمِنُوْنَ لَعَلَّكُمْ تُفْلِحُوْنَ','Katakanlah kepada wanita yang beriman: "Hendaklah mereka menahan pandangannya, dan kemaluannya, dan janganlah mereka menampakkan perhiasannya, kecuali yang (biasa) nampak dari padanya. Dan hendaklah mereka menutupkan kain kudung kedadanya, dan janganlah menampakkan perhiasannya kecuali kepada suami mereka, atau ayah mereka, atau ayah suami mereka, atau putera-putera mereka, atau putera-putera suami mereka, atau saudara-saudara laki-laki mereka, atau putera-putera saudara lelaki mereka, atau putera-putera saudara perempuan mereka, atau wanita-wanita islam, atau budak-budak yang mereka miliki, atau pelayan-pelayan laki-laki yang tidak mempunyai keinginan (terhadap wanita) atau anak-anak yang belum mengerti tentang aurat wanita. Dan janganlah mereka memukulkan kakinyua agar diketahui perhiasan yang mereka sembunyikan. Dan bertaubatlah kamu sekalian kepada Allah, hai orang-orang yang beriman supaya kamu beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,32,'وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصّٰلِحِيْنَ مِنْ عِبَادِكُمْ وَاِمَاۤىِٕكُمْۗ اِنْ يَّكُوْنُوْا فُقَرَاۤءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ','Dan kawinkanlah orang-orang yang sedirian diantara kamu, dan orang-orang yang layak (berkawin) dari hamba-hamba sahayamu yang lelaki dan hamba-hamba sahayamu yang perempuan. Jika mereka miskin Allah akan memampukan mereka dengan kurnia-Nya. Dan Allah Maha luas (pemberian-Nya) lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,33,'وَلْيَسْتَعْفِفِ الَّذِيْنَ لَا يَجِدُوْنَ نِكَاحًا حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِهٖ ۗوَالَّذِيْنَ يَبْتَغُوْنَ الْكِتٰبَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوْهُمْ اِنْ عَلِمْتُمْ فِيْهِمْ خَيْرًا وَّاٰتُوْهُمْ مِّنْ مَّالِ اللّٰهِ الَّذِيْٓ اٰتٰىكُمْ ۗوَلَا تُكْرِهُوْا فَتَيٰتِكُمْ عَلَى الْبِغَاۤءِ اِنْ اَرَدْنَ تَحَصُّنًا لِّتَبْتَغُوْا عَرَضَ الْحَيٰوةِ الدُّنْيَا ۗوَمَنْ يُّكْرِهْهُّنَّ فَاِنَّ اللّٰهَ مِنْۢ بَعْدِ اِكْرَاهِهِنَّ غَفُوْرٌ رَّحِيْمٌ','Dan orang-orang yang tidak mampu kawin hendaklah menjaga kesucian (diri)nya, sehingga Allah memampukan mereka dengan karunia-Nya. Dan budak-budak yang kamu miliki yang memginginkan perjanjian, hendaklah kamu buat perjanjian dengan mereka, jika kamu mengetahui ada kebaikan pada mereka, dan berikanlah kepada mereka sebahagian dari harta Allah yang dikaruniakan-Nya kepadamu. Dan janganlah kamu paksa budak-budak wanitamu untuk melakukan pelacuran, sedang mereka sendiri mengingini kesucian, karena kamu hendak mencari keuntungan duniawi. Dan barangsiapa yang memaksa mereka, maka sesungguhnya Allah adalah Maha Pengampun lagi Maha Penyayang (kepada mereka) sesudah mereka dipaksa itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,34,'وَلَقَدْ اَنْزَلْنَآ اِلَيْكُمْ اٰيٰتٍ مُّبَيِّنٰتٍ وَّمَثَلًا مِّنَ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِّلْمُتَّقِيْنَ ࣖ','Dan sesungguhnya Kami telah menurunkan kepada kamu ayat-ayat yang memberi penerangan, dan contoh-contoh dari orang-orang yang terdahulu sebelum kamu dan pelajaran bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,35,'۞ اَللّٰهُ نُوْرُ السَّمٰوٰتِ وَالْاَرْضِۗ مَثَلُ نُوْرِهٖ كَمِشْكٰوةٍ فِيْهَا مِصْبَاحٌۗ  اَلْمِصْبَاحُ فِيْ زُجَاجَةٍۗ  اَلزُّجَاجَةُ كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُّوْقَدُ مِنْ شَجَرَةٍ مُّبٰرَكَةٍ زَيْتُوْنَةٍ لَّا شَرْقِيَّةٍ وَّلَا غَرْبِيَّةٍۙ يَّكَادُ زَيْتُهَا يُضِيْۤءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۗ  نُوْرٌ عَلٰى نُوْرٍۗ يَهْدِى اللّٰهُ لِنُوْرِهٖ مَنْ يَّشَاۤءُۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ۙ ','Allah (Pemberi) cahaya (kepada) langit dan bumi. Perumpamaan cahaya Allah, adalah seperti sebuah lubang yang tak tembus, yang di dalamnya ada pelita besar. Pelita itu di dalam kaca (dan) kaca itu seakan-akan bintang (yang bercahaya) seperti mutiara, yang dinyalakan dengan minyak dari pohon yang berkahnya, (yaitu) pohon zaitun yang tumbuh tidak di sebelah timur (sesuatu) dan tidak pula di sebelah barat(nya), yang minyaknya (saja) hampir-hampir menerangi, walaupun tidak disentuh api. Cahaya di atas cahaya (berlapis-lapis), Allah membimbing kepada cahaya-Nya siapa yang dia kehendaki, dan Allah memperbuat perumpamaan-perumpamaan bagi manusia, dan Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,36,'فِيْ بُيُوْتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ فِيْهَا اسْمُهٗۙ يُسَبِّحُ لَهٗ فِيْهَا بِالْغُدُوِّ وَالْاٰصَالِ ۙ','Bertasbih kepada Allah di masjid-masjid yang telah diperintahkan untuk dimuliakan dan disebut nama-Nya di dalamnya, pada waktu pagi dan waktu petang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,37,'رِجَالٌ لَّا تُلْهِيْهِمْ تِجَارَةٌ وَّلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَاِيْتَاۤءِ الزَّكٰوةِ ۙيَخَافُوْنَ يَوْمًا تَتَقَلَّبُ فِيْهِ الْقُلُوْبُ وَالْاَبْصَارُ ۙ','laki-laki yang tidak dilalaikan oleh perniagaan dan tidak (pula) oleh jual beli dari mengingati Allah, dan (dari) mendirikan sembahyang, dan (dari) membayarkan zakat. Mereka takut kepada suatu hari yang (di hari itu) hati dan penglihatan menjadi goncang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,38,'لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوْا وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ','(Meraka mengerjakan yang demikian itu) supaya Allah memberikan balasan kepada mereka (dengan balasan) yang lebih baik dari apa yang telah mereka kerjakan, dan supaya Allah menambah karunia-Nya kepada mereka. Dan Allah memberi rezeki kepada siapa yang dikehendaki-Nya tanpa batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,39,'وَالَّذِيْنَ كَفَرُوْٓا اَعْمَالُهُمْ كَسَرَابٍۢ بِقِيْعَةٍ يَّحْسَبُهُ الظَّمْاٰنُ مَاۤءًۗ حَتّٰٓى اِذَا جَاۤءَهٗ لَمْ يَجِدْهُ شَيْـًٔا وَّوَجَدَ اللّٰهَ عِنْدَهٗ فَوَفّٰىهُ حِسَابَهٗ ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ۙ','Dan orang-orang kafir amal-amal mereka adalah laksana fatamorgana di tanah yang datar, yang disangka air oleh orang-orang yang dahaga, tetapi bila didatanginya air itu dia tidak mendapatinya sesuatu apapun. Dan didapatinya (ketetapan) Allah disisinya, lalu Allah memberikan kepadanya perhitungan amal-amal dengan cukup dan Allah adalah sangat cepat perhitungan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,40,'اَوْ كَظُلُمٰتٍ فِيْ بَحْرٍ لُّجِّيٍّ يَّغْشٰىهُ مَوْجٌ مِّنْ فَوْقِهٖ مَوْجٌ مِّنْ فَوْقِهٖ سَحَابٌۗ ظُلُمٰتٌۢ بَعْضُهَا فَوْقَ بَعْضٍۗ اِذَآ اَخْرَجَ يَدَهٗ لَمْ يَكَدْ يَرٰىهَاۗ وَمَنْ لَّمْ يَجْعَلِ اللّٰهُ لَهٗ نُوْرًا فَمَا لَهٗ مِنْ نُّوْرٍ ࣖ','Atau seperti gelap gulita di lautan yang dalam, yang diliputi oleh ombak, yang di atasnya ombak (pula), di atasnya (lagi) awan; gelap gulita yang tindih-bertindih, apabila dia mengeluarkan tangannya, tiadalah dia dapat melihatnya, (dan) barangsiapa yang tiada diberi cahaya (petunjuk) oleh Allah tiadalah dia mempunyai cahaya sedikitpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,41,'اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّحُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ وَالطَّيْرُ صٰۤفّٰتٍۗ كُلٌّ قَدْ عَلِمَ صَلَاتَهٗ وَتَسْبِيْحَهٗۗ وَاللّٰهُ عَلِيْمٌۢ بِمَا يَفْعَلُوْنَ ','Tidaklah kamu tahu bahwasanya Allah: kepada-Nya bertasbih apa yang di langit dan di bumi dan (juga) burung dengan mengembangkan sayapnya. Masing-masing telah mengetahui (cara) sembahyang dan tasbihnya, dan Allah Maha Mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,42,'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَصِيْرُ','Dan kepunyaan Allah-lah kerajaan langit dan bumi dan kepada Allah-lah kembali (semua makhluk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,43,'اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْجِيْ سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهٗ ثُمَّ يَجْعَلُهٗ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ وَيُنَزِّلُ مِنَ السَّمَاۤءِ مِنْ جِبَالٍ فِيْهَا مِنْۢ بَرَدٍ فَيُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ وَيَصْرِفُهٗ عَنْ مَّنْ يَّشَاۤءُۗ يَكَادُ سَنَا بَرْقِهٖ يَذْهَبُ بِالْاَبْصَارِ ۗ','Tidaklah kamu melihat bahwa Allah mengarak awan, kemudian mengumpulkan antara (bagian-bagian)nya, kemudian menjadikannya bertindih-tindih, maka kelihatanlah olehmu hujan keluar dari celah-celahnya dan Allah (juga) menurunkan (butiran-butiran) es dari langit, (yaitu) dari (gumpalan-gumpalan awan seperti) gunung-gunung, maka ditimpakan-Nya (butiran-butiran) es itu kepada siapa yang dikehendaki-Nya dan dipalingkan-Nya dari siapa yang dikehendaki-Nya. Kilauan kilat awan itu hampir-hampir menghilangkan penglihatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,44,'يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ ','Allah mempergantikan malam dan siang. Sesungguhnya pada yang demikian itu terdapat pelajaran yang besar bagi orang-orang yang mempunyai penglihatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,45,'وَاللّٰهُ خَلَقَ كُلَّ دَاۤبَّةٍ مِّنْ مَّاۤءٍۚ فَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى بَطْنِهٖۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰٓى اَرْبَعٍۗ يَخْلُقُ اللّٰهُ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Dan Allah telah menciptakan semua jenis hewan dari air, maka sebagian dari hewan itu ada yang berjalan di atas perutnya dan sebagian berjalan dengan dua kaki sedang sebagian (yang lain) berjalan dengan empat kaki. Allah menciptakan apa yang dikehendaki-Nya, sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,46,'لَقَدْ اَنْزَلْنَآ اٰيٰتٍ مُّبَيِّنٰتٍۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Sesungguhnya Kami telah menurunkan ayat-ayat yang menjelaskan. Dan Allah memimpin siapa yang dikehendaki-Nya kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,47,'وَيَقُوْلُوْنَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُوْلِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ مِّنْۢ بَعْدِ ذٰلِكَۗ وَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ','Dan mereka berkata: "Kami telah beriman kepada Allah dan rasul, dan kami mentaati (keduanya)". Kemudian sebagian dari mereka berpaling sesudah itu, sekali-kali mereka itu bukanlah orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,48,'وَاِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَرِيْقٌ مِّنْهُمْ مُّعْرِضُوْنَ ','Dan apabila mereka dipanggil kepada Allah dan rasul-Nya, agar rasul menghukum (mengadili) di antara mereka, tiba-tiba sebagian dari mereka menolak untuk datang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,49,'وَاِنْ يَّكُنْ لَّهُمُ الْحَقُّ يَأْتُوْٓا اِلَيْهِ مُذْعِنِيْنَ','Tetapi jika keputusan itu untuk (kemaslahatan) mereka, mereka datang kepada rasul dengan patuh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,50,'اَفِيْ قُلُوْبِهِمْ مَّرَضٌ اَمِ ارْتَابُوْٓا اَمْ يَخَافُوْنَ اَنْ يَّحِيْفَ اللّٰهُ عَلَيْهِمْ وَرَسُوْلُهٗ ۗبَلْ اُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ࣖ','Apakah (ketidak datangan mereka itu karena) dalam hati mereka ada penyakit, atau (karena) mereka ragu-ragu ataukah (karena) takut kalau-kalau Allah dan rasul-Nya berlaku zalim kepada mereka? Sebenarnya, mereka itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,51,'اِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِيْنَ اِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَّقُوْلُوْا سَمِعْنَا وَاَطَعْنَاۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Sesungguhnya jawaban oran-orang mukmin, bila mereka dipanggil kepada Allah dan rasul-Nya agar rasul menghukum (mengadili) di antara mereka ialah ucapan. "Kami mendengar, dan kami patuh". Dan mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,52,'وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ','Dan barang siapa yang taat kepada Allah dan rasul-Nya dan takut kepada Allah dan bertakwa kepada-Nya, maka mereka adalah orang-orang yang mendapat kemenangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,53,'۞ وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۗ قُلْ لَّا تُقْسِمُوْاۚ طَاعَةٌ مَّعْرُوْفَةٌ  ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ','Dan mereka bersumpah dengan nama Allah sekuat-kuat sumpah, jika kamu suruh mereka berperang, pastilah mereka akan pergi. Katakanlah: "Janganlah kamu bersumpah, (karena ketaatan yang diminta ialah) ketaatan yang sudah dikenal. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,54,'قُلْ اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَّا حُمِّلْتُمْۗ وَاِنْ تُطِيْعُوْهُ تَهْتَدُوْاۗ وَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ','Katakanlah: "Taat kepada Allah dan taatlah kepada rasul; dan jika kamu berpaling maka sesungguhnya kewajiban rasul itu adalah apa yang dibebankan kepadanya, dan kewajiban kamu sekalian adalah semata-mata apa yang dibebankan kepadamu. Dan jika kamu taat kepadanya, niscaya kamu mendapat petunjuk. Dan tidak lain kewajiban rasul itu melainkan menyampaikan (amanat Allah) dengan terang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,55,'وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَعَمِلُوا الصّٰلِحٰتِ لَيَسْتَخْلِفَنَّهُمْ فِى الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذِيْنَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ دِيْنَهُمُ الَّذِى ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِّنْۢ بَعْدِ خَوْفِهِمْ اَمْنًاۗ يَعْبُدُوْنَنِيْ لَا يُشْرِكُوْنَ بِيْ شَيْـًٔاۗ وَمَنْ كَفَرَ بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ','Dan Allah telah berjanji kepada orang-orang yang beriman di antara kamu dan mengerjakan amal-amal yang saleh bahwa Dia sungguh-sungguh akan menjadikan mereka berkuasa dimuka bumi, sebagaimana Dia telah menjadikan orang-orang sebelum mereka berkuasa, dan sungguh Dia akan meneguhkan bagi mereka agama yang telah diridhai-Nya untuk mereka, dan Dia benar-benar akan menukar (keadaan) mereka, sesudah mereka dalam ketakutan menjadi aman sentausa. Mereka tetap menyembahku-Ku dengan tiada mempersekutukan sesuatu apapun dengan Aku. Dan barangsiapa yang (tetap) kafir sesudah (janji) itu, maka mereka itulah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,56,'وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا الرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَ','Dan dirikanlah sembahyang, tunaikanlah zakat, dan taatlah kepada rasul, supaya kamu diberi rahmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,57,'لَا تَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا مُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَأْوٰىهُمُ النَّارُۗ وَلَبِئْسَ الْمَصِيْرُ ࣖ','Janganlah kamu kira bahwa orang-orang yang kafir itu dapat melemahkan (Allah dari mengazab mereka) di bumi ini, sedang tempat tinggal mereka (di akhirat) adalah neraka. Dan sungguh amat jeleklah tempat kembali itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,58,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِيَسْتَأْذِنْكُمُ الَّذِيْنَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذِيْنَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرّٰتٍۗ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَحِيْنَ تَضَعُوْنَ ثِيَابَكُمْ مِّنَ الظَّهِيْرَةِ وَمِنْۢ بَعْدِ صَلٰوةِ الْعِشَاۤءِۗ ثَلٰثُ عَوْرٰتٍ لَّكُمْۗ  لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌۢ بَعْدَهُنَّۗ طَوَّافُوْنَ عَلَيْكُمْ بَعْضُكُمْ عَلٰى بَعْضٍۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ','Hai orang-orang yang beriman, hendaklah budak-budak (lelaki dan wanita) yang kamu miliki, dan orang-orang yang belum balig di antara kamu, meminta izin kepada kamu tiga kali (dalam satu hari) yaitu: sebelum sembahyang subuh, ketika kamu menanggalkan pakaian (luar)mu di tengah hari dan sesudah sembahyang Isya´. (Itulah) tiga aurat bagi kamu. Tidak ada dosa atasmu dan tidak (pula) atas mereka selain dari (tiga waktu) itu. Mereka melayani kamu, sebahagian kamu (ada keperluan) kepada sebahagian (yang lain). Demikianlah Allah menjelaskan ayat-ayat bagi kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,59,'وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوْا كَمَا اسْتَأْذَنَ الَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','Dan apabila anak-anakmu telah sampai umur balig, maka hendaklah mereka meminta izin, seperti orang-orang yang sebelum mereka meminta izin. Demikianlah Allah menjelaskan ayat-ayat-Nya. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,60,'وَالْقَوَاعِدُ مِنَ النِّسَاۤءِ الّٰتِيْ لَا يَرْجُوْنَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَّضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجٰتٍۢ بِزِيْنَةٍۗ وَاَنْ يَّسْتَعْفِفْنَ خَيْرٌ لَّهُنَّۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ','Dan perempuan-perempuan tua yang telah terhenti (dari haid dan mengandung) yang tiada ingin kawin (lagi), tiadalah atas mereka dosa menanggalkan pakaian mereka dengan tidak (bermaksud) menampakkan perhiasan, dan berlaku sopan adalah lebih baik bagi mereka. Dan Allah Maha Mendengar lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,61,'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ وَّلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوْا مِنْۢ بُيُوْتِكُمْ اَوْ بُيُوْتِ اٰبَاۤىِٕكُمْ اَوْ بُيُوْتِ اُمَّهٰتِكُمْ اَوْ بُيُوْتِ اِخْوَانِكُمْ اَوْ بُيُوْتِ اَخَوٰتِكُمْ اَوْ بُيُوْتِ اَعْمَامِكُمْ اَوْ بُيُوْتِ عَمّٰتِكُمْ اَوْ بُيُوْتِ اَخْوَالِكُمْ اَوْ بُيُوْتِ خٰلٰتِكُمْ اَوْ مَا مَلَكْتُمْ مَّفَاتِحَهٗٓ اَوْ صَدِيْقِكُمْۗ  لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَأْكُلُوْا جَمِيْعًا اَوْ اَشْتَاتًاۗ فَاِذَا دَخَلْتُمْ بُيُوْتًا فَسَلِّمُوْا عَلٰٓى اَنْفُسِكُمْ تَحِيَّةً مِّنْ عِنْدِ اللّٰهِ مُبٰرَكَةً طَيِّبَةً ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ ࣖ','Tidak ada halangan bagi orang buta, tidak (pula) bagi orang pincang, tidak (pula) bagi orang sakit, dan tidak (pula) bagi dirimu sendiri, makan (bersama-sama mereka) dirumah kamu sendiri atau dirumah bapak-bapakmu, dirumah ibu-ibumu, dirumah saudara-saudaramu yang laki-laki, di rumah saudaramu yang perempuan, dirumah saudara bapakmu yang laki-laki, dirumah saudara bapakmu yang perempuan, dirumah saudara ibumu yang laki-laki, dirumah saudara ibumu yang perempuan, dirumah yang kamu miliki kuncinya atau dirumah kawan-kawanmu. Tidak ada halangan bagi kamu makan bersama-sama mereka atau sendirian. Maka apabila kamu memasuki (suatu rumah dari) rumah-rumah (ini) hendaklah kamu memberi salam kepada (penghuninya yang berarti memberi salam) kepada dirimu sendiri, salam yang ditetapkan dari sisi Allah, yang diberi berkat lagi baik. Demikianlah Allah menjelaskan ayat-ayatnya(Nya) bagimu, agar kamu memahaminya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,62,'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاِذَا كَانُوْا مَعَهٗ عَلٰٓى اَمْرٍ جَامِعٍ لَّمْ يَذْهَبُوْا حَتّٰى يَسْتَأْذِنُوْهُۗ اِنَّ الَّذِيْنَ يَسْتَأْذِنُوْنَكَ اُولٰۤىِٕكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖۚ فَاِذَا اسْتَأْذَنُوْكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِّمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ','Sesungguhnya yang sebenar-benar orang mukmin ialah orang-orang yang beriman kepada Allah dan Rasul-Nya, dan apabila mereka berada bersama-sama Rasulullah dalam sesuatu urusan yang memerlukan pertemuan, mereka tidak meninggalkan (Rasulullah) sebelum meminta izin kepadanya. Sesungguhnya orang-orang yang meminta izin kepadamu (Muhammad) mereka itulah orang-orang yang beriman kepada Allah dan rasul-Nya, maka apabila mereka meminta izin kepadamu karena sesuatu keperluan, berilah izin kepada siapa yang kamu kehendaki di antara mereka, dan mohonkanlah ampunan untuk mereka kepada Allah. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,63,'لَا تَجْعَلُوْا دُعَاۤءَ الرَّسُوْلِ بَيْنَكُمْ كَدُعَاۤءِ بَعْضِكُمْ بَعْضًاۗ قَدْ يَعْلَمُ اللّٰهُ الَّذِيْنَ يَتَسَلَّلُوْنَ مِنْكُمْ لِوَاذًاۚ فَلْيَحْذَرِ الَّذِيْنَ يُخَالِفُوْنَ عَنْ اَمْرِهٖٓ اَنْ تُصِيْبَهُمْ فِتْنَةٌ اَوْ يُصِيْبَهُمْ عَذَابٌ اَلِيْمٌ ','Janganlah kamu jadikan panggilan Rasul diantara kamu seperti panggilan sebahagian kamu kepada sebahagian (yang lain). Sesungguhnya Allah telah mengetahui orang-orang yang berangsur-angsur pergi di antara kamu dengan berlindung (kepada kawannya), maka hendaklah orang-orang yang menyalahi perintah Rasul takut akan ditimpa cobaan atau ditimpa azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,24,64,'اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قَدْ يَعْلَمُ مَآ اَنْتُمْ عَلَيْهِۗ وَيَوْمَ يُرْجَعُوْنَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ࣖ','Ketahuilah sesungguhnya kepunyaan Allahlah apa yang di langit dan di bumi. Sesungguhnya Dia mengetahui keadaan yang kamu berada di dalamnya (sekarang). Dan (mengetahui pula) hati (manusia) dikembalikan kepada-Nya, lalu diterangkan-Nya kepada mereka apa yang telah mereka kerjakan. Dan Allah Maha mengehui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,1,'تَبٰرَكَ الَّذِيْ نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِهٖ لِيَكُوْنَ لِلْعٰلَمِيْنَ نَذِيْرًا ۙ','Maha suci Allah yang telah menurunkan Al Furqaan (Al Quran) kepada hamba-Nya, agar dia menjadi pemberi peringatan kepada seluruh alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,2,' ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهٗ تَقْدِيْرًا','yang kepunyaan-Nya-lah kerajaan langit dan bumi, dan Dia tidak mempunyai anak, dan tidak ada sekutu bagi-Nya dalam kekuasaan(Nya), dan dia telah menciptakan segala sesuatu, dan Dia menetapkan ukuran-ukurannya dengan serapi-rapinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,3,'وَاتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً لَّا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَ وَلَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ ضَرًّا وَّلَا نَفْعًا وَّلَا يَمْلِكُوْنَ مَوْتًا وَّلَا حَيٰوةً وَّلَا نُشُوْرًا','Kemudian mereka mengambil tuhan-tuhan selain daripada-Nya (untuk disembah), yang tuhan-tuhan itu tidak menciptakan apapun, bahkan mereka sendiri diciptakan dan tidak kuasa untuk (menolak) sesuatu kemudharatan dari dirinya dan tidak (pula untuk mengambil) suatu kemanfaatanpun dan (juga) tidak kuasa mematikan, menghidupkan dan tidak (pula) membangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,4,'وَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اِفْكُ ِۨافْتَرٰىهُ وَاَعَانَهٗ عَلَيْهِ قَوْمٌ اٰخَرُوْنَۚ فَقَدْ جَاۤءُوْ ظُلْمًا وَّزُوْرًا ۚ','Dan orang-orang kafir berkata: "Al Quran ini tidak lain hanyalah kebohongan yang diada-adakan oleh Muhammad dan dia dibantu oleh kaum yang lain"; maka sesungguhnya mereka telah berbuat suatu kezaliman dan dusta yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,5,'وَقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَّاَصِيْلًا','Dan mereka berkata: "Dongengan-dongengan orang-orang dahulu, dimintanya supaya dituliskan, maka dibacakanlah dongengan itu kepadanya setiap pagi dan petang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,6,'قُلْ اَنْزَلَهُ الَّذِيْ يَعْلَمُ السِّرَّ  فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ كَانَ غَفُوْرًا رَّحِيْمًا','Katakanlah: "Al Quran itu diturunkan oleh (Allah) yang mengetahui rahasia di langit dan di bumi. Sesungguhnya Dia adalah Maha Pengampun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,7,'وَقَالُوْا مَالِ هٰذَا الرَّسُوْلِ يَأْكُلُ الطَّعَامَ وَيَمْشِيْ فِى الْاَسْوَاقِۗ  لَوْلَآ اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُوْنَ مَعَهٗ نَذِيْرًا ۙ','Dan mereka berkata: "Mengapa rasul itu memakan makanan dan berjalan di pasar-pasar? Mengapa tidak diturunkan kepadanya seorang malaikat agar malaikat itu memberikan peringatan bersama-sama dengan dia?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,8,'اَوْ يُلْقٰىٓ اِلَيْهِ كَنْزٌ اَوْ تَكُوْنُ لَهٗ جَنَّةٌ يَّأْكُلُ مِنْهَاۗ وَقَالَ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا','atau (mengapa tidak) diturunkan kepadanya perbendaharaan, atau (mengapa tidak) ada kebun baginya, yang dia dapat makan dari (hasil)nya?" Dan orang-orang yang zalim itu berkata: "Kamu sekalian tidak lain hanyalah mengikuti seorang lelaki yang kena sihir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,9,'اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا ࣖ','Perhatikanlah, bagaimana mereka membuat perbandingan-perbandingan tentang kamu, lalu sesatlah mereka, mereka tidak sanggup (mendapatkan) jalan (untuk menentang kerasulanmu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,10,'تَبٰرَكَ الَّذِيْٓ اِنْ شَاۤءَ جَعَلَ لَكَ خَيْرًا مِّنْ ذٰلِكَ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ وَيَجْعَلْ لَّكَ قُصُوْرًا','Maha Suci (Allah) yang jika Dia menghendaki, niscaya dijadikan-Nya bagimu yang lebih baik dari yang demikian, (yaitu) surga-surga yang mengalir sungai-sungai di bawahnya, dan dijadikan-Nya (pula) untukmu istana-istana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,11,'بَلْ كَذَّبُوْا بِالسَّاعَةِۙ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيْرًا','Bahkan mereka mendustakan hari kiamat. Dan kami menyediakan neraka yang menyala-nyala bagi siapa yang mendustakan hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,12,'اِذَا رَاَتْهُمْ مِّنْ مَّكَانٍۢ بَعِيْدٍ سَمِعُوْا لَهَا تَغَيُّظًا وَّزَفِيْرًا','Apabila neraka itu melihat mereka dari tempat yang jauh, mereka mendengar kegeramannya dan suara nyalanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,13,'وَاِذَآ اُلْقُوْا مِنْهَا مَكَانًا ضَيِّقًا مُّقَرَّنِيْنَ دَعَوْا هُنَالِكَ ثُبُوْرًا ۗ','Dan apabila mereka dilemparkan ke tempat yang sempit di neraka itu dengan dibelenggu, mereka di sana mengharapkan kebinasaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,14,'لَا تَدْعُوا الْيَوْمَ ثُبُوْرًا وَّاحِدًا وَّادْعُوْا ثُبُوْرًا كَثِيْرًا ','(Akan dikatakan kepada mereka): "Jangan kamu sekalian mengharapkan satu kebinasaan, melainkan harapkanlah kebinasaan yang banyak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,15,'قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ  كَانَتْ لَهُمْ جَزَاۤءً وَّمَصِيْرًا ','Katakanlah: "Apa (azab) yang demikian itukah yang baik, atau surga yang kekal yang telah dijanjikan kepada orang-orang yang bertakwa?" Dia menjadi balasan dan tempat kembali bagi mereka?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,16,'لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ خٰلِدِيْنَۗ  كَانَ عَلٰى رَبِّكَ وَعْدًا مَّسْـُٔوْلًا','Bagi mereka di dalam surga itu apa yang mereka kehendaki, sedang mereka kekal (di dalamnya). (Hal itu) adalah janji dari Tuhanmu yang patut dimohonkan (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,17,'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَقُوْلُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَادِيْ هٰٓؤُلَاۤءِ اَمْ هُمْ ضَلُّوا السَّبِيْلَ ۗ','Dan (ingatlah) suatu hari (ketika) Allah menghimpunkan mereka beserta apa yang mereka sembah selain Allah, lalu Allah berkata (kepada yang disembah); "Apakah kamu yang menyesatkan hamba-hamba-Ku itu, atau mereka sendirikah yang sesat dari jalan (yang benar)?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,18,'قَالُوْا سُبْحٰنَكَ مَا كَانَ يَنْۢبَغِيْ لَنَآ اَنْ نَّتَّخِذَ مِنْ دُوْنِكَ مِنْ اَوْلِيَاۤءَ وَلٰكِنْ مَّتَّعْتَهُمْ وَاٰبَاۤءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوْا قَوْمًاۢ بُوْرًا','Mereka (yang disembah itu) menjawab: "Maha Suci Engkau, tidaklah patut bagi kami mengambil selain engkau (untuk jadi) pelindung, akan tetapi Engkau telah memberi mereka dan bapak-bapak mereka kenikmatan hidup, sampai mereka lupa mengingati (Engkau); dan mereka adalah kaum yang binasa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,19,'فَقَدْ كَذَّبُوْكُمْ بِمَا تَقُوْلُوْنَۙ فَمَا تَسْتَطِيْعُوْنَ صَرْفًا وَّلَا نَصْرًاۚ وَمَنْ يَّظْلِمْ مِّنْكُمْ نُذِقْهُ عَذَابًا كَبِيْرًا','maka sesungguhnya mereka (yang disembah itu) telah mendustakan kamu tentang apa yang kamu katakan maka kamu tidak akan dapat menolak (azab) dan tidak (pula) menolong (dirimu), dan barang siapa di antara kamu yang berbuat zalim, niscaya Kami rasakan kepadanya azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,18,25,20,'وَمَآ اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِيْنَ اِلَّآ اِنَّهُمْ لَيَأْكُلُوْنَ الطَّعَامَ وَيَمْشُوْنَ فِى الْاَسْوَاقِۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً  ۗ اَتَصْبِرُوْنَۚ وَكَانَ رَبُّكَ بَصِيْرًا ࣖ   ۔','Dan Kami tidak mengutus rasul-rasul sebelummu, melainkan mereka sungguh memakan makanan dan berjalan di pasar-pasar. Dan kami jadikan sebahagian kamu cobaan bagi sebahagian yang lain. Maukah kamu bersabar?; dan adalah Tuhanmu maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,21,'۞ وَقَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا لَوْلَآ اُنْزِلَ عَلَيْنَا الْمَلٰۤىِٕكَةُ اَوْ نَرٰى رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوْا فِيْٓ اَنْفُسِهِمْ وَعَتَوْ عُتُوًّا كَبِيْرًا','Berkatalah orang-orang yang tidak menanti-nanti pertemuan(nya) dengan Kami: "Mengapakah tidak diturunkan kepada kita malaikat atau (mengapa) kita (tidak) melihat Tuhan kita?" Sesungguhnya mereka memandang besar tentang diri mereka dan mereka benar-benar telah melampaui batas(dalam melakukan) kezaliman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,22,'يَوْمَ يَرَوْنَ الْمَلٰۤىِٕكَةَ لَا بُشْرٰى يَوْمَىِٕذٍ لِّلْمُجْرِمِيْنَ وَيَقُوْلُوْنَ حِجْرًا مَّحْجُوْرًا ','Pada hari mereka melihat malaikat dihari itu tidak ada kabar gembira bagi orang-orang yang berdosa mereka berkata: "Hijraan mahjuuraa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,23,'وَقَدِمْنَآ اِلٰى مَا عَمِلُوْا مِنْ عَمَلٍ فَجَعَلْنٰهُ هَبَاۤءً مَّنْثُوْرًا ','Dan kami hadapi segala amal yang mereka kerjakan, lalu kami jadikan amal itu (bagaikan) debu yang berterbangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,24,'اَصْحٰبُ الْجَنَّةِ يَوْمَىِٕذٍ خَيْرٌ مُّسْتَقَرًّا وَّاَحْسَنُ مَقِيْلًا ','Penghuni-penghuni surga pada hari itu palig baik tempat tinggalnya dan paling indah tempat istirahatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,25,'وَيَوْمَ تَشَقَّقُ السَّمَاۤءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰۤىِٕكَةُ تَنْزِيْلًا ','Dan (ingatlah) hari (ketika) langit pecah belah mengeluarkan kabut putih dan diturunkanlah malaikat bergelombang-gelombang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,26,'اَلْمُلْكُ يَوْمَىِٕذِ ِۨالْحَقُّ لِلرَّحْمٰنِۗ وَكَانَ يَوْمًا عَلَى الْكٰفِرِيْنَ عَسِيْرًا ','Kerajaan yang hak pada hari itu adalah kepunyaan Tuhan Yang Maha Pemurah. Dan adalah (hari itu), satu hari penuh kesukaran bagi orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,27,'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُوْلُ يٰلَيْتَنِى اتَّخَذْتُ مَعَ الرَّسُوْلِ سَبِيْلًا ','Dan (ingatlah) hari (ketika itu) orang yang zalim menggigit dua tangannya, seraya berkata: "Aduhai kiranya (dulu) aku mengambil jalan bersama-sama Rasul".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,28,'يٰوَيْلَتٰى لَيْتَنِيْ لَمْ اَتَّخِذْ فُلَانًا خَلِيْلًا ','Kecelakaan besarlah bagiku; kiranya aku (dulu) tidak menjadikan sifulan itu teman akrab(ku).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,29,'لَقَدْ اَضَلَّنِيْ عَنِ الذِّكْرِ بَعْدَ اِذْ جَاۤءَنِيْۗ وَكَانَ الشَّيْطٰنُ لِلْاِنْسَانِ خَذُوْلًا ','Sesungguhnya dia telah menyesatkan aku dari Al Quran ketika Al Quran itu telah datang kepadaku. Dan adalah syaitan itu tidak mau menolong manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,30,'وَقَالَ الرَّسُوْلُ يٰرَبِّ اِنَّ قَوْمِى اتَّخَذُوْا هٰذَا الْقُرْاٰنَ مَهْجُوْرًا ','Berkatalah Rasul: "Ya Tuhanku, sesungguhnya kaumku menjadikan Al Quran itu sesuatu yang tidak diacuhkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,31,'وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِّنَ الْمُجْرِمِيْنَۗ وَكَفٰى بِرَبِّكَ هَادِيًا وَّنَصِيْرًا ','Dan seperti itulah, telah Kami adakan bagi tiap-tiap nabi, musuh dari orang-orang yang berdosa. Dan cukuplah Tuhanmu menjadi Pemberi petunjuk dan Penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,32,'وَقَالَ الَّذِيْنَ كَفَرُوْا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَّاحِدَةً ۛ كَذٰلِكَ ۛ لِنُثَبِّتَ بِهٖ فُؤَادَكَ وَرَتَّلْنٰهُ تَرْتِيْلًا ','Berkatalah orang-orang yang kafir: "Mengapa Al Quran itu tidak diturunkan kepadanya sekali turun saja?"; demikianlah supaya Kami perkuat hatimu dengannya dan Kami membacanya secara tartil (teratur dan benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,33,'وَلَا يَأْتُوْنَكَ بِمَثَلٍ اِلَّا جِئْنٰكَ بِالْحَقِّ وَاَحْسَنَ تَفْسِيْرًا ۗ','Tidaklah orang-orang kafir itu datang kepadamu (membawa) sesuatu yang ganjil, melainkan Kami datangkan kepadamu suatu yang benar dan yang paling baik penjelasannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,34,'اَلَّذِيْنَ يُحْشَرُوْنَ عَلٰى وُجُوْهِهِمْ اِلٰى جَهَنَّمَۙ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ سَبِيْلًا ࣖ ','Orang-orang yang dihimpunkan ke neraka Jahannam dengan diseret atas muka-muka mereka, mereka itulah orang yang paling buruk tempatnya dan paling sesat jalannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,35,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنَا مَعَهٗٓ اَخَاهُ هٰرُوْنَ وَزِيْرًا ۚ ','Dan sesungguhnya kami telah memberikan Al Kitab (Taurat) kepada Musa dan Kami telah menjadikan Harun saudaranya, menyertai dia sebagai wazir (pembantu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,36,'فَقُلْنَا اذْهَبَآ اِلَى الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ فَدَمَّرْنٰهُمْ تَدْمِيْرًا ۗ','Kemudian Kami berfirman kepada keduanya: "Pergilah kamu berdua kepada kaum yang mendustakan ayat-ayat Kami". Maka Kami membinasakan mereka sehancur-hancurnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,37,'وَقَوْمَ نُوْحٍ لَّمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنٰهُمْ وَجَعَلْنٰهُمْ لِلنَّاسِ اٰيَةًۗ وَاَعْتَدْنَا لِلظّٰلِمِيْنَ عَذَابًا اَلِيْمًا ۚ ','Dan (telah Kami binasakan) kaum Nuh tatkala mereka mendustakan rasul-rasul. Kami tenggelamkan mereka dan kami jadikan (cerita) mereka itu pelajaran bagi manusia. Dan Kami telah menyediakan bagi orang-orang zalim azab yang pedih;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,38,'وَعَادًا وَّثَمُوْدَا۟ وَاَصْحٰبَ الرَّسِّ وَقُرُوْنًاۢ بَيْنَ ذٰلِكَ كَثِيْرًا ','dan (Kami binasakan) kaum ´Aad dan Tsamud dan penduduk Rass dan banyak (lagi) generasi-generasi di antara kaum-kaum tersebut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,39,'وَكُلًّا ضَرَبْنَا لَهُ الْاَمْثَالَۖ وَكُلًّا تَبَّرْنَا تَتْبِيْرًا ','Dan Kami jadikan bagi masing-masing mereka perumpamaan dan masing-masing mereka itu benar benar telah Kami binasakan dengan sehancur-hancurnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,40,'وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّتِيْٓ اُمْطِرَتْ مَطَرَ السَّوْءِۗ اَفَلَمْ يَكُوْنُوْا يَرَوْنَهَاۚ بَلْ كَانُوْا لَا يَرْجُوْنَ نُشُوْرًا ','Dan sesungguhnya mereka (kaum musyrik Mekah) telah melalui sebuah negeri (Sadum) yang (dulu) dihujani dengan hujan yang sejelek-jeleknya (hujan batu). Maka apakah mereka tidak menyaksikan runtuhan itu; bahkan adalah mereka itu tidak mengharapkan akan kebangkitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,41,'وَاِذَا رَاَوْكَ اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ بَعَثَ اللّٰهُ رَسُوْلًا ','Dan apabila mereka melihat kamu (Muhammad), mereka hanyalah menjadikan kamu sebagai ejekan (dengan mengatakan): "Inikah orangnya yang di utus Allah sebagai Rasul?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,42,'اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَآ اَنْ صَبَرْنَا عَلَيْهَاۗ وَسَوْفَ يَعْلَمُوْنَ حِيْنَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَبِيْلًا ','Sesungguhnya hampirlah ia menyesatkan kita dari sembahan-sembahan kita, seandainya kita tidak sabar(menyembah)nya" dan mereka kelak akan mengetahui di saat mereka melihat azab, siapa yang paling sesat jalannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,43,'اَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُۗ اَفَاَنْتَ تَكُوْنُ عَلَيْهِ وَكِيْلًا ۙ','Terangkanlah kepadaku tentang orang yang menjadikan hawa nafsunya sebagai tuhannya. Maka apakah kamu dapat menjadi pemelihara atasnya?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,44,'اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُوْنَ اَوْ يَعْقِلُوْنَۗ اِنْ هُمْ اِلَّا كَالْاَنْعَامِ  بَلْ هُمْ اَضَلُّ سَبِيْلًا ࣖ','atau apakah kamu mengira bahwa kebanyakan mereka itu mendengar atau memahami. Mereka itu tidak lain, hanyalah seperti binatang ternak, bahkan mereka lebih sesat jalannya (dari binatang ternak itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,45,'اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَاۤءَ لَجَعَلَهٗ سَاكِنًاۚ  ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيْلًا ۙ','Apakah kamu tidak memperhatikan (penciptaan) Tuhanmu, bagaimana Dia memanjangkan (dan memendekkan) bayang-bayang dan kalau Dia menghendaki niscaya Dia menjadikan tetap bayang-bayang itu, kemudian Kami jadikan matahari sebagai petunjuk atas bayang-bayang itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,46,'ثُمَّ قَبَضْنٰهُ اِلَيْنَا قَبْضًا يَّسِيْرًا ','kemudian Kami menarik bayang-bayang itu kepada kami dengan tarikan yang perlahan-lahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,47,'وَهُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِبَاسًا وَّالنَّوْمَ سُبَاتًا وَّجَعَلَ النَّهَارَ نُشُوْرًا  ','Dialah yang menjadikan untukmu malam (sebagai) pakaian, dan tidur untuk istirahat, dan Dia menjadikan siang untuk bangun berusaha.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,48,'وَهُوَ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۚ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً طَهُوْرًا ۙ','Dialah yang meniupkan angin (sebagai) pembawa kabar gembira dekat sebelum kedatangan rahmat-nya (hujan); dan Kami turunkan dari langit air yang amat bersih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,49,'لِّنُحْيِ َۧ بِهٖ بَلْدَةً مَّيْتًا وَّنُسْقِيَهٗ مِمَّا خَلَقْنَآ اَنْعَامًا وَّاَنَاسِيَّ كَثِيْرًا ','agar Kami menghidupkan dengan air itu negeri (tanah) yang mati, dan agar Kami memberi minum dengan air itu sebagian besar dari makhluk Kami, binatang-binatang ternak dan manusia yang banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,50,'وَلَقَدْ صَرَّفْنٰهُ بَيْنَهُمْ لِيَذَّكَّرُوْاۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا ','Dan sesungguhnya Kami telah mempergilirkan hujan itu diantara manusia supaya mereka mengambil pelajaran (dari padanya); maka kebanyakan manusia itu tidak mau kecuali mengingkari (nikmat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,51,'وَلَوْ شِئْنَا لَبَعَثْنَا فِيْ كُلِّ قَرْيَةٍ نَّذِيْرًا ۖ','Dan andaikata Kami menghendaki benar-benarlah Kami utus pada tiap-tiap negeri seorang yang memberi peringatan (rasul).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,52,'فَلَا تُطِعِ الْكٰفِرِيْنَ وَجَاهِدْهُمْ بِهٖ جِهَادًا كَبِيْرًا ','Maka janganlah kamu mengikuti orang-orang kafir, dan berjihadlah terhadap mereka dengan Al Quran dengan jihad yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,53,'۞ وَهُوَ الَّذِيْ مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَّهٰذَا مِلْحٌ  اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَّحِجْرًا مَّحْجُوْرًا ','Dan Dialah yang membiarkan dua laut yang mengalir (berdampingan); yang ini tawar lagi segar dan yang lain asin lagi pahit; dan Dia jadikan antara keduanya dinding dan batas yang menghalangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,54,'وَهُوَ الَّذِيْ خَلَقَ مِنَ الْمَاۤءِ بَشَرًا فَجَعَلَهٗ نَسَبًا وَّصِهْرًاۗ وَكَانَ رَبُّكَ قَدِيْرًا ','Dan Dia (pula) yang menciptakan manusia dari air lalu dia jadikan manusia itu (punya) keturunan dan mushaharah dan adalah Tuhanmu Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,55,'وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۗ وَكَانَ الْكَافِرُ عَلٰى رَبِّهٖ ظَهِيْرًا ','Dan mereka menyembah selain Allah apa yang tidak memberi manfaat kepada mereka dan tidak (pula) memberi mudharat kepada mereka. Adalah orang-orang kafir itu penolong (syaitan untuk berbuat durhaka) terhadap Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,56,'وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًا ','Dan tidaklah Kami mengutus kamu melainkan hanya sebagai pembawa kabar gembira dan pemberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,57,'قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَاۤءَ اَنْ يَّتَّخِذَ اِلٰى رَبِّهٖ سَبِيْلًا ','Katakanlah: "Aku tidak meminta upah sedikitpun kepada kamu dalam menyampaikan risalah itu, melainkan (mengharapkan kepatuhan) orang-orang yang mau mengambil jalan kepada Tuhan nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,58,'وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِيْ لَا يَمُوْتُ وَسَبِّحْ بِحَمْدِهٖۗ وَكَفٰى بِهٖ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًا ۚ','Dan bertawakkallah kepada Allah yang hidup (kekal) Yang tidak mati, dan bertasbihlah dengan memuji-Nya. Dan cukuplah Dia Maha Mengetahui dosa-dosa hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,59,'اَلَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ  اَلرَّحْمٰنُ فَسْـَٔلْ بِهٖ خَبِيْرًا ','Yang menciptakan langit dan bumi dan apa yang ada antara keduanya dalam enam masa, kemudian dia bersemayam di atas ´Arsy, (Dialah) Yang Maha Pemurah, maka tanyakanlah (tentang Allah) kepada yang lebih mengetahui (Muhammad) tentang Dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,60,'وَاِذَا قِيْلَ لَهُمُ اسْجُدُوْا لِلرَّحْمٰنِ قَالُوْا وَمَا الرَّحْمٰنُ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوْرًا ۩ ࣖ','Dan apabila dikatakan kepada mereka: "Sujudlah kamu sekalian kepada yang Maha Penyayang", mereka menjawab: "Siapakah yang Maha Penyayang itu? Apakah kami akan sujud kepada Tuhan Yang kamu perintahkan kami(bersujud kepada-Nya)?", dan (perintah sujud itu) menambah mereka jauh (dari iman).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,61,'تَبٰرَكَ الَّذِيْ جَعَلَ فِى السَّمَاۤءِ بُرُوْجًا وَّجَعَلَ فِيْهَا سِرَاجًا وَّقَمَرًا مُّنِيْرًا  ','Maha Suci Allah yang menjadikan di langit gugusan-gugusan bintang dan Dia menjadikan juga padanya matahari dan bulan yang bercahaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,62,'وَهُوَ الَّذِيْ جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِّمَنْ اَرَادَ اَنْ يَّذَّكَّرَ اَوْ اَرَادَ شُكُوْرًا ','Dan Dia (pula) yang menjadikan malam dan siang silih berganti bagi orang yang ingin mengambil pelajaran atau orang yang ingin bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,63,'وَعِبَادُ الرَّحْمٰنِ الَّذِيْنَ يَمْشُوْنَ عَلَى الْاَرْضِ هَوْنًا وَّاِذَا خَاطَبَهُمُ الْجٰهِلُوْنَ قَالُوْا سَلٰمًا  ','Dan hamba-hamba Tuhan yang Maha Penyayang itu (ialah) orang-orang yang berjalan di atas bumi dengan rendah hati dan apabila orang-orang jahil menyapa mereka, mereka mengucapkan kata-kata (yang mengandung) keselamatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,64,'وَالَّذِيْنَ يَبِيْتُوْنَ لِرَبِّهِمْ سُجَّدًا وَّقِيَامًا ','Dan orang yang melalui malam hari dengan bersujud dan berdiri untuk Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,65,'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۖ اِنَّ عَذَابَهَا كَانَ غَرَامًا ۖ','Dan orang-orang yang berkata: "Ya Tuhan kami, jauhkan azab jahannam dari kami, sesungguhnya azabnya itu adalah kebinasaan yang kekal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,66,'اِنَّهَا سَاۤءَتْ مُسْتَقَرًّا وَّمُقَامًا ','Sesungguhnya jahannam itu seburuk-buruk tempat menetap dan tempat kediaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,67,'وَالَّذِيْنَ اِذَآ اَنْفَقُوْا لَمْ يُسْرِفُوْا وَلَمْ يَقْتُرُوْا وَكَانَ بَيْنَ ذٰلِكَ قَوَامًا ','Dan orang-orang yang apabila membelanjakan (harta), mereka tidak berlebihan, dan tidak (pula) kikir, dan adalah (pembelanjaan itu) di tengah-tengah antara yang demikian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,68,'وَالَّذِيْنَ لَا يَدْعُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ وَلَا يَقْتُلُوْنَ النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُوْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ يَلْقَ اَثَامًا ۙ','Dan orang-orang yang tidak menyembah tuhan yang lain beserta Allah dan tidak membunuh jiwa yang diharamkan Allah (membunuhnya) kecuali dengan (alasan) yang benar, dan tidak berzina, barang siapa yang melakukan yang demikian itu, niscaya dia mendapat (pembalasan) dosa(nya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,69,'يُّضٰعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ فِيْهٖ مُهَانًا ۙ','(yakni) akan dilipat gandakan azab untuknya pada hari kiamat dan dia akan kekal dalam azab itu, dalam keadaan terhina,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,70,'اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَاُولٰۤىِٕكَ يُبَدِّلُ اللّٰهُ سَيِّاٰتِهِمْ حَسَنٰتٍۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ','kecuali orang-orang yang bertaubat, beriman dan mengerjakan amal saleh; maka itu kejahatan mereka diganti Allah dengan kebajikan. Dan adalah Allah maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,71,'وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَاِنَّهٗ يَتُوْبُ اِلَى اللّٰهِ مَتَابًا ','Dan orang-orang yang bertaubat dan mengerjakan amal saleh, maka sesungguhnya dia bertaubat kepada Allah dengan taubat yang sebenar-benarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,72,'وَالَّذِيْنَ لَا يَشْهَدُوْنَ الزُّوْرَۙ وَاِذَا مَرُّوْا بِاللَّغْوِ مَرُّوْا كِرَامًا ','Dan orang-orang yang tidak memberikan persaksian palsu, dan apabila mereka bertemu dengan (orang-orang) yang mengerjakan perbuatan-perbuatan yang tidak berfaedah, mereka lalui (saja) dengan menjaga kehormatan dirinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,73,'وَالَّذِيْنَ اِذَا ذُكِّرُوْا بِاٰيٰتِ رَبِّهِمْ لَمْ يَخِرُّوْا عَلَيْهَا صُمًّا وَّعُمْيَانًا ','Dan orang-orang yang apabila diberi peringatan dengan ayat-ayat Tuhan mereka, mereka tidaklah menghadapinya sebagai orang-orang yang tuli dan buta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,74,'وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيّٰتِنَا قُرَّةَ اَعْيُنٍ وَّاجْعَلْنَا لِلْمُتَّقِيْنَ اِمَامًا ','Dan orang orang yang berkata: "Ya Tuhan kami, anugrahkanlah kepada kami isteri-isteri kami dan keturunan kami sebagai penyenang hati (kami), dan jadikanlah kami imam bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,75,'اُولٰۤىِٕكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوْا وَيُلَقَّوْنَ فِيْهَا تَحِيَّةً وَّسَلٰمًا ۙ','Mereka itulah orang yang dibalasi dengan martabat yang tinggi (dalam surga) karena kesabaran mereka dan mereka disambut dengan penghormatan dan ucapan selamat di dalamnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,76,'خٰلِدِيْنَ فِيْهَاۗ حَسُنَتْ مُسْتَقَرًّا وَّمُقَامًا ','mereka kekal di dalamnya. Surga itu sebaik-baik tempat menetap dan tempat kediaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,25,77,'قُلْ مَا يَعْبَؤُا بِكُمْ رَبِّيْ لَوْلَا دُعَاۤؤُكُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُوْنُ لِزَامًا ࣖ','Katakanlah (kepada orang-orang musyrik): "Tuhanku tidak mengindahkan kamu, melainkan kalau ada ibadatmu. (Tetapi bagaimana kamu beribadat kepada-Nya), padahal kamu sungguh telah mendustakan-Nya? karena itu kelak (azab) pasti (menimpamu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,1,'طٰسۤمّۤ ','Thaa Siim Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,2,'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ','Inilah ayat-ayat Al Quran yang menerangkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,3,'لَعَلَّكَ بَاخِعٌ نَّفْسَكَ اَلَّا يَكُوْنُوْا مُؤْمِنِيْنَ ','Boleh jadi kamu (Muhammad) akan membinasakan dirimu, karena mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,4,'اِنْ نَّشَأْ نُنَزِّلْ عَلَيْهِمْ مِّنَ السَّمَاۤءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِعِيْنَ ','Jika kami kehendaki niscaya Kami menurunkan kepada mereka mukjizat dari langit, maka senantiasa kuduk-kuduk mereka tunduk kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,5,'وَمَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوْا عَنْهُ مُعْرِضِيْنَ ','Dan sekali-kali tidak datang kepada mereka suatu peringatan baru dari Tuhan Yang Maha Pemurah, melainkan mereka selalu berpaling daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,6,'فَقَدْ كَذَّبُوْا فَسَيَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ','Sungguh mereka telah mendustakan (Al Quran), maka kelak akan datang kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,7,'اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ','Dan apakah mereka tidak memperhatikan bumi, berapakah banyaknya Kami tumbuhkan di bumi itu pelbagai macam tumbuh-tumbuhan yang baik?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,8,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةًۗ وَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ','Sesungguhnya pada yang demikian itu benar-benar terdapat suatu tanda kekuasaan Allah. Dan kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,9,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,10,'وَاِذْ نَادٰى رَبُّكَ مُوْسٰٓى اَنِ ائْتِ الْقَوْمَ الظّٰلِمِيْنَ ۙ','Dan (ingatlah) ketika Tuhanmu menyeru Musa (dengan firman-Nya): "Datangilah kaum yang zalim itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,11,'قَوْمَ فِرْعَوْنَ ۗ اَلَا يَتَّقُوْنَ  ','(yaitu) kaum Fir´aun. Mengapa mereka tidak bertakwa?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,12,'قَالَ رَبِّ اِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ۗ','Berkata Musa: "Ya Tuhanku, sesungguhnya aku takut bahwa mereka akan mendustakan aku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,13,'وَيَضِيْقُ صَدْرِيْ وَلَا يَنْطَلِقُ لِسَانِيْ فَاَرْسِلْ اِلٰى هٰرُوْنَ ','Dan (karenanya) sempitlah dadaku dan tidak lancar lidahku maka utuslah (Jibril) kepada Harun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,14,'وَلَهُمْ عَلَيَّ ذَنْۢبٌ فَاَخَافُ اَنْ يَّقْتُلُوْنِ  ۚ','Dan aku berdosa terhadap mereka, maka aku takut mereka akan membunuhku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,15,'قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيٰتِنَآ اِنَّا مَعَكُمْ مُّسْتَمِعُوْنَ ۙ ','Allah berfirman: "Jangan takut (mereka tidak akan dapat membunuhmu), maka pergilah kamu berdua dengan membawa ayat-ayat Kami (mukjizat-mukjizat); sesungguhnya Kami bersamamu mendengarkan (apa-apa yang mereka katakan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,16,'فَأْتِيَا فِرْعَوْنَ فَقُوْلَآ اِنَّا رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ۙ','Maka datanglah kamu berdua kepada Fir´aun dan katakanlah olehmu: "Sesungguhnya Kami adalah Rasul Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,17,'اَنْ اَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ','lepaskanlah Bani Israil (pergi) beserta kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,18,'قَالَ اَلَمْ نُرَبِّكَ فِيْنَا وَلِيْدًا وَّلَبِثْتَ فِيْنَا مِنْ عُمُرِكَ سِنِيْنَ ۗ ','Fir´aun menjawab: "Bukankah kami telah mengasuhmu di antara (keluarga) kami, waktu kamu masih kanak-kanak dan kamu tinggal bersama kami beberapa tahun dari umurmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,19,'وَفَعَلْتَ فَعْلَتَكَ الَّتِيْ فَعَلْتَ وَاَنْتَ مِنَ الْكٰفِرِيْنَ  ','dan kamu telah berbuat suatu perbuatan yang telah kamu lakukan itu dan kamu termasuk golongan orang-orang yang tidak membalas guna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,20,'قَالَ فَعَلْتُهَآ اِذًا وَّاَنَا۠ مِنَ الضَّاۤلِّيْنَ  ','Berkata Musa: "Aku telah melakukannya, sedang aku di waktu itu termasuk orang-orang yang khilaf.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,21,'فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِيْ رَبِّيْ حُكْمًا وَّجَعَلَنِيْ مِنَ الْمُرْسَلِيْنَ  ','Lalu aku lari meninggalkan kamu ketika aku takut kepadamu, kemudian Tuhanku memberikan kepadaku ilmu serta Dia menjadikanku salah seorang di antara rasul-rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,22,'وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَّ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ','Budi yang kamu limpahkan kepadaku itu adalah (disebabkan) kamu telah memperbudak Bani Israil".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,23,'قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعٰلَمِيْنَ ۗ ','Fir´aun bertanya: "Siapa Tuhan semesta alam itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,24,'قَالَ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ مُّوْقِنِيْنَ ','Musa menjawab: "Tuhan Pencipta langit dan bumi dan apa-apa yang di antara keduanya (Itulah Tuhanmu), jika kamu sekalian (orang-orang) mempercayai-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,25,'قَالَ لِمَنْ حَوْلَهٗٓ اَلَا تَسْتَمِعُوْنَ ','Berkata Fir´aun kepada orang-orang sekelilingnya: "Apakah kamu tidak mendengarkan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,26,'قَالَ رَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ ','Musa berkata (pula): "Tuhan kamu dan Tuhan nenek-nenek moyang kamu yang dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,27,'قَالَ اِنَّ رَسُوْلَكُمُ الَّذِيْٓ اُرْسِلَ اِلَيْكُمْ لَمَجْنُوْنٌ ','Fir´aun berkata: "Sesungguhnya Rasulmu yang diutus kepada kamu sekalian benar-benar orang gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,28,'قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ تَعْقِلُوْنَ  ','Musa berkata: "Tuhan yang menguasai timur dan barat dan apa yang ada di antara keduanya: (Itulah Tuhanmu) jika kamu mempergunakan akal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,29,'قَالَ لَىِٕنِ اتَّخَذْتَ اِلٰهًا غَيْرِيْ لَاَجْعَلَنَّكَ مِنَ الْمَسْجُوْنِيْنَ ','Fir´aun berkata: "Sungguh jika kamu menyembah Tuhan selain aku, benar-benar aku akan menjadikan kamu salah seorang yang dipenjarakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,30,'قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُّبِيْنٍ ','Musa berkata: "Dan apakah (kamu akan melakukan itu) kendatipun aku tunjukkan kepadamu sesuatu (keterangan) yang nyata?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,31,'قَالَ فَأْتِ بِهٖٓ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ','Fir´aun berkata: "Datangkanlah sesuatu (keterangan) yang nyata itu, jika kamu adalah termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,32,'فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۚ ','Maka Musa melemparkan tongkatnya, lalu tiba-tiba tongkat itu (menjadi) ular yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,33,'وَنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ ࣖ ','Dan ia menarik tangannya (dari dalam bajunya), maka tiba-tiba tangan itu jadi putih (bersinar) bagi orang-orang yang melihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,34,'قَالَ لِلْمَلَاِ حَوْلَهٗٓ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌ ۙ ','Fir´aun berkata kepada pembesar-pembesar yang berada sekelilingnya: Sesungguhnya Musa ini benar-benar seorang ahli sihir yang pandai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,35,'يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهٖۖ فَمَاذَا تَأْمُرُوْنَ ','ia hendak mengusir kamu dari negerimu sendiri dengan sihirnya; maka karena itu apakah yang kamu anjurkan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,36,'قَالُوْٓا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۙ ','Mereka menjawab: "Tundalah (urusan) dia dan saudaranya dan kirimkanlah ke seluruh negeri orang-orang yang akan mengumpulkan (ahli sihir),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,37,'يَأْتُوْكَ بِكُلِّ سَحَّارٍ عَلِيْمٍ ','niscaya mereka akan mendatangkan semua ahli sihir yang pandai kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,38,'فَجُمِعَ السَّحَرَةُ لِمِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ۙ ','Lalu dikumpulkan ahli-ahli sihir pada waktu yang ditetapkan di hari yang maklum,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,39,'وَّقِيْلَ لِلنَّاسِ هَلْ اَنْتُمْ مُّجْتَمِعُوْنَ ۙ ','dan dikatakan kepada orang banyak: "Berkumpullah kamu sekalian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,40,'لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوْا هُمُ الْغٰلِبِيْنَ  ','semoga kita mengikuti ahli-ahli sihir jika mereka adalah orang-orang yang menang"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,41,'فَلَمَّا جَاۤءَ السَّحَرَةُ قَالُوْا لِفِرْعَوْنَ اَىِٕنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ  ','Maka tatkala ahli-ahli sihir datang, merekapun bertanya kepada Fir´aun: "Apakah kami sungguh-sungguh mendapat upah yang besar jika kami adalah orang-orang yang menang?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,42,'قَالَ نَعَمْ وَاِنَّكُمْ اِذًا لَّمِنَ الْمُقَرَّبِيْنَ ','Fir´aun menjawab: "Ya, kalau demikian, sesungguhnya kamu sekalian benar-benar akan menjadi orang yang didekatkan (kepadaku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,43,'قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ  ','Berkatalah Musa kepada mereka: "Lemparkanlah apa yang hendak kamu lemparkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,44,'فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوْا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغٰلِبُوْنَ  ','Lalu mereka melemparkan tali temali dan tongkat-tongkat mereka dan berkata: "Demi kekuasaan Fir´aun, sesungguhnya kami benar-benar akan menang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,45,'فَاَلْقٰى مُوْسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَ ۚ ','Kemudian Musa menjatuhkan tongkatnya maka tiba-tiba ia menelan benda-benda palsu yang mereka ada-adakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,46,'فَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَ ۙ ','Maka tersungkurlah ahli-ahli sihir sambil bersujud (kepada Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,47,'قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَ ۙ ','mereka berkata: "Kami beriman kepada Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,48,'رَبِّ مُوْسٰى وَهٰرُوْنَ  ','(yaitu) Tuhan Musa dan Harun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,49,'قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَسَوْفَ تَعْلَمُوْنَ ەۗ لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَۚ','Fir´aun berkata: "Apakah kamu sekalian beriman kepada Musa sebelum aku memberi izin kepadamu? Sesungguhnya dia benar-benar pemimpinmu yang mengajarkan sihir kepadamu maka kamu nanti pasti benar-benar akan mengetahui (akibat perbuatanmu); sesungguhnya aku akan memotong tanganmu dan kakimu dengan bersilangan dan aku akan menyalibmu semuanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,50,'قَالُوْا لَا ضَيْرَ ۖاِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَ ۚ  ','Mereka berkata: "Tidak ada kemudharatan (bagi kami); sesungguhnya kami akan kembali kepada Tuhan kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,51,' اِنَّا نَطْمَعُ اَنْ يَّغْفِرَ لَنَا رَبُّنَا خَطٰيٰنَآ اَنْ كُنَّآ اَوَّلَ الْمُؤْمِنِيْنَ ۗ ࣖ','sesungguhnya kami amat menginginkan bahwa Tuhan kami akan mengampuni kesalahan kami, karena kami adalah orang-orang yang pertama-tama beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,52,'۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْٓ اِنَّكُمْ مُّتَّبَعُوْنَ  ','Dan Kami wahyukan (perintahkan) kepada Musa: "Pergilah di malam hari dengan membawa hamba-hamba-Ku (Bani Israil), karena sesungguhnya kamu sekalian akan disusuli".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,53,'فَاَرْسَلَ فِرْعَوْنُ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۚ ','Kemudian Fir´aun mengirimkan orang yang mengumpulkan (tentaranya) ke kota-kota.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,54,'اِنَّ هٰٓؤُلَاۤءِ لَشِرْذِمَةٌ قَلِيْلُوْنَۙ ','(Fir´aun berkata): "Sesungguhnya mereka (Bani Israil) benar-benar golongan kecil,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,55,'وَاِنَّهُمْ لَنَا لَغَاۤىِٕظُوْنَ ۙ  ','dan sesungguhnya mereka membuat hal-hal yang menimbulkan amarah kita,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,56,'وَاِنَّا لَجَمِيْعٌ حٰذِرُوْنَ ۗ ','dan sesungguhnya kita benar-benar golongan yang selalu berjaga-jaga".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,57,'فَاَخْرَجْنٰهُمْ مِّنْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ','Maka Kami keluarkan Fir´aun dan kaumnya dari taman-taman dan mata air,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,58,'وَّكُنُوْزٍ وَّمَقَامٍ كَرِيْمٍ ۙ ','dan (dari) perbendaharaan dan kedudukan yang mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,59,'كَذٰلِكَۚ وَاَوْرَثْنٰهَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ','demikianlah halnya dan Kami anugerahkan semuanya (itu) kepada Bani Israil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,60,'فَاَتْبَعُوْهُمْ مُّشْرِقِيْنَ  ','Maka Fir´aun dan bala tentaranya dapat menyusuli mereka di waktu matahari terbit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,61,'فَلَمَّا تَرَاۤءَ الْجَمْعٰنِ قَالَ اَصْحٰبُ مُوْسٰٓى اِنَّا لَمُدْرَكُوْنَ ۚ  ','Maka setelah kedua golongan itu saling melihat, berkatalah pengikut-pengikut Musa: "Sesungguhnya kita benar-benar akan tersusul".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,62,'قَالَ كَلَّاۗ اِنَّ مَعِيَ رَبِّيْ سَيَهْدِيْنِ  ','Musa menjawab: "Sekali-kali tidak akan tersusul; sesungguhnya Tuhanku besertaku, kelak Dia akan memberi petunjuk kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,63,'فَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنِ اضْرِبْ بِّعَصَاكَ الْبَحْرَۗ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيْمِ ۚ ','Lalu Kami wahyukan kepada Musa: "Pukullah lautan itu dengan tongkatmu". Maka terbelahlah lautan itu dan tiap-tiap belahan adalah seperti gunung yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,64,'وَاَزْلَفْنَا ثَمَّ الْاٰخَرِيْنَ ۚ ','Dan di sanalah Kami dekatkan golongan yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,65,'وَاَنْجَيْنَا مُوْسٰى وَمَنْ مَّعَهٗٓ اَجْمَعِيْنَ ۚ ','Dan Kami selamatkan Musa dan orang-orang yang besertanya semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,66,'ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ ۗ ','Dan Kami tenggelamkan golongan yang lain itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,67,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Sesungguhnya pada yang demikian itu benar-benar merupakan suatu tanda yang besar (mukjizat) dan tetapi adalah kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,68,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ','Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,69,'وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰهِيْمَ ۘ ','Dan bacakanlah kepada mereka kisah Ibrahim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,70,'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا تَعْبُدُوْنَ ','Ketika ia berkata kepada bapaknya dan kaumnya: "Apakah yang kamu sembah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,71,'قَالُوْا نَعْبُدُ اَصْنَامًا فَنَظَلُّ لَهَا عٰكِفِيْنَ  ','Mereka menjawab: "Kami menyembah berhala-berhala dan kami senantiasa tekun menyembahnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,72,'قَالَ هَلْ يَسْمَعُوْنَكُمْ اِذْ تَدْعُوْنَ ۙ  ','Berkata Ibrahim: "Apakah berhala-berhala itu mendengar (doa)mu sewaktu kamu berdoa (kepadanya)?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,73,'اَوْ يَنْفَعُوْنَكُمْ اَوْ يَضُرُّوْنَ  ','atau (dapatkah) mereka memberi manfaat kepadamu atau memberi mudharat?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,74,'قَالُوْا بَلْ وَجَدْنَآ اٰبَاۤءَنَا كَذٰلِكَ يَفْعَلُوْنَ  ','Mereka menjawab: "(Bukan karena itu) sebenarnya kami mendapati nenek moyang kami berbuat demikian".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,75,'قَالَ اَفَرَءَيْتُمْ مَّا كُنْتُمْ تَعْبُدُوْنَ ۙ ','Ibrahim berkata: "Maka apakah kamu telah memperhatikan apa yang selalu kamu sembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,76,'اَنْتُمْ وَاٰبَاۤؤُكُمُ الْاَقْدَمُوْنَ ۙ  ','kamu dan nenek moyang kamu yang dahulu?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,77,'فَاِنَّهُمْ عَدُوٌّ لِّيْٓ اِلَّا رَبَّ الْعٰلَمِيْنَ ۙ ','karena sesungguhnya apa yang kamu sembah itu adalah musuhku, kecuali Tuhan Semesta Alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,78,'الَّذِيْ خَلَقَنِيْ فَهُوَ يَهْدِيْنِ ۙ ','(yaitu Tuhan) Yang telah menciptakan aku, maka Dialah yang menunjuki aku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,79,'وَالَّذِيْ هُوَ يُطْعِمُنِيْ وَيَسْقِيْنِ ۙ ','dan Tuhanku, Yang Dia memberi makan dan minum kepadaku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,80,'وَاِذَا مَرِضْتُ فَهُوَ يَشْفِيْنِ ۙ  ','dan apabila aku sakit, Dialah Yang menyembuhkan aku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,81,'وَالَّذِيْ يُمِيْتُنِيْ ثُمَّ يُحْيِيْنِ ۙ ','dan Yang akan mematikan aku, kemudian akan menghidupkan aku (kembali),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,82,'وَالَّذِيْٓ اَطْمَعُ اَنْ يَّغْفِرَ لِيْ خَطِيْۤـَٔتِيْ يَوْمَ الدِّيْنِ ۗ','dan Yang amat kuinginkan akan mengampuni kesalahanku pada hari kiamat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,83,'رَبِّ هَبْ لِيْ حُكْمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ ۙ','(Ibrahim berdoa): "Ya Tuhanku, berikanlah kepadaku hikmah dan masukkanlah aku ke dalam golongan orang-orang yang saleh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,84,'وَاجْعَلْ لِّيْ لِسَانَ صِدْقٍ فِى الْاٰخِرِيْنَ ۙ ','dan jadikanlah aku buah tutur yang baik bagi orang-orang (yang datang) kemudian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,85,'وَاجْعَلْنِيْ مِنْ وَّرَثَةِ جَنَّةِ النَّعِيْمِ ۙ','dan jadikanlah aku termasuk orang-orang yang mempusakai surga yang penuh kenikmatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,86,'وَاغْفِرْ لِاَبِيْٓ اِنَّهٗ كَانَ مِنَ الضَّاۤلِّيْنَ ۙ ','dan ampunilah bapakku, karena sesungguhnya ia adalah termasuk golongan orang-orang yang sesat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,87,'وَلَا تُخْزِنِيْ يَوْمَ يُبْعَثُوْنَۙ','dan janganlah Engkau hinakan aku pada hari mereka dibangkitkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,88,'يَوْمَ لَا يَنْفَعُ مَالٌ وَّلَا بَنُوْنَ ۙ  ','(yaitu) di hari harta dan anak-anak laki-laki tidak berguna,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,89,'اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَلِيْمٍ ۗ ','kecuali orang-orang yang menghadap Allah dengan hati yang bersih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,90,'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ ۙ ','dan (di hari itu) didekatkanlah surga kepada orang-orang yang bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,91,'وَبُرِّزَتِ الْجَحِيْمُ لِلْغٰوِيْنَ ۙ ','dan diperlihatkan dengan jelas neraka Jahim kepada orang-orang yang sesat",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,92,'وَقِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُوْنَ ۙ ','dan dikatakan kepada mereka: "Dimanakah berhala-berhala yang dahulu kamu selalu menyembah(nya)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,93,'مِنْ دُوْنِ اللّٰهِ ۗهَلْ يَنْصُرُوْنَكُمْ اَوْ يَنْتَصِرُوْنَ ۗ  ','selain dari Allah? Dapatkah mereka menolong kamu atau menolong diri mereka sendiri?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,94,'فَكُبْكِبُوْا فِيْهَا هُمْ وَالْغَاوٗنَ ۙ ','Maka mereka (sembahan-sembahan itu) dijungkirkan ke dalam neraka bersama-sama orang-orang yang sesat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,95,'وَجُنُوْدُ اِبْلِيْسَ اَجْمَعُوْنَ ۗ  ','dan bala tentara iblis semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,96,'قَالُوْا وَهُمْ فِيْهَا يَخْتَصِمُوْنَ  ','Mereka berkata sedang mereka bertengkar di dalam neraka:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,97,'تَاللّٰهِ اِنْ كُنَّا لَفِيْ ضَلٰلٍ مُّبِيْنٍ ۙ ','"demi Allah: sungguh kita dahulu (di dunia) dalam kesesatan yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,98,'اِذْ نُسَوِّيْكُمْ بِرَبِّ الْعٰلَمِيْنَ ','karena kita mempersamakan kamu dengan Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,99,'وَمَآ اَضَلَّنَآ اِلَّا الْمُجْرِمُوْنَ ','Dan tiadalah yang menyesatkan kami kecuali orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,100,'فَمَا لَنَا مِنْ شَافِعِيْنَ ۙ ','Maka kami tidak mempunyai pemberi syafa´at seorangpun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,101,'وَلَا صَدِيْقٍ حَمِيْمٍ  ','dan tidak pula mempunyai teman yang akrab,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,102,'فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ  ','maka sekiranya kita dapat kembali sekali lagi (ke dunia) niscaya kami menjadi orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,103,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,104,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,105,'كَذَّبَتْ قَوْمُ نُوْحِ ِۨالْمُرْسَلِيْنَ ۚ ','Kaum Nuh telah mendustakan para rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,106,'اِذْ قَالَ لَهُمْ اَخُوْهُمْ نُوْحٌ اَلَا تَتَّقُوْنَ ۚ ','Ketika saudara mereka (Nuh) berkata kepada mereka: "Mengapa kamu tidak bertakwa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,107,'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ  ','Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,108,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ  ','maka bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,109,'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۚ ','Dan aku sekali-kali tidak minta upah kepadamu atas ajakan-ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,110,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ  ','Maka bertakwalah kepada Allah dan taatlah kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,111,'۞ قَالُوْٓا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُوْنَ ۗ ','Mereka berkata: "Apakah kami akan beriman kepadamu, padahal yang mengikuti kamu ialah orang-orang yang hina?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,112,'قَالَ وَمَا عِلْمِيْ بِمَا كَانُوْا يَعْمَلُوْنَ ۚ  ','Nuh menjawab: "Bagaimana aku mengetahui apa yang telah mereka kerjakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,113,'اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبِّيْ لَوْ تَشْعُرُوْنَ ۚ  ','Perhitungan (amal perbuatan) mereka tidak lain hanyalah kepada Tuhanku, kalau kamu menyadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,114,'وَمَآ اَنَا۠ بِطَارِدِ الْمُؤْمِنِيْنَ ۚ ','Dan aku sekali-kali tidak akan mengusir orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,115,'اِنْ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ۗ ','Aku (ini) tidak lain melainkan pemberi peringatan yang menjelaskan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,116,'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰنُوْحُ لَتَكُوْنَنَّ مِنَ الْمَرْجُوْمِيْنَۗ  ','Mereka berkata: "Sungguh jika kamu tidak (mau) berhenti hai Nuh, niscaya benar-benar kamu akan termasuk orang-orang yang dirajam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,117,'قَالَ رَبِّ اِنَّ قَوْمِيْ كَذَّبُوْنِۖ ','Nuh berkata: "Ya Tuhanku, sesungguhnya kaumku telah mendustakan aku;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,118,'فَافْتَحْ بَيْنِيْ وَبَيْنَهُمْ فَتْحًا وَّنَجِّنِيْ وَمَنْ مَّعِيَ مِنَ الْمُؤْمِنِيْنَ  ','maka itu adakanlah suatu keputusan antaraku dan antara mereka, dan selamatkanlah aku dan orang-orang yang mukmin besertaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,119,'فَاَنْجَيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ الْمَشْحُوْنِ  ','Maka Kami selamatkan Nuh dan orang-orang yang besertanya di dalam kapal yang penuh muatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,120,'ثُمَّ اَغْرَقْنَا بَعْدُ الْبٰقِيْنَ  ','Kemudian sesudah itu Kami tenggelamkan orang-orang yang tinggal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,121,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,122,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','Dan sesungguhnya Tuhanmu Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,123,'كَذَّبَتْ عَادُ ِۨالْمُرْسَلِيْنَ ۖ ','Kaum ´Aad telah mendustakan para rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,124,'اِذْ قَالَ لَهُمْ اَخُوْهُمْ هُوْدٌ اَلَا تَتَّقُوْنَ ۚ ','Ketika saudara mereka Hud berkata kepada mereka: "Mengapa kamu tidak bertakwa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,125,'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ','Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,126,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ','maka bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,127,'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ','Dan sekali-kali aku tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,128,'اَتَبْنُوْنَ بِكُلِّ رِيْعٍ اٰيَةً تَعْبَثُوْنَ ۙ ','Apakah kamu mendirikan pada tiap-tiap tanah tinggi bangunan untuk bermain-main,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,129,'وَتَتَّخِذُوْنَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُوْنَۚ  ','dan kamu membuat benteng-benteng dengan maksud supaya kamu kekal (di dunia)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,130,'وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِيْنَۚ  ','Dan apabila kamu menyiksa, maka kamu menyiksa sebagai orang-orang kejam dan bengis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,131,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ  ','Maka bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,132,'وَاتَّقُوا الَّذِيْٓ اَمَدَّكُمْ بِمَا تَعْلَمُوْنَ  ۚ','Dan bertakwalah kepada Allah yang telah menganugerahkan kepadamu apa yang kamu ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,133,'اَمَدَّكُمْ بِاَنْعَامٍ وَّبَنِيْنَۙ  ','Dia telah menganugerahkan kepadamu binatang-binatang ternak, dan anak-anak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,134,'وَجَنّٰتٍ وَّعُيُوْنٍۚ  ','dan kebun-kebun dan mata air,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,135,'اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ۗ ','sesungguhnya aku takut kamu akan ditimpa azab hari yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,136,'قَالُوْا سَوَاۤءٌ عَلَيْنَآ اَوَعَظْتَ اَمْ لَمْ تَكُنْ مِّنَ الْوَاعِظِيْنَ ۙ ','Mereka menjawab: "Adalah sama saja bagi kami, apakah kamu memberi nasehat atau tidak memberi nasehat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,137,'اِنْ هٰذَآ اِلَّا خُلُقُ الْاَوَّلِيْنَ ۙ ','(agama kami) ini tidak lain hanyalah adat kebiasaan orang dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,138,'وَمَا نَحْنُ بِمُعَذَّبِيْنَ ۚ ','dan kami sekali-kali tidak akan di "azab".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,139,'فَكَذَّبُوْهُ فَاَهْلَكْنٰهُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Maka mereka mendustakan Hud, lalu Kami binasakan mereka. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,140,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ','Dan sesungguhnya Tuhanmu, Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,141,'كَذَّبَتْ ثَمُوْدُ الْمُرْسَلِيْنَ  ۖ ','Kaum Tsamud telah mendustakan rasul-rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,142,'اِذْ قَالَ لَهُمْ اَخُوْهُمْ صٰلِحٌ اَلَا تَتَّقُوْنَ ۚ ','Ketika saudara mereka, Shaleh, berkata kepada mereka: "Mengapa kamu tidak bertakwa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,143,'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ  ','Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,144,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ  ','maka bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,145,'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ','Dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu, upahku tidak lain hanyalah dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,146,'اَتُتْرَكُوْنَ فِيْ مَا هٰهُنَآ اٰمِنِيْنَ ۙ ','Adakah kamu akan dibiarkan tinggal disini (di negeri kamu ini) dengan aman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,147,'فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ','di dalam kebun-kebun serta mata air,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,148,'وَّزُرُوْعٍ وَّنَخْلٍ طَلْعُهَا هَضِيْمٌ ۚ ','dan tanam-tanaman dan pohon-pohon korma yang mayangnya lembut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,149,'وَتَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا فٰرِهِيْنَ ','Dan kamu pahat sebagian dari gunung-gunung untuk dijadikan rumah-rumah dengan rajin;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,150,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ','maka bertakwalah kepada Allah dan taatlah kepadaku;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,151,'وَلَا تُطِيْعُوْٓا اَمْرَ الْمُسْرِفِيْنَ ۙ ','dan janganlah kamu mentaati perintah orang-orang yang melewati batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,152,'الَّذِيْنَ يُفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ  ','yang membuat kerusakan di muka bumi dan tidak mengadakan perbaikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,153,'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ','Mereka berkata: "Sesungguhnya kamu adalah salah seorang dari orang-orang yang kena sihir;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,154,'مَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَاۙ فَأْتِ بِاٰيَةٍ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ  ','Kamu tidak lain melainkan seorang manusia seperti kami; maka datangkanlah sesuatu mukjizat, jika kamu memang termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,155,'قَالَ هٰذِهٖ نَاقَةٌ لَّهَا شِرْبٌ وَّلَكُمْ شِرْبُ يَوْمٍ مَّعْلُوْمٍ ۚ ','Shaleh menjawab: "Ini seekor unta betina, ia mempunyai giliran untuk mendapatkan air, dan kamu mempunyai giliran pula untuk mendapatkan air di hari yang tertentu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,156,'وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيْمٍ  ','Dan janganlah kamu sentuh unta betina itu dengan sesuatu kejahatan, yang menyebabkan kamu akan ditimpa oleh azab hari yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,157,'فَعَقَرُوْهَا فَاَصْبَحُوْا نٰدِمِيْنَ ۙ ','Kemudian mereka membunuhnya, lalu mereka menjadi menyesal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,158,'فَاَخَذَهُمُ الْعَذَابُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','maka mereka ditimpa azab. Sesungguhnya pada yang demikian itu benar-benar terdapat bukti yang nyata. Dan adalah kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,159,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,160,'كَذَّبَتْ قَوْمُ لُوْطِ ِۨالْمُرْسَلِيْنَ ۖ ','Kaum Luth telah mendustakan rasul-rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,161,'اِذْ قَالَ لَهُمْ اَخُوْهُمْ لُوْطٌ اَلَا تَتَّقُوْنَ ۚ  ','ketika saudara mereka, Luth, berkata kepada mereka: mengapa kamu tidak bertakwa?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,162,'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ','Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,163,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ','maka bertakwalah kepada Allah dan taatlah kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,164,'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ','Dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semeta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,165,'اَتَأْتُوْنَ الذُّكْرَانَ مِنَ الْعٰلَمِيْنَ ۙ ','Mengapa kamu mendatangi jenis lelaki di antara manusia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,166,'وَتَذَرُوْنَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِّنْ اَزْوَاجِكُمْۗ بَلْ اَنْتُمْ قَوْمٌ عٰدُوْنَ  ','dan kamu tinggalkan isteri-isteri yang dijadikan oleh Tuhanmu untukmu, bahkan kamu adalah orang-orang yang melampaui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,167,'قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰلُوْطُ لَتَكُوْنَنَّ مِنَ الْمُخْرَجِيْنَ  ','Mereka menjawab: "Hai Luth, sesungguhnya jika kamu tidak berhenti, benar-benar kamu termasuk orang-orang yang diusir"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,168,'قَالَ ِانِّيْ لِعَمَلِكُمْ مِّنَ الْقَالِيْنَ  ۗ','Luth berkata: "Sesungguhnya aku sangat benci kepada perbuatanmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,169,'رَبِّ نَجِّنِيْ وَاَهْلِيْ مِمَّا يَعْمَلُوْنَ  ','(Luth berdoa): "Ya Tuhanku selamatkanlah aku beserta keluargaku dari (akibat) perbuatan yang mereka kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,170,'فَنَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْنَ ۙ ','Lalu Kami selamatkan ia beserta keluarganya semua,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,171,'اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ ۚ  ','kecuali seorang perempuan tua (isterinya), yang termasuk dalam golongan yang tinggal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,172,'ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ ۚ ','Kemudian Kami binasakan yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,173,'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ','Dan Kami hujani mereka dengan hujan (batu) maka amat jeleklah hujan yang menimpa orang-orang yang telah diberi peringatan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,174,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Sesunguhnya pada yang demikian itu benar-benar terdapat bukti-bukti yang nyata. Dan adalah kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,175,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ','Dan sesungguhnya Tuhanmu, benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,176,'كَذَّبَ اَصْحٰبُ لْـَٔيْكَةِ الْمُرْسَلِيْنَ ۖ ','Penduduk Aikah telah mendustakan rasul-rasul;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,177,'اِذْ قَالَ لَهُمْ شُعَيْبٌ اَلَا تَتَّقُوْنَ ۚ ','ketika Syu´aib berkata kepada mereka: "Mengapa kamu tidak bertakwa?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,178,'اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ','Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,179,'فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ','maka bertakwalah kepada Allah dan taatlah kepadaku;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,180,'وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ','dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,181,'۞ اَوْفُوا الْكَيْلَ وَلَا تَكُوْنُوْا مِنَ الْمُخْسِرِيْنَ ۚ ','Sempurnakanlah takaran dan janganlah kamu termasuk orang-orang yang merugikan;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,182,'وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِ ۚ ','dan timbanglah dengan timbangan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,183,'وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۚ ','Dan janganlah kamu merugikan manusia pada hak-haknya dan janganlah kamu merajalela di muka bumi dengan membuat kerusakan;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,184,'وَاتَّقُوا الَّذِيْ خَلَقَكُمْ وَالْجِبِلَّةَ الْاَوَّلِيْنَ ۗ ','dan bertakwalah kepada Allah yang telah menciptakan kamu dan umat-umat yang dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,185,'قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ','Mereka berkata: "Sesungguhnya kamu adalah salah seorang dari orang-orang yang kena sihir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,186,'وَمَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَا وَاِنْ نَّظُنُّكَ لَمِنَ الْكٰذِبِيْنَ ۚ ','dan kamu tidak lain melainkan seorang manusia seperti kami, dan sesungguhnya kami yakin bahwa kamu benar-benar termasuk orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,187,'فَاَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ السَّمَاۤءِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ۗ ','Maka jatuhkanlah atas kami gumpalan dari langit, jika kamu termasuk orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,188,'قَالَ رَبِّيْٓ اَعْلَمُ بِمَا تَعْمَلُوْنَ  ','Syu´aib berkata: "Tuhanku lebih mengetahui apa yang kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,189,'فَكَذَّبُوْهُ فَاَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۗاِنَّهٗ كَانَ عَذَابَ يَوْمٍ عَظِيْمٍ ','Kemudian mereka mendustakan Syu´aib, lalu mereka ditimpa azab pada hari mereka dinaungi awan. Sesungguhnya azab itu adalah azab hari yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,190,'اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ  ','Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,191,'وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,192,'وَاِنَّهٗ لَتَنْزِيْلُ رَبِّ الْعٰلَمِيْنَ ۗ  ','Dan sesungguhnya Al Quran ini benar-benar diturunkan oleh Tuhan semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,193,'نَزَلَ بِهِ الرُّوْحُ الْاَمِيْنُ ۙ ','dia dibawa turun oleh Ar-Ruh Al-Amin (Jibril),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,194,'عَلٰى قَلْبِكَ لِتَكُوْنَ مِنَ الْمُنْذِرِيْنَ ۙ  ','ke dalam hatimu (Muhammad) agar kamu menjadi salah seorang di antara orang-orang yang memberi peringatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,195,'بِلِسَانٍ عَرَبِيٍّ مُّبِيْنٍ ۗ ','dengan bahasa Arab yang jelas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,196,'وَاِنَّهٗ لَفِيْ زُبُرِ الْاَوَّلِيْنَ  ','Dan sesungguhnya Al Quran itu benar-benar (tersebut) dalam Kitab-kitab orang yang dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,197,'اَوَلَمْ يَكُنْ لَّهُمْ اٰيَةً اَنْ يَّعْلَمَهٗ عُلَمٰۤؤُا بَنِيْٓ اِسْرَاۤءِيْلَ  ','Dan apakah tidak cukup menjadi bukti bagi mereka, bahwa para ulama Bani Israil mengetahuinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,198,'وَلَوْ نَزَّلْنٰهُ عَلٰى بَعْضِ الْاَعْجَمِيْنَ ۙ ','Dan kalau Al Quran itu Kami turunkan kepada salah seorang dari golongan bukan Arab,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,199,'فَقَرَاَهٗ عَلَيْهِمْ مَّا كَانُوْا بِهٖ مُؤْمِنِيْنَ ۗ  ','lalu ia membacakannya kepada mereka (orang-orang kafir); niscaya mereka tidak akan beriman kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,200,'كَذٰلِكَ سَلَكْنٰهُ فِيْ قُلُوْبِ الْمُجْرِمِيْنَ ۗ  ','Demikianlah Kami masukkan Al Quran ke dalam hati orang-orang yang durhaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,201,'لَا يُؤْمِنُوْنَ بِهٖ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ  ','Mereka tidak beriman kepadanya, hingga mereka melihat azab yang pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,202,'فَيَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ۙ  ','maka datanglah azab kepada mereka dengan mendadak, sedang mereka tidak menyadarinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,203,'فَيَقُوْلُوْا هَلْ نَحْنُ مُنْظَرُوْنَ ۗ ','lalu mereka berkata: "Apakah kami dapat diberi tangguh?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,204,'اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ  ','Maka apakah mereka meminta supaya disegerakan azab Kami?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,205,'اَفَرَءَيْتَ اِنْ مَّتَّعْنٰهُمْ سِنِيْنَ ۙ  ','Maka bagaimana pendapatmu jika Kami berikan kepada mereka kenikmatan hidup bertahun-tahun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,206,'ثُمَّ جَاۤءَهُمْ مَّا كَانُوْا يُوْعَدُوْنَ ۙ  ','Kemudian datang kepada mereka azab yang telah diancamkan kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,207,'مَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يُمَتَّعُوْنَ ۗ ','niscaya tidak berguna bagi mereka apa yang mereka selalu menikmatinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,208,'وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُوْنَ ۖ  ','Dan Kami tidak membinasakan sesuatu negeripun, melainkan sesudah ada baginya orang-orang yang memberi peringatan;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,209,'ذِكْرٰىۚ وَمَا كُنَّا ظٰلِمِيْنَ  ','untuk menjadi peringatan. Dan Kami sekali-kali tidak berlaku zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,210,'وَمَا تَنَزَّلَتْ بِهِ الشَّيٰطِيْنُ  ','Dan Al Quran itu bukanlah dibawa turun oleh syaitan-syaitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,211,'وَمَا يَنْۢبَغِيْ لَهُمْ وَمَا يَسْتَطِيْعُوْنَ ۗ  ','Dan tidaklah patut mereka membawa turun Al Quran itu, dan merekapun tidak akan kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,212,'اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُوْلُوْنَ ۗ ','Sesungguhnya mereka benar-benar dijauhkan daripada mendengar Al Quran itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,213,'فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَكُوْنَ مِنَ الْمُعَذَّبِيْنَ','Maka janganlah kamu menyeru (menyembah) tuhan yang lain di samping Allah, yang menyebabkan kamu termasuk orang-orang yang diazab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,214,'وَاَنْذِرْ عَشِيْرَتَكَ الْاَقْرَبِيْنَ ۙ ','Dan berilah peringatan kepada kerabat-kerabatmu yang terdekat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,215,'وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ۚ  ','dan rendahkanlah dirimu terhadap orang-orang yang mengikutimu, yaitu orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,216,'فَاِنْ عَصَوْكَ فَقُلْ اِنِّيْ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ۚ ','Jika mereka mendurhakaimu maka katakanlah: "Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu kerjakan";')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,217,'وَتَوَكَّلْ عَلَى الْعَزِيْزِ الرَّحِيْمِ ۙ ','Dan bertawakkallah kepada (Allah) Yang Maha Perkasa lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,218,'الَّذِيْ يَرٰىكَ حِيْنَ تَقُوْمُ  ','Yang melihat kamu ketika kamu berdiri (untuk sembahyang),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,219,'وَتَقَلُّبَكَ فِى السّٰجِدِيْنَ  ','dan (melihat pula) perubahan gerak badanmu di antara orang-orang yang sujud.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,220,'اِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ  ','Sesungguhnya Dia adalah Yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,221,'هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيٰطِيْنُ ۗ  ','Apakah akan Aku beritakan kepadamu, kepada siapa syaitan-syaitan itu turun?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,222,'تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَثِيْمٍ ۙ ','Mereka turun kepada tiap-tiap pendusta lagi yang banyak dosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,223,'يُّلْقُوْنَ السَّمْعَ وَاَكْثَرُهُمْ كٰذِبُوْنَ ۗ  ','mereka menghadapkan pendengaran (kepada syaitan) itu, dan kebanyakan mereka adalah orang-orang pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,224,'وَالشُّعَرَاۤءُ يَتَّبِعُهُمُ الْغَاوٗنَ ۗ ','Dan penyair-penyair itu diikuti oleh orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,225,'اَلَمْ تَرَ اَنَّهُمْ فِيْ كُلِّ وَادٍ يَّهِيْمُوْنَ ۙ ','Tidakkah kamu melihat bahwasanya mereka mengembara di tiap-tiap lembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,226,'وَاَنَّهُمْ يَقُوْلُوْنَ مَا لَا يَفْعَلُوْنَ ۙ  ','dan bahwasanya mereka suka mengatakan apa yang mereka sendiri tidak mengerjakan(nya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,26,227,'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَذَكَرُوا اللّٰهَ كَثِيْرًا وَّانْتَصَرُوْا مِنْۢ بَعْدِ مَا ظُلِمُوْا ۗوَسَيَعْلَمُ الَّذِيْنَ ظَلَمُوْٓا اَيَّ مُنْقَلَبٍ يَّنْقَلِبُوْنَ ࣖ ','kecuali orang-orang (penyair-penyair) yang beriman dan beramal saleh dan banyak menyebut Allah dan mendapat kemenangan sesudah menderita kezaliman. Dan orang-orang yang zalim itu kelak akan mengetahui ke tempat mana mereka akan kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,1,'طٰسۤ ۚ تِلْكَ اٰيٰتُ الْقُرْاٰنِ وَكِتَابٍ مُّبِيْنٍ ۙ ','Thaa Siin (Surat) ini adalah ayat-ayat Al Quran, dan (ayat-ayat) Kitab yang menjelaskan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,2,'هُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ۙ  ','untuk menjadi petunjuk dan berita gembira untuk orang-orang yang beriman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,3,'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَ ','(yaitu) orang-orang yang mendirikan sembahyang dan menunaikan zakat dan mereka yakin akan adanya negeri akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,4,'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُوْنَ ۗ ','Sesungguhnya orang-orang yang tidak beriman kepada negeri akhirat, Kami jadikan mereka memandang indah perbuatan-perbuatan mereka, maka mereka bergelimang (dalam kesesatan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,5,'اُولٰۤىِٕكَ الَّذِيْنَ لَهُمْ سُوْۤءُ الْعَذَابِ وَهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ ','Mereka itulah orang-orang yang mendapat (di dunia) azab yang buruk dan mereka di akhirat adalah orang-orang yang paling merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,6,'وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَّدُنْ حَكِيْمٍ عَلِيْمٍ ','Dan sesungguhnya kamu benar-benar diberi Al Quran dari sisi (Allah) Yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,7,'اِذْ قَالَ مُوْسٰى لِاَهْلِهٖٓ اِنِّيْٓ اٰنَسْتُ نَارًاۗ سَاٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ اٰتِيْكُمْ بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُوْنَ ','(Ingatlah) ketika Musa berkata kepada keluarganya: "Sesungguhnya aku melihat api. Aku kelak akan membawa kepadamu khabar daripadanya, atau aku membawa kepadamu suluh api supaya kamu dapat berdiang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,8,'فَلَمَّا جَاۤءَهَا نُوْدِيَ اَنْۢ بُوْرِكَ مَنْ فِى النَّارِ وَمَنْ حَوْلَهَاۗ وَسُبْحٰنَ اللّٰهِ رَبِّ الْعٰلَمِيْنَ ','Maka tatkala dia tiba di (tempat) api itu, diserulah dia: "Bahwa telah diberkati orang-orang yang berada di dekat api itu, dan orang-orang yang berada di sekitarnya. Dan Maha Suci Allah, Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,9,'يٰمُوْسٰٓى اِنَّهٗٓ اَنَا اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ۙ ','(Allah berfirman): "Hai Musa, sesungguhnya, Akulah Allah, Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,10,'وَاَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰى لَا تَخَفْۗ اِنِّيْ لَا يَخَافُ لَدَيَّ الْمُرْسَلُوْنَ ۖ  ','dan lemparkanlah tongkatmu". Maka tatkala (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seperti dia seekor ular yang gesit, larilah ia berbalik ke belakang tanpa menoleh. "Hai Musa, janganlah kamu takut. Sesungguhnya orang yang dijadikan rasul, tidak takut di hadapan-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,11,'اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًاۢ بَعْدَ سُوْۤءٍ فَاِنِّيْ غَفُوْرٌ رَّحِيْمٌ  ','tetapi orang yang berlaku zalim, kemudian ditukarnya kezalimannya dengan kebaikan (Allah akan mengampuninya); maka seaungguhnya Aku Maha Pangampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,12,'وَاَدْخِلْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍۙ فِيْ تِسْعِ اٰيٰتٍ اِلٰى فِرْعَوْنَ وَقَوْمِهٖۚ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ','Dan masukkanlah tanganmu ke leher bajumu, niscaya ia akan ke luar putih (bersinar) bukan karena penyakit. (Kedua mukjizat ini) termasuk sembilan buah mukjizat (yang akan dikemukakan) kepada Fir´aun dan kaumnya. Sesungguhnya mereka adalah kaum yang fasik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,13,'فَلَمَّا جَاۤءَتْهُمْ اٰيٰتُنَا مُبْصِرَةً قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ۚ  ','Maka tatkala mukjizat-mukjizat Kami yang jelas itu sampai kepada mereka, berkatalah mereka: "Ini adalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,14,'وَجَحَدُوْا بِهَا وَاسْتَيْقَنَتْهَآ اَنْفُسُهُمْ ظُلْمًا وَّعُلُوًّاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ࣖ  ','Dan mereka mengingkarinya karena kezaliman dan kesombongan (mereka) padahal hati mereka meyakini (kebenaran)nya. Maka perhatikanlah betapa kesudahan orang-orang yang berbuat kebinasaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,15,'وَلَقَدْ اٰتَيْنَا دَاوٗدَ وَسُلَيْمٰنَ عِلْمًاۗ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذِيْ فَضَّلَنَا عَلٰى كَثِيْرٍ مِّنْ عِبَادِهِ الْمُؤْمِنِيْنَ  ','Dan sesungguhnya Kami telah memberi ilmu kepada Daud dan Sulaiman; dan keduanya mengucapkan: "Segala puji bagi Allah yang melebihkan kami dari kebanyakan hamba-hambanya yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,16,'وَوَرِثَ سُلَيْمٰنُ دَاوٗدَ وَقَالَ يٰٓاَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُوْتِيْنَا مِنْ كُلِّ شَيْءٍۗ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُبِيْنُ  ','Dan Sulaiman telah mewarisi Daud, dan dia berkata: "Hai Manusia, kami telah diberi pengertian tentang suara burung dan kami diberi segala sesuatu. Sesungguhnya (semua) ini benar-benar suatu kurnia yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,17,'وَحُشِرَ لِسُلَيْمٰنَ جُنُوْدُهٗ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوْزَعُوْنَ  ','Dan dihimpunkan untuk Sulaiman tentaranya dari jin, manusia dan burung lalu mereka itu diatur dengan tertib (dalam barisan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,18,'حَتّٰىٓ اِذَآ اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يّٰٓاَيُّهَا النَّمْلُ ادْخُلُوْا مَسٰكِنَكُمْۚ  لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُوْدُهٗۙ وَهُمْ لَا يَشْعُرُوْنَ  ','Hingga apabila mereka sampai di lembah semut berkatalah seekor semut: Hai semut-semut, masuklah ke dalam sarang-sarangmu, agar kamu tidak diinjak oleh Sulaiman dan tentaranya, sedangkan mereka tidak menyadari";')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,19,'فَتَبَسَّمَ ضَاحِكًا مِّنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَدْخِلْنِيْ بِرَحْمَتِكَ فِيْ عِبَادِكَ الصّٰلِحِيْنَ  ','maka dia tersenyum dengan tertawa karena (mendengar) perkataan semut itu. Dan dia berdoa: "Ya Tuhanku berilah aku ilham untuk tetap mensyukuri nikmat Mu yang telah Engkau anugerahkan kepadaku dan kepada dua orang ibu bapakku dan untuk mengerjakan amal saleh yang Engkau ridhai; dan masukkanlah aku dengan rahmat-Mu ke dalam golongan hamba-hamba-Mu yang saleh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,20,'وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَآ اَرَى الْهُدْهُدَۖ اَمْ كَانَ مِنَ الْغَاۤىِٕبِيْنَ  ','Dan dia memeriksa burung-burung lalu berkata: "Mengapa aku tidak melihat hud-hud, apakah dia termasuk yang tidak hadir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,21,'لَاُعَذِّبَنَّهٗ عَذَابًا شَدِيْدًا اَوْ لَاَا۟ذْبَحَنَّهٗٓ اَوْ لَيَأْتِيَنِّيْ بِسُلْطٰنٍ مُّبِيْنٍ  ','Sungguh aku benar-benar akan mengazabnya dengan azab yang keras atau benar-benar menyembelihnya kecuali jika benar-benar dia datang kepadaku dengan alasan yang terang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,22,'فَمَكَثَ غَيْرَ بَعِيْدٍ فَقَالَ اَحَطْتُّ بِمَا لَمْ تُحِطْ بِهٖ وَجِئْتُكَ مِنْ سَبَاٍ ۢبِنَبَاٍ يَّقِيْنٍ ','Maka tidak lama kemudian (datanglah hud-hud), lalu ia berkata: "Aku telah mengetahui sesuatu yang kamu belum mengetahuinya; dan kubawa kepadamu dari negeri Saba suatu berita penting yang diyakini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,23,'اِنِّيْ وَجَدْتُّ امْرَاَةً تَمْلِكُهُمْ وَاُوْتِيَتْ مِنْ كُلِّ شَيْءٍ وَّلَهَا عَرْشٌ عَظِيْمٌ  ','Sesungguhnya aku menjumpai seorang wanita yang memerintah mereka, dan dia dianugerahi segala sesuatu serta mempunyai singgasana yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,24,'وَجَدْتُّهَا وَقَوْمَهَا يَسْجُدُوْنَ لِلشَّمْسِ مِنْ دُوْنِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ فَهُمْ لَا يَهْتَدُوْنَۙ  ','Aku mendapati dia dan kaumnya menyembah matahari, selain Allah; dan syaitan telah menjadikan mereka memandang indah perbuatan-perbuatan mereka lalu menghalangi mereka dari jalan (Allah), sehingga mereka tidak dapat petunjuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,25,'اَلَّا يَسْجُدُوْا لِلّٰهِ الَّذِيْ يُخْرِجُ الْخَبْءَ فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُوْنَ وَمَا تُعْلِنُوْنَ  ','agar mereka tidak menyembah Allah Yang mengeluarkan apa yang terpendam di langit dan di bumi dan Yang mengetahui apa yang kamu sembunyikan dan apa yang kamu nyatakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,26,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۙ رَبُّ الْعَرْشِ الْعَظِيْمِ ۩ ','Allah, tiada Tuhan Yang disembah kecuali Dia, Tuhan Yang mempunyai ´Arsy yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,27,'۞ قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكٰذِبِيْنَ ','Berkata Sulaiman: "Akan kami lihat, apa kamu benar, ataukah kamu termasuk orang-orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,28,'اِذْهَبْ بِّكِتٰبِيْ هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُوْنَ ','Pergilah dengan (membawa) suratku ini, lalu jatuhkan kepada mereka, kemudian berpalinglah dari mereka, lalu perhatikanlah apa yang mereka bicarakan"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,29,'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اِنِّيْٓ اُلْقِيَ اِلَيَّ كِتٰبٌ كَرِيْمٌ  ','Berkata ia (Balqis): "Hai pembesar-pembesar, sesungguhnya telah dijatuhkan kepadaku sebuah surat yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,30,'اِنَّهٗ مِنْ سُلَيْمٰنَ وَاِنَّهٗ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ ۙ  ','Sesungguhnya surat itu, dari SuIaiman dan sesungguhnya (isi)nya: "Dengan menyebut nama Allah Yang Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,31,'اَلَّا تَعْلُوْا عَلَيَّ وَأْتُوْنِيْ مُسْلِمِيْنَ ࣖ ','Bahwa janganlah kamu sekalian berlaku sombong terhadapku dan datanglah kepadaku sebagai orang-orang yang berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,32,'قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اَفْتُوْنِيْ فِيْٓ اَمْرِيْۚ مَا كُنْتُ قَاطِعَةً اَمْرًا حَتّٰى تَشْهَدُوْنِ ','Berkata dia (Balqis): "Hai para pembesar berilah aku pertimbangan dalam urusanku (ini) aku tidak pernah memutuskan sesuatu persoalan sebelum kamu berada dalam majelis(ku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,33,'قَالُوْا نَحْنُ اُولُوْا قُوَّةٍ وَّاُولُوْا بَأْسٍ شَدِيْدٍ ەۙ وَّالْاَمْرُ اِلَيْكِ فَانْظُرِيْ مَاذَا تَأْمُرِيْنَ','Mereka menjawab: "Kita adalah orang-orang yang memiliki kekuatan dan (juga) memiliki keberanian yang sangat (dalam peperangan), dan keputusan berada ditanganmu: maka pertimbangkanlah apa yang akan kamu perintahkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,34,'قَالَتْ اِنَّ الْمُلُوْكَ اِذَا دَخَلُوْا قَرْيَةً اَفْسَدُوْهَا وَجَعَلُوْٓا اَعِزَّةَ اَهْلِهَآ اَذِلَّةً ۚوَكَذٰلِكَ يَفْعَلُوْنَ  ','Dia berkata: "Sesungguhnya raja-raja apabila memasuki suatu negeri, niscaya mereka membinasakannya, dan menjadikan penduduknya yang mulia jadi hina; dan demikian pulalah yang akan mereka perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,35,'وَاِنِّيْ مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنٰظِرَةٌ ۢبِمَ يَرْجِعُ الْمُرْسَلُوْنَ ','Dan sesungguhnya aku akan mengirim utusan kepada mereka dengan (membawa) hadiah, dan (aku akan) menunggu apa yang akan dibawa kembali oleh utusan-utusan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,36,'فَلَمَّا جَاۤءَ سُلَيْمٰنَ قَالَ اَتُمِدُّوْنَنِ بِمَالٍ فَمَآ اٰتٰىنِ  َۧ اللّٰهُ خَيْرٌ مِّمَّآ اٰتٰىكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُوْنَ  ','Maka tatkala utusan itu sampai kepada Sulaiman, Sulaiman berkata: "Apakah (patut) kamu menolong aku dengan harta? maka apa yang diberikan Allah kepadaku lebih baik daripada apa yang diberikan-Nya kepadamu; tetapi kamu merasa bangga dengan hadiahmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,37,'اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُوْدٍ لَّا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِّنْهَآ اَذِلَّةً وَّهُمْ صَاغِرُوْنَ ','Kembalilah kepada mereka sungguh kami akan mendatangi mereka dengan balatentara yang mereka tidak kuasa melawannya, dan pasti kami akan mengusir mereka dari negeri itu (Saba) dengan terhina dan mereka menjadi (tawanan-tawanan) yang hina dina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,38,'قَالَ يٰٓاَيُّهَا الْمَلَؤُا اَيُّكُمْ يَأْتِيْنِيْ بِعَرْشِهَا قَبْلَ اَنْ يَّأْتُوْنِيْ مُسْلِمِيْنَ ','Berkata Sulaiman: "Hai pembesar-pembesar, siapakah di antara kamu sekalian yang sanggup membawa singgasananya kepadaku sebelum mereka datang kepadaku sebagai orang-orang yang berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,39,'قَالَ عِفْرِيْتٌ مِّنَ الْجِنِّ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ تَقُوْمَ مِنْ مَّقَامِكَۚ وَاِنِّيْ عَلَيْهِ لَقَوِيٌّ اَمِيْنٌ  ','Berkata ´Ifrit (yang cerdik) dari golongan jin: "Aku akan datang kepadamu dengan membawa singgsana itu kepadamu sebelum kamu berdiri dari tempat dudukmu; sesungguhnya aku benar-benar kuat untuk membawanya lagi dapat dipercaya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,40,'قَالَ الَّذِيْ عِنْدَهٗ عِلْمٌ مِّنَ الْكِتٰبِ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ يَّرْتَدَّ اِلَيْكَ طَرْفُكَۗ فَلَمَّا رَاٰهُ مُسْتَقِرًّا عِنْدَهٗ قَالَ هٰذَا مِنْ فَضْلِ رَبِّيْۗ  لِيَبْلُوَنِيْٓ ءَاَشْكُرُ اَمْ اَكْفُرُۗ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ رَبِّيْ غَنِيٌّ كَرِيْمٌ  ','Berkatalah seorang yang mempunyai ilmu dari Al Kitab: "Aku akan membawa singgasana itu kepadamu sebelum matamu berkedip". Maka tatkala Sulaiman melihat singgasana itu terletak di hadapannya, iapun berkata: "Ini termasuk kurnia Tuhanku untuk mencoba aku apakah aku bersyukur atau mengingkari (akan nikmat-Nya). Dan barangsiapa yang bersyukur maka sesungguhnya dia bersyukur untuk (kebaikan) dirinya sendiri dan barangsiapa yang ingkar, maka sesungguhnya Tuhanku Maha Kaya lagi Maha Mulia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,41,'قَالَ نَكِّرُوْا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَدِيْٓ اَمْ تَكُوْنُ مِنَ الَّذِيْنَ لَا يَهْتَدُوْنَ ','Dia berkata: "Rubahlah baginya singgasananya; maka kita akan melihat apakah dia mengenal ataukah dia termasuk orang-orang yang tidak mengenal(nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,42,'فَلَمَّا جَاۤءَتْ قِيْلَ اَهٰكَذَا عَرْشُكِۗ قَالَتْ كَاَنَّهٗ هُوَۚ وَاُوْتِيْنَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِيْنَ  ','Dan ketika Balqis datang, ditanyakanlah kepadanya: "Serupa inikah singgasanamu?" Dia menjawab: "Seakan-akan singgasana ini singgasanaku, kami telah diberi pengetahuan sebelumnya dan kami adalah orang-orang yang berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,43,'وَصَدَّهَا مَا كَانَتْ تَّعْبُدُ مِنْ دُوْنِ اللّٰهِ ۗاِنَّهَا كَانَتْ مِنْ قَوْمٍ كٰفِرِيْنَ  ','Dan apa yang disembahnya selama ini selain Allah, mencegahnya (untuk melahirkan keislamannya), karena sesungguhnya dia dahulunya termasuk orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,44,'قِيْلَ لَهَا ادْخُلِى الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَّكَشَفَتْ عَنْ سَاقَيْهَاۗ قَالَ اِنَّهٗ صَرْحٌ مُّمَرَّدٌ مِّنْ قَوَارِيْرَ ەۗ قَالَتْ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ','Dikatakan kepadanya: "Masuklah ke dalam istana". Maka tatkala dia melihat lantai istana itu, dikiranya kolam air yang besar, dan disingkapkannya kedua betisnya. Berkatalah Sulaiman: "Sesungguhnya ia adalah istana licin terbuat dari kaca". Berkatalah Balqis: "Ya Tuhanku, sesungguhnya aku telah berbuat zalim terhadap diriku dan aku berserah diri bersama Sulaiman kepada Allah, Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,45,'وَلَقَدْ اَرْسَلْنَآ اِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا اَنِ اعْبُدُوا اللّٰهَ فَاِذَا هُمْ فَرِيْقٰنِ يَخْتَصِمُوْنَ  ','Dan sesungguhnya Kami telah mengutus kepada (kaum) Tsamud saudara mereka Shaleh (yang berseru): "Sembahlah Allah". Tetapi tiba-tiba mereka (jadi) dua golongan yang bermusuhan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,46,'قَالَ يٰقَوْمِ لِمَ تَسْتَعْجِلُوْنَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ  لَوْلَا تَسْتَغْفِرُوْنَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ ','Dia berkata: "Hai kaumku mengapa kamu minta disegerakan keburukan sebelum (kamu minta) kebaikan? Hendaklah kamu meminta ampun kepada Allah, agar kamu mendapat rahmat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,47,'قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَّعَكَۗ قَالَ طٰۤىِٕرُكُمْ عِنْدَ اللّٰهِ  بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُوْنَ ','Mereka menjawab: "Kami mendapat nasib yang malang, disebabkan kamu dan orang-orang yang besertamu". Shaleh berkata: "Nasibmu ada pada sisi Allah, (bukan kami yang menjadi sebab), tetapi kamu kaum yang diuji".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,48,'وَكَانَ فِى الْمَدِيْنَةِ تِسْعَةُ رَهْطٍ يُّفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ  ','Dan adalah di kota itu sembilan orang laki-laki yang membuat kerusakan di muka bumi, dan mereka tidak berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,49,'قَالُوْا تَقَاسَمُوْا بِاللّٰهِ لَنُبَيِّتَنَّهٗ وَاَهْلَهٗ ثُمَّ لَنَقُوْلَنَّ لِوَلِيِّهٖ مَا شَهِدْنَا مَهْلِكَ اَهْلِهٖ وَاِنَّا لَصٰدِقُوْنَ  ','Mereka berkata: "Bersumpahlah kamu dengan nama Allah, bahwa kita sungguh-sungguh akan menyerangnya dengan tiba-tiba beserta keluarganya di malam hari, kemudian kita katakan kepada warisnya (bahwa) kita tidak menyaksikan kematian keluarganya itu, dan sesungguhnya kita adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,50,'وَمَكَرُوْا مَكْرًا وَّمَكَرْنَا مَكْرًا وَّهُمْ لَا يَشْعُرُوْنَ  ','Dan merekapun merencanakan makar dengan sungguh-sungguh dan Kami merencanakan makar (pula), sedang mereka tidak menyadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,51,'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ اَنَّا دَمَّرْنٰهُمْ وَقَوْمَهُمْ اَجْمَعِيْنَ  ','Maka perhatikanlah betapa sesungguhnya akibat makar mereka itu, bahwasanya Kami membinasakan mereka dan kaum mereka semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,52,'فَتِلْكَ بُيُوْتُهُمْ خَاوِيَةً ۢبِمَا ظَلَمُوْاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْلَمُوْنَ  ','Maka itulah rumah-rumah mereka dalam keadaan runtuh disebabkan kezaliman mereka. Sesungguhnya pada yang demikian itu (terdapat) pelajaran bagi kaum yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,53,'وَاَنْجَيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ  ','Dan telah Kami selamatkan orang-orang yang beriman dan mereka itu selalu bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,54,'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُوْنَ  ','Dan (ingatlah kisah) Luth, ketika dia berkata kepada kaumnya: "Mengapa kamu mengerjakan perbuatan fahisyah itu sedang kamu memperlihatkan(nya)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,55,'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِ ۗبَلْ اَنْتُمْ قَوْمٌ تَجْهَلُوْنَ','"Mengapa kamu mendatangi laki-laki untuk (memenuhi) nafsu(mu), bukan (mendatangi) wanita? Sebenarnya kamu adalah kaum yang tidak mengetahui (akibat perbuatanmu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,56,'۞ فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْٓا اٰلَ لُوْطٍ مِّنْ قَرْيَتِكُمْۙ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ  ','Maka tidak lain jawaban kaumnya melainkan mengatakan: "Usirlah Luth beserta keluarganya dari negerimu; karena sesungguhnya mereka itu orang-orang yang (mendakwakan dirinya) bersih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,57,'فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ قَدَّرْنٰهَا مِنَ الْغٰبِرِيْنَ  ','Maka Kami selamatkan dia beserta keluarganya, kecuali isterinya. Kami telah mentakdirkan dia termasuk orang-orang yang tertinggal (dibinasakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,58,'وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ࣖ ','Dan Kami turunkan hujan atas mereka (hujan batu), maka amat buruklah hujan yang ditimpakan atas orang-orang yang diberi peringatan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,19,27,59,'قُلِ الْحَمْدُ لِلّٰهِ وَسَلٰمٌ عَلٰى عِبَادِهِ الَّذِيْنَ اصْطَفٰىۗ ءٰۤاللّٰهُ خَيْرٌ اَمَّا يُشْرِكُوْنَ  ۔ ','Katakanlah: "Segala puji bagi Allah dan kesejahteraan atas hamba-hamba-Nya yang dipilih-Nya. Apakah Allah yang lebih baik, ataukah apa yang mereka persekutukan dengan Dia?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,60,'اَمَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِّنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا بِهٖ حَدَاۤىِٕقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْۢبِتُوْا شَجَرَهَاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ هُمْ قَوْمٌ يَّعْدِلُوْنَ ۗ ','Atau siapakah yang telah menciptakan langit dan bumi dan yang menurunkan air untukmu dari langit, lalu Kami tumbuhkan dengan air itu kebun-kebun yang berpemandangan indah, yang kamu sekali-kali tidak mampu menumbuhkan pohon-pohonnya? Apakah disamping Allah ada tuhan (yang lain)? Bahkan (sebenarnya) mereka adalah orang-orang yang menyimpang (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,61,'اَمَّنْ جَعَلَ الْاَرْضَ قَرَارًا وَّجَعَلَ خِلٰلَهَآ اَنْهٰرًا وَّجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ۗ  ','Atau siapakah yang telah menjadikan bumi sebagai tempat berdiam, dan yang menjadikan sungai-sungai di celah-celahnya, dan yang menjadikan gunung-gunung untuk (mengkokohkan)nya dan menjadikan suatu pemisah antara dua laut? Apakah disamping Allah ada tuhan (yang lain)? Bahkan (sebenarnya) kebanyakan dari mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,62,'اَمَّنْ يُّجِيْبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّوْۤءَ وَيَجْعَلُكُمْ خُلَفَاۤءَ الْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقَلِيْلًا مَّا تَذَكَّرُوْنَۗ  ','Atau siapakah yang memperkenankan (doa) orang yang dalam kesulitan apabila ia berdoa kepada-Nya, dan yang menghilangkan kesusahan dan yang menjadikan kamu (manusia) sebagai khalifah di bumi? Apakah disamping Allah ada tuhan (yang lain)? Amat sedikitlah kamu mengingati(Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,63,'اَمَّنْ يَّهْدِيْكُمْ فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُّرْسِلُ الرِّيٰحَ بُشْرًا ۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ ','Atau siapakah yang memimpin kamu dalam kegelapan di dataran dan lautan dan siapa (pula)kah yang mendatangkan angin sebagai kabar gembira sebelum (kedatangan) rahmat-Nya? Apakah disamping Allah ada tuhan (yang lain)? Maha Tinggi Allah terhadap apa yang mereka persekutukan (dengan-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,64,'اَمَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَمَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Atau siapakah yang menciptakan (manusia dari permulaannya), kemudian mengulanginya (lagi), dan siapa (pula) yang memberikan rezeki kepadamu dari langit dan bumi? Apakah disamping Allah ada tuhan (yang lain)?. Katakanlah: "Unjukkanlah bukti kebenaranmu, jika kamu memang orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,65,'قُلْ لَّا يَعْلَمُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُ ۗوَمَا يَشْعُرُوْنَ اَيَّانَ يُبْعَثُوْنَ ','Katakanlah: "Tidak ada seorangpun di langit dan di bumi yang mengetahui perkara yang ghaib, kecuali Allah", dan mereka tidak mengetahui bila mereka akan dibangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,66,'بَلِ ادّٰرَكَ عِلْمُهُمْ فِى الْاٰخِرَةِۗ بَلْ هُمْ فِيْ شَكٍّ مِّنْهَاۗ بَلْ هُمْ مِّنْهَا عَمُوْنَ ࣖ ','Sebenarnya pengetahuan mereka tentang akhirat tidak sampai (kesana) malahan mereka ragu-ragu tentang akhirat itu, lebih-lebih lagi mereka buta daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,67,'وَقَالَ الَّذِيْنَ كَفَرُوْٓا ءَاِذَا كُنَّا تُرَابًا وَّاٰبَاۤؤُنَآ اَىِٕنَّا لَمُخْرَجُوْنَ ','Berkatalah orang-orang yang kafir: "Apakah setelah kita menjadi tanah dan (begitu pula) bapak-bapak kita; apakah sesungguhnya kita akan dikeluarkan (dari kubur)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,68,'لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَاۤؤُنَا مِنْ قَبْلُۙ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ','Sesungguhnya kami telah diberi ancaman dengan ini dan (juga) bapak-bapak kami dahulu; ini tidak lain hanyalah dongengan-dongengan orang dahulu kala".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,69,'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ','Katakanlah: "Berjalanlah kamu (di muka) bumi, lalu perhatikanlah bagaimana akibat orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,70,'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ','Dan janganlah kamu berduka cita terhadap mereka, dan janganlah (dadamu) merasa sempit terhadap apa yang mereka tipudayakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,71,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Dan mereka (orang-orang kafir) berkata: "Bilakah datangnya azab itu, jika memang kamu orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,72,'قُلْ عَسٰٓى اَنْ يَّكُوْنَ رَدِفَ لَكُمْ بَعْضُ الَّذِيْ تَسْتَعْجِلُوْنَ ','Katakanlah: "Mungkin telah hampir datang kepadamu sebagian dari (azab) yang kamu minta (supaya) disegerakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,73,'وَاِنَّ رَبَّكَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ','Dan sesungguhnya Tuhanmu benar-benar mempunyai kurnia yang besar (yang diberikan-Nya) kepada manusia, tetapi kebanyakan mereka tidak mensyukuri(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,74,'وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ','Dan sesungguhnya Tuhanmu, benar-benar mengetahui apa yang disembunyikan hati mereka dan apa yang mereka nyatakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,75,'وَمَا مِنْ غَاۤىِٕبَةٍ فِى السَّمَاۤءِ وَالْاَرْضِ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ','Tiada sesuatupun yang ghaib di langit dan di bumi, melainkan (terdapat) dalam kitab yang nyata (Lauhul Mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,76,'اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَكْثَرَ الَّذِيْ هُمْ فِيْهِ يَخْتَلِفُوْنَ  ','Sesungguhnya Al Quran ini menjelaskan kepada Bani lsrail sebahagian besar dari (perkara-perkara) yang mereka berselisih tentangnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,77,'وَاِنَّهٗ لَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ','Dan sesungguhnya Al Quran itu benar-benar menjadi petunjuk dan rahmat bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,78,'اِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ بِحُكْمِهٖۚ وَهُوَ الْعَزِيْزُ الْعَلِيْمُۚ ','Sesungguhnya Tuhanmu akan menyelesaikan perkara antara mereka dengan keputusan-Nya, dan Dia Maha Perkasa lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,79,'فَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّكَ عَلَى الْحَقِّ الْمُبِيْنِ ','Sebab itu bertakwalah kepada Allah, sesungguhnya kamu berada di atas kebenaran yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,80,'اِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ ','Sesungguhnya kamu tidak dapat menjadikan orang-orang yang mati mendengar dan (tidak pula) menjadikan orang-orang yang tuli mendengar panggilan, apabila mereka telah berpaling membelakang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,81,'وَمَآ اَنْتَ بِهٰدِى الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ ','Dan kamu sekali-kali tidak dapat memimpin (memalingkan) orang-orang buta dari kesesatan mereka. Kamu tidak dapat menjadikan (seorangpun) mendengar, kecuali orang-orang yang beriman kepada ayat-ayat Kami, lalu mereka berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,82,'۞ وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَاۤبَّةً مِّنَ الْاَرْضِ تُكَلِّمُهُمْ اَنَّ النَّاسَ كَانُوْا بِاٰيٰتِنَا لَا يُوْقِنُوْنَ ࣖ','Dan apabila perkataan telah jatuh atas mereka, Kami keluarkan sejenis binatang melata dari bumi yang akan mengatakan kepada mereka, bahwa sesungguhnya manusia dahulu tidak yakin kepada ayat-ayat Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,83,'وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجًا مِّمَّنْ يُّكَذِّبُ بِاٰيٰتِنَا فَهُمْ يُوْزَعُوْنَ ','Dan (ingatlah) hari (ketika) Kami kumpulkan dari tiap-tiap umat segolongan orang-orang yang mendustakan ayat-ayat Kami, lalu mereka dibagi-bagi (dalam kelompok-kelompok).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,84,'حَتّٰٓى اِذَا جَاۤءُوْ قَالَ اَكَذَّبْتُمْ بِاٰيٰتِيْ وَلَمْ تُحِيْطُوْا بِهَا عِلْمًا اَمَّاذَا كُنْتُمْ تَعْمَلُوْنَ ','Hingga apabila mereka datang, Allah berfirman: "Apakah kamu telah mendustakan ayat-ayat-Ku, padahal ilmu kamu tidak meliputinya, atau apakah yang telah kamu kerjakan?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,85,'وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوْا فَهُمْ لَا يَنْطِقُوْنَ ','Dan jatuhlah perkataan (azab) atas mereka disebabkan kezaliman mereka, maka mereka tidak dapat berkata (apa-apa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,86,'اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ','Apakah mereka tidak memperhatikan, bahwa sesungguhnya Kami telah menjadikan malam supaya mereka beristirahat padanya dan siang yang menerangi? Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,87,'وَيَوْمَ يُنْفَخُ فِى الصُّوْرِ فَفَزِعَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗوَكُلٌّ اَتَوْهُ دَاخِرِيْنَ ','Dan (ingatlah) hari (ketika) ditiup sangkakala, maka terkejutlah segala yang di langit dan segala yang di bumi, kecuali siapa yang dikehendaki Allah. Dan semua mereka datang menghadap-Nya dengan merendahkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,88,'وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَّهِيَ تَمُرُّ مَرَّ السَّحَابِۗ صُنْعَ اللّٰهِ الَّذِيْٓ اَتْقَنَ كُلَّ شَيْءٍۗ اِنَّهٗ خَبِيْرٌ ۢبِمَا تَفْعَلُوْنَ','Dan kamu lihat gunung-gunung itu, kamu sangka dia tetap di tempatnya, padahal ia berjalan sebagai jalannya awan. (Begitulah) perbuatan Allah yang membuat dengan kokoh tiap-tiap sesuatu; sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,89,'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَهُمْ مِّنْ فَزَعٍ يَّوْمَىِٕذٍ اٰمِنُوْنَ ','Barangsiapa yang membawa kebaikan, maka ia memperoleh (balasan) yang lebih baik dari padanya, sedang mereka itu adalah orang-orang yang aman tenteram dari pada kejutan yang dahsyat pada hari itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,90,'وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوْهُهُمْ فِى النَّارِۗ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ ','Dan barang siapa yang membawa kejahatan, maka disungkurkanlah muka mereka ke dalam neraka. Tiadalah kamu dibalasi, melainkan (setimpal) dengan apa yang dahulu kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,91,'اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذِيْ حَرَّمَهَا وَلَهٗ كُلُّ شَيْءٍ وَّاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ ۙ','Aku hanya diperintahkan untuk menyembah Tuhan negeri ini (Mekah) Yang telah menjadikannya suci dan kepunyaan-Nya-lah segala sesuatu, dan aku diperintahkan supaya aku termasuk orang-orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,92,'وَاَنْ اَتْلُوَا الْقُرْاٰنَ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَقُلْ اِنَّمَآ اَنَا۠ مِنَ الْمُنْذِرِيْنَ ','Dan supaya aku membacakan Al Quran (kepada manusia). Maka barangsiapa yang mendapat petunjuk maka sesungguhnya ia hanyalah mendapat petunjuk untuk (kebaikan) dirinya, dan barangsiapa yang sesat maka katakanlah: "Sesungguhnya aku (ini) tidak lain hanyalah salah seorang pemberi peringatan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,27,93,'وَقُلِ الْحَمْدُ لِلّٰهِ سَيُرِيْكُمْ اٰيٰتِهٖ فَتَعْرِفُوْنَهَاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ࣖ','Dan katakanlah: "Segala puji bagi Allah, Dia akan memperlihatkan kepadamu tanda-tanda kebesaran-Nya, maka kamu akan mengetahuinya. Dan Tuhanmu tiada lalai dari apa yang kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,1,' طٰسۤمّۤ ','Thaa Siin Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,2,'تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ ','Ini adalah ayat-ayat Kitab (Al Quran) yang nyata (dari Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,3,'نَتْلُوْا عَلَيْكَ مِنْ نَّبَاِ مُوْسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُّؤْمِنُوْنَ ','Kami membacakan kepadamu sebagian dari kisah Musa dan Fir´aun dengan benar untuk orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,4,'اِنَّ فِرْعَوْنَ عَلَا فِى الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعًا يَّسْتَضْعِفُ طَاۤىِٕفَةً مِّنْهُمْ يُذَبِّحُ اَبْنَاۤءَهُمْ وَيَسْتَحْيٖ نِسَاۤءَهُمْ ۗاِنَّهٗ كَانَ مِنَ الْمُفْسِدِيْنَ ','Sesungguhnya Fir´aun telah berbuat sewenang-wenang di muka bumi dan menjadikan penduduknya berpecah belah, dengan menindas segolongan dari mereka, menyembelih anak laki-laki mereka dan membiarkan hidup anak-anak perempuan mereka. Sesungguhnya Fir´aun termasuk orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,5,'وَنُرِيْدُ اَنْ نَّمُنَّ عَلَى الَّذِيْنَ اسْتُضْعِفُوْا فِى الْاَرْضِ وَنَجْعَلَهُمْ اَىِٕمَّةً وَّنَجْعَلَهُمُ الْوٰرِثِيْنَ ۙ','Dan Kami hendak memberi karunia kepada orang-orang yang tertindas di bumi (Mesir) itu dan hendak menjadikan mereka pemimpin dan menjadikan mereka orang-orang yang mewarisi (bumi),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,6,'وَنُمَكِّنَ لَهُمْ فِى الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا مِنْهُمْ مَّا كَانُوْا يَحْذَرُوْنَ ','dan akan Kami teguhkan kedudukan mereka di muka bumi dan akan Kami perlihatkan kepada Fir´aun dan Haman beserta tentaranya apa yang selalu mereka khawatirkan dari mereka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,7,'وَاَوْحَيْنَآ اِلٰٓى اُمِّ مُوْسٰٓى اَنْ اَرْضِعِيْهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْقِيْهِ فِى الْيَمِّ وَلَا تَخَافِيْ وَلَا تَحْزَنِيْ ۚاِنَّا رَاۤدُّوْهُ اِلَيْكِ وَجَاعِلُوْهُ مِنَ الْمُرْسَلِيْنَ ','Dan kami ilhamkan kepada ibu Musa; "Susuilah dia, dan apabila kamu khawatir terhadapnya maka jatuhkanlah dia ke sungai (Nil). Dan janganlah kamu khawatir dan janganlah (pula) bersedih hati, karena sesungguhnya Kami akan mengembalikannya kepadamu, dan menjadikannya (salah seorang) dari para rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,8,'فَالْتَقَطَهٗٓ اٰلُ فِرْعَوْنَ لِيَكُوْنَ لَهُمْ عَدُوًّا وَّحَزَنًاۗ اِنَّ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا كَانُوْا خٰطِـِٕيْنَ ','Maka dipungutlah ia oleh keluarga Fir´aun yang akibatnya dia menjadi musuh dan kesedihan bagi mereka. Sesungguhnya Fir´aun dan Haman beserta tentaranya adalah orang-orang yang bersalah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,9,'وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّيْ وَلَكَۗ  لَا تَقْتُلُوْهُ ۖعَسٰٓى اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا وَّهُمْ لَا يَشْعُرُوْنَ ','Dan berkatalah isteri Fir´aun: "(Ia) adalah penyejuk mata hati bagiku dan bagimu. Janganlah kamu membunuhnya, mudah-mudahan ia bermanfaat kepada kita atau kita ambil ia menjadi anak", sedang mereka tiada menyadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,10,'وَاَصْبَحَ فُؤَادُ اُمِّ مُوْسٰى فٰرِغًاۗ اِنْ كَادَتْ لَتُبْدِيْ بِهٖ لَوْلَآ اَنْ رَّبَطْنَا عَلٰى قَلْبِهَا لِتَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ','Dan menjadi kosonglah hati ibu Musa. Sesungguhnya hampir saja ia menyatakan rahasia tentang Musa, seandainya tidak Kami teguhkan hatinya, supaya ia termasuk orang-orang yang percaya (kepada janji Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,11,'وَقَالَتْ لِاُخْتِهٖ قُصِّيْهِۗ فَبَصُرَتْ بِهٖ عَنْ جُنُبٍ وَّهُمْ لَا يَشْعُرُوْنَ ۙ','Dan berkatalah ibu Musa kepada saudara Musa yang perempuan: "Ikutilah dia" Maka kelihatanlah olehnya Musa dari jauh, sedang mereka tidak mengetahuinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,12,'۞ وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَّكْفُلُوْنَهٗ لَكُمْ وَهُمْ لَهٗ نَاصِحُوْنَ ','dan Kami cegah Musa dari menyusu kepada perempuan-perempuan yang mau menyusui(nya) sebelum itu; maka berkatalah saudara Musa: "Maukah kamu aku tunjukkan kepadamu ahlul bait yang akan memeliharanya untukmu dan mereka dapat berlaku baik kepadanya?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,13,'فَرَدَدْنٰهُ اِلٰٓى اُمِّهٖ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ࣖ','Maka kami kembalikan Musa kepada ibunya, supaya senang hatinya dan tidak berduka cita dan supaya ia mengetahui bahwa janji Allah itu adalah benar, tetapi kebanyakan manusia tidak mengetahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,14,'وَلَمَّا بَلَغَ اَشُدَّهٗ وَاسْتَوٰىٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًاۗ وَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ ','Dan setelah Musa cukup umur dan sempurna akalnya, Kami berikan kepadanya hikmah (kenabian) dan pengetahuan. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,15,'وَدَخَلَ الْمَدِيْنَةَ عَلٰى حِيْنِ غَفْلَةٍ مِّنْ اَهْلِهَا فَوَجَدَ فِيْهَا رَجُلَيْنِ يَقْتَتِلٰنِۖ هٰذَا مِنْ شِيْعَتِهٖ وَهٰذَا مِنْ عَدُوِّهٖۚ فَاسْتَغَاثَهُ الَّذِيْ مِنْ شِيْعَتِهٖ عَلَى الَّذِيْ مِنْ عَدُوِّهٖ ۙفَوَكَزَهٗ مُوْسٰى فَقَضٰى عَلَيْهِۖ قَالَ هٰذَا مِنْ عَمَلِ الشَّيْطٰنِۗ اِنَّهٗ عَدُوٌّ مُّضِلٌّ مُّبِيْنٌ ','Dan Musa masuk ke kota (Memphis) ketika penduduknya sedang lengah, maka didapatinya di dalam kota itu dua orang laki-laki yang berkelahi; yang seorang dari golongannya (Bani Israil) dan seorang (lagi) dari musuhnya (kaum Fir´aun). Maka orang yang dari golongannya meminta pertolongan kepadanya, untuk mengalahkan orang yang dari musuhnya lalu Musa meninjunya, dan matilah musuhnya itu. Musa berkata: "Ini adalah perbuatan syaitan sesungguhnya syaitan itu adalah musuh yang menyesatkan lagi nyata (permusuhannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,16,'قَالَ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ فَاغْفِرْ لِيْ فَغَفَرَ لَهٗ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ ','Musa mendoa: "Ya Tuhanku, sesungguhnya aku telah menganiaya diriku sendiri karena itu ampunilah aku". Maka Allah mengampuninya, sesungguhnya Allah Dialah Yang Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,17,'قَالَ رَبِّ بِمَآ اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُوْنَ ظَهِيْرًا لِّلْمُجْرِمِيْنَ ','Musa berkata: "Ya Tuhanku, demi nikmat yang telah Engkau anugerahkan kepadaku, aku sekali-kali tiada akan menjadi penolong bagi orang-orang yang berdosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,18,'فَاَصْبَحَ فِى الْمَدِيْنَةِ خَاۤىِٕفًا يَّتَرَقَّبُ فَاِذَا الَّذِى اسْتَنْصَرَهٗ بِالْاَمْسِ يَسْتَصْرِخُهٗ ۗقَالَ لَهٗ مُوْسٰٓى اِنَّكَ لَغَوِيٌّ مُّبِيْنٌ ','Karena itu, jadilah Musa di kota itu merasa takut menunggu-nunggu dengan khawatir (akibat perbuatannya), maka tiba-tiba orang yang meminta pertolongan kemarin berteriak meminta pertolongan kepadanya. Musa berkata kepadanya: "Sesungguhnya kamu benar-benar orang sesat yang nyata (kesesatannya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,19,'فَلَمَّآ اَنْ اَرَادَ اَنْ يَّبْطِشَ بِالَّذِيْ هُوَ عَدُوٌّ لَّهُمَاۙ قَالَ يٰمُوْسٰٓى اَتُرِيْدُ اَنْ تَقْتُلَنِيْ كَمَا قَتَلْتَ نَفْسًاۢ بِالْاَمْسِۖ اِنْ تُرِيْدُ اِلَّآ اَنْ تَكُوْنَ جَبَّارًا فِى الْاَرْضِ وَمَا تُرِيْدُ اَنْ تَكُوْنَ مِنَ الْمُصْلِحِيْنَ ','Maka tatkala Musa hendak memegang dengan keras orang yang menjadi musuh keduanya, musuhnya berkata: "Hai Musa, apakah kamu bermaksud hendak membunuhku, sebagaimana kamu kemarin telah membunuh seorang manusia? Kamu tidak bermaksud melainkan hendak menjadi orang yang berbuat sewenang-wenang di negeri (ini), dan tiadalah kamu hendak menjadi salah seorang dari orang-orang yang mengadakan perdamaian".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,20,'وَجَاۤءَ رَجُلٌ مِّنْ اَقْصَى الْمَدِيْنَةِ يَسْعٰىۖ قَالَ يٰمُوْسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُوْنَ بِكَ لِيَقْتُلُوْكَ فَاخْرُجْ اِنِّيْ لَكَ مِنَ النّٰصِحِيْنَ ','Dan datanglah seorang laki-laki dari ujung kota bergegas-gegas seraya berkata: "Hai Musa, sesungguhnya pembesar negeri sedang berunding tentang kamu untuk membunuhmu, sebab itu keluarlah (dari kota ini) sesungguhnya aku termasuk orang-orang yang memberi nasehat kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,21,'فَخَرَجَ مِنْهَا خَاۤىِٕفًا يَّتَرَقَّبُ ۖقَالَ رَبِّ نَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ࣖ ','Maka keluarlah Musa dari kota itu dengan rasa takut menunggu-nunggu dengan khawatir, dia berdoa: "Ya Tuhanku, selamatkanlah aku dari orang-orang yang zalim itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,22,'وَلَمَّا تَوَجَّهَ تِلْقَاۤءَ مَدْيَنَ قَالَ عَسٰى رَبِّيْٓ اَنْ يَّهْدِيَنِيْ سَوَاۤءَ السَّبِيْلِ ','Dan tatkala ia menghadap kejurusan negeri Mad-yan ia berdoa (lagi): "Mudah-mudahan Tuhanku memimpinku ke jalan yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,23,'وَلَمَّا وَرَدَ مَاۤءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِّنَ النَّاسِ يَسْقُوْنَ ەۖ وَوَجَدَ مِنْ دُوْنِهِمُ امْرَاَتَيْنِ تَذُوْدٰنِۚ قَالَ مَا خَطْبُكُمَا ۗقَالَتَا لَا نَسْقِيْ حَتّٰى يُصْدِرَ الرِّعَاۤءُ وَاَبُوْنَا شَيْخٌ كَبِيْرٌ','Dan tatkala ia sampai di sumber air negeri Mad-yan ia menjumpai di sana sekumpulan orang yang sedang meminumkan (ternaknya), dan ia menjumpai di belakang orang banyak itu, dua orang wanita yang sedang menghambat (ternaknya). Musa berkata: "Apakah maksudmu (dengan berbuat at begitu)?" Kedua wanita itu menjawab: "Kami tidak dapat meminumkan (ternak kami), sebelum pengembala-pengembala itu memulangkan (ternaknya), sedang bapak kami adalah orang tua yang telah lanjut umurnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,24,'فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰىٓ اِلَى الظِّلِّ فَقَالَ رَبِّ اِنِّيْ لِمَآ اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَقِيْرٌ ','Maka Musa memberi minum ternak itu untuk (menolong) keduanya, kemudian dia kembali ke tempat yang teduh lalu berdoa: "Ya Tuhanku sesungguhnya aku sangat memerlukan sesuatu kebaikan yang Engkau turunkan kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,25,'فَجَاۤءَتْهُ اِحْدٰىهُمَا تَمْشِيْ عَلَى اسْتِحْيَاۤءٍ  ۖقَالَتْ اِنَّ اَبِيْ يَدْعُوْكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۗ فَلَمَّا جَاۤءَهٗ وَقَصَّ عَلَيْهِ الْقَصَصَۙ  قَالَ لَا تَخَفْۗ نَجَوْتَ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ','Kemudian datanglah kepada Musa salah seorang dari kedua wanita itu berjalan kemalu-maluan, ia berkata: "Sesungguhnya bapakku memanggil kamu agar ia memberikan balasan terhadap (kebaikan)mu memberi minum (ternak) kami". Maka tatkala Musa mendatangi bapaknya (Syu´aib) dan menceritakan kepadanya cerita (mengenai dirinya), Syu´aib berkata: "Janganlah kamu takut. Kamu telah selamat dari orang-orang yang zalim itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,26,'قَالَتْ اِحْدٰىهُمَا يٰٓاَبَتِ اسْتَأْجِرْهُ ۖاِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَمِيْنُ ','Salah seorang dari kedua wanita itu berkata: "Ya bapakku ambillah ia sebagai orang yang bekerja (pada kita), karena sesungguhnya orang yang paling baik yang kamu ambil untuk bekerja (pada kita) ialah orang yang kuat lagi dapat dipercaya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,27,'قَالَ اِنِّيْٓ اُرِيْدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هٰتَيْنِ عَلٰٓى اَنْ تَأْجُرَنِيْ ثَمٰنِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَۚ وَمَآ اُرِيْدُ اَنْ اَشُقَّ عَلَيْكَۗ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰلِحِيْنَ ','Berkatalah dia (Syu´aib): "Sesungguhnya aku bermaksud menikahkan kamu dengan salah seorang dari kedua anakku ini, atas dasar bahwa kamu bekerja denganku delapan tahun dan jika kamu cukupkan sepuluh tahun maka itu adalah (suatu kebaikan) dari kamu, maka aku tidak hendak memberati kamu. Dan kamu Insya Allah akan mendapatiku termasuk orang-orang yang baik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,28,'قَالَ ذٰلِكَ بَيْنِيْ وَبَيْنَكَۗ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۗ وَاللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ ࣖ','Dia (Musa) berkata: "Itulah (perjanjian) antara aku dan kamu. Mana saja dari kedua waktu yang ditentukan itu aku sempurnakan, maka tidak ada tuntutan tambahan atas diriku (lagi). Dan Allah adalah saksi atas apa yang kita ucapkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,29,'۞ فَلَمَّا قَضٰى مُوْسَى الْاَجَلَ وَسَارَ بِاَهْلِهٖٓ اٰنَسَ مِنْ جَانِبِ الطُّوْرِ نَارًاۗ قَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْٓ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِّنَ النَّارِ لَعَلَّكُمْ تَصْطَلُوْنَ','Maka tatkala Musa telah menyelesaikan waktu yang ditentukan dan dia berangkat dengan keluarganya, dilihatnyalah api di lereng gunung ia berkata kepada keluarganya: "Tunggulah (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa suatu berita kepadamu dari (tempat) api itu atau (membawa) sesuluh api, agar kamu dapat menghangatkan badan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,30,'فَلَمَّآ اَتٰىهَا نُوْدِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِى الْبُقْعَةِ الْمُبٰرَكَةِ مِنَ الشَّجَرَةِ اَنْ يّٰمُوْسٰٓى اِنِّيْٓ اَنَا اللّٰهُ رَبُّ الْعٰلَمِيْنَ ۙ','Maka tatkala Musa sampai ke (tempat) api itu, diserulah dia dari (arah) pinggir lembah yang sebelah kanan(nya) pada tempat yang diberkahi, dari sebatang pohon kayu, yaitu: "Ya Musa, sesungguhnya aku adalah Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,31,'وَاَنْ اَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰٓى اَقْبِلْ وَلَا تَخَفْۗ اِنَّكَ مِنَ الْاٰمِنِيْنَ ','dan lemparkanlah tongkatmu. Maka tatkala (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seolah-olah dia seekor ular yang gesit, larilah ia berbalik ke belakang tanpa menoleh. (Kemudian Musa diseru): "Hai Musa datanglah kepada-Ku dan janganlah kamu takut. Sesungguhnya kamu termasuk orang-orang yang aman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,32,'اُسْلُكْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ ۖوَّاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ  فَذٰنِكَ بُرْهَانٰنِ مِنْ رَّبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ','Masukkanlah tanganmu ke leher bajumu, niscaya ia keluar putih tidak bercacat bukan karena penyakit, dan dekapkanlah kedua tanganmu (ke dada)mu bila ketakutan, maka yang demikian itu adalah dua mukjizat dari Tuhanmu (yang akan kamu hadapkan kepada Fir´aun dan pembesar-pembesarnya). Sesungguhnya mereka adalah orang-orang yang fasik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,33,'قَالَ رَبِّ اِنِّيْ قَتَلْتُ مِنْهُمْ نَفْسًا فَاَخَافُ اَنْ يَّقْتُلُوْنِ ','Musa berkata: "Ya Tuhanku sesungguhnya aku, telah membunuh seorang manusia dari golongan mereka, maka aku takut mereka akan membunuhku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,34,'وَاَخِيْ هٰرُوْنُ هُوَ اَفْصَحُ مِنِّيْ لِسَانًا فَاَرْسِلْهُ مَعِيَ رِدْءًا يُّصَدِّقُنِيْٓ  ۖاِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ','Dan saudaraku Harun dia lebih fasih lidahnya daripadaku, maka utuslah dia bersamaku sebagai pembantuku untuk membenarkan (perkataan)ku; sesungguhnya aku khawatir mereka akan mendustakanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,35,'قَالَ سَنَشُدُّ عَضُدَكَ بِاَخِيْكَ وَنَجْعَلُ لَكُمَا سُلْطٰنًا فَلَا يَصِلُوْنَ اِلَيْكُمَا  ۛبِاٰيٰتِنَا  ۛ اَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغٰلِبُوْنَ ','Allah berfirman: "Kami akan membantumu dengan saudaramu, dan Kami berikan kepadamu berdua kekuasaan yang besar, maka mereka tidak dapat mencapaimu; (berangkatlah kamu berdua) dengan membawa mukjizat Kami, kamu berdua dan orang yang mengikuti kamulah yang akan menang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,36,'فَلَمَّا جَاۤءَهُمْ مُّوْسٰى بِاٰيٰتِنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا سِحْرٌ مُّفْتَرًىۙ وَّمَا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ','Maka tatkala Musa datang kepada mereka dengan (membawa) mukjizat-mukjizat Kami yang nyata, mereka berkata: "Ini tidak lain hanyalah sihir yang dibuat-buat dan kami belum pernah mendengar (seruan yang seperti) ini pada nenek moyang kami dahulu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,37,'وَقَالَ مُوْسٰى رَبِّيْٓ اَعْلَمُ بِمَنْ جَاۤءَ بِالْهُدٰى مِنْ عِنْدِهٖ وَمَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ','Musa menjawab: "Tuhanku lebih mengetahui orang yang (patut) membawa petunjuk dari sisi-Nya dan siapa yang akan mendapat kesudahan (yang baik) di negeri akhirat. Sesungguhnya tidaklah akan mendapat kemenangan orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,38,'وَقَالَ فِرْعَوْنُ يٰٓاَيُّهَا الْمَلَاُ مَا عَلِمْتُ لَكُمْ مِّنْ اِلٰهٍ غَيْرِيْۚ فَاَوْقِدْ لِيْ يٰهَامٰنُ عَلَى الطِّيْنِ فَاجْعَلْ لِّيْ صَرْحًا لَّعَلِّيْٓ اَطَّلِعُ اِلٰٓى اِلٰهِ مُوْسٰىۙ وَاِنِّيْ لَاَظُنُّهٗ مِنَ الْكٰذِبِيْنَ ','Dan berkata Fir´aun: "Hai pembesar kaumku, aku tidak mengetahui tuhan bagimu selain aku. Maka bakarlah hai Haman untukku tanah liat kemudian buatkanlah untukku bangunan yang tinggi supaya aku dapat naik melihat Tuhan Musa, dan sesungguhnya aku benar-benar yakin bahwa dia termasuk orang-orang pendusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,39,'وَاسْتَكْبَرَ هُوَ وَجُنُوْدُهٗ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوْٓا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُوْنَ ','dan berlaku angkuhlah Fir´aun dan bala tentaranya di bumi (Mesir) tanpa alasan yang benar dan mereka menyangka bahwa mereka tidak akan dikembalikan kepada Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,40,'فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ ۚفَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ','Maka Kami hukumlah Fir´aun dan bala tentaranya, lalu Kami lemparkan mereka ke dalam laut. Maka lihatlah bagaimana akibat orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,41,'وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّدْعُوْنَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُوْنَ ','Dan Kami jadikan mereka pemimpin-pemimpin yang menyeru (manusia) ke neraka dan pada hari kiamat mereka tidak akan ditolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,42,'وَاَتْبَعْنٰهُمْ فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً ۚوَيَوْمَ الْقِيٰمَةِ هُمْ مِّنَ الْمَقْبُوْحِيْنَ ࣖ','Dan Kami ikutkanlah laknat kepada mereka di dunia ini; dan pada hari kiamat mereka termasuk orang-orang yang dijauhkan (dari rahmat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,43,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ مِنْۢ بَعْدِ مَآ اَهْلَكْنَا الْقُرُوْنَ الْاُوْلٰى بَصَاۤىِٕرَ لِلنَّاسِ وَهُدًى وَّرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُوْنَ ','Dan sesungguhnya telah Kami berikan kepada Musa Al-Kitab (Taurat) sesudah Kami binasakan generasi-generasi yang terdahulu, untuk menjadi pelita bagi manusia dan petunjuk dan rahmat, agar mereka ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,44,'وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَآ اِلٰى مُوْسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشّٰهِدِيْنَ ۙ','Dan tidaklah kamu (Muhammad) berada di sisi yang sebelah barat ketika Kami menyampaikan perintah kepada Musa, dan tiada pula kamu termasuk orang-orang yang menyaksikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,45,'وَلٰكِنَّآ اَنْشَأْنَا قُرُوْنًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِيًا فِيْٓ اَهْلِ مَدْيَنَ تَتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِلِيْنَ ','Tetapi Kami telah mengadakan beberapa generasi, dan berlalulah atas mereka masa yang panjang, dan tiadalah kamu tinggal bersama-sama penduduk Mad-yan dengan membacakan ayat-ayat Kami kepada mereka, tetapi Kami telah mengutus rasul-rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,46,'وَمَا كُنْتَ بِجَانِبِ الطُّوْرِ اِذْ نَادَيْنَا وَلٰكِنْ رَّحْمَةً مِّنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ','Dan tiadalah kamu berada di dekat gunung Thur ketika Kami menyeru (Musa), tetapi (Kami beritahukan itu kepadamu) sebagai rahmat dari Tuhanmu, supaya kamu memberi peringatan kepada kaum (Quraisy) yang sekali-kali belum datang kepada mereka pemberi peringatan sebelum kamu agar mereka ingat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,47,'وَلَوْلَآ اَنْ تُصِيْبَهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَيَقُوْلُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ','Dan agar mereka tidak mengatakan ketika azab menimpa mereka disebabkan apa yang mereka kerjakan: "Ya Tuhan kami, mengapa Engkau tidak mengutus seorang rasul kepada kami, lalu kami mengikuti ayat-ayat Engkau dan jadilah kami termasuk orang-orang mukmin".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,48,'فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْا لَوْلَآ اُوْتِيَ مِثْلَ مَآ اُوْتِيَ مُوْسٰىۗ اَوَلَمْ يَكْفُرُوْا بِمَآ اُوْتِيَ مُوْسٰى مِنْ قَبْلُۚ قَالُوْا سِحْرٰنِ تَظَاهَرَاۗ وَقَالُوْٓا اِنَّا بِكُلٍّ كٰفِرُوْنَ ','Maka tatkala datang kepada mereka kebenaran dari sisi Kami, mereka berkata: "Mengapakah tidak diberikan kepadanya (Muhammad) seperti yang telah diberikan kepada Musa dahulu?". Dan bukankah mereka itu telah ingkar (juga) kepada apa yang telah diberikan kepada Musa dahulu?; mereka dahulu telah berkata: "Musa dan Harun adalah dua ahli sihir yang bantu membantu". Dan mereka (juga) berkata: "Sesungguhnya kami tidak mempercayai masing-masing mereka itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,49,'قُلْ فَأْتُوْا بِكِتٰبٍ مِّنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَآ اَتَّبِعْهُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Katakanlah: "Datangkanlah olehmu sebuah kitab dari sisi Allah yang kitab itu lebih (dapat) memberi petunjuk daripada keduanya (Taurat dan Al Quran) niscaya aku mengikutinya, jika kamu sungguh orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,50,'فَاِنْ لَّمْ يَسْتَجِيْبُوْا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُوْنَ اَهْوَاۤءَهُمْۗ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰىهُ بِغَيْرِ هُدًى مِّنَ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ࣖ','Maka jika mereka tidak menjawab (tantanganmu) ketahuilah bahwa sesungguhnya mereka hanyalah mengikuti hawa nafsu mereka (belaka). Dan siapakah yang lebih sesat daripada orang yang mengikuti hawa nafsunya dengan tidak mendapat petunjuk dari Allah sedikitpun. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,51,'۞ وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ۗ ','Dan sesungguhnya telah Kami turunkan berturut-turut perkataan ini (Al Quran) kepada mereka agar mereka mendapat pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,52,'اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ مِنْ قَبْلِهٖ هُمْ بِهٖ يُؤْمِنُوْنَ ','Orang-orang yang telah Kami datangkan kepada mereka Al Kitab sebelum Al Quran, mereka beriman (pula) dengan Al Quran itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,53,'وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُوْٓا اٰمَنَّا بِهٖٓ اِنَّهُ الْحَقُّ مِنْ رَّبِّنَآ اِنَّا كُنَّا مِنْ قَبْلِهٖ مُسْلِمِيْنَ ','Dan apabila dibacakan (Al Quran itu) kepada mereka, mereka berkata: "Kami beriman kepadanya; sesungguhnya; Al Quran itu adalah suatu kebenaran dari Tuhan kami, sesungguhnya kami sebelumnya adalah orang-orang yang membenarkan(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,54,'اُولٰۤىِٕكَ يُؤْتَوْنَ اَجْرَهُمْ مَّرَّتَيْنِ بِمَا صَبَرُوْا وَيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ','Mereka itu diberi pahala dua kali disebabkan kesabaran mereka, dan mereka menolak kejahatan dengan kebaikan, dan sebagian dari apa yang telah Kami rezekikan kepada mereka, mereka nafkahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,55,'وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوْا عَنْهُ وَقَالُوْا لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۖسَلٰمٌ عَلَيْكُمْ ۖ لَا نَبْتَغِى الْجٰهِلِيْنَ','Dan apabila mereka mendengar perkataan yang tidak bermanfaat, mereka berpaling daripadanya dan mereka berkata: "Bagi kami amal-amal kami dan bagimu amal-amalmu, kesejahteraan atas dirimu, kami tidak ingin bergaul dengan orang-orang jahil".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,56,'اِنَّكَ لَا تَهْدِيْ مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۚوَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ','Sesungguhnya kamu tidak akan dapat memberi petunjuk kepada orang yang kamu kasihi, tetapi Allah memberi petunjuk kepada orang yang dikehendaki-Nya, dan Allah lebih mengetahui orang-orang yang mau menerima petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,57,'وَقَالُوْٓا اِنْ نَّتَّبِعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۗ اَوَلَمْ نُمَكِّنْ لَّهُمْ حَرَمًا اٰمِنًا يُّجْبٰٓى اِلَيْهِ ثَمَرٰتُ كُلِّ شَيْءٍ رِّزْقًا مِّنْ لَّدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ','Dan mereka berkata: "Jika kami mengikuti petunjuk bersama kamu, niscaya kami akan diusir dari negeri kami". Dan apakah Kami tidak meneguhkan kedudukan mereka dalam daerah haram (tanah suci) yang aman, yang didatangkan ke tempat itu buah-buahan dari segala macam (tumbuh-tumbuhan) untuk menjadi rezeki (bagimu) dari sisi Kami?. Tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,58,'وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ ۢ بَطِرَتْ مَعِيْشَتَهَا ۚفَتِلْكَ مَسٰكِنُهُمْ لَمْ تُسْكَنْ مِّنْۢ بَعْدِهِمْ اِلَّا قَلِيْلًاۗ وَكُنَّا نَحْنُ الْوَارِثِيْنَ ','Dan berapa banyaknya (penduduk) negeri yang telah Kami binasakan, yang sudah bersenang-senang dalam kehidupannya; maka itulah tempat kediaman mereka yang tiada di diami (lagi) sesudah mereka, kecuali sebahagian kecil. Dan Kami adalah Pewaris(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,59,'وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ فِيْٓ اُمِّهَا رَسُوْلًا يَّتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۚ وَمَا كُنَّا مُهْلِكِى الْقُرٰىٓ اِلَّا وَاَهْلُهَا ظٰلِمُوْنَ ','Dan tidak adalah Tuhanmu membinasakan kota-kota, sebelum Dia mengutus di ibukota itu seorang rasul yang membacakan ayat-ayat Kami kepada mereka; dan tidak pernah (pula) Kami membinasakan kota-kota; kecuali penduduknya dalam keadaan melakukan kezaliman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,60,'وَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَزِيْنَتُهَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰىۗ اَفَلَا تَعْقِلُوْنَ ࣖ','Dan apa saja yang diberikan kepada kamu, maka itu adalah kenikmatan hidup duniawi dan perhiasannya; sedang apa yang di sisi Allah adalah lebih baik dan lebih kekal. Maka apakah kamu tidak memahaminya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,61,'اَفَمَنْ وَّعَدْنٰهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيْهِ كَمَنْ مَّتَّعْنٰهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَرِيْنَ ','Maka apakah orang yang Kami janjikan kepadanya suatu janji yang baik (surga) lalu ia memperolehnya, sama dengan orang yang Kami berikan kepadanya kenikmatan hidup duniawi; kemudian dia pada hari kiamat termasuk orang-orang yang diseret (ke dalam neraka)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,62,'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ','Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: "Di manakah sekutu-sekutu-Ku yang dahulu kamu katakan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,63,'قَالَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُلَاۤءِ الَّذِيْنَ اَغْوَيْنَاۚ  اَغْوَيْنٰهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَآ اِلَيْكَ مَا كَانُوْٓا اِيَّانَا يَعْبُدُوْنَ ','Berkatalah orang-orang yang telah tetap hukuman atas mereka; "Ya Tuhan kami, mereka inilah orang-orang yang kami sesatkan itu; kami telah menyesatkan mereka sebagaimana kami (sendiri) sesat, kami menyatakan berlepas diri (dari mereka) kepada Engkau, mereka sekali-kali tidak menyembah kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,64,'وَقِيْلَ ادْعُوْا شُرَكَاۤءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ ۗوَرَاَوُا الْعَذَابَۚ  لَوْ اَنَّهُمْ كَانُوْا يَهْتَدُوْنَ','Dikatakan (kepada mereka) "Serulah olehmu sekutu-sekutu kamu", lalu mereka menyerunya, maka sekutu-sekutu itu tidak memperkenankan (seruan) mereka, dan mereka melihat azab. (Mereka ketika itu berkeinginan) kiranya mereka dahulu menerima petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,65,'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ مَاذَآ اَجَبْتُمُ الْمُرْسَلِيْنَ ','Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: "Apakah jawabanmu kepada para rasul?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,66,'فَعَمِيَتْ عَلَيْهِمُ الْاَنْۢبَاۤءُ يَوْمَىِٕذٍ فَهُمْ لَا يَتَسَاۤءَلُوْنَ ','Maka gelaplah bagi mereka segala macam alasan pada hari itu, karena itu mereka tidak saling tanya menanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,67,'فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَعَسٰٓى اَنْ يَّكُوْنَ مِنَ الْمُفْلِحِيْنَ ','Adapun orang yang bertaubat dan beriman, serta mengerjakan amal yang saleh, semoga dia termasuk orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,68,'وَرَبُّكَ يَخْلُقُ مَا يَشَاۤءُ وَيَخْتَارُ ۗمَا كَانَ لَهُمُ الْخِيَرَةُ ۗسُبْحٰنَ اللّٰهِ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ','Dan Tuhanmu menciptakan apa yang Dia kehendaki dan memilihnya. Sekali-kali tidak ada pilihan bagi mereka. Maha Suci Allah dan Maha Tinggi dari apa yang mereka persekutukan (dengan Dia).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,69,'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ','Dan Tuhanmu mengetahui apa yang disembunyikan (dalam) dada mereka dan apa yang mereka nyatakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,70,'وَهُوَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ لَهُ الْحَمْدُ فِى الْاُوْلٰى وَالْاٰخِرَةِ ۖوَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ','Dan Dialah Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia, bagi-Nya-lah segala puji di dunia dan di akhirat, dan bagi-Nya-lah segala penentuan dan hanya kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,71,'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِضِيَاۤءٍ ۗ اَفَلَا تَسْمَعُوْنَ ','Katakanlah: "Terangkanlah kepadaku, jika Allah menjadikan untukmu malam itu terus menerus sampai hari kiamat, siapakah Tuhan selain Allah yang akan mendatangkan sinar terang kepadamu? Maka apakah kamu tidak mendengar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,72,'قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِلَيْلٍ تَسْكُنُوْنَ فِيْهِ ۗ اَفَلَا تُبْصِرُوْنَ ','Katakanlah: "Terangkanlah kepadaku, jika Allah menjadikan untukmu siang itu terus menerus sampai hari kiamat, siapakah Tuhan selain Allah yang akan mendatangkan malam kepadamu yang kamu beristirahat padanya? Maka apakah kamu tidak memperhatikan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,73,'وَمِنْ رَّحْمَتِهٖ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوْا فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ','Dan karena rahmat-Nya, Dia jadikan untukmu malam dan siang, supaya kamu beristirahat pada malam itu dan supaya kamu mencari sebahagian dari karunia-Nya (pada siang hari) dan agar kamu bersyukur kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,74,'وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ','Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: "Di manakah sekutu-sekutu-Ku yang dahulu kamu katakan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,75,'وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَهِيْدًا فَقُلْنَا هَاتُوْا بُرْهَانَكُمْ فَعَلِمُوْٓا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ࣖ','Dan Kami datangkan dari tiap-tiap umat seorang saksi, lalu Kami berkata "Tunjukkanlah bukti kebenaranmu", maka tahulah mereka bahwasanya yang hak itu kepunyaan Allah dan lenyaplah dari mereka apa yang dahulunya mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,76,'۞ اِنَّ قَارُوْنَ كَانَ مِنْ قَوْمِ مُوْسٰى فَبَغٰى عَلَيْهِمْ ۖوَاٰتَيْنٰهُ مِنَ الْكُنُوْزِ مَآ اِنَّ مَفَاتِحَهٗ لَتَنُوْۤاُ بِالْعُصْبَةِ اُولِى الْقُوَّةِ اِذْ قَالَ لَهٗ قَوْمُهٗ لَا تَفْرَحْ  اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِحِيْنَ ','Sesungguhnya Karun adalah termasuk kaum Musa, maka ia berlaku aniaya terhadap mereka, dan Kami telah menganugerahkan kepadanya perbendaharaan harta yang kunci-kuncinya sungguh berat dipikul oleh sejumlah orang yang kuat-kuat. (Ingatlah) ketika kaumnya berkata kepadanya: "Janganlah kamu terlalu bangga; sesungguhnya Allah tidak menyukai orang-orang yang terlalu membanggakan diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,77,'وَابْتَغِ فِيْمَآ اٰتٰىكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ  وَلَا تَنْسَ نَصِيْبَكَ مِنَ الدُّنْيَا  وَاَحْسِنْ كَمَآ اَحْسَنَ اللّٰهُ اِلَيْكَ  وَلَا تَبْغِ الْفَسَادَ فِى الْاَرْضِ ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِدِيْنَ','Dan carilah pada apa yang telah dianugerahkan Allah kepadamu (kebahagiaan) negeri akhirat, dan janganlah kamu melupakan bahagianmu dari (kenikmatan) duniawi dan berbuat baiklah (kepada orang lain) sebagaimana Allah telah berbuat baik, kepadamu, dan janganlah kamu berbuat kerusakan di (muka) bumi. Sesungguhnya Allah tidak menyukai orang-orang yang berbuat kerusakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,78,'قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ عِنْدِيْۗ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِهٖ مِنَ الْقُرُوْنِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَّاَكْثَرُ جَمْعًا ۗوَلَا يُسْـَٔلُ عَنْ ذُنُوْبِهِمُ الْمُجْرِمُوْنَ ','Karun berkata: "Sesungguhnya aku hanya diberi harta itu, karena ilmu yang ada padaku". Dan apakah ia tidak mengetahui, bahwasanya Allah sungguh telah membinasakan umat-umat sebelumnya yang lebih kuat daripadanya, dan lebih banyak mengumpulkan harta? Dan tidaklah perlu ditanya kepada orang-orang yang berdosa itu, tentang dosa-dosa mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,79,'فَخَرَجَ عَلٰى قَوْمِهٖ فِيْ زِيْنَتِهٖ ۗقَالَ الَّذِيْنَ يُرِيْدُوْنَ الْحَيٰوةَ الدُّنْيَا يٰلَيْتَ لَنَا مِثْلَ مَآ اُوْتِيَ قَارُوْنُۙ اِنَّهٗ لَذُوْ حَظٍّ عَظِيْمٍ ','Maka keluarlah Karun kepada kaumnya dalam kemegahannya. Berkatalah orang-orang yang menghendaki kehidupan dunia: "Moga-moga kiranya kita mempunyai seperti apa yang telah diberikan kepada Karun; sesungguhnya ia benar-benar mempunyai keberuntungan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,80,'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِّمَنْ اٰمَنَ وَعَمِلَ صَالِحًا ۚوَلَا يُلَقّٰىهَآ اِلَّا الصّٰبِرُوْنَ ','Berkatalah orang-orang yang dianugerahi ilmu: "Kecelakaan yang besarlah bagimu, pahala Allah adalah lebih baik bagi orang-orang yang beriman dan beramal saleh, dan tidak diperoleh pahala itu, kecuali oleh orang-orang yang sabar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,81,'فَخَسَفْنَا بِهٖ وَبِدَارِهِ الْاَرْضَ ۗفَمَا كَانَ لَهٗ مِنْ فِئَةٍ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ ۖوَمَا كَانَ مِنَ الْمُنْتَصِرِيْنَ ','Maka Kami benamkanlah Karun beserta rumahnya ke dalam bumi. Maka tidak ada baginya suatu golonganpun yang menolongnya terhadap azab Allah. Dan tiadalah ia termasuk orang-orang (yang dapat) membela (dirinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,82,'وَاَصْبَحَ الَّذِيْنَ تَمَنَّوْا مَكَانَهٗ بِالْاَمْسِ يَقُوْلُوْنَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُۚ  لَوْلَآ اَنْ مَّنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَا ۗوَيْكَاَنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ ࣖ ','Dan jadilah orang-orang yang kemarin mencita-citakan kedudukan Karun itu, berkata: "Aduhai, benarlah Allah melapangkan rezeki bagi siapa yang Dia kehendaki dari hamba-hambanya dan menyempitkannya; kalau Allah tidak melimpahkan karunia-Nya atas kita benar-benar Dia telah membenamkan kita (pula). Aduhai benarlah, tidak beruntung orang-orang yang mengingkari (nikmat Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,83,'تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذِيْنَ لَا يُرِيْدُوْنَ عُلُوًّا فِى الْاَرْضِ وَلَا فَسَادًا ۗوَالْعَاقِبَةُ لِلْمُتَّقِيْنَ ','Negeri akhirat itu, Kami jadikan untuk orang-orang yang tidak ingin menyombongkan diri dan berbuat kerusakan di (muka) bumi. Dan kesudahan (yang baik) itu adalah bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,84,'مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِيْنَ عَمِلُوا السَّيِّاٰتِ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ','Barangsiapa yang datang dengan (membawa) kebaikan, maka baginya (pahala) yang lebih baik daripada kebaikannya itu; dan barangsiapa yang datang dengan (membawa) kejahatan, maka tidaklah diberi pembalasan kepada orang-orang yang telah mengerjakan kejahatan itu, melainkan (seimbang) dengan apa yang dahulu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,85,'اِنَّ الَّذِيْ فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَرَاۤدُّكَ اِلٰى مَعَادٍ ۗقُلْ رَّبِّيْٓ اَعْلَمُ مَنْ جَاۤءَ بِالْهُدٰى وَمَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Sesungguhnya yang mewajibkan atasmu (melaksanakan hukum-hukum) Al Quran, benar-benar akan mengembalikan kamu ke tempat kembali. Katakanlah: "Tuhanku mengetahui orang yang membawa petunjuk dan orang yang dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,86,'وَمَا كُنْتَ تَرْجُوْٓا اَنْ يُّلْقٰٓى اِلَيْكَ الْكِتٰبُ اِلَّا رَحْمَةً مِّنْ رَّبِّكَ  فَلَا تَكُوْنَنَّ ظَهِيْرًا لِّلْكٰفِرِيْنَ ۖ ','Dan kamu tidak pernah mengharap agar Al Quran diturunkan kepadamu, tetapi ia (diturunkan) karena suatu rahmat yang besar dari Tuhanmu , sebab itu janganlah sekali-kali kamu menjadi penolong bagi orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,87,'وَلَا يَصُدُّنَّكَ عَنْ اٰيٰتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ۚ ','Dan janganlah sekali-kali mereka dapat menghalangimu dari (menyampaikan) ayat-ayat Allah, sesudah ayat-ayat itu diturunkan kepadamu, dan serulah mereka kepada (jalan) Tuhanmu, dan janganlah sekali-sekali kamu termasuk orang-orang yang mempersekutukan Tuhan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,28,88,'وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۘ  لَآ اِلٰهَ اِلَّا هُوَۗ كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهٗ ۗ لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ࣖ ','Janganlah kamu sembah di samping (menyembah) Allah, tuhan apapun yang lain. Tidak ada Tuhan (yang berhak disembah) melainkan Dia. Tiap-tiap sesuatu pasti binasa, kecuali Allah. Bagi-Nya-lah segala penentuan, dan hanya kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,1,'الۤمّۤ ۗ ','Alif laam miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,2,'اَحَسِبَ النَّاسُ اَنْ يُّتْرَكُوْٓا اَنْ يَّقُوْلُوْٓا اٰمَنَّا وَهُمْ لَا يُفْتَنُوْنَ ','Apakah manusia itu mengira bahwa mereka dibiarkan (saja) mengatakan: "Kami telah beriman", sedang mereka tidak diuji lagi?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,3,'وَلَقَدْ فَتَنَّا الَّذِيْنَ مِنْ قَبْلِهِمْ  فَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ صَدَقُوْا وَلَيَعْلَمَنَّ الْكٰذِبِيْنَ ','Dan sesungguhnya kami telah menguji orang-orang yang sebelum mereka, maka sesungguhnya Allah mengetahui orang-orang yang benar dan sesungguhnya Dia mengetahui orang-orang yang dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,4,'اَمْ حَسِبَ الَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِ اَنْ يَّسْبِقُوْنَا ۗسَاۤءَ مَا يَحْكُمُوْنَ ','Ataukah orang-orang yang mengerjakan kejahatan itu mengira bahwa mereka akan luput (dari azab) Kami? Amatlah buruk apa yang mereka tetapkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,5,'مَنْ كَانَ يَرْجُوْا لِقَاۤءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ','Barangsiapa yang mengharap pertemuan dengan Allah, maka sesungguhnya waktu (yang dijanjikan) Allah itu, pasti datang. Dan Dialah Yang Maha mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,6,'وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِهٖ ۗاِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعٰلَمِيْنَ ','Dan barangsiapa yang berjihad, maka sesungguhnya jihadnya itu adalah untuk dirinya sendiri. Sesungguhnya Allah benar-benar Maha Kaya (tidak memerlukan sesuatu) dari semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,7,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ ','Dan orang-orang yang beriman dan beramal saleh, benar-benar akan Kami hapuskan dari mereka dosa-dosa mereka dan benar-benar akan Kami beri mereka balasan yang lebih baik dari apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,8,'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْنًا ۗوَاِنْ جَاهَدٰكَ لِتُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا ۗاِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ','Dan Kami wajibkan manusia (berbuat) kebaikan kepada dua orang ibu-bapaknya. Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang tidak ada pengetahuanmu tentang itu, maka janganlah kamu mengikuti keduanya. Hanya kepada-Ku-lah kembalimu, lalu Aku kabarkan kepadamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,9,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُدْخِلَنَّهُمْ فِى الصّٰلِحِيْنَ ','Dan orang-orang yang beriman dan mengerjakan amal saleh benar-benar akan Kami masukkan mereka ke dalam (golongan) orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,10,'وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ فَاِذَآ اُوْذِيَ فِى اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِ ۗوَلَىِٕنْ جَاۤءَ نَصْرٌ مِّنْ رَّبِّكَ لَيَقُوْلُنَّ اِنَّا كُنَّا مَعَكُمْۗ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا فِيْ صُدُوْرِ الْعٰلَمِيْنَ ','Dan di antara manusia ada orang yang berkata: "Kami beriman kepada Allah", maka apabila ia disakiti (karena ia beriman) kepada Allah, ia menganggap fitnah manusia itu sebagai azab Allah. Dan sungguh jika datang pertolongan dari Tuhanmu, mereka pasti akan berkata: "Sesungguhnya kami adalah besertamu". Bukankah Allah lebih mengetahui apa yang ada dalam dada semua manusia?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,11,'وَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَلَيَعْلَمَنَّ الْمُنٰفِقِيْنَ ','Dan sesungguhnya Allah benar-benar mengetahui orang-orang yang beriman: dan sesungguhnya Dia mengetahui orang-orang yang munafik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,12,'وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوا اتَّبِعُوْا سَبِيْلَنَا وَلْنَحْمِلْ خَطٰيٰكُمْۗ وَمَا هُمْ بِحَامِلِيْنَ مِنْ خَطٰيٰهُمْ مِّنْ شَيْءٍۗ اِنَّهُمْ لَكٰذِبُوْنَ ','Dan berkatalah orang-orang kafir kepada orang-orang yang beriman: "Ikutilah jalan kami, dan nanti kami akan memikul dosa-dosamu", dan mereka (sendiri) sedikitpun tidak (sanggup), memikul dosa-dosa mereka. Sesungguhnya mereka adalah benar-benar orang pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,13,'وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالًا مَّعَ اَثْقَالِهِمْ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوْا يَفْتَرُوْنَ ࣖ ','Dan sesungguhnya mereka akan memikul beban (dosa) mereka, dan beban-beban (dosa yang lain) di samping beban-beban mereka sendiri, dan sesungguhnya mereka akan ditanya pada hari kiamat tentang apa yang selalu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,14,'وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَلَبِثَ فِيْهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْسِيْنَ عَامًا ۗفَاَخَذَهُمُ الطُّوْفَانُ وَهُمْ ظٰلِمُوْنَ ','Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, maka ia tinggal di antara mereka seribu tahun kurang lima puluh tahun. Maka mereka ditimpa banjir besar, dan mereka adalah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,15,'فَاَنْجَيْنٰهُ وَاَصْحٰبَ السَّفِيْنَةِ وَجَعَلْنٰهَآ اٰيَةً لِّلْعٰلَمِيْنَ ','Maka Kami selamatkan Nuh dan penumpang-penumpang bahtera itu dan Kami jadikan peristiwa itu pelajaran bagi semua umat manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,16,'وَاِبْرٰهِيْمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Dan (ingatlah) Ibrahim, ketika ia berkata kepada kaumnya: "Sembahlah olehmu Allah dan bertakwalah kepada-Nya. Yang demikian itu adalah lebih baik bagimu, jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,17,'اِنَّمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ اَوْثَانًا وَّتَخْلُقُوْنَ اِفْكًا ۗاِنَّ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَمْلِكُوْنَ لَكُمْ رِزْقًا فَابْتَغُوْا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوْهُ وَاشْكُرُوْا لَهٗ ۗاِلَيْهِ تُرْجَعُوْنَ ','Sesungguhnya apa yang kamu sembah selain Allah itu adalah berhala, dan kamu membuat dusta. Sesungguhnya yang kamu sembah selain Allah itu tidak mampu memberikan rezeki kepadamu; maka mintalah rezeki itu di sisi Allah, dan sembahlah Dia dan bersyukurlah kepada-Nya. Hanya kepada-Nya-lah kamu akan dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,18,'وَاِنْ تُكَذِّبُوْا فَقَدْ كَذَّبَ اُمَمٌ مِّنْ قَبْلِكُمْ ۗوَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ','Dan jika kamu (orang kafir) mendustakan, maka umat yang sebelum kamu juga telah mendustakan. Dan kewajiban rasul itu, tidak lain hanyalah menyampaikan (agama Allah) dengan seterang-terangnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,19,'اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُعِيْدُهٗ ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ','Dan apakah mereka tidak memperhatikan bagaimana Allah menciptakan (manusia) dari permulaannya, kemudian mengulanginya (kembali). Sesungguhnya yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,20,'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَ ۗاِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۚ','Katakanlah: "Berjalanlah di (muka) bumi, maka perhatikanlah bagaimana Allah menciptakan (manusia) dari permulaannya, kemudian Allah menjadikannya sekali lagi. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,21,'يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَرْحَمُ مَنْ يَّشَاۤءُ ۚوَاِلَيْهِ تُقْلَبُوْنَ ','Allah mengazab siapa yang dikehendaki-Nya, dan memberi rahmat kepada siapa yang dikehendaki-Nya, dan hanya kepada-Nya-lah kamu akan dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,22,'وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ ۖوَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ࣖ','Dan kamu sekali-kali tidak dapat melepaskan diri (dari azab Allah) di bumi dan tidak (pula) di langit dan sekali-kali tiadalah bagimu pelindung dan penolong selain Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,23,'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ وَلِقَاۤىِٕهٖٓ اُولٰۤىِٕكَ يَىِٕسُوْا مِنْ رَّحْمَتِيْ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ ','Dan orang-orang yang kafir terhadap ayat-ayat Allah dan pertemuan dengan Dia, mereka putus asa dari rahmat-Ku, dan mereka itu mendapat azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,24,'فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا اقْتُلُوْهُ اَوْ حَرِّقُوْهُ فَاَنْجٰىهُ اللّٰهُ مِنَ النَّارِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ','Maka tidak adalah jawaban kaum Ibrahim, selain mengatakan: "Bunuhlah atau bakarlah dia", lalu Allah menyelamatkannya dari api. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda kebesaran Allah bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,25,'وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِّنْ دُوْنِ اللّٰهِ اَوْثَانًاۙ مَّوَدَّةَ بَيْنِكُمْ فِى الْحَيٰوةِ الدُّنْيَا ۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَّيَلْعَنُ بَعْضُكُمْ بَعْضًا ۖوَّمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَۖ ','Dan berkata Ibrahim: "Sesungguhnya berhala-berhala yang kamu sembah selain Allah adalah untuk menciptakan perasaan kasih sayang di antara kamu dalam kehidupan dunia ini kemudian di hari kiamat sebahagian kamu mengingkari sebahagian (yang lain) dan sebahagian kamu melaknati sebahagian (yang lain); dan tempat kembalimu ialah neraka, dan sekali-kali tak ada bagimu para penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,26,'۞ فَاٰمَنَ لَهٗ لُوْطٌۘ وَقَالَ اِنِّيْ مُهَاجِرٌ اِلٰى رَبِّيْ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الْحَكِيْمُ ','Maka Luth membenarkan (kenabian)nya. Dan berkatalah Ibrahim: "Sesungguhnya aku akan berpindah ke (tempat yang diperintahkan) Tuhanku (kepadaku); sesungguhnya Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,27,'وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتٰبَ وَاٰتَيْنٰهُ اَجْرَهٗ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ','Dan Kami anugrahkan kepda Ibrahim, Ishak dan Ya´qub, dan Kami jadikan kenabian dan Al Kitab pada keturunannya, dan Kami berikan kepadanya balasannya di dunia; dan sesungguhnya dia di akhirat, benar-benar termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,28,'وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اِنَّكُمْ لَتَأْتُوْنَ الْفَاحِشَةَ ۖمَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ','Dan (ingatlah) ketika Luth berkata pepada kaumnya: "Sesungguhnya kamu benar-benar mengerjakan perbuatan yang amat keji yang belum pernah dikerjakan oleh seorangpun dari umat-umat sebelum kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,29,'اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ وَتَقْطَعُوْنَ السَّبِيْلَ ەۙ وَتَأْتُوْنَ فِيْ نَادِيْكُمُ الْمُنْكَرَ ۗفَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ','Apakah sesungguhnya kamu patut mendatangi laki-laki, menyamun dan mengerjakan kemungkaran di tempat-tempat pertemuanmu? Maka jawaban kaumnya tidak lain hanya mengatakan: "Datangkanlah kepada kami azab Allah, jika kamu termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,30,'قَالَ رَبِّ انْصُرْنِيْ عَلَى الْقَوْمِ الْمُفْسِدِيْنَ ࣖ','Luth berdoa: "Ya Tuhanku, tolonglah aku (dengan menimpakan azab) atas kaum yang berbuat kerusakan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,31,'وَلَمَّا جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰىۙ قَالُوْٓا اِنَّا مُهْلِكُوْٓا اَهْلِ هٰذِهِ الْقَرْيَةِ ۚاِنَّ اَهْلَهَا كَانُوْا ظٰلِمِيْنَ ۚ','Dan tatkala utusan Kami (para malaikat) datang kepada Ibrahim membawa kabar gembira, mereka mengatakan: "Sesungguhnya kami akan menghancurkan penduduk negeri (Sodom) ini; sesungguhnya penduduknya adalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,32,'قَالَ اِنَّ فِيْهَا لُوْطًا ۗقَالُوْا نَحْنُ اَعْلَمُ بِمَنْ فِيْهَا ۖ لَنُنَجِّيَنَّهٗ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ','Berkata Ibrahim: "Sesungguhnya di kota itu ada Luth". Para malaikat berkata: "Kami lebih mengetahui siapa yang ada di kota itu. Kami sungguh-sungguh akan menyelamatkan dia dan pengikut-pengikutnya kecuali isterinya. Dia adalah termasuk orang-orang yang tertinggal (dibinasakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,33,'وَلَمَّآ اَنْ جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالُوْا لَا تَخَفْ وَلَا تَحْزَنْ ۗاِنَّا مُنَجُّوْكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغٰبِرِيْنَ','Dan tatkala datang utusan-utusan Kami (para malaikat) itu kepada Luth, dia merasa susah karena (kedatangan) mereka, dan (merasa) tidak punya kekuatan untuk melindungi mereka dan mereka berkata: "Janganlah kamu takut dan jangan (pula) susah. Sesungguhnya kami akan menyelamatkan kamu dan pengikut-pengikutmu, kecuali isterimu, dia adalah termasuk orang-orang yang tertinggal (dibinasakan)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,34,'اِنَّا مُنْزِلُوْنَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ ','Sesungguhnya Kami akan menurunkan azab dari langit atas penduduk kota ini karena mereka berbuat fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,35,'وَلَقَدْ تَّرَكْنَا مِنْهَآ اٰيَةً ۢ بَيِّنَةً لِّقَوْمٍ يَّعْقِلُوْنَ ','Dan sesungguhnya Kami tinggalkan daripadanya satu tanda yang nyata bagi orang-orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,36,'وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۙ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۖ','Dan (Kami telah mengutus) kepada penduduk Mad-yan, saudara mereka Syu´aib, maka ia berkata: "Hai kaumku, sembahlah olehmu Allah, harapkanlah (pahala) hari akhir, dan jangan kamu berkeliaran di muka bumi berbuat kerusakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,37,'فَكَذَّبُوْهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ ۙ','Maka mereka mendustakan Syu´aib, lalu mereka ditimpa gempa yang dahsyat, dan jadilah mereka mayat-mayat yang bergelimpangan di tempat-tempat tinggal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,38,'وَعَادًا وَّثَمُوْدَا۟ وَقَدْ تَّبَيَّنَ لَكُمْ مِّنْ مَّسٰكِنِهِمْۗ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ وَكَانُوْا مُسْتَبْصِرِيْنَ ۙ','Dan (juga) kaum ´Aad dan Tsamud, dan sungguh telah nyata bagi kamu (kehancuran mereka) dari (puing-puing) tempat tinggal mereka. Dan syaitan menjadikan mereka memandang baik perbuatan-perbuatan mereka, lalu ia menghalangi mereka dari jalan (Allah), sedangkan mereka adalah orang-orang berpandangan tajam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,39,'وَقَارُوْنَ وَفِرْعَوْنَ وَهَامٰنَۗ وَلَقَدْ جَاۤءَهُمْ مُّوْسٰى بِالْبَيِّنٰتِ فَاسْتَكْبَرُوْا فِى الْاَرْضِ وَمَا كَانُوْا سَابِقِيْنَ ۚ','dan (juga) Karun, Fir´aun dan Haman. Dan sesungguhnya telah datang kepada mereka Musa dengan (membawa bukti-bukti) keterangan-keterangan yang nyata. Akan tetapi mereka berlaku sombong di (muka) bumi, dan tiadalah mereka orang-orang yang luput (dari kehancuran itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,40,'فَكُلًّا اَخَذْنَا بِذَنْۢبِهٖۙ فَمِنْهُمْ مَّنْ اَرْسَلْنَا عَلَيْهِ حَاصِبًا ۚوَمِنْهُمْ مَّنْ اَخَذَتْهُ الصَّيْحَةُ ۚوَمِنْهُمْ مَّنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَّنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ','Maka masing-masing (mereka itu) Kami siksa disebabkan dosanya, maka di antara mereka ada yang Kami timpakan kepadanya hujan batu kerikil dan di antara mereka ada yang ditimpa suara keras yang mengguntur, dan di antara mereka ada yang Kami benamkan ke dalam bumi, dan di antara mereka ada yang Kami tenggelamkan, dan Allah sekali-kali tidak hendak menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,41,'مَثَلُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَ كَمَثَلِ الْعَنْكَبُوْتِۚ اِتَّخَذَتْ بَيْتًاۗ وَاِنَّ اَوْهَنَ الْبُيُوْتِ لَبَيْتُ الْعَنْكَبُوْتِۘ لَوْ كَانُوْا يَعْلَمُوْنَ ','Perumpamaan orang-orang yang mengambil pelindung-pelindung selain Allah adalah seperti laba-laba yang membuat rumah. Dan sesungguhnya rumah yang paling lemah adalah rumah laba-laba kalau mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,42,'اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ مِنْ شَيْءٍۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Sesungguhnya Allah mengetahui apa saja yang mereka seru selain Allah. Dan Dia Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,43,'وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُهَآ اِلَّا الْعَالِمُوْنَ ','Dan perumpamaan-perumpamaan ini Kami buat untuk manusia; dan tiada yang memahaminya kecuali orang-orang yang berilmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,20,29,44,'خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَ ࣖ   ۔','Allah menciptakan langit dan bumi dengan hak. Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi orang-orang mukmin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,45,'اُتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنَ الْكِتٰبِ وَاَقِمِ الصَّلٰوةَۗ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ ۗوَلَذِكْرُ اللّٰهِ اَكْبَرُ ۗوَاللّٰهُ يَعْلَمُ مَا تَصْنَعُوْنَ','Bacalah apa yang telah diwahyukan kepadamu, yaitu Al Kitab (Al Quran) dan dirikanlah shalat. Sesungguhnya shalat itu mencegah dari (perbuatan-perbuatan) keji dan mungkar. Dan sesungguhnya mengingat Allah (shalat) adalah lebih besar (keutamaannya dari ibadat-ibadat yang lain). Dan Allah mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,46,'۞ وَلَا تُجَادِلُوْٓا اَهْلَ الْكِتٰبِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُۖ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ وَقُوْلُوْٓا اٰمَنَّا بِالَّذِيْٓ اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَّنَحْنُ لَهٗ مُسْلِمُوْنَ','Dan janganlah kamu berdebat denganAhli Kitab, melainkan dengan cara yang paling baik, kecuali dengan orang-orang zalim di antara mereka, dan katakanlah: "Kami telah beriman kepada (kitab-kitab) yang diturunkan kepada kami dan yang diturunkan kepadamu; Tuhan kami dan Tuhanmu adalah satu; dan kami hanya kepada-Nya berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,47,'وَكَذٰلِكَ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَۗ فَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يُؤْمِنُوْنَ بِهٖۚ وَمِنْ هٰٓؤُلَاۤءِ مَنْ يُّؤْمِنُ بِهٖۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الْكٰفِرُوْنَ','Dan demikian (pulalah) Kami turunkan kepadamu Al Kitab (Al Quran). Maka orang-orang yang telah kami berikan kepada mereka Al Kitab (Taurat) mereka beriman kepadanya (Al Quran); dan di antara mereka (orang-orang kafir Mekah) ada yang beriman kepadanya. Dan tiadalah yang mengingkari ayat-ayat kami selain orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,48,'وَمَا كُنْتَ تَتْلُوْا مِنْ قَبْلِهٖ مِنْ كِتٰبٍ وَّلَا تَخُطُّهٗ بِيَمِيْنِكَ اِذًا لَّارْتَابَ الْمُبْطِلُوْنَ','Dan kamu tidak pernah membaca sebelumnya (Al Quran) sesuatu Kitabpun dan kamu tidak (pernah) menulis suatu Kitab dengan tangan kananmu; andaikata (kamu pernah membaca dan menulis), benar-benar ragulah orang yang mengingkari(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,49,'بَلْ هُوَ اٰيٰتٌۢ بَيِّنٰتٌ فِيْ صُدُوْرِ الَّذِيْنَ اُوْتُوا الْعِلْمَۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الظّٰلِمُوْنَ','Sebenarnya, Al Quran itu adalah ayat-ayat yang nyata di dalam dada orang-orang yang diberi ilmu. Dan tidak ada yang mengingkari ayat-ayat Kami kecuali orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,50,'وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيٰتٌ مِّنْ رَّبِّهٖ ۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ ۗوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ','Dan orang-orang kafir Mekah berkata: "Mengapa tidak diturunkan kepadanya mukjizat-mukjizat dari Tuhannya?" Katakanlah: "Sesungguhnya mukjizat-mukjizat itu terserah kepada Allah. Dan sesungguhnya aku hanya seorang pemberi peringatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,51,'اَوَلَمْ يَكْفِهِمْ اَنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ يُتْلٰى عَلَيْهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَرَحْمَةً وَّذِكْرٰى لِقَوْمٍ يُّؤْمِنُوْنَ ࣖ ','Dan apakah tidak cukup bagi mereka bahwasanya Kami telah menurunkan kepadamu Al Kitab (Al Quran) sedang dia dibacakan kepada mereka? Sesungguhnya dalam (Al Quran) itu terdapat rahmat yang besar dan pelajaran bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,52,'قُلْ كَفٰى بِاللّٰهِ بَيْنِيْ وَبَيْنَكُمْ شَهِيْدًاۚ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ اٰمَنُوْا بِالْبَاطِلِ وَكَفَرُوْا بِاللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ','Katakanlah: "Cukuplah Allah menjadi saksi antaraku dan antaramu. Dia mengetahui apa yang di langit dan di bumi. Dan orang-orang yang percaya kepada yang batil dan ingkar kepada Allah, mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,53,'وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَلَوْلَآ اَجَلٌ مُّسَمًّى لَّجَاۤءَهُمُ الْعَذَابُۗ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ','Dan mereka meminta kepadamu supaya segera diturunkan azab. Kalau tidaklah karena waktu yang telah ditetapkan, benar-benar telah datang azab kepada mereka, dan azab itu benar-benar akan datang kepada mereka dengan tiba-tiba, sedang mereka tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,54,'يَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَۙ','Mereka meminta kepadamu supaya segera diturunkan azab. Dan sesungguhnya Jahannam benar-benar meliputi orang-orang yang kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,55,'يَوْمَ يَغْشٰىهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُوْلُ ذُوْقُوْا مَا كُنْتُمْ تَعْمَلُوْنَ','pada hari mereka ditutup oleh azab dari atas mereka dan dari bawah kaki mereka dan Allah berkata (kepada mereka): "Rasailah (pembalasan dari) apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,56,'يٰعِبَادِيَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ اَرْضِيْ وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُوْنِ','Hai hamba-hamba-Ku yang beriman, sesungguhnya bumi-Ku luas, maka sembahlah Aku saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,57,'كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ ثُمَّ اِلَيْنَا تُرْجَعُوْنَ','Tiap-tiap yang berjiwa akan merasakan mati. Kemudian hanyalah kepada Kami kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,58,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُبَوِّئَنَّهُمْ مِّنَ الْجَنَّةِ غُرَفًا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ نِعْمَ اَجْرُ الْعٰمِلِيْنَۖ','Dan orang-orang yang beriman dan mengerjakan amal-amal yang saleh, sesungguhnya akan Kami tempatkan mereka pada tempat-tempat yang tinggi di dalam surga, yang mengalir sungai-sungai di bawahnya, mereka kekal di dalamnya. Itulah sebaik-baik pembalasan bagi orang-orang yang beramal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,59,'الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ','(yaitu) yang bersabar dan bertawakkal kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,60,'وَكَاَيِّنْ مِّنْ دَاۤبَّةٍ لَّا تَحْمِلُ رِزْقَهَاۖ اللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْ وَهُوَ السَّمِيْعُ الْعَلِيْمُ','Dan berapa banyak binatang yang tidak (dapat) membawa (mengurus) rezekinya sendiri. Allah-lah yang memberi rezeki kepadanya dan kepadamu dan Dia Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,61,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُوْلُنَّ اللّٰهُ ۗفَاَنّٰى يُؤْفَكُوْنَ','Dan sesungguhnya jika kamu tanyakan kepada mereka: "Siapakah yang menjadikan langit dan bumi dan menundukkan matahari dan bulan?" Tentu mereka akan menjawab: "Allah", maka betapakah mereka (dapat) dipalingkan (dari jalan yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,62,'اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ','Allah melapangkan rezeki bagi siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan Dia (pula) yang menyempitkan baginya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,63,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ نَّزَّلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ مِنْۢ بَعْدِ مَوْتِهَا لَيَقُوْلُنَّ اللّٰهُ ۙقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ ࣖ','Dan sesungguhnya jika kamu menanyakan kepada mereka: "Siapakah yang menurunkan air dari langit lalu menghidupkan dengan air itu bumi sesudah matinya?" Tentu mereka akan menjawab: "Allah", Katakanlah: "Segala puji bagi Allah", tetapi kebanyakan mereka tidak memahami(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,64,'وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَآ اِلَّا لَهْوٌ وَّلَعِبٌۗ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۘ لَوْ كَانُوْا يَعْلَمُوْنَ','Dan tiadalah kehidupan dunia ini melainkan senda gurau dan main-main. Dan sesungguhnya akhirat itulah yang sebenarnya kehidupan, kalau mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,65,'فَاِذَا رَكِبُوْا فِى الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُوْنَۙ ','Maka apabila mereka naik kapal mereka mendoa kepada Allah dengan memurnikan ketaatan kepada-Nya; maka tatkala Allah menyelamatkan mereka sampai ke darat, tiba-tiba mereka (kembali) mempersekutukan (Allah)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,66,'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۙ وَلِيَتَمَتَّعُوْاۗ فَسَوْفَ يَعْلَمُوْنَ','agar mereka mengingkari nikmat yang telah Kami berikan kepada mereka dan agar mereka (hidup) bersenang-senang (dalam kekafiran). Kelak mereka akan mengetahui (akibat perbuatannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,67,'اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَمًا اٰمِنًا وَّيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُوْنَ','Dan apakah mereka tidak memperhatikan, bahwa sesungguhnya Kami telah menjadikan (negeri mereka) tanah suci yang aman, sedang manusia sekitarnya rampok-merampok. Maka mengapa (sesudah nyata kebenaran) mereka masih percaya kepada yang bathil dan ingkar kepada nikmat Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,68,'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاۤءَهٗ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ','Dan siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kedustaan terhadap Allah atau mendustakan yang hak tatkala yang hak itu datang kepadanya? Bukankah dalam neraka Jahannam itu ada tempat bagi orang-orang yang kafir?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,29,69,'وَالَّذِيْنَ جَاهَدُوْا فِيْنَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۗ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِنِيْنَ ࣖ ','Dan orang-orang yang berjihad untuk (mencari keridhaan) Kami, benar-benar akan Kami tunjukkan kepada mereka jalan-jalan Kami. Dan sesungguhnya Allah benar-benar beserta orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,1,' الۤمّۤ ۚ  ','Alif Laam Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,2,'غُلِبَتِ الرُّوْمُۙ ','Telah dikalahkan bangsa Rumawi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,3,'فِيْٓ اَدْنَى الْاَرْضِ وَهُمْ مِّنْۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُوْنَۙ ','di negeri yang terdekat dan mereka sesudah dikalahkan itu akan menang')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,4,'فِيْ بِضْعِ سِنِيْنَ ەۗ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْۢ بَعْدُ ۗوَيَوْمَىِٕذٍ يَّفْرَحُ الْمُؤْمِنُوْنَۙ','dalam beberapa tahun lagi. Bagi Allah-lah urusan sebelum dan sesudah (mereka menang). Dan di hari (kemenangan bangsa Rumawi) itu bergembiralah orang-orang yang beriman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,5,'بِنَصْرِ اللّٰهِ ۗيَنْصُرُ مَنْ يَّشَاۤءُۗ وَهُوَ الْعَزِيْزُ الرَّحِيْمُ','Karena pertolongan Allah. Dia menolong siapa yang dikehendaki-Nya. Dan Dialah Maha Perkasa lagi Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,6,'وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ وَعْدَهٗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ','(Sebagai) janji yang sebenarnya dari Allah. Allah tidak akan menyalahi janji-Nya, tetapi kebanyakan manusia tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,7,'يَعْلَمُوْنَ ظَاهِرًا مِّنَ الْحَيٰوةِ الدُّنْيَاۖ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غٰفِلُوْنَ','Mereka hanya mengetahui yang lahir (saja) dari kehidupan dunia; sedang mereka tentang (kehidupan) akhirat adalah lalai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,8,'اَوَلَمْ يَتَفَكَّرُوْا فِيْٓ اَنْفُسِهِمْ ۗ مَا خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَاِنَّ كَثِيْرًا مِّنَ النَّاسِ بِلِقَاۤئِ رَبِّهِمْ لَكٰفِرُوْنَ','Dan mengapa mereka tidak memikirkan tentang (kejadian) diri mereka? Allah tidak menjadikan langit dan bumi dan apa yang ada diantara keduanya melainkan dengan (tujuan) yang benar dan waktu yang ditentukan. Dan sesungguhnya kebanyakan di antara manusia benar-benar ingkar akan pertemuan dengan Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,9,'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ  كَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً وَّاَثَارُوا الْاَرْضَ وَعَمَرُوْهَآ اَكْثَرَ مِمَّا عَمَرُوْهَا وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۗ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَۗ','Dan apakah mereka tidak mengadakan perjalanan di muka bumi dan memperhatikan bagaimana akibat (yang diderita) oleh orang-orang sebelum mereka? orang-orang itu adalah lebihkuat dari mereka (sendiri) dan telah mengolah bumi (tanah) serta memakmurkannya lebih banyak dari apa yang telah mereka makmurkan. Dan telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata. Maka Allah sekali-kali tidak berlaku zalim kepada mereka, akan tetapi merekalah yang berlaku zalim kepada diri sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,10,'ثُمَّ كَانَ عَاقِبَةَ الَّذِيْنَ اَسَاۤءُوا السُّوْۤاٰىٓ اَنْ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ وَكَانُوْا بِهَا يَسْتَهْزِءُوْنَ ࣖ ','Kemudian, akibat orang-orang yang mengerjakan kejahatan adalah (azab) yang lebih buruk, karena mereka mendustakan ayat-ayat Allah dan mereka selalu memperolok-oloknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,11,'اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ','Allah menciptakan (manusia) dari permulaan, kemudian mengembalikan (menghidupkan)nya kembali; kemudian kepada-Nya-lah kamu dikembalikan')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,12,'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُوْنَ','Dan pada hari terjadinya kiamat, orang-orang yang berdosa terdiam berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,13,'وَلَمْ يَكُنْ لَّهُمْ مِّنْ شُرَكَاۤىِٕهِمْ شُفَعٰۤؤُا وَكَانُوْا بِشُرَكَاۤىِٕهِمْ كٰفِرِيْنَ','Dan sekali-kali tidak ada pemberi syafa´at bagi mereka dari berhala-berhala mereka dan adalah mereka mengingkari berhala mereka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,14,'وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّتَفَرَّقُوْنَ ','Dan pada hari terjadinya kiamat, di hari itu mereka (manusia) bergolong-golongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,15,'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَهُمْ فِيْ رَوْضَةٍ يُّحْبَرُوْنَ','Adapun orang-orang yang beriman dan mengerjakan amal saleh, maka mereka di dalam taman (surga) bergembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,16,'وَاَمَّا الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤئِ الْاٰخِرَةِ فَاُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ','Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami (Al Quran) serta (mendustakan) menemui hari akhirat, maka mereka tetap berada di dalam siksaan (neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,17,'فَسُبْحٰنَ اللّٰهِ حِيْنَ تُمْسُوْنَ وَحِيْنَ تُصْبِحُوْنَ','Maka bertasbihlah kepada Allah di waktu kamu berada di petang hari dan waktu kamu berada di waktu subuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,18,'وَلَهُ الْحَمْدُ فِى السَّمٰوٰتِ وَالْاَرْضِ وَعَشِيًّا وَّحِيْنَ تُظْهِرُوْنَ','dan bagi-Nya-lah segala puji di langit dan di bumi dan di waktu kamu berada pada petang hari dan di waktu kamu berada di waktu Zuhur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,19,'يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَا ۗوَكَذٰلِكَ تُخْرَجُوْنَ ࣖ','Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan menghidupkan bumi sesudah matinya. Dan seperti itulah kamu akan dikeluarkan (dari kubur).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,20,'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ اِذَآ اَنْتُمْ بَشَرٌ تَنْتَشِرُوْنَ','Dan di antara tanda-tanda kekuasaan-Nya ialah Dia menciptakan kamu dari tanah, kemudian tiba-tiba kamu (menjadi) manusia yang berkembang biak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,21,'وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا لِّتَسْكُنُوْٓا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَّوَدَّةً وَّرَحْمَةً ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ','Dan di antara tanda-tanda kekuasaan-Nya ialah Dia menciptakan untukmu isteri-isteri dari jenismu sendiri, supaya kamu cenderung dan merasa tenteram kepadanya, dan dijadikan-Nya diantaramu rasa kasih dan sayang. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,22,'وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْعٰلِمِيْنَ ','Dan di antara tanda-tanda kekuasaan-Nya ialah menciptakan langit dan bumi dan berlain-lainan bahasamu dan warna kulitmu. Sesungguhnya pada yang demikan itu benar-benar terdapat tanda-tanda bagi orang-orang yang mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,23,'وَمِنْ اٰيٰتِهٖ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَاۤؤُكُمْ مِّنْ فَضْلِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ','Dan di antara tanda-tanda kekuasaan-Nya ialah tidurmu di waktu malam dan siang hari dan usahamu mencari sebagian dari karunia-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mendengarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,24,'وَمِنْ اٰيٰتِهٖ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنَزِّلُ مِنَ السَّمَاۤءِ مَاۤءً فَيُحْيٖ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ','Dan di antara tanda-tanda kekuasaan-Nya, Dia memperlihatkan kepadamu kilat untuk (menimbulkan) ketakutan dan harapan, dan Dia menurunkan hujan dari langit, lalu menghidupkan bumi dengan air itu sesudah matinya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mempergunakan akalnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,25,'وَمِنْ اٰيٰتِهٖٓ اَنْ تَقُوْمَ السَّمَاۤءُ وَالْاَرْضُ بِاَمْرِهٖۗ  ثُمَّ اِذَا دَعَاكُمْ دَعْوَةًۖ مِّنَ الْاَرْضِ اِذَآ اَنْتُمْ تَخْرُجُوْنَ','Dan di antara tanda-tanda kekuasaan-Nya ialah berdirinya langit dan bumi dengan iradat-Nya. Kemudian apabila Dia memanggil kamu sekali panggil dari bumi, seketika itu (juga) kamu keluar (dari kubur).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,26,'وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ','Dan kepunyaan-Nya-lah siapa saja yang ada di langit dan di bumi. Semuanya hanya kepada-Nya tunduk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,27,'وَهُوَ الَّذِيْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَهُوَ اَهْوَنُ عَلَيْهِۗ وَلَهُ الْمَثَلُ الْاَعْلٰى فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ ','Dan Dialah yang menciptakan (manusia) dari permulaan, kemudian mengembalikan (menghidupkan)nya kembali, dan menghidupkan kembali itu adalah lebih mudah bagi-Nya. Dan bagi-Nya-lah sifat yang Maha Tinggi di langit dan di bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,28,'ضَرَبَ لَكُمْ مَّثَلًا مِّنْ اَنْفُسِكُمْۗ هَلْ لَّكُمْ مِّنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ شُرَكَاۤءَ فِيْ مَا رَزَقْنٰكُمْ فَاَنْتُمْ فِيْهِ سَوَاۤءٌ تَخَافُوْنَهُمْ كَخِيْفَتِكُمْ اَنْفُسَكُمْۗ  كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْقِلُوْنَ','Dia membuat perumpamaan untuk kamu dari dirimu sendiri. Apakah ada diantara hamba-sahaya yang dimiliki oleh tangan kananmu, sekutu bagimu dalam (memiliki) rezeki yang telah Kami berikan kepadamu; maka kamu sama dengan mereka dalam (hak mempergunakan) rezeki itu, kamu takut kepada mereka sebagaimana kamu takut kepada dirimu sendiri? Demikianlah Kami jelaskan ayat-ayat bagi kaum yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,29,'بَلِ اتَّبَعَ الَّذِيْنَ ظَلَمُوْٓا اَهْوَاۤءَهُمْ بِغَيْرِ عِلْمٍۗ فَمَنْ يَّهْدِيْ مَنْ اَضَلَّ اللّٰهُ ۗوَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ','Tetapi orang-orang yang zalim, mengikuti hawa nafsunya tanpa ilmu pengetahuan; maka siapakah yang akan menunjuki orang yang telah disesatkan Allah? Dan tiadalah bagi mereka seorang penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,30,'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۗ فِطْرَتَ اللّٰهِ الَّتِيْ فَطَرَ النَّاسَ عَلَيْهَاۗ  لَا تَبْدِيْلَ لِخَلْقِ اللّٰهِ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُۙ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ ','Maka hadapkanlah wajahmu dengan lurus kepada agama Allah; (tetaplah atas) fitrah Allah yang telah menciptakan manusia menurut fitrah itu. Tidak ada peubahan pada fitrah Allah. (Itulah) agama yang lurus; tetapi kebanyakan manusia tidak mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,31,'۞ مُنِيْبِيْنَ اِلَيْهِ وَاتَّقُوْهُ وَاَقِيْمُوا الصَّلٰوةَ وَلَا تَكُوْنُوْا مِنَ الْمُشْرِكِيْنَۙ','dengan kembali bertaubat kepada-Nya dan bertakwalah kepada-Nya serta dirikanlah shalat dan janganlah kamu termasuk orang-orang yang mempersekutukan Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,32,'مِنَ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا ۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ','yaitu orang-orang yang memecah-belah agama mereka dan mereka menjadi beberapa golongan. Tiap-tiap golongan merasa bangga dengan apa yang ada pada golongan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,33,'وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُّنِيْبِيْنَ اِلَيْهِ ثُمَّ اِذَآ اَذَاقَهُمْ مِّنْهُ رَحْمَةً اِذَا فَرِيْقٌ مِّنْهُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ','Dan apabila manusia disentuh oleh suatu bahaya, mereka menyeru Tuhannya dengan kembali bertaubat kepada-Nya, kemudian apabila Tuhan merasakan kepada mereka barang sedikit rahmat daripada-Nya, tiba-tiba sebagian dari mereka mempersekutukan Tuhannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,34,'لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ','sehingga mereka mengingkari akan rahmat yang telah Kami berikan kepada mereka. Maka bersenang-senanglah kamu sekalian, kelak kamu akan mengetahui (akibat perbuatanmu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,35,'اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطٰنًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوْا بِهٖ يُشْرِكُوْنَ ','Atau pernahkah Kami menurunkan kepada mereka keterangan, lalu keterangan itu menunjukkan (kebenaran) apa yang mereka selalu mempersekutukan dengan Tuhan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,36,'وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوْا بِهَاۗ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ اِذَا هُمْ يَقْنَطُوْنَ','Dan apabila Kami rasakan sesuatu rahmat kepada manusia, niscaya mereka gembira dengan rahmat itu. Dan apabila mereka ditimpa suatu musibah (bahaya) disebabkan kesalahan yang telah dikerjakan oleh tangan mereka sendiri, tiba-tiba mereka itu berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,37,'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ','Dan apakah mereka tidak memperhatikan bahwa Sesungguhnya Allah melapangkan rezeki bagi siapa yang dikehendaki-Nya dan Dia (pula) yang menyempitkan (rezeki itu). Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,38,'فَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِۗ ذٰلِكَ خَيْرٌ لِّلَّذِيْنَ يُرِيْدُوْنَ وَجْهَ اللّٰهِ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ','Maka berikanlah kepada kerabat yang terdekat akan haknya, demikian (pula) kepada fakir miskin dan orang-orang yang dalam perjalanan. Itulah yang lebih baik bagi orang-orang yang mencari keridhaan Allah; dan mereka itulah orang-orang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,39,'وَمَآ اٰتَيْتُمْ مِّنْ رِّبًا لِّيَرْبُوَا۠ فِيْٓ اَمْوَالِ النَّاسِ فَلَا يَرْبُوْا عِنْدَ اللّٰهِ ۚوَمَآ اٰتَيْتُمْ مِّنْ زَكٰوةٍ تُرِيْدُوْنَ وَجْهَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الْمُضْعِفُوْنَ','Dan sesuatu riba (tambahan) yang kamu berikan agar dia bertambah pada harta manusia, maka riba itu tidak menambah pada sisi Allah. Dan apa yang kamu berikan berupa zakat yang kamu maksudkan untuk mencapai keridhaan Allah, maka (yang berbuat demikian) itulah orang-orang yang melipat gandakan (pahalanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,40,'اَللّٰهُ الَّذِيْ خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّفْعَلُ مِنْ ذٰلِكُمْ مِّنْ شَيْءٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ࣖ','Allah-lah yang menciptakan kamu, kemudian memberimu rezeki, kemudian mematikanmu, kemudian menghidupkanmu (kembali). Adakah di antara yang kamu sekutukan dengan Allah itu yang dapat berbuat sesuatu dari yang demikian itu? Maha Sucilah Dia dan Maha Tinggi dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,41,'ظَهَرَ الْفَسَادُ فِى الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِى النَّاسِ لِيُذِيْقَهُمْ بَعْضَ الَّذِيْ عَمِلُوْا لَعَلَّهُمْ يَرْجِعُوْنَ','Telah nampak kerusakan di darat dan di laut disebabkan karena perbuatan tangan manusi, supay Allah merasakan kepada mereka sebahagian dari (akibat) perbuatan mereka, agar mereka kembali (ke jalan yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,42,'قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلُۗ  كَانَ اَكْثَرُهُمْ مُّشْرِكِيْنَ','Katakanlah: "Adakanlah perjalanan di muka bumi dan perhatikanlah bagaimana kesudahan orang-orang yang terdahulu. Kebanyakan dari mereka itu adalah orang-orang yang mempersekutukan (Allah)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,43,'فَاَقِمْ وَجْهَكَ لِلدِّيْنِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ يَوْمَىِٕذٍ يَّصَّدَّعُوْنَ','Oleh karena itu, hadapkanlah wajahmu kepada agama yang lurus (Islam) sebelum datang dari Allah suatu hari yang tidak dapat ditolak (kedatangannya): pada hari itu mereka terpisah-pisah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,44,'مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۚ وَمَنْ عَمِلَ صَالِحًا فَلِاَنْفُسِهِمْ يَمْهَدُوْنَۙ','Barangsiapa yang kafir maka dia sendirilah yang menanggung (akibat) kekafirannya itu; dan barangsiapa yang beramal saleh maka untuk diri mereka sendirilah mereka menyiapkan (tempat yang menyenangkan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,45,'لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْ فَضْلِهٖۗ  اِنَّهٗ لَا يُحِبُّ الْكٰفِرِيْنَ','agar Allah memberi pahala kepada orang-orang yang beriman dan beramal saleh dari karunia-Nya. Sesungguhnya Dia tidak menyukai orang-orang yang ingkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,46,'وَمِنْ اٰيٰتِهٖٓ اَنْ يُّرْسِلَ الرِّيٰحَ مُبَشِّرٰتٍ وَّلِيُذِيْقَكُمْ مِّنْ رَّحْمَتِهٖ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ','Dan di antara tanda-tanda kekuasan-Nya adalah bahwa Dia mengirimkan angin sebagai pembawa berita gembira dan untuk merasakan kepadamu sebagian dari rahmat-Nya dan supaya kapal dapat berlayar dengan perintah-Nya dan (juga) supaya kamu dapat mencari karunia-Nya; mudah-mudahn kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,47,'وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَانْتَقَمْنَا مِنَ الَّذِيْنَ اَجْرَمُوْاۗ وَكَانَ حَقًّاۖ عَلَيْنَا نَصْرُ الْمُؤْمِنِيْنَ','Dan Sesungguhnya Kami telah mengutus sebelum kamu beberapa orang rasul kepada kaumnya, mereka datang kepadanya dengan membawa keterangan-keterangan (yang cukup), lalu Kami melakukan pembalasan terhadap orang-orang yang berdosa. Dan Kami selalu berkewajiban menolong orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,48,'اَللّٰهُ الَّذِيْ يُرْسِلُ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَيَبْسُطُهٗ فِى السَّمَاۤءِ كَيْفَ يَشَاۤءُ وَيَجْعَلُهٗ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ فَاِذَآ اَصَابَ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَۚ','Allah, Dialah yang mengirim angin, lalu angin itu menggerakkan awan dan Allah membentangkannya di langit menurut yang dikehendaki-Nya, dan menjadikannya bergumpal-gumpal; lalu kamu lihat hujan keluar dari celah-celahnya, maka apabila hujan itu turun mengenai hamba-hamba-Nya yang dikehendaki-Nya, tiba-tiba mereka menjadi gembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,49,'وَاِنْ كَانُوْا مِنْ قَبْلِ اَنْ يُّنَزَّلَ عَلَيْهِمْ مِّنْ قَبْلِهٖ لَمُبْلِسِيْنَۚ','Dan Sesungguhnya sebelum hujan diturunkan kepada mereka, mereka benar-benar telah berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,50,'فَانْظُرْ اِلٰٓى اٰثٰرِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ ذٰلِكَ لَمُحْيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ','Maka perhatikanlah bekas-bekas rahmat Allah, bagaimana Allah menghidupkan bumi yang sudah mati. Sesungguhnya (Tuhan yang berkuasa seperti) demikian benar-benar (berkuasa) menghidupkan orang-orang yang telah mati. Dan Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,51,'وَلَىِٕنْ اَرْسَلْنَا رِيْحًا فَرَاَوْهُ مُصْفَرًّا لَّظَلُّوْا مِنْۢ بَعْدِهٖ يَكْفُرُوْنَ','Dan sungguh, jika Kami mengirimkan angin (kepada tumbuh-tumbuhan) lalu mereka melihat (tumbuh-tumbuhan itu) menjadi kuning (kering), benar-benar tetaplah mereka sesudah itu menjadi orang yang ingkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,52,'فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ','Maka Sesungguhnya kamu tidak akan sanggup menjadikan orang-orang yang mati itu dapat mendengar, dan menjadikan orang-orang yang tuli dapat mendengar seruan, apabila mereka itu berpaling membelakang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,53,'وَمَآ اَنْتَ بِهٰدِ الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ ࣖ','Dan kamu sekali-kali tidak akan dapat memberi petunjuk kepada orang-orang yang buta (mata hatinya) dari kesesatannya. Dan kamu tidak dapat memperdengarkan (petunjuk Tuhan) melainkan kepada orang-orang yang beriman dengan ayat-ayat Kami, mereka itulah orang-orang yang berserah diri (kepada Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,54,'۞ اَللّٰهُ الَّذِيْ خَلَقَكُمْ مِّنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْۢ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْۢ بَعْدِ قُوَّةٍ ضَعْفًا وَّشَيْبَةً ۗيَخْلُقُ مَا يَشَاۤءُۚ وَهُوَ الْعَلِيْمُ الْقَدِيْرُ','Allah, Dialah yang menciptakan kamu dari keadaan lemah, kemudian Dia menjadikan (kamu) sesudah keadaan lemah itu menjadi kuat, kemudian Dia menjadikan (kamu) sesudah kuat itu lemah (kembali) dan beruban. Dia menciptakan apa yang dikehendaki-Nya dan Dialah Yang Maha Mengetahui lagi Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,55,'وَيَوْمَ تَقُوْمُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُوْنَ ەۙ مَا لَبِثُوْا غَيْرَ سَاعَةٍ ۗ كَذٰلِكَ كَانُوْا يُؤْفَكُوْنَ','Dan pada hari terjadinya kiamat, bersumpahlah orang-orang yang berdosa; "mereka tidak berdiam (dalam kubur) melainkan sesaat (saja)". Seperti demikianlah mereka selalu dipalingkan (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,56,'وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَالْاِيْمَانَ لَقَدْ لَبِثْتُمْ فِيْ كِتٰبِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۖ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُوْنَ','Dan berkata orang-orang yang diberi ilmu pengetahuan dan keimanan (kepada orang-orang yang kafir): "Sesungguhnya kamu telah berdiam (dalam kubur) menurut ketetapan Allah, sampai hari berbangkit; maka inilah hari berbangkit itu akan tetapi kamu selalu tidak meyakini(nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,57,'فَيَوْمَىِٕذٍ لَّا يَنْفَعُ الَّذِيْنَ ظَلَمُوْا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُوْنَ','Maka pada hari itu tidak bermanfaat (lagi) bagi orang-orang yang zalim permintaan uzur mereka, dan tidak pula mereka diberi kesempatan bertaubat lagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,58,'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۗ وَلَىِٕنْ جِئْتَهُمْ بِاٰيَةٍ لَّيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ اَنْتُمْ اِلَّا مُبْطِلُوْنَ','Dan Sesungguhnya telah Kami buat dalam Al Quran ini segala macam perumpamaan untuk manusia. Dan Sesungguhnya jika kamu membawa kepada mereka suatu ayat, pastilah orang-orang yang kafir itu akan berkata: "Kamu tidak lain hanyalah orang-orang yang membuat kepalsuan belaka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,59,'كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الَّذِيْنَ لَا يَعْلَمُوْنَ','Demikianlah Allah mengunci mati hati orang-orang yang tidak (mau) memahami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,30,60,'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلَا يَسْتَخِفَّنَّكَ الَّذِيْنَ لَا يُوْقِنُوْنَ ࣖ ','Dan bersabarlah kamu, sesungguhnya janji Allah adalah benar dan sekali-kali janganlah orang-orang yang tidak meyakini (kebenaran ayat-ayat Allah) itu menggelisahkan kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,1,' الۤمّۤ ۗ ','Alif Laam Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,2,'تِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِۙ ','Inilah ayat-ayat Al Quran yang mengandung hikmat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,3,'هُدًى وَّرَحْمَةً لِّلْمُحْسِنِيْنَۙ ','menjadi petunjuk dan rahmat bagi orang-orang yang berbuat kebaikan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,4,'الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ ','(yaitu) orang-orang yang mendirikan shalat, menunaikan zakat dan mereka yakin akan adanya negeri akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,5,'اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ','Mereka itulah orang-orang yang tetap mendapat petunjuk dari Tuhannya dan mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,6,'وَمِنَ النَّاسِ مَنْ يَّشْتَرِيْ لَهْوَ الْحَدِيْثِ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ بِغَيْرِ عِلْمٍۖ وَّيَتَّخِذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ','Dan di antara manusia (ada) orang yang mempergunakan perkataan yang tidak berguna untuk menyesatkan (manusia) dari jalan Allah tanpa pengetahuan dan menjadikan jalan Allah itu olok-olokan. Mereka itu akan memperoleh azab yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,7,'وَاِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا وَلّٰى مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَا كَاَنَّ فِيْٓ اُذُنَيْهِ وَقْرًاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ','Dan apabila dibacakan kepadanya ayat-ayat Kami dia berpaling dengan menyombongkan diri seolah-olah dia belum mendengarnya, seakan-akan ada sumbat di kedua telinganya; maka beri kabar gembiralah dia dengan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,8,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتُ النَّعِيْمِۙ','Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh, bagi mereka surga-surga yang penuh kenikmatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,9,'خٰلِدِيْنَ فِيْهَاۗ وَعْدَ اللّٰهِ حَقًّاۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ','Kekal mereka di dalamnya; sebagai janji Allah yang benar. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,10,'خَلَقَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍۗ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ','Dia menciptakan langit tanpa tiang yang kamu melihatnya dan Dia meletakkan gunung-gunung (di permukaan) bumi supaya bumi itu tidak menggoyangkan kamu; dan memperkembang biakkan padanya segala macam jenis binatang. Dan Kami turunkan air hujan dari langit, lalu Kami tumbuhkan padanya segala macam tumbuh-tumbuhan yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,11,'هٰذَا خَلْقُ اللّٰهِ فَاَرُوْنِيْ مَاذَا خَلَقَ الَّذِيْنَ مِنْ دُوْنِهٖۗ بَلِ الظّٰلِمُوْنَ فِيْ ضَلٰلٍ مُّبِيْنٍ ࣖ','Inilah ciptaan Allah, maka perlihatkanlah olehmu kepadaku apa yang telah diciptakan oleh sembahan-sembahan(mu) selain Allah. Sebenarnya orang-orang yang zalim itu berada di dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,12,'وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِ ۗوَمَنْ يَّشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ ','Dan sesungguhnya telah Kami berikan hikmat kepada Luqman, yaitu: "Bersyukurlah kepada Allah. Dan barangsiapa yang bersyukur (kepada Allah), maka sesungguhnya ia bersyukur untuk dirinya sendiri; dan barangsiapa yang tidak bersyukur, maka sesungguhnya Allah Maha Kaya lagi Maha Terpuji".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,13,'وَاِذْ قَالَ لُقْمٰنُ لِابْنِهٖ وَهُوَ يَعِظُهٗ يٰبُنَيَّ لَا تُشْرِكْ بِاللّٰهِ ۗاِنَّ الشِّرْكَ لَظُلْمٌ عَظِيْمٌ ','Dan (ingatlah) ketika Luqman berkata kepada anaknya, di waktu ia memberi pelajaran kepadanya: "Hai anakku, janganlah kamu mempersekutukan Allah, sesungguhnya mempersekutukan (Allah) adalah benar-benar kezaliman yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,14,'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهٗ وَهْنًا عَلٰى وَهْنٍ وَّفِصَالُهٗ فِيْ عَامَيْنِ اَنِ اشْكُرْ لِيْ وَلِوَالِدَيْكَۗ اِلَيَّ الْمَصِيْرُ','Dan Kami perintahkan kepada manusia (berbuat baik) kepada dua orang ibu-bapaknya; ibunya telah mengandungnya dalam keadaan lemah yang bertambah-tambah, dan menyapihnya dalam dua tahun. Bersyukurlah kepada-Ku dan kepada dua orang ibu bapakmu, hanya kepada-Kulah kembalimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,15,'وَاِنْ جَاهَدٰكَ عَلٰٓى اَنْ تُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِى الدُّنْيَا مَعْرُوْفًا ۖوَّاتَّبِعْ سَبِيْلَ مَنْ اَنَابَ اِلَيَّۚ  ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ','Dan jika keduanya memaksamu untuk mempersekutukan dengan Aku sesuatu yang tidak ada pengetahuanmu tentang itu, maka janganlah kamu mengikuti keduanya, dan pergaulilah keduanya di dunia dengan baik, dan ikutilah jalan orang yang kembali kepada-Ku, kemudian hanya kepada-Kulah kembalimu, maka Kuberitakan kepadamu apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,16,'يٰبُنَيَّ اِنَّهَآ اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُنْ فِيْ صَخْرَةٍ اَوْ فِى السَّمٰوٰتِ اَوْ فِى الْاَرْضِ يَأْتِ بِهَا اللّٰهُ ۗاِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ','(Luqman berkata): "Hai anakku, sesungguhnya jika ada (sesuatu perbuatan) seberat biji sawi, dan berada dalam batu atau di langit atau di dalam bumi, niscaya Allah akan mendatangkannya (membalasinya). Sesungguhnya Allah Maha Halus lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,17,'يٰبُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوْفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَآ اَصَابَكَۗ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ','Hai anakku, dirikanlah shalat dan suruhlah (manusia) mengerjakan yang baik dan cegahlah (mereka) dari perbuatan yang mungkar dan bersabarlah terhadap apa yang menimpa kamu. Sesungguhnya yang demikian itu termasuk hal-hal yang diwajibkan (oleh Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,18,'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۚ','Dan janganlah kamu memalingkan mukamu dari manusia (karena sombong) dan janganlah kamu berjalan di muka bumi dengan angkuh. Sesungguhnya Allah tidak menyukai orang-orang yang sombong lagi membanggakan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,19,'وَاقْصِدْ فِيْ مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۗ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَمِيْرِ ࣖ','Dan sederhanalah kamu dalam berjalan dan lunakkanlah suaramu. Sesungguhnya seburuk-buruk suara ialah suara keledai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,20,'اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهٗ ظَاهِرَةً وَّبَاطِنَةً ۗوَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ','Tidakkah kamu perhatikan sesungguhnya Allah telah menundukkan untuk (kepentingan)mu apa yang di langit dan apa yang di bumi dan menyempurnakan untukmu nikmat-Nya lahir dan batin. Dan di antara manusia ada yang membantah tentang (keesaan) Allah tanpa ilmu pengetahuan atau petunjuk dan tanpa Kitab yang memberi penerangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,21,'وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَاۗ اَوَلَوْ كَانَ الشَّيْطٰنُ يَدْعُوْهُمْ اِلٰى عَذَابِ السَّعِيْرِ','Dan apabila dikatakan kepada mereka: "Ikutilah apa yang diturunkan Allah". Mereka menjawab: "(Tidak), tapi kami (hanya) mengikuti apa yang kami dapati bapak-bapak kami mengerjakannya". Dan apakah mereka (akan mengikuti bapak-bapak mereka) walaupun syaitan itu menyeru mereka ke dalam siksa api yang menyala-nyala (neraka)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,22,'۞ وَمَنْ يُّسْلِمْ وَجْهَهٗٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُوْرِ','Dan barangsiapa yang menyerahkan dirinya kepada Allah, sedang dia orang yang berbuat kebaikan, maka sesungguhnya ia telah berpegang kepada buhul tali yang kokoh. Dan hanya kepada Allah-lah kesudahan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,23,'وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهٗۗ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اِنَّ اللّٰهَ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ','Dan barangsiapa kafir maka kekafirannya itu janganlah menyedihkanmu. Hanya kepada Kami-lah mereka kembali, lalu Kami beritakan kepada mereka apa yang telah mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,24,'نُمَتِّعُهُمْ قَلِيْلًا ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَلِيْظٍ','Kami biarkan mereka bersenang-senang sebentar, kemudian Kami paksa mereka (masuk) ke dalam siksa yang keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,25,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ','Dan sesungguhnya jika kamu tanyakan kepada mereka: "Siapakah yang menciptakan langit dan bumi?" Tentu mereka akan menjawab: "Allah". Katakanlah: "Segala puji bagi Allah"; tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,26,'لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ','Kepunyaan Allah-lah apa yang di langit dan yang di bumi. Sesungguhnya Allah Dialah Yang Maha Kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,27,'وَلَوْ اَنَّ مَا فِى الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَّالْبَحْرُ يَمُدُّهٗ مِنْۢ بَعْدِهٖ سَبْعَةُ اَبْحُرٍ مَّا نَفِدَتْ كَلِمٰتُ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ','Dan seandainya pohon-pohon di bumi menjadi pena dan laut (menjadi tinta), ditambahkan kepadanya tujuh laut (lagi) sesudah (kering)nya, niscaya tidak akan habis-habisnya (dituliskan) kalimat Allah. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,28,'مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَّاحِدَةٍ ۗاِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ','Tidaklah Allah menciptakan dan membangkitkan kamu (dari dalam kubur) itu melainkan hanyalah seperti (menciptakan dan membangkitkan) satu jiwa saja. Sesungguhnya Allah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,29,'اَلَمْ تَرَ اَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۖ  كُلٌّ يَّجْرِيْٓ اِلٰٓى اَجَلٍ مُّسَمًّى وَّاَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ خَبِيْرٌ','Tidakkah kamu memperhatikan, bahwa sesungguhnya Allah memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan Dia tundukkan matahari dan bulan masing-masing berjalan sampai kepada waktu yang ditentukan, dan sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,30,'ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ ࣖ','Demikianlah, karena sesungguhnya Allah, Dialah yang hak dan sesungguhnya apa saja yang mereka seru selain dari Allah itulah yang batil; dan sesungguhnya Allah Dialah Yang Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,31,'اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِّنْ اٰيٰتِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ','Tidakkah kamu memperhatikan bahwa sesungguhnya kapal itu berlayar di laut dengan nikmat Allah, supaya diperlihatkan-Nya kepadamu sebahagian dari tanda-tanda (kekuasaan)-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi semua orang yang sangat sabar lagi banyak bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,32,'وَاِذَا غَشِيَهُمْ مَّوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُّقْتَصِدٌۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا كُلُّ خَتَّارٍ كَفُوْرٍ','Dan apabila mereka dilamun ombak yang besar seperti gunung, mereka menyeru Allah dengan memurnikan ketaatan kepada-Nya maka tatkala Allah menyelamatkan mereka sampai di daratan, lalu sebagian mereka tetap menempuh jalan yang lurus. Dan tidak ada yang mengingkari ayat-ayat Kami selain orang-orang yang tidak setia lagi ingkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,33,'يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَّا يَجْزِيْ وَالِدٌ عَنْ وَّلَدِهٖۖ وَلَا مَوْلُوْدٌ هُوَ جَازٍ عَنْ وَّالِدِهٖ شَيْـًٔاۗ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ','Hai manusia, bertakwalah kepada Tuhanmu dan takutilah suatu hari yang (pada hari itu) seorang bapak tidak dapat menolong anaknya dan seorang anak tidak dapat (pula) menolong bapaknya sedikitpun. Sesungguhnya janji Allah adalah benar, maka janganlah sekali-kali kehidupan dunia memperdayakan kamu, dan jangan (pula) penipu (syaitan) memperdayakan kamu dalam (mentaati) Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,31,34,'اِنَّ اللّٰهَ عِنْدَهٗ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِى الْاَرْحَامِۗ وَمَا تَدْرِيْ نَفْسٌ مَّاذَا تَكْسِبُ غَدًاۗ وَمَا تَدْرِيْ نَفْسٌۢ بِاَيِّ اَرْضٍ تَمُوْتُۗ اِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ ࣖ','Sesungguhnya Allah, hanya pada sisi-Nya sajalah pengetahuan tentang Hari Kiamat; dan Dialah Yang menurunkan hujan, dan mengetahui apa yang ada dalam rahim. Dan tiada seorangpun yang dapat mengetahui (dengan pasti) apa yang akan diusahakannya besok. Dan tiada seorangpun yang dapat mengetahui di bumi mana dia akan mati. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,1,' الۤمّۤ ۗ ','Alif Laam Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,2,'تَنْزِيْلُ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ ','Turunnya Al-Quran yang tidak ada keraguan di dalamnya, (adalah) dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,3,'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۚ بَلْ هُوَ الْحَقُّ مِنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُوْنَ','Tetapi mengapa mereka (orang kafir) mengatakan: "Dia Muhammad mengada-adakannya". Sebenarnya Al-Quran itu adalah kebenaran dari Rabbmu, agar kamu memberi peringatan kepada kaum yang belum datang kepada mereka orang yang memberi peringatan sebelum kamu; mudah-mudahan mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,4,'اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ مَا لَكُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّ وَّلَا شَفِيْعٍۗ  اَفَلَا تَتَذَكَّرُوْنَ','Allah lah yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ´Arsy. Tidak ada bagi kamu selain dari pada-Nya seorang penolongpun dan tidak (pula) seorang pemberi syafa´at. Maka apakah kamu tidak memperhatikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,5,'يُدَبِّرُ الْاَمْرَ مِنَ السَّمَاۤءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗٓ اَلْفَ سَنَةٍ مِّمَّا تَعُدُّوْنَ','Dia mengatur urusan dari langit ke bumi, kemudian (urusan) itu naik kepada-Nya dalam satu hari yang kadarnya adalah seribu tahun menurut perhitunganmu')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,6,'ذٰلِكَ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الرَّحِيْمُۙ ','Yang demikian itu ialah Tuhan Yang mengetahui yang ghaib dan yang nyata, Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,7,'الَّذِيْٓ اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهٗ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ طِيْنٍ','Yang membuat segala sesuatu yang Dia ciptakan sebaik-baiknya dan Yang memulai penciptaan manusia dari tanah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,8,'ثُمَّ جَعَلَ نَسْلَهٗ مِنْ سُلٰلَةٍ مِّنْ مَّاۤءٍ مَّهِيْنٍ ۚ ','Kemudian Dia menjadikan keturunannya dari saripati air yang hina.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,9,'ثُمَّ سَوّٰىهُ وَنَفَخَ فِيْهِ مِنْ رُّوْحِهٖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ','Kemudian Dia menyempurnakan dan meniupkan ke dalamnya roh (ciptaan)-Nya dan Dia menjadikan bagi kamu pendengaran, penglihatan dan hati; (tetapi) kamu sedikit sekali bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,10,'وَقَالُوْٓا ءَاِذَا ضَلَلْنَا فِى الْاَرْضِ ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ بَلْ هُمْ بِلِقَاۤءِ رَبِّهِمْ كٰفِرُوْنَ ','Dan mereka berkata: "Apakah bila kami telah lenyap (hancur) dalam tanah, kami benar-benar akan berada dalam ciptaan yang baru?" Bahkan mereka ingkar akan menemui Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,11,'۞ قُلْ يَتَوَفّٰىكُمْ مَّلَكُ الْمَوْتِ الَّذِيْ وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ ࣖ ','Katakanlah: "Malaikat maut yang diserahi untuk (mencabut nyawa)mu akan mematikanmu, kemudian hanya kepada Tuhanmulah kamu akan dikembalikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,12,'وَلَوْ تَرٰىٓ اِذِ الْمُجْرِمُوْنَ نَاكِسُوْا رُءُوْسِهِمْ عِنْدَ رَبِّهِمْۗ رَبَّنَآ اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا اِنَّا مُوْقِنُوْنَ ','Dan, jika sekiranya kamu melihat mereka ketika orang-orang yang berdosa itu menundukkan kepalanya di hadapan Tuhannya, (mereka berkata): "Ya Tuhan kami, kami telah melihat dan mendengar, maka kembalikanlah kami (ke dunia), kami akan mengerjakan amal saleh, sesungguhnya kami adalah orang-orang yang yakin".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,13,'وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰىهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنِّيْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ ','Dan kalau Kami menghendaki niscaya Kami akan berikan kepada tiap-tiap jiwa petunjuk, akan tetapi telah tetaplah perkataan dari pada-Ku: "Sesungguhnya akan Aku penuhi neraka jahannam itu dengan jin dan manusia bersama-sama".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,14,'فَذُوْقُوْا بِمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَسِيْنٰكُمْ وَذُوْقُوْا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُوْنَ','Maka rasailah olehmu (siksa ini) disebabkan kamu melupakan akan pertemuan dengan harimu ini. Sesungguhnya Kami telah melupakan kamu (pula) dan rasakanlah siksa yang kekal, disebabkan apa yang selalu kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,15,'اِنَّمَا يُؤْمِنُ بِاٰيٰتِنَا الَّذِيْنَ اِذَا ذُكِّرُوْا بِهَا خَرُّوْا سُجَّدًا وَّسَبَّحُوْا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُوْنَ ۩ ','Sesungguhnya orang yang benar-benar percaya kepada ayat-ayat Kami adalah mereka yang apabila diperingatkan dengan ayat-ayat itu mereka segera bersujud seraya bertasbih dan memuji Rabbnya, dan lagi pula mereka tidaklah sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,16,'تَتَجَافٰى جُنُوْبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُوْنَ رَبَّهُمْ خَوْفًا وَّطَمَعًاۖ وَّمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ','Lambung mereka jauh dari tempat tidurnya dan mereka selalu berdoa kepada Rabbnya dengan penuh rasa takut dan harap, serta mereka menafkahkan apa apa rezeki yang Kami berikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,17,'فَلَا تَعْلَمُ نَفْسٌ مَّآ اُخْفِيَ لَهُمْ مِّنْ قُرَّةِ اَعْيُنٍۚ جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ','Tak seorangpun mengetahui berbagai nikmat yang menanti, yang indah dipandang sebagai balasan bagi mereka, atas apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,18,'اَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًاۗ  لَا يَسْتَوٗنَ','Apakah orang-orang beriman itu sama dengan orang-orang yang fasik? Mereka tidak sama.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,19,'اَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ جَنّٰتُ الْمَأْوٰىۖ  نُزُلًا ۢبِمَا كَانُوْا يَعْمَلُوْنَ','Adapun orang-orang yang beriman dan mengerjakan amal saleh, maka bagi mereka jannah tempat kediaman, sebagai pahala terhadap apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,20,'وَاَمَّا الَّذِيْنَ فَسَقُوْا فَمَأْوٰىهُمُ النَّارُ كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَآ اُعِيْدُوْا فِيْهَا وَقِيْلَ لَهُمْ ذُوْقُوْا عَذَابَ النَّارِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ','Dan adapun orang-orang yang fasik (kafir) maka tempat mereka adalah jahannam. Setiap kali mereka hendak keluar daripadanya, mereka dikembalikan ke dalamnya dan dikatakan kepada mereka: "Rasakanlah siksa neraka yang dahulu kamu mendustakannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,21,'وَلَنُذِيْقَنَّهُمْ مِّنَ الْعَذَابِ الْاَدْنٰى دُوْنَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُوْنَ','Dan Sesungguhnya Kami merasakan kepada mereka sebahagian azab yang dekat (di dunia) sebelum azab yang lebih besar (di akhirat), mudah-mudahan mereka kembali (ke jalan yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,22,'وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ ثُمَّ اَعْرَضَ عَنْهَا ۗاِنَّا مِنَ الْمُجْرِمِيْنَ مُنْتَقِمُوْنَ ࣖ','Dan siapakah yang lebih zalim daripada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, kemudian ia berpaling daripadanya? Sesungguhnya Kami akan memberikan pembalasan kepada orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,23,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَلَا تَكُنْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤىِٕهٖ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَۚ','Dan Sesungguhnya Kami telah berikan kepada Musa Al-Kitab (Taurat), maka janganlah kamu (Muhammad) ragu menerima (Al-Quran itu) dan Kami jadikan Al-Kitab (Taurat) itu petunjuk bagi Bani Israil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,24,'وَجَعَلْنَا مِنْهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا لَمَّا صَبَرُوْاۗ وَكَانُوْا بِاٰيٰتِنَا يُوْقِنُوْنَ','Dan Kami jadikan di antara mereka itu pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami ketika mereka sabar. Dan adalah mereka meyakini ayat-ayat Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,25,'اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ','Sesungguhnya Tuhanmu Dialah yang memberikan keputusan di antara mereka pada hari kiamat tentang apa yang selalu mereka perselisihkan padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,26,'اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍۗ اَفَلَا يَسْمَعُوْنَ','Dan apakah tidak menjadi petunjuk bagi mereka, berapa banyak umat sebelum mereka yang telah Kami binasakan sedangkan mereka sendiri berjalan di tempat-tempat kediaman mereka itu. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah). Maka apakah mereka tidak mendengarkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,27,'اَوَلَمْ يَرَوْا اَنَّا نَسُوْقُ الْمَاۤءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِهٖ زَرْعًا تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۗ اَفَلَا يُبْصِرُوْنَ','Dan apakah mereka tidak memperhatikan, bahwasanya Kami menghalau (awan yang mengandung) air ke bumi yang tandus, lalu Kami tumbuhkan dengan air hujan itu tanaman yang daripadanya makan hewan ternak mereka dan mereka sendiri. Maka apakah mereka tidak memperhatikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,28,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صٰدِقِيْنَ','Dan mereka bertanya: "Bilakah kemenangan itu (datang) jika kamu memang orang-orang yang benar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,29,'قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذِيْنَ كَفَرُوْٓا اِيْمَانُهُمْ وَلَا هُمْ يُنْظَرُوْنَ','Katakanlah: "Pada hari kemenangan itu tidak berguna bagi orang-orang kafir, iman mereka dan tidak pula mereka diberi tangguh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,32,30,'فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُّنْتَظِرُوْنَ ࣖ','Maka berpalinglah kamu dari mereka dan tunggulah, sesungguhnya mereka (juga) menunggu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,1,'يٰٓاَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۙ ','Hai Nabi, bertakwalah kepada Allah dan janganlah kamu menuruti (keinginan) orang-orang kafir dan orang-orang munafik. Sesungguhnya Allah adalah Maha Mengetahui lagi Maha Bijaksana,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,2,'وَّاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ مِنْ رَّبِّكَ ۗاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًاۙ ','dan ikutilah apa yang diwahyukan Tuhan kepadamu. Sesungguhnya Allah adalah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,3,'وَّتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا','dan bertawakkallah kepada Allah. Dan cukuplah Allah sebagai Pemelihara.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,4,'مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِّنْ قَلْبَيْنِ فِيْ جَوْفِهٖ ۚوَمَا جَعَلَ اَزْوَاجَكُمُ الّٰـِٕۤيْ تُظٰهِرُوْنَ مِنْهُنَّ اُمَّهٰتِكُمْ ۚوَمَا جَعَلَ اَدْعِيَاۤءَكُمْ اَبْنَاۤءَكُمْۗ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْ ۗوَاللّٰهُ يَقُوْلُ الْحَقَّ وَهُوَ يَهْدِى السَّبِيْلَ','Allah sekali-kali tidak menjadikan bagi seseorang dua buah hati dalam rongganya; dan Dia tidak menjadikan istri-istrimu yang kamu zhihar itu sebagai ibumu, dan Dia tidak menjadikan anak-anak angkatmu sebagai anak kandungmu (sendiri). Yang demikian itu hanyalah perkataanmu di mulutmu saja. Dan Allah mengatakan yang sebenarnya dan Dia menunjukkan jalan (yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,5,'اُدْعُوْهُمْ لِاٰبَاۤىِٕهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِ ۚ فَاِنْ لَّمْ تَعْلَمُوْٓا اٰبَاۤءَهُمْ فَاِخْوَانُكُمْ فِى الدِّيْنِ وَمَوَالِيْكُمْ ۗوَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيْمَآ اَخْطَأْتُمْ بِهٖ وَلٰكِنْ مَّا تَعَمَّدَتْ قُلُوْبُكُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا','Panggilah mereka (anak-anak angkat itu) dengan (memakai) nama bapak-bapak mereka; itulah yang lebih adil pada sisi Allah, dan jika kamu tidak mengetahui bapak-bapak mereka, maka (panggilah mereka sebagai) saudara-saudaramu seagama dan maula-maulamu. Dan tidak ada dosa atasmu terhadap apa yang kamu khilaf padanya, tetapi (yang ada dosanya) apa yang disengaja oleh hatimu. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,6,'اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِنِيْنَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهٗٓ اُمَّهٰتُهُمْ ۗوَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ مِنَ الْمُؤْمِنِيْنَ وَالْمُهٰجِرِيْنَ اِلَّآ اَنْ تَفْعَلُوْٓا اِلٰٓى اَوْلِيَاۤىِٕكُمْ مَّعْرُوْفًا ۗ كَانَ ذٰلِكَ فِى الْكِتٰبِ مَسْطُوْرًا','Nabi itu (hendaknya) lebih utama bagi orang-orang mukmin dari diri mereka sendiri dan isteri-isterinya adalah ibu-ibu mereka. Dan orang-orang yang mempunyai hubungan darah satu sama lain lebih berhak (waris-mewarisi) di dalam Kitab Allah daripada orang-orang mukmim dan orang-orang Muhajirin, kecuali kalau kamu berbuat baik kepada saudara-saudaramu (seagama). Adalah yang demikian itu telah tertulis di dalam Kitab (Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,7,'وَاِذْ اَخَذْنَا مِنَ النَّبِيّٖنَ مِيْثَاقَهُمْ وَمِنْكَ وَمِنْ نُّوْحٍ وَّاِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسَى ابْنِ مَرْيَمَ ۖوَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًاۙ','Dan (ingatlah) ketika Kami mengambil perjanjian dari nabi-nabi dan dari kamu (sendiri) dari Nuh, Ibrahim, Musa dan Isa putra Maryam, dan Kami telah mengambil dari mereka perjanjian yang teguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,8,'لِّيَسْـَٔلَ الصّٰدِقِيْنَ عَنْ صِدْقِهِمْ ۚوَاَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا اَلِيْمًا ࣖ','agar Dia menanyakan kepada orang-orang yang benar tentang kebenaran mereka dan Dia menyediakan bagi orang-orang kafir siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,9,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَاۤءَتْكُمْ جُنُوْدٌ فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا وَّجُنُوْدًا لَّمْ تَرَوْهَا ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًاۚ ','Hai orang-orang yang beriman, ingatlah akan nikmat Allah (yang telah dikurniakan) kepadamu ketika datang kepadamu tentara-tentara, lalu Kami kirimkan kepada mereka angin topan dan tentara yang tidak dapat kamu melihatnya. Dan adalah Allah Maha Melihat akan apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,10,'اِذْ جَاۤءُوْكُمْ مِّنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوْبُ الْحَنَاجِرَ وَتَظُنُّوْنَ بِاللّٰهِ الظُّنُوْنَا۠   ۗ','(Yaitu) ketika mereka datang kepadamu dari atas dan dari bawahmu, dan ketika tidak tetap lagi penglihatan(mu) dan hatimu naik menyesak sampai ke tenggorokan dan kamu menyangka terhadap Allah dengan bermacam-macam purbasangka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,11,'هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُوْنَ وَزُلْزِلُوْا زِلْزَالًا شَدِيْدًا','Disitulah diuji orang-orang mukmin dan digoncangkan (hatinya) dengan goncangan yang sangat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,12,'وَاِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ مَّا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗٓ اِلَّا غُرُوْرًا','Dan (ingatlah) ketika orang-orang munafik dan orang-orang yang berpenyakit dalam hatinya berkata: "Allah dan Rasul-Nya tidak menjanjikan kepada kami melainkan tipu daya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,13,'وَاِذْ قَالَتْ طَّاۤىِٕفَةٌ مِّنْهُمْ يٰٓاَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوْا ۚوَيَسْتَأْذِنُ فَرِيْقٌ مِّنْهُمُ النَّبِيَّ يَقُوْلُوْنَ اِنَّ بُيُوْتَنَا عَوْرَةٌ  ۗوَمَا هِيَ بِعَوْرَةٍ ۗاِنْ يُّرِيْدُوْنَ اِلَّا فِرَارًا','Dan (ingatlah) ketika segolongan di antara mreka berkata: "Hai penduduk Yatsrib (Madinah), tidak ada tempat bagimu, maka kembalilah kamu". Dan sebahagian dari mereka minta izin kepada Nabi (untuk kembali pulang) dengan berkata: "Sesungguhnya rumah-rumah kami terbuka (tidak ada penjaga)". Dan rumah-rumah itu sekali-kali tidak terbuka, mereka tidak lain hanya hendak lari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,14,'وَلَوْ دُخِلَتْ عَلَيْهِمْ مِّنْ اَقْطَارِهَا ثُمَّ سُـِٕلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوْا بِهَآ اِلَّا يَسِيْرًا','Kalau (Yatsrib) diserang dari segala penjuru, kemudian diminta kepada mereka supaya murtad, niscaya mereka mengerjakannya; dan mereka tiada akan bertangguh untuk murtad itu melainkan dalam waktu yang singkat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,15,'وَلَقَدْ كَانُوْا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّوْنَ الْاَدْبَارَ ۗوَكَانَ عَهْدُ اللّٰهِ مَسْـُٔوْلًا','Dan sesungguhnya mereka sebelum itu telah berjanji kepada Allah: "Mereka tidak akan berbalik ke belakang (mundur)". Dan adalah perjanjian dengan Allah akan diminta pertanggungan jawabnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,16,'قُلْ لَّنْ يَّنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِّنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذًا لَّا تُمَتَّعُوْنَ اِلَّا قَلِيْلًا ','Katakanlah: "Lari itu sekali-kali tidaklah berguna bagimu, jika kamu melarikan diri dari kematian atau pembunuhan, dan jika (kamu terhindar dari kematian) kamu tidak juga akan mengecap kesenangan kecuali sebentar saja".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,17,'قُلْ مَنْ ذَا الَّذِيْ يَعْصِمُكُمْ مِّنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُوْۤءًا اَوْ اَرَادَ بِكُمْ رَحْمَةً ۗوَلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا','Katakanlah: "Siapakah yang dapat melindungi kamu dari (takdir) Allah jika Dia menghendaki bencana atasmu atau menghendaki rahmat untuk dirimu?" Dan orang-orang munafik itu tidak memperoleh bagi mereka pelindung dan penolong selain Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,18,'۞ قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّقِيْنَ مِنْكُمْ وَالْقَاۤىِٕلِيْنَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَا ۚوَلَا يَأْتُوْنَ الْبَأْسَ اِلَّا قَلِيْلًاۙ','Sesungguhnya Allah mengetahui orang-orang yang menghalang-halangi di antara kamu dan orang-orang yang berkata kepada saudara-saudaranya: "Marilah kepada kami". Dan mereka tidak mendatangi peperangan melainkan sebentar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,19,'اَشِحَّةً عَلَيْكُمْ ۖ فَاِذَا جَاۤءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُوْنَ اِلَيْكَ تَدُوْرُ اَعْيُنُهُمْ كَالَّذِيْ يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوْكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۗ اُولٰۤىِٕكَ لَمْ يُؤْمِنُوْا فَاَحْبَطَ اللّٰهُ اَعْمَالَهُمْۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا','Mereka bakhil terhadapmu, apabila datang ketakutan (bahaya), kamu lihat mereka itu memandang kepadamu dengan mata yang terbalik-balik seperti orang yang pingsan karena akan mati, dan apabila ketakutan telah hilang, mereka mencaci kamu dengan lidah yang tajam, sedang mereka bakhil untuk berbuat kebaikan. Mereka itu tidak beriman, maka Allah menghapuskan (pahala) amalnya. Dan yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,20,'يَحْسَبُوْنَ الْاَحْزَابَ لَمْ يَذْهَبُوْا ۚوَاِنْ يَّأْتِ الْاَحْزَابُ يَوَدُّوْا لَوْ اَنَّهُمْ بَادُوْنَ فِى الْاَعْرَابِ يَسْاَلُوْنَ عَنْ اَنْۢبَاۤىِٕكُمْ ۖوَلَوْ كَانُوْا فِيْكُمْ مَّا قٰتَلُوْٓا اِلَّا قَلِيْلًا ࣖ','Mereka mengira (bahwa) golongan-golongan yang bersekutu itu belum pergi; dan jika golongan-golongan yang bersekutu itu datang kembali, niscaya mereka ingin berada di dusun-dusun bersama-sama orang Arab Badwi, sambil menanya-nanyakan tentang berita-beritamu. Dan sekiranya mereka berada bersama kamu, mereka tidak akan berperang, melainkan sebentar saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,21,'لَقَدْ كَانَ لَكُمْ فِيْ رَسُوْلِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَثِيْرًاۗ','Sesungguhnya telah ada pada (diri) Rasulullah itu suri teladan yang baik bagimu (yaitu) bagi orang yang mengharap (rahmat) Allah dan (kedatangan) hari kiamat dan dia banyak menyebut Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,22,'وَلَمَّا رَاَ الْمُؤْمِنُوْنَ الْاَحْزَابَۙ قَالُوْا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗ وَصَدَقَ اللّٰهُ وَرَسُوْلُهٗ  ۖوَمَا زَادَهُمْ اِلَّآ اِيْمَانًا وَّتَسْلِيْمًاۗ ','Dan tatkala orang-orang mukmin melihat golongan-golongan yang bersekutu itu, mereka berkata: "Inilah yang dijanjikan Allah dan Rasul-Nya kepada kita". Dan benarlah Allah dan Rasul-Nya. Dan yang demikian itu tidaklah menambah kepada mereka kecuali iman dan ketundukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,23,'مِنَ الْمُؤْمِنِيْنَ رِجَالٌ صَدَقُوْا مَا عَاهَدُوا اللّٰهَ عَلَيْهِ ۚ فَمِنْهُمْ مَّنْ قَضٰى نَحْبَهٗۙ وَمِنْهُمْ مَّنْ يَّنْتَظِرُ ۖوَمَا بَدَّلُوْا تَبْدِيْلًاۙ ','Di antara orang-orang mukmin itu ada orang-orang yang menepati apa yang telah mereka janjikan kepada Allah; maka di antara mereka ada yang gugur. Dan di antara mereka ada (pula) yang menunggu-nunggu dan mereka tidak merubah (janjinya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,24,'لِيَجْزِيَ اللّٰهُ الصّٰدِقِيْنَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنٰفِقِيْنَ اِنْ شَاۤءَ اَوْ يَتُوْبَ عَلَيْهِمْ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ','supaya Allah memberikan balasan kepada orang-orang yang benar itu karena kebenarannya, dan menyiksa orang munafik jika dikehendaki-Nya, atau menerima taubat mereka. Sesungguhnya Allah adalah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,25,'وَرَدَّ اللّٰهُ الَّذِيْنَ كَفَرُوْا بِغَيْظِهِمْ لَمْ يَنَالُوْا خَيْرًا ۗوَكَفَى اللّٰهُ الْمُؤْمِنِيْنَ الْقِتَالَ ۗوَكَانَ اللّٰهُ قَوِيًّا عَزِيْزًاۚ ','Dan Allah menghalau orang-orang yang kafir itu yang keadaan mereka penuh kejengkelan, (lagi) mereka tidak memperoleh keuntungan apapun. Dan Allah menghindarkan orang-orang mukmin dari peperangan. Dan adalah Allah Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,26,'وَاَنْزَلَ الَّذِيْنَ ظَاهَرُوْهُمْ مِّنْ اَهْلِ الْكِتٰبِ مِنْ صَيَاصِيْهِمْ وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ فَرِيْقًا تَقْتُلُوْنَ وَتَأْسِرُوْنَ فَرِيْقًاۚ ','Dan Dia menurunkan orang-orang Ahli Kitab (Bani Quraizhah) yang membantu golongan-golongan yang bersekutu dari benteng-benteng mereka, dan Dia memesukkan rasa takut ke dalam hati mereka. Sebahagian mereka kamu bunuh dan sebahagian yang lain kamu tawan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,27,'وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضًا لَّمْ تَطَـُٔوْهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا ࣖ ','Dan Dia mewariskan kepada kamu tanah-tanah, rumah-rumah dan harta benda mereka, dan (begitu pula) tanah yang belum kamu injak. Dan adalah Allah Maha Kuasa terhadap segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,28,'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحًا جَمِيْلًا ','Hai Nabi, katakanlah kepada isteri-isterimu: "Jika kamu sekalian mengingini kehidupan dunia dan perhiasannya, maka marilah supaya kuberikan kepadamu mut´ah dan aku ceraikan kamu dengan cara yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,29,'وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُوْلَهٗ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنٰتِ مِنْكُنَّ اَجْرًا عَظِيْمًا','Dan jika kamu sekalian menghendaki (keridhaan) Allah dan Rasulnya-Nya serta (kesenangan) di negeri akhirat, maka sesungguhnya Allah menyediakan bagi siapa yang berbuat baik diantaramu pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,21,33,30,'يٰنِسَاۤءَ النَّبِيِّ مَنْ يَّأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُّبَيِّنَةٍ يُّضٰعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا   ۔','Hai isteri-isteri Nabi, siapa-siapa di antaramu yang mengerjakan perbuatan keji yang nyata, niscaya akan di lipat gandakan siksaan kepada mereka dua kali lipat. Dan adalah yang demikian itu mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,31,'۞ وَمَنْ يَّقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُوْلِهٖ وَتَعْمَلْ صَالِحًا نُّؤْتِهَآ اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقًا كَرِيْمًا ','Dan barang siapa diantara kamu sekalian (isteri-isteri nabi) tetap taat kepada Allah dan Rasul-Nya dan mengerjakan amal yang saleh, niscata Kami memberikan kepadanya pahala dua kali lipat dan Kami sediakan baginya rezeki yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,32,'يٰنِسَاۤءَ النَّبِيِّ لَسْتُنَّ كَاَحَدٍ مِّنَ النِّسَاۤءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِيْ فِيْ قَلْبِهٖ مَرَضٌ وَّقُلْنَ قَوْلًا مَّعْرُوْفًاۚ ','Hai isteri-isteri Nabi, kamu sekalian tidaklah seperti wanita yang lain, jika kamu bertakwa. Maka janganlah kamu tunduk dalam berbicara sehingga berkeinginanlah orang yang ada penyakit dalam hatinya dan ucapkanlah perkataan yang baik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,33,'وَقَرْنَ فِيْ بُيُوْتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُوْلٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰتِيْنَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيْرًاۚ ','dan hendaklah kamu tetap di rumahmu dan janganlah kamu berhias dan bertingkah laku seperti orang-orang Jahiliyah yang dahulu dan dirikanlah shalat, tunaikanlah zakat dan taatilah Allah dan Rasul-Nya. Sesungguhnya Allah bermaksud hendak menghilangkan dosa dari kamu, hai ahlul bait dan membersihkan kamu sebersih-bersihnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,34,'وَاذْكُرْنَ مَا يُتْلٰى فِيْ بُيُوْتِكُنَّ مِنْ اٰيٰتِ اللّٰهِ وَالْحِكْمَةِۗ اِنَّ اللّٰهَ كَانَ لَطِيْفًا خَبِيْرًا ࣖ ','Dan ingatlah apa yang dibacakan di rumahmu dari ayat-ayat Allah dan hikmah (sunnah nabimu). Sesungguhnya Allah adalah Maha Lembut lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,35,'اِنَّ الْمُسْلِمِيْنَ وَالْمُسْلِمٰتِ وَالْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ وَالْقٰنِتِيْنَ وَالْقٰنِتٰتِ وَالصّٰدِقِيْنَ وَالصّٰدِقٰتِ وَالصّٰبِرِيْنَ وَالصّٰبِرٰتِ وَالْخٰشِعِيْنَ وَالْخٰشِعٰتِ وَالْمُتَصَدِّقِيْنَ وَالْمُتَصَدِّقٰتِ وَالصَّاۤىِٕمِيْنَ وَالصّٰۤىِٕمٰتِ وَالْحٰفِظِيْنَ فُرُوْجَهُمْ وَالْحٰفِظٰتِ وَالذَّاكِرِيْنَ اللّٰهَ كَثِيْرًا وَّالذَّاكِرٰتِ اَعَدَّ اللّٰهُ لَهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا ','Sesungguhnya laki-laki dan perempuan yang muslim, laki-laki dan perempuan yang mukmin, laki-laki dan perempuan yang tetap dalam ketaatannya, laki-laki dan perempuan yang benar, laki-laki dan perempuan yang sabar, laki-laki dan perempuan yang khusyu´, laki-laki dan perempuan yang bersedekah, laki-laki dan perempuan yang berpuasa, laki-laki dan perempuan yang memelihara kehormatannya, laki-laki dan perempuan yang banyak menyebut (nama) Allah, Allah telah menyediakan untuk mereka ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,36,'وَمَا كَانَ لِمُؤْمِنٍ وَّلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُوْلُهٗٓ اَمْرًا اَنْ يَّكُوْنَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْ ۗوَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ ضَلَّ ضَلٰلًا مُّبِيْنًاۗ  ','Dan tidaklah patut bagi laki-laki yang mukmin dan tidak (pula) bagi perempuan yang mukmin, apabila Allah dan Rasul-Nya telah menetapkan suatu ketetapan, akan ada bagi mereka pilihan (yang lain) tentang urusan mereka. Dan barangsiapa mendurhakai Allah dan Rasul-Nya maka sungguhlah dia telah sesat, sesat yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,37,'وَاِذْ تَقُوْلُ لِلَّذِيْٓ اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْفِيْ فِيْ نَفْسِكَ مَا اللّٰهُ مُبْدِيْهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰىهُ ۗ فَلَمَّا قَضٰى زَيْدٌ مِّنْهَا وَطَرًاۗ زَوَّجْنٰكَهَا لِكَيْ لَا يَكُوْنَ عَلَى الْمُؤْمِنِيْنَ حَرَجٌ فِيْٓ اَزْوَاجِ اَدْعِيَاۤىِٕهِمْ اِذَا قَضَوْا مِنْهُنَّ وَطَرًاۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا ','Dan (ingatlah), ketika kamu berkata kepada orang yang Allah telah melimpahkan nikmat kepadanya dan kamu (juga) telah memberi nikmat kepadanya: "Tahanlah terus isterimu dan bertakwalah kepada Allah", sedang kamu menyembunyikan di dalam hatimu apa yang Allah akan menyatakannya, dan kamu takut kepada manusia, sedang Allah-lah yang lebih berhak untuk kamu takuti. Maka tatkala Zaid telah mengakhiri keperluan terhadap istrinya (menceraikannya), Kami kawinkan kamu dengan dia supaya tidak ada keberatan bagi orang mukmin untuk (mengawini) isteri-isteri anak-anak angkat mereka, apabila anak-anak angkat itu telah menyelesaikan keperluannya daripada isterinya. Dan adalah ketetapan Allah itu pasti terjadi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,38,'مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيْمَا فَرَضَ اللّٰهُ لَهٗ ۗسُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۗوَكَانَ اَمْرُ اللّٰهِ قَدَرًا مَّقْدُوْرًاۙ  ','Tidak ada suatu keberatanpun atas Nabi tentang apa yang telah ditetapkan Allah baginya. (Allah telah menetapkan yang demikian) sebagai sunnah-Nya pada nabi-nabi yang telah berlalu dahulu. Dan adalah ketetapan Allah itu suatu ketetapan yang pasti berlaku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,39,' ۨالَّذِيْنَ يُبَلِّغُوْنَ رِسٰلٰتِ اللّٰهِ وَيَخْشَوْنَهٗ وَلَا يَخْشَوْنَ اَحَدًا اِلَّا اللّٰهَ ۗوَكَفٰى بِاللّٰهِ حَسِيْبًا ','(yaitu) orang-orang yang menyapaikan risalah-risalah Allah, mereka takut kepada-Nya dan mereka tiada merasa takut kepada seorang(pun) selain kepada Allah. Dan cukuplah Allah sebagai Pembuat Perhitungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,40,'مَا كَانَ مُحَمَّدٌ اَبَآ اَحَدٍ مِّنْ رِّجَالِكُمْ وَلٰكِنْ رَّسُوْلَ اللّٰهِ وَخَاتَمَ النَّبِيّٖنَۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا ࣖ','Muhammad itu sekali-kali bukanlah bapak dari seorang laki-laki di antara kamu, tetapi dia adalah Rasulullah dan penutup nabi-nabi. Dan adalah Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,41,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْرًا كَثِيْرًاۙ ','Hai orang-orang yang beriman, berzdikirlah (dengan menyebut nama) Allah, zikir yang sebanyak-banyaknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,42,'وَّسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا ','Dan bertasbihlah kepada-Nya diwaktu pagi dan petang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,43,'هُوَ الَّذِيْ يُصَلِّيْ عَلَيْكُمْ وَمَلٰۤىِٕكَتُهٗ لِيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَكَانَ بِالْمُؤْمِنِيْنَ رَحِيْمًا ','Dialah yang memberi rahmat kepadamu dan malaikat-Nya (memohonkan ampunan untukmu), supaya Dia mengeluarkan kamu dari kegelapan kepada cahaya (yang terang). Dan adalah Dia Maha Penyayang kepada orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,44,'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهٗ سَلٰمٌ ۚوَاَعَدَّ لَهُمْ اَجْرًا كَرِيْمًا ','Salam penghormatan kepada mereka (orang-orang mukmin itu) pada hari mereka menemui-Nya ialah: Salam; dan Dia menyediakan pahala yang mulia bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,45,'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ ','Hai Nabi, sesungguhnya Kami mengutusmu untuk jadi saksi, dan pembawa kabar gemgira dan pemberi peringatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,46,'وَّدَاعِيًا اِلَى اللّٰهِ بِاِذْنِهٖ وَسِرَاجًا مُّنِيْرًا ','dan untuk jadi penyeru kepada Agama Allah dengan izin-Nya dan untuk jadi cahaya yang menerangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,47,'وَبَشِّرِ الْمُؤْمِنِيْنَ بِاَنَّ لَهُمْ مِّنَ اللّٰهِ فَضْلًا كَبِيْرًا ','Dan sampaikanlah berita gembira kepada orang-orang mukmin bahwa sesungguhnya bagi mereka karunia yang besar dari Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,48,'وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ وَدَعْ اَذٰىهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ','Dan janganlah kamu menuruti orang-orang yang kafir dan orang-orang munafik itu, janganlah kamu hiraukan gangguan mereka dan bertawakkallah kepada Allah. Dan cukuplah Allah sebagai Pelindung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,49,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَكَحْتُمُ الْمُؤْمِنٰتِ ثُمَّ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّوْنَهَاۚ فَمَتِّعُوْهُنَّ وَسَرِّحُوْهُنَّ سَرَاحًا جَمِيْلًا ','Hai orang-orang yang beriman, apabila kamu menikahi perempuan-perempuan yang beriman, kemudian kamu ceraikan mereka sebelum kamu mencampurinya maka sekali-sekali tidak wajib atas mereka ´iddah bagimu yang kamu minta menyempurnakannya. Maka berilah mereka mut´ah dan lepaskanlah mereka itu dengan cara yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,50,'يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰتِيْٓ اٰتَيْتَ اُجُوْرَهُنَّ وَمَا مَلَكَتْ يَمِيْنُكَ مِمَّآ اَفَاۤءَ اللّٰهُ عَلَيْكَ وَبَنٰتِ عَمِّكَ وَبَنٰتِ عَمّٰتِكَ وَبَنٰتِ خَالِكَ وَبَنٰتِ خٰلٰتِكَ الّٰتِيْ هَاجَرْنَ مَعَكَۗ وَامْرَاَةً مُّؤْمِنَةً اِنْ وَّهَبَتْ نَفْسَهَا لِلنَّبِيِّ اِنْ اَرَادَ النَّبِيُّ اَنْ يَّسْتَنْكِحَهَا خَالِصَةً لَّكَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِيْٓ اَزْوَاجِهِمْ وَمَا مَلَكَتْ اَيْمَانُهُمْ لِكَيْلَا يَكُوْنَ عَلَيْكَ حَرَجٌۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ','Hai Nabi, sesungguhnya Kami telah menghalalkan bagimu isteri-isterimu yang telah kamu berikan mas kawinnya dan hamba sahaya yang kamu miliki yang termasuk apa yang kamu peroleh dalam peperangan yang dikaruniakan Allah untukmu, dan (demikian pula) anak-anak perempuan dari saudara laki-laki bapakmu, anak-anak perempuan dari saudara perempuan bapakmu, anak-anak perempuan dari saudara laki-laki ibumu dan anak-anak perempuan dari saudara perempuan ibumu yang turut hijrah bersama kamu dan perempuan mukmin yang menyerahkan dirinya kepada Nabi kalau Nabi mau mengawininya, sebagai pengkhususan bagimu, bukan untuk semua orang mukmin. Sesungguhnya Kami telah mengetahui apa yang Kami wajibkan kepada mereka tentang isteri-isteri mereka dan hamba sahaya yang mereka miliki supaya tidak menjadi kesempitan bagimu. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,51,'۞ تُرْجِيْ مَنْ تَشَاۤءُ مِنْهُنَّ وَتُـْٔوِيْٓ اِلَيْكَ مَنْ تَشَاۤءُۗ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۗ ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ اٰتَيْتَهُنَّ كُلُّهُنَّۗ وَاللّٰهُ يَعْلَمُ مَا فِيْ قُلُوْبِكُمْ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَلِيْمًا ','Kamu boleh menangguhkan menggauli siapa yang kamu kehendaki di antara mereka (isteri-isterimu) dan (boleh pula) menggauli siapa yang kamu kehendaki. Dan siapa-siapa yang kamu ingini untuk menggaulinya kembali dari perempuan yang telah kamu cerai, maka tidak ada dosa bagimu. Yang demikian itu adalah lebih dekat untuk ketenangan hati mereka, dan mereka tidak merasa sedih, dan semuanya rela dengan apa yang telah kamu berikan kepada mereka. Dan Allah mengetahui apa yang (tersimpan) dalam hatimu. Dan adalah Allah Maha Mengetahui lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,52,'لَا يَحِلُّ لَكَ النِّسَاۤءُ مِنْۢ بَعْدُ وَلَآ اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَّلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَمِيْنُكَۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَّقِيْبًا ࣖ ','Tidak halal bagimu mengawini perempuan-perempuan sesudah itu dan tidak boleh (pula) mengganti mereka dengan isteri-isteri (yang lain), meskipun kecantikannya menarik hatimu kecuali perempuan-perempuan (hamba sahaya) yang kamu miliki. Dan adalah Allah Maha Mengawasi segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,53,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتَ النَّبِيِّ اِلَّآ اَنْ يُّؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نٰظِرِيْنَ اِنٰىهُ وَلٰكِنْ اِذَا دُعِيْتُمْ فَادْخُلُوْا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوْا وَلَا مُسْتَأْنِسِيْنَ لِحَدِيْثٍۗ اِنَّ ذٰلِكُمْ كَانَ يُؤْذِى النَّبِيَّ فَيَسْتَحْيٖ مِنْكُمْ ۖوَاللّٰهُ لَا يَسْتَحْيٖ مِنَ الْحَقِّۗ وَاِذَا سَاَلْتُمُوْهُنَّ مَتَاعًا فَاسْـَٔلُوْهُنَّ مِنْ وَّرَاۤءِ حِجَابٍۗ ذٰلِكُمْ اَطْهَرُ لِقُلُوْبِكُمْ وَقُلُوْبِهِنَّۗ وَمَا كَانَ لَكُمْ اَنْ تُؤْذُوْا رَسُوْلَ اللّٰهِ وَلَآ اَنْ تَنْكِحُوْٓا اَزْوَاجَهٗ مِنْۢ بَعْدِهٖٓ اَبَدًاۗ اِنَّ ذٰلِكُمْ كَانَ عِنْدَ اللّٰهِ عَظِيْمًا ','Hai orang-orang yang beriman, janganlah kamu memasuki rumah-rumah Nabi kecuali bila kamu diizinkan untuk makan dengan tidak menunggu-nunggu waktu masak (makanannya), tetapi jika kamu diundang maka masuklah dan bila kamu selesai makan, keluarlah kamu tanpa asyik memperpanjang percakapan. Sesungguhnya yang demikian itu akan mengganggu Nabi lalu Nabi malu kepadamu (untuk menyuruh kamu keluar), dan Allah tidak malu (menerangkan) yang benar. Apabila kamu meminta sesuatu (keperluan) kepada mereka (isteri-isteri Nabi), maka mintalah dari belakang tabir. Cara yang demikian itu lebih suci bagi hatimu dan hati mereka. Dan tidak boleh kamu menyakiti (hati) Rasulullah dan tidak (pula) mengawini isteri-isterinya selama-lamanya sesudah ia wafat. Sesungguhnya perbuatan itu adalah amat besar (dosanya) di sisi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,54,'اِنْ تُبْدُوْا شَيْـًٔا اَوْ تُخْفُوْهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ','Jika kamu melahirkan sesuatu atau menyembunyikannya, maka sesungguhnya Allah adalah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,55,'لَا جُنَاحَ عَلَيْهِنَّ فِيْٓ اٰبَاۤىِٕهِنَّ وَلَآ اَبْنَاۤىِٕهِنَّ وَلَآ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اَخَوٰتِهِنَّ وَلَا نِسَاۤىِٕهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّقِيْنَ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا ','Tidak ada dosa atas isteri-isteri Nabi (untuk berjumpa tanpa tabir) dengan bapak-bapak mereka, anak-anak laki-laki mereka, saudara laki-laki mereka, anak laki-laki dari saudara laki-laki mereka, anak laki-laki dari saudara mereka yang perempuan yang beriman dan hamba sahaya yang mereka miliki, dan bertakwalah kamu (hai isteri-isteri Nabi) kepada Allah. Sesungguhnya Allah Maha Menyaksikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,56,'اِنَّ اللّٰهَ وَمَلٰۤىِٕكَتَهٗ يُصَلُّوْنَ عَلَى النَّبِيِّۗ  يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا صَلُّوْا عَلَيْهِ وَسَلِّمُوْا تَسْلِيْمًا ','Sesungguhnya Allah dan malaikat-malaikat-Nya bershalawat untuk Nabi. Hai orang-orang yang beriman, bershalawatlah kamu untuk Nabi dan ucapkanlah salam penghormatan kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,57,'اِنَّ الَّذِيْنَ يُؤْذُوْنَ اللّٰهَ وَرَسُوْلَهٗ لَعَنَهُمُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَابًا مُّهِيْنًا ','Sesungguhnya orang-orang yang menyakiti Allah dan Rasul-Nya. Allah akan melaknatinya di dunia dan di akhirat, dan menyediakan baginya siksa yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,58,'وَالَّذِيْنَ يُؤْذُوْنَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ بِغَيْرِ مَا اكْتَسَبُوْا فَقَدِ احْتَمَلُوْا بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ࣖ ','Dan orang-orang yang menyakiti orang-orang yang mukmin dan mukminat tanpa kesalahan yang mereka perbuat, maka sesungguhnya mereka telah memikul kebohongan dan dosa yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,59,'يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ وَبَنٰتِكَ وَنِسَاۤءِ الْمُؤْمِنِيْنَ يُدْنِيْنَ عَلَيْهِنَّ مِنْ جَلَابِيْبِهِنَّۗ ذٰلِكَ اَدْنٰىٓ اَنْ يُّعْرَفْنَ فَلَا يُؤْذَيْنَۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ','Hai Nabi, katakanlah kepada isteri-isterimu, anak-anak perempuanmu dan isteri-isteri orang mukmin: "Hendaklah mereka mengulurkan jilbabnya ke seluruh tubuh mereka". Yang demikian itu supaya mereka lebih mudah untuk dikenal, karena itu mereka tidak di ganggu. Dan Allah adalah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,60,'۞ لَىِٕنْ لَّمْ يَنْتَهِ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْمُرْجِفُوْنَ فِى الْمَدِيْنَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُوْنَكَ فِيْهَآ اِلَّا قَلِيْلًا ','Sesungguhnya jika tidak berhenti orang-orang munafik, orang-orang yang berpenyakit dalam hatinya dan orang-orang yang menyebarkan kabar bohong di Madinah (dari menyakitimu), niscaya Kami perintahkan kamu (untuk memerangi) mereka, kemudian mereka tidak menjadi tetanggamu (di Madinah) melainkan dalam waktu yang sebentar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,61,'مَلْعُوْنِيْنَۖ اَيْنَمَا ثُقِفُوْٓا اُخِذُوْا وَقُتِّلُوْا تَقْتِيْلًا ','dalam keadaan terlaknat. Di mana saja mereka dijumpai, mereka ditangkap dan dibunuh dengan sehebat-hebatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,62,'سُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۚوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا ','Sebagai sunnah Allah yang berlaku atas orang-orang yang telah terdahulu sebelum(mu), dan kamu sekali-kali tiada akan mendapati peubahan pada sunnah Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,63,'يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ تَكُوْنُ قَرِيْبًا ','Manusia bertanya kepadamu tentang hari berbangkit. Katakanlah: "Sesungguhnya pengetahuan tentang hari berbangkit itu hanya di sisi Allah". Dan tahukah kamu (hai Muhammad), boleh jadi hari berbangkit itu sudah dekat waktunya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,64,'اِنَّ اللّٰهَ لَعَنَ الْكٰفِرِيْنَ وَاَعَدَّ لَهُمْ سَعِيْرًاۙ ','Sesungguhnya Allah melaknati orang-orang kafir dan menyediakan bagi mereka api yang menyala-nyala (neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,65,'خٰلِدِيْنَ فِيْهَآ اَبَدًاۚ  لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ۚ','mereka kekal di dalamnya selama-lamanya; mereka tidak memperoleh seorang pelindungpun dan tidak (pula) seorang penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,66,'يَوْمَ تُقَلَّبُ وُجُوْهُهُمْ فِى النَّارِ يَقُوْلُوْنَ يٰلَيْتَنَآ اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُوْلَا۠ ','Pada hari ketika muka mereka dibolak-balikan dalam neraka, mereka berkata: "Alangkah baiknya, andaikata kami taat kepada Allah dan taat (pula) kepada Rasul".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,67,'وَقَالُوْا رَبَّنَآ اِنَّآ اَطَعْنَا سَادَتَنَا وَكُبَرَاۤءَنَا فَاَضَلُّوْنَا السَّبِيْلَا۠ ','Dan mereka berkata: "Ya Tuhan kami, sesungguhnya kami telah mentaati pemimpin-pemimpin dan pembesar-pembesar kami, lalu mereka menyesatkan kami dari jalan (yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,68,'رَبَّنَآ اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيْرًا ࣖ ','Ya Tuhan kami, timpakanlah kepada mereka azab dua kali lipat dan kutuklah mereka dengan kutukan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,69,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ اٰذَوْا مُوْسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُوْا ۗوَكَانَ عِنْدَ اللّٰهِ وَجِيْهًا ۗ ','Hai orang-orang yang beriman, janganlah kamu menjadi seperti orang-orang yang menyakiti Musa; maka Allah membersihkannya dari tuduhan-tuduhan yang mereka katakan. Dan adalah dia seorang yang mempunyai kedudukan terhormat di sisi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,70,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُوْلُوْا قَوْلًا سَدِيْدًاۙ ','Hai orang-orang yang beriman, bertakwalah kamu kepada Allah dan katakanlah perkataan yang benar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,71,'يُّصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ فَازَ فَوْزًا عَظِيْمًا ','niscaya Allah memperbaiki bagimu amalan-amalanmu dan mengampuni bagimu dosa-dosamu. Dan barangsiapa mentaati Allah dan Rasul-Nya, maka sesungguhnya ia telah mendapat kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,72,'اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوٰتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَّحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۗ اِنَّهٗ كَانَ ظَلُوْمًا جَهُوْلًاۙ ','Sesungguhnya Kami telah mengemukakan amanat kepada langit, bumi dan gunung-gunung, maka semuanya enggan untuk memikul amanat itu dan mereka khawatir akan mengkhianatinya, dan dipikullah amanat itu oleh manusia. Sesungguhnya manusia itu amat zalim dan amat bodoh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,33,73,'لِّيُعَذِّبَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقَتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ وَيَتُوْبَ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ࣖ ','sehingga Allah mengazab orang-orang munafik laki-laki dan perempuan dan orang-orang musyrikin laki-laki dan perempuan; dan sehingga Allah menerima taubat orang-orang mukmin laki-laki dan perempuan. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,1,'اَلْحَمْدُ لِلّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَلَهُ الْحَمْدُ فِى الْاٰخِرَةِۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ','Segala puji bagi Allah yang memiliki apa yang di langit dan apa yang di bumi dan bagi-Nya (pula) segala puji di akhirat. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,2,'يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ الرَّحِيْمُ الْغَفُوْرُ','Dia mengetahui apa yang masuk ke dalam bumi, apa yang ke luar daripadanya, apa yang turun dari langit dan apa yang naik kepadanya. Dan Dialah Yang Maha Penyayang lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,3,'وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَأْتِيْنَا السَّاعَةُ ۗقُلْ بَلٰى وَرَبِّيْ لَتَأْتِيَنَّكُمْۙ عٰلِمِ الْغَيْبِۙ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَلَآ اَصْغَرُ مِنْ ذٰلِكَ وَلَآ اَكْبَرُ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍۙ  ','Dan orang-orang yang kafir berkata: "Hari berbangkit itu tidak akan datang kepada kami". Katakanlah: "Pasti datang, demi Tuhanku Yang Mengetahui yang ghaib, sesungguhnya kiamat itu pasti akan datang kepadamu. Tidak ada tersembunyi daripada-Nya sebesar zarrahpun yang ada di langit dan yang ada di bumi dan tidak ada (pula) yang lebih kecil dari itu dan yang lebih besar, melainkan tersebut dalam Kitab yang nyata (Lauh Mahfuzh)",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,4,'لِّيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ','supaya Allah memberi balasan kepada orang-orang yang beriman dan mengerjakan amal yang saleh. Mereka itu adalah orang-orang yang baginya ampunan dan rezeki yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,5,'وَالَّذِيْنَ سَعَوْ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ ','Dan orang-orang yang berusaha untuk (menentang) ayat-ayat Kami dengan anggapan mereka dapat melemahkan (menggagalkan azab Kami), mereka itu memperoleh azab, yaitu (jenis) azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,6,'وَيَرَى الَّذِيْنَ اُوْتُوا الْعِلْمَ الَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ هُوَ الْحَقَّۙ وَيَهْدِيْٓ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِ ','Dan orang-orang yang diberi ilmu (Ahli Kitab) berpendapat bahwa wahyu yang diturunkan kepadamu dari Tuhanmu itulah yang benar dan menunjuki (manusia) kepada jalan Tuhan Yang Maha Perkasa lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,7,'وَقَالَ الَّذِيْنَ كَفَرُوْا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُّنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَفِيْ خَلْقٍ جَدِيْدٍۚ ','Dan orang-orang kafir berkata (kepada teman-temannya). "Maukah kamu kami tunjukkan kepadamu seorang laki-laki yang memberitakan kepadamu bahwa apabila badanmu telah hancur sehancur-hancurnya, sesungguhnya kamu benar-benar (akan dibangkitkan kembali) dalam ciptaan yang baru?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,8,'اَفْتَرٰى عَلَى اللّٰهِ كَذِبًا اَمْ بِهٖ جِنَّةٌ  ۗبَلِ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ فِى الْعَذَابِ وَالضَّلٰلِ الْبَعِيْدِ ','Apakah dia mengada-adakan kebohongan terhadap Allah ataukah ada padanya penyakit gila?" (Tidak), tetapi orang-orang yang tidak beriman kepada negeri akhirat berada dalam siksaan dan kesesatan yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,9,'اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ اِنْ نَّشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ السَّمَاۤءِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّكُلِّ عَبْدٍ مُّنِيْبٍ ࣖ ','Maka apakah mereka tidak melihat langit dan bumi yang ada di hadapan dan di belakang mereka? Jika Kami menghendaki, niscaya Kami benamkan mereka di bumi atau Kami jatuhkan kepada mereka gumpalan dari langit. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Tuhan) bagi setiap hamba yang kembali (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,10,'۞ وَلَقَدْ اٰتَيْنَا دَاوٗدَ مِنَّا فَضْلًاۗ يٰجِبَالُ اَوِّبِيْ مَعَهٗ وَالطَّيْرَ ۚوَاَلَنَّا لَهُ الْحَدِيْدَۙ ','Dan sesungguhnya telah Kami berikan kepada Daud kurnia dari Kami. (Kami berfirman): "Hai gunung-gunung dan burung-burung, bertasbihlah berulang-ulang bersama Daud", dan Kami telah melunakkan besi untuknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,11,'اَنِ اعْمَلْ سٰبِغٰتٍ وَّقَدِّرْ فِى السَّرْدِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','(yaitu) buatlah baju besi yang besar-besar dan ukurlah anyamannya; dan kerjakanlah amalan yang saleh. Sesungguhnya Aku melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,12,'وَلِسُلَيْمٰنَ الرِّيْحَ غُدُوُّهَا شَهْرٌ وَّرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهٗ عَيْنَ الْقِطْرِۗ وَمِنَ الْجِنِّ مَنْ يَّعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّهٖۗ وَمَنْ يَّزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيْرِ ','Dan Kami (tundukkan) angin bagi Sulaiman, yang perjalanannya di waktu pagi sama dengan perjalanan sebulan dan perjalanannya di waktu sore sama dengan perjalanan sebulan (pula) dan Kami alirkan cairan tembaga baginya. Dan sebahagian dari jin ada yang bekerja di hadapannya (di bawah kekuasaannya) dengan izin Tuhannya. Dan siapa yang menyimpang di antara mereka dari perintah Kami, Kami rasakan kepadanya azab neraka yang apinya menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,13,'يَعْمَلُوْنَ لَهٗ مَا يَشَاۤءُ مِنْ مَّحَارِيْبَ وَتَمَاثِيْلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُوْرٍ رّٰسِيٰتٍۗ اِعْمَلُوْٓا اٰلَ دَاوٗدَ شُكْرًا ۗوَقَلِيْلٌ مِّنْ عِبَادِيَ الشَّكُوْرُ ','Para jin itu membuat untuk Sulaiman apa yang dikehendakinya dari gedung-gedung yang tinggi dan patung-patung dan piring-piring yang (besarnya) seperti kolam dan periuk yang tetap (berada di atas tungku). Bekerjalah hai keluarga Daud untuk bersyukur (kepada Allah). Dan sedikit sekali dari hamba-hamba-Ku yang berterima kasih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,14,'فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِهٖٓ اِلَّا دَاۤبَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهٗ ۚفَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَّوْ كَانُوْا يَعْلَمُوْنَ الْغَيْبَ مَا لَبِثُوْا فِى الْعَذَابِ الْمُهِيْنِۗ ','Maka tatkala Kami telah menetapkan kematian Sulaiman, tidak ada yang menunjukkan kepada mereka kematiannya itu kecuali rayap yang memakan tongkatnya. Maka tatkala ia telah tersungkur, tahulah jin itu bahwa kalau sekiranya mereka mengetahui yang ghaib tentulah mereka tidak akan tetap dalam siksa yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,15,'لَقَدْ كَانَ لِسَبَاٍ فِيْ مَسْكَنِهِمْ اٰيَةٌ  ۚجَنَّتٰنِ عَنْ يَّمِيْنٍ وَّشِمَالٍ ەۗ كُلُوْا مِنْ رِّزْقِ رَبِّكُمْ وَاشْكُرُوْا لَهٗ  ۗبَلْدَةٌ طَيِّبَةٌ وَّرَبٌّ غَفُوْرٌ','Sesungguhnya bagi kaum Saba´ ada tanda (kekuasaan Tuhan) di tempat kediaman mereka yaitu dua buah kebun di sebelah kanan dan di sebelah kiri. (kepada mereka dikatakan): "Makanlah olehmu dari rezeki yang (dianugerahkan) Tuhanmu dan bersyukurlah kamu kepada-Nya. (Negerimu) adalah negeri yang baik dan (Tuhanmu) adalah Tuhan Yang Maha Pengampun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,16,'فَاَعْرَضُوْا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنٰهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَّاَثْلٍ وَّشَيْءٍ مِّنْ سِدْرٍ قَلِيْلٍ ','Tetapi mereka berpaling, maka Kami datangkan kepada mereka banjir yang besar dan Kami ganti kedua kebun mereka dengan dua kebun yang ditumbuhi (pohon-pohon) yang berbuah pahit, pohon Atsl dan sedikit dari pohon Sidr.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,17,'ذٰلِكَ جَزَيْنٰهُمْ بِمَا كَفَرُوْاۗ وَهَلْ نُجٰزِيْٓ اِلَّا الْكَفُوْرَ ','Demikianlah Kami memberi balasan kepada mereka karena kekafiran mereka. Dan Kami tidak menjatuhkan azab (yang demikian itu), melainkan hanya kepada orang-orang yang sangat kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,18,'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِيْ بٰرَكْنَا فِيْهَا قُرًى ظَاهِرَةً وَّقَدَّرْنَا فِيْهَا السَّيْرَۗ سِيْرُوْا فِيْهَا لَيَالِيَ وَاَيَّامًا اٰمِنِيْنَ ','Dan Kami jadikan antara mereka dan antara negeri-negeri yang Kami limpahkan berkat kepadanya, beberapa negeri yang berdekatan dan Kami tetapkan antara negeri-negeri itu (jarak-jarak) perjalanan. Berjalanlah kamu di kota-kota itu pada malam hari dan siang hari dengan dengan aman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,19,'فَقَالُوْا رَبَّنَا بٰعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُوْٓا اَنْفُسَهُمْ فَجَعَلْنٰهُمْ اَحَادِيْثَ وَمَزَّقْنٰهُمْ كُلَّ مُمَزَّقٍۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ ','Maka mereka berkata: "Ya Tuhan kami jauhkanlah jarak perjalanan kami", dan mereka menganiaya diri mereka sendiri; maka Kami jadikan mereka buah mulut dan Kami hancurkan mereka sehancur-hancurnya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda kekuasaan Allah bagi setiap orang yang sabar lagi bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,20,'وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْلِيْسُ ظَنَّهٗ فَاتَّبَعُوْهُ اِلَّا فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ ','Dan sesungguhnya iblis telah dapat membuktikan kebenaran sangkaannya terhadap mereka lalu mereka mengikutinya, kecuali sebahagian orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,21,'وَمَا كَانَ لَهٗ عَلَيْهِمْ مِّنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُّؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا فِيْ شَكٍّ ۗوَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ ࣖ ','Dan tidak adalah kekuasaan iblis terhadap mereka, melainkan hanyalah agar Kami dapat membedakan siapa yang beriman kepada adanya kehidupan akhirat dari siapa yang ragu-ragu tentang itu. Dan Tuhanmu Maha Memelihara segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,22,'قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِ اللّٰهِۚ لَا يَمْلِكُوْنَ مِثْقَالَ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَمَا لَهُمْ فِيْهِمَا مِنْ شِرْكٍ وَّمَا لَهٗ مِنْهُمْ مِّنْ ظَهِيْرٍ ','Katakanlah: "Serulah mereka yang kamu anggap (sebagai tuhan) selain Allah, mereka tidak memiliki (kekuasaan) seberat zarrahpun di langit dan di bumi, dan mereka tidak mempunyai suatu sahampun dalam (penciptaan) langit dan bumi dan sekali-kali tidak ada di antara mereka yang menjadi pembantu bagi-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,23,'وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهٗٓ اِلَّا لِمَنْ اَذِنَ لَهٗ ۗحَتّٰىٓ اِذَا فُزِّعَ عَنْ قُلُوْبِهِمْ قَالُوْا مَاذَاۙ قَالَ رَبُّكُمْۗ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَبِيْرُ ','Dan tiadalah berguna syafa´at di sisi Allah melainkan bagi orang yang telah diizinkan-Nya memperoleh syafa´at itu, sehingga apabila telah dihilangkan ketakutan dari hati mereka, mereka berkata "Apakah yang telah difirmankan oleh Tuhan-mu?" Mereka menjawab: (Perkataan) yang benar", dan Dialah Yang Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,24,'۞ قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۙوَاِنَّآ اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Katakanlah: "Siapakan yang memberi rezeki kepadamu dari langit dan dari bumi?" Katakanlah: "Allah", dan sesungguhnya kami atau kamu (orang-orang musyrik), pasti berada dalam kebenaran atau dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,25,'قُلْ لَّا تُسْـَٔلُوْنَ عَمَّآ اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُوْنَ ','Katakanlah: "Kamu tidak akan ditanya (bertanggung jawab) tentang dosa yang kami perbuat dan kami tidak akan ditanya (pula) tentang apa yang kamu perbuat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,26,'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۗ وَهُوَ الْفَتَّاحُ الْعَلِيْمُ ','Katakanlah: "Tuhan kita akan mengumpulkan kita semua, kemudian Dia memberi keputusan antara kita dengan benar. Dan Dialah Maha Pemberi keputusan lagi Maha Mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,27,'قُلْ اَرُوْنِيَ الَّذِيْنَ اَلْحَقْتُمْ بِهٖ شُرَكَاۤءَ كَلَّا ۗبَلْ هُوَ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ','Katakanlah: "Perlihatkanlah kepadaku sembah-sembahan yang kamu hubungkan dengan Dia sebagai sekutu-sekutu-Nya, sekali-kali tidak mungkin! Sebenarnya Dialah Allah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,28,'وَمَآ اَرْسَلْنٰكَ اِلَّا كَاۤفَّةً لِّلنَّاسِ بَشِيْرًا وَّنَذِيْرًا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ','Dan Kami tidak mengutus kamu, melainkan kepada umat manusia seluruhnya sebagai pembawa berita gembira dan sebagai pemberi peringatan, tetapi kebanyakan manusia tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,29,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Dan mereka berkata: "Kapankah (datangnya) janji ini, jika kamu adalah orang-orang yang benar?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,30,'قُلْ لَّكُمْ مِّيْعَادُ يَوْمٍ لَّا تَسْتَأْخِرُوْنَ عَنْهُ سَاعَةً وَّلَا تَسْتَقْدِمُوْنَ ࣖ ','Katakanlah: "Bagimu ada hari yang telah dijanjikan (hari kiamat) yang tiada dapat kamu minta mundur daripadanya barang sesaatpun dan tidak (pula) kamu dapat meminta supaya diajukan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,31,'وَقَالَ الَّذِيْنَ كَفَرُوْا لَنْ نُّؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذِيْ بَيْنَ يَدَيْهِۗ وَلَوْ تَرٰىٓ اِذِ الظّٰلِمُوْنَ مَوْقُوْفُوْنَ عِنْدَ رَبِّهِمْۖ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضِ ِۨالْقَوْلَۚ يَقُوْلُ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا لَوْلَآ اَنْتُمْ لَكُنَّا مُؤْمِنِيْنَ ','Dan orang-orang kafir berkata: "Kami sekali-kali tidak akan beriman kepada Al Quran ini dan tidak (pula) kepada kitab yang sebelumnya". Dan (alangkah hebatnya) kalau kamu lihat ketika orang-orang yang zalim itu dihadapkan kepada Tuhannya, sebahagian dari mereka menghadap kan perkataan kepada sebagian yang lain; orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri: "Kalau tidaklah karena kamu tentulah kami menjadi orang-orang yang beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,32,'قَالَ الَّذِيْنَ اسْتَكْبَرُوْا لِلَّذِيْنَ اسْتُضْعِفُوْٓا اَنَحْنُ صَدَدْنٰكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَاۤءَكُمْ بَلْ كُنْتُمْ مُّجْرِمِيْنَ ','Orang-orang yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah: "Kamikah yang telah menghalangi kamu dari petunjuk sesudah petunjuk itu datang kepadamu? (Tidak), sebenarnya kamu sendirilah orang-orang yang berdosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,33,'وَقَالَ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُوْنَنَآ اَنْ نَّكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهٗٓ اَنْدَادًا ۗوَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۗ وَجَعَلْنَا الْاَغْلٰلَ فِيْٓ اَعْنَاقِ الَّذِيْنَ كَفَرُوْاۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ','Dan orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri: "(Tidak) sebenarnya tipu daya(mu) di waktu malam dan siang (yang menghalangi kami), ketika kamu menyeru kami supaya kami kafir kepada Allah dan menjadikan sekutu-sekutu bagi-Nya". Kedua belah pihak menyatakan penyesalan tatkala mereka melihat azab. Dan kami pasang belenggu di leher orang-orang yang kafir. Mereka tidak dibalas melainkan dengan apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,34,'وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍ ِالَّا قَالَ مُتْرَفُوْهَآ  ۙاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ','Dan Kami tidak mengutus kepada suatu negeri seorang pemberi peringatanpun, melainkan orang-orang yang hidup mewah di negeri itu berkata: "Sesungguhnya kami mengingkari apa yang kamu diutus untuk menyampaikannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,35,'وَقَالُوْا نَحْنُ اَكْثَرُ اَمْوَالًا وَّاَوْلَادًاۙ وَّمَا نَحْنُ بِمُعَذَّبِيْنَ ','Dan mereka berkata: "Kami lebih banyak mempunyai harta dan anak-anak (daripada kamu) dan kami sekali-kali tidak akan diazab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,36,'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ࣖ','Katakanlah: "Sesungguhnya Tuhanku melapangkan rezeki bagi siapa yang dikehendaki-Nya dan menyempitkan (bagi siapa yang dikehendaki-Nya). akan tetapi kebanyakan manusia tidak mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,37,'وَمَآ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ بِالَّتِيْ تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰىٓ اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًاۙ فَاُولٰۤىِٕكَ لَهُمْ جَزَاۤءُ الضِّعْفِ بِمَا عَمِلُوْا وَهُمْ فِى الْغُرُفٰتِ اٰمِنُوْنَ ','Dan sekali-kali bukanlah harta dan bukan (pula) anak-anak kamu yang mendekatkan kamu kepada Kami sedikitpun; tetapi orang-orang yang beriman dan mengerjakan amal-amal (saleh, mereka itulah yang memperoleh balasan yang berlipat ganda disebabkan apa yang telah mereka kerjakan; dan mereka aman sentosa di tempat-tempat yang tinggi (dalam surga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,38,'وَالَّذِيْنَ يَسْعَوْنَ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ ','Dan orang-orang yang berusaha (menentang) ayat-ayat Kami dengan anggapan untuk dapat melemahkan (menggagalkan azab Kami), mereka itu dimasukkan ke dalam azab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,39,'قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ  ۗوَمَآ اَنْفَقْتُمْ مِّنْ شَيْءٍ فَهُوَ يُخْلِفُهٗ ۚوَهُوَ خَيْرُ الرّٰزِقِيْنَ ','Katakanlah: "Sesungguhnya Tuhanku melapangkan rezeki bagi siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan menyempitkan bagi (siapa yang dikehendaki-Nya)". Dan barang apa saja yang kamu nafkahkan, maka Allah akan menggantinya dan Dialah Pemberi rezeki yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,40,'وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًا ثُمَّ يَقُوْلُ لِلْمَلٰۤىِٕكَةِ اَهٰٓؤُلَاۤءِ اِيَّاكُمْ كَانُوْا يَعْبُدُوْنَ ','Dan (ingatlah) hari (yang di waktu itu) Allah mengumpulkan mereka semuanya kemudian Allah berfirman kepada malaikat: "Apakah mereka ini dahulu menyembah kamu?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,41,'قَالُوْا سُبْحٰنَكَ اَنْتَ وَلِيُّنَا مِنْ دُوْنِهِمْ ۚبَلْ كَانُوْا يَعْبُدُوْنَ الْجِنَّ اَكْثَرُهُمْ بِهِمْ مُّؤْمِنُوْنَ ','Malaikat-malaikat itu menjawab: "Maha Suci Engkau. Engkaulah pelindung kami, bukan mereka; bahkan mereka telah menyembah jin; kebanyakan mereka beriman kepada jin itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,42,'فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَّلَا ضَرًّا ۗوَنَقُوْلُ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ النَّارِ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ ','Maka pada hari ini sebahagian kamu tidak berkuasa (untuk memberikan) kemanfaatan dan tidak pula kemudharatan kepada sebahagian yang lain. Dan Kami katakan kepada orang-orang yang zalim: "Rasakanlah olehmu azab neraka yang dahulunya kamu dustakan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,43,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا رَجُلٌ يُّرِيْدُ اَنْ يَّصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُكُمْ ۚوَقَالُوْا مَا هٰذَآ اِلَّآ اِفْكٌ مُّفْتَرًىۗ وَقَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ','Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang, mereka berkata: "Orang ini tiada lain hanyalah seorang laki-laki yang ingin menghalangi kamu dari apa yang disembah oleh bapak-bapakmu", dan mereka berkata: "(Al Quran) ini tidak lain hanyalah kebohongan yang diada-adakan saja". Dan orang-orang kafir berkata terhadap kebenaran tatkala kebenaran itu datang kepada mereka: "Ini tidak lain hanyalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,44,'وَمَآ اٰتَيْنٰهُمْ مِّنْ كُتُبٍ يَّدْرُسُوْنَهَا وَمَآ اَرْسَلْنَآ اِلَيْهِمْ قَبْلَكَ مِنْ نَّذِيْرٍۗ ','Dan Kami tidak pernah memberikan kepada mereka kitab-kitab yang mereka baca dan sekali-kali tidak pernah (pula) mengutus kepada mereka sebelum kamu seorang pemberi peringatanpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,45,'وَكَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوْا مِعْشَارَ مَآ اٰتَيْنٰهُمْ فَكَذَّبُوْا رُسُلِيْۗ فَكَيْفَ كَانَ نَكِيْرِ ࣖ','Dan orang-orang yang sebelum mereka telah mendustakan sedang orang-orang kafir Mekah itu belum sampai menerima sepersepuluh dari apa yang telah Kami berikan kepada orang-orang dahulu itu lalu mereka mendustakan rasul-rasul-Ku. Maka alangkah hebatnya akibat kemurkaan-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,46,'۞ قُلْ اِنَّمَآ اَعِظُكُمْ بِوَاحِدَةٍۚ اَنْ تَقُوْمُوْا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوْاۗ مَا بِصَاحِبِكُمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ لَّكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيْدٍ ','Katakanlah: "Sesungguhnya aku hendak memperingatkan kepadamu suatu hal saja, yaitu supaya kamu menghadap Allah (dengan ikhlas) berdua-dua atau sendiri-sendiri; kemudian kamu fikirkan (tentang Muhammad) tidak ada penyakit gila sedikitpun pada kawanmu itu. Dia tidak lain hanyalah pemberi peringatan bagi kamu sebelum (menghadapi) azab yang keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,47,'قُلْ مَا سَاَلْتُكُمْ مِّنْ اَجْرٍ فَهُوَ لَكُمْۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ','Katakanlah: "Upah apapun yang aku minta kepadamu, maka itu untuk kamu. Upahku hanyalah dari Allah, dan Dia Maha Mengetahui segala sesuatu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,48,'قُلْ اِنَّ رَبِّيْ يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوْبِ ','Katakanlah: "Sesungguhnya Tuhanku mewahyukan kebenaran. Dia Maha Mengetahui segala yang ghaib".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,49,'قُلْ جَاۤءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيْدُ ','Katakanlah: "Kebenaran telah datang dan yang batil itu tidak akan memulai dan tidak (pula) akan mengulangi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,50,'قُلْ اِنْ ضَلَلْتُ فَاِنَّمَآ اَضِلُّ عَلٰى نَفْسِيْۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوْحِيْٓ اِلَيَّ رَبِّيْۗ اِنَّهٗ سَمِيْعٌ قَرِيْبٌ ','Katakanlah: "Jika aku sesat maka sesungguhnya aku sesat atas kemudharatan diriku sendiri; dan jika aku mendapat petunjuk maka itu adalah disebabkan apa yang diwahyukan Tuhanku kepadaku. Sesungguhnya Dia Maha Mendengar lagi Maha Dekat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,51,'وَلَوْ تَرٰىٓ اِذْ فَزِعُوْا فَلَا فَوْتَ وَاُخِذُوْا مِنْ مَّكَانٍ قَرِيْبٍۙ ','Dan (alangkah hebatnya) jikalau kamu melihat ketika mereka (orang-orang kafir) terperanjat ketakutan (pada hari kiamat); maka mereka tidak dapat melepaskan diri dan mereka ditangkap dari tempat yang dekat (untuk dibawa ke neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,52,'وَّقَالُوْٓا اٰمَنَّا بِهٖۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ','dan (di waktu itu) mereka berkata: "Kami beriman kepada Allah", bagaimanakah mereka dapat mencapai (keimanan) dari tempat yang jauh itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,53,'وَقَدْ كَفَرُوْا بِهٖ مِنْ قَبْلُۚ وَيَقْذِفُوْنَ بِالْغَيْبِ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ','Dan sesungguhnya mereka telah mengingkari Allah sebelum itu; dan mereka menduga-duga tentang yang ghaib dari tempat yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,34,54,'وَحِيْلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُوْنَۙ  كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا فِيْ شَكٍّ مُّرِيْبٍ ࣖ','Dan dihalangi antara mereka dengan apa yang mereka ingini sebagaimana yang dilakukan terhadap orang-orang yang serupa dengan mereka pada masa dahulu. Sesungguhnya mereka dahulu (di dunia) dalam keraguan yang mendalam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,1,'اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ جَاعِلِ الْمَلٰۤىِٕكَةِ رُسُلًاۙ اُولِيْٓ اَجْنِحَةٍ مَّثْنٰى وَثُلٰثَ وَرُبٰعَۗ يَزِيْدُ فِى الْخَلْقِ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Segala puji bagi Allah Pencipta langit dan bumi, Yang menjadikan malaikat sebagai utusan-utusan (untuk mengurus berbagai macam urusan) yang mempunyai sayap, masing-masing (ada yang) dua, tiga dan empat. Allah menambahkan pada ciptaan-Nya apa yang dikehendaki-Nya. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,2,'مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا ۚوَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهٗ مِنْۢ بَعْدِهٖۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Apa saja yang Allah anugerahkan kepada manusia berupa rahmat, maka tidak ada seorangpun yang dapat menahannya; dan apa saja yang ditahan oleh Allah maka tidak seorangpun yang sanggup melepaskannya sesudah itu. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,3,'يٰٓاَيُّهَا النَّاسُ اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْۗ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ  لَآ اِلٰهَ اِلَّا هُوَۖ فَاَنّٰى تُؤْفَكُوْنَ ','Hai manusia, ingatlah akan nikmat Allah kepadamu. Adakah pencipta selain Allah yang dapat memberikan rezeki kepada kamu dari langit dan bumi? Tidak ada Tuhan selain Dia; maka mengapakah kamu berpaling (dari ketauhidan)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,4,'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ','Dan jika mereka mendustakan kamu (sesudah kamu beri peringatan) maka sungguh telah didustakan pula rasul-rasul sebelum kamu. Dan hanya kepada Allahlah dikembalikan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,5,'يٰٓاَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ ','Hai manusia, sesungguhnya janji Allah adalah benar, maka sekali-kali janganlah kehidupan dunia memperdayakan kamu dan sekali-kali janganlah syaitan yang pandai menipu, memperdayakan kamu tentang Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,6,'اِنَّ الشَّيْطٰنَ لَكُمْ عَدُوٌّ فَاتَّخِذُوْهُ عَدُوًّاۗ اِنَّمَا يَدْعُوْا حِزْبَهٗ لِيَكُوْنُوْا مِنْ اَصْحٰبِ السَّعِيْرِۗ ','Sesungguhnya syaitan itu adalah musuh bagimu, maka anggaplah ia musuh(mu), karena sesungguhnya syaitan-syaitan itu hanya mengajak golongannya supaya mereka menjadi penghuni neraka yang menyala-nyala')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,7,'اَلَّذِيْنَ كَفَرُوْا لَهُمْ عَذَابٌ شَدِيْدٌ ەۗ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ ࣖ ','Orang-orang yang kafir bagi mereka azab yang keras. Dan orang-orang yang beriman dan mengerjakan amal saleh bagi mereka ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,8,'اَفَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ فَرَاٰهُ حَسَنًاۗ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرٰتٍۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَصْنَعُوْنَ ','Maka apakah orang yang dijadikan (syaitan) menganggap baik pekerjaannya yang buruk lalu dia meyakini pekerjaan itu baik, (sama dengan orang yang tidak ditipu oleh syaitan)? Maka sesungguhnya Allah menyesatkan siapa yang dikehendaki-Nya dan menunjuki siapa yang dikehendaki-Nya; maka janganlah dirimu binasa karena kesedihan terhadap mereka. Sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,9,'وَاللّٰهُ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَسُقْنٰهُ اِلٰى بَلَدٍ مَّيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ  كَذٰلِكَ النُّشُوْرُ ','Dan Allah, Dialah Yang mengirimkan angin; lalu angin itu menggerakkan awan, maka Kami halau awan itu kesuatu negeri yang mati lalu Kami hidupkan bumi setelah matinya dengan hujan itu. Demikianlah kebangkitan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,10,'مَنْ كَانَ يُرِيْدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَمِيْعًاۗ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهٗ ۗوَالَّذِيْنَ يَمْكُرُوْنَ السَّيِّاٰتِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَمَكْرُ اُولٰۤىِٕكَ هُوَ يَبُوْرُ ','Barangsiapa yang menghendaki kemuliaan, maka bagi Allah-lah kemuliaan itu semuanya. Kepada-Nya-lah naik perkataan-perkataan yang baik dan amal yang saleh dinaikkan-Nya. Dan orang-orang yang merencanakan kejahatan bagi mereka azab yang keras. Dan rencana jahat mereka akan hancur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,11,'وَاللّٰهُ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجًاۗ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖۗ وَمَا يُعَمَّرُ مِنْ مُّعَمَّرٍ وَّلَا يُنْقَصُ مِنْ عُمُرِهٖٓ اِلَّا فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ','Dan Allah menciptakan kamu dari tanah kemudian dari air mani, kemudian Dia menjadikan kamu berpasangan (laki-laki dan perempuan). Dan tidak ada seorang perempuanpun mengandung dan tidak (pula) melahirkan melainkan dengan sepengetahuan-Nya. Dan sekali-kali tidak dipanjangkan umur seorang yang berumur panjang dan tidak pula dikurangi umurnya, melainkan (sudah ditetapkan) dalam Kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu bagi Allah adalah mudah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,12,'وَمَا يَسْتَوِى الْبَحْرٰنِۖ هٰذَا عَذْبٌ فُرَاتٌ سَاۤىِٕغٌ شَرَابُهٗ وَهٰذَا مِلْحٌ اُجَاجٌۗ وَمِنْ كُلٍّ تَأْكُلُوْنَ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْنَ حِلْيَةً تَلْبَسُوْنَهَا ۚوَتَرَى الْفُلْكَ فِيْهِ مَوَاخِرَ لِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ','Dan tiada sama (antara) dua laut; yang ini tawar, segar, sedap diminum dan yang lain asin lagi pahit. Dan dari masing-masing laut itu kamu dapat memakan daging yang segar dan kamu dapat mengeluarkan perhiasan yang dapat kamu memakainya, dan pada masing-masingnya kamu lihat kapal-kapal berlayar membelah laut supaya kamu dapat mencari karunia-Nya dan supaya kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,13,'يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۚ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ مَا يَمْلِكُوْنَ مِنْ قِطْمِيْرٍۗ ','Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan menundukkan matahari dan bulan, masing-masing berjalan menurut waktu yang ditentukan. Yang (berbuat) demikian itulah Allah Tuhanmu, kepunyaan-Nya-lah kerajaan. Dan orang-orang yang kamu seru (sembah) selain Allah tiada mempunyai apa-apa walaupun setipis kulit ari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,14,'اِنْ تَدْعُوْهُمْ لَا يَسْمَعُوْا دُعَاۤءَكُمْۚ وَلَوْ سَمِعُوْا مَا اسْتَجَابُوْا لَكُمْۗ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُوْنَ بِشِرْكِكُمْۗ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيْرٍ ࣖ','Jika kamu menyeru mereka, mereka tiada mendengar seruanmu; dan kalau mereka mendengar, mereka tidak dapat memperkenankan permintaanmu. Dan dihari kiamat mereka akan mengingkari kemusyirikanmu dan tidak ada yang dapat memberi keterangan kepadamu sebagai yang diberikan oleh Yang Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,15,'۞ يٰٓاَيُّهَا النَّاسُ اَنْتُمُ الْفُقَرَاۤءُ اِلَى اللّٰهِ ۚوَاللّٰهُ هُوَ الْغَنِيُّ الْحَمِيْدُ ','Hai manusia, kamulah yang berkehendak kepada Allah; dan Allah Dialah Yang Maha Kaya (tidak memerlukan sesuatu) lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,16,'اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۚ ','Jika Dia menghendaki, niscaya Dia memusnahkan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,17,'وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ ','Dan yang demikian itu sekali-kali tidak sulit bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,18,'وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰى ۗوَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَّلَوْ كَانَ ذَا قُرْبٰىۗ اِنَّمَا تُنْذِرُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَ ۗوَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَكّٰى لِنَفْسِهٖ ۗوَاِلَى اللّٰهِ الْمَصِيْرُ ','Dan orang yang berdosa tidak akan memikul dosa orang lain. Dan jika seseorang yang berat dosanya memanggil (orang lain) untuk memikul dosanya itu tiadalah akan dipikulkan untuknya sedikitpun meskipun (yang dipanggilnya itu) kaum kerabatnya. Sesungguhnya yang dapat kamu beri peringatan hanya orang-orang yang takut kepada azab Tuhannya (sekalipun) mereka tidak melihat-Nya dan mereka mendirikan sembahyang. Dan barangsiapa yang mensucikan dirinya, sesungguhnya ia mensucikan diri untuk kebaikan dirinya sendiri. Dan kepada Allahlah kembali(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,19,'وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ۙ ','Dan tidaklah sama orang yang buta dengan orang yang melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,20,'وَلَا الظُّلُمٰتُ وَلَا النُّوْرُۙ ','dan tidak (pula) sama gelap gulita dengan cahaya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,21,'وَلَا الظِّلُّ وَلَا الْحَرُوْرُۚ ','dan tidak (pula) sama yang teduh dengan yang panas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,22,'وَمَا يَسْتَوِى الْاَحْيَاۤءُ وَلَا الْاَمْوَاتُۗ اِنَّ اللّٰهَ يُسْمِعُ مَنْ يَّشَاۤءُ ۚوَمَآ اَنْتَ بِمُسْمِعٍ مَّنْ فِى الْقُبُوْرِ ','dan tidak (pula) sama orang-orang yang hidup dan orang-orang yang mati. Sesungguhnya Allah memberi pendengaran kepada siapa yang dikehendaki-Nya dan kamu sekali-kali tiada sanggup menjadikan orang yang didalam kubur dapat mendengar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,23,'اِنْ اَنْتَ اِلَّا نَذِيْرٌ','Kamu tidak lain hanyalah seorang pemberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,24,'اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًا ۗوَاِنْ مِّنْ اُمَّةٍ اِلَّا خَلَا فِيْهَا نَذِيْرٌ ','Sesungguhnya Kami mengutus kamu dengan membawa kebenaran sebagai pembawa berita gembira dan sebagai pemberi peringatan. Dan tidak ada suatu umatpun melainkan telah ada padanya seorang pemberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,25,'وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَبِالزُّبُرِ وَبِالْكِتٰبِ الْمُنِيْرِ ','Dan jika mereka mendustakan kamu, maka sesungguhnya orang-orang yang sebelum mereka telah mendustakan (rasul-rasulnya); kepada mereka telah datang rasul-rasulnya dengan membawa mukjizat yang nyata, zubur, dan kitab yang memberi penjelasan yang sempurna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,26,'ثُمَّ اَخَذْتُ الَّذِيْنَ كَفَرُوْا فَكَيْفَ كَانَ نَكِيْرِ ࣖ ','Kemudian Aku azab orang-orang yang kafir; maka (lihatlah) bagaimana (hebatnya) akibat kemurkaan-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,27,'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ ثَمَرٰتٍ مُّخْتَلِفًا اَلْوَانُهَا ۗوَمِنَ الْجِبَالِ جُدَدٌ ۢبِيْضٌ وَّحُمْرٌ مُّخْتَلِفٌ اَلْوَانُهَا وَغَرَابِيْبُ سُوْدٌ ','Tidakkah kamu melihat bahwasanya Allah menurunkan hujan dari langit lalu Kami hasilkan dengan hujan itu buah-buahan yang beraneka macam jenisnya. Dan di antara gunung-gunung itu ada garis-garis putih dan merah yang beraneka macam warnanya dan ada (pula) yang hitam pekat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,28,'وَمِنَ النَّاسِ وَالدَّوَاۤبِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهٗ كَذٰلِكَۗ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰۤؤُاۗ اِنَّ اللّٰهَ عَزِيْزٌ غَفُوْرٌ ','Dan demikian (pula) di antara manusia, binatang-binatang melata dan binatang-binatang ternak ada yang bermacam-macam warnanya (dan jenisnya). Sesungguhnya yang takut kepada Allah di antara hamba-hamba-Nya, hanyalah ulama. Sesungguhnya Allah Maha Perkasa lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,29,'اِنَّ الَّذِيْنَ يَتْلُوْنَ كِتٰبَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً يَّرْجُوْنَ تِجَارَةً لَّنْ تَبُوْرَۙ ','Sesungguhnya orang-orang yang selalu membaca kitab Allah dan mendirikan shalat dan menafkahkan sebahagian dari rezeki yang Kami anugerahkan kepada mereka dengan diam-diam dan terang-terangan, mereka itu mengharapkan perniagaan yang tidak akan merugi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,30,'لِيُوَفِّيَهُمْ اُجُوْرَهُمْ وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ اِنَّهٗ غَفُوْرٌ شَكُوْرٌ ','agar Allah menyempurnakan kepada mereka pahala mereka dan menambah kepada mereka dari karunia-Nya. Sesungguhnya Allah Maha Pengampun lagi Maha Mensyukuri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,31,'وَالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ مِنَ الْكِتٰبِ هُوَ الْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِۗ اِنَّ اللّٰهَ بِعِبَادِهٖ لَخَبِيْرٌۢ بَصِيْرٌ ','Dan apa yang telah Kami wahyukan kepadamu yaitu Al Kitab (Al Quran) itulah yang benar, dengan membenarkan kitab-kitab yang sebelumnya. Sesungguhnya Allah benar-benar Maha Mengetahui lagi Maha Melihat (keadaan) hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,32,'ثُمَّ اَوْرَثْنَا الْكِتٰبَ الَّذِيْنَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهٖ ۚوَمِنْهُمْ مُّقْتَصِدٌ ۚوَمِنْهُمْ سَابِقٌۢ بِالْخَيْرٰتِ بِاِذْنِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُۗ ','Kemudian Kitab itu Kami wariskan kepada orang-orang yang Kami pilih di antara hamba-hamba Kami, lalu di antara mereka ada yang menganiaya diri mereka sendiri dan di antara mereka ada yang pertengahan dan diantara mereka ada (pula) yang lebih dahulu berbuat kebaikan dengan izin Allah. Yang demikian itu adalah karunia yang amat besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,33,'جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًا ۚوَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ','(Bagi mereka) surga ´Adn mereka masuk ke dalamnya, di dalamnya mereka diberi perhiasan dengan gelang-gelang dari emas, dan dengan mutiara, dan pakaian mereka didalamnya adalah sutera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,34,'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْٓ اَذْهَبَ عَنَّا الْحَزَنَۗ اِنَّ رَبَّنَا لَغَفُوْرٌ شَكُوْرٌۙ ','Dan mereka berkata: "Segala puji bagi Allah yang telah menghilangkan duka cita dari kami. Sesungguhnya Tuhan kami benar-benar Maha Pengampum lagi Maha Mensyukuri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,35,' ۨالَّذِيْٓ اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهٖۚ  لَا يَمَسُّنَا فِيْهَا نَصَبٌ وَّلَا يَمَسُّنَا فِيْهَا لُغُوْبٌ ','Yang menempatkan kami dalam tempat yang kekal (surga) dari karunia-Nya; didalamnya kami tiada merasa lelah dan tiada pula merasa lesu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,36,'وَالَّذِيْنَ كَفَرُوْا لَهُمْ نَارُ جَهَنَّمَۚ  لَا يُقْضٰى عَلَيْهِمْ فَيَمُوْتُوْا وَلَا يُخَفَّفُ عَنْهُمْ مِّنْ عَذَابِهَاۗ  كَذٰلِكَ نَجْزِيْ كُلَّ كَفُوْرٍ ۚ','Dan orang-orang kafir bagi mereka neraka Jahannam. Mereka tidak dibinasakan sehingga mereka mati dan tidak (pula) diringankan dari mereka azabnya. Demikianlah Kami membalas setiap orang yang sangat kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,37,'وَهُمْ يَصْطَرِخُوْنَ فِيْهَاۚ رَبَّنَآ اَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ اَوَلَمْ نُعَمِّرْكُمْ مَّا يَتَذَكَّرُ فِيْهِ مَنْ تَذَكَّرَ وَجَاۤءَكُمُ النَّذِيْرُۗ فَذُوْقُوْا فَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ','Dan mereka berteriak di dalam neraka itu: "Ya Tuhan kami, keluarkanlah kami niscaya kami akan mengerjakan amal yang saleh berlainan dengan yang telah kami kerjakan". Dan apakah Kami tidak memanjangkan umurmu dalam masa yang cukup untuk berfikir bagi orang yang mau berfikir, dan (apakah tidak) datang kepada kamu pemberi peringatan? maka rasakanlah (azab Kami) dan tidak ada bagi orang-orang yang zalim seorang penolongpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,38,'اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ','Sesungguhnya Allah mengetahui yang tersembunyi di langit dan di bumi. Sesungguhnya Dia Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,39,'هُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِۗ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۗ وَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتًا ۚوَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ اِلَّا خَسَارًا ','Dialah yang menjadikan kamu khalifah-khalifah di muka bumi. Barangsiapa yang kafir, maka (akibat) kekafirannya menimpa dirinya sendiri. Dan kekafiran orang-orang yang kafir itu tidak lain hanyalah akan menambah kemurkaan pada sisi Tuhannya dan kekafiran orang-orang yang kafir itu tidak lain hanyalah akan menambah kerugian mereka belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,40,'قُلْ اَرَاَيْتُمْ شُرَكَاۤءَكُمُ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِۚ اَمْ اٰتَيْنٰهُمْ كِتٰبًا فَهُمْ عَلٰى بَيِّنَتٍ مِّنْهُۚ بَلْ اِنْ يَّعِدُ الظّٰلِمُوْنَ بَعْضُهُمْ بَعْضًا اِلَّا غُرُوْرًا ','Katakanlah: "Terangkanlah kepada-Ku tentang sekutu-sekutumu yang kamu seru selain Allah. Perlihatkanlah kepada-Ku (bahagian) manakah dari bumi ini yang telah mereka ciptakan ataukah mereka mempunyai saham dalam (penciptaan) langit atau adakah Kami memberi kepada mereka sebuah Kitab sehingga mereka mendapat keterangan-keterangan yang jelas daripadanya? Sebenarnya orang-orang yang zalim itu sebahagian dari mereka tidak menjanjikan kepada sebahagian yang lain, melainkan tipuan belaka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,41,'۞ اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوٰتِ وَالْاَرْضَ اَنْ تَزُوْلَا ەۚ وَلَىِٕنْ زَالَتَآ اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِّنْۢ بَعْدِهٖ ۗاِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا','Sesungguhnya Allah menahan langit dan bumi supaya jangan lenyap; dan sungguh jika keduanya akan lenyap tidak ada seorangpun yang dapat menahan keduanya selain Allah. Sesungguhnya Dia adalah Maha Penyantun lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,42,'وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَهُمْ نَذِيْرٌ لَّيَكُوْنُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَاۤءَهُمْ نَذِيْرٌ مَّا زَادَهُمْ اِلَّا نُفُوْرًاۙ ','Dan mereka bersumpah dengan nama Allah dengan sekuat-kuat sumpah; sesungguhnya jika datang kepada mereka seorang pemberi peringatan, niscaya mereka akan lebih mendapat petunjuk dari salah satu umat-umat (yang lain). Tatkala datang kepada mereka pemberi peringatan, maka kedatangannya itu tidak menambah kepada mereka, kecuali jauhnya mereka dari (kebenaran),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,43,' ۨاسْتِكْبَارًا فِى الْاَرْضِ وَمَكْرَ السَّيِّئِۗ وَلَا يَحِيْقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِهٖ ۗفَهَلْ يَنْظُرُوْنَ اِلَّا سُنَّتَ الْاَوَّلِيْنَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْدِيْلًا ەۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْوِيْلًا','karena kesombongan (mereka) di muka bumi dan karena rencana (mereka) yang jahat. Rencana yang jahat itu tidak akan menimpa selain orang yang merencanakannya sendiri. Tiadalah yang mereka nanti-nantikan melainkan (berlakunya) sunnah (Allah yang telah berlaku) kepada orang-orang yang terdahulu. Maka sekali-kali kamu tidak akan mendapat penggantian bagi sunnah Allah, dan sekali-kali tidak (pula) akan menemui penyimpangan bagi sunnah Allah itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,44,'اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ وَكَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً ۗوَمَا كَانَ اللّٰهُ لِيُعْجِزَهٗ مِنْ شَيْءٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ اِنَّهٗ كَانَ عَلِيْمًا قَدِيْرًا ','Dan apakah mereka tidak berjalan di muka bumi, lalu melihat bagaimana kesudahan orang-orang yang sebelum mereka, sedangkan orang-orang itu adalah lebih besar kekuatannya dari mereka? Dan tiada sesuatupun yang dapat melemahkan Allah baik di langit maupun di bumi. Sesungguhnya Allah Maha Mengetahui lagi Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,35,45,'وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوْا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِهٖ بَصِيْرًا ࣖ ','Dan kalau sekiranya Allah menyiksa manusia disebabkan usahanya, niscaya Dia tidak akan meninggalkan di atas permukaan bumi suatu mahluk yang melatapun akan tetapi Allah menangguhkan (penyiksaan) mereka, sampai waktu yang tertentu; maka apabila datang ajal mereka, maka sesungguhnya Allah adalah Maha Melihat (keadaan) hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,1,'يٰسۤ ۚ','Yaa siin')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,2,'وَالْقُرْاٰنِ الْحَكِيْمِۙ ','Demi Al Quran yang penuh hikmah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,3,'اِنَّكَ لَمِنَ الْمُرْسَلِيْنَۙ ','Sesungguhnya kamu salah seorang dari rasul-rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,4,'عَلٰى صِرَاطٍ مُّسْتَقِيْمٍۗ ','(yang berada) diatas jalan yang lurus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,5,'تَنْزِيْلَ الْعَزِيْزِ الرَّحِيْمِۙ ','(sebagai wahyu) yang diturunkan oleh Yang Maha Perkasa lagi Maha Penyayang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,6,'لِتُنْذِرَ قَوْمًا مَّآ اُنْذِرَ اٰبَاۤؤُهُمْ فَهُمْ غٰفِلُوْنَ ','Agar kamu memberi peringatan kepada kaum yang bapak-bapak mereka belum pernah diberi peringatan, karena itu mereka lalai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,7,'لَقَدْ حَقَّ الْقَوْلُ عَلٰٓى اَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُوْنَ ','Sesungguhnya telah pasti berlaku perkataan (ketentuan Allah) terhadap kebanyakan mereka, kerena mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,8,'اِنَّا جَعَلْنَا فِيْٓ اَعْنَاقِهِمْ اَغْلٰلًا فَهِيَ اِلَى الْاَذْقَانِ فَهُمْ مُّقْمَحُوْنَ ','Sesungguhnya Kami telah memasang belenggu dileher mereka, lalu tangan mereka (diangkat) ke dagu, maka karena itu mereka tertengadah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,9,'وَجَعَلْنَا مِنْۢ بَيْنِ اَيْدِيْهِمْ سَدًّا وَّمِنْ خَلْفِهِمْ سَدًّا فَاَغْشَيْنٰهُمْ فَهُمْ لَا يُبْصِرُوْنَ ','Dan Kami adakan di hadapan mereka dinding dan di belakang mereka dinding (pula), dan Kami tutup (mata) mereka sehingga mereka tidak dapat melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,10,'وَسَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ ','Sama saja bagi mereka apakah kamu memberi peringatan kepada mereka ataukah kamu tidak memberi peringatan kepada mereka, mereka tidak akan beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,11,'اِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمٰنَ بِالْغَيْبِۚ فَبَشِّرْهُ بِمَغْفِرَةٍ وَّاَجْرٍ كَرِيْمٍ ','Sesungguhnya kamu hanya memberi peringatan kepada orang-orang yang mau mengikuti peringatan dan yang takut kepada Tuhan Yang Maha Pemurah walaupun dia tidak melihatnya. Maka berilah mereka kabar gembira dengan ampunan dan pahala yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,12,'اِنَّا نَحْنُ نُحْيِ الْمَوْتٰى وَنَكْتُبُ مَا قَدَّمُوْا وَاٰثَارَهُمْۗ وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ فِيْٓ اِمَامٍ مُّبِيْنٍ ࣖ','Sesungguhnya Kami menghidupkan orang-orang mati dan Kami menuliskan apa yang telah mereka kerjakan dan bekas-bekas yang mereka tinggalkan. Dan segala sesuatu Kami kumpulkan dalam Kitab Induk yang nyata (Lauh Mahfuzh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,13,'وَاضْرِبْ لَهُمْ مَّثَلًا اَصْحٰبَ الْقَرْيَةِۘ اِذْ جَاۤءَهَا الْمُرْسَلُوْنَۚ ','Dan buatlah bagi mereka suatu perumpamaan, yaitu penduduk suatu negeri ketika utusan-utusan datang kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,14,'اِذْ اَرْسَلْنَآ اِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوْهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوْٓا اِنَّآ اِلَيْكُمْ مُّرْسَلُوْنَ ','(yaitu) ketika Kami mengutus kepada mereka dua orang utusan, lalu mereka mendustakan keduanya; kemudian Kami kuatkan dengan (utusan) yang ketiga, maka ketiga utusan itu berkata: "Sesungguhnya kami adalah orang-orang di utus kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,15,'قَالُوْا مَآ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَاۙ وَمَآ اَنْزَلَ الرَّحْمٰنُ مِنْ شَيْءٍۙ اِنْ اَنْتُمْ اِلَّا تَكْذِبُوْنَ ','Mereka menjawab: "Kamu tidak lain hanyalah manusia seperti kami dan Allah Yang Maha Pemurah tidak menurunkan sesuatupun, kamu tidak lain hanyalah pendusta belaka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,16,'قَالُوْا رَبُّنَا يَعْلَمُ اِنَّآ اِلَيْكُمْ لَمُرْسَلُوْنَ ','Mereka berkata: "Tuhan kami mengetahui bahwa sesungguhnya kami adalah orang yang diutus kepada kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,17,'وَمَا عَلَيْنَآ اِلَّا الْبَلٰغُ الْمُبِيْنُ ','Dan kewajiban kami tidak lain hanyalah menyampaikan (perintah Allah) dengan jelas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,18,'قَالُوْٓا اِنَّا تَطَيَّرْنَا بِكُمْۚ  لَىِٕنْ لَّمْ تَنْتَهُوْا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِّنَّا عَذَابٌ اَلِيْمٌ ','Mereka menjawab: "Sesungguhnya kami bernasib malang karena kamu, sesungguhnya jika kamu tidak berhenti (menyeru kami), niscaya kami akan merajam kamu dan kamu pasti akan mendapat siksa yang pedih dari kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,19,'قَالُوْا طَاۤىِٕرُكُمْ مَّعَكُمْۗ اَىِٕنْ ذُكِّرْتُمْۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ ','Utusan-utusan itu berkata: "Kemalangan kamu adalah karena kamu sendiri. Apakah jika kamu diberi peringatan (kamu bernasib malang)? Sebenarnya kamu adalah kaum yang melampui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,20,'وَجَاۤءَ مِنْ اَقْصَا الْمَدِيْنَةِ رَجُلٌ يَّسْعٰى قَالَ يٰقَوْمِ اتَّبِعُوا الْمُرْسَلِيْنَۙ  ','Dan datanglah dari ujung kota, seorang laki-laki dengan bergegas-gegas ia berkata: "Hai kaumku, ikutilah utusan-utusan itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,22,36,21,'اتَّبِعُوْا مَنْ لَّا يَسْـَٔلُكُمْ اَجْرًا وَّهُمْ مُّهْتَدُوْنَ  ۔','Ikutilah orang yang tiada minta balasan kepadamu; dan mereka adalah orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,22,'وَمَا لِيَ لَآ اَعْبُدُ الَّذِيْ فَطَرَنِيْ وَاِلَيْهِ تُرْجَعُوْنَ ','Mengapa aku tidak menyembah (Tuhan) yang telah menciptakanku dan yang hanya kepada-Nya-lah kamu (semua) akan dikembalikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,23,'ءَاَتَّخِذُ مِنْ دُوْنِهٖٓ اٰلِهَةً اِنْ يُّرِدْنِ الرَّحْمٰنُ بِضُرٍّ لَّا تُغْنِ عَنِّيْ شَفَاعَتُهُمْ شَيْـًٔا وَّلَا يُنْقِذُوْنِۚ ','Mengapa aku akan menyembah tuhan-tuhan selain-Nya jika (Allah) Yang Maha Pemurah menghendaki kemudharatan terhadapku, niscaya syafa´at mereka tidak memberi manfaat sedikitpun bagi diriku dan mereka tidak (pula) dapat menyelamatkanku?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,24,'اِنِّيْٓ اِذًا لَّفِيْ ضَلٰلٍ مُّبِيْنٍ ','Sesungguhnya aku kalau begitu pasti berada dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,25,'اِنِّيْٓ اٰمَنْتُ بِرَبِّكُمْ فَاسْمَعُوْنِۗ  ','Sesungguhnya aku telah beriman kepada Tuhanmu; maka dengarkanlah (pengakuan keimanan)ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,26,'قِيْلَ ادْخُلِ الْجَنَّةَ ۗقَالَ يٰلَيْتَ قَوْمِيْ يَعْلَمُوْنَۙ','Dikatakan (kepadanya): "Masuklah ke surga". Ia berkata: "Alangkah baiknya sekiranya kamumku mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,27,'بِمَا غَفَرَ لِيْ رَبِّيْ وَجَعَلَنِيْ مِنَ الْمُكْرَمِيْنَ','Apa yang menyebabkan Tuhanku memberi ampun kepadaku dan menjadikan aku termasuk orang-orang yang dimuliakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,28,'۞ وَمَآ اَنْزَلْنَا عَلٰى قَوْمِهٖ مِنْۢ بَعْدِهٖ مِنْ جُنْدٍ مِّنَ السَّمَاۤءِ وَمَا كُنَّا مُنْزِلِيْنَ  ','Dan kami tidak menurunkan kepada kaumnya sesudah dia (meninggal) suatu pasukanpun dari langit dan tidak layak Kami menurunkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,29,'اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ خَامِدُوْنَ ','Tidak ada siksaan atas mereka melainkan satu teriakan suara saja; maka tiba-tiba mereka semuanya mati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,30,'يٰحَسْرَةً عَلَى الْعِبَادِۚ مَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ','Alangkah besarnya penyesalan terhadap hamba-hamba itu, tiada datang seorang rasulpun kepada mereka melainkan mereka selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,31,'اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ اَنَّهُمْ اِلَيْهِمْ لَا يَرْجِعُوْنَ ','Tidakkah mereka mengetahui berapa banyaknya umat-umat sebelum mereka yang telah Kami binasakan, bahwasanya orang-orang (yang telah Kami binasakan) itu tiada kembali kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,32,'وَاِنْ كُلٌّ لَّمَّا جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ ࣖ','Dan setiap mereka semuanya akan dikumpulkan lagi kepada Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,33,'وَاٰيَةٌ لَّهُمُ الْاَرْضُ الْمَيْتَةُ ۖاَحْيَيْنٰهَا وَاَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُوْنَ','Dan suatu tanda (kekuasaan Allah yang besar) bagi mereka adalah bumi yang mati. Kami hidupkan bumi itu dan Kami keluarkan dari padanya biji-bijian, maka daripadanya mereka makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,34,'وَجَعَلْنَا فِيْهَا جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ وَّفَجَّرْنَا فِيْهَا مِنَ الْعُيُوْنِۙ','Dan Kami jadikan padanya kebun-kebun kurma dan anggur dan Kami pancarkan padanya beberapa mata air,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,35,'لِيَأْكُلُوْا مِنْ ثَمَرِهٖۙ وَمَا عَمِلَتْهُ اَيْدِيْهِمْ ۗ اَفَلَا يَشْكُرُوْنَ ','supaya mereka dapat makan dari buahnya, dan dari apa yang diusahakan oleh tangan mereka. Maka mengapakah mereka tidak bersyukur?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,36,'سُبْحٰنَ الَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا مِمَّا تُنْۢبِتُ الْاَرْضُ وَمِنْ اَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُوْنَ','Maha Suci Tuhan yang telah menciptakan pasangan-pasangan semuanya, baik dari apa yang ditumbuhkan oleh bumi dan dari diri mereka maupun dari apa yang tidak mereka ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,37,'وَاٰيَةٌ لَّهُمُ الَّيْلُ ۖنَسْلَخُ مِنْهُ النَّهَارَ فَاِذَا هُمْ مُّظْلِمُوْنَۙ','Dan suatu tanda (kekuasaan Allah yang besar) bagi mereka adalah malam; Kami tanggalkan siang dari malam itu, maka dengan serta merta mereka berada dalam kegelapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,38,'وَالشَّمْسُ تَجْرِيْ لِمُسْتَقَرٍّ لَّهَا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِۗ ','dan matahari berjalan ditempat peredarannya. Demikianlah ketetapan Yang Maha Perkasa lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,39,'وَالْقَمَرَ قَدَّرْنٰهُ مَنَازِلَ حَتّٰى عَادَ كَالْعُرْجُوْنِ الْقَدِيْمِ ','Dan telah Kami tetapkan bagi bulan manzilah-manzilah, sehingga (setelah dia sampai ke manzilah yang terakhir) kembalilah dia sebagai bentuk tandan yang tua.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,40,'لَا الشَّمْسُ يَنْۢبَغِيْ لَهَآ اَنْ تُدْرِكَ الْقَمَرَ وَلَا الَّيْلُ سَابِقُ النَّهَارِ ۗوَكُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ','Tidaklah mungkin bagi matahari mendapatkan bulan dan malampun tidak dapat mendahului siang. Dan masing-masing beredar pada garis edarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,41,'وَاٰيَةٌ لَّهُمْ اَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِى الْفُلْكِ الْمَشْحُوْنِۙ','Dan suatu tanda (kebesaran Allah yang besar) bagi mereka adalah bahwa Kami angkut keturunan mereka dalam bahtera yang penuh muatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,42,'وَخَلَقْنَا لَهُمْ مِّنْ مِّثْلِهٖ مَا يَرْكَبُوْنَ','dan Kami ciptakan untuk mereka yang akan mereka kendarai seperti bahtera itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,43,'وَاِنْ نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيْخَ لَهُمْ وَلَاهُمْ يُنْقَذُوْنَۙ','Dan jika Kami menghendaki niscaya Kami tenggelamkan mereka, maka tiadalah bagi mereka penolong dan tidak pula mereka diselamatkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,44,'اِلَّا رَحْمَةً مِّنَّا وَمَتَاعًا اِلٰى حِيْنٍ','Tetapi (Kami selamatkan mereka) karena rahmat yang besar dari Kami dan untuk memberikan kesenangan hidup sampai kepada suatu ketika.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,45,'وَاِذَا قِيْلَ لَهُمُ اتَّقُوْا مَا بَيْنَ اَيْدِيْكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُوْنَ','Dan apabila dikatakan kepada mereka: "Takutlah kamu akan siksa yang dihadapanmu dan siksa yang akan datang supaya kamu mendapat rahmat", (niscaya mereka berpaling).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,46,'وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ ','Dan sekali-kali tiada datang kepada mereka suatu tanda dari tanda tanda kekuasaan Tuhan mereka, melainkan mereka selalu berpaling daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,47,'وَاِذَا قِيْلَ لَهُمْ اَنْفِقُوْا مِمَّا رَزَقَكُمُ اللّٰهُ ۙقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓا اَنُطْعِمُ مَنْ لَّوْ يَشَاۤءُ اللّٰهُ اَطْعَمَهٗٓ   ۖاِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ مُّبِيْنٍ','Dan apabila dikatakakan kepada mereka: "Nafkahkanlah sebahagian dari reski yang diberikan Allah kepadamu", maka orang-orang yang kafir itu berkata kepada orang-orang yang beriman: "Apakah kami akan memberi makan kepada orang-orang yang jika Allah menghendaki tentulah Dia akan memberinya makan, tiadalah kamu melainkan dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,48,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ','Dan mereka berkata: "Bilakah (terjadinya) janji ini (hari berbangkit) jika kamu adalah orang-orang yang benar?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,49,'مَا يَنْظُرُوْنَ اِلَّا صَيْحَةً وَّاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُوْنَ','Mereka tidak menunggu melainkan satu teriakan saja yang akan membinasakan mereka ketika mereka sedang bertengkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,50,'فَلَا يَسْتَطِيْعُوْنَ تَوْصِيَةً وَّلَآ اِلٰٓى اَهْلِهِمْ يَرْجِعُوْنَ ࣖ  ','lalu mereka tidak kuasa membuat suatu wasiatpun dan tidak (pula) dapat kembali kepada keluarganya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,51,'وَنُفِخَ فِى الصُّوْرِ فَاِذَا هُمْ مِّنَ الْاَجْدَاثِ اِلٰى رَبِّهِمْ يَنْسِلُوْنَ','Dan ditiuplah sangkalala, maka tiba-tiba mereka keluar dengan segera dari kuburnya (menuju) kepada Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,52,'قَالُوْا يٰوَيْلَنَا مَنْۢ بَعَثَنَا مِنْ مَّرْقَدِنَا ۜهٰذَا مَا وَعَدَ الرَّحْمٰنُ وَصَدَقَ الْمُرْسَلُوْنَ','Mereka berkata: "Aduhai celakalah kami! Siapakah yang membangkitkan kami dari tempat-tidur kami (kubur)?". Inilah yang dijanjikan (Tuhan) Yang Maha Pemurah dan benarlah Rasul-rasul(Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,53,'اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ','Tidak adalah teriakan itu selain sekali teriakan saja, maka tiba-tiba mereka semua dikumpulkan kepada Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,54,'فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـًٔا وَّلَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ','Maka pada hari itu seseorang tidak akan dirugikan sedikitpun dan kamu tidak dibalasi, kecuali dengan apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,55,'اِنَّ اَصْحٰبَ الْجَنَّةِ الْيَوْمَ فِيْ شُغُلٍ فٰكِهُوْنَ ۚ  ','Sesungguhnya penghuni surga pada hari itu bersenang-senang dalam kesibukan (mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,56,'هُمْ وَاَزْوَاجُهُمْ فِيْ ظِلٰلٍ عَلَى الْاَرَاۤىِٕكِ مُتَّكِـُٔوْنَ ۚ   ','Mereka dan isteri-isteri mereka berada dalam tempat yang teduh, bertelekan di atas dipan-dipan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,57,'لَهُمْ فِيْهَا فَاكِهَةٌ وَّلَهُمْ مَّا يَدَّعُوْنَ ۚ ','Di surga itu mereka memperoleh buah-buahan dan memperoleh apa yang mereka minta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,58,'سَلٰمٌۗ قَوْلًا مِّنْ رَّبٍّ رَّحِيْمٍ','(Kepada mereka dikatakan): "Salam", sebagai ucapan selamat dari Tuhan Yang Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,59,'وَامْتَازُوا الْيَوْمَ اَيُّهَا الْمُجْرِمُوْنَ','Dan (dikatakan kepada orang-orang kafir): "Berpisahlah kamu (dari orang-orang mukmin) pada hari ini, hai orang-orang yang berbuat jahat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,60,'اَلَمْ اَعْهَدْ اِلَيْكُمْ يٰبَنِيْٓ اٰدَمَ اَنْ لَّا تَعْبُدُوا الشَّيْطٰنَۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ ','Bukankah Aku telah memerintahkan kepadamu hai Bani Adam supaya kamu tidak menyembah syaitan? Sesungguhnya syaitan itu adalah musuh yang nyata bagi kamu",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,61,'وَاَنِ اعْبُدُوْنِيْ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ','dan hendaklah kamu menyembah-Ku. Inilah jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,62,'وَلَقَدْ اَضَلَّ مِنْكُمْ جِبِلًّا كَثِيْرًا ۗاَفَلَمْ تَكُوْنُوْا تَعْقِلُوْنَ ','Sesungguhnya syaitan itu telah menyesatkan sebahagian besar diantaramu, Maka apakah kamu tidak memikirkan?.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,63,'هٰذِهٖ جَهَنَّمُ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ','Inilah Jahannam yang dahulu kamu diancam (dengannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,64,'اِصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ','Masuklah ke dalamnya pada hari ini disebabkan kamu dahulu mengingkarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,65,'اَلْيَوْمَ نَخْتِمُ عَلٰٓى اَفْوَاهِهِمْ وَتُكَلِّمُنَآ اَيْدِيْهِمْ وَتَشْهَدُ اَرْجُلُهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ ','Pada hari ini Kami tutup mulut mereka; dan berkatalah kepada Kami tangan mereka dan memberi kesaksianlah kaki mereka terhadap apa yang dahulu mereka usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,66,'وَلَوْ نَشَاۤءُ لَطَمَسْنَا عَلٰٓى اَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَاَنّٰى يُبْصِرُوْنَ','Dan jikalau Kami menghendaki pastilah Kami hapuskan penglihatan mata mereka; lalu mereka berlomba-lomba (mencari) jalan, Maka betapakah mereka dapat melihat(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,67,'وَلَوْ نَشَاۤءُ لَمَسَخْنٰهُمْ عَلٰى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوْا مُضِيًّا وَّلَا يَرْجِعُوْنَ ࣖ ','Dan jikalau Kami menghendaki pastilah Kami ubah mereka di tempat mereka berada; maka mereka tidak sanggup berjalan lagi dan tidak (pula) sanggup kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,68,'وَمَنْ نُّعَمِّرْهُ نُنَكِّسْهُ فِى الْخَلْقِۗ اَفَلَا يَعْقِلُوْنَ ','Dan barangsiapa yang Kami panjangkan umurnya niscaya Kami kembalikan dia kepada kejadian(nya). Maka apakah mereka tidak memikirkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,69,'وَمَا عَلَّمْنٰهُ الشِّعْرَ وَمَا يَنْۢبَغِيْ لَهٗ ۗاِنْ هُوَ اِلَّا ذِكْرٌ وَّقُرْاٰنٌ مُّبِيْنٌ ۙ','Dan Kami tidak mengajarkan syair kepadanya (Muhammad) dan bersyair itu tidaklah layak baginya. Al Quran itu tidak lain hanyalah pelajaran dan kitab yang memberi penerangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,70,'لِّيُنْذِرَ مَنْ كَانَ حَيًّا وَّيَحِقَّ الْقَوْلُ عَلَى الْكٰفِرِيْنَ','supaya dia (Muhammad) memberi peringatan kepada orang-orang yang hidup (hatinya) dan supaya pastilah (ketetapan azab) terhadap orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,71,'اَوَلَمْ يَرَوْا اَنَّا خَلَقْنَا لَهُمْ مِّمَّا عَمِلَتْ اَيْدِيْنَآ اَنْعَامًا فَهُمْ لَهَا مَالِكُوْنَ ','Dan apakah mereka tidak melihat bahwa sesungguhnya Kami telah menciptakan binatang ternak untuk mereka yaitu sebahagian dari apa yang telah Kami ciptakan dengan kekuasaan Kami sendiri, lalu mereka menguasainya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,72,'وَذَلَّلْنٰهَا لَهُمْ فَمِنْهَا رَكُوْبُهُمْ وَمِنْهَا يَأْكُلُوْنَ ','Dan Kami tundukkan binatang-binatang itu untuk mereka; maka sebahagiannya menjadi tunggangan mereka dan sebahagiannya mereka makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,73,'وَلَهُمْ فِيْهَا مَنَافِعُ وَمَشَارِبُۗ اَفَلَا يَشْكُرُوْنَ ','Dan mereka memperoleh padanya manfaat-manfaat dan minuman. Maka mengapakah mereka tidak bersyukur?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,74,'وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لَّعَلَّهُمْ يُنْصَرُوْنَ ۗ ','Mereka mengambil sembahan-sembahan selain Allah, agar mereka mendapat pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,75,'لَا يَسْتَطِيْعُوْنَ نَصْرَهُمْۙ وَهُمْ لَهُمْ جُنْدٌ مُّحْضَرُوْنَ','Berhala-berhala itu tiada dapat menolong mereka; padahal berhala-berhala itu menjadi tentara yang disiapkan untuk menjaga mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,76,'فَلَا يَحْزُنْكَ قَوْلُهُمْ ۘاِنَّا نَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ','Maka janganlah ucapan mereka menyedihkan kamu. Sesungguhnya Kami mengetahui apa yang mereka rahasiakan dan apa yang mereka nyatakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,77,'اَوَلَمْ يَرَ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ ','Dan apakah manusia tidak memperhatikan bahwa Kami menciptakannya dari setitik air (mani), maka tiba-tiba ia menjadi penantang yang nyata!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,78,'وَضَرَبَ لَنَا مَثَلًا وَّنَسِيَ خَلْقَهٗۗ قَالَ مَنْ يُّحْيِ الْعِظَامَ وَهِيَ رَمِيْمٌ ','Dan ia membuat perumpamaan bagi Kami; dan dia lupa kepada kejadiannya; ia berkata: "Siapakah yang dapat menghidupkan tulang belulang, yang telah hancur luluh?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,79,'قُلْ يُحْيِيْهَا الَّذِيْٓ اَنْشَاَهَآ اَوَّلَ مَرَّةٍ ۗوَهُوَ بِكُلِّ خَلْقٍ عَلِيْمٌ ۙ','Katakanlah: "Ia akan dihidupkan oleh Tuhan yang menciptakannya kali yang pertama. Dan Dia Maha Mengetahui tentang segala makhluk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,80,' ِۨالَّذِيْ جَعَلَ لَكُمْ مِّنَ الشَّجَرِ الْاَخْضَرِ نَارًاۙ فَاِذَآ اَنْتُمْ مِّنْهُ تُوْقِدُوْنَ ','yaitu Tuhan yang menjadikan untukmu api dari kayu yang hijau, maka tiba-tiba kamu nyalakan (api) dari kayu itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,81,'اَوَلَيْسَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِقٰدِرٍ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ ۗبَلٰى وَهُوَ الْخَلّٰقُ الْعَلِيْمُ ','Dan tidaklah Tuhan yang menciptakan langit dan bumi itu berkuasa menciptakan yang serupa dengan itu? Benar, Dia berkuasa. Dan Dialah Maha Pencipta lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,82,'اِنَّمَآ اَمْرُهٗٓ اِذَآ اَرَادَ شَيْـًٔاۖ اَنْ يَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ ','Sesungguhnya keadaan-Nya apabila Dia menghendaki sesuatu hanyalah berkata kepadanya: "Jadilah!" maka terjadilah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,36,83,'فَسُبْحٰنَ الَّذِيْ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّاِلَيْهِ تُرْجَعُوْنَ ࣖ','Maka Maha Suci (Allah) yang di tangan-Nya kekuasaaan atas segala sesuatu dan kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,1,'وَالصّٰۤفّٰتِ صَفًّاۙ','Demi (rombongan) yang ber shaf-shaf dengan sebenar-benarnya],')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,2,'فَالزّٰجِرٰتِ زَجْرًاۙ','dan demi (rombongan) yang melarang dengan sebenar-benarnya (dari perbuatan-perbuatan maksiat),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,3,'فَالتّٰلِيٰتِ ذِكْرًاۙ','dan demi (rombongan) yang membacakan pelajaran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,4,'اِنَّ اِلٰهَكُمْ لَوَاحِدٌۗ ','Sesungguhnya Tuhanmu benar-benar Esa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,5,'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِۗ ','Tuhan langit dan bumi dan apa yang berada di antara keduanya dan Tuhan tempat-tempat terbit matahari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,6,'اِنَّا زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِزِيْنَةِ ِۨالْكَوَاكِبِۙ','Sesungguhnya Kami telah menghias langit yang terdekat dengan hiasan, yaitu bintang-bintang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,7,'وَحِفْظًا مِّنْ كُلِّ شَيْطٰنٍ مَّارِدٍۚ ','dan telah memeliharanya (sebenar-benarnya) dari setiap syaitan yang sangat durhaka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,8,'لَا يَسَّمَّعُوْنَ اِلَى الْمَلَاِ الْاَعْلٰى وَيُقْذَفُوْنَ مِنْ كُلِّ جَانِبٍۖ ','syaitan syaitan itu tidak dapat mendengar-dengarkan (pembicaraan) para malaikat dan mereka dilempari dari segala penjuru.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,9,'دُحُوْرًا وَّلَهُمْ عَذَابٌ وَّاصِبٌ ','Untuk mengusir mereka dan bagi mereka siksaan yang kekal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,10,'اِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَاَتْبَعَهٗ شِهَابٌ ثَاقِبٌ ','akan tetapi barangsiapa (di antara mereka) yang mencuri-curi (pembicaraan); maka ia dikejar oleh suluh api yang cemerlang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,11,'فَاسْتَفْتِهِمْ اَهُمْ اَشَدُّ خَلْقًا اَمْ مَّنْ خَلَقْنَا ۗاِنَّا خَلَقْنٰهُمْ مِّنْ طِيْنٍ لَّازِبٍ ','Maka tanyakanlah kepada mereka (musyrik Mekah): "Apakah mereka yang lebih kukuh kejadiannya ataukah apa yang telah Kami ciptakan itu?" Sesungguhnya Kami telah menciptakan mereka dari tanah liat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,12,'بَلْ عَجِبْتَ وَيَسْخَرُوْنَ ۖ ','Bahkan kamu menjadi heran (terhadap keingkaran mereka) dan mereka menghinakan kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,13,'وَاِذَا ذُكِّرُوْا لَا يَذْكُرُوْنَ ۖ ','Dan apabila mereka diberi pelajaran mereka tiada mengingatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,14,'وَاِذَا رَاَوْا اٰيَةً يَّسْتَسْخِرُوْنَۖ ','Dan apabila mereka melihat sesuatu tanda kebesaran Allah, mereka sangat menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,15,'وَقَالُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ۚ ','Dan mereka berkata "Ini tiada lain hanyalah sihir yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,16,'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ','Apakah apabila kami telah mati dan telah menjadi tanah serta menjadi tulang belulang, apakah benar-benar kami akan dibangkitkan (kembali)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,17,'اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَۗ ','Dan apakah bapak-bapak kami yang telah terdahulu (akan dibangkitkan pula)"?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,18,'قُلْ نَعَمْ وَاَنْتُمْ دَاخِرُوْنَۚ ','Katakanlah: "Ya, dan kamu akan terhina"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,19,'فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌ فَاِذَا هُمْ يَنْظُرُوْنَ ','Maka sesungguhnya kebangkitan itu hanya dengan satu teriakan saja; maka tiba-tiba mereka meIihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,20,'وَقَالُوْا يٰوَيْلَنَا هٰذَا يَوْمُ الدِّيْنِ ','Dan mereka berkata: "Aduhai celakalah kita!" Inilah hari pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,21,'هٰذَا يَوْمُ الْفَصْلِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ ࣖ','Inilah hari keputusan yang kamu selalu mendustakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,22,'اُحْشُرُوا الَّذِيْنَ ظَلَمُوْا وَاَزْوَاجَهُمْ وَمَا كَانُوْا يَعْبُدُوْنَ ۙ','(kepada malaikat diperintahkan): "Kumpulkanlah orang-orang yang zalim beserta teman sejawat mereka dan sembahan-sembahan yang selalu mereka sembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,23,'مِنْ دُوْنِ اللّٰهِ فَاهْدُوْهُمْ اِلٰى صِرَاطِ الْجَحِيْمِ ','selain Allah; maka tunjukkanlah kepada mereka jalan ke neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,24,'وَقِفُوْهُمْ اِنَّهُمْ مَّسْـُٔوْلُوْنَ ۙ','Dan tahanlah mereka (di tempat perhentian) karena sesungguhnya mereka akan ditanya:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,25,'مَا لَكُمْ لَا تَنَاصَرُوْنَ ','"Kenapa kamu tidak tolong menolong?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,26,'بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُوْنَ','Bahkan mereka pada hari itu menyerah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,27,'وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ ','Sebahagian dan mereka menghadap kepada sebahagian yang lain berbantah-bantahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,28,'قَالُوْٓا اِنَّكُمْ كُنْتُمْ تَأْتُوْنَنَا عَنِ الْيَمِيْنِ ','Pengikut-pengikut mereka berkata (kepada pemimpin-pemimpin mereka): "Sesungguhnya kamulah yang datang kepada kami dan kanan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,29,'قَالُوْا بَلْ لَّمْ تَكُوْنُوْا مُؤْمِنِيْنَۚ ','Pemimpin-pemimpin mereka menjawab: "Sebenarnya kamulah yang tidak beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,30,'وَمَا كَانَ لَنَا عَلَيْكُمْ مِّنْ سُلْطٰنٍۚ بَلْ كُنْتُمْ قَوْمًا طٰغِيْنَ ','Dan sekali-kali kami tidak berkuasa terhadapmu, bahkan kamulah kaum yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,31,'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَآ  ۖاِنَّا لَذَاۤىِٕقُوْنَ ','Maka pastilah putusan (azab) Tuhan kita menimpa atas kita; sesungguhnya kita akan merasakan (azab itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,32,'فَاَغْوَيْنٰكُمْ اِنَّا كُنَّا غٰوِيْنَ ','Maka kami telah menyesatkan kamu, sesungguhnya kami adalah orang-orang yang sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,33,'فَاِنَّهُمْ يَوْمَىِٕذٍ فِى الْعَذَابِ مُشْتَرِكُوْنَ','Maka sesungguhnya mereka pada hari itu bersama-sama dalam azab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,34,'اِنَّا كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ','Sesungguhnya demikianlah Kami berbuat terhadap orang-orang yang berbuat jahat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,35,'اِنَّهُمْ كَانُوْٓا اِذَا قِيْلَ لَهُمْ لَآ اِلٰهَ اِلَّا اللّٰهُ يَسْتَكْبِرُوْنَ ۙ','Sesungguhnya mereka dahulu apabila dikatakan kepada mereka: "Laa ilaaha illallah" (Tiada Tuhan yang berhak disembah melainkan Allah) mereka menyombongkan diri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,36,'وَيَقُوْلُوْنَ اَىِٕنَّا لَتَارِكُوْٓا اٰلِهَتِنَا لِشَاعِرٍ مَّجْنُوْنٍ ۗ ','dan mereka berkata: "Apakah sesungguhnya kami harus meninggalkan sembahan-sembahan kami karena seorang penyair gila?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,37,'بَلْ جَاۤءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَلِيْنَ','Sebenarnya dia (Muhammad) telah datang membawa kebenaran dan membenarkan rasul-rasul (sebelumnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,38,'اِنَّكُمْ لَذَاۤىِٕقُوا الْعَذَابِ الْاَلِيْمِ ۚ ','Sesungguhnya kamu pasti akan merasakan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,39,'وَمَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَۙ','Dan kamu tidak diberi pembalasan melainkan terhadap kejahatan yang telah kamu kerjakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,40,'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ ','tetapi hamba-hamba Allah yang dibersihkan (dari dosa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,41,'اُولٰۤىِٕكَ لَهُمْ رِزْقٌ مَّعْلُوْمٌۙ','Mereka itu memperoleh rezeki yang tertentu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,42,'فَوَاكِهُ ۚوَهُمْ مُّكْرَمُوْنَۙ','yaitu buah-buahan. Dan mereka adalah orang-orang yang dimuliakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,43,'فِيْ جَنّٰتِ النَّعِيْمِۙ','di dalam surga-surga yang penuh nikmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,44,'عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ','di atas takhta-takhta kebesaran berhadap-hadapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,45,'يُطَافُ عَلَيْهِمْ بِكَأْسٍ مِّنْ مَّعِيْنٍۢ ۙ','Diedarkan kepada mereka gelas yang berisi khamar dari sungai yang mengalir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,46,'بَيْضَاۤءَ لَذَّةٍ لِّلشّٰرِبِيْنَۚ ','(Warnanya) putih bersih, sedap rasanya bagi orang-orang yang minum.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,47,'لَا فِيْهَا غَوْلٌ وَّلَا هُمْ عَنْهَا يُنْزَفُوْنَ','Tidak ada dalam khamar itu alkohol dan mereka tiada mabuk karenanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,48,'وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ عِيْنٌ ۙ','Di sisi mereka ada bidadari-bidadari yang tidak liar pandangannya dan jelita matanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,49,'كَاَنَّهُنَّ بَيْضٌ مَّكْنُوْنٌ','seakan-akan mereka adalah telur (burung unta) yang tersimpan dengan baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,50,'فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ','Lalu sebahagian mereka menghadap kepada sebahagian yang lain sambil bercakap-cakap.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,51,'قَالَ قَاۤىِٕلٌ مِّنْهُمْ اِنِّيْ كَانَ لِيْ قَرِيْنٌۙ','Berkatalah salah seorang di antara mereka: "Sesungguhnya aku dahulu (di dunia) mempunyai seorang teman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,52,'يَّقُوْلُ اَىِٕنَّكَ لَمِنَ الْمُصَدِّقِيْنَ','yang berkata: "Apakah kamu sungguh-sungguh termasuk orang-orang yang membenarkan (hari berbangkit)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,53,'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَدِيْنُوْنَ','Apakah bila kita telah mati dan kita telah menjadi tanah dan tulang belulang, apakah sesungguhnya kita benar-benar (akan dibangkitkan) untuk diberi pembalasan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,54,'قَالَ هَلْ اَنْتُمْ مُّطَّلِعُوْنَ','Berkata pulalah ia: "Maukah kamu meninjau (temanku itu)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,55,'فَاطَّلَعَ فَرَاٰهُ فِيْ سَوَاۤءِ الْجَحِيْمِ','Maka ia meninjaunya, lalu dia melihat temannya itu di tengah-tengah neraka menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,56,'قَالَ تَاللّٰهِ اِنْ كِدْتَّ لَتُرْدِيْنِ ۙ','Ia berkata (pula): "Demi Allah, sesungguhnya kamu benar-benar hampir mencelakakanku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,57,'وَلَوْلَا نِعْمَةُ رَبِّيْ لَكُنْتُ مِنَ الْمُحْضَرِيْنَ','jikalau tidaklah karena nikmat Tuhanku pastilah aku termasuk orang-orang yang diseret (ke neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,58,'اَفَمَا نَحْنُ بِمَيِّتِيْنَۙ','Maka apakah kita tidak akan mati?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,59,'اِلَّا مَوْتَتَنَا الْاُوْلٰى وَمَا نَحْنُ بِمُعَذَّبِيْنَ','melainkan hanya kematian kita yang pertama saja (di dunia), dan kita tidak akan disiksa (di akhirat ini)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,60,'اِنَّ هٰذَا لَهُوَ الْفَوْزُ الْعَظِيْمُ','Sesungguhnya ini benar-benar kemenangan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,61,'لِمِثْلِ هٰذَا فَلْيَعْمَلِ الْعٰمِلُوْنَ','Untuk kemenangan serupa ini hendaklah berusaha orang-orang yang bekerja"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,62,'اَذٰلِكَ خَيْرٌ نُّزُلًا اَمْ شَجَرَةُ الزَّقُّوْمِ','(Makanan surga) itukah hidangan yang lebih baik ataukah pohon zaqqum.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,63,'اِنَّا جَعَلْنٰهَا فِتْنَةً لِّلظّٰلِمِيْنَ','Sesungguhnya Kami menjadikan pohon zaqqum itu sebagai siksaan bagi orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,64,'اِنَّهَا شَجَرَةٌ تَخْرُجُ فِيْٓ اَصْلِ الْجَحِيْمِۙ','Sesungguhnya dia adalah sebatang pohon yang ke luar dan dasar neraka yang menyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,65,'طَلْعُهَا كَاَنَّهٗ رُءُوْسُ الشَّيٰطِيْنِ','mayangnya seperti kepala syaitan-syaitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,66,'فَاِنَّهُمْ لَاٰكِلُوْنَ مِنْهَا فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۗ ','Maka sesungguhnya mereka benar-benar memakan sebagian dari buah pohon itu, maka mereka memenuhi perutnya dengan buah zaqqum itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,67,'ثُمَّ اِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِّنْ حَمِيْمٍۚ ','Kemudian sesudah makan buah pohon zaqqum itu pasti mereka mendapat minuman yang bercampur dengan air yang sangat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,68,'ثُمَّ اِنَّ مَرْجِعَهُمْ لَاِلَى الْجَحِيْمِ','Kemudian sesungguhnya tempat kembali mereka benar-benar ke neraka Jahim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,69,'اِنَّهُمْ اَلْفَوْا اٰبَاۤءَهُمْ ضَاۤلِّيْنَۙ','Karena sesungguhnya mereka mendapati bapak-bapak mereka dalam Keadaaan sesat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,70,'فَهُمْ عَلٰٓى اٰثٰرِهِمْ يُهْرَعُوْنَ','Lalu mereka sangat tergesa-gesa mengikuti jejak orang-orang tua mereka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,71,'وَلَقَدْ ضَلَّ قَبْلَهُمْ اَكْثَرُ الْاَوَّلِيْنَۙ','Dan sesungguhnya telah sesat sebelum mereka (Quraisy) sebagian besar dari orang-orang yang dahulu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,72,'وَلَقَدْ اَرْسَلْنَا فِيْهِمْ مُّنْذِرِيْنَ','dan sesungguhnya telah Kami utus pemberi-pemberi peringatan (rasul-rasul) di kalangan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,73,'فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَۙ','Maka perhatikanlah bagaimana kesudahan orang-orang yang diberi peringatan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,74,'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ ࣖ ','Tetapi hamba-hamba Allah yang bersihkan (dari dosa tidak akan diazab).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,75,'وَلَقَدْ نَادٰىنَا نُوْحٌ فَلَنِعْمَ الْمُجِيْبُوْنَۖ ','Sesungguhnya Nuh telah menyeru Kami: maka sesungguhnya sebaik-baik yang memperkenankan (adalah Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,76,'وَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِۖ ','Dan Kami telah menyelamatkannya dan pengikutnya dari bencana yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,77,'وَجَعَلْنَا ذُرِّيَّتَهٗ هُمُ الْبٰقِيْنَ','Dan Kami jadikan anak cucunya orang-orang yang melanjutkan keturunan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,78,'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ ','Dan Kami abadikan untuk Nuh itu (pujian yang baik) di kalangan orang-orang yang datang kemudian;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,79,'سَلٰمٌ عَلٰى نُوْحٍ فِى الْعٰلَمِيْنَ','"Kesejahteraan dilimpahkan atas Nuh di seluruh alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,80,'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,81,'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ','Sesungguhnya dia termasuk di antara hamba-hamba Kami yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,82,'ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ','Kemudian Kami tenggelamkan orang-orang yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,83,'وَاِنَّ مِنْ شِيْعَتِهٖ لَاِبْرٰهِيْمَ ۘ ','Dan sesungguhnya Ibrahim benar-benar termasuk golongannya (Nuh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,84,'اِذْ جَاۤءَ رَبَّهٗ بِقَلْبٍ سَلِيْمٍۙ','(lngatlah) ketika ia datang kepada Tuhannya dengan hati yang suci:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,85,'اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَاذَا تَعْبُدُوْنَ ۚ ','(Ingatlah) ketika ia berkata kepada bapaknya dan kaumnya: "Apakah yang kamu sembah itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,86,'اَىِٕفْكًا اٰلِهَةً دُوْنَ اللّٰهِ تُرِيْدُوْنَۗ ','Apakah kamu menghendaki sembahan-sembahan selain Allah dengan jalan berbohong?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,87,'فَمَا ظَنُّكُمْ بِرَبِّ الْعٰلَمِيْنَ','Maka apakah anggapanmu terhadap Tuhan semesta alam?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,88,'فَنَظَرَ نَظْرَةً فِى النُّجُوْمِۙ','Lalu ia memandang sekali pandang ke bintang-bintang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,89,'فَقَالَ اِنِّيْ سَقِيْمٌ','Kemudian ia berkata: "Sesungguhnya aku sakit".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,90,'فَتَوَلَّوْا عَنْهُ مُدْبِرِيْنَ','Lalu mereka berpaling daripadanya dengan membelakang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,91,'فَرَاغَ اِلٰٓى اٰلِهَتِهِمْ فَقَالَ اَلَا تَأْكُلُوْنَۚ ','Kemudian ia pergi dengan diam-diam kepada berhala-berhala mereka; lalu ia berkata: "Apakah kamu tidak makan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,92,'مَا لَكُمْ لَا تَنْطِقُوْنَ','Kenapa kamu tidak menjawab?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,93,'فَرَاغَ عَلَيْهِمْ ضَرْبًا ۢبِالْيَمِيْنِ','Lalu dihadapinya berhala-berhala itu sambil memukulnya dengan tangan kanannya (dengan kuat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,94,'فَاَقْبَلُوْٓا اِلَيْهِ يَزِفُّوْنَ','Kemudian kaumnya datang kepadanya dengan bergegas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,95,'قَالَ اَتَعْبُدُوْنَ مَا تَنْحِتُوْنَۙ','Ibrahim berkata: "Apakah kamu menyembah patung-patung yang kamu pahat itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,96,'وَاللّٰهُ خَلَقَكُمْ وَمَا تَعْمَلُوْنَ','Padahal Allah-lah yang menciptakan kamu dan apa yang kamu perbuat itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,97,'قَالُوا ابْنُوْا لَهٗ بُنْيَانًا فَاَلْقُوْهُ فِى الْجَحِيْمِ','Mereka berkata: "Dirikanlah suatu bangunan untuk (membakar) Ibrahim;lalu lemparkanlah dia ke dalam api yang menyala-nyala itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,98,'فَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَسْفَلِيْنَ','Mereka hendak melakukan tipu muslihat kepadanya, maka Kami jadikan mereka orang-orang yang hina.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,99,'وَقَالَ اِنِّيْ ذَاهِبٌ اِلٰى رَبِّيْ سَيَهْدِيْنِ','Dan Ibrahim berkata: "Sesungguhnya aku pergi menghadap kepada Tuhanku, dan Dia akan memberi petunjuk kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,100,'رَبِّ هَبْ لِيْ مِنَ الصّٰلِحِيْنَ','Ya Tuhanku, anugrahkanlah kepadaku (seorang anak) yang termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,101,'فَبَشَّرْنٰهُ بِغُلٰمٍ حَلِيْمٍ','Maka Kami beri dia khabar gembira dengan seorang anak yang amat sabar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,102,'فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يٰبُنَيَّ اِنِّيْٓ اَرٰى فِى الْمَنَامِ اَنِّيْٓ اَذْبَحُكَ فَانْظُرْ مَاذَا تَرٰىۗ قَالَ يٰٓاَبَتِ افْعَلْ مَا تُؤْمَرُۖ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰبِرِيْنَ','Maka tatkala anak itu sampai (pada umur sanggup) berusaha bersama-sama Ibrahim, Ibrahim berkata: "Hai anakku sesungguhnya aku melihat dalam mimpi bahwa aku menyembelihmu. Maka fikirkanlah apa pendapatmu!" Ia menjawab: "Hai bapakku, kerjakanlah apa yang diperintahkan kepadamu; insya Allah kamu akan mendapatiku termasuk orang-orang yang sabar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,103,'فَلَمَّآ اَسْلَمَا وَتَلَّهٗ لِلْجَبِيْنِۚ ','Tatkala keduanya telah berserah diri dan Ibrahim membaringkan anaknya atas pelipis(nya), (nyatalah kesabaran keduanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,104,'وَنَادَيْنٰهُ اَنْ يّٰٓاِبْرٰهِيْمُ ۙ','Dan Kami panggillah dia: "Hai Ibrahim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,105,'قَدْ صَدَّقْتَ الرُّؤْيَا ۚاِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','sesungguhnya kamu telah membenarkan mimpi itu sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,106,'اِنَّ هٰذَا لَهُوَ الْبَلٰۤؤُا الْمُبِيْنُ','Sesungguhnya ini benar-benar suatu ujian yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,107,'وَفَدَيْنٰهُ بِذِبْحٍ عَظِيْمٍ','Dan Kami tebus anak itu dengan seekor sembelihan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,108,'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ ','Kami abadikan untuk Ibrahim itu (pujian yang baik) di kalangan orang-orang yang datang kemudian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,109,'سَلٰمٌ عَلٰٓى اِبْرٰهِيْمَ','(yaitu)"Kesejahteraan dilimpahkan atas Ibrahim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,110,'كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,111,'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ','Sesungguhnya ia termasuk hamba-hamba Kami yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,112,'وَبَشَّرْنٰهُ بِاِسْحٰقَ نَبِيًّا مِّنَ الصّٰلِحِيْنَ','Dan Kami beri dia kabar gembira dengan (kelahiran) Ishaq seorang nabi yang termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,113,'وَبٰرَكْنَا عَلَيْهِ وَعَلٰٓى اِسْحٰقَۗ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وَّظَالِمٌ لِّنَفْسِهٖ مُبِيْنٌ ࣖ','Kami limpahkan keberkatan atasnya dan atas Ishaq. Dan diantara anak cucunya ada yang berbuat baik dan ada (pula) yang Zalim terhadap dirinya sendiri dengan nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,114,'وَلَقَدْ مَنَنَّا عَلٰى مُوْسٰى وَهٰرُوْنَ ۚ ','Dan sesungguhnya Kami telah melimpahkan nikmat atas Musa dan Harun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,115,'وَنَجَّيْنٰهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظِيْمِۚ ','Dan Kami selamatkan keduanya dan kaumnya dari bencana yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,116,'وَنَصَرْنٰهُمْ فَكَانُوْا هُمُ الْغٰلِبِيْنَۚ ','Dan Kami tolong mereka, maka jadilah mereka orang-orang yang menang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,117,'وَاٰتَيْنٰهُمَا الْكِتٰبَ الْمُسْتَبِيْنَ ۚ ','Dan Kami berikan kepada keduanya kitab yang sangat jelas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,118,'وَهَدَيْنٰهُمَا الصِّرَاطَ الْمُسْتَقِيْمَۚ ','Dan Kami tunjuki keduanya ke jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,119,'وَتَرَكْنَا عَلَيْهِمَا فِى الْاٰخِرِيْنَ ۖ ','Dan Kami abadikan untuk keduanya (pujian yang baik) dikalangan orang-orang yang datang kemudian;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,120,'سَلٰمٌ عَلٰى مُوْسٰى وَهٰرُوْنَ','(yaitu): "Kesejahteraan dilimpahkan atas Musa dan Harun".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,121,'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,122,'اِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ','Sesungguhnya keduanya termasuk hamba-hamba Kami yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,123,'وَاِنَّ اِلْيَاسَ لَمِنَ الْمُرْسَلِيْنَۗ ','Dan sesungguhnya Ilyas benar-benar termasuk salah seorang rasul-rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,124,'اِذْ قَالَ لِقَوْمِهٖٓ اَلَا تَتَّقُوْنَ','(ingatlah) ketika ia berkata kepada kaumnya: "Mengapa kamu tidak bertakwa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,125,'اَتَدْعُوْنَ بَعْلًا وَّتَذَرُوْنَ اَحْسَنَ الْخَالِقِيْنَۙ','Patutkah kamu menyembah Ba´l dan kamu tinggalkan sebaik-baik Pencipta,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,126,'اللّٰهَ رَبَّكُمْ وَرَبَّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ','(yaitu) Allah Tuhanmu dan Tuhan bapak-bapakmu yang terdahulu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,127,'فَكَذَّبُوْهُ فَاِنَّهُمْ لَمُحْضَرُوْنَۙ','Maka mereka mendustakannya, karena itu mereka akan diseret (ke neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,128,'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ','kecuali hamba-hamba Allah yang dibersihkan (dari dosa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,129,'وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۙ','Dan Kami abadikan untuk Ilyas (pujian yang baik) di kalangan orang-orang yang datang kemudian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,130,'سَلٰمٌ عَلٰٓى اِلْ يَاسِيْنَ','(yaitu): "Kesejahteraan dilimpahkan atas Ilyas?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,131,'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ','Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,132,'اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ','Sesungguhnya dia termasuk hamba-hamba Kami yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,133,'وَاِنَّ لُوْطًا لَّمِنَ الْمُرْسَلِيْنَۗ ','Sesungguhnya Luth benar-benar salah seorang rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,134,'اِذْ نَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْۙنَ','(Ingatlah) ketika Kami selamatkan dia dan keluarganya (pengikut-pengikutnya) semua,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,135,'اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ','kecuali seorang perempuan tua (isterinya yang berada) bersama-sama orang yang tinggal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,136,'ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ','Kemudian Kami binasakan orang-orang yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,137,'وَاِنَّكُمْ لَتَمُرُّوْنَ عَلَيْهِمْ مُّصْبِحِيْنَۙ','Dan sesungguhnya kamu (hai penduduk Mekah) benar-benar akan melalui (bekas-bekas) mereka di waktu pagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,138,'وَبِالَّيْلِۗ اَفَلَا تَعْقِلُوْنَ ࣖ ','dan di waktu malam. Maka apakah kamu tidak memikirkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,139,'وَاِنَّ يُوْنُسَ لَمِنَ الْمُرْسَلِيْنَۗ ','Sesungguhnya Yunus benar-benar salah seorang rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,140,'اِذْ اَبَقَ اِلَى الْفُلْكِ الْمَشْحُوْنِۙ','(ingatlah) ketika ia lari, ke kapal yang penuh muatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,141,'فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَضِيْنَۚ','kemudian ia ikut berundi lalu dia termasuk orang-orang yang kalah dalam undian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,142,'فَالْتَقَمَهُ الْحُوْتُ وَهُوَ مُلِيْمٌ ','Maka ia ditelan oleh ikan besar dalam keadaan tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,143,'فَلَوْلَآ اَنَّهٗ كَانَ مِنَ الْمُسَبِّحِيْنَ ۙ','Maka kalau sekiranya dia tidak termasuk orang-orang yang banyak mengingat Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,144,'لَلَبِثَ فِيْ بَطْنِهٖٓ اِلٰى يَوْمِ يُبْعَثُوْنَۚ ','niscaya ia akan tetap tinggal di perut ikan itu sampai hari berbangkit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,145,'فَنَبَذْنٰهُ بِالْعَرَاۤءِ وَهُوَ سَقِيْمٌ ۚ ','Kemudian Kami lemparkan dia ke daerah yang tandus, sedang ia dalam keadaan sakit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,146,'وَاَنْۢبَتْنَا عَلَيْهِ شَجَرَةً مِّنْ يَّقْطِيْنٍۚ ','Dan Kami tumbuhkan untuk dia sebatang pohon dari jenis labu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,147,'وَاَرْسَلْنٰهُ اِلٰى مِائَةِ اَلْفٍ اَوْ يَزِيْدُوْنَۚ ','Dan Kami utus dia kepada seratus ribu orang atau lebih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,148,'فَاٰمَنُوْا فَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ','Lalu mereka beriman, karena itu Kami anugerahkan kenikmatan hidup kepada mereka hingga waktu yang tertentu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,149,'فَاسْتَفْتِهِمْ اَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُوْنَۚ ','Tanyakanlah (ya Muhammad) kepada mereka (orang-orang kafir Mekah): "Apakah untuk Tuhanmu anak-anak perempuan dan untuk mereka anak laki-laki,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,150,'اَمْ خَلَقْنَا الْمَلٰۤىِٕكَةَ اِنَاثًا وَّهُمْ شَاهِدُوْنَ','atau apakah Kami menciptakan malaikat-malaikat berupa perempuan dan mereka menyaksikan(nya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,151,'اَلَآ اِنَّهُمْ مِّنْ اِفْكِهِمْ لَيَقُوْلُوْنَۙ','Ketahuilah bahwa sesungguhnya mereka dengan kebohongannya benar-benar mengatakan:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,152,'وَلَدَ اللّٰهُ ۙوَاِنَّهُمْ لَكٰذِبُوْنَۙ','"Allah beranak". Dan sesungguhnya mereka benar-benar orang yang berdusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,153,'اَصْطَفَى الْبَنَاتِ عَلَى الْبَنِيْنَۗ ','Apakah Tuhan memilih (mengutamakan) anak-anak perempuan daripada anak laki-laki?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,154,'مَا لَكُمْۗ  كَيْفَ تَحْكُمُوْنَ','Apakah yang terjadi padamu? Bagaimana (caranya) kamu menetapkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,155,'اَفَلَا تَذَكَّرُوْنَۚ ','Maka apakah kamu tidak memikirkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,156,'اَمْ لَكُمْ سُلْطٰنٌ مُّبِيْنٌۙ','Atau apakah kamu mempunyai bukti yang nyata?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,157,'فَأْتُوْا بِكِتٰبِكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ','Maka bawalah kitabmu jika kamu memang orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,158,'وَجَعَلُوْا بَيْنَهٗ وَبَيْنَ الْجِنَّةِ نَسَبًا ۗوَلَقَدْ عَلِمَتِ الْجِنَّةُ اِنَّهُمْ لَمُحْضَرُوْنَۙ','Dan mereka adakan (hubungan) nasab antara Allah dan antara jin. Dan sesungguhnya jin mengetahui bahwa mereka benar-benar akan diseret (ke neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,159,'سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَۙ','Maha Suci Allah dari apa yang mereka sifatkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,160,'اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ','Kecuali hamba-hamba Allah yang dibersihkan dari (dosa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,161,'فَاِنَّكُمْ وَمَا تَعْبُدُوْنَۙ','Maka sesungguhnya kamu dan apa-apa yang kamu sembah itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,162,'مَآ اَنْتُمْ عَلَيْهِ بِفَاتِنِيْنَۙ','Sekali-kali tidak dapat menyesatkan (seseorang) terhadap Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,163,'اِلَّا مَنْ هُوَ صَالِ الْجَحِيْمِ','kecuali orang-orang yang akan masuk neraka yang menyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,164,'وَمَا مِنَّآ اِلَّا لَهٗ مَقَامٌ مَّعْلُوْمٌۙ','Tiada seorangpun di antara kami (malaikat) melainkan mempunyai kedudukan yang tertentu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,165,'وَاِنَّا لَنَحْنُ الصَّۤافُّوْنَۖ','dan sesungguhnya kami benar-benar bershaf-shaf (dalam menunaikan perintah Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,166,'وَاِنَّا لَنَحْنُ الْمُسَبِّحُوْنَ','Dan sesungguhnya kami benar-benar bertasbih (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,167,'وَاِنْ كَانُوْا لَيَقُوْلُوْنَۙ','Sesungguhnya mereka benar-benar akan berkata:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,168,'لَوْ اَنَّ عِنْدَنَا ذِكْرًا مِّنَ الْاَوَّلِيْنَۙ','"Kalau sekiranya di sksi kami ada sebuah kitab dari (kitab-kitab yang diturunkan) kepada orang-orang dahulu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,169,'لَكُنَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ','benar-benar kami akan jadi hamba Allah yang dibersihkan (dari dosa)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,170,'فَكَفَرُوْا بِهٖۚ فَسَوْفَ يَعْلَمُوْنَ','Tetapi mereka mengingkarinya (Al Quran); maka kelak mereka akan mengetahui (akibat keingkarannya itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,171,'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِيْنَ ۖ','Dan sesungguhnya telah tetap janji Kami kepada hamba-hamba Kami yang menjadi rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,172,'اِنَّهُمْ لَهُمُ الْمَنْصُوْرُوْنَۖ','(yaitu) sesungguhnya mereka itulah yang pasti mendapat pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,173,'وَاِنَّ جُنْدَنَا لَهُمُ الْغٰلِبُوْنَ','Dan sesungguhnya tentara Kami itulah yang pasti menang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,174,'فَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ','Maka berpalinglah kamu (Muhammad) dari mereka sampai suatu ketika.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,175,'وَّاَبْصِرْهُمْۗ فَسَوْفَ يُبْصِرُوْنَ','Dan lihatlah mereka, maka kelak mereka akan melihat (azab itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,176,'اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ','Maka apakah mereka meminta supaya siksa Kami disegerakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,177,'فَاِذَا نَزَلَ بِسَاحَتِهِمْ فَسَاۤءَ صَبَاحُ الْمُنْذَرِيْنَ','Maka apabila siksaan itu turun dihalaman mereka, maka amat buruklah pagi hari yang dialami oleh orang-orang yang diperingatkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,178,'وَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ','Dan berpalinglah kamu dari mereka hingga suatu ketika.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,179,'وَّاَبْصِرْۗ فَسَوْفَ يُبْصِرُوْنَ','Dan lihatlah, maka kelak mereka juga akan melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,180,'سُبْحٰنَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُوْنَۚ','Maha Suci Tuhanmu Yang mempunyai keperkasaan dari apa yang mereka katakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,181,'وَسَلٰمٌ عَلَى الْمُرْسَلِيْنَۚ ','Dan kesejahteraan dilimpahkan atas para rasul.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,37,182,'وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ','Dan segala puji bagi Allah Tuhan seru sekalian alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,1,'صۤ ۗوَالْقُرْاٰنِ ذِى الذِّكْرِۗ','Shaad, demi Al Quran yang mempunyai keagungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,2,'بَلِ الَّذِيْنَ كَفَرُوْا فِيْ عِزَّةٍ وَّشِقَاقٍ','Sebenarnya orang-orang kafir itu (berada) dalam kesombongan dan permusuhan yang sengit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,3,'كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ فَنَادَوْا وَّلَاتَ حِيْنَ مَنَاصٍ','Betapa banyaknya umat sebelum mereka yang telah Kami binasakan, lalu mereka meminta tolong padahal (waktu itu) bukanlah saat untuk lari melepaskan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,4,'وَعَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ ۖوَقَالَ الْكٰفِرُوْنَ هٰذَا سٰحِرٌ كَذَّابٌۚ','Dan mereka heran karena mereka kedatangan seorang pemberi peringatan (rasul) dari kalangan mereka; dan orang-orang kafir berkata: "Ini adalah seorang ahli sihir yang banyak berdusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,5,'اَجَعَلَ الْاٰلِهَةَ اِلٰهًا وَّاحِدًا ۖاِنَّ هٰذَا لَشَيْءٌ عُجَابٌ','Mengapa ia menjadikan tuhan-tuhan itu Tuhan Yang Satu saja? Sesungguhnya ini benar-benar suatu hal yang sangat mengherankan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,6,'وَانْطَلَقَ الْمَلَاُ مِنْهُمْ اَنِ امْشُوْا وَاصْبِرُوْا عَلٰٓى اٰلِهَتِكُمْ ۖاِنَّ هٰذَا لَشَيْءٌ يُّرَادُ ۖ','Dan pergilah pemimpin-pemimpin mereka (seraya berkata): "Pergilah kamu dan tetaplah (menyembah) tuhan-tuhanmu, sesungguhnya ini benar-benar suatu hal yang dikehendaki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,7,'مَا سَمِعْنَا بِهٰذَا فِى الْمِلَّةِ الْاٰخِرَةِ ۖاِنْ هٰذَآ اِلَّا اخْتِلَاقٌۚ','Kami tidak pernah mendengar hal ini dalam agama yang terakhir; ini (mengesakan Allah), tidak lain hanyalah (dusta) yang diada-adakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,8,'اَؤُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْۢ بَيْنِنَا ۗبَلْ هُمْ فِيْ شَكٍّ مِّنْ ذِكْرِيْۚ بَلْ لَّمَّا يَذُوْقُوْا عَذَابِ ۗ','mengapa Al Quran itu diturunkan kepadanya di antara kita?" Sebenarnya mereka ragu-ragu terhadap Al Quran-Ku, dan sebenarnya mereka belum merasakan azab-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,9,'اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَحْمَةِ رَبِّكَ الْعَزِيْزِ الْوَهَّابِۚ','Atau apakah mereka itu mempunyai perbendaharaan rahmat Tuhanmu Yang Maha Perkasa lagi Maha Pemberi?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,10,'اَمْ لَهُمْ مُّلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗفَلْيَرْتَقُوْا فِى الْاَسْبَابِ','Atau apakah bagi mereka kerajaan langit dan bumi dan yang ada di antara keduanya? (Jika ada), maka hendaklah mereka menaiki tangga-tangga (ke langit).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,11,'جُنْدٌ مَّا هُنَالِكَ مَهْزُوْمٌ مِّنَ الْاَحْزَابِ','Suatu tentara yang besar yang berada disana dari golongan-golongan yang berserikat, pasti akan dikalahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,12,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّفِرْعَوْنُ ذُو الْاَوْتَادِۙ','Telah mendustakan (rasul-rasul pula) sebelum mereka itu kaum Nuh, ´Aad, Fir´aun yang mempunyai tentara yang banyak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,13,'وَثَمُوْدُ وَقَوْمُ لُوْطٍ وَّاَصْحٰبُ لْـَٔيْكَةِ ۗ اُولٰۤىِٕكَ الْاَحْزَابُ','dan Tsamud, kaum Luth dan penduduk Aikah. Mereka itulah golongan-golongan yang bersekutu (menentang rasul-rasul).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,14,'اِنْ كُلٌّ اِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ ࣖ','Semua mereka itu tidak lain hanyalah mendustakan rasul-rasul, maka pastilah (bagi mereka) azab-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,15,'وَمَا يَنْظُرُ هٰٓؤُلَاۤءِ اِلَّا صَيْحَةً وَّاحِدَةً مَّا لَهَا مِنْ فَوَاقٍ','Tidaklah yang mereka tunggu melainkan hanya satu teriakan saja yang tidak ada baginya saat berselang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,16,'وَقَالُوْا رَبَّنَا عَجِّلْ لَّنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ','Dan mereka berkata: "Ya Tuhan kami cepatkanlah untuk kami azab yang diperuntukkan bagi kami sebelum hari berhisab".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,17,'اِصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاذْكُرْ عَبْدَنَا دَاوٗدَ ذَا الْاَيْدِۚ اِنَّهٗٓ اَوَّابٌ','Bersabarlah atas segala apa yang mereka katakan; dan ingatlah hamba Kami Daud yang mempunyai kekuatan; sesungguhnya dia amat taat (kepada Tuhan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,18,'اِنَّا سَخَّرْنَا الْجِبَالَ مَعَهٗ يُسَبِّحْنَ بِالْعَشِيِّ وَالْاِشْرَاقِۙ','Sesungguhnya Kami menundukkan gunung-gunung untuk bertasbih bersama dia (Daud) di waktu petang dan pagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,19,'وَالطَّيْرَمَحْشُوْرَةً ۗ كُلٌّ لَهٗٓ اَوَّابٌ','dan (Kami tundukkan pula) burung-burung dalam keadaan terkumpul. Masing-masingnya amat taat kepada Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,20,'وَشَدَدْنَا مُلْكَهٗ وَاٰتَيْنٰهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ','Dan Kami kuatkan kerajaannya dan Kami berikan kepadanya hikmahdan kebijaksanaan dalam menyelesaikan perselisihan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,21,'وَهَلْ اَتٰىكَ نَبَؤُ الْخَصْمِۘ اِذْ تَسَوَّرُوا الْمِحْرَابَۙ','Dan adakah sampai kepadamu berita orang-orang yang berperkara ketika mereka memanjat pagar?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,22,'اِذْ دَخَلُوْا عَلٰى دَاوٗدَ فَفَزِعَ مِنْهُمْ قَالُوْا لَا تَخَفْۚ خَصْمٰنِ بَغٰى بَعْضُنَا عَلٰى بَعْضٍ فَاحْكُمْ بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَآ اِلٰى سَوَاۤءِ الصِّرَاطِ','Ketika mereka masuk (menemui) Daud lalu ia terkejut karena kedatangan) mereka. Mereka berkata: "Janganlah kamu merasa takut; (kami) adalah dua orang yang berperkara yang salah seorang dari kami berbuat zalim kepada yang lain; maka berilah keputusan antara kami dengan adil dan janganlah kamu menyimpang dari kebenaran dan tunjukilah kami ke jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,23,'اِنَّ هٰذَآ اَخِيْ ۗ لَهٗ تِسْعٌ وَّتِسْعُوْنَ نَعْجَةً وَّلِيَ نَعْجَةٌ وَّاحِدَةٌ ۗفَقَالَ اَكْفِلْنِيْهَا وَعَزَّنِيْ فِى الْخِطَابِ','Sesungguhnya saudaraku ini mempunyai sembilan puluh sembilan ekor kambing betina dan aku mempunyai seekor saja. Maka dia berkata: "Serahkanlah kambingmu itu kepadaku dan dia mengalahkan aku dalam perdebatan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,24,'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ اِلٰى نِعَاجِهٖۗ وَاِنَّ كَثِيْرًا مِّنَ الْخُلَطَاۤءِ لَيَبْغِيْ بَعْضُهُمْ عَلٰى بَعْضٍ اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَقَلِيْلٌ مَّا هُمْۗ وَظَنَّ دَاوٗدُ اَنَّمَا فَتَنّٰهُ فَاسْتَغْفَرَ رَبَّهٗ وَخَرَّ رَاكِعًا وَّاَنَابَ ۩ ','Daud berkata: "Sesungguhnya dia telah berbuat zalim kepadamu dengan meminta kambingmu itu untuk ditambahkan kepada kambingnya. Dan sesungguhnya kebanyakan dari orang-orang yang berserikat itu sebahagian mereka berbuat zalim kepada sebahagian yang lain, kecuali orang-orang yang beriman dan mengerjakan amal yang saleh; dan amat sedikitlah mereka ini". Dan Daud mengetahui bahwa Kami mengujinya; maka ia meminta ampun kepada Tuhannya lalu menyungkur sujud dan bertaubat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,25,'فَغَفَرْنَا لَهٗ ذٰلِكَۗ وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ','Maka Kami ampuni baginya kesalahannya itu. Dan sesungguhnya dia mempunyai kedudukan dekat pada sisi Kami dan tempat kembali yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,26,'يٰدَاوٗدُ اِنَّا جَعَلْنٰكَ خَلِيْفَةً فِى الْاَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوٰى فَيُضِلَّكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّ الَّذِيْنَ يَضِلُّوْنَ عَنْ سَبِيْلِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۢبِمَا نَسُوْا يَوْمَ الْحِسَابِ ࣖ ','Hai Daud, sesungguhnya Kami menjadikan kamu khalifah (penguasa) di muka bumi, maka berilah keputusan (perkara) di antara manusia dengan adil dan janganlah kamu mengikuti hawa nafsu, karena ia akan menyesatkan kamu dari jalan Allah. Sesungguhnya orang-orang yang sesat darin jalan Allah akan mendapat azab yang berat, karena mereka melupakan hari perhitungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,27,'وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا بَاطِلًا ۗذٰلِكَ ظَنُّ الَّذِيْنَ كَفَرُوْا فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنَ النَّارِۗ','Dan Kami tidak menciptakan langit dan bumi dan apa yang ada antara keduanya tanpa hikmah. Yang demikian itu adalah anggapan orang-orang kafir, maka celakalah orang-orang kafir itu karena mereka akan masuk neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,28,'اَمْ نَجْعَلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَالْمُفْسِدِيْنَ فِى الْاَرْضِۖ اَمْ نَجْعَلُ الْمُتَّقِيْنَ كَالْفُجَّارِ','Patutkah Kami menganggap orang-orang yang beriman dan mengerjakan amal yang saleh sama dengan orang-orang yang berbuat kerusakan di muka bumi? Patutkah (pula) Kami menganggap orang-orang yang bertakwa sama dengan orang-orang yang berbuat maksiat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,29,'كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ مُبٰرَكٌ لِّيَدَّبَّرُوْٓا اٰيٰتِهٖ وَلِيَتَذَكَّرَ اُولُوا الْاَلْبَابِ','Ini adalah sebuah kitab yang Kami turunkan kepadamu penuh dengan berkah supaya mereka memperhatikan ayat-ayatnya dan supaya mendapat pelajaran orang-orang yang mempunyai fikiran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,30,'وَوَهَبْنَا لِدَاوٗدَ سُلَيْمٰنَۗ نِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌۗ','Dan Kami karuniakan kepada Daud, Sulaiman, dia adalah sebaik-baik hamba. Sesungguhnya dia amat taat (kepada Tuhannya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,31,'اِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصّٰفِنٰتُ الْجِيَادُۙ','(ingatlah) ketika dipertunjukkan kepadanya kuda-kuda yang tenang di waktu berhenti dan cepat waktu berlari pada waktu sore,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,32,'فَقَالَ اِنِّيْٓ اَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّيْۚ حَتّٰى تَوَارَتْ بِالْحِجَابِۗ','maka ia berkata: "Sesungguhnya aku menyukai kesenangan terhadap barang yang baik (kuda) sehingga aku lalai mengingat Tuhanku sampai kuda itu hilang dari pandangan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,33,'رُدُّوْهَا عَلَيَّ ۚفَطَفِقَ مَسْحًا ۢبِالسُّوْقِ وَالْاَعْنَاقِ','"Bawalah kuda-kuda itu kembali kepadaku". Lalu ia potong kaki dan leher kuda itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,34,'وَلَقَدْ فَتَنَّا سُلَيْمٰنَ وَاَلْقَيْنَا عَلٰى كُرْسِيِّهٖ جَسَدًا ثُمَّ اَنَابَ','Dan sesungguhnya Kami telah menguji Sulaiman dan Kami jadikan (dia) tergeletak di atas kursinya sebagai tubuh (yang lemah karena sakit), kemudian ia bertaubat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,35,'قَالَ رَبِّ اغْفِرْ لِيْ وَهَبْ لِيْ مُلْكًا لَّا يَنْۢبَغِيْ لِاَحَدٍ مِّنْۢ بَعْدِيْۚ اِنَّكَ اَنْتَ الْوَهَّابُ','Ia berkata: "Ya Tuhanku, ampunilah aku dan anugerahkanlah kepadaku kerajaan yang tidak dimiliki oleh seorang juapun sesudahku, sesungguhnya Engkaulah Yang Maha Pemberi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,36,'فَسَخَّرْنَا لَهُ الرِّيْحَ تَجْرِيْ بِاَمْرِهٖ رُخَاۤءً حَيْثُ اَصَابَۙ','Kemudian Kami tundukkan kepadanya angin yang berhembus dengan baik menurut ke mana saja yang dikehendakinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,37,'وَالشَّيٰطِيْنَ كُلَّ بَنَّاۤءٍ وَّغَوَّاصٍۙ','dan (Kami tundukkan pula kepadanya) syaitan-syaitan semuanya ahli bangunan dan penyelam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,38,'وَّاٰخَرِيْنَ مُقَرَّنِيْنَ فِى الْاَصْفَادِ','dan syaitan yang lain yang terikat dalam belenggu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,39,'هٰذَا عَطَاۤؤُنَا فَامْنُنْ اَوْ اَمْسِكْ بِغَيْرِ حِسَابٍ','Inilah anugerah Kami; maka berikanlah (kepada orang lain) atau tahanlah (untuk dirimu sendiri) dengan tiada pertanggungan jawab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,40,'وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ ࣖ','Dan sesungguhnya dia mempunyai kedudukan yang dekat pada sisi Kami dan tempat kembali yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,41,'وَاذْكُرْ عَبْدَنَآ اَيُّوْبَۘ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الشَّيْطٰنُ بِنُصْبٍ وَّعَذَابٍۗ','Dan ingatlah akan hamba Kami Ayyub ketika ia menyeru Tuhan-nya: "Sesungguhnya aku diganggu syaitan dengan kepayahan dan siksaan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,42,'اُرْكُضْ بِرِجْلِكَۚ هٰذَا مُغْتَسَلٌۢ بَارِدٌ وَّشَرَابٌ','(Allah berfirman): "Hantamkanlah kakimu; inilah air yang sejuk untuk mandi dan untuk minum".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,43,'وَوَهَبْنَا لَهٗٓ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنَّا وَذِكْرٰى لِاُولِى الْاَلْبَابِ','Dan Kami anugerahi dia (dengan mengumpulkan kembali) keluarganya dan (Kami tambahkan) kepada mereka sebanyak mereka pula sebagai rahmat dari Kami dan pelajaran bagi orang-orang yang mempunyai fikiran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,44,'وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِبْ بِّهٖ وَلَا تَحْنَثْ ۗاِنَّا وَجَدْنٰهُ صَابِرًا ۗنِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌ','Dan ambillah dengan tanganmu seikat (rumput), maka pukullah dengan itu dan janganlah kamu melanggar sumpah. Sesungguhnya Kami dapati dia (Ayyub) seorang yang sabar. Dialah sebaik-baik hamba. Sesungguhnya dia amat taat (kepada Tuhan-nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,45,'وَاذْكُرْ عِبٰدَنَآ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَ اُولِى الْاَيْدِيْ وَالْاَبْصَارِ','Dan ingatlah hamba-hamba Kami: Ibrahim, Ishaq dan Ya´qub yang mempunyai perbuatan-perbuatan yang besar dan ilmu-ilmu yang tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,46,'اِنَّآ اَخْلَصْنٰهُمْ بِخَالِصَةٍ ذِكْرَى الدَّارِۚ ','Sesungguhnya Kami telah mensucikan mereka dengan (menganugerahkan kepada mereka) akhlak yang tinggi yaitu selalu mengingatkan (manusia) kepada negeri akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,47,'وَاِنَّهُمْ عِنْدَنَا لَمِنَ الْمُصْطَفَيْنَ الْاَخْيَارِۗ','Dan sesungguhnya mereka pada sisi Kami benar-benar termasuk orang-orang pilihan yang paling baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,48,'وَاذْكُرْ اِسْمٰعِيْلَ وَالْيَسَعَ وَذَا الْكِفْلِ ۗوَكُلٌّ مِّنَ الْاَخْيَارِۗ','Dan ingatlah akan Ismail, Ilyasa´ dan Zulkifli. Semuanya termasuk orang-orang yang paling baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,49,'هٰذَا ذِكْرٌ ۗوَاِنَّ لِلْمُتَّقِيْنَ لَحُسْنَ مَاٰبٍۙ ','Ini adalah kehormatan (bagi mereka). Dan sesungguhnya bagi orang-orang yang bertakwa benar-benar (disediakan) tempat kembali yang baik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,50,'جَنّٰتِ عَدْنٍ مُّفَتَّحَةً لَّهُمُ الْاَبْوَابُۚ','(yaitu) surga ´Adn yang pintu-pintunya terbuka bagi mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,51,'مُتَّكِـِٕيْنَ فِيْهَا يَدْعُوْنَ فِيْهَا بِفَاكِهَةٍ كَثِيْرَةٍ وَّشَرَابٍ','di dalamnya mereka bertelekan (diatas dipan-dipan) sambil meminta buah-buahan yang banyak dan minuman di surga itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,52,'وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ اَتْرَابٌ','Dan pada sisi mereka (ada bidadari-bidadari) yang tidak liar pandangannya dan sebaya umurnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,53,'هٰذَا مَا تُوْعَدُوْنَ لِيَوْمِ الْحِسَابِ','Inilah apa yang dijanjikan kepadamu pada hari berhisab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,54,'اِنَّ هٰذَا لَرِزْقُنَا مَا لَهٗ مِنْ نَّفَادٍۚ','Sesungguhnya ini adalah benar-benar rezeki dari Kami yang tiada habis-habisnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,55,'هٰذَا ۗوَاِنَّ لِلطّٰغِيْنَ لَشَرَّ مَاٰبٍۙ','Beginilah (keadaan mereka). Dan sesungguhnya bagi orang-orang yang durhaka benar-benar (disediakan) tempat kembali yang buruk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,56,'جَهَنَّمَۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمِهَادُ','(yaitu) neraka Jahannam, yang mereka masuk ke dalamnya; maka amat buruklah Jahannam itu sebagai tempat tinggal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,57,'هٰذَاۙ فَلْيَذُوْقُوْهُ حَمِيْمٌ وَّغَسَّاقٌۙ','Inilah (azab neraka), biarlah mereka merasakannya, (minuman mereka) air yang sangat panas dan air yang sangat dingin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,58,'وَّاٰخَرُ مِنْ شَكْلِهٖٓ اَزْوَاجٌۗ','Dan azab yang lain yang serupa itu berbagai macam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,59,'هٰذَا فَوْجٌ مُّقْتَحِمٌ مَّعَكُمْۚ  لَا مَرْحَبًا ۢبِهِمْ ۗ اِنَّهُمْ صَالُوا النَّارِ','(Dikatakan kepada mereka): "Ini adalah suatu rombongan (pengikut-pengikutmu) yang masuk berdesak-desak bersama kamu (ke neraka)". (Berkata pemimpin-pemimpin mereka yang durhaka): "Tiadalah ucapan selamat datang kepada mereka karena sesungguhnya mereka akan masuk neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,60,'قَالُوْا بَلْ اَنْتُمْ لَا مَرْحَبًاۢ بِكُمْ ۗ اَنْتُمْ قَدَّمْتُمُوْهُ لَنَاۚ فَبِئْسَ الْقَرَارُ','Pengikut-pengikut mereka menjawab: "Sebenarnya kamulah. Tiada ucapan selamat datang bagimu, karena kamulah yang menjerumuskan kami ke dalam azab, maka amat buruklah Jahannam itu sebagai tempat menetap".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,61,'قَالُوْا رَبَّنَا مَنْ قَدَّمَ لَنَا هٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِى النَّارِ','Mereka berkata (lagi): "Ya Tuhan kami; barang siapa yang menjerumuskan kami ke dalam azab ini maka tambahkanlah azab kepadanya dengan berlipat ganda di dalam neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,62,'وَقَالُوْا مَا لَنَا لَا نَرٰى رِجَالًا كُنَّا نَعُدُّهُمْ مِّنَ الْاَشْرَارِ','Dan (orang-orang durhaka) berkata: "Mengapa kami tidak melihat orang-orang yang dahulu (di dunia) kami anggap sebagai orang-orang yang jahat (hina).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,63,'اَتَّخَذْنٰهُمْ سِخْرِيًّا اَمْ زَاغَتْ عَنْهُمُ الْاَبْصَارُ','Apakah kami dahulu menjadikan mereka olok-olokan, ataukah karena mata kami tidak melihat mereka?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,64,'اِنَّ ذٰلِكَ لَحَقٌّ تَخَاصُمُ اَهْلِ النَّارِ ࣖ','Sesungguhnya yang demikian itu pasti terjadi, (yaitu) pertengkaran penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,65,'قُلْ اِنَّمَآ اَنَا۠ مُنْذِرٌ ۖوَّمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ الْوَاحِدُ الْقَهَّارُ','Katakanlah (ya Muhammad): "Sesungguhnya aku hanya seorang pemberi peringatan, dan sekali-kali tidak ada Tuhan selain Allah Yang Maha Esa dan Maha Mengalahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,66,'رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الْعَزِيْزُ الْغَفَّارُ','Tuhan langit dan bumi dan apa yang ada di antara keduanya Yang Maha Perkasa lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,67,'قُلْ هُوَ نَبَؤٌا عَظِيْمٌۙ','Katakanlah: "Berita itu adalah berita yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,68,'اَنْتُمْ عَنْهُ مُعْرِضُوْنَ','yang kamu berpaling daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,69,'مَا كَانَ لِيَ مِنْ عِلْمٍۢ بِالْمَلَاِ الْاَعْلٰٓى اِذْ يَخْتَصِمُوْنَ','Aku tiada mempunyai pengetahuan sedikitpun tentang al mala´ul a´la (malaikat) itu ketika mereka berbantah-bantahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,70,'اِنْ يُّوْحٰىٓ اِلَيَّ اِلَّآ اَنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ','Tidak diwahyukan kepadaku, melainkan bahwa sesungguhnya aku hanyalah seorang pemberi peringatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,71,'اِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ طِيْنٍ','(Ingatlah) ketika Tuhanmu berfirman kepada malaikat: "Sesungguhnya Aku akan menciptakan manusia dari tanah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,72,'فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ','Maka apabila telah Kusempurnakan kejadiannya dan Kutiupkan kepadanya roh (ciptaan)Ku; maka hendaklah kamu tersungkur dengan bersujud kepadanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,73,'فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ','Lalu seluruh malaikat-malaikat itu bersujud semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,74,'اِلَّآ اِبْلِيْسَۗ اِسْتَكْبَرَ وَكَانَ مِنَ الْكٰفِرِيْنَ','kecuali iblis; dia menyombongkan diri dan adalah dia termasuk orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,75,'قَالَ يٰٓاِبْلِيْسُ مَا مَنَعَكَ اَنْ تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّ ۗ اَسْتَكْبَرْتَ اَمْ كُنْتَ مِنَ الْعَالِيْنَ','Allah berfirman: "Hai iblis, apakah yang menghalangi kamu sujud kepada yang telah Ku-ciptakan dengan kedua tangan-Ku. Apakah kamu menyombongkan diri ataukah kamu (merasa) termasuk orang-orang yang (lebih) tinggi?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,76,'قَالَ اَنَا۠ خَيْرٌ مِّنْهُ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ','Iblis berkata: "Aku lebih baik daripadanya, karena Engkau ciptakan aku dari api, sedangkan dia Engkau ciptakan dari tanah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,77,'قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۖ','Allah berfirman: "Maka keluarlah kamu dari surga; sesungguhnya kamu adalah orang yang terkutuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,78,'وَّاِنَّ عَلَيْكَ لَعْنَتِيْٓ اِلٰى يَوْمِ الدِّيْنِ','Sesungguhnya kutukan-Ku tetap atasmu sampai hari pembalasan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,79,'قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ','Iblis berkata: "Ya Tuhanku, beri tangguhlah aku sampai hari mereka dibangkitkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,80,'قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ','Allah berfirman: "Sesungguhnya kamu termasuk orang-orang yang diberi tangguh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,81,'اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ','sampai kepada hari yang telah ditentukan waktunya (hari Kiamat)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,82,'قَالَ فَبِعِزَّتِكَ لَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ','Iblis menjawab: "Demi kekuasaan Engkau aku akan menyesatkan mereka semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,83,'اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ','kecuali hamba-hamba-Mu yang mukhlis di antara mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,84,'قَالَ فَالْحَقُّۖ وَالْحَقَّ اَقُوْلُۚ','Allah berfirman: "Maka yang benar (adalah sumpah-Ku) dan hanya kebenaran itulah yang Ku-katakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,85,'لَاَمْلَئَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ اَجْمَعِيْنَ','Sesungguhnya Aku pasti akan memenuhi neraka Jahannam dengan jenis kamu dan dengan orang-orang yang mengikuti kamu di antara mereka kesemuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,86,'قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ وَّمَآ اَنَا۠ مِنَ الْمُتَكَلِّفِيْنَ','Katakanlah (hai Muhammad): "Aku tidak meminta upah sedikitpun padamu atas dakwahku dan bukanlah aku termasuk orang-orang yang mengada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,87,'اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ','Al Quran ini tidak lain hanyalah peringatan bagi semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,38,88,'وَلَتَعْلَمُنَّ نَبَاَهٗ بَعْدَ حِيْنٍ ࣖ','Dan sesungguhnya kamu akan mengetahui (kebenaran) berita Al Quran setelah beberapa waktu lagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,1,' تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ ','Kitab (Al Quran ini) diturunkan oleh Allah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,2,'اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ فَاعْبُدِ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَۗ','Sesunguhnya Kami menurunkan kepadamu Kitab (Al Quran) dengan (membawa) kebenaran. Maka sembahlah Allah dengan memurnikan ketaatan kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,3,'اَلَا لِلّٰهِ الدِّيْنُ الْخَالِصُ ۗوَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۘ مَا نَعْبُدُهُمْ اِلَّا لِيُقَرِّبُوْنَآ اِلَى اللّٰهِ زُلْفٰىۗ اِنَّ اللّٰهَ يَحْكُمُ بَيْنَهُمْ فِيْ مَا هُمْ فِيْهِ يَخْتَلِفُوْنَ ەۗ اِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ كٰذِبٌ كَفَّارٌ','Ingatlah, hanya kepunyaan Allah-lah agama yang bersih (dari syirik). Dan orang-orang yang mengambil pelindung selain Allah (berkata): "Kami tidak menyembah mereka melainkan supaya mereka mendekatkan kami kepada Allah dengan sedekat-dekatnya". Sesungguhnya Allah akan memutuskan di antara mereka tentang apa yang mereka berselisih padanya. Sesungguhnya Allah tidak menunjuki orang-orang yang pendusta dan sangat ingkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,4,'لَوْ اَرَادَ اللّٰهُ اَنْ يَّتَّخِذَ وَلَدًا لَّاصْطَفٰى مِمَّا يَخْلُقُ مَا يَشَاۤءُ ۙ سُبْحٰنَهٗ ۗهُوَ اللّٰهُ الْوَاحِدُ الْقَهَّارُ','Kalau sekiranya Allah hendak mengambil anak, tentu Dia akan memilih apa yang dikehendaki-Nya di antara ciptaan-ciptaan yang telah diciptakan-Nya. Maha Suci Allah. Dialah Allah Yang Maha Esa lagi Maha Mengalahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,5,'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۚ يُكَوِّرُ الَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ  اَلَا هُوَ الْعَزِيْزُ الْغَفَّارُ','Dia menciptakan langit dan bumi dengan (tujuan) yang benar; Dia menutupkan malam atas siang dan menutupkan siang atas malam dan menundukkan matahari dan bulan, masing-masing berjalan menurut waktu yang ditentukan. Ingatlah Dialah Yang Maha Perkasa lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,6,'خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَاَنْزَلَ لَكُمْ مِّنَ الْاَنْعَامِ ثَمٰنِيَةَ اَزْوَاجٍ ۗ يَخْلُقُكُمْ فِيْ بُطُوْنِ اُمَّهٰتِكُمْ خَلْقًا مِّنْۢ بَعْدِ خَلْقٍ فِيْ ظُلُمٰتٍ ثَلٰثٍۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ  لَآ اِلٰهَ اِلَّا هُوَۚ فَاَنّٰى تُصْرَفُوْنَ','Dia menciptakan kamu dari seorang diri kemudian Dia jadikan daripadanya isterinya dan Dia menurunkan untuk kamu delapan ekor yang berpasangan dari binatang ternak. Dia menjadikan kamu dalam perut ibumu kejadian demi kejadian dalam tiga kegelapan. Yang (berbuat) demikian itu adalah Allah, Tuhan kamu, Tuhan Yang mempunyai kerajaan. Tidak ada Tuhan selain Dia; maka bagaimana kamu dapat dipalingkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,7,'اِنْ تَكْفُرُوْا فَاِنَّ اللّٰهَ غَنِيٌّ عَنْكُمْ ۗوَلَا يَرْضٰى لِعِبَادِهِ الْكُفْرَۚ وَاِنْ تَشْكُرُوْا يَرْضَهُ لَكُمْۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ','Jika kamu kafir maka sesungguhnya Allah tidak memerlukan (iman)mu dan Dia tidak meridhai kekafiran bagi hamba-Nya; dan jika kamu bersyukur, niscaya Dia meridhai bagimu kesyukuranmu itu; dan seorang yang berdosa tidak akan memikul dosa orang lain. Kemudian kepada Tuhanmulah kembalimu lalu Dia memberitakan kepadamu apa yang telah kamu kerjakan. Sesungguhnya Dia Maha Mengetahui apa yang tersimpan dalam (dada)mu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,8,'وَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَا رَبَّهٗ مُنِيْبًا اِلَيْهِ ثُمَّ اِذَا خَوَّلَهٗ نِعْمَةً مِّنْهُ نَسِيَ مَا كَانَ يَدْعُوْٓا اِلَيْهِ مِنْ قَبْلُ وَجَعَلَ لِلّٰهِ اَنْدَادًا لِّيُضِلَّ عَنْ سَبِيْلِهٖ ۗ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيْلًا ۖاِنَّكَ مِنْ اَصْحٰبِ النَّارِ','Dan apabila manusia itu ditimpa kemudharatan, dia memohon (pertolongan) kepada Tuhannya dengan kembali kepada-Nya; kemudian apabila Tuhan memberikan nikmat-Nya kepadanya lupalah dia akan kemudharatan yang pernah dia berdoa (kepada Allah) untuk (menghilangkannya) sebelum itu, dan dia mengada-adakan sekutu-sekutu bagi Allah untuk menyesatkan (manusia) dari jalan-Nya. Katakanlah: "Bersenang-senanglah dengan kekafiranmu itu sementara waktu; sesungguhnya kamu termasuk penghuni neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,9,'اَمَّنْ هُوَ قَانِتٌ اٰنَاۤءَ الَّيْلِ سَاجِدًا وَّقَاۤىِٕمًا يَّحْذَرُ الْاٰخِرَةَ وَيَرْجُوْا رَحْمَةَ رَبِّهٖۗ قُلْ هَلْ يَسْتَوِى الَّذِيْنَ يَعْلَمُوْنَ وَالَّذِيْنَ لَا يَعْلَمُوْنَ ۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِ ࣖ','(Apakah kamu hai orang musyrik yang lebih beruntung) ataukah orang yang beribadat di waktu-waktu malam dengan sujud dan berdiri, sedang ia takut kepada (azab) akhirat dan mengharapkan rahmat Tuhannya? Katakanlah: "Adakah sama orang-orang yang mengetahui dengan orang-orang yang tidak mengetahui?" Sesungguhnya orang yang berakallah yang dapat menerima pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,10,'قُلْ يٰعِبَادِ الَّذِيْنَ اٰمَنُوا اتَّقُوْا رَبَّكُمْ ۗلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ  ۗوَاَرْضُ اللّٰهِ وَاسِعَةٌ  ۗاِنَّمَا يُوَفَّى الصّٰبِرُوْنَ اَجْرَهُمْ بِغَيْرِ حِسَابٍ ','Katakanlah: "Hai hamba-hamba-Ku yang beriman. bertakwalah kepada Tuhanmu". Orang-orang yang berbuat baik di dunia ini memperoleh kebaikan. Dan bumi Allah itu adalah luas. Sesungguhnya hanya orang-orang yang bersabarlah Yang dicukupkan pahala mereka tanpa batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,11,'قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَ','Katakanlah: "Sesungguhnya aku diperintahkan supaya menyembah Allah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agama.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,12,'وَاُمِرْتُ لِاَنْ اَكُوْنَ اَوَّلَ الْمُسْلِمِيْنَ','Dan aku diperintahkan supaya menjadi orang yang pertama-tama berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,13,'قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ','Katakanlah: "Sesungguhnya aku takut akan siksaan hari yang besar jika aku durhaka kepada Tuhanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,14,'قُلِ اللّٰهَ اَعْبُدُ مُخْلِصًا لَّهٗ دِيْنِيْۚ','Katakanlah: "Hanya Allah saja Yang aku sembah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agamaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,15,'فَاعْبُدُوْا مَا شِئْتُمْ مِّنْ دُوْنِهٖۗ قُلْ اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِۗ  اَلَا ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ','Maka sembahlah olehmu (hai orang-orang musyrik) apa yang kamu kehendaki selain Dia. Katakanlah: "Sesungguhnya orang-orang yang rugi ialah orang-orang yang merugikan diri mereka sendiri dan keluarganya pada hari kiamat". Ingatlah yang demikian itu adalah kerugian yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,16,'لَهُمْ مِّنْ فَوْقِهِمْ ظُلَلٌ مِّنَ النَّارِ وَمِنْ تَحْتِهِمْ ظُلَلٌ  ۗذٰلِكَ يُخَوِّفُ اللّٰهُ بِهٖ عِبَادَهٗ ۗيٰعِبَادِ فَاتَّقُوْنِ','Bagi mereka lapisan-lapisan dari api di atas mereka dan di bawah merekapun lapisan-lapisan (dari api). Demikianlah Allah mempertakuti hamba-hamba-Nya dengan azab itu. Maka bertakwalah kepada-Ku hai hamba-hamba-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,17,'وَالَّذِيْنَ اجْتَنَبُوا الطَّاغُوْتَ اَنْ يَّعْبُدُوْهَا وَاَنَابُوْٓا اِلَى اللّٰهِ لَهُمُ الْبُشْرٰىۚ فَبَشِّرْ عِبَادِۙ','Dan orang-orang yang menjauhi thaghut (yaitu) tidak menyembahnya dan kembali kepada Allah, bagi mereka berita gembira; sebab itu sampaikanlah berita itu kepada hamba-hamba-Ku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,18,'الَّذِيْنَ يَسْتَمِعُوْنَ الْقَوْلَ فَيَتَّبِعُوْنَ اَحْسَنَهٗ ۗ اُولٰۤىِٕكَ الَّذِيْنَ هَدٰىهُمُ اللّٰهُ وَاُولٰۤىِٕكَ هُمْ اُولُوا الْاَلْبَابِ','yang mendengarkan perkataan lalu mengikuti apa yang paling baik di antaranya. Mereka itulah orang-orang yang telah diberi Allah petunjuk dan mereka itulah orang-orang yang mempunyai akal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,19,'اَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِۗ اَفَاَنْتَ تُنْقِذُ مَنْ فِى النَّارِ ۚ','Apakah (kamu hendak merubah nasib) orang-orang yang telah pasti ketentuan azab atasnya? Apakah kamu akan menyelamatkan orang yang berada dalam api neraka?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,20,'لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِّنْ فَوْقِهَا غُرَفٌ مَّبْنِيَّةٌ  ۙتَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ الْمِيْعَادَ','Tetapi orang-orang yang bertakwa kepada Tuhannya mereka mendapat tempat-tempat yang tinggi, di atasnya dibangun pula tempat-tempat yang tinggi yang di bawahnya mengalir sungai-sungai. Allah telah berjanji dengan sebenar-benarnya. Allah tidak akan memungkiri janji-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,21,'اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَلَكَهٗ يَنَابِيْعَ فِى الْاَرْضِ ثُمَّ يُخْرِجُ بِهٖ زَرْعًا مُّخْتَلِفًا اَلْوَانُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَجْعَلُهٗ حُطَامًا ۗاِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِاُولِى الْاَلْبَابِ ࣖ ','Apakah kamu tidak memperhatikan, bahwa sesungguhnya Allah menurunkan air dari langit, maka diaturnya menjadi sumber-sumber air di bumi kemudian ditumbuhkan-Nya dengan air itu tanam-tanaman yang bermacam-macam warnanya, lalu menjadi kering lalu kamu melihatnya kekuning-kuningan, kemudian dijadikan-Nya hancur berderai-derai. Sesungguhnya pada yang demikian itu benar-benar terdapat pelajaran bagi orang-orang yang mempunyai akal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,22,'اَفَمَنْ شَرَحَ اللّٰهُ صَدْرَهٗ لِلْاِسْلَامِ فَهُوَ عَلٰى نُوْرٍ مِّنْ رَّبِّهٖ ۗفَوَيْلٌ لِّلْقٰسِيَةِ قُلُوْبُهُمْ مِّنْ ذِكْرِ اللّٰهِ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ','Maka apakah orang-orang yang dibukakan Allah hatinya untuk (menerima) agama Islam lalu ia mendapat cahaya dari Tuhannya (sama dengan orang yang membatu hatinya)? Maka kecelakaan yang besarlah bagi mereka yang telah membatu hatinya untuk mengingat Allah. Mereka itu dalam kesesatan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,23,'اَللّٰهُ نَزَّلَ اَحْسَنَ الْحَدِيْثِ كِتٰبًا مُّتَشَابِهًا مَّثَانِيَۙ  تَقْشَعِرُّ مِنْهُ جُلُوْدُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ ۚ ثُمَّ تَلِيْنُ جُلُوْدُهُمْ وَقُلُوْبُهُمْ اِلٰى ذِكْرِ اللّٰهِ ۗذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ','Allah telah menurunkan perkataan yang paling baik (yaitu) Al Quran yang serupa (mutu ayat-ayatnya) lagi berulang-ulang , gemetar karenanya kulit orang-orang yang takut kepada Tuhannya, kemudian menjadi tenang kulit dan hati mereka di waktu mengingat Allah. Itulah petunjuk Allah, dengan kitab itu Dia menunjuki siapa yang dikehendaki-Nya. Dan barangsiapa yang disesatkan Allah, niscaya tak ada baginya seorang pemimpinpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,24,'اَفَمَنْ يَّتَّقِيْ بِوَجْهِهٖ سُوْۤءَ الْعَذَابِ يَوْمَ الْقِيٰمَةِ ۗوَقِيْلَ لِلظّٰلِمِيْنَ ذُوْقُوْا مَا كُنْتُمْ تَكْسِبُوْنَ','Maka apakah orang-orang yang menoleh dengan mukanya menghindari azab yang buruk pada hari kiamat (sama dengan orang mukmin yang tidak kena azab)? Dan dikatakan kepada orang-orang yang zalim: "Rasakanlah olehmu balasan apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,25,'كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ','Orang-orang yang sebelum mereka telah mendustakan (rasul-rasul), maka datanglah kepada mereka azab dari arah yang tidak mereka sangka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,26,'فَاَذَاقَهُمُ اللّٰهُ الْخِزْيَ فِى الْحَيٰوةِ الدُّنْيَا ۚوَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُ ۘ لَوْ كَانُوْا يَعْلَمُوْنَ','Maka Allah merasakan kepada mereka kehinaan pada kehidupan dunia. Dan sesungguhnya azab pada hari akhirat lebih besar kalau mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,27,'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍ لَّعَلَّهُمْ يَتَذَكَّرُوْنَۚ','Sesungguhnya telah Kami buatkan bagi manusia dalam Al Quran ini setiap macam perumpamaan supaya mereka dapat pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,28,'قُرْاٰنًا عَرَبِيًّا غَيْرَ ذِيْ عِوَجٍ لَّعَلَّهُمْ يَتَّقُوْنَ','(Ialah) Al Quran dalam bahasa Arab yang tidak ada kebengkokan (di dalamnya) supaya mereka bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,29,'ضَرَبَ اللّٰهُ مَثَلًا رَّجُلًا فِيْهِ شُرَكَاۤءُ مُتَشَاكِسُوْنَ وَرَجُلًا سَلَمًا لِّرَجُلٍ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ','Allah membuat perumpamaan (yaitu) seorang laki-laki (budak) yang dimiliki oleh beberapa orang yang berserikat yang dalam perselisihan dan seorang budak yang menjadi milik penuh dari seorang laki-laki (saja); Adakah kedua budak itu sama halnya? Segala puji bagi Allah tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,30,'اِنَّكَ مَيِّتٌ وَّاِنَّهُمْ مَّيِّتُوْنَ ۖ','Sesungguhnya kamu akan mati dan sesungguhnya mereka akan mati (pula).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,23,39,31,'ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُوْنَ ࣖ   ۔','Kemudian sesungguhnya kamu pada hari kiamat akan berbantah-bantah di hadapan Tuhanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,32,'۞ فَمَنْ اَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللّٰهِ وَكَذَّبَ بِالصِّدْقِ اِذْ جَاۤءَهٗۗ  اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ ','Maka siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah dan mendustakan kebenaran ketika datang kepadanya? Bukankah di neraka Jahannam tersedia tempat tinggal bagi orang-orang yang kafir?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,33,'وَالَّذِيْ جَاۤءَ بِالصِّدْقِ وَصَدَّقَ بِهٖٓ اُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ','Dan orang yang membawa kebenaran (Muhammad) dan membenarkannya, mereka itulah orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,34,'لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗ ذٰلِكَ جَزٰۤؤُا الْمُحْسِنِيْنَۚ','Mereka memperoleh apa yang mereka kehendaki pada sisi Tuhan mereka. Demikianlah balasan orang-orang yang berbuat baik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,35,'لِيُكَفِّرَ اللّٰهُ عَنْهُمْ اَسْوَاَ الَّذِيْ عَمِلُوْا وَيَجْزِيَهُمْ اَجْرَهُمْ بِاَحْسَنِ الَّذِيْ كَانُوْا يَعْمَلُوْنَ','agar Allah akan menutupi (mengampuni) bagi mereka perbuatan yang paling buruk yang mereka kerjakan dan membalas mereka dengan upah yang lebih baik dari apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,36,'اَلَيْسَ اللّٰهُ بِكَافٍ عَبْدَهٗۗ وَيُخَوِّفُوْنَكَ بِالَّذِيْنَ مِنْ دُوْنِهٖۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍۚ','Bukankah Allah cukup untuk melindungi hamba-hamba-Nya. Dan mereka mempertakuti kamu dengan (sembahan-sembahan) yang selain Allah? Dan siapa yang disesatkan Allah maka tidak seorangpun pemberi petunjuk baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,37,'وَمَنْ يَّهْدِ اللّٰهُ فَمَا لَهٗ مِنْ مُّضِلٍّ ۗ اَلَيْسَ اللّٰهُ بِعَزِيْزٍ ذِى انْتِقَامٍ','Dan barangsiapa yang diberi petunjuk oleh Allah, maka tidak seorangpun yang dapat menyesatkannya. Bukankah Allah Maha Perkasa lagi mempunyai (kekuasaan untuk) mengazab?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,38,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗ قُلْ اَفَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اِنْ اَرَادَنِيَ اللّٰهُ بِضُرٍّ هَلْ هُنَّ كٰشِفٰتُ ضُرِّهٖٓ اَوْ اَرَادَنِيْ بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكٰتُ رَحْمَتِهٖۗ قُلْ حَسْبِيَ اللّٰهُ ۗعَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُوْنَ ','Dan sungguh jika kamu bertanya kepada mereka: "Siapakah yang menciptakan langit dan bumi?", niscaya mereka menjawab: "Allah". Katakanlah: "Maka terangkanlah kepadaku tentang apa yang kamu seru selain Allah, jika Allah hendak mendatangkan kemudharatan kepadaku, apakah berhala-berhalamu itu dapat menghilangkan kemudharatan itu, atau jika Allah hendak memberi rahmat kepadaku, apakah mereka dapat menahan rahmat-Nya?. Katakanlah: "Cukuplah Allah bagiku". Kepada-Nya-lah bertawakkal orang-orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,39,'قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ ۚفَسَوْفَ تَعْلَمُوْنَۙ','Katakanlah: "Hai kaumku, bekerjalah sesuai dengan keadaanmu, sesungguhnya aku akan bekerja (pula), maka kelak kamu akan mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,40,'مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ','siapa yang akan mendapat siksa yang menghinakannya dan lagi ditimpa oleh azab yang kekal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,41,'اِنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ لِلنَّاسِ بِالْحَقِّۚ فَمَنِ اهْتَدٰى فَلِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ࣖ','Sesungguhnya Kami menurunkan kepadamu Al Kitab (Al Quran) untuk manusia dengan membawa kebenaran; siapa yang mendapat petunjuk maka (petunjuk itu) untuk dirinya sendiri, dan siapa yang sesat maka sesungguhnya dia semata-mata sesat buat (kerugian) dirinya sendiri, dan kamu sekali-kali bukanlah orang yang bertanggung jawab terhadap mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,42,'اَللّٰهُ يَتَوَفَّى الْاَنْفُسَ حِيْنَ مَوْتِهَا وَالَّتِيْ لَمْ تَمُتْ فِيْ مَنَامِهَا ۚ فَيُمْسِكُ الَّتِي قَضٰى عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْاُخْرٰىٓ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ','Allah memegang jiwa (orang) ketika matinya dan (memegang) jiwa (orang) yang belum mati di waktu tidurnya; maka Dia tahanlah jiwa (orang) yang telah Dia tetapkan kematiannya dan Dia melepaskan jiwa yang lain sampai waktu yang ditetapkan. Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi kaum yang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,43,'اَمِ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ شُفَعَاۤءَۗ قُلْ اَوَلَوْ كَانُوْا لَا يَمْلِكُوْنَ شَيْـًٔا وَّلَا يَعْقِلُوْنَ ','Bahkan mereka mengambil pemberi syafa´at selain Allah. Katakanlah: "Dan apakah (kamu mengambilnya juga) meskipun mereka tidak memiliki sesuatupun dan tidak berakal?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,44,'قُلْ لِّلّٰهِ الشَّفَاعَةُ جَمِيْعًا ۗ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ','Katakanlah: "Hanya kepunyaan Allah syafa´at itu semuanya. Kepunyaan-Nya kerajaan langit dan bumi. Kemudian kepada-Nya-lah kamu dikembalikan"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,45,'وَاِذَا ذُكِرَ اللّٰهُ وَحْدَهُ اشْمَـَٔزَّتْ قُلُوْبُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِۚ وَاِذَا ذُكِرَ الَّذِيْنَ مِنْ دُوْنِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَ','Dan apabila hanya nama Allah saja disebut, kesallah hati orang-orang yang tidak beriman kepada kehidupan akhirat; dan apabila nama sembahan-sembahan selain Allah yang disebut, tiba-tiba mereka bergirang hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,46,'قُلِ اللهم فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِ عٰلِمَ الْغَيْبِ وَالشَّهَادَةِ اَنْتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِيْ مَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ','Katakanlah: "Wahai Allah, Pencipta langit dan bumi, Yang mengetahui barang ghaib dan yang nyata, Engkaulah Yang memutuskan antara hamba-hamba-Mu tentang apa yang selalu mereka memperselisihkannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,47,'وَلَوْ اَنَّ لِلَّذِيْنَ ظَلَمُوْا مَا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ مِنْ سُوْۤءِ الْعَذَابِ يَوْمَ الْقِيٰمَةِۗ وَبَدَا لَهُمْ مِّنَ اللّٰهِ مَا لَمْ يَكُوْنُوْا يَحْتَسِبُوْنَ','Dan sekiranya orang-orang yang zalim mempunyai apa yang ada di bumi semuanya dan (ada pula) sebanyak itu besertanya, niscaya mereka akan menebus dirinya dengan itu dari siksa yang buruk pada hari kiamat. Dan jelaslah bagi mereka azab dari Allah yang belum pernah mereka perkirakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,48,'وَبَدَا لَهُمْ سَيِّاٰتُ مَا كَسَبُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ','Dan (jelaslah) bagi mereka akibat buruk dari apa yang telah mereka perbuat dan mereka diliputi oleh pembalasan yang mereka dahulu selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,49,'فَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَانَاۖ  ثُمَّ اِذَا خَوَّلْنٰهُ نِعْمَةً مِّنَّاۙ قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ ۗبَلْ هِيَ فِتْنَةٌ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Maka apabila manusia ditimpa bahaya ia menyeru Kami, kemudian apabila Kami berikan kepadanya nikmat dari Kami ia berkata: "Sesungguhnya aku diberi nikmat itu hanyalah karena kepintaranku". Sebenarnya itu adalah ujian, tetapi kebanyakan mereka itu tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,50,'قَدْ قَالَهَا الَّذِيْنَ مِنْ قَبْلِهِمْ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ','Sungguh orang-orang yang sebelum mereka (juga) telah mengatakan itu pula, maka tiadalah berguna bagi mereka apa yang dahulu mereka usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,51,'فَاَصَابَهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۗوَالَّذِيْنَ ظَلَمُوْا مِنْ هٰٓؤُلَاۤءِ سَيُصِيْبُهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۙوَمَا هُمْ بِمُعْجِزِيْنَ','Maka mereka ditimpa oleh akibat buruk dari apa yang mereka usahakan. Dan orang-orang yang zalim di antara mereka akan ditimpa akibat buruk dari usahanya dan mereka tidak dapat melepaskan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,52,'اَوَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ࣖ','Dan tidakkah mereka mengetahui bahwa Allah melapangkan rezeki dan menyempitkannya bagi siapa yang dikehendaki-Nya? Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi kaum yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,53,'۞ قُلْ يٰعِبَادِيَ الَّذِيْنَ اَسْرَفُوْا عَلٰٓى اَنْفُسِهِمْ لَا تَقْنَطُوْا مِنْ رَّحْمَةِ اللّٰهِ ۗاِنَّ اللّٰهَ يَغْفِرُ الذُّنُوْبَ جَمِيْعًا ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ','Katakanlah: "Hai hamba-hamba-Ku yang malampaui batas terhadap diri mereka sendiri, janganlah kamu berputus asa dari rahmat Allah. Sesungguhnya Allah mengampuni dosa-dosa semuanya. Sesungguhnya Dialah Yang Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,54,'وَاَنِيْبُوْٓا اِلٰى رَبِّكُمْ وَاَسْلِمُوْا لَهٗ مِنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنْصَرُوْنَ','Dan kembalilah kamu kepada Tuhanmu, dan berserah dirilah kepada-Nya sebelum datang azab kepadamu kemudian kamu tidak dapat ditolong (lagi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,55,'وَاتَّبِعُوْٓا اَحْسَنَ مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَّاَنْتُمْ لَا تَشْعُرُوْنَ ۙ  ','Dan ikutilah sebaik-baik apa yang telah diturunkan kepadamu dari Tuhanmu sebelum datang azab kepadamu dengan tiba-tiba, sedang kamu tidak menyadarinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,56,'اَنْ تَقُوْلَ نَفْسٌ يّٰحَسْرَتٰى عَلٰى مَا فَرَّطْتُّ فِيْ جَنْۢبِ اللّٰهِ وَاِنْ كُنْتُ لَمِنَ السَّاخِرِيْنَۙ   ','supaya jangan ada orang yang mengatakan: "Amat besar penyesalanku atas kelalaianku dalam (menunaikan kewajiban) terhadap Allah, sedang aku sesungguhnya termasuk orang-orang yang memperolok-olokkan (agama Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,57,'اَوْ تَقُوْلَ لَوْ اَنَّ اللّٰهَ هَدٰىنِيْ لَكُنْتُ مِنَ الْمُتَّقِيْنَ ۙ','atau supaya jangan ada yang berkata: ´Kalau sekiranya Allah memberi petunjuk kepadaku tentulah aku termasuk orang-orang yang bertakwa´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,58,'اَوْ تَقُوْلَ حِيْنَ تَرَى الْعَذَابَ لَوْ اَنَّ لِيْ كَرَّةً فَاَكُوْنَ مِنَ الْمُحْسِنِيْنَ','Atau supaya jangan ada yang berkata ketika ia melihat azab ´Kalau sekiranya aku dapat kemnbali (ke dunia), niscaya aku akan termasuk orang-orang berbuat baik´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,59,'بَلٰى قَدْ جَاۤءَتْكَ اٰيٰتِيْ فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنْتَ مِنَ الْكٰفِرِيْنَ','(Bukan demikian) sebenarya telah datang keterangan-keterangan-Ku kepadamu lalu kamu mendustakannya dan kamu menyombongkan diri dan adalah kamu termasuk orang-orang yang kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,60,'وَيَوْمَ الْقِيٰمَةِ تَرَى الَّذِيْنَ كَذَبُوْا عَلَى اللّٰهِ وُجُوْهُهُمْ مُّسْوَدَّةٌ  ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْمُتَكَبِّرِيْنَ','Dan pada hari kiamat kamu akan melihat orang-orang yang berbuat dusta terhadap Allah, mukanya menjadi hitam. Bukankah dalam neraka Jahannam itu ada tempat bagi orang-orang yang menyombongkan diri?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,61,'وَيُنَجِّى اللّٰهُ الَّذِيْنَ اتَّقَوْا بِمَفَازَتِهِمْۖ  لَا يَمَسُّهُمُ السُّوْۤءُ وَلَا هُمْ يَحْزَنُوْنَ','Dan Allah menyelamatkan orang-orang yang bertakwa karena kemenangan mereka, mereka tiada disentuh oleh azab (neraka dan tidak pula) mereka berduka cita.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,62,'اَللّٰهُ خَالِقُ كُلِّ شَيْءٍ ۙوَّهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ','Allah menciptakan segala sesuatu dan Dia memelihara segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,63,'لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ࣖ ','Kepunyaan-Nya-lah kunci-kunci (perbendaharaan) langit dan bumi. Dan orang-orang yang kafir terhadap ayat-ayat Allah, mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,64,'قُلْ اَفَغَيْرَ اللّٰهِ تَأْمُرُوْۤنِّيْٓ اَعْبُدُ اَيُّهَا الْجٰهِلُوْنَ','Katakanlah: "Maka apakah kamu menyuruh aku menyembah selain Allah, hai orang-orang yang tidak berpengetahuan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,65,'وَلَقَدْ اُوْحِيَ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۚ  لَىِٕنْ اَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ','Dan sesungguhnya telah diwahyukan kepadamu dan kepada (nabi-nabi) yang sebelummu. "Jika kamu mempersekutukan (Tuhan), niscaya akan hapuslah amalmu dan tentulah kamu termasuk orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,66,'بَلِ اللّٰهَ فَاعْبُدْ وَكُنْ مِّنَ الشّٰكِرِيْنَ','Karena itu, maka hendaklah Allah saja kamu sembah dan hendaklah kamu termasuk orang-orang yang bersyukur".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,67,'وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۖ وَالْاَرْضُ جَمِيْعًا قَبْضَتُهٗ يَوْمَ الْقِيٰمَةِ وَالسَّمٰوٰتُ مَطْوِيّٰتٌۢ بِيَمِيْنِهٖ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ','Dan mereka tidak mengagungkan Allah dengan pengagungan yang semestinya padahal bumi seluruhnya dalam genggaman-Nya pada hari kiamat dan langit digulung dengan tangan kanan-Nya. Maha Suci Tuhan dan Maha Tinggi Dia dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,68,'وَنُفِخَ فِى الصُّوْرِ فَصَعِقَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗ ثُمَّ نُفِخَ فِيْهِ اُخْرٰى فَاِذَا هُمْ قِيَامٌ يَّنْظُرُوْنَ','Dan ditiuplah sangkakala, maka matilah siapa yang di langit dan di bumi kecuali siapa yang dikehendaki Allah. Kemudian ditiup sangkakala itu sekali lagi maka tiba-tiba mereka berdiri menunggu (putusannya masing-masing).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,69,'وَاَشْرَقَتِ الْاَرْضُ بِنُوْرِ رَبِّهَا وَوُضِعَ الْكِتٰبُ وَجِايْۤءَ بِالنَّبِيّٖنَ وَالشُّهَدَاۤءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ','Dan terang benderanglah bumi (padang mahsyar) dengan cahaya (keadilan) Tuhannya; dan diberikanlah buku (perhitungan perbuatan masing-masing) dan didatangkanlah para nabi dan saksi-saksi dan diberi keputusan di antara mereka dengan adil, sedang mereka tidak dirugikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,70,'وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُوَ اَعْلَمُ بِمَا يَفْعَلُوْنَ ࣖ ','Dan disempurnakan bagi tiap-tiap jiwa (balasan) apa yang telah dikerjakannya dan Dia lebih mengetahui apa yang mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,71,'وَسِيْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا فُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَتْلُوْنَ عَلَيْكُمْ اٰيٰتِ رَبِّكُمْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَا ۗقَالُوْا بَلٰى وَلٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكٰفِرِيْنَ','Orang-orang kafir dibawa ke neraka Jahannam berombong-rombongan. Sehingga apabila mereka sampai ke neraka itu dibukakanlah pintu-pintunya dan berkatalah kepada mereka penjaga-penjaganya: "Apakah belum pernah datang kepadamu rasul-rasul di antaramu yang membacakan kepadamu ayat-ayat Tuhanmu dan memperingatkan kepadamu akan pertemuan dengan hari ini?" Mereka menjawab: "Benar (telah datang)". Tetapi telah pasti berlaku ketetapan azab terhadap orang-orang yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,72,'قِيْلَ ادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ','Dikatakan (kepada mereka): "Masukilah pintu-pintu neraka Jahannam itu, sedang kamu kekal di dalamnya" Maka neraka Jahannam itulah seburuk-buruk tempat bagi orang-orang yang menyombongkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,73,'وَسِيْقَ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ اِلَى الْجَنَّةِ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا وَفُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلٰمٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوْهَا خٰلِدِيْنَ','Dan orang-orang yang bertakwa kepada Tuhan dibawa ke dalam surga berombong-rombongan (pula). Sehingga apabila mereka sampai ke surga itu sedang pintu-pintunya telah terbuka dan berkatalah kepada mereka penjaga-penjaganya: "Kesejahteraan (dilimpahkan) atasmu. Berbahagialah kamu! maka masukilah surga ini, sedang kamu kekal di dalamnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,74,'وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ صَدَقَنَا وَعْدَهٗ وَاَوْرَثَنَا الْاَرْضَ نَتَبَوَّاُ مِنَ الْجَنَّةِ حَيْثُ نَشَاۤءُ ۚفَنِعْمَ اَجْرُ الْعٰمِلِيْنَ','Dan mereka mengucapkan: "Segala puji bagi Allah yang telah memenuhi janji-Nya kepada kami dan telah (memberi) kepada kami tempat ini sedang kami (diperkenankan) menempati tempat dalam surga di mana saja yang kami kehendaki; maka surga itulah sebaik-baik balasan bagi orang-orang yang beramal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,39,75,'وَتَرَى الْمَلٰۤىِٕكَةَ حَاۤفِّيْنَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْۚ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَقِيْلَ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ࣖ','Dan kamu (Muhammad) akan melihat malaikat-mmlaikat berlingkar di sekeliling ´Arsy bertasbih sambil memuji Tuhannya; dan diberi putusan di antara hamba-hamba Allah dengan adil dan diucapkan: "Segala puji bagi Allah, Tuhan semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,1,'حٰمۤ  ۚ ','Haa Miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,2,'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْعَلِيْمِۙ','Diturunkan Kitab ini (Al Quran) dari Allah Yang Maha Perkasa lagi Maha Mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,3,'غَافِرِ الذَّنْۢبِ وَقَابِلِ التَّوْبِ شَدِيْدِ الْعِقَابِ ذِى الطَّوْلِۗ  لَآ اِلٰهَ اِلَّا هُوَ ۗاِلَيْهِ الْمَصِيْرُ ','Yang Mengampuni dosa dan Menerima taubat lagi keras hukuman-Nya. Yang mempunyai karunia. Tiada Tuhan (yang berhak disembah) selain Dia. Hanya kepada-Nya-lah kembali (semua makhluk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,4,'مَا يُجَادِلُ فِيْٓ اٰيٰتِ اللّٰهِ اِلَّا الَّذِيْنَ كَفَرُوْا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى الْبِلَادِ','Tidak ada yang memperdebatkan tentang ayat-ayat Allah, kecuali orang-orang yang kafir. Karena itu janganlah pulang balik mereka dengan bebas dari suatu kota ke kota yang lain memperdayakan kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,5,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَّالْاَحْزَابُ مِنْۢ بَعْدِهِمْ ۖوَهَمَّتْ كُلُّ اُمَّةٍۢ بِرَسُوْلِهِمْ لِيَأْخُذُوْهُ وَجَادَلُوْا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَاَخَذْتُهُمْ ۗفَكَيْفَ كَانَ عِقَابِ','Sebelum mereka, kaum Nuh dan golongan-golongan yang bersekutu sesudah mereka telah mendustakan (rasul) dan tiap-tiap umat telah merencanakan makar terhadap rasul mereka untuk menawannya dan mereka membantah dengan (alasan) yang batil untuk melenyapkan kebenaran dengan yang batil itu; karena itu Aku azab mereka. Maka betapa (pedihnya) azab-Ku?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,6,'وَكَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ اَصْحٰبُ النَّارِۘ ','Dan demikianlah telah pasti berlaku ketetapan azab Tuhanmu terhadap orang-orang kafir, karena sesungguhnya mereka adalah penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,7,'اَلَّذِيْنَ يَحْمِلُوْنَ الْعَرْشَ وَمَنْ حَوْلَهٗ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُوْنَ بِهٖ وَيَسْتَغْفِرُوْنَ لِلَّذِيْنَ اٰمَنُوْاۚ رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَّحْمَةً وَّعِلْمًا فَاغْفِرْ لِلَّذِيْنَ تَابُوْا وَاتَّبَعُوْا سَبِيْلَكَ وَقِهِمْ عَذَابَ الْجَحِيْمِ ','(Malaikat-malaikat) yang memikul ´Arsy dan malaikat yang berada di sekelilingnya bertasbih memuji Tuhannya dan mereka beriman kepada-Nya serta memintakan ampun bagi orang-orang yang beriman (seraya mengucapkan): "Ya Tuhan kami, rahmat dan ilmu Engkau meliputi segala sesuatu, maka berilah ampunan kepada orang-orang yang bertaubat dan mengikuti jalan Engkau dan peliharalah mereka dari siksaan neraka yang menyala-nyala,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,8,'رَبَّنَا وَاَدْخِلْهُمْ جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدْتَّهُمْ وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ ۗاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُۙ ','ya Tuhan kami, dan masukkanlah mereka ke dalam surga ´Adn yang telah Engkau janjikan kepada mereka dan orang-orang yang saleh di antara bapak-bapak mereka, dan isteri-isteri mereka, dan keturunan mereka semua. Sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,9,'وَقِهِمُ السَّيِّاٰتِۗ وَمَنْ تَقِ السَّيِّاٰتِ يَوْمَىِٕذٍ فَقَدْ رَحِمْتَهٗ ۗوَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ࣖ ','dan peliharalah mereka dari (balasan) kejahatan. Dan orang-orang yang Engkau pelihara dari (pembalasan) kejahatan pada hari itu maka sesungguhnya telah Engkau anugerahkan rahmat kepadanya dan itulah kemenangan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,10,'اِنَّ الَّذِيْنَ كَفَرُوْا يُنَادَوْنَ لَمَقْتُ اللّٰهِ اَكْبَرُ مِنْ مَّقْتِكُمْ اَنْفُسَكُمْ اِذْ تُدْعَوْنَ اِلَى الْاِيْمَانِ فَتَكْفُرُوْنَ','Sesungguhnya orang-orang yang kafir diserukan kepada mereka (pada hari kiamat): "Sesungguhnya kebencian Allah (kepadamu) lebih besar daripada kebencianmu kepada dirimu sendiri karena kamu diseru untuk beriman lalu kamu kafir".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,11,'قَالُوْا رَبَّنَآ اَمَتَّنَا اثْنَتَيْنِ وَاَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوْبِنَا فَهَلْ اِلٰى خُرُوْجٍ مِّنْ سَبِيْلٍ','Mereka menjawab: "Ya Tuhan kami Engkau telah mematikan kami dua kali dan telah menghidupkan kami dua kali (pula), lalu kami mengakui dosa-dosa kami. Maka adakah sesuatu jalan (bagi kami) untuk keluar (dari neraka)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,12,'ذٰلِكُمْ بِاَنَّهٗٓ اِذَا دُعِيَ اللّٰهُ وَحْدَهٗ كَفَرْتُمْۚ وَاِنْ يُّشْرَكْ بِهٖ تُؤْمِنُوْا ۗفَالْحُكْمُ لِلّٰهِ الْعَلِيِّ الْكَبِيْرِ','Yang demikian itu adalah karena kamu kafir apabila Allah saja disembah. Dan kamu percaya apabila Allah dipersekutukan. Maka putusan (sekarang ini) adalah pada Allah Yang Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,13,'هُوَ الَّذِيْ يُرِيْكُمْ اٰيٰتِهٖ وَيُنَزِّلُ لَكُمْ مِّنَ السَّمَاۤءِ رِزْقًا ۗوَمَا يَتَذَكَّرُ اِلَّا مَنْ يُّنِيْبُ','Dialah yang memperlihatkan kepadamu tanda-tanda (kekuasaan)-Nya dan menurunkan untukmu rezeki dari langit. Dan tiadalah mendapat pelajaran kecuali orang-orang yang kembali (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,14,'فَادْعُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ وَلَوْ كَرِهَ الْكٰفِرُوْنَ','Maka sembahlah Allah dengan memurnikan ibadat kepada-Nya, meskipun orang-orang kafir tidak menyukai(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,15,'رَفِيْعُ الدَّرَجٰتِ ذُو الْعَرْشِۚ يُلْقِى الرُّوْحَ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ لِيُنْذِرَ يَوْمَ التَّلَاقِۙ','(Dialah) Yang Maha Tinggi derajat-Nya, Yang mempunyai ´Arsy, Yang mengutus Jibril dengan (membawa) perintah-Nya kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya, supaya dia memperingatkan (manusia) tentang hari pertemuan (hari kiamat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,16,'يَوْمَ هُمْ بَارِزُوْنَ ۚ لَا يَخْفٰى عَلَى اللّٰهِ مِنْهُمْ شَيْءٌ ۗلِمَنِ الْمُلْكُ الْيَوْمَ ۗ لِلّٰهِ الْوَاحِدِ الْقَهَّارِ','(Yaitu) hari (ketika) mereka keluar (dari kubur); tiada suatupun dari keadaan mereka yang tersembunyi bagi Allah. (Lalu Allah berfirman): "Kepunyaan siapakah kerajaan pada hari ini?" Kepunyaan Allah Yang Maha Esa lagi Maha Mengalahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,17,'اَلْيَوْمَ تُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ ۗ لَا ظُلْمَ الْيَوْمَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ','Pada hari ini tiap-tiap jiwa diberi balasan dengan apa yang diusahakannya. Tidak ada yang dirugikan pada hari ini. Sesungguhnya Allah amat cepat hisabnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,18,'وَاَنْذِرْهُمْ يَوْمَ الْاٰزِفَةِ اِذِ الْقُلُوْبُ لَدَى الْحَنَاجِرِ كَاظِمِيْنَ ەۗ مَا لِلظّٰلِمِيْنَ مِنْ حَمِيْمٍ وَّلَا شَفِيْعٍ يُّطَاعُۗ','Berilah mereka peringatan dengan hari yang dekat (hari kiamat yaitu) ketika hati (menyesak) sampai di kerongkongan dengan menahan kesedihan. Orang-orang yang zalim tidak mempunyai teman setia seorangpun dan tidak (pula) mempunyai seorang pemberi syafa´at yang diterima syafa´atnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,19,'يَعْلَمُ خَاۤىِٕنَةَ الْاَعْيُنِ وَمَا تُخْفِى الصُّدُوْرُ','Dia mengetahui (pandangan) mata yang khianat dan apa yang disembunyikan oleh hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,20,'وَاللّٰهُ يَقْضِيْ بِالْحَقِّ ۗوَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَقْضُوْنَ بِشَيْءٍ ۗاِنَّ اللّٰهَ هُوَ السَّمِيْعُ الْبَصِيْرُ ࣖ','Dan Allah menghukum dengan keadilan. Dan sembahan-sembahan yang mereka sembah selain Allah tiada dapat menghukum dengan sesuatu apapun. Sesungguhnya Allah Dialah Yang Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,21,'۞ اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ كَانُوْا مِنْ قَبْلِهِمْ ۗ كَانُوْا هُمْ اَشَدَّ مِنْهُمْ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗوَمَا كَانَ لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ','Dan apakah mereka tidak mengadakan perjalanan di muka bumi, lalu memperhatikan betapa kesudahan orang-orang yang sebelum mereka. Mereka itu adalah lebih hebat kekuatannya daripada mereka dan (lebih banyak) bekas-bekas mereka di muka bumi, maka Allah mengazab mereka disebabkan dosa-dosa mereka. Dan mereka tidak mempunyai seorang pelindung dari azab Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,22,'ذٰلِكَ بِاَنَّهُمْ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَكَفَرُوْا فَاَخَذَهُمُ اللّٰهُ ۗاِنَّهٗ قَوِيٌّ شَدِيْدُ الْعِقَابِ','Yang demiklan itu adalah karena telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata lalu mereka kafir; maka Allah mengazab mereka. Sesungguhnya Dia Maha Kuat lagi Maha Keras hukuman-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,23,'وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ','Dan sesungguhnya telah Kami utus Musa dengan membawa ayat-ayat Kami dan keterangan yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,24,'اِلٰى فِرْعَوْنَ وَهَامٰنَ وَقَارُوْنَ فَقَالُوْا سٰحِرٌ كَذَّابٌ','kepada Fir´aun, Haman dan Qarun; maka mereka berkata: "(Ia) adalah seorang ahli sihir yang pendusta".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,25,'فَلَمَّا جَاۤءَهُمْ بِالْحَقِّ مِنْ عِنْدِنَا قَالُوا اقْتُلُوْٓا اَبْنَاۤءَ الَّذِيْنَ اٰمَنُوْا مَعَهٗ وَاسْتَحْيُوْا نِسَاۤءَهُمْ ۗوَمَا كَيْدُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ','Maka tatkala Musa datang kepada mereka membawa kebenaran dari sisi Kami mereka berkata: "Bunuhlah anak-anak orang-orang yang beriman bersama dengan dia dan biarkanlah hidup wanita-wanita mereka". Dan tipu daya orang-orang kafir itu tak lain hanyalah sia-sia (belaka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,26,'وَقَالَ فِرْعَوْنُ ذَرُوْنِيْٓ اَقْتُلْ مُوْسٰى وَلْيَدْعُ رَبَّهٗ ۚاِنِّيْٓ اَخَافُ اَنْ يُّبَدِّلَ دِيْنَكُمْ اَوْ اَنْ يُّظْهِرَ فِى الْاَرْضِ الْفَسَادَ','Dan berkata Fir´aun (kepada pembesar-pembesarnya): "Biarkanlah aku membunuh Musa dan hendaklah ia memohon kepada Tuhannya, karena sesungguhnya aku khawatir dia akan menukar agamamu atau menimbulkan kerusakan di muka bumi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,27,'وَقَالَ مُوْسٰىٓ اِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ مِّنْ كُلِّ مُتَكَبِّرٍ لَّا يُؤْمِنُ بِيَوْمِ الْحِسَابِ ࣖ ','Dan Musa berkata: "Sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu dari setiap orang yang menyombongkan diri yang tidak beriman kepada hari berhisab".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,28,'وَقَالَ رَجُلٌ مُّؤْمِنٌۖ مِّنْ اٰلِ فِرْعَوْنَ يَكْتُمُ اِيْمَانَهٗٓ اَتَقْتُلُوْنَ رَجُلًا اَنْ يَّقُوْلَ رَبِّيَ اللّٰهُ وَقَدْ جَاۤءَكُمْ بِالْبَيِّنٰتِ مِنْ رَّبِّكُمْ ۗوَاِنْ يَّكُ كَاذِبًا فَعَلَيْهِ كَذِبُهٗ ۚوَاِنْ يَّكُ صَادِقًا يُّصِبْكُمْ بَعْضُ الَّذِيْ يَعِدُكُمْ ۗاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ مُسْرِفٌ كَذَّابٌ','Dan seorang laki-laki yang beriman di antara pengikut-pengikut Fir´aun yang menyembunyikan imannya berkata: "Apakah kamu akan membunuh seorang laki-laki karena dia menyatakan: "Tuhanku ialah Allah padahal dia telah datang kepadamu dengan membawa keterangan-keterangan dari Tuhanmu. Dan jika ia seorang pendusta maka dialah yang menanggung (dosa) dustanya itu; dan jika ia seorang yang benar niscaya sebagian (bencana) yang diancamkannya kepadamu akan menimpamu". Sesungguhnya Allah tidak menunjuki orang-orang yang melampaui batas lagi pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,29,'يٰقَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِرِيْنَ فِى الْاَرْضِۖ فَمَنْ يَّنْصُرُنَا مِنْۢ بَأْسِ اللّٰهِ اِنْ جَاۤءَنَا ۗقَالَ فِرْعَوْنُ مَآ اُرِيْكُمْ اِلَّا مَآ اَرٰى وَمَآ اَهْدِيْكُمْ اِلَّا سَبِيْلَ الرَّشَادِ','(Musa berkata): "Hai kaumku, untukmulah kerajaan pada hari ini dengan berkuasa di muka bumi. Siapakah yang akan menolong kita dari azab Allah jika azab itu menimpa kita!" Fir´aun berkata: "Aku tidak mengemukakan kepadamu, melainkan apa yang aku pandang baik; dan aku tiada menunjukkan kepadamu selain jalan yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,30,'وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ مِّثْلَ يَوْمِ الْاَحْزَابِۙ  ','Dan orang yang beriman itu berkata: "Hai kaumku, sesungguhnya aku khawatir kamu akan ditimpa (bencana) seperti peristiwa kehancuran golongan yang bersekutu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,31,'مِثْلَ دَأْبِ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗوَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعِبَادِ','(Yakni) seperti keadaan kaum Nuh, ´Aad, Tsamud dan orang-orang yang datang sesudah mereka. Dan Allah tidak menghendaki berbuat kezaliman terhadap hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,32,'وَيٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِۙ  ','Hai kaumku, sesungguhnya aku khawatir terhadapmu akan siksaan hari panggil-memanggil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,33,'يَوْمَ تُوَلُّوْنَ مُدْبِرِيْنَۚ مَا لَكُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ','(yaitu) hari (ketika) kamu (lari) berpaling ke belakang, tidak ada bagimu seorangpun yang menyelamatkan kamu dari (azab) Allah, dan siapa yang disesatkan Allah, niscaya tidak ada baginya seorangpun yang akan memberi petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,34,'وَلَقَدْ جَاۤءَكُمْ يُوْسُفُ مِنْ قَبْلُ بِالْبَيِّنٰتِ فَمَا زِلْتُمْ فِيْ شَكٍّ مِّمَّا جَاۤءَكُمْ بِهٖ ۗحَتّٰىٓ اِذَا هَلَكَ قُلْتُمْ لَنْ يَّبْعَثَ اللّٰهُ مِنْۢ بَعْدِهٖ رَسُوْلًا ۗ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ هُوَ مُسْرِفٌ مُّرْتَابٌۙ','Dan sesungguhnya telah datang Yusuf kepadamu dengan membawa keterangan-keterangan, tetapi kamu senantiasa dalam keraguan tentang apa yang dibawanya kepadamu, hingga ketika dia meninggal, kamu berkata: "Allah tidak akan mengirim seorang (rasulpun) sesudahnya. Demikianlah Allah menyesatkan orang-orang yang melampaui batas dan ragu-ragu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,35,' ۨالَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْۗ  كَبُرَ مَقْتًا عِنْدَ اللّٰهِ وَعِنْدَ الَّذِيْنَ اٰمَنُوْا ۗ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ','(Yaitu) orang-orang yang memperdebatkan ayat-ayat Allah tanpa alasan yang sampai kepada mereka. Amat besar kemurkaan (bagi mereka) di sisi Allah dan di sisi orang-orang yang beriman. Demikianlah Allah mengunci mati hati orang yang sombong dan sewenang-wenang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,36,'وَقَالَ فِرْعَوْنُ يٰهَامٰنُ ابْنِ لِيْ صَرْحًا لَّعَلِّيْٓ اَبْلُغُ الْاَسْبَابَۙ','Dan berkatalah Fir´aun: "Hai Haman, buatkanlah bagiku sebuah bangunan yang tinggi supaya aku sampai ke pintu-pintu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,37,'اَسْبَابَ السَّمٰوٰتِ فَاَطَّلِعَ اِلٰٓى اِلٰهِ مُوْسٰى وَاِنِّيْ لَاَظُنُّهٗ كَاذِبًا ۗوَكَذٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوْۤءُ عَمَلِهٖ وَصُدَّ عَنِ السَّبِيْلِ ۗوَمَا كَيْدُ فِرْعَوْنَ اِلَّا فِيْ تَبَابٍ ࣖ ','(yaitu) pintu-pintu langit, supaya aku dapat melihat Tuhan Musa dan sesungguhnya aku memandangnya seorang pendusta". Demikianlah dijadikan Fir´aun memandang baik perbuatan yang buruk itu, dan dia dihalangi dari jalan (yang benar); dan tipu daya Fir´aun itu tidak lain hanyalah membawa kerugian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,38,'وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اتَّبِعُوْنِ اَهْدِكُمْ سَبِيْلَ الرَّشَادِۚ','Orang yang beriman itu berkata: "Hai kaumku, ikutilah aku, aku akan menunjukkan kepadamu jalan yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,39,'يٰقَوْمِ اِنَّمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَا مَتَاعٌ  ۖوَّاِنَّ الْاٰخِرَةَ هِيَ دَارُ الْقَرَارِ','Hai kaumku, sesungguhnya kehidupan dunia ini hanyalah kesenangan (sementara) dan sesungguhnya akhirat itulah negeri yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,40,'مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزٰىٓ اِلَّا مِثْلَهَاۚ وَمَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ يُرْزَقُوْنَ فِيْهَا بِغَيْرِ حِسَابٍ ','Barangsiapa mengerjakan perbuatan jahat, maka dia tidak akan dibalasi melainkan sebanding dengan kejahatan itu. Dan barangsiapa mengerjakan amal yang saleh baik laki-laki maupun perempuan sedang ia dalam keadaan beriman, maka mereka akan masuk surga, mereka diberi rezeki di dalamnya tanpa hisab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,41,'۞ وَيٰقَوْمِ مَا لِيْٓ اَدْعُوْكُمْ اِلَى النَّجٰوةِ وَتَدْعُوْنَنِيْٓ اِلَى النَّارِۗ','Hai kaumku, bagaimanakah kamu, aku menyeru kamu kepada keselamatan, tetapi kamu menyeru aku ke neraka?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,42,'تَدْعُوْنَنِيْ لِاَكْفُرَ بِاللّٰهِ وَاُشْرِكَ بِهٖ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ وَّاَنَا۠ اَدْعُوْكُمْ اِلَى الْعَزِيْزِ الْغَفَّارِ','(Kenapa) kamu menyeruku supaya kafir kepada Allah dan mempersekutukan-Nya dengan apa yang tidak kuketahui padahal aku menyeru kamu (beriman) kepada Yang Maha Perkasa lagi Maha Pengampun?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,43,'لَا جَرَمَ اَنَّمَا تَدْعُوْنَنِيْٓ اِلَيْهِ لَيْسَ لَهٗ دَعْوَةٌ فِى الدُّنْيَا وَلَا فِى الْاٰخِرَةِ وَاَنَّ مَرَدَّنَآ اِلَى اللّٰهِ وَاَنَّ الْمُسْرِفِيْنَ هُمْ اَصْحٰبُ النَّارِ','Sudah pasti bahwa apa yang kamu seru supaya aku (beriman) kepadanya tidak dapat memperkenankan seruan apapun baik di dunia maupun di akhirat. Dan sesungguhnya kita kembali kepada Allah dan sesungguhnya orang-orang yang melampaui batas, mereka itulah penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,44,'فَسَتَذْكُرُوْنَ مَآ اَقُوْلُ لَكُمْۗ وَاُفَوِّضُ اَمْرِيْٓ اِلَى اللّٰهِ ۗاِنَّ اللّٰهَ بَصِيْرٌ ۢبِالْعِبَادِ','Kelak kamu akan ingat kepada apa yang kukatakan kepada kamu. Dan aku menyerahkan urusanku kepada Allah. Sesungguhnya Allah Maha Melihat akan hamba-hamba-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,45,'فَوَقٰىهُ اللّٰهُ سَيِّاٰتِ مَا مَكَرُوْا وَحَاقَ بِاٰلِ فِرْعَوْنَ سُوْۤءُ الْعَذَابِۚ','Maka Allah memeliharanya dari kejahatan tipu daya mereka, dan Fir´aun beserta kaumnya dikepung oleh azab yang amat buruk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,46,'اَلنَّارُ يُعْرَضُوْنَ عَلَيْهَا غُدُوًّا وَّعَشِيًّا ۚوَيَوْمَ تَقُوْمُ السَّاعَةُ ۗ اَدْخِلُوْٓا اٰلَ فِرْعَوْنَ اَشَدَّ الْعَذَابِ','Kepada mereka dinampakkan neraka pada pagi dan petang, dan pada hari terjadinya Kiamat. (Dikatakan kepada malaikat): "Masukkanlah Fir´aun dan kaumnya ke dalam azab yang sangat keras".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,47,'وَاِذْ يَتَحَاۤجُّوْنَ فِى النَّارِ فَيَقُوْلُ الضُّعَفٰۤؤُ لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا نَصِيْبًا مِّنَ النَّارِ','Dan (ingatlah), ketika mereka berbantah-bantah dalam neraka, maka orang-orang yang lemah berkata kepada orang-orang yang menyombongkan diri: "Sesungguhnya kami adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan dari kami sebahagian azab api neraka?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,48,'قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُلٌّ فِيْهَآ اِنَّ اللّٰهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ','Orang-orang yang menyombongkan diri menjawab: "Sesungguhnya kita semua sama-sama dalam neraka karena sesungguhnya Allah telah menetapkan keputusan antara hamba-hamba-(Nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,49,'وَقَالَ الَّذِيْنَ فِى النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوْا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِّنَ الْعَذَابِ ','Dan orang-orang yang berada dalam neraka berkata kepada penjaga-penjaga neraka Jahannam: "Mohonkanlah kepada Tuhanmu supaya Dia meringankan azab dari kami barang sehari".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,50,'قَالُوْٓا اَوَلَمْ تَكُ تَأْتِيْكُمْ رُسُلُكُمْ بِالْبَيِّنٰتِ ۗقَالُوْا بَلٰىۗ قَالُوْا فَادْعُوْا ۚوَمَا دُعٰۤؤُا الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ ࣖ','Penjaga Jahannam berkata: "Dan apakah belum datang kepada kamu rasul-rasulmu dengan membawa keterangan-keterangan?" Mereka menjawab: "Benar, sudah datang". Penjaga-penjaga Jahannam berkata: "Berdoalah kamu". Dan doa orang-orang kafir itu hanyalah sia-sia belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,51,'اِنَّا لَنَنْصُرُ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا وَيَوْمَ يَقُوْمُ الْاَشْهَادُۙ ','Sesungguhnya Kami menolong rasul-rasul Kami dan orang-orang yang beriman dalam kehidupan dunia dan pada hari berdirinya saksi-saksi (hari kiamat),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,52,'يَوْمَ لَا يَنْفَعُ الظّٰلِمِيْنَ مَعْذِرَتُهُمْ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ ','(yaitu) hari yang tidak berguna bagi orang-orang zalim permintaan maafnya dan bagi merekalah laknat dan bagi merekalah tempat tinggal yang buruk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,53,'وَلَقَدْاٰتَيْنَا مُوْسٰى الْهُدٰى وَاَوْرَثْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَۙ ','Dan sesungguhnya telah Kami berikan petunjuk kepada Musa; dan Kami wariskan Taurat kepada Bani Israil,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,54,'هُدًى وَّذِكْرٰى لِاُولِى الْاَلْبَابِ','untuk menjadi petunjuk dan peringatan bagi orang-orang yang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,55,'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاسْتَغْفِرْ لِذَنْۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْاِبْكَارِ','Maka bersabarlah kamu, karena sesungguhnya janji Allah itu benar, dan mohonlah ampunan untuk dosamu dan bertasbihlah seraya memuji Tuhanmu pada waktu petang dan pagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,56,'اِنَّ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْ ۙاِنْ فِيْ صُدُوْرِهِمْ اِلَّا كِبْرٌ مَّا هُمْ بِبَالِغِيْهِۚ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ','Sesungguhhnya orang-orang yang memperdebatkan tentang ayat-ayat Allah tanpa alasan yang sampai kepada mereka tidak ada dalam dada mereka melainkan hanyalah (keinginan akan) kebesaran yang mereka sekali-kali tiada akan mencapainya, maka mintalah perlindungan kepada Allah. Sesungguhnya Dia Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,57,'لَخَلْقُ السَّمٰوٰتِ وَالْاَرْضِ اَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ','Sesungguhnya penciptaan langit dan bumi lebih besar daripada penciptaan manusia akan tetapi kebanyakan manusia tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,58,'وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَلَا الْمُسِيْۤئُ ۗقَلِيْلًا مَّا تَتَذَكَّرُوْنَ','Dan tidaklah sama orang yang buta dengan orang yang melihat, dan tidaklah (pula sama) orang-orang yang beriman serta mengerjakan amal saleh dengan orang-orang yang durhaka. Sedikit sekali kamu mengambil pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,59,'اِنَّ السَّاعَةَ لَاٰتِيَةٌ لَّا رَيْبَ فِيْهَا ۖوَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ','Sesungguhnya hari kiamat pasti akan datang, tidak ada keraguan tentangnya, akan tetapi kebanyakan manusia tiada beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,60,'وَقَالَ رَبُّكُمُ ادْعُوْنِيْٓ اَسْتَجِبْ لَكُمْ ۗاِنَّ الَّذِيْنَ يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِيْ سَيَدْخُلُوْنَ جَهَنَّمَ دَاخِرِيْنَ ࣖࣖࣖ ','Dan Tuhanmu berfirman: "Berdoalah kepada-Ku, niscaya akan Kuperkenankan bagimu. Sesungguhnya orang-orang yang menyombongkan diri dari menyembah-Ku akan masuk neraka Jahannam dalam keadaan hina dina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,61,'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ','Allah-lah yang menjadikan malam untuk kamu supaya kamu beristirahat padanya; dan menjadikan siang terang benderang. Sesungguhnya Allah benar-benar mempunyal karunia yang dilimpahkan atas manusia, akan tetapi kebanyakan manusia tidak bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,62,'ذٰلِكُمُ اللّٰهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍۘ  لَآ اِلٰهَ اِلَّا هُوَ ۖفَاَنّٰى تُؤْفَكُوْنَ','Yang demikian itu adalah Allah, Tuhanmu, Pencipta segala sesuatu, tiada Tuhan (yang berhak disembah) melainkan Dia; maka bagaimanakah kamu dapat dipalingkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,63,'كَذٰلِكَ يُؤْفَكُ الَّذِيْنَ كَانُوْا بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ','Seperti demikianlah dipalingkan orang-orang yang selalu mengingkari ayat-ayat Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,64,'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ قَرَارًا وَّالسَّمَاۤءَ بِنَاۤءً وَّصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ ۗذٰلِكُمُ اللّٰهُ رَبُّكُمْ ۚ فَتَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ','Allah-lah yang menjadikan bumi bagi kamu tempat menetap dan langit sebagai atap, dan membentuk kamu lalu membaguskan rupamu serta memberi kamu rezeki dengan sebahagian yang baik-baik. Yang demikian itu adalah Allah Tuhanmu, Maha Agung Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,65,'هُوَ الْحَيُّ لَآ اِلٰهَ اِلَّا هُوَ فَادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ۗ اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ','Dialah Yang hidup kekal, tiada Tuhan (yang berhak disembah) melainkan Dia; maka sembahlah Dia dengan memurnikan ibadat kepada-Nya. Segala puji bagi Allah Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,66,'۞ قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَمَّا جَاۤءَنِيَ الْبَيِّنٰتُ مِنْ رَّبِّيْ وَاُمِرْتُ اَنْ اُسْلِمَ لِرَبِّ الْعٰلَمِيْنَ','Katakanlah (ya Muhammad): "Sesungguhnya aku dilarang menyembah sembahan yang kamu sembah selain Allah setelah datang kepadaku keterangan-keterangan dari Tuhanku; dan aku diperintahkan supaya tunduk patuh kepada Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,67,'هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْ ثُمَّ لِتَكُوْنُوْا شُيُوْخًا ۚوَمِنْكُمْ مَّنْ يُّتَوَفّٰى مِنْ قَبْلُ وَلِتَبْلُغُوْٓا اَجَلًا مُّسَمًّى وَّلَعَلَّكُمْ تَعْقِلُوْنَ','Dialah yang menciptakan kamu dari tanah kemudian dari setetes mani, sesudah itu dari segumpal darah, kemudian dilahirkannya kamu sebagai seorang anak, kemudian (kamu dibiarkan hidup) supaya kamu sampai kepada masa (dewasa), kemudian (dibiarkan kamu hidup lagi) sampai tua, di antara kamu ada yang diwafatkan sebelum itu. (Kami perbuat demikian) supaya kamu sampai kepada ajal yang ditentukan dan supaya kamu memahami(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,68,'هُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُۚ فَاِذَا قَضٰىٓ اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ࣖ ','Dialah yang menghidupkan dan mematikan, maka apabila Dia menetapkan sesuatu urusan, Dia hanya bekata kepadanya: "Jadilah", maka jadilah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,69,'اَلَمْ تَرَ اِلَى الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ ۗاَنّٰى يُصْرَفُوْنَۚ','Apakah kamu tidak melihat kepada orang-orang yang membantah ayat-ayat Allah? Bagaimanakah mereka dapat dipalingkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,70,'اَلَّذِيْنَ كَذَّبُوْا بِالْكِتٰبِ وَبِمَآ اَرْسَلْنَا بِهٖ رُسُلَنَا ۗفَسَوْفَ يَعْلَمُوْنَۙ','(Yaitu) orang-orang yang mendustakan Al Kitab (Al Quran) dan wahyu yang dibawa oleh rasul-rasul Kami yang telah Kami utus. Kelak mereka akan mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,71,'اِذِ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْ وَالسَّلٰسِلُۗ يُسْحَبُوْنَۙ','ketika belenggu dan rantai dipasang di leher mereka, seraya mereka diseret,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,72,'فِى الْحَمِيْمِ ەۙ ثُمَّ فِى النَّارِ يُسْجَرُوْنَۚ','ke dalam air yang sangat panas, kemudian mereka dibakar dalam api,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,73,'ثُمَّ قِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تُشْرِكُوْنَۙ','kemudian dikatakan kepada mereka: "Manakah berhala-berhala yang selalu kamu persekutukan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,74,'مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا بَلْ لَّمْ نَكُنْ نَّدْعُوْا مِنْ قَبْلُ شَيْـًٔاۚ  كَذٰلِكَ يُضِلُّ اللّٰهُ الْكٰفِرِيْنَ','(yang kamu sembah) selain Allah?" Mereka menjawab: "Mereka telah hilang lenyap dari kami, bahkan kami dahulu tiada pernah menyembah sesuatu". Seperti demikianlah Allah menyesatkan orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,75,'ذٰلِكُمْ بِمَا كُنْتُمْ تَفْرَحُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَمْرَحُوْنَ','Yang demikian itu disebabkan karena kamu bersuka ria di muka bumi dengan tidak benar dan karena kamu selalu bersuka ria (dalam kemaksiatan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,76,'اُدْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ','(Dikatakan kepada mereka): "Masuklah kamu ke pintu-pintu neraka Jahannam, sedang kamu kekal di dalamnya. Maka itulah seburuk-buruk tempat bagi orang-orang yang sombong".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,77,'فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ ۚفَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا يُرْجَعُوْنَ','Maka bersabarlah kamu, sesungguhnya janji Allah adalah benar; maka meskipun Kami perlihatkan kepadamu sebagian siksa yang Kami ancamkan kepada mereka ataupun Kami wafatkan kamu (sebelum ajal menimpa mereka), namun kepada Kami sajalah mereka dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,78,'وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ مِنْهُمْ مَّنْ قَصَصْنَا عَلَيْكَ وَمِنْهُمْ مَّنْ لَّمْ نَقْصُصْ عَلَيْكَ ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۚفَاِذَا جَاۤءَ اَمْرُ اللّٰهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُوْنَ ࣖ ','Dan sesungguhnya telah Kami utus beberapa orang rasul sebelum kamu, di antara mereka ada yang Kami ceritakan kepadamu dan di antara mereka ada (pula) yang tidak Kami ceritakan kepadamu. Tidak dapat bagi seorang rasul membawa suatu mukjizat, melainkan dengan seizin Allah; maka apabila telah datang perintah Allah, diputuskan (semua perkara) dengan adil. Dan ketika itu rugilah orang-orang yang berpegang kepada yang batil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,79,'اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَنْعَامَ لِتَرْكَبُوْا مِنْهَا وَمِنْهَا تَأْكُلُوْنَۖ','Allahlah yang menjadikan binatang ternak untuk kamu, sebagiannya untuk kamu kendarai dan sebagiannya untuk kamu makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,80,'وَلَكُمْ فِيْهَا مَنَافِعُ وَلِتَبْلُغُوْا عَلَيْهَا حَاجَةً فِيْ صُدُوْرِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَۗ','Dan (ada lagi) manfaat-manfaat yang lain pada binatang ternak itu untuk kamu dan supaya kamu mencapai suatu keperluan yang tersimpan dalam hati dengan mengendarainya. Dan kamu dapat diangkut dengan mengendarai binatang-binatang itu dan dengan mengendarai bahtera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,81,'وَيُرِيْكُمْ اٰيٰتِهٖۖ فَاَيَّ اٰيٰتِ اللّٰهِ تُنْكِرُوْنَ','Dan Dia memperlihatkan kepada kamu tanda-tanda (kekuasaan-Nya); maka tanda-tanda (kekuasaan) Allah yang manakah yang kamu ingkari?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,82,'اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ كَانُوْٓا اَكْثَرَ مِنْهُمْ وَاَشَدَّ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ','Maka apakah mereka tiada mengadakan perjalanan di muka bumi lalu memperhatikan betapa kesudahan orang-orang yang sebelum mereka. Adalah orang-orang yang sebelum mereka itu lebih hebat kekuatannya dan (lebih banyak) bekas-bekas mereka di muka bumi, maka apa yang mereka usahakan itu tidak dapat menolong mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,83,'فَلَمَّا جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرِحُوْا بِمَا عِنْدَهُمْ مِّنَ الْعِلْمِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ','Maka tatkala datang kepada mereka rasul-rasul (yang diutus kepada) mereka dengan membawa ketarangan-keterangan, mereka merasa senang dengan pengetahuan yang ada pada mereka dan mereka dikepung oleh azab Allah yang selalu mereka perolok-olokkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,84,'فَلَمَّا رَاَوْا بَأْسَنَاۗ قَالُوْٓا اٰمَنَّا بِاللّٰهِ وَحْدَهٗ وَكَفَرْنَا بِمَا كُنَّا بِهٖ مُشْرِكِيْنَ','Maka tatkala mereka melihat azab Kami, mereka berkata: "Kami beriman hanya kepada Allah saja, dan kami kafir kepada sembahan-sembahan yang telah kami persekutukan dengan Allah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,40,85,'فَلَمْ يَكُ يَنْفَعُهُمْ اِيْمَانُهُمْ لَمَّا رَاَوْا بَأْسَنَا ۗسُنَّتَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ فِيْ عِبَادِهِۚ وَخَسِرَ هُنَالِكَ الْكٰفِرُوْنَ ࣖ ','Maka iman mereka tiada berguna bagi mereka tatkala mereka telah melihat siksa Kami. Itulah sunnah Allah yang telah berlaku terhadap hamba-hamba-Nya. Dan di waktu itu binasalah orang-orang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,1,' حٰمۤ ۚ ','Haa Miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,2,'تَنْزِيْلٌ مِّنَ الرَّحْمٰنِ الرَّحِيْمِ ۚ ','Diturunkan dari Tuhan Yang Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,3,'كِتٰبٌ فُصِّلَتْ اٰيٰتُهٗ قُرْاٰنًا عَرَبِيًّا لِّقَوْمٍ يَّعْلَمُوْنَۙ ','Kitab yang dijelaskan ayat-ayatnya, yakni bacaan dalam bahasa Arab, untuk kaum yang mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,4,'بَشِيْرًا وَّنَذِيْرًاۚ فَاَعْرَضَ اَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُوْنَ ','yang membawa berita gembira dan yang membawa peringatan, tetapi kebanyakan mereka berpaling, tidak mau mendengarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,5,'وَقَالُوْا قُلُوْبُنَا فِيْٓ اَكِنَّةٍ مِّمَّا تَدْعُوْنَآ اِلَيْهِ وَفِيْٓ اٰذَانِنَا وَقْرٌ وَّمِنْۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ اِنَّنَا عٰمِلُوْنَ','Mereka berkata: "Hati kami berada dalam tutupan (yang menutupi) apa yang kamu seru kami kepadanya dan telinga kami ada sumbatan dan antara kami dan kamu ada dinding, maka bekerjalah kamu; sesungguhnya kami bekerja (pula)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,6,'قُلْ اِنَّمَآ اَنَا۟ بَشَرٌ مِّثْلُكُمْ يُوْحٰىٓ اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَاسْتَقِيْمُوْٓا اِلَيْهِ وَاسْتَغْفِرُوْهُ ۗوَوَيْلٌ لِّلْمُشْرِكِيْنَۙ ','Katakanlah: "Bahwasanya aku hanyalah seorang manusia seperti kamu, diwahyukan kepadaku bahwasanya Tuhan kamu adalah Tuhan yang Maha Esa, maka tetaplah pada jalan yang lurus menuju kepada-Nya dan mohonlah ampun kepada-Nya. Dan kecelakaan besarlah bagi orang-orang yang mempersekutukan-Nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,7,'الَّذِيْنَ لَا يُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَ','(yaitu) orang-orang yang tidak menunaikan zakat dan mereka kafir akan adanya (kehidupan) akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,8,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ ࣖ ','Sesungguhnya orang-orang yang beriman dan beramal saleh, mereka mendapat pahala yang tiada putus-putusnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,9,'۞ قُلْ اَىِٕنَّكُمْ لَتَكْفُرُوْنَ بِالَّذِيْ خَلَقَ الْاَرْضَ فِيْ يَوْمَيْنِ وَتَجْعَلُوْنَ لَهٗٓ اَنْدَادًا ۗذٰلِكَ رَبُّ الْعٰلَمِيْنَ ۚ ','Katakanlah: "Sesungguhnya patutkah kamu kafir kepada Yang menciptakan bumi dalam dua masa dan kamu adakan sekutu-sekutu bagi-Nya? (Yang bersifat) demikian itu adalah Rabb semesta alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,10,'وَجَعَلَ فِيْهَا رَوَاسِيَ مِنْ فَوْقِهَا وَبٰرَكَ فِيْهَا وَقَدَّرَ فِيْهَآ اَقْوَاتَهَا فِيْٓ اَرْبَعَةِ اَيَّامٍۗ سَوَاۤءً لِّلسَّاۤىِٕلِيْنَ','Dan dia menciptakan di bumi itu gunung-gunung yang kokoh di atasnya. Dia memberkahinya dan Dia menentukan padanya kadar makanan-makanan (penghuni)nya dalam empat masa. (Penjelasan itu sebagai jawaban) bagi orang-orang yang bertanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,11,'ثُمَّ اسْتَوٰىٓ اِلَى السَّمَاۤءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْاَرْضِ ائْتِيَا طَوْعًا اَوْ كَرْهًاۗ قَالَتَآ اَتَيْنَا طَاۤىِٕعِيْنَ ','Kemudian Dia menuju kepada penciptaan langit dan langit itu masih merupakan asap, lalu Dia berkata kepadanya dan kepada bumi: "Datanglah kamu keduanya menurut perintah-Ku dengan suka hati atau terpaksa". Keduanya menjawab: "Kami datang dengan suka hati".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,12,'فَقَضٰىهُنَّ سَبْعَ سَمٰوٰتٍ فِيْ يَوْمَيْنِ وَاَوْحٰى فِيْ كُلِّ سَمَاۤءٍ اَمْرَهَا ۗوَزَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَۖ وَحِفْظًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ','Maka Dia menjadikannya tujuh langit dalam dua masa. Dia mewahyukan pada tiap-tiap langit urusannya. Dan Kami hiasi langit yang dekat dengan bintang-bintang yang cemerlang dan Kami memeliharanya dengan sebaik-baiknya. Demikianlah ketentuan Yang Maha Perkasa lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,13,'فَاِنْ اَعْرَضُوْا فَقُلْ اَنْذَرْتُكُمْ صٰعِقَةً مِّثْلَ صٰعِقَةِ عَادٍ وَّثَمُوْدَ ۗ ','Jika mereka berpaling maka katakanlah: "Aku telah memperingatkan kamu dengan petir, seperti petir yang menimpa kaum ´Aad dan Tsamud".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,14,'اِذْ جَاۤءَتْهُمُ الرُّسُلُ مِنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗقَالُوْا لَوْ شَاۤءَ رَبُّنَا لَاَنْزَلَ مَلٰۤىِٕكَةً فَاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ','Ketika para rasul datang kepada mereka dari depan dan belakang mereka (dengan menyerukan): "Janganlah kamu menyembah selain Allah". Mereka menjawab: "Kalau Tuhan kami menghendaki tentu Dia akan menurunkan malaikat-malaikat-Nya, maka sesungguhnya kami kafir kepada wahyu yang kamu diutus membawanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,15,'فَاَمَّا عَادٌ فَاسْتَكْبَرُوْا فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوْا مَنْ اَشَدُّ مِنَّا قُوَّةً ۗ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَهُمْ هُوَ اَشَدُّ مِنْهُمْ قُوَّةً ۗ وَكَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ','Adapun kaum ´Aad maka mereka menyombongkan diri di muka bumi tanpa alasan yang benar dan berkata: "Siapakah yang lebih besar kekuatannya dari kami?" Dan apakah mereka itu tidak memperhatikan bahwa Allah Yang menciptakan mereka adalah lebih besar kekuatan-Nya daripada mereka? Dan adalah mereka mengingkari tanda-tanda (kekuatan) Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,16,'فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْٓ اَيَّامٍ نَّحِسَاتٍ لِّنُذِيْقَهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا ۗوَلَعَذَابُ الْاٰخِرَةِ اَخْزٰى وَهُمْ لَا يُنْصَرُوْنَ','Maka Kami meniupkan angin yang amat gemuruh kepada mereka dalam beberapa hari yang sial, karena Kami hendak merasakan kepada mereka itu siksaan yang menghinakan dalam kehidupan dunia. Dan Sesungguhnya siksa akhirat lebih menghinakan sedang mereka tidak diberi pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,17,'وَاَمَّا ثَمُوْدُ فَهَدَيْنٰهُمْ فَاسْتَحَبُّوا الْعَمٰى عَلَى الْهُدٰى فَاَخَذَتْهُمْ صٰعِقَةُ الْعَذَابِ الْهُوْنِ بِمَا كَانُوْا يَكْسِبُوْنَ ۚ ','Dan adapun kaum Tsamud, maka mereka telah Kami beri petunjuk tetapi mereka lebih menyukai buta (kesesatan) daripada petunjuk, maka mereka disambar petir azab yang menghinakan disebabkan apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,18,'وَنَجَّيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ ࣖ ','Dan Kami selamatkan orang-orang yang beriman dan mereka adalah orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,19,'وَيَوْمَ يُحْشَرُ اَعْدَاۤءُ اللّٰهِ اِلَى النَّارِ فَهُمْ يُوْزَعُوْنَ','Dan (ingatlah) hari (ketika) musuh-musuh Allah di giring ke dalam neraka, lalu mereka dikumpulkan semuanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,20,'حَتّٰىٓ اِذَا مَا جَاۤءُوْهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَاَبْصَارُهُمْ وَجُلُوْدُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ','Sehingga apabila mereka sampai ke neraka, pendengaran, penglihatan dan kulit mereka menjadi saksi terhadap mereka tentang apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,21,'وَقَالُوْا لِجُلُوْدِهِمْ لِمَ شَهِدْتُّمْ عَلَيْنَا ۗقَالُوْٓا اَنْطَقَنَا اللّٰهُ الَّذِيْٓ اَنْطَقَ كُلَّ شَيْءٍ وَّهُوَ خَلَقَكُمْ اَوَّلَ مَرَّةٍۙ وَّاِلَيْهِ تُرْجَعُوْنَ','Dan mereka berkata kepada kulit mereka: "Mengapa kamu menjadi saksi terhadap kami?" Kulit mereka menjawab: "Allah yang menjadikan segala sesuatu pandai berkata telah menjadikan kami pandai (pula) berkata, dan Dialah yang menciptakan kamu pada kali pertama dan hanya kepada-Nya lah kamu dikembalikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,22,'وَمَا كُنْتُمْ تَسْتَتِرُوْنَ اَنْ يَّشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ اَبْصَارُكُمْ وَلَا جُلُوْدُكُمْ وَلٰكِنْ ظَنَنْتُمْ اَنَّ اللّٰهَ لَا يَعْلَمُ كَثِيْرًا مِّمَّا تَعْمَلُوْنَ','Kamu sekali-sekali tidak dapat bersembunyi dari kesaksian pendengaran, penglihatan dan kulitmu kepadamu bahkan kamu mengira bahwa Allah tidak mengetahui kebanyakan dari apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,23,'وَذٰلِكُمْ ظَنُّكُمُ الَّذِيْ ظَنَنْتُمْ بِرَبِّكُمْ اَرْدٰىكُمْ فَاَصْبَحْتُمْ مِّنَ الْخٰسِرِيْنَ','Dan yang demikian itu adalah prasangkamu yang telah kamu sangka kepada Tuhanmu, Dia telah membinasakan kamu, maka jadilah kamu termasuk orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,24,'فَاِنْ يَّصْبِرُوْا فَالنَّارُ مَثْوًى لَّهُمْ ۚوَاِنْ يَّسْتَعْتِبُوْا فَمَا هُمْ مِّنَ الْمُعْتَبِيْنَ','Jika mereka bersabar (menderita azab) maka nerakalah tempat diam mereka dan jika mereka mengemukakan alasan-alasan, maka tidaklah mereka termasuk orang-orang yang diterima alasannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,25,'۞ وَقَيَّضْنَا لَهُمْ قُرَنَاۤءَ فَزَيَّنُوْا لَهُمْ مَّا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِۚ اِنَّهُمْ كَانُوْا خٰسِرِيْنَ ࣖ ','Dan Kami tetapkan bagi mereka teman-teman yang menjadikan mereka memandang bagus apa yang ada di hadapan dan di belakang mereka dan tetaplah atas mereka keputusan azab pada umat-umat yang terdahulu sebelum mereka dari jinn dan manusia, sesungguhnya mereka adalah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,26,'وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَسْمَعُوْا لِهٰذَا الْقُرْاٰنِ وَالْغَوْا فِيْهِ لَعَلَّكُمْ تَغْلِبُوْنَ','Dan orang-orang yang kafir berkata: "Janganlah kamu mendengar dengan sungguh-sungguh akan Al Quran ini dan buatlah hiruk-pikuk terhadapnya, supaya kamu dapat mengalahkan mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,27,'فَلَنُذِيْقَنَّ الَّذِيْنَ كَفَرُوْا عَذَابًا شَدِيْدًاۙ وَّلَنَجْزِيَنَّهُمْ اَسْوَاَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ','Maka sesungguhnya Kami akan merasakan azab yang keras kepada orang-orang kafir dan Kami akan memberi balasan kepada mereka dengan seburuk-buruk pembalasan bagi apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,28,'ذٰلِكَ جَزَاۤءُ اَعْدَاۤءِ اللّٰهِ النَّارُ لَهُمْ فِيْهَا دَارُ الْخُلْدِ ۗجَزَاۤءً ۢبِمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ','Demikianlah balasan terhadap musuh-musuh Allah, (yaitu) neraka; mereka mendapat tempat tinggal yang kekal di dalamnya sebagai balasan atas keingkaran mereka terhadap ayat-ayat Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,29,'وَقَالَ الَّذِيْنَ كَفَرُوْا رَبَّنَآ اَرِنَا الَّذَيْنِ اَضَلّٰنَا مِنَ الْجِنِّ وَالْاِنْسِ نَجْعَلْهُمَا تَحْتَ اَقْدَامِنَا لِيَكُوْنَا مِنَ الْاَسْفَلِيْنَ','Dan orang-orang kafir berkata: "Ya Rabb kami perlihatkanlah kepada kami dua jenis orang yang telah menyesatkan kami (yaitu) sebagian dari jinn dan manusia agar kami letakkan keduanya di bawah telapak kaki kami supaya kedua jenis itu menjadi orang-orang yang hina".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,30,'اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا تَتَنَزَّلُ عَلَيْهِمُ الْمَلٰۤىِٕكَةُ اَلَّا تَخَافُوْا وَلَا تَحْزَنُوْا وَاَبْشِرُوْا بِالْجَنَّةِ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ','Sesungguhnya orang-orang yang mengatakan: "Tuhan kami ialah Allah" kemudian mereka meneguhkan pendirian mereka, maka malaikat akan turun kepada mereka dengan mengatakan: "Janganlah kamu takut dan janganlah merasa sedih; dan gembirakanlah mereka dengan jannah yang telah dijanjikan Allah kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,31,'نَحْنُ اَوْلِيَاۤؤُكُمْ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِ ۚوَلَكُمْ فِيْهَا مَا تَشْتَهِيْٓ اَنْفُسُكُمْ وَلَكُمْ فِيْهَا مَا تَدَّعُوْنَ ۗ ','Kamilah pelindung-pelindungmu dalam kehidupan dunia dan akhirat; di dalamnya kamu memperoleh apa yang kamu inginkan dan memperoleh (pula) di dalamnya apa yang kamu minta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,32,'نُزُلًا مِّنْ غَفُوْرٍ رَّحِيْمٍ ࣖ ','Sebagai hidangan (bagimu) dari Tuhan Yang Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,33,'وَمَنْ اَحْسَنُ قَوْلًا مِّمَّنْ دَعَآ اِلَى اللّٰهِ وَعَمِلَ صَالِحًا وَّقَالَ اِنَّنِيْ مِنَ الْمُسْلِمِيْنَ ','Siapakah yang lebih baik perkataannya daripada orang yang menyeru kepada Allah, mengerjakan amal yang saleh, dan berkata: "Sesungguhnya aku termasuk orang-orang yang menyerah diri?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,34,'وَلَا تَسْتَوِى الْحَسَنَةُ وَلَا السَّيِّئَةُ ۗاِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ فَاِذَا الَّذِيْ بَيْنَكَ وَبَيْنَهٗ عَدَاوَةٌ كَاَنَّهٗ وَلِيٌّ حَمِيْمٌ  ','Dan tidaklah sama kebaikan dan kejahatan. Tolaklah (kejahatan itu) dengan cara yang lebih baik, maka tiba-tiba orang yang antaramu dan antara dia ada permusuhan seolah-olah telah menjadi teman yang sangat setia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,35,'وَمَا يُلَقّٰىهَآ اِلَّا الَّذِيْنَ صَبَرُوْاۚ وَمَا يُلَقّٰىهَآ اِلَّا ذُوْ حَظٍّ عَظِيْمٍ','Sifat-sifat yang baik itu tidak dianugerahkan melainkan kepada orang-orang yang sabar dan tidak dianugerahkan melainkan kepada orang-orang yang mempunyai keuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,36,'وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ','Dan jika syetan mengganggumu dengan suatu gangguan, maka mohonlah perlindungan kepada Allah. Sesungguhnya Dialah yang Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,37,'وَمِنْ اٰيٰتِهِ الَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُۗ  لَا تَسْجُدُوْا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوْا لِلّٰهِ الَّذِيْ خَلَقَهُنَّ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ  ','Dan di antara tanda-tanda kekuasaan-Nya ialah malam, siang, matahari dan bulan. Janganlah sembah matahari maupun bulan, tapi sembahlah Allah Yang menciptakannya, Jika Ialah yang kamu hendak sembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,38,'فَاِنِ اسْتَكْبَرُوْا فَالَّذِيْنَ عِنْدَ رَبِّكَ يُسَبِّحُوْنَ لَهٗ بِالَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْـَٔمُوْنَ ۩','Jika mereka menyombongkan diri, maka mereka (malaikat) yang di sisi Tuhanmu bertasbih kepada-Nya di malam dan siang hari, sedang mereka tidak jemu-jemu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,39,'وَمِنْ اٰيٰتِهٖٓ اَنَّكَ تَرَى الْاَرْضَ خَاشِعَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْۗ اِنَّ الَّذِيْٓ اَحْيَاهَا لَمُحْيِ الْمَوْتٰى ۗاِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan di antara tanda-tanda-Nya (Ialah) bahwa kau lihat bumi kering dan gersang, maka apabila Kami turunkan air di atasnya, niscaya ia bergerak dan subur. Sesungguhnya Tuhan Yang menghidupkannya, Pastilah dapat menghidupkan yang mati. Sesungguhnya Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,40,'اِنَّ الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اٰيٰتِنَا لَا يَخْفَوْنَ عَلَيْنَاۗ اَفَمَنْ يُّلْقٰى فِى النَّارِ خَيْرٌ اَمَّنْ يَّأْتِيْٓ اٰمِنًا يَّوْمَ الْقِيٰمَةِ ۗاِعْمَلُوْا مَا شِئْتُمْ ۙاِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Sesungguhnya orang-orang yang mengingkari ayat-ayat Kami, mereka tidak tersembunyi dari Kami. Maka apakah orang-orang yang dilemparkan ke dalam neraka lebih baik, ataukah orang-orang yang datang dengan aman sentosa pada hari Kiamat? Perbuatlah apa yang kamu kehendaki; Sesungguhnya Dia Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,41,'اِنَّ الَّذِيْنَ كَفَرُوْا بِالذِّكْرِ لَمَّا جَاۤءَهُمْ ۗوَاِنَّهٗ لَكِتٰبٌ عَزِيْزٌ ۙ','Sesungguhnya orang-orang yang mengingkari Al Quran ketika Al Quran itu datang kepada mereka, (mereka itu pasti akan celaka), dan sesungguhnya Al Quran itu adalah kitab yang mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,42,'لَّا يَأْتِيْهِ الْبَاطِلُ مِنْۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهٖ ۗتَنْزِيْلٌ مِّنْ حَكِيْمٍ حَمِيْدٍ ','Yang tidak datang kepadanya (Al Quran) kebatilan baik dari depan maupun dari belakangnya, yang diturunkan dari Rabb Yang Maha Bijaksana lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,43,'مَا يُقَالُ لَكَ اِلَّا مَا قَدْ قِيْلَ لِلرُّسُلِ مِنْ قَبْلِكَ ۗاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ وَّذُوْ عِقَابٍ اَلِيْمٍ ','Tidaklah ada yang dikatakan (oleh orang-orang kafir) kepadamu itu selain apa yang sesungguhnya telah dikatakan kepada rasul-rasul sebelum kamu. Sesungguhnya Rabb-mu benar-benar mempunyai ampunan dan hukuman yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,44,'وَلَوْ جَعَلْنٰهُ قُرْاٰنًا اَعْجَمِيًّا لَّقَالُوْا لَوْلَا فُصِّلَتْ اٰيٰتُهٗ ۗ ءَاَ۬عْجَمِيٌّ وَّعَرَبِيٌّ ۗ قُلْ هُوَ لِلَّذِيْنَ اٰمَنُوْا هُدًى وَّشِفَاۤءٌ ۗوَالَّذِيْنَ لَا يُؤْمِنُوْنَ فِيْٓ اٰذَانِهِمْ وَقْرٌ وَّهُوَ عَلَيْهِمْ عَمًىۗ اُولٰۤىِٕكَ يُنَادَوْنَ مِنْ مَّكَانٍۢ بَعِيْدٍ ࣖ','Dan jikalau Kami jadikan Al Quran itu suatu bacaan dalam bahasa selain Arab, tentulah mereka mengatakan: "Mengapa tidak dijelaskan ayat-ayatnya?" Apakah (patut Al Quran) dalam bahasa asing sedang (rasul adalah orang) Arab? Katakanlah: "Al Quran itu adalah petunjuk dan penawar bagi orang-orang mukmin. Dan orang-orang yang tidak beriman pada telinga mereka ada sumbatan, sedang Al Quran itu suatu kegelapan bagi mereka. Mereka itu adalah (seperti) yang dipanggil dari tempat yang jauh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,45,'وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ ','Dan sesungguhnya telah Kami berikan kepada Musa Taurat lalu diperselisihkan tentang Taurat itu. Kalau tidak ada keputusan yang telah terdahulu dari Rabb-mu, tentulah orang-orang kafir itu sudah dibinasakan. Dan Sesungguhnya mereka terhadap Al Quran benar-benar dalam keragu-raguan yang membingungkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,24,41,46,'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖ ۙوَمَنْ اَسَاۤءَ فَعَلَيْهَا ۗوَمَا رَبُّكَ بِظَلَّامٍ لِّلْعَبِيْدِ  ۔ ','Barangsiapa yang mengerjakan amal yang saleh maka (pahalanya) untuk dirinya sendiri dan barangsiapa mengerjakan perbuatan jahat, maka (dosanya) untuk dirinya sendiri; dan sekali-kali tidaklah Rabb-mu menganiaya hamba-hamba-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,47,'۞ اِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ ۗوَمَا تَخْرُجُ مِنْ ثَمَرٰتٍ ِمّنْ اَكْمَامِهَا وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖ ۗوَيَوْمَ يُنَادِيْهِمْ اَيْنَ شُرَكَاۤءِيْۙ قَالُوْٓا اٰذَنّٰكَ مَا مِنَّا مِنْ شَهِيْدٍ ۚ ','Kepada-Nya-lah dikembalikan pengetahuan tentang hari Kiamat. Dan tidak ada buah-buahan keluar dari kelopaknya dan tidak seorang perempuanpun mengandung dan tidak (pula) melahirkan, melainkan dengan sepengetahuan-Nya. Pada hari Tuhan memanggil mereka: "Dimanakah sekutu-sekutu-Ku itu?", mereka menjawab: "Kami nyatakan kepada Engkau bahwa tidak ada seorangpun di antara kami yang memberi kesaksian (bahwa Engkau punya sekutu)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,48,'وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَدْعُوْنَ مِنْ قَبْلُ وَظَنُّوْا مَا لَهُمْ مِّنْ مَّحِيْصٍ ','Dan hilang lenyaplah dari mereka apa yang selalu mereka sembah dahulu, dan mereka yakin bahwa tidak ada bagi mereka satu jalan keluarpun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,49,'لَا يَسْـَٔمُ الْاِنْسَانُ مِنْ دُعَاۤءِ الْخَيْرِۖ وَاِنْ مَّسَّهُ الشَّرُّ فَيَـُٔوْسٌ قَنُوْطٌ ','Manusia tidak jemu memohon kebaikan, dan jika mereka ditimpa malapetaka dia menjadi putus asa lagi putus harapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,50,'وَلَىِٕنْ اَذَقْنٰهُ رَحْمَةً مِّنَّا مِنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ هٰذَا لِيْۙ وَمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةًۙ وَّلَىِٕنْ رُّجِعْتُ اِلٰى رَبِّيْٓ اِنَّ لِيْ عِنْدَهٗ لَلْحُسْنٰىۚ فَلَنُنَبِّئَنَّ الَّذِيْنَ كَفَرُوْا بِمَا عَمِلُوْاۖ وَلَنُذِيْقَنَّهُمْ مِّنْ عَذَابٍ غَلِيْظٍ ','Dan jika Kami merasakan kepadanya sesuatu rahmat dari Kami sesudah dia ditimpa kesusahan, pastilah dia berkata: "Ini adalah hakku, dan aku tidak yakin bahwa hari Kiamat itu akan datang. Dan jika aku dikembalikan kepada Tuhanku maka sesungguhnya aku akan memperoleh kebaikan pada sisi-Nya". Maka Kami benar-benar akan memberitakan kepada orang-orang kafir apa yang telah mereka kerjakan dan akan Kami rasakan kepada mereka azab yang keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,51,'وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ فَذُوْ دُعَاۤءٍ عَرِيْضٍ ','Dan apabila Kami memberikan nikmat kepada manusia, ia berpaling dan menjauhkan diri; tetapi apabila ia ditimpa malapetaka, maka ia banyak berdoa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,52,'قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ ثُمَّ كَفَرْتُمْ بِهٖ مَنْ اَضَلُّ مِمَّنْ هُوَ فِيْ شِقَاقٍۢ بَعِيْدٍ ','Katakanlah: "Bagaimana pendapatmu jika (Al Quran) itu datang dari sisi Allah, kemudian kamu mengingkarinya. Siapakah yang lebih sesat daripada orang yang selalu berada dalam penyimpangan yang jauh?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,53,'سَنُرِيْهِمْ اٰيٰتِنَا فِى الْاٰفَاقِ وَفِيْٓ اَنْفُسِهِمْ حَتّٰى يَتَبَيَّنَ لَهُمْ اَنَّهُ الْحَقُّۗ اَوَلَمْ يَكْفِ بِرَبِّكَ اَنَّهٗ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ','Kami akan memperlihatkan kepada mereka tanda-tanda (kekuasaan) Kami di segala wilayah bumi dan pada diri mereka sendiri, hingga jelas bagi mereka bahwa Al Quran itu adalah benar. Tiadakah cukup bahwa sesungguhnya Tuhanmu menjadi saksi atas segala sesuatu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,41,54,'اَلَآ اِنَّهُمْ  فِيْ مِرْيَةٍ مِّنْ لِّقَاۤءِ رَبِّهِمْ ۗ اَلَآ اِنَّهٗ بِكُلِّ شَيْءٍ مُّحِيْطٌ ࣖ','Ingatlah bahwa sesungguhnya mereka adalah dalam keraguan tentang pertemuan dengan Tuhan mereka. Ingatlah bahwa sesungguhnya Dia Maha Meliputi segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,1,' حٰمۤ  ۚ ','Haa Miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,2,'عۤسۤقۤ  ۗ  ','´Ain Siin Qaaf.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,3,'كَذٰلِكَ يُوْحِيْٓ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۙ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ','Demikianlah Allah Yang Maha Perkasa lagi Maha Bijaksana, mewahyukan kepada kamu dan kepada orang-orang sebelum kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,4,'لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَهُوَ الْعَلِيُّ الْعَظِيْمُ ','Kepunyaan-Nya-lah apa yang ada di langit dan apa yang ada di bumi. Dan Dialah yang Maha Tinggi lagi Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,5,'تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ وَالْمَلٰۤىِٕكَةُ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُوْنَ لِمَنْ فِى الْاَرْضِۗ  اَلَآ اِنَّ اللّٰهَ هُوَ الْغَفُوْرُ الرَّحِيْمُ ','Hampir saja langit itu pecah dari sebelah atas (karena kebesaran Tuhan) dan malaikat-malaikat bertasbih serta memuji Tuhan-nya dan memohonkan ampun bagi orang-orang yang ada di bumi. Ingatlah, bahwa sesungguhnya Allah Dialah Yang Maha Pengampun lagi Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,6,'وَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَ اللّٰهُ حَفِيْظٌ عَلَيْهِمْۖ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ','Dan orang-orang yang mengambil pelindung-pelindung selain Allah, Allah mengawasi (perbuatan) mereka; dan kamu (ya Muhammad) bukanlah orang yang diserahi mengawasi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,7,'وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ قُرْاٰنًا عَرَبِيًّا لِّتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَا وَتُنْذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ فِيْهِ ۗفَرِيْقٌ فِى الْجَنَّةِ وَفَرِيْقٌ فِى السَّعِيْرِ ','Demikianlah Kami wahyukan kepadamu Al Quran dalam bahasa Arab, supaya kamu memberi peringatan kepada ummul Qura (penduduk Mekah) dan penduduk (negeri-negeri) sekelilingnya serta memberi peringatan (pula) tentang hari berkumpul (kiamat) yang tidak ada keraguan padanya. Segolongan masuk surga, dan segolongan masuk Jahannam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,8,'وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَهُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمُوْنَ مَا لَهُمْ مِّنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ','Dan kalau Allah menghendaki niscaya Allah menjadikan mereka satu umat (saja), tetapi Dia memasukkan orang-orang yang dikehendaki-Nya ke dalam rahmat-Nya. Dan orang-orang yang zalim tidak ada bagi mereka seorang pelindungpun dan tidak pula seorang penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,9,'اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۚ فَاللّٰهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِ الْمَوْتٰى ۖوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ࣖ ','Atau patutkah mereka mengambil pelindung-pelindung selain Allah? Maka Allah, Dialah pelindung (yang sebenarnya) dan Dia menghidupkan orang-orang yang mati, dan Dia adalah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,10,'وَمَا اخْتَلَفْتُمْ فِيْهِ مِنْ شَيْءٍ فَحُكْمُهٗٓ اِلَى اللّٰهِ ۗذٰلِكُمُ اللّٰهُ رَبِّيْ عَلَيْهِ تَوَكَّلْتُۖ وَاِلَيْهِ اُنِيْبُ  ','Tentang sesuatu apapun kamu berselisih, maka putusannya (terserah) kepada Allah. (Yang mempunyai sifat-sifat demikian) itulah Allah Tuhanku. Kepada-Nya lah aku bertawakkal dan kepada-Nya-lah aku kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,11,'فَاطِرُ السَّمٰوٰتِ وَالْاَرْضِۗ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّمِنَ الْاَنْعَامِ اَزْوَاجًاۚ يَذْرَؤُكُمْ فِيْهِۗ  لَيْسَ كَمِثْلِهٖ شَيْءٌ ۚوَهُوَ السَّمِيْعُ الْبَصِيْرُ ','(Dia) Pencipta langit dan bumi. Dia menjadikan bagi kamu dari jenis kamu sendiri pasangan-pasangan dan dari jenis binatang ternak pasangan-pasangan (pula), dijadikan-Nya kamu berkembang biak dengan jalan itu. Tidak ada sesuatupun yang serupa dengan Dia, dan Dialah yang Maha Mendengar dan Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,12,'لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۚ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۚاِنَّهٗ بِكُلِّ شَيْءٍ عَلِيْمٌ  ','Kepunyaan-Nya-lah perbendaharaan langit dan bumi; Dia melapangkan rezeki bagi siapa yang dikehendaki-Nya dan menyempitkan(nya). Sesungguhnya Dia Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,13,'۞ شَرَعَ لَكُمْ مِّنَ الدِّيْنِ مَا وَصّٰى بِهٖ نُوْحًا وَّالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَمَا وَصَّيْنَا بِهٖٓ اِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسٰٓى اَنْ اَقِيْمُوا الدِّيْنَ وَلَا تَتَفَرَّقُوْا فِيْهِۗ  كَبُرَ عَلَى الْمُشْرِكِيْنَ مَا تَدْعُوْهُمْ اِلَيْهِۗ  اَللّٰهُ يَجْتَبِيْٓ اِلَيْهِ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ يُّنِيْبُۗ','Dia telah mensyari´atkan bagi kamu tentang agama apa yang telah diwasiatkan-Nya kepada Nuh dan apa yang telah Kami wahyukan kepadamu dan apa yang telah Kami wasiatkan kepada Ibrahim, Musa dan Isa yaitu: Tegakkanlah agama dan janganlah kamu berpecah belah tentangnya. Amat berat bagi orang-orang musyrik agama yang kamu seru mereka kepadanya. Allah menarik kepada agama itu orang yang dikehendaki-Nya dan memberi petunjuk kepada (agama)-Nya orang yang kembali (kepada-Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,14,'وَمَا تَفَرَّقُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ اِلٰٓى اَجَلٍ مُّسَمًّى لَّقُضِيَ بَيْنَهُمْۗ وَاِنَّ الَّذِيْنَ اُوْرِثُوا الْكِتٰبَ مِنْۢ بَعْدِهِمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ  ','Dan mereka (ahli kitab) tidak berpecah belah, kecuali setelah datang pada mereka ilmu pengetahuan, karena kedengkian di antara mereka. Kalau tidaklah karena sesuatu ketetapan yang telah ada dari Tuhanmu dahulunya (untuk menangguhkan azab) sampai kepada waktu yang ditentukan, pastilah mereka telah dibinasakan. Dan sesungguhnya orang-orang yang diwariskan kepada mereka Al-Kitab (Taurat dan Injil) sesudah mereka, benar-benar berada dalam keraguan yang menggoncangkan tentang kitab itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,15,'فَلِذٰلِكَ فَادْعُ ۚوَاسْتَقِمْ كَمَآ اُمِرْتَۚ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْۚ وَقُلْ اٰمَنْتُ بِمَآ اَنْزَلَ اللّٰهُ مِنْ كِتٰبٍۚ وَاُمِرْتُ لِاَعْدِلَ بَيْنَكُمْ ۗ اَللّٰهُ رَبُّنَا وَرَبُّكُمْ ۗ لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۗ لَاحُجَّةَ بَيْنَنَا وَبَيْنَكُمْ ۗ اَللّٰهُ يَجْمَعُ بَيْنَنَا ۚوَاِلَيْهِ الْمَصِيْرُ ۗ ','Maka karena itu serulah (mereka kepada agama ini) dan tetaplah sebagai mana diperintahkan kepadamu dan janganlah mengikuti hawa nafsu mereka dan katakanlah: "Aku beriman kepada semua Kitab yang diturunkan Allah dan aku diperintahkan supaya berlaku adil diantara kamu. Allah-lah Tuhan kami dan Tuhan kamu. Bagi kami amal-amal kami dan bagi kamu amal-amal kamu. Tidak ada pertengkaran antara kami dan kamu, Allah mengumpulkan antara kita dan kepada-Nya-lah kembali (kita)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,16,'وَالَّذِيْنَ يُحَاۤجُّوْنَ فِى اللّٰهِ مِنْۢ بَعْدِ مَا اسْتُجِيْبَ لَهٗ حُجَّتُهُمْ دَاحِضَةٌ عِنْدَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَّلَهُمْ عَذَابٌ شَدِيْدٌ ','Dan orang-orang yang membantah (agama) Allah sesudah agama itu diterima maka bantahan mereka itu sia-sia saja, di sisi Tuhan mereka. Mereka mendapat kemurkaan (Allah) dan bagi mereka azab yang sangat keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,17,'اَللّٰهُ الَّذِيْٓ اَنْزَلَ الْكِتٰبَ بِالْحَقِّ وَالْمِيْزَانَ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ قَرِيْبٌ ','Allah-lah yang menurunkan kitab dengan (membawa) kebenaran dan (menurunkan) neraca (keadilan). Dan tahukah kamu, boleh jadi hari kiamat itu (sudah) dekat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,18,'يَسْتَعْجِلُ بِهَا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِهَاۚ وَالَّذِيْنَ اٰمَنُوْا مُشْفِقُوْنَ مِنْهَاۙ وَيَعْلَمُوْنَ اَنَّهَا الْحَقُّ ۗ اَلَآ اِنَّ الَّذِيْنَ يُمَارُوْنَ فِى السَّاعَةِ لَفِيْ ضَلٰلٍۢ بَعِيْدٍ ','Orang-orang yang tidak beriman kepada hari kiamat meminta supaya hari itu segera didatangkan dan orang-orang yang beriman merasa takut kepadanya dan mereka yakin bahwa kiamat itu adalah benar (akan terjadi). Ketahuilah bahwa sesungguhnya orang-orang yang membantah tentang terjadinya kiamat itu benar-benar dalam kesesatan yang jauh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,19,'اَللّٰهُ لَطِيْفٌۢ بِعِبَادِهٖ يَرْزُقُ مَنْ يَّشَاۤءُ ۚوَهُوَ الْقَوِيُّ الْعَزِيْزُ ࣖ ','Allah Maha lembut terhadap hamba-hamba-Nya; Dia memberi rezeki kepada yang di kehendaki-Nya dan Dialah Yang Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,20,'مَنْ كَانَ يُرِيْدُ حَرْثَ الْاٰخِرَةِ نَزِدْ لَهٗ فِيْ حَرْثِهٖۚ وَمَنْ كَانَ يُرِيْدُ حَرْثَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۙ وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ نَّصِيْبٍ  ','Barang siapa yang menghendaki keuntungan di akhirat akan Kami tambah keuntungan itu baginya dan barang siapa yang menghendaki keuntungan di dunia Kami berikan kepadanya sebagian dari keuntungan dunia dan tidak ada baginya suatu bahagianpun di akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,21,'اَمْ لَهُمْ شُرَكٰۤؤُا شَرَعُوْا لَهُمْ مِّنَ الدِّيْنِ مَا لَمْ يَأْذَنْۢ بِهِ اللّٰهُ ۗوَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ  ','Apakah mereka mempunyai sembahan-sembahan selain Allah yang mensyariatkan untuk mereka agama yang tidak diizinkan Allah? Sekiranya tak ada ketetapan yang menentukan (dari Allah) tentulah mereka telah dibinasakan. Dan sesungguhnya orang-orang yang zalim itu akan memperoleh azab yang amat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,22,'تَرَى الظّٰلِمِيْنَ مُشْفِقِيْنَ مِمَّا كَسَبُوْا وَهُوَ وَاقِعٌۢ بِهِمْ ۗوَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ رَوْضٰتِ الْجَنّٰتِۚ  لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُ ','Kamu lihat orang-orang yang zalim sangat ketakutan karena kejahatan-kejahatan yang telah mereka kerjakan, sedang siksaan menimpa mereka. Dan orang-orang yang beriman serta mengerjakan amal saleh (berada) di dalam taman-taman surga, mereka memperoleh apa yang mereka kehendaki di sisi Tuhan mereka. Yang demikian itu adalah karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,23,'ذٰلِكَ الَّذِيْ يُبَشِّرُ اللّٰهُ عِبَادَهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا اِلَّا الْمَوَدَّةَ فِى الْقُرْبٰىۗ وَمَنْ يَّقْتَرِفْ حَسَنَةً نَّزِدْ لَهٗ فِيْهَا حُسْنًا ۗاِنَّ اللّٰهَ غَفُوْرٌ شَكُوْرٌ  ','Itulah (karunia) yang (dengan itu) Allah menggembirakan hamba-hamba-Nya yang beriman dan mengerjakan amal yang saleh. Katakanlah: "Aku tidak meminta kepadamu sesuatu upahpun atas seruanku kecuali kasih sayang dalam kekeluargaan". Dan siapa yang mengerjakan kebaikan akan Kami tambahkan baginya kebaikan pada kebaikannya itu. Sesungguhnya Allah Maha Pengampun lagi Maha Mensyukuri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,24,'اَمْ يَقُوْلُوْنَ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۚ فَاِنْ يَّشَاِ اللّٰهُ يَخْتِمْ عَلٰى قَلْبِكَ ۗوَيَمْحُ اللّٰهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمٰتِهٖ ۗاِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ  ','Bahkan mereka mengatakan: "Dia (Muhammad) telah mengada-adakan dusta terhadap Allah". Maka jika Allah menghendaki niscaya Dia mengunci mati hatimu; dan Allah menghapuskan yang batil dan membenarkan yang hak dengan kalimat-kalimat-Nya (Al Quran). Sesungguhnya Dia Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,25,'وَهُوَ الَّذِيْ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَعْفُوْا عَنِ السَّيِّاٰتِ وَيَعْلَمُ مَا تَفْعَلُوْنَۙ  ','Dan Dialah yang menerima taubat dari hamba-hamba-Nya dan memaafkan kesalahan-kesalahan dan mengetahui apa yang kamu kerjakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,26,'وَيَسْتَجِيْبُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖ ۗوَالْكٰفِرُوْنَ لَهُمْ عَذَابٌ شَدِيْدٌ ','dan Dia memperkenankan (doa) orang-orang yang beriman serta mengerjakan amal yang saleh dan menambah (pahala) kepada mereka dari karunia-Nya. Dan orang-orang yang kafir bagi mereka azab yang sangat keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,27,'۞ وَلَوْ بَسَطَ اللّٰهُ الرِّزْقَ لِعِبَادِهٖ لَبَغَوْا فِى الْاَرْضِ وَلٰكِنْ يُنَزِّلُ بِقَدَرٍ مَّا يَشَاۤءُ ۗاِنَّهٗ بِعِبَادِهٖ خَبِيْرٌۢ بَصِيْرٌ ','Dan jikalau Allah melapangkan rezeki kepada hamba-hamba-Nya tentulah mereka akan melampaui batas di muka bumi, tetapi Allah menurunkan apa yang dikehendaki-Nya dengan ukuran. Sesungguhnya Dia Maha Mengetahui (keadaan) hamba-hamba-Nya lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,28,'وَهُوَ الَّذِيْ يُنَزِّلُ الْغَيْثَ مِنْۢ بَعْدِ مَا قَنَطُوْا وَيَنْشُرُ رَحْمَتَهٗ ۗوَهُوَ الْوَلِيُّ الْحَمِيْدُ ','Dan Dialah Yang menurunkan hujan sesudah mereka berputus asa dan menyebarkan rahmat-Nya. Dan Dialah Yang Maha Pelindung lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,29,'وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَثَّ فِيْهِمَا مِنْ دَاۤبَّةٍ ۗوَهُوَ عَلٰى جَمْعِهِمْ اِذَا يَشَاۤءُ قَدِيْرٌ ࣖ','Di antara (ayat-ayat) tanda-tanda-Nya ialah menciptakan langit dan bumi dan makhluk-makhluk yang melata Yang Dia sebarkan pada keduanya. Dan Dia Maha Kuasa mengumpulkan semuanya apabila dikehendaki-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,30,'وَمَآ اَصَابَكُمْ مِّنْ مُّصِيْبَةٍ فَبِمَا كَسَبَتْ اَيْدِيْكُمْ وَيَعْفُوْا عَنْ كَثِيْرٍۗ ','Dan apa saja musibah yang menimpa kamu maka adalah disebabkan oleh perbuatan tanganmu sendiri, dan Allah memaafkan sebagian besar (dari kesalahan-kesalahanmu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,31,'وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ','Dan kamu tidak dapat melepaskan diri (dari azab Allah) di muka bumi, dan kamu tidak memperoleh seorang pelindung dan tidak pula penolong selain Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,32,'وَمِنْ اٰيٰتِهِ الْجَوَارِ فِى الْبَحْرِ كَالْاَعْلَامِ ۗ ','Dan di antara tanda-tanda kekuasaan-Nya ialah kapal-kapal di tengah (yang berlayar) di laut seperti gunung-gunung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,33,'اِنْ يَّشَأْ يُسْكِنِ الرِّيْحَ فَيَظْلَلْنَ رَوَاكِدَ عَلٰى ظَهْرِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍۙ','Jika Dia menghendaki, Dia akan menenangkan angin, maka jadilah kapal-kapal itu terhenti di permukaan laut. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaannya) bagi setiap orang yang banyak bersabar dan banyak bersyukur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,34,'اَوْ يُوْبِقْهُنَّ بِمَا كَسَبُوْا وَيَعْفُ عَنْ كَثِيْرٍۙ ','atau kapal-kapal itu dibinasakan-Nya karena perbuatan mereka atau Dia memberi maaf sebagian besar (dari mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,35,'وَّيَعْلَمَ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِنَاۗ مَا لَهُمْ مِّنْ مَّحِيْصٍ ','Dan supaya orang-orang yang membantah ayat-ayat (kekuasaan) Kami mengetahui bahwa mereka sekali-kali tidak akan memperoleh jalan ke luar (dari siksaan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,36,'فَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰى لِلَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۚ ','Maka sesuatu yang diberikan kepadamu, itu adalah kenikmatan hidup di dunia; dan yang ada pada sisi Allah lebih baik dan lebih kekal bagi orang-orang yang beriman, dan hanya kepada Tuhan mereka, mereka bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,37,'وَالَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ وَاِذَا مَا غَضِبُوْا هُمْ يَغْفِرُوْنَ ۚ ','Dan (bagi) orang-orang yang menjauhi dosa-dosa besar dan perbuatan-perbuatan keji, dan apabila mereka marah mereka memberi maaf.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,38,'وَالَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمْ وَاَقَامُوا الصَّلٰوةَۖ وَاَمْرُهُمْ شُوْرٰى بَيْنَهُمْۖ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۚ ','Dan (bagi) orang-orang yang menerima (mematuhi) seruan Tuhannya dan mendirikan shalat, sedang urusan mereka (diputuskan) dengan musyawarat antara mereka; dan mereka menafkahkan sebagian dari rezeki yang Kami berikan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,39,'وَالَّذِيْنَ اِذَآ اَصَابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُوْنَ ','Dan ( bagi) orang-orang yang apabila mereka diperlakukan dengan zalim mereka membela diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,40,'وَجَزٰۤؤُا سَيِّئَةٍ سَيِّئَةٌ مِّثْلُهَا ۚفَمَنْ عَفَا وَاَصْلَحَ فَاَجْرُهٗ عَلَى اللّٰهِ ۗاِنَّهٗ لَا يُحِبُّ الظّٰلِمِيْنَ','Dan balasan suatu kejahatan adalah kejahatan yang serupa, maka barang siapa memaafkan dan berbuat baik maka pahalanya atas (tanggungan) Allah. Sesungguhnya Dia tidak menyukai orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,41,'وَلَمَنِ انْتَصَرَ بَعْدَ ظُلْمِهٖ فَاُولٰۤىِٕكَ مَا عَلَيْهِمْ مِّنْ سَبِيْلٍۗ ','Dan sesungguhnya orang-orang yang membela diri sesudah teraniaya, tidak ada satu dosapun terhadap mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,42,'اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَظْلِمُوْنَ النَّاسَ وَيَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ  ','Sesungguhnya dosa itu atas orang-orang yang berbuat zalim kepada manusia dan melampaui batas di muka bumi tanpa hak. Mereka itu mendapat azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,43,'وَلَمَنْ صَبَرَ وَغَفَرَ اِنَّ ذٰلِكَ لَمِنْ عَزْمِ الْاُمُوْرِ ࣖ ','Tetapi orang yang bersabar dan memaafkan, sesungguhnya (perbuatan) yang demikian itu termasuk hal-hal yang diutamakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,44,'وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ وَّلِيٍّ مِّنْۢ بَعْدِهٖ ۗوَتَرَى الظّٰلِمِيْنَ لَمَّا رَاَوُا الْعَذَابَ يَقُوْلُوْنَ هَلْ اِلٰى مَرَدٍّ مِّنْ سَبِيْلٍۚ ','Dan siapa yang disesatkan Allah maka tidak ada baginya seorang pemimpinpun sesudah itu. Dan kamu akan melihat orang-orang yang zalim ketika mereka melihat azab berkata: "Adakah kiranya jalan untuk kembali (ke dunia)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,45,'وَتَرٰىهُمْ يُعْرَضُوْنَ عَلَيْهَا خٰشِعِيْنَ مِنَ الذُّلِّ يَنْظُرُوْنَ مِنْ طَرْفٍ خَفِيٍّۗ وَقَالَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ الظّٰلِمِيْنَ فِيْ عَذَابٍ مُّقِيْمٍ ','Dan kamu akan melihat mereka dihadapkan ke neraka dalam keadaan tunduk karena (merasa) hina, mereka melihat dengan pandangan yang lesu. Dan orang-orang yang beriman berkata: "Sesungguhnya orang-orang yang merugi ialah orang-orang yang kehilangan diri mereka sendiri dan (kehilangan) keluarga mereka pada hari kiamat. Ingatlah, sesungguhnya orang-orang yang zalim itu berada dalam azab yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,46,'وَمَا كَانَ لَهُمْ مِّنْ اَوْلِيَاۤءَ يَنْصُرُوْنَهُمْ مِّنْ دُوْنِ اللّٰهِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ سَبِيْلٍ ۗ ','Dan mereka sekali-kali tidak mempunyai pelindung-pelindung yang dapat menolong mereka selain Allah. Dan siapa yang disesatkan Allah maka tidaklah ada baginya satu jalanpun (untuk mendapat petunjuk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,47,'اِسْتَجِيْبُوْا لِرَبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ ۗمَا لَكُمْ مِّنْ مَّلْجَاٍ يَّوْمَىِٕذٍ وَّمَا لَكُمْ مِّنْ نَّكِيْرٍ ','Patuhilah seruan Tuhanmu sebelum datang dari Allah suatu hari yang tidak dapat ditolak kedatangannya. Kamu tidak memperoleh tempat berlindung pada hari itu dan tidak (pula) dapat mengingkari (dosa-dosamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,48,'فَاِنْ اَعْرَضُوْا فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗاِنْ عَلَيْكَ اِلَّا الْبَلٰغُ ۗوَاِنَّآ اِذَآ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَا ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَاِنَّ الْاِنْسَانَ كَفُوْرٌ ','Jika mereka berpaling maka Kami tidak mengutus kamu sebagai pengawas bagi mereka. Kewajibanmu tidak lain hanyalah menyampaikan (risalah). Sesungguhnya apabila Kami merasakan kepada manusia sesuatu rahmat dari Kami dia bergembira ria karena rahmat itu. Dan jika mereka ditimpa kesusahan disebabkan perbuatan tangan mereka sendiri (niscaya mereka ingkar) karena sesungguhnya manusia itu amat ingkar (kepada nikmat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,49,'لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَخْلُقُ مَا يَشَاۤءُ ۗيَهَبُ لِمَنْ يَّشَاۤءُ اِنَاثًا وَّيَهَبُ لِمَنْ يَّشَاۤءُ الذُّكُوْرَ ۙ','Kepunyaan Allah-lah kerajaan langit dan bumi, Dia menciptakan apa yang Dia kehendaki. Dia memberikan anak-anak perempuan kepada siapa yang Dia kehendaki dan memberikan anak-anak lelaki kepada siapa yang Dia kehendaki,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,50,'اَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَّاِنَاثًا ۚوَيَجْعَلُ مَنْ يَّشَاۤءُ عَقِيْمًا ۗاِنَّهٗ عَلِيْمٌ قَدِيْرٌ ','atau Dia menganugerahkan kedua jenis laki-laki dan perempuan (kepada siapa) yang dikehendaki-Nya, dan Dia menjadikan mandul siapa yang Dia kehendaki. Sesungguhnya Dia Maha Mengetahui lagi Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,51,'۞ وَمَا كَانَ لِبَشَرٍ اَنْ يُّكَلِّمَهُ اللّٰهُ اِلَّا وَحْيًا اَوْ مِنْ وَّرَاۤئِ حِجَابٍ اَوْ يُرْسِلَ رَسُوْلًا فَيُوْحِيَ بِاِذْنِهٖ مَا يَشَاۤءُ ۗاِنَّهٗ عَلِيٌّ حَكِيْمٌ ','Dan tidak mungkin bagi seorang manusiapun bahwa Allah berkata-kata dengan dia kecuali dengan perantaraan wahyu atau dibelakang tabir atau dengan mengutus seorang utusan (malaikat) lalu diwahyukan kepadanya dengan seizin-Nya apa yang Dia kehendaki. Sesungguhnya Dia Maha Tinggi lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,52,'وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ رُوْحًا مِّنْ اَمْرِنَا ۗمَا كُنْتَ تَدْرِيْ مَا الْكِتٰبُ وَلَا الْاِيْمَانُ وَلٰكِنْ جَعَلْنٰهُ نُوْرًا نَّهْدِيْ بِهٖ مَنْ نَّشَاۤءُ مِنْ عِبَادِنَا ۗوَاِنَّكَ لَتَهْدِيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍۙ ','Dan demikianlah Kami wahyukan kepadamu wahyu (Al Quran) dengan perintah Kami. Sebelumnya kamu tidaklah mengetahui apakah Al Kitab (Al Quran) dan tidak pula mengetahui apakah iman itu, tetapi Kami menjadikan Al Quran itu cahaya, yang Kami tunjuki dengan dia siapa yang kami kehendaki di antara hamba-hamba Kami. Dan sesungguhnya kamu benar-benar memberi petunjuk kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,42,53,'صِرَاطِ اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ  اَلَآ اِلَى اللّٰهِ تَصِيْرُ الْاُمُوْرُ ࣖ ','(Yaitu) jalan Allah yang kepunyaan-Nya segala apa yang ada di langit dan apa yang ada di bumi. Ingatlah, bahwa kepada Allah-lah kembali semua urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,1,' حٰمۤ  ۚ ','Haa Miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,2,'وَالْكِتٰبِ الْمُبِيْنِ ۙ ','Demi Kitab (Al Quran) yang menerangkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,3,'اِنَّا جَعَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَۚ ','Sesungguhnya Kami menjadikan Al Quran dalam bahasa Arab supaya kamu memahami(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,4,'وَاِنَّهٗ فِيْٓ اُمِّ الْكِتٰبِ لَدَيْنَا لَعَلِيٌّ حَكِيْمٌ ۗ ','Dan sesungguhnya Al Quran itu dalam induk Al Kitab (Lauh Mahfuzh) di sisi Kami, adalah benar-benar tinggi (nilainya) dan amat banyak mengandung hikmah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,5,'اَفَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحًا اَنْ كُنْتُمْ قَوْمًا مُّسْرِفِيْنَ','Maka apakah Kami akan berhenti menurunkan Al Quran kepadamu, karena kamu adalah kaum yang melampaui batas?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,6,'وَكَمْ اَرْسَلْنَا مِنْ نَّبِيٍّ فِى الْاَوَّلِيْنَ ','Berapa banyaknya nabi-nabi yang telah Kami utus kepada umat-umat yang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,7,'وَمَا يَأْتِيْهِمْ مِّنْ نَّبِيٍّ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ','Dan tiada seorang nabipun datang kepada mereka melainkan mereka selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,8,'فَاَهْلَكْنَٓا اَشَدَّ مِنْهُمْ بَطْشًا وَّمَضٰى مَثَلُ الْاَوَّلِيْنَ ','Maka telah Kami binasakan orang-orang yang lebih besar kekuatannya dari mereka itu (musyrikin Mekah) dan telah terdahulu (tersebut dalam Al Quran) perumpamaan umat-umat masa dahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,9,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ خَلَقَهُنَّ الْعَزِيْزُ الْعَلِيْمُۙ','Dan sungguh jika kamu tanyakan kepada mereka: "Siapakah yang menciptakan langit dan bumi?", niscaya mereka akan menjawab: "Semuanya diciptakan oleh Yang Maha Perkasa lagi Maha Mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,10,'الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّجَعَلَ لَكُمْ فِيْهَا سُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَ ۚ ','Yang menjadikan bumi untuk kamu sebagai tempat menetap dan Dia membuat jalan-jalan di atas bumi untuk kamu supaya kamu mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,11,'وَالَّذِيْ نَزَّلَ مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍۚ فَاَنْشَرْنَا بِهٖ بَلْدَةً مَّيْتًا ۚ كَذٰلِكَ تُخْرَجُوْنَ ','Dan Yang menurunkan air dari langit menurut kadar (yang diperlukan) lalu Kami hidupkan dengan air itu negeri yang mati, seperti itulah kamu akan dikeluarkan (dari dalam kubur).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,12,'وَالَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُمْ مِّنَ الْفُلْكِ وَالْاَنْعَامِ مَا تَرْكَبُوْنَۙ ','Dan Yang menciptakan semua yang berpasang-pasangan dan menjadikan untukmu kapal dan binatang ternak yang kamu tunggangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,13,'لِتَسْتَوٗا عَلٰى ظُهُوْرِهٖ ثُمَّ تَذْكُرُوْا نِعْمَةَ رَبِّكُمْ اِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُوْلُوْا سُبْحٰنَ الَّذِيْ سَخَّرَ لَنَا هٰذَا وَمَا كُنَّا لَهٗ مُقْرِنِيْنَۙ ','Supaya kamu duduk di atas punggungnya kemudian kamu ingat nikmat Tuhanmu apabila kamu telah duduk di atasnya; dan supaya kamu mengucapkan: "Maha Suci Tuhan yang telah menundukkan semua ini bagi kami padahal kami sebelumnya tidak mampu menguasainya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,14,'وَاِنَّآ اِلٰى رَبِّنَا لَمُنْقَلِبُوْنَ ','dan sesungguhnya kami akan kembali kepada Tuhan kami".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,15,'وَجَعَلُوْا لَهٗ مِنْ عِبَادِهٖ جُزْءًا ۗاِنَّ الْاِنْسَانَ لَكَفُوْرٌ مُّبِيْنٌ ۗ ࣖ ','Dan mereka menjadikan sebahagian dari hamba-hamba-Nya sebagai bahagian daripada-Nya. Sesungguhnya manusia itu benar-benar pengingkar yang nyata (terhadap rahmat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,16,'اَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنٰتٍ وَّاَصْفٰىكُمْ بِالْبَنِيْنَ ۗ ','Patutkah Dia mengambil anak perempuan dari yang diciptakan-Nya dan Dia mengkhususkan buat kamu anak laki-laki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,17,'وَاِذَا بُشِّرَ اَحَدُهُمْ بِمَا ضَرَبَ لِلرَّحْمٰنِ مَثَلًا ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌ ','Padahal apabila salah seorang di antara mereka diberi kabar gembira dengan apa yang dijadikan sebagai misal bagi Allah Yang Maha Pemurah; jadilah mukanya hitam pekat sedang dia amat menahan sedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,18,'اَوَمَنْ يُّنَشَّؤُا فِى الْحِلْيَةِ وَهُوَ فِى الْخِصَامِ غَيْرُ مُبِيْنٍ ','Dan apakah patut (menjadi anak Allah) orang yang dibesarkan dalam keadaan berperhiasan sedang dia tidak dapat memberi alasan yang terang dalam pertengkaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,19,'وَجَعَلُوا الْمَلٰۤىِٕكَةَ الَّذِيْنَ هُمْ عِبٰدُ الرَّحْمٰنِ اِنَاثًا ۗ اَشَهِدُوْا خَلْقَهُمْ ۗسَتُكْتَبُ شَهَادَتُهُمْ وَيُسْٔـَلُوْنَ ','Dan mereka menjadikan malaikat-malaikat yang mereka itu adalah hamba-hamba Allah Yang Maha Pemurah sebagai orang-orang perempuan. Apakah mereka menyaksikan penciptaan malaika-malaikat itu? Kelak akan dituliskan persaksian mereka dan mereka akan dimintai pertanggung-jawaban.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,20,'وَقَالُوْا لَوْ شَاۤءَ الرَّحْمٰنُ مَا عَبَدْنٰهُمْ ۗمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍ اِنْ هُمْ اِلَّا يَخْرُصُوْنَۗ ','Dan mereka berkata: "Jikalau Allah Yang Maha Pemurah menghendaki tentulah kami tidak menyembah mereka (malaikat)". Mereka tidak mempunyai pengetahuan sedikitpun tentang itu, mereka tidak lain hanyalah menduga-duga belaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,21,'اَمْ اٰتَيْنٰهُمْ كِتٰبًا مِّنْ قَبْلِهٖ فَهُمْ بِهٖ مُسْتَمْسِكُوْنَ ','Atau adakah Kami memberikan sebuah kitab kepada mereka sebelum Al Quran, lalu mereka berpegang dengan kitab itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,22,'بَلْ قَالُوْٓا اِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّهْتَدُوْنَ ','Bahkan mereka berkata: "Sesungguhnya kami mendapati bapak-bapak kami menganut suatu agama, dan sesungguhnya kami orang-orang yang mendapat petunjuk dengan (mengikuti) jejak mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,23,'وَكَذٰلِكَ مَآ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍۙ اِلَّا قَالَ مُتْرَفُوْهَآ  ۙاِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّقْتَدُوْنَ','Dan demikianlah, Kami tidak mengutus sebelum kamu seorang pemberi peringatanpun dalam suatu negeri, melainkan orang-orang yang hidup mewah di negeri itu berkata: "Sesungguhnya kami mendapati bapak-bapak kami menganut suatu agama dan sesungguhnya kami adalah pengikut jejak-jejak mereka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,24,'۞ قٰلَ اَوَلَوْ جِئْتُكُمْ بِاَهْدٰى مِمَّا وَجَدْتُّمْ عَلَيْهِ اٰبَاۤءَكُمْۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ','(Rasul itu) berkata: "Apakah (kamu akan mengikutinya juga) sekalipun aku membawa untukmu (agama) yang lebih (nyata) memberi petunjuk daripada apa yang kamu dapati bapak-bapakmu menganutnya?" Mereka menjawab: "Sesungguhnya kami mengingkari agama yang kamu diutus untuk menyampaikannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,25,'فَانْتَقَمْنَا مِنْهُمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ࣖ','Maka Kami binasakan mereka maka perhatikanlah bagaimana kesudahan orang-orang yang mendustakan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,26,'وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ وَقَوْمِهٖٓ اِنَّنِيْ بَرَاۤءٌ مِّمَّا تَعْبُدُوْنَۙ','Dan ingatlah ketika Ibrahim berkata kepada bapaknya dan kaumnya: "Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu sembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,27,'اِلَّا الَّذِيْ فَطَرَنِيْ فَاِنَّهٗ سَيَهْدِيْنِ ','tetapi (aku menyembah) Tuhan Yang menjadikanku; karena sesungguhnya Dia akan memberi hidayah kepadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,28,'وَجَعَلَهَا كَلِمَةً ۢ بَاقِيَةً فِيْ عَقِبِهٖ لَعَلَّهُمْ يَرْجِعُوْنَۗ ','Dan (lbrahim a. s.) menjadikan kalimat tauhid itu kalimat yang kekal pada keturunannya supaya mereka kembali kepada kalimat tauhid itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,29,'بَلْ مَتَّعْتُ هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى جَاۤءَهُمُ الْحَقُّ وَرَسُوْلٌ مُّبِيْنٌ ','Tetapi Aku telah memberikan kenikmatan hidup kepada mereka dan bapak-bapak mereka sehingga datanglah kepada mereka kebenaran (Al Quran) dan seorang rasul yang memberi penjelasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,30,'وَلَمَّا جَاۤءَهُمُ الْحَقُّ قَالُوْا هٰذَا سِحْرٌ وَّاِنَّا بِهٖ كٰفِرُوْنَ ','Dan tatkala kebenaran (Al Quran) itu datang kepada mereka, mereka berkata: "Ini adalah sihir dan sesungguhnya kami adalah orang-orang yang mengingkarinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,31,'وَقَالُوْا لَوْلَا نُزِّلَ هٰذَا الْقُرْاٰنُ عَلٰى رَجُلٍ مِّنَ الْقَرْيَتَيْنِ عَظِيْمٍ','Dan mereka berkata: "Mengapa Al Quran ini tidak diturunkan kepada seorang besar dari salah satu dua negeri (Mekah dan Thaif) ini?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,32,'اَهُمْ يَقْسِمُوْنَ رَحْمَتَ رَبِّكَۗ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَّعِيْشَتَهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَتَّخِذَ بَعْضُهُمْ بَعْضًا سُخْرِيًّا ۗوَرَحْمَتُ رَبِّكَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ ','Apakah mereka yang membagi-bagi rahmat Tuhanmu? Kami telah menentukan antara mereka penghidupan mereka dalam kehidupan dunia, dan kami telah meninggikan sebahagian mereka atas sebagian yang lain beberapa derajat, agar sebagian mereka dapat mempergunakan sebagian yang lain. Dan rahmat Tuhanmu lebih baik dari apa yang mereka kumpulkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,33,'وَلَوْلَآ اَنْ يَّكُوْنَ النَّاسُ اُمَّةً وَّاحِدَةً لَّجَعَلْنَا لِمَنْ يَّكْفُرُ بِالرَّحْمٰنِ لِبُيُوْتِهِمْ سُقُفًا مِّنْ فِضَّةٍ وَّمَعَارِجَ عَلَيْهَا يَظْهَرُوْنَۙ ','Dan sekiranya bukan karena hendak menghindari manusia menjadi umat yang satu (dalam kekafiran), tentulah kami buatkan bagi orang-orang yang kafir kepada Tuhan Yang Maha Pemurah loteng-loteng perak bagi rumah mereka dan (juga) tangga-tangga (perak) yang mereka menaikinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,34,'وَلِبُيُوْتِهِمْ اَبْوَابًا وَّسُرُرًا عَلَيْهَا يَتَّكِـُٔوْنَۙ ','Dan (Kami buatkan pula) pintu-pintu (perak) bagi rumah-rumah mereka dan (begitu pula) dipan-dipan yang mereka bertelekan atasnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,35,'وَزُخْرُفًاۗ وَاِنْ كُلُّ ذٰلِكَ لَمَّا مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَالْاٰخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِيْنَ ࣖ ','Dan (Kami buatkan pula) perhiasan-perhiasan (dari emas untuk mereka). Dan semuanya itu tidak lain hanyalah kesenangan kehidupan dunia, dan kehidupan akhirat itu di sisi Tuhanmu adalah bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,36,'وَمَنْ يَّعْشُ عَنْ ذِكْرِ الرَّحْمٰنِ نُقَيِّضْ لَهٗ شَيْطٰنًا فَهُوَ لَهٗ قَرِيْنٌ ','Barangsiapa yang berpaling dari pengajaran Tuhan Yang Maha Pemurah (Al Quran), kami adakan baginya syaitan (yang menyesatkan) maka syaitan itulah yang menjadi teman yang selalu menyertainya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,37,'وَاِنَّهُمْ لَيَصُدُّوْنَهُمْ عَنِ السَّبِيْلِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ ','Dan sesungguhnya syaitan-syaitan itu benar-benar menghalangi mereka dari jalan yang benar dan mereka menyangka bahwa mereka mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,38,'حَتّٰىٓ اِذَا جَاۤءَنَا قَالَ يٰلَيْتَ بَيْنِيْ وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِيْنُ','Sehingga apabila orang-orang yang berpaling itu datang kepada kami (di hari kiamat) dia berkata: "Aduhai, semoga (jarak) antaraku dan kamu seperti jarak antara masyrik dan maghrib, maka syaitan itu adalah sejahat-jahat teman (yang menyertai manusia)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,39,'وَلَنْ يَّنْفَعَكُمُ الْيَوْمَ اِذْ ظَّلَمْتُمْ اَنَّكُمْ فِى الْعَذَابِ مُشْتَرِكُوْنَ ','(Harapanmu itu) sekali-kali tidak akan memberi manfaat kepadamu di hari itu karena kamu telah menganiaya (dirimu sendiri). Sesungguhnya kamu bersekutu dalam azab itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,40,'اَفَاَنْتَ تُسْمِعُ الصُّمَّ اَوْ تَهْدِى الْعُمْيَ وَمَنْ كَانَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Maka apakah kamu dapat menjadikan orang yang pekak bisa mendengar atau (dapatkah) kamu memberi petunjuk kepada orang yang buta (hatinya) dan kepada orang yang tetap dalam kesesatan yang nyata?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,41,'فَاِمَّا نَذْهَبَنَّ بِكَ فَاِنَّا مِنْهُمْ مُّنْتَقِمُوْنَۙ ','Sungguh, jika Kami mewafatkan kamu (sebelum kamu mencapai kemenangan) maka sesungguhnya Kami akan menyiksa mereka (di akhirat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,42,'اَوْ نُرِيَنَّكَ الَّذِيْ وَعَدْنٰهُمْ فَاِنَّا عَلَيْهِمْ مُّقْتَدِرُوْنَ ','Atau Kami memperlihatkan kepadamu (azab) yang telah Kami ancamkan kepada mereka. Maka sesungguhnya Kami berkuasa atas mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,43,'فَاسْتَمْسِكْ بِالَّذِيْٓ اُوْحِيَ اِلَيْكَ ۚاِنَّكَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Maka berpegang teguhlah kamu kepada agama yang telah diwahyukan kepadamu. Sesungguhnya kamu berada di atas jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,44,'وَاِنَّهٗ لَذِكْرٌ لَّكَ وَلِقَوْمِكَ ۚوَسَوْفَ تُسْٔـَلُوْنَ ','Dan sesungguhnya Al Quran itu benar-benar adalah suatu kemuliaan besar bagimu dan bagi kaummu dan kelak kamu akan diminta pertanggungan jawab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,45,'وَسْٔـَلْ مَنْ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُّسُلِنَآ  ۖ اَجَعَلْنَا مِنْ دُوْنِ الرَّحْمٰنِ اٰلِهَةً يُّعْبَدُوْنَ ࣖ','Dan tanyakanlah kepada rasul-rasul Kami yang telah Kami utus sebelum kamu: "Adakah Kami menentukan tuhan-tuhan untuk disembah selain Allah Yang Maha Pemurah?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,46,'وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَقَالَ اِنِّيْ رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ','Dan sesunguhnya Kami telah mengutus Musa dengan membawa mukjizat-mukjizat Kami kepada Fir´aun dan pemuka-pemuka kaumnya. Maka Musa berkata: "Sesungguhnya aku adalah utusan dari Tuhan seru sekalian alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,47,'فَلَمَّا جَاۤءَهُمْ بِاٰيٰتِنَآ اِذَا هُمْ مِّنْهَا يَضْحَكُوْنَ ','Maka tatkala dia datang kepada mereka dengan membawa mukjizat-mukjizat Kami dengan serta merta mereka mentertawakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,48,'وَمَا نُرِيْهِمْ مِّنْ اٰيَةٍ اِلَّا هِيَ اَكْبَرُ مِنْ اُخْتِهَاۗ وَاَخَذْنٰهُمْ بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُوْنَ ','Dan tidaklah Kami perlihatkan kepada mereka sesuatu mukjizat kecuali mukjizat itu lebih besar dari mukjizat-mukjizat yang sebelumnya. Dan Kami timpakan kepada mereka azab supaya mereka kembali (ke jalan yang benar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,49,'وَقَالُوْا يٰٓاَيُّهَ السّٰحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ اِنَّنَا لَمُهْتَدُوْنَ ','Dan mereka berkata: "Hai ahli sihir, berdoalah kepada Tuhanmu untuk (melepaskan) kami sesuai dengan apa yang telah dijanjikan-Nya kepadamu; sesungguhnya kami (jika doamu dikabulkan) benar-benar akan menjadi orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,50,'فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ اِذَا هُمْ يَنْكُثُوْنَ','Maka tatkala Kami hilangkan azab itu dari mereka, dengan serta merta mereka memungkiri (janjinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,51,'وَنَادٰى فِرْعَوْنُ فِيْ قَوْمِهٖ قَالَ يٰقَوْمِ اَلَيْسَ لِيْ مُلْكُ مِصْرَ وَهٰذِهِ الْاَنْهٰرُ تَجْرِيْ مِنْ تَحْتِيْۚ اَفَلَا تُبْصِرُوْنَۗ ','Dan Fir´aun berseru kepada kaumnya (seraya) berkata: "Hai kaumku, bukankah kerajaan Mesir ini kepunyaanku dan (bukankah) sungai-sungai ini mengalir di bawahku; maka apakah kamu tidak melihat(nya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,52,'اَمْ اَنَا۠ خَيْرٌ مِّنْ هٰذَا الَّذِيْ هُوَ مَهِيْنٌ ەۙ وَّلَا يَكَادُ يُبِيْنُ ','Bukankah aku lebih baik dari orang yang hina ini dan yang hampir tidak dapat menjelaskan (perkataannya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,53,'فَلَوْلَٓا اُلْقِيَ عَلَيْهِ اَسْوِرَةٌ مِّنْ ذَهَبٍ اَوْ جَاۤءَ مَعَهُ الْمَلٰۤىِٕكَةُ مُقْتَرِنِيْنَ ','Mengapa tidak dipakaikan kepadanya gelang dari emas atau malaikat datang bersama-sama dia untuk mengiringkannya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,54,'فَاسْتَخَفَّ قَوْمَهٗ فَاَطَاعُوْهُ ۗاِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ','Maka Fir´aun mempengaruhi kaumnya (dengan perkataan itu) lalu mereka patuh kepadanya. Karena sesungguhnya mereka adalah kaum yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,55,'فَلَمَّآ اٰسَفُوْنَا انْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَۙ ','Maka tatkala mereka membuat Kami murka, Kami menghukum mereka lalu kami tenggelamkan mereka semuanya (di laut),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,56,'فَجَعَلْنٰهُمْ سَلَفًا وَّمَثَلًا لِّلْاٰخِرِيْنَ ࣖ','dan Kami jadikan mereka sebagai pelajaran dan contoh bagi orang-orang yang kemudian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,57,'وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلًا اِذَا قَوْمُكَ مِنْهُ يَصِدُّوْنَ','Dan tatkala putra Maryam (Isa) dijadikan perumpamnaan tiba-tiba kaummu (Quraisy) bersorak karenanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,58,'وَقَالُوْٓا ءَاٰلِهَتُنَا خَيْرٌ اَمْ هُوَ ۗمَا ضَرَبُوْهُ لَكَ اِلَّا جَدَلًا ۗبَلْ هُمْ قَوْمٌ خَصِمُوْنَ','Dan mereka berkata: "Manakah yang lebih baik tuhan-tuhan kami atau dia (Isa)?" Mereka tidak memberikan perumpamaan itu kepadamu melainkan dengan maksud membantah saja, sebenarnya mereka adalah kaum yang suka bertengkar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,59,'اِنْ هُوَ اِلَّا عَبْدٌ اَنْعَمْنَا عَلَيْهِ وَجَعَلْنٰهُ مَثَلًا لِّبَنِيْٓ اِسْرَاۤءِيْلَ ۗ  ','Isa tidak lain hanyalah seorang hamba yang Kami berikan kepadanya nikmat (kenabian) dan Kami jadikan dia sebagai tanda bukti (kekuasaan Allah) untuk Bani lsrail')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,60,'وَلَوْ نَشَاۤءُ لَجَعَلْنَا مِنْكُمْ مَّلٰۤىِٕكَةً فِى الْاَرْضِ يَخْلُفُوْنَ ','Dan kalau Kami kehendaki benar-benar Kami jadikan sebagai gantimu di muka bumi malaikat-malaikat yang turun temurun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,61,'وَاِنَّهٗ لَعِلْمٌ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُوْنِۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ','Dan sesungguhnya Isa itu benar-benar memberikan pengetahuan tentang hari kiamat. Karena itu janganlah kamu ragu-ragu tentang kiamat itu dan ikutilah Aku. Inilah jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,62,'وَلَا يَصُدَّنَّكُمُ الشَّيْطٰنُۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ ','Dan janganlah kamu sekali-kali dipalingkan oleh syaitan; sesungguhnya syaitan itu musuh yang nyata bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,63,'وَلَمَّا جَاۤءَ عِيْسٰى بِالْبَيِّنٰتِ قَالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَلِاُبَيِّنَ لَكُمْ بَعْضَ الَّذِيْ تَخْتَلِفُوْنَ فِيْهِۚ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ','Dan tatkala Isa datang membawa keterangan dia berkata: "Sesungguhnya aku datang kepadamu dengan membawa hikmat dan untuk menjelaskan kepadamu sebagian dari apa yang kamu berselisih tentangnya, maka bertakwalah kepada Allah dan taatlah (kepada)ku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,64,'اِنَّ اللّٰهَ هُوَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ','Sesungguhnya Allah Dialah Tuhanku dan Tuhan kamu maka sembahlah Dia, ini adalah jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,65,'فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْ ۚفَوَيْلٌ لِّلَّذِيْنَ ظَلَمُوْا مِنْ عَذَابِ يَوْمٍ اَلِيْمٍ ','Maka berselisihlah golongan-golongan (yang terdapat) di antara mereka, lalu kecelakaan yang besarlah bagi orang-orang yang zalim yakni siksaan hari yang pedih (kiamat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,66,'هَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ','Mereka tidak menunggu kecuali kedatangan hari kiamat kepada mereka dengan tiba-tiba sedang mereka tidak menyadarinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,67,'اَلْاَخِلَّاۤءُ يَوْمَىِٕذٍۢ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ اِلَّا الْمُتَّقِيْنَ ۗ ࣖ','Teman-teman akrab pada hari itu sebagiannya menjadi musuh bagi sebagian yang lain kecuali orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,68,'يٰعِبَادِ لَاخَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَآ اَنْتُمْ تَحْزَنُوْنَۚ ','"Hai hamba-hamba-Ku, tiada kekhawatiran terhadapmu pada hari ini dan tidak pula kamu bersedih hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,69,'اَلَّذِيْنَ اٰمَنُوْا بِاٰيٰتِنَا وَكَانُوْا مُسْلِمِيْنَۚ ','(Yaitu) orang-orang yang beriman kepada ayat-ayat Kami dan adalah mereka dahulu orang-orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,70,'اُدْخُلُوا الْجَنَّةَ اَنْتُمْ وَاَزْوَاجُكُمْ تُحْبَرُوْنَ ','Masuklah kamu ke dalam surga, kamu dan isteri-isteri kamu digembirakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,71,'يُطَافُ عَلَيْهِمْ بِصِحَافٍ مِّنْ ذَهَبٍ وَّاَكْوَابٍ ۚوَفِيْهَا مَا تَشْتَهِيْهِ الْاَنْفُسُ وَتَلَذُّ الْاَعْيُنُ ۚوَاَنْتُمْ فِيْهَا خٰلِدُوْنَۚ ','Diedarkan kepada mereka piring-piring dari emas, dan piala-piala dan di dalam surga itu terdapat segala apa yang diingini oleh hati dan sedap (dipandang) mata dan kamu kekal di dalamnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,72,'وَتِلْكَ الْجَنَّةُ الَّتِيْٓ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ ','Dan itulah surga yang diwariskan kepada kamu disebabkan amal-amal yang dahulu kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,73,'لَكُمْ فِيْهَا فَاكِهَةٌ كَثِيْرَةٌ مِّنْهَا تَأْكُلُوْنَ','Di dalam surga itu ada buah-buahan yang banyak untukmu yang sebahagiannya kamu makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,74,'اِنَّ الْمُجْرِمِيْنَ فِيْ عَذَابِ جَهَنَّمَ خٰلِدُوْنَۖ ','Sesungguhnya orang-orang yang berdosa kekal di dalam azab neraka Jahannam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,75,'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيْهِ مُبْلِسُوْنَ ۚ ','Tidak diringankan azab itu dari mereka dan mereka di dalamnya berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,76,'وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْا هُمُ الظّٰلِمِيْنَ','Dan tidaklah Kami menganiaya mereka tetapi merekalah yang menganiaya diri mereka sendiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,77,'وَنَادَوْا يٰمٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَۗ قَالَ اِنَّكُمْ مَّاكِثُوْنَ','Mereka berseru: "Hai Malik biarlah Tuhanmu membunuh kami saja". Dia menjawab: "Kamu akan tetap tinggal (di neraka ini)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,78,'لَقَدْ جِئْنٰكُمْ بِالْحَقِّ وَلٰكِنَّ اَكْثَرَكُمْ لِلْحَقِّ كٰرِهُوْنَ','Sesungguhnya Kami benar-benar telah memhawa kebenaran kepada kamu tetapi kebanyakan di antara kamu benci pada kebenaran itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,79,'اَمْ اَبْرَمُوْٓا اَمْرًا فَاِنَّا مُبْرِمُوْنَۚ','Bahkan mereka telah menetapkan satu tipu daya (jahat), maka sesungguhnya Kami menetapkan pula.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,80,'اَمْ يَحْسَبُوْنَ اَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوٰىهُمْ ۗ بَلٰى وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُوْنَ ','Apakah mereka mengira, bahwa Kami tidak mendengar rahasia dan bisikan-bisikan mereka? Sebenarnya (Kami mendengar), dan utusan-utusan (malaikat-malaikat) Kami selalu mencatat di sisi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,81,'قُلْ اِنْ كَانَ لِلرَّحْمٰنِ وَلَدٌ  ۖفَاَنَا۠ اَوَّلُ الْعٰبِدِيْنَ ','Katakanlah, jika benar Tuhan Yang Maha Pemurah mempunyai anak, maka akulah (Muhammad) orang yang mula-mula memuliakan (anak itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,82,'سُبْحٰنَ رَبِّ السَّمٰوٰتِ وَالْاَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ ','Maha Suci Tuhan Yang empunya langit dan bumi, Tuhan Yang empunya ´Arsy, dari apa yang mereka sifatkan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,83,'فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَ','Maka biarlah mereka tenggelam (dalam kesesatan) dan bermain-main sampai mereka menemui hari yang dijanjikan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,84,'وَهُوَ الَّذِيْ فِى السَّمَاۤءِ اِلٰهٌ وَّ فِى الْاَرْضِ اِلٰهٌ ۗوَهُوَ الْحَكِيْمُ الْعَلِيْمُ ','Dan Dialah Tuhan (Yang disembah) di langit dan Tuhan (Yang disembah) di bumi dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,85,'وَتَبٰرَكَ الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۚوَعِنْدَهٗ عِلْمُ السَّاعَةِۚ وَاِلَيْهِ تُرْجَعُوْنَ','Dan Maha Suci Tuhan Yang mempunyai kerajaan langit dan bumi; dan apa yang ada di antara keduanya; dan di sisi-Nya-lah pengetahuan tentang hari kiamat dan hanya kepada-Nya-lah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,86,'وَلَا يَمْلِكُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهِ الشَّفَاعَةَ اِلَّا مَنْ شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُوْنَ','Dan sembahan-sembahan yang mereka sembah selain Allah tidak dapat memberi syafa´at; akan tetapi (orang yang dapat memberi syafa´at ialah) orang yang mengakui yang hak (tauhid) dan mereka meyakini(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,87,'وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَهُمْ لَيَقُوْلُنَّ اللّٰهُ فَاَنّٰى يُؤْفَكُوْنَۙ','Dan sungguh jika kamu bertanya kepada mereka: "Siapakah yang menciptakan mereka, niscaya mereka menjawab: "Allah", maka bagaimanakah mereka dapat dipalingkan (dari menyembah Allah)?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,88,'وَقِيْلِهٖ يٰرَبِّ اِنَّ هٰٓؤُلَاۤءِ قَوْمٌ لَّا يُؤْمِنُوْنَۘ','dan (Allah mengetabui) ucapan Muhammad: "Ya Tuhanku, sesungguhnya mereka itu adalah kaum yang tidak beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,43,89,'فَاصْفَحْ عَنْهُمْ وَقُلْ سَلٰمٌۗ فَسَوْفَ يَعْلَمُوْنَ ࣖ','Maka berpalinglah (hai Muhammad) dari mereka dan katakanlah: "Salam (selamat tinggal)". Kelak mereka akan mengetahui (nasib mereka yang buruk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,1,'حٰمۤ  ۚ ','Haa miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,2,'وَالْكِتٰبِ الْمُبِيْنِۙ ','Demi Kitab (Al Quran) yang menjelaskan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,3,'اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةٍ مُّبٰرَكَةٍ اِنَّا كُنَّا مُنْذِرِيْنَ','sesungguhnya Kami menurunkannya pada suatu malam yang diberkahi dan sesungguhnya Kami-lah yang memberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,4,'فِيْهَا يُفْرَقُ كُلُّ اَمْرٍ حَكِيْمٍۙ ','Pada malam itu dijelaskan segala urusan yang penuh hikmah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,5,'اَمْرًا مِّنْ عِنْدِنَاۗ اِنَّا كُنَّا مُرْسِلِيْنَۖ ','(yaitu) urusan yang besar dari sisi Kami. Sesungguhnya Kami adalah Yang mengutus rasul-rasul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,6,'رَحْمَةً مِّنْ رَّبِّكَ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُۗ ','sebagai rahmat dari Tuhanmu. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,7,'رَبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۘ اِنْ كُنْتُمْ مُّوْقِنِيْنَ','Tuhan Yang memelihara langit dan bumi dan apa yang ada di antara keduanya, jika kamu adalah orang yang meyakini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,8,'لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُ ۗرَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ','Tidak ada Tuhan (yang berhak disembah) melainkan Dia, Yang menghidupkan dan Yang mematikan (Dialah) Tuhanmu dan Tuhan bapak-bapakmu yang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,9,'بَلْ هُمْ فِيْ شَكٍّ يَّلْعَبُوْنَ','Tetapi mereka bermain-main dalam keragu-raguan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,10,'فَارْتَقِبْ يَوْمَ تَأْتِى السَّمَاۤءُ بِدُخَانٍ مُّبِيْنٍ ','Maka tunggulah hari ketika langit membawa kabut yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,11,'يَغْشَى النَّاسَۗ هٰذَا عَذَابٌ اَلِيْمٌ ','yang meliputi manusia. Inilah azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,12,'رَبَّنَا اكْشِفْ عَنَّا الْعَذَابَ اِنَّا مُؤْمِنُوْنَ ','(Mereka berdoa): "Ya Tuhan kami, lenyapkanlah dari kami azab itu. Sesungguhnya kami akan beriman".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,13,'اَنّٰى لَهُمُ الذِّكْرٰى وَقَدْ جَاۤءَهُمْ رَسُوْلٌ مُّبِيْنٌۙ ','Bagaimanakah mereka dapat menerima peringatan, padahal telah datang kepada mereka seorang rasul yang memberi penjelasan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,14,'ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوْا مُعَلَّمٌ مَّجْنُوْنٌۘ ','kemudian mereka berpaling daripadanya dan berkata: "Dia adalah seorang yang menerima ajaran (dari orang lain) lagi pula seorang yang gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,15,'اِنَّا كَاشِفُوا الْعَذَابِ قَلِيْلًا اِنَّكُمْ عَاۤىِٕدُوْنَۘ ','Sesungguhnya (kalau) Kami akan melenyapkan siksaan itu agak sedikit sesungguhnya kamu akan kembali (ingkar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,16,'يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرٰىۚ اِنَّا مُنْتَقِمُوْنَ','(Ingatlah) hari (ketika) Kami menghantam mereka dengan hantaman yang keras. Sesungguhnya Kami adalah Pemberi balasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,17,'۞ وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَاۤءَهُمْ رَسُوْلٌ كَرِيْمٌۙ ','Sesungguhnya sebelum mereka telah Kami uji kaum Fir´aun dan telah datang kepada mereka seorang rasul yang mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,18,'اَنْ اَدُّوْٓا اِلَيَّ عِبَادَ اللّٰهِ ۗاِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌۙ ','(dengan berkata): "Serahkanlah kepadaku hamba-hamba Allah (Bani Israil yang kamu perbudak). Sesungguhnya aku adalah utusan (Allah) yang dipercaya kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,19,'وَّاَنْ لَّا تَعْلُوْا عَلَى اللّٰهِ ۚاِنِّيْٓ اٰتِيْكُمْ بِسُلْطٰنٍ مُّبِيْنٍۚ ','dan janganlah kamu menyombongkan diri terhadap Allah. Sesungguhnya aku datang kepadamu dengan membawa bukti yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,20,'وَاِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ اَنْ تَرْجُمُوْنِۚ ','Dan sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu, dari keinginanmu merajamku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,21,'وَاِنْ لَّمْ تُؤْمِنُوْا لِيْ فَاعْتَزِلُوْنِ ','dan jika kamu tidak beriman kepadaku maka biarkanlah aku (memimpin Bani Israil)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,22,'فَدَعَا رَبَّهٗٓ اَنَّ هٰٓؤُلَاۤءِ قَوْمٌ مُّجْرِمُوْنَ ','Kemudian Musa berdoa kepada Tuhannya: "Sesungguhnya mereka ini adalah kaum yang berdosa (segerakanlah azab kepada mereka)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,23,'فَاَسْرِ بِعِبَادِيْ لَيْلًا اِنَّكُمْ مُّتَّبَعُوْنَۙ ','(Allah berfirman): "Maka berjalanlah kamu dengan membawa hamba-hamba-Ku pada malam hari, sesungguhnya kamu akan dikejar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,24,'وَاتْرُكِ الْبَحْرَ رَهْوًاۗ اِنَّهُمْ جُنْدٌ مُّغْرَقُوْنَ ','dan biarkanlah laut itu tetap terbelah. Sesungguhnya mereka adalah tentara yang akan ditenggelamkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,25,'كَمْ تَرَكُوْا مِنْ جَنّٰتٍ وَّعُيُوْنٍۙ ','Alangkah banyaknya taman dan mata air yang mereka tinggalkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,26,'وَّزُرُوْعٍ وَّمَقَامٍ كَرِيْمٍۙ ','dan kebun-kebun serta tempat-tempat yang indah-indah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,27,'وَّنَعْمَةٍ كَانُوْا فِيْهَا فٰكِهِيْنَۙ ','dan kesenangan-kesenangan yang mereka menikmatinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,28,'كَذٰلِكَ ۗوَاَوْرَثْنٰهَا قَوْمًا اٰخَرِيْنَۚ','demikianlah. Dan Kami wariskan semua itu kepada kaum yang lain.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,29,'فَمَا بَكَتْ عَلَيْهِمُ السَّمَاۤءُ وَالْاَرْضُۗ وَمَا كَانُوْا مُنْظَرِيْنَ ࣖ','Maka langit dan bumi tidak menangisi mereka dan merekapun tidak diberi tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,30,'وَلَقَدْ نَجَّيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مِنَ الْعَذَابِ الْمُهِيْنِۙ ','Dan sesungguhnya telah Kami selamatkan Bani Israil dari siksa yang menghinakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,31,'مِنْ فِرْعَوْنَ ۗاِنَّهٗ كَانَ عَالِيًا مِّنَ الْمُسْرِفِيْنَ ','dari (azab) Fir´aun. Sesungguhnya dia adalah orang yang sombong, salah seorang dari orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,32,'وَلَقَدِ اخْتَرْنٰهُمْ عَلٰى عِلْمٍ عَلَى الْعٰلَمِيْنَ ۚ ','Dan sesungguhnya telah Kami pilih mereka dengan pengetahuan (Kami) atas bangsa-bangsa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,33,'وَاٰتَيْنٰهُمْ مِّنَ الْاٰيٰتِ مَا فِيْهِ بَلٰۤـؤٌا مُّبِيْنٌ ','Dan Kami telah memberikan kepada mereka di antara tanda-tanda kekuasaan (Kami) sesuatu yang di dalamnya terdapat nikmat yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,34,'اِنَّ هٰٓؤُلَاۤءِ لَيَقُوْلُوْنَۙ ','Sesungguhnya mereka (kaum musyrik) itu benar-benar berkata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,35,'اِنْ هِيَ اِلَّا مَوْتَتُنَا الْاُوْلٰى وَمَا نَحْنُ بِمُنْشَرِيْنَ ','"tidak ada kematian selain kematian di dunia ini. Dan kami sekali-kali tidak akan dibangkitkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,36,'فَأْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ ','maka datangkanlah (kembali) bapak-bapak kami jika kamu memang orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,37,'اَهُمْ خَيْرٌ اَمْ قَوْمُ تُبَّعٍۙ وَّالَّذِيْنَ مِنْ قَبْلِهِمْۗ اَهْلَكْنٰهُمْ اِنَّهُمْ كَانُوْا مُجْرِمِيْنَ','Apakah mereka (kaum musyrikin) yang lebih baik ataukah kaum Tubba´ dan orang-orang yang sebelum mereka. Kami telah membinasakan mereka karena sesungguhnya mereka adalah orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,38,'وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ','Dan Kami tidak menciptakan langit dan bumi dan apa yang ada antara keduanya dengan bermain-main.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,39,'مَا خَلَقْنٰهُمَآ اِلَّا بِالْحَقِّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ','Kami tidak menciptakan keduanya melainkan dengan haq, tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,40,'اِنَّ يَوْمَ الْفَصْلِ مِيْقَاتُهُمْ اَجْمَعِيْنَ ۙ ','Sesungguhnya hari keputusan (hari kiamat) itu adalah waktu yang dijanjikan bagi mereka semuanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,41,'يَوْمَ لَا يُغْنِيْ مَوْلًى عَنْ مَّوْلًى شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۙ ','yaitu hari yang seorang karib tidak dapat memberi manfaat kepada karibnya sedikitpun, dan mereka tidak akan mendapat pertolongan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,42,'اِلَّا مَنْ رَّحِمَ اللّٰهُ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الرَّحِيْمُ ࣖ ','kecuali orang yang diberi rahmat oleh Allah. Sesungguhnya Dialah Yang Maha Perkasa lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,43,'اِنَّ شَجَرَتَ الزَّقُّوْمِۙ','Sesungguhnya pohon zaqqum itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,44,'طَعَامُ الْاَثِيْمِ ۛ ','makanan orang yang banyak berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,45,'كَالْمُهْلِ ۛ يَغْلِيْ فِى الْبُطُوْنِۙ ','(Ia) sebagai kotoran minyak yang mendidih di dalam perut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,46,'كَغَلْيِ الْحَمِيْمِ ۗ','seperti mendidihnya air yang amat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,47,'خُذُوْهُ فَاعْتِلُوْهُ اِلٰى سَوَاۤءِ الْجَحِيْمِۙ ','Peganglah dia kemudian seretlah dia ke tengah-tengah neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,48,'ثُمَّ صُبُّوْا فَوْقَ رَأْسِهٖ مِنْ عَذَابِ الْحَمِيْمِۗ','Kemudian tuangkanlah di atas kepalanya siksaan (dari) air yang amat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,49,'ذُقْۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْكَرِيْمُ ','Rasakanlah, sesungguhnya kamu orang yang perkasa lagi mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,50,'اِنَّ هٰذَا مَا كُنْتُمْ بِهٖ تَمْتَرُوْنَ ','Sesungguhnya ini adalah azab yang dahulu selalu kamu meragu-ragukannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,51,'اِنَّ الْمُتَّقِيْنَ فِيْ مَقَامٍ اَمِيْنٍۙ','Sesungguhnya orang-orang yang bertakwa berada dalam tempat yang aman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,52,'فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ','(yaitu) di dalam taman-taman dan mata-air-mata-air;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,53,'يَّلْبَسُوْنَ مِنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَقٰبِلِيْنَۚ','mereka memakai sutera yang halus dan sutera yang tebal, (duduk) berhadap-hadapan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,54,'كَذٰلِكَۗ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍۗ ','demikianlah. Dan Kami berikan kepada mereka bidadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,55,'يَدْعُوْنَ فِيْهَا بِكُلِّ فَاكِهَةٍ اٰمِنِيْنَۙ ','Di dalamnya mereka meminta segala macam buah-buahan dengan aman (dari segala kekhawatiran),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,56,'لَا يَذُوْقُوْنَ فِيْهَا الْمَوْتَ اِلَّا الْمَوْتَةَ الْاُوْلٰىۚ وَوَقٰىهُمْ عَذَابَ الْجَحِيْمِۙ ','mereka tidak akan merasakan mati di dalamnya kecuali mati di dunia. Dan Allah memelihara mereka dari azab neraka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,57,'فَضْلًا مِّنْ رَّبِّكَۚ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ','sebagai karunia dari Tuhanmu. Yang demikian itu adalah keberuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,58,'فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ','Sesungguhnya Kami mudahkan Al Quran itu dengan bahasamu supaya mereka mendapat pelajaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,44,59,'فَارْتَقِبْ اِنَّهُمْ مُّرْتَقِبُوْنَ ࣖࣖ','Maka tunggulah; sesungguhnya mereka itu menunggu (pula).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,1,'حٰمۤ  ','Haa Miim')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,2,'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ','Kitab (ini) diturunkan dari Allah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,3,'اِنَّ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّلْمُؤْمِنِيْنَۗ','Sesungguhnya pada langit dan bumi benar-benar terdapat tanda-tanda (kekuasaan Allah) untuk orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,4,'وَفِيْ خَلْقِكُمْ وَمَا يَبُثُّ مِنْ دَاۤبَّةٍ اٰيٰتٌ لِّقَوْمٍ يُّوْقِنُوْنَۙ','Dan pada penciptakan kamu dan pada binatang-binatang yang melata yang bertebaran (di muka bumi) terdapat tanda-tanda (kekuasaan Allah) untuk kaum yang meyakini,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,5,'وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ رِّزْقٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيْفِ الرِّيٰحِ اٰيٰتٌ لِّقَوْمٍ يَّعْقِلُوْنَ','dan pada pergantian malam dan siang dan hujan yang diturunkan Allah dari langit lalu dihidupkan-Nya dengan air hujan itu bumi sesudah matinya; dan pada perkisaran angin terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,6,'تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّۚ فَبِاَيِّ حَدِيْثٍۢ بَعْدَ اللّٰهِ وَاٰيٰتِهٖ يُؤْمِنُوْنَ','Itulah ayat-ayat Allah yang Kami membacakannya kepadamu dengan sebenarnya; maka dengan perkataan manakah lagi mereka akan beriman sesudah (kalam) Allah dan keterangan-keterangan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,7,'وَيْلٌ لِّكُلِّ اَفَّاكٍ اَثِيْمٍۙ','Kecelakaan besarlah bagi tiap-tiap orang yang banyak berdusta lagi banyak berdosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,8,'يَّسْمَعُ اٰيٰتِ اللّٰهِ تُتْلٰى عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ','dia mendengar ayat-ayat Allah dibacakan kepadanya kemudian dia tetap menyombongkan diri seakan-akan dia tidak mendengarnya. Maka beri khabar gembiralah dia dengan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,9,'وَاِذَا عَلِمَ مِنْ اٰيٰتِنَا شَيْـًٔا ۨاتَّخَذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌۗ ','Dan apabila dia mengetahui barang sedikit tentang ayat-ayat Kami, maka ayat-ayat itu dijadikan olok-olok. Merekalah yang memperoleh azab yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,10,'مِنْ وَّرَاۤىِٕهِمْ جَهَنَّمُ ۚوَلَا يُغْنِيْ عَنْهُمْ مَّا كَسَبُوْا شَيْـًٔا وَّلَا مَا اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَۚ وَلَهُمْ عَذَابٌ عَظِيْمٌۗ ','Di hadapan mereka neraka Jahannam dan tidak akan berguna bagi mereka sedikitpun apa yang telah mereka kerjakan, dan tidak pula berguna apa yang mereka jadikan sebagai sembahan-sembahan (mereka) dari selain Allah. Dan bagi mereka azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,11,'هٰذَا هُدًىۚ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ ࣖ','Ini (Al Quran) adalah petunjuk. Dan orang-orang yang kafir kepada ayat-ayat Tuhannya bagi mereka azab yaitu siksaan yang sangat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,12,'۞ اَللّٰهُ الَّذِيْ سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيْهِ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَۚ ','Allah-lah yang menundukkan lautan untukmu supaya kapal-kapal dapat berlayar padanya dengan seizin-Nya dan supaya kamu dapat mencari karunia -Nya dan mudah-mudahan kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,13,'وَسَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ جَمِيْعًا مِّنْهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ ','Dan Dia telah menundukkan untukmu apa yang di langit dan apa yang di bumi semuanya, (sebagai rahmat) daripada-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,14,'قُلْ لِّلَّذِيْنَ اٰمَنُوْا يَغْفِرُوْا لِلَّذِيْنَ لَا يَرْجُوْنَ اَيَّامَ اللّٰهِ لِيَجْزِيَ قَوْمًا ۢبِمَا كَانُوْا يَكْسِبُوْنَ ','Katakanlah kepada orang-orang yang beriman hendaklah mereka memaafkan orang-orang yang tiada takut hari-hari Allah karena Dia akan membalas sesuatu kaum terhadap apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,15,'مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖۚ وَمَنْ اَسَاۤءَ فَعَلَيْهَا ۖ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ','Barangsiapa yang mengerjakan amal saleh, maka itu adalah untuk dirinya sendiri, dan barangsiapa yang mengerjakan kejahatan, maka itu akan menimpa dirinya sendiri, kemudian kepada Tuhanmulah kamu dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,16,'وَلَقَدْ اٰتَيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلَى الْعٰلَمِيْنَ ۚ ','Dan sesungguhnya telah Kami berikan kepada Bani Israil Al Kitab (Taurat), kekuasaan dan kenabian dan Kami berikan kepada mereka rezeki-rezeki yang baik dan Kami lebihkan mereka atas bangsa-bangsa (pada masanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,17,'وَاٰتَيْنٰهُمْ بَيِّنٰتٍ مِّنَ الْاَمْرِۚ فَمَا اخْتَلَفُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ','Dan Kami berikan kepada mereka keterangan-keterangan yang nyata tentang urusan (agama); maka mereka tidak berselisih melainkan sesudah datang kepada mereka pengetahuan karena kedengkian yang ada di antara mereka. Sesungguhnya Tuhanmu akan memutuskan antara mereka pada hari kiamat terhadap apa yang mereka selalu berselisih padanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,18,'ثُمَّ جَعَلْنٰكَ عَلٰى شَرِيْعَةٍ مِّنَ الْاَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ لَا يَعْلَمُوْنَ','Kemudian Kami jadikan kamu berada di atas suatu syariat (peraturan) dari urusan (agama itu), maka ikutilah syariat itu dan janganlah kamu ikuti hawa nafsu orang-orang yang tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,19,'اِنَّهُمْ لَنْ يُّغْنُوْا عَنْكَ مِنَ اللّٰهِ شَيْـًٔا  ۗوَاِنَّ الظّٰلِمِيْنَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۚ وَاللّٰهُ وَلِيُّ الْمُتَّقِيْنَ ','Sesungguhnya mereka sekali-kali tidak akan dapat menolak dari kamu sedikitpun dari siksaan Allah. Dan Sesungguhnya orang-orang yang zalim itu sebagian mereka menjadi penolong bagi sebagian yang lain, dan Allah adalah pelindung orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,20,'هٰذَا بَصَاۤىِٕرُ لِلنَّاسِ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّوْقِنُوْنَ ','Al Quran ini adalah pedoman bagi manusia, petunjuk dan rahmat bagi kaum yang meyakini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,21,'اَمْ حَسِبَ الَّذِيْنَ اجْتَرَحُوا السَّيِّاٰتِ اَنْ نَّجْعَلَهُمْ كَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَوَاۤءً مَّحْيَاهُمْ وَمَمَاتُهُمْ ۗسَاۤءَ مَا يَحْكُمُوْنَ ࣖࣖ','Apakah orang-orang yang membuat kejahatan itu menyangka bahwa Kami akan menjadikan mereka seperti orang-orang yang beriman dan mengerjakan amal saleh, yaitu sama antara kehidupan dan kematian mereka? Amat buruklah apa yang mereka sangka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,22,'وَخَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَلِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ','Dan Allah menciptakan langit dan bumi dengan tujuan yang benar dan agar dibalasi tiap-tiap diri terhadap apa yang dikerjakannya, dan mereka tidak akan dirugikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,23,'اَفَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُ وَاَضَلَّهُ اللّٰهُ عَلٰى عِلْمٍ وَّخَتَمَ عَلٰى سَمْعِهٖ وَقَلْبِهٖ وَجَعَلَ عَلٰى بَصَرِهٖ غِشٰوَةًۗ فَمَنْ يَّهْدِيْهِ مِنْۢ بَعْدِ اللّٰهِ ۗ اَفَلَا تَذَكَّرُوْنَ','Maka pernahkah kamu melihat orang yang menjadikan hawa nafsunya sebagai tuhannya dan Allah membiarkannya berdasarkan ilmu-Nya dan Allah telah mengunci mati pendengaran dan hatinya dan meletakkan tutupan atas penglihatannya? Maka siapakah yang akan memberinya petunjuk sesudah Allah (membiarkannya sesat). Maka mengapa kamu tidak mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,24,'وَقَالُوْا مَا هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا يُهْلِكُنَآ اِلَّا الدَّهْرُۚ وَمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍۚ اِنْ هُمْ اِلَّا يَظُنُّوْنَ','Dan mereka berkata: "Kehidupan ini tidak lain hanyalah kehidupan di dunia saja, kita mati dan kita hidup dan tidak ada yang akan membinasakan kita selain masa", dan mereka sekali-kali tidak mempunyai pengetahuan tentang itu, mereka tidak lain hanyalah menduga-duga saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,25,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ مَّا كَانَ حُجَّتَهُمْ اِلَّآ اَنْ قَالُوا ائْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ','Dan apabila dibacakan kepada mereka ayat-ayat Kami yang jelas, tidak ada bantahan mereka selain dari mengatakan: "Datangkanlah nenek moyang kami jika kamu adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,26,'قُلِ اللّٰهُ يُحْيِيْكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يَجْمَعُكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَارَيْبَ فِيْهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ࣖ','Katakanlah: "Allah-lah yang menghidupkan kamu kemudian mematikan kamu, setelah itu mengumpulkan kamu pada hari kiamat yang tidak ada keraguan padanya; akan tetapi kebanyakan manusia tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,27,'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّخْسَرُ الْمُبْطِلُوْنَ','Dan hanya kepunyaan Allah kerajaan langit dan bumi. Dan pada hari terjadinya kebangkitan, akan rugilah pada hari itu orang-orang yang mengerjakan kebathilan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,28,'وَتَرٰى كُلَّ اُمَّةٍ جَاثِيَةً ۗ كُلُّ اُمَّةٍ تُدْعٰٓى اِلٰى كِتٰبِهَاۗ  اَلْيَوْمَ تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ','Dan (pada hari itu) kamu lihat tiap-tiap umat berlutut. Tiap-tiap umat dipanggil untuk (melihat) buku catatan amalnya. Pada hari itu kamu diberi balasan terhadap apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,29,'هٰذَا كِتٰبُنَا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّ ۗاِنَّا كُنَّا نَسْتَنْسِخُ مَا كُنْتُمْ تَعْمَلُوْنَ','(Allah berfirman): "Inilah kitab (catatan) Kami yang menuturkan terhadapmu dengan benar. Sesungguhnya Kami telah menyuruh mencatat apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,30,'فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُدْخِلُهُمْ رَبُّهُمْ  فِيْ رَحْمَتِهٖۗ ذٰلِكَ هُوَ الْفَوْزُ الْمُبِيْنُ','Adapun orang-orang yang beriman dan mengerjakan amal saleh maka Tuhan mereka memasukkan mereka ke dalam rahmat-Nya (surga). Itulah keberuntungan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,31,'وَاَمَّا الَّذِيْنَ كَفَرُوْاۗ  اَفَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَكُنْتُمْ قَوْمًا مُّجْرِمِيْنَ','Dan adapun orang-orang yang kafir (kepada mereka dikatakan): "Maka apakah belum ada ayat-ayat-Ku yang dibacakan kepadamu lalu kamu menyombongkan diri dan kamu jadi kaum yang berbuat dosa?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,32,'وَاِذَا قِيْلَ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّالسَّاعَةُ لَا رَيْبَ فِيْهَا قُلْتُمْ مَّا نَدْرِيْ مَا السَّاعَةُۙ اِنْ نَّظُنُّ اِلَّا ظَنًّا وَّمَا نَحْنُ بِمُسْتَيْقِنِيْنَ ','Dan apabila dikatakan (kepadamu): "Sesungguhnya janji Allah itu adalah benar dan hari berbangkit itu tidak ada keraguan padanya", niscaya kamu menjawab: "Kami tidak tahu apakah hari kiamat itu, kami sekali-kali tidak lain hanyalah menduga-duga saja dan kami sekali-kali tidak meyakini(nya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,33,'وَبَدَا لَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ','Dan nyatalah bagi mereka keburukan-keburukan dari apa yang mereka kerjakan dan mereka diliputi oleh (azab) yang mereka selalu memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,34,'وَقِيْلَ الْيَوْمَ نَنْسٰىكُمْ كَمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۙ وَمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَ','Dan dikatakan (kepada mereka): "Pada hari ini Kami melupakan kamu sebagaimana kamu telah melupakan pertemuan (dengan) harimu ini dan tempat kembalimu ialah neraka dan kamu sekali-kali tidak memperoleh penolong".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,35,'ذٰلِكُمْ بِاَنَّكُمُ اتَّخَذْتُمْ اٰيٰتِ اللّٰهِ هُزُوًا وَّغَرَّتْكُمُ الْحَيٰوةُ الدُّنْيَا ۚفَالْيَوْمَ لَا يُخْرَجُوْنَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُوْنَ','Yang demikian itu, karena sesungguhnya kamu menjadikan ayat-ayat Allah sebagai olok-olokan dan kamu telah ditipu oleh kehidupan dunia, maka pada hari ini mereka tidak dikeluarkan dari neraka dan tidak pula mereka diberi kesempatan untuk bertaubat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,36,'فَلِلّٰهِ الْحَمْدُ رَبِّ السَّمٰوٰتِ وَرَبِّ الْاَرْضِ رَبِّ الْعٰلَمِيْنَ','Maka bagi Allah-lah segala puji, Tuhan langit dan Tuhan bumi, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,25,45,37,'وَلَهُ الْكِبْرِيَاۤءُ فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ   ۔','Dan bagi-Nya-lah keagungan di langit dan bumi, Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,1,'حٰمۤ  ۚ','Haa Miim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,2,'تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ ','Diturunkan Kitab ini dari Allah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,3,'مَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَالَّذِيْنَ كَفَرُوْا عَمَّآ اُنْذِرُوْا مُعْرِضُوْنَ','Kami tiada menciptakan langit dan bumi dan apa yang ada antara keduanya melainkan dengan (tujuan) yang benar dan dalam waktu yang ditentukan. Dan orang-orang yang kafir berpaling dari apa yang diperingatkan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,4,'قُلْ اَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِ ۖائْتُوْنِيْ بِكِتٰبٍ مِّنْ قَبْلِ هٰذَآ اَوْ اَثٰرَةٍ مِّنْ عِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Katakanlah: "Terangkanlah kepadaku tentang apa yang kamu sembah selain Allah; perlihatkan kepada-Ku apakah yang telah mereka ciptakan dari bumi ini atau adakah mereka berserikat (dengan Allah) dalam (penciptaan) langit? Bawalah kepada-Ku Kitab yang sebelum (Al Quran) ini atau peninggalan dari pengetahuan (orang-orang dahulu), jika kamu adalah orang-orang yang benar"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,5,'وَمَنْ اَضَلُّ مِمَّنْ يَّدْعُوْا مِنْ دُوْنِ اللّٰهِ مَنْ لَّا يَسْتَجِيْبُ لَهٗٓ اِلٰى يَوْمِ الْقِيٰمَةِ وَهُمْ عَنْ دُعَاۤىِٕهِمْ غٰفِلُوْنَ ','Dan siapakah yang lebih sesat daripada orang yang menyembah sembahan-sembahan selain Allah yang tiada dapat memperkenankan (doa)nya sampai hari kiamat dan mereka lalai dari (memperhatikan) doa mereka?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,6,'وَاِذَا حُشِرَ النَّاسُ كَانُوْا لَهُمْ اَعْدَاۤءً وَّكَانُوْا بِعِبَادَتِهِمْ كٰفِرِيْنَ ','Dan apabila manusia dikumpulkan (pada hari kiamat) niscaya sembahan-sembahan itu menjadi musuh mereka dan mengingkari pemujaan-pemujaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,7,'وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ هٰذَا سِحْرٌ مُّبِيْنٌۗ','Dan apabila dibacakan kepada mereka ayat-ayat Kami yang menjelaskan, berkatalah orang-orang yang mengingkari kebenaran ketika kebenaran itu datang kepada mereka: "Ini adalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,8,'اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ اِنِ افْتَرَيْتُهٗ فَلَا تَمْلِكُوْنَ لِيْ مِنَ اللّٰهِ شَيْـًٔا ۗهُوَ اَعْلَمُ بِمَا تُفِيْضُوْنَ فِيْهِۗ  كَفٰى بِهٖ شَهِيْدًا ۢ بَيْنِيْ وَبَيْنَكُمْ ۗ وَهُوَ الْغَفُوْرُ الرَّحِيْمُ','Bahkan mereka mengatakan: "Dia (Muhammad) telah mengada-adakannya (Al Quran)". Katakanlah: "Jika aku mengada-adakannya, maka kamu tiada mempunyai kuasa sedikitpun mempertahankan aku dari (azab) Allah itu. Dia lebih mengetahui apa-apa yang kamu percakapkan tentang Al Quran itu. Cukuplah Dia menjadi saksi antaraku dan antaramu dan Dialah Yang Maha Pengampun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,9,'قُلْ مَا كُنْتُ بِدْعًا مِّنَ الرُّسُلِ وَمَآ اَدْرِيْ مَا يُفْعَلُ بِيْ وَلَا بِكُمْۗ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ وَمَآ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ','Katakanlah: "Aku bukanlah rasul yang pertama di antara rasul-rasul dan aku tidak mengetahui apa yang akan diperbuat terhadapku dan tidak (pula) terhadapmu. Aku tidak lain hanyalah mengikuti apa yang diwahyukan kepadaku dan aku tidak lain hanyalah seorang pemberi peringatan yang menjelaskan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,10,'قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ وَكَفَرْتُمْ بِهٖ وَشَهِدَ شَاهِدٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى مِثْلِهٖ فَاٰمَنَ وَاسْتَكْبَرْتُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ࣖ ','Katakanlah: "Terangkanlah kepadaku, bagaimanakah pendapatmu jika Al Quran itu datang dari sisi Allah, padahal kamu mengingkarinya dan seorang saksi dari Bani Israil mengakui (kebenaran) yang serupa dengan (yang tersebut dalam) Al Quran lalu dia beriman, sedang kamu menyombongkan diri. Sesungguhnya Allah tiada memberi petunjuk kepada orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,11,'وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْا لَوْ كَانَ خَيْرًا مَّا سَبَقُوْنَآ اِلَيْهِۗ وَاِذْ لَمْ يَهْتَدُوْا بِهٖ فَسَيَقُوْلُوْنَ هٰذَآ اِفْكٌ قَدِيْمٌ  ','Dan orang-orang kafir berkata kepada orang-orang yang beriman: "Kalau sekiranya di (Al Quran) adalah suatu yang baik, tentulah mereka tiada mendahului kami (beriman) kepadanya. Dan karena mereka tidak mendapat petunjuk dengannya maka mereka akan berkata: "Ini adalah dusta yang lama".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,12,'وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰٓى اِمَامًا وَّرَحْمَةً ۗوَهٰذَا كِتٰبٌ مُّصَدِّقٌ لِّسَانًا عَرَبِيًّا لِّيُنْذِرَ الَّذِيْنَ ظَلَمُوْا ۖوَبُشْرٰى لِلْمُحْسِنِيْنَ','Dan sebelum Al Quran itu telah ada kitab Musa sebagai petunjuk dan rahmat. Dan ini (Al Quran) adalah kitab yang membenarkannya dalam bahasa Arab untuk memberi peringatan kepada orang-orang yang zalim dan memberi kabar gembira kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,13,'اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ','Sesungguhnya orang-orang yang mengatakan: "Tuhan kami ialah Allah", kemudian mereka tetap istiqamah maka tidak ada kekhawatiran terhadap mereka dan mereka tiada (pula) berduka cita.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,14,'اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ خٰلِدِيْنَ فِيْهَاۚ جَزَاۤءً ۢبِمَا كَانُوْا يَعْمَلُوْنَ ','Mereka itulah penghuni-penghuni surga, mereka kekal di dalamnya; sebagai balasan atas apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,15,'وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ اِحْسَانًا ۗحَمَلَتْهُ اُمُّهٗ كُرْهًا وَّوَضَعَتْهُ كُرْهًا ۗوَحَمْلُهٗ وَفِصٰلُهٗ ثَلٰثُوْنَ شَهْرًا ۗحَتّٰىٓ اِذَا بَلَغَ اَشُدَّهٗ وَبَلَغَ اَرْبَعِيْنَ سَنَةًۙ  قَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَصْلِحْ لِيْ فِيْ ذُرِّيَّتِيْۗ اِنِّيْ تُبْتُ اِلَيْكَ وَاِنِّيْ مِنَ الْمُسْلِمِيْنَ ','Kami perintahkan kepada manusia supaya berbuat baik kepada dua orang ibu bapaknya, ibunya mengandungnya dengan susah payah, dan melahirkannya dengan susah payah (pula). Mengandungnya sampai menyapihnya adalah tiga puluh bulan, sehingga apabila dia telah dewasa dan umurnya sampai empat puluh tahun ia berdoa: "Ya Tuhanku, tunjukilah aku untuk mensyukuri nikmat Engkau yang telah Engkau berikan kepadaku dan kepada ibu bapakku dan supaya aku dapat berbuat amal yang saleh yang Engkau ridhai; berilah kebaikan kepadaku dengan (memberi kebaikan) kepada anak cucuku. Sesungguhnya aku bertaubat kepada Engkau dan sesungguhnya aku termasuk orang-orang yang berserah diri".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,16,'اُولٰۤىِٕكَ الَّذِيْنَ نَتَقَبَّلُ عَنْهُمْ اَحْسَنَ مَا عَمِلُوْا وَنَتَجَاوَزُ عَنْ سَيِّاٰتِهِمْ فِيْٓ اَصْحٰبِ الْجَنَّةِۗ وَعْدَ الصِّدْقِ الَّذِيْ كَانُوْا يُوْعَدُوْنَ ','Mereka itulah orang-orang yang Kami terima dari mereka amal yang baik yang telah mereka kerjakan dan Kami ampuni kesalahan-kesalahan mereka, bersama penghuni-penghuni surga, sebagai janji yang benar yang telah dijanjikan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,17,'وَالَّذِيْ قَالَ لِوَالِدَيْهِ اُفٍّ لَّكُمَآ اَتَعِدَانِنِيْٓ اَنْ اُخْرَجَ وَقَدْ خَلَتِ الْقُرُوْنُ مِنْ قَبْلِيْۚ وَهُمَا يَسْتَغِيْثٰنِ اللّٰهَ وَيْلَكَ اٰمِنْ ۖاِنَّ وَعْدَ اللّٰهِ حَقٌّۚ فَيَقُوْلُ مَا هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ','Dan orang yang berkata kepada dua orang ibu bapaknya: "Cis bagi kamu keduanya, apakah kamu keduanya memperingatkan kepadaku bahwa aku akan dibangkitkan, padahal sungguh telah berlalu beberapa umat sebelumku? lalu kedua ibu bapaknya itu memohon pertolongan kepada Allah seraya mengatakan: "Celaka kamu, berimanlah! Sesungguhnya janji Allah adalah benar". Lalu dia berkata: "Ini tidak lain hanyalah dongengan orang-orang dahulu belaka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,18,'اُولٰۤىِٕكَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِ ۗاِنَّهُمْ كَانُوْا خٰسِرِيْنَ','Mereka itulah orang-orang yang telah pasti ketetapan (azab) atas mereka bersama umat-umat yang telah berlalu sebelum mereka dari jin dan manusia. Sesungguhnya mereka adalah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,19,'وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۚ وَلِيُوَفِّيَهُمْ اَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُوْنَ ','Dan bagi masing-masing mereka derajat menurut apa yang telah mereka kerjakan dan agar Allah mencukupkan bagi mereka (balasan) pekerjaan-pekerjaan mereka sedang mereka tiada dirugikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,20,'وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ  اَذْهَبْتُمْ طَيِّبٰتِكُمْ فِيْ حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُمْ بِهَاۚ فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَسْتَكْبِرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَفْسُقُوْنَ ࣖ ','Dan (ingatlah) hari (ketika) orang-orang kafir dihadapkan ke neraka (kepada mereka dikatakan): "Kamu telah menghabiskan rezekimu yang baik dalam kehidupan duniawimu (saja) dan kamu telah bersenang-senang dengannya; maka pada hari ini kamu dibalasi dengan azab yang menghinakan karena kamu telah menyombongkan diri di muka bumi tanpa hak dan karena kamu telah fasik".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,21,'۞ وَاذْكُرْ اَخَا عَادٍۗ اِذْ اَنْذَرَ قَوْمَهٗ بِالْاَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖٓ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ','Dan ingatlah (Hud) saudara kaum ´Aad yaitu ketika dia memberi peringatan kepada kaumnya di Al Ahqaaf dan sesungguhnya telah terdahulu beberapa orang pemberi peringatan sebelumnya dan sesudahnya (dengan mengatakan): "Janganlah kamu menyembah selain Allah, sesungguhnya aku khawatir kamu akan ditimpa azab hari yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,22,'قَالُوْٓا اَجِئْتَنَا لِتَأْفِكَنَا عَنْ اٰلِهَتِنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ','Mereka menjawab: "Apakah kamu datang kepada kami untuk memalingkan kami dari (menyembah) tuhan-tuhan kami? Maka datangkanlah kepada kami azab yang telah kamu ancamkan kepada kami jika kamu termasuk orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,23,'قَالَ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاُبَلِّغُكُمْ مَّآ اُرْسِلْتُ بِهٖ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ ','Ia berkata: "Sesungguhnya pengetahuan (tentang itu) hanya pada sisi Allah dan aku (hanya) menyampaikan kepadamu apa yang aku diutus dengan membawanya tetapi aku lihat kamu adalah kaum yang bodoh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,24,'فَلَمَّا رَاَوْهُ عَارِضًا مُّسْتَقْبِلَ اَوْدِيَتِهِمْ قَالُوْا هٰذَا عَارِضٌ مُّمْطِرُنَا ۗبَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِهٖ ۗرِيْحٌ فِيْهَا عَذَابٌ اَلِيْمٌۙ','Maka tatkala mereka melihat azab itu berupa awan yang menuju ke lembah-lembah mereka, berkatalah mereka: "Inilah awan yang akan menurunkan hujan kepada kami". (Bukan!) bahkan itulah azab yang kamu minta supaya datang dengan segera (yaitu) angin yang mengandung azab yang pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,25,'تُدَمِّرُ كُلَّ شَيْءٍۢ بِاَمْرِ رَبِّهَا فَاَصْبَحُوْا لَا يُرٰىٓ اِلَّا مَسٰكِنُهُمْۗ  كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ ','yang menghancurkan segala sesuatu dengan perintah Tuhannya, maka jadilah mereka tidak ada yang kelihatan lagi kecuali (bekas-bekas) tempat tinggal mereka. Demikianlah Kami memberi balasan kepada kaum yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,26,'وَلَقَدْ مَكَّنّٰهُمْ فِيْمَآ اِنْ مَّكَّنّٰكُمْ فِيْهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَّاَبْصَارًا وَّاَفْـِٕدَةًۖ فَمَآ اَغْنٰى عَنْهُمْ سَمْعُهُمْ وَلَآ اَبْصَارُهُمْ وَلَآ اَفْـِٕدَتُهُمْ مِّنْ شَيْءٍ اِذْ كَانُوْا يَجْحَدُوْنَ بِاٰيٰتِ اللّٰهِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ࣖ','Dan sesungguhnya Kami telah meneguhkan kedudukan mereka dalam hal-hal yang Kami belum pernah meneguhkan kedudukanmu dalam hal itu dan Kami telah memberikan kepada mereka pendengaran, penglihatan dan hati; tetapi pendengaran, penglihatan dan hati mereka itu tidak berguna sedikit juapun bagi mereka, karena mereka selalu mengingkari ayat-ayat Allah dan mereka telah diliputi oleh siksa yang dahulu selalu mereka memperolok-olokkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,27,'وَلَقَدْ اَهْلَكْنَا مَا حَوْلَكُمْ مِّنَ الْقُرٰى وَصَرَّفْنَا الْاٰيٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ','Dan sesungguhnya Kami telah membinasakan negeri-negeri di sekitarmu dan Kami telah mendatangkan tanda-tanda kebesaran Kami berulang-ulang supaya mereka kembali (bertaubat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,28,'فَلَوْلَا نَصَرَهُمُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ قُرْبَانًا اٰلِهَةً  ۗبَلْ ضَلُّوْا عَنْهُمْۚ وَذٰلِكَ اِفْكُهُمْ وَمَا كَانُوْا يَفْتَرُوْنَ ','Maka mengapa yang mereka sembah selain Allah sebagai Tuhan untuk mendekatkan diri (kepada Allah) tidak dapat menolong mereka. Bahkan tuhan-tuhan itu telah lenyap dari mereka? Itulah akibat kebohongan mereka dan apa yang dahulu mereka ada-adakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,29,'وَاِذْ صَرَفْنَآ اِلَيْكَ نَفَرًا مِّنَ الْجِنِّ يَسْتَمِعُوْنَ الْقُرْاٰنَۚ فَلَمَّا حَضَرُوْهُ قَالُوْٓا اَنْصِتُوْاۚ فَلَمَّا قُضِيَ وَلَّوْا اِلٰى قَوْمِهِمْ مُّنْذِرِيْنَ ','Dan (ingatlah) ketika Kami hadapkan serombongan jin kepadamu yang mendengarkan Al Quran, maka tatkala mereka menghadiri pembacaan(nya) lalu mereka berkata: "Diamlah kamu (untuk mendengarkannya)". Ketika pembacaan telah selesai mereka kembali kepada kaumnya (untuk) memberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,30,'قَالُوْا يٰقَوْمَنَآ اِنَّا سَمِعْنَا كِتٰبًا اُنْزِلَ مِنْۢ بَعْدِ مُوْسٰى مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ يَهْدِيْٓ اِلَى الْحَقِّ وَاِلٰى طَرِيْقٍ مُّسْتَقِيْمٍ ','Mereka berkata: "Hai kaum kami, sesungguhnya kami telah mendengarkan kitab (Al Quran) yang telah diturunkan sesudah Musa yang membenarkan kitab-kitab yang sebelumnya lagi memimpin kepada kebenaran dan kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,31,'يٰقَوْمَنَآ اَجِيْبُوْا دَاعِيَ اللّٰهِ وَاٰمِنُوْا بِهٖ يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُجِرْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ ','Hai kaum kami, terimalah (seruan) orang yang menyeru kepada Allah dan berimanlah kepada-Nya, niscaya Allah akan mengampuni dosa-dosa kamu dan melepaskan kamu dari azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,32,'وَمَنْ لَّا يُجِبْ دَاعِيَ اللّٰهِ فَلَيْسَ بِمُعْجِزٍ فِى الْاَرْضِ وَلَيْسَ لَهٗ مِنْ دُوْنِهٖٓ اَوْلِيَاۤءُ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Dan orang yang tidak menerima (seruan) orang yang menyeru kepada Allah maka dia tidak akan melepaskan diri dari azab Allah di muka bumi dan tidak ada baginya pelindung selain Allah. Mereka itu dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,33,'اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى  ۗبَلٰٓى اِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan apakah mereka tidak memperhatikan bahwa sesungguhnya Allah yang menciptakan langit dan bumi dan Dia tidak merasa payah karena menciptakannya, kuasa menghidupkan orang-orang mati? Ya (bahkan) sesungguhnya Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,34,'وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ  اَلَيْسَ هٰذَا بِالْحَقِّ ۗ قَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ','Dan (ingatlah) hari (ketika) orang-orang kafir dihadapkan kepada neraka, (dikatakan kepada mereka): "Bukankah (azab) ini benar?" Mereka menjawab: "Ya benar, demi Tuhan kami". Allah berfirman "Maka rasakanlah azab ini disebabkan kamu selalu ingkar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,46,35,'فَاصْبِرْ كَمَا صَبَرَ اُولُوا الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِلْ لَّهُمْ ۗ كَاَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوْعَدُوْنَۙ  لَمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنْ نَّهَارٍ ۗ بَلٰغٌ ۚفَهَلْ يُهْلَكُ اِلَّا الْقَوْمُ الْفٰسِقُوْنَ ࣖ','Maka bersabarlah kamu seperti orang-orang yang mempunyai keteguhan hati dari rasul-rasul telah bersabar dan janganlah kamu meminta disegerakan (azab) bagi mereka. Pada hari mereka melihat azab yang diancamkan kepada mereka (merasa) seolah-olah tidak tinggal (di dunia) melainkan sesaat pada siang hari. (Inilah) suatu pelajaran yang cukup, maka tidak dibinasakan melainkan kaum yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,1,'اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ اَضَلَّ اَعْمَالَهُمْ ','Orang-orang yang kafir dan menghalangi (manusia) dari jalan Allah, Allah menyesatkan perbuatan-perbuatan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,2,'وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاٰمَنُوْا بِمَا نُزِّلَ عَلٰى مُحَمَّدٍ وَّهُوَ الْحَقُّ مِنْ رَّبِّهِمْ ۚ كَفَّرَ عَنْهُمْ سَيِّاٰتِهِمْ وَاَصْلَحَ بَالَهُمْ ','Dan orang-orang mukmin dan beramal soleh serta beriman kepada apa yang diturunkan kepada Muhammad dan itulah yang haq dari Tuhan mereka, Allah menghapuskan kesalahan-kesalahan mereka dan memperbaiki keadaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,3,'ذٰلِكَ بِاَنَّ الَّذِيْنَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَاَنَّ الَّذِيْنَ اٰمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَّبِّهِمْ ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ لِلنَّاسِ اَمْثَالَهُمْ ','Yang demikian adalah karena sesungguhnya orang-orang kafir mengikuti yang bathil dan sesungguhnya orang-orang mukmin mengikuti yang haq dari Tuhan mereka. Demikianlah Allah membuat untuk manusia perbandingan-perbandingan bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,4,'فَاِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا فَضَرْبَ الرِّقَابِۗ حَتّٰٓى اِذَآ اَثْخَنْتُمُوْهُمْ فَشُدُّوا الْوَثَاقَۖ فَاِمَّا مَنًّاۢ بَعْدُ وَاِمَّا فِدَاۤءً حَتّٰى تَضَعَ الْحَرْبُ اَوْزَارَهَا ەۛ ذٰلِكَ ۛ وَلَوْ يَشَاۤءُ اللّٰهُ لَانْتَصَرَ مِنْهُمْ وَلٰكِنْ لِّيَبْلُوَا۟ بَعْضَكُمْ بِبَعْضٍۗ وَالَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ فَلَنْ يُّضِلَّ اَعْمَالَهُمْ ','Apabila kamu bertemu dengan orang-orang kafir (di medan perang) maka pancunglah batang leher mereka. Sehingga apabila kamu telah mengalahkan mereka maka tawanlah mereka dan sesudah itu kamu boleh membebaskan mereka atau menerima tebusan sampai perang berakhir. Demikianlah apabila Allah menghendaki niscaya Allah akan membinasakan mereka tetapi Allah hendak menguji sebahagian kamu dengan sebahagian yang lain. Dan orang-orang yang syahid pada jalan Allah, Allah tidak akan menyia-nyiakan amal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,5,'سَيَهْدِيْهِمْ وَيُصْلِحُ بَالَهُمْۚ','Allah akan memberi pimpinan kepada mereka dan memperbaiki keadaan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,6,'وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ ','dan memasukkan mereka ke dalam jannah yang telah diperkenankan-Nya kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,7,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَنْصُرُوا اللّٰهَ يَنْصُرْكُمْ وَيُثَبِّتْ اَقْدَامَكُمْ ','Hai orang-orang mukmin, jika kamu menolong (agama) Allah, niscaya Dia akan menolongmu dan meneguhkan kedudukanmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,8,'وَالَّذِيْنَ كَفَرُوْا فَتَعْسًا لَّهُمْ وَاَضَلَّ اَعْمَالَهُمْ ','Dan orang-orang yang kafir, maka kecelakaanlah bagi mereka dan Allah menyesatkan amal-amal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,9,'ذٰلِكَ بِاَنَّهُمْ كَرِهُوْا مَآ اَنْزَلَ اللّٰهُ فَاَحْبَطَ اَعْمَالَهُمْ ','Yang demikian itu adalah karena Sesungguhnya mereka benci kepada apa yang diturunkan Allah (Al Quran) lalu Allah menghapuskan (pahala-pahala) amal-amal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,10,'۞ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ دَمَّرَ اللّٰهُ عَلَيْهِمْ ۖوَلِلْكٰفِرِيْنَ اَمْثَالُهَا ','Maka apakah mereka tidak mengadakan perjalanan di muka bumi sehingga mereka dapat memperhatikan bagaimana kesudahan orang-orang sebelum mereka; Allah telah menimpakan kebinasaan atas mereka dan orang-orang kafir akan menerima (akibat-akibat) seperti itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,11,'ذٰلِكَ بِاَنَّ اللّٰهَ مَوْلَى الَّذِيْنَ اٰمَنُوْا وَاَنَّ الْكٰفِرِيْنَ لَا مَوْلٰى لَهُمْ ࣖ','Yang demikian itu karena sesungguhnya Allah adalah pelindung orang-orang yang beriman dan karena sesungguhnya orang-orang kafir itu tidak mempunyai pelindung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,12,'اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗوَالَّذِيْنَ كَفَرُوْا يَتَمَتَّعُوْنَ وَيَأْكُلُوْنَ كَمَا تَأْكُلُ الْاَنْعَامُ وَالنَّارُ مَثْوًى لَّهُمْ ','Sesungguhnya Allah memasukkan orang-orang mukmin dan beramal saleh ke dalam jannah yang mengalir di bawahnya sungai-sungai. Dan orang-orang kafir bersenang-senang (di dunia) dan mereka makan seperti makannya binatang. Dan jahannam adalah tempat tinggal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,13,'وَكَاَيِّنْ مِّنْ قَرْيَةٍ هِيَ اَشَدُّ قُوَّةً مِّنْ قَرْيَتِكَ الَّتِيْٓ اَخْرَجَتْكَۚ اَهْلَكْنٰهُمْ فَلَا نَاصِرَ لَهُمْ ','Dan betapa banyaknya negeri yang (penduduknya) lebih kuat dari pada (penduduk) negerimu (Muhammad) yang telah mengusirmu itu. Kami telah membinasakan mereka, maka tidak ada seorang penolongpun bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,14,'اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ كَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ ','Maka apakah orang yang berpegang pada keterangan yang datang dari Rabbnya sama dengan orang yang (shaitan) menjadikan dia memandang baik perbuatannya yang buruk itu dan mengikuti hawa nafsunya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,15,'مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَ ۗفِيْهَآ اَنْهٰرٌ مِّنْ مَّاۤءٍ غَيْرِ اٰسِنٍۚ وَاَنْهٰرٌ مِّنْ لَّبَنٍ لَّمْ يَتَغَيَّرْ طَعْمُهٗ ۚوَاَنْهٰرٌ مِّنْ خَمْرٍ لَّذَّةٍ لِّلشّٰرِبِيْنَ ەۚ وَاَنْهٰرٌ مِّنْ عَسَلٍ مُّصَفًّى ۗوَلَهُمْ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِ وَمَغْفِرَةٌ مِّنْ رَّبِّهِمْ ۗ كَمَنْ هُوَ خَالِدٌ فِى النَّارِ وَسُقُوْا مَاۤءً حَمِيْمًا فَقَطَّعَ اَمْعَاۤءَهُمْ','(Apakah) perumpamaan (penghuni) jannah yang dijanjikan kepada orang-orang yang bertakwa yang di dalamnya ada sungai-sungai dari air yang tiada berubah rasa dan baunya, sungai-sungai dari air susu yang tidak berubah rasanya, sungai-sungai dari khamar yang lezat rasanya bagi peminumnya dan sungai-sungai dari madu yang disaring; dan mereka memperoleh di dalamnya segala macam buah-buahan dan ampunan dari Rabb mereka, sama dengan orang yang kekal dalam jahannam dan diberi minuman dengan air yang mendidih sehingga memotong ususnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,16,'وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَۚ حَتّٰىٓ اِذَا خَرَجُوْا مِنْ عِنْدِكَ قَالُوْا لِلَّذِيْنَ اُوْتُوا الْعِلْمَ مَاذَا قَالَ اٰنِفًا ۗ اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ','Dan di antara mereka ada orang yang mendengarkan perkataanmu sehingga apabila mereka keluar dari sisimu orang-orang berkata kepada orang yang telah diberi ilmu pengetahuan (sahabat-sahabat Nabi): "Apakah yang dikatakannya tadi?" Mereka itulah orang-orang yang dikunci mati hati mereka oleh Allah dan mengikuti hawa nafsu mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,17,'وَالَّذِيْنَ اهْتَدَوْا زَادَهُمْ هُدًى وَّاٰتٰىهُمْ تَقْوٰىهُمْ ','Dan orang-orang yang mau menerima petunjuk, Allah menambah petunjuk kepada mereka dan memberikan balasan ketakwaannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,18,'فَهَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً ۚ فَقَدْ جَاۤءَ اَشْرَاطُهَا ۚ فَاَنّٰى لَهُمْ اِذَا جَاۤءَتْهُمْ ذِكْرٰىهُمْ ','Maka tidaklah yang mereka tunggu-tunggu melainkan hari kiamat (yaitu) kedatangannya kepada mereka dengan tiba-tiba, karena sesungguhnya telah datang tanda-tandanya. Maka apakah faedahnya bagi mereka kesadaran mereka itu apabila Kiamat sudah datang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,19,'فَاعْلَمْ اَنَّهٗ لَآ اِلٰهَ اِلَّا اللّٰهُ وَاسْتَغْفِرْ لِذَنْۢبِكَ وَلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۚ وَاللّٰهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوٰىكُمْ ࣖ','Maka ketahuilah, bahwa sesungguhnya tidak ada Ilah (sesembahan, tuhan) selain Allah dan mohonlah ampunan bagi dosamu dan bagi (dosa) orang-orang mukmin, laki-laki dan perempuan. Dan Allah mengetahui tempat kamu berusaha dan tempat kamu tinggal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,20,'وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْا لَوْلَا نُزِّلَتْ سُوْرَةٌ ۚفَاِذَآ اُنْزِلَتْ سُوْرَةٌ مُّحْكَمَةٌ وَّذُكِرَ فِيْهَا الْقِتَالُ ۙرَاَيْتَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يَّنْظُرُوْنَ اِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِۗ فَاَوْلٰى لَهُمْۚ ','Dan orang-orang yang beriman berkata: "Mengapa tiada diturunkan suatu surat?" Maka apabila diturunkan suatu surat yang jelas maksudnya dan disebutkan di dalamnya (perintah) perang, kamu lihat orang-orang yang ada penyakit di dalam hatinya memandang kepadamu seperti pandangan orang yang pingsan karena takut mati, dan kecelakaanlah bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,21,'طَاعَةٌ وَّقَوْلٌ مَّعْرُوْفٌۗ فَاِذَا عَزَمَ الْاَمْرُۗ فَلَوْ صَدَقُوا اللّٰهَ لَكَانَ خَيْرًا لَّهُمْۚ','Taat dan mengucapkan perkataan yang baik (adalah lebih baik bagi mereka). Apabila telah tetap perintah perang (mereka tidak menyukainya). Tetapi jikalau mereka benar (imannya) terhadap Allah, niscaya yang demikian itu lebih baik bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,22,'فَهَلْ عَسَيْتُمْ اِنْ تَوَلَّيْتُمْ اَنْ تُفْسِدُوْا فِى الْاَرْضِ وَتُقَطِّعُوْٓا اَرْحَامَكُمْ ','Maka apakah kiranya jika kamu berkuasa kamu akan membuat kerusakan di muka bumi dan memutuskan hubungan kekeluargaan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,23,'اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ فَاَصَمَّهُمْ وَاَعْمٰٓى اَبْصَارَهُمْ ','Mereka itulah orang-orang yang dilaknati Allah dan ditulikan-Nya telinga mereka dan dibutakan-Nya penglihatan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,24,'اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ اَمْ عَلٰى قُلُوْبٍ اَقْفَالُهَا ','Maka apakah mereka tidak memperhatikan Al Quran ataukah hati mereka terkunci?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,25,'اِنَّ الَّذِيْنَ ارْتَدُّوْا عَلٰٓى اَدْبَارِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَى الشَّيْطٰنُ سَوَّلَ لَهُمْۗ وَاَمْلٰى لَهُمْ ','Sesungguhnya orang-orang yang kembali ke belakang (kepada kekafiran) sesudah petunjuk itu jelas bagi mereka, syaitan telah menjadikan mereka mudah (berbuat dosa) dan memanjangkan angan-angan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,26,'ذٰلِكَ بِاَنَّهُمْ قَالُوْا لِلَّذِيْنَ كَرِهُوْا مَا نَزَّلَ اللّٰهُ سَنُطِيْعُكُمْ فِيْ بَعْضِ الْاَمْرِۚ وَاللّٰهُ يَعْلَمُ اِسْرَارَهُمْ ','Yang demikian itu karena sesungguhnya mereka (orang-orang munafik) itu berkata kepada orang-orang yang benci kepada apa yang diturunkan Allah (orang-orang Yahudi): "Kami akan mematuhi kamu dalam beberapa urusan", sedang Allah mengetahui rahasia mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,27,'فَكَيْفَ اِذَا تَوَفَّتْهُمُ الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْ ','Bagaimanakah (keadaan mereka) apabila malaikat mencabut nyawa mereka seraya memukul-mukul muka mereka dan punggung mereka?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,28,'ذٰلِكَ بِاَنَّهُمُ اتَّبَعُوْا مَآ اَسْخَطَ اللّٰهَ وَكَرِهُوْا رِضْوَانَهٗ فَاَحْبَطَ اَعْمَالَهُمْ ࣖ ','Yang demikian itu adalah karena sesungguhnya mereka mengikuti apa yang menimbulkan kemurkaan Allah dan karena mereka membenci keridhaan-Nya, sebab itu Allah menghapus (pahala) amal-amal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,29,'اَمْ حَسِبَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ اَنْ لَّنْ يُّخْرِجَ اللّٰهُ اَضْغَانَهُمْ ','Atau apakah orang-orang yang ada penyakit dalam hatinya mengira bahwa Allah tidak akan menampakkan kedengkian mereka?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,30,'وَلَوْ نَشَاۤءُ لَاَرَيْنٰكَهُمْ فَلَعَرَفْتَهُمْ بِسِيْمٰهُمْ ۗوَلَتَعْرِفَنَّهُمْ فِيْ لَحْنِ الْقَوْلِۗ وَاللّٰهُ يَعْلَمُ اَعْمَالَكُمْ ','Dan kalau Kami kehendaki, niscaya Kami tunjukkan mereka kepadamu sehingga kamu benar-benar dapat mengenal mereka dengan tanda-tandanya. Dan kamu benar-benar akan mengenal mereka dari kiasan-kiasan perkataan mereka dan Allah mengetahui perbuatan-perbuatan kamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,31,'وَلَنَبْلُوَنَّكُمْ حَتّٰى نَعْلَمَ الْمُجٰهِدِيْنَ مِنْكُمْ وَالصّٰبِرِيْنَۙ وَنَبْلُوَا۟ اَخْبَارَكُمْ ','Dan sesungguhnya Kami benar-benar akan menguji kamu agar Kami mengetahui orang-orang yang berjihad dan bersabar di antara kamu, dan agar Kami menyatakan (baik buruknya) hal ihwalmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,32,'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ وَشَاۤقُّوا الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدٰى لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۗ وَسَيُحْبِطُ اَعْمَالَهُمْ ','Sesungguhnya orang-orang kafir dan (yang) menghalangi manusia dari jalan Allah serta memusuhi Rasul setelah petunjuk itu jelas bagi mereka, mereka tidak dapat memberi mudharat kepada Allah sedikitpun. Dan Allah akan menghapuskan (pahala) amal-amal mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,33,'۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَلَا تُبْطِلُوْٓا اَعْمَالَكُمْ','Hai orang-orang yang beriman, taatilah Allah dan taatilah Rasul dan janganlah kamu merusakkan (pahala) amal-amalmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,34,'اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ثُمَّ مَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ','Sesungguhnya orang-orang kafir dan (yang) menghalangi manusia dari jalan Allah kemudian mereka mati dalam keadaan kafir, maka sekali-kali Allah tidak akan memberi ampun kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,35,'فَلَا تَهِنُوْا وَتَدْعُوْٓا اِلَى السَّلْمِۖ وَاَنْتُمُ الْاَعْلَوْنَۗ وَاللّٰهُ مَعَكُمْ وَلَنْ يَّتِرَكُمْ اَعْمَالَكُمْ ','Janganlah kamu lemah dan minta damai padahal kamulah yang di atas dan Allah pun bersamamu dan Dia sekali-kali tidak akan mengurangi pahala amal-amalmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,36,'اِنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ ۗوَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا يُؤْتِكُمْ اُجُوْرَكُمْ وَلَا يَسْـَٔلْكُمْ اَمْوَالَكُمْ ','Sesungguhnya kehidupan dunia hanyalah permainan dan senda gurau. Dan jika kamu beriman dan bertakwa, Allah akan memberikan pahala kepadamu dan Dia tidak akan meminta harta-hartamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,37,'اِنْ يَّسْـَٔلْكُمُوْهَا فَيُحْفِكُمْ تَبْخَلُوْا وَيُخْرِجْ اَضْغَانَكُمْ ','Jika Dia meminta harta kepadamu lalu mendesak kamu (supaya memberikan semuanya) niscaya kamu akan kikir dan Dia akan menampakkan kedengkianmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,47,38,'هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ تُدْعَوْنَ لِتُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِۚ فَمِنْكُمْ مَّنْ يَّبْخَلُ ۚوَمَنْ يَّبْخَلْ فَاِنَّمَا يَبْخَلُ عَنْ نَّفْسِهٖ ۗوَاللّٰهُ الْغَنِيُّ وَاَنْتُمُ الْفُقَرَاۤءُ ۗ وَاِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْۙ ثُمَّ لَا يَكُوْنُوْٓا اَمْثَالَكُمْ ࣖ ','Ingatlah, kamu ini orang-orang yang diajak untuk menafkahkan (hartamu) pada jalan Allah. Maka di antara kamu ada yang kikir, dan siapa yang kikir sesungguhnya dia hanyalah kikir terhadap dirinya sendiri. Dan Allah-lah yang Maha Kaya sedangkan kamulah orang-orang yang berkehendak (kepada-Nya); dan jika kamu berpaling niscaya Dia akan mengganti (kamu) dengan kaum yang lain; dan mereka tidak akan seperti kamu ini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,1,' اِنَّا فَتَحْنَا لَكَ فَتْحًا مُّبِيْنًاۙ','Sesungguhnya Kami telah memberikan kepadamu kemenangan yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,2,'لِّيَغْفِرَ لَكَ اللّٰهُ مَا تَقَدَّمَ مِنْ ذَنْۢبِكَ وَمَا تَاَخَّرَ وَيُتِمَّ نِعْمَتَهٗ عَلَيْكَ وَيَهْدِيَكَ صِرَاطًا مُّسْتَقِيْمًاۙ','supaya Allah memberi ampunan kepadamu terhadap dosamu yang telah lalu dan yang akan datang serta menyempurnakan nikmat-Nya atasmu dan memimpin kamu kepada jalan yang lurus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,3,'وَّيَنْصُرَكَ اللّٰهُ نَصْرًا عَزِيْزًا ','dan supaya Allah menolongmu dengan pertolongan yang kuat (banyak).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,4,'هُوَ الَّذِيْٓ اَنْزَلَ السَّكِيْنَةَ فِيْ قُلُوْبِ الْمُؤْمِنِيْنَ لِيَزْدَادُوْٓا اِيْمَانًا مَّعَ اِيْمَانِهِمْ ۗ وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًاۙ','Dialah yang telah menurunkan ketenangan ke dalam hati orang-orang mukmin supaya keimanan mereka bertambah di samping keimanan mereka (yang telah ada). Dan kepunyaan Allah-lah tentara langit dan bumi dan adalah Allah Maha Mengetahui lagi Maha Bijaksana,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,5,'لِّيُدْخِلَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَيُكَفِّرَ عَنْهُمْ سَيِّاٰتِهِمْۗ وَكَانَ ذٰلِكَ عِنْدَ اللّٰهِ فَوْزًا عَظِيْمًاۙ','supaya Dia memasukkan orang-orang mukmin laki-laki dan perempuan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya dan supaya Dia menutupi kesalahan-kesalahan mereka. Dan yang demikian itu adalah keberuntungan yang besar di sisi Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,6,'وَّيُعَذِّبَ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ الظَّاۤنِّيْنَ بِاللّٰهِ ظَنَّ السَّوْءِۗ عَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِۚ وَغَضِبَ اللّٰهُ عَلَيْهِمْ وَلَعَنَهُمْ وَاَعَدَّ لَهُمْ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا ','dan supaya Dia mengazab orang-orang munafik laki-laki dan perempuan dan orang-orang musyrik laki-laki dan perempuan yang mereka itu berprasangka buruk terhadap Allah. Mereka akan mendapat giliran (kebinasaan) yang amat buruk dan Allah memurkai dan mengutuk mereka serta menyediakan bagi mereka neraka Jahannam. Dan (neraka Jahannam) itulah sejahat-jahat tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,7,'وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا','Dan kepunyaan Allah-lah tentara langit dan bumi. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,8,'اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ','Sesungguhnya Kami mengutus kamu sebagai saksi, pembawa berita gembira dan pemberi peringatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,9,'لِّتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَتُعَزِّرُوْهُ وَتُوَقِّرُوْهُۗ وَتُسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا ','supaya kamu sekalian beriman kepada Allah dan Rasul-Nya, menguatkan (agama)Nya, membesarkan-Nya. Dan bertasbih kepada-Nya di waktu pagi dan petang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,10,'اِنَّ الَّذِيْنَ يُبَايِعُوْنَكَ اِنَّمَا يُبَايِعُوْنَ اللّٰهَ ۗيَدُ اللّٰهِ فَوْقَ اَيْدِيْهِمْ ۚ فَمَنْ نَّكَثَ فَاِنَّمَا يَنْكُثُ عَلٰى نَفْسِهٖۚ وَمَنْ اَوْفٰى بِمَا عٰهَدَ عَلَيْهُ اللّٰهَ فَسَيُؤْتِيْهِ اَجْرًا عَظِيْمًا ࣖ ','Bahwasanya orang-orang yang berjanji setia kepada kamu sesungguhnya mereka berjanji setia kepada Allah. Tangan Allah di atas tangan mereka, maka barangsiapa yang melanggar janjinya niscaya akibat ia melanggar janji itu akan menimpa dirinya sendiri dan barangsiapa menepati janjinya kepada Allah maka Allah akan memberinya pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,11,'سَيَقُوْلُ لَكَ الْمُخَلَّفُوْنَ مِنَ الْاَعْرَابِ شَغَلَتْنَآ اَمْوَالُنَا وَاَهْلُوْنَا فَاسْتَغْفِرْ لَنَا ۚيَقُوْلُوْنَ بِاَلْسِنَتِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْۗ قُلْ فَمَنْ يَّمْلِكُ لَكُمْ مِّنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ بِكُمْ ضَرًّا اَوْ اَرَادَ بِكُمْ نَفْعًا ۗبَلْ كَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرًا ','Orang-orang Badwi yang tertinggal (tidak turut ke Hudaibiyah) akan mengatakan: "Harta dan keluarga kami telah merintangi kami, maka mohonkanlah ampunan untuk kami"; mereka mengucapkan dengan lidahnya apa yang tidak ada dalam hatinya. Katakanlah: "Maka siapakah (gerangan) yang dapat menghalang-halangi kehendak Allah jika Dia menghendaki kemudharatan bagimu atau jika Dia menghendaki manfaat bagimu. Sebenarnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,12,'بَلْ ظَنَنْتُمْ اَنْ لَّنْ يَّنْقَلِبَ الرَّسُوْلُ وَالْمُؤْمِنُوْنَ اِلٰٓى اَهْلِيْهِمْ اَبَدًا وَّزُيِّنَ ذٰلِكَ فِيْ قُلُوْبِكُمْ وَظَنَنْتُمْ ظَنَّ السَّوْءِۚ وَكُنْتُمْ قَوْمًاۢ  بُوْرًا ','Tetapi kamu menyangka bahwa Rasul dan orang-orang mukmin tidak sekali-kali akan kembali kepada keluarga mereka selama-lamanya dan syaitan telah menjadikan kamu memandang baik dalam hatimu persangkaan itu, dan kamu telah menyangka dengan sangkaan yang buruk dan kamu menjadi kaum yang binasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,13,'وَمَنْ لَّمْ يُؤْمِنْۢ بِاللّٰهِ وَرَسُوْلِهٖ فَاِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَعِيْرًا ','Dan barangsiapa yang tidak beriman kepada Allah dan Rasul-Nya maka sesungguhnya Kami menyediakan untuk orang-orang yang kafir neraka yang bernyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,14,'وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا','Dan hanya kepunyaan Allah-lah kerajaan langit dan bumi. Dia memberikan ampun kepada siapa yang dikehendaki-Nya dan mengazab siapa yang dikehendaki-Nya. Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,15,'سَيَقُوْلُ الْمُخَلَّفُوْنَ اِذَا انْطَلَقْتُمْ اِلٰى مَغَانِمَ لِتَأْخُذُوْهَا ذَرُوْنَا نَتَّبِعْكُمْ ۚ يُرِيْدُوْنَ اَنْ يُّبَدِّلُوْا كَلٰمَ اللّٰهِ ۗ قُلْ لَّنْ تَتَّبِعُوْنَا كَذٰلِكُمْ قَالَ اللّٰهُ مِنْ قَبْلُ  ۖفَسَيَقُوْلُوْنَ بَلْ تَحْسُدُوْنَنَا ۗ بَلْ كَانُوْا لَا يَفْقَهُوْنَ اِلَّا قَلِيْلًا','Orang-orang Badwi yang tertinggal itu akan berkata apabila kamu berangkat untuk mengambil barang rampasan: "Biarkanlah kami, niscaya kami mengikuti kamu"; mereka hendak merubah janji Allah. Katakanlah: "Kamu sekali-kali tidak (boleh) mengikuti kami; demikian Allah telah menetapkan sebelumnya"; mereka akan mengatakan: "Sebenarnya kamu dengki kepada kami". Bahkan mereka tidak mengerti melainkan sedikit sekali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,16,'قُلْ لِّلْمُخَلَّفِيْنَ مِنَ الْاَعْرَابِ سَتُدْعَوْنَ اِلٰى قَوْمٍ اُولِيْ بَأْسٍ شَدِيْدٍ تُقَاتِلُوْنَهُمْ اَوْ يُسْلِمُوْنَ ۚ فَاِنْ تُطِيْعُوْا يُؤْتِكُمُ اللّٰهُ اَجْرًا حَسَنًا ۚ وَاِنْ تَتَوَلَّوْا كَمَا تَوَلَّيْتُمْ مِّنْ قَبْلُ يُعَذِّبْكُمْ عَذَابًا اَلِيْمًا ','Katakanlah kepada orang-orang Badwi yang tertinggal: "Kamu akan diajak untuk (memerangi) kaum yang mempunyai kekuatan yang besar, kamu akan memerangi mereka atau mereka menyerah (masuk Islam). Maka jika kamu patuhi (ajakan itu) niscaya Allah akan memberikan kepadamu pahala yang baik dan jika kamu berpaling sebagaimana kamu telah berpaling sebelumnya, niscaya Dia akan mengazab kamu dengan azab yang pedih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,17,'لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۚ وَمَنْ يَّتَوَلَّ يُعَذِّبْهُ عَذَابًا اَلِيْمًا ࣖ ','Tiada dosa atas orang-orang yang buta dan atas orang yang pincang dan atas orang yang sakit (apabila tidak ikut berperang). Dan barangsiapa yang taat kepada Allah dan Rasul-Nya; niscaya Allah akan memasukkannya ke dalam surga yang mengalir di bawahnya sungai-sungai dan barang siapa yang berpaling niscaya akan diazab-Nya dengan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,18,'۞ لَقَدْ رَضِيَ اللّٰهُ عَنِ الْمُؤْمِنِيْنَ اِذْ يُبَايِعُوْنَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا فِيْ قُلُوْبِهِمْ فَاَنْزَلَ السَّكِيْنَةَ عَلَيْهِمْ وَاَثَابَهُمْ فَتْحًا قَرِيْبًاۙ','Sesungguhnya Allah telah ridha terhadap orang-orang mukmin ketika mereka berjanji setia kepadamu di bawah pohon, maka Allah mengetahui apa yang ada dalam hati mereka lalu menurunkan ketenangan atas mereka dan memberi balasan kepada mereka dengan kemenangan yang dekat (waktunya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,19,'وَّمَغَانِمَ كَثِيْرَةً يَّأْخُذُوْنَهَا ۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا ','Serta harta rampasan yang banyak yang dapat mereka ambil. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,20,'وَعَدَكُمُ اللّٰهُ مَغَانِمَ كَثِيْرَةً تَأْخُذُوْنَهَا فَعَجَّلَ لَكُمْ هٰذِهٖ وَكَفَّ اَيْدِيَ النَّاسِ عَنْكُمْۚ وَلِتَكُوْنَ اٰيَةً لِّلْمُؤْمِنِيْنَ وَيَهْدِيَكُمْ صِرَاطًا مُّسْتَقِيْمًاۙ','Allah menjanjikan kepada kamu harta rampasan yang banyak yang dapat kamu ambil, maka disegerakan-Nya harta rampasan ini untukmu dan Dia menahan tangan manusia dari (membinasakan)mu (agar kamu mensyukuri-Nya) dan agar hal itu menjadi bukti bagi orang-orang mukmin dan agar Dia menunjuki kamu kepada jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,21,'وَّاُخْرٰى لَمْ تَقْدِرُوْا عَلَيْهَا قَدْ اَحَاطَ اللّٰهُ بِهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا ','Dan (telah menjanjikan pula kemenangan-kemenangan) yang lain (atas negeri-negeri) yang kamu belum dapat menguasainya yang sungguh Allah telah menentukan-Nya. Dan adalah Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,22,'وَلَوْ قَاتَلَكُمُ الَّذِيْنَ كَفَرُوْا لَوَلَّوُا الْاَدْبَارَ ثُمَّ لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ','Dan sekiranya orang-orang kafir itu memerangi kamu pastilah mereka berbalik melarikan diri ke belakang (kalah) kemudian mereka tiada memperoleh pelindung dan tidak (pula) penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,23,'سُنَّةَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ مِنْ قَبْلُ  ۖوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا ','Sebagai suatu sunnatullah yang telah berlaku sejak dahulu, kamu sekali-kali tiada akan menemukan perubahan bagi sunnatullah itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,24,'وَهُوَ الَّذِيْ كَفَّ اَيْدِيَهُمْ عَنْكُمْ وَاَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْۢ بَعْدِ اَنْ اَظْفَرَكُمْ عَلَيْهِمْ ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًا ','Dan Dialah yang menahan tangan mereka dari (membinasakan) kamu dan (menahan) tangan kamu dari (membinasakan) mereka di tengah kota Mekah sesudah Allah memenangkan kamu atas mereka, dan adalah Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,25,'هُمُ الَّذِيْنَ كَفَرُوْا وَصَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوْفًا اَنْ يَّبْلُغَ مَحِلَّهٗ ۚوَلَوْلَا رِجَالٌ مُّؤْمِنُوْنَ وَنِسَاۤءٌ مُّؤْمِنٰتٌ لَّمْ تَعْلَمُوْهُمْ اَنْ تَطَـُٔوْهُمْ فَتُصِيْبَكُمْ مِّنْهُمْ مَّعَرَّةٌ ۢبِغَيْرِ عِلْمٍ ۚ لِيُدْخِلَ اللّٰهُ فِيْ رَحْمَتِهٖ مَنْ يَّشَاۤءُۚ  لَوْ تَزَيَّلُوْا لَعَذَّبْنَا الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابًا اَلِيْمًا','Merekalah orang-orang yang kafir yang menghalangi kamu dari (masuk) Masjidil Haram dan menghalangi hewan korban sampai ke tempat (penyembelihan)nya. Dan kalau tidaklah karena laki-laki yang mukmin dan perempuan-perempuan yang mukmin yang tiada kamu ketahui, bahwa kamu akan membunuh mereka yang menyebabkan kamu ditimpa kesusahan tanpa pengetahuanmu (tentulah Allah tidak akan menahan tanganmu dari membinasakan mereka). Supaya Allah memasukkan siapa yang dikehendaki-Nya ke dalam rahmat-Nya. Sekiranya mereka tidak bercampur-baur, tentulah Kami akan mengazab orang-orang yag kafir di antara mereka dengan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,26,'اِذْ جَعَلَ الَّذِيْنَ كَفَرُوْا فِيْ قُلُوْبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَلْزَمَهُمْ كَلِمَةَ التَّقْوٰى وَكَانُوْٓا اَحَقَّ بِهَا وَاَهْلَهَا ۗوَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا ࣖ ','Ketika orang-orang kafir menanamkan dalam hati mereka kesombongan (yaitu) kesombongan jahiliyah lalu Allah menurunkan ketenangan kepada Rasul-Nya, dan kepada orang-orang mukmin dan Allah mewajibkan kepada mereka kalimat-takwa dan adalah mereka berhak dengan kalimat takwa itu dan patut memilikinya. Dan adalah Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,27,'لَقَدْ صَدَقَ اللّٰهُ رَسُوْلَهُ الرُّءْيَا بِالْحَقِّ ۚ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَۙ مُحَلِّقِيْنَ رُءُوْسَكُمْ وَمُقَصِّرِيْنَۙ  لَا تَخَافُوْنَ ۗفَعَلِمَ مَا لَمْ تَعْلَمُوْا فَجَعَلَ مِنْ دُوْنِ ذٰلِكَ فَتْحًا قَرِيْبًا ','Sesungguhnya Allah akan membuktikan kepada Rasul-Nya, tentang kebenaran mimpinya dengan sebenarnya (yaitu) bahwa sesungguhnya kamu pasti akan memasuki Masjidil Haram, insya Allah dalam keadaan aman, dengan mencukur rambut kepala dan mengguntingnya, sedang kamu tidak merasa takut. Maka Allah mengetahui apa yang tiada kamu ketahui dan Dia memberikan sebelum itu kemenangan yang dekat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,28,'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًا ','Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang hak agar dimenangkan-Nya terhadap semua agama. Dan cukuplah Allah sebagai saksi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,48,29,'مُحَمَّدٌ رَّسُوْلُ اللّٰهِ ۗوَالَّذِيْنَ مَعَهٗٓ اَشِدَّاۤءُ عَلَى الْكُفَّارِ رُحَمَاۤءُ بَيْنَهُمْ تَرٰىهُمْ رُكَّعًا سُجَّدًا يَّبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا ۖ سِيْمَاهُمْ فِيْ وُجُوْهِهِمْ مِّنْ اَثَرِ السُّجُوْدِ ۗذٰلِكَ مَثَلُهُمْ فِى التَّوْرٰىةِ  ۖوَمَثَلُهُمْ فِى الْاِنْجِيْلِۚ  كَزَرْعٍ اَخْرَجَ شَطْـَٔهٗ فَاٰزَرَهٗ فَاسْتَغْلَظَ فَاسْتَوٰى عَلٰى سُوْقِهٖ يُعْجِبُ الزُّرَّاعَ لِيَغِيْظَ بِهِمُ الْكُفَّارَ ۗوَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا ࣖ ','Muhammad itu adalah utusan Allah dan orang-orang yang bersama dengan dia adalah keras terhadap orang-orang kafir, tetapi berkasih sayang sesama mereka. Kamu lihat mereka ruku´ dan sujud mencari karunia Allah dan keridhaan-Nya, tanda-tanda mereka tampak pada muka mereka dari bekas sujud. Demikianlah sifat-sifat mereka dalam Taurat dan sifat-sifat mereka dalam Injil, yaitu seperti tanaman yang mengeluarkan tunasnya maka tunas itu menjadikan tanaman itu kuat lalu menjadi besarlah dia dan tegak lurus di atas pokoknya; tanaman itu menyenangkan hati penanam-penanamnya karena Allah hendak menjengkelkan hati orang-orang kafir (dengan kekuatan orang-orang mukmin). Allah menjanjikan kepada orang-orang yang beriman dan mengerjakan amal yang saleh di antara mereka ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,1,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُقَدِّمُوْا بَيْنَ يَدَيِ اللّٰهِ وَرَسُوْلِهٖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ','Hai orang-orang yang beriman, janganlah kamu mendahului Allah dan Rasulnya dan bertakwalah kepada Allah. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,2,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَرْفَعُوْٓا اَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوْا لَهٗ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ اَنْ تَحْبَطَ اَعْمَالُكُمْ وَاَنْتُمْ لَا تَشْعُرُوْنَ ','Hai orang-orang yang beriman, janganlah kamu meninggikan suaramu melebihi suara Nabi, dan janganlah kamu berkata kepadanya dengan suara yang keras, sebagaimana kerasnya suara sebagian kamu terhadap sebagian yang lain, supaya tidak hapus (pahala) amalanmu, sedangkan kamu tidak menyadari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,3,'اِنَّ الَّذِيْنَ يَغُضُّوْنَ اَصْوَاتَهُمْ عِنْدَ رَسُوْلِ اللّٰهِ اُولٰۤىِٕكَ الَّذِيْنَ امْتَحَنَ اللّٰهُ قُلُوْبَهُمْ لِلتَّقْوٰىۗ  لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ ','Sesungguhnya orang yang merendahkan suaranya di sisi Rasulullah mereka itulah orang-orang yang telah diuji hati mereka oleh Allah untuk bertakwa. Bagi mereka ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,4,'اِنَّ الَّذِيْنَ يُنَادُوْنَكَ مِنْ وَّرَاۤءِ الْحُجُرٰتِ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ ','Sesungguhnya orang-orang yang memanggil kamu dari luar kamar(mu) kebanyakan mereka tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,5,'وَلَوْ اَنَّهُمْ صَبَرُوْا حَتّٰى تَخْرُجَ اِلَيْهِمْ لَكَانَ خَيْرًا لَّهُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ','Dan kalau sekiranya mereka bersabar sampai kamu keluar menemui mereka sesungguhnya itu lebih baik bagi mereka, dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,6,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ جَاۤءَكُمْ فَاسِقٌۢ بِنَبَاٍ فَتَبَيَّنُوْٓا اَنْ تُصِيْبُوْا قَوْمًاۢ بِجَهَالَةٍ فَتُصْبِحُوْا عَلٰى مَا فَعَلْتُمْ نٰدِمِيْنَ ','Hai orang-orang yang beriman, jika datang kepadamu orang fasik membawa suatu berita, maka periksalah dengan teliti agar kamu tidak menimpakan suatu musibah kepada suatu kaum tanpa mengetahui keadaannya yang menyebabkan kamu menyesal atas perbuatanmu itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,7,'وَاعْلَمُوْٓا اَنَّ فِيْكُمْ رَسُوْلَ اللّٰهِ ۗ لَوْ يُطِيْعُكُمْ فِيْ كَثِيْرٍ مِّنَ الْاَمْرِ لَعَنِتُّمْ وَلٰكِنَّ اللّٰهَ حَبَّبَ اِلَيْكُمُ الْاِيْمَانَ وَزَيَّنَهٗ فِيْ قُلُوْبِكُمْ وَكَرَّهَ اِلَيْكُمُ الْكُفْرَ وَالْفُسُوْقَ وَالْعِصْيَانَ ۗ اُولٰۤىِٕكَ هُمُ الرَّاشِدُوْنَۙ','Dan ketahuilah olehmu bahwa di kalanganmu ada Rasulullah. Kalau ia menuruti kemauanmu dalam beberapa urusan benar-benarlah kamu mendapat kesusahan, tetapi Allah menjadikan kamu "cinta" kepada keimanan dan menjadikan keimanan itu indah di dalam hatimu serta menjadikan kamu benci kepada kekafiran, kefasikan, dan kedurhakaan. Mereka itulah orang-orang yang mengikuti jalan yang lurus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,8,'فَضْلًا مِّنَ اللّٰهِ وَنِعْمَةً ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','sebagai karunia dan nikmat dari Allah. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,9,'وَاِنْ طَاۤىِٕفَتٰنِ مِنَ الْمُؤْمِنِيْنَ اقْتَتَلُوْا فَاَصْلِحُوْا بَيْنَهُمَاۚ فَاِنْۢ بَغَتْ اِحْدٰىهُمَا عَلَى الْاُخْرٰى فَقَاتِلُوا الَّتِيْ تَبْغِيْ حَتّٰى تَفِيْۤءَ اِلٰٓى اَمْرِ اللّٰهِ ۖفَاِنْ فَاۤءَتْ فَاَصْلِحُوْا بَيْنَهُمَا بِالْعَدْلِ وَاَقْسِطُوْا ۗاِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ ','Dan kalau ada dua golongan dari mereka yang beriman itu berperang hendaklah kamu damaikan antara keduanya! Tapi kalau yang satu melanggar perjanjian terhadap yang lain, hendaklah yang melanggar perjanjian itu kamu perangi sampai surut kembali pada perintah Allah. Kalau dia telah surut, damaikanlah antara keduanya menurut keadilan, dan hendaklah kamu berlaku adil; sesungguhnya Allah mencintai orang-orang yang berlaku adil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,10,'اِنَّمَا الْمُؤْمِنُوْنَ اِخْوَةٌ فَاَصْلِحُوْا بَيْنَ اَخَوَيْكُمْ  وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ ࣖ ','Orang-orang beriman itu sesungguhnya bersaudara. Sebab itu damaikanlah (perbaikilah hubungan) antara kedua saudaramu itu dan takutlah terhadap Allah, supaya kamu mendapat rahmat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,11,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَسْخَرْ قَوْمٌ مِّنْ قَوْمٍ عَسٰٓى اَنْ يَّكُوْنُوْا خَيْرًا مِّنْهُمْ وَلَا نِسَاۤءٌ مِّنْ نِّسَاۤءٍ عَسٰٓى اَنْ يَّكُنَّ خَيْرًا مِّنْهُنَّۚ وَلَا تَلْمِزُوْٓا اَنْفُسَكُمْ وَلَا تَنَابَزُوْا بِالْاَلْقَابِۗ بِئْسَ الِاسْمُ الْفُسُوْقُ بَعْدَ الْاِيْمَانِۚ وَمَنْ لَّمْ يَتُبْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ','Hai orang-orang yang beriman, janganlah sekumpulan orang laki-laki merendahkan kumpulan yang lain, boleh jadi yang ditertawakan itu lebih baik dari mereka. Dan jangan pula sekumpulan perempuan merendahkan kumpulan lainnya, boleh jadi yang direndahkan itu lebih baik. Dan janganlah suka mencela dirimu sendiri dan jangan memanggil dengan gelaran yang mengandung ejekan. Seburuk-buruk panggilan adalah (panggilan) yang buruk sesudah iman dan barangsiapa yang tidak bertobat, maka mereka itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,12,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اجْتَنِبُوْا كَثِيْرًا مِّنَ الظَّنِّۖ اِنَّ بَعْضَ الظَّنِّ اِثْمٌ وَّلَا تَجَسَّسُوْا وَلَا يَغْتَبْ بَّعْضُكُمْ بَعْضًاۗ  اَيُحِبُّ اَحَدُكُمْ اَنْ يَّأْكُلَ لَحْمَ اَخِيْهِ مَيْتًا فَكَرِهْتُمُوْهُۗ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ تَوَّابٌ رَّحِيْمٌ','Hai orang-orang yang beriman, jauhilah kebanyakan purba-sangka (kecurigaan), karena sebagian dari purba-sangka itu dosa. Dan janganlah mencari-cari keburukan orang dan janganlah menggunjingkan satu sama lain. Adakah seorang diantara kamu yang suka memakan daging saudaranya yang sudah mati? Maka tentulah kamu merasa jijik kepadanya. Dan bertakwalah kepada Allah. Sesungguhnya Allah Maha Penerima Taubat lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,13,'يٰٓاَيُّهَا النَّاسُ اِنَّا خَلَقْنٰكُمْ مِّنْ ذَكَرٍ وَّاُنْثٰى وَجَعَلْنٰكُمْ شُعُوْبًا وَّقَبَاۤىِٕلَ لِتَعَارَفُوْا ۚ اِنَّ اَكْرَمَكُمْ عِنْدَ اللّٰهِ اَتْقٰىكُمْ ۗاِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ ','Hai manusia, sesungguhnya Kami menciptakan kamu dari seorang laki-laki dan seorang perempuan dan menjadikan kamu berbangsa-bangsa dan bersuku-suku supaya kamu saling kenal-mengenal. Sesungguhnya orang yang paling mulia diantara kamu disisi Allah ialah orang yang paling takwa diantara kamu. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,14,'۞ قَالَتِ الْاَعْرَابُ اٰمَنَّا ۗ قُلْ لَّمْ تُؤْمِنُوْا وَلٰكِنْ قُوْلُوْٓا اَسْلَمْنَا وَلَمَّا يَدْخُلِ الْاِيْمَانُ فِيْ قُلُوْبِكُمْ ۗوَاِنْ تُطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ لَا يَلِتْكُمْ مِّنْ اَعْمَالِكُمْ شَيْـًٔا ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Orang-orang Arab Badui itu berkata: "Kami telah beriman". Katakanlah: "Kamu belum beriman, tapi katakanlah ´kami telah tunduk´, karena iman itu belum masuk ke dalam hatimu; dan jika kamu taat kepada Allah dan Rasul-Nya, Dia tidak akan mengurangi sedikitpun pahala amalanmu; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,15,'اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ ثُمَّ لَمْ يَرْتَابُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَ ','Sesungguhnya orang-orang yang beriman itu hanyalah orang-orang yang percaya (beriman) kepada Allah dan Rasul-Nya, kemudian mereka tidak ragu-ragu dan mereka berjuang (berjihad) dengan harta dan jiwa mereka pada jalan Allah. Mereka itulah orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,16,'قُلْ اَتُعَلِّمُوْنَ اللّٰهَ بِدِيْنِكُمْۗ وَاللّٰهُ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Katakanlah: "Apakah kamu akan memberitahukan kepada Allah tentang agamamu, padahal Allah mengetahui apa yang di langit dan apa yang di bumi dan Allah Maha Mengetahui segala sesuatu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,17,'يَمُنُّوْنَ عَلَيْكَ اَنْ اَسْلَمُوْا ۗ قُلْ لَّا تَمُنُّوْا عَلَيَّ اِسْلَامَكُمْ ۚبَلِ اللّٰهُ يَمُنُّ عَلَيْكُمْ اَنْ هَدٰىكُمْ لِلْاِيْمَانِ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Mereka merasa telah memberi nikmat kepadamu dengan keislaman mereka. Katakanlah: "Janganlah kamu merasa telah memberi nikmat kepadaku dengan keislamanmu, sebenarnya Allah, Dialah yang melimpahkan nikmat kepadamu dengan menunjuki kamu kepada keimanan jika kamu adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,49,18,'اِنَّ اللّٰهَ يَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا تَعْمَلُوْنَ ࣖ ','Sesungguhnya Allah mengetahui apa yang ghaib di langit dan bumi. Dan Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,1,' قۤ  ۗوَالْقُرْاٰنِ الْمَجِيْدِ  ۖ ','Qaaf Demi Al Quran yang sangat mulia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,2,'بَلْ عَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ فَقَالَ الْكٰفِرُوْنَ هٰذَا شَيْءٌ عَجِيْبٌ ۚ','(Mereka tidak menerimanya) bahkan mereka tercengang karena telah datang kepada mereka seorang pemberi peringatan dari (kalangan) mereka sendiri, maka berkatalah orang-orang kafir: "Ini adalah suatu yang amat ajaib".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,3,'ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا ۚ ذٰلِكَ رَجْعٌۢ بَعِيْدٌ ','Apakah kami setelah mati dan setelah menjadi tanah (kami akan kembali lagi)?, itu adalah suatu pengembalian yang tidak mungkin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,4,'قَدْ عَلِمْنَا مَا تَنْقُصُ الْاَرْضُ مِنْهُمْ ۚوَعِنْدَنَا كِتٰبٌ حَفِيْظٌ ','Sesungguhnya Kami telah mengetahui apa yang dihancurkan oleh bumi dari (tubuh-tubuh) mereka, dan pada sisi Kamipun ada kitab yang memelihara (mencatat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,5,'بَلْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْ فَهُمْ فِيْٓ اَمْرٍ مَّرِيْجٍ ','Sebenarnya, mereka telah mendustakan kebenaran tatkala kebenaran itu datang kepada mereka, maka mereka berada dalam keadaan kacau balau.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,6,'اَفَلَمْ يَنْظُرُوْٓا اِلَى السَّمَاۤءِ فَوْقَهُمْ كَيْفَ بَنَيْنٰهَا وَزَيَّنّٰهَا وَمَا لَهَا مِنْ فُرُوْجٍ ','Maka apakah mereka tidak melihat akan langit yang ada di atas mereka, bagaimana Kami meninggikannya dan menghiasinya dan langit itu tidak mempunyai retak-retak sedikitpun?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,7,'وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍۙ ','Dan Kami hamparkan bumi itu dan Kami letakkan padanya gunung-gunung yang kokoh dan Kami tumbuhkan padanya segala macam tanaman yang indah dipandang mata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,8,'تَبْصِرَةً وَّذِكْرٰى لِكُلِّ عَبْدٍ مُّنِيْبٍ ','untuk menjadi pelajaran dan peringatan bagi tiap-tiap hamba yang kembali (mengingat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,9,'وَنَزَّلْنَا مِنَ السَّمَاۤءِ مَاۤءً مُّبٰرَكًا فَاَنْۢبَتْنَا بِهٖ جَنّٰتٍ وَّحَبَّ الْحَصِيْدِۙ','Dan Kami turunkan dari langit air yang banyak manfaatnya lalu Kami tumbuhkan dengan air itu pohon-pohon dan biji-biji tanaman yang diketam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,10,'وَالنَّخْلَ بٰسِقٰتٍ لَّهَا طَلْعٌ نَّضِيْدٌۙ','dan pohon kurma yang tinggi-tinggi yang mempunyai mayang yang bersusun-susun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,11,'رِّزْقًا لِّلْعِبَادِۙ وَاَحْيَيْنَا بِهٖ بَلْدَةً مَّيْتًاۗ  كَذٰلِكَ الْخُرُوْجُ  ','untuk menjadi rezeki bagi hamba-hamba (Kami), dan Kami hidupkan dengan air itu tanah yang mati (kering). Seperti itulah terjadinya kebangkitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,12,'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّاَصْحٰبُ الرَّسِّ وَثَمُوْدُ ','Sebelum mereka telah mendustakan (pula) kaum Nuh dan penduduk Rass dan Tsamud,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,13,'وَعَادٌ وَّفِرْعَوْنُ وَاِخْوَانُ لُوْطٍۙ','dan kaum Aad, kaum Fir´aun dan kaum Luth,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,14,'وَّاَصْحٰبُ الْاَيْكَةِ وَقَوْمُ تُبَّعٍۗ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيْدِ','dan penduduk Aikah serta kaum Tubba´ semuanya telah mendustakan rasul-rasul maka sudah semestinyalah mereka mendapat hukuman yang sudah diancamkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,15,'اَفَعَيِيْنَا بِالْخَلْقِ الْاَوَّلِۗ بَلْ هُمْ فِيْ لَبْسٍ مِّنْ خَلْقٍ جَدِيْدٍ ࣖ','Maka apakah Kami letih dengan penciptaan yang pertama? Sebenarnya mereka dalam keadaan ragu-ragu tentang penciptaan yang baru.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,16,'وَلَقَدْ خَلَقْنَا الْاِنْسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهٖ نَفْسُهٗ ۖوَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْ حَبْلِ الْوَرِيْدِ ','Dan sesungguhnya Kami telah menciptakan manusia dan mengetahui apa yang dibisikkan oleh hatinya, dan Kami lebih dekat kepadanya daripada urat lehernya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,17,'اِذْ يَتَلَقَّى الْمُتَلَقِّيٰنِ عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ قَعِيْدٌ ','(yaitu) ketika dua orang malaikat mencatat amal perbuatannya, seorang duduk di sebelah kanan dan yang lain duduk di sebelah kiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,18,'مَا يَلْفِظُ مِنْ قَوْلٍ اِلَّا لَدَيْهِ رَقِيْبٌ عَتِيْدٌ ','Tiada suatu ucapanpun yang diucapkannya melainkan ada di dekatnya malaikat pengawas yang selalu hadir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,19,'وَجَاۤءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ۗذٰلِكَ مَا كُنْتَ مِنْهُ تَحِيْدُ','Dan datanglah sakaratul maut dengan sebenar-benarnya. Itulah yang kamu selalu lari daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,20,'وَنُفِخَ فِى الصُّوْرِۗ ذٰلِكَ يَوْمُ الْوَعِيْدِ ','Dan ditiuplah sangkakala. Itulah hari terlaksananya ancaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,21,'وَجَاۤءَتْ كُلُّ نَفْسٍ مَّعَهَا سَاۤىِٕقٌ وَّشَهِيْدٌ ','Dan datanglah tiap-tiap diri, bersama dengan dia seorang malaikat penggiring dan seorang malaikat penyaksi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,22,'لَقَدْ كُنْتَ فِيْ غَفْلَةٍ مِّنْ هٰذَا فَكَشَفْنَا عَنْكَ غِطَاۤءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيْدٌ ','Sesungguhnya kamu berada dalam keadaan lalai dari (hal) ini, maka Kami singkapkan daripadamu tutup (yang menutupi) matamu, maka penglihatanmu pada hari itu amat tajam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,23,'وَقَالَ قَرِيْنُهٗ هٰذَا مَا لَدَيَّ عَتِيْدٌۗ','Dan yang menyertai dia berkata: "Inilah (catatan amalnya) yang tersedia pada sisiku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,24,'اَلْقِيَا فِيْ جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيْدٍ ','Allah berfirman: "Lemparkanlah olehmu berdua ke dalam neraka semua orang yang sangat ingkar dan keras kepala,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,25,'مَنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ مُّرِيْبٍۙ','yang sangat menghalangi kebajikan, melanggar batas lagi ragu-ragu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,26,' ۨالَّذِيْ جَعَلَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَاَلْقِيٰهُ فِى الْعَذَابِ الشَّدِيْدِ ','yang menyembah sembahan yang lain beserta Allah maka lemparkanlah dia ke dalam siksaan yang sangat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,27,'۞ قَالَ قَرِيْنُهٗ رَبَّنَا مَآ اَطْغَيْتُهٗ وَلٰكِنْ كَانَ فِيْ ضَلٰلٍۢ بَعِيْدٍ ','Yang menyertai dia berkata (pula): "Ya Tuhan kami, aku tidak menyesatkannya tetapi dialah yang berada dalam kesesatan yang jauh".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,28,'قَالَ لَا تَخْتَصِمُوْا لَدَيَّ وَقَدْ قَدَّمْتُ اِلَيْكُمْ بِالْوَعِيْدِ ','Allah berfirman: "Janganlah kamu bertengkar di hadapan-Ku, padahal sesungguhnya Aku dahulu telah memberikan ancaman kepadamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,29,'مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَآ اَنَا۠ بِظَلَّامٍ لِّلْعَبِيْدِ ࣖ ','Keputusan di sisi-Ku tidak dapat diubah dan Aku sekali-kali tidak menganiaya hamba-hamba-Ku')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,30,'يَوْمَ نَقُوْلُ لِجَهَنَّمَ هَلِ امْتَلَـْٔتِ وَتَقُوْلُ هَلْ مِنْ مَّزِيْدٍ ','(Dan ingatlah akan) hari (yang pada hari itu) Kami bertanya kepada jahannam: "Apakah kamu sudah penuh?" Dia menjawab: "Masih ada tambahan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,31,'وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ غَيْرَ بَعِيْدٍ ','Dan didekatkanlah surga itu kepada orang-orang yang bertakwa pada tempat yang tiada jauh (dari mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,32,'هٰذَا مَا تُوْعَدُوْنَ لِكُلِّ اَوَّابٍ حَفِيْظٍۚ','Inilah yang dijanjikan kepadamu, (yaitu) kepada setiap hamba yang selalu kembali (kepada Allah) lagi memelihara (semua peraturan-peraturan-Nya)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,33,'مَنْ خَشِيَ الرَّحْمٰنَ بِالْغَيْبِ وَجَاۤءَ بِقَلْبٍ مُّنِيْبٍۙ','(Yaitu) orang yang takut kepada Tuhan Yang Maha Pemurah sedang Dia tidak kelihatan (olehnya) dan dia datang dengan hati yang bertaubat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,34,' ۨادْخُلُوْهَا بِسَلٰمٍ ۗذٰلِكَ يَوْمُ الْخُلُوْدِ ','masukilah surga itu dengan aman, itulah hari kekekalan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,35,'لَهُمْ مَّا يَشَاۤءُوْنَ فِيْهَا وَلَدَيْنَا مَزِيْدٌ ','Mereka di dalamnya memperoleh apa yang mereka kehendaki; dan pada sisi Kami ada tambahannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,36,'وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَشَدُّ مِنْهُمْ بَطْشًا فَنَقَّبُوْا فِى الْبِلَادِۗ هَلْ مِنْ مَّحِيْصٍ ','Dan berapa banyaknya umat-umat yang telah Kami binasakan sebelum mereka yang mereka itu lebih besar kekuatannya daripada mereka ini, maka mereka (yang telah dibinasakan itu) telah pernah menjelajah di beberapa negeri. Adakah (mereka) mendapat tempat lari (dari kebinasaan)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,37,'اِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِمَنْ كَانَ لَهٗ قَلْبٌ اَوْ اَلْقَى السَّمْعَ وَهُوَ شَهِيْدٌ ','Sesungguhnya pada yang demikian itu benar-benar terdapat peringatan bagi orang-orang yang mempunyai akal atau yang menggunakan pendengarannya, sedang dia menyaksikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,38,'وَلَقَدْ خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍۖ وَّمَا مَسَّنَا مِنْ لُّغُوْبٍ ','Dan sesungguhnya telah Kami ciptakan langit dan bumi dan apa yang ada antara keduanya dalam enam masa, dan Kami sedikitpun tidak ditimpa keletihan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,39,'فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ الْغُرُوْبِ ','Maka bersabarlah kamu terhadap apa yang mereka katakan dan bertasbihlah sambil memuji Tuhanmu sebelum terbit matahari dan sebelum terbenam(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,40,'وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاَدْبَارَ السُّجُوْدِ ','Dan bertasbihlah kamu kepada-Nya di malam hari dan setiap selesai sembahyang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,41,'وَاسْتَمِعْ يَوْمَ يُنَادِ الْمُنَادِ مِنْ مَّكَانٍ قَرِيْبٍ ','Dan dengarkanlah (seruan) pada hari penyeru (malaikat) menyeru dari tempat yang dekat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,42,'يَوْمَ يَسْمَعُوْنَ الصَّيْحَةَ بِالْحَقِّ ۗذٰلِكَ يَوْمُ الْخُرُوْجِ ','(Yaitu) pada hari mereka mendengar teriakan dengan sebenar-benarnya itulah hari ke luar (dari kubur).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,43,'اِنَّا نَحْنُ نُحْيٖ وَنُمِيْتُ وَاِلَيْنَا الْمَصِيْرُۙ','Sesungguhnya Kami menghidupkan dan mematikan dan hanya kepada Kami-lah tempat kembali (semua makhluk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,44,'يَوْمَ تَشَقَّقُ الْاَرْضُ عَنْهُمْ سِرَاعًا ۗذٰلِكَ حَشْرٌ عَلَيْنَا يَسِيْرٌ ','(Yaitu) pada hari bumi terbelah-belah menampakkan mereka (lalu mereka ke luar) dengan cepat. Yang demikian itu adalah pengumpulan yang mudah bagi Kami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,50,45,'نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ وَمَآ اَنْتَ عَلَيْهِمْ بِجَبَّارٍۗ فَذَكِّرْ بِالْقُرْاٰنِ مَنْ يَّخَافُ وَعِيْدِ ࣖ ','Kami lebih mengetahui tentang apa yang mereka katakan, dan kamu sekali-kali bukanlah seorang pemaksa terhadap mereka. Maka beri peringatanlah dengan Al Quran orang yang takut dengan ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,1,' وَالذّٰرِيٰتِ ذَرْوًاۙ','Demi (angin) yang menerbangkan debu dengan kuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,2,'فَالْحٰمِلٰتِ وِقْرًاۙ','dan awan yang mengandung hujan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,3,'فَالْجٰرِيٰتِ يُسْرًاۙ','dan kapal-kapal yang berlayar dengan mudah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,4,'فَالْمُقَسِّمٰتِ اَمْرًاۙ','dan (malaikat-malaikat) yang membagi-bagi urusan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,5,'اِنَّمَا تُوْعَدُوْنَ لَصَادِقٌۙ','sesungguhnya apa yang dijanjikan kepadamu pasti benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,6,'وَّاِنَّ الدِّيْنَ لَوَاقِعٌۗ','dan sesungguhnya (hari) pembalasan pasti terjadi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,7,'وَالسَّمَاۤءِ ذَاتِ الْحُبُكِۙ','Demi langit yang mempunyai jalan-jalan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,8,'اِنَّكُمْ لَفِيْ قَوْلٍ مُّخْتَلِفٍۙ','sesungguhnya kamu benar-benar dalam keadaan berbeda pendapat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,9,'يُّؤْفَكُ عَنْهُ مَنْ اُفِكَۗ','dipalingkan daripadanya (Rasul dan Al-Quran) orang yang dipalingkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,10,'قُتِلَ الْخَرَّاصُوْنَۙ','Terkutuklah orang-orang yang banyak berdusta,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,11,'الَّذِيْنَ هُمْ فِيْ غَمْرَةٍ سَاهُوْنَۙ','(yaitu) orang-orang yang terbenam dalam kebodohan yang lalai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,12,'يَسْـَٔلُوْنَ اَيَّانَ يَوْمُ الدِّيْنِۗ','mereka bertanya: "Bilakah hari pembalasan itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,13,'يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُوْنَ ','(Hari pembalasan itu) ialah pada hari ketika mereka diazab di atas api neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,14,'ذُوْقُوْا فِتْنَتَكُمْۗ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ ','(Dikatakan kepada mereka): "Rasakanlah azabmu itu. Inilah azab yang dulu kamu minta untuk disegerakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,15,'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۙ','Sesungguhnya orang-orang yang bertakwa itu berada dalam taman-taman (surga) dan mata air-mata air,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,16,'اٰخِذِيْنَ مَآ اٰتٰىهُمْ رَبُّهُمْ ۗ اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُحْسِنِيْنَۗ','sambil menerima segala pemberian Rabb mereka. Sesungguhnya mereka sebelum itu di dunia adalah orang-orang yang berbuat kebaikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,17,'كَانُوْا قَلِيْلًا مِّنَ الَّيْلِ مَا يَهْجَعُوْنَ ','Di dunia mereka sedikit sekali tidur diwaktu malam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,18,'وَبِالْاَسْحَارِ هُمْ يَسْتَغْفِرُوْنَ ','Dan selalu memohonkan ampunan diwaktu pagi sebelum fajar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,19,'وَفِيْٓ اَمْوَالِهِمْ حَقٌّ لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِ ','Dan pada harta-harta mereka ada hak untuk orang miskin yang meminta dan orang miskin yang tidak mendapat bagian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,20,'وَفِى الْاَرْضِ اٰيٰتٌ لِّلْمُوْقِنِيْنَۙ','Dan di bumi itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang yakin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,21,'وَفِيْٓ اَنْفُسِكُمْ ۗ اَفَلَا تُبْصِرُوْنَ ','dan (juga) pada dirimu sendiri. Maka apakah kamu tidak memperhatikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,22,'وَفِى السَّمَاۤءِ رِزْقُكُمْ وَمَا تُوْعَدُوْنَ ','Dan di langit terdapat (sebab-sebab) rezekimu dan terdapat (pula) apa yang dijanjikan kepadamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,23,'فَوَرَبِّ السَّمَاۤءِ وَالْاَرْضِ اِنَّهٗ لَحَقٌّ مِّثْلَ مَآ اَنَّكُمْ تَنْطِقُوْنَ ࣖ','Maka demi Tuhan langit dan bumi, sesungguhnya yang dijanjikan itu adalah benar-benar (akan terjadi) seperti perkataan yang kamu ucapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,24,'هَلْ اَتٰىكَ حَدِيْثُ ضَيْفِ اِبْرٰهِيْمَ الْمُكْرَمِيْنَۘ ','Sudahkah sampai kepadamu (Muhammad) cerita tentang tamu Ibrahim (yaitu malaikat-malaikat) yang dimuliakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,25,'اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًا ۗقَالَ سَلٰمٌۚ قَوْمٌ مُّنْكَرُوْنَ ','(Ingatlah) ketika mereka masuk ke tempatnya lalu mengucapkan: "Salaamun". Ibrahim menjawab: "Salaamun (kamu) adalah orang-orang yang tidak dikenal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,26,'فَرَاغَ اِلٰٓى اَهْلِهٖ فَجَاۤءَ بِعِجْلٍ سَمِيْنٍۙ','Maka dia pergi dengan diam-diam menemui keluarganya, kemudian dibawanya daging anak sapi gemuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,27,'فَقَرَّبَهٗٓ اِلَيْهِمْۚ قَالَ اَلَا تَأْكُلُوْنَ ','Lalu dihidangkannya kepada mereka. Ibrahim lalu berkata: "Silahkan anda makan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,28,'فَاَوْجَسَ مِنْهُمْ خِيْفَةً  ۗقَالُوْا لَا تَخَفْۗ وَبَشَّرُوْهُ بِغُلٰمٍ عَلِيْمٍ ','(Tetapi mereka tidak mau makan), karena itu Ibrahim merasa takut terhadap mereka. Mereka berkata: "Janganlah kamu takut", dan mereka memberi kabar gembira kepadanya dengan (kelahiran) seorang anak yang alim (Ishak).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,29,'فَاَقْبَلَتِ امْرَاَتُهٗ فِيْ صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوْزٌ عَقِيْمٌ ','Kemudian isterinya datang memekik lalu menepuk mukanya sendiri seraya berkata: "(Aku adalah) seorang perempuan tua yang mandul".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,26,51,30,'قَالُوْا كَذٰلِكِۙ قَالَ رَبُّكِ ۗاِنَّهٗ هُوَ الْحَكِيْمُ الْعَلِيْمُ  ۔','Mereka berkata: "Demikianlah Tuhanmu memfirmankan" Sesungguhnya Dialah yang Maha Bijaksana lagi Maha Mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,31,'قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَۚ  ','Ibrahim bertanya: "Apakah urusanmu hai para utusan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,32,'قَالُوْٓ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ ','Mereka menjawab: "Sesungguhnya kami diutus kepada kaum yang berdosa (kaum Luth),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,33,'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِّنْ طِيْنٍۙ','agar kami timpakan kepada mereka batu-batu dari tanah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,34,'مُّسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِفِيْنَ ','yang ditandai di sisi Tuhanmu untuk membinasakan orang-orang yang melampaui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,35,'فَاَخْرَجْنَا مَنْ كَانَ فِيْهَا مِنَ الْمُؤْمِنِيْنَۚ ','Lalu Kami keluarkan orang-orang yang beriman yang berada di negeri kaum Luth itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,36,'فَمَا وَجَدْنَا فِيْهَا غَيْرَ بَيْتٍ مِّنَ الْمُسْلِمِيْنَۚ  ','Dan Kami tidak mendapati negeri itu, kecuali sebuah rumah dari orang yang berserah diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,37,'وَتَرَكْنَا فِيْهَآ اٰيَةً لِّلَّذِيْنَ يَخَافُوْنَ الْعَذَابَ الْاَلِيْمَۗ ','Dan Kami tinggalkan pada negeri itu suatu tanda bagi orang-orang yang takut kepada siksa yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,38,'وَفِيْ مُوْسٰىٓ اِذْ اَرْسَلْنٰهُ اِلٰى فِرْعَوْنَ بِسُلْطٰنٍ مُّبِيْنٍ ','Dan juga pada Musa (terdapat tanda-tanda kekuasaan Allah) ketika Kami mengutusnya kepada Fir´aun dengan membawa mukjizat yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,39,'فَتَوَلّٰى بِرُكْنِهٖ وَقَالَ سٰحِرٌ اَوْ مَجْنُوْنٌ','Maka dia (Fir´aun) berpaling (dari iman) bersama tentaranya dan berkata: "Dia adalah seorang tukang sihir atau seorang gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,40,'فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ وَهُوَ مُلِيْمٌۗ  ','Maka Kami siksa dia dan tentaranya lalu Kami lemparkan mereka ke dalam laut, sedang dia melakukan pekerjaan yang tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,41,'وَفِيْ عَادٍ اِذْ اَرْسَلْنَا عَلَيْهِمُ الرِّيْحَ الْعَقِيْمَۚ  ','Dan juga pada (kisah) Aad ketika Kami kirimkan kepada mereka angin yang membinasakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,42,'مَا تَذَرُ مِنْ شَيْءٍ اَتَتْ عَلَيْهِ اِلَّا جَعَلَتْهُ كَالرَّمِيْمِۗ ','angin itu tidak membiarkan satupun yang dilaluinya, melainkan dijadikannya seperti serbuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,43,'وَفِيْ ثَمُوْدَ اِذْ قِيْلَ لَهُمْ تَمَتَّعُوْا حَتّٰى حِيْنٍ ','Dan pada (kisah) kaum Tsamud ketika dikatakan kepada mereka: "Bersenang-senanglah kalian sampai suatu waktu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,44,'فَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ فَاَخَذَتْهُمُ الصّٰعِقَةُ وَهُمْ يَنْظُرُوْنَ','Maka mereka berlaku angkuh terhadap perintah Tuhannya, lalu mereka disambar petir dan mereka melihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,45,'فَمَا اسْتَطَاعُوْا مِنْ قِيَامٍ وَّمَا كَانُوْا مُنْتَصِرِيْنَۙ ','Maka mereka sekali-kali tidak dapat bangun dan tidak pula mendapat pertolongan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,46,'وَقَوْمَ نُوْحٍ مِّنْ قَبْلُ ۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ࣖ ','dan (Kami membinasakan) kaum Nuh sebelum itu. Sesungguhnya mereka adalah kaum yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,47,'وَالسَّمَاۤءَ بَنَيْنٰهَا بِاَيْىدٍ وَّاِنَّا لَمُوْسِعُوْنَ','Dan langit itu Kami bangun dengan kekuasaan (Kami) dan sesungguhnya Kami benar-benar berkuasa')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,48,'وَالْاَرْضَ فَرَشْنٰهَا فَنِعْمَ الْمَاهِدُوْنَ','Dan bumi itu Kami hamparkan, maka sebaik-baik yang menghamparkan (adalah Kami).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,49,'وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُوْنَ','Dan segala sesuatu Kami ciptakan berpasang-pasangan supaya kamu mengingat kebesaran Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,50,'فَفِرُّوْٓا اِلَى اللّٰهِ ۗاِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌۚ  ','Maka segeralah kembali kepada (mentaati) Allah. Sesungguhnya aku seorang pemberi peringatan yang nyata dari Allah untukmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,51,'وَلَا تَجْعَلُوْا مَعَ اللّٰهِ اِلٰهًا اٰخَرَۗ اِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌ','Dan janganlah kamu mengadakan tuhan yang lain disamping Allah. Sesungguhnya aku seorang pemberi peringatan yang nyata dari Allah untukmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,52,'كَذٰلِكَ مَآ اَتَى الَّذِيْنَ مِنْ قَبْلِهِمْ مِّنْ رَّسُوْلٍ اِلَّا قَالُوْا سَاحِرٌ اَوْ مَجْنُوْنٌ','Demikianlah tidak seorang rasulpun yang datang kepada orang-orang yang sebelum mereka, melainkan mereka mengatakan: "Dia adalah seorang tukang sihir atau seorang gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,53,'اَتَوَاصَوْا بِهٖۚ بَلْ هُمْ قَوْمٌ طَاغُوْنَۚ ','Apakah mereka saling berpesan tentang apa yang dikatakan itu. Sebenarnya mereka adalah kaum yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,54,'فَتَوَلَّ عَنْهُمْ فَمَآ اَنْتَ بِمَلُوْمٍ  ','Maka berpalinglah kamu dari mereka dan kamu sekali-kali tidak tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,55,'وَذَكِّرْ فَاِنَّ الذِّكْرٰى تَنْفَعُ الْمُؤْمِنِيْنَ ','Dan tetaplah memberi peringatan, karena sesungguhnya peringatan itu bermanfaat bagi orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,56,'وَمَا خَلَقْتُ الْجِنَّ وَالْاِنْسَ اِلَّا لِيَعْبُدُوْنِ','Dan aku tidak menciptakan jin dan manusia melainkan supaya mereka mengabdi kepada-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,57,'مَآ اُرِيْدُ مِنْهُمْ مِّنْ رِّزْقٍ وَّمَآ اُرِيْدُ اَنْ يُّطْعِمُوْنِ ','Aku tidak menghendaki rezeki sedikitpun dari mereka dan Aku tidak menghendaki supaya mereka memberi-Ku makan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,58,'اِنَّ اللّٰهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِيْنُ ','Sesungguhnya Allah Dialah Maha Pemberi rezeki Yang mempunyai Kekuatan lagi Sangat Kokoh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,59,'فَاِنَّ لِلَّذِيْنَ ظَلَمُوْا ذَنُوْبًا مِّثْلَ ذَنُوْبِ اَصْحٰبِهِمْ فَلَا يَسْتَعْجِلُوْنِ','Maka sesungguhnya untuk orang-orang zalim ada bagian (siksa) seperti bahagian teman mereka (dahulu); maka janganlah mereka meminta kepada-Ku untuk menyegerakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,51,60,'فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ يَّوْمِهِمُ الَّذِيْ يُوْعَدُوْنَ ࣖ ','Maka kecelakaanlah bagi orang-orang yang kafir pada hari yang diancamkan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,1,'وَالطُّوْرِۙ  ','Demi bukit,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,2,'وَكِتٰبٍ مَّسْطُوْرٍۙ  ','dan Kitab yang ditulis,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,3,'فِيْ رَقٍّ مَّنْشُوْرٍۙ  ','pada lembaran yang terbuka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,4,'وَّالْبَيْتِ الْمَعْمُوْرِۙ  ','dan demi Baitul Ma´mur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,5,'وَالسَّقْفِ الْمَرْفُوْعِۙ  ','dan atap yang ditinggikan (langit),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,6,'وَالْبَحْرِ الْمَسْجُوْرِۙ ','dan laut yang di dalam tanahnya ada api,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,7,'اِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌۙ  ','sesungguhnya azab Tuhanmu pasti terjadi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,8,'مَّا لَهٗ مِنْ دَافِعٍۙ  ','tidak seorangpun yang dapat menolaknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,9,'يَّوْمَ تَمُوْرُ السَّمَاۤءُ مَوْرًاۙ ','pada hari ketika langit benar-benar bergoncang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,10,'وَّتَسِيْرُ الْجِبَالُ سَيْرًاۗ  ','dan gunung benar-benar berjalan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,11,'فَوَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ  ','Maka kecelakaan yang besarlah di hari itu bagi orang-orang yang mendustakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,12,'الَّذِيْنَ هُمْ فِيْ خَوْضٍ يَّلْعَبُوْنَۘ  ','(yaitu) orang-orang yang bermain-main dalam kebathilan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,13,'يَوْمَ يُدَعُّوْنَ اِلٰى نَارِ جَهَنَّمَ دَعًّاۗ','pada hari mereka didorong ke neraka Jahannam dengan sekuat-kuatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,14,'هٰذِهِ النَّارُ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ ','(Dikatakan kepada mereka): "Inilah neraka yang dahulu kamu selalu mendustakannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,15,'اَفَسِحْرٌ هٰذَآ اَمْ اَنْتُمْ لَا تُبْصِرُوْنَ ','Maka apakah ini sihir? Ataukah kamu tidak melihat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,16,'اِصْلَوْهَا فَاصْبِرُوْٓا اَوْ لَا تَصْبِرُوْاۚ سَوَاۤءٌ عَلَيْكُمْۗ  اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ ','Masukklah kamu ke dalamnya (rasakanlah panas apinya); maka baik kamu bersabar atau tidak, sama saja bagimu; kamu diberi balasan terhadap apa yang telah kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,17,'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَعِيْمٍۙ  ','Sesungguhnya orang-orang yang bertakwa berada dalam surga dan kenikmatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,18,'فَاكِهِيْنَ بِمَآ اٰتٰىهُمْ رَبُّهُمْۚ وَوَقٰىهُمْ رَبُّهُمْ عَذَابَ الْجَحِيْمِ','mereka bersuka ria dengan apa yang diberikan kepada mereka oleh Tuhan mereka; dan Tuhan mereka memelihara mereka dari azab neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,19,'كُلُوْا وَاشْرَبُوْا هَنِيْۤئًا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَۙ  ','(Dikatakan kepada mereka): "Makan dan minumlah dengan enak sebagai balasan dari apa yang telah kamu kerjakan",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,20,'مُتَّكِـِٕيْنَ عَلٰى سُرُرٍ مَّصْفُوْفَةٍۚ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍ','mereka bertelekan di atas dipan-dipan berderetan dan Kami kawinkan mereka dengan bidadari-bidadari yang cantik bermata jeli.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,21,'وَالَّذِيْنَ اٰمَنُوْا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِاِيْمَانٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَآ اَلَتْنٰهُمْ مِّنْ عَمَلِهِمْ مِّنْ شَيْءٍۚ  كُلُّ امْرِئٍ ۢبِمَا كَسَبَ رَهِيْنٌ ','Dan orang-oranng yang beriman, dan yang anak cucu mereka mengikuti mereka dalam keimanan, Kami hubungkan anak cucu mereka dengan mereka, dan Kami tiada mengurangi sedikitpun dari pahala amal mereka. Tiap-tiap manusia terikat dengan apa yang dikerjakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,22,'وَاَمْدَدْنٰهُمْ بِفَاكِهَةٍ وَّلَحْمٍ مِّمَّا يَشْتَهُوْنَ ','Dan Kami beri mereka tambahan dengan buah-buahan dan daging dari segala jenis yang mereka ingini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,23,'يَتَنَازَعُوْنَ فِيْهَا كَأْسًا لَّا لَغْوٌ فِيْهَا وَلَا تَأْثِيْمٌ ','Di dalam surga mereka saling memperebutkan piala (gelas) yang isinya tidak (menimbulkan) kata-kata yang tidak berfaedah dan tiada pula perbuatan dosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,24,'وَيَطُوْفُ عَلَيْهِمْ غِلْمَانٌ لَّهُمْ كَاَنَّهُمْ لُؤْلُؤٌ مَّكْنُوْنٌۚ  ','Dan berkeliling di sekitar mereka anak-anak muda untuk (melayani) mereka, seakan-akan mereka itu mutiara yang tersimpan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,25,'وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ ','Dan sebahagian mereka menghadap kepada sebahagian yang lain saling tanya-menanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,26,'قَالُوْٓا اِنَّا كُنَّا قَبْلُ فِيْٓ اَهْلِنَا مُشْفِقِيْنَ ','Mereka berkata: "Sesungguhnya kami dahulu, sewaktu berada di tengah-tengah keluarga kami merasa takut (akan diazab)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,27,'فَمَنَّ اللّٰهُ عَلَيْنَا وَوَقٰىنَا عَذَابَ السَّمُوْمِ ','Maka Allah memberikan karunia kepada kami dan memelihara kami dari azab neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,28,'اِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوْهُۗ اِنَّهٗ هُوَ الْبَرُّ الرَّحِيْمُ ','Sesungguhnya kami dahulu menyembah-Nya. Sesungguhnya Dialah yang melimpahkan kebaikan lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,29,'فَذَكِّرْ فَمَآ اَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَّلَا مَجْنُوْنٍۗ ','Maka tetaplah memberi peringatan, dan kamu disebabkan nikmat Tuhanmu bukanlah seorang tukang tenung dan bukan pula seorang gila.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,30,'اَمْ يَقُوْلُوْنَ شَاعِرٌ نَّتَرَبَّصُ بِهٖ رَيْبَ الْمَنُوْنِ ','Bahkan mereka mengatakan: "Dia adalah seorang penyair yang kami tunggu-tunggu kecelakaan menimpanya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,31,'قُلْ تَرَبَّصُوْا فَاِنِّيْ مَعَكُمْ مِّنَ الْمُتَرَبِّصِيْنَۗ  ','Katakanlah: "Tunggulah, maka sesungguhnya akupun termasuk orang yang menunggu (pula) bersama kamu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,32,'اَمْ تَأْمُرُهُمْ اَحْلَامُهُمْ بِهٰذَآ اَمْ هُمْ قَوْمٌ طَاغُوْنَۚ ','Apakah mereka diperintah oleh fikiran-fikiran mereka untuk mengucapkan tuduhan-tuduhan ini ataukah mereka kaum yang melampaui batas?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,33,'اَمْ يَقُوْلُوْنَ تَقَوَّلَهٗۚ بَلْ لَّا يُؤْمِنُوْنَۚ ','Ataukah mereka mengatakan: "Dia (Muhammad) membuat-buatnya". Sebenarnya mereka tidak beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,34,'فَلْيَأْتُوْا بِحَدِيْثٍ مِّثْلِهٖٓ اِنْ كَانُوْا صٰدِقِيْنَۗ  ','Maka hendaklah mereka mendatangkan kalimat yang semisal Al Quran itu jika mereka orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,35,'اَمْ خُلِقُوْا مِنْ غَيْرِ شَيْءٍ اَمْ هُمُ الْخَالِقُوْنَۗ  ','Apakah mereka diciptakan tanpa sesuatupun ataukah mereka yang menciptakan (diri mereka sendiri)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,36,'اَمْ خَلَقُوا السَّمٰوٰتِ وَالْاَرْضَۚ بَلْ لَّا يُوْقِنُوْنَۗ  ','Ataukah mereka telah menciptakan langit dan bumi itu?; sebenarnya mereka tidak meyakini (apa yang mereka katakan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,37,'اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَبِّكَ اَمْ هُمُ الْمُصَۣيْطِرُوْنَۗ  ','Ataukah di sisi mereka ada perbendaharaan Tuhanmu atau merekakah yang berkuasa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,38,'اَمْ لَهُمْ سُلَّمٌ يَّسْتَمِعُوْنَ فِيْهِۚ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطٰنٍ مُّبِيْنٍۗ  ','Ataukah mereka mempunyai tangga (ke langit) untuk mendengarkan pada tangga itu (hal-hal yang gaib)? Maka hendaklah orang yang mendengarkan di antara mereka mendatangkan suatu keterangan yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,39,'اَمْ لَهُ الْبَنٰتُ وَلَكُمُ الْبَنُوْنَۗ  ','Ataukah untuk Allah anak-anak perempuan dan untuk kamu anak-anak laki-laki?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,40,'اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۗ  ','Ataukah kamu meminta upah kepada mereka sehingga mereka dibebani dengan hutang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,41,'اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَۗ ','Apakah ada pada sisi mereka pengetahuan tentang yang gaib lalu mereka menuliskannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,42,'اَمْ يُرِيْدُوْنَ كَيْدًاۗ فَالَّذِيْنَ كَفَرُوْا هُمُ الْمَكِيْدُوْنَۗ  ','Ataukah mereka hendak melakukan tipu daya? Maka orang-orang yang kafir itu merekalah yang kena tipu daya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,43,'اَمْ لَهُمْ اِلٰهٌ غَيْرُ اللّٰهِ ۗسُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ  ','Ataukah mereka mempunyai tuhan selain Allah. Maha Suci Allah dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,44,'وَاِنْ يَّرَوْا كِسْفًا مِّنَ السَّمَاۤءِ سَاقِطًا يَّقُوْلُوْا سَحَابٌ مَّرْكُوْمٌ ','Jika mereka melihat sebagian dari langit gugur, mereka akan mengatakan: "Itu adalah awan yang bertindih-tindih".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,45,'فَذَرْهُمْ حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ فِيْهِ يُصْعَقُوْنَۙ  ','Maka biarkanlah mereka hingga mereka menemui hari (yang dijanjikan kepada) mereka yang pada hari itu mereka dibinasakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,46,'يَوْمَ لَا يُغْنِيْ عَنْهُمْ كَيْدُهُمْ شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۗ  ','(yaitu) hari ketika tidak berguna bagi mereka sedikitpun tipu daya mereka dan mereka tidak ditolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,47,'وَاِنَّ لِلَّذِيْنَ ظَلَمُوْا عَذَابًا دُوْنَ ذٰلِكَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ','Dan sesungguhnya untuk orang-orang yang zalim ada azab selain daripada itu. Tetapi kebanyakan mereka tidak mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,48,'وَاصْبِرْ لِحُكْمِ رَبِّكَ فَاِنَّكَ بِاَعْيُنِنَا وَسَبِّحْ بِحَمْدِ رَبِّكَ حِيْنَ تَقُوْمُۙ  ','Dan bersabarlah dalam menunggu ketetapan Tuhanmu, maka sesungguhnya kamu berada dalam penglihatan Kami, dan bertasbihlah dengan memuji Tuhanmu ketika kamu bangun berdiri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,52,49,'وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاِدْبَارَ النُّجُوْمِ ࣖ','dan bertasbihlah kepada-Nya pada beberapa saat di malam hari dan di waktu terbenam bintang-bintang (di waktu fajar).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,1,'وَالنَّجْمِ اِذَا هَوٰىۙ ','Demi bintang ketika terbenam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,2,'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوٰىۚ ','kawanmu (Muhammad) tidak sesat dan tidak pula keliru.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,3,'وَمَا يَنْطِقُ عَنِ الْهَوٰى','dan tiadalah yang diucapkannya itu (Al-Quran) menurut kemauan hawa nafsunya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,4,'اِنْ هُوَ اِلَّا وَحْيٌ يُّوْحٰىۙ ','Ucapannya itu tiada lain hanyalah wahyu yang diwahyukan (kepadanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,5,'عَلَّمَهٗ شَدِيْدُ الْقُوٰىۙ ','yang diajarkan kepadanya oleh (Jibril) yang sangat kuat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,6,'ذُوْ مِرَّةٍۗ فَاسْتَوٰىۙ ','yang mempunyai akal yang cerdas; dan (Jibril itu) menampakkan diri dengan rupa yang asli.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,7,'وَهُوَ بِالْاُفُقِ الْاَعْلٰىۗ ','sedang dia berada di ufuk yang tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,8,'ثُمَّ دَنَا فَتَدَلّٰىۙ  ','Kemudian dia mendekat, lalu bertambah dekat lagi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,9,'فَكَانَ قَابَ قَوْسَيْنِ اَوْ اَدْنٰىۚ','maka jadilah dia dekat (pada Muhammad sejarak) dua ujung busur panah atau lebih dekat (lagi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,10,'فَاَوْحٰىٓ اِلٰى عَبْدِهٖ مَآ اَوْحٰىۗ','Lalu dia menyampaikan kepada hamba-Nya (Muhammad) apa yang telah Allah wahyukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,11,'مَا كَذَبَ الْفُؤَادُ مَا رَاٰى','Hatinya tidak mendustakan apa yang telah dilihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,12,'اَفَتُمٰرُوْنَهٗ عَلٰى مَا يَرٰى ','Maka apakah kaum (musyrik Mekah) hendak membantahnya tentang apa yang telah dilihatnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,13,'وَلَقَدْ رَاٰهُ نَزْلَةً اُخْرٰىۙ','Dan sesungguhnya Muhammad telah melihat Jibril itu (dalam rupanya yang asli) pada waktu yang lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,14,'عِنْدَ سِدْرَةِ الْمُنْتَهٰى ','(yaitu) di Sidratil Muntaha.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,15,'عِنْدَهَا جَنَّةُ الْمَأْوٰىۗ  ','Di dekatnya ada surga tempat tinggal,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,16,'اِذْ يَغْشَى السِّدْرَةَ مَا يَغْشٰىۙ ','(Muhammad melihat Jibril) ketika Sidratil Muntaha diliputi oleh sesuatu yang meliputinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,17,'مَا زَاغَ الْبَصَرُ وَمَا طَغٰى','Penglihatannya (muhammad) tidak berpaling dari yang dilihatnya itu dan tidak (pula) melampauinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,18,'لَقَدْ رَاٰى مِنْ اٰيٰتِ رَبِّهِ الْكُبْرٰى','Sesungguhnya dia telah melihat sebahagian tanda-tanda (kekuasaan) Tuhannya yang paling besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,19,'اَفَرَءَيْتُمُ اللّٰتَ وَالْعُزّٰى','Maka apakah patut kamu (hai orang-orang musyrik) menganggap al Lata dan al Uzza,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,20,'وَمَنٰوةَ الثَّالِثَةَ الْاُخْرٰى ','dan Manah yang ketiga, yang paling terkemudian (sebagai anak perempuan Allah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,21,'اَلَكُمُ الذَّكَرُ وَلَهُ الْاُنْثٰى ','Apakah (patut) untuk kamu (anak) laki-laki dan untuk Allah (anak) perempuan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,22,'تِلْكَ اِذًا قِسْمَةٌ ضِيْزٰى ','Yang demikian itu tentulah suatu pembagian yang tidak adil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,23,'اِنْ هِيَ اِلَّآ اَسْمَاۤءٌ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَمَا تَهْوَى الْاَنْفُسُۚ وَلَقَدْ جَاۤءَهُمْ مِّنْ رَّبِّهِمُ الْهُدٰىۗ','Itu tidak lain hanyalah nama-nama yang kamu dan bapak-bapak kamu mengadakannya; Allah tidak menurunkan suatu keteranganpun untuk (menyembah)nya. Mereka tidak lain hanyalah mengikuti sangkaan-sangkaan, dan apa yang diingini oleh hawa nafsu mereka dan sesungguhnya telah datang petunjuk kepada mereka dari Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,24,'اَمْ لِلْاِنْسَانِ مَا تَمَنّٰىۖ  ','Atau apakah manusia akan mendapat segala yang dicita-citakannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,25,'فَلِلّٰهِ الْاٰخِرَةُ وَالْاُوْلٰى ࣖ ','(Tidak), maka hanya bagi Allah kehidupan akhirat dan kehidupan dunia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,26,'وَكَمْ مِّنْ مَّلَكٍ فِى السَّمٰوٰتِ لَا تُغْنِيْ شَفَاعَتُهُمْ شَيْـًٔا اِلَّا مِنْۢ بَعْدِ اَنْ يَّأْذَنَ اللّٰهُ لِمَنْ يَّشَاۤءُ وَيَرْضٰى ','Dan berapa banyaknya malaikat di langit, syafa´at mereka sedikitpun tidak berguna, kecuali sesudah Allah mengijinkan bagi orang yang dikehendaki dan diridhai (Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,27,'اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ لَيُسَمُّوْنَ الْمَلٰۤىِٕكَةَ تَسْمِيَةَ الْاُنْثٰى ','Sesungguhnya orang-orang yang tiada beriman kepada kehidupan akhirat, mereka benar-benar menamakan malaikat itu dengan nama perempuan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,28,'وَمَا لَهُمْ بِهٖ مِنْ عِلْمٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۚ ','Dan mereka tidak mempunyai sesuatu pengetahuanpun tentang itu. Mereka tidak lain hanyalah mengikuti persangkaan sedang sesungguhnya persangkaan itu tiada berfaedah sedikitpun terhadap kebenaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,29,'فَاَعْرِضْ عَنْ مَّنْ تَوَلّٰىۙ عَنْ ذِكْرِنَا وَلَمْ يُرِدْ اِلَّا الْحَيٰوةَ الدُّنْيَاۗ  ','Maka berpalinglah (hai Muhammad) dari orang yang berpaling dari peringatan Kami, dan tidak mengingini kecuali kehidupan duniawi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,30,'ذٰلِكَ مَبْلَغُهُمْ مِّنَ الْعِلْمِۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۙ وَهُوَ اَعْلَمُ بِمَنِ اهْتَدٰى','Itulah sejauh-jauh pengetahuan mereka. Sesungguhnya Tuhanmu, Dialah yang paling mengetahui siapa yang tersesat dari jalan-Nya dan Dia pulalah yang paling mengetahui siapa yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,31,'وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لِيَجْزِيَ الَّذِيْنَ اَسَاۤءُوْا بِمَا عَمِلُوْا وَيَجْزِيَ الَّذِيْنَ اَحْسَنُوْا بِالْحُسْنٰىۚ ','Dan hanya kepunyaan Allah-lah apa yang ada di langit dan apa yang ada di bumi supaya Dia memberi balasan kepada orang-orang yang berbuat jahat terhadap apa yang telah mereka kerjakan dan memberi balasan kepada orang-orang yang berbuat baik dengan pahala yang lebih baik (surga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,32,'اَلَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ اِلَّا اللَّمَمَۙ اِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِۗ هُوَ اَعْلَمُ بِكُمْ اِذْ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاِذْ اَنْتُمْ اَجِنَّةٌ فِيْ بُطُوْنِ اُمَّهٰتِكُمْۗ فَلَا تُزَكُّوْٓا اَنْفُسَكُمْۗ  هُوَ اَعْلَمُ بِمَنِ اتَّقٰى ࣖ ','(Yaitu) orang-orang yang menjauhi dosa-dosa besar dan perbuatan keji yang selain dari kesalahan-kesalahan kecil. Sesungguhnya Tuhanmu maha luas ampunan-Nya. Dan Dia lebih mengetahui (tentang keadaan)mu ketika Dia menjadikan kamu dari tanah dan ketika kamu masih janin dalam perut ibumu; maka janganlah kamu mengatakan dirimu suci. Dialah yang paling mengetahui tentang orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,33,'اَفَرَءَيْتَ الَّذِيْ تَوَلّٰىۙ  ','Maka apakah kamu melihat orang yang berpaling (dari Al-Quran)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,34,'وَاَعْطٰى قَلِيْلًا وَّاَكْدٰى ','serta memberi sedikit dan tidak mau memberi lagi?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,35,'اَعِنْدَهٗ عِلْمُ الْغَيْبِ فَهُوَ يَرٰى ','Apakah dia mempunyai pengetahuan tentang yang ghaib, sehingga dia mengetahui (apa yang dikatakan)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,36,'اَمْ لَمْ يُنَبَّأْ بِمَا فِيْ صُحُفِ مُوْسٰى  ','Ataukah belum diberitakan kepadanya apa yang ada dalam lembaran-lembaran Musa?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,37,'وَاِبْرٰهِيْمَ الَّذِيْ وَفّٰىٓ ۙ ','dan lembaran-lembaran Ibrahim yang selalu menyempurnakan janji?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,38,'اَلَّا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۙ  ','(yaitu) bahwasanya seorang yang berdosa tidak akan memikul dosa orang lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,39,'وَاَنْ لَّيْسَ لِلْاِنْسَانِ اِلَّا مَا سَعٰىۙ  ','dan bahwasanya seorang manusia tiada memperoleh selain apa yang telah diusahakannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,40,'وَاَنَّ سَعْيَهٗ سَوْفَ يُرٰىۖ','dan bahwasanya usaha itu kelak akan diperlihatkan (kepadanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,41,'ثُمَّ يُجْزٰىهُ الْجَزَاۤءَ الْاَوْفٰىۙ ','Kemudian akan diberi balasan kepadanya dengan balasan yang paling sempurna,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,42,'وَاَنَّ اِلٰى رَبِّكَ الْمُنْتَهٰىۙ  ','dan bahwasanya kepada Tuhanmulah kesudahan (segala sesuatu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,43,'وَاَنَّهٗ هُوَ اَضْحَكَ وَاَبْكٰى','dan bahwasanya Dialah yang menjadikan orang tertawa dan menangis,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,44,'وَاَنَّهٗ هُوَ اَمَاتَ وَاَحْيَاۙ  ','dan bahwasanya Dialah yang mematikan dan menghidupkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,45,'وَاَنَّهٗ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰى ','dan bahwasanya Dialah yang menciptakan berpasang-pasangan pria dan wanita.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,46,'مِنْ نُّطْفَةٍ اِذَا تُمْنٰىۙ  ','dari air mani, apabila dipancarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,47,'وَاَنَّ عَلَيْهِ النَّشْاَةَ الْاُخْرٰىۙ ','Dan bahwasanya Dialah yang menetapkan kejadian yang lain (kebangkitan sesudah mati),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,48,'وَاَنَّهٗ هُوَ اَغْنٰى وَاَقْنٰىۙ ','dan bahwasanya Dia yang memberikan kekayaan dan memberikan kecukupan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,49,'وَاَنَّهٗ هُوَ رَبُّ الشِّعْرٰىۙ','dan bahwasanya Dialah yang Tuhan (yang memiliki) bintang syi´ra,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,50,'وَاَنَّهٗٓ اَهْلَكَ عَادًا ۨالْاُوْلٰىۙ','dan bahwasanya Dia telah membinasakan kaum ´Aad yang pertama,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,51,'وَثَمُوْدَا۟ فَمَآ اَبْقٰىۙ','dan kaum Tsamud. Maka tidak seorangpun yang ditinggalkan-Nya (hidup).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,52,'وَقَوْمَ نُوْحٍ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا هُمْ اَظْلَمَ وَاَطْغٰىۗ  ','Dan kaum Nuh sebelum itu. Sesungguhnya mereka adalah orang-orang yang paling zalim dan paling durhaka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,53,'وَالْمُؤْتَفِكَةَ اَهْوٰىۙ  ','dan negeri-negeri kaum Luth yang telah dihancurkan Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,54,'فَغَشّٰىهَا مَا غَشّٰىۚ  ','lalu Allah menimpakan atas negeri itu azab besar yang menimpanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,55,'فَبِاَيِّ اٰلَاۤءِ رَبِّكَ تَتَمَارٰى','Maka terhadap nikmat Tuhanmu yang manakah kamu ragu-ragu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,56,'هٰذَا نَذِيْرٌ مِّنَ النُّذُرِ الْاُوْلٰى','Ini (Muhammad) adalah seorang pemberi peringatan di antara pemberi-pemberi peringatan yang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,57,'اَزِفَتِ الْاٰزِفةُ ۚ ','Telah dekat terjadinya hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,58,'لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ كَاشِفَةٌ  ۗ ','Tidak ada yang akan menyatakan terjadinya hari itu selain Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,59,'اَفَمِنْ هٰذَا الْحَدِيْثِ تَعْجَبُوْنَۙ  ','Maka apakah kamu merasa heran terhadap pemberitaan ini?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,60,'وَتَضْحَكُوْنَ وَلَا تَبْكُوْنَۙ  ','Dan kamu mentertawakan dan tidak menangis?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,61,'وَاَنْتُمْ سَامِدُوْنَ ','Sedang kamu melengahkan(nya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,53,62,'فَاسْجُدُوْا لِلّٰهِ وَاعْبُدُوْا ࣖ ۩ ','Maka bersujudlah kepada Allah dan sembahlah (Dia).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,1,'اِقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ ','Telah dekat datangnya saat itu dan telah terbelah bulan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,2,'وَاِنْ يَّرَوْا اٰيَةً يُّعْرِضُوْا وَيَقُوْلُوْا سِحْرٌ مُّسْتَمِرٌّ','Dan jika mereka (orang-orang musyrikin) melihat suatu tanda (mukjizat), mereka berpaling dan berkata: "(Ini adalah) sihir yang terus menerus".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,3,'وَكَذَّبُوْا وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ وَكُلُّ اَمْرٍ مُّسْتَقِرٌّ','Dan mereka mendutakan (Nabi) dan mengikuti hawa nafsu mereka, sedang tiap-tiap urusan telah ada ketetapannya')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,4,'وَلَقَدْ جَاۤءَهُمْ مِّنَ الْاَنْبَاۤءِ مَا فِيْهِ مُزْدَجَرٌۙ  ','Dan sesungguhnya telah datang kepada mereka beberapa kisah yang di dalamnya terdapat cegahan (dari kekafiran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,5,'حِكْمَةٌ ۢ بَالِغَةٌ فَمَا تُغْنِ النُّذُرُۙ  ','Itulah suatu hikmah yang sempurna maka peringatan-peringatan itu tidak berguna (bagi mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,6,'فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ الدَّاعِ اِلٰى شَيْءٍ نُّكُرٍۙ  ','Maka berpalinglah kamu dari mereka. (Ingatlah) hari (ketika) seorang penyeru (malaikat) menyeru kepada sesuatu yang tidak menyenangkan (hari pembalasan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,7,'خُشَّعًا اَبْصَارُهُمْ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ كَاَنَّهُمْ جَرَادٌ مُّنْتَشِرٌۙ  ','sambil menundukkan pandangan-pandangan mereka keluar dari kuburan seakan-akan mereka belalang yang beterbangan')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,8,'مُّهْطِعِيْنَ اِلَى الدَّاعِۗ يَقُوْلُ الْكٰفِرُوْنَ هٰذَا يَوْمٌ عَسِرٌ ','mereka datang dengan cepat kepada penyeru itu. Orang-orang kafir berkata: "Ini adalah hari yang berat".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,9,'۞ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ فَكَذَّبُوْا عَبْدَنَا وَقَالُوْا مَجْنُوْنٌ وَّازْدُجِرَ','Sebelum mereka, telah mendustakan (pula) kamu Nuh, maka mereka mendustakan hamba Kami (Nuh) dan mengatakan: "Dia seorang gila dan dia sudah pernah diberi ancaman).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,10,'فَدَعَا رَبَّهٗٓ اَنِّيْ مَغْلُوْبٌ فَانْتَصِرْ','Maka dia mengadu kepada Tuhannya: "bahwasanya aku ini adalah orang yang dikalahkan, oleh sebab itu menangkanlah (aku)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,11,'فَفَتَحْنَآ اَبْوَابَ السَّمَاۤءِ بِمَاۤءٍ مُّنْهَمِرٍۖ  ','Maka Kami bukakan pintu-pintu langit dengan (menurunkan) air yang tercurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,12,'وَّفَجَّرْنَا الْاَرْضَ عُيُوْنًا فَالْتَقَى الْمَاۤءُ عَلٰٓى اَمْرٍ قَدْ قُدِرَ ۚ','Dan Kami jadikan bumi memancarkan mata air-mata air, maka bertemulah air-air itu untuk suatu urusan yang sungguh telah ditetapkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,13,'وَحَمَلْنٰهُ عَلٰى ذَاتِ اَلْوَاحٍ وَّدُسُرٍۙ  ','Dan Kami angkut Nuh ke atas (bahtera) yang terbuat dari papan dan paku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,14,'تَجْرِيْ بِاَعْيُنِنَاۚ جَزَاۤءً لِّمَنْ كَانَ كُفِرَ ','Yang berlayar dengan pemeliharaan Kami sebagai belasan bagi orang-orang yang diingkari (Nuh).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,15,'وَلَقَدْ تَّرَكْنٰهَآ اٰيَةً فَهَلْ مِنْ مُّدَّكِرٍ','Dan sesungguhnya telah Kami jadikan kapal itu sebagai pelajaran, maka adakah orang yang mau mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,16,'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ','Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,17,'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ','Dan sesungguhnya telah Kami mudahkan Al-Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,18,'كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ','Kaum ´Aad pun mendustakan (pula). Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,19,'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْ يَوْمِ نَحْسٍ مُّسْتَمِرٍّۙ  ','Sesungguhnya Kami telah menghembuskan kepada mereka angin yang sangat kencang pada hari nahas yang terus menerus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,20,'تَنْزِعُ النَّاسَۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ مُّنْقَعِرٍ ','yang menggelimpangkan manusia seakan-akan mereka pokok korma yang tumbang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,21,'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ','Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,22,'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ࣖ ','Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,23,'كَذَّبَتْ ثَمُوْدُ بِالنُّذُرِ','Kaum Tsamudpun telah mendustakan ancaman-ancaman (itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,24,'فَقَالُوْٓا اَبَشَرًا مِّنَّا وَاحِدًا نَّتَّبِعُهٗٓ  ۙاِنَّآ اِذًا لَّفِيْ ضَلٰلٍ وَّسُعُرٍ','Maka mereka berkata: "Bagaimana kita akan mengikuti seorang manusia (biasa) di antara kita?" Sesungguhnya kalau kita begitu benar-benar berada dalam keadaan sesat dan gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,25,'ءَاُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْۢ بَيْنِنَا بَلْ هُوَ كَذَّابٌ اَشِرٌ ','Apakah wahyu itu diturunkan kepadanya di antara kita? Sebenarnya dia adalah seorang yang amat pendusta lagi sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,26,'سَيَعْلَمُوْنَ غَدًا مَّنِ الْكَذَّابُ الْاَشِرُ ','Kelak mereka akan mengetahui siapakah yang sebenarnya amat pendusta lagi sombong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,27,'اِنَّا مُرْسِلُوا النَّاقَةِ فِتْنَةً لَّهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْۖ  ','Sesungguhnya Kami akan mengirimkan unta betina sebagai cobaan bagi mereka, maka tunggulah (tindakan) mereka dan bersabarlah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,28,'وَنَبِّئْهُمْ اَنَّ الْمَاۤءَ قِسْمَةٌ ۢ بَيْنَهُمْۚ  كُلُّ شِرْبٍ مُّحْتَضَرٌ ','Dan beritakanlah kepada mereka bahwa sesungguhnya air itu terbagi antara mereka (dengan unta betina itu); tiap-tiap giliran minum dihadiri (oleh yang punya giliran)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,29,'فَنَادَوْا صَاحِبَهُمْ فَتَعَاطٰى فَعَقَرَ ','Maka mereka memanggil kawannya, lalu kawannya menangkap (unta itu) dan membunuhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,30,'فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ','Alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,31,'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَّاحِدَةً فَكَانُوْا كَهَشِيْمِ الْمُحْتَظِرِ','Sesungguhnya Kami menimpakan atas mereka satu suara yang keras mengguntur, maka jadilah mereka seperti rumput kering (yang dikumpulkan oleh) yang punya kandang binatang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,32,'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ','Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,33,'كَذَّبَتْ قَوْمُ لُوْطٍ ۢبِالنُّذُرِ ','Kaum Luth-pun telah mendustakan ancaman-ancaman (nabinya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,34,'اِنَّآ اَرْسَلْنَا عَلَيْهِمْ حَاصِبًا اِلَّآ اٰلَ لُوْطٍ ۗنَجَّيْنٰهُمْ بِسَحَرٍۙ','Sesungguhnya Kami telah menghembuskan kepada mereka angin yang membawa batu-batu (yang menimpa mereka), kecuali keluarga Luth. Mereka Kami selamatkan sebelum fajar menyingsing,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,35,'نِّعْمَةً مِّنْ عِنْدِنَاۗ  كَذٰلِكَ نَجْزِيْ مَنْ شَكَرَ ','sebagai nikmat dari Kami. Demikianlah Kami memberi balasan kepada orang-orang yang bersyukur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,36,'وَلَقَدْ اَنْذَرَهُمْ بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ ','Dan sesungguhnya dia (Luth) telah memperingatkan mereka akan azab-azab Kami, maka mereka mendustakan ancaman-ancaman itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,37,'وَلَقَدْ رَاوَدُوْهُ عَنْ ضَيْفِهٖ فَطَمَسْنَآ اَعْيُنَهُمْ فَذُوْقُوْا عَذَابِيْ وَنُذُرِ ','Dan sesungguhnya mereka telah membujuknya (agar menyerahkan) tamunya (kepada mereka), lalu Kami butakan mata mereka, maka rasakanlah azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,38,'وَلَقَدْ صَبَّحَهُمْ بُكْرَةً عَذَابٌ مُّسْتَقِرٌّۚ  ','Dan sesungguhnya pada esok harinya mereka ditimpa azab yang kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,39,'فَذُوْقُوْا عَذَابِيْ وَنُذُرِ ','Maka rasakanlah azab-Ku dan ancaman-ancaman-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,40,'وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ࣖ','Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,41,'وَلَقَدْ جَاۤءَ اٰلَ فِرْعَوْنَ النُّذُرُۚ  ','Dan sesungguhnya telah datang kepada kaum Fir´aun ancaman-ancaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,42,'كَذَّبُوْا بِاٰيٰتِنَا كُلِّهَا فَاَخَذْنٰهُمْ اَخْذَ عَزِيْزٍ مُّقْتَدِرٍ ','Mereka mendustakan mukjizat Kami semuanya, lalu Kami azab mereka sebagai azab dari Yang Maha Perkasa lagi Maha Kuasa')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,43,'اَكُفَّارُكُمْ خَيْرٌ مِّنْ اُولٰۤىِٕكُمْ اَمْ لَكُمْ بَرَاۤءَةٌ فِى الزُّبُرِۚ','Apakah orang-orang kafirmu (hai kaum musyrikin) lebih baik dari mereka itu, atau apakah kamu telah mempunyai jaminan kebebasan (dari azab) dalam Kitab-kitab yang dahulu')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,44,'اَمْ يَقُوْلُوْنَ نَحْنُ جَمِيْعٌ مُّنْتَصِرٌ ','Atau apakah mereka mengatakan: "Kami adalah satu golongan yang bersatu yang pasti menang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,45,'سَيُهْزَمُ الْجَمْعُ وَيُوَلُّوْنَ الدُّبُرَ','Golongan itu pasti akan dikalahkan dan mereka akan mundur ke belakang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,46,'بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ اَدْهٰى وَاَمَرُّ ','Sebenarnya hari kiamat itulah hari yang dijanjikan kepada mereka dan kiamat itu lebih dahsyat dan lebih pahit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,47,'اِنَّ الْمُجْرِمِيْنَ فِيْ ضَلٰلٍ وَّسُعُرٍۘ ','Sesungguhnya orang-orang yang berdosa berada dalam kesesatan (di dunia) dan dalam neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,48,'يَوْمَ يُسْحَبُوْنَ فِى النَّارِ عَلٰى وُجُوْهِهِمْۗ ذُوْقُوْا مَسَّ سَقَرَ','(Ingatlah) pada hari mereka diseret ke neraka atas muka mereka. (Dikatakan kepada mereka): "Rasakanlah sentuhan api neraka!"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,49,'اِنَّا كُلَّ شَيْءٍ خَلَقْنٰهُ بِقَدَرٍ ','Sesungguhnya Kami menciptakan segala sesuatu menurut ukuran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,50,'وَمَآ اَمْرُنَآ اِلَّا وَاحِدَةٌ كَلَمْحٍ ۢبِالْبَصَرِ ','Dan perintah Kami hanyalah satu perkataan seperti kejapan mata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,51,'وَلَقَدْ اَهْلَكْنَآ اَشْيَاعَكُمْ فَهَلْ مِنْ مُّدَّكِرٍ ','Dan sesungguhnya telah Kami binasakan orang yang serupa dengan kamu. Maka adakah orang yang mau mengambil pelajaran?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,52,'وَكُلُّ شَيْءٍ فَعَلُوْهُ فِى الزُّبُرِ ','Dan segala sesuatu yang telah mereka perbuat tercatat dalam buku-buku catatan')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,53,'وَكُلُّ صَغِيْرٍ وَّكَبِيْرٍ مُّسْتَطَرٌ ','Dan segala (urusan) yang kecil maupun yang besar adalah tertulis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,54,'اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَهَرٍۙ  ','Sesungguhnya orang-orang yang bertakwa itu di dalam taman-taman dan sungai-sungai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,54,55,'فِيْ مَقْعَدِ صِدْقٍ عِنْدَ مَلِيْكٍ مُّقْتَدِرٍ ࣖ ','di tempat yang disenangi di sisi Tuhan Yang Berkuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,1,'اَلرَّحْمٰنُۙ  ','(Tuhan) Yang Maha Pemurah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,2,'عَلَّمَ الْقُرْاٰنَۗ ','Yang telah mengajarkan al Quran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,3,'خَلَقَ الْاِنْسَانَۙ ','Dia menciptakan manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,4,'عَلَّمَهُ الْبَيَانَ ','Mengajarnya pandai berbicara.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,5,'اَلشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍۙ ','Matahari dan bulan (beredar) menurut perhitungan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,6,'وَّالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ ','Dan tumbuh-tumbuhan dan pohon-pohonan kedua-duanya tunduk kepada-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,7,'وَالسَّمَاۤءَ رَفَعَهَا وَوَضَعَ الْمِيْزَانَۙ  ','Dan Allah telah meninggikan langit dan Dia meletakkan neraca (keadilan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,8,'اَلَّا تَطْغَوْا فِى الْمِيْزَانِ ','Supaya kamu jangan melampaui batas tentang neraca itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,9,'وَاَقِيْمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْمِيْزَانَ ','Dan tegakkanlah timbangan itu dengan adil dan janganlah kamu mengurangi neraca itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,10,'وَالْاَرْضَ وَضَعَهَا لِلْاَنَامِۙ  ','Dan Allah telah meratakan bumi untuk makhluk(Nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,11,'فِيْهَا فَاكِهَةٌ وَّالنَّخْلُ ذَاتُ الْاَكْمَامِۖ  ','Di bumi itu ada buah-buahan dan pohon kurma yang mempunyai kelopak mayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,12,'وَالْحَبُّ ذُو الْعَصْفِ وَالرَّيْحَانُۚ  ','Dan biji-bijian yang berkulit dan bunga-bunga yang harum baunya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,13,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,14,'خَلَقَ الْاِنْسَانَ مِنْ صَلْصَالٍ كَالْفَخَّارِ ','Dia menciptakan manusia dari tanah kering seperti tembikar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,15,'وَخَلَقَ الْجَاۤنَّ مِنْ مَّارِجٍ مِّنْ نَّارٍۚ  ','dan Dia menciptakan jin dari nyala api.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,16,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,17,'رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِۚ ','Tuhan yang memelihara kedua tempat terbit matahari dan Tuhan yang memelihara kedua tempat terbenamnya')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,18,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,19,'مَرَجَ الْبَحْرَيْنِ يَلْتَقِيٰنِۙ','Dia membiarkan dua lautan mengalir yang keduanya kemudian bertemu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,20,'بَيْنَهُمَا بَرْزَخٌ لَّا يَبْغِيٰنِۚ','antara keduanya ada batas yang tidak dilampaui masing-masing.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,21,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,22,'يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَالْمَرْجَانُۚ','Dari keduanya keluar mutiara dan marjan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,23,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,24,'وَلَهُ الْجَوَارِ الْمُنْشَاٰتُ فِى الْبَحْرِ كَالْاَعْلَامِۚ  ','Dan kepunyaan-Nya lah bahtera-bahtera yang tinggi layarnya di lautan laksana gunung-gunung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,25,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ࣖ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,26,'كُلُّ مَنْ عَلَيْهَا فَانٍۖ  ','Semua yang ada di bumi itu akan binasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,27,'وَّيَبْقٰى وَجْهُ رَبِّكَ ذُو الْجَلٰلِ وَالْاِكْرَامِۚ  ','Dan tetap kekal Dzat Tuhanmu yang mempunyai kebesaran dan kemuliaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,28,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,29,'يَسْـَٔلُهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ  كُلَّ يَوْمٍ هُوَ فِيْ شَأْنٍۚ','Semua yang ada di langit dan bumi selalu meminta kepada-Nya. Setiap waktu Dia dalam kesibukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,30,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Rabb-mu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,31,'سَنَفْرُغُ لَكُمْ اَيُّهَ الثَّقَلٰنِۚ  ','Kami akan memperhatikan sepenuhnya kepadamu hai manusia dan jin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,32,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,33,'يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اِنِ اسْتَطَعْتُمْ اَنْ تَنْفُذُوْا مِنْ اَقْطَارِ السَّمٰوٰتِ وَالْاَرْضِ فَانْفُذُوْاۗ  لَا تَنْفُذُوْنَ اِلَّا بِسُلْطٰنٍۚ ','Hai jama´ah jin dan manusia, jika kamu sanggup menembus (melintasi) penjuru langit dan bumi, maka lintasilah, kamu tidak dapat menembusnya kecuali dengan kekuatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,34,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,35,'يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِّنْ نَّارٍۙ وَّنُحَاسٌ فَلَا تَنْتَصِرَانِۚ ','Kepada kamu, (jin dan manusia) dilepaskan nyala api dan cairan tembaga maka kamu tidak dapat menyelamatkan diri (dari padanya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,36,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,37,'فَاِذَا انْشَقَّتِ السَّمَاۤءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِۚ ','Maka apabila langit telah terbelah dan menjadi merah mawar seperti (kilapan) minyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,38,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,39,'فَيَوْمَئِذٍ لَّا يُسْـَٔلُ عَنْ ذَنْۢبِهٖٓ اِنْسٌ وَّلَا جَاۤنٌّۚ  ','Pada waktu itu manusia dan jin tidak ditanya tentang dosanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,40,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ  ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,41,'يُعْرَفُ الْمُجْرِمُوْنَ بِسِيْمٰهُمْ فَيُؤْخَذُ بِالنَّوَاصِيْ وَالْاَقْدَامِۚ  ','Orang-orang yang berdosa dikenal dengan tanda-tandannya, lalu dipegang ubun-ubun dan kaki mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,42,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,43,'هٰذِهٖ جَهَنَّمُ الَّتِيْ يُكَذِّبُ بِهَا الْمُجْرِمُوْنَۘ ','Inilah neraka Jahannam yang didustakan oleh orang-orang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,44,'يَطُوْفُوْنَ بَيْنَهَا وَبَيْنَ حَمِيْمٍ اٰنٍۚ  ','Mereka berkeliling di antaranya dan di antara air mendidih yang memuncak panasnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,45,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ࣖ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,46,'وَلِمَنْ خَافَ مَقَامَ رَبِّهٖ جَنَّتٰنِۚ','Dan bagi orang yang takut akan saat menghadap Tuhannya ada dua surga.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,47,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,48,'ذَوَاتَآ اَفْنَانٍۚ ','kedua surga itu mempunyai pohon-pohonan dan buah-buahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,49,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,50,'فِيْهِمَا عَيْنٰنِ تَجْرِيٰنِۚ ','Di dalam kedua surga itu ada dua buah mata air yang mengalir')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,51,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,52,'فِيْهِمَا مِنْ كُلِّ فَاكِهَةٍ زَوْجٰنِۚ ','Di dalam kedua surga itu terdapat segala macam buah-buahan yang berpasangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,53,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,54,'مُتَّكِـِٕيْنَ عَلٰى فُرُشٍۢ بَطَاۤىِٕنُهَا مِنْ اِسْتَبْرَقٍۗ وَجَنَا الْجَنَّتَيْنِ دَانٍۚ ','Mereka bertelekan di atas permadani yang sebelah dalamnya dari sutera. Dan buah-buahan di kedua surga itu dapat (dipetik) dari dekat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,55,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,56,'فِيْهِنَّ قٰصِرٰتُ الطَّرْفِۙ لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ  ','Di dalam surga itu ada bidadari-bidadari yang sopan menundukkan pandangannya, tidak pernah disentuh oleh manusia sebelum mereka (penghuni-penghuni surga yang menjadi suami mereka), dan tidak pula oleh jin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,57,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,58,'كَاَنَّهُنَّ الْيَاقُوْتُ وَالْمَرْجَانُۚ ','Seakan-akan bidadari itu permata yakut dan marjan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,59,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,60,'هَلْ جَزَاۤءُ الْاِحْسَانِ اِلَّا الْاِحْسَانُۚ ','Tidak ada balasan kebaikan kecuali kebaikan (pula).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,61,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,62,'وَمِنْ دُوْنِهِمَا جَنَّتٰنِۚ ','Dan selain dari dua surga itu ada dua surga lagi')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,63,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,64,'مُدْهَاۤمَّتٰنِۚ  ','Kedua surga itu (kelihatan) hijau tua warnanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,65,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,66,'فِيْهِمَا عَيْنٰنِ نَضَّاخَتٰنِۚ ','Di dalam kedua surga itu ada dua buah mata air yang memancar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,67,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ  ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,68,'فِيْهِمَا فَاكِهَةٌ وَّنَخْلٌ وَّرُمَّانٌۚ  ','Di dalam keduanya (ada macam-macam) buah-buahan dan kurma serta delima.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,69,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,70,'فِيْهِنَّ خَيْرٰتٌ حِسَانٌۚ  ','Di dalam surga itu ada bidadari-bidadari yang baik-baik lagi cantik-cantik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,71,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,72,'حُوْرٌ مَّقْصُوْرٰتٌ فِى الْخِيَامِۚ  ','(Bidadari-bidadari) yang jelita, putih bersih, dipingit dalam rumah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,73,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ  ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,74,'لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ  ','Mereka tidak pernah disentuh oleh manusia sebelum mereka (penghuni-penghuni surga yang menjadi suami mereka), dan tidak pula oleh jin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,75,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ  ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,76,'مُتَّكِـِٕيْنَ عَلٰى رَفْرَفٍ خُضْرٍ وَّعَبْقَرِيٍّ حِسَانٍۚ  ','Mereka bertelekan pada bantal-bantal yang hijau dan permadani-permadani yang indah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,77,'فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ','Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,55,78,'تَبٰرَكَ اسْمُ رَبِّكَ ذِى الْجَلٰلِ وَالْاِكْرَامِ ࣖ ','Maha Agung nama Tuhanmu Yang Mempunyai Kebesaran dan Karunia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,1,'اِذَا وَقَعَتِ الْوَاقِعَةُۙ ','Apabila terjadi hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,2,'لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ  ۘ ','tidak seorangpun dapat berdusta tentang kejadiannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,3,'خَافِضَةٌ رَّافِعَةٌ ','(Kejadian itu) merendahkan (satu golongan) dan meninggikan (golongan yang lain),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,4,'اِذَا رُجَّتِ الْاَرْضُ رَجًّاۙ ','apabila bumi digoncangkan sedahsyat-dahsyatnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,5,'وَّبُسَّتِ الْجِبَالُ بَسًّاۙ  ','dan gunung-gunung dihancur luluhkan seluluh-luluhnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,6,'فَكَانَتْ هَبَاۤءً مُّنْۢبَثًّاۙ ','maka jadilah ia debu yang beterbangan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,7,'وَّكُنْتُمْ اَزْوَاجًا ثَلٰثَةً ۗ','dan kamu menjadi tiga golongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,8,'فَاَصْحٰبُ الْمَيْمَنَةِ ەۙ مَآ اَصْحٰبُ الْمَيْمَنَةِ ۗ','Yaitu golongan kanan. Alangkah mulianya golongan kanan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,9,'وَاَصْحٰبُ الْمَشْـَٔمَةِ ەۙ مَآ اَصْحٰبُ الْمَشْـَٔمَةِ ۗ','Dan golongan kiri. Alangkah sengsaranya golongan kiri itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,10,'وَالسّٰبِقُوْنَ السّٰبِقُوْنَۙ  ','Dan orang-orang yang beriman paling dahulu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,11,'اُولٰۤىِٕكَ الْمُقَرَّبُوْنَۚ ','Mereka itulah yang didekatkan kepada Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,12,'فِيْ جَنّٰتِ النَّعِيْمِ ','Berada dalam jannah kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,13,'ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ  ','Segolongan besar dari orang-orang yang terdahulu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,14,'وَقَلِيْلٌ مِّنَ الْاٰخِرِيْنَۗ  ','dan segolongan kecil dari orang-orang yang kemudian')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,15,'عَلٰى سُرُرٍ مَّوْضُوْنَةٍۙ  ','Mereka berada di atas dipan yang bertahta emas dan permata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,16,'مُّتَّكِـِٕيْنَ عَلَيْهَا مُتَقٰبِلِيْنَ','seraya bertelekan di atasnya berhadap-hadapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,17,'يَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۙ  ','Mereka dikelilingi oleh anak-anak muda yang tetap muda,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,18,'بِاَكْوَابٍ وَّاَبَارِيْقَۙ وَكَأْسٍ مِّنْ مَّعِيْنٍۙ  ','dengan membawa gelas, cerek dan minuman yang diambil dari air yang mengalir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,19,'لَّا يُصَدَّعُوْنَ عَنْهَا وَلَا يُنْزِفُوْنَۙ  ','mereka tidak pening karenanya dan tidak pula mabuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,20,'وَفَاكِهَةٍ مِّمَّا يَتَخَيَّرُوْنَۙ  ','dan buah-buahan dari apa yang mereka pilih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,21,'وَلَحْمِ طَيْرٍ مِّمَّا يَشْتَهُوْنَۗ  ','dan daging burung dari apa yang mereka inginkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,22,'وَحُوْرٌ عِيْنٌۙ  ','Dan ada bidadari-bidadari bermata jeli,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,23,'كَاَمْثَالِ اللُّؤْلُؤِ الْمَكْنُوْنِۚ  ','laksana mutiara yang tersimpan baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,24,'جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ','Sebagai balasan bagi apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,25,'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا تَأْثِيْمًاۙ  ','Mereka tidak mendengar di dalamnya perkataan yang sia-sia dan tidak pula perkataan yang menimbulkan dosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,26,'اِلَّا قِيْلًا سَلٰمًا سَلٰمًا ','akan tetapi mereka mendengar ucapan salam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,27,'وَاَصْحٰبُ الْيَمِينِ ەۙ مَآ اَصْحٰبُ الْيَمِيْنِۗ','Dan golongan kanan, alangkah bahagianya golongan kanan itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,28,'فِيْ سِدْرٍ مَّخْضُوْدٍۙ  ','Berada di antara pohon bidara yang tak berduri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,29,'وَّطَلْحٍ مَّنْضُوْدٍۙ  ','dan pohon pisang yang bersusun-susun (buahnya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,30,'وَّظِلٍّ مَّمْدُوْدٍۙ  ','dan naungan yang terbentang luas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,31,'وَّمَاۤءٍ مَّسْكُوْبٍۙ  ','dan air yang tercurah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,32,'وَّفَاكِهَةٍ كَثِيْرَةٍۙ  ','dan buah-buahan yang banyak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,33,'لَّا مَقْطُوْعَةٍ وَّلَا مَمْنُوْعَةٍۙ  ','yang tidak berhenti (berbuah) dan tidak terlarang mengambilnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,34,'وَّفُرُشٍ مَّرْفُوْعَةٍۗ  ','dan kasur-kasur yang tebal lagi empuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,35,'اِنَّآ اَنْشَأْنٰهُنَّ اِنْشَاۤءًۙ  ','Sesungguhnya Kami menciptakan mereka (bidadari-bidadari) dengan langsung')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,36,'فَجَعَلْنٰهُنَّ اَبْكَارًاۙ  ','dan Kami jadikan mereka gadis-gadis perawan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,37,'عُرُبًا اَتْرَابًاۙ  ','penuh cinta lagi sebaya umurnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,38,'لِّاَصْحٰبِ الْيَمِيْنِۗ  ࣖ ','(Kami ciptakan mereka) untuk golongan kanan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,39,'ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ  ','(yaitu) segolongan besar dari orang-orang yang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,40,'وَثُلَّةٌ مِّنَ الْاٰخِرِيْنَۗ  ','dan segolongan besar pula dari orang-orang yang kemudian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,41,'وَاَصْحٰبُ الشِّمَالِ ەۙ مَآ اَصْحٰبُ الشِّمَالِۗ','Dan golongan kiri, siapakah golongan kiri itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,42,'فِيْ سَمُوْمٍ وَّحَمِيْمٍۙ  ','Dalam (siksaan) angin yang amat panas, dan air panas yang mendidih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,43,'وَّظِلٍّ مِّنْ يَّحْمُوْمٍۙ  ','dan dalam naungan asap yang hitam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,44,'لَّا بَارِدٍ وَّلَا كَرِيْمٍ ','Tidak sejuk dan tidak menyenangkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,45,'اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُتْرَفِيْنَۚ ','Sesungguhnya mereka sebelum itu hidup bermewahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,46,'وَكَانُوْا يُصِرُّوْنَ عَلَى الْحِنْثِ الْعَظِيْمِۚ  ','Dan mereka terus-menerus mengerjakan dosa besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,47,'وَكَانُوْا يَقُوْلُوْنَ ەۙ اَىِٕذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ','Dan mereka selalu mengatakan: "Apakah bila kami mati dan menjadi tanah dan tulang belulang, apakah sesungguhnya kami akan benar-benar dibangkitkan kembali?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,48,'اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَ ','apakah bapak-bapak kami yang terdahulu (juga)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,49,'قُلْ اِنَّ الْاَوَّلِيْنَ وَالْاٰخِرِيْنَۙ ','Katakanlah: "Sesungguhnya orang-orang yang terdahulu dan orang-orang yang terkemudian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,50,'لَمَجْمُوْعُوْنَۙ اِلٰى مِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ','benar-benar akan dikumpulkan di waktu tertentu pada hari yang dikenal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,51,'ثُمَّ اِنَّكُمْ اَيُّهَا الضَّاۤ لُّوْنَ الْمُكَذِّبُوْنَۙ  ','Kemudian sesungguhnya kamu hai orang-orang yang sesat lagi mendustakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,52,'لَاٰكِلُوْنَ مِنْ شَجَرٍ مِّنْ زَقُّوْمٍۙ  ','benar-benar akan memakan pohon zaqqum,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,53,'فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۚ  ','dan akan memenuhi perutmu dengannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,54,'فَشَارِبُوْنَ عَلَيْهِ مِنَ الْحَمِيْمِۚ  ','Sesudah itu kamu akan meminum air yang sangat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,55,'فَشَارِبُوْنَ شُرْبَ الْهِيْمِۗ  ','Maka kamu minum seperti unta yang sangat haus minum.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,56,'هٰذَا نُزُلُهُمْ يَوْمَ الدِّيْنِۗ  ','Itulah hidangan untuk mereka pada hari Pembalasan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,57,'نَحْنُ خَلَقْنٰكُمْ فَلَوْلَا تُصَدِّقُوْنَ ','Kami telah menciptakan kamu, maka mengapa kamu tidak membenarkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,58,'اَفَرَءَيْتُمْ مَّا تُمْنُوْنَۗ  ','Maka terangkanlah kepadaku tentang nutfah yang kamu pancarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,59,'ءَاَنْتُمْ تَخْلُقُوْنَهٗٓ اَمْ نَحْنُ الْخَالِقُوْنَ ','Kamukah yang menciptakannya, atau Kamikah yang menciptakannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,60,'نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوْقِيْنَۙ  ','Kami telah menentukan kematian di antara kamu dan Kami sekali-sekali tidak akan dapat dikalahkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,61,'عَلٰٓى اَنْ نُّبَدِّلَ اَمْثَالَكُمْ وَنُنْشِئَكُمْ فِيْ مَا لَا تَعْلَمُوْنَ','untuk menggantikan kamu dengan orang-orang yang seperti kamu (dalam dunia) dan menciptakan kamu kelak (di akhirat) dalam keadaan yang tidak kamu ketahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,62,'وَلَقَدْ عَلِمْتُمُ النَّشْاَةَ الْاُوْلٰى فَلَوْلَا تَذَكَّرُوْنَ ','Dan Sesungguhnya kamu telah mengetahui penciptaan yang pertama, maka mengapakah kamu tidak mengambil pelajaran (untuk penciptaan yang kedua)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,63,'اَفَرَءَيْتُمْ مَّا تَحْرُثُوْنَۗ  ','Maka terangkanlah kepadaku tentang yang kamu tanam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,64,'ءَاَنْتُمْ تَزْرَعُوْنَهٗٓ اَمْ نَحْنُ الزَّارِعُوْنَ ','Kamukah yang menumbuhkannya atau Kamikah yang menumbuhkannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,65,'لَوْ نَشَاۤءُ لَجَعَلْنٰهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُوْنَۙ  ','Kalau Kami kehendaki, benar-benar Kami jadikan dia hancur dan kering, maka jadilah kamu heran dan tercengang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,66,'اِنَّا لَمُغْرَمُوْنَۙ  ','(Sambil berkata): "Sesungguhnya kami benar-benar menderita kerugian",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,67,'بَلْ نَحْنُ مَحْرُوْمُوْنَ ','bahkan kami menjadi orang-orang yang tidak mendapat hasil apa-apa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,68,'اَفَرَءَيْتُمُ الْمَاۤءَ الَّذِيْ تَشْرَبُوْنَۗ  ','Maka terangkanlah kepadaku tentang air yang kamu minum.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,69,'ءَاَنْتُمْ اَنْزَلْتُمُوْهُ مِنَ الْمُزْنِ اَمْ نَحْنُ الْمُنْزِلُوْنَ ','Kamukah yang menurunkannya atau Kamikah yang menurunkannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,70,'لَوْ نَشَاۤءُ جَعَلْنٰهُ اُجَاجًا فَلَوْلَا تَشْكُرُوْنَ ','Kalau Kami kehendaki, niscaya Kami jadikan dia asin, maka mengapakah kamu tidak bersyukur?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,71,'اَفَرَءَيْتُمُ النَّارَ الَّتِيْ تُوْرُوْنَۗ  ','Maka terangkanlah kepadaku tentang api yang kamu nyalakan (dengan menggosok-gosokkan kayu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,72,'ءَاَنْتُمْ اَنْشَأْتُمْ شَجَرَتَهَآ اَمْ نَحْنُ الْمُنْشِـُٔوْنَ ','Kamukah yang menjadikan kayu itu atau Kamikah yang menjadikannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,73,'نَحْنُ جَعَلْنٰهَا تَذْكِرَةً وَّمَتَاعًا لِّلْمُقْوِيْنَۚ ','Kami jadikan api itu untuk peringatan dan bahan yang berguna bagi musafir di padang pasir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,74,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ ࣖ ','Maka bertasbihlah dengan (menyebut) nama Rabbmu Yang Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,75,'فَلَآ اُقْسِمُ بِمَوٰقِعِ النُّجُوْمِ ','Maka Aku bersumpah dengan masa turunnya bagian-bagian Al-Quran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,76,'وَاِنَّهٗ لَقَسَمٌ لَّوْ تَعْلَمُوْنَ عَظِيْمٌۙ  ','Sesungguhnya sumpah itu adalah sumpah yang besar kalau kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,77,'اِنَّهٗ لَقُرْاٰنٌ كَرِيْمٌۙ  ','Sesungguhnya Al-Quran ini adalah bacaan yang sangat mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,78,'فِيْ كِتٰبٍ مَّكْنُوْنٍۙ ','pada kitab yang terpelihara (Lauhul Mahfuzh),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,79,'لَّا يَمَسُّهٗٓ اِلَّا الْمُطَهَّرُوْنَۙ  ','tidak menyentuhnya kecuali orang-orang yang disucikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,80,'تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ','Diturunkan dari Rabbil ´alamiin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,81,'اَفَبِهٰذَا الْحَدِيْثِ اَنْتُمْ مُّدْهِنُوْنَ ','Maka apakah kamu menganggap remeh saja Al-Quran ini?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,82,'وَتَجْعَلُوْنَ رِزْقَكُمْ اَنَّكُمْ تُكَذِّبُوْنَ ','kamu mengganti rezeki (yang Allah berikan) dengan mendustakan Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,83,'فَلَوْلَآ اِذَا بَلَغَتِ الْحُلْقُوْمَۙ  ','Maka mengapa ketika nyawa sampai di kerongkongan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,84,'وَاَنْتُمْ حِيْنَىِٕذٍ تَنْظُرُوْنَۙ  ','padahal kamu ketika itu melihat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,85,'وَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْكُمْ وَلٰكِنْ لَّا تُبْصِرُوْنَ ','dan Kami lebih dekat kepadanya dari pada kamu. Tetapi kamu tidak melihat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,86,'فَلَوْلَآ اِنْ كُنْتُمْ غَيْرَ مَدِيْنِيْنَۙ  ','maka mengapa jika kamu tidak dikuasai (oleh Allah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,87,'تَرْجِعُوْنَهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ','Kamu tidak mengembalikan nyawa itu (kepada tempatnya) jika kamu adalah orang-orang yang benar?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,88,'فَاَمَّآ اِنْ كَانَ مِنَ الْمُقَرَّبِيْنَۙ  ','adapun jika dia (orang yang mati) termasuk orang-orang yang didekatkan (kepada Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,89,'فَرَوْحٌ وَّرَيْحَانٌ ەۙ وَّجَنَّتُ نَعِيْمٍ','maka dia memperoleh ketenteraman dan rezeki serta jannah kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,90,'وَاَمَّآ اِنْ كَانَ مِنْ اَصْحٰبِ الْيَمِيْنِۙ  ','Dan adapun jika dia termasuk golongan kanan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,91,'فَسَلٰمٌ لَّكَ مِنْ اَصْحٰبِ الْيَمِيْنِۗ  ','maka keselamatanlah bagimu karena kamu dari golongan kanan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,92,'وَاَمَّآ اِنْ كَانَ مِنَ الْمُكَذِّبِيْنَ الضَّاۤلِّيْنَۙ  ','Dan adapun jika dia termasuk golongan yang mendustakan lagi sesat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,93,'فَنُزُلٌ مِّنْ حَمِيْمٍۙ  ','maka dia mendapat hidangan air yang mendidih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,94,'وَّتَصْلِيَةُ جَحِيْمٍ ','dan dibakar di dalam jahannam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,95,'اِنَّ هٰذَا لَهُوَ حَقُّ الْيَقِيْنِۚ  ','Sesungguhnya (yang disebutkan ini) adalah suatu keyakinan yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,56,96,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ ࣖ ','Maka bertasbihlah dengan (menyebut) nama Rabbmu yang Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,1,'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Semua yang berada di langit dan yang berada di bumi bertasbih kepada Allah (menyatakan kebesaran Allah). Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,2,'لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ يُحْيٖ وَيُمِيْتُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Kepunyaan-Nya-lah kerajaan langit dan bumi, Dia menghidupkan dan mematikan, dan Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,3,'هُوَ الْاَوَّلُ وَالْاٰخِرُ وَالظَّاهِرُ وَالْبَاطِنُۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Dialah Yang Awal dan Yang Akhir Yang Zhahir dan Yang Bathin; dan Dia Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,4,'هُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ مَعَكُمْ اَيْنَ مَا كُنْتُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌۗ','Dialah yang menciptakan langit dan bumi dalam enam masa: Kemudian Dia bersemayam di atas ´Arsy. Dia mengetahui apa yang masuk ke dalam bumi dan apa yang keluar daripadanya dan apa yang turun dari langit dan apa yang naik kepada-Nya. Dan Dia bersama kamu di mana saja kamu berada. Dan Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,5,'لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ','Kepunyaan-Nya-lah kerajaan langit dan bumi. Dan kepada Allah-lah dikembalikan segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,6,'يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۗ وَهُوَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ','Dialah yang memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam. Dan Dia Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,7,'اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاَنْفِقُوْا مِمَّا جَعَلَكُمْ مُّسْتَخْلَفِيْنَ فِيْهِۗ فَالَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَاَنْفَقُوْا لَهُمْ اَجْرٌ كَبِيْرٌ','Berimanlah kamu kepada Allah dan Rasul-Nya dan nafkahkanlah sebagian dari hartamu yang Allah telah menjadikan kamu menguasainya. Maka orang-orang yang beriman di antara kamu dan menafkahkan (sebagian) dari hartanya memperoleh pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,8,'وَمَا لَكُمْ لَا تُؤْمِنُوْنَ بِاللّٰهِ ۚوَالرَّسُوْلُ يَدْعُوْكُمْ لِتُؤْمِنُوْا بِرَبِّكُمْ وَقَدْ اَخَذَ مِيْثَاقَكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ','Dan mengapa kamu tidak beriman kepada Allah padahal Rasul menyeru kamu supaya kamu beriman kepada Tuhanmu. Dan sesungguhnya Dia telah mengambil perjanjianmu jika kamu adalah orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,9,'هُوَ الَّذِيْ يُنَزِّلُ عَلٰى عَبْدِهٖٓ اٰيٰتٍۢ بَيِّنٰتٍ لِّيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَاِنَّ اللّٰهَ بِكُمْ لَرَءُوْفٌ رَّحِيْمٌ ','Dialah yang menurunkan kepada hamba-Nya ayat-ayat yang terang (Al-Quran) supaya Dia mengeluarkan kamu dari kegelapan kepada cahaya. Dan sesungguhnya Allah benar-benar Maha Penyantun lagi Maha Penyayang terhadapmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,10,'وَمَا لَكُمْ اَلَّا تُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ  لَا يَسْتَوِيْ مِنْكُمْ مَّنْ اَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَۗ  اُولٰۤىِٕكَ اَعْظَمُ دَرَجَةً مِّنَ الَّذِيْنَ اَنْفَقُوْا مِنْۢ بَعْدُ وَقَاتَلُوْاۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ࣖ ','Dan mengapa kamu tidak menafkahkan (sebagian hartamu) pada jalan Allah, padahal Allah-lah yang mempusakai (mempunyai) langit dan bumi? Tidak sama di antara kamu orang yang menafkahkan (hartanya) dan berperang sebelum penaklukan (Mekah). Mereka lebih tingi derajatnya daripada orang-orang yang menafkahkan (hartanya) dan berperang sesudah itu. Allah menjanjikan kepada masing-masing mereka (balasan) yang lebih baik. Dan Allah mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,11,'مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗ وَلَهٗٓ اَجْرٌ كَرِيْمٌ ','Siapakah yang mau meminjamkan kepada Allah pinjaman yang baik, maka Allah akan melipat-gandakan (balasan) pinjaman itu untuknya, dan dia akan memperoleh pahala yang banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,12,'يَوْمَ تَرَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ يَسْعٰى نُوْرُهُمْ بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ بُشْرٰىكُمُ الْيَوْمَ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۚ ','(yaitu) pada hari ketika kamu melihat orang mukmin laki-laki dan perempuan, sedang cahaya mereka bersinar di hadapan dan di sebelah kanan mereka, (dikatakan kepada mereka): "Pada hari ini ada berita gembira untukmu, (yaitu) surga yang mengalir di bawahnya sungai-sungai, yang kamu kekal di dalamnya. Itulah keberuntungan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,13,'يَوْمَ يَقُوْلُ الْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ لِلَّذِيْنَ اٰمَنُوا انْظُرُوْنَا نَقْتَبِسْ مِنْ نُّوْرِكُمْۚ قِيْلَ ارْجِعُوْا وَرَاۤءَكُمْ فَالْتَمِسُوْا نُوْرًاۗ فَضُرِبَ بَيْنَهُمْ بِسُوْرٍ لَّهٗ بَابٌۗ بَاطِنُهٗ فِيْهِ الرَّحْمَةُ وَظَاهِرُهٗ مِنْ قِبَلِهِ الْعَذَابُۗ','Pada hari ketika orang-orang munafik laki-laki dan perempuan berkata kepada orang-orang yang beriman: "Tunggulah kami supaya kami dapat mengambil sebahagian dari cahayamu". Dikatakan (kepada mereka): "Kembalilah kamu ke belakang dan carilah sendiri cahaya (untukmu)". Lalu diadakan di antara mereka dinding yang mempunyai pintu. Di sebelah dalamnya ada rahmat dan di sebelah luarnya dari situ ada siksa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,14,'يُنَادُوْنَهُمْ اَلَمْ نَكُنْ مَّعَكُمْۗ قَالُوْا بَلٰى وَلٰكِنَّكُمْ فَتَنْتُمْ اَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْاَمَانِيُّ حَتّٰى جَاۤءَ اَمْرُ اللّٰهِ وَغَرَّكُمْ بِاللّٰهِ الْغَرُوْرُ ','Orang-orang munafik itu memanggil mereka (orang-orang mukmin) seraya berkata: "Bukankah kami dahulu bersama-sama dengan kamu?" Mereka menjawab: "Benar, tetapi kamu mencelakakan dirimu sendiri dan menunggu (kehancuran kami) dan kamu ragu-ragu serta ditipu oleh angan-angan kosong sehingga datanglah ketetapan Allah;dan kamu telah ditipu terhadap Allah oleh (syaitan) yang amat penipu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,15,'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَّلَا مِنَ الَّذِيْنَ كَفَرُوْاۗ مَأْوٰىكُمُ النَّارُۗ هِيَ مَوْلٰىكُمْۗ وَبِئْسَ الْمَصِيْرُ ','Maka pada hari ini tidak diterima tebusan dari kamu dan tidak pula dari orang-orang kafir. Tempat kamu ialah neraka. Dialah tempat berlindungmu. Dan dia adalah sejahat-jahat tempat kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,16,'اَلَمْ يَأْنِ لِلَّذِيْنَ اٰمَنُوْٓا اَنْ تَخْشَعَ قُلُوْبُهُمْ لِذِكْرِ اللّٰهِ وَمَا نَزَلَ مِنَ الْحَقِّۙ وَلَا يَكُوْنُوْا كَالَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْاَمَدُ فَقَسَتْ قُلُوْبُهُمْۗ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ  ','Belumkah datang waktunya bagi orang-orang yang beriman, untuk tunduk hati mereka mengingat Allah dan kepada kebenaran yang telah turun (kepada mereka), dan janganlah mereka seperti orang-orang yang sebelumnya telah diturunkan Al Kitab kepadanya, kemudian berlalulah masa yang panjang atas mereka lalu hati mereka menjadi keras. Dan kebanyakan di antara mereka adalah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,17,'اِعْلَمُوْٓا اَنَّ اللّٰهَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ ','Ketahuilah olehmu bahwa sesungguhnya Allah menghidupkan bumi sesudah matinya. Sesungguhnya Kami telah menjelaskan kepadamu tanda-tanda kebesaran (Kami) supaya kamu memikirkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,18,'اِنَّ الْمُصَّدِّقِيْنَ وَالْمُصَّدِّقٰتِ وَاَقْرَضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعَفُ لَهُمْ وَلَهُمْ اَجْرٌ كَرِيْمٌ ','Sesungguhnya orang-orang yang membenarkan (Allah dan Rasul-Nya) baik laki-laki maupun perempuan dan meminjamkan kepada Allah pinjaman yang baik, niscaya akan dilipatgandakan (pembayarannya) kepada mereka; dan bagi mereka pahala yang banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,19,'وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖٓ اُولٰۤىِٕكَ هُمُ الصِّدِّيْقُوْنَ ۖوَالشُّهَدَاۤءُ عِنْدَ رَبِّهِمْۗ  لَهُمْ اَجْرُهُمْ وَنُوْرُهُمْۗ وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ࣖ','Dan orang-orang yang beriman kepada Allah dan Rasul-Nya, mereka itu orang-orang Shiddiqien dan orang-orang yang menjadi saksi di sisi Tuhan mereka. Bagi mereka pahala dan cahaya mereka. Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni-penghuni neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,20,'اِعْلَمُوْٓا اَنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ وَّزِيْنَةٌ وَّتَفَاخُرٌۢ بَيْنَكُمْ وَتَكَاثُرٌ فِى الْاَمْوَالِ وَالْاَوْلَادِۗ  كَمَثَلِ غَيْثٍ اَعْجَبَ الْكُفَّارَ نَبَاتُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَكُوْنُ حُطَامًاۗ وَفِى الْاٰخِرَةِ عَذَابٌ شَدِيْدٌۙ وَّمَغْفِرَةٌ مِّنَ اللّٰهِ وَرِضْوَانٌ  ۗوَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ ','Ketahuilah, bahwa sesungguhnya kehidupan dunia ini hanyalah permainan dan suatu yang melalaikan, perhiasan dan bermegah-megah antara kamu serta berbangga-banggaan tentang banyaknya harta dan anak, seperti hujan yang tanam-tanamannya mengagumkan para petani; kemudian tanaman itu menjadi kering dan kamu lihat warnanya kuning kemudian menjadi hancur. Dan di akhirat (nanti) ada azab yang keras dan ampunan dari Allah serta keridhaan-Nya. Dan kehidupan dunia ini tidak lain hanyalah kesenangan yang menipu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,21,'سَابِقُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاۤءِ وَالْاَرْضِۙ اُعِدَّتْ لِلَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖۗ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۚوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ','Berlomba-lombalah kamu kepada (mendapatkan) ampunan dari Tuhanmu dan surga yang luasnya seluas langit dan bumi, yang disediakan bagi orang-orang yang beriman kepada Allah dan Rasul-rasul-Nya. Itulah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya. Dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,22,'مَآ اَصَابَ مِنْ مُّصِيْبَةٍ فِى الْاَرْضِ وَلَا فِيْٓ اَنْفُسِكُمْ اِلَّا فِيْ كِتٰبٍ مِّنْ قَبْلِ اَنْ نَّبْرَاَهَا ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌۖ  ','Tiada suatu bencanapun yang menimpa di bumi dan (tidak pula) pada dirimu sendiri melainkan telah tertulis dalam kitab (Lauhul Mahfuzh) sebelum Kami menciptakannya. Sesungguhnya yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,23,'لِّكَيْلَا تَأْسَوْا عَلٰى مَا فَاتَكُمْ وَلَا تَفْرَحُوْا بِمَآ اٰتٰىكُمْ ۗوَاللّٰهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۙ','(Kami jelaskan yang demikian itu) supaya kamu jangan berduka cita terhadap apa yang luput dari kamu, dan supaya kamu jangan terlalu gembira terhadap apa yang diberikan-Nya kepadamu. Dan Allah tidak menyukai setiap orang yang sombong lagi membanggakan diri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,24,' ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ ۗوَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ ','(yaitu) orang-orang yang kikir dan menyuruh manusia berbuat kikir. Dan barangsiapa yang berpaling (dari perintah-perintah Allah) maka sesungguhnya Allah Dialah Yang Maha Kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,25,'لَقَدْ اَرْسَلْنَا رُسُلَنَا بِالْبَيِّنٰتِ وَاَنْزَلْنَا مَعَهُمُ الْكِتٰبَ وَالْمِيْزَانَ لِيَقُوْمَ النَّاسُ بِالْقِسْطِۚ وَاَنْزَلْنَا الْحَدِيْدَ فِيْهِ بَأْسٌ شَدِيْدٌ وَّمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللّٰهُ مَنْ يَّنْصُرُهٗ وَرُسُلَهٗ بِالْغَيْبِۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ ࣖ ','Sesungguhnya Kami telah mengutus rasul-rasul Kami dengan membawa bukti-bukti yang nyata dan telah Kami turunkan bersama mereka Al Kitab dan neraca (keadilan) supaya manusia dapat melaksanakan keadilan. Dan Kami ciptakan besi yang padanya terdapat kekuatan yang hebat dan berbagai manfaat bagi manusia, (supaya mereka mempergunakan besi itu) dan supaya Allah mengetahui siapa yang menolong (agama)Nya dan rasul-rasul-Nya padahal Allah tidak dilihatnya. Sesungguhnya Allah Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,26,'وَلَقَدْ اَرْسَلْنَا نُوْحًا وَّاِبْرٰهِيْمَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتٰبَ فَمِنْهُمْ مُّهْتَدٍۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ','Dan sesungguhnya Kami telah mengutus Nuh dan Ibrahim dan Kami jadikan kepada keturunan keduanya kenabian dan Al Kitab, maka di antara mereka ada yang menerima petunjuk dan banyak di antara mereka fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,27,'ثُمَّ قَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيْسَى ابْنِ مَرْيَمَ وَاٰتَيْنٰهُ الْاِنْجِيْلَ ەۙ وَجَعَلْنَا فِيْ قُلُوْبِ الَّذِيْنَ اتَّبَعُوْهُ رَأْفَةً وَّرَحْمَةً  ۗوَرَهْبَانِيَّةَ ِۨابْتَدَعُوْهَا مَا كَتَبْنٰهَا عَلَيْهِمْ اِلَّا ابْتِغَاۤءَ رِضْوَانِ اللّٰهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۚفَاٰتَيْنَا الَّذِيْنَ اٰمَنُوْا مِنْهُمْ اَجْرَهُمْ ۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ','Kemudian Kami iringi di belakang mereka dengan rasul-rasul Kami dan Kami iringi (pula) dengan Isa putra Maryam; dan Kami berikan kepadanya Injil dan Kami jadikan dalam hati orang-orang yang mengikutinya rasa santun dan kasih sayang. Dan mereka mengada-adakan rahbaniyyah padahal kami tidak mewajibkannya kepada mereka tetapi (mereka sendirilah yang mengada-adakannya) untuk mencari keridhaan Allah, lalu mereka tidak memeliharanya dengan pemeliharaan yang semestinya. Maka Kami berikan kepada orang-orang yang beriman di antara mereka pahalanya dan banyak di antara mereka orang-orang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,28,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَاٰمِنُوْا بِرَسُوْلِهٖ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَّحْمَتِهٖ وَيَجْعَلْ لَّكُمْ نُوْرًا تَمْشُوْنَ بِهٖ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ','Hai orang-orang yang beriman (kepada para rasul), bertakwalah kepada Allah dan berimanlah kepada Rasul-Nya, niscaya Allah memberikan rahmat-Nya kepadamu dua bagian, dan menjadikan untukmu cahaya yang dengan cahaya itu kamu dapat berjalan dan Dia mengampuni kamu. Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,27,57,29,'لِّئَلَّا يَعْلَمَ اَهْلُ الْكِتٰبِ اَلَّا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّنْ فَضْلِ اللّٰهِ وَاَنَّ الْفَضْلَ بِيَدِ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ࣖ   ۔','(Kami terangkan yang demikian itu) supaya ahli Kitab mengetahui bahwa mereka tiada mendapat sedikitpun akan karunia Allah (jika mereka tidak beriman kepada Muhammad), dan bahwasanya karunia itu adalah di tangan Allah. Dia berikan karunia itu kepada siapa yang dikehendaki-Nya. Dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,1,'قَدْ سَمِعَ اللّٰهُ قَوْلَ الَّتِيْ تُجَادِلُكَ فِيْ زَوْجِهَا وَتَشْتَكِيْٓ اِلَى اللّٰهِ  ۖوَاللّٰهُ يَسْمَعُ تَحَاوُرَكُمَاۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ','Sesungguhnya Allah telah mendengar perkataan wanita yang mengajukan gugatan kepada kamu tentang suaminya, dan mengadukan (halnya) kepada Allah. Dan Allah mendengar soal jawab antara kamu berdua. Sesungguhnya Allah Maha Mendengar lagi Maha Melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,2,'اَلَّذِيْنَ يُظٰهِرُوْنَ مِنْكُمْ مِّنْ نِّسَاۤىِٕهِمْ مَّا هُنَّ اُمَّهٰتِهِمْۗ اِنْ اُمَّهٰتُهُمْ اِلَّا الّٰۤـِٔيْ وَلَدْنَهُمْۗ وَاِنَّهُمْ لَيَقُوْلُوْنَ مُنْكَرًا مِّنَ الْقَوْلِ وَزُوْرًاۗ وَاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ ','Orang-orang yang menzhihar isterinya di antara kamu, (menganggap isterinya sebagai ibunya, padahal) tiadalah isteri mereka itu ibu mereka. Ibu-ibu mereka tidak lain hanyalah wanita yang melahirkan mereka. Dan sesungguhnya mereka sungguh-sungguh mengucapkan suatu perkataan mungkar dan dusta. Dan sesungguhnya Allah Maha Pemaaf lagi Maha Pengampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,3,'وَالَّذِيْنَ يُظٰهِرُوْنَ مِنْ نِّسَاۤىِٕهِمْ ثُمَّ يَعُوْدُوْنَ لِمَا قَالُوْا فَتَحْرِيْرُ رَقَبَةٍ مِّنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ ذٰلِكُمْ تُوْعَظُوْنَ بِهٖۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ','Orang-orang yang menzhihar isteri mereka, kemudian mereka hendak menarik kembali apa yang mereka ucapkan, maka (wajib atasnya) memerdekakan seorang budak sebelum kedua suami isteri itu bercampur. Demikianlah yang diajarkan kepada kamu, dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,4,'فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ فَمَنْ لَّمْ يَسْتَطِعْ فَاِطْعَامُ سِتِّيْنَ مِسْكِيْنًاۗ ذٰلِكَ لِتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ ','Barangsiapa yang tidak mendapatkan (budak), maka (wajib atasnya) berpuasa dua bulan berturut-turut sebelum keduanya bercampur. Maka siapa yang tidak kuasa (wajiblah atasnya) memberi makan enam puluh orang miskin. Demikianlah supaya kamu beriman kepada Allah dan Rasul-Nya. Dan itulah hukum-hukum Allah, dan bagi orang kafir ada siksaan yang sangat pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,5,'اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗ كُبِتُوْا كَمَا كُبِتَ الَّذِيْنَ مِنْ قَبْلِهِمْ وَقَدْ اَنْزَلْنَآ اٰيٰتٍۢ بَيِّنٰتٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌۚ ','Sesungguhnya orang-orang yang yang menentang Allah dan Rasul-Nya, pasti mendapat kehinaan sebagaimana orang-orang yang sebelum mereka telah mendapat kehinaan. Sesungguhnya Kami telah menurunkan bukti-bukti nyata. Dan bagi orang-orang kafir ada siksa yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,6,'يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ  اَحْصٰىهُ اللّٰهُ وَنَسُوْهُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ࣖ','Pada hari ketika mereka dibangkitkan Allah semuanya, lalu diberitakan-Nya kepada mereka apa yang telah mereka kerjakan. Allah mengumpulkan (mencatat) amal perbuatan itu, padahal mereka telah melupakannya. Dan Allah Maha Menyaksikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,7,'اَلَمْ تَرَ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَا يَكُوْنُ مِنْ نَّجْوٰى ثَلٰثَةٍ اِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ اِلَّا هُوَ سَادِسُهُمْ وَلَآ اَدْنٰى مِنْ ذٰلِكَ وَلَآ اَكْثَرَ اِلَّا هُوَ مَعَهُمْ اَيْنَ مَا كَانُوْاۚ  ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوْا يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Tidakkah kamu perhatikan, bahwa sesungguhnya Allah mengetahui apa yang ada di langit dan di bumi? Tiada pembicaraan rahasia antara tiga orang, melainkan Dialah keempatnya. Dan tiada (pembicaraan antara) lima orang, melainkan Dialah keenamnya. Dan tiada (pula) pembicaraan antara jumlah yang kurang dari itu atau lebih banyak, melainkan Dia berada bersama mereka di manapun mereka berada. Kemudian Dia akan memberitahukan kepada mereka pada hari kiamat apa yang telah mereka kerjakan. Sesungguhnya Allah Maha mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,8,'اَلَمْ تَرَ اِلَى الَّذِيْنَ نُهُوْا عَنِ النَّجْوٰى ثُمَّ يَعُوْدُوْنَ لِمَا نُهُوْا عَنْهُ وَيَتَنٰجَوْنَ بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِۖ وَاِذَا جَاۤءُوْكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللّٰهُ ۙوَيَقُوْلُوْنَ فِيْٓ اَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللّٰهُ بِمَا نَقُوْلُۗ حَسْبُهُمْ جَهَنَّمُۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمَصِيْرُ ','Apakah tidak kamu perhatikan orang-orang yang telah dilarang mengadakan pembicaraan rahasia, kemudian mereka kembali (mengerjakan) larangan itu dan mereka mengadakan pembicaraan rahasia untuk berbuat dosa, permusuhan dan durhaka kepada Rasul. Dan apabila mereka datang kepadamu, mereka mengucapkan salam kepadamu dengan memberi salam yang bukan sebagai yang ditentukan Allah untukmu. Dan mereka mengatakan kepada diri mereka sendiri: "Mengapa Allah tidak menyiksa kita disebabkan apa yang kita katakan itu?" Cukuplah bagi mereka Jahannam yang akan mereka masuki. Dan neraka itu adalah seburuk-buruk tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,9,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوٰىۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ','Hai orang-orang beriman, apabila kamu mengadakan pembicaraan rahasia, janganlah kamu membicarakan tentang membuat dosa, permusuhan dan berbuat durhaka kepada Rasul. Dan bicarakanlah tentang membuat kebajikan dan takwa. Dan bertakwalah kepada Allah yang kepada-Nya kamu akan dikembalikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,10,'اِنَّمَا النَّجْوٰى مِنَ الشَّيْطٰنِ لِيَحْزُنَ الَّذِيْنَ اٰمَنُوْا وَلَيْسَ بِضَاۤرِّهِمْ شَيْـًٔا اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ','Sesungguhnya pembicaraan rahasia itu adalah dari syaitan, supaya orang-orang yang beriman itu berduka cita, sedang pembicaraan itu tiadalah memberi mudharat sedikitpun kepada mereka, kecuali dengan izin Allah dan kepada Allah-lah hendaknya orang-orang yang beriman bertawakkal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,11,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قِيْلَ لَكُمْ تَفَسَّحُوْا فِى الْمَجٰلِسِ فَافْسَحُوْا يَفْسَحِ اللّٰهُ لَكُمْۚ وَاِذَا قِيْلَ انْشُزُوْا فَانْشُزُوْا يَرْفَعِ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْۙ وَالَّذِيْنَ اُوْتُوا الْعِلْمَ دَرَجٰتٍۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ','Hai orang-orang beriman apabila dikatakan kepadamu: "Berlapang-lapanglah dalam majlis", maka lapangkanlah niscaya Allah akan memberi kelapangan untukmu. Dan apabila dikatakan: "Berdirilah kamu", maka berdirilah, niscaya Allah akan meninggikan orang-orang yang beriman di antaramu dan orang-orang yang diberi ilmu pengetahuan beberapa derajat. Dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,12,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَاجَيْتُمُ الرَّسُوْلَ فَقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقَةً ۗذٰلِكَ خَيْرٌ لَّكُمْ وَاَطْهَرُۗ فَاِنْ لَّمْ تَجِدُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Hai orang-orang beriman, apabila kamu mengadakan pembicaraan khusus dengan Rasul hendaklah kamu mengeluarkan sedekah (kepada orang miskin) sebelum pembicaraan itu. Yang demikian itu lebih baik bagimu dan lebih bersih; jika kamu tidak memperoleh (yang akan disedekahkan) maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,13,'ءَاَشْفَقْتُمْ اَنْ تُقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقٰتٍۗ فَاِذْ لَمْ تَفْعَلُوْا وَتَابَ اللّٰهُ عَلَيْكُمْ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ ۗوَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ࣖ ','Apakah kamu takut akan (menjadi miskin) karena kamu memberikan sedekah sebelum mengadakan pembicaraan dengan Rasul? Maka jika kamu tiada memperbuatnya dan Allah telah memberi taubat kepadamu maka dirikanlah shalat, tunaikanlah zakat, taatlah kepada Allah dan Rasul-Nya; dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,14,'۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ تَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْۗ مَا هُمْ مِّنْكُمْ وَلَا مِنْهُمْۙ وَيَحْلِفُوْنَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُوْنَ ','Tidakkah kamu perhatikan orang-orang yang menjadikan suatu kaum yang dimurkai Allah sebagai teman? Orang-orang itu bukan dari golongan kamu dan bukan (pula) dari golongan mereka. Dan mereka bersumpah untuk menguatkan kebohongan, sedang mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,15,'اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًاۗ اِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ','Allah telah menyediakan bagi mereka azab yang sangat keras, sesungguhnya amat buruklah apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,16,'اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ فَلَهُمْ عَذَابٌ مُّهِيْنٌ ','Mereka menjadikan sumpah-sumpah mereka sebagai perisai, lalu mereka halangi (manusia) dari jalan Allah; karena itu mereka mendapat azab yang menghinakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,17,'لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔاۗ  اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۗ هُمْ فِيْهَا خٰلِدُوْنَ ','Harta benda dan anak-anak mereka tiada berguna sedikitpun (untuk menolong) mereka dari azab Allah. Mereka itulah penghuni neraka, dan mereka kekal di dalamnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,18,'يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيَحْلِفُوْنَ لَهٗ كَمَا يَحْلِفُوْنَ لَكُمْ وَيَحْسَبُوْنَ اَنَّهُمْ عَلٰى شَيْءٍۗ  اَلَآ اِنَّهُمْ هُمُ الْكٰذِبُوْنَ','(Ingatlah) hari (ketika) mereka semua dibangkitkan Allah lalu mereka bersumpah kepada-Nya (bahwa mereka bukan musyrikin) sebagaimana mereka bersumpah kepadamu; dan mereka menyangka bahwa mereka akan memperoleh suatu (manfaat). Ketahuilah, bahwa sesungguhnya merekalah orang-orang pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,19,'اِسْتَحْوَذَ عَلَيْهِمُ الشَّيْطٰنُ فَاَنْسٰىهُمْ ذِكْرَ اللّٰهِ ۗ اُولٰۤىِٕكَ حِزْبُ الشَّيْطٰنِۗ  اَلَآ اِنَّ حِزْبَ الشَّيْطٰنِ هُمُ الْخٰسِرُوْنَ','Syaitan telah menguasai mereka lalu menjadikan mereka lupa mengingat Allah; mereka itulah golongan syaitan. Ketahuilah, bahwa sesungguhnya golongan syaitan itulah golongan yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,20,'اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗٓ اُولٰۤىِٕكَ فِى الْاَذَلِّيْنَ','Sesungguhnya orang-orang yang menentang Allah dan Rasul-Nya, mereka termasuk orang-orang yang sangat hina.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,21,'كَتَبَ اللّٰهُ لَاَغْلِبَنَّ اَنَا۠ وَرُسُلِيْۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ ','Allah telah menetapkan: "Aku dan rasul-rasul-Ku pasti menang". Sesungguhnya Allah Maha Kuat lagi Maha Perkasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,58,22,'لَا تَجِدُ قَوْمًا يُّؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ يُوَاۤدُّوْنَ مَنْ حَاۤدَّ اللّٰهَ وَرَسُوْلَهٗ وَلَوْ كَانُوْٓا اٰبَاۤءَهُمْ اَوْ اَبْنَاۤءَهُمْ اَوْ اِخْوَانَهُمْ اَوْ عَشِيْرَتَهُمْۗ  اُولٰۤىِٕكَ كَتَبَ فِيْ قُلُوْبِهِمُ الْاِيْمَانَ وَاَيَّدَهُمْ بِرُوْحٍ مِّنْهُ ۗوَيُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُۗ  اُولٰۤىِٕكَ حِزْبُ اللّٰهِ ۗ اَلَآ اِنَّ حِزْبَ اللّٰهِ هُمُ الْمُفْلِحُوْنَ ࣖ ','Kamu tak akan mendapati kaum yang beriman pada Allah dan hari akhirat, saling berkasih-sayang dengan orang-orang yang menentang Allah dan Rasul-Nya, sekalipun orang-orang itu bapak-bapak, atau anak-anak atau saudara-saudara ataupun keluarga mereka. Mereka itulah orang-orang yang telah menanamkan keimanan dalam hati mereka dan menguatkan mereka dengan pertolongan yang datang daripada-Nya. Dan dimasukan-Nya mereka ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Allah ridha terhadap mereka, dan merekapun merasa puas terhadap (limpahan rahmat)-Nya. Mereka itulah golongan Allah. Ketahuilah, bahwa sesungguhnya hizbullah itu adalah golongan yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,1,'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Telah bertasbih kepada Allah apa yang ada di langit dan bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,2,'هُوَ الَّذِيْٓ اَخْرَجَ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ مِنْ دِيَارِهِمْ لِاَوَّلِ الْحَشْرِۗ مَا ظَنَنْتُمْ اَنْ يَّخْرُجُوْا وَظَنُّوْٓا اَنَّهُمْ مَّانِعَتُهُمْ حُصُوْنُهُمْ مِّنَ اللّٰهِ فَاَتٰىهُمُ اللّٰهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوْا وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ يُخْرِبُوْنَ بُيُوْتَهُمْ بِاَيْدِيْهِمْ وَاَيْدِى الْمُؤْمِنِيْنَۙ فَاعْتَبِرُوْا يٰٓاُولِى الْاَبْصَارِ ','Dialah yang mengeluarkan orang-orang kafir di antara ahli kitab dari kampung-kampung mereka pada saat pengusiran yang pertama. Kamu tidak menyangka, bahwa mereka akan keluar dan merekapun yakin, bahwa benteng-benteng mereka dapat mempertahankan mereka dari (siksa) Allah; maka Allah mendatangkan kepada mereka (hukuman) dari arah yang tidak mereka sangka-sangka. Dan Allah melemparkan ketakutan dalam hati mereka; mereka memusnahkan rumah-rumah mereka dengan tangan mereka sendiri dan tangan orang-orang mukmin. Maka ambillah (kejadian itu) untuk menjadi pelajaran, hai orang-orang yang mempunyai wawasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,3,'وَلَوْلَآ اَنْ كَتَبَ اللّٰهُ عَلَيْهِمُ الْجَلَاۤءَ لَعَذَّبَهُمْ فِى الدُّنْيَاۗ وَلَهُمْ فِى الْاٰخِرَةِ عَذَابُ النَّارِ ','Dan jika tidaklah karena Allah telah menetapkan pengusiran terhadap mereka, benar-benar Allah mengazab mereka di dunia. Dan bagi mereka di akhirat azab neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,4,'ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗ  ۖوَمَنْ يُّشَاۤقِّ اللّٰهَ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ','Yang demikian itu adalah karena Sesungguhnya mereka menentang Allah dan Rasul-Nya. Barangsiapa menentang Allah dan Rasul-Nya, Sesungguhnya Allah sangat keras hukuman-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,5,'مَا قَطَعْتُمْ مِّنْ لِّيْنَةٍ اَوْ تَرَكْتُمُوْهَا قَاۤىِٕمَةً عَلٰٓى اُصُوْلِهَا فَبِاِذْنِ اللّٰهِ وَلِيُخْزِيَ الْفٰسِقِيْنَ ','Apa saja yang kamu tebang dari pohon kurma (milik orang-orang kafir) atau yang kamu biarkan (tumbuh) berdiri di atas pokoknya, maka (semua itu) adalah dengan izin Allah; dan karena Dia hendak memberikan kehinaan kepada orang-orang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,6,'وَمَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْهُمْ فَمَآ اَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَّلَا رِكَابٍ وَّلٰكِنَّ اللّٰهَ يُسَلِّطُ رُسُلَهٗ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Dan apa saja harta rampasan (fai-i) yang diberikan Allah kepada Rasul-Nya (dari harta benda) mereka, maka untuk mendapatkan itu kamu tidak mengerahkan seekor kudapun dan (tidak pula) seekor untapun, tetapi Allah yang memberikan kekuasaan kepada Rasul-Nya terhadap apa saja yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,7,'مَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْ اَهْلِ الْقُرٰى فَلِلّٰهِ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِۙ كَيْ لَا يَكُوْنَ دُوْلَةً ۢ بَيْنَ الْاَغْنِيَاۤءِ مِنْكُمْۗ وَمَآ اٰتٰىكُمُ الرَّسُوْلُ فَخُذُوْهُ وَمَا نَهٰىكُمْ عَنْهُ فَانْتَهُوْاۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۘ ','Apa saja harta rampasan (fai-i) yang diberikan Allah kepada Rasul-Nya (dari harta benda) yang berasal dari penduduk kota-kota maka adalah untuk Allah, untuk Rasul, kaum kerabat, anak-anak yatim, orang-orang miskin dan orang-orang yang dalam perjalanan, supaya harta itu jangan beredar di antara orang-orang kaya saja di antara kamu. Apa yang diberikan Rasul kepadamu, maka terimalah. Dan apa yang dilarangnya bagimu, maka tinggalkanlah. Dan bertakwalah kepada Allah. Sesungguhnya Allah amat keras hukumannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,8,'لِلْفُقَرَاۤءِ الْمُهٰجِرِيْنَ الَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ وَاَمْوَالِهِمْ يَبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا وَّيَنْصُرُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَۚ ','(Juga) bagi orang fakir yang berhijrah yang diusir dari kampung halaman dan dari harta benda mereka (karena) mencari karunia dari Allah dan keridhaan-Nya dan mereka menolong Allah dan Rasul-Nya. Mereka itulah orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,9,'وَالَّذِيْنَ تَبَوَّءُو الدَّارَ وَالْاِيْمَانَ مِنْ قَبْلِهِمْ يُحِبُّوْنَ مَنْ هَاجَرَ اِلَيْهِمْ وَلَا يَجِدُوْنَ فِيْ صُدُوْرِهِمْ حَاجَةً مِّمَّآ اُوْتُوْا وَيُؤْثِرُوْنَ عَلٰٓى اَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ  ۗوَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَۚ ','Dan orang-orang yang telah menempati kota Madinah dan telah beriman (Anshor) sebelum (kedatangan) mereka (Muhajirin), mereka (Anshor) ´mencintai´ orang yang berhijrah kepada mereka (Muhajirin). Dan mereka (Anshor) tiada menaruh keinginan dalam hati mereka terhadap apa-apa yang diberikan kepada mereka (Muhajirin); dan mereka mengutamakan (orang-orang Muhajirin), atas diri mereka sendiri, sekalipun mereka dalam kesusahan. Dan siapa yang dipelihara dari kekikiran dirinya, mereka itulah orang orang yang beruntung')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,10,'وَالَّذِيْنَ جَاۤءُوْ مِنْۢ بَعْدِهِمْ يَقُوْلُوْنَ رَبَّنَا اغْفِرْ لَنَا وَلِاِخْوَانِنَا الَّذِيْنَ سَبَقُوْنَا بِالْاِيْمَانِ وَلَا تَجْعَلْ فِيْ قُلُوْبِنَا غِلًّا لِّلَّذِيْنَ اٰمَنُوْا رَبَّنَآ اِنَّكَ رَءُوْفٌ رَّحِيْمٌ ࣖ ','Dan orang-orang yang datang sesudah mereka (Muhajirin dan Anshor), mereka berdoa: "Ya Rabb kami, beri ampunlah kami dan saudara-saudara kami yang telah beriman lebih dulu dari kami, dan janganlah Engkau membiarkan kedengkian dalam hati kami terhadap orang-orang yang beriman; Ya Rabb kami, Sesungguhnya Engkau Maha Penyantun lagi Maha Penyayang".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,11,'۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ نَافَقُوْا يَقُوْلُوْنَ لِاِخْوَانِهِمُ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ لَىِٕنْ اُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيْعُ فِيْكُمْ اَحَدًا اَبَدًاۙ وَّاِنْ قُوْتِلْتُمْ لَنَنْصُرَنَّكُمْۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ ','Apakah kamu tidak memperhatikan orang-orang munafik yang berkata kepada saudara-saudara mereka yang kafir di antara ahli kitab: "Sesungguhnya jika kamu diusir niscaya kamipun akan keluar bersamamu; dan kami selama-lamanya tidak akan patuh kepada siapapun untuk (menyusahkan) kamu, dan jika kamu diperangi pasti kami akan membantu kamu". Dan Allah menyaksikan bahwa Sesungguhnya mereka benar-benar pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,12,'لَىِٕنْ اُخْرِجُوْا لَا يَخْرُجُوْنَ مَعَهُمْۚ وَلَىِٕنْ قُوْتِلُوْا لَا يَنْصُرُوْنَهُمْۚ وَلَىِٕنْ نَّصَرُوْهُمْ لَيُوَلُّنَّ الْاَدْبَارَۙ ثُمَّ لَا يُنْصَرُوْنَ ','Sesungguhnya jika mereka diusir, orang-orang munafik itu tidak akan keluar bersama mereka, dan sesungguhnya jika mereka diperangi, niscaya mereka tidak akan menolongnya; sesungguhnya jika mereka menolongnya, niscaya mereka akan berpaling lari ke belakang; kemudian mereka tidak akan mendapat pertolongan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,13,'لَاَنْتُمْ اَشَدُّ رَهْبَةً فِيْ صُدُوْرِهِمْ مِّنَ اللّٰهِ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ','Sesungguhnya kamu dalam hati mereka lebih ditakuti daripada Allah. Yang demikian itu karena mereka adalah kaum yang tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,14,'لَا يُقَاتِلُوْنَكُمْ جَمِيْعًا اِلَّا فِيْ قُرًى مُّحَصَّنَةٍ اَوْ مِنْ وَّرَاۤءِ جُدُرٍۗ بَأْسُهُمْ بَيْنَهُمْ شَدِيْدٌ ۗ تَحْسَبُهُمْ جَمِيْعًا وَّقُلُوْبُهُمْ شَتّٰىۗ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَۚ ','Mereka tidak akan memerangi kamu dalam keadaan bersatu padu, kecuali dalam kampung-kampung yang berbenteng atau di balik tembok. Permusuhan antara sesama mereka adalah sangat hebat. Kamu kira mereka itu bersatu, sedang hati mereka berpecah belah. Yang demikian itu karena sesungguhnya mereka adalah kaum yang tidak mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,15,'كَمَثَلِ الَّذِيْنَ مِنْ قَبْلِهِمْ قَرِيْبًا ذَاقُوْا وَبَالَ اَمْرِهِمْۚ وَلَهُمْ عَذَابٌ اَلِيْمٌۚ ','(Mereka adalah) seperti orang-orang Yahudi yang belum lama sebelum mereka telah merasai akibat buruk dari perbuatan mereka, dan bagi mereka azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,16,'كَمَثَلِ الشَّيْطٰنِ اِذْ قَالَ لِلْاِنْسَانِ اكْفُرْۚ فَلَمَّا كَفَرَ قَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكَ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ ','(Bujukan orang-orang munafik itu adalah) seperti (bujukan) shaitan ketika dia berkata kepada manusia: "Kafirlah kamu", maka tatkala manusia itu telah kafir, maka ia berkata: "Sesungguhnya aku berlepas diri dari kamu, karena sesungguhnya aku takut kepada Allah, Rabb semesta Alam".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,17,'فَكَانَ عَاقِبَتَهُمَآ اَنَّهُمَا فِى النَّارِ خَالِدَيْنِ فِيْهَاۗ وَذٰلِكَ جَزٰۤؤُا الظّٰلِمِيْنَ ࣖ','Maka adalah kesudahan keduanya, bahwa sesungguhnya keduanya (masuk) ke dalam neraka, mereka kekal di dalamnya. Demikianlah balasan orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,18,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَلْتَنْظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ','Hai orang-orang yang beriman, bertakwalah kepada Allah dan hendaklah setiap diri memperhatikan apa yang telah diperbuatnya untuk hari esok (akhirat); dan bertakwalah kepada Allah, sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,19,'وَلَا تَكُوْنُوْا كَالَّذِيْنَ نَسُوا اللّٰهَ فَاَنْسٰىهُمْ اَنْفُسَهُمْۗ  اُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ','Dan janganlah kamu seperti orang-orang yang lupa kepada Allah, lalu Allah menjadikan mereka lupa kepada mereka sendiri. Mereka itulah orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,20,'لَا يَسْتَوِيْٓ اَصْحٰبُ النَّارِ وَاَصْحٰبُ الْجَنَّةِۗ  اَصْحٰبُ الْجَنَّةِ هُمُ الْفَاۤىِٕزُوْنَ ','Tidaklah sama penghuni-penghuni neraka dengan penghuni-penghuni jannah; penghuni-penghuni jannah itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,21,'لَوْ اَنْزَلْنَا هٰذَا الْقُرْاٰنَ عَلٰى جَبَلٍ لَّرَاَيْتَهٗ خَاشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ اللّٰهِ ۗوَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُوْنَ ','Kalau sekiranya Kami turunkan Al-Quran ini kepada sebuah gunung, pasti kamu akan melihatnya tunduk terpecah belah disebabkan ketakutannya kepada Allah. Dan perumpamaan-perumpamaan itu Kami buat untuk manusia supaya mereka berfikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,22,'هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۚ هُوَ الرَّحْمٰنُ الرَّحِيْمُ ','Dialah Allah Yang tiada Tuhan selain Dia, Yang Mengetahui yang ghaib dan yang nyata, Dialah Yang Maha Pemurah lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,23,'هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَ ۚ اَلْمَلِكُ الْقُدُّوْسُ السَّلٰمُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيْزُ الْجَبَّارُ الْمُتَكَبِّرُۗ سُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ','Dialah Allah Yang tiada Tuhan selain Dia, Raja, Yang Maha Suci, Yang Maha Sejahtera, Yang Mengaruniakan Keamanan, Yang Maha Memelihara, Yang Maha Perkasa, Yang Maha Kuasa, Yang Memiliki segala Keagungan, Maha Suci Allah dari apa yang mereka persekutukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,59,24,'هُوَ اللّٰهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْاَسْمَاۤءُ الْحُسْنٰىۗ يُسَبِّحُ لَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ࣖ ','Dialah Allah Yang Menciptakan, Yang Mengadakan, Yang Membentuk Rupa, Yang Mempunyai Asmaaul Husna. Bertasbih kepada-Nya apa yang di langit dan bumi. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,1,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا عَدُوِّيْ وَعَدُوَّكُمْ اَوْلِيَاۤءَ تُلْقُوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوْا بِمَا جَاۤءَكُمْ مِّنَ الْحَقِّۚ  يُخْرِجُوْنَ الرَّسُوْلَ وَاِيَّاكُمْ اَنْ تُؤْمِنُوْا بِاللّٰهِ رَبِّكُمْۗ اِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِيْ سَبِيْلِيْ وَابْتِغَاۤءَ مَرْضَاتِيْ تُسِرُّوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَاَنَا۠ اَعْلَمُ بِمَآ اَخْفَيْتُمْ وَمَآ اَعْلَنْتُمْۗ وَمَنْ يَّفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ ','Hai orang-orang yang beriman, janganlah kamu mengambil musuh-Ku dan musuhmu menjadi teman-teman setia yang kamu sampaikan kepada mereka (berita-berita Muhammad), karena rasa kasih sayang; padahal sesungguhnya mereka telah ingkar kepada kebenaran yang datang kepadamu, mereka mengusir Rasul dan (mengusir) kamu karena kamu beriman kepada Allah, Tuhanmu. Jika kamu benar-benar keluar untuk berjihad di jalan-Ku dan mencari keridhaan-Ku (janganlah kamu berbuat demikian). Kamu memberitahukan secara rahasia (berita-berita Muhammad) kepada mereka, karena rasa kasih sayang. Aku lebih mengetahui apa yang kamu sembunyikan dan apa yang kamu nyatakan. Dan barangsiapa di antara kamu yang melakukannya, maka sesungguhnya dia telah tersesat dari jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,2,'اِنْ يَّثْقَفُوْكُمْ يَكُوْنُوْا لَكُمْ اَعْدَاۤءً وَّيَبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ وَاَلْسِنَتَهُمْ بِالسُّوْۤءِ وَوَدُّوْا لَوْ تَكْفُرُوْنَۗ ','Jika mereka menangkap kamu, niscaya mereka bertindak sebagai musuh bagimu dan melepaskan tangan dan lidah mereka kepadamu dengan menyakiti(mu); dan mereka ingin supaya kamu (kembali) kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,3,'لَنْ تَنْفَعَكُمْ اَرْحَامُكُمْ وَلَآ اَوْلَادُكُمْ ۛيَوْمَ الْقِيٰمَةِ ۛيَفْصِلُ بَيْنَكُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Karib kerabat dan anak-anakmu sekali-sekali tiada bermanfaat bagimu pada Hari Kiamat. Dia akan memisahkan antara kamu. Dan Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,4,'قَدْ كَانَتْ لَكُمْ اُسْوَةٌ حَسَنَةٌ فِيْٓ اِبْرٰهِيْمَ وَالَّذِيْنَ مَعَهٗۚ اِذْ قَالُوْا لِقَوْمِهِمْ اِنَّا بُرَءٰۤؤُا مِنْكُمْ وَمِمَّا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۖ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاۤءُ اَبَدًا حَتّٰى تُؤْمِنُوْا بِاللّٰهِ وَحْدَهٗٓ اِلَّا قَوْلَ اِبْرٰهِيْمَ لِاَبِيْهِ لَاَسْتَغْفِرَنَّ لَكَ وَمَآ اَمْلِكُ لَكَ مِنَ اللّٰهِ مِنْ شَيْءٍۗ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَاِلَيْكَ اَنَبْنَا وَاِلَيْكَ الْمَصِيْرُ ','Sesungguhnya telah ada suri tauladan yang baik bagimu pada Ibrahim dan orang-orang yang bersama dengan dia; ketika mereka berkata kepada kaum mereka: "Sesungguhnya kami berlepas diri daripada kamu dari daripada apa yang kamu sembah selain Allah, kami ingkari (kekafiran)mu dan telah nyata antara kami dan kamu permusuhan dan kebencian buat selama-lamanya sampai kamu beriman kepada Allah saja. Kecuali perkataan Ibrahim kepada bapaknya: "Sesungguhnya aku akan memohonkan ampunan bagi kamu dan aku tiada dapat menolak sesuatupun dari kamu (siksaan) Allah". (Ibrahim berkata): "Ya Tuhan kami hanya kepada Engkaulah kami bertawakkal dan hanya kepada Engkaulah kami bertaubat dan hanya kepada Engkaulah kami kembali".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,5,'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْا وَاغْفِرْ لَنَا رَبَّنَاۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ','"Ya Tuhan kami, janganlah Engkau jadikan kami (sasaran) fitnah bagi orang-orang kafir. Dan ampunilah kami ya Tuhan kami. Sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,6,'لَقَدْ كَانَ لَكُمْ فِيْهِمْ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُو اللّٰهَ وَالْيَوْمَ الْاٰخِرَۗ وَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ ࣖ ','Sesungguhnya pada mereka itu (Ibrahim dan umatnya) ada teladan yang baik bagimu; (yaitu) bagi orang-orang yang mengharap (pahala) Allah dan (keselamatan pada) Hari Kemudian. Dan barangsiapa yang berpaling, maka sesungguhnya Allah Dialah yang Maha kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,7,'۞ عَسَى اللّٰهُ اَنْ يَّجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِيْنَ عَادَيْتُمْ مِّنْهُمْ مَّوَدَّةًۗ وَاللّٰهُ قَدِيْرٌۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ','Mudah-mudahan Allah menimbulkan kasih sayang antaramu dengan orang-orang yang kamu musuhi di antara mereka. Dan Allah adalah Maha Kuasa. Dan Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,8,'لَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ لَمْ يُقَاتِلُوْكُمْ فِى الدِّيْنِ وَلَمْ يُخْرِجُوْكُمْ مِّنْ دِيَارِكُمْ اَنْ تَبَرُّوْهُمْ وَتُقْسِطُوْٓا اِلَيْهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ ','Allah tidak melarang kamu untuk berbuat baik dan berlaku adil terhadap orang-orang yang tiada memerangimu karena agama dan tidak (pula) mengusir kamu dari negerimu. Sesungguhnya Allah menyukai orang-orang yang berlaku adil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,9,'اِنَّمَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ قَاتَلُوْكُمْ فِى الدِّيْنِ وَاَخْرَجُوْكُمْ مِّنْ دِيَارِكُمْ وَظَاهَرُوْا عَلٰٓى اِخْرَاجِكُمْ اَنْ تَوَلَّوْهُمْۚ وَمَنْ يَّتَوَلَّهُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ','Sesungguhnya Allah hanya melarang kamu menjadikan sebagai kawanmu orang-orang yang memerangimu karena agama dan mengusir kamu dari negerimu, dan membantu (orang lain) untuk mengusirmu. Dan barangsiapa menjadikan mereka sebagai kawan, maka mereka itulah orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,10,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا جَاۤءَكُمُ الْمُؤْمِنٰتُ مُهٰجِرٰتٍ فَامْتَحِنُوْهُنَّۗ  اَللّٰهُ اَعْلَمُ بِاِيْمَانِهِنَّ فَاِنْ عَلِمْتُمُوْهُنَّ مُؤْمِنٰتٍ فَلَا تَرْجِعُوْهُنَّ اِلَى الْكُفَّارِۗ  لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّوْنَ لَهُنَّۗ وَاٰتُوْهُمْ مَّآ اَنْفَقُوْاۗ وَلَا جُنَاحَ عَلَيْكُمْ اَنْ تَنْكِحُوْهُنَّ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّۗ وَلَا تُمْسِكُوْا بِعِصَمِ الْكَوَافِرِ وَسْـَٔلُوْا مَآ اَنْفَقْتُمْ وَلْيَسْـَٔلُوْا مَآ اَنْفَقُوْاۗ ذٰلِكُمْ حُكْمُ اللّٰهِ ۗيَحْكُمُ بَيْنَكُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ','Hai orang-orang yang beriman, apabila datang berhijrah kepadamu perempuan-perempuan yang beriman, maka hendaklah kamu uji (keimanan) mereka. Allah lebih mengetahui tentang keimanan mereka;maka jika kamu telah mengetahui bahwa mereka (benar-benar) beriman maka janganlah kamu kembalikan mereka kepada (suami-suami mereka) orang-orang kafir. Mereka tiada halal bagi orang-orang kafir itu dan orang-orang kafir itu tiada halal pula bagi mereka. Dan berikanlah kepada (suami suami) mereka, mahar yang telah mereka bayar. Dan tiada dosa atasmu mengawini mereka apabila kamu bayar kepada mereka maharnya. Dan janganlah kamu tetap berpegang pada tali (perkawinan) dengan perempuan-perempuan kafir; dan hendaklah kamu minta mahar yang telah kamu bayar; dan hendaklah mereka meminta mahar yang telah mereka bayar. Demikianlah hukum Allah yang ditetapkan-Nya di antara kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,11,'وَاِنْ فَاتَكُمْ شَيْءٌ مِّنْ اَزْوَاجِكُمْ اِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَاٰتُوا الَّذِيْنَ ذَهَبَتْ اَزْوَاجُهُمْ مِّثْلَ مَآ اَنْفَقُوْاۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ ','Dan jika seseorang dari isteri-isterimu lari kepada orang-orang kafir, lalu kamu mengalahkan mereka maka bayarkanlah kepada orang-orang yang lari isterinya itu mahar sebanyak yang telah mereka bayar. Dan bertakwalah kepada Allah Yang kepada-Nya kamu beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,12,'يٰٓاَيُّهَا النَّبِيُّ اِذَا جَاۤءَكَ الْمُؤْمِنٰتُ يُبَايِعْنَكَ عَلٰٓى اَنْ لَّا يُشْرِكْنَ بِاللّٰهِ شَيْـًٔا وَّلَا يَسْرِقْنَ وَلَا يَزْنِيْنَ وَلَا يَقْتُلْنَ اَوْلَادَهُنَّ وَلَا يَأْتِيْنَ بِبُهْتَانٍ يَّفْتَرِيْنَهٗ بَيْنَ اَيْدِيْهِنَّ وَاَرْجُلِهِنَّ وَلَا يَعْصِيْنَكَ فِيْ مَعْرُوْفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Hai Nabi, apabila datang kepadamu perempuan-perempuan yang beriman untuk mengadakan janji setia, bahwa mereka tiada akan menyekutukan Allah, tidak akan mencuri, tidak akan berzina, tidak akan membunuh anak-anaknya, tidak akan berbuat dusta yang mereka ada-adakan antara tangan dan kaki mereka dan tidak akan mendurhakaimu dalam urusan yang baik, maka terimalah janji setia mereka dan mohonkanlah ampunan kepada Allah untuk mereka. Sesungguhnya Allah maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,60,13,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْ قَدْ يَىِٕسُوْا مِنَ الْاٰخِرَةِ كَمَا يَىِٕسَ الْكُفَّارُ مِنْ اَصْحٰبِ الْقُبُوْرِ ࣖ ','Hai orang-orang yang beriman, janganlah kamu jadikan penolongmu kaum yang dimurkai Allah. Sesungguhnya mereka telah putus asa terhadap negeri akhirat sebagaimana orang-orang kafir yang telah berada dalam kubur berputus asa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,1,'سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ','Telah bertasbih kepada Allah apa saja yang ada di langit dan apa saja yang ada di bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,2,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِمَ تَقُوْلُوْنَ مَا لَا تَفْعَلُوْنَ ','Wahai orang-orang yang beriman, kenapakah kamu mengatakan sesuatu yang tidak kamu kerjakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,3,'كَبُرَ مَقْتًا عِنْدَ اللّٰهِ اَنْ تَقُوْلُوْا مَا لَا تَفْعَلُوْنَ ','Amat besar kebencian di sisi Allah bahwa kamu mengatakan apa-apa yang tidak kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,4,'اِنَّ اللّٰهَ يُحِبُّ الَّذِيْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِهٖ صَفًّا كَاَنَّهُمْ بُنْيَانٌ مَّرْصُوْصٌ','Sesungguhnya Allah menyukai orang yang berperang dijalan-Nya dalam barisan yang teratur seakan-akan mereka seperti suatu bangunan yang tersusun kokoh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,5,'وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ لِمَ تُؤْذُوْنَنِيْ وَقَدْ تَّعْلَمُوْنَ اَنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْۗ فَلَمَّا زَاغُوْٓا اَزَاغَ اللّٰهُ قُلُوْبَهُمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ','Dan (ingatlah) ketika Musa berkata kepada kaumnya: "Hai kaumku, mengapa kamu menyakitiku, sedangkan kamu mengetahui bahwa sesungguhnya aku adalah utusan Allah kepadamu?" Maka tatkala mereka berpaling (dari kebenaran), Allah memalingkan hati mereka; dan Allah tidak memberi petunjuk kepada kaum yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,6,'وَاِذْ قَالَ عِيْسَى ابْنُ مَرْيَمَ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ مُّصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَمُبَشِّرًاۢ بِرَسُوْلٍ يَّأْتِيْ مِنْۢ بَعْدِى اسْمُهٗٓ اَحْمَدُۗ فَلَمَّا جَاۤءَهُمْ بِالْبَيِّنٰتِ قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ','Dan (ingatlah) ketika Isa ibnu Maryam berkata: "Hai Bani Israil, sesungguhnya aku adalah utusan Allah kepadamu, membenarkan kitab sebelumku, yaitu Taurat, dan memberi khabar gembira dengan (datangnya) seorang Rasul yang akan datang sesudahku, yang namanya Ahmad (Muhammad)". Maka tatkala rasul itu datang kepada mereka dengan membawa bukti-bukti yang nyata, mereka berkata: "Ini adalah sihir yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,7,'وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ وَهُوَ يُدْعٰىٓ اِلَى الْاِسْلَامِۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ','Dan siapakah yang lebih zalim daripada orang yang mengada-adakan dusta terhadap Allah sedang dia diajak kepada Islam? Dan Allah tidak memberi petunjuk kepada orang-orang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,8,'يُرِيْدُوْنَ لِيُطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْۗ وَاللّٰهُ مُتِمُّ نُوْرِهٖ وَلَوْ كَرِهَ الْكٰفِرُوْنَ ','Mereka ingin memadamkan cahaya Allah dengan mulut (tipu daya) mereka, tetapi Allah (justru) menyempurnakan cahaya-Nya, walau orang-orang kafir membencinya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,9,'هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ ࣖ ','Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang benar agar Dia memenangkannya di atas segala agama-agama meskipun orang musyrik membenci.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,10,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا هَلْ اَدُلُّكُمْ عَلٰى تِجَارَةٍ تُنْجِيْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ ','Hai orang-orang yang beriman, sukakah kamu aku tunjukkan suatu perniagaan yang dapat menyelamatkanmu dari azab yang pedih?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,11,'تُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖ وَتُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِكُمْ وَاَنْفُسِكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَۙ ','(yaitu) kamu beriman kepada Allah dan Rasul-Nya dan berjihad di jalan Allah dengan harta dan jiwamu. Itulah yang lebih baik bagimu, jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,12,'يَغْفِرْ لَكُمْ ذُنُوْبَكُمْ وَيُدْخِلْكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُۙ ','Niscaya Allah akan mengampuni dosa-dosamu dan memasukkanmu ke dalam jannah yang mengalir di bawahnya sungai-sungai; dan (memasukkan kamu) ke tempat tinggal yang baik di dalam jannah ´Adn. Itulah keberuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,13,'وَاُخْرٰى تُحِبُّوْنَهَاۗ نَصْرٌ مِّنَ اللّٰهِ وَفَتْحٌ قَرِيْبٌۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ','Dan (ada lagi) karunia yang lain yang kamu sukai (yaitu) pertolongan dari Allah dan kemenangan yang dekat (waktunya). Dan sampaikanlah berita gembira kepada orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,61,14,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْٓا اَنْصَارَ اللّٰهِ كَمَا قَالَ عِيْسَى ابْنُ مَرْيَمَ لِلْحَوَارِيّٖنَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗقَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ فَاٰمَنَتْ طَّاۤىِٕفَةٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ وَكَفَرَتْ طَّاۤىِٕفَةٌ ۚفَاَيَّدْنَا الَّذِيْنَ اٰمَنُوْا عَلٰى عَدُوِّهِمْ فَاَصْبَحُوْا ظَاهِرِيْنَ ࣖ ','Hai orang-orang yang beriman, jadilah kamu penolong (agama) Allah sebagaimana Isa ibnu Maryam telah berkata kepada pengikut-pengikutnya yang setia: "Siapakah yang akan menjadi penolong-penolongku (untuk menegakkan agama) Allah?" Pengikut-pengikut yang setia itu berkata: "Kamilah penolong-penolong agama Allah", lalu segolongan dari Bani Israil beriman dan segolongan lain kafir; maka Kami berikan kekuatan kepada orang-orang yang beriman terhadap musuh-musuh mereka, lalu mereka menjadi orang-orang yang menang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,1,'يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ الْمَلِكِ الْقُدُّوْسِ الْعَزِيْزِ الْحَكِيْمِ ','Senantiasa bertasbih kepada Allah apa yang ada di langit dan apa yang ada di bumi. Raja, Yang Maha Suci, Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,2,'هُوَ الَّذِيْ بَعَثَ فِى الْاُمِّيّٖنَ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ ','Dialah yang mengutus kepada kaum yang buta huruf seorang Rasul di antara mereka, yang membacakan ayat-ayat-Nya kepada mereka, mensucikan mereka dan mengajarkan mereka Kitab dan Hikmah (As Sunnah). Dan sesungguhnya mereka sebelumnya benar-benar dalam kesesatan yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,3,'وَّاٰخَرِيْنَ مِنْهُمْ لَمَّا يَلْحَقُوْا بِهِمْۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُۙ ','dan (juga) kepada kaum yang lain dari mereka yang belum berhubungan dengan mereka. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,4,'ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ','Demikianlah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya; dan Allah mempunyai karunia yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,5,'مَثَلُ الَّذِيْنَ حُمِّلُوا التَّوْرٰىةَ ثُمَّ لَمْ يَحْمِلُوْهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ اَسْفَارًاۗ بِئْسَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ','Perumpamaan orang-orang yang dipikulkan kepadanya Taurat, kemudian mereka tiada memikulnya adalah seperti keledai yang membawa kitab-kitab yang tebal. Amatlah buruknya perumpamaan kaum yang mendustakan ayat-ayat Allah itu. Dan Allah tiada memberi petunjuk kepada kaum yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,6,'قُلْ يٰٓاَيُّهَا الَّذِيْنَ هَادُوْٓا اِنْ زَعَمْتُمْ اَنَّكُمْ اَوْلِيَاۤءُ لِلّٰهِ مِنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Katakanlah: "Hai orang-orang yang menganut agama Yahudi, jika kamu mendakwakan bahwa sesungguhnya kamu sajalah kekasih Allah bukan manusia-manusia yang lain, maka harapkanlah kematianmu, jika kamu adalah orang-orang yang benar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,7,'وَلَا يَتَمَنَّوْنَهٗٓ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ ','Mereka tiada akan mengharapkan kematian itu selama-lamanya disebabkan kejahatan yang telah mereka perbuat dengan tangan mereka sendiri. Dan Allah Maha Mengetahui akan orang-orang yang zalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,8,'قُلْ اِنَّ الْمَوْتَ الَّذِيْ تَفِرُّوْنَ مِنْهُ فَاِنَّهٗ مُلٰقِيْكُمْ ثُمَّ تُرَدُّوْنَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ࣖ ','Katakanlah: "Sesungguhnya kematian yang kamu lari daripadanya, maka sesungguhnya kematian itu akan menemui kamu, kemudian kamu akan dikembalikan kepada (Allah), yang mengetahui yang ghaib dan yang nyata, lalu Dia beritakan kepadamu apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,9,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نُوْدِيَ لِلصَّلٰوةِ مِنْ يَّوْمِ الْجُمُعَةِ فَاسْعَوْا اِلٰى ذِكْرِ اللّٰهِ وَذَرُوا الْبَيْعَۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ','Hai orang-orang beriman, apabila diseru untuk menunaikan shalat Jum´at, maka bersegeralah kamu kepada mengingat Allah dan tinggalkanlah jual beli. Yang demikian itu lebih baik bagimu jika kamu mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,10,'فَاِذَا قُضِيَتِ الصَّلٰوةُ فَانْتَشِرُوْا فِى الْاَرْضِ وَابْتَغُوْا مِنْ فَضْلِ اللّٰهِ وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَ ','Apabila telah ditunaikan shalat, maka bertebaranlah kamu di muka bumi; dan carilah karunia Allah dan ingatlah Allah banyak-banyak supaya kamu beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,62,11,'وَاِذَا رَاَوْا تِجَارَةً اَوْ لَهْوًا ۨانْفَضُّوْٓا اِلَيْهَا وَتَرَكُوْكَ قَاۤىِٕمًاۗ قُلْ مَا عِنْدَ اللّٰهِ خَيْرٌ مِّنَ اللَّهْوِ وَمِنَ التِّجَارَةِۗ وَاللّٰهُ خَيْرُ الرّٰزِقِيْنَ ࣖ ','Dan apabila mereka melihat perniagaan atau permainan, mereka bubar untuk menuju kepadanya dan mereka tinggalkan kamu sedang berdiri (berkhotbah). Katakanlah: "Apa yang di sisi Allah lebih baik daripada permainan dan perniagaan", dan Allah Sebaik-baik Pemberi rezeki.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,1,'اِذَا جَاۤءَكَ الْمُنٰفِقُوْنَ قَالُوْا نَشْهَدُ اِنَّكَ لَرَسُوْلُ اللّٰهِ ۘوَاللّٰهُ يَعْلَمُ اِنَّكَ لَرَسُوْلُهٗ ۗوَاللّٰهُ يَشْهَدُ اِنَّ الْمُنٰفِقِيْنَ لَكٰذِبُوْنَۚ ','Apabila orang-orang munafik datang kepadamu, mereka berkata: "Kami mengakui, bahwa sesungguhnya kamu benar-benar Rasul Allah". Dan Allah mengetahui bahwa sesungguhnya kamu benar-benar Rasul-Nya; dan Allah mengetahui bahwa sesungguhnya orang-orang munafik itu benar-benar orang pendusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,2,'اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ ','Mereka itu menjadikan sumpah mereka sebagai perisai, lalu mereka menghalangi (manusia) dari jalan Allah. Sesungguhnya amat buruklah apa yang telah mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,3,'ذٰلِكَ بِاَنَّهُمْ اٰمَنُوْا ثُمَّ كَفَرُوْا فَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ ','Yang demikian itu adalah karena bahwa sesungguhnya mereka telah beriman, kemudian menjadi kafir (lagi) lalu hati mereka dikunci mati; karena itu mereka tidak dapat mengerti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,4,'۞ وَاِذَا رَاَيْتَهُمْ تُعْجِبُكَ اَجْسَامُهُمْۗ وَاِنْ يَّقُوْلُوْا تَسْمَعْ لِقَوْلِهِمْۗ  كَاَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ  ۗيَحْسَبُوْنَ كُلَّ صَيْحَةٍ عَلَيْهِمْۗ هُمُ الْعَدُوُّ فَاحْذَرْهُمْۗ قَاتَلَهُمُ اللّٰهُ ۖاَنّٰى يُؤْفَكُوْنَ','Dan apabila kamu melihat mereka, tubuh-tubuh mereka menjadikan kamu kagum. Dan jika mereka berkata kamu mendengarkan perkataan mereka. Mereka adalah seakan-akan kayu yang tersandar. Mereka mengira bahwa tiap-tiap teriakan yang keras ditujukan kepada mereka. Mereka itulah musuh (yang sebenarnya) maka waspadalah terhadap mereka; semoga Allah membinasakan mereka. Bagaimanakah mereka sampai dipalingkan (dari kebenaran)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,5,'وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُوْلُ اللّٰهِ لَوَّوْا رُءُوْسَهُمْ وَرَاَيْتَهُمْ يَصُدُّوْنَ وَهُمْ مُّسْتَكْبِرُوْنَ ','Dan apabila dikatakan kepada mereka: Marilah (beriman), agar Rasulullah memintakan ampunan bagimu, mereka membuang muka mereka dan kamu lihat mereka berpaling sedang mereka menyombongkan diri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,6,'سَوَاۤءٌ عَلَيْهِمْ اَسْتَغْفَرْتَ لَهُمْ اَمْ لَمْ تَسْتَغْفِرْ لَهُمْۗ  لَنْ يَّغْفِرَ اللّٰهُ لَهُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ','Sama saja bagi mereka, kamu mintakan ampunan atau tidak kamu mintakan ampunan bagi mereka. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang fasik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,7,'هُمُ الَّذِيْنَ يَقُوْلُوْنَ لَا تُنْفِقُوْا عَلٰى مَنْ عِنْدَ رَسُوْلِ اللّٰهِ حَتّٰى يَنْفَضُّوْاۗ وَلِلّٰهِ خَزَاۤىِٕنُ السَّمٰوٰتِ وَالْاَرْضِۙ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَفْقَهُوْنَ ','Mereka orang-orang yang mengatakan (kepada orang-orang Anshar): "Janganlah kamu memberikan perbelanjaan kepada orang-orang (Muhajirin) yang ada disisi Rasulullah supaya mereka bubar (meninggalkan Rasulullah)". Padahal kepunyaan Allah-lah perbendaharaan langit dan bumi, tetapi orang-orang munafik itu tidak memahami.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,8,'يَقُوْلُوْنَ لَىِٕنْ رَّجَعْنَآ اِلَى الْمَدِيْنَةِ لَيُخْرِجَنَّ الْاَعَزُّ مِنْهَا الْاَذَلَّ ۗوَلِلّٰهِ الْعِزَّةُ وَلِرَسُوْلِهٖ وَلِلْمُؤْمِنِيْنَ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَعْلَمُوْنَ ࣖ ','Mereka berkata: "Sesungguhnya jika kita telah kembali ke Madinah, benar-benar orang yang kuat akan mengusir orang-orang yang lemah dari padanya". Padahal kekuatan itu hanyalah bagi Allah, bagi Rasul-Nya dan bagi orang-orang mukmin, tetapi orang-orang munafik itu tiada mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,9,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُلْهِكُمْ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ عَنْ ذِكْرِ اللّٰهِ ۚوَمَنْ يَّفْعَلْ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ','Hai orang-orang beriman, janganlah hartamu dan anak-anakmu melalaikan kamu dari mengingat Allah. Barangsiapa yang berbuat demikian maka mereka itulah orang-orang yang merugi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,10,'وَاَنْفِقُوْا مِنْ مَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ اَحَدَكُمُ الْمَوْتُ فَيَقُوْلَ رَبِّ لَوْلَآ اَخَّرْتَنِيْٓ اِلٰٓى اَجَلٍ قَرِيْبٍۚ فَاَصَّدَّقَ وَاَكُنْ مِّنَ الصّٰلِحِيْنَ ','Dan belanjakanlah sebagian dari apa yang telah Kami berikan kepadamu sebelum datang kematian kepada salah seorang di antara kamu; lalu ia berkata: "Ya Rabb-ku, mengapa Engkau tidak menangguhkan (kematian)ku sampai waktu yang dekat, yang menyebabkan aku dapat bersedekah dan aku termasuk orang-orang yang saleh?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,63,11,'وَلَنْ يُّؤَخِّرَ اللّٰهُ نَفْسًا اِذَا جَاۤءَ اَجَلُهَاۗ وَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ ࣖ ','Dan Allah sekali-kali tidak akan menangguhkan (kematian) seseorang apabila telah datang waktu kematiannya. Dan Allah Maha Mengenal apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,1,'يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Bertasbih kepada Allah apa yang ada di langit dan apa yang ada di bumi; hanya Allah lah yang mempunyai semua kerajaan dan semua pujian, dan Dia Maha Kuasa atas segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,2,'هُوَ الَّذِيْ خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَّمِنْكُمْ مُّؤْمِنٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ','Dialah yang menciptakan kamu maka di antara kamu ada yang kafir dan di antaramu ada yang mukmin. Dan Allah Maha Melihat apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,3,'خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْۚ وَاِلَيْهِ الْمَصِيْرُ ','Dia menciptakan langit dan bumi dengan haq. Dia membentuk rupamu dan dibaguskan-Nya rupamu itu dan hanya kepada Allah-lah kembali(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,4,'يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ','Dia mengetahui apa yang ada di langit dan di bumi dan mengetahui apa yang kamu rahasiakan dan yang kamu nyatakan. Dan Allah Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,5,'اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۖفَذَاقُوْا وَبَالَ اَمْرِهِمْ وَلَهُمْ عَذَابٌ اَلِيْمٌ ','Apakah belum datang kepadamu (hai orang-orang kafir) berita orang-orang kafir terdahulu. Maka mereka telah merasakan akibat yang buruk dari perbuatan mereka dan mereka memperoleh azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,6,'ذٰلِكَ بِاَنَّهٗ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَقَالُوْٓا اَبَشَرٌ يَّهْدُوْنَنَاۖ فَكَفَرُوْا وَتَوَلَّوْا وَّاسْتَغْنَى اللّٰهُ ۗوَاللّٰهُ غَنِيٌّ حَمِيْدٌ ','Yang demikian itu adalah karena sesungguhnya telah datang kepada mereka Rasul-Rasul mereka membawa keterangan-keterangan lalu mereka berkata: "Apakah manusia yang akan memberi petunjuk kepada kami?" lalu mereka ingkar dan berpaling; dan Allah tidak memerlukan (mereka). Dan Allah Maha Kaya lagi Maha Terpuji.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,7,'زَعَمَ الَّذِيْنَ كَفَرُوْٓا اَنْ لَّنْ يُّبْعَثُوْاۗ قُلْ بَلٰى وَرَبِّيْ لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْۗ وَذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ','Orang-orang yang kafir mengatakan bahwa mereka sekali-kali tidak akan dibangkitkan. Katakanlah: "Memang, demi Tuhanku, benar-benar kamu akan dibangkitkan, kemudian akan diberitakan kepadamu apa yang telah kamu kerjakan". Yang demikian itu adalah mudah bagi Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,8,'فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالنُّوْرِ الَّذِيْٓ اَنْزَلْنَاۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ','Maka berimanlah kamu kepada Allah dan Rasul-Nya dan kepada cahaya (Al-Quran) yang telah Kami turunkan. Dan Allah Maha Mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,9,'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذٰلِكَ يَوْمُ التَّغَابُنِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ ','(Ingatlah) hari (dimana) Allah mengumpulkan kamu pada hari pengumpulan, itulah hari dinampakkan kesalahan-kesalahan. Dan barangsiapa yang beriman kepada Allah dan beramal saleh, niscaya Allah akan menutupi kesalahan-kesalahannya dan memasukkannya ke dalam jannah yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah keberuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,10,'وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ خٰلِدِيْنَ فِيْهَاۗ وَبِئْسَ الْمَصِيْرُ ࣖ ','Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni-penghuni neraka, mereka kekal di dalamnya. Dan itulah seburuk-buruk tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,11,'مَآ اَصَابَ مِنْ مُّصِيْبَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَمَنْ يُّؤْمِنْۢ بِاللّٰهِ يَهْدِ قَلْبَهٗ ۗوَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ','Tidak ada suatu musibah pun yang menimpa seseorang kecuali dengan ijin Allah; dan barangsiapa yang beriman kepada Allah niscaya Dia akan memberi petunjuk kepada hatinya. Dan Allah Maha Mengetahui segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,12,'وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّيْتُمْ فَاِنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ ','Dan taatlah kepada Allah dan taatlah kepada Rasul-Nya, jika kamu berpaling sesungguhnya kewajiban Rasul Kami hanyalah menyampaikan (amanat Allah) dengan terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,13,'اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ','(Dialah) Allah tidak ada Tuhan selain Dia. Dan hendaklah orang-orang mukmin bertawakkal kepada Allah saja.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,14,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّ مِنْ اَزْوَاجِكُمْ وَاَوْلَادِكُمْ عَدُوًّا لَّكُمْ فَاحْذَرُوْهُمْۚ وَاِنْ تَعْفُوْا وَتَصْفَحُوْا وَتَغْفِرُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ','Hai orang-orang mukmin, sesungguhnya di antara isteri-isterimu dan anak-anakmu ada yang menjadi musuh bagimu maka berhati-hatilah kamu terhadap mereka dan jika kamu memaafkan dan tidak memarahi serta mengampuni (mereka) maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,15,'اِنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ  ۗوَاللّٰهُ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ','Sesungguhnya hartamu dan anak-anakmu hanyalah cobaan (bagimu), dan di sisi Allah-lah pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,16,'فَاتَّقُوا اللّٰهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوْا وَاَطِيْعُوْا وَاَنْفِقُوْا خَيْرًا لِّاَنْفُسِكُمْۗ وَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ','Maka bertakwalah kamu kepada Allah menurut kesanggupanmu dan dengarlah serta taatlah dan nafkahkanlah nafkah yang baik untuk dirimu. Dan barangsiapa yang dipelihara dari kekikiran dirinya, maka mereka itulah orang-orang yang beruntung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,17,'اِنْ تُقْرِضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ شَكُوْرٌ حَلِيْمٌۙ ','Jika kamu meminjamkan kepada Allah pinjaman yang baik, niscaya Allah melipat gandakan balasannya kepadamu dan mengampuni kamu. Dan Allah Maha Pembalas Jasa lagi Maha Penyantun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,64,18,'عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الْحَكِيْمُ ࣖ ','Yang mengetahui yang ghaib dan yang nyata. Yang Maha Perkasa lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,1,'يٰٓاَيُّهَا النَّبِيُّ اِذَا طَلَّقْتُمُ النِّسَاۤءَ فَطَلِّقُوْهُنَّ لِعِدَّتِهِنَّ وَاَحْصُوا الْعِدَّةَۚ وَاتَّقُوا اللّٰهَ رَبَّكُمْۚ  لَا تُخْرِجُوْهُنَّ مِنْۢ بُيُوْتِهِنَّ وَلَا يَخْرُجْنَ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ لَا تَدْرِيْ لَعَلَّ اللّٰهَ يُحْدِثُ بَعْدَ ذٰلِكَ اَمْرًا','Hai Nabi, apabila kamu menceraikan isteri-isterimu maka hendaklah kamu ceraikan mereka pada waktu mereka dapat (menghadapi) iddahnya (yang wajar) dan hitunglah waktu iddah itu serta bertakwalah kepada Allah Tuhanmu. Janganlah kamu keluarkan mereka dari rumah mereka dan janganlah mereka (diizinkan) ke luar kecuali mereka mengerjakan perbuatan keji yang terang. Itulah hukum-hukum Allah dan barangsiapa yang melanggar hukum-hukum Allah, maka sesungguhnya dia telah berbuat zalim terhadap dirinya sendiri. Kamu tidak mengetahui barangkali Allah mengadakan sesudah itu sesuatu hal yang baru.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,2,'فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ فَارِقُوْهُنَّ بِمَعْرُوْفٍ وَّاَشْهِدُوْا ذَوَيْ عَدْلٍ مِّنْكُمْ وَاَقِيْمُوا الشَّهَادَةَ لِلّٰهِ ۗذٰلِكُمْ يُوْعَظُ بِهٖ مَنْ كَانَ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ەۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مَخْرَجًا ۙ','Apabila mereka telah mendekati akhir iddahnya, maka rujukilah mereka dengan baik atau lepaskanlah mereka dengan baik dan persaksikanlah dengan dua orang saksi yang adil di antara kamu dan hendaklah kamu tegakkan kesaksian itu karena Allah. Demikianlah diberi pengajaran dengan itu orang yang beriman kepada Allah dan hari akhirat. Barangsiapa bertakwa kepada Allah niscaya Dia akan mengadakan baginya jalan keluar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,3,'وَّيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَهُوَ حَسْبُهٗ ۗاِنَّ اللّٰهَ بَالِغُ اَمْرِهٖۗ قَدْ جَعَلَ اللّٰهُ لِكُلِّ شَيْءٍ قَدْرًا ','Dan memberinya rezeki dari arah yang tiada disangka-sangkanya. Dan barangsiapa yang bertawakkal kepada Allah niscaya Allah akan mencukupkan (keperluan)nya. Sesungguhnya Allah melaksanakan urusan yang (dikehendaki)Nya. Sesungguhnya Allah telah mengadakan ketentuan bagi tiap-tiap sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,4,'وَالّٰۤـِٔيْ يَىِٕسْنَ مِنَ الْمَحِيْضِ مِنْ نِّسَاۤىِٕكُمْ اِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلٰثَةُ اَشْهُرٍۙ وَّالّٰۤـِٔيْ لَمْ يَحِضْنَۗ وَاُولَاتُ الْاَحْمَالِ اَجَلُهُنَّ اَنْ يَّضَعْنَ حَمْلَهُنَّۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مِنْ اَمْرِهٖ يُسْرًا ','Dan perempuan-perempuan yang tidak haid lagi (monopause) di antara perempuan-perempuanmu jika kamu ragu-ragu (tentang masa iddahnya), maka masa iddah mereka adalah tiga bulan; dan begitu (pula) perempuan-perempuan yang tidak haid. Dan perempuan-perempuan yang hamil, waktu iddah mereka itu ialah sampai mereka melahirkan kandungannya. Dan barang -siapa yang bertakwa kepada Allah, niscaya Allah menjadikan baginya kemudahan dalam urusannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,5,'ذٰلِكَ اَمْرُ اللّٰهِ اَنْزَلَهٗٓ اِلَيْكُمْۗ وَمَنْ يَّتَّقِ اللّٰهَ يُكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُعْظِمْ لَهٗٓ اَجْرًا ','Itulah perintah Allah yang diturunkan-Nya kepada kamu, dan barangsiapa yang bertakwa kepada Allah, niscaya Dia akan menghapus kesalahan-kesalahannya dan akan melipat gandakan pahala baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,6,'اَسْكِنُوْهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِّنْ وُّجْدِكُمْ وَلَا تُضَاۤرُّوْهُنَّ لِتُضَيِّقُوْا عَلَيْهِنَّۗ وَاِنْ كُنَّ اُولَاتِ حَمْلٍ فَاَنْفِقُوْا عَلَيْهِنَّ حَتّٰى يَضَعْنَ حَمْلَهُنَّۚ فَاِنْ اَرْضَعْنَ لَكُمْ فَاٰتُوْهُنَّ اُجُوْرَهُنَّۚ وَأْتَمِرُوْا بَيْنَكُمْ بِمَعْرُوْفٍۚ وَاِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهٗٓ اُخْرٰىۗ ','Tempatkanlah mereka (para isteri) di mana kamu bertempat tinggal menurut kemampuanmu dan janganlah kamu menyusahkan mereka untuk menyempitkan (hati) mereka. Dan jika mereka (isteri-isteri yang sudah ditalaq) itu sedang hamil, maka berikanlah kepada mereka nafkahnya hingga mereka bersalin, kemudian jika mereka menyusukan (anak-anak)mu untukmu maka berikanlah kepada mereka upahnya, dan musyawarahkanlah di antara kamu (segala sesuatu) dengan baik; dan jika kamu menemui kesulitan maka perempuan lain boleh menyusukan (anak itu) untuknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,7,'لِيُنْفِقْ ذُوْ سَعَةٍ مِّنْ سَعَتِهٖۗ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهٗ فَلْيُنْفِقْ مِمَّآ اٰتٰىهُ اللّٰهُ ۗ لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا مَآ اٰتٰىهَاۗ سَيَجْعَلُ اللّٰهُ بَعْدَ عُسْرٍ يُّسْرًا ࣖ ','Hendaklah orang yang mampu memberi nafkah menurut kemampuannya. Dan orang yang disempitkan rezekinya hendaklah memberi nafkah dari harta yang diberikan Allah kepadanya. Allah tidak memikulkan beban kepada seseorang melainkan sekedar apa yang Allah berikan kepadanya. Allah kelak akan memberikan kelapangan sesudah kesempitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,8,'وَكَاَيِّنْ مِّنْ قَرْيَةٍ عَتَتْ عَنْ اَمْرِ رَبِّهَا وَرُسُلِهٖ فَحَاسَبْنٰهَا حِسَابًا شَدِيْدًاۙ وَّعَذَّبْنٰهَا عَذَابًا نُّكْرًا ','Dan berapalah banyaknya (penduduk) negeri yang mendurhakai perintah Tuhan mereka dan Rasul-rasul-Nya, maka Kami hisab penduduk negeri itu dengan hisab yang keras, dan Kami azab mereka dengan azab yang mengerikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,9,'فَذَاقَتْ وَبَالَ اَمْرِهَا وَكَانَ عَاقِبَةُ اَمْرِهَا خُسْرًا ','Maka mereka merasakan akibat yang buruk dari perbuatannya, dan adalah akibat perbuatan mereka kerugian yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,10,'اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًا ۖفَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِۛ الَّذِيْنَ اٰمَنُوْا ۛ قَدْ اَنْزَلَ اللّٰهُ اِلَيْكُمْ ذِكْرًاۙ ','Allah menyediakan bagi mereka azab yang keras, maka bertakwalah kepada Allah hai orang-orang yang mempunyai akal; (yaitu) orang-orang yang beriman. Sesungguhnya Allah telah menurunkan peringatan kepadamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,11,'رَّسُوْلًا يَّتْلُوْا عَلَيْكُمْ اٰيٰتِ اللّٰهِ مُبَيِّنٰتٍ لِّيُخْرِجَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ قَدْ اَحْسَنَ اللّٰهُ لَهٗ رِزْقًا ','(Dan mengutus) seorang Rasul yang membacakan kepadamu ayat-ayat Allah yang menerangkan (bermacam-macam hukum) supaya Dia mengeluarkan orang-orang yang beriman dan beramal saleh dari kegelapan kepada cahaya. Dan barangsiapa beriman kepada Allah dan mengerjakan amal yang saleh niscaya Allah akan memasukkannya ke dalam surga-surga yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Sesungguhnya Allah memberikan rezeki yang baik kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,65,12,'اَللّٰهُ الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ وَّمِنَ الْاَرْضِ مِثْلَهُنَّۗ يَتَنَزَّلُ الْاَمْرُ بَيْنَهُنَّ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ەۙ وَّاَنَّ اللّٰهَ قَدْ اَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا ࣖ','Allah-lah yang menciptakan tujuh langit dan seperti itu pula bumi. Perintah Allah berlaku padanya, agar kamu mengetahui bahwasanya Allah Maha Kuasa atas segala sesuatu, dan sesungguhnya Allah ilmu-Nya benar-benar meliputi segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,1,'يٰٓاَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَآ اَحَلَّ اللّٰهُ لَكَۚ تَبْتَغِيْ مَرْضَاتَ اَزْوَاجِكَۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ','Hai Nabi, mengapa kamu mengharamkan apa yang Allah halalkan bagimu; kamu mencari kesenangan hati isteri-isterimu? Dan Allah Maha Pengampun lagi Maha Penyayang')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,2,'قَدْ فَرَضَ اللّٰهُ لَكُمْ تَحِلَّةَ اَيْمَانِكُمْۚ وَاللّٰهُ مَوْلٰىكُمْۚ وَهُوَ الْعَلِيْمُ الْحَكِيْمُ ','Sesungguhnya Allah telah mewajibkan kepadamu sekalian membebaskan diri dari sumpahmu dan Allah adalah Pelindungmu dan Dia Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,3,'وَاِذْ اَسَرَّ النَّبِيُّ اِلٰى بَعْضِ اَزْوَاجِهٖ حَدِيْثًاۚ فَلَمَّا نَبَّاَتْ بِهٖ وَاَظْهَرَهُ اللّٰهُ عَلَيْهِ عَرَّفَ بَعْضَهٗ وَاَعْرَضَ عَنْۢ بَعْضٍۚ فَلَمَّا نَبَّاَهَا بِهٖ قَالَتْ مَنْ اَنْۢبَاَكَ هٰذَاۗ قَالَ نَبَّاَنِيَ الْعَلِيْمُ الْخَبِيْرُ ','Dan ingatlah ketika Nabi membicarakan secara rahasia kepada salah seorang isterinya (Hafsah) suatu peristiwa. Maka tatkala (Hafsah) menceritakan peristiwa itu (kepada Aisyah) dan Allah memberitahukan hal itu (pembicaraan Hafsah dan Aisyah) kepada Muhammad lalu Muhammad memberitahukan sebagian (yang diberitakan Allah kepadanya) dan menyembunyikan sebagian yang lain (kepada Hafsah). Maka tatkala (Muhammad) memberitahukan pembicaraan (antara Hafsah dan Aisyah) lalu (Hafsah) bertanya: "Siapakah yang telah memberitahukan hal ini kepadamu?" Nabi menjawab: "Telah diberitahukan kepadaku oleh Allah yang Maha Mengetahui lagi Maha Mengenal".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,4,'اِنْ تَتُوْبَآ اِلَى اللّٰهِ فَقَدْ صَغَتْ قُلُوْبُكُمَاۚ وَاِنْ تَظٰهَرَا عَلَيْهِ فَاِنَّ اللّٰهَ هُوَ مَوْلٰىهُ وَجِبْرِيْلُ وَصَالِحُ الْمُؤْمِنِيْنَۚ وَالْمَلٰۤىِٕكَةُ بَعْدَ ذٰلِكَ ظَهِيْرٌ ','Jika kamu berdua bertaubat kepada Allah, maka sesungguhnya hati kamu berdua telah condong (untuk menerima kebaikan); dan jika kamu berdua bantu-membantu menyusahkan Nabi, maka sesungguhnya Allah adalah Pelindungnya dan (begitu pula) Jibril dan orang-orang mukmin yang baik; dan selain dari itu malaikat-malaikat adalah penolongnya pula.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,5,'عَسٰى رَبُّهٗٓ اِنْ طَلَّقَكُنَّ اَنْ يُّبْدِلَهٗٓ اَزْوَاجًا خَيْرًا مِّنْكُنَّ مُسْلِمٰتٍ مُّؤْمِنٰتٍ قٰنِتٰتٍ تٰۤىِٕبٰتٍ عٰبِدٰتٍ سٰۤىِٕحٰتٍ ثَيِّبٰتٍ وَّاَبْكَارًا ','Jika Nabi menceraikan kamu, boleh jadi Tuhannya akan memberi ganti kepadanya dengan isteri yang lebih baik daripada kamu, yang patuh, yang beriman, yang taat, yang bertaubat, yang mengerjakan ibadat, yang berpuasa, yang janda dan yang perawan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,6,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قُوْٓا اَنْفُسَكُمْ وَاَهْلِيْكُمْ نَارًا وَّقُوْدُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلٰۤىِٕكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُوْنَ اللّٰهَ مَآ اَمَرَهُمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ','Hai orang-orang yang beriman, peliharalah dirimu dan keluargamu dari api neraka yang bahan bakarnya adalah manusia dan batu; penjaganya malaikat-malaikat yang kasar, keras, dan tidak mendurhakai Allah terhadap apa yang diperintahkan-Nya kepada mereka dan selalu mengerjakan apa yang diperintahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,7,'يٰٓاَيُّهَا الَّذِيْنَ كَفَرُوْا لَا تَعْتَذِرُوا الْيَوْمَۗ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ ࣖ','Hai orang-orang kafir, janganlah kamu mengemukakan uzur pada hari ini. Sesungguhnya kamu hanya diberi balasan menurut apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,8,'يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا تُوْبُوْٓا اِلَى اللّٰهِ تَوْبَةً نَّصُوْحًاۗ عَسٰى رَبُّكُمْ اَنْ يُّكَفِّرَ عَنْكُمْ سَيِّاٰتِكُمْ وَيُدْخِلَكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ يَوْمَ لَا يُخْزِى اللّٰهُ النَّبِيَّ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۚ نُوْرُهُمْ يَسْعٰى بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ يَقُوْلُوْنَ رَبَّنَآ اَتْمِمْ لَنَا نُوْرَنَا وَاغْفِرْ لَنَاۚ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ','Hai orang-orang yang beriman, bertaubatlah kepada Allah dengan taubatan nasuhaa (taubat yang semurni-murninya). Mudah-mudahan Rabbmu akan menutupi kesalahan-kesalahanmu dan memasukkanmu ke dalam jannah yang mengalir di bawahnya sungai-sungai, pada hari ketika Allah tidak menghinakan Nabi dan orang-orang mukmin yang bersama dia; sedang cahaya mereka memancar di hadapan dan di sebelah kanan mereka, sambil mereka mengatakan: "Ya Rabb kami, sempurnakanlah bagi kami cahaya kami dan ampunilah kami; Sesungguhnya Engkau Maha Kuasa atas segala sesuatu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,9,'يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْۗ وَمَأْوٰىهُمْ جَهَنَّمُۗ وَبِئْسَ الْمَصِيْرُ ','Hai Nabi, perangilah orang-orang kafir dan orang-orang munafik dan bersikap keraslah terhadap mereka. Tempat mereka adalah jahannam dan itu adalah seburuk-buruknya tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,10,'ضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ كَفَرُوا امْرَاَتَ نُوْحٍ وَّامْرَاَتَ لُوْطٍۗ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتٰهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللّٰهِ شَيْـًٔا وَّقِيْلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِيْنَ ','Allah membuat isteri Nuh dan isteri Luth sebagai perumpamaan bagi orang-orang kafir. Keduanya berada di bawah pengawasan dua orang hamba yang saleh di antara hamba-hamba Kami; lalu kedua isteri itu berkhianat kepada suaminya (masing-masing), maka suaminya itu tiada dapat membantu mereka sedikitpun dari (siksa) Allah; dan dikatakan (kepada keduanya): "Masuklah ke dalam jahannam bersama orang-orang yang masuk (jahannam)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,11,'وَضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ اٰمَنُوا امْرَاَتَ فِرْعَوْنَۘ اِذْ قَالَتْ رَبِّ ابْنِ لِيْ عِنْدَكَ بَيْتًا فِى الْجَنَّةِ وَنَجِّنِيْ مِنْ فِرْعَوْنَ وَعَمَلِهٖ وَنَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَۙ ','Dan Allah membuat isteri Fir´aun perumpamaan bagi orang-orang yang beriman, ketika ia berkata: "Ya Rabbku, bangunkanlah untukku sebuah rumah di sisi-Mu dalam firdaus, dan selamatkanlah aku dari Fir´aun dan perbuatannya, dan selamatkanlah aku dari kaum yang zhalim.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,28,66,12,'وَمَرْيَمَ ابْنَتَ عِمْرٰنَ الَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهِ مِنْ رُّوْحِنَا وَصَدَّقَتْ بِكَلِمٰتِ رَبِّهَا وَكُتُبِهٖ وَكَانَتْ مِنَ الْقٰنِتِيْنَ ࣖ   ۔','dan (ingatlah) Maryam binti Imran yang memelihara kehormatannya, maka Kami tiupkan ke dalam rahimnya sebagian dari ruh (ciptaan) Kami, dan dia membenarkan kalimat Rabbnya dan Kitab-Kitab-Nya, dan dia adalah termasuk orang-orang yang taat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,1,'تَبٰرَكَ الَّذِيْ بِيَدِهِ الْمُلْكُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌۙ','Maha Suci Allah Yang di tangan-Nya-lah segala kerajaan, dan Dia Maha Kuasa atas segala sesuatu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,2,' ۨالَّذِيْ خَلَقَ الْمَوْتَ وَالْحَيٰوةَ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًاۗ وَهُوَ الْعَزِيْزُ الْغَفُوْرُۙ','Yang menjadikan mati dan hidup, supaya Dia menguji kamu, siapa di antara kamu yang lebih baik amalnya. Dan Dia Maha Perkasa lagi Maha Pengampun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,3,'الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ طِبَاقًاۗ مَا تَرٰى فِيْ خَلْقِ الرَّحْمٰنِ مِنْ تَفٰوُتٍۗ فَارْجِعِ الْبَصَرَۙ هَلْ تَرٰى مِنْ فُطُوْرٍ ','Yang telah menciptakan tujuh langit berlapis-lapis. Kamu sekali-kali tidak melihat pada ciptaan Tuhan Yang Maha Pemurah sesuatu yang tidak seimbang. Maka lihatlah berulang-ulang, adakah kamu lihat sesuatu yang tidak seimbang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,4,'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ اِلَيْكَ الْبَصَرُ خَاسِئًا وَّهُوَ حَسِيْرٌ ','Kemudian pandanglah sekali lagi niscaya penglihatanmu akan kembali kepadamu dengan tidak menemukan sesuatu cacat dan penglihatanmu itupun dalam keadaan payah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,5,'وَلَقَدْ زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَ وَجَعَلْنٰهَا رُجُوْمًا لِّلشَّيٰطِيْنِ وَاَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيْرِ ','Sesungguhnya Kami telah menghiasi langit yang dekat dengan bintang-bintang, dan Kami jadikan bintang-bintang itu alat-alat pelempar syaitan, dan Kami sediakan bagi mereka siksa neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,6,'وَلِلَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ عَذَابُ جَهَنَّمَۗ وَبِئْسَ الْمَصِيْرُ','Dan orang-orang yang kafir kepada Tuhannya, memperoleh azab Jahannam. Dan itulah seburuk-buruk tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,7,'اِذَآ اُلْقُوْا فِيْهَا سَمِعُوْا لَهَا شَهِيْقًا وَّهِيَ تَفُوْرُۙ','Apabila mereka dilemparkan ke dalamnya mereka mendengar suara neraka yang mengerikan, sedang neraka itu menggelegak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,8,'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِۗ كُلَّمَآ اُلْقِيَ فِيْهَا فَوْجٌ سَاَلَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ نَذِيْرٌۙ','hampir-hampir (neraka) itu terpecah-pecah lantaran marah. Setiap kali dilemparkan ke dalamnya sekumpulan (orang-orang kafir), penjaga-penjaga (neraka itu) bertanya kepada mereka: "Apakah belum pernah datang kepada kamu (di dunia) seorang pemberi peringatan?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,9,'قَالُوْا بَلٰى قَدْ جَاۤءَنَا نَذِيْرٌ ەۙ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللّٰهُ مِنْ شَيْءٍۖ اِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ كَبِيْرٍ','Mereka menjawab: "Benar ada", sesungguhnya telah datang kepada kami seorang pemberi peringatan, maka kami mendustakan(nya) dan kami katakan: "Allah tidak menurunkan sesuatupun; kamu tidak lain hanyalah di dalam kesesatan yang besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,10,'وَقَالُوْا لَوْ كُنَّا نَسْمَعُ اَوْ نَعْقِلُ مَا كُنَّا فِيْٓ اَصْحٰبِ السَّعِيْرِ','Dan mereka berkata: "Sekiranya kami mendengarkan atau memikirkan (peringatan itu) niscaya tidaklah kami termasuk penghuni-penghuni neraka yang menyala-nyala".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,11,'فَاعْتَرَفُوْا بِذَنْۢبِهِمْۚ فَسُحْقًا لِّاَصْحٰبِ السَّعِيْرِ ','Mereka mengakui dosa mereka. Maka kebinasaanlah bagi penghuni-penghuni neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,12,'اِنَّ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ ','Sesungguhnya orang-orang yang takut kepada Tuhannya Yang tidak nampak oleh mereka, mereka akan memperoleh ampunan dan pahala yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,13,'وَاَسِرُّوْا قَوْلَكُمْ اَوِ اجْهَرُوْا بِهٖۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ','Dan rahasiakanlah perkataanmu atau lahirkanlah; sesungguhnya Dia Maha Mengetahui segala isi hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,14,'اَلَا يَعْلَمُ مَنْ خَلَقَۗ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ ࣖ ','Apakah Allah Yang menciptakan itu tidak mengetahui (yang kamu lahirkan atau rahasiakan); dan Dia Maha Halus lagi Maha Mengetahui?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,15,'هُوَ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ ذَلُوْلًا فَامْشُوْا فِيْ مَنَاكِبِهَا وَكُلُوْا مِنْ رِّزْقِهٖۗ وَاِلَيْهِ النُّشُوْرُ ','Dialah Yang menjadikan bumi itu mudah bagi kamu, maka berjalanlah di segala penjurunya dan makanlah sebahagian dari rezeki-Nya. Dan hanya kepada-Nya-lah kamu (kembali setelah) dibangkitkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,16,'ءَاَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يَّخْسِفَ بِكُمُ الْاَرْضَ فَاِذَا هِيَ تَمُوْرُۙ','Apakah kamu merasa aman terhadap Allah yang (berkuasa) di langit bahwa Dia akan menjungkir balikkan bumi bersama kamu, sehingga dengan tiba-tiba bumi itu bergoncang?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,17,'اَمْ اَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يُّرْسِلَ عَلَيْكُمْ حَاصِبًاۗ فَسَتَعْلَمُوْنَ كَيْفَ نَذِيْرِ ','atau apakah kamu merasa aman terhadap Allah yang (berkuasa) di langit bahwa Dia akan mengirimkan badai yang berbatu. Maka kelak kamu akan mengetahui bagaimana (akibat mendustakan) peringatan-Ku?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,18,'وَلَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيْرِ ','Dan sesungguhnya orang-orang yang sebelum mereka telah mendustakan (rasul-rasul-Nya). Maka alangkah hebatnya kemurkaan-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,19,'اَوَلَمْ يَرَوْا اِلَى الطَّيْرِ فَوْقَهُمْ صٰۤفّٰتٍ وَّيَقْبِضْنَۘ مَا يُمْسِكُهُنَّ اِلَّا الرَّحْمٰنُۗ اِنَّهٗ بِكُلِّ شَيْءٍۢ بَصِيْرٌ ','Dan apakah mereka tidak memperhatikan burung-burung yang mengembangkan dan mengatupkan sayapnya di atas mereka? Tidak ada yang menahannya (di udara) selain Yang Maha Pemurah. Sesungguhnya Dia Maha Melihat segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,20,'اَمَّنْ هٰذَا الَّذِيْ هُوَ جُنْدٌ لَّكُمْ يَنْصُرُكُمْ مِّنْ دُوْنِ الرَّحْمٰنِۗ اِنِ الْكٰفِرُوْنَ اِلَّا فِيْ غُرُوْرٍۚ ','Atau siapakah dia yang menjadi tentara bagimu yang akan menolongmu selain daripada Allah Yang Maha Pemurah? Orang-orang kafir itu tidak lain hanyalah dalam (keadaan) tertipu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,21,'اَمَّنْ هٰذَا الَّذِيْ يَرْزُقُكُمْ اِنْ اَمْسَكَ رِزْقَهٗ ۚ بَلْ لَّجُّوْا فِيْ عُتُوٍّ وَّنُفُوْرٍ ','Atau siapakah dia yang memberi kamu rezeki jika Allah menahan rezeki-Nya? Sebenarnya mereka terus menerus dalam kesombongan dan menjauhkan diri?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,22,'اَفَمَنْ يَّمْشِيْ مُكِبًّا عَلٰى وَجْهِهٖٓ اَهْدٰىٓ اَمَّنْ يَّمْشِيْ سَوِيًّا عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ','Maka apakah orang yang berjalan terjungkal di atas mukanya itu lebih banyak mendapatkan petunjuk ataukah orang yang berjalan tegap di atas jalan yang lurus?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,23,'قُلْ هُوَ الَّذِيْٓ اَنْشَاَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ','Katakanlah: "Dialah Yang menciptakan kamu dan menjadikan bagi kamu pendengaran, penglihatan dan hati". (Tetapi) amat sedikit kamu bersyukur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,24,'قُلْ هُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ ','Katakanlah: "Dialah Yang menjadikan kamu berkembang biak di muka bumi, dan hanya kepada-Nya-lah kamu kelak dikumpulkan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,25,'وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ','Dan mereka berkata: "Kapankah datangnya ancaman itu jika kamu adalah orang-orang yang benar?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,26,'قُلْ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ ','Katakanlah: "Sesungguhnya ilmu (tentang hari kiamat itu) hanya pada sisi Allah. Dan sesungguhnya aku hanyalah seorang pemberi peringatan yang menjelaskan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,27,'فَلَمَّا رَاَوْهُ زُلْفَةً سِيْۤـَٔتْ وُجُوْهُ الَّذِيْنَ كَفَرُوْا وَقِيْلَ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَدَّعُوْنَ ','Ketika mereka melihat azab (pada hari kiamat) sudah dekat, muka orang-orang kafir itu menjadi muram. Dan dikatakan (kepada mereka) inilah (azab) yang dahulunya kamu selalu meminta-mintanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,28,'قُلْ اَرَءَيْتُمْ اِنْ اَهْلَكَنِيَ اللّٰهُ وَمَنْ مَّعِيَ اَوْ رَحِمَنَاۙ فَمَنْ يُّجِيْرُ الْكٰفِرِيْنَ مِنْ عَذَابٍ اَلِيْمٍ ','Katakanlah: "Terangkanlah kepadaku jika Allah mematikan aku dan orang-orang yang bersama dengan aku atau memberi rahmat kepada kami, (maka kami akan masuk surga), tetapi siapakah yang dapat melindungi orang-orang yang kafir dari siksa yang pedih?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,29,'قُلْ هُوَ الرَّحْمٰنُ اٰمَنَّا بِهٖ وَعَلَيْهِ تَوَكَّلْنَاۚ فَسَتَعْلَمُوْنَ مَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ ','Katakanlah: "Dialah Allah Yang Maha Penyayang kami beriman kepada-Nya dan kepada-Nya-lah kami bertawakkal. Kelak kamu akan mengetahui siapakah yang berada dalam kesesatan yang nyata".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,67,30,'قُلْ اَرَءَيْتُمْ اِنْ اَصْبَحَ مَاۤؤُكُمْ غَوْرًا فَمَنْ يَّأْتِيْكُمْ بِمَاۤءٍ مَّعِيْنٍ ࣖ ','Katakanlah: "Terangkanlah kepadaku jika sumber air kamu menjadi kering; maka siapakah yang akan mendatangkan air yang mengalir bagimu?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,1,'نۤ ۚوَالْقَلَمِ وَمَا يَسْطُرُوْنَۙ','Nun, demi kalam dan apa yang mereka tulis,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,2,'مَآ اَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُوْنٍ ','berkat nikmat Tuhanmu kamu (Muhammad) sekali-kali bukan orang gila.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,3,'وَاِنَّ لَكَ لَاَجْرًا غَيْرَ مَمْنُوْنٍۚ','Dan sesungguhnya bagi kamu benar-benar pahala yang besar yang tidak putus-putusnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,4,'وَاِنَّكَ لَعَلٰى خُلُقٍ عَظِيْمٍ ','Dan sesungguhnya kamu benar-benar berbudi pekerti yang agung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,5,'فَسَتُبْصِرُ وَيُبْصِرُوْنَۙ','Maka kelak kamu akan melihat dan mereka (orang-orang kafir)pun akan melihat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,6,'بِاَيِّىكُمُ الْمَفْتُوْنُ ','siapa di antara kamu yang gila.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,7,'اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ','Sesungguhnya Tuhanmu, Dialah Yang Paling Mengetahui siapa yang sesat dari jalan-Nya; dan Dialah Yang Paling Mengetahui orang-orang yang mendapat petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,8,'فَلَا تُطِعِ الْمُكَذِّبِيْنَ ','Maka janganlah kamu ikuti orang-orang yang mendustakan (ayat-ayat Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,9,'وَدُّوْا لَوْ تُدْهِنُ فَيُدْهِنُوْنَۚ','Maka mereka menginginkan supaya kamu bersikap lunak lalu mereka bersikap lunak (pula kepadamu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,10,'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِيْنٍۙ','Dan janganlah kamu ikuti setiap orang yang banyak bersumpah lagi hina,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,11,'هَمَّازٍ مَّشَّاۤءٍۢ بِنَمِيْمٍۙ','yang banyak mencela, yang kian ke mari menghambur fitnah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,12,'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ اَثِيْمٍۙ','yang banyak menghalangi perbuatan baik, yang melampaui batas lagi banyak dosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,13,'عُتُلٍّۢ بَعْدَ ذٰلِكَ زَنِيْمٍۙ','yang kaku kasar, selain dari itu, yang terkenal kejahatannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,14,'اَنْ كَانَ ذَا مَالٍ وَّبَنِيْنَۗ','karena dia mempunyai (banyak) harta dan anak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,15,'اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ','Apabila dibacakan kepadanya ayat-ayat Kami, ia berkata: "(Ini adalah) dongeng-dongengan orang-orang dahulu kala".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,16,'سَنَسِمُهٗ عَلَى الْخُرْطُوْمِ ','Kelak akan Kami beri tanda dia di belalai(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,17,'اِنَّا بَلَوْنٰهُمْ كَمَا بَلَوْنَآ اَصْحٰبَ الْجَنَّةِۚ اِذْ اَقْسَمُوْا لَيَصْرِمُنَّهَا مُصْبِحِيْنَۙ','Sesungguhnya Kami telah mencobai mereka (musyrikin Mekah) sebagaimana Kami telah mencobai pemilik-pemilik kebun, ketika mereka bersumpah bahwa mereka sungguh-sungguh akanmemetik (hasil)nya di pagi hari,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,18,'وَلَا يَسْتَثْنُوْنَ ','dan mereka tidak menyisihkan (hak fakir miskin),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,19,'فَطَافَ عَلَيْهَا طَاۤىِٕفٌ مِّنْ رَّبِّكَ وَهُمْ نَاۤىِٕمُوْنَ ','lalu kebun itu diliputi malapetaka (yang datang) dari Tuhanmu ketika mereka sedang tidur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,20,'فَاَصْبَحَتْ كَالصَّرِيْمِۙ','maka jadilah kebun itu hitam seperti malam yang gelap gulita.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,21,'فَتَنَادَوْا مُصْبِحِيْنَۙ','lalu mereka panggil memanggil di pagi hari:')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,22,'اَنِ اغْدُوْا عَلٰى حَرْثِكُمْ اِنْ كُنْتُمْ صَارِمِيْنَ ','"Pergilah diwaktu pagi (ini) ke kebunmu jika kamu hendak memetik buahnya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,23,'فَانْطَلَقُوْا وَهُمْ يَتَخَافَتُوْنَۙ','Maka pergilah mereka saling berbisik-bisik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,24,'اَنْ لَّا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِّسْكِيْنٌۙ','"Pada hari ini janganlah ada seorang miskinpun masuk ke dalam kebunmu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,25,'وَّغَدَوْا عَلٰى حَرْدٍ قَادِرِيْنَ ','Dan berangkatlah mereka di pagi hari dengan niat menghalangi (orang-orang miskin) padahal mereka (menolongnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,26,'فَلَمَّا رَاَوْهَا قَالُوْٓا اِنَّا لَضَاۤلُّوْنَۙ','Tatkala mereka melihat kebun itu, mereka berkata: "Sesungguhnya kita benar-benar orang-orang yang sesat (jalan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,27,'بَلْ نَحْنُ مَحْرُوْمُوْنَ ','bahkan kita dihalangi (dari memperoleh hasilnya)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,28,'قَالَ اَوْسَطُهُمْ اَلَمْ اَقُلْ لَّكُمْ لَوْلَا تُسَبِّحُوْنَ ','Berkatalah seorang yang paling baik pikirannya di antara mereka: "Bukankah aku telah mengatakan kepadamu, hendaklah kamu bertasbih (kepada Tuhanmu)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,29,'قَالُوْا سُبْحٰنَ رَبِّنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ','Mereka mengucapkan: "Maha Suci Tuhan kami, sesungguhnya kami adalah orang-orang yang zalim".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,30,'فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَلَاوَمُوْنَ ','Lalu sebahagian mereka menghadapi sebahagian yang lain seraya cela mencela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,31,'قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا طٰغِيْنَ ','Mereka berkata: "Aduhai celakalah kita; sesungguhnya kita ini adalah orang-orang yang melampaui batas".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,32,'عَسٰى رَبُّنَآ اَنْ يُّبْدِلَنَا خَيْرًا مِّنْهَآ اِنَّآ اِلٰى رَبِّنَا رَاغِبُوْنَ ','Mudah-mudahan Tuhan kita memberikan ganti kepada kita dengan (kebun) yang lebih baik daripada itu; sesungguhnya kita mengharapkan ampunan dari Tuhan kita.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,33,'كَذٰلِكَ الْعَذَابُۗ وَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَ ࣖ ','Seperti itulah azab (dunia). Dan sesungguhnya azab akhirat lebih besar jika mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,34,'اِنَّ لِلْمُتَّقِيْنَ عِنْدَ رَبِّهِمْ جَنّٰتِ النَّعِيْمِ ','Sesungguhnya bagi orang-orang yang bertakwa (disediakan) surga-surga yang penuh kenikmatan di sisi Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,35,'اَفَنَجْعَلُ الْمُسْلِمِيْنَ كَالْمُجْرِمِيْنَۗ','Maka apakah patut Kami menjadikan orang-orang Islam itu sama dengan orang-orang yang berdosa (orang kafir)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,36,'مَا لَكُمْۗ  كَيْفَ تَحْكُمُوْنَۚ','Atau adakah kamu (berbuat demikian): bagaimanakah kamu mengambil keputusan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,37,'اَمْ لَكُمْ كِتٰبٌ فِيْهِ تَدْرُسُوْنَۙ','Atau adakah kamu mempunyai sebuah kitab (yang diturunkan Allah) yang kamu membacanya?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,38,'اِنَّ لَكُمْ فِيْهِ لَمَا تَخَيَّرُوْنَۚ','bahwa di dalamnya kamu benar-benar boleh memilih apa yang kamu sukai untukmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,39,'اَمْ لَكُمْ اَيْمَانٌ عَلَيْنَا بَالِغَةٌ اِلٰى يَوْمِ الْقِيٰمَةِۙ اِنَّ لَكُمْ لَمَا تَحْكُمُوْنَۚ','Atau apakah kamu memperoleh janji yang diperkuat dengan sumpah dari Kami, yang tetap berlaku sampai hari kiamat; sesungguhnya kamu benar-benar dapat mengambil keputusan (sekehendakmu)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,40,'سَلْهُمْ اَيُّهُمْ بِذٰلِكَ زَعِيْمٌۚ','Tanyakanlah kepada mereka: "Siapakah di antara mereka yang bertanggung jawab terhadap keputusan yang diambil itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,41,'اَمْ لَهُمْ شُرَكَاۤءُۚ فَلْيَأْتُوْا بِشُرَكَاۤىِٕهِمْ اِنْ كَانُوْا صٰدِقِيْنَ ','Atau apakah mereka mempunyai sekutu-sekutu? Maka hendaklah mereka mendatangkan sekutu-sekutunya jika mereka adalah orang-orang yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,42,'يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَّيُدْعَوْنَ اِلَى السُّجُوْدِ فَلَا يَسْتَطِيْعُوْنَۙ','Pada hari betis disingkapkan dan mereka dipanggil untuk bersujud; maka mereka tidak kuasa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,43,'خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ  ۗوَقَدْ كَانُوْا يُدْعَوْنَ اِلَى السُّجُوْدِ وَهُمْ سَالِمُوْنَ ','(dalam keadaan) pandangan mereka tunduk ke bawah, lagi mereka diliputi kehinaan. Dan sesungguhnya mereka dahulu (di dunia) diseru untuk bersujud, dan mereka dalam keadaan sejahtera.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,44,'فَذَرْنِيْ وَمَنْ يُّكَذِّبُ بِهٰذَا الْحَدِيْثِۗ سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَۙ','Maka serahkanlah (ya Muhammad) kepada-Ku (urusan) orang-orang yang mendustakan perkataan ini (Al Quran). Nanti Kami akan menarik mereka dengan berangsur-angsur (ke arah kebinasaan) dari arah yang tidak mereka ketahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,45,'وَاُمْلِيْ لَهُمْۗ اِنَّ كَيْدِيْ مَتِيْنٌ ','dan Aku memberi tangguh kepada mereka. Sesungguhnya rencana-Ku amat tangguh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,46,'اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۚ ','Apakah kamu meminta upah kepada mereka, lalu mereka diberati dengan hutang?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,47,'اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَ ','Ataukah ada pada mereka ilmu tentang yang ghaib lalu mereka menulis (padanya apa yang mereka tetapkan)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,48,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوْتِۘ اِذْ نَادٰى وَهُوَ مَكْظُوْمٌۗ','Maka bersabarlah kamu (hai Muhammad) terhadap ketetapan Tuhanmu, dan janganlah kamu seperti orang yang berada dalam (perut) ikan ketika ia berdoa sedang ia dalam keadaan marah (kepada kaumnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,49,'لَوْلَآ اَنْ تَدَارَكَهٗ نِعْمَةٌ مِّنْ رَّبِّهٖ لَنُبِذَ بِالْعَرَاۤءِ وَهُوَ مَذْمُوْمٌ ','Kalau sekiranya ia tidak segera mendapat nikmat dari Tuhannya, benar-benar ia dicampakkan ke tanah tandus dalam keadaan tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,50,'فَاجْتَبٰىهُ رَبُّهٗ فَجَعَلَهٗ مِنَ الصّٰلِحِيْنَ ','Lalu Tuhannya memilihnya dan menjadikannya termasuk orang-orang yang saleh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,51,'وَاِنْ يَّكَادُ الَّذِيْنَ كَفَرُوْا لَيُزْلِقُوْنَكَ بِاَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُوْلُوْنَ اِنَّهٗ لَمَجْنُوْنٌ ۘ','Dan sesungguhnya orang-orang kafir itu benar-benar hampir menggelincirkan kamu dengan pandangan mereka, tatkala mereka mendengar Al Quran dan mereka berkata: "Sesungguhnya ia (Muhammad) benar-benar orang yang gila".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,68,52,'وَمَا هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ ࣖ ','Dan Al Quran itu tidak lain hanyalah peringatan bagi seluruh umat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,1,'اَلْحَاۤقَّةُۙ','Hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,2,'مَا الْحَاۤقَّةُ ۚ ','apakah hari kiamat itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,3,'وَمَآ اَدْرٰىكَ مَا الْحَاۤقَّةُ ۗ ','Dan tahukah kamu apakah hari kiamat itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,4,'كَذَّبَتْ ثَمُوْدُ وَعَادٌ ۢبِالْقَارِعَةِ ','Kaum Tsamud dan ´Aad telah mendustakan hari kiamat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,5,'فَاَمَّا ثَمُوْدُ فَاُهْلِكُوْا بِالطَّاغِيَةِ ','Adapun kaum Tsamud, maka mereka telah dibinasakan dengan kejadian yang luar biasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,6,'وَاَمَّا عَادٌ فَاُهْلِكُوْا بِرِيْحٍ صَرْصَرٍ عَاتِيَةٍۙ','Adapun kaum ´Aad maka mereka telah dibinasakan dengan angin yang sangat dingin lagi amat kencang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,7,'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَّثَمٰنِيَةَ اَيَّامٍۙ حُسُوْمًا فَتَرَى الْقَوْمَ فِيْهَا صَرْعٰىۙ  كَاَنَّهُمْ اَعْجَازُ نَخْلٍ خَاوِيَةٍۚ','yang Allah menimpakan angin itu kepada mereka selama tujuh malam dan delapan hari terus menerus; maka kamu lihat kaum ´Aad pada waktu itu mati bergelimpangan seakan-akan mereka tunggul pohon kurma yang telah kosong (lapuk).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,8,'فَهَلْ تَرٰى لَهُمْ مِّنْۢ بَاقِيَةٍ ','Maka kamu tidak melihat seorangpun yang tinggal di antara mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,9,'وَجَاۤءَ فِرْعَوْنُ وَمَنْ قَبْلَهٗ وَالْمُؤْتَفِكٰتُ بِالْخَاطِئَةِۚ','Dan telah datang Fir´aun dan orang-orang yang sebelumnya dan (penduduk) negeri-negeri yang dijungkir balikkan karena kesalahan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,10,'فَعَصَوْا رَسُوْلَ رَبِّهِمْ فَاَخَذَهُمْ اَخْذَةً رَّابِيَةً ','Maka (masing-masing) mereka mendurhakai rasul Tuhan mereka, lalu Allah menyiksa mereka dengan siksaan yang sangat keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,11,'اِنَّا لَمَّا طَغَا الْمَاۤءُ حَمَلْنٰكُمْ فِى الْجَارِيَةِۙ','Sesungguhnya Kami, tatkala air telah naik (sampai ke gunung) Kami bawa (nenek moyang) kamu, ke dalam bahtera,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,12,'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَّتَعِيَهَآ اُذُنٌ وَّاعِيَةٌ ','agar Kami jadikan peristiwa itu peringatan bagi kamu dan agar diperhatikan oleh telinga yang mau mendengar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,13,'فَاِذَا نُفِخَ فِى الصُّوْرِ نَفْخَةٌ وَّاحِدَةٌ ۙ','Maka apabila sangkakala ditiup sekali tiup')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,14,'وَّحُمِلَتِ الْاَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَّاحِدَةًۙ','dan diangkatlah bumi dan gunung-gunung, lalu dibenturkan keduanya sekali bentur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,15,'فَيَوْمَىِٕذٍ وَّقَعَتِ الْوَاقِعَةُۙ','Maka pada hari itu terjadilah hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,16,'وَانْشَقَّتِ السَّمَاۤءُ فَهِيَ يَوْمَىِٕذٍ وَّاهِيَةٌۙ','dan terbelahlah langit, karena pada hari itu langit menjadi lemah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,17,'وَّالْمَلَكُ عَلٰٓى اَرْجَاۤىِٕهَاۗ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَىِٕذٍ ثَمٰنِيَةٌ  ۗ','Dan malaikat-malaikat berada di penjuru-penjuru langit. Dan pada hari itu delapan orang malaikat menjunjung ´Arsy Tuhanmu di atas (kepala) mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,18,'يَوْمَىِٕذٍ تُعْرَضُوْنَ لَا تَخْفٰى مِنْكُمْ خَافِيَةٌ ','Pada hari itu kamu dihadapkan (kepada Tuhanmu), tiada sesuatupun dari keadaanmu yang tersembunyi (bagi Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,19,'فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَيَقُوْلُ هَاۤؤُمُ اقْرَءُوْا كِتٰبِيَهْۚ ','Adapun orang-orang yang diberikan kepadanya kitabnya dari sebelah kanannya, maka dia berkata: "Ambillah, bacalah kitabku (ini)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,20,'اِنِّيْ ظَنَنْتُ اَنِّيْ مُلٰقٍ حِسَابِيَهْۚ','Sesungguhnya aku yakin, bahwa sesungguhnya aku akan menemui hisab terhadap diriku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,21,'فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۚ','Maka orang itu berada dalam kehidupan yang diridhai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,22,'فِيْ جَنَّةٍ عَالِيَةٍۙ','dalam surga yang tinggi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,23,'قُطُوْفُهَا دَانِيَةٌ ','buah-buahannya dekat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,24,'كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَآ اَسْلَفْتُمْ فِى الْاَيَّامِ الْخَالِيَةِ ','(kepada mereka dikatakan): "Makan dan minumlah dengan sedap disebabkan amal yang telah kamu kerjakan pada hari-hari yang telah lalu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,25,'وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِشِمَالِهٖ ەۙ فَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُوْتَ كِتٰبِيَهْۚ','Adapun orang yang diberikan kepadanya kitabnya dari sebelah kirinya, maka dia berkata: "Wahai alangkah baiknya kiranya tidak diberikan kepadaku kitabku (ini).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,26,'وَلَمْ اَدْرِ مَا حِسَابِيَهْۚ','Dan aku tidak mengetahui apa hisab terhadap diriku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,27,'يٰلَيْتَهَا كَانَتِ الْقَاضِيَةَۚ','Wahai kiranya kematian itulah yang menyelesaikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,28,'مَآ اَغْنٰى عَنِّيْ مَالِيَهْۚ ','Hartaku sekali-kali tidak memberi manfaat kepadaku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,29,'هَلَكَ عَنِّيْ سُلْطٰنِيَهْۚ','Telah hilang kekuasaanku daripadaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,30,'خُذُوْهُ فَغُلُّوْهُۙ','(Allah berfirman): "Peganglah dia lalu belenggulah tangannya ke lehernya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,31,'ثُمَّ الْجَحِيْمَ صَلُّوْهُۙ','Kemudian masukkanlah dia ke dalam api neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,32,'ثُمَّ فِيْ سِلْسِلَةٍ ذَرْعُهَا سَبْعُوْنَ ذِرَاعًا فَاسْلُكُوْهُۗ','Kemudian belitlah dia dengan rantai yang panjangnya tujuh puluh hasta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,33,'اِنَّهٗ كَانَ لَا يُؤْمِنُ بِاللّٰهِ الْعَظِيْمِۙ','Sesungguhnya dia dahulu tidak beriman kepada Allah Yang Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,34,'وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ','Dan juga dia tidak mendorong (orang lain) untuk memberi makan orang miskin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,35,'فَلَيْسَ لَهُ الْيَوْمَ هٰهُنَا حَمِيْمٌۙ','Maka tiada seorang temanpun baginya pada hari ini di sini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,36,'وَّلَا طَعَامٌ اِلَّا مِنْ غِسْلِيْنٍۙ','Dan tiada (pula) makanan sedikitpun (baginya) kecuali dari darah dan nanah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,37,'لَّا يَأْكُلُهٗٓ اِلَّا الْخَاطِـُٔوْنَ ࣖ ','Tidak ada yang memakannya kecuali orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,38,'فَلَآ اُقْسِمُ بِمَا تُبْصِرُوْنَۙ','Maka Aku bersumpah dengan apa yang kamu lihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,39,'وَمَا لَا تُبْصِرُوْنَۙ','Dan dengan apa yang tidak kamu lihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,40,'اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ','Sesungguhnya Al Quran itu adalah benar-benar wahyu (Allah yang diturunkan kepada) Rasul yang mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,41,'وَّمَا هُوَ بِقَوْلِ شَاعِرٍۗ قَلِيْلًا مَّا تُؤْمِنُوْنَۙ','dan Al Quran itu bukanlah perkataan seorang penyair. Sedikit sekali kamu beriman kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,42,'وَلَا بِقَوْلِ كَاهِنٍۗ قَلِيْلًا مَّا تَذَكَّرُوْنَۗ','Dan bukan pula perkataan tukang tenung. Sedikit sekali kamu mengambil pelajaran daripadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,43,'تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ','Ia adalah wahyu yang diturunkan dari Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,44,'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْاَقَاوِيْلِۙ','Seandainya dia (Muhammad) mengadakan sebagian perkataan atas (nama) Kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,45,'لَاَخَذْنَا مِنْهُ بِالْيَمِيْنِۙ','niscaya benar-benar Kami pegang dia pada tangan kanannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,46,'ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِيْنَۖ','Kemudian benar-benar Kami potong urat tali jantungnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,47,'فَمَا مِنْكُمْ مِّنْ اَحَدٍ عَنْهُ حَاجِزِيْنَۙ','Maka sekali-kali tidak ada seorangpun dari kamu yang dapat menghalangi (Kami), dari pemotongan urat nadi itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,48,'وَاِنَّهٗ لَتَذْكِرَةٌ لِّلْمُتَّقِيْنَ ','Dan sesungguhnya Al Quran itu benar-benar suatu pelajaran bagi orang-orang yang bertakwa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,49,'وَاِنَّا لَنَعْلَمُ اَنَّ مِنْكُمْ مُّكَذِّبِيْنَۗ','Dan sesungguhnya Kami benar-benar mengetahui bahwa di antara kamu ada orang yang mendustakan(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,50,'وَاِنَّهٗ لَحَسْرَةٌ عَلَى الْكٰفِرِيْنَۚ','Dan sesungguhnya Al Quran itu benar-benar menjadi penyesalan bagi orang-orang kafir (di akhirat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,51,'وَاِنَّهٗ لَحَقُّ الْيَقِيْنِ ','Dan sesungguhnya Al Quran itu benar-benar kebenaran yang diyakini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,69,52,'فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ ࣖ','Maka bertasbihlah dengan (menyebut) nama Tuhanmu Yang Maha Besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,1,'سَاَلَ سَاۤىِٕلٌۢ بِعَذَابٍ وَّاقِعٍۙ','Seseorang telah meminta kedatangan azab yang akan menimpa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,2,'لِّلْكٰفِرِيْنَ لَيْسَ لَهٗ دَافِعٌۙ','orang-orang kafir, yang tidak seorangpun dapat menolaknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,3,'مِّنَ اللّٰهِ ذِى الْمَعَارِجِۗ','(yang datang) dari Allah, Yang mempunyai tempat-tempat naik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,4,'تَعْرُجُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗ خَمْسِيْنَ اَلْفَ سَنَةٍۚ','Malaikat-malaikat dan Jibril naik (menghadap) kepada Tuhan dalam sehari yang kadarnya limapuluh ribu tahun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,5,'فَاصْبِرْ صَبْرًا جَمِيْلًا','Maka bersabarlah kamu dengan sabar yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,6,'اِنَّهُمْ يَرَوْنَهٗ بَعِيْدًاۙ','Sesungguhnya mereka memandang siksaaan itu jauh (mustahil).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,7,'وَّنَرٰىهُ قَرِيْبًاۗ','Sedangkan Kami memandangnya dekat (mungkin terjadi).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,8,'يَوْمَ تَكُوْنُ السَّمَاۤءُ كَالْمُهْلِۙ','Pada hari ketika langit menjadi seperti luluhan perak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,9,'وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِۙ','dan gunung-gunung menjadi seperti bulu (yang berterbangan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,10,'وَلَا يَسْـَٔلُ حَمِيْمٌ حَمِيْمًاۚ ','dan tidak ada seorang teman akrabpun menanyakan temannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,11,'يُبَصَّرُوْنَهُمْۗ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِيْ مِنْ عَذَابِ يَوْمِىِٕذٍۢ بِبَنِيْهِۙ','sedang mereka saling memandang. Orang kafir ingin kalau sekiranya dia dapat menebus (dirinya) dari azab hari itu dengan anak-anaknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,12,'وَصَاحِبَتِهٖ وَاَخِيْهِۙ','dan isterinya dan saudaranya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,13,'وَفَصِيْلَتِهِ الَّتِيْ تُـْٔوِيْهِۙ','dan kaum familinya yang melindunginya (di dunia).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,14,'وَمَنْ فِى الْاَرْضِ جَمِيْعًاۙ ثُمَّ يُنْجِيْهِۙ','Dan orang-orang di atas bumi seluruhnya kemudian (mengharapkan) tebusan itu dapat menyelamatkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,15,'كَلَّاۗ اِنَّهَا لَظٰىۙ','Sekali-kali tidak dapat, sesungguhnya neraka itu adalah api yang bergolak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,16,'نَزَّاعَةً لِّلشَّوٰىۚ','yang mengelupas kulit kepala,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,17,'تَدْعُوْا مَنْ اَدْبَرَ وَتَوَلّٰىۙ','yang memanggil orang yang membelakang dan yang berpaling (dari agama),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,18,'وَجَمَعَ فَاَوْعٰى ','serta mengumpulkan (harta benda) lalu menyimpannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,19,'۞ اِنَّ الْاِنْسَانَ خُلِقَ هَلُوْعًاۙ','Sesungguhnya manusia diciptakan bersifat keluh kesah lagi kikir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,20,'اِذَا مَسَّهُ الشَّرُّ جَزُوْعًاۙ','Apabila ia ditimpa kesusahan ia berkeluh kesah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,21,'وَّاِذَا مَسَّهُ الْخَيْرُ مَنُوْعًاۙ','dan apabila ia mendapat kebaikan ia amat kikir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,22,'اِلَّا الْمُصَلِّيْنَۙ','kecuali orang-orang yang mengerjakan shalat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,23,'الَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ دَاۤىِٕمُوْنَۖ','yang mereka itu tetap mengerjakan shalatnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,24,'وَالَّذِيْنَ فِيْٓ اَمْوَالِهِمْ حَقٌّ مَّعْلُوْمٌۖ','dan orang-orang yang dalam hartanya tersedia bagian tertentu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,25,'لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِۖ','bagi orang (miskin) yang meminta dan orang yang tidak mempunyai apa-apa (yang tidak mau meminta),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,26,'وَالَّذِيْنَ يُصَدِّقُوْنَ بِيَوْمِ الدِّيْنِۖ','dan orang-orang yang mempercayai hari pembalasan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,27,'وَالَّذِيْنَ هُمْ مِّنْ عَذَابِ رَبِّهِمْ مُّشْفِقُوْنَۚ','dan orang-orang yang takut terhadap azab Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,28,'اِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُوْنٍۖ','Karena sesungguhnya azab Tuhan mereka tidak dapat orang merasa aman (dari kedatangannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,29,'وَّالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَۙ','Dan orang-orang yang memelihara kemaluannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,30,'اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ','kecuali terhadap isteri-isteri mereka atau budak-budak yang mereka miliki, maka sesungguhnya mereka dalam hal ini tiada tercela.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,31,'فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَۚ','Barangsiapa mencari yang di balik itu, maka mereka itulah orang-orang yang melampaui batas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,32,'وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَۖ','Dan orang-orang yang memelihara amanat-amanat (yang dipikulnya) dan janjinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,33,'وَالَّذِيْنَ هُمْ بِشَهٰدٰتِهِمْ قَاۤىِٕمُوْنَۖ','Dan orang-orang yang memberikan kesaksiannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,34,'وَالَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُوْنَۖ','Dan orang-orang yang memelihara shalatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,35,'اُولٰۤىِٕكَ فِيْ جَنّٰتٍ مُّكْرَمُوْنَ ۗ ࣖ','Mereka itu (kekal) di surga lagi dimuliakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,36,'فَمَالِ الَّذِيْنَ كَفَرُوْا قِبَلَكَ مُهْطِعِيْنَۙ','Mengapakah orang-orang kafir itu bersegera datang ke arahmu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,37,'عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ عِزِيْنَ ','dari kanan dan dari kiri dengan berkelompok-kelompok.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,38,'اَيَطْمَعُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّدْخَلَ جَنَّةَ نَعِيْمٍۙ','Adakah setiap orang dari orang-orang kafir itu ingin masuk ke dalam surga yang penuh kenikmatan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,39,'كَلَّاۗ اِنَّا خَلَقْنٰهُمْ مِّمَّا يَعْلَمُوْنَ ','sekali-kali tidak! Sesungguhnya Kami ciptakan mereka dari apa yang mereka ketahui (air mani).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,40,'فَلَآ اُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغٰرِبِ اِنَّا لَقٰدِرُوْنَۙ','Maka aku bersumpah dengan Tuhan Yang memiliki timur dan barat, sesungguhnya Kami benar-benar Maha Kuasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,41,'عَلٰٓى اَنْ نُّبَدِّلَ خَيْرًا مِّنْهُمْۙ وَمَا نَحْنُ بِمَسْبُوْقِيْنَ ','Untuk mengganti (mereka) dengan kaum yang lebih baik dari mereka, dan Kami sekali-kali tidak dapat dikalahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,42,'فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَۙ','Maka biarkanlah mereka tenggelam (dalam kebatilan) dan bermain-main sampai mereka menjumpai hari yang diancamkan kepada mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,43,'يَوْمَ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ سِرَاعًا كَاَنَّهُمْ اِلٰى نُصُبٍ يُّوْفِضُوْنَۙ','(yaitu) pada hari mereka keluar dari kubur dengan cepat seakan-akan mereka pergi dengan segera kepada berhala-berhala (sewaktu di dunia),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,70,44,'خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ  ۗذٰلِكَ الْيَوْمُ الَّذِيْ كَانُوْا يُوْعَدُوْنَ ࣖ ','dalam keadaan mereka menekurkan pandangannya (serta) diliputi kehinaan. Itulah hari yang dahulunya diancamkan kepada mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,1,'اِنَّآ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اَنْ اَنْذِرْ قَوْمَكَ مِنْ قَبْلِ اَنْ يَّأْتِيَهُمْ عَذَابٌ اَلِيْمٌ ','Sesungguhnya Kami telah mengutus Nuh kepada kaumnya (dengan memerintahkan): "Berilah kaummu peringatan sebelum datang kepadanya azab yang pedih",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,2,'قَالَ يٰقَوْمِ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌۙ','Nuh berkata: "Hai kaumku, sesungguhnya aku adalah pemberi peringatan yang menjelaskan kepada kamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,3,'اَنِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ وَاَطِيْعُوْنِۙ ','(yaitu) sembahlah olehmu Allah, bertakwalah kepada-Nya dan taatlah kepadaku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,4,'يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرْكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ اَجَلَ اللّٰهِ اِذَا جَاۤءَ لَا يُؤَخَّرُۘ لَوْ كُنْتُمْ تَعْلَمُوْنَ ','niscaya Allah akan mengampuni sebagian dosa-dosamu dan menangguhkan kamu sampai kepada waktu yang ditentukan. Sesungguhnya ketetapan Allah apabila telah datang tidak dapat ditangguhkan, kalau kamu mengetahui".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,5,'قَالَ رَبِّ اِنِّيْ دَعَوْتُ قَوْمِيْ لَيْلًا وَّنَهَارًاۙ','Nuh berkata: "Ya Tuhanku sesungguhnya aku telah menyeru kaumku malam dan siang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,6,'فَلَمْ يَزِدْهُمْ دُعَاۤءِيْٓ اِلَّا فِرَارًا ','maka seruanku itu hanyalah menambah mereka lari (dari kebenaran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,7,'وَاِنِّيْ كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوْٓا اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَاَصَرُّوْا وَاسْتَكْبَرُوا اسْتِكْبَارًاۚ ','Dan sesungguhnya setiap kali aku menyeru mereka (kepada iman) agar Engkau mengampuni mereka, mereka memasukkan anak jari mereka ke dalam telinganya dan menutupkan bajunya (kemukanya) dan mereka tetap (mengingkari) dan menyombongkan diri dengan sangat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,8,'ثُمَّ اِنِّيْ دَعَوْتُهُمْ جِهَارًاۙ','Kemudian sesungguhnya aku telah menyeru mereka (kepada iman) dengan cara terang-terangan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,9,'ثُمَّ اِنِّيْٓ اَعْلَنْتُ لَهُمْ وَاَسْرَرْتُ لَهُمْ اِسْرَارًاۙ','kemudian sesungguhnya aku (menyeru) mereka (lagi) dengan terang-terangan dan dengan diam-diam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,10,'فَقُلْتُ اسْتَغْفِرُوْا رَبَّكُمْ اِنَّهٗ كَانَ غَفَّارًاۙ','maka aku katakan kepada mereka: ´Mohonlah ampun kepada Tuhanmu, -sesungguhnya Dia adalah Maha Pengampun-,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,11,'يُّرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًاۙ','niscaya Dia akan mengirimkan hujan kepadamu dengan lebat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,12,'وَّيُمْدِدْكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَيَجْعَلْ لَّكُمْ جَنّٰتٍ وَّيَجْعَلْ لَّكُمْ اَنْهٰرًاۗ','dan membanyakkan harta dan anak-anakmu, dan mengadakan untukmu kebun-kebun dan mengadakan (pula di dalamnya) untukmu sungai-sungai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,13,'مَا لَكُمْ لَا تَرْجُوْنَ لِلّٰهِ وَقَارًاۚ','Mengapa kamu tidak percaya akan kebesaran Allah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,14,'وَقَدْ خَلَقَكُمْ اَطْوَارًا ','Padahal Dia sesungguhnya telah menciptakan kamu dalam beberapa tingkatan kejadian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,15,'اَلَمْ تَرَوْا كَيْفَ خَلَقَ اللّٰهُ سَبْعَ سَمٰوٰتٍ طِبَاقًاۙ','Tidakkah kamu perhatikan bagaimana Allah telah menciptakan tujuh langit bertingkat-tingkat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,16,'وَّجَعَلَ الْقَمَرَ فِيْهِنَّ نُوْرًا وَّجَعَلَ الشَّمْسَ سِرَاجًا ','Dan Allah menciptakan padanya bulan sebagai cahaya dan menjadikan matahari sebagai pelita?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,17,'وَاللّٰهُ اَنْۢبَتَكُمْ مِّنَ الْاَرْضِ نَبَاتًاۙ','Dan Allah menumbuhkan kamu dari tanah dengan sebaik-baiknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,18,'ثُمَّ يُعِيْدُكُمْ فِيْهَا وَيُخْرِجُكُمْ اِخْرَاجًا ','kemudian Dia mengembalikan kamu ke dalam tanah dan mengeluarkan kamu (daripadanya pada hari kiamat) dengan sebenar-benarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,19,'وَاللّٰهُ جَعَلَ لَكُمُ الْاَرْضَ بِسَاطًاۙ','Dan Allah menjadikan bumi untukmu sebagai hamparan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,20,'لِّتَسْلُكُوْا مِنْهَا سُبُلًا فِجَاجًا ࣖ ','supaya kamu menjalani jalan-jalan yang luas di bumi itu".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,21,'قَالَ نُوْحٌ رَّبِّ اِنَّهُمْ عَصَوْنِيْ وَاتَّبَعُوْا مَنْ لَّمْ يَزِدْهُ مَالُهٗ وَوَلَدُهٗٓ اِلَّا خَسَارًاۚ ','Nuh berkata: "Ya Tuhanku, sesungguhnya mereka telah mendurhakaiku dan telah mengikuti orang-orang yang harta dan anak-anaknya tidak menambah kepadanya melainkan kerugian belaka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,22,'وَمَكَرُوْا مَكْرًا كُبَّارًاۚ','dan melakukan tipu-daya yang amat besar".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,23,'وَقَالُوْا لَا تَذَرُنَّ اٰلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَّلَا سُوَاعًا ەۙ وَّلَا يَغُوْثَ وَيَعُوْقَ وَنَسْرًاۚ','Dan mereka berkata: "Jangan sekali-kali kamu meninggalkan (penyembahan) tuhan-tuhan kamu dan jangan pula sekali-kali kamu meninggalkan (penyembahan) wadd, dan jangan pula suwwa´, yaghuts, ya´uq dan nasr".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,24,'وَقَدْ اَضَلُّوْا كَثِيْرًا ەۚ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا ضَلٰلًا','Dan sesudahnya mereka menyesatkan kebanyakan (manusia); dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kesesatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,25,'مِمَّا خَطِيْۤـٰٔتِهِمْ اُغْرِقُوْا فَاُدْخِلُوْا نَارًا ەۙ فَلَمْ يَجِدُوْا لَهُمْ مِّنْ دُوْنِ اللّٰهِ اَنْصَارًا','Disebabkan kesalahan-kesalahan mereka, mereka ditenggelamkan lalu dimasukkan ke neraka, maka mereka tidak mendapat penolong-penolong bagi mereka selain dari Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,26,'وَقَالَ نُوْحٌ رَّبِّ لَا تَذَرْ عَلَى الْاَرْضِ مِنَ الْكٰفِرِيْنَ دَيَّارًا ','Nuh berkata: "Ya Tuhanku, janganlah Engkau biarkan seorangpun di antara orang-orang kafir itu tinggal di atas bumi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,27,'اِنَّكَ اِنْ تَذَرْهُمْ يُضِلُّوْا عِبَادَكَ وَلَا يَلِدُوْٓا اِلَّا فَاجِرًا كَفَّارًا ','Sesungguhnya jika Engkau biarkan mereka tinggal, niscaya mereka akan menyesatkan hamba-hamba-Mu, dan mereka tidak akan melahirkan selain anak yang berbuat maksiat lagi sangat kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,71,28,'رَبِّ اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَّلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا تَبَارًا ࣖ','Ya Tuhanku! Ampunilah aku, ibu bapakku, orang yang masuk ke rumahku dengan beriman dan semua orang yang beriman laki-laki dan perempuan. Dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kebinasaan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,1,'قُلْ اُوْحِيَ اِلَيَّ اَنَّهُ اسْتَمَعَ نَفَرٌ مِّنَ الْجِنِّ فَقَالُوْٓا اِنَّا سَمِعْنَا قُرْاٰنًا عَجَبًاۙ','Katakanlah (hai Muhammad): "Telah diwahyukan kepadamu bahwasanya: telah mendengarkan sekumpulan jin (akan Al Quran), lalu mereka berkata: Sesungguhnya kami telah mendengarkan Al Quran yang menakjubkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,2,'يَّهْدِيْٓ اِلَى الرُّشْدِ فَاٰمَنَّا بِهٖۗ وَلَنْ نُّشْرِكَ بِرَبِّنَآ اَحَدًاۖ','(yang) memberi petunjuk kapada jalan yang benar, lalu kami beriman kepadanya. Dan kami sekali-kali tidak akan mempersekutukan seseorangpun dengan Tuhan kami,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,3,'وَّاَنَّهٗ تَعٰلٰى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَّلَا وَلَدًاۖ','dan bahwasanya Maha Tinggi kebesaran Tuhan kami, Dia tidak beristeri dan tidak (pula) beranak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,4,'وَّاَنَّهٗ كَانَ يَقُوْلُ سَفِيْهُنَا عَلَى اللّٰهِ شَطَطًاۖ','Dan bahwasanya: orang yang kurang akal daripada kami selalu mengatakan (perkataan) yang melampaui batas terhadap Allah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,5,'وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ تَقُوْلَ الْاِنْسُ وَالْجِنُّ عَلَى اللّٰهِ كَذِبًاۙ','dan sesungguhnya kami mengira, bahwa manusia dan jin sekali-kali tidak akan mengatakan perkataan yang dusta terhadap Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,6,'وَّاَنَّهٗ كَانَ رِجَالٌ مِّنَ الْاِنْسِ يَعُوْذُوْنَ بِرِجَالٍ مِّنَ الْجِنِّ فَزَادُوْهُمْ رَهَقًاۖ','Dan bahwasanya ada beberapa orang laki-laki di antara manusia meminta perlindungan kepada beberapa laki-laki di antara jin, maka jin-jin itu menambah bagi mereka dosa dan kesalahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,7,'وَّاَنَّهُمْ ظَنُّوْا كَمَا ظَنَنْتُمْ اَنْ لَّنْ يَّبْعَثَ اللّٰهُ اَحَدًاۖ','Dan sesungguhnya mereka (jin) menyangka sebagaimana persangkaan kamu (orang-orang kafir Mekah), bahwa Allah sekali-kali tidak akan membangkitkan seorang (rasul)pun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,8,'وَّاَنَّا لَمَسْنَا السَّمَاۤءَ فَوَجَدْنٰهَا مُلِئَتْ حَرَسًا شَدِيْدًا وَّشُهُبًاۖ','dan sesungguhnya kami telah mencoba mengetahui (rahasia) langit, maka kami mendapatinya penuh dengan penjagaan yang kuat dan panah-panah api,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,9,'وَّاَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِۗ فَمَنْ يَّسْتَمِعِ الْاٰنَ يَجِدْ لَهٗ شِهَابًا رَّصَدًاۖ','dan sesungguhnya kami dahulu dapat menduduki beberapa tempat di langit itu untuk mendengar-dengarkan (berita-beritanya). Tetapi sekarang barangsiapa yang (mencoba) mendengar-dengarkan (seperti itu) tentu akan menjumpai panah api yang mengintai (untuk membakarnya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,10,'وَّاَنَّا لَا نَدْرِيْٓ اَشَرٌّ اُرِيْدَ بِمَنْ فِى الْاَرْضِ اَمْ اَرَادَ بِهِمْ رَبُّهُمْ رَشَدًاۙ','Dan sesungguhnya kami tidak mengetahui (dengan adanya penjagaan itu) apakah keburukan yang dikehendaki bagi orang yang di bumi ataukah Tuhan mereka menghendaki kebaikan bagi mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,11,'وَّاَنَّا مِنَّا الصّٰلِحُوْنَ وَمِنَّا دُوْنَ ذٰلِكَۗ  كُنَّا طَرَاۤىِٕقَ قِدَدًاۙ','Dan sesungguhnya di antara kami ada orang-orang yang saleh dan di antara kami ada (pula) yang tidak demikian halnya. Adalah kami menempuh jalan yang berbeda-beda.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,12,'وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ نُّعْجِزَ اللّٰهَ فِى الْاَرْضِ وَلَنْ نُّعْجِزَهٗ هَرَبًاۖ','Dan sesungguhnya kami mengetahui bahwa kami sekali-kali tidak akan dapat melepaskan diri (dari kekuasaan) Allah di muka bumi dan sekali-kali tidak (pula) dapat melepaskan diri (daripada)Nya dengan lari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,13,'وَّاَنَّا لَمَّا سَمِعْنَا الْهُدٰىٓ اٰمَنَّا بِهٖۗ فَمَنْ يُّؤْمِنْۢ بِرَبِّهٖ فَلَا يَخَافُ بَخْسًا وَّلَا رَهَقًاۖ','Dan sesungguhnya kami tatkala mendengar petunjuk (Al Quran), kami beriman kepadanya. Barangsiapa beriman kepada Tuhannya, maka ia tidak takut akan pengurangan pahala dan tidak (takut pula) akan penambahan dosa dan kesalahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,14,'وَّاَنَّا مِنَّا الْمُسْلِمُوْنَ وَمِنَّا الْقَاسِطُوْنَۗ فَمَنْ اَسْلَمَ فَاُولٰۤىِٕكَ تَحَرَّوْا رَشَدًا ','Dan sesungguhnya di antara kami ada orang-orang yang taat dan ada (pula) orang-orang yang menyimpang dari kebenaran. Barangsiapa yang yang taat, maka mereka itu benar-benar telah memilih jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,15,'وَاَمَّا الْقَاسِطُوْنَ فَكَانُوْا لِجَهَنَّمَ حَطَبًاۙ','Adapun orang-orang yang menyimpang dari kebenaran, maka mereka menjadi kayu api bagi neraka Jahannam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,16,'وَّاَنْ لَّوِ اسْتَقَامُوْا عَلَى الطَّرِيْقَةِ لَاَسْقَيْنٰهُمْ مَّاۤءً غَدَقًاۙ','Dan bahwasanya: jikalau mereka tetap berjalan lurus di atas jalan itu (agama Islam), benar-benar Kami akan memberi minum kepada mereka air yang segar (rezeki yang banyak).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,17,'لِّنَفْتِنَهُمْ فِيْهِۗ وَمَنْ يُّعْرِضْ عَنْ ذِكْرِ رَبِّهٖ يَسْلُكْهُ عَذَابًا صَعَدًاۙ','Untuk Kami beri cobaan kepada mereka padanya. Dan barangsiapa yang berpaling dari peringatan Tuhannya, niscaya akan dimasukkan-Nya ke dalam azab yang amat berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,18,'وَّاَنَّ الْمَسٰجِدَ لِلّٰهِ فَلَا تَدْعُوْا مَعَ اللّٰهِ اَحَدًاۖ','Dan sesungguhnya mesjid-mesjid itu adalah kepunyaan Allah. Maka janganlah kamu menyembah seseorangpun di dalamnya di samping (menyembah) Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,19,'وَّاَنَّهٗ لَمَّا قَامَ عَبْدُ اللّٰهِ يَدْعُوْهُ كَادُوْا يَكُوْنُوْنَ عَلَيْهِ لِبَدًاۗ  ࣖ','Dan bahwasanya tatkala hamba Allah (Muhammad) berdiri menyembah-Nya (mengerjakan ibadat), hampir saja jin-jin itu desak mendesak mengerumuninya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,20,'قُلْ اِنَّمَآ اَدْعُوْا رَبِّيْ وَلَآ اُشْرِكُ بِهٖٓ اَحَدًا ','Katakanlah: "Sesungguhnya aku hanya menyembah Tuhanku dan aku tidak mempersekutukan sesuatupun dengan-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,21,'قُلْ اِنِّيْ لَآ اَمْلِكُ لَكُمْ ضَرًّا وَّلَا رَشَدًا ','Katakanlah: "Sesungguhnya aku tidak kuasa mendatangkan sesuatu kemudharatanpun kepadamu dan tidak (pula) suatu kemanfaatan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,22,'قُلْ اِنِّيْ لَنْ يُّجِيْرَنِيْ مِنَ اللّٰهِ اَحَدٌ ەۙ وَّلَنْ اَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا ۙ','Katakanlah: "Sesungguhnya aku sekali-kali tiada seorangpun dapat melindungiku dari (azab) Allah dan sekali-kali aku tiada akan memperoleh tempat berlindung selain daripada-Nya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,23,'اِلَّا بَلٰغًا مِّنَ اللّٰهِ وَرِسٰلٰتِهٖۗ وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ لَهٗ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ','Akan tetapi (aku hanya) menyampaikan (peringatan) dari Allah dan risalah-Nya. Dan barangsiapa yang mendurhakai Allah dan Rasul-Nya maka sesungguhnya baginyalah neraka Jahannam, mereka kekal di dalamnya selama-lamanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,24,'حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ فَسَيَعْلَمُوْنَ مَنْ اَضْعَفُ نَاصِرًا وَّاَقَلُّ عَدَدًاۗ','Sehingga apabila mereka melihat azab yang diancamkan kepada mereka, maka mereka akan mengetahui siapakah yang lebih lemah penolongnya dan lebih sedikit bilangannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,25,'قُلْ اِنْ اَدْرِيْٓ اَقَرِيْبٌ مَّا تُوْعَدُوْنَ اَمْ يَجْعَلُ لَهٗ رَبِّيْٓ اَمَدًا ','Katakanlah: "Aku tidak mengetahui, apakah azab yang diancamkan kepadamu itu dekat ataukah Tuhanku menjadikan bagi (kedatangan) azab itu masa yang panjang?".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,26,'عٰلِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلٰى غَيْبِهٖٓ اَحَدًاۙ','(Dia adalah Tuhan) Yang Mengetahui yang ghaib, maka Dia tidak memperlihatkan kepada seorangpun tentang yang ghaib itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,27,'اِلَّا مَنِ ارْتَضٰى مِنْ رَّسُوْلٍ فَاِنَّهٗ يَسْلُكُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ رَصَدًاۙ','Kecuali kepada rasul yang diridhai-Nya, maka sesungguhnya Dia mengadakan penjaga-penjaga (malaikat) di muka dan di belakangnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,72,28,'لِّيَعْلَمَ اَنْ قَدْ اَبْلَغُوْا رِسٰلٰتِ رَبِّهِمْ وَاَحَاطَ بِمَا لَدَيْهِمْ وَاَحْصٰى كُلَّ شَيْءٍ عَدَدًا ࣖ','Supaya Dia mengetahui, bahwa sesungguhnya rasul-rasul itu telah menyampaikan risalah-risalah Tuhannya, sedang (sebenarnya) ilmu-Nya meliputi apa yang ada pada mereka, dan Dia menghitung segala sesuatu satu persatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,1,'يٰٓاَيُّهَا الْمُزَّمِّلُۙ','Hai orang yang berselimut (Muhammad),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,2,'قُمِ الَّيْلَ اِلَّا قَلِيْلًاۙ','bangunlah (untuk sembahyang) di malam hari, kecuali sedikit (daripadanya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,3,'نِّصْفَهٗٓ اَوِ انْقُصْ مِنْهُ قَلِيْلًاۙ','(yaitu) seperduanya atau kurangilah dari seperdua itu sedikit.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,4,'اَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْاٰنَ تَرْتِيْلًاۗ','atau lebih dari seperdua itu. Dan bacalah Al Quran itu dengan perlahan-lahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,5,'اِنَّا سَنُلْقِيْ عَلَيْكَ قَوْلًا ثَقِيْلًا','Sesungguhnya Kami akan menurunkan kapadamu perkataan yang berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,6,'اِنَّ نَاشِئَةَ الَّيْلِ هِيَ اَشَدُّ وَطْـًٔا وَّاَقْوَمُ قِيْلًاۗ','Sesungguhnya bangun di waktu malam adalah lebih tepat (untuk khusyu´) dan bacaan di waktu itu lebih berkesan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,7,'اِنَّ لَكَ فِى النَّهَارِ سَبْحًا طَوِيْلًاۗ','Sesungguhnya kamu pada siang hari mempunyai urusan yang panjang (banyak).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,8,'وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ اِلَيْهِ تَبْتِيْلًاۗ','Sebutlah nama Tuhanmu, dan beribadatlah kepada-Nya dengan penuh ketekunan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,9,'رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَآ اِلٰهَ اِلَّا هُوَ فَاتَّخِذْهُ وَكِيْلًا','(Dialah) Tuhan masyrik dan maghrib, tiada Tuhan (yang berhak disembah) melainkan Dia, maka ambillah Dia sebagai pelindung.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,10,'وَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاهْجُرْهُمْ هَجْرًا جَمِيْلًا ','Dan bersabarlah terhadap apa yang mereka ucapkan dan jauhilah mereka dengan cara yang baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,11,'وَذَرْنِيْ وَالْمُكَذِّبِيْنَ اُولِى النَّعْمَةِ وَمَهِّلْهُمْ قَلِيْلًا ','Dan biarkanlah Aku (saja) bertindak terhadap orang-orang yang mendustakan itu, orang-orang yang mempunyai kemewahan dan beri tangguhlah mereka barang sebentar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,12,'اِنَّ لَدَيْنَآ اَنْكَالًا وَّجَحِيْمًاۙ','Karena sesungguhnya pada sisi Kami ada belenggu-belenggu yang berat dan neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,13,'وَّطَعَامًا ذَا غُصَّةٍ وَّعَذَابًا اَلِيْمًا ','Dan makanan yang menyumbat di kerongkongan dan azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,14,'يَوْمَ تَرْجُفُ الْاَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيْبًا مَّهِيْلًا ','Pada hari bumi dan gunung-gunung bergoncangan, dan menjadilah gunung-gunung itu tumpukan-tumpukan pasir yang berterbangan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,15,'اِنَّآ اَرْسَلْنَآ اِلَيْكُمْ رَسُوْلًا ەۙ شَاهِدًا عَلَيْكُمْ كَمَآ اَرْسَلْنَآ اِلٰى فِرْعَوْنَ رَسُوْلًا ۗ','Sesungguhnya Kami telah mengutus kepada kamu (hai orang kafir Mekah) seorang Rasul, yang menjadi saksi terhadapmu, sebagaimana Kami telah mengutus (dahulu) seorang Rasul kepada Fir´aun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,16,'فَعَصٰى فِرْعَوْنُ الرَّسُوْلَ فَاَخَذْنٰهُ اَخْذًا وَّبِيْلًاۚ','Maka Fir´aun mendurhakai Rasul itu, lalu Kami siksa dia dengan siksaan yang berat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,17,'فَكَيْفَ تَتَّقُوْنَ اِنْ كَفَرْتُمْ يَوْمًا يَّجْعَلُ الْوِلْدَانَ شِيْبًاۖ','Maka bagaimanakah kamu akan dapat memelihara dirimu jika kamu tetap kafir kepada hari yang menjadikan anak-anak beruban.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,18,' ۨالسَّمَاۤءُ مُنْفَطِرٌۢ بِهٖۗ كَانَ وَعْدُهٗ مَفْعُوْلًا','Langit(pun) menjadi pecah belah pada hari itu. Adalah janji-Nya itu pasti terlaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,19,'اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا ࣖ ','Sesungguhnya ini adalah suatu peringatan. Maka barangsiapa yang menghendaki niscaya ia menempuh jalan (yang menyampaikannya) kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,73,20,'۞ اِنَّ رَبَّكَ يَعْلَمُ اَنَّكَ تَقُوْمُ اَدْنٰى مِنْ ثُلُثَيِ الَّيْلِ وَنِصْفَهٗ وَثُلُثَهٗ وَطَاۤىِٕفَةٌ مِّنَ الَّذِيْنَ مَعَكَۗ وَاللّٰهُ يُقَدِّرُ الَّيْلَ وَالنَّهَارَۗ عَلِمَ اَنْ لَّنْ تُحْصُوْهُ فَتَابَ عَلَيْكُمْ فَاقْرَءُوْا مَا تَيَسَّرَ مِنَ الْقُرْاٰنِۗ عَلِمَ اَنْ سَيَكُوْنُ مِنْكُمْ مَّرْضٰىۙ وَاٰخَرُوْنَ يَضْرِبُوْنَ فِى الْاَرْضِ يَبْتَغُوْنَ مِنْ فَضْلِ اللّٰهِ ۙوَاٰخَرُوْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۖفَاقْرَءُوْا مَا تَيَسَّرَ مِنْهُۙ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَقْرِضُوا اللّٰهَ قَرْضًا حَسَنًاۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۙهُوَ خَيْرًا وَّاَعْظَمَ اَجْرًاۗ وَاسْتَغْفِرُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ࣖ ','Sesungguhnya Tuhanmu mengetahui bahwasanya kamu berdiri (sembahyang) kurang dari dua pertiga malam, atau seperdua malam atau sepertiganya dan (demikian pula) segolongan dari orang-orang yang bersama kamu. Dan Allah menetapkan ukuran malam dan siang. Allah mengetahui bahwa kamu sekali-kali tidak dapat menentukan batas-batas waktu-waktu itu, maka Dia memberi keringanan kepadamu, karena itu bacalah apa yang mudah (bagimu) dari Al Quran. Dia mengetahui bahwa akan ada di antara kamu orang-orang yang sakit dan orang-orang yang berjalan di muka bumi mencari sebagian karunia Allah; dan orang-orang yang lain lagi berperang di jalan Allah, maka bacalah apa yang mudah (bagimu) dari Al Quran dan dirikanlah sembahyang, tunaikanlah zakat dan berikanlah pinjaman kepada Allah pinjaman yang baik. Dan kebaikan apa saja yang kamu perbuat untuk dirimu niscaya kamu memperoleh (balasan)nya di sisi Allah sebagai balasan yang paling baik dan yang paling besar pahalanya. Dan mohonlah ampunan kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,1,'يٰٓاَيُّهَا الْمُدَّثِّرُۙ','Hai orang yang berkemul (berselimut),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,2,'قُمْ فَاَنْذِرْۖ','bangunlah, lalu berilah peringatan!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,3,'وَرَبَّكَ فَكَبِّرْۖ','dan Tuhanmu agungkanlah!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,4,'وَثِيَابَكَ فَطَهِّرْۖ','dan pakaianmu bersihkanlah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,5,'وَالرُّجْزَ فَاهْجُرْۖ ','dan perbuatan dosa tinggalkanlah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,6,'وَلَا تَمْنُنْ تَسْتَكْثِرُۖ ','dan janganlah kamu memberi (dengan maksud) memperoleh (balasan) yang lebih banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,7,'وَلِرَبِّكَ فَاصْبِرْۗ ','Dan untuk (memenuhi perintah) Tuhanmu, bersabarlah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,8,'فَاِذَا نُقِرَ فِى النَّاقُوْرِۙ ','Apabila ditiup sangkakala,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,9,'فَذٰلِكَ يَوْمَىِٕذٍ يَّوْمٌ عَسِيْرٌۙ ','maka waktu itu adalah waktu (datangnya) hari yang sulit,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,10,'عَلَى الْكٰفِرِيْنَ غَيْرُ يَسِيْرٍ ','bagi orang-orang kafir lagi tidak mudah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,11,'ذَرْنِيْ وَمَنْ خَلَقْتُ وَحِيْدًاۙ ','Biarkanlah Aku bertindak terhadap orang yang Aku telah menciptakannya sendirian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,12,'وَّجَعَلْتُ لَهٗ مَالًا مَّمْدُوْدًاۙ ','Dan Aku jadikan baginya harta benda yang banyak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,13,'وَّبَنِيْنَ شُهُوْدًاۙ ','dan anak-anak yang selalu bersama dia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,14,'وَّمَهَّدْتُّ لَهٗ تَمْهِيْدًاۙ ','dan Ku-lapangkan baginya (rezeki dan kekuasaan) dengan selapang-lapangnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,15,'ثُمَّ يَطْمَعُ اَنْ اَزِيْدَۙ    ','kemudian dia ingin sekali supaya Aku menambahnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,16,'كَلَّاۗ اِنَّهٗ كَانَ لِاٰيٰتِنَا عَنِيْدًاۗ ','Sekali-kali tidak (akan Aku tambah), karena sesungguhnya dia menentang ayat-ayat Kami (Al Quran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,17,'سَاُرْهِقُهٗ صَعُوْدًاۗ ','Aku akan membebaninya mendaki pendakian yang memayahkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,18,'اِنَّهٗ فَكَّرَ وَقَدَّرَۙ ','Sesungguhnya dia telah memikirkan dan menetapkan (apa yang ditetapkannya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,19,'فَقُتِلَ كَيْفَ قَدَّرَۙ ','maka celakalah dia! Bagaimana dia menetapkan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,20,'ثُمَّ قُتِلَ كَيْفَ قَدَّرَۙ ','kemudian celakalah dia! Bagaimanakah dia menetapkan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,21,'ثُمَّ نَظَرَۙ ','kemudian dia memikirkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,22,'ثُمَّ عَبَسَ وَبَسَرَۙ ','sesudah itu dia bermasam muka dan merengut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,23,'ثُمَّ اَدْبَرَ وَاسْتَكْبَرَۙ ','kemudian dia berpaling (dari kebenaran) dan menyombongkan diri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,24,'فَقَالَ اِنْ هٰذَآ اِلَّا سِحْرٌ يُّؤْثَرُۙ ','lalu dia berkata: "(Al Quran) ini tidak lain hanyalah sihir yang dipelajari (dari orang-orang dahulu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,25,'اِنْ هٰذَآ اِلَّا قَوْلُ الْبَشَرِۗ ','ini tidak lain hanyalah perkataan manusia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,26,'سَاُصْلِيْهِ سَقَرَ','Aku akan memasukkannya ke dalam (neraka) Saqar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,27,'وَمَآ اَدْرٰىكَ مَا سَقَرُۗ ','Tahukah kamu apakah (neraka) Saqar itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,28,'لَا تُبْقِيْ وَلَا تَذَرُۚ ','Saqar itu tidak meninggalkan dan tidak membiarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,29,'لَوَّاحَةٌ لِّلْبَشَرِۚ ','(Neraka Saqar) adalah pembakar kulit manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,30,'عَلَيْهَا تِسْعَةَ عَشَرَۗ ','Dan di atasnya ada sembilan belas (malaikat penjaga).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,31,'وَمَا جَعَلْنَآ اَصْحٰبَ النَّارِ اِلَّا مَلٰۤىِٕكَةً ۖوَّمَا جَعَلْنَا عِدَّتَهُمْ اِلَّا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْاۙ لِيَسْتَيْقِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَيَزْدَادَ الَّذِيْنَ اٰمَنُوْٓا اِيْمَانًا وَّلَا يَرْتَابَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْمُؤْمِنُوْنَۙ وَلِيَقُوْلَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْكٰفِرُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًاۗ  كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَمَا يَعْلَمُ جُنُوْدَ رَبِّكَ اِلَّا هُوَۗ وَمَا هِيَ اِلَّا ذِكْرٰى لِلْبَشَرِ ࣖ','Dan tiada Kami jadikan penjaga neraka itu melainkan dari malaikat: dan tidaklah Kami menjadikan bilangan mereka itu melainkan untuk jadi cobaan bagi orang-orang kafir, supaya orang-orang yang diberi Al-Kitab menjadi yakin dan supaya orang yang beriman bertambah imannya dan supaya orang-orang yang diberi Al Kitab dan orng-orang mukmin itu tidak ragu-ragu dan supaya orang-orang yang di dalam hatinya ada penyakit dan orang-orang kafir (mengatakan): "Apakah yang dikehendaki Allah dengan bilangan ini sebagai suatu perumpamaan?" Demikianlah Allah membiarkan sesat orang-orang yang dikehendaki-Nya dan memberi petunjuk kepada siapa yang dikehendaki-Nya. Dan tidak ada yang mengetahui tentara Tuhanmu melainkan Dia sendiri. Dan Saqar itu tiada lain hanyalah peringatan bagi manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,32,'كَلَّا وَالْقَمَرِۙ','Sekali-kali tidak, demi bulan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,33,'وَالَّيْلِ اِذْ اَدْبَرَۙ','dan malam ketika telah berlalu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,34,'وَالصُّبْحِ اِذَآ اَسْفَرَۙ','dan subuh apabila mulai terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,35,'اِنَّهَا لَاِحْدَى الْكُبَرِۙ','Sesungguhnya Saqar itu adalah salah satu bencana yang amat besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,36,'نَذِيْرًا لِّلْبَشَرِۙ','sebagai ancaman bagi manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,37,'لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّتَقَدَّمَ اَوْ يَتَاَخَّرَۗ','(Yaitu) bagi siapa di antaramu yang berkehendak akan maju atau mundur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,38,'كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ رَهِيْنَةٌۙ','Tiap-tiap diri bertanggung jawab atas apa yang telah diperbuatnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,39,'اِلَّآ اَصْحٰبَ الْيَمِيْنِ ۛ ','kecuali golongan kanan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,40,'فِيْ جَنّٰتٍ ۛ يَتَسَاۤءَلُوْنَۙ','berada di dalam surga, mereka tanya menanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,41,'عَنِ الْمُجْرِمِيْنَۙ','tentang (keadaan) orang-orang yang berdosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,42,'مَا سَلَكَكُمْ فِيْ سَقَرَ ','"Apakah yang memasukkan kamu ke dalam Saqar (neraka)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,43,'قَالُوْا لَمْ نَكُ مِنَ الْمُصَلِّيْنَۙ','Mereka menjawab: "Kami dahulu tidak termasuk orang-orang yang mengerjakan shalat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,44,'وَلَمْ نَكُ نُطْعِمُ الْمِسْكِيْنَۙ','dan kami tidak (pula) memberi makan orang miskin,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,45,'وَكُنَّا نَخُوْضُ مَعَ الْخَاۤىِٕضِيْنَۙ','dan adalah kami membicarakan yang bathil, bersama dengan orang-orang yang membicarakannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,46,'وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّيْنِۙ','dan adalah kami mendustakan hari pembalasan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,47,'حَتّٰىٓ اَتٰىنَا الْيَقِيْنُۗ','hingga datang kepada kami kematian".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,48,'فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِيْنَۗ','Maka tidak berguna lagi bagi mereka syafa´at dari orang-orang yang memberikan syafa´at.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,49,'فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِيْنَۙ','Maka mengapa mereka (orang-orang kafir) berpaling dari peringatan (Allah)?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,50,'كَاَنَّهُمْ حُمُرٌ مُّسْتَنْفِرَةٌۙ','seakan-akan mereka itu keledai liar yang lari terkejut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,51,'فَرَّتْ مِنْ قَسْوَرَةٍۗ','lari daripada singa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,52,'بَلْ يُرِيْدُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّؤْتٰى صُحُفًا مُّنَشَّرَةًۙ','Bahkan tiap-tiap orang dari mereka berkehendak supaya diberikan kepadanya lembaran-lembaran yang terbuka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,53,'كَلَّاۗ بَلْ لَّا يَخَافُوْنَ الْاٰخِرَةَۗ','Sekali-kali tidak. Sebenarnya mereka tidak takut kepada negeri akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,54,'كَلَّآ اِنَّهٗ تَذْكِرَةٌ ۚ','Sekali-kali tidak demikian halnya. Sesungguhnya Al Quran itu adalah peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,55,'فَمَنْ شَاۤءَ ذَكَرَهٗۗ','Maka barangsiapa menghendaki, niscaya dia mengambil pelajaran daripadanya (Al Quran).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,74,56,'وَمَا يَذْكُرُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗهُوَ اَهْلُ التَّقْوٰى وَاَهْلُ الْمَغْفِرَةِ ࣖ','Dan mereka tidak akan mengambil pelajaran daripadanya kecuali (jika) Allah menghendakinya. Dia (Allah) adalah Tuhan Yang patut (kita) bertakwa kepada-Nya dan berhak memberi ampun.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,1,'لَآ اُقْسِمُ بِيَوْمِ الْقِيٰمَةِۙ','Aku bersumpah demi hari kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,2,'وَلَآ اُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ ','dan aku bersumpah dengan jiwa yang amat menyesali (dirinya sendiri).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,3,'اَيَحْسَبُ الْاِنْسَانُ اَلَّنْ نَّجْمَعَ عِظَامَهٗ ۗ','Apakah manusia mengira, bahwa Kami tidak akan mengumpulkan (kembali) tulang belulangnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,4,'بَلٰى قَادِرِيْنَ عَلٰٓى اَنْ نُّسَوِّيَ بَنَانَهٗ ','Bukan demikian, sebenarnya Kami kuasa menyusun (kembali) jari jemarinya dengan sempurna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,5,'بَلْ يُرِيْدُ الْاِنْسَانُ لِيَفْجُرَ اَمَامَهٗۚ','Bahkan manusia itu hendak membuat maksiat terus menerus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,6,'يَسْـَٔلُ اَيَّانَ يَوْمُ الْقِيٰمَةِۗ','Ia berkata: "Bilakah hari kiamat itu?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,7,'فَاِذَا بَرِقَ الْبَصَرُۙ','Maka apabila mata terbelalak (ketakutan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,8,'وَخَسَفَ الْقَمَرُۙ','dan apabila bulan telah hilang cahayanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,9,'وَجُمِعَ الشَّمْسُ وَالْقَمَرُۙ','dan matahari dan bulan dikumpulkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,10,'يَقُوْلُ الْاِنْسَانُ يَوْمَىِٕذٍ اَيْنَ الْمَفَرُّۚ','pada hari itu manusia berkata: "Ke mana tempat berlari?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,11,'كَلَّا لَا وَزَرَۗ','sekali-kali tidak! Tidak ada tempat berlindung!')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,12,'اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمُسْتَقَرُّۗ','Hanya kepada Tuhanmu sajalah pada hari itu tempat kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,13,'يُنَبَّؤُا الْاِنْسَانُ يَوْمَىِٕذٍۢ بِمَا قَدَّمَ وَاَخَّرَۗ','Pada hari itu diberitakan kepada manusia apa yang telah dikerjakannya dan apa yang dilalaikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,14,'بَلِ الْاِنْسَانُ عَلٰى نَفْسِهٖ بَصِيْرَةٌۙ','Bahkan manusia itu menjadi saksi atas dirinya sendiri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,15,'وَّلَوْ اَلْقٰى مَعَاذِيْرَهٗۗ','meskipun dia mengemukakan alasan-alasannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,16,'لَا تُحَرِّكْ بِهٖ لِسَانَكَ لِتَعْجَلَ بِهٖۗ','Janganlah kamu gerakkan lidahmu untuk (membaca) Al Quran karena hendak cepat-cepat (menguasai)nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,17,'اِنَّ عَلَيْنَا جَمْعَهٗ وَقُرْاٰنَهٗ ۚ','Sesungguhnya atas tanggungan Kamilah mengumpulkannya (di dadamu) dan (membuatmu pandai) membacanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,18,'فَاِذَا قَرَأْنٰهُ فَاتَّبِعْ قُرْاٰنَهٗ ۚ','Apabila Kami telah selesai membacakannya maka ikutilah bacaannya itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,19,'ثُمَّ اِنَّ عَلَيْنَا بَيَانَهٗ ۗ','Kemudian, sesungguhnya atas tanggungan Kamilah penjelasannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,20,'كَلَّا بَلْ تُحِبُّوْنَ الْعَاجِلَةَۙ','Sekali-kali janganlah demikian. Sebenarnya kamu (hai manusia) mencintai kehidupan dunia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,21,'وَتَذَرُوْنَ الْاٰخِرَةَۗ','dan meninggalkan (kehidupan) akhirat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,22,'وُجُوْهٌ يَّوْمَىِٕذٍ نَّاضِرَةٌۙ','Wajah-wajah (orang-orang mukmin) pada hari itu berseri-seri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,23,'اِلٰى رَبِّهَا نَاظِرَةٌ ۚ','Kepada Tuhannyalah mereka melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,24,'وَوُجُوْهٌ يَّوْمَىِٕذٍۢ بَاسِرَةٌۙ','Dan wajah-wajah (orang kafir) pada hari itu muram,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,25,'تَظُنُّ اَنْ يُّفْعَلَ بِهَا فَاقِرَةٌ ۗ','mereka yakin bahwa akan ditimpakan kepadanya malapetaka yang amat dahsyat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,26,'كَلَّآ اِذَا بَلَغَتِ التَّرَاقِيَۙ','Sekali-kali jangan. Apabila nafas (seseorang) telah (mendesak) sampai ke kerongkongan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,27,'وَقِيْلَ مَنْ ۜرَاقٍۙ','dan dikatakan (kepadanya): "Siapakah yang dapat menyembuhkan?",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,28,'وَّظَنَّ اَنَّهُ الْفِرَاقُۙ','dan dia yakin bahwa sesungguhnya itulah waktu perpisahan (dengan dunia),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,29,'وَالْتَفَّتِ السَّاقُ بِالسَّاقِۙ','dan bertaut betis (kiri) dan betis (kanan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,30,'اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمَسَاقُ ۗ ࣖ','kepada Tuhanmulah pada hari itu kamu dihalau.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,31,'فَلَا صَدَّقَ وَلَا صَلّٰىۙ','Dan ia tidak mau membenarkan (Rasul dan Al Quran) dan tidak mau mengerjakan shalat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,32,'وَلٰكِنْ كَذَّبَ وَتَوَلّٰىۙ','tetapi ia mendustakan (Rasul) dam berpaling (dari kebenaran),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,33,'ثُمَّ ذَهَبَ اِلٰٓى اَهْلِهٖ يَتَمَطّٰىۗ','kemudian ia pergi kepada ahlinya dengan berlagak (sombong).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,34,'اَوْلٰى لَكَ فَاَوْلٰىۙ','Kecelakaanlah bagimu (hai orang kafir) dan kecelakaanlah bagimu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,35,'ثُمَّ اَوْلٰى لَكَ فَاَوْلٰىۗ','kemudian kecelakaanlah bagimu (hai orang kafir) dan kecelakaanlah bagimu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,36,'اَيَحْسَبُ الْاِنْسَانُ اَنْ يُّتْرَكَ سُدًىۗ','Apakah manusia mengira, bahwa ia akan dibiarkan begitu saja (tanpa pertanggung jawaban)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,37,'اَلَمْ يَكُ نُطْفَةً مِّنْ مَّنِيٍّ يُّمْنٰى ','Bukankah dia dahulu setetes mani yang ditumpahkan (ke dalam rahim),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,38,'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ','kemudian mani itu menjadi segumpal darah, lalu Allah menciptakannya, dan menyempurnakannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,39,'فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۗ','lalu Allah menjadikan daripadanya sepasang: laki-laki dan perempuan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,75,40,'اَلَيْسَ ذٰلِكَ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى ࣖ','Bukankah (Allah yang berbuat) demikian berkuasa (pula) menghidupkan orang mati?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,1,'هَلْ اَتٰى عَلَى الْاِنْسَانِ حِيْنٌ مِّنَ الدَّهْرِ لَمْ يَكُنْ شَيْـًٔا مَّذْكُوْرًا ','Bukankah telah datang atas manusia satu waktu dari masa, sedang dia ketika itu belum merupakan sesuatu yang dapat disebut?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,2,'اِنَّا خَلَقْنَا الْاِنْسَانَ مِنْ نُّطْفَةٍ اَمْشَاجٍۖ نَّبْتَلِيْهِ فَجَعَلْنٰهُ سَمِيْعًاۢ بَصِيْرًا ','Sesungguhnya Kami telah menciptakan manusia dari setetes mani yang bercampur yang Kami hendak mengujinya (dengan perintah dan larangan), karena itu Kami jadikan dia mendengar dan melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,3,'اِنَّا هَدَيْنٰهُ السَّبِيْلَ اِمَّا شَاكِرًا وَّاِمَّا كَفُوْرًا ','Sesungguhnya Kami telah menunjukinya jalan yang lurus; ada yang bersyukur dan ada pula yang kafir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,4,'اِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَلٰسِلَا۟ وَاَغْلٰلًا وَّسَعِيْرًا ','Sesungguhnya Kami menyediakan bagi orang-orang kafir rantai, belenggu dan neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,5,'اِنَّ الْاَبْرَارَ يَشْرَبُوْنَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُوْرًاۚ','Sesungguhnya orang-orang yang berbuat kebajikan minum dari gelas (berisi minuman) yang campurannya adalah air kafur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,6,'عَيْنًا يَّشْرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُوْنَهَا تَفْجِيْرًا ','(yaitu) mata air (dalam surga) yang daripadanya hamba-hamba Allah minum, yang mereka dapat mengalirkannya dengan sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,7,'يُوْفُوْنَ بِالنَّذْرِ وَيَخَافُوْنَ يَوْمًا كَانَ شَرُّهٗ مُسْتَطِيْرًا ','Mereka menunaikan nazar dan takut akan suatu hari yang azabnya merata di mana-mana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,8,'وَيُطْعِمُوْنَ الطَّعَامَ عَلٰى حُبِّهٖ مِسْكِيْنًا وَّيَتِيْمًا وَّاَسِيْرًا ','Dan mereka memberikan makanan yang disukainya kepada orang miskin, anak yatim dan orang yang ditawan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,9,'اِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللّٰهِ لَا نُرِيْدُ مِنْكُمْ جَزَاۤءً وَّلَا شُكُوْرًا ','Sesungguhnya kami memberi makanan kepadamu hanyalah untuk mengharapkan keridhaan Allah, kami tidak menghendaki balasan dari kamu dan tidak pula (ucapan) terima kasih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,10,'اِنَّا نَخَافُ مِنْ رَّبِّنَا يَوْمًا عَبُوْسًا قَمْطَرِيْرًا ','Sesungguhnya kami takut akan (azab) Tuhan kami pada suatu hari yang (di hari itu) orang-orang bermuka masam penuh kesulitan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,11,'فَوَقٰىهُمُ اللّٰهُ شَرَّ ذٰلِكَ الْيَوْمِ وَلَقّٰىهُمْ نَضْرَةً وَّسُرُوْرًاۚ','Maka Tuhan memelihara mereka dari kesusahan hari itu, dan memberikan kepada mereka kejernihan (wajah) dan kegembiraan hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,12,'وَجَزٰىهُمْ بِمَا صَبَرُوْا جَنَّةً وَّحَرِيْرًاۙ','Dan Dia memberi balasan kepada mereka karena kesabaran mereka (dengan) surga dan (pakaian) sutera,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,13,'مُّتَّكِـِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۚ  لَا يَرَوْنَ فِيْهَا شَمْسًا وَّلَا زَمْهَرِيْرًاۚ','di dalamnya mereka duduk bertelakan di atas dipan, mereka tidak merasakan di dalamnya (teriknya) matahari dan tidak pula dingin yang bersangatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,14,'وَدَانِيَةً عَلَيْهِمْ ظِلٰلُهَا وَذُلِّلَتْ قُطُوْفُهَا تَذْلِيْلًا ','Dan naungan (pohon-pohon surga itu) dekat di atas mereka dan buahnya dimudahkan memetiknya semudah-mudahnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,15,'وَيُطَافُ عَلَيْهِمْ بِاٰنِيَةٍ مِّنْ فِضَّةٍ وَّاَكْوَابٍ كَانَتْ قَوَارِيْرَا۠ ','Dan diedarkan kepada mereka bejana-bejana dari perak dan piala-piala yang bening laksana kaca,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,16,'قَوَارِيْرَا۟ مِنْ فِضَّةٍ قَدَّرُوْهَا تَقْدِيْرًا ','(yaitu) kaca-kaca (yang terbuat) dari perak yang telah diukur mereka dengan sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,17,'وَيُسْقَوْنَ فِيْهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيْلًاۚ','Di dalam surga itu mereka diberi minum segelas (minuman) yang campurannya adalah jahe.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,18,'عَيْنًا فِيْهَا تُسَمّٰى سَلْسَبِيْلًا ','(Yang didatangkan dari) sebuah mata air surga yang dinamakan salsabil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,19,'۞ وَيَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۚ اِذَا رَاَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنْثُوْرًا','Dan mereka dikelilingi oleh pelayan-pelayan muda yang tetap muda. Apabila kamu melihat mereka, kamu akan mengira mereka, mutiara yang bertaburan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,20,'وَاِذَا رَاَيْتَ ثَمَّ رَاَيْتَ نَعِيْمًا وَّمُلْكًا كَبِيْرًا ','Dan apabila kamu melihat di sana (surga), niscaya kamu akan melihat berbagai macam kenikmatan dan kerajaan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,21,'عٰلِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَّاِسْتَبْرَقٌۖ وَّحُلُّوْٓا اَسَاوِرَ مِنْ فِضَّةٍۚ وَسَقٰىهُمْ رَبُّهُمْ شَرَابًا طَهُوْرًا ','Mereka memakai pakaian sutera halus yang hijau dan sutera tebal dan dipakaikan kepada mereka gelang terbuat dari perak, dan Tuhan memberikan kepada mereka minuman yang bersih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,22,'اِنَّ هٰذَا كَانَ لَكُمْ جَزَاۤءً وَّكَانَ سَعْيُكُمْ مَّشْكُوْرًا ࣖ ','Sesungguhnya ini adalah balasan untukmu, dan usahamu adalah disyukuri (diberi balasan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,23,'اِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْاٰنَ تَنْزِيْلًاۚ','Sesungguhnya Kami telah menurunkan Al Quran kepadamu (hai Muhammad) dengan berangsur-angsur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,24,'فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ اٰثِمًا اَوْ كَفُوْرًاۚ','Maka bersabarlah kamu untuk (melaksanakan) ketetapan Tuhanmu, dan janganlah kamu ikuti orang yang berdosa dan orang yang kafir di antar mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,25,'وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَّاَصِيْلًاۚ','Dan sebutlah nama Tuhanmu pada (waktu) pagi dan petang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,26,'وَمِنَ الَّيْلِ فَاسْجُدْ لَهٗ وَسَبِّحْهُ لَيْلًا طَوِيْلًا ','Dan pada sebagian dari malam, maka sujudlah kepada-Nya dan bertasbihlah kepada-Nya pada bagian yang panjang dimalam hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,27,'اِنَّ هٰٓؤُلَاۤءِ يُحِبُّوْنَ الْعَاجِلَةَ وَيَذَرُوْنَ وَرَاۤءَهُمْ يَوْمًا ثَقِيْلًا ','Sesungguhnya mereka (orang kafir) menyukai kehidupan dunia dan mereka tidak memperdulikan kesudahan mereka, pada hari yang berat (hari akhirat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,28,'نَحْنُ خَلَقْنٰهُمْ وَشَدَدْنَآ اَسْرَهُمْۚ وَاِذَا شِئْنَا بَدَّلْنَآ اَمْثَالَهُمْ تَبْدِيْلًا ','Kami telah menciptakan mereka dan menguatkan persendian tubuh mereka, apabila Kami menghendaki, Kami sungguh-sungguh mengganti (mereka) dengan orang-orang yang serupa dengan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,29,'اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا ','Sesungguhnya (ayat-ayat) ini adalah suatu peringatan, maka barangsiapa menghendaki (kebaikan bagi dirinya) niscaya dia mengambil jalan kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,30,'وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۖ','Dan kamu tidak mampu (menempuh jalan itu), kecuali bila dikehendaki Allah. Sesungguhnya Allah adalah Maha Mengetahui lagi Maha Bijaksana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,76,31,'يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمِيْنَ اَعَدَّ لَهُمْ عَذَابًا اَلِيْمًا ࣖ','Dan memasukkan siapa yang dikehendaki-Nya ke dalam rahmat-Nya (surga). Dan bagi orang-orang zalim disediakan-Nya azab yang pedih.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,1,'وَالْمُرْسَلٰتِ عُرْفًاۙ','Demi malaikat-malaikat yang diutus untuk membawa kebaikan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,2,'فَالْعٰصِفٰتِ عَصْفًاۙ','dan (malaikat-malaikat) yang terbang dengan kencangnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,3,'وَّالنّٰشِرٰتِ نَشْرًاۙ','dan (malaikat-malaikat) yang menyebarkan (rahmat Tuhannya) dengan seluas-luasnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,4,'فَالْفٰرِقٰتِ فَرْقًاۙ','dan (malaikat-malaikat) yang membedakan (antara yang hak dan yang bathil) dengan sejelas-jelasnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,5,'فَالْمُلْقِيٰتِ ذِكْرًاۙ','dan (malaikat-malaikat) yang menyampaikan wahyu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,6,'عُذْرًا اَوْ نُذْرًاۙ','untuk menolak alasan-alasan atau memberi peringatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,7,'اِنَّمَا تُوْعَدُوْنَ لَوَاقِعٌۗ','sesungguhnya apa yang dijanjikan kepadamu itu pasti terjadi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,8,'فَاِذَا النُّجُوْمُ طُمِسَتْۙ','Maka apabila bintang-bintang telah dihapuskan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,9,'وَاِذَا السَّمَاۤءُ فُرِجَتْۙ','dan apabila langit telah dibelah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,10,'وَاِذَا الْجِبَالُ نُسِفَتْۙ','dan apabila gunung-gunung telah dihancurkan menjadi debu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,11,'وَاِذَا الرُّسُلُ اُقِّتَتْۗ','dan apabila rasul-rasul telah ditetapkan waktu (mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,12,'لِاَيِّ يَوْمٍ اُجِّلَتْۗ','(Niscaya dikatakan kepada mereka:) "Sampai hari apakah ditangguhkan (mengazab orang-orang kafir itu)?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,13,'لِيَوْمِ الْفَصْلِۚ','Sampai hari keputusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,14,'وَمَآ اَدْرٰىكَ مَا يَوْمُ الْفَصْلِۗ','Dan tahukah kamu apakah hari keputusan itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,15,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,16,'اَلَمْ نُهْلِكِ الْاَوَّلِيْنَۗ','Bukankah Kami telah membinasakan orang-orang yang dahulu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,17,'ثُمَّ نُتْبِعُهُمُ الْاٰخِرِيْنَ ','Lalu Kami iringkan (azab Kami terhadap) mereka dengan (mengazab) orang-orang yang datang kemudian.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,18,'كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ ','Demikianlah Kami berbuat terhadap orang-orang yang berdosa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,19,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,20,'اَلَمْ نَخْلُقْكُّمْ مِّنْ مَّاۤءٍ مَّهِيْنٍۙ','Bukankah Kami menciptakan kamu dari air yang hina?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,21,'فَجَعَلْنٰهُ فِيْ قَرَارٍ مَّكِيْنٍ','kemudian Kami letakkan dia dalam tempat yang kokoh (rahim),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,22,'اِلٰى قَدَرٍ مَّعْلُوْمٍۙ','sampai waktu yang ditentukan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,23,'فَقَدَرْنَاۖ فَنِعْمَ الْقٰدِرُوْنَ ','lalu Kami tentukan (bentuknya), maka Kami-lah sebaik-baik yang menentukan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,24,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,25,'اَلَمْ نَجْعَلِ الْاَرْضَ كِفَاتًاۙ','Bukankah Kami menjadikan bumi (tempat) berkumpul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,26,'اَحْيَاۤءً وَّاَمْوَاتًاۙ','orang-orang hidup dan orang-orang mati?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,27,'وَّجَعَلْنَا فِيْهَا رَوَاسِيَ شٰمِخٰتٍ وَّاَسْقَيْنٰكُمْ مَّاۤءً فُرَاتًاۗ','dan Kami jadikan padanya gunung-gunung yang tinggi, dan Kami beri minum kamu dengan air tawar?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,28,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,29,'اِنْطَلِقُوْٓا اِلٰى مَا كُنْتُمْ بِهٖ تُكَذِّبُوْنَۚ','(Dikatakan kepada mereka pada hari kiamat): "Pergilah kamu mendapatkan azab yang dahulunya kamu mendustakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,30,'اِنْطَلِقُوْٓا اِلٰى ظِلٍّ ذِيْ ثَلٰثِ شُعَبٍ ','Pergilah kamu mendapatkan naungan yang mempunyai tiga cabang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,31,'لَا ظَلِيْلٍ وَّلَا يُغْنِيْ مِنَ اللَّهَبِۗ','yang tidak melindungi dan tidak pula menolak nyala api neraka".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,32,'اِنَّهَا تَرْمِيْ بِشَرَرٍ كَالْقَصْرِۚ','Sesungguhnya neraka itu melontarkan bunga api sebesar dan setinggi istana.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,33,'كَاَنَّهٗ جِمٰلَتٌ صُفْرٌۗ','Seolah-olah ia iringan unta yang kuning.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,34,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,35,'هٰذَا يَوْمُ لَا يَنْطِقُوْنَۙ','Ini adalah hari, yang mereka tidak dapat berbicara (pada hari itu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,36,'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُوْنَ ','dan tidak diizinkan kepada mereka minta uzur sehingga mereka (dapat) minta uzur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,37,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,38,'هٰذَا يَوْمُ الْفَصْلِ جَمَعْنٰكُمْ وَالْاَوَّلِيْنَ ','Ini adalah hari keputusan; (pada hari ini) Kami mengumpulkan kamu dan orang-orang terdahulu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,39,'فَاِنْ كَانَ لَكُمْ كَيْدٌ فَكِيْدُوْنِ ','Jika kamu mempunyai tipu daya, maka lakukanlah tipu dayamu itu terhadap-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,40,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ࣖ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,41,'اِنَّ الْمُتَّقِيْنَ فِيْ ظِلٰلٍ وَّعُيُوْنٍۙ','Sesungguhnya orang-orang yang bertakwa berada dalam naungan (yang teduh) dan (di sekitar) mata-mata air.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,42,'وَّفَوَاكِهَ مِمَّا يَشْتَهُوْنَۗ','Dan (mendapat) buah-buahan dari (macam-macam) yang mereka ingini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,43,'كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَ ','(Dikatakan kepada mereka): "Makan dan minumlah kamu dengan enak karena apa yang telah kamu kerjakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,44,'اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ ','Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,45,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,46,'كُلُوْا وَتَمَتَّعُوْا قَلِيْلًا اِنَّكُمْ مُّجْرِمُوْنَ ','(Dikatakan kepada orang-orang kafir): "Makanlah dan bersenang-senanglah kamu (di dunia dalam waktu) yang pendek; sesungguhnya kamu adalah orang-orang yang berdosa".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,47,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,48,'وَاِذَا قِيْلَ لَهُمُ ارْكَعُوْا لَا يَرْكَعُوْنَ ','Dan apabila dikatakan kepada mereka: "Rukuklah, niscaya mereka tidak mau ruku´.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,49,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,29,77,50,'فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ ࣖ   ۔','Maka kepada perkataan apakah selain Al Quran ini mereka akan beriman?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,1,'عَمَّ يَتَسَاۤءَلُوْنَۚ ','Tentang apakah mereka saling bertanya-tanya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,2,'عَنِ النَّبَاِ الْعَظِيْمِۙ ','Tentang berita yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,3,'الَّذِيْ هُمْ فِيْهِ مُخْتَلِفُوْنَۗ ','yang mereka perselisihkan tentang ini.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,4,'كَلَّا سَيَعْلَمُوْنَۙ ','Sekali-kali tidak; kelak mereka akan mengetahui,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,5,'ثُمَّ كَلَّا سَيَعْلَمُوْنَ','kemudian sekali-kali tidak; kelak mereka mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,6,'اَلَمْ نَجْعَلِ الْاَرْضَ مِهٰدًاۙ','Bukankah Kami telah menjadikan bumi itu sebagai hamparan?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,7,'وَّالْجِبَالَ اَوْتَادًاۖ','dan gunung-gunung sebagai pasak?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,8,'وَّخَلَقْنٰكُمْ اَزْوَاجًاۙ ','dan Kami jadikan kamu berpasang-pasangan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,9,'وَّجَعَلْنَا نَوْمَكُمْ سُبَاتًاۙ ','dan Kami jadikan tidurmu untuk istirahat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,10,'وَّجَعَلْنَا الَّيْلَ لِبَاسًاۙ ','dan Kami jadikan malam sebagai pakaian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,11,'وَّجَعَلْنَا النَّهَارَ مَعَاشًاۚ','dan Kami jadikan siang untuk mencari penghidupan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,12,'وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًاۙ ','dan Kami bina di atas kamu tujuh buah (langit) yang kokoh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,13,'وَّجَعَلْنَا سِرَاجًا وَّهَّاجًاۖ','dan Kami jadikan pelita yang amat terang (matahari),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,14,'وَّاَنْزَلْنَا مِنَ الْمُعْصِرٰتِ مَاۤءً ثَجَّاجًاۙ','dan Kami turunkan dari awan air yang banyak tercurah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,15,'لِّنُخْرِجَ بِهٖ حَبًّا وَّنَبَاتًاۙ','supaya Kami tumbuhkan dengan air itu biji-bijian dan tumbuh-tumbuhan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,16,'وَّجَنّٰتٍ اَلْفَافًاۗ','dan kebun-kebun yang lebat?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,17,'اِنَّ يَوْمَ الْفَصْلِ كَانَ مِيْقَاتًاۙ','Sesungguhnya Hari Keputusan adalah suatu waktu yang ditetapkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,18,'يَّوْمَ يُنْفَخُ فِى الصُّوْرِ فَتَأْتُوْنَ اَفْوَاجًاۙ','yaitu hari (yang pada waktu itu) ditiup sangkakala lalu kamu datang berkelompok-kelompok,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,19,'وَّفُتِحَتِ السَّمَاۤءُ فَكَانَتْ اَبْوَابًاۙ','dan dibukalah langit, maka terdapatlah beberapa pintu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,20,'وَّسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًاۗ','dan dijalankanlah gunung-gunung maka menjadi fatamorganalah ia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,21,'اِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًاۙ','Sesungguhnya neraka Jahannam itu (padanya) ada tempat pengintai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,22,'لِّلطّٰغِيْنَ مَاٰبًاۙ','lagi menjadi tempat kembali bagi orang-orang yang melampaui batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,23,'لّٰبِثِيْنَ فِيْهَآ اَحْقَابًاۚ','mereka tinggal di dalamnya berabad-abad lamanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,24,'لَا يَذُوْقُوْنَ فِيْهَا بَرْدًا وَّلَا شَرَابًاۙ','mereka tidak merasakan kesejukan di dalamnya dan tidak (pula mendapat) minuman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,25,'اِلَّا حَمِيْمًا وَّغَسَّاقًاۙ','selain air yang mendidih dan nanah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,26,'جَزَاۤءً وِّفَاقًاۗ','sebagai pambalasan yang setimpal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,27,'اِنَّهُمْ كَانُوْا لَا يَرْجُوْنَ حِسَابًاۙ','Sesungguhnya mereka tidak berharap (takut) kepada hisab,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,28,'وَّكَذَّبُوْا بِاٰيٰتِنَا كِذَّابًاۗ','dan mereka mendustakan ayat-ayat Kami dengan sesungguh-sungguhnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,29,'وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ كِتٰبًاۙ','Dan segala sesuatu telah Kami catat dalam suatu kitab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,30,'فَذُوْقُوْا فَلَنْ نَّزِيْدَكُمْ اِلَّا عَذَابًا ࣖ','Karena itu rasakanlah. Dan Kami sekali-kali tidak akan menambah kepada kamu selain daripada azab.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,31,'اِنَّ لِلْمُتَّقِيْنَ مَفَازًاۙ','Sesungguhnya orang-orang yang bertakwa mendapat kemenangan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,32,'حَدَاۤىِٕقَ وَاَعْنَابًاۙ','(yaitu) kebun-kebun dan buah anggur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,33,'وَّكَوَاعِبَ اَتْرَابًاۙ','dan gadis-gadis remaja yang sebaya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,34,'وَّكَأْسًا دِهَاقًاۗ','dan gelas-gelas yang penuh (berisi minuman).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,35,'لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا كِذَّابًا','Di dalamnya mereka tidak mendengar perkataan yang sia-sia dan tidak (pula) perkataan dusta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,36,'جَزَاۤءً مِّنْ رَّبِّكَ عَطَاۤءً حِسَابًاۙ','Sebagai pembalasan dari Tuhanmu dan pemberian yang cukup banyak,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,37,'رَّبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الرَّحْمٰنِ لَا يَمْلِكُوْنَ مِنْهُ خِطَابًاۚ ','Tuhan Yang memelihara langit dan bumi dan apa yang ada di antara keduanya; Yang Maha Pemurah. Mereka tidak dapat berbicara dengan Dia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,38,'يَوْمَ يَقُوْمُ الرُّوْحُ وَالْمَلٰۤىِٕكَةُ صَفًّاۙ  لَّا يَتَكَلَّمُوْنَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَابًا','Pada hari, ketika ruh dan para malaikat berdiri bershaf-shaf, mereka tidak berkata-kata, kecuali siapa yang telah diberi izin kepadanya oleh Tuhan Yang Maha Pemurah; dan ia mengucapkan kata yang benar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,39,'ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا','Itulah hari yang pasti terjadi. Maka barangsiapa yang menghendaki, niscaya ia menempuh jalan kembali kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,78,40,'اِنَّآ اَنْذَرْنٰكُمْ عَذَابًا قَرِيْبًا ەۙ يَّوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُوْلُ الْكٰفِرُ يٰلَيْتَنِيْ كُنْتُ تُرَابًا ࣖ','Sesungguhnya Kami telah memperingatkan kepadamu (hai orang kafir) siksa yang dekat, pada hari manusia melihat apa yang telah diperbuat oleh kedua tangannya; dan orang kafir berkata: "Alangkah baiknya sekiranya aku dahulu adalah tanah".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,1,'وَالنّٰزِعٰتِ غَرْقًاۙ','Demi (malaikat-malaikat) yang mencabut (nyawa) dengan keras,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,2,'وَّالنّٰشِطٰتِ نَشْطًاۙ','dan (malaikat-malaikat) yang mencabut (nyawa) dengan lemah-lembut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,3,'وَّالسّٰبِحٰتِ سَبْحًاۙ','dan (malaikat-malaikat) yang turun dari langit dengan cepat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,4,'فَالسّٰبِقٰتِ سَبْقًاۙ','dan (malaikat-malaikat) yang mendahului dengan kencang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,5,'فَالْمُدَبِّرٰتِ اَمْرًاۘ','dan (malaikat-malaikat) yang mengatur urusan (dunia).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,6,'يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ','(Sesungguhnya kamu akan dibangkitkan) pada hari ketika tiupan pertama menggoncang alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,7,'تَتْبَعُهَا الرَّادِفَةُ ۗ','tiupan pertama itu diiringi oleh tiupan kedua.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,8,'قُلُوْبٌ يَّوْمَىِٕذٍ وَّاجِفَةٌۙ','Hati manusia pada waktu itu sangat takut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,9,'اَبْصَارُهَا خَاشِعَةٌ  ۘ','Pandangannya tunduk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,10,'يَقُوْلُوْنَ ءَاِنَّا لَمَرْدُوْدُوْنَ فِى الْحَافِرَةِۗ','(Orang-orang kafir) berkata: "Apakah sesungguhnya kami benar-benar dikembalikan kepada kehidupan semula?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,11,'ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ۗ','Apakah (akan dibangkitkan juga) apabila kami telah menjadi tulang belulang yang hancur lumat?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,12,'قَالُوْا تِلْكَ اِذًا كَرَّةٌ خَاسِرَةٌ  ۘ','Mereka berkata: "Kalau demikian, itu adalah suatu pengembalian yang merugikan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,13,'فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌۙ','Sesungguhnya pengembalian itu hanyalah satu kali tiupan saja,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,14,'فَاِذَا هُمْ بِالسَّاهِرَةِۗ','maka dengan serta merta mereka hidup kembali di permukaan bumi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,15,'هَلْ اَتٰىكَ حَدِيْثُ مُوْسٰىۘ','Sudah sampaikah kepadamu (ya Muhammad) kisah Musa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,16,'اِذْ نَادٰىهُ رَبُّهٗ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ','Tatkala Tuhannya memanggilnya di lembah suci ialah Lembah Thuwa;')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,17,'اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۖ','"Pergilah kamu kepada Fir´aun, sesungguhnya dia telah melampaui batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,18,'فَقُلْ هَلْ لَّكَ اِلٰٓى اَنْ تَزَكّٰىۙ','dan katakanlah (kepada Fir´aun): "Adakah keinginan bagimu untuk membersihkan diri (dari kesesatan)".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,19,'وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ','Dan kamu akan kupimpin ke jalan Tuhanmu agar supaya kamu takut kepada-Nya?"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,20,'فَاَرٰىهُ الْاٰيَةَ الْكُبْرٰىۖ','Lalu Musa memperlihatkan kepadanya mukjizat yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,21,'فَكَذَّبَ وَعَصٰىۖ','Tetapi Fir´aun mendustakan dan mendurhakai.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,22,'ثُمَّ اَدْبَرَ يَسْعٰىۖ','Kemudian dia berpaling seraya berusaha menantang (Musa).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,23,'فَحَشَرَ فَنَادٰىۖ','Maka dia mengumpulkan (pembesar-pembesarnya) lalu berseru memanggil kaumnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,24,'فَقَالَ اَنَا۠ رَبُّكُمُ الْاَعْلٰىۖ','(Seraya) berkata: "Akulah tuhanmu yang paling tinggi".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,25,'فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُوْلٰىۗ','Maka Allah mengazabnya dengan azab di akhirat dan azab di dunia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,26,'اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّمَنْ يَّخْشٰى ۗ ࣖ','Sesungguhnya pada yang demikian itu terdapat pelajaran bagi orang yang takut (kepada Tuhannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,27,'ءَاَنْتُمْ اَشَدُّ خَلْقًا اَمِ السَّمَاۤءُ ۚ بَنٰىهَاۗ','Apakah kamu lebih sulit penciptaanya ataukah langit? Allah telah membinanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,28,'رَفَعَ سَمْكَهَا فَسَوّٰىهَاۙ','Dia meninggikan bangunannya lalu menyempurnakannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,29,'وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰىهَاۖ','dan Dia menjadikan malamnya gelap gulita, dan menjadikan siangnya terang benderang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,30,'وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰىهَاۗ','Dan bumi sesudah itu dihamparkan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,31,'اَخْرَجَ مِنْهَا مَاۤءَهَا وَمَرْعٰىهَاۖ','Ia memancarkan daripadanya mata airnya, dan (menumbuhkan) tumbuh-tumbuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,32,'وَالْجِبَالَ اَرْسٰىهَاۙ','Dan gunung-gunung dipancangkan-Nya dengan teguh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,33,'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ','(semua itu) untuk kesenanganmu dan untuk binatang-binatang ternakmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,34,'فَاِذَا جَاۤءَتِ الطَّاۤمَّةُ الْكُبْرٰىۖ','Maka apabila malapetaka yang sangat besar (hari kiamat) telah datang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,35,'يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ','Pada hari (ketika) manusia teringat akan apa yang telah dikerjakannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,36,'وَبُرِّزَتِ الْجَحِيْمُ لِمَنْ يَّرٰى','dan diperlihatkan neraka dengan jelas kepada setiap orang yang melihat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,37,'فَاَمَّا مَنْ طَغٰىۖ','Adapun orang yang melampaui batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,38,'وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ','dan lebih mengutamakan kehidupan dunia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,39,'فَاِنَّ الْجَحِيْمَ هِيَ الْمَأْوٰىۗ','maka sesungguhnya nerakalah tempat tinggal(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,40,'وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّهٖ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ','Dan adapun orang-orang yang takut kepada kebesaran Tuhannya dan menahan diri dari keinginan hawa nafsunya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,41,'فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۗ','maka sesungguhnya surgalah tempat tinggal(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,42,'يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ','(Orang-orang kafir) bertanya kepadamu (Muhammad) tentang hari kebangkitan, kapankah terjadinya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,43,'فِيْمَ اَنْتَ مِنْ ذِكْرٰىهَاۗ','Siapakah kamu (maka) dapat menyebutkan (waktunya)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,44,'اِلٰى رَبِّكَ مُنْتَهٰىهَاۗ','Kepada Tuhanmulah dikembalikan kesudahannya (ketentuan waktunya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,45,'اِنَّمَآ اَنْتَ مُنْذِرُ مَنْ يَّخْشٰىهَاۗ','Kamu hanyalah pemberi peringatan bagi siapa yang takut kepadanya (hari berbangkit)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,79,46,'كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوْٓا اِلَّا عَشِيَّةً اَوْ ضُحٰىهَا ࣖ','Pada hari mereka melihat hari berbangkit itu, mereka merasa seakan-akan tidak tinggal (di dunia) melainkan (sebentar saja) di waktu sore atau pagi hari.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,1,'عَبَسَ وَتَوَلّٰىٓۙ','Dia (Muhammad) bermuka masam dan berpaling,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,2,'اَنْ جَاۤءَهُ الْاَعْمٰىۗ','karena telah datang seorang buta kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,3,'وَمَا يُدْرِيْكَ لَعَلَّهٗ يَزَّكّٰىٓۙ','Tahukah kamu barangkali ia ingin membersihkan dirinya (dari dosa),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,4,'اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۗ','atau dia (ingin) mendapatkan pengajaran, lalu pengajaran itu memberi manfaat kepadanya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,5,'اَمَّا مَنِ اسْتَغْنٰىۙ','Adapun orang yang merasa dirinya serba cukup,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,6,'فَاَنْتَ لَهٗ تَصَدّٰىۗ','maka kamu melayaninya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,7,'وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۗ','Padahal tidak ada (celaan) atasmu kalau dia tidak membersihkan diri (beriman).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,8,'وَاَمَّا مَنْ جَاۤءَكَ يَسْعٰىۙ','Dan adapun orang yang datang kepadamu dengan bersegera (untuk mendapatkan pengajaran),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,9,'وَهُوَ يَخْشٰىۙ','sedang ia takut kepada (Allah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,10,'فَاَنْتَ عَنْهُ تَلَهّٰىۚ','maka kamu mengabaikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,11,'كَلَّآ اِنَّهَا تَذْكِرَةٌ ۚ','Sekali-kali jangan (demikian)! Sesungguhnya ajaran-ajaran Tuhan itu adalah suatu peringatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,12,'فَمَنْ شَاۤءَ ذَكَرَهٗ ۘ','maka barangsiapa yang menghendaki, tentulah ia memperhatikannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,13,'فِيْ صُحُفٍ مُّكَرَّمَةٍۙ','di dalam kitab-kitab yang dimuliakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,14,'مَّرْفُوْعَةٍ مُّطَهَّرَةٍ ۢ ۙ','yang ditinggikan lagi disucikan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,15,'بِاَيْدِيْ سَفَرَةٍۙ','di tangan para penulis (malaikat),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,16,'كِرَامٍۢ بَرَرَةٍۗ','yang mulia lagi berbakti.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,17,'قُتِلَ الْاِنْسَانُ مَآ اَكْفَرَهٗۗ','Binasalah manusia; alangkah amat sangat kekafirannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,18,'مِنْ اَيِّ شَيْءٍ خَلَقَهٗۗ','Dari apakah Allah menciptakannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,19,'مِنْ نُّطْفَةٍۗ خَلَقَهٗ فَقَدَّرَهٗۗ','Dari setetes mani, Allah menciptakannya lalu menentukannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,20,'ثُمَّ السَّبِيْلَ يَسَّرَهٗۙ','Kemudian Dia memudahkan jalannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,21,'ثُمَّ اَمَاتَهٗ فَاَقْبَرَهٗۙ','kemudian Dia mematikannya dan memasukkannya ke dalam kubur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,22,'ثُمَّ اِذَا شَاۤءَ اَنْشَرَهٗۗ','kemudian bila Dia menghendaki, Dia membangkitkannya kembali.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,23,'كَلَّا لَمَّا يَقْضِ مَآ اَمَرَهٗۗ','Sekali-kali jangan; manusia itu belum melaksanakan apa yang diperintahkan Allah kepadanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,24,'فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِهٖٓ ۙ ','maka hendaklah manusia itu memperhatikan makanannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,25,'اَنَّا صَبَبْنَا الْمَاۤءَ صَبًّاۙ','Sesungguhnya Kami benar-benar telah mencurahkan air (dari langit),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,26,'ثُمَّ شَقَقْنَا الْاَرْضَ شَقًّاۙ','kemudian Kami belah bumi dengan sebaik-baiknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,27,'فَاَنْۢبَتْنَا فِيْهَا حَبًّاۙ','lalu Kami tumbuhkan biji-bijian di bumi itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,28,'وَّعِنَبًا وَّقَضْبًاۙ','anggur dan sayur-sayuran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,29,'وَّزَيْتُوْنًا وَّنَخْلًاۙ','zaitun dan kurma,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,30,'وَّحَدَاۤئِقَ غُلْبًا','kebun-kebun (yang) lebat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,31,'وَفَاكِهَةً وَّاَبًّا','dan buah-buahan serta rumput-rumputan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,32,'مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ','untuk kesenanganmu dan untuk binatang-binatang ternakmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,33,'فَاِذَا جَاۤءَتِ الصَّاۤخَّةُ ۖ','Dan apabila datang suara yang memekakkan (tiupan sangkakala yang kedua),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,34,'يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخِيْهِۙ','pada hari ketika manusia lari dari saudaranya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,35,'وَاُمِّهٖ وَاَبِيْهِۙ','dari ibu dan bapaknya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,36,'وَصَاحِبَتِهٖ وَبَنِيْهِۗ','dari istri dan anak-anaknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,37,'لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَىِٕذٍ شَأْنٌ يُّغْنِيْهِۗ','Setiap orang dari mereka pada hari itu mempunyai urusan yang cukup menyibukkannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,38,'وُجُوْهٌ يَّوْمَىِٕذٍ مُّسْفِرَةٌۙ','Banyak muka pada hari itu berseri-seri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,39,'ضَاحِكَةٌ مُّسْتَبْشِرَةٌ ۚ','tertawa dan bergembira ria,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,40,'وَوُجُوْهٌ يَّوْمَىِٕذٍ عَلَيْهَا غَبَرَةٌۙ','dan banyak (pula) muka pada hari itu tertutup debu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,41,'تَرْهَقُهَا قَتَرَةٌ ۗ','dan ditutup lagi oleh kegelapan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,80,42,'اُولٰۤىِٕكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ ࣖ','Mereka itulah orang-orang kafir lagi durhaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,1,'اِذَا الشَّمْسُ كُوِّرَتْۖ','Apabila matahari digulung,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,2,'وَاِذَا النُّجُوْمُ انْكَدَرَتْۖ','dan apabila bintang-bintang berjatuhan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,3,'وَاِذَا الْجِبَالُ سُيِّرَتْۖ','dan apabila gunung-gunung dihancurkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,4,'وَاِذَا الْعِشَارُ عُطِّلَتْۖ','dan apabila unta-unta yang bunting ditinggalkan (tidak diperdulikan)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,5,'وَاِذَا الْوُحُوْشُ حُشِرَتْۖ','dan apabila binatang-binatang liar dikumpulkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,6,'وَاِذَا الْبِحَارُ سُجِّرَتْۖ','dan apabila lautan dijadikan meluap')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,7,'وَاِذَا النُّفُوْسُ زُوِّجَتْۖ','dan apabila ruh-ruh dipertemukan (dengan tubuh)')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,8,'وَاِذَا الْمَوْءٗدَةُ سُىِٕلَتْۖ ','dan apabila bayi-bayi perempuan yang dikubur hidup-hidup ditanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,9,'بِاَيِّ ذَنْۢبٍ قُتِلَتْۚ','karena dosa apakah dia dibunuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,10,'وَاِذَا الصُّحُفُ نُشِرَتْۖ','dan apabila catatan-catatan (amal perbuatan manusia) dibuka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,11,'وَاِذَا السَّمَاۤءُ كُشِطَتْۖ','dan apabila langit dilenyapkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,12,'وَاِذَا الْجَحِيْمُ سُعِّرَتْۖ','dan apabila neraka Jahim dinyalakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,13,'وَاِذَا الْجَنَّةُ اُزْلِفَتْۖ','dan apabila surga didekatkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,14,'عَلِمَتْ نَفْسٌ مَّآ اَحْضَرَتْۗ','maka tiap-tiap jiwa akan mengetahui apa yang telah dikerjakannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,15,'فَلَآ اُقْسِمُ بِالْخُنَّسِۙ','Sungguh, Aku bersumpah dengan bintang-bintang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,16,'الْجَوَارِ الْكُنَّسِۙ','yang beredar dan terbenam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,17,'وَالَّيْلِ اِذَا عَسْعَسَۙ','demi malam apabila telah hampir meninggalkan gelapnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,18,'وَالصُّبْحِ اِذَا تَنَفَّسَۙ','dan demi subuh apabila fajarnya mulai menyingsing,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,19,'اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ','sesungguhnya Al Quran itu benar-benar firman (Allah yang dibawa oleh) utusan yang mulia (Jibril),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,20,'ذِيْ قُوَّةٍ عِنْدَ ذِى الْعَرْشِ مَكِيْنٍۙ','yang mempunyai kekuatan, yang mempunyai kedudukan tinggi di sisi Allah yang mempunyai ´Arsy,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,21,'مُّطَاعٍ ثَمَّ اَمِيْنٍۗ','yang ditaati di sana (di alam malaikat) lagi dipercaya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,22,'وَمَا صَاحِبُكُمْ بِمَجْنُوْنٍۚ','Dan temanmu (Muhammad) itu bukanlah sekali-kali orang yang gila.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,23,'وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُبِيْنِۚ','Dan sesungguhnya Muhammad itu melihat Jibril di ufuk yang terang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,24,'وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِيْنٍۚ','Dan dia (Muhammad) bukanlah orang yang bakhil untuk menerangkan yang ghaib.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,25,'وَمَا هُوَ بِقَوْلِ شَيْطٰنٍ رَّجِيْمٍۚ','Dan Al Quran itu bukanlah perkataan syaitan yang terkutuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,26,'فَاَيْنَ تَذْهَبُوْنَۗ','maka ke manakah kamu akan pergi?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,27,'اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَۙ','Al Quran itu tiada lain hanyalah peringatan bagi semesta alam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,28,'لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّسْتَقِيْمَۗ','(yaitu) bagi siapa di antara kamu yang mau menempuh jalan yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,81,29,'وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ ࣖ','Dan kamu tidak dapat menghendaki (menempuh jalan itu) kecuali apabila dikehendaki Allah, Tuhan semesta alam.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,1,'اِذَا السَّمَاۤءُ انْفَطَرَتْۙ','Apabila langit terbelah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,2,'وَاِذَا الْكَوَاكِبُ انْتَثَرَتْۙ','dan apabila bintang-bintang jatuh berserakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,3,'وَاِذَا الْبِحَارُ فُجِّرَتْۙ','dan apabila lautan menjadikan meluap,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,4,'وَاِذَا الْقُبُوْرُ بُعْثِرَتْۙ','dan apabila kuburan-kuburan dibongkar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,5,'عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَاَخَّرَتْۗ','maka tiap-tiap jiwa akan mengetahui apa yang telah dikerjakan dan yang dilalaikannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,6,'يٰٓاَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيْمِۙ','Hai manusia, apakah yang telah memperdayakan kamu (berbuat durhaka) terhadap Tuhanmu Yang Maha Pemurah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,7,'الَّذِيْ خَلَقَكَ فَسَوّٰىكَ فَعَدَلَكَۙ','Yang telah menciptakan kamu lalu menyempurnakan kejadianmu dan menjadikan (susunan tubuh)mu seimbang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,8,'فِيْٓ اَيِّ صُوْرَةٍ مَّا شَاۤءَ رَكَّبَكَۗ','dalam bentuk apa saja yang Dia kehendaki, Dia menyusun tubuhmu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,9,'كَلَّا بَلْ تُكَذِّبُوْنَ بِالدِّيْنِۙ','Bukan hanya durhaka saja, bahkan kamu mendustakan hari pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,10,'وَاِنَّ عَلَيْكُمْ لَحٰفِظِيْنَۙ','Padahal sesungguhnya bagi kamu ada (malaikat-malaikat) yang mengawasi (pekerjaanmu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,11,'كِرَامًا كَاتِبِيْنَۙ','yang mulia (di sisi Allah) dan mencatat (pekerjaan-pekerjaanmu itu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,12,'يَعْلَمُوْنَ مَا تَفْعَلُوْنَ','mereka mengetahui apa yang kamu kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,13,'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ','Sesungguhnya orang-orang yang banyak berbakti benar-benar berada dalam surga yang penuh kenikmatan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,14,'وَّاِنَّ الْفُجَّارَ لَفِيْ جَحِيْمٍ','dan sesungguhnya orang-orang yang durhaka benar-benar berada dalam neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,15,'يَصْلَوْنَهَا يَوْمَ الدِّيْنِ','Mereka masuk ke dalamnya pada hari pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,16,'وَمَا هُمْ عَنْهَا بِغَاۤىِٕبِيْنَۗ','Dan mereka sekali-kali tidak dapat keluar dari neraka itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,17,'وَمَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۙ','Tahukah kamu apakah hari pembalasan itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,18,'ثُمَّ مَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۗ','Sekali lagi, tahukah kamu apakah hari pembalasan itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,82,19,'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۗوَالْاَمْرُ يَوْمَىِٕذٍ لِّلّٰهِ ࣖ','(Yaitu) hari (ketika) seseorang tidak berdaya sedikitpun untuk menolong orang lain. Dan segala urusan pada hari itu dalam kekuasaan Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,1,'وَيْلٌ لِّلْمُطَفِّفِيْنَۙ','Kecelakaan besarlah bagi orang-orang yang curang')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,2,'الَّذِيْنَ اِذَا اكْتَالُوْا عَلَى النَّاسِ يَسْتَوْفُوْنَۖ','(yaitu) orang-orang yang apabila menerima takaran dari orang lain mereka minta dipenuhi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,3,'وَاِذَا كَالُوْهُمْ اَوْ وَّزَنُوْهُمْ يُخْسِرُوْنَۗ','dan apabila mereka menakar atau menimbang untuk orang lain, mereka mengurangi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,4,'اَلَا يَظُنُّ اُولٰۤىِٕكَ اَنَّهُمْ مَّبْعُوْثُوْنَۙ','Tidaklah orang-orang itu menyangka, bahwa sesungguhnya mereka akan dibangkitkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,5,'لِيَوْمٍ عَظِيْمٍۙ','pada suatu hari yang besar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,6,'يَّوْمَ يَقُوْمُ النَّاسُ لِرَبِّ الْعٰلَمِيْنَۗ','(yaitu) hari (ketika) manusia berdiri menghadap Tuhan semesta alam?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,7,'كَلَّآ اِنَّ كِتٰبَ الْفُجَّارِ لَفِيْ سِجِّيْنٍۗ','Sekali-kali jangan curang, karena sesungguhnya kitab orang yang durhaka tersimpan dalam sijjin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,8,'وَمَآ اَدْرٰىكَ مَا سِجِّيْنٌۗ','Tahukah kamu apakah sijjin itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,9,'كِتٰبٌ مَّرْقُوْمٌۗ','(Ialah) kitab yang bertulis.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,10,'وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ','Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,11,'الَّذِيْنَ يُكَذِّبُوْنَ بِيَوْمِ الدِّيْنِۗ','(yaitu) orang-orang yang mendustakan hari pembalasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,12,'وَمَا يُكَذِّبُ بِهٖٓ اِلَّا كُلُّ مُعْتَدٍ اَثِيْمٍۙ','Dan tidak ada yang mendustakan hari pembalasan itu melainkan setiap orang yang melampaui batas lagi berdosa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,13,'اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ','yang apabila dibacakan kepadanya ayat-ayat Kami, ia berkata: "Itu adalah dongengan orang-orang yang dahulu"')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,14,'كَلَّا بَلْ ۜرَانَ عَلٰى قُلُوْبِهِمْ مَّا كَانُوْا يَكْسِبُوْنَ ','Sekali-kali tidak (demikian), sebenarnya apa yang selalu mereka usahakan itu menutupi hati mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,15,'كَلَّآ اِنَّهُمْ عَنْ رَّبِّهِمْ يَوْمَىِٕذٍ لَّمَحْجُوْبُوْنَۗ','Sekali-kali tidak, sesungguhnya mereka pada hari itu benar-benar tertutup dari (rahmat) Tuhan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,16,'ثُمَّ اِنَّهُمْ لَصَالُوا الْجَحِيْمِۗ','Kemudian, sesungguhnya mereka benar-benar masuk neraka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,17,'ثُمَّ يُقَالُ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَۗ','Kemudian, dikatakan (kepada mereka): "Inilah azab yang dahulu selalu kamu dustakan".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,18,'كَلَّآ اِنَّ كِتٰبَ الْاَبْرَارِ لَفِيْ عِلِّيِّيْنَۗ ','Sekali-kali tidak, sesungguhnya kitab orang-orang yang berbakti itu (tersimpan) dalam ´Illiyyin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,19,'وَمَآ اَدْرٰىكَ مَا عِلِّيُّوْنَۗ','Tahukah kamu apakah ´Illiyyin itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,20,'كِتٰبٌ مَّرْقُوْمٌۙ','(Yaitu) kitab yang bertulis,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,21,'يَّشْهَدُهُ الْمُقَرَّبُوْنَۗ','yang disaksikan oleh malaikat-malaikat yang didekatkan (kepada Allah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,22,'اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ','Sesungguhnya orang yang berbakti itu benar-benar berada dalam kenikmatan yang besar (surga),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,23,'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۙ','mereka (duduk) di atas dipan-dipan sambil memandang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,24,'تَعْرِفُ فِيْ وُجُوْهِهِمْ نَضْرَةَ النَّعِيْمِۚ','Kamu dapat mengetahui dari wajah mereka kesenangan mereka yang penuh kenikmatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,25,'يُسْقَوْنَ مِنْ رَّحِيْقٍ مَّخْتُوْمٍۙ','Mereka diberi minum dari khamar murni yang dilak (tempatnya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,26,'خِتٰمُهٗ مِسْكٌ ۗوَفِيْ ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُوْنَۗ','laknya adalah kesturi; dan untuk yang demikian itu hendaknya orang berlomba-lomba.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,27,'وَمِزَاجُهٗ مِنْ تَسْنِيْمٍۙ','Dan campuran khamar murni itu adalah dari tasnim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,28,'عَيْنًا يَّشْرَبُ بِهَا الْمُقَرَّبُوْنَۗ','(yaitu) mata air yang minum daripadanya orang-orang yang didekatkan kepada Allah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,29,'اِنَّ الَّذِيْنَ اَجْرَمُوْا كَانُوْا مِنَ الَّذِيْنَ اٰمَنُوْا يَضْحَكُوْنَۖ','Sesungguhnya orang-orang yang berdosa, adalah mereka yang menertawakan orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,30,'وَاِذَا مَرُّوْا بِهِمْ يَتَغَامَزُوْنَۖ','Dan apabila orang-orang yang beriman lalu di hadapan mereka, mereka saling mengedip-ngedipkan matanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,31,'وَاِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمُ انْقَلَبُوْا فَكِهِيْنَۖ','Dan apabila orang-orang yang berdosa itu kembali kepada kaumnya, mereka kembali dengan gembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,32,'وَاِذَا رَاَوْهُمْ قَالُوْٓا اِنَّ هٰٓؤُلَاۤءِ لَضَاۤلُّوْنَۙ','Dan apabila mereka melihat orang-orang mukmin, mereka mengatakan: "Sesungguhnya mereka itu benar-benar orang-orang yang sesat",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,33,'وَمَآ اُرْسِلُوْا عَلَيْهِمْ حٰفِظِيْنَۗ','padahal orang-orang yang berdosa itu tidak dikirim untuk penjaga bagi orang-orang mukmin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,34,'فَالْيَوْمَ الَّذِيْنَ اٰمَنُوْا مِنَ الْكُفَّارِ يَضْحَكُوْنَۙ','Maka pada hari ini, orang-orang yang beriman menertawakan orang-orang kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,35,'عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۗ','mereka (duduk) di atas dipan-dipan sambil memandang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,83,36,'هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوْا يَفْعَلُوْنَ ࣖ','Sesungguhnya orang-orang kafir telah diberi ganjaran terhadap apa yang dahulu mereka kerjakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,1,'اِذَا السَّمَاۤءُ انْشَقَّتْۙ ','Apabila langit terbelah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,2,'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ ','dan patuh kepada Tuhannya, dan sudah semestinya langit itu patuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,3,'وَاِذَا الْاَرْضُ مُدَّتْۙ ','dan apabila bumi diratakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,4,'وَاَلْقَتْ مَا فِيْهَا وَتَخَلَّتْۙ ','dan dilemparkan apa yang ada di dalamnya dan menjadi kosong,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,5,'وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۗ ','dan patuh kepada Tuhannya, dan sudah semestinya bumi itu patuh, (pada waktu itu manusia akan mengetahui akibat perbuatannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,6,'يٰٓاَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحًا فَمُلٰقِيْهِۚ ','Hai manusia, sesungguhnya kamu telah bekerja dengan sungguh-sungguh menuju Tuhanmu, maka pasti kamu akan menemui-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,7,'فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖۙ ','Adapun orang yang diberikan kitabnya dari sebelah kanannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,8,'فَسَوْفَ يُحَاسَبُ حِسَابًا يَّسِيْرًاۙ ','maka dia akan diperiksa dengan pemeriksaan yang mudah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,9,'وَّيَنْقَلِبُ اِلٰٓى اَهْلِهٖ مَسْرُوْرًاۗ ','dan dia akan kembali kepada kaumnya (yang sama-sama beriman) dengan gembira.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,10,'وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ وَرَاۤءَ ظَهْرِهٖۙ ','Adapun orang-orang yang diberikan kitabnya dari belakang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,11,'فَسَوْفَ يَدْعُوْ ثُبُوْرًاۙ ','maka dia akan berteriak: "Celakalah aku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,12,'وَّيَصْلٰى سَعِيْرًاۗ ','Dan dia akan masuk ke dalam api yang menyala-nyala (neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,13,'اِنَّهٗ كَانَ فِيْٓ اَهْلِهٖ مَسْرُوْرًاۗ ','Sesungguhnya dia dahulu (di dunia) bergembira di kalangan kaumnya (yang sama-sama kafir).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,14,'اِنَّهٗ ظَنَّ اَنْ لَّنْ يَّحُوْرَ ۛ ','Sesungguhnya dia menyangka bahwa dia sekali-kali tidak akan kembali (kepada Tuhannya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,15,'بَلٰىۛ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيْرًاۗ ','(Bukan demikian), yang benar, sesungguhnya Tuhannya selalu melihatnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,16,'فَلَآ اُقْسِمُ بِالشَّفَقِۙ ','Maka sesungguhnya Aku bersumpah dengan cahaya merah di waktu senja,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,17,'وَالَّيْلِ وَمَا وَسَقَۙ ','dan dengan malam dan apa yang diselubunginya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,18,'وَالْقَمَرِ اِذَا اتَّسَقَۙ ','dan dengan bulan apabila jadi purnama,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,19,'لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍۗ ','sesungguhnya kamu melalui tingkat demi tingkat (dalam kehidupan),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,20,'فَمَا لَهُمْ لَا يُؤْمِنُوْنَۙ ','Mengapa mereka tidak mau beriman?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,21,'وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُوْنَ ۗ ۩ ','dan apabila Al Quran dibacakan kepada mereka, mereka tidak bersujud,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,22,'بَلِ الَّذِيْنَ كَفَرُوْا يُكَذِّبُوْنَۖ ','bahkan orang-orang kafir itu mendustakan(nya).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,23,'وَاللّٰهُ اَعْلَمُ بِمَا يُوْعُوْنَۖ ','Padahal Allah mengetahui apa yang mereka sembunyikan (dalam hati mereka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,24,'فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ ','Maka beri kabar gembiralah mereka dengan azab yang pedih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,84,25,'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ ࣖ ','tetapi orang-orang yang beriman dan beramal saleh, bagi mereka pahala yang tidak putus-putusnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,1,'وَالسَّمَاۤءِ ذَاتِ الْبُرُوْجِۙ ','Demi langit yang mempunyai gugusan bintang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,2,'وَالْيَوْمِ الْمَوْعُوْدِۙ ','dan hari yang dijanjikan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,3,'وَشَاهِدٍ وَّمَشْهُوْدٍۗ ','dan yang menyaksikan dan yang disaksikan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,4,'قُتِلَ اَصْحٰبُ الْاُخْدُوْدِۙ ','Binasa dan terlaknatlah orang-orang yang membuat parit,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,5,'النَّارِ ذَاتِ الْوَقُوْدِۙ ','yang berapi (dinyalakan dengan) kayu bakar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,6,'اِذْ هُمْ عَلَيْهَا قُعُوْدٌۙ ','ketika mereka duduk di sekitarnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,7,'وَّهُمْ عَلٰى مَا يَفْعَلُوْنَ بِالْمُؤْمِنِيْنَ شُهُوْدٌ  ۗ ','sedang mereka menyaksikan apa yang mereka perbuat terhadap orang-orang yang beriman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,8,'وَمَا نَقَمُوْا مِنْهُمْ اِلَّآ اَنْ يُّؤْمِنُوْا بِاللّٰهِ الْعَزِيْزِ الْحَمِيْدِۙ ','Dan mereka tidak menyiksa orang-orang mukmin itu melainkan karena orang-orang mukmin itu beriman kepada Allah Yang Maha Perkasa lagi Maha Terpuji,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,9,'الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ  ۗ ','Yang mempunyai kerajaan langit dan bumi; dan Allah Maha Menyaksikan segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,10,'اِنَّ الَّذِيْنَ فَتَنُوا الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ ثُمَّ لَمْ يَتُوْبُوْا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيْقِۗ ','Sesungguhnya orang-orang yang mendatangkan cobaan kepada orang-orang yang mukmin laki-laki dan perempuan kemudian mereka tidak bertaubat, maka bagi mereka azab Jahannam dan bagi mereka azab (neraka) yang membakar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,11,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ ذٰلِكَ الْفَوْزُ الْكَبِيْرُۗ','Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal yang saleh bagi mereka surga yang mengalir di bawahnya sungai-sungai; itulah keberuntungan yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,12,'اِنَّ بَطْشَ رَبِّكَ لَشَدِيْدٌ ۗ ','Sesungguhnya azab Tuhanmu benar-benar keras.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,13,'اِنَّهٗ هُوَ يُبْدِئُ وَيُعِيْدُۚ ','Sesungguhnya Dialah Yang menciptakan (makhluk) dari permulaan dan menghidupkannya (kembali).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,14,'وَهُوَ الْغَفُوْرُ الْوَدُوْدُۙ ','Dialah Yang Maha Pengampun lagi Maha Pengasih,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,15,'ذُو الْعَرْشِ الْمَجِيْدُۙ ','yang mempunyai ´Arsy, lagi Maha Mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,16,'فَعَّالٌ لِّمَا يُرِيْدُۗ ','Maha Kuasa berbuat apa yang dikehendaki-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,17,'هَلْ اَتٰىكَ حَدِيْثُ الْجُنُوْدِۙ ','Sudahkah datang kepadamu berita kaum-kaum penentang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,18,'فِرْعَوْنَ وَثَمُوْدَۗ ','(yaitu kaum) Fir´aun dan (kaum) Tsamud?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,19,'بَلِ الَّذِيْنَ كَفَرُوْا فِيْ تَكْذِيْبٍۙ ','Sesungguhnya orang-orang kafir selalu mendustakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,20,'وَّاللّٰهُ مِنْ وَّرَاۤىِٕهِمْ مُّحِيْطٌۚ ','padahal Allah mengepung mereka dari belakang mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,21,'بَلْ هُوَ قُرْاٰنٌ مَّجِيْدٌۙ ','Bahkan yang didustakan mereka itu ialah Al Quran yang mulia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,85,22,'فِيْ لَوْحٍ مَّحْفُوْظٍ ࣖ ','yang (tersimpan) dalam Lauh Mahfuzh.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,1,'وَالسَّمَاۤءِ وَالطَّارِقِۙ ','Demi langit dan yang datang pada malam hari,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,2,'وَمَآ اَدْرٰىكَ مَا الطَّارِقُۙ ','tahukah kamu apakah yang datang pada malam hari itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,3,'النَّجْمُ الثَّاقِبُۙ ','(yaitu) bintang yang cahayanya menembus,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,4,'اِنْ كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌۗ ','tidak ada suatu jiwapun (diri) melainkan ada penjaganya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,5,'فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَ ','Maka hendaklah manusia memperhatikan dari apakah dia diciptakan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,6,'خُلِقَ مِنْ مَّاۤءٍ دَافِقٍۙ ','Dia diciptakan dari air yang dipancarkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,7,'يَّخْرُجُ مِنْۢ بَيْنِ الصُّلْبِ وَالتَّرَاۤىِٕبِۗ ','yang keluar dari antara tulang sulbi laki-laki dan tulang dada perempuan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,8,'اِنَّهٗ عَلٰى رَجْعِهٖ لَقَادِرٌۗ ','Sesungguhnya Allah benar-benar kuasa untuk mengembalikannya (hidup sesudah mati).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,9,'يَوْمَ تُبْلَى السَّرَاۤىِٕرُۙ ','Pada hari dinampakkan segala rahasia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,10,'فَمَا لَهٗ مِنْ قُوَّةٍ وَّلَا نَاصِرٍۗ ','maka sekali-kali tidak ada bagi manusia itu suatu kekuatanpun dan tidak (pula) seorang penolong.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,11,'وَالسَّمَاۤءِ ذَاتِ الرَّجْعِۙ ','Demi langit yang mengandung hujan')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,12,'وَالْاَرْضِ ذَاتِ الصَّدْعِۙ ','dan bumi yang mempunyai tumbuh-tumbuhan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,13,'اِنَّهٗ لَقَوْلٌ فَصْلٌۙ ','sesungguhnya Al Quran itu benar-benar firman yang memisahkan antara yang hak dan yang bathil.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,14,'وَّمَا هُوَ بِالْهَزْلِۗ ','dan sekali-kali bukanlah dia senda gurau.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,15,'اِنَّهُمْ يَكِيْدُوْنَ كَيْدًاۙ ','Sesungguhnya orang kafir itu merencanakan tipu daya yang jahat dengan sebenar-benarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,16,'وَّاَكِيْدُ كَيْدًاۖ ','Dan Akupun membuat rencana (pula) dengan sebenar-benarnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,86,17,'فَمَهِّلِ الْكٰفِرِيْنَ اَمْهِلْهُمْ رُوَيْدًا ࣖ ','Karena itu beri tangguhlah orang-orang kafir itu yaitu beri tangguhlah mereka itu barang sebentar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,1,'سَبِّحِ اسْمَ رَبِّكَ الْاَعْلَىۙ ','Sucikanlah nama Tuhanmu Yang Maha Tingi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,2,'الَّذِيْ خَلَقَ فَسَوّٰىۖ ','yang menciptakan, dan menyempurnakan (penciptaan-Nya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,3,'وَالَّذِيْ قَدَّرَ فَهَدٰىۖ ','dan yang menentukan kadar (masing-masing) dan memberi petunjuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,4,'وَالَّذِيْٓ اَخْرَجَ الْمَرْعٰىۖ ','dan yang menumbuhkan rumput-rumputan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,5,'فَجَعَلَهٗ غُثَاۤءً اَحْوٰىۖ ','lalu dijadikan-Nya rumput-rumput itu kering kehitam-hitaman.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,6,'سَنُقْرِئُكَ فَلَا تَنْسٰىٓ  ۖ ','Kami akan membacakan (Al Quran) kepadamu (Muhammad) maka kamu tidak akan lupa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,7,'اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّهٗ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۗ ','kecuali kalau Allah menghendaki. Sesungguhnya Dia mengetahui yang terang dan yang tersembunyi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,8,'وَنُيَسِّرُكَ لِلْيُسْرٰىۖ ','dan Kami akan memberi kamu taufik ke jalan yang mudah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,9,'فَذَكِّرْ اِنْ نَّفَعَتِ الذِّكْرٰىۗ ','oleh sebab itu berikanlah peringatan karena peringatan itu bermanfaat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,10,'سَيَذَّكَّرُ مَنْ يَّخْشٰىۙ ','orang yang takut (kepada Allah) akan mendapat pelajaran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,11,'وَيَتَجَنَّبُهَا الْاَشْقَىۙ ','dan orang-orang yang celaka (kafir) akan menjauhinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,12,'الَّذِيْ يَصْلَى النَّارَ الْكُبْرٰىۚ ','(Yaitu) orang yang akan memasuki api yang besar (neraka).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,13,'ثُمَّ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰىۗ ','Kemudian dia tidak akan mati di dalamnya dan tidak (pula) hidup.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,14,'قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ ','Sesungguhnya beruntunglah orang yang membersihkan diri (dengan beriman),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,15,'وَذَكَرَ اسْمَ رَبِّهٖ فَصَلّٰىۗ ','dan dia ingat nama Tuhannya, lalu dia sembahyang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,16,'بَلْ تُؤْثِرُوْنَ الْحَيٰوةَ الدُّنْيَاۖ ','Tetapi kamu (orang-orang kafir) memilih kehidupan duniawi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,17,'وَالْاٰخِرَةُ خَيْرٌ وَّاَبْقٰىۗ ','Sedang kehidupan akhirat adalah lebih baik dan lebih kekal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,18,'اِنَّ هٰذَا لَفِى الصُّحُفِ الْاُوْلٰىۙ ','Sesungguhnya ini benar-benar terdapat dalam kitab-kitab yang dahulu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,87,19,'صُحُفِ اِبْرٰهِيْمَ وَمُوْسٰى ࣖ ','(yaitu) Kitab-kitab Ibrahim dan Musa')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,1,'هَلْ اَتٰىكَ حَدِيْثُ الْغَاشِيَةِۗ','Sudah datangkah kepadamu berita (tentang) hari pembalasan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,2,'وُجُوْهٌ يَّوْمَىِٕذٍ خَاشِعَةٌ  ۙ','Banyak muka pada hari itu tunduk terhina,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,3,'عَامِلَةٌ نَّاصِبَةٌ  ۙ','bekerja keras lagi kepayahan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,4,'تَصْلٰى نَارًا حَامِيَةً  ۙ','memasuki api yang sangat panas (neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,5,'تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍ ۗ','diberi minum (dengan air) dari sumber yang sangat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,6,'لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَرِيْعٍۙ','Mereka tiada memperoleh makanan selain dari pohon yang berduri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,7,'لَّا يُسْمِنُ وَلَا يُغْنِيْ مِنْ جُوْعٍۗ','yang tidak menggemukkan dan tidak pula menghilangkan lapar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,8,'وُجُوْهٌ يَّوْمَىِٕذٍ نَّاعِمَةٌ  ۙ','Banyak muka pada hari itu berseri-seri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,9,'لِّسَعْيِهَا رَاضِيَةٌ  ۙ ','merasa senang karena usahanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,10,'فِيْ جَنَّةٍ عَالِيَةٍۙ','dalam surga yang tinggi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,11,'لَّا تَسْمَعُ فِيْهَا لَاغِيَةً ۗ','tidak kamu dengar di dalamnya perkataan yang tidak berguna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,12,'فِيْهَا عَيْنٌ جَارِيَةٌ  ۘ','Di dalamnya ada mata air yang mengalir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,13,'فِيْهَا سُرُرٌ مَّرْفُوْعَةٌ  ۙ','Di dalamnya ada takhta-takhta yang ditinggikan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,14,'وَّاَكْوَابٌ مَّوْضُوْعَةٌ  ۙ','dan gelas-gelas yang terletak (di dekatnya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,15,'وَّنَمَارِقُ مَصْفُوْفَةٌ  ۙ ','dan bantal-bantal sandaran yang tersusun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,16,'وَّزَرَابِيُّ مَبْثُوْثَةٌ  ۗ','dan permadani-permadani yang terhampar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,17,'اَفَلَا يَنْظُرُوْنَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْۗ','Maka apakah mereka tidak memperhatikan unta bagaimana dia diciptakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,18,'وَاِلَى السَّمَاۤءِ كَيْفَ رُفِعَتْۗ','Dan langit, bagaimana ia ditinggikan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,19,'وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْۗ','Dan gunung-gunung bagaimana ia ditegakkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,20,'وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْۗ ','Dan bumi bagaimana ia dihamparkan?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,21,'فَذَكِّرْۗ اِنَّمَآ اَنْتَ مُذَكِّرٌۙ','Maka berilah peringatan, karena sesungguhnya kamu hanyalah orang yang memberi peringatan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,22,'لَّسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ','Kamu bukanlah orang yang berkuasa atas mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,23,'اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ','tetapi orang yang berpaling dan kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,24,'فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۗ','maka Allah akan mengazabnya dengan azab yang besar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,25,'اِنَّ اِلَيْنَآ اِيَابَهُمْ','Sesungguhnya kepada Kami-lah kembali mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,88,26,'ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ ࣖ','kemudian sesungguhnya kewajiban Kami-lah menghisab mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,1,'وَالْفَجْرِۙ','Demi fajar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,2,'وَلَيَالٍ عَشْرٍۙ','dan malam yang sepuluh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,3,'وَّالشَّفْعِ وَالْوَتْرِۙ','dan yang genap dan yang ganjil,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,4,'وَالَّيْلِ اِذَا يَسْرِۚ','dan malam bila berlalu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,5,'هَلْ فِيْ ذٰلِكَ قَسَمٌ لِّذِيْ حِجْرٍۗ','Pada yang demikian itu terdapat sumpah (yang dapat diterima) oleh orang-orang yang berakal.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,6,'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۖ','Apakah kamu tidak memperhatikan bagaimana Tuhanmu berbuat terhadap kaum ´Aad?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,7,'اِرَمَ ذَاتِ الْعِمَادِۖ','(yaitu) penduduk Iram yang mempunyai bangunan-bangunan yang tinggi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,8,'الَّتِيْ لَمْ يُخْلَقْ مِثْلُهَا فِى الْبِلَادِۖ','yang belum pernah dibangun (suatu kota) seperti itu, di negeri-negeri lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,9,'وَثَمُوْدَ الَّذِيْنَ جَابُوا الصَّخْرَ بِالْوَادِۖ','dan kaum Tsamud yang memotong batu-batu besar di lembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,10,'وَفِرْعَوْنَ ذِى الْاَوْتَادِۖ','dan kaum Fir´aun yang mempunyai pasak-pasak (tentara yang banyak),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,11,'الَّذِيْنَ طَغَوْا فِى الْبِلَادِۖ','yang berbuat sewenang-wenang dalam negeri,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,12,'فَاَكْثَرُوْا فِيْهَا الْفَسَادَۖ','lalu mereka berbuat banyak kerusakan dalam negeri itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,13,'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۖ','karena itu Tuhanmu menimpakan kepada mereka cemeti azab,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,14,'اِنَّ رَبَّكَ لَبِالْمِرْصَادِۗ','sesungguhnya Tuhanmu benar-benar mengawasi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,15,'فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰىهُ رَبُّهٗ فَاَكْرَمَهٗ وَنَعَّمَهٗۙ فَيَقُوْلُ رَبِّيْٓ اَكْرَمَنِۗ','Adapun manusia apabila Tuhannya mengujinya lalu dia dimuliakan-Nya dan diberi-Nya kesenangan, maka dia akan berkata: "Tuhanku telah memuliakanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,16,'وَاَمَّآ اِذَا مَا ابْتَلٰىهُ فَقَدَرَ عَلَيْهِ رِزْقَهٗ ەۙ فَيَقُوْلُ رَبِّيْٓ اَهَانَنِۚ','Adapun bila Tuhannya mengujinya lalu membatasi rezekinya maka dia berkata: "Tuhanku menghinakanku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,17,'كَلَّا بَلْ لَّا تُكْرِمُوْنَ الْيَتِيْمَۙ','Sekali-kali tidak (demikian), sebenarnya kamu tidak memuliakan anak yatim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,18,'وَلَا تَحٰۤضُّوْنَ عَلٰى طَعَامِ الْمِسْكِيْنِۙ','dan kamu tidak saling mengajak memberi makan orang miskin,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,19,'وَتَأْكُلُوْنَ التُّرَاثَ اَكْلًا لَّمًّاۙ','dan kamu memakan harta pusaka dengan cara mencampur baurkan (yang halal dan yang bathil),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,20,'وَّتُحِبُّوْنَ الْمَالَ حُبًّا جَمًّاۗ','dan kamu mencintai harta benda dengan kecintaan yang berlebihan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,21,'كَلَّآ اِذَا دُكَّتِ الْاَرْضُ دَكًّا دَكًّاۙ','Jangan (berbuat demikian). Apabila bumi digoncangkan berturut-turut,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,22,'وَّجَآءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّاۚ','dan datanglah Tuhanmu; sedang malaikat berbaris-baris.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,23,'وَجِايْۤءَ يَوْمَىِٕذٍۢ بِجَهَنَّمَۙ يَوْمَىِٕذٍ يَّتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۗ','Dan pada hari itu diperlihatkan neraka Jahannam; dan pada hari itu ingatlah manusia, akan tetapi tidak berguna lagi mengingat itu baginya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,24,'يَقُوْلُ يٰلَيْتَنِيْ قَدَّمْتُ لِحَيَاتِيْۚ','Dia mengatakan: "Alangkah baiknya kiranya aku dahulu mengerjakan (amal saleh) untuk hidupku ini".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,25,'فَيَوْمَىِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗٓ اَحَدٌ ۙ','Maka pada hari itu tiada seorangpun yang menyiksa seperti siksa-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,26,'وَّلَا يُوْثِقُ وَثَاقَهٗٓ اَحَدٌ ۗ','dan tiada seorangpun yang mengikat seperti ikatan-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,27,'يٰٓاَيَّتُهَا النَّفْسُ الْمُطْمَىِٕنَّةُۙ','Hai jiwa yang tenang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,28,'ارْجِعِيْٓ اِلٰى رَبِّكِ رَاضِيَةً مَّرْضِيَّةً ۚ','Kembalilah kepada Tuhanmu dengan hati yang puas lagi diridhai-Nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,29,'فَادْخُلِيْ فِيْ عِبٰدِيْۙ','Maka masuklah ke dalam jama´ah hamba-hamba-Ku,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,89,30,'وَادْخُلِيْ جَنَّتِيْ ࣖࣖ','masuklah ke dalam surga-Ku.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,1,'لَآ اُقْسِمُ بِهٰذَا الْبَلَدِۙ','Aku benar-benar bersumpah dengan kota ini (Mekah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,2,'وَاَنْتَ حِلٌّۢ بِهٰذَا الْبَلَدِۙ','dan kamu (Muhammad) bertempat di kota Mekah ini,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,3,'وَوَالِدٍ وَّمَا وَلَدَۙ','dan demi bapak dan anaknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,4,'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْ كَبَدٍۗ','Sesungguhnya Kami telah menciptakan manusia berada dalam susah payah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,5,'اَيَحْسَبُ اَنْ لَّنْ يَّقْدِرَ عَلَيْهِ اَحَدٌ ۘ','Apakah manusia itu menyangka bahwa sekali-kali tiada seorangpun yang berkuasa atasnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,6,'يَقُوْلُ اَهْلَكْتُ مَالًا لُّبَدًاۗ','Dan mengatakan: "Aku telah menghabiskan harta yang banyak".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,7,'اَيَحْسَبُ اَنْ لَّمْ يَرَهٗٓ اَحَدٌۗ','Apakah dia menyangka bahwa tiada seorangpun yang melihatnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,8,'اَلَمْ نَجْعَلْ لَّهٗ عَيْنَيْنِۙ','Bukankah Kami telah memberikan kepadanya dua buah mata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,9,'وَلِسَانًا وَّشَفَتَيْنِۙ','lidah dan dua buah bibir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,10,'وَهَدَيْنٰهُ النَّجْدَيْنِۙ','Dan Kami telah menunjukkan kepadanya dua jalan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,11,'فَلَا اقْتَحَمَ الْعَقَبَةَ ۖ','Tetapi dia tiada menempuh jalan yang mendaki lagi sukar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,12,'وَمَآ اَدْرٰىكَ مَا الْعَقَبَةُ ۗ','Tahukah kamu apakah jalan yang mendaki lagi sukar itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,13,'فَكُّ رَقَبَةٍۙ','(yaitu) melepaskan budak dari perbudakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,14,'اَوْ اِطْعَامٌ فِيْ يَوْمٍ ذِيْ مَسْغَبَةٍۙ','atau memberi makan pada hari kelaparan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,15,'يَّتِيْمًا ذَا مَقْرَبَةٍۙ','(kepada) anak yatim yang ada hubungan kerabat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,16,'اَوْ مِسْكِيْنًا ذَا مَتْرَبَةٍۗ','atau kepada orang miskin yang sangat fakir.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,17,'ثُمَّ كَانَ مِنَ الَّذِيْنَ اٰمَنُوْا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۗ','Dan dia (tidak pula) termasuk orang-orang yang beriman dan saling berpesan untuk bersabar dan saling berpesan untuk berkasih sayang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,18,'اُولٰۤىِٕكَ اَصْحٰبُ الْمَيْمَنَةِۗ','Mereka (orang-orang yang beriman dan saling berpesan itu) adalah golongan kanan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,19,'وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا هُمْ اَصْحٰبُ الْمَشْئَمَةِۗ','Dan orang-orang yang kafir kepada ayat-ayat Kami, mereka itu adalah golongan kiri.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,90,20,'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ ࣖ','Mereka berada dalam neraka yang ditutup rapat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,1,'وَالشَّمْسِ وَضُحٰىهَاۖ','Demi matahari dan cahayanya di pagi hari,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,2,'وَالْقَمَرِ اِذَا تَلٰىهَاۖ','dan bulan apabila mengiringinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,3,'وَالنَّهَارِ اِذَا جَلّٰىهَاۖ','dan siang apabila menampakkannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,4,'وَالَّيْلِ اِذَا يَغْشٰىهَاۖ','dan malam apabila menutupinya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,5,'وَالسَّمَاۤءِ وَمَا بَنٰىهَاۖ','dan langit serta pembinaannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,6,'وَالْاَرْضِ وَمَا طَحٰىهَاۖ','dan bumi serta penghamparannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,7,'وَنَفْسٍ وَّمَا سَوّٰىهَاۖ','dan jiwa serta penyempurnaannya (ciptaannya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,8,'فَاَلْهَمَهَا فُجُوْرَهَا وَتَقْوٰىهَاۖ','maka Allah mengilhamkan kepada jiwa itu (jalan) kefasikan dan ketakwaannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,9,'قَدْ اَفْلَحَ مَنْ زَكّٰىهَاۖ','sesungguhnya beruntunglah orang yang mensucikan jiwa itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,10,'وَقَدْ خَابَ مَنْ دَسّٰىهَاۗ','dan sesungguhnya merugilah orang yang mengotorinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,11,'كَذَّبَتْ ثَمُوْدُ بِطَغْوٰىهَآ  ۖ','(Kaum) Tsamud telah mendustakan (rasulnya) karena mereka melampaui batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,12,'اِذِ انْۢبَعَثَ اَشْقٰىهَاۖ','ketika bangkit orang yang paling celaka di antara mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,13,'فَقَالَ لَهُمْ رَسُوْلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰهَاۗ','lalu Rasul Allah (Saleh) berkata kepada mereka: ("Biarkanlah) unta betina Allah dan minumannya".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,14,'فَكَذَّبُوْهُ فَعَقَرُوْهَاۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْۢبِهِمْ فَسَوّٰىهَاۖ','Lalu mereka mendustakannya dan menyembelih unta itu, maka Tuhan mereka membinasakan mereka disebabkan dosa mereka, lalu Allah menyama-ratakan mereka (dengan tanah),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,91,15,'وَلَا يَخَافُ عُقْبٰهَا ࣖ','dan Allah tidak takut terhadap akibat tindakan-Nya itu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,1,'وَالَّيْلِ اِذَا يَغْشٰىۙ','Demi malam apabila menutupi (cahaya siang),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,2,'وَالنَّهَارِ اِذَا تَجَلّٰىۙ','dan siang apabila terang benderang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,3,'وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىٓ ۙ','dan penciptaan laki-laki dan perempuan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,4,'اِنَّ سَعْيَكُمْ لَشَتّٰىۗ','sesungguhnya usaha kamu memang berbeda-beda.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,5,'فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ','Adapun orang yang memberikan (hartanya di jalan Allah) dan bertakwa,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,6,'وَصَدَّقَ بِالْحُسْنٰىۙ','dan membenarkan adanya pahala yang terbaik (surga),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,7,'فَسَنُيَسِّرُهٗ لِلْيُسْرٰىۗ','maka Kami kelak akan menyiapkan baginya jalan yang mudah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,8,'وَاَمَّا مَنْۢ بَخِلَ وَاسْتَغْنٰىۙ','Dan adapun orang-orang yang bakhil dan merasa dirinya cukup,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,9,'وَكَذَّبَ بِالْحُسْنٰىۙ','serta mendustakan pahala terbaik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,10,'فَسَنُيَسِّرُهٗ لِلْعُسْرٰىۗ','maka kelak Kami akan menyiapkan baginya (jalan) yang sukar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,11,'وَمَا يُغْنِيْ عَنْهُ مَالُهٗٓ اِذَا تَرَدّٰىٓۙ','Dan hartanya tidak bermanfaat baginya apabila ia telah binasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,12,'اِنَّ عَلَيْنَا لَلْهُدٰىۖ','Sesungguhnya kewajiban Kamilah memberi petunjuk,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,13,'وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُوْلٰىۗ','dan sesungguhnya kepunyaan Kamilah akhirat dan dunia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,14,'فَاَنْذَرْتُكُمْ نَارًا تَلَظّٰىۚ','Maka, kami memperingatkan kamu dengan neraka yang menyala-nyala.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,15,'لَا يَصْلٰىهَآ اِلَّا الْاَشْقَىۙ','Tidak ada yang masuk ke dalamnya kecuali orang yang paling celaka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,16,'الَّذِيْ كَذَّبَ وَتَوَلّٰىۗ','yang mendustakan (kebenaran) dan berpaling (dari iman).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,17,'وَسَيُجَنَّبُهَا الْاَتْقَىۙ','Dan kelak akan dijauhkan orang yang paling takwa dari neraka itu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,18,'الَّذِيْ يُؤْتِيْ مَالَهٗ يَتَزَكّٰىۚ','yang menafkahkan hartanya (di jalan Allah) untuk membersihkannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,19,'وَمَا لِاَحَدٍ عِنْدَهٗ مِنْ نِّعْمَةٍ تُجْزٰىٓۙ','padahal tidak ada seseorangpun memberikan suatu nikmat kepadanya yang harus dibalasnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,20,'اِلَّا ابْتِغَاۤءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ','tetapi (dia memberikan itu semata-mata) karena mencari keridhaan Tuhannya yang Maha Tinggi.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,92,21,'وَلَسَوْفَ يَرْضٰى ࣖ','Dan kelak dia benar-benar mendapat kepuasan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,1,'وَالضُّحٰىۙ','Demi waktu matahari sepenggalahan naik,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,2,'وَالَّيْلِ اِذَا سَجٰىۙ','dan demi malam apabila telah sunyi (gelap),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,3,'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۗ','Tuhanmu tiada meninggalkan kamu dan tiada (pula) benci kepadamu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,4,'وَلَلْاٰخِرَةُ خَيْرٌ لَّكَ مِنَ الْاُوْلٰىۗ','Dan sesungguhnya hari kemudian itu lebih baik bagimu daripada yang sekarang (permulaan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,5,'وَلَسَوْفَ يُعْطِيْكَ رَبُّكَ فَتَرْضٰىۗ','Dan kelak Tuhanmu pasti memberikan karunia-Nya kepadamu , lalu (hati) kamu menjadi puas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,6,'اَلَمْ يَجِدْكَ يَتِيْمًا فَاٰوٰىۖ','Bukankah Dia mendapatimu sebagai seorang yatim, lalu Dia melindungimu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,7,'وَوَجَدَكَ ضَاۤلًّا فَهَدٰىۖ','Dan Dia mendapatimu sebagai seorang yang bingung, lalu Dia memberikan petunjuk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,8,'وَوَجَدَكَ عَاۤىِٕلًا فَاَغْنٰىۗ','Dan Dia mendapatimu sebagai seorang yang kekurangan, lalu Dia memberikan kecukupan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,9,'فَاَمَّا الْيَتِيْمَ فَلَا تَقْهَرْۗ','Sebab itu, terhadap anak yatim janganlah kamu berlaku sewenang-wenang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,10,'وَاَمَّا السَّاۤىِٕلَ فَلَا تَنْهَرْ','Dan terhadap orang yang minta-minta, janganlah kamu menghardiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,93,11,'وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ ࣖ','Dan terhadap nikmat Tuhanmu, maka hendaklah kamu siarkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,1,'اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ','Bukankah Kami telah melapangkan untukmu dadamu?,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,2,'وَوَضَعْنَا عَنْكَ وِزْرَكَۙ','dan Kami telah menghilangkan daripadamu bebanmu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,3,'الَّذِيْٓ اَنْقَضَ ظَهْرَكَۙ','yang memberatkan punggungmu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,4,'وَرَفَعْنَا لَكَ ذِكْرَكَۗ','Dan Kami tinggikan bagimu sebutan (nama)mu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,5,'فَاِنَّ مَعَ الْعُسْرِ يُسْرًاۙ','Karena sesungguhnya sesudah kesulitan itu ada kemudahan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,6,'اِنَّ مَعَ الْعُسْرِ يُسْرًاۗ','sesungguhnya sesudah kesulitan itu ada kemudahan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,7,'فَاِذَا فَرَغْتَ فَانْصَبْۙ','Maka apabila kamu telah selesai (dari sesuatu urusan), kerjakanlah dengan sungguh-sungguh (urusan) yang lain,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,94,8,'وَاِلٰى رَبِّكَ فَارْغَبْ ࣖ','dan hanya kepada Tuhanmulah hendaknya kamu berharap.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,1,'وَالتِّيْنِ وَالزَّيْتُوْنِۙ','Demi (buah) Tin dan (buah) Zaitun,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,2,'وَطُوْرِ سِيْنِيْنَۙ','dan demi bukit Sinai,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,3,'وَهٰذَا الْبَلَدِ الْاَمِيْنِۙ','dan demi kota (Mekah) ini yang aman,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,4,'لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْٓ اَحْسَنِ تَقْوِيْمٍۖ','sesungguhnya Kami telah menciptakan manusia dalam bentuk yang sebaik-baiknya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,5,'ثُمَّ رَدَدْنٰهُ اَسْفَلَ سَافِلِيْنَۙ','Kemudian Kami kembalikan dia ke tempat yang serendah-rendahnya (neraka),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,6,'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍۗ','kecuali orang-orang yang beriman dan mengerjakan amal saleh; maka bagi mereka pahala yang tiada putus-putusnya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,7,'فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّيْنِۗ','Maka apakah yang menyebabkan kamu mendustakan (hari) pembalasan sesudah (adanya keterangan-keterangan) itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,95,8,'اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحٰكِمِيْنَ ࣖ','Bukankah Allah Hakim yang seadil-adilnya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,1,'اِقْرَأْ بِاسْمِ رَبِّكَ الَّذِيْ خَلَقَۚ','Bacalah dengan (menyebut) nama Tuhanmu Yang menciptakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,2,'خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ','Dia telah menciptakan manusia dari segumpal darah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,3,'اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ','Bacalah, dan Tuhanmulah Yang Maha Pemurah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,4,'الَّذِيْ عَلَّمَ بِالْقَلَمِۙ','Yang mengajar (manusia) dengan perantaran kalam,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,5,'عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۗ','Dia mengajar kepada manusia apa yang tidak diketahuinya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,6,'كَلَّآ اِنَّ الْاِنْسَانَ لَيَطْغٰىٓ ۙ','Ketahuilah! Sesungguhnya manusia benar-benar melampaui batas,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,7,'اَنْ رَّاٰهُ اسْتَغْنٰىۗ','karena dia melihat dirinya serba cukup.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,8,'اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۗ','Sesungguhnya hanya kepada Tuhanmulah kembali(mu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,9,'اَرَاَيْتَ الَّذِيْ يَنْهٰىۙ','Bagaimana pendapatmu tentang orang yang melarang,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,10,'عَبْدًا اِذَا صَلّٰىۗ','seorang hamba ketika mengerjakan shalat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,11,'اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىٓۙ','bagaimana pendapatmu jika orang yang melarang itu berada di atas kebenaran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,12,'اَوْ اَمَرَ بِالتَّقْوٰىۗ','atau dia menyuruh bertakwa (kepada Allah)?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,13,'اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۗ','Bagaimana pendapatmu jika orang yang melarang itu mendustakan dan berpaling?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,14,'اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۗ','Tidaklah dia mengetahui bahwa sesungguhnya Allah melihat segala perbuatannya?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,15,'كَلَّا لَىِٕنْ لَّمْ يَنْتَهِ ەۙ لَنَسْفَعًاۢ بِالنَّاصِيَةِۙ','Ketahuilah, sungguh jika dia tidak berhenti (berbuat demikian) niscaya Kami tarik ubun-ubunnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,16,'نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ','(yaitu) ubun-ubun orang yang mendustakan lagi durhaka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,17,'فَلْيَدْعُ نَادِيَهٗۙ','Maka biarlah dia memanggil golongannya (untuk menolongnya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,18,'سَنَدْعُ الزَّبَانِيَةَۙ','kelak Kami akan memanggil malaikat Zabaniyah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,96,19,'كَلَّاۗ  لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩ ࣖ','sekali-kali jangan, janganlah kamu patuh kepadanya; dan sujudlah dan dekatkanlah (dirimu kepada Tuhan).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,97,1,'اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةِ الْقَدْرِ ','Sesungguhnya Kami telah menurunkannya (Al Quran) pada malam kemuliaan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,97,2,'وَمَآ اَدْرٰىكَ مَا لَيْلَةُ الْقَدْرِۗ','Dan tahukah kamu apakah malam kemuliaan itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,97,3,'لَيْلَةُ الْقَدْرِ ەۙ خَيْرٌ مِّنْ اَلْفِ شَهْرٍۗ','Malam kemuliaan itu lebih baik dari seribu bulan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,97,4,'تَنَزَّلُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ فِيْهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۛ','Pada malam itu turun malaikat-malaikat dan malaikat Jibril dengan izin Tuhannya untuk mengatur segala urusan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,97,5,'سَلٰمٌ ۛهِيَ حَتّٰى مَطْلَعِ الْفَجْرِ ࣖ','Malam itu (penuh) kesejahteraan sampai terbit fajar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,1,'لَمْ يَكُنِ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ مُنْفَكِّيْنَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ','Orang-orang kafir yakni ahli Kitab dan orang-orang musyrik (mengatakan bahwa mereka) tidak akan meninggalkan (agamanya) sebelum datang kepada mereka bukti yang nyata,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,2,'رَسُوْلٌ مِّنَ اللّٰهِ يَتْلُوْا صُحُفًا مُّطَهَّرَةًۙ','(yaitu) seorang Rasul dari Allah (Muhammad) yang membacakan lembaran-lembaran yang disucikan (Al Quran),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,3,'فِيْهَا كُتُبٌ قَيِّمَةٌ  ۗ','di dalamnya terdapat (isi) Kitab-kitab yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,4,'وَمَا تَفَرَّقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنَةُ ۗ','Dan tidaklah berpecah belah orang-orang yang didatangkan Al Kitab (kepada mereka) melainkan sesudah datang kepada mereka bukti yang nyata.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,5,'وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۙ حُنَفَاۤءَ وَيُقِيْمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ دِيْنُ الْقَيِّمَةِۗ','Padahal mereka tidak disuruh kecuali supaya menyembah Allah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agama yang lurus, dan supaya mereka mendirikan shalat dan menunaikan zakat; dan yang demikian itulah agama yang lurus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,6,'اِنَّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ فِيْ نَارِ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ اُولٰۤىِٕكَ هُمْ شَرُّ الْبَرِيَّةِۗ','Sesungguhnya orang-orang yang kafir yakni ahli Kitab dan orang-orang yang musyrik (akan masuk) ke neraka Jahannam; mereka kekal di dalamnya. Mereka itu adalah seburuk-buruk makhluk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,7,'اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ هُمْ خَيْرُ الْبَرِيَّةِۗ','Sesungguhnya orang-orang yang beriman dan mengerjakan amal saleh, mereka itu adalah sebaik-baik makhluk.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,98,8,'جَزَاۤؤُهُمْ عِنْدَ رَبِّهِمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهٗ ࣖ','Balasan mereka di sisi Tuhan mereka ialah surga ´Adn yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Allah ridha terhadap mereka dan merekapun ridha kepada-Nya. Yang demikian itu adalah (balasan) bagi orang yang takut kepada Tuhannya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,1,'اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ','Apabila bumi digoncangkan dengan goncangan (yang dahsyat),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,2,'وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ','dan bumi telah mengeluarkan beban-beban berat (yang dikandung)nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,3,'وَقَالَ الْاِنْسَانُ مَا لَهَاۚ','dan manusia bertanya: "Mengapa bumi (menjadi begini)?",')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,4,'يَوْمَىِٕذٍ تُحَدِّثُ اَخْبَارَهَاۙ','pada hari itu bumi menceritakan beritanya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,5,'بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۗ','karena sesungguhnya Tuhanmu telah memerintahkan (yang sedemikian itu) kepadanya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,6,'يَوْمَىِٕذٍ يَّصْدُرُ النَّاسُ اَشْتَاتًا ەۙ لِّيُرَوْا اَعْمَالَهُمْۗ','Pada hari itu manusia ke luar dari kuburnya dalam keadaan bermacam-macam, supaya diperlihatkan kepada mereka (balasan) pekerjaan mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,7,'فَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَّرَهٗۚ','Barangsiapa yang mengerjakan kebaikan seberat dzarrahpun, niscaya dia akan melihat (balasan)nya.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,99,8,'وَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ ࣖ','Dan barangsiapa yang mengerjakan kejahatan sebesar dzarrahpun, niscaya dia akan melihat (balasan)nya pula.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,1,'وَالْعٰدِيٰتِ ضَبْحًاۙ ','Demi kuda perang yang berlari kencang dengan terengah-engah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,2,'فَالْمُوْرِيٰتِ قَدْحًاۙ','dan kuda yang mencetuskan api dengan pukulan (kuku kakinya),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,3,'فَالْمُغِيْرٰتِ صُبْحًاۙ','dan kuda yang menyerang dengan tiba-tiba di waktu pagi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,4,'فَاَثَرْنَ بِهٖ نَقْعًاۙ','maka ia menerbangkan debu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,5,'فَوَسَطْنَ بِهٖ جَمْعًاۙ','dan menyerbu ke tengah-tengah kumpulan musuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,6,'اِنَّ الْاِنْسَانَ لِرَبِّهٖ لَكَنُوْدٌ ۚ','sesungguhnya manusia itu sangat ingkar, tidak berterima kasih kepada Tuhannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,7,'وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيْدٌۚ','dan sesungguhnya manusia itu menyaksikan (sendiri) keingkarannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,8,'وَاِنَّهٗ لِحُبِّ الْخَيْرِ لَشَدِيْدٌ ۗ','dan sesungguhnya dia sangat bakhil karena cintanya kepada harta.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,9,'۞ اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِى الْقُبُوْرِۙ','Maka apakah dia tidak mengetahui apabila dibangkitkan apa yang ada di dalam kubur,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,10,'وَحُصِّلَ مَا فِى الصُّدُوْرِۙ','dan dilahirkan apa yang ada di dalam dada,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,100,11,'اِنَّ رَبَّهُمْ بِهِمْ يَوْمَىِٕذٍ لَّخَبِيْرٌ ࣖ','sesungguhnya Tuhan mereka pada hari itu Maha Mengetahui keadaan mereka.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,1,'اَلْقَارِعَةُۙ','Hari Kiamat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,2,'مَا الْقَارِعَةُ ۚ','apakah hari Kiamat itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,3,'وَمَآ اَدْرٰىكَ مَا الْقَارِعَةُ ۗ','Tahukah kamu apakah hari Kiamat itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,4,'يَوْمَ يَكُوْنُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوْثِۙ','Pada hari itu manusia adalah seperti anai-anai yang bertebaran,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,5,'وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوْشِۗ','dan gunung-gunung adalah seperti bulu yang dihambur-hamburkan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,6,'فَاَمَّا مَنْ ثَقُلَتْ مَوَازِينُهٗۙ','Dan adapun orang-orang yang berat timbangan (kebaikan)nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,7,'فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۗ','maka dia berada dalam kehidupan yang memuaskan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,8,'وَاَمَّا مَنْ خَفَّتْ مَوَازِيْنُهٗۙ','Dan adapun orang-orang yang ringan timbangan (kebaikan)nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,9,'فَاُمُّهُ هَاوِيَةٌ  ۗ','maka tempat kembalinya adalah neraka Hawiyah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,10,'وَمَآ اَدْرٰىكَ مَا هِيَهْۗ','Tahukah kamu apakah neraka Hawiyah itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,101,11,'نَارٌ حَامِيَةٌ ࣖ','(Yaitu) api yang sangat panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,1,'اَلْهٰىكُمُ التَّكَاثُرُۙ','Bermegah-megahan telah melalaikan kamu,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,2,'حَتّٰى زُرْتُمُ الْمَقَابِرَۗ','sampai kamu masuk ke dalam kubur.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,3,'كَلَّا سَوْفَ تَعْلَمُوْنَۙ','Janganlah begitu, kelak kamu akan mengetahui (akibat perbuatanmu itu),')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,4,'ثُمَّ كَلَّا سَوْفَ تَعْلَمُوْنَ ','dan janganlah begitu, kelak kamu akan mengetahui.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,5,'كَلَّا لَوْ تَعْلَمُوْنَ عِلْمَ الْيَقِيْنِۗ','Janganlah begitu, jika kamu mengetahui dengan pengetahuan yang yakin,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,6,'لَتَرَوُنَّ الْجَحِيْمَۙ','niscaya kamu benar-benar akan melihat neraka Jahiim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,7,'ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِيْنِۙ','dan sesungguhnya kamu benar-benar akan melihatnya dengan ´ainul yaqin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,102,8,'ثُمَّ لَتُسْـَٔلُنَّ يَوْمَىِٕذٍ عَنِ النَّعِيْمِ ࣖ','kemudian kamu pasti akan ditanyai pada hari itu tentang kenikmatan (yang kamu megah-megahkan di dunia itu).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,103,1,'وَالْعَصْرِۙ','Demi masa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,103,2,'اِنَّ الْاِنْسَانَ لَفِيْ خُسْرٍۙ','Sesungguhnya manusia itu benar-benar dalam kerugian,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,103,3,'اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوْا بِالْحَقِّ ەۙ وَتَوَاصَوْا بِالصَّبْرِ ࣖ','kecuali orang-orang yang beriman dan mengerjakan amal saleh dan nasehat menasehati supaya mentaati kebenaran dan nasehat menasehati supaya menetapi kesabaran.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,1,'وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍۙ','Kecelakaanlah bagi setiap pengumpat lagi pencela,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,2,' ۨالَّذِيْ جَمَعَ مَالًا وَّعَدَّدَهٗۙ','yang mengumpulkan harta dan menghitung-hitung,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,3,'يَحْسَبُ اَنَّ مَالَهٗٓ اَخْلَدَهٗۚ','dia mengira bahwa hartanya itu dapat mengkekalkannya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,4,'كَلَّا لَيُنْۢبَذَنَّ فِى الْحُطَمَةِۖ','sekali-kali tidak! Sesungguhnya dia benar-benar akan dilemparkan ke dalam Huthamah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,5,'وَمَآ اَدْرٰىكَ مَا الْحُطَمَةُ ۗ','Dan tahukah kamu apa Huthamah itu?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,6,'نَارُ اللّٰهِ الْمُوْقَدَةُۙ','(yaitu) api (yang disediakan) Allah yang dinyalakan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,7,'الَّتِيْ تَطَّلِعُ عَلَى الْاَفْـِٕدَةِۗ','yang (membakar) sampai ke hati.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,8,'اِنَّهَا عَلَيْهِمْ مُّؤْصَدَةٌۙ','Sesungguhnya api itu ditutup rapat atas mereka,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,104,9,'فِيْ عَمَدٍ مُّمَدَّدَةٍ ࣖ','(sedang mereka itu) diikat pada tiang-tiang yang panjang.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,105,1,'اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحٰبِ الْفِيْلِۗ','Apakah kamu tidak memperhatikan bagaimana Tuhanmu telah bertindak terhadap tentara bergajah?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,105,2,'اَلَمْ يَجْعَلْ كَيْدَهُمْ فِيْ تَضْلِيْلٍۙ','Bukankah Dia telah menjadikan tipu daya mereka (untuk menghancurkan Ka´bah) itu sia-sia?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,105,3,'وَّاَرْسَلَ عَلَيْهِمْ طَيْرًا اَبَابِيْلَۙ','dan Dia mengirimkan kapada mereka burung yang berbondong-bondong,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,105,4,'تَرْمِيْهِمْ بِحِجَارَةٍ مِّنْ سِجِّيْلٍۙ','yang melempari mereka dengan batu (berasal) dari tanah yang terbakar,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,105,5,'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُوْلٍ ࣖ ','lalu Dia menjadikan mereka seperti daun-daun yang dimakan (ulat).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,106,1,'لِاِيْلٰفِ قُرَيْشٍۙ','Karena kebiasaan orang-orang Quraisy,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,106,2,'اٖلٰفِهِمْ رِحْلَةَ الشِّتَاۤءِ وَالصَّيْفِۚ','(yaitu) kebiasaan mereka bepergian pada musim dingin dan musim panas.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,106,3,'فَلْيَعْبُدُوْا رَبَّ هٰذَا الْبَيْتِۙ','Maka hendaklah mereka menyembah Tuhan Pemilik rumah ini (Ka´bah).')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,106,4,'الَّذِيْٓ اَطْعَمَهُمْ مِّنْ جُوْعٍ ەۙ وَّاٰمَنَهُمْ مِّنْ خَوْفٍ ࣖ','Yang telah memberi makanan kepada mereka untuk menghilangkan lapar dan mengamankan mereka dari ketakutan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,1,'اَرَءَيْتَ الَّذِيْ يُكَذِّبُ بِالدِّيْنِۗ','Tahukah kamu (orang) yang mendustakan agama?')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,2,'فَذٰلِكَ الَّذِيْ يَدُعُّ الْيَتِيْمَۙ','Itulah orang yang menghardik anak yatim,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,3,'وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ','dan tidak menganjurkan memberi makan orang miskin.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,4,'فَوَيْلٌ لِّلْمُصَلِّيْنَۙ','Maka kecelakaanlah bagi orang-orang yang shalat,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,5,'الَّذِيْنَ هُمْ عَنْ صَلَاتِهِمْ سَاهُوْنَۙ','(yaitu) orang-orang yang lalai dari shalatnya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,6,'الَّذِيْنَ هُمْ يُرَاۤءُوْنَۙ','orang-orang yang berbuat riya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,107,7,'وَيَمْنَعُوْنَ الْمَاعُوْنَ ࣖ','dan enggan (menolong dengan) barang berguna.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,108,1,'اِنَّآ اَعْطَيْنٰكَ الْكَوْثَرَۗ','Sesungguhnya Kami telah memberikan kepadamu nikmat yang banyak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,108,2,'فَصَلِّ لِرَبِّكَ وَانْحَرْۗ','Maka dirikanlah shalat karena Tuhanmu; dan berkorbanlah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,108,3,'اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ ࣖ','Sesungguhnya orang-orang yang membenci kamu dialah yang terputus.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,1,'قُلْ يٰٓاَيُّهَا الْكٰفِرُوْنَۙ','Katakanlah: "Hai orang-orang kafir,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,2,'لَآ اَعْبُدُ مَا تَعْبُدُوْنَۙ','Aku tidak akan menyembah apa yang kamu sembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,3,'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۚ','Dan kamu bukan penyembah Tuhan yang aku sembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,4,'وَلَآ اَنَا۠ عَابِدٌ مَّا عَبَدْتُّمْۙ','Dan aku tidak pernah menjadi penyembah apa yang kamu sembah,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,5,'وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۗ','dan kamu tidak pernah (pula) menjadi penyembah Tuhan yang aku sembah.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,109,6,'لَكُمْ دِيْنُكُمْ وَلِيَ دِيْنِ ࣖ','Untukmu agamamu, dan untukkulah, agamaku".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,110,1,'اِذَا جَاۤءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ','Apabila telah datang pertolongan Allah dan kemenangan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,110,2,'وَرَاَيْتَ النَّاسَ يَدْخُلُوْنَ فِيْ دِيْنِ اللّٰهِ اَفْوَاجًاۙ','dan kamu lihat manusia masuk agama Allah dengan berbondong-bondong,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,110,3,'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۗ اِنَّهٗ كَانَ تَوَّابًا ࣖ','maka bertasbihlah dengan memuji Tuhanmu dan mohonlah ampun kepada-Nya. Sesungguhnya Dia adalah Maha Penerima taubat.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,111,1,'تَبَّتْ يَدَآ اَبِيْ لَهَبٍ وَّتَبَّۗ','Binasalah kedua tangan Abu Lahab dan sesungguhnya dia akan binasa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,111,2,'مَآ اَغْنٰى عَنْهُ مَالُهٗ وَمَا كَسَبَۗ','Tidaklah berfaedah kepadanya harta bendanya dan apa yang ia usahakan.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,111,3,'سَيَصْلٰى نَارًا ذَاتَ لَهَبٍۙ','Kelak dia akan masuk ke dalam api yang bergejolak.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,111,4,'وَّامْرَاَتُهٗ ۗحَمَّالَةَ الْحَطَبِۚ','Dan (begitu pula) istrinya, pembawa kayu bakar.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,111,5,'فِيْ جِيْدِهَا حَبْلٌ مِّنْ مَّسَدٍ ࣖ','Yang di lehernya ada tali dari sabut.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,112,1,'قُلْ هُوَ اللّٰهُ اَحَدٌۚ','Katakanlah: "Dialah Allah, Yang Maha Esa.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,112,2,'اَللّٰهُ الصَّمَدُۚ','Allah adalah Tuhan yang bergantung kepada-Nya segala sesuatu.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,112,3,'لَمْ يَلِدْ وَلَمْ يُوْلَدْۙ','Dia tiada beranak dan tidak pula diperanakkan,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,112,4,'وَلَمْ يَكُنْ لَّهٗ كُفُوًا اَحَدٌ ࣖ','dan tidak ada seorangpun yang setara dengan Dia".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,113,1,'قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِۙ','Katakanlah: "Aku berlindung kepada Tuhan Yang Menguasai subuh,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,113,2,'مِنْ شَرِّ مَا خَلَقَۙ','dari kejahatan makhluk-Nya,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,113,3,'وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ','dan dari kejahatan malam apabila telah gelap gulita,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,113,4,'وَمِنْ شَرِّ النَّفّٰثٰتِ فِى الْعُقَدِۙ','dan dari kejahatan wanita-wanita tukang sihir yang menghembus pada buhul-buhul,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,113,5,'وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ ࣖ','dan dari kejahatan pendengki bila ia dengki".')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,1,'قُلْ اَعُوْذُ بِرَبِّ النَّاسِۙ','Katakanlah: "Aku berlindung kepada Tuhan (yang memelihara dan menguasai) manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,2,'مَلِكِ النَّاسِۙ','Raja manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,3,'اِلٰهِ النَّاسِۙ','Sembahan manusia.')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,4,'مِنْ شَرِّ الْوَسْوَاسِ ەۙ الْخَنَّاسِۖ','Dari kejahatan (bisikan) syaitan yang biasa bersembunyi,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,5,'الَّذِيْ يُوَسْوِسُ فِيْ صُدُوْرِ النَّاسِۙ','yang membisikkan (kejahatan) ke dalam dada manusia,')
INSERT INTO 'Quran' ('DatabaseID','JuzID','SuratID','VerseID','AyatText','Translation') VALUES (1,30,114,6,'مِنَ الْجِنَّةِ وَالنَّاسِ ࣖ ','dari (golongan) jin dan manusia."')